CREATE TABLE `songs` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `uuid` char(36) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL,
  `views` int DEFAULT '0',
  `likes` int DEFAULT '0',
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `num` int NOT NULL,
  `songId` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `lyrics` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `lyricsHtml` mediumtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `slug` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `video` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `language` varchar(2) COLLATE utf8mb4_unicode_ci NOT NULL,
  `SongCategoryId` bigint NOT NULL,
