INSERT INTO `songs` (`id`, `uuid`, `views`, `likes`, `title`, `num`, `songId`, `lyrics`, `lyricsHtml`, `slug`, `video`, `language`, `SongCategoryId`, `createdAt`, `updatedAt`) VALUES (1216,'af8a4657-f23f-4129-970e-4b693fb89c2a',3,0,'Gloire à Dieu',1,'1_CE_fr','1.Gloire à Dieu notre Créateur!Gloire à Christ notre Rédempteur!Gloire à l\'Esprit Consolateur!Louange et gloire au Dieu Sauveur!','<p><strong>1.</strong> </p>\n<p>Gloire à Dieu notre Créateur! </p>\n<p>Gloire à Christ notre Rédempteur! </p>\n<p>Gloire à l&#39;Esprit Consolateur! </p>\n<p>Louange et gloire au Dieu Sauveur! </p>\n','Gloire-a-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-09-21 22:52:32'),(1217,'88c3aa5e-3624-44aa-af8f-d3384840c072',0,0,'À celui qui nous a lavés',20,'20_CE_fr','1.\nA Celui qui nous a lavés\nQui nous a tant aimés\nPar son sang nous a rachetés\nSoit gloire, gloire, gloire\nLouange à jamais\n\n2.\nChantons le cantique nouveau\nA l\'honneur de l\'Agneau\nQui sortit vainqueur du tombeau\nOui gloire, gloire, gloire\nLouange à jamais\n\n3.\nAdorons le Triomphateur\nJésus, notre Sauveur\nSur tous ses ennemis vainqueur\nOui, gloire, gloire, gloire\nLouange à jamais\n\n4.\nDu ciel bientôt Il reviendra\nEt tout œil le verra\nLe monde entier l\'adorera\nOui, gloire, gloire, gloire\nLouange à jamais','<p><strong>1.</strong> </p>\n<p>A Celui qui nous a lavés </p>\n<p>Qui nous a tant aimés </p>\n<p>Par son sang nous a rachetés </p>\n<p>Soit gloire, gloire, gloire </p>\n<p>Louange à jamais </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chantons le cantique nouveau </p>\n<p>A l&#39;honneur de l&#39;Agneau </p>\n<p>Qui sortit vainqueur du tombeau </p>\n<p>Oui gloire, gloire, gloire </p>\n<p>Louange à jamais </p>\n<p><br/> <strong>3.</strong></p>\n<p>Adorons le Triomphateur </p>\n<p>Jésus, notre Sauveur </p>\n<p>Sur tous ses ennemis vainqueur </p>\n<p>Oui, gloire, gloire, gloire </p>\n<p>Louange à jamais </p>\n<p><br/> <strong>4.</strong></p>\n<p>Du ciel bientôt Il reviendra </p>\n<p>Et tout œil le verra </p>\n<p>Le monde entier l&#39;adorera </p>\n<p>Oui, gloire, gloire, gloire </p>\n<p>Louange à jamais </p>\n','A-celui-qui-nous-a-laves',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1218,'230f976d-0150-4597-94fb-16d38870a59f',0,0,'Que ne puis-je, Ô mon Dieu',15,'15_CE_fr','1.\nQue ne puis-je, ô mon Dieu,\nDieu de ma délivrance,\nRemplir de ta louange\nEt la terre et les cieux,\nLes prendre pour témoins,\nDe ma reconnaissance,\nEt dire au monde entier,\nCombien je suis heureux.\n\n\n2.\nHeureux quand je te parle,\nEt que ma poussière,\nJe fais monter vers Toi\nMon hommage ou mon voeu,\nAvec la liberté\nD\'un fils devant son père,\nEt le saint tremblement\nD\'un pécheur devant Dieu.\n\n\n3.\nHeureux, toujours heureux!\nJ\'ai le Dieu fort pour Père,\nPour Frère, Jésus-Christ,\nPour conseil, l\'Esprit-Saint:\nQue peut ôter l\'enfer,\nQue peut donner la terre\nA qui jouit du ciel\nEt du Dieu trois saint?','<p><strong>1.</strong> </p>\n<p>Que ne puis-je, ô mon Dieu, </p>\n<p>Dieu de ma délivrance, </p>\n<p>Remplir de ta louange </p>\n<p>Et la terre et les cieux, </p>\n<p>Les prendre pour témoins, </p>\n<p>De ma reconnaissance, </p>\n<p>Et dire au monde entier, </p>\n<p>Combien je suis heureux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Heureux quand je te parle, </p>\n<p>Et que ma poussière, </p>\n<p>Je fais monter vers Toi </p>\n<p>Mon hommage ou mon voeu, </p>\n<p>Avec la liberté </p>\n<p>D&#39;un fils devant son père, </p>\n<p>Et le saint tremblement </p>\n<p>D&#39;un pécheur devant Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Heureux, toujours heureux! </p>\n<p>J&#39;ai le Dieu fort pour Père, </p>\n<p>Pour Frère, Jésus-Christ, </p>\n<p>Pour conseil, l&#39;Esprit-Saint: </p>\n<p>Que peut ôter l&#39;enfer, </p>\n<p>Que peut donner la terre </p>\n<p>A qui jouit du ciel </p>\n<p>Et du Dieu trois saint? </p>\n','Que-ne-puis-je-O-mon-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1219,'6468f0b1-85b1-4684-a509-5ad20df2cecf',0,0,'Hosanna! Béni soit',24,'24_CE_fr','1.\nHosanna! Béni soit\nLe Sauveur débonnaire\nQui vers nous, plein d\'amour,\nDescend du sein du Père!\nBéni soit le Seigneur,\nQui vient des plus haut cieux\nApporter aux humains\nUn salut glorieux!  (bis)\n\n2.\nHosanna! Béni soit\nLe Prince de la vie!\nQue de joie, en son nom,\nNotre âme soit ravie!\nQu\'en des accents nouveaux\nElle éclate aujourd\'hui.\nQue tout enfant de Dieu\nTressaille devant Lui.  (bis)\n\n3.\nHosanna! Béni soit\nCette Ami charitable\nQue le plus grand pécheur\nVa trouver favorable!\nHumble et sans apparat,\nSous notre humanité\nIl a voilé l\'éclat\nDe sa divinité.  (bis)\n\n4.\nHosanna! Béni soit\nJésus notre justice!\nPour nous, pauvre pécheurs,\nIl s\'offre en sacrifice;\nCe Fils du Dieu Très-Haut,\nCe puissant Roi des rois,\nPour nous ouvrir le ciel\nVient mourir sur la croix.  (bis)\n\n5.\nHosanna! Racheté,\nPeuple libre et fidèle,\nRépétez: hosanna!\nPleins d\'une ardeur nouvelle;\nC\'est votre hymne d\'amour,\nC\'est votre chant de paix:\nQue ce chant parmi vous\nRetentisse à jamais!  (bis)','<p><strong>1.</strong> </p>\n<p>Hosanna! Béni soit </p>\n<p>Le Sauveur débonnaire </p>\n<p>Qui vers nous, plein d&#39;amour, </p>\n<p>Descend du sein du Père! </p>\n<p>Béni soit le Seigneur, </p>\n<p>Qui vient des plus haut cieux </p>\n<p>Apporter aux humains </p>\n<p>Un salut glorieux!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Hosanna! Béni soit </p>\n<p>Le Prince de la vie! </p>\n<p>Que de joie, en son nom, </p>\n<p>Notre âme soit ravie! </p>\n<p>Qu&#39;en des accents nouveaux </p>\n<p>Elle éclate aujourd&#39;hui. </p>\n<p>Que tout enfant de Dieu </p>\n<p>Tressaille devant Lui.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Hosanna! Béni soit </p>\n<p>Cette Ami charitable </p>\n<p>Que le plus grand pécheur </p>\n<p>Va trouver favorable! </p>\n<p>Humble et sans apparat, </p>\n<p>Sous notre humanité </p>\n<p>Il a voilé l&#39;éclat </p>\n<p>De sa divinité.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Hosanna! Béni soit </p>\n<p>Jésus notre justice! </p>\n<p>Pour nous, pauvre pécheurs, </p>\n<p>Il s&#39;offre en sacrifice; </p>\n<p>Ce Fils du Dieu Très-Haut, </p>\n<p>Ce puissant Roi des rois, </p>\n<p>Pour nous ouvrir le ciel </p>\n<p>Vient mourir sur la croix.  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Hosanna! Racheté, </p>\n<p>Peuple libre et fidèle, </p>\n<p>Répétez: hosanna! </p>\n<p>Pleins d&#39;une ardeur nouvelle; </p>\n<p>C&#39;est votre hymne d&#39;amour, </p>\n<p>C&#39;est votre chant de paix: </p>\n<p>Que ce chant parmi vous </p>\n<p>Retentisse à jamais!  (bis) </p>\n','Hosanna!-Beni-soit',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1220,'e4da4dd1-5bd5-47f8-b0b7-bc1b9c2bd8c2',1,0,'On n’adore – Pas encore',4,'4_CE_fr','1.\nOn n\'adore pas encore\nQuand on chante à l\'Éternel.\nPour lui plaire, -C\'est peu faire\nQue de monter à Béthel.\nDieu regarde – Si l\'on garde\nLe coeur autant que l\'autel.\n\n2.\nQui s\'excuse – Et n\'accuse\nQue le prochain devant toi,\nPar son culte – Fait insulte\nSeigneur, à ta sainte loi.\nAh! Qu\'il sorte – Et remporte\nSes offrandes avec soi.\n\n3.\nDivin Maître, - Fais-nous être\nUn peuple d\'adorateurs!\nTa louange, - Chère à l\'ange,\nEst la gloire des pécheurs.\nQuand des larmes – Sont nos armes,\nTon amour nous rend vainqueurs.','<p><strong>1.</strong> </p>\n<p>On n&#39;adore pas encore </p>\n<p>Quand on chante à l&#39;Éternel. </p>\n<p>Pour lui plaire, -C&#39;est peu faire </p>\n<p>Que de monter à Béthel. </p>\n<p>Dieu regarde – Si l&#39;on garde </p>\n<p>Le coeur autant que l&#39;autel. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qui s&#39;excuse – Et n&#39;accuse </p>\n<p>Que le prochain devant toi, </p>\n<p>Par son culte – Fait insulte </p>\n<p>Seigneur, à ta sainte loi. </p>\n<p>Ah! Qu&#39;il sorte – Et remporte </p>\n<p>Ses offrandes avec soi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Divin Maître, - Fais-nous être </p>\n<p>Un peuple d&#39;adorateurs! </p>\n<p>Ta louange, - Chère à l&#39;ange, </p>\n<p>Est la gloire des pécheurs. </p>\n<p>Quand des larmes – Sont nos armes, </p>\n<p>Ton amour nous rend vainqueurs. </p>\n','On-n\'adore-Pas-encore',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-23 00:26:23'),(1221,'3c085758-fc5d-4ec3-a723-0b4f6139e92b',0,0,'Du rocher de Jacob',9,'9_CE_fr','1.\nDu rocher de Jacob\nToute l\'oeuvre est parfaite.\nCe que sa bouche a dit,\nSa main l\'accomplira!\nAlléluia! Alléluia! (bis)\nCar Il est notre Dieu, (ter)\nNotre haute retraite!\n\n2.\nC\'est pour l\'éternité\nQue le Seigneur nous aime:\nSa grâce en notre coeur\nJamais ne cessera.\nAlléluia!  Alléluia!  (bis)\nCar Il est notre espoir,  (ter)\nNotre bonheur suprême.\n\n3.\nDe tous nos ennemis\nIl sait quel est le nombre;\nSon bras combat pour nous\nEt nous délivrera.\nAlléluia!  Alléluia!  (bis)\nLes méchants devant lui,  (ter)\nS\'enfuiront comme l\'ombre.\n\n4.\nLouons donc l\'Éternel,\nNotre Dieu, notre Père!\nLe Seigneur est pour nous:\nContre nous qui sera?\nAlléluia!  Alléluia!  (bis)\nTriomphons en Jésus,  (ter)\nEt vivons pour lui plaire.','<p><strong>1.</strong> </p>\n<p>Du rocher de Jacob </p>\n<p>Toute l&#39;oeuvre est parfaite. </p>\n<p>Ce que sa bouche a dit, </p>\n<p>Sa main l&#39;accomplira! </p>\n<p>Alléluia! Alléluia! (bis) </p>\n<p>Car Il est notre Dieu, (ter) </p>\n<p>Notre haute retraite! </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est pour l&#39;éternité </p>\n<p>Que le Seigneur nous aime: </p>\n<p>Sa grâce en notre coeur </p>\n<p>Jamais ne cessera. </p>\n<p>Alléluia!  Alléluia!  (bis) </p>\n<p>Car Il est notre espoir,  (ter) </p>\n<p>Notre bonheur suprême. </p>\n<p><br/> <strong>3.</strong></p>\n<p>De tous nos ennemis </p>\n<p>Il sait quel est le nombre; </p>\n<p>Son bras combat pour nous </p>\n<p>Et nous délivrera. </p>\n<p>Alléluia!  Alléluia!  (bis) </p>\n<p>Les méchants devant lui,  (ter) </p>\n<p>S&#39;enfuiront comme l&#39;ombre. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Louons donc l&#39;Éternel, </p>\n<p>Notre Dieu, notre Père! </p>\n<p>Le Seigneur est pour nous: </p>\n<p>Contre nous qui sera? </p>\n<p>Alléluia!  Alléluia!  (bis) </p>\n<p>Triomphons en Jésus,  (ter) </p>\n<p>Et vivons pour lui plaire. </p>\n','Du-rocher-de-Jacob',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1222,'a5276bdd-a4a9-4509-a434-dd21d905fb30',0,0,'À toi nos transports d’allégresse',13,'13_CE_fr','1.\nA toi nos transports d\'allégresse\nA toi, Seigneur, nos plus beaux chants\nA toi nos rêves de jeunesse\nA toi les fleurs de nos printemps\n\nRefrain\nGloire, honneur, sagesse et puissance\nAu Dieu qui nous a tant aimés\nRichesse, force, obéissance\nA l\'agneau qui nous a sauvés\n\n2.\nA toi nos élans juvéniles\nNos frais bonheurs, nos saints désirs\nLes gerbes de nos champs fertiles\nNos durs labeurs, nos purs loisirs\n\n3.\nSois le centre de notre vie\nVis au tréfonds de notre cœur\nEt toujours notre âme affranchie\nChantera son libérateur','<p><strong>1.</strong> </p>\n<p>A toi nos transports d&#39;allégresse </p>\n<p>A toi, Seigneur, nos plus beaux chants </p>\n<p>A toi nos rêves de jeunesse </p>\n<p>A toi les fleurs de nos printemps </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire, honneur, sagesse et puissance</strong> </p>\n<p><strong>Au Dieu qui nous a tant aimés</strong> </p>\n<p><strong>Richesse, force, obéissance</strong> </p>\n<p><strong>A l&#39;agneau qui nous a sauvés</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A toi nos élans juvéniles </p>\n<p>Nos frais bonheurs, nos saints désirs </p>\n<p>Les gerbes de nos champs fertiles </p>\n<p>Nos durs labeurs, nos purs loisirs </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sois le centre de notre vie </p>\n<p>Vis au tréfonds de notre cœur </p>\n<p>Et toujours notre âme affranchie </p>\n<p>Chantera son libérateur </p>\n','A-toi-nos-transports-d\'allegresse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1223,'3d59cb39-1425-4035-a727-780c097eab5a',0,0,'Mon seul appui, c’est l’Ami Céleste',28,'28_CE_fr','1.\nMon seul appui, c\'est l\'Ami Céleste,\nJésus seul!  Jésus seul!\nLes ans s\'en vont, cet Ami me reste,\nJésus seul!  Jésus seul!\n\nRefrain\nCet Ami connaît mes alarmes,\nSon amour guérit ma douleur;\nSa main essuie toutes mes larmes,\nDoux Sauveur!  Doux Sauveur!\n\n2.\nTout mon désir c\'est de le connaître,\nJésus seul!  Jésus seul!\nEt que sa paix remplisse mon être,\nJésus seul!  Jésus seul!\n\n3.\nDans le danger, toujours Il me garde,\nJésus seul!  Jésus seul!\nDans mes soucis, à Lui je regarde,\nJésus seul!  Jésus seul!','<p><strong>1.</strong> </p>\n<p>Mon seul appui, c&#39;est l&#39;Ami Céleste, </p>\n<p>Jésus seul!  Jésus seul! </p>\n<p>Les ans s&#39;en vont, cet Ami me reste, </p>\n<p>Jésus seul!  Jésus seul! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Cet Ami connaît mes alarmes,</strong> </p>\n<p><strong>Son amour guérit ma douleur;</strong> </p>\n<p><strong>Sa main essuie toutes mes larmes,</strong> </p>\n<p><strong>Doux Sauveur!  Doux Sauveur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout mon désir c&#39;est de le connaître, </p>\n<p>Jésus seul!  Jésus seul! </p>\n<p>Et que sa paix remplisse mon être, </p>\n<p>Jésus seul!  Jésus seul! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans le danger, toujours Il me garde, </p>\n<p>Jésus seul!  Jésus seul! </p>\n<p>Dans mes soucis, à Lui je regarde, </p>\n<p>Jésus seul!  Jésus seul! </p>\n','Mon-seul-appui-c\'est-l\'Ami-Celeste',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1224,'83c05637-f396-4df3-881c-7f94366d67e2',0,0,'Mon Sauveur m\'aime',36,'36_CE_fr','1.\nMon Sauveur m\'aime,\nQuel ferme appui pour ma foi!\nL\'amour suprême\nDescend vers moi!\nDieu vint sur la terre\nVivre en homme de douleur;\nVoyant ma misère,\nIl m\'ouvre son coeur.\n\nRefrain\nJésus! Jésus! Qui peut sonder ton amour?\nJésus! Jésus! Je t\'aime en retour.\n\n2.\nMon Sauveur m\'aime\nMalgré mon indignité,\nToujours le même,\nPlein de pitié!\nIl voit ma souffrance,\nIl enlève mon souci;\nSon amour immense\nEst mon sûr abri.\n\n3.\nMon Sauveur m\'aime\nComme un joyau précieux\nDu diadème\nQu\'il porte aux cieux.\nSa vie éternelle,\nIl partage avec moi,\nSa gloire immortelle,\nIl offre à ma foi.','<p><strong>1.</strong> </p>\n<p>Mon Sauveur m&#39;aime, </p>\n<p>Quel ferme appui pour ma foi! </p>\n<p>L&#39;amour suprême </p>\n<p>Descend vers moi! </p>\n<p>Dieu vint sur la terre </p>\n<p>Vivre en homme de douleur; </p>\n<p>Voyant ma misère, </p>\n<p>Il m&#39;ouvre son coeur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus! Jésus! Qui peut sonder ton amour?</strong> </p>\n<p><strong>Jésus! Jésus! Je t&#39;aime en retour.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon Sauveur m&#39;aime </p>\n<p>Malgré mon indignité, </p>\n<p>Toujours le même, </p>\n<p>Plein de pitié! </p>\n<p>Il voit ma souffrance, </p>\n<p>Il enlève mon souci; </p>\n<p>Son amour immense </p>\n<p>Est mon sûr abri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon Sauveur m&#39;aime </p>\n<p>Comme un joyau précieux </p>\n<p>Du diadème </p>\n<p>Qu&#39;il porte aux cieux. </p>\n<p>Sa vie éternelle, </p>\n<p>Il partage avec moi, </p>\n<p>Sa gloire immortelle, </p>\n<p>Il offre à ma foi. </p>\n','Mon-Sauveur-m\'aime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1225,'6ccf2eff-8833-489f-9f95-5d934ad33c14',0,0,'Sur la paille fraîche',45,'45_CE_fr','1.\nSur la paille fraîche\nUn pauvre crèche\nDort l\'enfant si beau;\nLui sert de berceau.\nDe l\'azur céleste\nL\'Étoile a souri\nA l\'Enfant qui reste\nSi tard endormi.\n\n2.\nVoilà que l\'ânesse\nRéveille l\'enfant!\nMais Lui, la caresse\nTout en souriant.\nCher Jésus, je t\'aime!\nPrends mon petit coeur\nEt veille toi-même\nSur moi, doux Seigneur!\n\n3.\nPrès, plus près encore\nDe mon petit lit\nDu soir à l\'aurore\nCar j\'ai peur, la nuit!\nBénis, frère tendre\nLes autres petits…\nViens bientôt nous prendre\nDans ton paradis.','<p><strong>1.</strong> </p>\n<p>Sur la paille fraîche </p>\n<p>Un pauvre crèche </p>\n<p>Dort l&#39;enfant si beau; </p>\n<p>Lui sert de berceau. </p>\n<p>De l&#39;azur céleste </p>\n<p>L&#39;Étoile a souri </p>\n<p>A l&#39;Enfant qui reste </p>\n<p>Si tard endormi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voilà que l&#39;ânesse </p>\n<p>Réveille l&#39;enfant! </p>\n<p>Mais Lui, la caresse </p>\n<p>Tout en souriant. </p>\n<p>Cher Jésus, je t&#39;aime! </p>\n<p>Prends mon petit coeur </p>\n<p>Et veille toi-même </p>\n<p>Sur moi, doux Seigneur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Près, plus près encore </p>\n<p>De mon petit lit </p>\n<p>Du soir à l&#39;aurore </p>\n<p>Car j&#39;ai peur, la nuit! </p>\n<p>Bénis, frère tendre </p>\n<p>Les autres petits… </p>\n<p>Viens bientôt nous prendre </p>\n<p>Dans ton paradis. </p>\n','Sur-la-paille-fraiche',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1226,'7f45775b-2d9b-41bb-a1c0-07016b11620d',0,0,'Jésus, Agneau de Dieu',61,'61_CE_fr','1.\nJésus, Agneau de Dieu!\nDu sang de l\'alliance\nRépandu sur la croix\nPour de pauvres pécheurs,\nJusqu\'à la fin des temps\nDurera ta puissance,\nEt tous les rachetés\nSeront plus que vainqueurs.\n\n2.\nLe brigand converti\nTrouva dans ce refuge\nUne pleine espérance\nA ses derniers moments;\nCoupable comme lui,\nTremblant devant mon juge,\nC\'est là que j\'ai cherché\nLa fin de mes tourments.\n\n3.\nDans la sainte cité\nPar mon Dieu préparée,\nJe chanterai l\'amour\nEt le nom glorieux\nDu berger qui chercha\nSa brebis égarée,\nEt la prit dans ses bras\nPour la porter aux cieux.','<p><strong>1.</strong> </p>\n<p>Jésus, Agneau de Dieu! </p>\n<p>Du sang de l&#39;alliance </p>\n<p>Répandu sur la croix </p>\n<p>Pour de pauvres pécheurs, </p>\n<p>Jusqu&#39;à la fin des temps </p>\n<p>Durera ta puissance, </p>\n<p>Et tous les rachetés </p>\n<p>Seront plus que vainqueurs. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le brigand converti </p>\n<p>Trouva dans ce refuge </p>\n<p>Une pleine espérance </p>\n<p>A ses derniers moments; </p>\n<p>Coupable comme lui, </p>\n<p>Tremblant devant mon juge, </p>\n<p>C&#39;est là que j&#39;ai cherché </p>\n<p>La fin de mes tourments. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans la sainte cité </p>\n<p>Par mon Dieu préparée, </p>\n<p>Je chanterai l&#39;amour </p>\n<p>Et le nom glorieux </p>\n<p>Du berger qui chercha </p>\n<p>Sa brebis égarée, </p>\n<p>Et la prit dans ses bras </p>\n<p>Pour la porter aux cieux. </p>\n','Jesus-Agneau-de-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1227,'3bf5c816-6b34-43e3-8264-bd9d3e8de756',0,0,'Vers toi monte notre',25,'25_CE_fr','1.\nVers Toi monte notre hommage,\nFils de Dieu, puissant Sauveur,\nQui demeure d\'âge en âge\nLe refuge du pécheur.\n\nRefrain\nLoué soit ton amour\nLoués soient à jamais\nTon nom, Jésus,\nTa gloire et tes bienfaits;\nLoué soit ton amour,\nLoués soient à jamais,\nTon nom, ta gloire et tes bienfaits!\n\n2.\nDe Toi vient la délivrance:\nTu payas notre rançon.\nC\'est en Toi qu\'est l\'espérance,\nLa paix et la guérison.\n\n3.\nOh! Qu\'heureux sous ta bannière\nEst le peuple racheté\nQui marche, dans la lumière,\nVers la céleste cité.\n\n4.\nPar ta divine parole\nTu l\'enseignes, Tu l\'instruits,\nEt, par l\'Esprit qui console,\nSûrement Tu le conduits.','<p><strong>1.</strong> </p>\n<p>Vers Toi monte notre hommage, </p>\n<p>Fils de Dieu, puissant Sauveur, </p>\n<p>Qui demeure d&#39;âge en âge </p>\n<p>Le refuge du pécheur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Loué soit ton amour</strong> </p>\n<p><strong>Loués soient à jamais</strong> </p>\n<p><strong>Ton nom, Jésus,</strong> </p>\n<p><strong>Ta gloire et tes bienfaits;</strong> </p>\n<p><strong>Loué soit ton amour,</strong> </p>\n<p><strong>Loués soient à jamais,</strong> </p>\n<p><strong>Ton nom, ta gloire et tes bienfaits!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De Toi vient la délivrance: </p>\n<p>Tu payas notre rançon. </p>\n<p>C&#39;est en Toi qu&#39;est l&#39;espérance, </p>\n<p>La paix et la guérison. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh! Qu&#39;heureux sous ta bannière </p>\n<p>Est le peuple racheté </p>\n<p>Qui marche, dans la lumière, </p>\n<p>Vers la céleste cité. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par ta divine parole </p>\n<p>Tu l&#39;enseignes, Tu l&#39;instruits, </p>\n<p>Et, par l&#39;Esprit qui console, </p>\n<p>Sûrement Tu le conduits. </p>\n','Vers-toi-monte-notre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1228,'ee7f26d4-0897-4b47-a8a0-87eae5c0b703',0,0,'Chantons du Sauveur la tendresse',11,'11_CE_fr','1.\nChantons du Sauveur la tendresse\nSur la croix il est mort pour nous\nIl remplit nos cœurs d\'allégresse\nAu ciel il nous invite tous\n\nRefrain\nJe veux ( oui je veux)\nChanter ( mon Sauveur)\nJe veux dire à tous mon bonheur.\nChantons ( le Sauveur ),(bis)\nChantons l\'amour du rédempteur!\n\n2.\nChantons du Sauveur la puissance\nC\'est lui qui brisa nos liens\nPerdus, sans Dieu, sans espérance\nIl nous racheta, nous fit siens\n\n3.\nChantons, remplis de confiance\nChantons sans peur du lendemain\nEn paix, gardés pas sa puissance\nConduits chaque jour par sa main','<p><strong>1.</strong> </p>\n<p>Chantons du Sauveur la tendresse </p>\n<p>Sur la croix il est mort pour nous </p>\n<p>Il remplit nos cœurs d&#39;allégresse </p>\n<p>Au ciel il nous invite tous </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je veux ( oui je veux)</strong> </p>\n<p><strong>Chanter ( mon Sauveur)</strong> </p>\n<p><strong>Je veux dire à tous mon bonheur.</strong> </p>\n<p><strong>Chantons ( le Sauveur ),(bis)</strong> </p>\n<p><strong>Chantons l&#39;amour du rédempteur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chantons du Sauveur la puissance </p>\n<p>C&#39;est lui qui brisa nos liens </p>\n<p>Perdus, sans Dieu, sans espérance </p>\n<p>Il nous racheta, nous fit siens </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chantons, remplis de confiance </p>\n<p>Chantons sans peur du lendemain </p>\n<p>En paix, gardés pas sa puissance </p>\n<p>Conduits chaque jour par sa main </p>\n','Chantons-du-Sauveur-la-tendresse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1229,'3ed7ba61-b073-4918-b242-040d5b4e6561',25,1,'Adorons le Père',3,'3_CE_fr','1.Adorons le PèreAu plus haut des cieux,La sainte lumièreQui brille en tout lieux.Proclamons sa grâce,Le pardon divin,L\'amour efficace,Le bonheur ans fin.2.Célébrons la gloireDu puissant Sauveur;Chantons la victoire,Du grand Rédempteur!Il vint sur la terrePour nous secourir;Plus tendre qu\'un frère,Il voulut mourir.3.Viens, céleste flamme,Messager divin,Viens, montre à notre âme,Jésus, le chemin!Descend à cette heureDans nos coeur joyeux;Qu\'ils soient ta demeure,Esprit glorieux!','<p><strong>1.</strong> </p>\n<p>Adorons le Père </p>\n<p>Au plus haut des cieux, </p>\n<p>La sainte lumière </p>\n<p>Qui brille en tout lieux. </p>\n<p>Proclamons sa grâce, </p>\n<p>Le pardon divin, </p>\n<p>L&#39;amour efficace, </p>\n<p>Le bonheur sans fin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Célébrons la gloire </p>\n<p>Du puissant Sauveur; </p>\n<p>Chantons la victoire, </p>\n<p>Du grand Rédempteur! </p>\n<p>Il vint sur la terre </p>\n<p>Pour nous secourir; </p>\n<p>Plus tendre qu&#39;un frère, </p>\n<p>Il voulut mourir. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Viens, céleste flamme, </p>\n<p>Messager divin, </p>\n<p>Viens, montre à notre âme, </p>\n<p>Jésus, le chemin! </p>\n<p>Descend à cette heure </p>\n<p>Dans nos coeur joyeux; </p>\n<p>Qu&#39;ils soient ta demeure, </p>\n<p>Esprit glorieux! </p>\n','Adorons-le-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-10-03 20:26:34'),(1230,'e18dbf1e-ec92-48ae-bac9-3cc123b40b7b',0,0,'Oui je veux te bénir',16,'16_CE_fr','1.\nOui, je veux te bénir\nEt chanter ta clémence!\nHélas! Seigneur, je t\'avais irrité,\nMais tu m\'as d\'un regard\nRendu ta bienveillance,\nEn me lavant de mon iniquité. (bis)\n\n2.\nLe Dieu fort est ma paix,\nIl est ma délivrance;\nDe mon esprit il bannit la frayeur;\nJe veux donc en lui seul\nMettre ma confiance,\nEt donner gloire à mon libérateur. (bis)\n\n3.\nVenez, enfants de Dieu!\nPuisez, plein d\'allégresse,\nL\'eau de salut à la source des eaux,\nRacontez les bienfaits,\nProclamez la promesse\nDe l\'Éternel qui guérit tous vos maux. (bis)','<p><strong>1.</strong> </p>\n<p>Oui, je veux te bénir </p>\n<p>Et chanter ta clémence! </p>\n<p>Hélas! Seigneur, je t&#39;avais irrité, </p>\n<p>Mais tu m&#39;as d&#39;un regard </p>\n<p>Rendu ta bienveillance, </p>\n<p>En me lavant de mon iniquité. (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le Dieu fort est ma paix, </p>\n<p>Il est ma délivrance; </p>\n<p>De mon esprit il bannit la frayeur; </p>\n<p>Je veux donc en lui seul </p>\n<p>Mettre ma confiance, </p>\n<p>Et donner gloire à mon libérateur. (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Venez, enfants de Dieu! </p>\n<p>Puisez, plein d&#39;allégresse, </p>\n<p>L&#39;eau de salut à la source des eaux, </p>\n<p>Racontez les bienfaits, </p>\n<p>Proclamez la promesse </p>\n<p>De l&#39;Éternel qui guérit tous vos maux. (bis) </p>\n','Oui-je-veux-te-benir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1231,'098b33d3-b89a-458b-a11a-4351f101df45',0,0,'Seigneur, ô Sauveur',37,'37_CE_fr','1.\nO Seigneur, ô Sauveur,\nQue nos lèves te louent,\nMais qu\'avec nos accents\nNos oeuvres soient d\'accord!\nSi, par nos actions,\nNos coeurs te désavouent,\nDans nos chants les plus beaux.\nTout est vain, tout est mort.  (bis)\n\n2.\nTu naquis pour servir,\nEt servir fut ta gloire;\nServir est à jamais\nLe sceau de tes enfants.\nQui fait peu t\'aime peu;\nQui se borne à te croire,\nNe te croit point encore,\nO Sauveur des croyants.  (bis)\n\n3.\nMourut-il avec Christ\nAu rocher du Calvaire,\nL\'amour pieux et tendre,\nAsile du malheur?\nNon, l\'amour y naquit,\nEt dès lors sur la terre,\nComme on cherche un trésor,\nIl cherche la douleur.\n\n4.\nQue de maux, de périls,\nEt de besoins m\'appellent!\nQue de frères, d\'amis,\nDieu jette dans mes bras!\nQue d\'oeuvres à fonder,\nQue d\'oeuvres qui chancellent…\nTravaillons: le loisir\nN\'appartient qu\'aux ingrats.  (bis)','<p><strong>1.</strong> </p>\n<p>O Seigneur, ô Sauveur, </p>\n<p>Que nos lèves te louent, </p>\n<p>Mais qu&#39;avec nos accents </p>\n<p>Nos oeuvres soient d&#39;accord! </p>\n<p>Si, par nos actions, </p>\n<p>Nos coeurs te désavouent, </p>\n<p>Dans nos chants les plus beaux. </p>\n<p>Tout est vain, tout est mort.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu naquis pour servir, </p>\n<p>Et servir fut ta gloire; </p>\n<p>Servir est à jamais </p>\n<p>Le sceau de tes enfants. </p>\n<p>Qui fait peu t&#39;aime peu; </p>\n<p>Qui se borne à te croire, </p>\n<p>Ne te croit point encore, </p>\n<p>O Sauveur des croyants.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mourut-il avec Christ </p>\n<p>Au rocher du Calvaire, </p>\n<p>L&#39;amour pieux et tendre, </p>\n<p>Asile du malheur? </p>\n<p>Non, l&#39;amour y naquit, </p>\n<p>Et dès lors sur la terre, </p>\n<p>Comme on cherche un trésor, </p>\n<p>Il cherche la douleur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que de maux, de périls, </p>\n<p>Et de besoins m&#39;appellent! </p>\n<p>Que de frères, d&#39;amis, </p>\n<p>Dieu jette dans mes bras! </p>\n<p>Que d&#39;oeuvres à fonder, </p>\n<p>Que d&#39;oeuvres qui chancellent… </p>\n<p>Travaillons: le loisir </p>\n<p>N&#39;appartient qu&#39;aux ingrats.  (bis) </p>\n','Seigneur-o-Sauveur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1232,'2bffe2f8-7a7c-4cb1-9a8b-3066741bc400',0,0,'Écoutez! Un saint cantique',41,'41_CE_fr','1.\nÉcoutez! Un saint cantique\nVient d\'éclater dans les cieux:\nC\'est un hymne magnifique,\nC\'est un chant simple et joyeux.\nLes voix parcourant l\'espace,\nDisent aux humains surpris:\n“La loi fait place à la grâce,\nEt Moïse à Jésus-Christ.”\n\n2.\nLa promesse est accomplie,\nLe salut nous est donné:\nEn Christ, Dieu réconcilie\nSa justice et sa bonté.\nL\'heure de la délivrance\nPour les captifs a sonné;\nC\'est la nouvelle alliance:\nL\'enfant Jésus nous est né.\n\n3.\nEt, soudain, perçant le voile\nDu firmament ténébreux,\nL\'éclat nouveau d\'une étoile\nVers Judas jette ses feux.\nLes Mages, à sa lumière,\nAccourent de l\'Orient;\nEt pour entendre gloire au Père,\nIls vont adorer l\'Enfant.\n\n4.\nCe Jésus est notre Frère,\nIl a vécu parmi nous;\nIl a pris notre misère,\nPécheurs, il est mort pour nous.\nSa charité, nous enlace,\nIl veut régner sur nos coeurs;\nSachons accepter sa grâce:\nJésus, sois notre Sauveur!','<p><strong>1.</strong> </p>\n<p>Écoutez! Un saint cantique </p>\n<p>Vient d&#39;éclater dans les cieux: </p>\n<p>C&#39;est un hymne magnifique, </p>\n<p>C&#39;est un chant simple et joyeux. </p>\n<p>Les voix parcourant l&#39;espace, </p>\n<p>Disent aux humains surpris: </p>\n<p>“La loi fait place à la grâce, </p>\n<p>Et Moïse à Jésus-Christ.” </p>\n<p><br/> <strong>2.</strong></p>\n<p>La promesse est accomplie, </p>\n<p>Le salut nous est donné: </p>\n<p>En Christ, Dieu réconcilie </p>\n<p>Sa justice et sa bonté. </p>\n<p>L&#39;heure de la délivrance </p>\n<p>Pour les captifs a sonné; </p>\n<p>C&#39;est la nouvelle alliance: </p>\n<p>L&#39;enfant Jésus nous est né. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et, soudain, perçant le voile </p>\n<p>Du firmament ténébreux, </p>\n<p>L&#39;éclat nouveau d&#39;une étoile </p>\n<p>Vers Judas jette ses feux. </p>\n<p>Les Mages, à sa lumière, </p>\n<p>Accourent de l&#39;Orient; </p>\n<p>Et pour entendre gloire au Père, </p>\n<p>Ils vont adorer l&#39;Enfant. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ce Jésus est notre Frère, </p>\n<p>Il a vécu parmi nous; </p>\n<p>Il a pris notre misère, </p>\n<p>Pécheurs, il est mort pour nous. </p>\n<p>Sa charité, nous enlace, </p>\n<p>Il veut régner sur nos coeurs; </p>\n<p>Sachons accepter sa grâce: </p>\n<p>Jésus, sois notre Sauveur! </p>\n','Ecoutez!-Un-saint-cantique',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1233,'66b56f32-8298-4445-9c44-66d12098439b',0,0,'Quelle est au ciel cette brillante étoile',46,'46_CE_fr','1.\nQuelle est au ciel cette brillante étoile\nVers l\'Orient jetant un feu nouveau?\nJamais encore aucun astre aussi beau\nDes sombres nuits n\'avait percé le voile.\n\n2.\nVers Bethléem, dans la sainte Judée,\nElle répand ses célestes lueurs;\nElle y conduit de vrais adorateurs\nDe qui la foi sur le Christ est fondée.\n\n3.\nIls ont trouvé le berceau du Messie.\nDans une crèche, un enfant ignoré\nDort humblement, de bergers entouré;\nC\'est l\'Oint de Dieu, le Prince de la vie.\n\n4.\nDivin Jésus, étoile matinière,\nDe tes rayons viens éclairer nos yeux;\nViens nous conduire au chemin glorieux\nQue ton amour nous traça sur la terre.','<p><strong>1.</strong> </p>\n<p>Quelle est au ciel cette brillante étoile </p>\n<p>Vers l&#39;Orient jetant un feu nouveau? </p>\n<p>Jamais encore aucun astre aussi beau </p>\n<p>Des sombres nuits n&#39;avait percé le voile. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vers Bethléem, dans la sainte Judée, </p>\n<p>Elle répand ses célestes lueurs; </p>\n<p>Elle y conduit de vrais adorateurs </p>\n<p>De qui la foi sur le Christ est fondée. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ils ont trouvé le berceau du Messie. </p>\n<p>Dans une crèche, un enfant ignoré </p>\n<p>Dort humblement, de bergers entouré; </p>\n<p>C&#39;est l&#39;Oint de Dieu, le Prince de la vie. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Divin Jésus, étoile matinière, </p>\n<p>De tes rayons viens éclairer nos yeux; </p>\n<p>Viens nous conduire au chemin glorieux </p>\n<p>Que ton amour nous traça sur la terre. </p>\n','Quelle-est-au-ciel-cette-brillante-etoile',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1234,'f56ffadc-0468-4a80-9053-c38f79f5bb8a',0,0,'Sur cette Terre, ô divin Frère',51,'51_CE_fr','1.\nSur cette Terre, O divin Frère!\nJésus, tu vins subir la mort,\nDe moi, coupable et misérable,\nTu voulus partager le sort.\n\n2.\nTa voix bénie donne la vie,\nC\'est pour sauver que tu souffris.\nTon sacrifice est ma justice:\nPour les pécheurs tu répondis.\n\n3.\nCe saint mystère que je révère,\nOh!  Je ne pourrais le sonder!\nPour tout comprendre, il faut attendre,\nMais, Rédempteur!  Je puis t\'aimer.','<p><strong>1.</strong> </p>\n<p>Sur cette Terre, O divin Frère! </p>\n<p>Jésus, tu vins subir la mort, </p>\n<p>De moi, coupable et misérable, </p>\n<p>Tu voulus partager le sort. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ta voix bénie donne la vie, </p>\n<p>C&#39;est pour sauver que tu souffris. </p>\n<p>Ton sacrifice est ma justice: </p>\n<p>Pour les pécheurs tu répondis. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ce saint mystère que je révère, </p>\n<p>Oh!  Je ne pourrais le sonder! </p>\n<p>Pour tout comprendre, il faut attendre, </p>\n<p>Mais, Rédempteur!  Je puis t&#39;aimer. </p>\n','Sur-cette-Terre-o-divin-Frere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1235,'10a1573e-34b2-41c5-8709-5e36cf70276a',0,0,'Mon coeur te cherche au point du jour',22,'22_CE_fr','1.\nMon coeur te cherche au point du jour,\nO, Jésus, mon Ami suprême!\nToi qui m\'as tant aimé, je t\'aime,\nEt je vis de ton amour.\n\nRefrain\nMon âme est attachée à Toi,\nEt Tu fais ta demeure en moi,\nJe sens ton coeur battre en mon coeur,\nEt ta présence est mon bonheur.\n\n2.\nPour moi, sur la terre étranger,\nTon amour vaut mieux que la vie.\nJésus, pour mon âme ravie,\nN\'es-tu pas le bon berger?\n\n3.\nDevant ton merveilleux amour,\nEn t\'adorant, mon âme heureuse\nTriomphe et célèbre, joyeuse,\nTes bontés de chaque jour!','<p><strong>1.</strong> </p>\n<p>Mon coeur te cherche au point du jour, </p>\n<p>O, Jésus, mon Ami suprême! </p>\n<p>Toi qui m&#39;as tant aimé, je t&#39;aime, </p>\n<p>Et je vis de ton amour. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon âme est attachée à Toi,</strong> </p>\n<p><strong>Et Tu fais ta demeure en moi,</strong> </p>\n<p><strong>Je sens ton coeur battre en mon coeur,</strong> </p>\n<p><strong>Et ta présence est mon bonheur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour moi, sur la terre étranger, </p>\n<p>Ton amour vaut mieux que la vie. </p>\n<p>Jésus, pour mon âme ravie, </p>\n<p>N&#39;es-tu pas le bon berger? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Devant ton merveilleux amour, </p>\n<p>En t&#39;adorant, mon âme heureuse </p>\n<p>Triomphe et célèbre, joyeuse, </p>\n<p>Tes bontés de chaque jour! </p>\n','Mon-coeur-te-cherche-au-point-du-jour',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1236,'5f3d6f2b-ea00-4928-b6f0-f0a80d272c60',0,0,'Obscur et pauvre au monde présenté',53,'53_CE_fr','1.\nObscur et pauvre au monde présenté.\nNous le voyons sans éclat, sans beauté,\nCe Roi des rois, ce Fils du Père,\nVit ici-bas dans la misère.\n\nRefrain\nIl s\'est chargé de toutes nos langueurs,\nEt sur la croix a porté nos douleur,  (bis)\n\n2.\nMon Dieu, mon Dieu! Pourquoi m\'as-tu laissé?\nTel est le cris de son coeur angoissé.\nPuis, Seigneur, il baisse la tête,\nEt ta justice est satisfaite.\n\n3.\nSeigneur Jésus, que nous avons percé,\nDans notre coeur par la foi sois placé;\nCar ta mort, qui nous justifie,\nPar la foi devient notre vie.','<p><strong>1.</strong> </p>\n<p>Obscur et pauvre au monde présenté. </p>\n<p>Nous le voyons sans éclat, sans beauté, </p>\n<p>Ce Roi des rois, ce Fils du Père, </p>\n<p>Vit ici-bas dans la misère. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il s&#39;est chargé de toutes nos langueurs,</strong> </p>\n<p><strong>Et sur la croix a porté nos douleur,  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon Dieu, mon Dieu! Pourquoi m&#39;as-tu laissé? </p>\n<p>Tel est le cris de son coeur angoissé. </p>\n<p>Puis, Seigneur, il baisse la tête, </p>\n<p>Et ta justice est satisfaite. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur Jésus, que nous avons percé, </p>\n<p>Dans notre coeur par la foi sois placé; </p>\n<p>Car ta mort, qui nous justifie, </p>\n<p>Par la foi devient notre vie. </p>\n','Obscur-et-pauvre-au-monde-presente',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1237,'7530bf0c-8c27-4b3a-9222-5dc46f603725',0,0,'Par tous les saints glorifié',35,'35_CE_fr','1.\nPar tous les saints glorifié,\nJésus inspire leurs louanges,\nPlus belle que le chant des anges,\nGloire à l\'Agneau,  (bis)\nGloire à l\'Agneau sacrifié!\n\n2.\nC\'est par Lui qu\'est justifié\nTout pécheur qui demande grâce.\nPrêtes et rois devant sa face,\nChantons l\'Agneau,  (bis)\nChantons l\'Agneau sacrifié!\n\n3.\nPar le Père magnifié,\nTout l\'univers lui rend hommage.\nL\'Agneau règnera d\'âge en âge.\nGloire à l\'Agneau,  (bis)\nGloire à l\'Agneau sacrifié.\n\n4.\nPar ton Esprit vivifié,\nJe veux jusqu\'à ma dernière heure,\nChanter l\'amour qui seul demeure:\nGloire à l\'Agneau,  (bis)\nGloire à l\'Agneau sacrifié.','<p><strong>1.</strong> </p>\n<p>Par tous les saints glorifié, </p>\n<p>Jésus inspire leurs louanges, </p>\n<p>Plus belle que le chant des anges, </p>\n<p>Gloire à l&#39;Agneau,  (bis) </p>\n<p>Gloire à l&#39;Agneau sacrifié! </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est par Lui qu&#39;est justifié </p>\n<p>Tout pécheur qui demande grâce. </p>\n<p>Prêtes et rois devant sa face, </p>\n<p>Chantons l&#39;Agneau,  (bis) </p>\n<p>Chantons l&#39;Agneau sacrifié! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Par le Père magnifié, </p>\n<p>Tout l&#39;univers lui rend hommage. </p>\n<p>L&#39;Agneau règnera d&#39;âge en âge. </p>\n<p>Gloire à l&#39;Agneau,  (bis) </p>\n<p>Gloire à l&#39;Agneau sacrifié. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par ton Esprit vivifié, </p>\n<p>Je veux jusqu&#39;à ma dernière heure, </p>\n<p>Chanter l&#39;amour qui seul demeure: </p>\n<p>Gloire à l&#39;Agneau,  (bis) </p>\n<p>Gloire à l&#39;Agneau sacrifié. </p>\n','Par-tous-les-saints-glorifie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1238,'1d8a36e0-b395-4243-840c-fc6875bcff7b',0,0,'Salut, Blanche Étoile',43,'43_CE_fr','1.\nSalut, blanche étoile,\nAu plus haut du ciel!\nRayonne sans voile,\nSplendeur de Noël!\nVenez, tendre enfance,\nQu\'un chant d\'espérance,  (bis 2 lignes)\nDe nos coeurs s\'élance\nJusqu\'à l\'Éternel!\n\n2.\nArdente jeunesse,\nLouez le Seigneur!\nTremblante vieillesse,\nN\'ayez point de peur!\nDieu même, ô mystère!\nSe fait notre Frère,  (bis 2 lignes)\nEt vient sur la terre\nSauver le pécheur.\n\n3.\nLe Fils adorable\nLui. Le Roi des rois,\nNaît dans une étable,\nMeurt sur une croix:\nSa grâce fidèle\nPardonne aux rebelle…(bis 2 lignes)\nJésus nous appelle:\nÉcoutons sa voix!\n\n4.\nSalut, blanche étoile,\nAu plus haut du ciel!\nRayonne sans voile,\nSplendeur de Noël!\nPour l\'âme qui t\'aime,\nTa clarté suprême  (bis 2 lignes)\nEst le doux emblème\nDu jour éternel!','<p><strong>1.</strong> </p>\n<p>Salut, blanche étoile, </p>\n<p>Au plus haut du ciel! </p>\n<p>Rayonne sans voile, </p>\n<p>Splendeur de Noël! </p>\n<p>Venez, tendre enfance, </p>\n<p>Qu&#39;un chant d&#39;espérance,  (bis 2 lignes) </p>\n<p>De nos coeurs s&#39;élance </p>\n<p>Jusqu&#39;à l&#39;Éternel! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ardente jeunesse, </p>\n<p>Louez le Seigneur! </p>\n<p>Tremblante vieillesse, </p>\n<p>N&#39;ayez point de peur! </p>\n<p>Dieu même, ô mystère! </p>\n<p>Se fait notre Frère,  (bis 2 lignes) </p>\n<p>Et vient sur la terre </p>\n<p>Sauver le pécheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Fils adorable </p>\n<p>Lui. Le Roi des rois, </p>\n<p>Naît dans une étable, </p>\n<p>Meurt sur une croix: </p>\n<p>Sa grâce fidèle </p>\n<p>Pardonne aux rebelle…(bis 2 lignes) </p>\n<p>Jésus nous appelle: </p>\n<p>Écoutons sa voix! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Salut, blanche étoile, </p>\n<p>Au plus haut du ciel! </p>\n<p>Rayonne sans voile, </p>\n<p>Splendeur de Noël! </p>\n<p>Pour l&#39;âme qui t&#39;aime, </p>\n<p>Ta clarté suprême  (bis 2 lignes) </p>\n<p>Est le doux emblème </p>\n<p>Du jour éternel! </p>\n','Salut-Blanche-Etoile',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1239,'a460a684-d17e-4cbd-a857-5f32373f4188',0,0,'Agneau de Dieu',6,'6_CE_fr','1.\nAgneau de Dieu, par tes langueurs\nTu pris sur Toi notre misère,\nEt tu nous fis, pour Dieu, ton Père,\nEt rois et sacrificateurs.\nEnsemble aussi nous te rendrons,\nHonneur, gloire et magnificence,\nForce, pouvoir, obéissance\nEt dans nos coeurs nous t\'adorons,\nAmen!  Amen!  Seigneur, Amen!','<p><strong>1.</strong> </p>\n<p>Agneau de Dieu, par tes langueurs </p>\n<p>Tu pris sur Toi notre misère, </p>\n<p>Et tu nous fis, pour Dieu, ton Père, </p>\n<p>Et rois et sacrificateurs. </p>\n<p>Ensemble aussi nous te rendrons, </p>\n<p>Honneur, gloire et magnificence, </p>\n<p>Force, pouvoir, obéissance </p>\n<p>Et dans nos coeurs nous t&#39;adorons, </p>\n<p>Amen!  Amen!  Seigneur, Amen! </p>\n','Agneau-de-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1240,'47bf7c7c-18fa-4276-a4e7-72cbfd93de7b',0,0,'Gloire, gloire à l\'Éternel',30,'30_CE_fr','1.\nGloire, gloire à l\'Éternel!\nQu\'un cantique solennel  (bis)\nDe nos coeur monte à son trône.\nQuand Il crée, oh! Qu\'il est grand! (bis)\nQu\'il est juste en punissant,\nQu\'il est bon quand il pardonne.\n\n2.\nIl commande… et le néant\nTressaille au premier accent (bis)\nDe sa parole vivante;\nEt des astres radieux  (bis)\nSa main jette dans les cieux\nLa poussière étincelante.\n\n3.\nIl accuse, et le pécheur,\nDevant cet accusateur  (bis)\nSent sa profonde misère,\nEt s\'écrie, en son effroi:  (bis)\nMontagnes, tombez sur moi,\nCachez-moi de sa colère!\n\n4.\nMais l\'âme à qui le Seigneur\nS\'est donné pour Rédempteur  (bis)\nGoûte une paix ineffable:\nObjet d\'un si grand amour  (bis)\nElle se donne en retour\nA ce Sauveur adorable.\n\n5.\nO Dieu!  Que tes rachetés\nToujours chantent les bontés  (bis)\nDe celui qui leur pardonne!\nGloire, gloire à l\'Éternel!  (bis)\nCe cantique solennel\nMontera jusqu\'à son trône!','<p><strong>1.</strong> </p>\n<p>Gloire, gloire à l&#39;Éternel! </p>\n<p>Qu&#39;un cantique solennel  (bis) </p>\n<p>De nos coeur monte à son trône. </p>\n<p>Quand Il crée, oh! Qu&#39;il est grand! (bis) </p>\n<p>Qu&#39;il est juste en punissant, </p>\n<p>Qu&#39;il est bon quand il pardonne. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il commande… et le néant </p>\n<p>Tressaille au premier accent (bis) </p>\n<p>De sa parole vivante; </p>\n<p>Et des astres radieux  (bis) </p>\n<p>Sa main jette dans les cieux </p>\n<p>La poussière étincelante. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il accuse, et le pécheur, </p>\n<p>Devant cet accusateur  (bis) </p>\n<p>Sent sa profonde misère, </p>\n<p>Et s&#39;écrie, en son effroi:  (bis) </p>\n<p>Montagnes, tombez sur moi, </p>\n<p>Cachez-moi de sa colère! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais l&#39;âme à qui le Seigneur </p>\n<p>S&#39;est donné pour Rédempteur  (bis) </p>\n<p>Goûte une paix ineffable: </p>\n<p>Objet d&#39;un si grand amour  (bis) </p>\n<p>Elle se donne en retour </p>\n<p>A ce Sauveur adorable. </p>\n<p><br/> <strong>5.</strong></p>\n<p>O Dieu!  Que tes rachetés </p>\n<p>Toujours chantent les bontés  (bis) </p>\n<p>De celui qui leur pardonne! </p>\n<p>Gloire, gloire à l&#39;Éternel!  (bis) </p>\n<p>Ce cantique solennel </p>\n<p>Montera jusqu&#39;à son trône! </p>\n','Gloire-gloire-a-l\'Eternel',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1241,'5e23edbb-63b9-4265-bd8c-f3112b2fd787',0,0,'Que tout genou fléchisse',5,'5_CE_fr','1.\nQue tout genoux fléchisse\nDevant ta majesté,\nEt qu\'aujourd\'hui je puisse\nLâcher ma volonté. (bis 2 lignes)\n\n2.\nOui, devant toi je plie,\nJe me courbe, ô mon Roi,\nJe pleur et m\'humilie\nEt c\'est un don de toi. (bis 2 lignes)\n\n3.\nQue tout ceux qui s\'élève\nEn moi soit abaissé!\nQue mon coeur, de ton glaive,\nS\'il le faut, soit percé! (bis 2 lignes)\n\n4.\nMourir, mais c\'est renaître\nEt vivre désormais.\nOh! Courbe tout mon être\nA tes pieds pour jamais! (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Que tout genoux fléchisse </p>\n<p>Devant ta majesté, </p>\n<p>Et qu&#39;aujourd&#39;hui je puisse </p>\n<p>Lâcher ma volonté. (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, devant toi je plie, </p>\n<p>Je me courbe, ô mon Roi, </p>\n<p>Je pleur et m&#39;humilie </p>\n<p>Et c&#39;est un don de toi. (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que tout ceux qui s&#39;élève </p>\n<p>En moi soit abaissé! </p>\n<p>Que mon coeur, de ton glaive, </p>\n<p>S&#39;il le faut, soit percé! (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mourir, mais c&#39;est renaître </p>\n<p>Et vivre désormais. </p>\n<p>Oh! Courbe tout mon être </p>\n<p>A tes pieds pour jamais! (bis 2 lignes) </p>\n','Que-tout-genou-flechisse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1242,'6210d7d9-7b13-4b2a-93a2-6727ee4cd948',2,0,'Ton nom soit à jamais béni',12,'12_CE_fr','1.\nTon nom soit à jamais béni,\nDieu d\'amour.  (bis)\nMerci pour ton don infini,\nDieu d\'amour.  (bis)\nL\'amour rayonne sur ta face,\nLibrement tu veux faire grâce,\nPour nous ton coeur a de la place,\nDieu d\'amour.  (bis)\n\n\n2.\nTon rôle d\'angoisse est rempli,\nO Sauveur.  (bis)\nPar ta mort tout est accompli,\nO Sauveur.  (bis)\nPlus dévoué qu\'un tendre frère,\nPrenant sur toi notre misère,\nTu nous ramènes à ton Père,\nO Sauveur.  (bis)\n\n\n3.\nQui voudrait s\'éloigner de toi\nA jamais?  (bis)\nEt demeurer sans Dieu, sans foi,\nA jamais?  (bis)\nAcceptons tous ce don suprême,\nVenons à Celui qui nous aime;\nSon salut restera al même,\nA jamais.  (bis)','<p><strong>1.</strong> </p>\n<p>Ton nom soit à jamais béni, </p>\n<p>Dieu d&#39;amour.  (bis) </p>\n<p>Merci pour ton don infini, </p>\n<p>Dieu d&#39;amour.  (bis) </p>\n<p>L&#39;amour rayonne sur ta face, </p>\n<p>Librement tu veux faire grâce, </p>\n<p>Pour nous ton coeur a de la place, </p>\n<p>Dieu d&#39;amour.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton rôle d&#39;angoisse est rempli, </p>\n<p>O Sauveur.  (bis) </p>\n<p>Par ta mort tout est accompli, </p>\n<p>O Sauveur.  (bis) </p>\n<p>Plus dévoué qu&#39;un tendre frère, </p>\n<p>Prenant sur toi notre misère, </p>\n<p>Tu nous ramènes à ton Père, </p>\n<p>O Sauveur.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui voudrait s&#39;éloigner de toi </p>\n<p>A jamais?  (bis) </p>\n<p>Et demeurer sans Dieu, sans foi, </p>\n<p>A jamais?  (bis) </p>\n<p>Acceptons tous ce don suprême, </p>\n<p>Venons à Celui qui nous aime; </p>\n<p>Son salut restera al même, </p>\n<p>A jamais.  (bis) </p>\n','Ton-nom-soit-a-jamais-beni',NULL,'fr',7,'2023-07-22 22:41:15','2023-10-03 18:34:20'),(1243,'57c151ce-2b4c-4115-9ed3-c7adea3047e3',0,0,'L\'Éternel seul est Seigneur',21,'21_CE_fr','1.\nL\'Éternel seul est Seigneur;\nSeul il est dominateur  (bis)\nSur les peuples de la terre;\nIl est maître souverain  (bis)\nDes ouvrages que sa main\nPour sa gloire a voulu faire.\n\n2.\nMais quel bienheureux mortel\nAu saint mont de l\'Éternel  (bis)\nAura le doit de paraître?\nEt quel homme, ô puissant Roi!  (bis)\nPour demeurer avec Toi\nAssez juste pourrait être?\n\n3.\nC\'est l\'homme qui, dans son coeur,\nPar ton Esprit, ô Seigneur!  (bis)\nHait du péché les souillures;\nQui, fuyant la fausseté,  (bis)\nTe sert en sincérité,\nLevant vers toi des mains pures.\n\n4.\nOui, cet homme recevra,\nDe son Dieu qu\'il cherchera,  (bis)\nLe salut et la justice,\nOui, tes enfants, à jamais,  (bis)\nSeigneur, trouveront la paix\nEt la lumière propice.','<p><strong>1.</strong> </p>\n<p>L&#39;Éternel seul est Seigneur; </p>\n<p>Seul il est dominateur  (bis) </p>\n<p>Sur les peuples de la terre; </p>\n<p>Il est maître souverain  (bis) </p>\n<p>Des ouvrages que sa main </p>\n<p>Pour sa gloire a voulu faire. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais quel bienheureux mortel </p>\n<p>Au saint mont de l&#39;Éternel  (bis) </p>\n<p>Aura le doit de paraître? </p>\n<p>Et quel homme, ô puissant Roi!  (bis) </p>\n<p>Pour demeurer avec Toi </p>\n<p>Assez juste pourrait être? </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est l&#39;homme qui, dans son coeur, </p>\n<p>Par ton Esprit, ô Seigneur!  (bis) </p>\n<p>Hait du péché les souillures; </p>\n<p>Qui, fuyant la fausseté,  (bis) </p>\n<p>Te sert en sincérité, </p>\n<p>Levant vers toi des mains pures. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, cet homme recevra, </p>\n<p>De son Dieu qu&#39;il cherchera,  (bis) </p>\n<p>Le salut et la justice, </p>\n<p>Oui, tes enfants, à jamais,  (bis) </p>\n<p>Seigneur, trouveront la paix </p>\n<p>Et la lumière propice. </p>\n','L\'Eternel-seul-est-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1244,'f267e282-4122-4b48-9ef4-3b0305b16501',0,0,'Oui, ton amour est un amour sublime',32,'32_CE_fr','1.\nOui, ton amour est un amour sublime;\nIl est plus haut que la plus haute cime\nEt que l\'azur insondable des cieux.\nComment pourrais-je, ô Dieu!  Vers cet abîme\nLever les yeux?\n\n2.\nOh! Quel amour! Il m\'entoure, il m\'inonde;\nC\'est une mer calme, pure, profonde,\nVaste océan, que murmure ton onde?\n“Pour toi, pour toi!”\n\n3.\nOui, c\'est pour moi, je le crois j\'adore,\nO Christ Sauveur!  Qu\'il retentisse encore\nCe mot divin, ce mot de ton amour!\nRedis-le moi, jusqu\'à la douce aurore\nDe ton amour.\n\n4.\nEt quand luira cette aurore bénie,\nQuand, à Jésus plus tendrement unie,\nMon âme, enfin, mon âme le verra\nD\'elle vers Lui ma louange infinie\nS\'élèvera!','<p><strong>1.</strong> </p>\n<p>Oui, ton amour est un amour sublime; </p>\n<p>Il est plus haut que la plus haute cime </p>\n<p>Et que l&#39;azur insondable des cieux. </p>\n<p>Comment pourrais-je, ô Dieu!  Vers cet abîme </p>\n<p>Lever les yeux? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Quel amour! Il m&#39;entoure, il m&#39;inonde; </p>\n<p>C&#39;est une mer calme, pure, profonde, </p>\n<p>Vaste océan, que murmure ton onde? </p>\n<p>“Pour toi, pour toi!” </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, c&#39;est pour moi, je le crois j&#39;adore, </p>\n<p>O Christ Sauveur!  Qu&#39;il retentisse encore </p>\n<p>Ce mot divin, ce mot de ton amour! </p>\n<p>Redis-le moi, jusqu&#39;à la douce aurore </p>\n<p>De ton amour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et quand luira cette aurore bénie, </p>\n<p>Quand, à Jésus plus tendrement unie, </p>\n<p>Mon âme, enfin, mon âme le verra </p>\n<p>D&#39;elle vers Lui ma louange infinie </p>\n<p>S&#39;élèvera! </p>\n','Oui-ton-amour-est-un-amour-sublime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1245,'72226458-26a2-427d-bd04-5124baf30687',39,1,'Crions à Dieu',0,'0_CE_fr','1. Crions à Dieu pour que notre patrieS\'éveille enfin de son sommeil de mortQue constamment tout enfant de Dieu prieAh! Par la foi changeons son triste sort2. Oui, qu\'à ces soins chacun de nous s\'appliqueNous l\'obtiendrons indicible bonheurTemple, maison, rue et place publiqueTout redira le beau nom du Sauveur3.Allons! Allons! enfant du Dieu de gloireLevons nos fronts trop longtemps abattusComme au beau jour de notre sainte histoireChrétiens vivons ou mourons pour Jésus4.L\'aurore vient de ce jour magnifiqueOù secourant les fers de l\'oppresseurTout l\'univers dans un même cantiqueProclamera le beau nom du Sauveur','<p><strong>1.</strong> </p>\n<p>Crions à Dieu pour que notre patrie </p>\n<p>S&#39;éveille enfin de son sommeil de mort </p>\n<p>Que constamment tout enfant de Dieu prie </p>\n<p>Ah! Par la foi changeons son triste sort </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, qu&#39;à ces soins chacun de nous s&#39;applique </p>\n<p>Nous l&#39;obtiendrons indicible bonheur </p>\n<p>Temple, maison, rue et place publique </p>\n<p>Tout redira le beau nom du Sauveur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Allons! Allons! enfant du Dieu de gloire </p>\n<p>Levons nos fronts trop longtemps abattus </p>\n<p>Comme au beau jour de notre sainte histoire </p>\n<p>Chrétiens vivons ou mourons pour Jésus </p>\n<p><br/> <strong>4.</strong></p>\n<p>L&#39;aurore vient de ce jour magnifique </p>\n<p>Où secourant les fers de l&#39;oppresseur </p>\n<p>Tout l&#39;univers dans un même cantique </p>\n<p>Proclamera le beau nom du Sauveur </p>\n','Crions-a-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-10-03 20:27:42'),(1246,'9ba4271b-c57c-4d92-b4d1-e6fe23e69aae',0,0,'Dans les cieux et sur la Terre',8,'8_CE_fr','1.Dans les cieux et sur la terreIl n\'est aucun nom plus doux,Aucun que mon coeur préfèreAu nom de Christ mort pour nous.RefrainQuel beau nom (bis)Porte l\'Oint de l\'Éternel!Quel beau nom  (bis)Que celui d\'Émmanuel!2.Quelque grand que soit un homme,Qu\'il soit prince ou qu\'il soi roi,De quelque nom qu\'on le nomme,Jésus est plus grand pour moi.3.Dans les maux, Jésus soulage,Il guérit l\'esprit froissé,Il ranime le courage,Du coeur le plus oppressé.','<p><strong>1.</strong> </p>\n<p>Dans les cieux et sur la terre </p>\n<p>Il n&#39;est aucun nom plus doux, </p>\n<p>Aucun que mon coeur préfère </p>\n<p>Au nom de Christ mort pour nous. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Quel beau nom (bis)</strong> </p>\n<p><strong>Porte l&#39;Oint de l&#39;Éternel!</strong> </p>\n<p><strong>Quel beau nom (bis)</strong> </p>\n<p><strong>Que celui d&#39;Émmanuel!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quelque grand que soit un homme, </p>\n<p>Qu&#39;il soit prince ou qu&#39;il soi roi, </p>\n<p>De quelque nom qu&#39;on le nomme, </p>\n<p>Jésus est plus grand pour moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans les maux, Jésus soulage, </p>\n<p>Il guérit l&#39;esprit froissé, </p>\n<p>Il ranime le courage, </p>\n<p>Du coeur le plus oppressé. </p>\n','Dans-les-cieux-et-sur-la-Terre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1247,'2abe3484-5275-4d81-8f8e-659600d74c5e',0,0,'Mon coeur joyeux, plein d\'espérance',10,'10_CE_fr','1.\nMon coeur joyeux, plein d\'espérance,\nS\'élève à toi, mon Rédempteur!\nDaigne écouter avec clémence\nUn pauvre humain faible et pécheur.\nEn toi seul est ma confiance,\nEn toi seul est tout mon bonheur.\n\n2.\nLe jour, je marche à ta lumière;\nLa nuit, je repose en ton sein;\nDès le matin, à ma prière,\nTu viens éclairer mon chemin;\nEt chaque soir, ô mon bon Père,\nTu prépare mon lendemain.\n\n3.\nJe vois ainsi venir le terme\nDe mon voyage en ces bas lieux,\nEt j\'ai l\'attente vive et ferme\nDu saint héritage des cieux:\nSur moi, si la tombe se ferme,\nJ\'en sortirai victorieux.','<p><strong>1.</strong> </p>\n<p>Mon coeur joyeux, plein d&#39;espérance, </p>\n<p>S&#39;élève à toi, mon Rédempteur! </p>\n<p>Daigne écouter avec clémence </p>\n<p>Un pauvre humain faible et pécheur. </p>\n<p>En toi seul est ma confiance, </p>\n<p>En toi seul est tout mon bonheur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le jour, je marche à ta lumière; </p>\n<p>La nuit, je repose en ton sein; </p>\n<p>Dès le matin, à ma prière, </p>\n<p>Tu viens éclairer mon chemin; </p>\n<p>Et chaque soir, ô mon bon Père, </p>\n<p>Tu prépare mon lendemain. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je vois ainsi venir le terme </p>\n<p>De mon voyage en ces bas lieux, </p>\n<p>Et j&#39;ai l&#39;attente vive et ferme </p>\n<p>Du saint héritage des cieux: </p>\n<p>Sur moi, si la tombe se ferme, </p>\n<p>J&#39;en sortirai victorieux. </p>\n','Mon-coeur-joyeux-plein-d\'esperance',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1248,'9185b432-e97b-4776-8e3c-fcf74d47c467',0,0,'Dans le pays de la gloire éternelle',14,'14_CE_fr','1.\nDans le pays de la gloire éternelle,\nDans ce beau ciel où l\'on ne souffre plus!\nTe contempler, abrité sous ton aile,\nC\'est le bonheur que je rêve, ô Jésus!\n\nRefrain\nGloire à jamais, gloire é Jésus!  (bis)\nAuprès de lui, je ne pécherai plus!\nAuprès de lui, je ne pleurerai plus!\nAuprès de lui, je ne souffrirai plus!\nOh! Gloire à jamais, gloire à toi, Jésus!\n\n2.\nQuand tu m\'auras, dans ta grâce infinie,\nFait une place au milieu des élus,\nTout mon bonheur, et ma gloire, et ma vie\nSera toujours de te louer, Jésus!\n\n\n3.\nMes bien-aimés partageront ma gloire,\nDans ton ciel où l\'on ne se quitte plus.\nMais pour mon coeur la suprême victoire\nSera d\'avoir ton sourire, ô Jésus!','<p><strong>1.</strong> </p>\n<p>Dans le pays de la gloire éternelle, </p>\n<p>Dans ce beau ciel où l&#39;on ne souffre plus! </p>\n<p>Te contempler, abrité sous ton aile, </p>\n<p>C&#39;est le bonheur que je rêve, ô Jésus! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à jamais, gloire é Jésus!  (bis)</strong> </p>\n<p><strong>Auprès de lui, je ne pécherai plus!</strong> </p>\n<p><strong>Auprès de lui, je ne pleurerai plus!</strong> </p>\n<p><strong>Auprès de lui, je ne souffrirai plus!</strong> </p>\n<p><strong>Oh! Gloire à jamais, gloire à toi, Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand tu m&#39;auras, dans ta grâce infinie, </p>\n<p>Fait une place au milieu des élus, </p>\n<p>Tout mon bonheur, et ma gloire, et ma vie </p>\n<p>Sera toujours de te louer, Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mes bien-aimés partageront ma gloire, </p>\n<p>Dans ton ciel où l&#39;on ne se quitte plus. </p>\n<p>Mais pour mon coeur la suprême victoire </p>\n<p>Sera d&#39;avoir ton sourire, ô Jésus! </p>\n','Dans-le-pays-de-la-gloire-eternelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1249,'ce897d54-bb3c-4821-9c79-fcb310e385df',0,0,'Oh! quel bonheur de le connaître',17,'17_CE_fr','1.Oh! quel bonheur de le connaîtreL\'Ami qui ne saurait changerDe l\'avoir ici-bas pour MaîtrePour défenseur et pour bergerRefrainChantons, chantons d\'un cœur joyeuxLe grand amour du RédempteurQui vint à nous du haut des cieuxEt nous sauva du destructeur2.Dans la misère et l\'ignoranceNous nous débattions sans espoirLa mort au cœur, l\'âme en souffranceQuand à nos yeux Il se fit voir3.Il nous apporta la lumièreLa victoire et la libertéL\'ennemi mordit le poussièrePour toujours Satan fut dompté4.Vers l\'avenir marchons sans crainteEt sans souci de lendemainPas à pas, nos pieds dans l\'empreinteDes ses pieds sur notre chemin','<p><strong>1.</strong> </p>\n<p>Oh! quel bonheur de le connaître </p>\n<p>L&#39;Ami qui ne saurait changer </p>\n<p>De l&#39;avoir ici-bas pour Maître </p>\n<p>Pour défenseur et pour berger </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chantons, chantons d&#39;un cœur joyeux</strong> </p>\n<p><strong>Le grand amour du Rédempteur</strong> </p>\n<p><strong>Qui vint à nous du haut des cieux</strong> </p>\n<p><strong>Et nous sauva du destructeur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans la misère et l&#39;ignorance </p>\n<p>Nous nous débattions sans espoir </p>\n<p>La mort au cœur, l&#39;âme en souffrance </p>\n<p>Quand à nos yeux Il se fit voir </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il nous apporta la lumière </p>\n<p>La victoire et la liberté </p>\n<p>L&#39;ennemi mordit le poussière </p>\n<p>Pour toujours Satan fut dompté </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vers l&#39;avenir marchons sans crainte </p>\n<p>Et sans souci de lendemain </p>\n<p>Pas à pas, nos pieds dans l&#39;empreinte </p>\n<p>De ses pieds sur notre chemin </p>\n','Oh!-quel-bonheur-de-le-connaitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1250,'3e304415-7544-459a-af32-3d0606525cdd',0,0,'Le nom de Jésus est si doux',19,'19_CE_fr','1.\nLe nom de Jésus est si doux!\nDe Dieu désarmant le courroux,\nIl nous appelle au rendez-vous,\nPrécieux nom de Jésus!\n\nRefrain\nJésus! Bénit soit ton nom!\nJésus! Oh! Merveilleux don!\nJésus! Suprême rançon,\nSois adoré pour toujours.\n\n2.\nJ\'aime ce nom dans le chagrin,\nIl me soutient sur le chemin,\nSa musique est un nom divin,\nPrécieux nom de Jésus!\n\n3.\nJ\'aime le nom de mon Sauveur,\nCar Lui seul connaît tout mon coeur,\nLui seul me rend plus que vainqueur:\nPrécieux nom de Jésus!\n\n4.\nEt si parfois j\'ai succombé,\nSi dans le mal je suis tombé,\nSon nom puissant m\'a relevé:\nPrécieux nom de Jésus!\n\n5.\nEt lorsqu\'avec Lui je serai,\nEt lorsqu\'enfin je Le verrai,\nAlors sans fin je Le dirai:\nPrécieux nom de Jésus!','<p><strong>1.</strong> </p>\n<p>Le nom de Jésus est si doux! </p>\n<p>De Dieu désarmant le courroux, </p>\n<p>Il nous appelle au rendez-vous, </p>\n<p>Précieux nom de Jésus! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus! Bénit soit ton nom!</strong> </p>\n<p><strong>Jésus! Oh! Merveilleux don!</strong> </p>\n<p><strong>Jésus! Suprême rançon,</strong> </p>\n<p><strong>Sois adoré pour toujours.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;aime ce nom dans le chagrin, </p>\n<p>Il me soutient sur le chemin, </p>\n<p>Sa musique est un nom divin, </p>\n<p>Précieux nom de Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;aime le nom de mon Sauveur, </p>\n<p>Car Lui seul connaît tout mon coeur, </p>\n<p>Lui seul me rend plus que vainqueur: </p>\n<p>Précieux nom de Jésus! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et si parfois j&#39;ai succombé, </p>\n<p>Si dans le mal je suis tombé, </p>\n<p>Son nom puissant m&#39;a relevé: </p>\n<p>Précieux nom de Jésus! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Et lorsqu&#39;avec Lui je serai, </p>\n<p>Et lorsqu&#39;enfin je Le verrai, </p>\n<p>Alors sans fin je Le dirai: </p>\n<p>Précieux nom de Jésus! </p>\n','Le-nom-de-Jesus-est-si-doux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1251,'ab2ab849-9c96-4304-b483-d7044d359979',0,0,'Je chanterai, Seigneur',26,'26_CE_fr','1.\nJe chanterai, Seigneur,\nTes oeuvres magnifiques,\nTon auguste pouvoir,\nTa suprême grandeur,\nAux concerts de tes saint\nJ\'unirai les cantiques\nQue pour toi me dicte mon coeur.  (bis)\n\n2.\nOh! Que de l\'Éternel\nLa parole est féconde!\nL\'univers fut jadis\nL\'ouvrage de sa voix;\nIl dit…les éléments,\nLe ciel, la terre et l\'onde\nDu néant sortent à la fois.  (bis)\n\n3.\nLe monde passera;\nCe superbe édifice\nUn jour s\'ébranlera\nJusqu\'en ses fondements.\nTa sagesse, grand Dieu,\nTa bonté, ta justice\nSubsisteront dans tous les temps.  (bis)','<p><strong>1.</strong> </p>\n<p>Je chanterai, Seigneur, </p>\n<p>Tes oeuvres magnifiques, </p>\n<p>Ton auguste pouvoir, </p>\n<p>Ta suprême grandeur, </p>\n<p>Aux concerts de tes saint </p>\n<p>J&#39;unirai les cantiques </p>\n<p>Que pour toi me dicte mon coeur.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Que de l&#39;Éternel </p>\n<p>La parole est féconde! </p>\n<p>L&#39;univers fut jadis </p>\n<p>L&#39;ouvrage de sa voix; </p>\n<p>Il dit…les éléments, </p>\n<p>Le ciel, la terre et l&#39;onde </p>\n<p>Du néant sortent à la fois.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le monde passera; </p>\n<p>Ce superbe édifice </p>\n<p>Un jour s&#39;ébranlera </p>\n<p>Jusqu&#39;en ses fondements. </p>\n<p>Ta sagesse, grand Dieu, </p>\n<p>Ta bonté, ta justice </p>\n<p>Subsisteront dans tous les temps.  (bis) </p>\n','Je-chanterai-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1252,'1e1f407b-afd2-46d3-88ad-87ea34489885',0,0,'Jésus, je voudrais mieux connaître',31,'31_CE_fr','1.\nJésus, je voudrais mieux connaître,\nMieux saisir ta grâce efficace,\nL\'amour qui mes péchés efface;\nQui sur la croix mourut pour moi.\n\nMieux connaître Jésus,  (bis)\nL\'amour qui mes péchés efface\nQui sur la croix mourut pour moi.\n\n2.\nJésus, je voudrais mieux connaître,\nMieux suivre ta volonté sainte,\nL\'amour qui seul bannit la crainte\nQuand en mon coeur il est vainqueur.\n\nMieux connaître Jésus,  (bis)\nL\'amour qui seul bannit la crainte\nQuand en mon coeur il est vainqueur.\n\n3.\nJésus, je voudrais mieux connaître,\nMieux sentir de ton Saint-Esprit,\nLa puissance qui m\'affranchit,\nQui me console, et qui m\'instruit.\n\nMieux connaître Jésus,  (bis)\nLa puissance qui m\'affranchit,\nQui me console, et qui m\'instruit.\n\n4.\nJésus, je voudrais mieux connaître,\nMieux contempler mon héritage:\nLa joie qui sera le partage\nDes rachetés, tes bien-aimés.\n\nMieux connaître Jésus,  (bis)\nLa joie qui sera le partage\nDes rachetés, tes bien-aimés.','<p><strong>1.</strong> </p>\n<p>Jésus, je voudrais mieux connaître, </p>\n<p>Mieux saisir ta grâce efficace, </p>\n<p>L&#39;amour qui mes péchés efface; </p>\n<p>Qui sur la croix mourut pour moi. </p>\n<p>Mieux connaître Jésus,  (bis) </p>\n<p>L&#39;amour qui mes péchés efface </p>\n<p>Qui sur la croix mourut pour moi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, je voudrais mieux connaître, </p>\n<p>Mieux suivre ta volonté sainte, </p>\n<p>L&#39;amour qui seul bannit la crainte </p>\n<p>Quand en mon coeur il est vainqueur. </p>\n<p>Mieux connaître Jésus,  (bis) </p>\n<p>L&#39;amour qui seul bannit la crainte </p>\n<p>Quand en mon coeur il est vainqueur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus, je voudrais mieux connaître, </p>\n<p>Mieux sentir de ton Saint-Esprit, </p>\n<p>La puissance qui m&#39;affranchit, </p>\n<p>Qui me console, et qui m&#39;instruit. </p>\n<p>Mieux connaître Jésus,  (bis) </p>\n<p>La puissance qui m&#39;affranchit, </p>\n<p>Qui me console, et qui m&#39;instruit. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus, je voudrais mieux connaître, </p>\n<p>Mieux contempler mon héritage: </p>\n<p>La joie qui sera le partage </p>\n<p>Des rachetés, tes bien-aimés. </p>\n<p>Mieux connaître Jésus,  (bis) </p>\n<p>La joie qui sera le partage </p>\n<p>Des rachetés, tes bien-aimés. </p>\n','Jesus-je-voudrais-mieux-connaitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1253,'1c8f2287-5e0a-4a8f-85e8-6ddcfed37cd5',0,0,'Ô Christ! J\'ai vu ton agonie',49,'49_CE_fr','1.\nO Christ!  J\'ai vu ton agonie\nEt mon âme a frémi d\'horreur!\nOui, tu viens de perdre la vie,\nEt c\'est pour moi, pauvre pécheur.\n\n2.\nA ta mort, la nature entière\nSe répand en cris de douleur;\nLe soleil cache sa lumière,\nLes élus pleurent leur Sauveur.\n\n3.\nQue ta mort, ô sainte Victime,\nSoit toujours présente à nos yeux!\nTon sang peut seul laver le crime!\nSeul il peut nous ouvrir les cieux.\n\n4.\nO Christ!  Ta charité profonde\nTouche et pénètre notre coeur;\nTu meurs pour les péchés du monde;\nToi seul es notre Dieu Sauveur!','<p><strong>1.</strong> </p>\n<p>O Christ!  J&#39;ai vu ton agonie </p>\n<p>Et mon âme a frémi d&#39;horreur! </p>\n<p>Oui, tu viens de perdre la vie, </p>\n<p>Et c&#39;est pour moi, pauvre pécheur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>A ta mort, la nature entière </p>\n<p>Se répand en cris de douleur; </p>\n<p>Le soleil cache sa lumière, </p>\n<p>Les élus pleurent leur Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que ta mort, ô sainte Victime, </p>\n<p>Soit toujours présente à nos yeux! </p>\n<p>Ton sang peut seul laver le crime! </p>\n<p>Seul il peut nous ouvrir les cieux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Christ!  Ta charité profonde </p>\n<p>Touche et pénètre notre coeur; </p>\n<p>Tu meurs pour les péchés du monde; </p>\n<p>Toi seul es notre Dieu Sauveur! </p>\n','O-Christ!-J\'ai-vu-ton-agonie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1254,'a2742f68-dc93-4ae6-9d84-ea4be8b27206',0,0,'Regarde, âme angoissée',62,'62_CE_fr','1.\nRegarde, âme angoissée,\nAu mourant du Calvaire;\nRegarde à Christ sur la croix élevé!\nC\'est là qu\'est ton Sauveur,\nContemple-le, mon frère;\nUn seul regard, et sois sauvé!\nRegarde et crois!\nLa vie et le pardon descendent du Calvaire.\nOh!  Regarde, regarde à la croix!\n\n2.\nPourquoi fut-Il frappé\nPar les foudres divines?\nPourquoi fut-il sur le bois attaché?\nPourquoi son front sacré\nFut-il meurtri d\'épines,\nSinon pour toi, pour ton péché?\nRegarde et crois!\nPour lui la mort, pour toi les promesses divines;\nOh!  Regarde, regarde à la croix!\n\n3.\nTu ne peux effacer\nPar ton sang, par tes larmes,\nTon long oubli de la divine loi;\nPour vivre et triompher,\nIl n\'est pas d\'autres armes\nQue l\'humble regard de la foi!\nRegarde et crois!\nJésus, divin soleil, dissipera tes larmes;\nOh!  Regarde, regarde à la croix!','<p><strong>1.</strong> </p>\n<p>Regarde, âme angoissée, </p>\n<p>Au mourant du Calvaire; </p>\n<p>Regarde à Christ sur la croix élevé! </p>\n<p>C&#39;est là qu&#39;est ton Sauveur, </p>\n<p>Contemple-le, mon frère; </p>\n<p>Un seul regard, et sois sauvé! </p>\n<p>Regarde et crois! </p>\n<p>La vie et le pardon descendent du Calvaire. </p>\n<p>Oh!  Regarde, regarde à la croix! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pourquoi fut-Il frappé </p>\n<p>Par les foudres divines? </p>\n<p>Pourquoi fut-il sur le bois attaché? </p>\n<p>Pourquoi son front sacré </p>\n<p>Fut-il meurtri d&#39;épines, </p>\n<p>Sinon pour toi, pour ton péché? </p>\n<p>Regarde et crois! </p>\n<p>Pour lui la mort, pour toi les promesses divines; </p>\n<p>Oh!  Regarde, regarde à la croix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu ne peux effacer </p>\n<p>Par ton sang, par tes larmes, </p>\n<p>Ton long oubli de la divine loi; </p>\n<p>Pour vivre et triompher, </p>\n<p>Il n&#39;est pas d&#39;autres armes </p>\n<p>Que l&#39;humble regard de la foi! </p>\n<p>Regarde et crois! </p>\n<p>Jésus, divin soleil, dissipera tes larmes; </p>\n<p>Oh!  Regarde, regarde à la croix! </p>\n','Regarde-ame-angoissee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1255,'0ff5ff8c-29ea-4e63-930c-8b2125113447',39,8,'Grand Dieu, nous te bénissons',2,'2_CE_fr','1.\nGrand Dieu, nous te bénissons,\nNous célébrons tes louanges!\nÉternel, nous t\'exaltons,\nDe concert avec les anges;\nEt prosternés devant toi,\nNous t\'adorons, o grand Roi (bis)\n\n2.\nLes saints et les bienheureux,\nLes trônes et les puissances,\nToutes les vertus des cieux,\nDisent tes magnificences,\nProclamant dans leurs concerts\nLe grand Dieu de l\'univers (bis)\n\n3.\nSauve ton peuple, Seigneur,\nEt bénis ton héritage;\nQue ta gloire et ta splendeur\nSoient à jamais son partage,\nConduis-le par ton amour\nJusqu\'au céleste séjour! (bis)\n\n4.\nGloire soit au Saint-Esprit!\nGloire soit à Dieu le père!\nGloire soit à Jésus-Christ,\nNotre Sauveur, notre Frère!\nSon immense charité\nDure à perpétuité. (bis)','<p><strong>1.</strong> </p>\n<p>Grand Dieu, nous te bénissons, </p>\n<p>Nous célébrons tes louanges! </p>\n<p>Éternel, nous t&#39;exaltons, </p>\n<p>De concert avec les anges; </p>\n<p>Et prosternés devant toi, </p>\n<p>Nous t&#39;adorons, o grand Roi (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les saints et les bienheureux, </p>\n<p>Les trônes et les puissances, </p>\n<p>Toutes les vertus des cieux, </p>\n<p>Disent tes magnificences, </p>\n<p>Proclamant dans leurs concerts </p>\n<p>Le grand Dieu de l&#39;univers (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sauve ton peuple, Seigneur, </p>\n<p>Et bénis ton héritage; </p>\n<p>Que ta gloire et ta splendeur </p>\n<p>Soient à jamais son partage, </p>\n<p>Conduis-le par ton amour </p>\n<p>Jusqu&#39;au céleste séjour! (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gloire soit au Saint-Esprit! </p>\n<p>Gloire soit à Dieu le père! </p>\n<p>Gloire soit à Jésus-Christ, </p>\n<p>Notre Sauveur, notre Frère! </p>\n<p>Son immense charité </p>\n<p>Dure à perpétuité. (bis) </p>\n','Grand-Dieu-nous-te-benissons',NULL,'fr',7,'2023-07-22 22:41:15','2023-10-03 20:27:45'),(1256,'68bf1309-3503-4c18-af63-086cdba6c08e',1,0,'Jésus, Jésus, Jésus, Jésus',7,'7_CE_fr','1.\nJésus, Jésus, Jésus, Jésus,\nSeul nom que mon coeur aime,\nJe voudrais ne prononcer plus\nQu\'un seul mot, ce nom même.\n\n\n2.\nJésus, Jésus, Jésus, Jésus,\nC\'est celui don\'t les anges,\nProsternés autour de l\'Agneau,\nRemplisse leurs louanges.\n\n\n3.\nJésus, Jésus, Jésus, Jésus,\nBientôt, mon coeur qui t\'aime,\nAuprès de Toi ne diras plus\nQu\'un seul mot, ton nom même.','<p><strong>1.</strong> </p>\n<p>Jésus, Jésus, Jésus, Jésus, </p>\n<p>Seul nom que mon coeur aime, </p>\n<p>Je voudrais ne prononcer plus </p>\n<p>Qu&#39;un seul mot, ce nom même. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, Jésus, Jésus, Jésus, </p>\n<p>C&#39;est celui don&#39;t les anges, </p>\n<p>Prosternés autour de l&#39;Agneau, </p>\n<p>Remplisse leurs louanges. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus, Jésus, Jésus, Jésus, </p>\n<p>Bientôt, mon coeur qui t&#39;aime, </p>\n<p>Auprès de Toi ne diras plus </p>\n<p>Qu&#39;un seul mot, ton nom même. </p>\n','Jesus-Jesus-Jesus-Jesus',NULL,'fr',7,'2023-07-22 22:41:15','2023-10-03 18:33:55'),(1257,'8a6a180c-1d3d-4885-a008-eae9e471f9c2',0,0,'Jésus, Ô nom qui surpasse!',29,'29_CE_fr','1.\nJésus, ô nom qui surpasse\nTout nom qu\'on puisse exalter!\nQue jamais je ne me lasse,\nNom béni, de te chanter!\nSeule clarté qui rayonne\nSur les gloires du saint lieu,\nSeul nom don\'t l\'écho résonne\nDans le coeur même de Dieu!\n\n2.\nJésus, c\'est l\'Amour suprême,\nDe son trône descendu,\nDu ceint de son diadème\nLe front de l\'homme perdu.\nC\'est le Roi qui s\'humilie\nPour vaincre le révolté;\nC\'est la divine folie,\nDans la divine bonté.\n\n3.\nQui pleura sur ceux qui pleurent?\nC\'est Lui, l\'homme méprisé!\nQui mourut pour ceux qui meurent?\nC\'est Lui, l\'homme au coeur brisé!\nDe son sang et ses larmes\nIl arrosa son chemin,\nEt par ces seules armes\nQu\'il sauva le genre humain.\n\n4.\nJésus, par qui Dieu pardonne,\nRoi d\'épines couronné,\nQue le monde t\'abandonne,\nA Toi mon coeur s\'est donné!\nTa mort est ma délivrance,\nJe suis heureux sous ta loi,\nO Jésus, mon espérance,\nQuelle autre aurais-je que toi?','<p><strong>1.</strong> </p>\n<p>Jésus, ô nom qui surpasse </p>\n<p>Tout nom qu&#39;on puisse exalter! </p>\n<p>Que jamais je ne me lasse, </p>\n<p>Nom béni, de te chanter! </p>\n<p>Seule clarté qui rayonne </p>\n<p>Sur les gloires du saint lieu, </p>\n<p>Seul nom don&#39;t l&#39;écho résonne </p>\n<p>Dans le coeur même de Dieu! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, c&#39;est l&#39;Amour suprême, </p>\n<p>De son trône descendu, </p>\n<p>Du ceint de son diadème </p>\n<p>Le front de l&#39;homme perdu. </p>\n<p>C&#39;est le Roi qui s&#39;humilie </p>\n<p>Pour vaincre le révolté; </p>\n<p>C&#39;est la divine folie, </p>\n<p>Dans la divine bonté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui pleura sur ceux qui pleurent? </p>\n<p>C&#39;est Lui, l&#39;homme méprisé! </p>\n<p>Qui mourut pour ceux qui meurent? </p>\n<p>C&#39;est Lui, l&#39;homme au coeur brisé! </p>\n<p>De son sang et ses larmes </p>\n<p>Il arrosa son chemin, </p>\n<p>Et par ces seules armes </p>\n<p>Qu&#39;il sauva le genre humain. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus, par qui Dieu pardonne, </p>\n<p>Roi d&#39;épines couronné, </p>\n<p>Que le monde t&#39;abandonne, </p>\n<p>A Toi mon coeur s&#39;est donné! </p>\n<p>Ta mort est ma délivrance, </p>\n<p>Je suis heureux sous ta loi, </p>\n<p>O Jésus, mon espérance, </p>\n<p>Quelle autre aurais-je que toi? </p>\n','Jesus-O-nom-qui-surpasse!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1258,'5206c035-df2b-417b-8892-11ab67573623',0,0,'Je l’ai trouvé, je l’ai trouvé',18,'18_CE_fr','1.\nJe l\'ai trouvé, je l\'ai trouvé,\nLe bonheur ineffable!\nJe suis sauvé, je suis sauvé,\nOh! Joie inexprimable!\nTous mes péchés son effacés;\nLe sang de Christ me lave.\nLes jours des larmes sont passés:je ne suis plus esclave!\n\n2.\nOh! Quel bonheur! Oh! Quel bonheur!\nD\'avoir Jésus pour Maître!\nO, mon Sauveur, mon seul Sauveur,\nA toi seul je veux être!\nTu vins briser, puissant Vainqueur,\nDu mal la tyrannie,\nAffranchissant mon pauvre coeur,\nEt me donnant la vie!\n\n3.\nAh! Laissez-moi chanter mon Roi;\nOui, qu\'à genoux, je chante!\nJésus n\'est-il pas tout pour moi?\nGloire à sa croix sanglante!\nSans se lasser, jour après jour,\nIl m\'aime, Il m\'aime encore…\nComment répondre à tant d\'amour?\nJe crois, j\'aime et j\'adore!','<p><strong>1.</strong> </p>\n<p>Je l&#39;ai trouvé, je l&#39;ai trouvé, </p>\n<p>Le bonheur ineffable! </p>\n<p>Je suis sauvé, je suis sauvé, </p>\n<p>Oh! Joie inexprimable! </p>\n<p>Tous mes péchés son effacés; </p>\n<p>Le sang de Christ me lave. </p>\n<p>Les jours des larmes sont passés:je ne suis plus esclave! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Quel bonheur! Oh! Quel bonheur! </p>\n<p>D&#39;avoir Jésus pour Maître! </p>\n<p>O, mon Sauveur, mon seul Sauveur, </p>\n<p>A toi seul je veux être! </p>\n<p>Tu vins briser, puissant Vainqueur, </p>\n<p>Du mal la tyrannie, </p>\n<p>Affranchissant mon pauvre coeur, </p>\n<p>Et me donnant la vie! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ah! Laissez-moi chanter mon Roi; </p>\n<p>Oui, qu&#39;à genoux, je chante! </p>\n<p>Jésus n&#39;est-il pas tout pour moi? </p>\n<p>Gloire à sa croix sanglante! </p>\n<p>Sans se lasser, jour après jour, </p>\n<p>Il m&#39;aime, Il m&#39;aime encore… </p>\n<p>Comment répondre à tant d&#39;amour? </p>\n<p>Je crois, j&#39;aime et j&#39;adore! </p>\n','Je-l\'ai-trouve-je-l\'ai-trouve',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1259,'d93258db-a865-45a0-8d61-3b40bb637917',0,0,'Quand finira le combat',34,'34_CE_fr','1.\nQuand finira le combat de la foi,\nQuand aux rayons de la nouvelle aurore\nMes yeux verront le Sauveur que j\'adore\nAh! Ce sera le triomphe pour moi.\n\nRefrain\nJour de victoire et de bonheur!\nMes yeux verront mon Rédempteur,\nLoin du péché, de la mort, de l\'erreur…\nO jour de victoire et de bonheur!\n\n2.\nDans ton palais, ô mon bien-aimé Roi\nTu daigneras m\'accorder une place…\nÊtre à tes pieds et contempler ta face:\nJe ne veux pas d\'autre gloire pour moi.\n\n3.\nLà, je verrai, groupés autour de toi,\nCeux que jamais autrefois sur la terre;\nMais ton sourire, ô Prince de lumière,\nSera le ciel pour eux comme pour moi!','<p><strong>1.</strong> </p>\n<p>Quand finira le combat de la foi, </p>\n<p>Quand aux rayons de la nouvelle aurore </p>\n<p>Mes yeux verront le Sauveur que j&#39;adore </p>\n<p>Ah! Ce sera le triomphe pour moi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jour de victoire et de bonheur!</strong> </p>\n<p><strong>Mes yeux verront mon Rédempteur,</strong> </p>\n<p><strong>Loin du péché, de la mort, de l&#39;erreur…</strong> </p>\n<p><strong>O jour de victoire et de bonheur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans ton palais, ô mon bien-aimé Roi </p>\n<p>Tu daigneras m&#39;accorder une place… </p>\n<p>Être à tes pieds et contempler ta face: </p>\n<p>Je ne veux pas d&#39;autre gloire pour moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Là, je verrai, groupés autour de toi, </p>\n<p>Ceux que jamais autrefois sur la terre; </p>\n<p>Mais ton sourire, ô Prince de lumière, </p>\n<p>Sera le ciel pour eux comme pour moi! </p>\n','Quand-finira-le-combat',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1260,'8ab0e8f0-887a-40c5-8d94-0e040ed6f236',0,0,'Voici Noël, Ô douce nuit',38,'38_CE_fr','1.\nVoici Noël, o douce nuit\nL\'étoile est là qui nous conduit\nAllons donc tous avec les mages\nPorter à Jésus nos hommages\nCar l\'enfant nous est né\nLe fils nous est donné\n\n2.\nVoici Noël! Oh! Quel beau jour\nJésus est né! Quel grand amour\nC\'est pour nous qu\'il vient sur la terre\nQu\'il prend sur lui notre misère\nUn sauveur nous est né\nLe fils nous est donné\n\n3.\nVoici Noël! Ah, d\'un seul cœur\nJoignons nos voix au divin chœur\nQui proclame au ciel les louanges\nDe celui qu\'annoncent les anges\nOui, l\'enfant nous est né\nLe fils nous est donné\n\n4.\nVoici Noël! Ne craignons pas\nCar Dieu nous dit : « Paix ici-bas\nBienveillance envers tous les hommes»\nPour nous aussi, tels que nous sommes\nUn sauveur nous est né\nLe fils nous est donné','<p><strong>1.</strong> </p>\n<p>Voici Noël, o douce nuit </p>\n<p>L&#39;étoile est là qui nous conduit </p>\n<p>Allons donc tous avec les mages </p>\n<p>Porter à Jésus nos hommages </p>\n<p>Car l&#39;enfant nous est né </p>\n<p>Le fils nous est donné </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voici Noël! Oh! Quel beau jour </p>\n<p>Jésus est né! Quel grand amour </p>\n<p>C&#39;est pour nous qu&#39;il vient sur la terre </p>\n<p>Qu&#39;il prend sur lui notre misère </p>\n<p>Un sauveur nous est né </p>\n<p>Le fils nous est donné </p>\n<p><br/> <strong>3.</strong></p>\n<p>Voici Noël! Ah, d&#39;un seul cœur </p>\n<p>Joignons nos voix au divin chœur </p>\n<p>Qui proclame au ciel les louanges </p>\n<p>De celui qu&#39;annoncent les anges </p>\n<p>Oui, l&#39;enfant nous est né </p>\n<p>Le fils nous est donné </p>\n<p><br/> <strong>4.</strong></p>\n<p>Voici Noël! Ne craignons pas </p>\n<p>Car Dieu nous dit : « Paix ici-bas </p>\n<p>Bienveillance envers tous les hommes» </p>\n<p>Pour nous aussi, tels que nous sommes </p>\n<p>Un sauveur nous est né </p>\n<p>Le fils nous est donné </p>\n','Voici-Noel-O-douce-nuit',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1261,'6a6a03ab-9988-448c-af52-77e757da996c',0,0,'Un regard sur ta croix sanglante',57,'57_CE_fr','1.\nUn regard sur ta croix sanglante,\nJésus, fait déborder mon coeur\nD\'amour et de douleur poignante,\nD\'amour et d\'éternel bonheur.\n\n2.\nTu seras seul toute ma gloire\nCar tout ce dont je me vantais\nM\'empêcherait encore de croire\nQu\'en ta mort seule j\'ai la paix.\n\n3.\nTe donnerais-je quelques larmes,\nQuelques soupirs et quelque amour?\nDevant ta croix, je rends les larmes:\nPrends-moi tout entier dès ce jour.\n\n4.\nA tes pieds, mon Sauveur, je jette\nCe que mon coeur aime le plus;\nOui, prends mon idole secrète;\nTu seras mon trésor, Jésus!','<p><strong>1.</strong> </p>\n<p>Un regard sur ta croix sanglante, </p>\n<p>Jésus, fait déborder mon coeur </p>\n<p>D&#39;amour et de douleur poignante, </p>\n<p>D&#39;amour et d&#39;éternel bonheur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu seras seul toute ma gloire </p>\n<p>Car tout ce dont je me vantais </p>\n<p>M&#39;empêcherait encore de croire </p>\n<p>Qu&#39;en ta mort seule j&#39;ai la paix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Te donnerais-je quelques larmes, </p>\n<p>Quelques soupirs et quelque amour? </p>\n<p>Devant ta croix, je rends les larmes: </p>\n<p>Prends-moi tout entier dès ce jour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A tes pieds, mon Sauveur, je jette </p>\n<p>Ce que mon coeur aime le plus; </p>\n<p>Oui, prends mon idole secrète; </p>\n<p>Tu seras mon trésor, Jésus! </p>\n','Un-regard-sur-ta-croix-sanglante',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1262,'b9d33a86-985b-4de5-92a7-f4e23efe849e',0,0,'Pour quel péché, Jésus, pour quelle offense',59,'59_CE_fr','1.\nPour quel péché, Jésus, pour quel offense\nA-t-on sur Toi prononcé la sentence?\nAh!  Qu\'as-tu fais, innocente victime?\nQuel est ton crime?\n\n2.\nQui peut t\'avoir attiré ce supplice?\nC\'est moi,  Seigneur! Oui, c\'est mon injustice:\nDe ces tourments où ton amour t\'expose\nJe suis la cause.\n\n3.\nPour me sauver, sacrifice adorable!\nLe maître meurt pour l\'esclave coupable,\nLe bon berger pour sa brebis chérie\nDonne sa vie.','<p><strong>1.</strong> </p>\n<p>Pour quel péché, Jésus, pour quel offense </p>\n<p>A-t-on sur Toi prononcé la sentence? </p>\n<p>Ah!  Qu&#39;as-tu fais, innocente victime? </p>\n<p>Quel est ton crime? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qui peut t&#39;avoir attiré ce supplice? </p>\n<p>C&#39;est moi,  Seigneur! Oui, c&#39;est mon injustice: </p>\n<p>De ces tourments où ton amour t&#39;expose </p>\n<p>Je suis la cause. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour me sauver, sacrifice adorable! </p>\n<p>Le maître meurt pour l&#39;esclave coupable, </p>\n<p>Le bon berger pour sa brebis chérie </p>\n<p>Donne sa vie. </p>\n','Pour-quel-peche-Jesus-pour-quelle-offense',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1263,'72bd9dea-ef2c-4fed-9a65-3c4e20c93f04',0,0,'Portant sa croix, il monte',48,'48_CE_fr','1.\nPortant sa croix, il monte\nLe sinistre coteau\nDans l\'opprobre et la honte,\nMuet comme un agneau.\nCe Sauveur débonnaire,\nPour le pécheur mortel,\nVa trouver au Calvaire,\nLa mort d\'un criminel.\n\n2.\nSur cette croix sanglante,\nSous le divin courroux,\nRegarde, âme tremblante,\nL\'Agneau souffrant pour nous.\nIl porte sur sa tête\nNos péchés odieux;\nIl veut payer ta dette,\nPour t\'introduire aux cieux.\n\n3.\nOh! Charité profonde!\nInsondable grandeur!\nLe Créateur du monde\nVeut être ton Sauveur.\nAinsi, plus de détresse,\nJésus est recours!\nIl est ta forteresse!\nIl t\'aime pour toujours!','<p><strong>1.</strong> </p>\n<p>Portant sa croix, il monte </p>\n<p>Le sinistre coteau </p>\n<p>Dans l&#39;opprobre et la honte, </p>\n<p>Muet comme un agneau. </p>\n<p>Ce Sauveur débonnaire, </p>\n<p>Pour le pécheur mortel, </p>\n<p>Va trouver au Calvaire, </p>\n<p>La mort d&#39;un criminel. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sur cette croix sanglante, </p>\n<p>Sous le divin courroux, </p>\n<p>Regarde, âme tremblante, </p>\n<p>L&#39;Agneau souffrant pour nous. </p>\n<p>Il porte sur sa tête </p>\n<p>Nos péchés odieux; </p>\n<p>Il veut payer ta dette, </p>\n<p>Pour t&#39;introduire aux cieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh! Charité profonde! </p>\n<p>Insondable grandeur! </p>\n<p>Le Créateur du monde </p>\n<p>Veut être ton Sauveur. </p>\n<p>Ainsi, plus de détresse, </p>\n<p>Jésus est recours! </p>\n<p>Il est ta forteresse! </p>\n<p>Il t&#39;aime pour toujours! </p>\n','Portant-sa-croix-il-monte',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1264,'a287499a-52d8-4df7-9079-787c7cf44b23',0,0,'En contemplant la croix bénie',63,'63_CE_fr','1.\nEn contemplant la croix bénie\nOù mourut le Prince de paix,\nTout mon orgueil, mes biens, ma vie,\nSont abandonnés à jamais!\n\nRefrain\nOh!  Sur la croix son sang fut versé,\nIl fut là crucifié!\nMaintenant, il vit dans mon coeur\nOù tout est paix, parfait bonheur.\n\n2.\nQue toujours je me glorifie\nDans ta mort seule, ô mon Sauveur;\nTout autre but je sacrifie\nPour suivre l\'Homme de douleur.\n\n3.\nOh!  Regardez tous sa couronne,\nSes mains, ses pieds pour nous percés,\nD\'où coule un fleuve qui nous donne\nPardon, paix, joie et sainteté.\n\n4.\nSi je possédais tout le monde,\nCe serait un don trop petit;\nAmour divin, grâce profonde,\nTout est à toi dès aujourd\'hui.','<p><strong>1.</strong> </p>\n<p>En contemplant la croix bénie </p>\n<p>Où mourut le Prince de paix, </p>\n<p>Tout mon orgueil, mes biens, ma vie, </p>\n<p>Sont abandonnés à jamais! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh!  Sur la croix son sang fut versé,</strong> </p>\n<p><strong>Il fut là crucifié!</strong> </p>\n<p><strong>Maintenant, il vit dans mon coeur</strong> </p>\n<p><strong>Où tout est paix, parfait bonheur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que toujours je me glorifie </p>\n<p>Dans ta mort seule, ô mon Sauveur; </p>\n<p>Tout autre but je sacrifie </p>\n<p>Pour suivre l&#39;Homme de douleur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh!  Regardez tous sa couronne, </p>\n<p>Ses mains, ses pieds pour nous percés, </p>\n<p>D&#39;où coule un fleuve qui nous donne </p>\n<p>Pardon, paix, joie et sainteté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si je possédais tout le monde, </p>\n<p>Ce serait un don trop petit; </p>\n<p>Amour divin, grâce profonde, </p>\n<p>Tout est à toi dès aujourd&#39;hui. </p>\n','En-contemplant-la-croix-benie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1265,'67fd4636-de3b-4e8f-aaa4-cfc67a23227c',0,0,'Agneau de Dieu, messager de la grâce',56,'56_CE_fr','1.\nAgneau de dieu,\nMessager de la grâce,\nJe veux entendre ta voix,\nLe langage de la croix:\nSolo:\nPour toi je fus livré,\nMéprisé, maltraité,\nBattu, meurtri, blessé,\nPour ton iniquité.\n\n2.\nAgneau de Dieu,\nMessager de la grâce,\nJ\'ai péché, je viens à toi,\nJe t\'invoque, réponds-moi!\nSolo:\nPour toi je fus brisé,\nD\'épines couronné,\nDe tous abandonné,\nFrappé pour ton péché.\n\n3.\nAgneau de Dieu,\nMessager de la grâce,\nOh!  Fais passer sur mon coeur\nTout ton sang, divin Sauveur!\nSolo:\nMon côté fut percé,\nEt mon sang fut versé;\nDans ce sang, ton passé,\nPécheur, est effacé.\n\n4.\nAgneau de Dieu,\nMessager de la grâce,\nJe me confie et je crois;\nTon pardon, je le reçois!\nSolo:\nSur l\'enfant racheté\nQui fait ma volonté,\nJe mets ma sainteté,\nMa divine beauté!','<p><strong>1.</strong> </p>\n<p>Agneau de dieu, </p>\n<p>Messager de la grâce, </p>\n<p>Je veux entendre ta voix, </p>\n<p>Le langage de la croix: </p>\n<p>Solo: </p>\n<p>Pour toi je fus livré, </p>\n<p>Méprisé, maltraité, </p>\n<p>Battu, meurtri, blessé, </p>\n<p>Pour ton iniquité. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Agneau de Dieu, </p>\n<p>Messager de la grâce, </p>\n<p>J&#39;ai péché, je viens à toi, </p>\n<p>Je t&#39;invoque, réponds-moi! </p>\n<p>Solo: </p>\n<p>Pour toi je fus brisé, </p>\n<p>D&#39;épines couronné, </p>\n<p>De tous abandonné, </p>\n<p>Frappé pour ton péché. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Agneau de Dieu, </p>\n<p>Messager de la grâce, </p>\n<p>Oh!  Fais passer sur mon coeur </p>\n<p>Tout ton sang, divin Sauveur! </p>\n<p>Solo: </p>\n<p>Mon côté fut percé, </p>\n<p>Et mon sang fut versé; </p>\n<p>Dans ce sang, ton passé, </p>\n<p>Pécheur, est effacé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Agneau de Dieu, </p>\n<p>Messager de la grâce, </p>\n<p>Je me confie et je crois; </p>\n<p>Ton pardon, je le reçois! </p>\n<p>Solo: </p>\n<p>Sur l&#39;enfant racheté </p>\n<p>Qui fait ma volonté, </p>\n<p>Je mets ma sainteté, </p>\n<p>Ma divine beauté! </p>\n','Agneau-de-Dieu-messager-de-la-grace',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1266,'dff55d87-dadf-4b0b-81eb-ae672a133f5a',0,0,'J\'entends ta douce voix',58,'58_CE_fr','1.\nJ\'entends ta douce voix,\nJésus, je viens à Toi.\nJe viens, ô Sauveur, lave-moi,\nDans le sang de ta croix!\n\nRefrain\nJésus, Roi des rois,\nQui mourus pour moi,\nJe veux mourir avec Toi,\nAvec Toi sur la croix.\n\n2.\nJ\'entends ta douce voix,\nQui me dit: “Crois en moi!”\nJe crois, Seigneur, soutiens ma foi,\nTiens-moi près de ta croix!\n\n3.\nJ\'entends ta douce voix,\nElle pénètre en moi\nEt me dit d\'aimer comme Toi\nDe l\'amour de la croix.\n\n4.\nJ\'entends ta douce voix,\nToi qui mourus pour moi,\nSeigneur, que je m\'unisse à Toi\nDans la mort, par la foi!','<p><strong>1.</strong> </p>\n<p>J&#39;entends ta douce voix, </p>\n<p>Jésus, je viens à Toi. </p>\n<p>Je viens, ô Sauveur, lave-moi, </p>\n<p>Dans le sang de ta croix! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus, Roi des rois,</strong> </p>\n<p><strong>Qui mourus pour moi,</strong> </p>\n<p><strong>Je veux mourir avec Toi,</strong> </p>\n<p><strong>Avec Toi sur la croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;entends ta douce voix, </p>\n<p>Qui me dit: “Crois en moi!” </p>\n<p>Je crois, Seigneur, soutiens ma foi, </p>\n<p>Tiens-moi près de ta croix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;entends ta douce voix, </p>\n<p>Elle pénètre en moi </p>\n<p>Et me dit d&#39;aimer comme Toi </p>\n<p>De l&#39;amour de la croix. </p>\n<p><br/> <strong>4.</strong></p>\n<p>J&#39;entends ta douce voix, </p>\n<p>Toi qui mourus pour moi, </p>\n<p>Seigneur, que je m&#39;unisse à Toi </p>\n<p>Dans la mort, par la foi! </p>\n','J\'entends-ta-douce-voix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1267,'f84d52e3-cbd1-4e48-9999-07558e80aa9e',0,0,'Rédempteur adorable',60,'60_CE_fr','1.\nRédempteur, adorable,\nSur la croix attaché,\nTraité comme un coupable,\nBrisé pour mon péché\nTon angoisse suprême,\nTa douleur, ton tourment\nMe disent: “Vois, je t\'aime,\nJ\'ai pris ton châtiment.”\n\n2.\nAbandonné du Père,\nDans mon âme troublé,\nBuvant la coupe amère\nPour ton iniquité,\nDe l\'éternelle flamme,\nMon amour te sauva,\nJe mourus pour ton âme,\nPécheur, à Golgotha!”\n\n3.\nLe sang de mes blessures,\nMa couronne de Roi,\nToutes ces meurtrissures,\nComprends-le, c\'est pour toi!\nJ\'ai subi ta souffrance,\nJ\'ai porté ta langueur;\nContemple en assurance\nTon grand Libérateur!”\n\n4.\nTon amour me réclame,\nMe voici, cher Sauveur!\nPrends mon corps et mon âme\nPour prix de ta douleur.\nOui, mon âme ravie,\nDésormais ne veut plus\nQue vivre de ta vie,\nA ta gloire, ô Jésus!','<p><strong>1.</strong> </p>\n<p>Rédempteur, adorable, </p>\n<p>Sur la croix attaché, </p>\n<p>Traité comme un coupable, </p>\n<p>Brisé pour mon péché </p>\n<p>Ton angoisse suprême, </p>\n<p>Ta douleur, ton tourment </p>\n<p>Me disent: “Vois, je t&#39;aime, </p>\n<p>J&#39;ai pris ton châtiment.” </p>\n<p><br/> <strong>2.</strong></p>\n<p>Abandonné du Père, </p>\n<p>Dans mon âme troublé, </p>\n<p>Buvant la coupe amère </p>\n<p>Pour ton iniquité, </p>\n<p>De l&#39;éternelle flamme, </p>\n<p>Mon amour te sauva, </p>\n<p>Je mourus pour ton âme, </p>\n<p>Pécheur, à Golgotha!” </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le sang de mes blessures, </p>\n<p>Ma couronne de Roi, </p>\n<p>Toutes ces meurtrissures, </p>\n<p>Comprends-le, c&#39;est pour toi! </p>\n<p>J&#39;ai subi ta souffrance, </p>\n<p>J&#39;ai porté ta langueur; </p>\n<p>Contemple en assurance </p>\n<p>Ton grand Libérateur!” </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ton amour me réclame, </p>\n<p>Me voici, cher Sauveur! </p>\n<p>Prends mon corps et mon âme </p>\n<p>Pour prix de ta douleur. </p>\n<p>Oui, mon âme ravie, </p>\n<p>Désormais ne veut plus </p>\n<p>Que vivre de ta vie, </p>\n<p>A ta gloire, ô Jésus! </p>\n','Redempteur-adorable',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1268,'e6f02bdb-0d74-4043-b0ae-8ced8c05fd6e',0,0,'Dieu fort et grand!',27,'27_CE_fr','1.\nDieu fort et grand!\nTu vois toute ma vie,\nTu m\'as connu,\nTu m\'as sondé des cieux:\nOù puis-je fuir\nTa science infinie?Éternel roi,\nTu me suis en tous lieux.  (bis 4 lignes)\n\n2.\nSoit que je marche\nOu bien que je m\'arrête,\nVoici, Seigneur!\nTu te tiens près de moi;\nEt, pour parler\nQuand ma langue s\'apprête,\nTout mon dessein\nEst déjà devant toi.  (bis 4 lignes)\n\n3.\nVivant ou mort,\nDans les cieux, sur la terre,\nCeint de lumière\nOu ceint d\'obscurité,\nPartout ta main\nPeut me saisir, ô mon Père!\nPartout sur moi\nTon oeil s\'arrêté.  (bis 4 lignes)\n\n4.\nQue ta pensée\nEst donc mystérieuse!\nJe n\'en saurais\nMesure la hauteur;\nA mes regards\nTon oeuvre est merveilleuse,\nEt, confondu,\nJ\'adore ta grandeur.  (bis 4 lignes)\n\n5.\nConnaître, ô Dieu!\nTon amour, ta puissance,\nSur mon sentier\nVoir briller ta splendeur\nFonder sur toi\nToute mon espérance\nSont les seul biens\nQue désire mon coeur.  (bis 4 lignes)','<p><strong>1.</strong> </p>\n<p>Dieu fort et grand! </p>\n<p>Tu vois toute ma vie, </p>\n<p>Tu m&#39;as connu, </p>\n<p>Tu m&#39;as sondé des cieux: </p>\n<p>Où puis-je fuir </p>\n<p>Ta science infinie?Éternel roi, </p>\n<p>Tu me suis en tous lieux.  (bis 4 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Soit que je marche </p>\n<p>Ou bien que je m&#39;arrête, </p>\n<p>Voici, Seigneur! </p>\n<p>Tu te tiens près de moi; </p>\n<p>Et, pour parler </p>\n<p>Quand ma langue s&#39;apprête, </p>\n<p>Tout mon dessein </p>\n<p>Est déjà devant toi.  (bis 4 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vivant ou mort, </p>\n<p>Dans les cieux, sur la terre, </p>\n<p>Ceint de lumière </p>\n<p>Ou ceint d&#39;obscurité, </p>\n<p>Partout ta main </p>\n<p>Peut me saisir, ô mon Père! </p>\n<p>Partout sur moi </p>\n<p>Ton oeil s&#39;arrêté.  (bis 4 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que ta pensée </p>\n<p>Est donc mystérieuse! </p>\n<p>Je n&#39;en saurais </p>\n<p>Mesure la hauteur; </p>\n<p>A mes regards </p>\n<p>Ton oeuvre est merveilleuse, </p>\n<p>Et, confondu, </p>\n<p>J&#39;adore ta grandeur.  (bis 4 lignes) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Connaître, ô Dieu! </p>\n<p>Ton amour, ta puissance, </p>\n<p>Sur mon sentier </p>\n<p>Voir briller ta splendeur </p>\n<p>Fonder sur toi </p>\n<p>Toute mon espérance </p>\n<p>Sont les seul biens </p>\n<p>Que désire mon coeur.  (bis 4 lignes) </p>\n','Dieu-fort-et-grand!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1269,'9512c1f8-1311-4350-bdbb-8d7de66f4292',0,0,'Vers toi, Seigneur',33,'33_CE_fr','1.\nVers toi, Seigneur, que notre hymne s\'élève;\nC\'est le parfum du soir que nous t\'offrons.\nPour que ce jour en ta grâce s\'achève,\nVois à tes pieds s\'incliner tous nos fronts.\n\n2.\nLa nuit descend; une douce rosée\nBaigne la terre après les feux du jour…\nO bon Sauveur, que notre âme épuisée\nRetrouve aussi la vie en ton amour!\n\n3.\nVeuille détruire en nous ce qui t\'offense;\nDaigne effacer nos fautes de ce jour;\nEt qu\'en ta paix retrouvant l\'innocence,\nNous soyons prêts, ô Christ, pour ton retour!','<p><strong>1.</strong> </p>\n<p>Vers toi, Seigneur, que notre hymne s&#39;élève; </p>\n<p>C&#39;est le parfum du soir que nous t&#39;offrons. </p>\n<p>Pour que ce jour en ta grâce s&#39;achève, </p>\n<p>Vois à tes pieds s&#39;incliner tous nos fronts. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La nuit descend; une douce rosée </p>\n<p>Baigne la terre après les feux du jour… </p>\n<p>O bon Sauveur, que notre âme épuisée </p>\n<p>Retrouve aussi la vie en ton amour! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Veuille détruire en nous ce qui t&#39;offense; </p>\n<p>Daigne effacer nos fautes de ce jour; </p>\n<p>Et qu&#39;en ta paix retrouvant l&#39;innocence, </p>\n<p>Nous soyons prêts, ô Christ, pour ton retour! </p>\n','Vers-toi-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1270,'2761eaef-0fd4-47b7-8dfb-563da2363c83',0,0,'Il est né Roi du monde',39,'39_CE_fr','1.\nIl est né le Roi du monde,\nLe Christ, le Libérateur!\nQue la terre au ciel réponde\nD\'une voix, d\'un même coeur.\n\nRefrain\nDans l\'étable misérable,\nContemplez ce nouveau né!\nA la terre, ô mystère,\nDieu Lui-même s\'est donné!\n\n2.\nA tes pieds, Roi sans couronne,\nJésus, nous courbons nos fronts.\nTa crèche est pour nous un trône,\nC\'est là que nous t\'adorons.\n\n3.\nEn notre âme vient renaître,\nO Christ, elle a soif de toi!\nElle veut t\'avoir pour Maître,\nHumble enfant, glorieux Roi!','<p><strong>1.</strong> </p>\n<p>Il est né le Roi du monde, </p>\n<p>Le Christ, le Libérateur! </p>\n<p>Que la terre au ciel réponde </p>\n<p>D&#39;une voix, d&#39;un même coeur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dans l&#39;étable misérable,</strong> </p>\n<p><strong>Contemplez ce nouveau né!</strong> </p>\n<p><strong>A la terre, ô mystère,</strong> </p>\n<p><strong>Dieu Lui-même s&#39;est donné!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A tes pieds, Roi sans couronne, </p>\n<p>Jésus, nous courbons nos fronts. </p>\n<p>Ta crèche est pour nous un trône, </p>\n<p>C&#39;est là que nous t&#39;adorons. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En notre âme vient renaître, </p>\n<p>O Christ, elle a soif de toi! </p>\n<p>Elle veut t&#39;avoir pour Maître, </p>\n<p>Humble enfant, glorieux Roi! </p>\n','Il-est-ne-Roi-du-monde',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1271,'5c67c892-ba7e-4b8e-80ce-0460d1027960',0,0,'Dans les champs le berger veille',40,'40_CE_fr','1.\nDans les champs le berger veille\nAux approche du matin,\nQuand la brise à son oreille,\nApporte un écho lointain.\nEt tandis que tout rayonne\nD\'un soudaine clarté,\nPlus forte la voix résonne\nDu fond de l\'immensité.\nGloire à Dieu dans les hauts cieux,\nPaix aux hommes en tous lieux!\nLe Rédempteur et le Maître\nQu\'attendaient vos coeurs pieux,\nDans la crèche vient de naître:\nGloire à Dieu dans les hauts cieux!\n\n2.\nBergers, pourrez-vous comprendre\nL\'amour du Dieu tout-puissant,\nQui, pour vous, daigne descendre\nA n\'être qu\'un faible enfant,\nUn enfant que la souffrance\nSaisira dès le berceau,\nQui marchera sans défense\nVers la croix et le tombeau?\nGloire à dieu dans les hauts cieux,\nPaix au hommes en tous lieux!\nPour sauver l\'âme coupable\nLe verbe victorieux,\nNaît dans un pauvre étable:\nGloire à Dieu dans les hauts cieux!\n\n3.\nEn chantant l\'hymne nouvelle\nL\'ange au ciel s\'est envolé,\nEt sous la voûte éternelle\nLe berger reste isolé;\nMais, ravi du saint mystère,\nSon coeur murmure tout bas:\n“Si l\'ange a quitté la terre,\nLe Sauveur ne sen vas pas!”\nGloire à Dieu dans les hauts cieux,\nPaix aux hommes en tous lieux!\nPour cet enfant dans ses langes,\nPour ce don si précieux,\nChantons tous, comme les anges:\nGloire à Dieu dans les hauts cieux!','<p><strong>1.</strong> </p>\n<p>Dans les champs le berger veille </p>\n<p>Aux approche du matin, </p>\n<p>Quand la brise à son oreille, </p>\n<p>Apporte un écho lointain. </p>\n<p>Et tandis que tout rayonne </p>\n<p>D&#39;un soudaine clarté, </p>\n<p>Plus forte la voix résonne </p>\n<p>Du fond de l&#39;immensité. </p>\n<p>Gloire à Dieu dans les hauts cieux, </p>\n<p>Paix aux hommes en tous lieux! </p>\n<p>Le Rédempteur et le Maître </p>\n<p>Qu&#39;attendaient vos coeurs pieux, </p>\n<p>Dans la crèche vient de naître: </p>\n<p>Gloire à Dieu dans les hauts cieux! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bergers, pourrez-vous comprendre </p>\n<p>L&#39;amour du Dieu tout-puissant, </p>\n<p>Qui, pour vous, daigne descendre </p>\n<p>A n&#39;être qu&#39;un faible enfant, </p>\n<p>Un enfant que la souffrance </p>\n<p>Saisira dès le berceau, </p>\n<p>Qui marchera sans défense </p>\n<p>Vers la croix et le tombeau? </p>\n<p>Gloire à dieu dans les hauts cieux, </p>\n<p>Paix au hommes en tous lieux! </p>\n<p>Pour sauver l&#39;âme coupable </p>\n<p>Le verbe victorieux, </p>\n<p>Naît dans un pauvre étable: </p>\n<p>Gloire à Dieu dans les hauts cieux! </p>\n<p><br/> <strong>3.</strong></p>\n<p>En chantant l&#39;hymne nouvelle </p>\n<p>L&#39;ange au ciel s&#39;est envolé, </p>\n<p>Et sous la voûte éternelle </p>\n<p>Le berger reste isolé; </p>\n<p>Mais, ravi du saint mystère, </p>\n<p>Son coeur murmure tout bas: </p>\n<p>“Si l&#39;ange a quitté la terre, </p>\n<p>Le Sauveur ne sen vas pas!” </p>\n<p>Gloire à Dieu dans les hauts cieux, </p>\n<p>Paix aux hommes en tous lieux! </p>\n<p>Pour cet enfant dans ses langes, </p>\n<p>Pour ce don si précieux, </p>\n<p>Chantons tous, comme les anges: </p>\n<p>Gloire à Dieu dans les hauts cieux! </p>\n','Dans-les-champs-le-berger-veille',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1272,'b35c3a66-ca42-40ea-86f9-83737de9deff',0,0,'Pécheurs, voyez l\'agneau du Père',50,'50_CE_fr','1.\nPécheurs, voyez l\'Agneau du Père\nSur le bois,\nSur le bois de la croix.\nPour nous il rend l\'âme au Calvaire,\nSur le bois,\nSur le bois de la croix.\nJ\'entends son cris d\'agonie,\nJe vois sa souffrance infinie!\nTa mort, Jésus, sera ma vie.\nOui, je crois\nA Jésus mort en croix!  (bis 2 lignes)\n\n2.\nTon sang, Jésus, seul purifie;\nDu pécheur\nTu peux laver le coeur.\nTa voix d\'amour m\'offre la vie;\nDu pécheur\nTu peux laver le coeur.\nDe Golgotha de ta voix crie;\nC\'est pour vous que j\'offre ma vie!\nO Christ!  J\'entends ta voix chérie;\nBon Sauveur,\nJe te donne mon coeur.  (bis 2 lignes)\n\n3.\nAgneau de Dieu, près de ton Père,\nSouviens-toi\nD\'un pécheur tel que moi!\nO Christ!  Souviens-toi du Calvaire;\nSouviens-toi\nD\'un pécheur tel que moi!\nPour moi fut ton agonie;\nPour moi fut ta peine infinie!\nA toi sera toute ma vie;\nO mon Roi!\nO Christ, je suis à toi.  (bis  2 lignes)','<p><strong>1.</strong> </p>\n<p>Pécheurs, voyez l&#39;Agneau du Père </p>\n<p>Sur le bois, </p>\n<p>Sur le bois de la croix. </p>\n<p>Pour nous il rend l&#39;âme au Calvaire, </p>\n<p>Sur le bois, </p>\n<p>Sur le bois de la croix. </p>\n<p>J&#39;entends son cris d&#39;agonie, </p>\n<p>Je vois sa souffrance infinie! </p>\n<p>Ta mort, Jésus, sera ma vie. </p>\n<p>Oui, je crois </p>\n<p>A Jésus mort en croix!  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton sang, Jésus, seul purifie; </p>\n<p>Du pécheur </p>\n<p>Tu peux laver le coeur. </p>\n<p>Ta voix d&#39;amour m&#39;offre la vie; </p>\n<p>Du pécheur </p>\n<p>Tu peux laver le coeur. </p>\n<p>De Golgotha de ta voix crie; </p>\n<p>C&#39;est pour vous que j&#39;offre ma vie! </p>\n<p>O Christ!  J&#39;entends ta voix chérie; </p>\n<p>Bon Sauveur, </p>\n<p>Je te donne mon coeur.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Agneau de Dieu, près de ton Père, </p>\n<p>Souviens-toi </p>\n<p>D&#39;un pécheur tel que moi! </p>\n<p>O Christ!  Souviens-toi du Calvaire; </p>\n<p>Souviens-toi </p>\n<p>D&#39;un pécheur tel que moi! </p>\n<p>Pour moi fut ton agonie; </p>\n<p>Pour moi fut ta peine infinie! </p>\n<p>A toi sera toute ma vie; </p>\n<p>O mon Roi! </p>\n<p>O Christ, je suis à toi.  (bis  2 lignes) </p>\n','Pecheurs-voyez-l\'agneau-du-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1273,'73fb4a70-ceeb-4695-bdb3-5e004ebe2858',0,0,'Christ, immolé sur la colline',55,'55_CE_fr','1.\nChrist, immolé sur la colline\nEt cloué sur la croix,\nMéprisé, couronné d\'épines,\nJe te couronne Roi.\n\nRefrain\nPour me rappeler Gethsémani,\nPour me rappeler ton agonie,\nPour me rappeler ton coeur meurtri\nConduis-moi vers la croix.\n\n2.\nMontre-moi ton tombeau si sombre\nOù les femmes pleuraient,\nOù les anges de Dieu, sans nombre,\nVeillaient et te gardaient.\n\n3.\nQuand au jardin, toujours humide,\nJ\'entre en portant mon don,\nOh!  Montre-moi le tombeau vide\nM\'assurant le pardon.\n\n4.\nPorter ma croix!  Seigneur, que j\'aime\nA le faire pour toi.\nJe veux goûter la coupe même\nQue tu vidas pour moi.','<p><strong>1.</strong> </p>\n<p>Christ, immolé sur la colline </p>\n<p>Et cloué sur la croix, </p>\n<p>Méprisé, couronné d&#39;épines, </p>\n<p>Je te couronne Roi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Pour me rappeler Gethsémani,</strong> </p>\n<p><strong>Pour me rappeler ton agonie,</strong> </p>\n<p><strong>Pour me rappeler ton coeur meurtri</strong> </p>\n<p><strong>Conduis-moi vers la croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Montre-moi ton tombeau si sombre </p>\n<p>Où les femmes pleuraient, </p>\n<p>Où les anges de Dieu, sans nombre, </p>\n<p>Veillaient et te gardaient. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand au jardin, toujours humide, </p>\n<p>J&#39;entre en portant mon don, </p>\n<p>Oh!  Montre-moi le tombeau vide </p>\n<p>M&#39;assurant le pardon. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Porter ma croix!  Seigneur, que j&#39;aime </p>\n<p>A le faire pour toi. </p>\n<p>Je veux goûter la coupe même </p>\n<p>Que tu vidas pour moi. </p>\n','Christ-immole-sur-la-colline',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1274,'3bcc798b-3322-4f98-a19d-a24f932b8e7d',0,0,'Terre, chante de Joie!',42,'42_CE_fr','1.\nTerre, chante de joie!\nCelui que Dieu t\'envoie,\nLe Christ est né!\nOui, chante, ô race humaine!\nC\'est pour briser ta chaîne\nEt pour t\'ouvrir le ciel\nQu\'Il t\'est donné!\n\n2.\nTon Seigneur et ton Maître,\nPour toi, voulut connaître\nLa pauvreté;\nMais rien n\'est comparable\nA ce lieu misérable\nOù rayonne, de Dieu,\nLa charité.\n\n3.\nCette crèche est un temple\nOù la foi te contemple,\nO Roi des rois!\nCar c\'est là que commence\nLe sacrifice immense\nQue tu dois achever\nSur une croix!\n\n4.\nAh! Fais qu\'à ton école,\nLoin du monde frivole,\nEnfant Sauveur\nDans l\'amour qui pardonne,\nS\'humilie et se donne,\nJ\'apprenne le secret\nDu vrai bonheur!','<p><strong>1.</strong> </p>\n<p>Terre, chante de joie! </p>\n<p>Celui que Dieu t&#39;envoie, </p>\n<p>Le Christ est né! </p>\n<p>Oui, chante, ô race humaine! </p>\n<p>C&#39;est pour briser ta chaîne </p>\n<p>Et pour t&#39;ouvrir le ciel </p>\n<p>Qu&#39;Il t&#39;est donné! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton Seigneur et ton Maître, </p>\n<p>Pour toi, voulut connaître </p>\n<p>La pauvreté; </p>\n<p>Mais rien n&#39;est comparable </p>\n<p>A ce lieu misérable </p>\n<p>Où rayonne, de Dieu, </p>\n<p>La charité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Cette crèche est un temple </p>\n<p>Où la foi te contemple, </p>\n<p>O Roi des rois! </p>\n<p>Car c&#39;est là que commence </p>\n<p>Le sacrifice immense </p>\n<p>Que tu dois achever </p>\n<p>Sur une croix! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ah! Fais qu&#39;à ton école, </p>\n<p>Loin du monde frivole, </p>\n<p>Enfant Sauveur </p>\n<p>Dans l&#39;amour qui pardonne, </p>\n<p>S&#39;humilie et se donne, </p>\n<p>J&#39;apprenne le secret </p>\n<p>Du vrai bonheur! </p>\n','Terre-chante-de-Joie!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1275,'e846f73b-ed56-42c4-bd80-2096f5f14498',0,0,'Mortels, voulez-vous savoir',52,'52_CE_fr','1.\nMortels, voulez-vous savoir\nQuel est mon unique espoir,\nMa sagesse et ma science,\nMon trésor, ma récompense?  (bis)\nC\'est Jésus crucifié.\nC\'est Jésus,  (bis)\nC\'est Jésus crucifié.\n\n2.\nQuelle est l\'ancre de ma foi?\nDe mon coeur, quelle est la loi?\nQuel est mon seul sacrifice,\nMa sainteté, ma justice?  (bis)\nC\'est Jésus crucifié.\nC\'est Jésus,  (bis)\nC\'est Jésus crucifié.\n\n3.\nDe mon esprit languissant,\nQui fait cesser le tourment?\nDans la peine et la souffrance,\nQui soutient ma confiance?  (bis)\nC\'est Jésus crucifié.\nC\'est Jésus,  (bis)\nC\'est Jésus crucifié.\n\n4.\nQuel est celui don\'t la mort\nMe prépare un nouveau sort?\nQuel est cet Ami fidèle\nQui sans cesse à Lui m\'appelle?  (bis)\nC\'est Jésus glorifié.\nC\'est Jésus,  (bis)\nC\'est Jésus glorifié.','<p><strong>1.</strong> </p>\n<p>Mortels, voulez-vous savoir </p>\n<p>Quel est mon unique espoir, </p>\n<p>Ma sagesse et ma science, </p>\n<p>Mon trésor, ma récompense?  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p>C&#39;est Jésus,  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quelle est l&#39;ancre de ma foi? </p>\n<p>De mon coeur, quelle est la loi? </p>\n<p>Quel est mon seul sacrifice, </p>\n<p>Ma sainteté, ma justice?  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p>C&#39;est Jésus,  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>De mon esprit languissant, </p>\n<p>Qui fait cesser le tourment? </p>\n<p>Dans la peine et la souffrance, </p>\n<p>Qui soutient ma confiance?  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p>C&#39;est Jésus,  (bis) </p>\n<p>C&#39;est Jésus crucifié. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quel est celui don&#39;t la mort </p>\n<p>Me prépare un nouveau sort? </p>\n<p>Quel est cet Ami fidèle </p>\n<p>Qui sans cesse à Lui m&#39;appelle?  (bis) </p>\n<p>C&#39;est Jésus glorifié. </p>\n<p>C&#39;est Jésus,  (bis) </p>\n<p>C&#39;est Jésus glorifié. </p>\n','Mortels-voulez-vous-savoir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1276,'1a5d7479-d896-460c-b699-1b13f5692c46',0,0,'Jésus est né! Venez, bergers et mages',44,'44_CE_fr','1.\nJésus est né!  Venez, bergers et Mages;\nAnges du ciel, portez-lui vos hommages;\nOui, gloire aux cieux!  Paix en tous lieux!\n\n2.\nVoilà l\'enfant qui doit sauver le monde;\nQuel doux éclat et quelle paix profonde\nRayonne autour – Du Dieu d\'amour.\n\n3.\nIl a voulu, pour notre délivrance,\nNaître ignoré, pauvre et sans apparence;\nHumble aussi, allons à Lui.\n\n4.\nTout ce qui plaît à ce Roi débonnaire,\nC\'est un coeur pur, formé par la prière;\nA toi ce coeur, - O bon Sauveur!\n\n5.\nA toi ce coeur, et qu\'il te glorifie,\nNon pour un jour, mais pour toute la vie;\nIl est à toi, - Sois-en le Roi!','<p><strong>1.</strong> </p>\n<p>Jésus est né!  Venez, bergers et Mages; </p>\n<p>Anges du ciel, portez-lui vos hommages; </p>\n<p>Oui, gloire aux cieux!  Paix en tous lieux! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voilà l&#39;enfant qui doit sauver le monde; </p>\n<p>Quel doux éclat et quelle paix profonde </p>\n<p>Rayonne autour – Du Dieu d&#39;amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il a voulu, pour notre délivrance, </p>\n<p>Naître ignoré, pauvre et sans apparence; </p>\n<p>Humble aussi, allons à Lui. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tout ce qui plaît à ce Roi débonnaire, </p>\n<p>C&#39;est un coeur pur, formé par la prière; </p>\n<p>A toi ce coeur, - O bon Sauveur! </p>\n<p><br/> <strong>5.</strong></p>\n<p>A toi ce coeur, et qu&#39;il te glorifie, </p>\n<p>Non pour un jour, mais pour toute la vie; </p>\n<p>Il est à toi, - Sois-en le Roi! </p>\n','Jesus-est-ne!-Venez-bergers-et-mages',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1277,'7b25edc3-2ad1-4642-af85-761625231309',0,0,'J\'ai découvert dans la vallée',47,'47_CE_fr','1.\nJ\'ai découvert dans la vallée,\nOù j\'avançais le front penché,\nUne humble fleur dissimulée\nDans le feuillage desséché.\n\nRefrain\nCéleste Fleur que Dieu fit naître\nPour consoler l\'humanité,\nFils de Marie, humble et doux Maître,\n(1)Revêts-nous de ta pureté!\n(2)Enseignes-nous l\'humilité!\n(3)Remplis-nous de ta charité!\n(4)Tu donnes l\'immortalité!\n\n2.\nA genoux parmi les épines\nJe cueillis la suave fleur,\nCar toujours les grâces divines\nS\'offrent à nous dans la douleur.\n\n3.\nJ\'emportai la plante admirable;\nMon coeur en fut tout parfumé,\nEt dans mon logis misérable,\nPar elle tout fut transformé.\n\n4.\nJe la garde, elle est toujours belle,\nEt rien ne saura la flétrir.\nJ\'irai dans la tombe avec elle:\nAvec elle on ne peut mourir.','<p><strong>1.</strong> </p>\n<p>J&#39;ai découvert dans la vallée, </p>\n<p>Où j&#39;avançais le front penché, </p>\n<p>Une humble fleur dissimulée </p>\n<p>Dans le feuillage desséché. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Céleste Fleur que Dieu fit naître</strong> </p>\n<p><strong>Pour consoler l&#39;humanité,</strong> </p>\n<p><strong>Fils de Marie, humble et doux Maître,</strong> </p>\n<p><strong>(1)Revêts-nous de ta pureté!</strong> </p>\n<p><strong>(2)Enseignes-nous l&#39;humilité!</strong> </p>\n<p><strong>(3)Remplis-nous de ta charité!</strong> </p>\n<p><strong>(4)Tu donnes l&#39;immortalité!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A genoux parmi les épines </p>\n<p>Je cueillis la suave fleur, </p>\n<p>Car toujours les grâces divines </p>\n<p>S&#39;offrent à nous dans la douleur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;emportai la plante admirable; </p>\n<p>Mon coeur en fut tout parfumé, </p>\n<p>Et dans mon logis misérable, </p>\n<p>Par elle tout fut transformé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je la garde, elle est toujours belle, </p>\n<p>Et rien ne saura la flétrir. </p>\n<p>J&#39;irai dans la tombe avec elle: </p>\n<p>Avec elle on ne peut mourir. </p>\n','J\'ai-decouvert-dans-la-vallee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1278,'962a0f45-65aa-408a-8e6e-609b7d43c126',0,0,'Béni soit le jour où j’ai fais',23,'23_CE_fr','1.\nBéni soit le jour où j\'ai fait\nChoix de Jésus pour Maître;\nJe veux célébrer le bienfait\nQui vient de m\'apparaître.\nHeureux jour  (bis)\nOù j\'ai connu tout son amour!\nSauvé par son divin secours,\nJe me donne à Lui pour toujours,\nHeureux jour  (bis)\nOù j\'ai connu tout son amour.\n\n2.\nO divin compassion!\nLe bien-aimé du Père\nLui-même a payé ma rançon\nSur la croix du Calvaire.\nDe la mort  (bis)\nJésus-Christ a vaincu l\'effort.\nMon âme accepte avec transport\nLe salut qu\'offre le Dieu fort.\nDe la mort.  (bis)\nJésus-Christ a vaincu l\'effort.\n\n3.\nDans son amour, Il m\'a cherché\nQuand je fuyais sa face;\nIl s\'est chargé de mon péché,\nPar Lui j\'ai trouvé grâce.\nTout joyeux  (bis)\nJe veux le redire en tout lieux;\nMes chants monteront jusqu\'au cieux\nS\'unir au chants des bienheureux,\nTout joyeux  (bis)\nJe veux le redire en tout lieux.\n\n4.\nMaintenant, à ton Rédempteur,\nConsacré sans partage,\nRepose près de Lui, mon coeur,\nA l\'abri de l\'orage,\nSans regrets,  (bis)\nLoin du monde et de ses attraits,\nGoûtant le véritable paix\nQui vient combler tous mes souhaits;\nSans regrets,  (bis)\nLoin de ce qu\'autrefois j\'aimais.','<p><strong>1.</strong> </p>\n<p>Béni soit le jour où j&#39;ai fait </p>\n<p>Choix de Jésus pour Maître; </p>\n<p>Je veux célébrer le bienfait </p>\n<p>Qui vient de m&#39;apparaître. </p>\n<p>Heureux jour  (bis) </p>\n<p>Où j&#39;ai connu tout son amour! </p>\n<p>Sauvé par son divin secours, </p>\n<p>Je me donne à Lui pour toujours, </p>\n<p>Heureux jour  (bis) </p>\n<p>Où j&#39;ai connu tout son amour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>O divin compassion! </p>\n<p>Le bien-aimé du Père </p>\n<p>Lui-même a payé ma rançon </p>\n<p>Sur la croix du Calvaire. </p>\n<p>De la mort  (bis) </p>\n<p>Jésus-Christ a vaincu l&#39;effort. </p>\n<p>Mon âme accepte avec transport </p>\n<p>Le salut qu&#39;offre le Dieu fort. </p>\n<p>De la mort.  (bis) </p>\n<p>Jésus-Christ a vaincu l&#39;effort. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans son amour, Il m&#39;a cherché </p>\n<p>Quand je fuyais sa face; </p>\n<p>Il s&#39;est chargé de mon péché, </p>\n<p>Par Lui j&#39;ai trouvé grâce. </p>\n<p>Tout joyeux  (bis) </p>\n<p>Je veux le redire en tout lieux; </p>\n<p>Mes chants monteront jusqu&#39;au cieux </p>\n<p>S&#39;unir au chants des bienheureux, </p>\n<p>Tout joyeux  (bis) </p>\n<p>Je veux le redire en tout lieux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Maintenant, à ton Rédempteur, </p>\n<p>Consacré sans partage, </p>\n<p>Repose près de Lui, mon coeur, </p>\n<p>A l&#39;abri de l&#39;orage, </p>\n<p>Sans regrets,  (bis) </p>\n<p>Loin du monde et de ses attraits, </p>\n<p>Goûtant le véritable paix </p>\n<p>Qui vient combler tous mes souhaits; </p>\n<p>Sans regrets,  (bis) </p>\n<p>Loin de ce qu&#39;autrefois j&#39;aimais. </p>\n','Beni-soit-le-jour-ou-j\'ai-fais',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1279,'1e680e8f-f9cf-4fa2-bfef-9d572e91927c',0,0,'À la croix où mourut mon Sauveur',54,'54_CE_fr','1.\nA la croix où mourut mon Sauveur,\nJe suis venu, brisé de douleur;\nLà son sang purifia mon coeur:\nA son nom la gloire!  (ter)\nLà son sang purifia mon coeur:\nA son nom la gloire!\n\n2.\nQuelle merveille! Il vint me sauver\nQuand de sa croix je me fus chargé,\nEn moi Jésus vint pour demeurer:\nA son nom la gloire!  (ter)\nEn moi Jésus vint pour demeurer:\nA son nom la gloire!\n\n3.\nO fontaine effaçant le péché,\nDonnant la vie au coeur desséché!\nLà, Jésus me retient attaché:\nA son nom la gloire!  (ter)\nLà, Jésus me retient attaché:\nA son nom la gloire!\n\n4.\nPauvre âme, viens aux pieds du Sauveur,\nA la source ouverte à tout pécheur,\nViens t\'y plonger, trouver le bonheur:\nA son nom la gloire!  (bis)\nViens t\'y plonger, trouver le bonheur:\nA son nom la gloire!','<p><strong>1.</strong> </p>\n<p>A la croix où mourut mon Sauveur, </p>\n<p>Je suis venu, brisé de douleur; </p>\n<p>Là son sang purifia mon coeur: </p>\n<p>A son nom la gloire!  (ter) </p>\n<p>Là son sang purifia mon coeur: </p>\n<p>A son nom la gloire! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quelle merveille! Il vint me sauver </p>\n<p>Quand de sa croix je me fus chargé, </p>\n<p>En moi Jésus vint pour demeurer: </p>\n<p>A son nom la gloire!  (ter) </p>\n<p>En moi Jésus vint pour demeurer: </p>\n<p>A son nom la gloire! </p>\n<p><br/> <strong>3.</strong></p>\n<p>O fontaine effaçant le péché, </p>\n<p>Donnant la vie au coeur desséché! </p>\n<p>Là, Jésus me retient attaché: </p>\n<p>A son nom la gloire!  (ter) </p>\n<p>Là, Jésus me retient attaché: </p>\n<p>A son nom la gloire! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pauvre âme, viens aux pieds du Sauveur, </p>\n<p>A la source ouverte à tout pécheur, </p>\n<p>Viens t&#39;y plonger, trouver le bonheur: </p>\n<p>A son nom la gloire!  (bis) </p>\n<p>Viens t&#39;y plonger, trouver le bonheur: </p>\n<p>A son nom la gloire! </p>\n','A-la-croix-ou-mourut-mon-Sauveur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1280,'a054e9d6-7532-42c2-adab-c061f32f1113',0,0,'Apporte sur le calvaire',64,'64_CE_fr','1.\nApporte sur la Calvaire\nTes pesants fardeaux,\nLà, tu trouveras, mon frère,\nLe repos.\n\n2.\n“C\'est moi, c\'est moi qui console!”\nQuelle est cette voix?\nD\'où viens-tu, douce parole?\n“De la croix!”\n\n3.\nRegarde ma main percée,\nMon côté sanglant,\nPour toi, pauvre âme angoissée,\nCoeur tremblant!”\n\n4.\nVoilant mes splendeurs divines\nSous l\'horrible affront,\nPour toi saignent les épines\nSur mon front!”\n\n5.\nAh!  Quand même, pour te suivre,\nIl faille souffrir,\nPour toi, Sauveur, je veux vivre\nEt mourir!\n\n6.\nCar tu soutiendras ma tête\nDans les grandes aux,\nM\'ouvrant, après la tempête,\nLe repos.','<p><strong>1.</strong> </p>\n<p>Apporte sur la Calvaire </p>\n<p>Tes pesants fardeaux, </p>\n<p>Là, tu trouveras, mon frère, </p>\n<p>Le repos. </p>\n<p><br/> <strong>2.</strong></p>\n<p>“C&#39;est moi, c&#39;est moi qui console!” </p>\n<p>Quelle est cette voix? </p>\n<p>D&#39;où viens-tu, douce parole? </p>\n<p>“De la croix!” </p>\n<p><br/> <strong>3.</strong></p>\n<p>Regarde ma main percée, </p>\n<p>Mon côté sanglant, </p>\n<p>Pour toi, pauvre âme angoissée, </p>\n<p>Coeur tremblant!” </p>\n<p><br/> <strong>4.</strong></p>\n<p>Voilant mes splendeurs divines </p>\n<p>Sous l&#39;horrible affront, </p>\n<p>Pour toi saignent les épines </p>\n<p>Sur mon front!” </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ah!  Quand même, pour te suivre, </p>\n<p>Il faille souffrir, </p>\n<p>Pour toi, Sauveur, je veux vivre </p>\n<p>Et mourir! </p>\n<p><br/> <strong>6.</strong></p>\n<p>Car tu soutiendras ma tête </p>\n<p>Dans les grandes aux, </p>\n<p>M&#39;ouvrant, après la tempête, </p>\n<p>Le repos. </p>\n','Apporte-sur-le-calvaire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1281,'f143cd96-41fb-4145-a6ad-aaf180a0cc8e',0,0,'Il Va Venir, Le Seigneur Que J’adore',100,'100_CE_fr','1.\nIl va venir, le Seigneur que j\'adore;\nBientôt sa voix retentira du ciel.\nA mes regards l\'horizon se colore\nDes purs rayons du soleil éternel.\n\nRefrain\nGloire à toi seul, Seigneur Jésus!\nGloire à jamais, gloire à ton nom!\nDe ton beau ciel je ne sortirai plus\nEt pour toujours, j\'exalterai ton nom!\n\n2.\nQu\'autour de moi s\'élève la tempête,\nJe suis en paix, car tu veilles sur moi;\nJe puis, ô mon Sauveur, lever la tête,\nTu vas venir me prendre auprès de toi.\n\n3.\nMaison du Père, où ton amour m\'appelle,\nSaint Cité, demeure des élus,\nLes lieux qu\'éclaire une gloire éternelle\nMe sont ouverts par toi-même, ô Jésus!','<p><strong>1.</strong> </p>\n<p>Il va venir, le Seigneur que j&#39;adore; </p>\n<p>Bientôt sa voix retentira du ciel. </p>\n<p>A mes regards l&#39;horizon se colore </p>\n<p>Des purs rayons du soleil éternel. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à toi seul, Seigneur Jésus!</strong> </p>\n<p><strong>Gloire à jamais, gloire à ton nom!</strong> </p>\n<p><strong>De ton beau ciel je ne sortirai plus</strong> </p>\n<p><strong>Et pour toujours, j&#39;exalterai ton nom!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;autour de moi s&#39;élève la tempête, </p>\n<p>Je suis en paix, car tu veilles sur moi; </p>\n<p>Je puis, ô mon Sauveur, lever la tête, </p>\n<p>Tu vas venir me prendre auprès de toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Maison du Père, où ton amour m&#39;appelle, </p>\n<p>Saint Cité, demeure des élus, </p>\n<p>Les lieux qu&#39;éclaire une gloire éternelle </p>\n<p>Me sont ouverts par toi-même, ô Jésus! </p>\n','Il-Va-Venir-Le-Seigneur-Que-J\'adore',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1282,'ffa4c344-de6b-4853-ace8-91e3d975886d',0,0,'Jésus, par ton sang précieux',65,'65_CE_fr','1.\nJésus, par ton sang précieux,\nEnlève mon iniquité!\nRegarde-moi du haut des cieux,\nDis-moi que tu m\'as pardonné…\nJ\'ai longtemps erré, coeur rebelle,\nMais j\'entends ta voix qui m\'appelle,\nAu pied de ta croix maintenant,\nTout confus, brisé, je me rends.\nBlanc, plus blanc que neige,  (bis)\nLavé dans le sang de l\'Agneau,\nJe serai plus blanc que la neige!\n\n2.\nOh!  Le fardeau de mon péché,\nDieu très saint, est trop grand pour moi!\nJe veux en être délivré,\nA cette heure, oh!  Révèle-moi!\nJésus, viens, sois ma délivrance,\nSeul tu peux calmer ma souffrance!\nAu pied de ta croix maintenant,\nTout confus, brisé, je me rends.\nBlanc, plus blanc que neige,  (bis)\nLavé dans le sang de l\'Agneau,\nJe serai plus blanc que la neige!\n\n3.\nOh!  Jésus, ton sang précieux\nA lavé mon iniquité,\nOui, tu m\'as répondu des cieux,\nTon amour m\'a tout pardonné!\nJe te contemple et puis croire\nQu\'en toi j\'ai complète victoire…\nAu pied de ta croix maintenant,\nJe me relève triomphant!\nBlanc, plus blanc que neige,  (bis)\nLavé dans le sang de l\'Agneau,\nMon coeur est plus blanc que la neige!','<p><strong>1.</strong> </p>\n<p>Jésus, par ton sang précieux, </p>\n<p>Enlève mon iniquité! </p>\n<p>Regarde-moi du haut des cieux, </p>\n<p>Dis-moi que tu m&#39;as pardonné… </p>\n<p>J&#39;ai longtemps erré, coeur rebelle, </p>\n<p>Mais j&#39;entends ta voix qui m&#39;appelle, </p>\n<p>Au pied de ta croix maintenant, </p>\n<p>Tout confus, brisé, je me rends. </p>\n<p>Blanc, plus blanc que neige,  (bis) </p>\n<p>Lavé dans le sang de l&#39;Agneau, </p>\n<p>Je serai plus blanc que la neige! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh!  Le fardeau de mon péché, </p>\n<p>Dieu très saint, est trop grand pour moi! </p>\n<p>Je veux en être délivré, </p>\n<p>A cette heure, oh!  Révèle-moi! </p>\n<p>Jésus, viens, sois ma délivrance, </p>\n<p>Seul tu peux calmer ma souffrance! </p>\n<p>Au pied de ta croix maintenant, </p>\n<p>Tout confus, brisé, je me rends. </p>\n<p>Blanc, plus blanc que neige,  (bis) </p>\n<p>Lavé dans le sang de l&#39;Agneau, </p>\n<p>Je serai plus blanc que la neige! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh!  Jésus, ton sang précieux </p>\n<p>A lavé mon iniquité, </p>\n<p>Oui, tu m&#39;as répondu des cieux, </p>\n<p>Ton amour m&#39;a tout pardonné! </p>\n<p>Je te contemple et puis croire </p>\n<p>Qu&#39;en toi j&#39;ai complète victoire… </p>\n<p>Au pied de ta croix maintenant, </p>\n<p>Je me relève triomphant! </p>\n<p>Blanc, plus blanc que neige,  (bis) </p>\n<p>Lavé dans le sang de l&#39;Agneau, </p>\n<p>Mon coeur est plus blanc que la neige! </p>\n','Jesus-par-ton-sang-precieux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1283,'24ae2e58-ce35-4470-9fa6-a82ff9e07581',0,0,'Lorsque dans ma souillure',76,'76_CE_fr','1.\nLorsque dans ma souillure\nSans bonheur et sans paix\nDans une voie impure\nLoin de Dieu je marchais\nTout l\'amour du Calvaire\nA moi s\'est révélé\nTerrassant l\'adversaire\nBrisant mon cœur souillé\n\n\nRefrain\nL\'amour de Jésus m\'est doux\nChaque jour il m\'est plus doux\nAmour qui me presse\nD\'inviter sans cesse\nTous au divin rendez-vous\n\n2.\nCet amour fort et tendre\nDe mon divin Sauveur\nOh! Je le veux répandre\nPartout avec ardeur\nParmi tous ceux dont l\'âme\nLutte et soupire encore\nSous l\'esclavage infâme\nDu péché, de la mort\n\n3.\nQuand la foi, l\'espérance\nPasseront pour jamais\nDe Christ, l\'amour immense\nNous remplira de paix\nObjets de sa tendresse\nLes phalanges d\'élus\nRépéteront sans cesse\nTout l\'amour de Jésus','<p><strong>1.</strong> </p>\n<p>Lorsque dans ma souillure </p>\n<p>Sans bonheur et sans paix </p>\n<p>Dans une voie impure </p>\n<p>Loin de Dieu je marchais </p>\n<p>Tout l&#39;amour du Calvaire </p>\n<p>A moi s&#39;est révélé </p>\n<p>Terrassant l&#39;adversaire </p>\n<p>Brisant mon cœur souillé </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>L&#39;amour de Jésus m&#39;est doux</strong> </p>\n<p><strong>Chaque jour il m&#39;est plus doux</strong> </p>\n<p><strong>Amour qui me presse</strong> </p>\n<p><strong>D&#39;inviter sans cesse</strong> </p>\n<p><strong>Tous au divin rendez-vous</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Cet amour fort et tendre </p>\n<p>De mon divin Sauveur </p>\n<p>Oh! Je le veux répandre </p>\n<p>Partout avec ardeur </p>\n<p>Parmi tous ceux dont l&#39;âme </p>\n<p>Lutte et soupire encore </p>\n<p>Sous l&#39;esclavage infâme </p>\n<p>Du péché, de la mort </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand la foi, l&#39;espérance </p>\n<p>Passeront pour jamais </p>\n<p>De Christ, l&#39;amour immense </p>\n<p>Nous remplira de paix </p>\n<p>Objets de sa tendresse </p>\n<p>Les phalanges d&#39;élus </p>\n<p>Répéteront sans cesse </p>\n<p>Tout l&#39;amour de Jésus </p>\n','Lorsque-dans-ma-souillure',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1284,'df30f153-ef64-4a79-947a-074d595ab2d6',0,0,'A toi la gloire, Ô ressuscité',68,'68_CE_fr','1.\nA toi la gloire,\nO ressuscite!\nA toi la victoire\nPour l\'éternité!\nBrillant de lumière,\nL\'ange est descendu,\nIl roule la pierre\nDu tombeau vaincu.\n\nRefrain\nA toi la gloire, - O ressuscité!\nA toi la victoire – Pour l\'éternité!\n\n2.\nVois-le paraître!\nC\'est lui, c\'est Jésus,\nTon Sauveur, ton maître!\nOh!  Ne doute plus!\nSois dans l\'allégresse,\nPeuple du Seigneur,\nEt redis sans cesse\nQue Christ est vainqueur.\n\n3.\nCraindrais-je encore?\nIl vit à jamais,\nCelui que j\'adore,\nLe Prince de paix;\nIl est ma victoire,\nMon puissant soutien,\nMa vie et ma gloire:\nNon, je ne crains rien.','<p><strong>1.</strong> </p>\n<p>A toi la gloire, </p>\n<p>O ressuscite! </p>\n<p>A toi la victoire </p>\n<p>Pour l&#39;éternité! </p>\n<p>Brillant de lumière, </p>\n<p>L&#39;ange est descendu, </p>\n<p>Il roule la pierre </p>\n<p>Du tombeau vaincu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>A toi la gloire, - O ressuscité!</strong> </p>\n<p><strong>A toi la victoire – Pour l&#39;éternité!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois-le paraître! </p>\n<p>C&#39;est lui, c&#39;est Jésus, </p>\n<p>Ton Sauveur, ton maître! </p>\n<p>Oh!  Ne doute plus! </p>\n<p>Sois dans l&#39;allégresse, </p>\n<p>Peuple du Seigneur, </p>\n<p>Et redis sans cesse </p>\n<p>Que Christ est vainqueur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Craindrais-je encore? </p>\n<p>Il vit à jamais, </p>\n<p>Celui que j&#39;adore, </p>\n<p>Le Prince de paix; </p>\n<p>Il est ma victoire, </p>\n<p>Mon puissant soutien, </p>\n<p>Ma vie et ma gloire: </p>\n<p>Non, je ne crains rien. </p>\n','A-toi-la-gloire-O-ressuscite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1285,'f9588dbf-c1c1-4687-8b0d-bb745f3fe063',0,0,'Jésus est notre ami suprême',75,'75_CE_fr','1.\nJésus est notre Ami suprême;\nOh!  Quel amour!\nMieux qu\'un tendre frère, il nous aime;\nOh!  Quel amour!\nIci parents, amis, tout passes;\nLe bonheur paraît et s\'efface;\nSon coeur seul jamais ne se lasse…\nOh!  Quel amour!\n\n2.\nIl est notre vie éternelle;\nOh!  Quel amour!\nCélébrons son oeuvre immortelle;\nOh!  Quel amour!\nPar son sang, notre âme est lavée;\nAu désert il l\'avait trouvée;\nDans son bercail Il l\'a sauvée;\nOh!  Quel amour!\n\n3.\nSeigneur Jésus, fais-nous comprendre\nTout ton amour!\nDans nos coeurs, oh! Daigne répandre\nTout ton amour!\nQue cet amour soit notre vie!\nQu\'à jamais notre âme ravie\nSavoure une joie infinie\nEn ton amour!','<p><strong>1.</strong> </p>\n<p>Jésus est notre Ami suprême; </p>\n<p>Oh!  Quel amour! </p>\n<p>Mieux qu&#39;un tendre frère, il nous aime; </p>\n<p>Oh!  Quel amour! </p>\n<p>Ici parents, amis, tout passes; </p>\n<p>Le bonheur paraît et s&#39;efface; </p>\n<p>Son coeur seul jamais ne se lasse… </p>\n<p>Oh!  Quel amour! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il est notre vie éternelle; </p>\n<p>Oh!  Quel amour! </p>\n<p>Célébrons son oeuvre immortelle; </p>\n<p>Oh!  Quel amour! </p>\n<p>Par son sang, notre âme est lavée; </p>\n<p>Au désert il l&#39;avait trouvée; </p>\n<p>Dans son bercail Il l&#39;a sauvée; </p>\n<p>Oh!  Quel amour! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur Jésus, fais-nous comprendre </p>\n<p>Tout ton amour! </p>\n<p>Dans nos coeurs, oh! Daigne répandre </p>\n<p>Tout ton amour! </p>\n<p>Que cet amour soit notre vie! </p>\n<p>Qu&#39;à jamais notre âme ravie </p>\n<p>Savoure une joie infinie </p>\n<p>En ton amour! </p>\n','Jesus-est-notre-ami-supreme',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1286,'e3f19160-2433-410d-b3c2-c9f90c5a103e',0,0,'Il Me Conduit, Douce Pensée',89,'89_CE_fr','1.\nIl me conduit, douce pensée!\nRepos pour mon âme lassée!\nEn tous lieux son regard me suit,\nEt par la main, Il me conduit.\n\nRefrain\nIl me conduit, Il me conduit!\nDésormais pour Lui je veux vivre;\nBrebis fidèle, je veux suivre\nLe Bon Berger qui me conduit.\n\n2.\nJésus sur moi veille sans cesse;\nDans la joie et dans la tristesse,\nDans le jour comme dans la nuit,\nPas à pas sa main me conduit.\n\n3.\nComme un rempart, Il me protège,\nIl me préserve de tout piège;\nLoin de moi l\'ennemi s\'enfuit:\nQuand par sa main Christ me conduit.\n\n4.\nQuand mon heure sera sonnée,\nMon oeuvre ici-bas terminée,\nJe dirai, dans la sombre nuit:\nJe ne crains point, Il me conduit!','<p><strong>1.</strong> </p>\n<p>Il me conduit, douce pensée! </p>\n<p>Repos pour mon âme lassée! </p>\n<p>En tous lieux son regard me suit, </p>\n<p>Et par la main, Il me conduit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il me conduit, Il me conduit!</strong> </p>\n<p><strong>Désormais pour Lui je veux vivre;</strong> </p>\n<p><strong>Brebis fidèle, je veux suivre</strong> </p>\n<p><strong>Le Bon Berger qui me conduit.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus sur moi veille sans cesse; </p>\n<p>Dans la joie et dans la tristesse, </p>\n<p>Dans le jour comme dans la nuit, </p>\n<p>Pas à pas sa main me conduit. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme un rempart, Il me protège, </p>\n<p>Il me préserve de tout piège; </p>\n<p>Loin de moi l&#39;ennemi s&#39;enfuit: </p>\n<p>Quand par sa main Christ me conduit. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand mon heure sera sonnée, </p>\n<p>Mon oeuvre ici-bas terminée, </p>\n<p>Je dirai, dans la sombre nuit: </p>\n<p>Je ne crains point, Il me conduit! </p>\n','Il-Me-Conduit-Douce-Pensee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1287,'2a37587d-ce89-47bc-a846-81524a080a85',0,0,'Redites-moi l’histoire',85,'85_CE_fr','1.\nRedites-moi l\'histoire\nDe l\'amour de Jésus;\nParlez-moi de la gloire\nQu\'il promet aux élus.\nJ\'ai besoin qu\'on m\'instruise,\nCar je suis ignorant,\nQu\'à Christ on me conduise\nComme un petit enfant.\n\nRefrain\nRedites-moi l\'histoire  (ter)\nDe l\'amour de Jésus!\n\n2.\nRedites-moi l\'histoire\nDe la crèche à la croix;\nÉveillez ma mémoire,\nOublieuse parfois.\nCette histoire si belle,\nDites-la simplement;\nElle est toujours nouvelle;\nRépétez-la souvent.\n\n3.\nRedites-moi l\'histoire\nDe mon divin Sauveur;\nC\'est Lui dont la victoire\nAffranchit le pécheur.\nCe glorieux message,\nOh! Redites-le moi,\nLorsque je perds courage,\nLorsque faiblis ma foi.\n\n4.\nRedites-moi l\'histoire,\nQuand le monde trompeur\nMe vend sa vaine gloire\nAu prix de mon bonheur.\nEt quand, loin de la terre,\nJe prendrai mon essor,\nEn fermant la paupière,\nQue je l\'entende encore!','<p><strong>1.</strong> </p>\n<p>Redites-moi l&#39;histoire </p>\n<p>De l&#39;amour de Jésus; </p>\n<p>Parlez-moi de la gloire </p>\n<p>Qu&#39;il promet aux élus. </p>\n<p>J&#39;ai besoin qu&#39;on m&#39;instruise, </p>\n<p>Car je suis ignorant, </p>\n<p>Qu&#39;à Christ on me conduise </p>\n<p>Comme un petit enfant. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Redites-moi l&#39;histoire  (ter)</strong> </p>\n<p><strong>De l&#39;amour de Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Redites-moi l&#39;histoire </p>\n<p>De la crèche à la croix; </p>\n<p>Éveillez ma mémoire, </p>\n<p>Oublieuse parfois. </p>\n<p>Cette histoire si belle, </p>\n<p>Dites-la simplement; </p>\n<p>Elle est toujours nouvelle; </p>\n<p>Répétez-la souvent. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Redites-moi l&#39;histoire </p>\n<p>De mon divin Sauveur; </p>\n<p>C&#39;est Lui dont la victoire </p>\n<p>Affranchit le pécheur. </p>\n<p>Ce glorieux message, </p>\n<p>Oh! Redites-le moi, </p>\n<p>Lorsque je perds courage, </p>\n<p>Lorsque faiblis ma foi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Redites-moi l&#39;histoire, </p>\n<p>Quand le monde trompeur </p>\n<p>Me vend sa vaine gloire </p>\n<p>Au prix de mon bonheur. </p>\n<p>Et quand, loin de la terre, </p>\n<p>Je prendrai mon essor, </p>\n<p>En fermant la paupière, </p>\n<p>Que je l&#39;entende encore! </p>\n','Redites-moi-l\'histoire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1288,'9110ebe4-39bc-4840-86e1-0dbefb6d05fc',0,0,'Saint-Esprit, que ta clarté',110,'110_CE_fr','1.\nSaint-Esprit, que ta clarté\nVienne briller sur nos coeurs,\nEt que ses rayons vainqueurs\nChassent toute obscurité.\n\n2.\nSaint-Esprit, ô vérité!\nGuide infaillible et divin,\nMontre-nous le droit chemin,\nCourbe notre volonté.\n\n3.\nSaint-Esprit, Consolateur,\nViens calmer tous nos effrois.\nApaise, à ta douce voix,\nLa révolte et la douleur.\n\n4.\nSaint-Esprit, ô Toi qui sais\nCombien chancellent nos pas;\nNe nous abandonne pas,\nEt conduis-nous dans la paix.\n\n5.\nSaint-Esprit, Libérateur,\nDon\'t la puissance est pour tous,\nÀ jamais délivre-nous\nDes liens de l\'oppresseur.','<p><strong>1.</strong> </p>\n<p>Saint-Esprit, que ta clarté </p>\n<p>Vienne briller sur nos coeurs, </p>\n<p>Et que ses rayons vainqueurs </p>\n<p>Chassent toute obscurité. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Saint-Esprit, ô vérité! </p>\n<p>Guide infaillible et divin, </p>\n<p>Montre-nous le droit chemin, </p>\n<p>Courbe notre volonté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Saint-Esprit, Consolateur, </p>\n<p>Viens calmer tous nos effrois. </p>\n<p>Apaise, à ta douce voix, </p>\n<p>La révolte et la douleur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Saint-Esprit, ô Toi qui sais </p>\n<p>Combien chancellent nos pas; </p>\n<p>Ne nous abandonne pas, </p>\n<p>Et conduis-nous dans la paix. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Saint-Esprit, Libérateur, </p>\n<p>Don&#39;t la puissance est pour tous, </p>\n<p>À jamais délivre-nous </p>\n<p>Des liens de l&#39;oppresseur. </p>\n','Saint-Esprit-que-ta-clarte',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1289,'0cbd90cd-ace0-4661-81a6-1094088e82a5',0,0,'Je lève les yeux vers les monts que j’aime',122,'122_CE_fr','1.\nJe lève les yeux vers les monts que j\'aime,\nD\'où peut me venir ici le secours?\nLe secours me viens de l\'Éternel même,\nDu Dieu qui créa les nuits et les jours.\n\n2.\nPourra-t-il souffrir que ton pied chancelle?\nTon gardien peut-il sommeiller jamais?\nNon, il ne dort pas, le gardien fidèle,\nCelui qui maintient Israël en paix.\n\n3.\nPour toi, l\'Éternel est une retraite;\nIl te sert à droite et d\'ombre et d\'appui,\nLe soleil ne peut frapper sur ta tête,\nNi la lune à l\'heure où le jour a fui.\n\n4.\nIl te gardera de tout mal possible;\nIl garde ton âme, il garde tes jours;\nIl te gardera!  Rentre ou sors paisible!\nL\'Éternel sur toi veillera toujours.','<p><strong>1.</strong> </p>\n<p>Je lève les yeux vers les monts que j&#39;aime, </p>\n<p>D&#39;où peut me venir ici le secours? </p>\n<p>Le secours me viens de l&#39;Éternel même, </p>\n<p>Du Dieu qui créa les nuits et les jours. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pourra-t-il souffrir que ton pied chancelle? </p>\n<p>Ton gardien peut-il sommeiller jamais? </p>\n<p>Non, il ne dort pas, le gardien fidèle, </p>\n<p>Celui qui maintient Israël en paix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour toi, l&#39;Éternel est une retraite; </p>\n<p>Il te sert à droite et d&#39;ombre et d&#39;appui, </p>\n<p>Le soleil ne peut frapper sur ta tête, </p>\n<p>Ni la lune à l&#39;heure où le jour a fui. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il te gardera de tout mal possible; </p>\n<p>Il garde ton âme, il garde tes jours; </p>\n<p>Il te gardera!  Rentre ou sors paisible! </p>\n<p>L&#39;Éternel sur toi veillera toujours. </p>\n','Je-leve-les-yeux-vers-les-monts-que-j\'aime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1290,'d58b3d4c-fc98-4297-8441-82ac29d1de31',0,0,'Je ne sais pourquoi',82,'82_CE_fr','1.\nJe ne sais pourquoi, dans sa grâce,\nJésus m\'a tant aimé,\nPourquoi par son sang Il efface\nMa dette, mon péché.\n\nRefrain\nMais, je sais qu\'en Lui j\'ai la vie,\nIl m\'a sauvé dans son amour;\nEt gardé par sa main meurtrie,\nJ\'attends l\'heure de son retour.\n\n2.\nJe ne sais comment la lumière\nÉclaire tout mon coeur,\nComment je compris ma misère\nEt reçus mon Sauveur!\n\n3.\nJe ne sais quelle est la mesure\nDe joie et de douleur\nQue pour moi, faible créature,\nRéserve mon Sauveur.\n\n4.\nJe ne sais quand de la victoire\nL\'heure enfin sonnera,\nQuand l\'Agneau, l\'Époux dans sa gloire,\nAvec Lui me prendra.','<p><strong>1.</strong> </p>\n<p>Je ne sais pourquoi, dans sa grâce, </p>\n<p>Jésus m&#39;a tant aimé, </p>\n<p>Pourquoi par son sang Il efface </p>\n<p>Ma dette, mon péché. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mais, je sais qu&#39;en Lui j&#39;ai la vie,</strong> </p>\n<p><strong>Il m&#39;a sauvé dans son amour;</strong> </p>\n<p><strong>Et gardé par sa main meurtrie,</strong> </p>\n<p><strong>J&#39;attends l&#39;heure de son retour.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je ne sais comment la lumière </p>\n<p>Éclaire tout mon coeur, </p>\n<p>Comment je compris ma misère </p>\n<p>Et reçus mon Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je ne sais quelle est la mesure </p>\n<p>De joie et de douleur </p>\n<p>Que pour moi, faible créature, </p>\n<p>Réserve mon Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je ne sais quand de la victoire </p>\n<p>L&#39;heure enfin sonnera, </p>\n<p>Quand l&#39;Agneau, l&#39;Époux dans sa gloire, </p>\n<p>Avec Lui me prendra. </p>\n','Je-ne-sais-pourquoi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1291,'a42db1ee-b9a4-4c0d-bc08-ef6e4ffc2079',0,0,'Divine Parole',117,'117_CE_fr','1.\nDivine Parole\nQui soutiens ma foi,\nTa clarté console,\nViens briller en moi.\nLorsqu\'un voile sombre\nS\'étend sur mon coeur,\nTu dissipes l\'ombre\nD\'un rayon vainqueur.\n\nRefrain\nDivine Parole, Qui soutiens ma foi,\nTa clarté console, Viens briller en moi.\n\n2.\nMessage si tendre\nDu Sauveur en croix,\nQu\'il est doux d\'entendre\nCes mots: «Viens et crois!»\nPar son sacrifice,\nIneffable don,\nLa loi, la justice\nFont place au pardon.\n\n3.\nQue ce triste monde\nT\'écoute en tout lieu,\nÔ Bible, et répondre\nA l\'appel de Dieu!\nQue toute âme humaine\nPar toi trouve enfin,\nAn brisant sa chaîne,\nLe bonheur sans fin!','<p><strong>1.</strong> </p>\n<p>Divine Parole </p>\n<p>Qui soutiens ma foi, </p>\n<p>Ta clarté console, </p>\n<p>Viens briller en moi. </p>\n<p>Lorsqu&#39;un voile sombre </p>\n<p>S&#39;étend sur mon coeur, </p>\n<p>Tu dissipes l&#39;ombre </p>\n<p>D&#39;un rayon vainqueur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Divine Parole, Qui soutiens ma foi,</strong> </p>\n<p><strong>Ta clarté console, Viens briller en moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Message si tendre </p>\n<p>Du Sauveur en croix, </p>\n<p>Qu&#39;il est doux d&#39;entendre </p>\n<p>Ces mots: «Viens et crois!» </p>\n<p>Par son sacrifice, </p>\n<p>Ineffable don, </p>\n<p>La loi, la justice </p>\n<p>Font place au pardon. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que ce triste monde </p>\n<p>T&#39;écoute en tout lieu, </p>\n<p>Ô Bible, et répondre </p>\n<p>A l&#39;appel de Dieu! </p>\n<p>Que toute âme humaine </p>\n<p>Par toi trouve enfin, </p>\n<p>An brisant sa chaîne, </p>\n<p>Le bonheur sans fin! </p>\n','Divine-Parole',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1292,'77688f4b-bcae-4241-b2c1-52e3b00176f0',0,0,'Le Fils De Dieu Vint Sur La Terre',90,'90_CE_fr','1.\nLe fils de Dieu vint sur la terre\nMourir pour moi, car il m\'aimait;\nEst-il de plus profond mystère,\nD\'amour plus grand et plus parfait?\n\nRefrain\nJésus m\'a tant aimé,  (bis)\nIl fût pour moi crucifié,\n(2) ressuscité,\n(3) glorifié,\n(4) crucifié,\nJésus m\'a tant aimé.\n\n2.\nPour jamais ton sang pur efface\nMes péchés, mon iniquité;\nEt tu fais de moi, par ta grâce,\nUn enfant de l\'éternité.\n\n3.\nTu connais toute ma carrière,\nTu me conduis jour après jour,\nJe marche à ta pure lumière,\nToujours gardé par ton amour.\n\n4.\nJésus-Christ est toujours le même,\nIl sauve encore parfaitement,\nQui que tu sois, ce Sauveur t\'aime:\nViens, coeur brisé, viens confiant.','<p><strong>1.</strong> </p>\n<p>Le fils de Dieu vint sur la terre </p>\n<p>Mourir pour moi, car il m&#39;aimait; </p>\n<p>Est-il de plus profond mystère, </p>\n<p>D&#39;amour plus grand et plus parfait? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus m&#39;a tant aimé,  (bis)</strong> </p>\n<p><strong>Il fût pour moi crucifié,</strong> </p>\n<p><strong>(2) ressuscité,</strong> </p>\n<p><strong>(3) glorifié,</strong> </p>\n<p><strong>(4) crucifié,</strong> </p>\n<p><strong>Jésus m&#39;a tant aimé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour jamais ton sang pur efface </p>\n<p>Mes péchés, mon iniquité; </p>\n<p>Et tu fais de moi, par ta grâce, </p>\n<p>Un enfant de l&#39;éternité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu connais toute ma carrière, </p>\n<p>Tu me conduis jour après jour, </p>\n<p>Je marche à ta pure lumière, </p>\n<p>Toujours gardé par ton amour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus-Christ est toujours le même, </p>\n<p>Il sauve encore parfaitement, </p>\n<p>Qui que tu sois, ce Sauveur t&#39;aime: </p>\n<p>Viens, coeur brisé, viens confiant. </p>\n','Le-Fils-De-Dieu-Vint-Sur-La-Terre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1293,'d0a814c8-50fc-4a63-b744-03109ecb85c5',0,0,'J\'ai Quitté Le Ciel Pour Toi',91,'91_CE_fr','1.\nJ\'ai quitté le  ciel pour toi,\nJ\'ai connu ta misère extrême;\nPerdu, mourant loin de moi,\nIgnorant mon amour suprême.\n\nRefrain\nJe suis ton Sauveur, ton Ami,\nReviens à moi dans ta tristesse;\nOh!  Sois sauvé dès aujourd\'hui,\nEt tu chantera d\'allégresse.\n\n2.\nJ\'ai vécu, pécheur, pour toi,\nJ\'ai souffert l\'angoisse profonde;\nN\'ai-je pas été le Roi\nRejeté, méprisé du monde?\n\n3.\nJ\'ai subi la croix pour toi,\nJe suis mort pour ta délivrance;\nSi tu voulais croire en moi,\nJe te remplirais de puissance.\n\n4.\nJe vis maintenant pour toi,\nLà-haut dans la maison du Père;\nOh!  Viens, suis-moi par la foi,\nOui, jusqu\'à ton  heure dernière.','<p><strong>1.</strong> </p>\n<p>J&#39;ai quitté le  ciel pour toi, </p>\n<p>J&#39;ai connu ta misère extrême; </p>\n<p>Perdu, mourant loin de moi, </p>\n<p>Ignorant mon amour suprême. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je suis ton Sauveur, ton Ami,</strong> </p>\n<p><strong>Reviens à moi dans ta tristesse;</strong> </p>\n<p><strong>Oh!  Sois sauvé dès aujourd&#39;hui,</strong> </p>\n<p><strong>Et tu chantera d&#39;allégresse.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai vécu, pécheur, pour toi, </p>\n<p>J&#39;ai souffert l&#39;angoisse profonde; </p>\n<p>N&#39;ai-je pas été le Roi </p>\n<p>Rejeté, méprisé du monde? </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;ai subi la croix pour toi, </p>\n<p>Je suis mort pour ta délivrance; </p>\n<p>Si tu voulais croire en moi, </p>\n<p>Je te remplirais de puissance. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je vis maintenant pour toi, </p>\n<p>Là-haut dans la maison du Père; </p>\n<p>Oh!  Viens, suis-moi par la foi, </p>\n<p>Oui, jusqu&#39;à ton  heure dernière. </p>\n','J\'ai-Quitte-Le-Ciel-Pour-Toi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1294,'76695161-e78d-4dc6-98ed-cc1b3565cda8',0,0,'T’aimer, O Sauveur Charitable',95,'95_CE_fr','1.\nT\'aimer ô Sauveur charitable,\nC\'est de mon coeur l\'ardent désir;\nSeigneur, ta grâce est redoutable\nÀ qui ne veut pas la saisir.  (bis 2 lignes)\n\n2.\nTu m\'aimas d\'un amour immense,\nÔ Christ! Que je t\'aime à mon tour,\nEt n\'aspire à d\'autre science\nQue de connaître ton amour.  (bis 2 lignes)\n\n3.\nChrist! Pour moi tu donnas ta vie,\nEt, dépouillant ta majesté,\nTu revêtis l\'ignominie,\nCar tu m\'aimas d\'éternité.  (bis)\n\n4.\nFais que mon âme aussi réponde\nÀ tant de biens reçus de Toi;\nPour qu\'en mon coeur ta grâce abonde\nReste, ô Jésus, reste avec moi.\n\n5.\nCéleste maison paternelle,\nQuand s\'ouvriront tes portes d\'or?\nQuand, vers toi, patrie éternelle,\nPourrons-nous prendre notre essor?  (bis)\n\n6.\nO Seigneur. En ce jour suprême\nTon enfant saisira ta main;\nCar Toi seul es toujours le même,\nFidèle hier, aujourd\'hui, demain.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>T&#39;aimer ô Sauveur charitable, </p>\n<p>C&#39;est de mon coeur l&#39;ardent désir; </p>\n<p>Seigneur, ta grâce est redoutable </p>\n<p>À qui ne veut pas la saisir.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu m&#39;aimas d&#39;un amour immense, </p>\n<p>Ô Christ! Que je t&#39;aime à mon tour, </p>\n<p>Et n&#39;aspire à d&#39;autre science </p>\n<p>Que de connaître ton amour.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Christ! Pour moi tu donnas ta vie, </p>\n<p>Et, dépouillant ta majesté, </p>\n<p>Tu revêtis l&#39;ignominie, </p>\n<p>Car tu m&#39;aimas d&#39;éternité.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Fais que mon âme aussi réponde </p>\n<p>À tant de biens reçus de Toi; </p>\n<p>Pour qu&#39;en mon coeur ta grâce abonde </p>\n<p>Reste, ô Jésus, reste avec moi. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Céleste maison paternelle, </p>\n<p>Quand s&#39;ouvriront tes portes d&#39;or? </p>\n<p>Quand, vers toi, patrie éternelle, </p>\n<p>Pourrons-nous prendre notre essor?  (bis) </p>\n<p><br/> <strong>6.</strong></p>\n<p>O Seigneur. En ce jour suprême </p>\n<p>Ton enfant saisira ta main; </p>\n<p>Car Toi seul es toujours le même, </p>\n<p>Fidèle hier, aujourd&#39;hui, demain.  (bis 2 lignes) </p>\n','T\'aimer-O-Sauveur-Charitable',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1295,'24b809c1-4fef-435e-be0b-4f003506b706',0,0,'Rends-toi Maitre de nos âmes',107,'107_CE_fr','1.\nRends-toi Maître de nos âmes,\nEsprit saint, Esprit d\'amour,\nEt de tes divines flammes\nEmbrase-nous en ce jour!\n\nRefrain\nOh!  Viens, Esprit de Dieu!\nFais-nous sentir ta présence,\nRevêts-nous de ta puissance,\nEt baptise-nous de feu!\nEsprit de Dieu!  Baptise-nous de feu!\n\n2.\nSaint-Esprit de la promesse,\nQui nous scellas de ton sceau,\nDévoile-nous la richesse\nDe l\'héritage d\'en haut!\n\n3.\nForme-nous pour le service\nDe notre divin Sauveur;\nA ses pieds, en sacrifice,\nNous apportons notre coeur.\n\n4.\nEsprit de vie et de gloire,\nConduis-nous de jour en jour\nEt de victoire en victoire\nJusqu\'au céleste séjour.','<p><strong>1.</strong> </p>\n<p>Rends-toi Maître de nos âmes, </p>\n<p>Esprit saint, Esprit d&#39;amour, </p>\n<p>Et de tes divines flammes </p>\n<p>Embrase-nous en ce jour! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh!  Viens, Esprit de Dieu!</strong> </p>\n<p><strong>Fais-nous sentir ta présence,</strong> </p>\n<p><strong>Revêts-nous de ta puissance,</strong> </p>\n<p><strong>Et baptise-nous de feu!</strong> </p>\n<p><strong>Esprit de Dieu!  Baptise-nous de feu!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Saint-Esprit de la promesse, </p>\n<p>Qui nous scellas de ton sceau, </p>\n<p>Dévoile-nous la richesse </p>\n<p>De l&#39;héritage d&#39;en haut! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Forme-nous pour le service </p>\n<p>De notre divin Sauveur; </p>\n<p>A ses pieds, en sacrifice, </p>\n<p>Nous apportons notre coeur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Esprit de vie et de gloire, </p>\n<p>Conduis-nous de jour en jour </p>\n<p>Et de victoire en victoire </p>\n<p>Jusqu&#39;au céleste séjour. </p>\n','Rends-toi-Maitre-de-nos-ames',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1296,'b54754a9-3451-4896-8745-df62da449c24',0,0,'La foi fait tomber sous nos yeux',124,'124_CE_fr','1.\nLa foi fait tomber sous nos yeux\nLes plus fortes murailles;\nLa foi nous rend victorieux\nEt gagne les batailles.  (bis)\n\n2.\nLa foi nous ouvre les trésors\nDe la toute-puissance;\nLes plus faibles deviennent forts\nSous sa sainte influence.  (bis)\n\n3.\nProtégés par ce bouclier,\nNous n\'avons nulle crainte.\nQui sait en Dieu se confier\nDu mal brave l\'atteinte.  (bis)\n\n4.\nQue n\'ont pas souffert les héros\nDont nous suivons la trace!\nQue de dangers, que de travaux,\nMais quelle sainte audace!  (bis)\n\n5.\nQuand la foi vit au fond du coeur\nEt nourrit l\'espérance,\nOn est, par l\'amour du Sauveur,\nJoyeux dans la souffrance.  (bis)','<p><strong>1.</strong> </p>\n<p>La foi fait tomber sous nos yeux </p>\n<p>Les plus fortes murailles; </p>\n<p>La foi nous rend victorieux </p>\n<p>Et gagne les batailles.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>La foi nous ouvre les trésors </p>\n<p>De la toute-puissance; </p>\n<p>Les plus faibles deviennent forts </p>\n<p>Sous sa sainte influence.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Protégés par ce bouclier, </p>\n<p>Nous n&#39;avons nulle crainte. </p>\n<p>Qui sait en Dieu se confier </p>\n<p>Du mal brave l&#39;atteinte.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que n&#39;ont pas souffert les héros </p>\n<p>Dont nous suivons la trace! </p>\n<p>Que de dangers, que de travaux, </p>\n<p>Mais quelle sainte audace!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Quand la foi vit au fond du coeur </p>\n<p>Et nourrit l&#39;espérance, </p>\n<p>On est, par l&#39;amour du Sauveur, </p>\n<p>Joyeux dans la souffrance.  (bis) </p>\n','La-foi-fait-tomber-sous-nos-yeux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1297,'ea81d897-dc92-43c0-8610-d80d020eaadf',0,0,'Lorsque devant l’Agneau',103,'103_CE_fr','1.\nLorsque devant l\'Agneau,\nS\'ouvrira le grand livre\nOù sont les noms\nDes coupables absous,\nDans le ciel pour jamais\nNous aurons droit de vivre;\nSous le regard de Dieu,\nLe repos sera doux.\n\nRefrain\nSerez-vous avec nous\nAu divin rendez-vous (bis 2 lignes)\n\n2.\nNous serons tous assis\nÀ la table du maître,\nJouissant du bonheur\nDes amis de l\'époux;\nComme Il nous a connus,\nNous pourrons le connaître;\nSous le regard de Dieu,\nL\'amour sera bien doux.\n\n3.\nDu revoir Éternel\nNous connaîtrons les charmes;\nPour ne plus les quitter,\nNous retrouverons tous\nLes amis dont la mort\nNous coûta tant de larmes!\nSous le regard de Dieu,\nLe revoir sera doux.\n\n4.\nOuvrez, ouvrez vos coeurs\nAux appels de la grâce;\nAux délices du ciel,\nDieu vous invite tous.\nQuel que soit le péché\nLe sang de Christ l\'efface:\nSous le regard de Dieu,\nLe pardon est bien doux.','<p><strong>1.</strong> </p>\n<p>Lorsque devant l&#39;Agneau, </p>\n<p>S&#39;ouvrira le grand livre </p>\n<p>Où sont les noms </p>\n<p>Des coupables absous, </p>\n<p>Dans le ciel pour jamais </p>\n<p>Nous aurons droit de vivre; </p>\n<p>Sous le regard de Dieu, </p>\n<p>Le repos sera doux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Serez-vous avec nous</strong> </p>\n<p><strong>Au divin rendez-vous (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nous serons tous assis </p>\n<p>À la table du maître, </p>\n<p>Jouissant du bonheur </p>\n<p>Des amis de l&#39;époux; </p>\n<p>Comme Il nous a connus, </p>\n<p>Nous pourrons le connaître; </p>\n<p>Sous le regard de Dieu, </p>\n<p>L&#39;amour sera bien doux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Du revoir Éternel </p>\n<p>Nous connaîtrons les charmes; </p>\n<p>Pour ne plus les quitter, </p>\n<p>Nous retrouverons tous </p>\n<p>Les amis dont la mort </p>\n<p>Nous coûta tant de larmes! </p>\n<p>Sous le regard de Dieu, </p>\n<p>Le revoir sera doux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ouvrez, ouvrez vos coeurs </p>\n<p>Aux appels de la grâce; </p>\n<p>Aux délices du ciel, </p>\n<p>Dieu vous invite tous. </p>\n<p>Quel que soit le péché </p>\n<p>Le sang de Christ l&#39;efface: </p>\n<p>Sous le regard de Dieu, </p>\n<p>Le pardon est bien doux. </p>\n','Lorsque-devant-l\'Agneau',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1298,'ac5f72ef-c15e-452e-87df-b26baae43046',0,0,'Comme Un Phare Sur La Plage',94,'94_CE_fr','1.\nComme un phare sur la plage,\nPerçant l\'ombre de la nuit,\nL\'amour de Dieu, dans l\'orage,\nCherche l\'homme et le conduit.\n\nRefrain\nO Sauveur!  Que ta lumière\nResplendisse sur les flots,\nEt, vers le ciel, qu\'elle éclaire\nEt sauve les matelots.\n\n2.\nNulle étoile n\'étincelle\nPour vous guider, ô nochers!\nQui gardera la nacelle\nDes écueils et des rochers?\n\n3.\nDans la nuit qui m\'environne,\nDe ton amour, ô Jésus!\nQue par moi l\'éclat rayonne\nAux yeux des marins perdus!','<p><strong>1.</strong> </p>\n<p>Comme un phare sur la plage, </p>\n<p>Perçant l&#39;ombre de la nuit, </p>\n<p>L&#39;amour de Dieu, dans l&#39;orage, </p>\n<p>Cherche l&#39;homme et le conduit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>O Sauveur!  Que ta lumière</strong> </p>\n<p><strong>Resplendisse sur les flots,</strong> </p>\n<p><strong>Et, vers le ciel, qu&#39;elle éclaire</strong> </p>\n<p><strong>Et sauve les matelots.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nulle étoile n&#39;étincelle </p>\n<p>Pour vous guider, ô nochers! </p>\n<p>Qui gardera la nacelle </p>\n<p>Des écueils et des rochers? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans la nuit qui m&#39;environne, </p>\n<p>De ton amour, ô Jésus! </p>\n<p>Que par moi l&#39;éclat rayonne </p>\n<p>Aux yeux des marins perdus! </p>\n','Comme-Un-Phare-Sur-La-Plage',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1299,'c7aea3c5-44b9-4fa0-8e6c-1bb5a363ddd9',0,0,'En Christ seul est mon espérance',123,'123_CE_fr','1.\nEn Christ seul est mon espérance;\nSa justice est mon assurance.\nIl est devant Dieu mon appui;\nJe n\'en veux point d\'autre que lui.\n\nRefrain\nJésus est ma retraite sûre,\nLe Rocher en qui je m\'assure.  (bis)\n\n2.\nTout autre asile est périssable;\nTout autre appui n\'est que du sable.\nQui n\'a posé ce fondement\nTravaille et souffre vainement.\n\n3.\nLorsque sur moi s\'abat l\'orage,\nSa croix ranime mon courage.\nQuand tout faiblit autour de moi,\nSa présence soutient ma foi.\n\n4.\nEt quand la dernière tempête\nFondra, terrible, sur ma tête,\nComme lui, vainqueur de la mort,\nJ\'entrerai radieux au port.','<p><strong>1.</strong> </p>\n<p>En Christ seul est mon espérance; </p>\n<p>Sa justice est mon assurance. </p>\n<p>Il est devant Dieu mon appui; </p>\n<p>Je n&#39;en veux point d&#39;autre que lui. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus est ma retraite sûre,</strong> </p>\n<p><strong>Le Rocher en qui je m&#39;assure.  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout autre asile est périssable; </p>\n<p>Tout autre appui n&#39;est que du sable. </p>\n<p>Qui n&#39;a posé ce fondement </p>\n<p>Travaille et souffre vainement. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lorsque sur moi s&#39;abat l&#39;orage, </p>\n<p>Sa croix ranime mon courage. </p>\n<p>Quand tout faiblit autour de moi, </p>\n<p>Sa présence soutient ma foi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et quand la dernière tempête </p>\n<p>Fondra, terrible, sur ma tête, </p>\n<p>Comme lui, vainqueur de la mort, </p>\n<p>J&#39;entrerai radieux au port. </p>\n','En-Christ-seul-est-mon-esperance',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1300,'fd0dacb7-f90e-43f0-a6f1-883d1bd3865b',0,0,'Quel ami fidèle et tendre',80,'80_CE_fr','1.Quel ami fidèle et tendreNous avons en Jésus-Christ,Toujours prêt à nous entendre;À répondre à notre cri!Il connaît nos défaillances,Nos chutes de chaque jour.Sévère en ses exigences,Il est riche en son amour.2.Quel ami fidèle et tendreNous avons en Jésus-Christ,Toujours prêt à nous comprendreQuand nous sommes en souci!Disons-lui toutes nos craintes,Ouvrons-lui tout notre coeur.Bientôt ses paroles sainteNous rendront le vrai bonheur.3.Quel ami fidèle et tendreNous avons en Jésus-Christ,Toujours prêt à nous apprendreQuand nous presse l\'ennemi!Il nous suit dans la mêlée,Nous entoure de ses bras,Et c\'est lui qui tient l\'épéeQui décide des combats.4.Quel ami fidèle et tendreNous avons en Jésus-Christ,Toujours prêt à nous apprendreÀ vaincre en comptant sur lui!S\'il nous voit vrais et sincèresÀ chercher la sainteté,Il écoute nos prièresEt nous met en liberté.5.Quel ami fidèle et tendreNous avons en Jésus-Christ!Bientôt Il viendra nous prendrePour être au ciel avec lui.Suivons donc l\'étroite voieEn comptant sur son secours.Bientôt nous aurons la joieDe vivre avec lui toujours!','<p><strong>1.</strong> </p>\n<p>Quel ami fidèle et tendre </p>\n<p>Nous avons en Jésus-Christ, </p>\n<p>Toujours prêt à nous entendre; </p>\n<p>À répondre à notre cri! </p>\n<p>Il connaît nos défaillances, </p>\n<p>Nos chutes de chaque jour. </p>\n<p>Sévère en ses exigences, </p>\n<p>Il est riche en son amour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quel ami fidèle et tendre </p>\n<p>Nous avons en Jésus-Christ, </p>\n<p>Toujours prêt à nous comprendre </p>\n<p>Quand nous sommes en souci! </p>\n<p>Disons-lui toutes nos craintes, </p>\n<p>Ouvrons-lui tout notre coeur. </p>\n<p>Bientôt ses paroles saintes </p>\n<p>Nous rendrons le vrai bonheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quel ami fidèle et tendre </p>\n<p>Nous avons en Jésus-Christ, </p>\n<p>Toujours prêt à nous défendre </p>\n<p>Quand nous presse l&#39;ennemi! </p>\n<p>Il nous suit dans la mêlée, </p>\n<p>Nous entoure de ses bras, </p>\n<p>Et c&#39;est lui qui tient l&#39;épée </p>\n<p>Qui décide des combats. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quel ami fidèle et tendre </p>\n<p>Nous avons en Jésus-Christ, </p>\n<p>Toujours prêt à nous apprendre </p>\n<p>À vaincre en comptant sur lui! </p>\n<p>S&#39;il nous voit vrais et sincères </p>\n<p>À chercher la sainteté, </p>\n<p>Il écoute nos prières </p>\n<p>Et nous mets en liberté. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Quel ami fidèle et tendre </p>\n<p>Nous avons en Jésus-Christ! </p>\n<p>Bientôt Il viendra nous prendre </p>\n<p>Pour être au ciel avec lui. </p>\n<p>Suivons donc l&#39;étroite voie </p>\n<p>En comptant sur son secours. </p>\n<p>Bientôt nous aurons la joie </p>\n<p>De vivre avec lui toujours! </p>\n','Quel-ami-fidele-et-tendre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1301,'9d1a0eaf-91bb-4b00-bacd-42ee67b1ee4f',0,0,'Oui, pour son peuple Jésus prie',71,'71_CE_fr','1.\nOui, pour son peuple Jésus prie;\nPrêtons l\'oreille à ses soupirs;\nQu\'à sa voix notre âme attendrie\nRépondre par de saints désirs.\nDans les hauts lieux, brillant de gloire,\nIl est entré victorieux\nEt sur l\'autel expiatoire\nIl offre son sang précieux.\n\n2.\nOui, pour nos âmes Jésus prie;\nDans cet instant, ô charité!\nIl plaide, Il intercède, Il crie\nPour nous, qui l\'avons contristé.\nÀ son enfant, auprès du Père,\nSon coeur obtient un doux pardon,\nEt pour l\'aider dans sa misère,\nSa voix réclame un nouveau don.\n\n3.\nOui, pour les tiens, Jésus, tu pries;\nQu\'il nous est doux de le savoir!\nAinsi, Seigneur, tu nous convies\nÀ mettre en toi tout notre espoir.\nSous le parfum de ta prière\nFais-nous marcher, remplis d\'ardeur\nPour te bénir, notre âme entière\nS\'élève à Toi, puissant Sauveur!','<p><strong>1.</strong> </p>\n<p>Oui, pour son peuple Jésus prie; </p>\n<p>Prêtons l&#39;oreille à ses soupirs; </p>\n<p>Qu&#39;à sa voix notre âme attendrie </p>\n<p>Répondre par de saints désirs. </p>\n<p>Dans les hauts lieux, brillant de gloire, </p>\n<p>Il est entré victorieux </p>\n<p>Et sur l&#39;autel expiatoire </p>\n<p>Il offre son sang précieux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, pour nos âmes Jésus prie; </p>\n<p>Dans cet instant, ô charité! </p>\n<p>Il plaide, Il intercède, Il crie </p>\n<p>Pour nous, qui l&#39;avons contristé. </p>\n<p>À son enfant, auprès du Père, </p>\n<p>Son coeur obtient un doux pardon, </p>\n<p>Et pour l&#39;aider dans sa misère, </p>\n<p>Sa voix réclame un nouveau don. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, pour les tiens, Jésus, tu pries; </p>\n<p>Qu&#39;il nous est doux de le savoir! </p>\n<p>Ainsi, Seigneur, tu nous convies </p>\n<p>À mettre en toi tout notre espoir. </p>\n<p>Sous le parfum de ta prière </p>\n<p>Fais-nous marcher, remplis d&#39;ardeur </p>\n<p>Pour te bénir, notre âme entière </p>\n<p>S&#39;élève à Toi, puissant Sauveur! </p>\n','Oui-pour-son-peuple-Jesus-prie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1302,'b36d663e-2b81-4457-86fc-796ab8897e67',0,0,'Ami Tender Et Secourable',81,'81_CE_fr','1.\nAmi tendre et secourable,\nJésus, mon divin Berger,\nQue ta grâce est admirable\nPour le pauvre péager!\nPlus sa misère est profonde,\nPlus il se sent grand pécheur,\nPlus en lui ta grâce abonde,\nPlus il te croit son Sauveur.\n\n2.\nAmi tendre et secourable,\nJésus, mon divin Berger,\nJe suis aussi misérable\nQue le pauvre péager.\nPlus que lui je suis indigne\nD\'élever mes yeux vers toi;\nMais dans ta faveur insigne,\nTon coeur a pitié de moi.\n\n3.\nAmi tendre et secourable,\nJésus, mon divin Berger,\nEnvers le plus grand coupable,\nTon amour ne peut changer.\nPlus sa douleur est profonde,\nPlus il se sent grand pécheur,\nPlus en lui ta grâce abonde,\nPlus il t\'aime, ô bon Sauveur!','<p><strong>1.</strong> </p>\n<p>Ami tendre et secourable, </p>\n<p>Jésus, mon divin Berger, </p>\n<p>Que ta grâce est admirable </p>\n<p>Pour le pauvre péager! </p>\n<p>Plus sa misère est profonde, </p>\n<p>Plus il se sent grand pécheur, </p>\n<p>Plus en lui ta grâce abonde, </p>\n<p>Plus il te croit son Sauveur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ami tendre et secourable, </p>\n<p>Jésus, mon divin Berger, </p>\n<p>Je suis aussi misérable </p>\n<p>Que le pauvre péager. </p>\n<p>Plus que lui je suis indigne </p>\n<p>D&#39;élever mes yeux vers toi; </p>\n<p>Mais dans ta faveur insigne, </p>\n<p>Ton coeur a pitié de moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ami tendre et secourable, </p>\n<p>Jésus, mon divin Berger, </p>\n<p>Envers le plus grand coupable, </p>\n<p>Ton amour ne peut changer. </p>\n<p>Plus sa douleur est profonde, </p>\n<p>Plus il se sent grand pécheur, </p>\n<p>Plus en lui ta grâce abonde, </p>\n<p>Plus il t&#39;aime, ô bon Sauveur! </p>\n','Ami-Tender-Et-Secourable',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1303,'e56f35bd-955c-4a18-a372-d0f9fb79aa57',0,0,'Ô cieux, unissez-vous',74,'74_CE_fr','1.\nO Cieux, unissez-vous\nAux transports de la terre;\nÉglise du Seigneur,\nRenouvelle tes chants;\nEt qu\'en de saints accents,  (bis)\nDe tous les coeurs pieux\nS\'élève la prière.\n\n2.\nJésus vient de monter\nAu séjour de la gloire.\nSes travaux sont infinis:\nSon peuple est racheté;\nEt ceint de majesté,  (bis)\nIl entre aux lieux très-hauts\nAvec cri de victoire.\n\n3.\nA la droite de Dieu\nSa place est préparée.\nLà notre humanité\nS\'assied avec pouvoir.\nOh! Qui peut concevoir  (bis)\nQue de cette grandeur\nElle soit honorée!\n\n4.\nTu règnes, ô Jésus,\nDans la toute-puissance.\nSur le monde vaincu\nTon sceptre est souverain.\nL\'empire est dans ta main;  (bis)\nEt tu remplis les cieux\nDe ta magnificence.\n\n5.\nEn toi, Fils du Très-Hauts,\nRéside notre vie.\nOn ne voit point encore\nCe qu\'un jour nous serons;\nMais quand nous te verrons,  (bis)\nTu nous revêtiras\nD\'une gloire infinie.','<p><strong>1.</strong> </p>\n<p>O Cieux, unissez-vous </p>\n<p>Aux transports de la terre; </p>\n<p>Église du Seigneur, </p>\n<p>Renouvelle tes chants; </p>\n<p>Et qu&#39;en de saints accents,  (bis) </p>\n<p>De tous les coeurs pieux </p>\n<p>S&#39;élève la prière. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus vient de monter </p>\n<p>Au séjour de la gloire. </p>\n<p>Ses travaux sont infinis: </p>\n<p>Son peuple est racheté; </p>\n<p>Et ceint de majesté,  (bis) </p>\n<p>Il entre aux lieux très-hauts </p>\n<p>Avec cri de victoire. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A la droite de Dieu </p>\n<p>Sa place est préparée. </p>\n<p>Là notre humanité </p>\n<p>S&#39;assied avec pouvoir. </p>\n<p>Oh! Qui peut concevoir  (bis) </p>\n<p>Que de cette grandeur </p>\n<p>Elle soit honorée! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tu règnes, ô Jésus, </p>\n<p>Dans la toute-puissance. </p>\n<p>Sur le monde vaincu </p>\n<p>Ton sceptre est souverain. </p>\n<p>L&#39;empire est dans ta main;  (bis) </p>\n<p>Et tu remplis les cieux </p>\n<p>De ta magnificence. </p>\n<p><br/> <strong>5.</strong></p>\n<p>En toi, Fils du Très-Hauts, </p>\n<p>Réside notre vie. </p>\n<p>On ne voit point encore </p>\n<p>Ce qu&#39;un jour nous serons; </p>\n<p>Mais quand nous te verrons,  (bis) </p>\n<p>Tu nous revêtiras </p>\n<p>D&#39;une gloire infinie. </p>\n','O-cieux-unissez-vous',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1304,'997c7175-473f-44fc-9f82-a101273c6edd',0,0,'Reste avec nous',120,'120_CE_fr','1.\nReste avec nous,\nSeigneur, le jour décline,\nLa nuit s\'approche\nEt nous menace tous;\nNous implorons ta présence divine;\nReste avec nous, Seigneur,\nReste avec nous!\n\n2.\nEn Toi nos coeurs\nOnt salué leur Maître,\nEn Toi notre âme\nÀ trouvé son Époux;\nÀ ta lumière, elle se sent renaître;\nReste avec nous, Seigneur,\nReste avec nous.\n\n3.\nDans nos combats,\nSi ta main nous délaisse,\nSatan vainqueur\nNous tiendra sous ses coups;\nQue ta puissance arme notre faiblesse\nReste avec nous, Seigneur,\nReste avec nous!\n\n4.\nSous ton regard,\nLa joie est sainte et bonne;\nPrès de ton coeur\nLes pleurs mêmes sont doux;\nSoit que ta main nous frappe ou nous couronne,\nReste avec, Seigneur,\nReste avec nous!\n\n5.\nEt quand, au bout\nDe ce pèlerinage,\nNous partirons\nPour le grand rendez-vous;\nPour nous guider dans ce dernier passage,\nReste avec nous, Seigneur,\nReste avec nous!','<p><strong>1.</strong> </p>\n<p>Reste avec nous, </p>\n<p>Seigneur, le jour décline, </p>\n<p>La nuit s&#39;approche </p>\n<p>Et nous menace tous; </p>\n<p>Nous implorons ta présence divine; </p>\n<p>Reste avec nous, Seigneur, </p>\n<p>Reste avec nous! </p>\n<p><br/> <strong>2.</strong></p>\n<p>En Toi nos coeurs </p>\n<p>Ont salué leur Maître, </p>\n<p>En Toi notre âme </p>\n<p>À trouvé son Époux; </p>\n<p>À ta lumière, elle se sent renaître; </p>\n<p>Reste avec nous, Seigneur, </p>\n<p>Reste avec nous. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans nos combats, </p>\n<p>Si ta main nous délaisse, </p>\n<p>Satan vainqueur </p>\n<p>Nous tiendra sous ses coups; </p>\n<p>Que ta puissance arme notre faiblesse </p>\n<p>Reste avec nous, Seigneur, </p>\n<p>Reste avec nous! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sous ton regard, </p>\n<p>La joie est sainte et bonne; </p>\n<p>Près de ton coeur </p>\n<p>Les pleurs mêmes sont doux; </p>\n<p>Soit que ta main nous frappe ou nous couronne, </p>\n<p>Reste avec, Seigneur, </p>\n<p>Reste avec nous! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Et quand, au bout </p>\n<p>De ce pèlerinage, </p>\n<p>Nous partirons </p>\n<p>Pour le grand rendez-vous; </p>\n<p>Pour nous guider dans ce dernier passage, </p>\n<p>Reste avec nous, Seigneur, </p>\n<p>Reste avec nous! </p>\n','Reste-avec-nous',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1305,'677bd4f7-a58c-4516-a20c-519f28928c40',0,0,'Bon Sauveur, Berger fidèle',96,'96_CE_fr','1.\nBon Sauveur, Berger fidèle,\nGarde-nous par ton amour;\nEt que ta main paternelle\nNous conduise chaque jour.\n\nRefrain\nBéni sois-tu, tendre Maître,\nJésus, nous sommes à Toi;\nÀ toi seul nous voulons être,\nBéni sois-tu, notre Roi!\n\n2.\nToi qui nous reçus par grâce,\nBien que faible et pécheurs,\nPar ta puissance efficace,\nPurifie encore nos coeurs.\n\n3.\nSeigneur, nous voulons te plaire,\nT\'obéir, garder ta loi;\nOh! Pour cela daigne faire\nQue nous vivions par la foi!','<p><strong>1.</strong> </p>\n<p>Bon Sauveur, Berger fidèle, </p>\n<p>Garde-nous par ton amour; </p>\n<p>Et que ta main paternelle </p>\n<p>Nous conduise chaque jour. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Béni sois-tu, tendre Maître,</strong> </p>\n<p><strong>Jésus, nous sommes à Toi;</strong> </p>\n<p><strong>À toi seul nous voulons être,</strong> </p>\n<p><strong>Béni sois-tu, notre Roi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Toi qui nous reçus par grâce, </p>\n<p>Bien que faible et pécheurs, </p>\n<p>Par ta puissance efficace, </p>\n<p>Purifie encore nos coeurs. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur, nous voulons te plaire, </p>\n<p>T&#39;obéir, garder ta loi; </p>\n<p>Oh! Pour cela daigne faire </p>\n<p>Que nous vivions par la foi! </p>\n','Bon-Sauveur-Berger-fidele',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1306,'8f7838bd-99e8-43ad-b66d-27a20492c36c',0,0,'Voir mon Sauveur face a face',102,'102_CE_fr','1.\nVoir mon Sauveur face à face,\nVoir Jésus dans sa beauté,\nO joie! Ô suprême grâce!\nO bonheur! Félicité!\n\nRefrain\nOui, dans ta magnificence\nJe te verrai, divin Roi!\nPour toujours en ta présence\nJe serai semblable à toi!\n\n2.\nTa gloire est encore voilée,\nAh! D\'un voile ensanglanté!...\nBientôt sera révélée\nTon ineffable beauté!\n\n3.\nOh! Quels transports d\'allégresse,\nQuand tes yeux baissés sur moi,\nMe diront avec tendresse:\n« je mourus aussi pour toi! »','<p><strong>1.</strong> </p>\n<p>Voir mon Sauveur face à face, </p>\n<p>Voir Jésus dans sa beauté, </p>\n<p>O joie! Ô suprême grâce! </p>\n<p>O bonheur! Félicité! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, dans ta magnificence</strong> </p>\n<p><strong>Je te verrai, divin Roi!</strong> </p>\n<p><strong>Pour toujours en ta présence</strong> </p>\n<p><strong>Je serai semblable à toi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ta gloire est encore voilée, </p>\n<p>Ah! D&#39;un voile ensanglanté!... </p>\n<p>Bientôt sera révélée </p>\n<p>Ton ineffable beauté! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh! Quels transports d&#39;allégresse, </p>\n<p>Quand tes yeux baissés sur moi, </p>\n<p>Me diront avec tendresse: </p>\n<p>« je mourus aussi pour toi! » </p>\n','Voir-mon-Sauveur-face-a-face',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1307,'c3bfed98-ce63-41f6-bd10-67660e38f446',0,0,'Chef couvert de blessures',66,'66_CE_fr','1.\nChef couvert de blessures,\nMeurtri par nous, pécheurs,\nChef accablé d\'injures,\nD\'opprobres, de douleurs,\nDes splendeurs éternelles\nNaguère environné,\nC\'est d\'épines cruelles\nQu\'on te voit couronné!\n\n2.\nC\'est ainsi que tu paies\nLe prix de ma rançon.\nTes langueurs et tes plates,\nVoilà ma guérison.\nMon âme criminelle\nEst à tes pieds, Seigneur;\nDaigne jeter sur elle\nUn regard de faveur.\n\n3.\nAu sein de ma misère,\nSauvé par ton amour,\nPour toi que puis-je faire?\nQue t\'offrir en retour?\nAh!  Du moins, Dieu suprême,\nPrends à jamais mon coeur:\nQu\'il te serve et qu\'il t\'aime,\nPlein d\'une sainte ardeur.\n\n4.\nPour ta longue agonie,\nPour ta mort sur la croix,\nJe veux toute ma vie\nTe louer, Roi des rois!\nTa grâce est éternelle,\nEt rien jusqu\'à la fin\nNe pourra, Dieu fidèle,\nMe ravir de ta main.','<p><strong>1.</strong> </p>\n<p>Chef couvert de blessures, </p>\n<p>Meurtri par nous, pécheurs, </p>\n<p>Chef accablé d&#39;injures, </p>\n<p>D&#39;opprobres, de douleurs, </p>\n<p>Des splendeurs éternelles </p>\n<p>Naguère environné, </p>\n<p>C&#39;est d&#39;épines cruelles </p>\n<p>Qu&#39;on te voit couronné! </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est ainsi que tu paies </p>\n<p>Le prix de ma rançon. </p>\n<p>Tes langueurs et tes plates, </p>\n<p>Voilà ma guérison. </p>\n<p>Mon âme criminelle </p>\n<p>Est à tes pieds, Seigneur; </p>\n<p>Daigne jeter sur elle </p>\n<p>Un regard de faveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au sein de ma misère, </p>\n<p>Sauvé par ton amour, </p>\n<p>Pour toi que puis-je faire? </p>\n<p>Que t&#39;offrir en retour? </p>\n<p>Ah!  Du moins, Dieu suprême, </p>\n<p>Prends à jamais mon coeur: </p>\n<p>Qu&#39;il te serve et qu&#39;il t&#39;aime, </p>\n<p>Plein d&#39;une sainte ardeur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour ta longue agonie, </p>\n<p>Pour ta mort sur la croix, </p>\n<p>Je veux toute ma vie </p>\n<p>Te louer, Roi des rois! </p>\n<p>Ta grâce est éternelle, </p>\n<p>Et rien jusqu&#39;à la fin </p>\n<p>Ne pourra, Dieu fidèle, </p>\n<p>Me ravir de ta main. </p>\n','Chef-couvert-de-blessures',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1308,'de365ad8-ddd2-492e-aed5-9cbd64aa1fa4',0,0,'Toujours ta divine présence',87,'87_CE_fr','1.\nToujours ta divine présence\nJette un rayon sur mon chemin;\nEt, le coeur joyeux, je m\'avance;\nJe n\'ai pas peur du lendemain.\n\nRefrain\nOù tu voudras, je veux te suivre;\nAgneau de Dieu, conduis mes pas.\nVivre sans Toi, ce n\'est pas vivre;\nJe ne puis être où tu n\'es pas.\n\n2.\nOh! Que jamais rien ne me voile\nTon doux regard, bien-aimé Roi!\nDans le danger, brillante Étoile,\nGarde mes yeux fixés sur Toi.\n\n3.\nAuprès de Toi, la vie est belle,\nC\'est le bonheur, la liberté,\nC\'est une jeunesse éternelle,\nC\'est le ciel, la félicité!','<p><strong>1.</strong> </p>\n<p>Toujours ta divine présence </p>\n<p>Jette un rayon sur mon chemin; </p>\n<p>Et, le coeur joyeux, je m&#39;avance; </p>\n<p>Je n&#39;ai pas peur du lendemain. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Où tu voudras, je veux te suivre;</strong> </p>\n<p><strong>Agneau de Dieu, conduis mes pas.</strong> </p>\n<p><strong>Vivre sans Toi, ce n&#39;est pas vivre;</strong> </p>\n<p><strong>Je ne puis être où tu n&#39;es pas.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Que jamais rien ne me voile </p>\n<p>Ton doux regard, bien-aimé Roi! </p>\n<p>Dans le danger, brillante Étoile, </p>\n<p>Garde mes yeux fixés sur Toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Auprès de Toi, la vie est belle, </p>\n<p>C&#39;est le bonheur, la liberté, </p>\n<p>C&#39;est une jeunesse éternelle, </p>\n<p>C&#39;est le ciel, la félicité! </p>\n','Toujours-ta-divine-presence',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1309,'c87b2482-66fe-4899-b92b-10a6681c82b0',0,0,'Ta Parole est un beau jardin',113,'113_CE_fr','1.\nTa Parole est un beau jardin.\nEn gerbes embaumées,\nJ\'y moissonne chaque matin,\nDes fleurs par Toi semées.\n\nRefrain\nJ\'aime ta Parole, Seigneur,\nTu me donnes par elle,\nRichesses, lumière, bonheur,\nEt victoire éternelle!\n\n2.\nSeigneur, ta Parole est pour moi\nLa mine pleine d\'ombre,\nOù je découvre par la foi,\nDes diamants sans nombre!\n\n3.\nPareille à la voûte des cieux,\nLumineuse et profonde,\nTa Parole brille à mes yeux\nDans la nuit de ce monde.\n\n4.\nTa Parole est un coeur puissant\nAux pures harmonies,\nOù, dans un hymne ravissant,\nMille voix sont unies.\n\n5.\nPar elle, un céleste aliment\nRenouvelle mon être:\nManne divine, pure froment,\nChair et sang de mon Maître!','<p><strong>1.</strong> </p>\n<p>Ta Parole est un beau jardin. </p>\n<p>En gerbes embaumées, </p>\n<p>J&#39;y moissonne chaque matin, </p>\n<p>Des fleurs par Toi semées. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>J&#39;aime ta Parole, Seigneur,</strong> </p>\n<p><strong>Tu me donnes par elle,</strong> </p>\n<p><strong>Richesses, lumière, bonheur,</strong> </p>\n<p><strong>Et victoire éternelle!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seigneur, ta Parole est pour moi </p>\n<p>La mine pleine d&#39;ombre, </p>\n<p>Où je découvre par la foi, </p>\n<p>Des diamants sans nombre! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pareille à la voûte des cieux, </p>\n<p>Lumineuse et profonde, </p>\n<p>Ta Parole brille à mes yeux </p>\n<p>Dans la nuit de ce monde. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ta Parole est un coeur puissant </p>\n<p>Aux pures harmonies, </p>\n<p>Où, dans un hymne ravissant, </p>\n<p>Mille voix sont unies. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Par elle, un céleste aliment </p>\n<p>Renouvelle mon être: </p>\n<p>Manne divine, pure froment, </p>\n<p>Chair et sang de mon Maître! </p>\n','Ta-Parole-est-un-beau-jardin',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1310,'a01ca664-dfff-43fb-991e-37730253ecfd',0,0,'Livre Saint, céleste Livre',114,'114_CE_fr','1.\nLivre Saint, céleste Livre,\nMon plus précieux trésor,\nGuide sûr que j\'aime à suivre,\nTu vaux plus que beaucoup d\'or.\nC\'est toi, Bible bien-aimée,\nQui me montre le Sauveur.\nTu peux à l\'âme affamée\nProcurer le vrai bonheur.\n\n2.\nO puissante et sainte Épée,\nMarteau brisant les rochers,\nPar toi, mon âme frappée\nA pleuré sur ses péchés.\nOui, dans toutes mes détresses,\nDans mes larmes, ma douleur,\nPar tes divines promesses.\nTu viens consoler mon coeur.','<p><strong>1.</strong> </p>\n<p>Livre Saint, céleste Livre, </p>\n<p>Mon plus précieux trésor, </p>\n<p>Guide sûr que j&#39;aime à suivre, </p>\n<p>Tu vaux plus que beaucoup d&#39;or. </p>\n<p>C&#39;est toi, Bible bien-aimée, </p>\n<p>Qui me montre le Sauveur. </p>\n<p>Tu peux à l&#39;âme affamée </p>\n<p>Procurer le vrai bonheur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>O puissante et sainte Épée, </p>\n<p>Marteau brisant les rochers, </p>\n<p>Par toi, mon âme frappée </p>\n<p>A pleuré sur ses péchés. </p>\n<p>Oui, dans toutes mes détresses, </p>\n<p>Dans mes larmes, ma douleur, </p>\n<p>Par tes divines promesses. </p>\n<p>Tu viens consoler mon coeur. </p>\n','Livre-Saint-celeste-Livre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1311,'3de3153b-9372-496b-af8b-54e3b329ada1',0,0,'C’est un Rempart Que Notre Dieu',118,'118_CE_fr','1.\nC\'est un rempart que notre Dieu,\nUne invincible armure,\nNotre délivrance en tout lieu,\nNotre défense sûre.\nL\'ennemi contre nous\nRedouble de courroux;\nVaine colère!\nQue pourrait l\'adversaire?\nL\'Éternel détourne ses coups.\n\n2.\nSeuls, nous bronchons à chaque pas,\nNotre force est faiblesse;\nMais un héros, dans les combats,\nPour nous lutte sans cesse.\nC\'est toi, divin Sauveur!\nQuel est ce défenseur?\nDieu des armées!\nTes tribus opprimées\nConnaissent leur Libérateur.','<p><strong>1.</strong> </p>\n<p>C&#39;est un rempart que notre Dieu, </p>\n<p>Une invincible armure, </p>\n<p>Notre délivrance en tout lieu, </p>\n<p>Notre défense sûre. </p>\n<p>L&#39;ennemi contre nous </p>\n<p>Redouble de courroux; </p>\n<p>Vaine colère! </p>\n<p>Que pourrait l&#39;adversaire? </p>\n<p>L&#39;Éternel détourne ses coups. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seuls, nous bronchons à chaque pas, </p>\n<p>Notre force est faiblesse; </p>\n<p>Mais un héros, dans les combats, </p>\n<p>Pour nous lutte sans cesse. </p>\n<p>C&#39;est toi, divin Sauveur! </p>\n<p>Quel est ce défenseur? </p>\n<p>Dieu des armées! </p>\n<p>Tes tribus opprimées </p>\n<p>Connaissent leur Libérateur. </p>\n','C\'est-un-Rempart-Que-Notre-Dieu',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1312,'07fb2e81-efcb-47da-85eb-a2026a6d042a',0,0,'Jésus, Mis A Mort Pour Moi',84,'84_CE_fr','1.\nJésus, mis à mort pour moi,\nJe cherche un refuge en toi!\nQue tout sang, que l\'onde pure\nDécoulant de ta blessure,\nÔtant mon iniquité,\nMe rendent la liberté!\n\n2.\nTous les travaux de mes mains\nPour te plaire seraient vains.\nLors même qu\'en ma détresse\nMes pleurs couleraient sans cesse,\nIls ne sauraient me laver:\nToi seul peux et veux sauver!\n\n3.\nSeigneur, je n\'apporte rien;\nTa croix seule est mon soutien.\nJe viens à toi, sans ressource;\nSouillé, je viens à la source\nOù les péchés sont lavés\nEt les pécheurs sont sauvés!\n\n4.\nQu\'en toi je vive ici-bas!\nQue je meure entre tes bras!\nEt que, vers ton ciel splendide\nPrenant mon essor rapide,\nJésus, mis à mort pour moi,\nJe trouve un refuge en toi!','<p><strong>1.</strong> </p>\n<p>Jésus, mis à mort pour moi, </p>\n<p>Je cherche un refuge en toi! </p>\n<p>Que tout sang, que l&#39;onde pure </p>\n<p>Découlant de ta blessure, </p>\n<p>Ôtant mon iniquité, </p>\n<p>Me rendent la liberté! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tous les travaux de mes mains </p>\n<p>Pour te plaire seraient vains. </p>\n<p>Lors même qu&#39;en ma détresse </p>\n<p>Mes pleurs couleraient sans cesse, </p>\n<p>Ils ne sauraient me laver: </p>\n<p>Toi seul peux et veux sauver! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur, je n&#39;apporte rien; </p>\n<p>Ta croix seule est mon soutien. </p>\n<p>Je viens à toi, sans ressource; </p>\n<p>Souillé, je viens à la source </p>\n<p>Où les péchés sont lavés </p>\n<p>Et les pécheurs sont sauvés! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Qu&#39;en toi je vive ici-bas! </p>\n<p>Que je meure entre tes bras! </p>\n<p>Et que, vers ton ciel splendide </p>\n<p>Prenant mon essor rapide, </p>\n<p>Jésus, mis à mort pour moi, </p>\n<p>Je trouve un refuge en toi! </p>\n','Jesus-Mis-A-Mort-Pour-Moi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1313,'068e478d-7006-4d82-929a-27160cb007b5',0,0,'Qui me relève dans mes chutes',73,'73_CE_fr','1.\nQui me relève dans mes chutes?\nC\'est Jésus-Christ!\nQui combat pour moi dans mes luttes?\nC\'est Jésus-Christ!\nJésus à parler, je veux croire\nQue je puis lutter pour sa gloire,\nCar mon bouclier, ma victoire,\nC\'est Jésus-Christ!\n\n2.\nJe vais à mon Père, et ma voie,\nC\'est Jésus-Christ!\nJe suis bienheureux, et ma joie,\nC\'est Jésus-Christ!\nEt si, même dans la souffrance,\nMon coeur me parle d\'espérance,\nC\'est que j\'ai mis ma confiance\nEn Jésus-Christ!\n\n3.\nSauvé, je ne me glorifie\nQu\'en Jésus-Christ!\nPour la terre et le ciel, ma vie!\nC\'est Jésus-Christ!\nBientôt, adieu, choses mortelles!\nLoin de vous je prendrai des ailes\nVers les demeures éternelles\nVers Jésus-Christ!','<p><strong>1.</strong> </p>\n<p>Qui me relève dans mes chutes? </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p>Qui combat pour moi dans mes luttes? </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p>Jésus à parler, je veux croire </p>\n<p>Que je puis lutter pour sa gloire, </p>\n<p>Car mon bouclier, ma victoire, </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je vais à mon Père, et ma voie, </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p>Je suis bienheureux, et ma joie, </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p>Et si, même dans la souffrance, </p>\n<p>Mon coeur me parle d&#39;espérance, </p>\n<p>C&#39;est que j&#39;ai mis ma confiance </p>\n<p>En Jésus-Christ! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sauvé, je ne me glorifie </p>\n<p>Qu&#39;en Jésus-Christ! </p>\n<p>Pour la terre et le ciel, ma vie! </p>\n<p>C&#39;est Jésus-Christ! </p>\n<p>Bientôt, adieu, choses mortelles! </p>\n<p>Loin de vous je prendrai des ailes </p>\n<p>Vers les demeures éternelles </p>\n<p>Vers Jésus-Christ! </p>\n','Qui-me-releve-dans-mes-chutes',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1314,'76450c9b-58eb-46da-ac9d-3249502a93a4',0,0,'Viens, o Jésus, ton Eglise t\'appelle',101,'101_CE_fr','1.\nViens, ô Jésus, ton Église t\'appelle;\nViens établir ton règne glorieux!\nNous t\'attendons:\nDescends du haut des cieux!\nTu l\'as promis, et tu seras fidèle.  (bis)\n\n2.\nNe laisse pas plus longtemps notre terre\nLivrée au mal, à la haine, à l\'erreur:\nN\'entends-tu point\nLe cri de sa douleur?\nAh!  Dans sa nuit fais briller ta lumière!  (bis)\n\n3.\nNos faibles mains se lassent de combattre,\nNos tristes coeurs se lassent d\'espérer\nViens sans regard,\nOh!  Viens nous délivrer:\nNotre courage est si prompt à s\'abattre!  (bis)\n\n4.\nHâte, ô Jésus, l\'heure de la victoire!\nParais bientôt, Étoile du matin!\nAube du jour\nOù le mal prendra fin\nAnéanti par l\'éclat de ta gloire!  (bis)','<p><strong>1.</strong> </p>\n<p>Viens, ô Jésus, ton Église t&#39;appelle; </p>\n<p>Viens établir ton règne glorieux! </p>\n<p>Nous t&#39;attendons: </p>\n<p>Descends du haut des cieux! </p>\n<p>Tu l&#39;as promis, et tu seras fidèle.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ne laisse pas plus longtemps notre terre </p>\n<p>Livrée au mal, à la haine, à l&#39;erreur: </p>\n<p>N&#39;entends-tu point </p>\n<p>Le cri de sa douleur? </p>\n<p>Ah!  Dans sa nuit fais briller ta lumière!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nos faibles mains se lassent de combattre, </p>\n<p>Nos tristes coeurs se lassent d&#39;espérer </p>\n<p>Viens sans regard, </p>\n<p>Oh!  Viens nous délivrer: </p>\n<p>Notre courage est si prompt à s&#39;abattre!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Hâte, ô Jésus, l&#39;heure de la victoire! </p>\n<p>Parais bientôt, Étoile du matin! </p>\n<p>Aube du jour </p>\n<p>Où le mal prendra fin </p>\n<p>Anéanti par l&#39;éclat de ta gloire!  (bis) </p>\n','Viens-o-Jesus-ton-Eglise-t\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1315,'215b64bb-f9fa-4de1-a261-67e555dbe192',0,0,'Suives, Suives L\'agneau',86,'86_CE_fr','1.\nSuivez, suivez l\'Agneau\nJusqu\'au soir de la vie;\nVers un gras pâturage\nIl mène son troupeau.\nAu bonheur des élus\nSa grâce vous convie;\nSuivez toujours,\nSuivez toujours l\'Agneau!\n\n2.\nSuivez-le sans frayeur,\nAu jour de la tristesse;\nDéposez dans son sein\nVotre pesant fardeau;\nSuivez-le dans la paix,\nAu jour de l\'allégresse;\nSuivez toujours,\nSuivez toujours l\'Agneau!\n\n3.\nMais, suivez-le partout.\nLe Berger qui vous mène\nVous aime, et ne saurait\nÉgarer son troupeau.\nOui, le joug du Seigneur\nEst une douce chaîne.\nSuivez toujours,\nSuivez toujours l\'agneau!\n\n4.\nSuivez-le sans broncher;\nLe chemin solitaire\nDoit vous conduire un jour\nAu céleste repos.\nMais pour le suivre au ciel,\nSuivez-le sur la terre.\nSuivez toujours,\nSuivez toujours l\'agneau!','<p><strong>1.</strong> </p>\n<p>Suivez, suivez l&#39;Agneau </p>\n<p>Jusqu&#39;au soir de la vie; </p>\n<p>Vers un gras pâturage </p>\n<p>Il mène son troupeau. </p>\n<p>Au bonheur des élus </p>\n<p>Sa grâce vous convie; </p>\n<p>Suivez toujours, </p>\n<p>Suivez toujours l&#39;Agneau! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Suivez-le sans frayeur, </p>\n<p>Au jour de la tristesse; </p>\n<p>Déposez dans son sein </p>\n<p>Votre pesant fardeau; </p>\n<p>Suivez-le dans la paix, </p>\n<p>Au jour de l&#39;allégresse; </p>\n<p>Suivez toujours, </p>\n<p>Suivez toujours l&#39;Agneau! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais, suivez-le partout. </p>\n<p>Le Berger qui vous mène </p>\n<p>Vous aime, et ne saurait </p>\n<p>Égarer son troupeau. </p>\n<p>Oui, le joug du Seigneur </p>\n<p>Est une douce chaîne. </p>\n<p>Suivez toujours, </p>\n<p>Suivez toujours l&#39;agneau! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Suivez-le sans broncher; </p>\n<p>Le chemin solitaire </p>\n<p>Doit vous conduire un jour </p>\n<p>Au céleste repos. </p>\n<p>Mais pour le suivre au ciel, </p>\n<p>Suivez-le sur la terre. </p>\n<p>Suivez toujours, </p>\n<p>Suivez toujours l&#39;agneau! </p>\n','Suives-Suives-L\'agneau',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1316,'0160fe27-0192-4dc3-a4af-ff71d30d3ec2',0,0,'Tenons nos lampes prêtes',99,'99_CE_fr','1.\nTenons nos lampes prêtes,\nChrétiens, préparons-nous\nPour l\'heure où les trompettes\nAnnonceront l\'Époux.\n\nRefrain\nQu\'à répondre on s\'empresse,\nHosanna! Hosanna!\nEt qu\'avec allégresse\nOn chante: Alléluia! (bis)\n\n2.\nVoici déjà les anges,\nAvec eux les élus,\nUnissant leurs louanges\nEn l\'honneur de Jésus\n\n3.\nVoici Jésus lui-même,\nPuissant, victorieux!\nDe son pur diadème\nL\'éclat remplit les cieux.\n\n4.\nVenez, bénie du père\nQui m\'avez attendu;\nEntrez dans la lumière,\nLe ciel vous est rendu.','<p><strong>1.</strong> </p>\n<p>Tenons nos lampes prêtes, </p>\n<p>Chrétiens, préparons-nous </p>\n<p>Pour l&#39;heure où les trompettes </p>\n<p>Annonceront l&#39;Époux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Qu&#39;à répondre on s&#39;empresse,</strong> </p>\n<p><strong>Hosanna! Hosanna!</strong> </p>\n<p><strong>Et qu&#39;avec allégresse</strong> </p>\n<p><strong>On chante: Alléluia! (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voici déjà les anges, </p>\n<p>Avec eux les élus, </p>\n<p>Unissant leurs louanges </p>\n<p>En l&#39;honneur de Jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>Voici Jésus lui-même, </p>\n<p>Puissant, victorieux! </p>\n<p>De son pur diadème </p>\n<p>L&#39;éclat remplit les cieux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Venez, bénie du père </p>\n<p>Qui m&#39;avez attendu; </p>\n<p>Entrez dans la lumière, </p>\n<p>Le ciel vous est rendu. </p>\n','Tenons-nos-lampes-pretes',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1317,'4606a106-bf9a-48ec-97b7-87990f6d8b2b',0,0,'Oh! Croyez que Dieu vous Donne',127,'127_CE_fr','1.\nOh!  Croyez que Dieu vous donne\nTout ce qu\'il promet,\nUn Sauveur qui vous pardonne,\nUn Sauveur parfait,\nUn Sauveur plein de puissance\nSur la terre et dans les cieux,\nUn Sauveur dont la présence\nSeul rend heureux.\n\n2.\nCe Sauveur vous fera vive\nComme il a vécu,\nVous pourrez partout le suivre\nSans être vaincu.\nJusqu\'au bout dans la mêlée\nSon bras vous protégera,\nEt dans la sombre vallée\nIl vous conduira.\n\n3.\nOh!  Jésus, dis-leur toi-même\nQue ta forte main\nFait passer celui qui t\'aime\nPar un sûr chemin,\nQue tu veux de toute chute\nPréserver ton faible enfant,\nPour qu\'il sorte de la lutte\nPur et triomphant.\n\n4.\nOui, Seigneur, malgré l\'orage\nEt malgré la nuit,\nNous voulons prendre courage,\nForts de ton appui,\nEt joyeux, pleins d\'assurance,\nNous avancer vers le ciel,\nEn saluant à l\'avance\nLe jour éternel.','<p><strong>1.</strong> </p>\n<p>Oh!  Croyez que Dieu vous donne </p>\n<p>Tout ce qu&#39;il promet, </p>\n<p>Un Sauveur qui vous pardonne, </p>\n<p>Un Sauveur parfait, </p>\n<p>Un Sauveur plein de puissance </p>\n<p>Sur la terre et dans les cieux, </p>\n<p>Un Sauveur dont la présence </p>\n<p>Seul rend heureux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ce Sauveur vous fera vive </p>\n<p>Comme il a vécu, </p>\n<p>Vous pourrez partout le suivre </p>\n<p>Sans être vaincu. </p>\n<p>Jusqu&#39;au bout dans la mêlée </p>\n<p>Son bras vous protégera, </p>\n<p>Et dans la sombre vallée </p>\n<p>Il vous conduira. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh!  Jésus, dis-leur toi-même </p>\n<p>Que ta forte main </p>\n<p>Fait passer celui qui t&#39;aime </p>\n<p>Par un sûr chemin, </p>\n<p>Que tu veux de toute chute </p>\n<p>Préserver ton faible enfant, </p>\n<p>Pour qu&#39;il sorte de la lutte </p>\n<p>Pur et triomphant. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, Seigneur, malgré l&#39;orage </p>\n<p>Et malgré la nuit, </p>\n<p>Nous voulons prendre courage, </p>\n<p>Forts de ton appui, </p>\n<p>Et joyeux, pleins d&#39;assurance, </p>\n<p>Nous avancer vers le ciel, </p>\n<p>En saluant à l&#39;avance </p>\n<p>Le jour éternel. </p>\n','Oh!-Croyez-que-Dieu-vous-Donne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1318,'d09e8903-5b81-4a1b-a7d4-484b1413a69f',0,0,'Jésus Soit Avec Vous A Jamais',98,'98_CE_fr','1.\nJésus soit avec vous à jamais,\nVous guidant avec sagesse,\nVous entourant de tendresse,\nVous remplissant toujours de sa paix!\n\nRefrain\nAvec vous, toujours avec vous!\nOui, qu\'il soit tous les jours avec vous!\nAvec vous, toujours avec vous,\nJusqu\'au jour du dernier rendez-vous!\n\n2.\nAvec vous au milieu du danger,\nÀ l\'heure de la détresse,\nQu\'Il soit votre forteresse,\nQu\'Il soit toujours votre Bon Berger!\n\n3.\nAvec vous quand votre ciel est bleu,\nComme au jour de la tempête,\nQue son coeur soit la retraite\nOù vous retrouvez la paix de Dieu!\n\n4.\nEnfin, dans le dernier des combats,\nPour échanger cette terre\nContre la maison du Père,\nQue Jésus vous porte dans ses bras!','<p><strong>1.</strong> </p>\n<p>Jésus soit avec vous à jamais, </p>\n<p>Vous guidant avec sagesse, </p>\n<p>Vous entourant de tendresse, </p>\n<p>Vous remplissant toujours de sa paix! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Avec vous, toujours avec vous!</strong> </p>\n<p><strong>Oui, qu&#39;il soit tous les jours avec vous!</strong> </p>\n<p><strong>Avec vous, toujours avec vous,</strong> </p>\n<p><strong>Jusqu&#39;au jour du dernier rendez-vous!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Avec vous au milieu du danger, </p>\n<p>À l&#39;heure de la détresse, </p>\n<p>Qu&#39;Il soit votre forteresse, </p>\n<p>Qu&#39;Il soit toujours votre Bon Berger! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Avec vous quand votre ciel est bleu, </p>\n<p>Comme au jour de la tempête, </p>\n<p>Que son coeur soit la retraite </p>\n<p>Où vous retrouvez la paix de Dieu! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Enfin, dans le dernier des combats, </p>\n<p>Pour échanger cette terre </p>\n<p>Contre la maison du Père, </p>\n<p>Que Jésus vous porte dans ses bras! </p>\n','Jesus-Soit-Avec-Vous-A-Jamais',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1319,'8e13b979-a8b3-4f3c-9da2-b5618959ec58',0,0,'Du ciel, bientôt, Jesus va revenir',104,'104_CE_fr','1.\nDu ciel, bientôt, Jésus va revenir!\nSi c\'était aujourd\'hui?\nTous, radieux, nous le verront venir!\nSi c\'était aujourd\'hui?\nAlors tous les saints à sa vue,\nSes enfants sûrs de sa venue,\nIront avec Lui sur la nue!\nSi c\'était aujourd\'hui?\n\nRefrain\nGloire!  Gloire!  Jésus revient bientôt!…\nGloire!  Gloire!  Nous le couronneront!…\nGloire!  Gloire!  Et nous l\'adorerons!\nGloire!  Gloire!  Jésus revient bientôt!…\n\n2.\nDans ce beau ciel, le mal ne sera plus!…\nSi c\'était aujourd\'hui!…\nPlus de douleur, le regard de Jésus!\nSi c\'était aujourd\'hui?\nNotre âme après ce jour soupire!…\nMais déjà la nuit se retire,\nLe grand matin va bientôt luire!\nSi c\'était aujourd\'hui?…\n\n3.\nOh!  Que Jésus trouve en nous des coeurs droits,\nS\'il revient aujourd\'hui!\nVeillant, priant, dociles à sa voix,\nS\'il revient aujourd\'hui!\nAvons-nous cette assurance\nD\'être enlevés en sa présence,\nDans le séjour de sa puissance!\nS\'il revient aujoud\'hui?','<p><strong>1.</strong> </p>\n<p>Du ciel, bientôt, Jésus va revenir! </p>\n<p>Si c&#39;était aujourd&#39;hui? </p>\n<p>Tous, radieux, nous le verront venir! </p>\n<p>Si c&#39;était aujourd&#39;hui? </p>\n<p>Alors tous les saints à sa vue, </p>\n<p>Ses enfants sûrs de sa venue, </p>\n<p>Iront avec Lui sur la nue! </p>\n<p>Si c&#39;était aujourd&#39;hui? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire!  Gloire!  Jésus revient bientôt!…</strong> </p>\n<p><strong>Gloire!  Gloire!  Nous le couronneront!…</strong> </p>\n<p><strong>Gloire!  Gloire!  Et nous l&#39;adorerons!</strong> </p>\n<p><strong>Gloire!  Gloire!  Jésus revient bientôt!…</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans ce beau ciel, le mal ne sera plus!… </p>\n<p>Si c&#39;était aujourd&#39;hui!… </p>\n<p>Plus de douleur, le regard de Jésus! </p>\n<p>Si c&#39;était aujourd&#39;hui? </p>\n<p>Notre âme après ce jour soupire!… </p>\n<p>Mais déjà la nuit se retire, </p>\n<p>Le grand matin va bientôt luire! </p>\n<p>Si c&#39;était aujourd&#39;hui?… </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh!  Que Jésus trouve en nous des coeurs droits, </p>\n<p>S&#39;il revient aujourd&#39;hui! </p>\n<p>Veillant, priant, dociles à sa voix, </p>\n<p>S&#39;il revient aujourd&#39;hui! </p>\n<p>Avons-nous cette assurance </p>\n<p>D&#39;être enlevés en sa présence, </p>\n<p>Dans le séjour de sa puissance! </p>\n<p>S&#39;il revient aujoud&#39;hui? </p>\n','Du-ciel-bientot-Jesus-va-revenir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1320,'63fb8321-dea8-4a95-8d11-65191a82edfe',0,0,'Viens, o Créateur de nos âmes',111,'111_CE_fr','1.\nViens, ô Créateur de nos âmes,\nEsprit-Saint, Dieu de vérité!\nRemplis nos coeurs des pures flammes\nDe ton ardente charité!\n\n2.\nVerse en nos âmes tes lumières,\nVerse ton amour dans nos coeurs;\nPrête l\'oreille à nos prières,\nEt comble-nous de tes faveurs.\n\n3.\nViens, rends-nous vainqueurs du monde;\nÉcarte tous nos ennemis,\nEt de la paix la plus féconde,\nQue nos triomphes soient suivis!','<p><strong>1.</strong> </p>\n<p>Viens, ô Créateur de nos âmes, </p>\n<p>Esprit-Saint, Dieu de vérité! </p>\n<p>Remplis nos coeurs des pures flammes </p>\n<p>De ton ardente charité! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Verse en nos âmes tes lumières, </p>\n<p>Verse ton amour dans nos coeurs; </p>\n<p>Prête l&#39;oreille à nos prières, </p>\n<p>Et comble-nous de tes faveurs. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Viens, rends-nous vainqueurs du monde; </p>\n<p>Écarte tous nos ennemis, </p>\n<p>Et de la paix la plus féconde, </p>\n<p>Que nos triomphes soient suivis! </p>\n','Viens-o-Createur-de-nos-ames',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1321,'ce263026-6562-4794-afd3-9579dcfe3a3d',0,0,'Sur Toi je me repose',119,'119_CE_fr','1.\nSur Toi je me repose,\nÔ Jésus, mon Sauveur!\nFaut-il donc autre chose\nPour un pauvre pécheur?\nConduit par ta lumière,\nGardé par ton amour,\nVers la maison du Père,\nMarchant de jour en jour…\n\nRefrain\nSur Toi je me repose,\nÔ Jésus, mon Sauveur!\nFaut-il donc autre chose\nPour un pauvre pécheur?\n\n2.\nAh! Ma misère est grande,\nMais tu m\'as pardonné;\nSainte et vivante offrande,\nPour moi, tu t\'es donné;\nEt de toute souillure,\nPar le sang de ta croix,\nMon âme devient pure;\nTu l\'as dit, je le crois!\n\n3.\nMoi-même en sacrifice\nImmolé désormais,\nSeigneur, à ton service\nMe voici pour jamais!\nQu\'importe ma faiblesse,\nPuisque je t\'appartiens?\nTu n\'as point de richesse\nQui ne soit pour les tiens.\n\n4.\nEn Toi j\'ai la victoire,\nLa paix, la liberté;\nÀ Toi je rendrai gloire\nDurant l\'éternité.\nSi du bonheur qui passe\nLa source doit tarir,\nC\'est assez de grâce\nPour vivre et pour mourir.','<p><strong>1.</strong> </p>\n<p>Sur Toi je me repose, </p>\n<p>Ô Jésus, mon Sauveur! </p>\n<p>Faut-il donc autre chose </p>\n<p>Pour un pauvre pécheur? </p>\n<p>Conduit par ta lumière, </p>\n<p>Gardé par ton amour, </p>\n<p>Vers la maison du Père, </p>\n<p>Marchant de jour en jour… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Sur Toi je me repose,</strong> </p>\n<p><strong>Ô Jésus, mon Sauveur!</strong> </p>\n<p><strong>Faut-il donc autre chose</strong> </p>\n<p><strong>Pour un pauvre pécheur?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ah! Ma misère est grande, </p>\n<p>Mais tu m&#39;as pardonné; </p>\n<p>Sainte et vivante offrande, </p>\n<p>Pour moi, tu t&#39;es donné; </p>\n<p>Et de toute souillure, </p>\n<p>Par le sang de ta croix, </p>\n<p>Mon âme devient pure; </p>\n<p>Tu l&#39;as dit, je le crois! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Moi-même en sacrifice </p>\n<p>Immolé désormais, </p>\n<p>Seigneur, à ton service </p>\n<p>Me voici pour jamais! </p>\n<p>Qu&#39;importe ma faiblesse, </p>\n<p>Puisque je t&#39;appartiens? </p>\n<p>Tu n&#39;as point de richesse </p>\n<p>Qui ne soit pour les tiens. </p>\n<p><br/> <strong>4.</strong></p>\n<p>En Toi j&#39;ai la victoire, </p>\n<p>La paix, la liberté; </p>\n<p>À Toi je rendrai gloire </p>\n<p>Durant l&#39;éternité. </p>\n<p>Si du bonheur qui passe </p>\n<p>La source doit tarir, </p>\n<p>C&#39;est assez de grâce </p>\n<p>Pour vivre et pour mourir. </p>\n','Sur-Toi-je-me-repose',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1322,'392dd043-ef18-4f5b-9b83-aeb6d0ad64a2',0,0,'Oh! Que ton joug est facile',77,'77_CE_fr','1.\nOh! Que ton joug est facile!\nOh! Combien j\'aime ta loi!\nDieu sait, Dieu de l\'Évangile,\nElle est toujours devant moi.\nDe mes pas c\'est la lumière,\nC\'est le repos de mon coeur;\nMais, pour la voir tout entière,\nOuvre mes yeux, bon Sauveur!  (bis 2 lignes)\n\n2.\nNon, ta loi n\'est point pénible\nPour quiconque est né de Toi;\nToute victoire est possible\nÀ qui combat avec foi.\nSeigneur, dans ta forteresse,\nAucun mal ne m\'atteindra:\nSi je tremble en ma faiblesse,\nTa droite me soutiendra.  (bis 2 lignes)\n\n3.\nD\'un triste et rude esclavage\nAffranchi par Jésus-Christ,\nJ\'ai part à ton héritage,\nAu secours de ton Esprit.\nAu lieu d\'un Maître sévère,\nPrêt à juger et punir,\nJe sers le plus tendre Père,\nToujours prêt a me bénir.  (bis 2 lignes)\n\n4.\nDieu qui guides, qui consoles,\nJ\'ai connu que le bonheur\nC\'est de garder tes paroles,\nEt je les serre en mon coeur.\nFais-moi marcher dans ta voie\nEt me plaire en tes statuts:\nSi je cherche en Toi ma joie,\nJe ne serai pas confus.  (bis)','<p><strong>1.</strong> </p>\n<p>Oh! Que ton joug est facile! </p>\n<p>Oh! Combien j&#39;aime ta loi! </p>\n<p>Dieu sait, Dieu de l&#39;Évangile, </p>\n<p>Elle est toujours devant moi. </p>\n<p>De mes pas c&#39;est la lumière, </p>\n<p>C&#39;est le repos de mon coeur; </p>\n<p>Mais, pour la voir tout entière, </p>\n<p>Ouvre mes yeux, bon Sauveur!  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non, ta loi n&#39;est point pénible </p>\n<p>Pour quiconque est né de Toi; </p>\n<p>Toute victoire est possible </p>\n<p>À qui combat avec foi. </p>\n<p>Seigneur, dans ta forteresse, </p>\n<p>Aucun mal ne m&#39;atteindra: </p>\n<p>Si je tremble en ma faiblesse, </p>\n<p>Ta droite me soutiendra.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>D&#39;un triste et rude esclavage </p>\n<p>Affranchi par Jésus-Christ, </p>\n<p>J&#39;ai part à ton héritage, </p>\n<p>Au secours de ton Esprit. </p>\n<p>Au lieu d&#39;un Maître sévère, </p>\n<p>Prêt à juger et punir, </p>\n<p>Je sers le plus tendre Père, </p>\n<p>Toujours prêt a me bénir.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dieu qui guides, qui consoles, </p>\n<p>J&#39;ai connu que le bonheur </p>\n<p>C&#39;est de garder tes paroles, </p>\n<p>Et je les serre en mon coeur. </p>\n<p>Fais-moi marcher dans ta voie </p>\n<p>Et me plaire en tes statuts: </p>\n<p>Si je cherche en Toi ma joie, </p>\n<p>Je ne serai pas confus.  (bis) </p>\n','Oh!-Que-ton-joug-est-facile',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1323,'76789032-cfa5-4f16-9987-94c29a3ba0a2',0,0,'Brisant ses liens funèbres, alléluia',69,'69_CE_fr','1.\nBrisant ses liens funèbres, Alléluia!\nChrist est sorti des ténèbres; Alléluia!\nLe ciel, la terre ont chantés: Alléluia!\nJésus est ressuscité!   Alléluia!\n\n2.\nLes soldats, le sceau, la pierre, Alléluia!\nN\'ont pu Le regarder en terre; Alléluia!\nEt c\'est pour nous qu\'aujourd\'hui, Alléluia!\nLe ciel s\'ouvre devant Lui!   Alléluia!\n\n3.\nIl vit, notre Roi de gloire!  Alléluia!\nSépulcre, où est ta victoire?  Alléluia!\nIl a détruit sans effort,  Alléluia!\nLa puissance de la mort.   Alléluia!\n\n4.\nPuisque ta mort fut suivie,  Alléluia!\nDu triomphe de la vie,  Alléluia!\nJe veux, ô mon divin Roi,  Alléluia!\nMourir et naître avec Toi!  Alléluia!','<p><strong>1.</strong> </p>\n<p>Brisant ses liens funèbres, Alléluia! </p>\n<p>Christ est sorti des ténèbres; Alléluia! </p>\n<p>Le ciel, la terre ont chantés: Alléluia! </p>\n<p>Jésus est ressuscité!   Alléluia! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les soldats, le sceau, la pierre, Alléluia! </p>\n<p>N&#39;ont pu Le regarder en terre; Alléluia! </p>\n<p>Et c&#39;est pour nous qu&#39;aujourd&#39;hui, Alléluia! </p>\n<p>Le ciel s&#39;ouvre devant Lui!   Alléluia! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il vit, notre Roi de gloire!  Alléluia! </p>\n<p>Sépulcre, où est ta victoire?  Alléluia! </p>\n<p>Il a détruit sans effort,  Alléluia! </p>\n<p>La puissance de la mort.   Alléluia! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Puisque ta mort fut suivie,  Alléluia! </p>\n<p>Du triomphe de la vie,  Alléluia! </p>\n<p>Je veux, ô mon divin Roi,  Alléluia! </p>\n<p>Mourir et naître avec Toi!  Alléluia! </p>\n','Brisant-ses-liens-funebres-alleluia',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1324,'da01e1e5-8261-4155-8dbb-5ea8e683c802',0,0,'Christ est ressuscité',67,'67_CE_fr','1.\nChrist est ressuscité,\nQu\'en des chants joyeux\nSon triomphe en tous lieux\nSoit exalté!\n\nRefrain\nA toi la gloire et l\'honneur,\nO Sauveur, ô puissant Rédempteur!\nDu sépulcre tu sortis vainqueur,\nPrince de vie et Prince de paix.\nGloire à toi  (ter), gloire à jamais!\n\n2.\nChrist est ressuscité,\nCherche en Lui toujours,\nO peuple racheté,\nForce et secours!\n\n3.\nChrist est ressuscité,\nPar Lui nous vivrons\nEt dans l\'éternité\nNous règnerons!','<p><strong>1.</strong> </p>\n<p>Christ est ressuscité, </p>\n<p>Qu&#39;en des chants joyeux </p>\n<p>Son triomphe en tous lieux </p>\n<p>Soit exalté! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>A toi la gloire et l&#39;honneur,</strong> </p>\n<p><strong>O Sauveur, ô puissant Rédempteur!</strong> </p>\n<p><strong>Du sépulcre tu sortis vainqueur,</strong> </p>\n<p><strong>Prince de vie et Prince de paix.</strong> </p>\n<p><strong>Gloire à toi  (ter), gloire à jamais!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Christ est ressuscité, </p>\n<p>Cherche en Lui toujours, </p>\n<p>O peuple racheté, </p>\n<p>Force et secours! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Christ est ressuscité, </p>\n<p>Par Lui nous vivrons </p>\n<p>Et dans l&#39;éternité </p>\n<p>Nous règnerons! </p>\n','Christ-est-ressuscite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1325,'cc364719-54f3-4bf6-adfe-6b4fa6cb27d4',0,0,'Dieu lui-même, Ô Mystère',79,'79_CE_fr','1.\nDieu lui-même, ô mystère!\nDescendant sur la terre,\nÀ voulu se vêtir de notre infirmités\nChérif et misérable,\nIl naît dans une étable…\n\nRefrain\nNous dirons son amour\nPendant l\'éternité.  (bis)\n\n2.\nSur une croix infâme\nVoyez-le rendre l\'âme,\nDe ses propres bourreaux portant l\'iniquité,\nPour briser notre chaîne\nIl subit notre peine…\n\n3.\nComme on sort d\'un vain rêve,\nIl s\'éveille… il se lève…\nTressaillez, cieux et terre: il est ressuscité!\nIl nous ouvre la voie,\nIl nous donne la joie…\n\n4.\nRentré dans la lumière,\nÀ la droite du Père,\nIl conserve envers nous la même charité,\nPour nous venir en aide\nSans cesse il intercède…','<p><strong>1.</strong> </p>\n<p>Dieu lui-même, ô mystère! </p>\n<p>Descendant sur la terre, </p>\n<p>À voulu se vêtir de notre infirmités </p>\n<p>Chérif et misérable, </p>\n<p>Il naît dans une étable… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nous dirons son amour</strong> </p>\n<p><strong>Pendant l&#39;éternité.  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sur une croix infâme </p>\n<p>Voyez-le rendre l&#39;âme, </p>\n<p>De ses propres bourreaux portant l&#39;iniquité, </p>\n<p>Pour briser notre chaîne </p>\n<p>Il subit notre peine… </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme on sort d&#39;un vain rêve, </p>\n<p>Il s&#39;éveille… il se lève… </p>\n<p>Tressaillez, cieux et terre: il est ressuscité! </p>\n<p>Il nous ouvre la voie, </p>\n<p>Il nous donne la joie… </p>\n<p><br/> <strong>4.</strong></p>\n<p>Rentré dans la lumière, </p>\n<p>À la droite du Père, </p>\n<p>Il conserve envers nous la même charité, </p>\n<p>Pour nous venir en aide </p>\n<p>Sans cesse il intercède… </p>\n','Dieu-lui-meme-O-Mystere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1326,'1b0b9e7e-b60b-4d74-a814-582208f08d93',0,0,'Le sauveur est ressuscité',70,'70_CE_fr','1.\nLe Sauveur est ressuscité!  (bis)\nAlléluia!  L\'enfer succombe.\nPlein de gloire et de majesté,\nJésus triomphe de la tombe!\n\n2.\nÂmes en deuil, ne pleurez pas!  (bis)\nJésus est votre délivrance;\nDans la nuit sombre du trépas,\nIl fait rayonner l\'espérance.\n\n3.\nOui, gloire à Toi, mon Rédempteur!  (bis)\nQu\'à ton nom tout genou fléchisse,\nEt que la terre, ô mon Sauveur,\nPour t\'adorer au ciel s\'unisse.','<p><strong>1.</strong> </p>\n<p>Le Sauveur est ressuscité!  (bis) </p>\n<p>Alléluia!  L&#39;enfer succombe. </p>\n<p>Plein de gloire et de majesté, </p>\n<p>Jésus triomphe de la tombe! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Âmes en deuil, ne pleurez pas!  (bis) </p>\n<p>Jésus est votre délivrance; </p>\n<p>Dans la nuit sombre du trépas, </p>\n<p>Il fait rayonner l&#39;espérance. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, gloire à Toi, mon Rédempteur!  (bis) </p>\n<p>Qu&#39;à ton nom tout genou fléchisse, </p>\n<p>Et que la terre, ô mon Sauveur, </p>\n<p>Pour t&#39;adorer au ciel s&#39;unisse. </p>\n','Le-sauveur-est-ressuscite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1327,'80b471b8-ec21-41cd-8476-fd14908944f0',0,0,'Saisis ma main craintive',78,'78_CE_fr','1.\nSaisis ma main craintive\nEt conduis-moi;\nFais que toujours je vive\nPlus près de Toi.\nSans Toi, mon tendre Père,\nPour me guider,\nJe ne sais sur la terre\nComment marcher.\n\n2.\nQue ta main me dispense\nJoie ou douleur,\nPaisible en ta présence\nGarde mon coeur;\nEn Toi pour toute chose,\nSe confiant,\nÀ tes pieds se repose\nTon faible enfant.\n\n3.\nQuand la nuit la plus noire\nTe voilerait,\nTon bras jusqu\'à la gloire\nMe porterait.\nSaisis ma main craintive\nEt conduis-moi;\nFais toujours je vive\nPlus près de Toi!','<p><strong>1.</strong> </p>\n<p>Saisis ma main craintive </p>\n<p>Et conduis-moi; </p>\n<p>Fais que toujours je vive </p>\n<p>Plus près de Toi. </p>\n<p>Sans Toi, mon tendre Père, </p>\n<p>Pour me guider, </p>\n<p>Je ne sais sur la terre </p>\n<p>Comment marcher. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que ta main me dispense </p>\n<p>Joie ou douleur, </p>\n<p>Paisible en ta présence </p>\n<p>Garde mon coeur; </p>\n<p>En Toi pour toute chose, </p>\n<p>Se confiant, </p>\n<p>À tes pieds se repose </p>\n<p>Ton faible enfant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand la nuit la plus noire </p>\n<p>Te voilerait, </p>\n<p>Ton bras jusqu&#39;à la gloire </p>\n<p>Me porterait. </p>\n<p>Saisis ma main craintive </p>\n<p>Et conduis-moi; </p>\n<p>Fais toujours je vive </p>\n<p>Plus près de Toi! </p>\n','Saisis-ma-main-craintive',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1328,'90819392-645b-49ae-b441-2befa9668b38',0,0,'Douce Clarté',93,'93_CE_fr','1.\nDouce clarté,\nDans la nuit qui m\'enserre,\nDirige-moi!\nLa route est noire,\nEt longue et solitaire,\nDirige-moi!\nBrille à mes pieds; je ne demande pas\nAvoir de loin: guide-moi pas à pas!\n\n2.\nAh!  Je n\'ai pas\nToujours désiré d\'être\nConduit par toi;\nMais aujourd\'hui\nJ\'ai retrouvé mon Maître:\nDirige-moi!\nJe reconnais mes fautes du passé,\nÔ Dieu, pardonne au pèlerin lassé!\n\n3.\nN\'étais-je pas,\nMême en mes jours de doute,\nConduit par Toi!\nRavins, torrents,\nEn vain barrent ma route:\nDirige-moi!\nJusqu\'à l\'aurore où me seront rendus\nMes bien-aimés, un instant disparus!','<p><strong>1.</strong> </p>\n<p>Douce clarté, </p>\n<p>Dans la nuit qui m&#39;enserre, </p>\n<p>Dirige-moi! </p>\n<p>La route est noire, </p>\n<p>Et longue et solitaire, </p>\n<p>Dirige-moi! </p>\n<p>Brille à mes pieds; je ne demande pas </p>\n<p>Avoir de loin: guide-moi pas à pas! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ah!  Je n&#39;ai pas </p>\n<p>Toujours désiré d&#39;être </p>\n<p>Conduit par toi; </p>\n<p>Mais aujourd&#39;hui </p>\n<p>J&#39;ai retrouvé mon Maître: </p>\n<p>Dirige-moi! </p>\n<p>Je reconnais mes fautes du passé, </p>\n<p>Ô Dieu, pardonne au pèlerin lassé! </p>\n<p><br/> <strong>3.</strong></p>\n<p>N&#39;étais-je pas, </p>\n<p>Même en mes jours de doute, </p>\n<p>Conduit par Toi! </p>\n<p>Ravins, torrents, </p>\n<p>En vain barrent ma route: </p>\n<p>Dirige-moi! </p>\n<p>Jusqu&#39;à l&#39;aurore où me seront rendus </p>\n<p>Mes bien-aimés, un instant disparus! </p>\n','Douce-Clarte',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1329,'d6769239-4be0-4199-937e-60965f2cc178',0,0,'Tiens Dans Ta Main',83,'83_CE_fr','1.\nTiens dans ta main,\nTa main puissante et forte,\nTon faible enfant\nQui seul ne peut marcher.\nVers toi, Seigneur,\nTout mon désir se porte,\nSur mon chemin\nGarde-moi de broncher!\n\n2.\nTiens dans ta main\nTous les jours de ma vie,\nQu\'ils soient des jours\nDe peine ou de bonheur;\nQue de t\'aimer,\nSoit toute mon envie,\nQue je sois à\nToi, Jésus, mon Sauveur!\n\n3.\nTiens dans ta main\nMa main parfois tremblante,\nQuand vient l\'épreuve\nEt ses jours de douleurs.\nDonne à ma foi,\nSouvent si chancelante,\nPlus de douceur,\nDe paix et de ferveur!\n\n4.\nTiens dans ta main\nMon âme toute entière;\nRévèle-moi,\nJésus, tout mon amour;\nSois mon rocher,\nMon ancre, ma lumière,\nMoi Roi, mon Maître,\nO Jésus, chaque jour!','<p><strong>1.</strong> </p>\n<p>Tiens dans ta main, </p>\n<p>Ta main puissante et forte, </p>\n<p>Ton faible enfant </p>\n<p>Qui seul ne peut marcher. </p>\n<p>Vers toi, Seigneur, </p>\n<p>Tout mon désir se porte, </p>\n<p>Sur mon chemin </p>\n<p>Garde-moi de broncher! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tiens dans ta main </p>\n<p>Tous les jours de ma vie, </p>\n<p>Qu&#39;ils soient des jours </p>\n<p>De peine ou de bonheur; </p>\n<p>Que de t&#39;aimer, </p>\n<p>Soit toute mon envie, </p>\n<p>Que je sois à </p>\n<p>Toi, Jésus, mon Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tiens dans ta main </p>\n<p>Ma main parfois tremblante, </p>\n<p>Quand vient l&#39;épreuve </p>\n<p>Et ses jours de douleurs. </p>\n<p>Donne à ma foi, </p>\n<p>Souvent si chancelante, </p>\n<p>Plus de douceur, </p>\n<p>De paix et de ferveur! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tiens dans ta main </p>\n<p>Mon âme toute entière; </p>\n<p>Révèle-moi, </p>\n<p>Jésus, tout mon amour; </p>\n<p>Sois mon rocher, </p>\n<p>Mon ancre, ma lumière, </p>\n<p>Moi Roi, mon Maître, </p>\n<p>O Jésus, chaque jour! </p>\n','Tiens-Dans-Ta-Main',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1330,'51b14263-5754-4b99-bd8a-47e9d1836a92',0,0,'Je Connais Un Guide Infaillible',97,'97_CE_fr','1.\nJe connais un guide infaillible\nQui, constamment, veille sur moi;\nSur ses pas je poursuis, paisible,\nLa sainte course de la foi.\n\nRefrain\nConduis par toi, céleste guide,\nJe me dirige vers les cieux;\nEt mon sentier, sous ton égide,\nDevient toujours plus radieux.\n\n2.\nParfois ma route est fort obscure,\nSans un seul rayon lumineux,\nMais quand mon pauvre coeur murmure,\nMon guide me soutient des cieux.\n\n3.\nQuand au dehors, Satan m\'assaille,\nQuand au dedans tout est bien noir,\nMon guide, au fort de la bataille,\nMe dit: «Mets en moi ton espoir!»\n\n4.\nAinsi, tout le long de la route,\nHier, aujourd\'hui, jusqu\'à la fin,\nPrès du guide, je ne redoute,\nNi pleurs, ni chute, ni chagrin.','<p><strong>1.</strong> </p>\n<p>Je connais un guide infaillible </p>\n<p>Qui, constamment, veille sur moi; </p>\n<p>Sur ses pas je poursuis, paisible, </p>\n<p>La sainte course de la foi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Conduis par toi, céleste guide,</strong> </p>\n<p><strong>Je me dirige vers les cieux;</strong> </p>\n<p><strong>Et mon sentier, sous ton égide,</strong> </p>\n<p><strong>Devient toujours plus radieux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Parfois ma route est fort obscure, </p>\n<p>Sans un seul rayon lumineux, </p>\n<p>Mais quand mon pauvre coeur murmure, </p>\n<p>Mon guide me soutient des cieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand au dehors, Satan m&#39;assaille, </p>\n<p>Quand au dedans tout est bien noir, </p>\n<p>Mon guide, au fort de la bataille, </p>\n<p>Me dit: «Mets en moi ton espoir!» </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ainsi, tout le long de la route, </p>\n<p>Hier, aujourd&#39;hui, jusqu&#39;à la fin, </p>\n<p>Près du guide, je ne redoute, </p>\n<p>Ni pleurs, ni chute, ni chagrin. </p>\n','Je-Connais-Un-Guide-Infaillible',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1331,'8431f8ea-a509-4e66-8e63-4b85086e3329',0,0,'Pour triompher dans les combats',115,'115_CE_fr','1.\nPour triompher dans les combats,\nQuelle est notre force ici-bas?\nQuelle est la divine lumière,\nQui nous dirige et nous éclaire\nDans la nuit sombre de l\'erreur?\nC\'est la Parole du Seigneur.\n\n2.\nQuel est le glaive à deux tranchants\nDont les coups sont assez puissant\nPour briser larme la plus dure,\nEt, la plus forte armure,\nPour pénétrer jusques au coeur?\nC\'est la Parole du Seigneur.\n\n3.\nQuel est le vent impétueux\nQui mugit et descend des cieux,\nEt don\'t la grave et sévère,\nSemblable à celle du tonnerre,\nFait trembler l\'âme du pécheur?\nC\'est la Parole du Seigneur.\n\n4.\nQuel est ce chant suave et doux\nQui, d\'en haut venant jusqu\'à nous,\nParle de grâce et de clémence,\nRéveille en nos coeurs l\'espérance\nEt calme partout la douleur?\nC\'est la Parole du Seigneur.','<p><strong>1.</strong> </p>\n<p>Pour triompher dans les combats, </p>\n<p>Quelle est notre force ici-bas? </p>\n<p>Quelle est la divine lumière, </p>\n<p>Qui nous dirige et nous éclaire </p>\n<p>Dans la nuit sombre de l&#39;erreur? </p>\n<p>C&#39;est la Parole du Seigneur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quel est le glaive à deux tranchants </p>\n<p>Dont les coups sont assez puissant </p>\n<p>Pour briser larme la plus dure, </p>\n<p>Et, la plus forte armure, </p>\n<p>Pour pénétrer jusques au coeur? </p>\n<p>C&#39;est la Parole du Seigneur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quel est le vent impétueux </p>\n<p>Qui mugit et descend des cieux, </p>\n<p>Et don&#39;t la grave et sévère, </p>\n<p>Semblable à celle du tonnerre, </p>\n<p>Fait trembler l&#39;âme du pécheur? </p>\n<p>C&#39;est la Parole du Seigneur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quel est ce chant suave et doux </p>\n<p>Qui, d&#39;en haut venant jusqu&#39;à nous, </p>\n<p>Parle de grâce et de clémence, </p>\n<p>Réveille en nos coeurs l&#39;espérance </p>\n<p>Et calme partout la douleur? </p>\n<p>C&#39;est la Parole du Seigneur. </p>\n','Pour-triompher-dans-les-combats',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1332,'a5a44056-14ed-4a5f-8bfc-a52e0e1eea6f',0,0,'La Parole du Seigneur',116,'116_CE_fr','1.\nLa Parole du Seigneur\nEst la source jaillissante,\nOù peut toujours notre coeur\nÉtancher sa soif ardente.\nMon Dieu, du Livre sacré\nRépands la grâce infinie;\nConduis tout coeur altéré\nÀ la fontaine de vie.\n\n2.\nC\'est un flambeau dans la main,\nDe sa splendeur vive et pure,\nÉclairant notre chemin\nÀ travers la nuit obscure.\nOh!  De ce divin flambeau,\nSeigneur, sur notre carrière,\nChaque jour, jusqu\'au tombeau,\nFais rayonner la lumière!\n\n3.\nC\'est un grain de sénevé\nQue l\'Esprit de Dieu féconde;\nCar, à peine a-t-il levé,\nQu\'il envahit tout le monde.\nQue le moindre grain planté\nDe la divine semence,\nIci, pour l\'éternité,\nFructifie en abondance!','<p><strong>1.</strong> </p>\n<p>La Parole du Seigneur </p>\n<p>Est la source jaillissante, </p>\n<p>Où peut toujours notre coeur </p>\n<p>Étancher sa soif ardente. </p>\n<p>Mon Dieu, du Livre sacré </p>\n<p>Répands la grâce infinie; </p>\n<p>Conduis tout coeur altéré </p>\n<p>À la fontaine de vie. </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est un flambeau dans la main, </p>\n<p>De sa splendeur vive et pure, </p>\n<p>Éclairant notre chemin </p>\n<p>À travers la nuit obscure. </p>\n<p>Oh!  De ce divin flambeau, </p>\n<p>Seigneur, sur notre carrière, </p>\n<p>Chaque jour, jusqu&#39;au tombeau, </p>\n<p>Fais rayonner la lumière! </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est un grain de sénevé </p>\n<p>Que l&#39;Esprit de Dieu féconde; </p>\n<p>Car, à peine a-t-il levé, </p>\n<p>Qu&#39;il envahit tout le monde. </p>\n<p>Que le moindre grain planté </p>\n<p>De la divine semence, </p>\n<p>Ici, pour l&#39;éternité, </p>\n<p>Fructifie en abondance! </p>\n','La-Parole-du-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1333,'f38f24b6-d1a2-47eb-b9f8-3e8f205122fe',0,0,'Ne crains rien, je t’aime',121,'121_CE_fr','1.\nNe crains rien, je t\'aime!\nJe suis avec toi!\nPromesse suprême,\nQui soutient ma foi.\nLa sombre vallée\nN\'a plus de terreur;\nL\'âme consolée,\nJe marche avec mon Sauveur.\n\nRefrain\nNon, jamais tout seul!  (bis)\nJésus, mon Sauveur, me garde,\nJamais ne me laisse seul.\nNon, jamais tout seul!  (bis)\nJésus, mon Sauveur, me garde,\nJe ne suis jamais tout seul.\n\n2.\nL\'aube matinière\nNe luit qu\'aux beaux jours;\nJésus, ma lumière,\nM\'éclaire toujours!\nQuand je perds de vue\nL\'astre radieux,\nÀ travers la nue,\nJésus me montre les cieux!\n\n3.\nLes dangers accourent,\nSubtils, inconnus:\nDe près ils m\'entourent,\nPlus près est Jésus,\nQui, dans le voyage,\nMe redit: «C\'est moi!\nNe crains rien, courage!\nJe suis toujours avec toi!»','<p><strong>1.</strong> </p>\n<p>Ne crains rien, je t&#39;aime! </p>\n<p>Je suis avec toi! </p>\n<p>Promesse suprême, </p>\n<p>Qui soutient ma foi. </p>\n<p>La sombre vallée </p>\n<p>N&#39;a plus de terreur; </p>\n<p>L&#39;âme consolée, </p>\n<p>Je marche avec mon Sauveur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Non, jamais tout seul!  (bis)</strong> </p>\n<p><strong>Jésus, mon Sauveur, me garde,</strong> </p>\n<p><strong>Jamais ne me laisse seul.</strong> </p>\n<p><strong>Non, jamais tout seul!  (bis)</strong> </p>\n<p><strong>Jésus, mon Sauveur, me garde,</strong> </p>\n<p><strong>Je ne suis jamais tout seul.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>L&#39;aube matinière </p>\n<p>Ne luit qu&#39;aux beaux jours; </p>\n<p>Jésus, ma lumière, </p>\n<p>M&#39;éclaire toujours! </p>\n<p>Quand je perds de vue </p>\n<p>L&#39;astre radieux, </p>\n<p>À travers la nue, </p>\n<p>Jésus me montre les cieux! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Les dangers accourent, </p>\n<p>Subtils, inconnus: </p>\n<p>De près ils m&#39;entourent, </p>\n<p>Plus près est Jésus, </p>\n<p>Qui, dans le voyage, </p>\n<p>Me redit: «C&#39;est moi! </p>\n<p>Ne crains rien, courage! </p>\n<p>Je suis toujours avec toi!» </p>\n','Ne-crains-rien-je-t\'aime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1334,'0b95a08e-a618-4c95-880f-8b6f65fbdb18',0,0,'Jésus Est Au Milieu De Nous',88,'88_CE_fr','1.\nJésus est au milieu de nous,\nSon regard s\'abaisse sur nous,\nSa douce voix, l\'entendez-vous?\nJe veux vous bénir tous!  (bis)\nSa douce voix, l\'entendez-vous?\nJe veux vous bénir tous!\n\n2.\nJésus est au milieu de nous,\nSon regard s\'abaisse sur nous,\nSa douce voix, l\'entendez-vous?\nJe veux vous sauver tous!  (bis)\nSa douce voix, l\'entendez-vous?\nJe veux vous sauver tous!\n\n3.\nJésus est au milieu de nous,\nSon regard s\'abaisse sur nous,\nSa douce voix, l\'entendez-vous?\nOh!  Je vous aime tous!  (bis)\nAs douce voix, l\'entendez-vous?\nOh!  Je vous aime tous!','<p><strong>1.</strong> </p>\n<p>Jésus est au milieu de nous, </p>\n<p>Son regard s&#39;abaisse sur nous, </p>\n<p>Sa douce voix, l&#39;entendez-vous? </p>\n<p>Je veux vous bénir tous!  (bis) </p>\n<p>Sa douce voix, l&#39;entendez-vous? </p>\n<p>Je veux vous bénir tous! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus est au milieu de nous, </p>\n<p>Son regard s&#39;abaisse sur nous, </p>\n<p>Sa douce voix, l&#39;entendez-vous? </p>\n<p>Je veux vous sauver tous!  (bis) </p>\n<p>Sa douce voix, l&#39;entendez-vous? </p>\n<p>Je veux vous sauver tous! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus est au milieu de nous, </p>\n<p>Son regard s&#39;abaisse sur nous, </p>\n<p>Sa douce voix, l&#39;entendez-vous? </p>\n<p>Oh!  Je vous aime tous!  (bis) </p>\n<p>As douce voix, l&#39;entendez-vous? </p>\n<p>Oh!  Je vous aime tous! </p>\n','Jesus-Est-Au-Milieu-De-Nous',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1335,'9270e12a-62fa-4484-8e9a-747984f1f9a7',0,0,'Saint-Esprit, notre Créateur',106,'106_CE_fr','1.\nSaint-Esprit, notre Créateur\nEt notre grand Consolateur,\nRends-toi le Maître de nos âmes!\nEsprit du Dieu de vérité,\nÉclaire-nous par ta clarté\nEt nous embrase de tes flammes!\nEsprit de Jésus notre Roi,\nSoumets-nous à ta douce loi.\n\n2.\nHumilie et change nos coeurs;\nRègle notre vie et nos moeurs;\nProduis en nous la repentance,\nUne parfaite humilité,\nUne sincère charité\nUne constante patience.\nOpère en nous tous puissamment\nEt fais-nous vivre saintement.\n\n3.\nRépands dans nos âmes ta paix;\nDaigne inspirer tous nos projets;\nImprime en nos coeurs ta parole;\nTriomphe de nos passions;\nDans toutes nos afflictions\nExauce-nous et nous console;\nSoutiens-nous dans tous nos combats;\nRedresse et conduis tous nos pas.','<p><strong>1.</strong> </p>\n<p>Saint-Esprit, notre Créateur </p>\n<p>Et notre grand Consolateur, </p>\n<p>Rends-toi le Maître de nos âmes! </p>\n<p>Esprit du Dieu de vérité, </p>\n<p>Éclaire-nous par ta clarté </p>\n<p>Et nous embrase de tes flammes! </p>\n<p>Esprit de Jésus notre Roi, </p>\n<p>Soumets-nous à ta douce loi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Humilie et change nos coeurs; </p>\n<p>Règle notre vie et nos moeurs; </p>\n<p>Produis en nous la repentance, </p>\n<p>Une parfaite humilité, </p>\n<p>Une sincère charité </p>\n<p>Une constante patience. </p>\n<p>Opère en nous tous puissamment </p>\n<p>Et fais-nous vivre saintement. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Répands dans nos âmes ta paix; </p>\n<p>Daigne inspirer tous nos projets; </p>\n<p>Imprime en nos coeurs ta parole; </p>\n<p>Triomphe de nos passions; </p>\n<p>Dans toutes nos afflictions </p>\n<p>Exauce-nous et nous console; </p>\n<p>Soutiens-nous dans tous nos combats; </p>\n<p>Redresse et conduis tous nos pas. </p>\n','Saint-Esprit-notre-Createur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1336,'62ccad6e-75a8-4188-a663-79fe55b8c95a',0,0,'O Saint-Esprit, Esprit de vie',109,'109_CE_fr','1.\nO Saint-Esprit, Esprit de vie,\nDe lumière et de foi,\nSource de puissance infinie,\nDans ma faiblesse, à toi je crie:\nAccomplis tout en moi!\n\n2.\nJ\'ai compris que, sans ta présence,\nTout est sombre ici-bas.\nJe veux, dans chaque circonstance,\nMe placer sous ta dépendance:\nTu me dirigeras.\n\n3.\nConduis-moi, selon ta promesse,\nEn toute vérité.\nSois mon secours, sois ma sagesse;\nFais que je marche sans faiblesse\nÀ ta sainte clarté.\n\n4.\nInspire-moi chaque prière;\nGarde-moi de l\'erreur;\nÉclaire-moi de ta lumière,\nViens transformer ma vie entière:\nEn tout rends-moi vainqueur!','<p><strong>1.</strong> </p>\n<p>O Saint-Esprit, Esprit de vie, </p>\n<p>De lumière et de foi, </p>\n<p>Source de puissance infinie, </p>\n<p>Dans ma faiblesse, à toi je crie: </p>\n<p>Accomplis tout en moi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai compris que, sans ta présence, </p>\n<p>Tout est sombre ici-bas. </p>\n<p>Je veux, dans chaque circonstance, </p>\n<p>Me placer sous ta dépendance: </p>\n<p>Tu me dirigeras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Conduis-moi, selon ta promesse, </p>\n<p>En toute vérité. </p>\n<p>Sois mon secours, sois ma sagesse; </p>\n<p>Fais que je marche sans faiblesse </p>\n<p>À ta sainte clarté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Inspire-moi chaque prière; </p>\n<p>Garde-moi de l&#39;erreur; </p>\n<p>Éclaire-moi de ta lumière, </p>\n<p>Viens transformer ma vie entière: </p>\n<p>En tout rends-moi vainqueur! </p>\n','O-Saint-Esprit-Esprit-de-vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1337,'6d8b0f22-f38b-4a30-9e53-ddd3669727ca',0,0,'Quand Le Vol De La Tempête',126,'126_CE_fr','1.\nQuand le vol de la tempête\nVient assombrir ton ciel bleu,\nAu lieu de baisser la tête,\nCompte les bienfaits de Dieu.\n\nRefrain\nCompte les bienfaits de Dieu,\nMets-les tous devant tes yeux,\nTu verras, en adorant,\nCombien le nombre en est grand.\n\n2.\nQuand, sur la route glissante\nTu chancelles sous ta croix,\nPense à cette main puissante\nQui t\'a béni tant de fois.\n\n3.\nSi tu perds dans le voyage\nPlus d\'un cher et doux trésor,\nPense au divin héritage\nQui là-haut te reste encore.\n\n4.\nBénis donc, bénis sans cesse\nCe Père qui, chaque jour\nRépand sur toi la richesse\nDe son merveilleux amour.','<p><strong>1.</strong> </p>\n<p>Quand le vol de la tempête </p>\n<p>Vient assombrir ton ciel bleu, </p>\n<p>Au lieu de baisser la tête, </p>\n<p>Compte les bienfaits de Dieu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Compte les bienfaits de Dieu,</strong> </p>\n<p><strong>Mets-les tous devant tes yeux,</strong> </p>\n<p><strong>Tu verras, en adorant,</strong> </p>\n<p><strong>Combien le nombre en est grand.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand, sur la route glissante </p>\n<p>Tu chancelles sous ta croix, </p>\n<p>Pense à cette main puissante </p>\n<p>Qui t&#39;a béni tant de fois. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si tu perds dans le voyage </p>\n<p>Plus d&#39;un cher et doux trésor, </p>\n<p>Pense au divin héritage </p>\n<p>Qui là-haut te reste encore. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bénis donc, bénis sans cesse </p>\n<p>Ce Père qui, chaque jour </p>\n<p>Répand sur toi la richesse </p>\n<p>De son merveilleux amour. </p>\n','Quand-Le-Vol-De-La-Tempete',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1338,'c60f237e-a7f8-4245-a3e6-87370b8570cf',0,0,'Chantons, chantons sans cesse',72,'72_CE_fr','1.\nChantons, chantons sans cesse\nLa bonté du Seigneur!\nQu\'une sainte allégresse\nRemplisse notre coeur!\nUn salut éternel\nEst descendu du ciel:\nNous avons un Sauveur!  (bis)\n\n2.\nVers le trône de la grâce,\nSi nous levons les yeux,\nNous rencontrons la face\nD\'un Sauveur glorieux.\nIl est notre avocat;\nPour les siens, il combat,\nToujours victorieux!  (bis)\n\n3.\nBientôt, vêtu de gloire,\nDu ciel il reviendra…\nConsommant sa victoire,\nEt nous affranchira;\nEt son heureux enfant,\nAvec lui triomphant,\nTel qu\'il est le verra.  (bis)','<p><strong>1.</strong> </p>\n<p>Chantons, chantons sans cesse </p>\n<p>La bonté du Seigneur! </p>\n<p>Qu&#39;une sainte allégresse </p>\n<p>Remplisse notre coeur! </p>\n<p>Un salut éternel </p>\n<p>Est descendu du ciel: </p>\n<p>Nous avons un Sauveur!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vers le trône de la grâce, </p>\n<p>Si nous levons les yeux, </p>\n<p>Nous rencontrons la face </p>\n<p>D&#39;un Sauveur glorieux. </p>\n<p>Il est notre avocat; </p>\n<p>Pour les siens, il combat, </p>\n<p>Toujours victorieux!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientôt, vêtu de gloire, </p>\n<p>Du ciel il reviendra… </p>\n<p>Consommant sa victoire, </p>\n<p>Et nous affranchira; </p>\n<p>Et son heureux enfant, </p>\n<p>Avec lui triomphant, </p>\n<p>Tel qu&#39;il est le verra.  (bis) </p>\n','Chantons-chantons-sans-cesse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1339,'df353022-24a8-401f-990a-271f63f6d271',0,0,'Jésus, Au Nom Saint Et Doux',92,'92_CE_fr','1.\nJésus, au nom saint et doux,\nQui voulus porter pour nous\nLe poids du divin courroux,\nNos âmes t\'implorent.\n\n2.\nPar le jour où tu fléchis,\nSous l\'opprobre et le mépris,\nPar la mort que tu souffris,\nNos âmes t\'implorent.\n\n3.\nPar l\'heure où Gethsémané\nT\'a vu gémir prosterné,\nEt des tiens abandonné,\nNos âmes t\'implorent.\n\n4.\nPar ton corps qui fut percé,\nEt sur la croix exposé,\nPar ton sang pour nous versé,\nNos âmes t\'implorent.','<p><strong>1.</strong> </p>\n<p>Jésus, au nom saint et doux, </p>\n<p>Qui voulus porter pour nous </p>\n<p>Le poids du divin courroux, </p>\n<p>Nos âmes t&#39;implorent. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Par le jour où tu fléchis, </p>\n<p>Sous l&#39;opprobre et le mépris, </p>\n<p>Par la mort que tu souffris, </p>\n<p>Nos âmes t&#39;implorent. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Par l&#39;heure où Gethsémané </p>\n<p>T&#39;a vu gémir prosterné, </p>\n<p>Et des tiens abandonné, </p>\n<p>Nos âmes t&#39;implorent. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par ton corps qui fut percé, </p>\n<p>Et sur la croix exposé, </p>\n<p>Par ton sang pour nous versé, </p>\n<p>Nos âmes t&#39;implorent. </p>\n','Jesus-Au-Nom-Saint-Et-Doux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1340,'87222b9b-a589-4cf7-b805-82ab55d5320c',0,0,'Promesse du Père',108,'108_CE_fr','1.\nPromesse du Père,\nEsprit glorieux!\nToi dans la lumière\nDessille les yeux!\n\nRefrain\nVeuille sur nous descendre,\nSaint Consolateur!\nEn nos coeurs viens répandre\nL\'amour du Sauveur.\n\n2.\nRanime le zèle\nDu coeur triste et las;\nDu pied qui chancelle\nRaffermis les pas.\n\n3.\nComble notre attente;\nEn nous fais jaillir\nLa source abondante\nQui ne peut tarir.\n\n4.\nViens vivre en notre âme,\nEsprit de Jésus!\nQue ta sainte flamme\nNe pâlisse plus!','<p><strong>1.</strong> </p>\n<p>Promesse du Père, </p>\n<p>Esprit glorieux! </p>\n<p>Toi dans la lumière </p>\n<p>Dessille les yeux! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Veuille sur nous descendre,</strong> </p>\n<p><strong>Saint Consolateur!</strong> </p>\n<p><strong>En nos coeurs viens répandre</strong> </p>\n<p><strong>L&#39;amour du Sauveur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ranime le zèle </p>\n<p>Du coeur triste et las; </p>\n<p>Du pied qui chancelle </p>\n<p>Raffermis les pas. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comble notre attente; </p>\n<p>En nous fais jaillir </p>\n<p>La source abondante </p>\n<p>Qui ne peut tarir. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Viens vivre en notre âme, </p>\n<p>Esprit de Jésus! </p>\n<p>Que ta sainte flamme </p>\n<p>Ne pâlisse plus! </p>\n','Promesse-du-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1341,'b5e7d268-f0a9-4f11-9576-ee8b1f7e8f69',0,0,'Romps-nous le pain de vie',112,'112_CE_fr','1.\nRomps-nous le pain de vie!\nQue ta bonté,\nSeigneur, nous rassasie\nDe vérité!\nAmour qui nous fait vivre,\nRévèle-Toi!\nParle dans le saint Livre\nÀ notre foi!\n\n2.\nO toi don\'t la clémence\nCréa du pain\nPour une foule immense\nMourant de faim.\nVois, ton peuple se presse\nAutour de Toi…\nSecours notre détresse\nEt notre foi!\n\n3.\nC\'est toi le Pain de vie.\nVerbe puissant!\nC\'est de ta chair meurtrie,\nC\'est de ton sang\nQue notre âme doit vivre…\nAh!  Donne-toi\nPar l\'Esprit et le livre\nÀ notre foi!\n\n4.\nTu bénis tes apôtres,\nPuis, à leur tour,\nIls portèrent à d\'autres\nTon grand amour…\nÔ parole féconde,\nQue notre foi\nT\'offre à ce pauvre monde\nQui meurt sans Toi!','<p><strong>1.</strong> </p>\n<p>Romps-nous le pain de vie! </p>\n<p>Que ta bonté, </p>\n<p>Seigneur, nous rassasie </p>\n<p>De vérité! </p>\n<p>Amour qui nous fait vivre, </p>\n<p>Révèle-Toi! </p>\n<p>Parle dans le saint Livre </p>\n<p>À notre foi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>O toi don&#39;t la clémence </p>\n<p>Créa du pain </p>\n<p>Pour une foule immense </p>\n<p>Mourant de faim. </p>\n<p>Vois, ton peuple se presse </p>\n<p>Autour de Toi… </p>\n<p>Secours notre détresse </p>\n<p>Et notre foi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est toi le Pain de vie. </p>\n<p>Verbe puissant! </p>\n<p>C&#39;est de ta chair meurtrie, </p>\n<p>C&#39;est de ton sang </p>\n<p>Que notre âme doit vivre… </p>\n<p>Ah!  Donne-toi </p>\n<p>Par l&#39;Esprit et le livre </p>\n<p>À notre foi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tu bénis tes apôtres, </p>\n<p>Puis, à leur tour, </p>\n<p>Ils portèrent à d&#39;autres </p>\n<p>Ton grand amour… </p>\n<p>Ô parole féconde, </p>\n<p>Que notre foi </p>\n<p>T&#39;offre à ce pauvre monde </p>\n<p>Qui meurt sans Toi! </p>\n','Romps-nous-le-pain-de-vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1342,'246f141e-6e75-4867-8ce6-8c16484289a2',0,0,'En expirant, le rédempteur',105,'105_CE_fr','1.\nEn expirant, le Rédempteur\nLaissa, don suprême,\nA son peuple un Consolateur:\nDieu lui-même.\n\n2.\nDans tous les coeurs l\'Hôte divin\nVeut prendre une place,\nEt personne n\'implore en vain\nCette grâce.\n\n3.\nSans se lasser, sa douce voix,\nConsole ou châtie;\nEt tout bas nous chante parfois\nLa Patrie!\n\n4.\nToutes nos vertus sont le fruit\nDon\'t il est la sève;\nIl commence l\'oeuvre et c\'est Lui\nQui l\'achève.\n\n5.\nEsprit de lumière et de paix,\nAh!  Que dès cette heure,\nNos coeurs deviennent à jamais\nTa demeure!','<p><strong>1.</strong> </p>\n<p>En expirant, le Rédempteur </p>\n<p>Laissa, don suprême, </p>\n<p>A son peuple un Consolateur: </p>\n<p>Dieu lui-même. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans tous les coeurs l&#39;Hôte divin </p>\n<p>Veut prendre une place, </p>\n<p>Et personne n&#39;implore en vain </p>\n<p>Cette grâce. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sans se lasser, sa douce voix, </p>\n<p>Console ou châtie; </p>\n<p>Et tout bas nous chante parfois </p>\n<p>La Patrie! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Toutes nos vertus sont le fruit </p>\n<p>Don&#39;t il est la sève; </p>\n<p>Il commence l&#39;oeuvre et c&#39;est Lui </p>\n<p>Qui l&#39;achève. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Esprit de lumière et de paix, </p>\n<p>Ah!  Que dès cette heure, </p>\n<p>Nos coeurs deviennent à jamais </p>\n<p>Ta demeure! </p>\n','En-expirant-le-redempteur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1343,'a823a120-0d18-4478-93ce-9b2caea2302c',0,0,'Es-tu lassé, rempli de tristesse',125,'125_CE_fr','1.\nEs-tu lassé, rempli de tristesse?…\nDit tout à Jésus!  (bis)\nSon coeur est ouvert à ta voix sans cesse\nOh!  Dit tout à Jésus!\n\nRefrain\nDis tout à Jésus!\nOh!  Dis-Lui tout!\nCombien son accueil est doux.\nIl peut comprendre,\nIl aime à t\'entendre:\nDis-Lui simplement tout!\n\n2.\nIl voit tes yeux rougis par les larmes:\nDis tout à Jésus!  (bis)\nIl connaît ton coeur, Il sait tes alarmes.\nOh!  Dis tout à Jésus!\n\n3.\nSi ton passé surgit comme un ombre:\nDis tout à Jésus!  (bis)\nIl peut effacer tes péchés sans nombre.\nOh!  Dis tout à Jésus!\n\n4.\nEt pour demain ce que tu redoutes:\nDis tout à Jésus!  (bis)\nIl est près de toit le long de la route.\nOh!  Dis tout à Jésus!','<p><strong>1.</strong> </p>\n<p>Es-tu lassé, rempli de tristesse?… </p>\n<p>Dit tout à Jésus!  (bis) </p>\n<p>Son coeur est ouvert à ta voix sans cesse </p>\n<p>Oh!  Dit tout à Jésus! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dis tout à Jésus!</strong> </p>\n<p><strong>Oh!  Dis-Lui tout!</strong> </p>\n<p><strong>Combien son accueil est doux.</strong> </p>\n<p><strong>Il peut comprendre,</strong> </p>\n<p><strong>Il aime à t&#39;entendre:</strong> </p>\n<p><strong>Dis-Lui simplement tout!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il voit tes yeux rougis par les larmes: </p>\n<p>Dis tout à Jésus!  (bis) </p>\n<p>Il connaît ton coeur, Il sait tes alarmes. </p>\n<p>Oh!  Dis tout à Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ton passé surgit comme un ombre: </p>\n<p>Dis tout à Jésus!  (bis) </p>\n<p>Il peut effacer tes péchés sans nombre. </p>\n<p>Oh!  Dis tout à Jésus! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et pour demain ce que tu redoutes: </p>\n<p>Dis tout à Jésus!  (bis) </p>\n<p>Il est près de toit le long de la route. </p>\n<p>Oh!  Dis tout à Jésus! </p>\n','Es-tu-lasse-rempli-de-tristesse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1344,'39494178-38c9-4153-8241-bbcfe13b948a',0,0,'Pleine paix, Jésus en ta présence',174,'174_CE_fr','1.\nPleine paix, Jésus en ta présence,\nPleine paix, soutenu par ta main,\nPleine paix, gardé par ta puissance,\nPleine paix, tout le long du chemin.\n\nRefrain\nParfaite paix, douce paix,  (bis)\nMême en marchant\nDans la plus sombre nuit.\nJésus, mon Sauveur,\nMe dit: Sois sans peur!\nJe suis la lumière de qui me suit.\n\n\n2.\nPleine paix, dans mon pèlerinage,\nPleine paix, malgré le dur exil,\nPleine paix, si l\'ennemi fait rage,\nPleine paix, quel que soit le péril.\n\n\n3.\nPleine paix, au fort de la bataille,\nPleine paix, dans les difficultés,\nPleine paix, lorsque Satan m\'assaille,\nPleine paix, sur les flots agités.','<p><strong>1.</strong> </p>\n<p>Pleine paix, Jésus en ta présence, </p>\n<p>Pleine paix, soutenu par ta main, </p>\n<p>Pleine paix, gardé par ta puissance, </p>\n<p>Pleine paix, tout le long du chemin. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Parfaite paix, douce paix,  (bis)</strong> </p>\n<p><strong>Même en marchant</strong> </p>\n<p><strong>Dans la plus sombre nuit.</strong> </p>\n<p><strong>Jésus, mon Sauveur,</strong> </p>\n<p><strong>Me dit: Sois sans peur!</strong> </p>\n<p><strong>Je suis la lumière de qui me suit.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pleine paix, dans mon pèlerinage, </p>\n<p>Pleine paix, malgré le dur exil, </p>\n<p>Pleine paix, si l&#39;ennemi fait rage, </p>\n<p>Pleine paix, quel que soit le péril. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pleine paix, au fort de la bataille, </p>\n<p>Pleine paix, dans les difficultés, </p>\n<p>Pleine paix, lorsque Satan m&#39;assaille, </p>\n<p>Pleine paix, sur les flots agités. </p>\n','Pleine-paix-Jesus-en-ta-presence',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1345,'e4b4b3be-a342-4e0f-80f4-4018ecd65cd9',0,0,'Oui, Jésus, c’est vers Toi',165,'165_CE_fr','1.\nOui, Jésus, c\'est vers Toi,\nToujours plus haut!\nQue s\'élève ma foi,\nToujours plus haut\nLibre de tout lien,\nPoursuivant le seul bien,\nMon regard sur le tien,\nToujours plus haut!\n\n\n2.\nOui, c\'est là mon soupir,\nToujours plus haut!\nTu connais mon désir,\nToujours plus haut!\nPerçant l\'obscurité,\nMontre-moi ta clarté,\nTa divine beauté,\nToujours plus haut!\n\n\n3.\nMarche, me dit Jésus,\nToujours plus haut!\nOh! N\'arrête plus,\nToujours plus haut!\nBientôt, je reviendrai,\nEt te délivrerai,\nOui, je t\'introduirai\nChez Moi, là-haut!','<p><strong>1.</strong> </p>\n<p>Oui, Jésus, c&#39;est vers Toi, </p>\n<p>Toujours plus haut! </p>\n<p>Que s&#39;élève ma foi, </p>\n<p>Toujours plus haut </p>\n<p>Libre de tout lien, </p>\n<p>Poursuivant le seul bien, </p>\n<p>Mon regard sur le tien, </p>\n<p>Toujours plus haut! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, c&#39;est là mon soupir, </p>\n<p>Toujours plus haut! </p>\n<p>Tu connais mon désir, </p>\n<p>Toujours plus haut! </p>\n<p>Perçant l&#39;obscurité, </p>\n<p>Montre-moi ta clarté, </p>\n<p>Ta divine beauté, </p>\n<p>Toujours plus haut! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Marche, me dit Jésus, </p>\n<p>Toujours plus haut! </p>\n<p>Oh! N&#39;arrête plus, </p>\n<p>Toujours plus haut! </p>\n<p>Bientôt, je reviendrai, </p>\n<p>Et te délivrerai, </p>\n<p>Oui, je t&#39;introduirai </p>\n<p>Chez Moi, là-haut! </p>\n','Oui-Jesus-c\'est-vers-Toi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1346,'67cba76a-43a9-47d1-804f-b68eee319fbe',0,0,'Mon Corps, Mon Cœur, Mon Ame',137,'137_CE_fr','1.\nMon corps, mon coeur, mon âme\nNe m\'appartiennent plus;\nTon amour les réclames;\nIls sont à toi, Jésus!\n\nRefrain\nReçois mon sacrifice,\nIl est sur mon autel!\nEsprit, Esprit, descends!\nJ\'attends le feu du ciel.\n\n2.\nEn toi je me confie,\nJe m\'abandonne à Toi;\nTon sang me purifie\nEt ta grâce est ma Loi.\n\n3.\nConsacre mon offrande,\nMets ton sceau sur mon coeur!\nLe sceau que je demande\nC\'est ton Esprit, Seigneur.','<p><strong>1.</strong> </p>\n<p>Mon corps, mon coeur, mon âme </p>\n<p>Ne m&#39;appartiennent plus; </p>\n<p>Ton amour les réclames; </p>\n<p>Ils sont à toi, Jésus! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Reçois mon sacrifice,</strong> </p>\n<p><strong>Il est sur mon autel!</strong> </p>\n<p><strong>Esprit, Esprit, descends!</strong> </p>\n<p><strong>J&#39;attends le feu du ciel.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>En toi je me confie, </p>\n<p>Je m&#39;abandonne à Toi; </p>\n<p>Ton sang me purifie </p>\n<p>Et ta grâce est ma Loi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Consacre mon offrande, </p>\n<p>Mets ton sceau sur mon coeur! </p>\n<p>Le sceau que je demande </p>\n<p>C&#39;est ton Esprit, Seigneur. </p>\n','Mon-Corps-Mon-Coeur-Mon-Ame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1347,'c70945ae-8e77-4808-90c7-6da115a27f3a',0,0,'Qu’il fait bon à ton service',144,'144_CE_fr','1.\nQu\'il fait bon à ton service,\nJésus, mon Sauveur!\nQu\'il est doux, le sacrifice\nQue t\'offre mon coeur!\n\nRefrain\nPrends, ô Jésus, prends ma vie,\nElle est tout à Toi!\nEt dans ta grâce infinie,\nDu mal garde-moi!\n\n\n2.\nMon désir, mon voeu suprême,\nC\'est la sainteté…\nRien je ne veux et je n\'aime\nQue ta volonté!\n\n\n3.\nComme l\'ange au vol rapide,\nJe veux te servir,\nLes yeux fixés sur mon guide,\nToujours obéir!\n\n\n4.\nTravail, douleur et souffrance,\nNon, je ne crains rien!\nToi, Jésus, mon Espérance,\nVoilà mon seul bien!\n\n\n5.\nEnsemble donc vers la gloire,\nMarchons en avant!\nChantant l\'hymne de victoire,\nToujours triomphant!','<p><strong>1.</strong> </p>\n<p>Qu&#39;il fait bon à ton service, </p>\n<p>Jésus, mon Sauveur! </p>\n<p>Qu&#39;il est doux, le sacrifice </p>\n<p>Que t&#39;offre mon coeur! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prends, ô Jésus, prends ma vie,</strong> </p>\n<p><strong>Elle est tout à Toi!</strong> </p>\n<p><strong>Et dans ta grâce infinie,</strong> </p>\n<p><strong>Du mal garde-moi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon désir, mon voeu suprême, </p>\n<p>C&#39;est la sainteté… </p>\n<p>Rien je ne veux et je n&#39;aime </p>\n<p>Que ta volonté! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme l&#39;ange au vol rapide, </p>\n<p>Je veux te servir, </p>\n<p>Les yeux fixés sur mon guide, </p>\n<p>Toujours obéir! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Travail, douleur et souffrance, </p>\n<p>Non, je ne crains rien! </p>\n<p>Toi, Jésus, mon Espérance, </p>\n<p>Voilà mon seul bien! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ensemble donc vers la gloire, </p>\n<p>Marchons en avant! </p>\n<p>Chantant l&#39;hymne de victoire, </p>\n<p>Toujours triomphant! </p>\n','Qu\'il-fait-bon-a-ton-service',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1348,'069fb5d8-268b-4040-bc88-0eabd6036d25',0,0,'Mon Jésus, je t’aime',149,'149_CE_fr','1.\nMon Jésus, je t\'aime,\nJe Te sais à moi,\nJe fuis les plaisirs de\nCe monde pour Toi.\nSauveur qui fait grâce\nA tout coeur repentant,\nSi jamais je T\'aime,\nC\'est bien maintenant.\n\n\n2.\nJe T\'aime, car Toi,\nLe premier, Tu m\'aimas;\nJe T\'aime pour la croix\nOù Tu Te donnas,\nEt pour les épines\nSur Ton front sanglant,\nSi jamais je T\'aime,\nC\'est bien maintenant.\n\n\n3.\nJe T\'aimerai dans la vie\nEt dans la mort;\nOui, je T\'aimerai\nJusqu\'à la fin encore,\nEt tout bas, je dirai\nLe mot consolant:\nSi jamais je T\'aime,\nC\'est bien maintenant.\n\n\n4.\nDans la gloire et le\nBonheur de Ton palais,\nJe T\'adorerai, Jésus,\nPlus que jamais;\nDe l\'Église en triomphe\nÉclate ce chant:\nSi jamais je T\'aime,\nC\'est bien maintenant.','<p><strong>1.</strong> </p>\n<p>Mon Jésus, je t&#39;aime, </p>\n<p>Je Te sais à moi, </p>\n<p>Je fuis les plaisirs de </p>\n<p>Ce monde pour Toi. </p>\n<p>Sauveur qui fait grâce </p>\n<p>A tout coeur repentant, </p>\n<p>Si jamais je T&#39;aime, </p>\n<p>C&#39;est bien maintenant. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je T&#39;aime, car Toi, </p>\n<p>Le premier, Tu m&#39;aimas; </p>\n<p>Je T&#39;aime pour la croix </p>\n<p>Où Tu Te donnas, </p>\n<p>Et pour les épines </p>\n<p>Sur Ton front sanglant, </p>\n<p>Si jamais je T&#39;aime, </p>\n<p>C&#39;est bien maintenant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je T&#39;aimerai dans la vie </p>\n<p>Et dans la mort; </p>\n<p>Oui, je T&#39;aimerai </p>\n<p>Jusqu&#39;à la fin encore, </p>\n<p>Et tout bas, je dirai </p>\n<p>Le mot consolant: </p>\n<p>Si jamais je T&#39;aime, </p>\n<p>C&#39;est bien maintenant. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dans la gloire et le </p>\n<p>Bonheur de Ton palais, </p>\n<p>Je T&#39;adorerai, Jésus, </p>\n<p>Plus que jamais; </p>\n<p>De l&#39;Église en triomphe </p>\n<p>Éclate ce chant: </p>\n<p>Si jamais je T&#39;aime, </p>\n<p>C&#39;est bien maintenant. </p>\n','Mon-Jesus-je-t\'aime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1349,'a7dffc8b-6656-4f0e-9d10-60980c3cfe55',0,0,'Torrents d\'amour et de grâce',180,'180_CE_fr','1.\nTorrents d\'amour et de grâce,\nAmour du Sauveur en croix!\nA ce grand fleuve qui passe,\nJe m\'abandonne et je crois.\n\nRefrain\nJe crois à ton sacrifice,\nO Jésus, Agneau de Dieu,\nEt, couvert par ta justice,\nJ\'entrerai dans le saint lieu.\n\n\n2.\nAh! Que partout se répande\nCe fleuve à la grande voix;\nQue tout l\'univers entende\nL\'appel qui vient de la croix!\n\n\n3.\nQue toute âme condamnée\nPour qui tu versas ton sang,\nSoit Père ramenée\nPar ton amour tout-puissant.','<p><strong>1.</strong> </p>\n<p>Torrents d&#39;amour et de grâce, </p>\n<p>Amour du Sauveur en croix! </p>\n<p>A ce grand fleuve qui passe, </p>\n<p>Je m&#39;abandonne et je crois. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je crois à ton sacrifice,</strong> </p>\n<p><strong>O Jésus, Agneau de Dieu,</strong> </p>\n<p><strong>Et, couvert par ta justice,</strong> </p>\n<p><strong>J&#39;entrerai dans le saint lieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ah! Que partout se répande </p>\n<p>Ce fleuve à la grande voix; </p>\n<p>Que tout l&#39;univers entende </p>\n<p>L&#39;appel qui vient de la croix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que toute âme condamnée </p>\n<p>Pour qui tu versas ton sang, </p>\n<p>Soit Père ramenée </p>\n<p>Par ton amour tout-puissant. </p>\n','Torrents-d\'amour-et-de-grace',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1350,'7b8a7ea9-21d1-44c0-b3f8-672a95d254de',0,0,'Debout, sainte cohorte',212,'212_CE_fr','1.\nDebout, sainte cohorte,\nSoldats, du Roi des rois!\nTenez d\'une main forte\nL\'étendard de la croix.\nAu sentier de la gloire\nJésus-Christ nous conduit;\nDe victoire en victoire\nIl mène qui le suit.\n\n\n2.\nDebout pour la bataille!\nPortez, n\'hésitez plus;\nPour que nulle ne défaille\nRegardez à Jésus.\nDe l\'armure invincible,\nSoldats, revêtez-vous!\nLe triomphe est possible\nPour qui lutte à genoux.\n\n\n3.\nLa trompette résonne:\nDebout, vaillants soldats!\nL\'immortelle couronne\nEst le prix des combats.\nSi l\'ennemi fait rage,\nSoyez fermes et forts;\nRedoublez de courage\nS\'il redouble d\'efforts.\n\n\n4.\nDebout, debout encore!\nLuttez jusqu\'au matin.\nDéjà brille l\'aurore\nA l\'horizon lointain.\nBientôt, jetant nos armes\nAux pieds du Roi des rois,\nLes chants après les larmes,\nLe trône après la croix!','<p><strong>1.</strong> </p>\n<p>Debout, sainte cohorte, </p>\n<p>Soldats, du Roi des rois! </p>\n<p>Tenez d&#39;une main forte </p>\n<p>L&#39;étendard de la croix. </p>\n<p>Au sentier de la gloire </p>\n<p>Jésus-Christ nous conduit; </p>\n<p>De victoire en victoire </p>\n<p>Il mène qui le suit. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Debout pour la bataille! </p>\n<p>Portez, n&#39;hésitez plus; </p>\n<p>Pour que nulle ne défaille </p>\n<p>Regardez à Jésus. </p>\n<p>De l&#39;armure invincible, </p>\n<p>Soldats, revêtez-vous! </p>\n<p>Le triomphe est possible </p>\n<p>Pour qui lutte à genoux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La trompette résonne: </p>\n<p>Debout, vaillants soldats! </p>\n<p>L&#39;immortelle couronne </p>\n<p>Est le prix des combats. </p>\n<p>Si l&#39;ennemi fait rage, </p>\n<p>Soyez fermes et forts; </p>\n<p>Redoublez de courage </p>\n<p>S&#39;il redouble d&#39;efforts. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Debout, debout encore! </p>\n<p>Luttez jusqu&#39;au matin. </p>\n<p>Déjà brille l&#39;aurore </p>\n<p>A l&#39;horizon lointain. </p>\n<p>Bientôt, jetant nos armes </p>\n<p>Aux pieds du Roi des rois, </p>\n<p>Les chants après les larmes, </p>\n<p>Le trône après la croix! </p>\n','Debout-sainte-cohorte',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1351,'9467692e-465c-48a6-891e-bb8cda699b67',0,0,'Place pour toi, pécheur, Jésus t’appelle',245,'245_CE_fr','1.\nPlace pour toi, pécheur, Jésus t\'appelle\nAu grand festin de la vie éternelle.\n\nRefrain\nPlace pour toi! pécheur, viens par la foi!\n\n\n2.\nDu grand banquet la salle est préparée;\nDans son amour, Dieu t\'en donne l\'entrée.\n\n\n3.\nDans son palais, où sa gloire rayonne,\nAux malheureux Il offre une couronne.\n\n\n4.\nLa nuit s\'approche, Oh! Que rien ne s\'arrête,\nLà-haut ton Père à t\'accueillir s\'apprête.\n\n\n5.\nEntre, il est temps!… Lorsque l\'heure est sonnée\nPlus de pardon pour l\'âme condamnée.\n\nRefrain\nO cri d\'effroi!  Plus de place pour toi!','<p><strong>1.</strong> </p>\n<p>Place pour toi, pécheur, Jésus t&#39;appelle </p>\n<p>Au grand festin de la vie éternelle. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Place pour toi! pécheur, viens par la foi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Du grand banquet la salle est préparée; </p>\n<p>Dans son amour, Dieu t&#39;en donne l&#39;entrée. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans son palais, où sa gloire rayonne, </p>\n<p>Aux malheureux Il offre une couronne. </p>\n<p><br/> <strong>4.</strong></p>\n<p>La nuit s&#39;approche, Oh! Que rien ne s&#39;arrête, </p>\n<p>Là-haut ton Père à t&#39;accueillir s&#39;apprête. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Entre, il est temps!… Lorsque l&#39;heure est sonnée </p>\n<p>Plus de pardon pour l&#39;âme condamnée. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>O cri d&#39;effroi!  Plus de place pour toi!</strong> </p>\n','Place-pour-toi-pecheur-Jesus-t\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1352,'7bc01907-706c-4d3f-bab7-ebb7e8f1dd59',0,0,'Entends-tu le chant joyeux?',246,'246_CE_fr','1.\nEntends-tu le chant joyeux?\nJésus sauve aujourd\'hui!\nIl retentit en tout lieu:\nJésus sauve aujourd\'hui!\nC\'est un cri de délivrance,\nUn cantique d\'espérance,\nQui remplit l\'espace immense:\nJésus sauve aujourd\'hui!\n\n\n2.\nPartout élève la voix:\nJésus sauve aujourd\'hui!\nVaillant héraut de la croix,\nJésus sauve aujourd\'hui!\nA parler, Jésus t\'appelle,\nRépands au loin la nouvelle,\nAn connais-tu de plus belle?\nJésus sauve aujourd\'hui!\n\n\n3.\nRépète au pécheur contrit:\nJésus sauve aujourd\'hui!\nCeux que le mal asservit,\nJésus sauve aujourd\'hui!\nVa jusqu\'à l\'île lointaine,\nBriser du captif la chaîne,\nRedire aux coeurs dans la peine:\nJésus sauve aujourd\'hui!\n\n\n4.\nJusqu\'aux confins des déserts,\nJésus sauve aujourd\'hui!\nJusque par delà les mers,\nJésus sauve aujourd\'hui!\nQue d\'un pôle à l\'autre pôle\nCoure la sainte parole\nQui relève, instruit, console,\nJésus sauve aujourd\'hui!','<p><strong>1.</strong> </p>\n<p>Entends-tu le chant joyeux? </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Il retentit en tout lieu: </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>C&#39;est un cri de délivrance, </p>\n<p>Un cantique d&#39;espérance, </p>\n<p>Qui remplit l&#39;espace immense: </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Partout élève la voix: </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Vaillant héraut de la croix, </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>A parler, Jésus t&#39;appelle, </p>\n<p>Répands au loin la nouvelle, </p>\n<p>An connais-tu de plus belle? </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Répète au pécheur contrit: </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Ceux que le mal asservit, </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Va jusqu&#39;à l&#39;île lointaine, </p>\n<p>Briser du captif la chaîne, </p>\n<p>Redire aux coeurs dans la peine: </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jusqu&#39;aux confins des déserts, </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Jusque par delà les mers, </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n<p>Que d&#39;un pôle à l&#39;autre pôle </p>\n<p>Coure la sainte parole </p>\n<p>Qui relève, instruit, console, </p>\n<p>Jésus sauve aujourd&#39;hui! </p>\n','Entends-tu-le-chant-joyeux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1353,'127a4415-1a3b-4ad1-938a-fafdf56d4594',0,0,'Connais- tu cette cité',250,'250_CE_fr','1.\nConnais-tu cette cité,\nLa cité céleste?\nDans ses murs tout est clarté,\nPlus d\'ombre funeste.\nPèlerins et voyageurs,\nIci-bas dans les douleurs,\nToujours ce trésor nous reste,\nLa cité céleste.\n\n\n2.\nConnais-tu cette cité,\nLa cité céleste?\nOù tout est félicité,\nSublime harmonie.\nL\'Agneau seul est son soleil,\nEt son éclat sans pareil\nIllumine et vivifie\nLa cité céleste.\n\n\n3.\nD\'or, de perles et d\'azur\nEst notre patrie!\nConnais-tu le fleuve pur,\nL\'arbre de la vie?\nPlus de fardeaux à jamais,\nPour les élus tout est paix!\nDe Dieu la cité chérie\nC\'est notre patrie.\n\n\n4.\nO Salem, repos si doux,\nMon coeur te désire!\nAprès son Chef, son Époux,\nL\'Église soupire!\nÊtre à toujours devant toi,\nMon Seigneur! Mon divin Roi!\nPour te chanter et te dire:\nMon coeur te désire.','<p><strong>1.</strong> </p>\n<p>Connais-tu cette cité, </p>\n<p>La cité céleste? </p>\n<p>Dans ses murs tout est clarté, </p>\n<p>Plus d&#39;ombre funeste. </p>\n<p>Pèlerins et voyageurs, </p>\n<p>Ici-bas dans les douleurs, </p>\n<p>Toujours ce trésor nous reste, </p>\n<p>La cité céleste. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Connais-tu cette cité, </p>\n<p>La cité céleste? </p>\n<p>Où tout est félicité, </p>\n<p>Sublime harmonie. </p>\n<p>L&#39;Agneau seul est son soleil, </p>\n<p>Et son éclat sans pareil </p>\n<p>Illumine et vivifie </p>\n<p>La cité céleste. </p>\n<p><br/> <strong>3.</strong></p>\n<p>D&#39;or, de perles et d&#39;azur </p>\n<p>Est notre patrie! </p>\n<p>Connais-tu le fleuve pur, </p>\n<p>L&#39;arbre de la vie? </p>\n<p>Plus de fardeaux à jamais, </p>\n<p>Pour les élus tout est paix! </p>\n<p>De Dieu la cité chérie </p>\n<p>C&#39;est notre patrie. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Salem, repos si doux, </p>\n<p>Mon coeur te désire! </p>\n<p>Après son Chef, son Époux, </p>\n<p>L&#39;Église soupire! </p>\n<p>Être à toujours devant toi, </p>\n<p>Mon Seigneur! Mon divin Roi! </p>\n<p>Pour te chanter et te dire: </p>\n<p>Mon coeur te désire. </p>\n','Connais-tu-cette-cite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1354,'9f1a6bcf-31ab-4a06-9b76-fa2376ab4f05',0,0,'Serrons nos rangs autour de notre Maître',262,'262_CE_fr','1.\nSerrons nos rangs autour de notre Maître,\nSoyons unis, la victoire est à nous!\nPar notre amour, faisons à tous connaître\nDe notre Dieu combien le joug est doux.\n\nRefrain\nFrères, frères, son nom est amour;\nFrères, frères, aimons-nous toujours!\n\n\n2.\nC\'est par amour qu\'Il entra dans nos âmes\nC\'est par amour qu\'Il lave nos péchés;\nNous embrasant d\'une céleste flamme,\nQue son amour nous retienne attachés.\n\n\n3.\nAmour, amour! Insondable mystère\nQui nous unit, et tous ensemble à Dieu!\nAmour, amour! Si doux sur cette terre,\nAmour, amour! Oh! Que sera-ce aux cieux!','<p><strong>1.</strong> </p>\n<p>Serrons nos rangs autour de notre Maître, </p>\n<p>Soyons unis, la victoire est à nous! </p>\n<p>Par notre amour, faisons à tous connaître </p>\n<p>De notre Dieu combien le joug est doux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Frères, frères, son nom est amour;</strong> </p>\n<p><strong>Frères, frères, aimons-nous toujours!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est par amour qu&#39;Il entra dans nos âmes </p>\n<p>C&#39;est par amour qu&#39;Il lave nos péchés; </p>\n<p>Nous embrasant d&#39;une céleste flamme, </p>\n<p>Que son amour nous retienne attachés. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Amour, amour! Insondable mystère </p>\n<p>Qui nous unit, et tous ensemble à Dieu! </p>\n<p>Amour, amour! Si doux sur cette terre, </p>\n<p>Amour, amour! Oh! Que sera-ce aux cieux! </p>\n','Serrons-nos-rangs-autour-de-notre-Maitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1355,'b2ec2eb3-d533-41b9-a04d-376a14071262',0,0,'Demain, peut-être',283,'283_CE_fr','1.\nDemain, peut-être,\nJe te croirai,\nEt pour mon Maître\nJe te prendrai.\nAinsi, quand Dieu l\'invite,\nL\'âme réponds: « Attends! »\nAh! Pécheur, entre vite!\nViens, il est temps!\n\n\n2.\nDemain, peut-être,\nPlus de pardon!\nQuoi! Méconnaître\nUn pareil don!\nDieu t\'offre, dans sa grâce,\nLe bonheur des élus;\nAvant que l\'heure passe,\nViens à Jésus!\n\n\n3.\nDemain, peut-être,\nDu châtiment\nTu vas connaître\nL\'affreux tourment.\nJésus t\'appelle, Il t\'aime;\nIl est le seul chemin;\nViens, c\'est l\'instant suprême!\nPourquoi demain?','<p><strong>1.</strong> </p>\n<p>Demain, peut-être, </p>\n<p>Je te croirai, </p>\n<p>Et pour mon Maître </p>\n<p>Je te prendrai. </p>\n<p>Ainsi, quand Dieu l&#39;invite, </p>\n<p>L&#39;âme réponds: « Attends! » </p>\n<p>Ah! Pécheur, entre vite! </p>\n<p>Viens, il est temps! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Demain, peut-être, </p>\n<p>Plus de pardon! </p>\n<p>Quoi! Méconnaître </p>\n<p>Un pareil don! </p>\n<p>Dieu t&#39;offre, dans sa grâce, </p>\n<p>Le bonheur des élus; </p>\n<p>Avant que l&#39;heure passe, </p>\n<p>Viens à Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Demain, peut-être, </p>\n<p>Du châtiment </p>\n<p>Tu vas connaître </p>\n<p>L&#39;affreux tourment. </p>\n<p>Jésus t&#39;appelle, Il t&#39;aime; </p>\n<p>Il est le seul chemin; </p>\n<p>Viens, c&#39;est l&#39;instant suprême! </p>\n<p>Pourquoi demain? </p>\n','Demain-peut-etre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1356,'c5ca05ce-06d0-456e-b696-3b2b1d8c60df',0,0,'Viens à Jésus, Il t’appelle',293,'293_CE_fr','1.\nViens à Jésus, Il t\'appelle;\nIl t\'appelle aujourd\'hui;\nTrop longtemps tu fus rebelle,\nAujourd\'hui viens à Lui!\n\n\n2.\nJésus t\'aime, Jésus t\'aime,\nJésus t\'aime aujourd\'hui.\nMalgré ta misère extrême,\nAujourd\'hui, viens à Lui!\n\n\n3.\nIl pardonne, Il pardonne\nIl pardonne aujourd\'hui.\nReçois le salut qu\'Il donne;\nAujourd\'hui, viens à Lui!','<p><strong>1.</strong> </p>\n<p>Viens à Jésus, Il t&#39;appelle; </p>\n<p>Il t&#39;appelle aujourd&#39;hui; </p>\n<p>Trop longtemps tu fus rebelle, </p>\n<p>Aujourd&#39;hui viens à Lui! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus t&#39;aime, Jésus t&#39;aime, </p>\n<p>Jésus t&#39;aime aujourd&#39;hui. </p>\n<p>Malgré ta misère extrême, </p>\n<p>Aujourd&#39;hui, viens à Lui! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il pardonne, Il pardonne </p>\n<p>Il pardonne aujourd&#39;hui. </p>\n<p>Reçois le salut qu&#39;Il donne; </p>\n<p>Aujourd&#39;hui, viens à Lui! </p>\n','Viens-a-Jesus-Il-t\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1357,'918c9432-6759-48b7-a3d4-18dce37ff359',0,0,'Eternel, répands ta paix',312,'312_CE_fr','1.\nÉternel, répands ta paix\nSur ces époux, à jamais.\nComble-les de tes bienfaits,\nAccomplis tous leurs souhaits!\n\nRefrain\nViens, Seigneur, à cette heure,\nFais en eux ta demeure!\nRépands en leurs coeurs chaque jour,\nTon amour!  Ton amour!\n\n\n2.\nDieu puissant, Dieu souverain,\nDaigne prendre par la main\nEt conduire au bon chemin\nCes époux jusqu\'à la fin.\n\n\n3.\nQue toujours unis en Toi,\nToujours scellés dans la foi,\nIls gardent la sainte loi\nDe Jésus, leur divin Roi.','<p><strong>1.</strong> </p>\n<p>Éternel, répands ta paix </p>\n<p>Sur ces époux, à jamais. </p>\n<p>Comble-les de tes bienfaits, </p>\n<p>Accomplis tous leurs souhaits! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Viens, Seigneur, à cette heure,</strong> </p>\n<p><strong>Fais en eux ta demeure!</strong> </p>\n<p><strong>Répands en leurs coeurs chaque jour,</strong> </p>\n<p><strong>Ton amour!  Ton amour!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dieu puissant, Dieu souverain, </p>\n<p>Daigne prendre par la main </p>\n<p>Et conduire au bon chemin </p>\n<p>Ces époux jusqu&#39;à la fin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que toujours unis en Toi, </p>\n<p>Toujours scellés dans la foi, </p>\n<p>Ils gardent la sainte loi </p>\n<p>De Jésus, leur divin Roi. </p>\n','Eternel-repands-ta-paix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1358,'a2d89503-9857-4e3d-a8b8-0fba16ab8607',0,0,'Non, ce n’est pas mourir',317,'317_CE_fr','1.\nNon, ce n\'est pas mourir\nQue d\'aller vers son Dieu\nEt que de dire adieu\nA cette sombre terre,\nPour entrer au séjour\nDe la pure lumière.\n\n\n2.\nNon, ce n\'est pas mourir\nQue de monter au ciel,\nAu repos éternel,\nA la gloire ineffable,\nAprès tous les combats\nD\'un monde périssable!\n\n\n3.\nNon, ce n\'est pas mourir\nQue d\'adorer Jésus\nAu milieu des élus,\nCélébrant sa victoire,\nEt d\'être couronné\nD\'allégresse et de gloire!','<p><strong>1.</strong> </p>\n<p>Non, ce n&#39;est pas mourir </p>\n<p>Que d&#39;aller vers son Dieu </p>\n<p>Et que de dire adieu </p>\n<p>A cette sombre terre, </p>\n<p>Pour entrer au séjour </p>\n<p>De la pure lumière. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non, ce n&#39;est pas mourir </p>\n<p>Que de monter au ciel, </p>\n<p>Au repos éternel, </p>\n<p>A la gloire ineffable, </p>\n<p>Après tous les combats </p>\n<p>D&#39;un monde périssable! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Non, ce n&#39;est pas mourir </p>\n<p>Que d&#39;adorer Jésus </p>\n<p>Au milieu des élus, </p>\n<p>Célébrant sa victoire, </p>\n<p>Et d&#39;être couronné </p>\n<p>D&#39;allégresse et de gloire! </p>\n','Non-ce-n\'est-pas-mourir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1359,'0fc9d98b-29de-4e9d-9cde-0dcdb76f56d3',0,0,'Je Dois Voyager Au Monde',169,'169_CE_fr','1.\nJe dois voyager au monde\nComme un esquif sur les eaux,\nEt la tempête qui gronde\nDéjà fait mugir les flots.\n\nRefrain\nChrist est ma vie, Il est mon Roi.\nToujours Il prie notre Père pour moi.\n\n\n2.\nIl m\'a donné la boussole\nQui, sans erreur, pointe au port:\nC\'est sa vivante Parole;\nAvec elle, point de mort.\n\n\n3.\nSi le vent m\'est favorable,\nSi mon ciel est clair et pur,\nCe pilote charitable\nMe dit: «Veille!  Rien n\'est sûr!»\n\n\n4.\nEt si vient le sombre orage,\nSi tout semble menaçant,\nMon pilote m\'encourage\nDe son regard tout-puissant.\n\n\n5.\nJe ne crains donc ni naufrage,\nNi pirates, ni récif;\nJ\'ai déjà reçu le gage\nDu salut de mon esquif.','<p><strong>1.</strong> </p>\n<p>Je dois voyager au monde </p>\n<p>Comme un esquif sur les eaux, </p>\n<p>Et la tempête qui gronde </p>\n<p>Déjà fait mugir les flots. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Christ est ma vie, Il est mon Roi.</strong> </p>\n<p><strong>Toujours Il prie notre Père pour moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il m&#39;a donné la boussole </p>\n<p>Qui, sans erreur, pointe au port: </p>\n<p>C&#39;est sa vivante Parole; </p>\n<p>Avec elle, point de mort. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si le vent m&#39;est favorable, </p>\n<p>Si mon ciel est clair et pur, </p>\n<p>Ce pilote charitable </p>\n<p>Me dit: «Veille!  Rien n&#39;est sûr!» </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et si vient le sombre orage, </p>\n<p>Si tout semble menaçant, </p>\n<p>Mon pilote m&#39;encourage </p>\n<p>De son regard tout-puissant. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Je ne crains donc ni naufrage, </p>\n<p>Ni pirates, ni récif; </p>\n<p>J&#39;ai déjà reçu le gage </p>\n<p>Du salut de mon esquif. </p>\n','Je-Dois-Voyager-Au-Monde',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1360,'b300388b-595a-42d5-b1bf-7041c35a2898',0,0,'Comme un fleuve immense',175,'175_CE_fr','1.\nComme un fleuve immense\nEst la paix de Dieu.\nParfaite elle avance\nVainqueur en tout lieu;\nParfaite elle augmente\nConstamment son cours;\nParfaite sa pente\nS\'abaisse toujours.\n\nRefrain\nSelon ta promesse, O Jésus, mon Roi,\nJe trouve sans cesse Paix parfaite en toi.\n\n\n2.\nTu traces ma voie:\nJ\'y marche avec toi!\nL\'épreuve et la joie\nMe viennent de toi!\nCadran de nos vies,\nMarque, chaque jour,\nLes heures bénies\nDu soleil d\'amour!\n\n\n3.\nOcéan de gloire,\nPaix de mon Sauveur!\nGage de victoire!\nTrésor de bonheur!\nTa grâce infinie,\nCoulant nuit et jour,\nInonde ma vie\nDe vagues d\'amour!','<p><strong>1.</strong> </p>\n<p>Comme un fleuve immense </p>\n<p>Est la paix de Dieu. </p>\n<p>Parfaite elle avance </p>\n<p>Vainqueur en tout lieu; </p>\n<p>Parfaite elle augmente </p>\n<p>Constamment son cours; </p>\n<p>Parfaite sa pente </p>\n<p>S&#39;abaisse toujours. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Selon ta promesse, O Jésus, mon Roi,</strong> </p>\n<p><strong>Je trouve sans cesse Paix parfaite en toi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu traces ma voie: </p>\n<p>J&#39;y marche avec toi! </p>\n<p>L&#39;épreuve et la joie </p>\n<p>Me viennent de toi! </p>\n<p>Cadran de nos vies, </p>\n<p>Marque, chaque jour, </p>\n<p>Les heures bénies </p>\n<p>Du soleil d&#39;amour! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Océan de gloire, </p>\n<p>Paix de mon Sauveur! </p>\n<p>Gage de victoire! </p>\n<p>Trésor de bonheur! </p>\n<p>Ta grâce infinie, </p>\n<p>Coulant nuit et jour, </p>\n<p>Inonde ma vie </p>\n<p>De vagues d&#39;amour! </p>\n','Comme-un-fleuve-immense',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1361,'c5393206-8e1c-4586-a0d6-a86c9fd2c153',0,0,'Jésus te confie',194,'194_CE_fr','1.\nJésus te confie\nUne oeuvre d\'amour,\nUtile et bénie\nJusqu\'à son retour;\nCette sainte tâche,\nVeux-tu l\'accomplir\nPour Lui, sans relâche,\nSans jamais faiblir?\n\nRefrain\nPrie, agis, jour après jour,\nSans broncher, suis ton Sauveur avec amour;\nSoit fidèle, obéissant,\nEt le Maître rendra ton travail puissant.\n\n\n2.\nVa chercher ton frère,\nEsclave enlacé,\nLas de sa misère,\nDe son noir passé;\nArrache son âme\nAu plaisir trompeur,\nLe salut proclame,\nEn Christ, ton Sauveur.\n\n\n3.\nVa vers la jeunesse,\nQue le tentateur\nVeut leurrer sans cesse,\nLoin du vrai bonheur;\nCombattant le doute,\nParle-lui d\'amour;\nMontre-lui la route\nDu ciel, saint séjour.\n\n\n4.\nVa vers ceux qui meurent\nSans Dieu, sans espoir;\nDis à ceux qui pleurent\nQuand tout semble noir:\n« Jésus donne vie,\nBonheur, joie et paix\nA qui se confie\nEn Lui, pour jamais. »','<p><strong>1.</strong> </p>\n<p>Jésus te confie </p>\n<p>Une oeuvre d&#39;amour, </p>\n<p>Utile et bénie </p>\n<p>Jusqu&#39;à son retour; </p>\n<p>Cette sainte tâche, </p>\n<p>Veux-tu l&#39;accomplir </p>\n<p>Pour Lui, sans relâche, </p>\n<p>Sans jamais faiblir? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prie, agis, jour après jour,</strong> </p>\n<p><strong>Sans broncher, suis ton Sauveur avec amour;</strong> </p>\n<p><strong>Soit fidèle, obéissant,</strong> </p>\n<p><strong>Et le Maître rendra ton travail puissant.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Va chercher ton frère, </p>\n<p>Esclave enlacé, </p>\n<p>Las de sa misère, </p>\n<p>De son noir passé; </p>\n<p>Arrache son âme </p>\n<p>Au plaisir trompeur, </p>\n<p>Le salut proclame, </p>\n<p>En Christ, ton Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Va vers la jeunesse, </p>\n<p>Que le tentateur </p>\n<p>Veut leurrer sans cesse, </p>\n<p>Loin du vrai bonheur; </p>\n<p>Combattant le doute, </p>\n<p>Parle-lui d&#39;amour; </p>\n<p>Montre-lui la route </p>\n<p>Du ciel, saint séjour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Va vers ceux qui meurent </p>\n<p>Sans Dieu, sans espoir; </p>\n<p>Dis à ceux qui pleurent </p>\n<p>Quand tout semble noir: </p>\n<p>« Jésus donne vie, </p>\n<p>Bonheur, joie et paix </p>\n<p>A qui se confie </p>\n<p>En Lui, pour jamais. » </p>\n','Jesus-te-confie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1362,'70a7637d-6f3c-460c-b01d-7a7cdac3be12',0,0,'Seigneur, du sein de la poussière',197,'197_CE_fr','1.\nSeigneur, du sein de la poussière,\nMon âme crie à Toi:\nO Dieu, descends dans ma prière!\nQue je te sente en moi.\n\n\n2.\nJe ne veux plus l\'ombre qui passe,\nL\'image qui pâlit;\nMais la substance de ta grâce,\nToi-même, ton Esprit!\n\n\n3.\nC\'est assez et trop se répandre\nEn long et vague espoir;\nJe veux te parler et t\'entendre,\nTe toucher et te voir.\n\n\n4.\nJe veux brûler, mais de ta flamme,\nLuire, mais de ton jour,\nDe ton âme, animer mon âme,\nAimer de ton amour.\n\n\n5.\nVoilà le seul bien que j\'envie,\nQue j\'implore, ô mon Roi!\nNe plus vivre que de ta vie,\nQue par Toi, que pour Toi!','<p><strong>1.</strong> </p>\n<p>Seigneur, du sein de la poussière, </p>\n<p>Mon âme crie à Toi: </p>\n<p>O Dieu, descends dans ma prière! </p>\n<p>Que je te sente en moi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je ne veux plus l&#39;ombre qui passe, </p>\n<p>L&#39;image qui pâlit; </p>\n<p>Mais la substance de ta grâce, </p>\n<p>Toi-même, ton Esprit! </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est assez et trop se répandre </p>\n<p>En long et vague espoir; </p>\n<p>Je veux te parler et t&#39;entendre, </p>\n<p>Te toucher et te voir. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je veux brûler, mais de ta flamme, </p>\n<p>Luire, mais de ton jour, </p>\n<p>De ton âme, animer mon âme, </p>\n<p>Aimer de ton amour. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Voilà le seul bien que j&#39;envie, </p>\n<p>Que j&#39;implore, ô mon Roi! </p>\n<p>Ne plus vivre que de ta vie, </p>\n<p>Que par Toi, que pour Toi! </p>\n','Seigneur-du-sein-de-la-poussiere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1363,'b909606b-33c9-4770-86ac-92cd3e14baf0',0,0,'Ô Toi Qui Donnes La Vie',205,'205_CE_fr','1.\nO Toi qui donne la vie,\nA tes pieds je veux m\'asseoir\nComme s\'asseyait Marie\nA l\'heure douce du soir.  (bis 2 lignes)\n\n\n2.\nMon être entier te réclame;\nTout n\'est que faiblesse en moi;\nViens te pencher sur mon âme,\nElle a tant besoin de Toi!  (bis 2 lignes)\n\n\n3.\nMaître, à tes pieds je m\'incline,\nJe t\'appartiens sans retour,\nVerse en moi ta paix divine,\nRépands en moi ton amour!  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>O Toi qui donne la vie, </p>\n<p>A tes pieds je veux m&#39;asseoir </p>\n<p>Comme s&#39;asseyait Marie </p>\n<p>A l&#39;heure douce du soir.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon être entier te réclame; </p>\n<p>Tout n&#39;est que faiblesse en moi; </p>\n<p>Viens te pencher sur mon âme, </p>\n<p>Elle a tant besoin de Toi!  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Maître, à tes pieds je m&#39;incline, </p>\n<p>Je t&#39;appartiens sans retour, </p>\n<p>Verse en moi ta paix divine, </p>\n<p>Répands en moi ton amour!  (bis 2 lignes) </p>\n','O-Toi-Qui-Donnes-La-Vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1364,'4dbffd35-8b4c-4af7-87b1-09b97544c40a',0,0,'Honneur aux vaillants, aux braves',224,'224_CE_fr','1.\nHonneur aux vaillants, aux braves,\nAux soldats de Dieu,\nQui, même chargés d\'entraves,\nLuttent en tout lieu.\n\nRefrain\nJésus-Christ leur donne\nUn coeur libre et fort,\nEt leur promet la couronne\nQuand viendra la mort.\n\n\n2.\nQu\'on les raille ou les immole,\nFermes jusqu\'au bout,\nOn les voit devant l\'idole,\nSeuls rester debout!\n\n\n3.\nJoignant l\'amour au courage,\nCes nobles lutteurs\nDu monde qui les outrage\nSont les bienfaiteurs.\n\n\n4.\nIl s\'en vont… et l\'on oublie\nLeurs noms, leurs combats,\nMais leur sublime folie\nNe périra pas!\n\nRefrain\nC\'est toi qui leur donnes\nUn coeur libre et fort,\nEt leur promets des couronnes\nQuand viendra la mort.','<p><strong>1.</strong> </p>\n<p>Honneur aux vaillants, aux braves, </p>\n<p>Aux soldats de Dieu, </p>\n<p>Qui, même chargés d&#39;entraves, </p>\n<p>Luttent en tout lieu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus-Christ leur donne</strong> </p>\n<p><strong>Un coeur libre et fort,</strong> </p>\n<p><strong>Et leur promet la couronne</strong> </p>\n<p><strong>Quand viendra la mort.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;on les raille ou les immole, </p>\n<p>Fermes jusqu&#39;au bout, </p>\n<p>On les voit devant l&#39;idole, </p>\n<p>Seuls rester debout! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Joignant l&#39;amour au courage, </p>\n<p>Ces nobles lutteurs </p>\n<p>Du monde qui les outrage </p>\n<p>Sont les bienfaiteurs. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il s&#39;en vont… et l&#39;on oublie </p>\n<p>Leurs noms, leurs combats, </p>\n<p>Mais leur sublime folie </p>\n<p>Ne périra pas! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>C&#39;est toi qui leur donnes</strong> </p>\n<p><strong>Un coeur libre et fort,</strong> </p>\n<p><strong>Et leur promets des couronnes</strong> </p>\n<p><strong>Quand viendra la mort.</strong> </p>\n','Honneur-aux-vaillants-aux-braves',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1365,'86aa94cd-24f6-46d0-81da-b50866363d27',0,0,'Dès que l’aube dépose',227,'227_CE_fr','1.\nDès que l\'aube dépose\nSes perles sur les fleurs,\nDès que s\'ouvre la rose\nAux brillantes couleurs,\nDès que l\'ombre s\'efface\nDevant le jour qui luit,\nA l\'oeuvre, le temps passe !\nA l\'oeuvre avant la nuit!\n\n\n2.\nQuand le soleil inonde\nEt remplit le ciel bleu,\nIlluminant le monde\nDe ses rayons de feu,\nA l\'oeuvre, sans relâche,\nA l\'oeuvre le jour fuit!\nSi pénible est la tâche,\nBientôt viendra la nuit!\n\n\n3.\nA cette heure indécise\nOù le jour disparaît,\nOù murmure la brise\nA travers la forêt,\nQuand le couchant se dore\nEt que s\'éteint le bruit,\nFrères, à l\'oeuvre encore!\nVoici, voici, la nuit!','<p><strong>1.</strong> </p>\n<p>Dès que l&#39;aube dépose </p>\n<p>Ses perles sur les fleurs, </p>\n<p>Dès que s&#39;ouvre la rose </p>\n<p>Aux brillantes couleurs, </p>\n<p>Dès que l&#39;ombre s&#39;efface </p>\n<p>Devant le jour qui luit, </p>\n<p>A l&#39;oeuvre, le temps passe ! </p>\n<p>A l&#39;oeuvre avant la nuit! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand le soleil inonde </p>\n<p>Et remplit le ciel bleu, </p>\n<p>Illuminant le monde </p>\n<p>De ses rayons de feu, </p>\n<p>A l&#39;oeuvre, sans relâche, </p>\n<p>A l&#39;oeuvre le jour fuit! </p>\n<p>Si pénible est la tâche, </p>\n<p>Bientôt viendra la nuit! </p>\n<p><br/> <strong>3.</strong></p>\n<p>A cette heure indécise </p>\n<p>Où le jour disparaît, </p>\n<p>Où murmure la brise </p>\n<p>A travers la forêt, </p>\n<p>Quand le couchant se dore </p>\n<p>Et que s&#39;éteint le bruit, </p>\n<p>Frères, à l&#39;oeuvre encore! </p>\n<p>Voici, voici, la nuit! </p>\n','Des-que-l\'aube-depose',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1366,'48080bec-d56b-4ddc-bf1d-715789303d5c',0,0,'Seigneur, donne-moi des ailes',252,'252_CE_fr','1.\nSeigneur donne-moi des ailes\nPour m\'élever par la foi\nJusqu\'aux rives éternelles,\nOù je vivrai près de Toi.\nC\'est là-haut qu\'est ma patrie.\n\nRefrain\nLà-haut, là-haut, je trouve mon Sauveur.\nEnsemble aux sources de la vie\nAbreuvons tous notre coeur.\n\n\n2.\nLoin du monde et de ses charmes,\nDu péché, de la douleur,\nLoin des pleurs et des alarmes,\nJe veux m\'élever, Seigneur.\nC\'es là-haut qu\'est ma patrie.\n\n\n3.\nFais-moi es ailes de flamme\nPour porter en ces bas lieux\nTon salut aux pauvres âmes\nQui périssent loin des cieux.\nMais là-haut j\'ai ma patrie.','<p><strong>1.</strong> </p>\n<p>Seigneur donne-moi des ailes </p>\n<p>Pour m&#39;élever par la foi </p>\n<p>Jusqu&#39;aux rives éternelles, </p>\n<p>Où je vivrai près de Toi. </p>\n<p>C&#39;est là-haut qu&#39;est ma patrie. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Là-haut, là-haut, je trouve mon Sauveur.</strong> </p>\n<p><strong>Ensemble aux sources de la vie</strong> </p>\n<p><strong>Abreuvons tous notre coeur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Loin du monde et de ses charmes, </p>\n<p>Du péché, de la douleur, </p>\n<p>Loin des pleurs et des alarmes, </p>\n<p>Je veux m&#39;élever, Seigneur. </p>\n<p>C&#39;es là-haut qu&#39;est ma patrie. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fais-moi es ailes de flamme </p>\n<p>Pour porter en ces bas lieux </p>\n<p>Ton salut aux pauvres âmes </p>\n<p>Qui périssent loin des cieux. </p>\n<p>Mais là-haut j&#39;ai ma patrie. </p>\n','Seigneur-donne-moi-des-ailes',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1367,'4e8a3816-a4d5-4aac-a955-67c9ce0a7177',0,0,'Unissons nos cœurs et nos voix',267,'267_CE_fr','1.\nUnissons nos coeurs et nos voix\nPour célébrer le Roi des rois\nQui mourut pour nous sur la croix,\nVictime expiatoire!\n\nRefrain\nBéni soit le jour du Seigneur,\nJour de triomphe et de bonheur!\nQue partout monte en son honneur\nUn hymne de victoire!\n\n\n2.\nIl vit, Il est ressuscité!\nRayonnant d\'immortalité!\nDu sépulcre Il est remonté\nVers son trône de gloire!\n\n\n3.\nDu haut de son trône, Il sourit\nAu pécheur qui pleure contrit:\nEt la clarté de son Esprit\nDissipe la nuit noire.','<p><strong>1.</strong> </p>\n<p>Unissons nos coeurs et nos voix </p>\n<p>Pour célébrer le Roi des rois </p>\n<p>Qui mourut pour nous sur la croix, </p>\n<p>Victime expiatoire! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Béni soit le jour du Seigneur,</strong> </p>\n<p><strong>Jour de triomphe et de bonheur!</strong> </p>\n<p><strong>Que partout monte en son honneur</strong> </p>\n<p><strong>Un hymne de victoire!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il vit, Il est ressuscité! </p>\n<p>Rayonnant d&#39;immortalité! </p>\n<p>Du sépulcre Il est remonté </p>\n<p>Vers son trône de gloire! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Du haut de son trône, Il sourit </p>\n<p>Au pécheur qui pleure contrit: </p>\n<p>Et la clarté de son Esprit </p>\n<p>Dissipe la nuit noire. </p>\n','Unissons-nos-coeurs-et-nos-voix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1368,'6919d5e6-64d4-4f85-bc80-88c729fc81ce',0,0,'L’astre à l’horizon descend',270,'270_CE_fr','1.\nL\'astre à l\'horizon descend,\nSur les monts l\'ombre s\'étend,\nVeillons pendant que la nuit\nAu ciel allume sans bruit\nSes lampes d\'or.\n\nRefrain\nGloire, gloire à l\'Éternel,\nAu Dieu de paix,\nSur la terre et dans le ciel\nTout te loue, Emmanuel,\nPour tes bienfaits.\n\n\n2.\nTandis que s\'éteint le jour,\nLa prière, ô Dieu d\'amour,\nVers ton saint trône voilé,\nAu fond du ciel étoilé,\nPrend son essor.\n\n\n3.\nDans ton Église, en tous lieux,\nSeigneur, sous les vastes cieux,\nRassemble tous tes enfants;\nRends-les courageux, vaillants\nSous ton drapeau.\n\n\n4.\nQuand tu reviendra, Jésus,\nQuand le mal ne sera plus,\nTu feras luire à nos yeux\nL\'aube du jour radieux\nQui luit là-haut.','<p><strong>1.</strong> </p>\n<p>L&#39;astre à l&#39;horizon descend, </p>\n<p>Sur les monts l&#39;ombre s&#39;étend, </p>\n<p>Veillons pendant que la nuit </p>\n<p>Au ciel allume sans bruit </p>\n<p>Ses lampes d&#39;or. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire, gloire à l&#39;Éternel,</strong> </p>\n<p><strong>Au Dieu de paix,</strong> </p>\n<p><strong>Sur la terre et dans le ciel</strong> </p>\n<p><strong>Tout te loue, Emmanuel,</strong> </p>\n<p><strong>Pour tes bienfaits.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tandis que s&#39;éteint le jour, </p>\n<p>La prière, ô Dieu d&#39;amour, </p>\n<p>Vers ton saint trône voilé, </p>\n<p>Au fond du ciel étoilé, </p>\n<p>Prend son essor. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans ton Église, en tous lieux, </p>\n<p>Seigneur, sous les vastes cieux, </p>\n<p>Rassemble tous tes enfants; </p>\n<p>Rends-les courageux, vaillants </p>\n<p>Sous ton drapeau. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand tu reviendra, Jésus, </p>\n<p>Quand le mal ne sera plus, </p>\n<p>Tu feras luire à nos yeux </p>\n<p>L&#39;aube du jour radieux </p>\n<p>Qui luit là-haut. </p>\n','L\'astre-a-l\'horizon-descend',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1369,'6bb4991f-e704-4987-a5d5-bf2293633327',0,0,'Jour de repos, jour du Seigneur',272,'272_CE_fr','1.\nJour de repos, jour du Seigneur\nNos âmes te bénissent\nQuand, après six jours de labeur\nTous nos travaux finissent\nLaisse là tes outils\nTes calculs, tes soucis\nO foule humaine\nDieu rompt la lourde chaîne\nTu peux déployer tes bras meurtris\n\n2.\nDe ce jour qui fait tant d\'heureux\nSachons donner une heure\nAux malades, aux malheureux\nA ceux dont le coeur pleure\nNous aurons imité\nLa sainte activité\nDe notre Maître\nO saint jour, tu dois être\nLa fête de la charité\n\n3.\nQu\'on s\'en souvienne pour t\'aimer\nO Dieu, plein de clémence\nEt, dans tous les coeurs, pour semer\nLa divine semence\nDans les temples remplis\nD\'auditeurs recueillis\nQu\'on fasse entendre\nTon appel doux et tendre\nAux pécheurs sauvés par ton Fils','<p><strong>1.</strong> </p>\n<p>Jour de repos, jour du Seigneur </p>\n<p>Nos âmes te bénissent </p>\n<p>Quand, après six jours de labeur </p>\n<p>Tous nos travaux finissent </p>\n<p>Laisse là tes outils </p>\n<p>Tes calculs, tes soucis </p>\n<p>O foule humaine </p>\n<p>Dieu rompt la lourde chaîne </p>\n<p>Tu peux déployer tes bras meurtris </p>\n<p><br/> <strong>2.</strong></p>\n<p>De ce jour qui fait tant d&#39;heureux </p>\n<p>Sachons donner une heure </p>\n<p>Aux malades, aux malheureux </p>\n<p>A ceux dont le coeur pleure </p>\n<p>Nous aurons imité </p>\n<p>La sainte activité </p>\n<p>De notre Maître </p>\n<p>O saint jour, tu dois être </p>\n<p>La fête de la charité </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;on s&#39;en souvienne pour t&#39;aimer </p>\n<p>O Dieu, plein de clémence </p>\n<p>Et, dans tous les coeurs, pour semer </p>\n<p>La divine semence </p>\n<p>Dans les temples remplis </p>\n<p>D&#39;auditeurs recueillis </p>\n<p>Qu&#39;on fasse entendre </p>\n<p>Ton appel doux et tendre </p>\n<p>Aux pécheurs sauvés par ton Fils </p>\n','Jour-de-repos-jour-du-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1370,'c1c10eec-5989-46d7-9fb2-9650a0f9c612',0,0,'Qu’aujourd’hui toute la terre',273,'273_CE_fr','1.\nQu\'aujourd\'hui toute la terre\nS\'égaie au nom du Seigneur;\nQu\'à Dieu monte sa prière,\nPar Jésus, le Rédempteur.\n\n\n2.\nQu\'aujourd\'hui son Évangile\nEn tous lieux soit publié;\nQu\'à porter son joug facile\nTout pécheur soit convié!\n\n\n3.\nQu\'aujourd\'hui remplis de joie\nA la voix de l\'Éternel,\nBien des coeurs trouvent la voie\nQui, d\'ici, conduit au ciel!\n\n\n4.\nQu\'aujourd\'hui, comme Marie,\nA tes pieds, ô mon Sauveur,\nJe t\'écoute et je te prie\nEt te reçoive en mon coeur!','<p><strong>1.</strong> </p>\n<p>Qu&#39;aujourd&#39;hui toute la terre </p>\n<p>S&#39;égaie au nom du Seigneur; </p>\n<p>Qu&#39;à Dieu monte sa prière, </p>\n<p>Par Jésus, le Rédempteur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;aujourd&#39;hui son Évangile </p>\n<p>En tous lieux soit publié; </p>\n<p>Qu&#39;à porter son joug facile </p>\n<p>Tout pécheur soit convié! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;aujourd&#39;hui remplis de joie </p>\n<p>A la voix de l&#39;Éternel, </p>\n<p>Bien des coeurs trouvent la voie </p>\n<p>Qui, d&#39;ici, conduit au ciel! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Qu&#39;aujourd&#39;hui, comme Marie, </p>\n<p>A tes pieds, ô mon Sauveur, </p>\n<p>Je t&#39;écoute et je te prie </p>\n<p>Et te reçoive en mon coeur! </p>\n','Qu\'aujourd\'hui-toute-la-terre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1371,'21643e2e-e72d-466a-b86a-69dd53c7f909',0,0,'Sans fruit, sans fruit',281,'281_CE_fr','1.\nSans fruit, sans fruit!\nPassé détruit,\nRegrets, tourments stériles,\nCombats vainement soutenus,\nVoeux formés et jamais tenus,\nTant d\'efforts inutiles\nSans un seul fruit!  (bis)\n\n\n2.\nSans fruit, sans fruit!\nÊtre réduit,\nA vivre sur la terre\nSans loi, sans but; s\'user, vieillir,\nSemer le vent, ne recueillir\nQue trouble et misère,\nSans un seul fruit!  (bis)\n\n\n3.\nToujours sans fruit!\nVoici la nuit;\nJésus, le divin Maître,\nSur son tribunal est monté.\nAux yeux de ce Juge irrité,\nComment oser paraître\nSans un seul fruit!  (bis)','<p><strong>1.</strong> </p>\n<p>Sans fruit, sans fruit! </p>\n<p>Passé détruit, </p>\n<p>Regrets, tourments stériles, </p>\n<p>Combats vainement soutenus, </p>\n<p>Voeux formés et jamais tenus, </p>\n<p>Tant d&#39;efforts inutiles </p>\n<p>Sans un seul fruit!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sans fruit, sans fruit! </p>\n<p>Être réduit, </p>\n<p>A vivre sur la terre </p>\n<p>Sans loi, sans but; s&#39;user, vieillir, </p>\n<p>Semer le vent, ne recueillir </p>\n<p>Que trouble et misère, </p>\n<p>Sans un seul fruit!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Toujours sans fruit! </p>\n<p>Voici la nuit; </p>\n<p>Jésus, le divin Maître, </p>\n<p>Sur son tribunal est monté. </p>\n<p>Aux yeux de ce Juge irrité, </p>\n<p>Comment oser paraître </p>\n<p>Sans un seul fruit!  (bis) </p>\n','Sans-fruit-sans-fruit',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1372,'d601295c-f790-4fc4-9206-74348783d534',0,0,'Quelle assurance, je suis sauvé',307,'307_CE_fr','1.\nQuelle assurance, je suis sauvé!\nQuel avant-goût du ciel m\'est donné!\nNé de l\'Esprit, baptisé de feu,\nCréé de nouveau, je vis pour Dieu.\n\nRefrain\nC\'est mon cantique, c\'est mon histoire!\nAlléluia! Je chante à sa gloire!\nGloire! Alléluia! Gloire au Sauveur!\nGloire éternelle à mon Rédempteur!\n\n\n2.\nQuelle assurance, je suis joyeux!\nDéjà mon nom est inscrit aux cieux!\nL\'amour céleste remplit mon coeur\nDepuis mon abandon au Sauveur!\n\n\n3.\nQuelle assurance, je suis en paix!\nLa paix de Jésus dure à jamais!\nPlein de sa bonté, de son amour,\nTout prêt pour son glorieux retour.','<p><strong>1.</strong> </p>\n<p>Quelle assurance, je suis sauvé! </p>\n<p>Quel avant-goût du ciel m&#39;est donné! </p>\n<p>Né de l&#39;Esprit, baptisé de feu, </p>\n<p>Créé de nouveau, je vis pour Dieu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>C&#39;est mon cantique, c&#39;est mon histoire!</strong> </p>\n<p><strong>Alléluia! Je chante à sa gloire!</strong> </p>\n<p><strong>Gloire! Alléluia! Gloire au Sauveur!</strong> </p>\n<p><strong>Gloire éternelle à mon Rédempteur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quelle assurance, je suis joyeux! </p>\n<p>Déjà mon nom est inscrit aux cieux! </p>\n<p>L&#39;amour céleste remplit mon coeur </p>\n<p>Depuis mon abandon au Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quelle assurance, je suis en paix! </p>\n<p>La paix de Jésus dure à jamais! </p>\n<p>Plein de sa bonté, de son amour, </p>\n<p>Tout prêt pour son glorieux retour. </p>\n','Quelle-assurance-je-suis-sauve',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1373,'810fffaa-c3cc-4fb4-a371-cb2302828400',0,0,'Jésus, du fardeau de la loi',308,'308_CE_fr','1.\nJésus, du fardeau de la loi,\nPar ton mon âme est libérée;\nPour toujours elle est délivrée\nDe ce qui causait son effroi.\n\nRefrain\nLibre en regardant au Calvaire,\nLibre par ta mort salutaire,\nLibre, car j\'entendis ta voix,\nLibre par le sang de ta croix.\n\n\n2.\nO bonheur! Il est éternel,\nLe salut que Jésus me donne!\nEt son amour qui me pardonne\nM\'ouvrira la porte du ciel!\n\n\n3.\nDieu m\'aime, je suis son enfant;\nEn sa fidélité j\'espère;\nDans tous les combats de la terre\nIl veut me rendre triomphant.','<p><strong>1.</strong> </p>\n<p>Jésus, du fardeau de la loi, </p>\n<p>Par ton mon âme est libérée; </p>\n<p>Pour toujours elle est délivrée </p>\n<p>De ce qui causait son effroi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Libre en regardant au Calvaire,</strong> </p>\n<p><strong>Libre par ta mort salutaire,</strong> </p>\n<p><strong>Libre, car j&#39;entendis ta voix,</strong> </p>\n<p><strong>Libre par le sang de ta croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O bonheur! Il est éternel, </p>\n<p>Le salut que Jésus me donne! </p>\n<p>Et son amour qui me pardonne </p>\n<p>M&#39;ouvrira la porte du ciel! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dieu m&#39;aime, je suis son enfant; </p>\n<p>En sa fidélité j&#39;espère; </p>\n<p>Dans tous les combats de la terre </p>\n<p>Il veut me rendre triomphant. </p>\n','Jesus-du-fardeau-de-la-loi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1374,'44831561-6ef4-47c9-a111-8ad6311a50bc',0,0,'Jésus, A Toi J’appartiens Pour Jamais',153,'153_CE_fr','1.\nJésus, à toi j\'appartient pour jamais;\nViens en mon âme habiter désormais;\nDivine Parole,\nBrise toute idole!\n\nRefrain\nPar ton Saint-Esprit\nRends-moi (pur comme toi!  (ter)\nPar ton Saint-Esprit\nRends-moi pur comme toi!\n\n\n2.\nJésus, du ciel où ton trône est assis,\nMontre la route à mes pas indécis.\nJe te sacrifie\nMon coeur et ma vie.\n\n\n3.\nTu veux, Seigneur, un coeur sanctifié;\nDonne-le moi, divin Crucifié!\nSeigneur, quand serai-je\nPlus blanc que la neige?','<p><strong>1.</strong> </p>\n<p>Jésus, à toi j&#39;appartient pour jamais; </p>\n<p>Viens en mon âme habiter désormais; </p>\n<p>Divine Parole, </p>\n<p>Brise toute idole! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Par ton Saint-Esprit</strong> </p>\n<p><strong>Rends-moi (pur comme toi!  (ter)</strong> </p>\n<p><strong>Par ton Saint-Esprit</strong> </p>\n<p><strong>Rends-moi pur comme toi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, du ciel où ton trône est assis, </p>\n<p>Montre la route à mes pas indécis. </p>\n<p>Je te sacrifie </p>\n<p>Mon coeur et ma vie. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu veux, Seigneur, un coeur sanctifié; </p>\n<p>Donne-le moi, divin Crucifié! </p>\n<p>Seigneur, quand serai-je </p>\n<p>Plus blanc que la neige? </p>\n','Jesus-A-Toi-J\'appartiens-Pour-Jamais',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1375,'bdf4e0bc-2345-4436-8def-793c93503665',0,0,'Mon Sauveur, je voudrais être',170,'170_CE_fr','1.\nMon Sauveur, je voudrais être\nUn fleur de tes parvis,\nBriller pou Toi seul, ô Maître,\nPuisque par Toi seul je vis;\nDans le silence répandre,\nLe parfum de ton amour;\nDans la nuit obscure, attendre\nL\'aurore de ton retour.\n\n\n2.\nMon Sauveur, je voudrais être\nSur le sentier ténébreux,\nUn faible reflet, ô Maître,\nDe ton éclat radieux;\nLuire sur la sombre route\nDes coeur perdus loin de Toi,\nDissiper la nuit de doute\nPar la clarté de la Foi.\n\n\n3.\nMon Sauveur, je voudrais être\nComme un écho de ta voix\nPour proclamer, ô doux Maître,\nLe Mystère de ta croix.\nPour parler de délivrance\nA ceux qu\'assaille la mort,\nPour consoler la souffrance\nEt montrer à tous le port.','<p><strong>1.</strong> </p>\n<p>Mon Sauveur, je voudrais être </p>\n<p>Un fleur de tes parvis, </p>\n<p>Briller pou Toi seul, ô Maître, </p>\n<p>Puisque par Toi seul je vis; </p>\n<p>Dans le silence répandre, </p>\n<p>Le parfum de ton amour; </p>\n<p>Dans la nuit obscure, attendre </p>\n<p>L&#39;aurore de ton retour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon Sauveur, je voudrais être </p>\n<p>Sur le sentier ténébreux, </p>\n<p>Un faible reflet, ô Maître, </p>\n<p>De ton éclat radieux; </p>\n<p>Luire sur la sombre route </p>\n<p>Des coeur perdus loin de Toi, </p>\n<p>Dissiper la nuit de doute </p>\n<p>Par la clarté de la Foi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon Sauveur, je voudrais être </p>\n<p>Comme un écho de ta voix </p>\n<p>Pour proclamer, ô doux Maître, </p>\n<p>Le Mystère de ta croix. </p>\n<p>Pour parler de délivrance </p>\n<p>A ceux qu&#39;assaille la mort, </p>\n<p>Pour consoler la souffrance </p>\n<p>Et montrer à tous le port. </p>\n','Mon-Sauveur-je-voudrais-etre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1376,'f93d5865-206d-4bff-8554-821077a64c26',0,0,'Jésus me demande d\'être',177,'177_CE_fr','1.\nJésus me demande d\'être\nUn rayon de soleil,\nQui gaîment fasse connaître\nSon amour sans pareil.\n\nRefrain\nUn rayon de joie,\nUn doux rayon de soleil\nQue Jésus envoie!\nOh! Quel bonheur sans pareil!\n\n2.\nJésus me demande d\'être\nUn rayon bienfaisant,\nComme un reflet de ce Maître\nAimable et complaisant.\n\n3.\nOui, Seigneur, donne-moi d\'être\nComme un rayon d\'espoir,\nParaissant à la fenêtre\nQuand le ciel est tout noir.\n\n4.\nOh! Que m\'oubliant moi-même,\nJe ne m\'efforce plus\nQue d\'être pour ceux que j\'aime,\nUn reflet de Jésus.\n\n5.\nQue partout mon gai sourire\nEt ma joyeuse humeur\nSoit un baume à qui soupire\nEt réjouisse en coeur\n\nRefrain\nQue Jésus m\'emploie\nEt qu\'Il me rende pareil\nAux rayons de joie,\nAux doux rayons du soleil!','<p><strong>1.</strong> </p>\n<p>Jésus me demande d&#39;être </p>\n<p>Un rayon de soleil, </p>\n<p>Qui gaîment fasse connaître </p>\n<p>Son amour sans pareil. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Un rayon de joie,</strong> </p>\n<p><strong>Un doux rayon de soleil</strong> </p>\n<p><strong>Que Jésus envoie!</strong> </p>\n<p><strong>Oh! Quel bonheur sans pareil!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus me demande d&#39;être </p>\n<p>Un rayon bienfaisant, </p>\n<p>Comme un reflet de ce Maître </p>\n<p>Aimable et complaisant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, Seigneur, donne-moi d&#39;être </p>\n<p>Comme un rayon d&#39;espoir, </p>\n<p>Paraissant à la fenêtre </p>\n<p>Quand le ciel est tout noir. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh! Que m&#39;oubliant moi-même, </p>\n<p>Je ne m&#39;efforce plus </p>\n<p>Que d&#39;être pour ceux que j&#39;aime, </p>\n<p>Un reflet de Jésus. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Que partout mon gai sourire </p>\n<p>Et ma joyeuse humeur </p>\n<p>Soit un baume à qui soupire </p>\n<p>Et réjouisse en coeur </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Que Jésus m&#39;emploie</strong> </p>\n<p><strong>Et qu&#39;Il me rende pareil</strong> </p>\n<p><strong>Aux rayons de joie,</strong> </p>\n<p><strong>Aux doux rayons du soleil!</strong> </p>\n','Jesus-me-demande-d\'etre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1377,'c7f5de4c-cd52-4b9d-946b-36c4be252f1a',0,0,'Quel Repos Céleste',181,'181_CE_fr','1.\nQuel repos céleste,\nJésus, d\'être à Toi!\nA toi pour la mort et la vie,\nDans les jours mauvais\nDe chanter avec foi:\nTout est bien, ma paix est infinie!\n\nRefrain\nQuel repos,  (ter) quel céleste repos!\n\n\n2.\nQuel repos céleste!\nMon fardeau n\'est plus!\nLibre par le sang du Calvaire,\nTous mes ennemis,\nJésus les a vaincus,\nGloire et louanges à Dieu notre Père!\n\n\n3.\nQuel repos céleste!\nTu conduis mes pas,\nTu me combles de tes richesse,\nDans ton grand amour,\nChaque jour tu sauras\nDéployer envers moi tes tendresses.\n\n\n4.\nQuel repos céleste,\nQuand enfin, Seigneur,\nAuprès de Toi, j\'aurai ma place,\nAprès les travaux,\nLes combats, la douleur,\nA jamais je pourrai voir ta face!','<p><strong>1.</strong> </p>\n<p>Quel repos céleste, </p>\n<p>Jésus, d&#39;être à Toi! </p>\n<p>A toi pour la mort et la vie, </p>\n<p>Dans les jours mauvais </p>\n<p>De chanter avec foi: </p>\n<p>Tout est bien, ma paix est infinie! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Quel repos,  (ter) quel céleste repos!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quel repos céleste! </p>\n<p>Mon fardeau n&#39;est plus! </p>\n<p>Libre par le sang du Calvaire, </p>\n<p>Tous mes ennemis, </p>\n<p>Jésus les a vaincus, </p>\n<p>Gloire et louanges à Dieu notre Père! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quel repos céleste! </p>\n<p>Tu conduis mes pas, </p>\n<p>Tu me combles de tes richesse, </p>\n<p>Dans ton grand amour, </p>\n<p>Chaque jour tu sauras </p>\n<p>Déployer envers moi tes tendresses. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quel repos céleste, </p>\n<p>Quand enfin, Seigneur, </p>\n<p>Auprès de Toi, j&#39;aurai ma place, </p>\n<p>Après les travaux, </p>\n<p>Les combats, la douleur, </p>\n<p>A jamais je pourrai voir ta face! </p>\n','Quel-Repos-Celeste',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1378,'6315e0cd-70ee-4a15-a74a-417654e80a96',0,0,'Jamais Dieu ne délaisse',185,'185_CE_fr','1.\nJamais Dieu ne délaisse\nQui se confie en Lui;\nSi le monde m\'oppresse\nJésus est mon appui.\nCe Dieu tendre et fidèle\nGarde en sa paix les siens\nPour la vie éternelle,\nEt les comble de biens.\n\n\n2.\nJe veux, sachant qu\'Il m\'aime,\nMe remettre à ses soins;\nBeaucoup mieux que moi-même\nIl connaît mes besoins.\nCe Dieu plein de tendresse,\nConfondrait-Il ma foi?\nNon! Plus le mal me presse\nPlus Il est près de moi.\n\n\n3.\nSeigneur, par l\'efficace\nDu sang versé pour moi,\nAccorde-moi la grâce\nDe vivre tout pour toi!\nC\'est la vie éternelle\nDéjà dès ici-bas,\nJusqu\'au jour qui m\'appelle\nA passer dans tes bras.','<p><strong>1.</strong> </p>\n<p>Jamais Dieu ne délaisse </p>\n<p>Qui se confie en Lui; </p>\n<p>Si le monde m&#39;oppresse </p>\n<p>Jésus est mon appui. </p>\n<p>Ce Dieu tendre et fidèle </p>\n<p>Garde en sa paix les siens </p>\n<p>Pour la vie éternelle, </p>\n<p>Et les comble de biens. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je veux, sachant qu&#39;Il m&#39;aime, </p>\n<p>Me remettre à ses soins; </p>\n<p>Beaucoup mieux que moi-même </p>\n<p>Il connaît mes besoins. </p>\n<p>Ce Dieu plein de tendresse, </p>\n<p>Confondrait-Il ma foi? </p>\n<p>Non! Plus le mal me presse </p>\n<p>Plus Il est près de moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur, par l&#39;efficace </p>\n<p>Du sang versé pour moi, </p>\n<p>Accorde-moi la grâce </p>\n<p>De vivre tout pour toi! </p>\n<p>C&#39;est la vie éternelle </p>\n<p>Déjà dès ici-bas, </p>\n<p>Jusqu&#39;au jour qui m&#39;appelle </p>\n<p>A passer dans tes bras. </p>\n','Jamais-Dieu-ne-delaisse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1379,'f30c1c5e-9921-4ea8-92fa-e7ad3a6a7f86',0,0,'Dans l’abîme de misères',206,'206_CE_fr','1.\nDans l\'abîme de misères\nOù l\'expirais loin de toi,\nTa bonté, Dieu de mes pères,\nDescendit jusques à moi.\nTu parlas: mes yeux s\'ouvrirent;\nA mes regards éperdus\nTes secrets se découvrirent;\nJ\'étais mort, et je vécus.\n\n\n2.\nPlus haut que toute pensée,\nTa main étendit les cieux;\nTu veux: leur voûte embrasée\nMais privés d\'aimer, de croire\nTous ces cieux et leur splendeur\nNe valent pas pour ta gloire\nUn seul soupir d\'un seul coeur.\n\n\n3.\nEsprit du Dieu que j\'adore,\nAh!  Forme en moi ce soupir,\nCe feu qui n\'a point encore\nRéchauffé mon repentir!\nQu\'à l\'amour mon coeur se livre,\nAimer, aimer, voilà vivre;\nFais-moi vivre, ô Dieu de paix!','<p><strong>1.</strong> </p>\n<p>Dans l&#39;abîme de misères </p>\n<p>Où l&#39;expirais loin de toi, </p>\n<p>Ta bonté, Dieu de mes pères, </p>\n<p>Descendit jusques à moi. </p>\n<p>Tu parlas: mes yeux s&#39;ouvrirent; </p>\n<p>A mes regards éperdus </p>\n<p>Tes secrets se découvrirent; </p>\n<p>J&#39;étais mort, et je vécus. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Plus haut que toute pensée, </p>\n<p>Ta main étendit les cieux; </p>\n<p>Tu veux: leur voûte embrasée </p>\n<p>Mais privés d&#39;aimer, de croire </p>\n<p>Tous ces cieux et leur splendeur </p>\n<p>Ne valent pas pour ta gloire </p>\n<p>Un seul soupir d&#39;un seul coeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Esprit du Dieu que j&#39;adore, </p>\n<p>Ah!  Forme en moi ce soupir, </p>\n<p>Ce feu qui n&#39;a point encore </p>\n<p>Réchauffé mon repentir! </p>\n<p>Qu&#39;à l&#39;amour mon coeur se livre, </p>\n<p>Aimer, aimer, voilà vivre; </p>\n<p>Fais-moi vivre, ô Dieu de paix! </p>\n','Dans-l\'abime-de-miseres',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1380,'87333f9e-f1eb-4e5d-a83c-cc68a4bf1d3e',0,0,'La croix reste debout',221,'221_CE_fr','1.\nLa croix reste debout,\nAlléluia!  (bis)\nElle domine tout,\nAlléluia!  (bis)\nC\'est en vain que la haine\nContre elle se déchaîne:\nLumineuse et sereine\nLa croix reste debout!\n\nRefrain\nAlléluia!  Alléluia!\nAu plus fort des combats,\nAlléluia!  Alléluia!\nLa croix ne recule pas!\n\n\n2.\nSur elle en Golgotha,\nAlléluia!  (bis)\nLe fils de Dieu monta.\nAlléluia!  (bis)\nAmour incomparable!\nCe Sauveur adorable,\nPour l\'homme misérable,\nMourut en Golgotha.\n\n\n3.\nSoldats du Roi des rois,\nAlléluia!  (bis)\nPortons partout sa croix!\nAlléluia!  (bis)\nCar tous ceux qu\'il délivre\nSous la croix doivent vivre,\nSur la croix doivent suivre\nJésus le Roi des rois.','<p><strong>1.</strong> </p>\n<p>La croix reste debout, </p>\n<p>Alléluia!  (bis) </p>\n<p>Elle domine tout, </p>\n<p>Alléluia!  (bis) </p>\n<p>C&#39;est en vain que la haine </p>\n<p>Contre elle se déchaîne: </p>\n<p>Lumineuse et sereine </p>\n<p>La croix reste debout! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Alléluia!  Alléluia!</strong> </p>\n<p><strong>Au plus fort des combats,</strong> </p>\n<p><strong>Alléluia!  Alléluia!</strong> </p>\n<p><strong>La croix ne recule pas!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sur elle en Golgotha, </p>\n<p>Alléluia!  (bis) </p>\n<p>Le fils de Dieu monta. </p>\n<p>Alléluia!  (bis) </p>\n<p>Amour incomparable! </p>\n<p>Ce Sauveur adorable, </p>\n<p>Pour l&#39;homme misérable, </p>\n<p>Mourut en Golgotha. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Soldats du Roi des rois, </p>\n<p>Alléluia!  (bis) </p>\n<p>Portons partout sa croix! </p>\n<p>Alléluia!  (bis) </p>\n<p>Car tous ceux qu&#39;il délivre </p>\n<p>Sous la croix doivent vivre, </p>\n<p>Sur la croix doivent suivre </p>\n<p>Jésus le Roi des rois. </p>\n','La-croix-reste-debout',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1381,'4a0da50b-3ed4-45ff-a975-1b5938377670',0,0,'Oh! viens à moi, ton Sauver et ton Frère',275,'275_CE_fr','1.\nOh! Viens à moi, ton Sauveur et ton frère,\nJe donnerai, pèlerin, à ton coeur,\nTous mes trésors au lieu de ta misère\nEt mon repos au sein de ton labeur!\n\nRefrain\nOh! Viens à moi! (ter)\nMon repos est pour toi.  (ter)\n\n\n2.\nLa paix pour toi, et plus d\'inquiétude!\nTout tes péchés, oh! Viens les déposer\nDevant la croix, remède à toute chute,\nViens sur mon coeur, enfin, te reposer!\n\nRefrain\nOh!  Viens à moi!  (ter)\nMa paix, elle est pour toi.  (ter)\n\n\n3.\nAccours vers Moi, si tu te sens coupable\nSi du péché tu crains le châtiment;\nNe sais-tu pas, vérité redoutable,\nQu\'après la mort suivra le jugement?\n\nRefrain\nOh! Viens à moi!  (ter)\nMon pardon est pour toi!  (ter)','<p><strong>1.</strong> </p>\n<p>Oh! Viens à moi, ton Sauveur et ton frère, </p>\n<p>Je donnerai, pèlerin, à ton coeur, </p>\n<p>Tous mes trésors au lieu de ta misère </p>\n<p>Et mon repos au sein de ton labeur! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Viens à moi! (ter)</strong> </p>\n<p><strong>Mon repos est pour toi.  (ter)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La paix pour toi, et plus d&#39;inquiétude! </p>\n<p>Tout tes péchés, oh! Viens les déposer </p>\n<p>Devant la croix, remède à toute chute, </p>\n<p>Viens sur mon coeur, enfin, te reposer! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh!  Viens à moi!  (ter)</strong> </p>\n<p><strong>Ma paix, elle est pour toi.  (ter)</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Accours vers Moi, si tu te sens coupable </p>\n<p>Si du péché tu crains le châtiment; </p>\n<p>Ne sais-tu pas, vérité redoutable, </p>\n<p>Qu&#39;après la mort suivra le jugement? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Viens à moi!  (ter)</strong> </p>\n<p><strong>Mon pardon est pour toi!  (ter)</strong> </p>\n','Oh!-viens-a-moi-ton-Sauver-et-ton-Frere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1382,'ff8a4de2-4cfe-4ffe-ae3c-3f135e0ee960',0,0,'Si vous saviez',279,'279_CE_fr','1.\nSi vous saviez quel Sauveur je possède!\nIl est l\'ami le plus tendre de tous;\nPour moi devant le Père Il intercède:\nOh! Je voudrais qu\'Il fût aussi pour vous!\n\nRefrain\nMon Sauveur vous aime;\nAh! Cherchez en Lui\nVotre ami suprême,\nVotre seul appui!\n\n\n2.\nSi vous saviez la paix douce et profonde\nQue le Sauveur en mon âme apporta!\nPour cette paix que peut donne le monde?\nElle jaillit pour vous de Golgotha.\n\n\n3.\nSi vous saviez quelle douce espérance\nLe Dieu de paix fait rayonner des cieux!\nCombien sa voix sait calmer la souffrance\nEt son regard rendre le coeur joyeux!\n\n\n4.\nQuand vous saurez combien Jésus vous aime,\nQuand vous saurez combien son joug est doux,\nNe gardez pas ce trésor pour vous-même,\nMais avec moi, dites autour de vous:','<p><strong>1.</strong> </p>\n<p>Si vous saviez quel Sauveur je possède! </p>\n<p>Il est l&#39;ami le plus tendre de tous; </p>\n<p>Pour moi devant le Père Il intercède: </p>\n<p>Oh! Je voudrais qu&#39;Il fût aussi pour vous! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon Sauveur vous aime;</strong> </p>\n<p><strong>Ah! Cherchez en Lui</strong> </p>\n<p><strong>Votre ami suprême,</strong> </p>\n<p><strong>Votre seul appui!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si vous saviez la paix douce et profonde </p>\n<p>Que le Sauveur en mon âme apporta! </p>\n<p>Pour cette paix que peut donne le monde? </p>\n<p>Elle jaillit pour vous de Golgotha. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si vous saviez quelle douce espérance </p>\n<p>Le Dieu de paix fait rayonner des cieux! </p>\n<p>Combien sa voix sait calmer la souffrance </p>\n<p>Et son regard rendre le coeur joyeux! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand vous saurez combien Jésus vous aime, </p>\n<p>Quand vous saurez combien son joug est doux, </p>\n<p>Ne gardez pas ce trésor pour vous-même, </p>\n<p>Mais avec moi, dites autour de vous: </p>\n','Si-vous-saviez',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1383,'9fd32180-8f48-403a-9f8c-1f46688c6cf2',0,0,'Où cherchez- vous le bonheur',285,'285_CE_fr','1.\nOù cherchez-vous le bonheur,\nDans ce monde où tout passe?\nAvez-vous en votre coeur\nPour Jésus une place?\nA votre porte il se tient;\nSans se lasser, il revient…\nPour Jésus, votre Sauveur,\nN\'avez-vous point de place?\n\nRefrain\nN\'avez-vous point de place  (bis)\nPour Jésus, votre Sauveur,\nN\'avez-vous point de place?\n\n\n2.\nS\'Il a souffert, c\'est pour vous,\nOh! Merveilleuse grâce!\nLorsqu\'Il luttait à genoux,\nDieu lui voilant sa face!\nAh! De sa sublime croix,\nN\'entendez-vous pas la voix,\nQui vous dit: Pauvre pécheur,\nN\'as-tu donc point de place?\n\n\n3.\nSi le monde a votre coeur,\nCroyez-vous donc qu\'Il fasse\nJusqu\'au bout votre bonheur?\nVous savez que tout passe!\nOh! Pendant qu\'il est temps,\nÉcoutez les doux accents\nDe la voix du grand Vainqueur,\nEt faites-lui donc place!','<p><strong>1.</strong> </p>\n<p>Où cherchez-vous le bonheur, </p>\n<p>Dans ce monde où tout passe? </p>\n<p>Avez-vous en votre coeur </p>\n<p>Pour Jésus une place? </p>\n<p>A votre porte il se tient; </p>\n<p>Sans se lasser, il revient… </p>\n<p>Pour Jésus, votre Sauveur, </p>\n<p>N&#39;avez-vous point de place? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>N&#39;avez-vous point de place  (bis)</strong> </p>\n<p><strong>Pour Jésus, votre Sauveur,</strong> </p>\n<p><strong>N&#39;avez-vous point de place?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>S&#39;Il a souffert, c&#39;est pour vous, </p>\n<p>Oh! Merveilleuse grâce! </p>\n<p>Lorsqu&#39;Il luttait à genoux, </p>\n<p>Dieu lui voilant sa face! </p>\n<p>Ah! De sa sublime croix, </p>\n<p>N&#39;entendez-vous pas la voix, </p>\n<p>Qui vous dit: Pauvre pécheur, </p>\n<p>N&#39;as-tu donc point de place? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si le monde a votre coeur, </p>\n<p>Croyez-vous donc qu&#39;Il fasse </p>\n<p>Jusqu&#39;au bout votre bonheur? </p>\n<p>Vous savez que tout passe! </p>\n<p>Oh! Pendant qu&#39;il est temps, </p>\n<p>Écoutez les doux accents </p>\n<p>De la voix du grand Vainqueur, </p>\n<p>Et faites-lui donc place! </p>\n','Ou-cherchez-vous-le-bonheur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1384,'c101a7a9-4270-4d4a-b70f-f39c6e73def4',0,0,'C’est a l’ombre de tes ailes',176,'176_CE_fr','1.\nC\'est à l\'ombre de tes ailes\nQu\'est le vrai repos;\nLà plus de douleurs cruelles,\nLà plus d\'angoisses mortelles,\nLà plus d\'écrasants fardeaux:\nC\'est le vrai repos!  (bis 2 lignes)\n\nRefrain\nVrai repos, paix parfaite et bonheur\nSous les ailes du Seigneur!  (bis 2 lignes)\n\n\n2.\nC\'est à l\'ombre de tes ailes\nQu\'on trouve la paix…\nLes oiseaux dans leurs nids frêles,\nSous les plumes maternelles,\nNe s\'épouvante jamais:\nIls dorment en paix!  (bis 2 lignes)\n\n\n3.\nC\'est à l\'ombre de tes ailes\nQu\'est le vrai bonheur.\nToutes choses sont nouvelles,\nEt c\'est la vie éternelle,\nQue d\'être près de ton coeur:\nC\'est le vrai bonheur!  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>C&#39;est à l&#39;ombre de tes ailes </p>\n<p>Qu&#39;est le vrai repos; </p>\n<p>Là plus de douleurs cruelles, </p>\n<p>Là plus d&#39;angoisses mortelles, </p>\n<p>Là plus d&#39;écrasants fardeaux: </p>\n<p>C&#39;est le vrai repos!  (bis 2 lignes) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Vrai repos, paix parfaite et bonheur</strong> </p>\n<p><strong>Sous les ailes du Seigneur!  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est à l&#39;ombre de tes ailes </p>\n<p>Qu&#39;on trouve la paix… </p>\n<p>Les oiseaux dans leurs nids frêles, </p>\n<p>Sous les plumes maternelles, </p>\n<p>Ne s&#39;épouvante jamais: </p>\n<p>Ils dorment en paix!  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est à l&#39;ombre de tes ailes </p>\n<p>Qu&#39;est le vrai bonheur. </p>\n<p>Toutes choses sont nouvelles, </p>\n<p>Et c&#39;est la vie éternelle, </p>\n<p>Que d&#39;être près de ton coeur: </p>\n<p>C&#39;est le vrai bonheur!  (bis 2 lignes) </p>\n','C\'est-a-l\'ombre-de-tes-ailes',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1385,'469bc8a1-dfa9-4a7a-9968-f984bea1a1e9',0,0,'Au combat de la vie',215,'215_CE_fr','1.\nAu combat de la vie,\nConscrits et vétérans,\nLe Seigneur nous convie:\nSoldats, serrons nos rangs!\nQu\'au divin Capitaine\nNotre coeur soit uni:\nLa victoire est certaine\nSous son drapeau béni.\n\n\n2.\nLa croix est sa bannière,\nSon beau nom est Jésus;\nDes armes de lumière\nIl revêt ses élus.\nSon Esprit les enflamme\nAu plus fort des combats;\nSon âme est dans leur âme,\nSa force est dans leur bras.\n\n\n3.\nJeunesse ardente et fière,\nJeunesse au coeur vaillant,\nDonne-toi tout entière\nAu Sauveur tout-puissant!\nSoumise à sa loi pure,\nTu mettras sous tes pieds,\nTes péchés, ta souillure,\nPar sa mort expiés.\n\n\n4.\nPar sa grâce infinie\nIl guérira tes maux;\nEt, la lutte finie,\nAprès bien de travaux,\nSur ton front qui rayonne\nD\'espoir et de clarté,\nIl mettra la couronne\nDe l\'immortalité!','<p><strong>1.</strong> </p>\n<p>Au combat de la vie, </p>\n<p>Conscrits et vétérans, </p>\n<p>Le Seigneur nous convie: </p>\n<p>Soldats, serrons nos rangs! </p>\n<p>Qu&#39;au divin Capitaine </p>\n<p>Notre coeur soit uni: </p>\n<p>La victoire est certaine </p>\n<p>Sous son drapeau béni. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La croix est sa bannière, </p>\n<p>Son beau nom est Jésus; </p>\n<p>Des armes de lumière </p>\n<p>Il revêt ses élus. </p>\n<p>Son Esprit les enflamme </p>\n<p>Au plus fort des combats; </p>\n<p>Son âme est dans leur âme, </p>\n<p>Sa force est dans leur bras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jeunesse ardente et fière, </p>\n<p>Jeunesse au coeur vaillant, </p>\n<p>Donne-toi tout entière </p>\n<p>Au Sauveur tout-puissant! </p>\n<p>Soumise à sa loi pure, </p>\n<p>Tu mettras sous tes pieds, </p>\n<p>Tes péchés, ta souillure, </p>\n<p>Par sa mort expiés. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par sa grâce infinie </p>\n<p>Il guérira tes maux; </p>\n<p>Et, la lutte finie, </p>\n<p>Après bien de travaux, </p>\n<p>Sur ton front qui rayonne </p>\n<p>D&#39;espoir et de clarté, </p>\n<p>Il mettra la couronne </p>\n<p>De l&#39;immortalité! </p>\n','Au-combat-de-la-vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1386,'ff3dbd30-3336-4cbd-b42e-781a485925db',0,0,'Voyez l\'étendard céleste',223,'223_CE_fr','1.\nVoyez l\'étendard céleste\nQui s\'agite au vent!\nC\'est un secours manifeste;\nFrères, en avant!\n\nRefrain\n« Tenez ferme, car j\'avance, »\nAmis répondons:\n« O Jésus, notre espérance,\nPar Toi nous vaincrons! »\n\n\n2.\nSatan, prince de ce monde,\nRedouble ses coups;\nNotre faiblesse est profonde;\nQue deviendrons-nous?\n\n\n3.\nSur la montagne prochaine\nSonnent les clairons…\nAu nom du grand Capitaine\nNous triompherons!\n\n\n4.\nLe combat est long, peut-être,\nÉlevons nos coeurs!\nAvec Jésus nos Maître,\nNous serons vainqueurs!','<p><strong>1.</strong> </p>\n<p>Voyez l&#39;étendard céleste </p>\n<p>Qui s&#39;agite au vent! </p>\n<p>C&#39;est un secours manifeste; </p>\n<p>Frères, en avant! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>« Tenez ferme, car j&#39;avance, »</strong> </p>\n<p><strong>Amis répondons:</strong> </p>\n<p><strong>« O Jésus, notre espérance,</strong> </p>\n<p><strong>Par Toi nous vaincrons! »</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Satan, prince de ce monde, </p>\n<p>Redouble ses coups; </p>\n<p>Notre faiblesse est profonde; </p>\n<p>Que deviendrons-nous? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sur la montagne prochaine </p>\n<p>Sonnent les clairons… </p>\n<p>Au nom du grand Capitaine </p>\n<p>Nous triompherons! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le combat est long, peut-être, </p>\n<p>Élevons nos coeurs! </p>\n<p>Avec Jésus nos Maître, </p>\n<p>Nous serons vainqueurs! </p>\n','Voyez-l\'etendard-celeste',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1387,'c81e2f08-b230-4c7a-b9aa-e30dfdfbbdf7',0,0,'Au ciel est la maison du Père',254,'254_CE_fr','1.\nAu ciel est la maison du Père,\nÉtincelante de beauté;\nTout en elle est gloire et lumière,\nIneffable félicité.\n\nRefrain\nVers le ciel,  (bis)\nNous marchons vers le ciel.\nC\'est au ciel,  (bis)\nQu\'est notre héritage éternel.\n\n\n2.\nLà le bonheur est sans mélange,\nLà le péché ne règne plus;\nC\'est l\'amour et c\'est la louange,\nC\'est la présence de Jésus.\n\n\n3.\nDans nos fatigues sur la terre,\nDans nos combats et nos douleurs,\nC\'est toi, douce maison du Père\nQue cherchent nos yeux et nos coeurs.\n\n\n4.\nJours de peine ou jours d\'allégresse,\nDouce brise ou vents orageux,\nPoussez-nous, poussez-nous sans cesse\nVers notre demeure des cieux.','<p><strong>1.</strong> </p>\n<p>Au ciel est la maison du Père, </p>\n<p>Étincelante de beauté; </p>\n<p>Tout en elle est gloire et lumière, </p>\n<p>Ineffable félicité. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Vers le ciel,  (bis)</strong> </p>\n<p><strong>Nous marchons vers le ciel.</strong> </p>\n<p><strong>C&#39;est au ciel,  (bis)</strong> </p>\n<p><strong>Qu&#39;est notre héritage éternel.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Là le bonheur est sans mélange, </p>\n<p>Là le péché ne règne plus; </p>\n<p>C&#39;est l&#39;amour et c&#39;est la louange, </p>\n<p>C&#39;est la présence de Jésus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans nos fatigues sur la terre, </p>\n<p>Dans nos combats et nos douleurs, </p>\n<p>C&#39;est toi, douce maison du Père </p>\n<p>Que cherchent nos yeux et nos coeurs. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jours de peine ou jours d&#39;allégresse, </p>\n<p>Douce brise ou vents orageux, </p>\n<p>Poussez-nous, poussez-nous sans cesse </p>\n<p>Vers notre demeure des cieux. </p>\n','Au-ciel-est-la-maison-du-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1388,'5f37cfa8-bc8f-4078-ada1-dc2b0a2bfb41',0,0,'Pécheur, je voudrais te guérir',286,'286_CE_fr','1.\nPécheur, je voudrais te guérir;\nJ\'ai vu tes larmes, ta souffrance,\nMais pour avoir la délivrance,\nIl faut apprendre à m\'obéir.\nVoici, je me tiens à la porte,\nJe suis ton Maître et ton Sauveur;\nC\'est le bonheur que je t\'apporte:\nNe veux-tu pas m\'ouvrir ton coeur?\n\n\n2.\nSais-tu que je suis né pour toi,\nQue pour toi j\'ai donné ma vie?\nTon coeur est-il l\'hôtellerie\nSans place, même pour ton Roi?\nSouvent, année après année,\nChez toi j\'ai frappé, mais en vain.\nVoici le soir de la journée,\nNe veux-tu pas m\'ouvrir enfin?\n\n\n3.\nN\'auras-tu pas besoin de moi,\nBientôt, dans la nuit éternelle?\nDès aujourd\'hui, viens sous mon aile,\nJe serai tout, oui, tout pour toi.\nLe temps rapidement t\'emporte;\nPourquoi renvoyer à demain?\nTrop tard, un jour, devant ma porte,\nTu frapperas peut-être, en vain…\n\n\n4.\nSi tu n\'as pas besoin de Moi,\nÉcoute, obéis sans comprendre:\nJusques à quand devrai-je attendre?\nTon Seigneur a besoin de toi.\nVoici, je me tiens à la porte.\nJe suis ton Maître et ton Sauveur;\nC\'est le bonheur que je t\'apporte;\nNe veux-tu pas m\'ouvrir ton coeur?','<p><strong>1.</strong> </p>\n<p>Pécheur, je voudrais te guérir; </p>\n<p>J&#39;ai vu tes larmes, ta souffrance, </p>\n<p>Mais pour avoir la délivrance, </p>\n<p>Il faut apprendre à m&#39;obéir. </p>\n<p>Voici, je me tiens à la porte, </p>\n<p>Je suis ton Maître et ton Sauveur; </p>\n<p>C&#39;est le bonheur que je t&#39;apporte: </p>\n<p>Ne veux-tu pas m&#39;ouvrir ton coeur? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sais-tu que je suis né pour toi, </p>\n<p>Que pour toi j&#39;ai donné ma vie? </p>\n<p>Ton coeur est-il l&#39;hôtellerie </p>\n<p>Sans place, même pour ton Roi? </p>\n<p>Souvent, année après année, </p>\n<p>Chez toi j&#39;ai frappé, mais en vain. </p>\n<p>Voici le soir de la journée, </p>\n<p>Ne veux-tu pas m&#39;ouvrir enfin? </p>\n<p><br/> <strong>3.</strong></p>\n<p>N&#39;auras-tu pas besoin de moi, </p>\n<p>Bientôt, dans la nuit éternelle? </p>\n<p>Dès aujourd&#39;hui, viens sous mon aile, </p>\n<p>Je serai tout, oui, tout pour toi. </p>\n<p>Le temps rapidement t&#39;emporte; </p>\n<p>Pourquoi renvoyer à demain? </p>\n<p>Trop tard, un jour, devant ma porte, </p>\n<p>Tu frapperas peut-être, en vain… </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si tu n&#39;as pas besoin de Moi, </p>\n<p>Écoute, obéis sans comprendre: </p>\n<p>Jusques à quand devrai-je attendre? </p>\n<p>Ton Seigneur a besoin de toi. </p>\n<p>Voici, je me tiens à la porte. </p>\n<p>Je suis ton Maître et ton Sauveur; </p>\n<p>C&#39;est le bonheur que je t&#39;apporte; </p>\n<p>Ne veux-tu pas m&#39;ouvrir ton coeur? </p>\n','Pecheur-je-voudrais-te-guerir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1389,'de2dc06f-6c88-4cb2-ba67-5eda26f12e8e',0,0,'Ô vous qui n’avez pas la paix',289,'289_CE_fr','1.\nO vous qui n\'avez pas la paix,\nVenez, Jésus la donne,\nPure, profonde et pour jamais,\nVenez, Jésus pardonne.\nQuand Jésus remplit un coeur,\nIl déborde de bonheur,\nEt l\'effroi ne l\'atteint plus:\nGloire, gloire à Jésus!\n\n\n2.\nVous qui tombez à chaque pas,\nVenez, Jésus délivre;\nCelui qui se jette en ses bras\nPeut à toujours le suivre.\nQuand Jésus remplit un coeur,\nIl déborde de bonheur,\nCar il ne chancelle plus:\nGloire, gloire à Jésus!\n\n\n3.\nVous qui tremblez sous la terreur\nQue la mort vous inspire,\nVenez, votre Libérateur\nA détruit son empire!\nAvec Lui nous revivrons,\nAvec Lui nous règnerons,\nEt la mort ne sera plus:\nGloire, gloire à Jésus!','<p><strong>1.</strong> </p>\n<p>O vous qui n&#39;avez pas la paix, </p>\n<p>Venez, Jésus la donne, </p>\n<p>Pure, profonde et pour jamais, </p>\n<p>Venez, Jésus pardonne. </p>\n<p>Quand Jésus remplit un coeur, </p>\n<p>Il déborde de bonheur, </p>\n<p>Et l&#39;effroi ne l&#39;atteint plus: </p>\n<p>Gloire, gloire à Jésus! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vous qui tombez à chaque pas, </p>\n<p>Venez, Jésus délivre; </p>\n<p>Celui qui se jette en ses bras </p>\n<p>Peut à toujours le suivre. </p>\n<p>Quand Jésus remplit un coeur, </p>\n<p>Il déborde de bonheur, </p>\n<p>Car il ne chancelle plus: </p>\n<p>Gloire, gloire à Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vous qui tremblez sous la terreur </p>\n<p>Que la mort vous inspire, </p>\n<p>Venez, votre Libérateur </p>\n<p>A détruit son empire! </p>\n<p>Avec Lui nous revivrons, </p>\n<p>Avec Lui nous règnerons, </p>\n<p>Et la mort ne sera plus: </p>\n<p>Gloire, gloire à Jésus! </p>\n','O-vous-qui-n\'avez-pas-la-paix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1390,'dcf0108d-07d8-4ad1-93a5-c308a93f821c',0,0,'Entends-tu? Jésus t\'appelle',290,'290_CE_fr','1.\nEntends-tu? Jésus t\'appelle;\nViens, ô pécheur, Il t\'attend…\nA cette voix si fidèle\nTu résista trop souvent.\n\nRefrain\nLaisse entrer le Roi de gloire;\nOuvre ton coeur à Jésus!\nLaisse entrer le Roi de gloire,\nHâte-toi, ne tarde plus.\n\n\n2.\nPour le péché, pour le monde,\nTu trouves place en ton coeur…\nPoint pour le Sauveur du monde,\nRien pour l\'Homme de douleur!\n\n\n3.\nJésus frappe, Il frappe encore;\nOuvre à ton Libérateur,\nEt pour toi luira l\'aurore\nDu véritable bonheur.\n\n\n4.\nAujourd\'hui, c\'est jour de grâce;\nNe compte pas sur demain.\nPendant que ton Sauveur passe,\nSaisis sa puissante main!','<p><strong>1.</strong> </p>\n<p>Entends-tu? Jésus t&#39;appelle; </p>\n<p>Viens, ô pécheur, Il t&#39;attend… </p>\n<p>A cette voix si fidèle </p>\n<p>Tu résista trop souvent. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Laisse entrer le Roi de gloire;</strong> </p>\n<p><strong>Ouvre ton coeur à Jésus!</strong> </p>\n<p><strong>Laisse entrer le Roi de gloire,</strong> </p>\n<p><strong>Hâte-toi, ne tarde plus.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour le péché, pour le monde, </p>\n<p>Tu trouves place en ton coeur… </p>\n<p>Point pour le Sauveur du monde, </p>\n<p>Rien pour l&#39;Homme de douleur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus frappe, Il frappe encore; </p>\n<p>Ouvre à ton Libérateur, </p>\n<p>Et pour toi luira l&#39;aurore </p>\n<p>Du véritable bonheur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Aujourd&#39;hui, c&#39;est jour de grâce; </p>\n<p>Ne compte pas sur demain. </p>\n<p>Pendant que ton Sauveur passe, </p>\n<p>Saisis sa puissante main! </p>\n','Entends-tu-Jesus-t\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1391,'868c907f-16a0-4435-b226-d0ed5f98a57b',0,0,'Sans Jésus, je ne peux vivre',151,'151_CE_fr','1.\nSans Jésus, je ne peux vivre,\nJe n\'ose faire un seul pas;\nC\'est lui seul que je veux suivre\nPour lui seul vivre ici-bas!\n\nRefrain\nEt mon coeur n\'a rien à craindre,\nPuisque tu me conduira:\nJe te suivrai sans me plaindre\nEn m\'appuyant sur ton bras.\n\n\n2.\nTon amour et ta promesse\nRemplissent mon coeur de foi;\nSi je ne suis que faiblesse,\nJe puis être fort en toi.\n\n\n3.\nEnlève toute amertume,\nTout orgueil et lâcheté;\nQue l\'amour divin consume\nTout mon être racheté.\n\n\n4.\nDans la nuit la plus profonde,\nQuand Satan veux m\'assaillir,\nQuand l\'orage au désert gronde,\nTon bras vient me secourir.\n\n\n5.\nRevêtu de ta puissance,\nBaptisé par ton Esprit,\nJ\'annonce le délivrance\nA tout coeur brisé, contrit.','<p><strong>1.</strong> </p>\n<p>Sans Jésus, je ne peux vivre, </p>\n<p>Je n&#39;ose faire un seul pas; </p>\n<p>C&#39;est lui seul que je veux suivre </p>\n<p>Pour lui seul vivre ici-bas! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Et mon coeur n&#39;a rien à craindre,</strong> </p>\n<p><strong>Puisque tu me conduira:</strong> </p>\n<p><strong>Je te suivrai sans me plaindre</strong> </p>\n<p><strong>En m&#39;appuyant sur ton bras.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton amour et ta promesse </p>\n<p>Remplissent mon coeur de foi; </p>\n<p>Si je ne suis que faiblesse, </p>\n<p>Je puis être fort en toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Enlève toute amertume, </p>\n<p>Tout orgueil et lâcheté; </p>\n<p>Que l&#39;amour divin consume </p>\n<p>Tout mon être racheté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dans la nuit la plus profonde, </p>\n<p>Quand Satan veux m&#39;assaillir, </p>\n<p>Quand l&#39;orage au désert gronde, </p>\n<p>Ton bras vient me secourir. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Revêtu de ta puissance, </p>\n<p>Baptisé par ton Esprit, </p>\n<p>J&#39;annonce le délivrance </p>\n<p>A tout coeur brisé, contrit. </p>\n','Sans-Jesus-je-ne-peux-vivre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1392,'0489e501-bfb7-4d9e-aed7-07e809db100a',0,0,'La croix que Dieu me donne',158,'158_CE_fr','1.\nLa croix que Dieu me donne\nA porter ici-bas,\nEst jointe à la couronne\nQui ne flétrit pas.\nCelui qui me l\'impose\nSe nomme mon Sauveur;\nSur lui je me repose;\nIl est mon défenseur.\n\n\n2.\nLe premier, sur lui-même\nIl a charger la croix;\nAprès Lui, puisqu\'il m\'aime,\nDois-je en craindre le poids?\nJésus, en qui j\'espère\nEt qui la prit sur Lui,\nMe la rendra légère;\nIl est mon sûr appui.\n\n\n3.\nC\'est Lui don\'t la sagesse\nMe trace mon chemin,\nLui qui, dans ma faiblesse,\nMe tend toujours la main.\nC\'est Lui qui renouvelle\nMa force chaque jour;\nJamais ce Dieu fidèle\nN\'a trompé mon amour.\n\n\n4.\nPrends donc, prends sans tristesse,\nO mon âme, ta croix!\nDu Seigneur la sagesse\nEn mesure le poids.\nLa douleur qu\'Il t\'envoie\nBientôt disparaîtra.\nD\'une éternelle joie,\nTon Dieu te comblera.','<p><strong>1.</strong> </p>\n<p>La croix que Dieu me donne </p>\n<p>A porter ici-bas, </p>\n<p>Est jointe à la couronne </p>\n<p>Qui ne flétrit pas. </p>\n<p>Celui qui me l&#39;impose </p>\n<p>Se nomme mon Sauveur; </p>\n<p>Sur lui je me repose; </p>\n<p>Il est mon défenseur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le premier, sur lui-même </p>\n<p>Il a charger la croix; </p>\n<p>Après Lui, puisqu&#39;il m&#39;aime, </p>\n<p>Dois-je en craindre le poids? </p>\n<p>Jésus, en qui j&#39;espère </p>\n<p>Et qui la prit sur Lui, </p>\n<p>Me la rendra légère; </p>\n<p>Il est mon sûr appui. </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est Lui don&#39;t la sagesse </p>\n<p>Me trace mon chemin, </p>\n<p>Lui qui, dans ma faiblesse, </p>\n<p>Me tend toujours la main. </p>\n<p>C&#39;est Lui qui renouvelle </p>\n<p>Ma force chaque jour; </p>\n<p>Jamais ce Dieu fidèle </p>\n<p>N&#39;a trompé mon amour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Prends donc, prends sans tristesse, </p>\n<p>O mon âme, ta croix! </p>\n<p>Du Seigneur la sagesse </p>\n<p>En mesure le poids. </p>\n<p>La douleur qu&#39;Il t&#39;envoie </p>\n<p>Bientôt disparaîtra. </p>\n<p>D&#39;une éternelle joie, </p>\n<p>Ton Dieu te comblera. </p>\n','La-croix-que-Dieu-me-donne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1393,'65ac41aa-8cac-405f-a521-3c364dfb88a8',0,0,'Ah! Donne A Mon Ame',159,'159_CE_fr','1.\nAh!  Donne à mon âme\nPlus de sainteté…\nPlus d\'ardente flamme,\nDe sérénité,\nPlus de confiance\nPour rester debout,\nPlus de patience\nPour supporter tout.\n\n\n2.\nFais que je contemple\nSans cesse l\'Agneau,\nSon vivant exemple,\nSa croix, son tombeau,\nSa grâce fidèle,\nSon immense amour,\nSa gloire éternelle,\nSon prochain retour.\n\n\n3.\nJésus, à mes larmes,\nTu veux compatir;\nDe toutes tes armes,\nViens me revêtir;\nPar plus de prière.\nDe zèle et de foi.,\nQue dans la lumière,\nJe marche avec joie!\n\n\n4.\nDonne, à ton service,\nUn coeur plus joyeux,\nPrompt au sacrifice,\nToujours sous tes yeux,\nQui chante et qui tremble,\nHumble en sa ferveur,\nUn coeur qui ressemble\nAu tien, mon Sauveur.','<p><strong>1.</strong> </p>\n<p>Ah!  Donne à mon âme </p>\n<p>Plus de sainteté… </p>\n<p>Plus d&#39;ardente flamme, </p>\n<p>De sérénité, </p>\n<p>Plus de confiance </p>\n<p>Pour rester debout, </p>\n<p>Plus de patience </p>\n<p>Pour supporter tout. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fais que je contemple </p>\n<p>Sans cesse l&#39;Agneau, </p>\n<p>Son vivant exemple, </p>\n<p>Sa croix, son tombeau, </p>\n<p>Sa grâce fidèle, </p>\n<p>Son immense amour, </p>\n<p>Sa gloire éternelle, </p>\n<p>Son prochain retour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus, à mes larmes, </p>\n<p>Tu veux compatir; </p>\n<p>De toutes tes armes, </p>\n<p>Viens me revêtir; </p>\n<p>Par plus de prière. </p>\n<p>De zèle et de foi., </p>\n<p>Que dans la lumière, </p>\n<p>Je marche avec joie! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Donne, à ton service, </p>\n<p>Un coeur plus joyeux, </p>\n<p>Prompt au sacrifice, </p>\n<p>Toujours sous tes yeux, </p>\n<p>Qui chante et qui tremble, </p>\n<p>Humble en sa ferveur, </p>\n<p>Un coeur qui ressemble </p>\n<p>Au tien, mon Sauveur. </p>\n','Ah!-Donne-A-Mon-Ame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1394,'5045aeb2-2fd8-40d2-83ce-e2a93fccb316',0,0,'Lutte quand l’Esprit de grâce',166,'166_CE_fr','1.\nLutte quand l\'Esprit de grâce\nT\'attire et te convertit;\nQue ton coeur se débarrasse\nDe tout secret interdit!\nNe reste pas en chemin!\nCoeur qui doute n\'obtient rien:\nLutte bien!  (bis)\n\n\n2.\nLutte pour brûler de zèle,\nPour que, le premier amour\nT\'arrache au monde infidèle,\nT\'en détache sans retour.\nNe t\'accorde aucun répit;\nPersévère jour et nuit:\nLutte bien!  (bis)\n\n\n3.\nLorsque la perle est gagnée,\nNe crois pas que, désormais,\nLa lutte soit terminée,\nLe mal détruit pour jamais.\nDéfie-toi de ton coeur;\nNe regarde qu\'au Sauveur!\nVeille bien!  (bis)\n\n\n4.\nUn coeur fidèle et sincère\nAime Christ, hait le péché,\nPleure et combat sur la terre,\nA soif de l\'éternité.\nSoldat de Christ, lutte encore!\nDans le ciel est ton trésor:\nLutte bien!  (bis)','<p><strong>1.</strong> </p>\n<p>Lutte quand l&#39;Esprit de grâce </p>\n<p>T&#39;attire et te convertit; </p>\n<p>Que ton coeur se débarrasse </p>\n<p>De tout secret interdit! </p>\n<p>Ne reste pas en chemin! </p>\n<p>Coeur qui doute n&#39;obtient rien: </p>\n<p>Lutte bien!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lutte pour brûler de zèle, </p>\n<p>Pour que, le premier amour </p>\n<p>T&#39;arrache au monde infidèle, </p>\n<p>T&#39;en détache sans retour. </p>\n<p>Ne t&#39;accorde aucun répit; </p>\n<p>Persévère jour et nuit: </p>\n<p>Lutte bien!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lorsque la perle est gagnée, </p>\n<p>Ne crois pas que, désormais, </p>\n<p>La lutte soit terminée, </p>\n<p>Le mal détruit pour jamais. </p>\n<p>Défie-toi de ton coeur; </p>\n<p>Ne regarde qu&#39;au Sauveur! </p>\n<p>Veille bien!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Un coeur fidèle et sincère </p>\n<p>Aime Christ, hait le péché, </p>\n<p>Pleure et combat sur la terre, </p>\n<p>A soif de l&#39;éternité. </p>\n<p>Soldat de Christ, lutte encore! </p>\n<p>Dans le ciel est ton trésor: </p>\n<p>Lutte bien!  (bis) </p>\n','Lutte-quand-l\'Esprit-de-grace',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1395,'11902eea-6eab-4fd7-ac5e-64eb72d1239b',0,0,'Comme un cerf altéré brame',172,'172_CE_fr','1.\nComme un cerf altéré brame\nAprès le courant des eaux,\nAinsi soupire mon âme,\nSeigneur, après tes ruisseaux,\nElle a soif du Dieu vivant,\nEt s\'écrie en le suivant:\nO mon Dieu, quand donc sera-ce\nQue mes yeux verront ta face?\n\n\n2.\nPour pain je n\'ai que mes larmes,\nEt nuit et jour en tout lieu,\nLorsqu\'en mes dures alarmes\nOn me dit: Que fait ton Dieu?\nJe regrette la saison\nOù j\'allais en ta maison\nChantant avec les fidèles\nTes louanges immortelles.\n\n\n3.\nMais quel chagrin te dévore?\nMon âme rassure-toi:\nEspère en Dieu, car encore\nIl sera loué de moi.\nC\'est son regard seulement\nQui guérira mon tourment:\nMon Dieu, je sens que mon âme\nD\'un ardent désir de pâme.\n\n\n4.\nTous les flots de ta colère\nSeigneur, sur moi, ont passé;\nMais par ta grâce, j\'espère\nQu\'enfin l\'orage a cessé.\nLe jour tu me conduiras,\nEt la nuit tu me feras\nChanter, d\'une âme ravie,\nTon saint nom, Dieu de ma vie.\n\n\n5.\nMais quel chagrin te dévore?\nMon âme, rassure-toi:\nEspère en Dieu, car encore\nIl sera loué de moi.\nUn regard dans sa faveur,\nMe dit qu\'Il est mon Sauveur;\nEt c\'est aussi Lui, mon âme,\nQu\'en tous mes maux je réclame.','<p><strong>1.</strong> </p>\n<p>Comme un cerf altéré brame </p>\n<p>Après le courant des eaux, </p>\n<p>Ainsi soupire mon âme, </p>\n<p>Seigneur, après tes ruisseaux, </p>\n<p>Elle a soif du Dieu vivant, </p>\n<p>Et s&#39;écrie en le suivant: </p>\n<p>O mon Dieu, quand donc sera-ce </p>\n<p>Que mes yeux verront ta face? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour pain je n&#39;ai que mes larmes, </p>\n<p>Et nuit et jour en tout lieu, </p>\n<p>Lorsqu&#39;en mes dures alarmes </p>\n<p>On me dit: Que fait ton Dieu? </p>\n<p>Je regrette la saison </p>\n<p>Où j&#39;allais en ta maison </p>\n<p>Chantant avec les fidèles </p>\n<p>Tes louanges immortelles. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais quel chagrin te dévore? </p>\n<p>Mon âme rassure-toi: </p>\n<p>Espère en Dieu, car encore </p>\n<p>Il sera loué de moi. </p>\n<p>C&#39;est son regard seulement </p>\n<p>Qui guérira mon tourment: </p>\n<p>Mon Dieu, je sens que mon âme </p>\n<p>D&#39;un ardent désir de pâme. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tous les flots de ta colère </p>\n<p>Seigneur, sur moi, ont passé; </p>\n<p>Mais par ta grâce, j&#39;espère </p>\n<p>Qu&#39;enfin l&#39;orage a cessé. </p>\n<p>Le jour tu me conduiras, </p>\n<p>Et la nuit tu me feras </p>\n<p>Chanter, d&#39;une âme ravie, </p>\n<p>Ton saint nom, Dieu de ma vie. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mais quel chagrin te dévore? </p>\n<p>Mon âme, rassure-toi: </p>\n<p>Espère en Dieu, car encore </p>\n<p>Il sera loué de moi. </p>\n<p>Un regard dans sa faveur, </p>\n<p>Me dit qu&#39;Il est mon Sauveur; </p>\n<p>Et c&#39;est aussi Lui, mon âme, </p>\n<p>Qu&#39;en tous mes maux je réclame. </p>\n','Comme-un-cerf-altere-brame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1396,'5f2d4820-d9bc-4f9c-91f9-05821d264178',0,0,'J’ai soif de ta présence',195,'195_CE_fr','1.\nJ\'ai soif de ta présence,\nDivin chef de ma foi;\nDans ma faiblesse immense\nQue ferais-je sans Toi?\n\nRefrain\nChaque jour, à chaque heure,\nOh! J\'ai besoin de toi;\nViens, Jésus, et demeure\nAuprès de moi.\n\n\n2.\nPendant les jours d\'orage,\nD\'obscurité, d\'effroi,\nQuand faiblit mon courage,\nQue ferais-je sans Toi?\n\n\n3.\nO Jésus, ta présence,\nC\'est la vie et la paix;\nLa paix dans la souffrance,\nEt la vie à jamais.','<p><strong>1.</strong> </p>\n<p>J&#39;ai soif de ta présence, </p>\n<p>Divin chef de ma foi; </p>\n<p>Dans ma faiblesse immense </p>\n<p>Que ferais-je sans Toi? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chaque jour, à chaque heure,</strong> </p>\n<p><strong>Oh! J&#39;ai besoin de toi;</strong> </p>\n<p><strong>Viens, Jésus, et demeure</strong> </p>\n<p><strong>Auprès de moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pendant les jours d&#39;orage, </p>\n<p>D&#39;obscurité, d&#39;effroi, </p>\n<p>Quand faiblit mon courage, </p>\n<p>Que ferais-je sans Toi? </p>\n<p><br/> <strong>3.</strong></p>\n<p>O Jésus, ta présence, </p>\n<p>C&#39;est la vie et la paix; </p>\n<p>La paix dans la souffrance, </p>\n<p>Et la vie à jamais. </p>\n','J\'ai-soif-de-ta-presence',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1397,'c8d71968-155a-4b40-9a52-51369acb8479',0,0,'Toi qui Dispose',200,'200_CE_fr','1.\nToi qui dispose de toutes choses\nEt nous les donnes chaque jour,\nReçois, ô Père, notre prière\nDe reconnaissance et d\'amour.\n\n\n2.\nLe don suprême, que ta main sème,\nC\'est notre pardon, c\'est ta paix,\nEt ta clémence, trésor immense,\nEst le plus grand de tes bienfaits.\n\n\n3.\nQui par ta grâce, l\'instant qui passe\nServe à nous rapprocher de Toi!\nEt qu\'à chaque heure, vers ta demeure\nNos coeurs s\'élèvent par la foi!','<p><strong>1.</strong> </p>\n<p>Toi qui dispose de toutes choses </p>\n<p>Et nous les donnes chaque jour, </p>\n<p>Reçois, ô Père, notre prière </p>\n<p>De reconnaissance et d&#39;amour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le don suprême, que ta main sème, </p>\n<p>C&#39;est notre pardon, c&#39;est ta paix, </p>\n<p>Et ta clémence, trésor immense, </p>\n<p>Est le plus grand de tes bienfaits. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui par ta grâce, l&#39;instant qui passe </p>\n<p>Serve à nous rapprocher de Toi! </p>\n<p>Et qu&#39;à chaque heure, vers ta demeure </p>\n<p>Nos coeurs s&#39;élèvent par la foi! </p>\n','Toi-qui-Dispose',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1398,'7a3421cf-3ad4-40ae-998e-0ea689653bb4',0,0,'Parle, Parle, Seigneur',201,'201_CE_fr','1.\nParle, parle, Seigneur,\nTon serviteur écoute:\nJe dis ton serviteur,\nCar enfin je le suis;\nJe le suis, je veux l\'être,\nEt marcher dans la route,\nEt les jours et les nuits.\nJe le suis, je veux l\'être,\nEt marcher dans la route,\nEt les jours et les nuits.\n\n\n2.\nRemplis-moi d\'un esprit\nQui me fasse comprendre\nCe qu\'ordonnent de moi\nTes saintes volontés,\nEt réduis mes désirs\nAu seul désir d\'entendre\nTes hautes vérités.\nEt réduis mes désirs\nAu désir d\'entendre\nTes hautes vérités.\n\n\n3.\nParle, parle, Seigneur,\nTon serviteur fidèle\nPour écouter ta voix\nRéunit tous ses sens,\nEt trouve les douceurs\nDe la vie éternelle\nEn tes divins accents.\nEt trouve les douceurs\nDe la vie éternelle\nEn tes divins accents.\n\n\n4.\nParle, parle, Seigneur,\nMon âme inquiétée;\nParle, pour la conduire\nA quelque amendement;\nParle, afin que ta gloire,\nAinsi plus exaltée,\nCroisse éternellement!\nParle, afin que ta gloire,\nAinsi plus exaltée,\nCroisse éternellement!','<p><strong>1.</strong> </p>\n<p>Parle, parle, Seigneur, </p>\n<p>Ton serviteur écoute: </p>\n<p>Je dis ton serviteur, </p>\n<p>Car enfin je le suis; </p>\n<p>Je le suis, je veux l&#39;être, </p>\n<p>Et marcher dans la route, </p>\n<p>Et les jours et les nuits. </p>\n<p>Je le suis, je veux l&#39;être, </p>\n<p>Et marcher dans la route, </p>\n<p>Et les jours et les nuits. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Remplis-moi d&#39;un esprit </p>\n<p>Qui me fasse comprendre </p>\n<p>Ce qu&#39;ordonnent de moi </p>\n<p>Tes saintes volontés, </p>\n<p>Et réduis mes désirs </p>\n<p>Au seul désir d&#39;entendre </p>\n<p>Tes hautes vérités. </p>\n<p>Et réduis mes désirs </p>\n<p>Au désir d&#39;entendre </p>\n<p>Tes hautes vérités. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Parle, parle, Seigneur, </p>\n<p>Ton serviteur fidèle </p>\n<p>Pour écouter ta voix </p>\n<p>Réunit tous ses sens, </p>\n<p>Et trouve les douceurs </p>\n<p>De la vie éternelle </p>\n<p>En tes divins accents. </p>\n<p>Et trouve les douceurs </p>\n<p>De la vie éternelle </p>\n<p>En tes divins accents. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Parle, parle, Seigneur, </p>\n<p>Mon âme inquiétée; </p>\n<p>Parle, pour la conduire </p>\n<p>A quelque amendement; </p>\n<p>Parle, afin que ta gloire, </p>\n<p>Ainsi plus exaltée, </p>\n<p>Croisse éternellement! </p>\n<p>Parle, afin que ta gloire, </p>\n<p>Ainsi plus exaltée, </p>\n<p>Croisse éternellement! </p>\n','Parle-Parle-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1399,'8ff0d4f8-1521-46ee-8916-83ca110de081',0,0,'Plus le mal est pressant',218,'218_CE_fr','1.\nPlus le mal est pressant,\nPlus ma misère est grande,\nPlus l\'abîme est profond\nEt béant sous mes pas,\nPlus le péril extrême\nUn prompt secours demande,\nPlus je me réfugie,\nO Jésus, dans tes bras!\n\n\n2.\nParmi tous les dangers,\nC\'est Toi qui me rassures,\nContre tous les assauts,\nC\'est Toi mon bouclier;\nC\'est Toi, si je faiblis,\nQui guérit mes blessures;\nPour pouvoir tout, sur Toi,\nJe n\'ai qu\'à m\'appuyer.\n\n\n3.\nTu m\'as associé,\nJésus, à ta victoire;\nMets ta force en mon bras,\nMets ta flamme en mon coeur!\nOui, viens par mon triomphe,\nAjouter à ta gloire,\nCombattre par mes mains\nEt me rendre vainqueur.\n\n\n4.\nTu me donnes toujours\nSelon ma confiance,\nQuand j\'ai tout demandé,\nN\'ai-je pas tout reçu?\nAvec Toi, tout triomphe\nEst assuré d\'avance:\nQuand on est sur de vaincre,\nOn a déjà vaincu.','<p><strong>1.</strong> </p>\n<p>Plus le mal est pressant, </p>\n<p>Plus ma misère est grande, </p>\n<p>Plus l&#39;abîme est profond </p>\n<p>Et béant sous mes pas, </p>\n<p>Plus le péril extrême </p>\n<p>Un prompt secours demande, </p>\n<p>Plus je me réfugie, </p>\n<p>O Jésus, dans tes bras! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Parmi tous les dangers, </p>\n<p>C&#39;est Toi qui me rassures, </p>\n<p>Contre tous les assauts, </p>\n<p>C&#39;est Toi mon bouclier; </p>\n<p>C&#39;est Toi, si je faiblis, </p>\n<p>Qui guérit mes blessures; </p>\n<p>Pour pouvoir tout, sur Toi, </p>\n<p>Je n&#39;ai qu&#39;à m&#39;appuyer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu m&#39;as associé, </p>\n<p>Jésus, à ta victoire; </p>\n<p>Mets ta force en mon bras, </p>\n<p>Mets ta flamme en mon coeur! </p>\n<p>Oui, viens par mon triomphe, </p>\n<p>Ajouter à ta gloire, </p>\n<p>Combattre par mes mains </p>\n<p>Et me rendre vainqueur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tu me donnes toujours </p>\n<p>Selon ma confiance, </p>\n<p>Quand j&#39;ai tout demandé, </p>\n<p>N&#39;ai-je pas tout reçu? </p>\n<p>Avec Toi, tout triomphe </p>\n<p>Est assuré d&#39;avance: </p>\n<p>Quand on est sur de vaincre, </p>\n<p>On a déjà vaincu. </p>\n','Plus-le-mal-est-pressant',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1400,'11ad0b62-e144-4167-b22b-44f6896d5d13',0,0,'Annonçons partout le dernier Message',233,'233_CE_fr','1.\nAnnonçons partout le dernier Message\nDu Seigneur Jésus revenant des cieux;\nDans le mon entier, rendons témoignage\nAu Roi saint et glorieux\n\nRefrain\nAnnonçons partout la bonne nouvelle:\nJésus réunit tous ses rachetés;\nIl vient leur donné la vie éternelle.\nOh!  Publions ses bontés.\n\n\n2.\nAnnonçons partout le dernier Message:\nLe Seigneur Jésus vient sécher nos pleurs\nBriser les liens de notre esclavage,\nMettre un terme à nos douleurs.\n\n\n3.\nAnnonçons partout le dernier Message:\nBientôt nous verrons la sainte Cité.\nGloire à notre Dieu!  Chrétiens, bon courage!\nCar voici l\'éternité','<p><strong>1.</strong> </p>\n<p>Annonçons partout le dernier Message </p>\n<p>Du Seigneur Jésus revenant des cieux; </p>\n<p>Dans le mon entier, rendons témoignage </p>\n<p>Au Roi saint et glorieux </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Annonçons partout la bonne nouvelle:</strong> </p>\n<p><strong>Jésus réunit tous ses rachetés;</strong> </p>\n<p><strong>Il vient leur donné la vie éternelle.</strong> </p>\n<p><strong>Oh!  Publions ses bontés.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Annonçons partout le dernier Message: </p>\n<p>Le Seigneur Jésus vient sécher nos pleurs </p>\n<p>Briser les liens de notre esclavage, </p>\n<p>Mettre un terme à nos douleurs. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Annonçons partout le dernier Message: </p>\n<p>Bientôt nous verrons la sainte Cité. </p>\n<p>Gloire à notre Dieu!  Chrétiens, bon courage! </p>\n<p>Car voici l&#39;éternité </p>\n','Annoncons-partout-le-dernier-Message',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1401,'c0f694e1-ceb8-412c-8fdd-3b59f93a3da5',0,0,'Sur mon chemin l’éternité s’avance',234,'234_CE_fr','1.\nSur mon chemin l\'éternité s\'avance;\nPour moi le temps fait un pas aujourd\'hui.\nMais le Seigneur m\'abandonné l\'espérance;\nElle subsiste et repose sur Lui.\n\n\n2.\nCombien de fois, et des ans des heures\nMes yeux lassés ont mesuré le cours!\n« O Dieu, disais-je, ouvre-moi ces demeures\nOù dans ton sein je vivrai pour toujours. »\n\n\n3.\nMais, c\'est à toi de régler ma carrière;\nJe te désire, et ne murmure pas;\nCar, je le sais, à mon heure dernière\nJe trouverai mon refuge en tes bras.\n\n\n4.\nAux jours mauvais, si mon âme chancelle,\nSi mes genoux fléchissent en chemin,\nViens de ma foi ranimer l\'étincelle;\nTends-moi du ciel ta secourable main.','<p><strong>1.</strong> </p>\n<p>Sur mon chemin l&#39;éternité s&#39;avance; </p>\n<p>Pour moi le temps fait un pas aujourd&#39;hui. </p>\n<p>Mais le Seigneur m&#39;abandonné l&#39;espérance; </p>\n<p>Elle subsiste et repose sur Lui. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Combien de fois, et des ans des heures </p>\n<p>Mes yeux lassés ont mesuré le cours! </p>\n<p>« O Dieu, disais-je, ouvre-moi ces demeures </p>\n<p>Où dans ton sein je vivrai pour toujours. » </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais, c&#39;est à toi de régler ma carrière; </p>\n<p>Je te désire, et ne murmure pas; </p>\n<p>Car, je le sais, à mon heure dernière </p>\n<p>Je trouverai mon refuge en tes bras. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Aux jours mauvais, si mon âme chancelle, </p>\n<p>Si mes genoux fléchissent en chemin, </p>\n<p>Viens de ma foi ranimer l&#39;étincelle; </p>\n<p>Tends-moi du ciel ta secourable main. </p>\n','Sur-mon-chemin-l\'eternite-s\'avance',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1402,'5a06eac2-c833-4b2d-93f2-3986cd70bbd0',0,0,'Un jour je cesserai mes chants',255,'255_CE_fr','1.\nUn jour je cesserai mes chants,\nMa voix sera silencieuse…\nMais avec les saints triomphants,\nPsalmodiera mon âme heureuse.\n\nRefrain\nJe Le verrai, Lui, tel qu\'Il est,\nJésus, le Rédempteur parfait!  (bis 2 lignes)\n\n\n2.\nUn jour, je devrai sans retour\nQuitter ma terresse demeure…\nMais l\'entrée au divin séjour\nMe sera donnée à cette heure.\n\n\n3.\nUn jour l\'obscurité viendra,\nLa mort froide et silencieuse…\nMais là-haut Jésus me prendra\nDans sa lumière radieuse.\n\n\n4.\nCe jour viendra, jour glorieux,\nC\'est là ma joyeuse assurance…\nSeigneur, tiens-moi prêt pour les cieux,\nJe mets en toi mon espérance.','<p><strong>1.</strong> </p>\n<p>Un jour je cesserai mes chants, </p>\n<p>Ma voix sera silencieuse… </p>\n<p>Mais avec les saints triomphants, </p>\n<p>Psalmodiera mon âme heureuse. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je Le verrai, Lui, tel qu&#39;Il est,</strong> </p>\n<p><strong>Jésus, le Rédempteur parfait!  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Un jour, je devrai sans retour </p>\n<p>Quitter ma terresse demeure… </p>\n<p>Mais l&#39;entrée au divin séjour </p>\n<p>Me sera donnée à cette heure. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Un jour l&#39;obscurité viendra, </p>\n<p>La mort froide et silencieuse… </p>\n<p>Mais là-haut Jésus me prendra </p>\n<p>Dans sa lumière radieuse. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ce jour viendra, jour glorieux, </p>\n<p>C&#39;est là ma joyeuse assurance… </p>\n<p>Seigneur, tiens-moi prêt pour les cieux, </p>\n<p>Je mets en toi mon espérance. </p>\n','Un-jour-je-cesserai-mes-chants',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1403,'f758bda8-3fdc-47d1-b216-45dbc9b1999f',0,0,'Quel qu’un frappe à votre porte',284,'284_CE_fr','1.\nQuelqu\'un frappe à votre porte:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nC\'est le ciel qu\'il vous apporte:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nChez vous Il veut prendre place;\nOuvrez avant qu\'Il se lasse!\nLe temps presse, le temps passe;\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\n\n\n2.\nOuvrez! C\'est lui qui pardonne:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nAcceptez tous ceux qu\'il donne:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nSa main va briser vos chaînes:\nSes promesses sont certaines;\nBientôt finiront vos peines:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\n\n\n3.\nSeul Il ôte les souillures:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nSeul Il guérit les blessures:\nOuvrez-Lui!  Ouvrez-Lui aujourd\'hui!\nQuand la mort va vous surprendre,\nSeul Il pourra vous défendre,\nEt sous son aile vous prendre:\nOuvrez-lui!  Ouvrez-Lui aujourd\'hui!','<p><strong>1.</strong> </p>\n<p>Quelqu&#39;un frappe à votre porte: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>C&#39;est le ciel qu&#39;il vous apporte: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>Chez vous Il veut prendre place; </p>\n<p>Ouvrez avant qu&#39;Il se lasse! </p>\n<p>Le temps presse, le temps passe; </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ouvrez! C&#39;est lui qui pardonne: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>Acceptez tous ceux qu&#39;il donne: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>Sa main va briser vos chaînes: </p>\n<p>Ses promesses sont certaines; </p>\n<p>Bientôt finiront vos peines: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seul Il ôte les souillures: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>Seul Il guérit les blessures: </p>\n<p>Ouvrez-Lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n<p>Quand la mort va vous surprendre, </p>\n<p>Seul Il pourra vous défendre, </p>\n<p>Et sous son aile vous prendre: </p>\n<p>Ouvrez-lui!  Ouvrez-Lui aujourd&#39;hui! </p>\n','Quel-qu\'un-frappe-a-votre-porte',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1404,'4da51748-f636-41c3-9c33-0d0a61c435b5',0,0,'Viens au Père qui t’appelle',287,'287_CE_fr','1.\nViens au Père qui t\'appelle,\nOh! Reviens à la maison!\nVoici, l\'heure est solennelle,\nOù Dieu t\'offre son pardon.\n\nRefrain\nAujourd\'hui, viens à Lui!\nAh!  Pourquoi tarderais-tu?\nAujourd\'hui, viens à Lui,\nViens, reçois son grand salut!\n\n\n2.\nTrop longtemps dans la misère,\nTu vécus sans vrai bonheur!\nSe peut-il que tu préfères,\nFuir encore loin du Sauveur?\n\n\n3.\nAh! Ton âme est angoissée!\nEn vain tu chercheras la paix;\nPar sa croix qui fut dressée,\nChrist la donne et pour jamais.\n\n\n4.\nA Jésus dis ta souffrance,\nParle-Lui de ton péché,\nMets en Lui ton espérance,\nCar en Lui Dieu t\'a cherché.','<p><strong>1.</strong> </p>\n<p>Viens au Père qui t&#39;appelle, </p>\n<p>Oh! Reviens à la maison! </p>\n<p>Voici, l&#39;heure est solennelle, </p>\n<p>Où Dieu t&#39;offre son pardon. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Aujourd&#39;hui, viens à Lui!</strong> </p>\n<p><strong>Ah!  Pourquoi tarderais-tu?</strong> </p>\n<p><strong>Aujourd&#39;hui, viens à Lui,</strong> </p>\n<p><strong>Viens, reçois son grand salut!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Trop longtemps dans la misère, </p>\n<p>Tu vécus sans vrai bonheur! </p>\n<p>Se peut-il que tu préfères, </p>\n<p>Fuir encore loin du Sauveur? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ah! Ton âme est angoissée! </p>\n<p>En vain tu chercheras la paix; </p>\n<p>Par sa croix qui fut dressée, </p>\n<p>Christ la donne et pour jamais. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A Jésus dis ta souffrance, </p>\n<p>Parle-Lui de ton péché, </p>\n<p>Mets en Lui ton espérance, </p>\n<p>Car en Lui Dieu t&#39;a cherché. </p>\n','Viens-au-Pere-qui-t\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1405,'774ff0f4-143d-4827-b264-8942dced448f',0,0,'Le Ciel Etait Voilé',306,'306_CE_fr','1.\nLe ciel était voilé,\nLa route était obscure;\nVoyageur désolé,\nJ\'errais à l\'aventure.\nChaque arbre du chemin\nÉtait une menace,\nEt je cherchais en vain\nLa porte de la grâce.  (bis)\n\n\n2.\nEnfin, las et transi,\nJe tombai sur la route\nEn disant: « C\'est ici\nQue je mourrai sans doute! »\nQuand un rayon des cieux,\nPour moi perçant l\'espace,\nVint montrer à mes yeux\nLa porte de la grâce.  (bis)\n\n\n3.\nMais je doutais encore:\nSe peut-il, ô merveille,\nQue seule, quand tout dort,\nLa grâce toujours veille?\n« Pitié! Je vais mourir! »\nMurmurai-je à voix basse.\nEt je la vis s\'ouvrir,\nLa porte de la grâce!  (bis)\n\n\n4.\nEt je vis sur le seuil,\nDebout dans la lumière,\nJésus! – Quel doux accueil\nIl fit à ma misère!\n« Je t\'attendais; pourquoi, »\nDit-Il, « Pauvre âme lasse,\nViens-tu si tard? C\'est Moi,\nC\'est Moi qui suis la grâce! »  (bis)','<p><strong>1.</strong> </p>\n<p>Le ciel était voilé, </p>\n<p>La route était obscure; </p>\n<p>Voyageur désolé, </p>\n<p>J&#39;errais à l&#39;aventure. </p>\n<p>Chaque arbre du chemin </p>\n<p>Était une menace, </p>\n<p>Et je cherchais en vain </p>\n<p>La porte de la grâce.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Enfin, las et transi, </p>\n<p>Je tombai sur la route </p>\n<p>En disant: « C&#39;est ici </p>\n<p>Que je mourrai sans doute! » </p>\n<p>Quand un rayon des cieux, </p>\n<p>Pour moi perçant l&#39;espace, </p>\n<p>Vint montrer à mes yeux </p>\n<p>La porte de la grâce.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais je doutais encore: </p>\n<p>Se peut-il, ô merveille, </p>\n<p>Que seule, quand tout dort, </p>\n<p>La grâce toujours veille? </p>\n<p>« Pitié! Je vais mourir! » </p>\n<p>Murmurai-je à voix basse. </p>\n<p>Et je la vis s&#39;ouvrir, </p>\n<p>La porte de la grâce!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et je vis sur le seuil, </p>\n<p>Debout dans la lumière, </p>\n<p>Jésus! – Quel doux accueil </p>\n<p>Il fit à ma misère! </p>\n<p>« Je t&#39;attendais; pourquoi, » </p>\n<p>Dit-Il, « Pauvre âme lasse, </p>\n<p>Viens-tu si tard? C&#39;est Moi, </p>\n<p>C&#39;est Moi qui suis la grâce! »  (bis) </p>\n','Le-Ciel-Etait-Voile',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1406,'946b74ae-4fc0-4a59-86b8-837d02c5abe9',0,0,'Il Est Là-haut',319,'319_CE_fr','1.\nIl est là-haut!\nNous nous courbons, ô Père!\nCar mieux que nous\nTu sais ce qu\'il nous faut.\nLe pèlerin\nA fini sa carrière,\nIl n\'est point là\nSous le marbre ou la pierre:\nIl est là-haut! Il est là-haut!\n\n\n2.\nIl est là-haut!\nCar, soldat sous les armes,\nIl a vaincu\nLors du dernier assaut!\nIl est heureux,\nLoin des maux, des alarmes;\nEt nous chantons,\nAu travers de nos larmes:\nIl est là-haut!  Il est là-haut!\n\n\n3.\nIl est là-haut!\nDans la sainte lumière,\nIl resplendit,\nSans ombre, sans défaut;\nIl nous attend\nDans les parvis du Père.\nOh! Quel revoir,\nLorsqu\'on dira sur terre:\nIls sont là-haut!  Ils sont là-haut!','<p><strong>1.</strong> </p>\n<p>Il est là-haut! </p>\n<p>Nous nous courbons, ô Père! </p>\n<p>Car mieux que nous </p>\n<p>Tu sais ce qu&#39;il nous faut. </p>\n<p>Le pèlerin </p>\n<p>A fini sa carrière, </p>\n<p>Il n&#39;est point là </p>\n<p>Sous le marbre ou la pierre: </p>\n<p>Il est là-haut! Il est là-haut! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il est là-haut! </p>\n<p>Car, soldat sous les armes, </p>\n<p>Il a vaincu </p>\n<p>Lors du dernier assaut! </p>\n<p>Il est heureux, </p>\n<p>Loin des maux, des alarmes; </p>\n<p>Et nous chantons, </p>\n<p>Au travers de nos larmes: </p>\n<p>Il est là-haut!  Il est là-haut! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il est là-haut! </p>\n<p>Dans la sainte lumière, </p>\n<p>Il resplendit, </p>\n<p>Sans ombre, sans défaut; </p>\n<p>Il nous attend </p>\n<p>Dans les parvis du Père. </p>\n<p>Oh! Quel revoir, </p>\n<p>Lorsqu&#39;on dira sur terre: </p>\n<p>Ils sont là-haut!  Ils sont là-haut! </p>\n','Il-Est-La-haut',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1407,'701bbbaa-05a5-4786-9b48-70a7b78e96f8',0,0,'Une nacelle en silence',129,'129_CE_fr','1.\nUne nacelle en silence\nVogue sur un lac d\'azur;\nTout doucement elle avance,\nSous un ciel tranquille et pur:\nMais soudain le vent s\'élève\nChassant un nuage noir,\nEt les vagues qu\'il soulève\nFont trembler, car c\'est le soir.  (bis 2 lignes)\n\n2.\nGrande est alors la détresse\nDes voyageurs éperdus;\nGrande est aussi leur faiblesse,\nLeur foi ne les soutient plus.\nMais il en est Un qui veille\nSur eux tous, bien qu\'endormi;\nAh!  Faudra-t-il qu\'on l\'éveille?\nN\'est-il plus leur tendre Ami?  (bis 2 lignes)\n\n3.\n«Maître, es-tu donc insensible?\nTu le vois, nous périssons!\nTout miracle t\'est possible,\nSauve-nous, nous t\'en prions!»\nD\'eux aussitôt il s\'approche,\nPuis, il dit au vent: «Tais-toi!»\nEt tendrement leur reproche\nD\'avoir eu si peu de foi.  (bis 2 lignes)\n\n4.\nAinsi souvent, dans la vie,\nL\'orage assombrit nos coeurs,\nBien que pour nous Jésus prie,\nPrêt à calmer nos terreurs.\nComptons mieux sur sa tendresse,\nSon coeur ne saurait changer:\nDe sa brebis en détresse\nIl est toujours le Berger.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Une nacelle en silence </p>\n<p>Vogue sur un lac d&#39;azur; </p>\n<p>Tout doucement elle avance, </p>\n<p>Sous un ciel tranquille et pur: </p>\n<p>Mais soudain le vent s&#39;élève </p>\n<p>Chassant un nuage noir, </p>\n<p>Et les vagues qu&#39;il soulève </p>\n<p>Font trembler, car c&#39;est le soir.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Grande est alors la détresse </p>\n<p>Des voyageurs éperdus; </p>\n<p>Grande est aussi leur faiblesse, </p>\n<p>Leur foi ne les soutient plus. </p>\n<p>Mais il en est Un qui veille </p>\n<p>Sur eux tous, bien qu&#39;endormi; </p>\n<p>Ah!  Faudra-t-il qu&#39;on l&#39;éveille? </p>\n<p>N&#39;est-il plus leur tendre Ami?  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>«Maître, es-tu donc insensible? </p>\n<p>Tu le vois, nous périssons! </p>\n<p>Tout miracle t&#39;est possible, </p>\n<p>Sauve-nous, nous t&#39;en prions!» </p>\n<p>D&#39;eux aussitôt il s&#39;approche, </p>\n<p>Puis, il dit au vent: «Tais-toi!» </p>\n<p>Et tendrement leur reproche </p>\n<p>D&#39;avoir eu si peu de foi.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ainsi souvent, dans la vie, </p>\n<p>L&#39;orage assombrit nos coeurs, </p>\n<p>Bien que pour nous Jésus prie, </p>\n<p>Prêt à calmer nos terreurs. </p>\n<p>Comptons mieux sur sa tendresse, </p>\n<p>Son coeur ne saurait changer: </p>\n<p>De sa brebis en détresse </p>\n<p>Il est toujours le Berger.  (bis 2 lignes) </p>\n','Une-nacelle-en-silence',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1408,'e4d95edc-66ff-4a5e-9baa-9d535a9a9795',0,0,'Jésus,doux, Maître, Règne sur moi',143,'143_CE_fr','1.\nJésus, doux Maître, - Règne sur moi,\nSoumets mon être, - Sois-en le Roi;\nJe suis l\'argile, - Toi, le Potier,\nRends-moi docile, - Ton prisonnier.\n\n\n2.\nJésus, lumière, - Pénètre en moi,\nÉprouve, éclaire – Ma faible foi;\nPlus blanc que neige – Rends-moi, Seigneur,\nEt de tout piège, - Garde mon coeur.\n\n\n3.\nO Jésus, source – De guérison,\nSois dans ma course, - Santé, pardon;\nPar ta puissance, - Protège-moi;\nPar ta présence, - Révèle-toi.\n\n\n4.\nJe m\'abandonne, - Jésus à toi;\nDétruis, pardonne – Tout mal en moi.\nRemplis mon âme – De ton Esprit,\nEt qu\'il m\'enflamme, - Et jour et nuit!','<p><strong>1.</strong> </p>\n<p>Jésus, doux Maître, - Règne sur moi, </p>\n<p>Soumets mon être, - Sois-en le Roi; </p>\n<p>Je suis l&#39;argile, - Toi, le Potier, </p>\n<p>Rends-moi docile, - Ton prisonnier. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, lumière, - Pénètre en moi, </p>\n<p>Éprouve, éclaire – Ma faible foi; </p>\n<p>Plus blanc que neige – Rends-moi, Seigneur, </p>\n<p>Et de tout piège, - Garde mon coeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O Jésus, source – De guérison, </p>\n<p>Sois dans ma course, - Santé, pardon; </p>\n<p>Par ta puissance, - Protège-moi; </p>\n<p>Par ta présence, - Révèle-toi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je m&#39;abandonne, - Jésus à toi; </p>\n<p>Détruis, pardonne – Tout mal en moi. </p>\n<p>Remplis mon âme – De ton Esprit, </p>\n<p>Et qu&#39;il m&#39;enflamme, - Et jour et nuit! </p>\n','Jesusdoux-Maitre-Regne-sur-moi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1409,'673dd214-f2e7-4569-9380-f62a543654c4',0,0,'Bientôt, bientôt nous comprendrons',128,'128_CE_fr','1.\nBientôt, bientôt nous comprendrons\nTous les pourquoi de nos alarmes,\nEt celui de tant de nos larmes,\nBientôt, bientôt nous comprendrons.\n\nRefrain\nEn attendant, de notre Père,\nSaisissons la divine main.\nAujourd\'hui même et puis demain,\nConfions-nous en sa lumière.\nOui, bientôt, nous le bénirons,\nBientôt, bientôt nous comprendrons.\n\n2.\nNous saurons pourquoi notre ciel\nUn jour s\'est voilé d\'un nuage,\nEt fut troublé par un orage\nQui nous parut alors cruel.\n\n3.\nPourquoi tel désir de nos coeurs,\nTelle douce et chère espérance\nSoudain fut changée en souffrance,\nBientôt, nous le saurons, ailleurs.','<p><strong>1.</strong> </p>\n<p>Bientôt, bientôt nous comprendrons </p>\n<p>Tous les pourquoi de nos alarmes, </p>\n<p>Et celui de tant de nos larmes, </p>\n<p>Bientôt, bientôt nous comprendrons. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>En attendant, de notre Père,</strong> </p>\n<p><strong>Saisissons la divine main.</strong> </p>\n<p><strong>Aujourd&#39;hui même et puis demain,</strong> </p>\n<p><strong>Confions-nous en sa lumière.</strong> </p>\n<p><strong>Oui, bientôt, nous le bénirons,</strong> </p>\n<p><strong>Bientôt, bientôt nous comprendrons.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nous saurons pourquoi notre ciel </p>\n<p>Un jour s&#39;est voilé d&#39;un nuage, </p>\n<p>Et fut troublé par un orage </p>\n<p>Qui nous parut alors cruel. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pourquoi tel désir de nos coeurs, </p>\n<p>Telle douce et chère espérance </p>\n<p>Soudain fut changée en souffrance, </p>\n<p>Bientôt, nous le saurons, ailleurs. </p>\n','Bientot-bientot-nous-comprendrons',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1410,'ee3f3d26-32c4-4b9c-b1c1-5f1241c0c8d3',0,0,'Il est un roc séculaire',130,'130_CE_fr','1.\nIl  est un roc séculaire,\nQue Dieu, pour mon coeur lassé,\nComme un abri tutélaire,\nAu sein des flots a placé.\n\nRefrain\nMon rocher, ma forteresse,\nMon asile protecteur,\nMon recours dans la détresse,\nC\'est Jésus, le Rédempteur!\n\n2.\nA mes pieds l\'océan gronde;\nLe vent siffle autour de moi…\nSur Christ, mon rocher, je fonde\nMon espérance et ma foi.\n\n3.\nEn vain l\'ouragan fait rage,\nEt nulle clarté ne luit,\nPaisible au sein de l\'orage,\nJ\'attends l\'aube après la nuit.\n\n4.\nJouet de l\'onde agitée,\nCet abri, l\'as-tu trouvé?\nViens, pauvre âme tourmentée,\nAu rocher qui m\'a sauvé!','<p><strong>1.</strong> </p>\n<p>Il  est un roc séculaire, </p>\n<p>Que Dieu, pour mon coeur lassé, </p>\n<p>Comme un abri tutélaire, </p>\n<p>Au sein des flots a placé. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon rocher, ma forteresse,</strong> </p>\n<p><strong>Mon asile protecteur,</strong> </p>\n<p><strong>Mon recours dans la détresse,</strong> </p>\n<p><strong>C&#39;est Jésus, le Rédempteur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A mes pieds l&#39;océan gronde; </p>\n<p>Le vent siffle autour de moi… </p>\n<p>Sur Christ, mon rocher, je fonde </p>\n<p>Mon espérance et ma foi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En vain l&#39;ouragan fait rage, </p>\n<p>Et nulle clarté ne luit, </p>\n<p>Paisible au sein de l&#39;orage, </p>\n<p>J&#39;attends l&#39;aube après la nuit. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jouet de l&#39;onde agitée, </p>\n<p>Cet abri, l&#39;as-tu trouvé? </p>\n<p>Viens, pauvre âme tourmentée, </p>\n<p>Au rocher qui m&#39;a sauvé! </p>\n','Il-est-un-roc-seculaire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1411,'0b3ff308-2f0b-48c6-88d6-d024926e7045',0,0,'Mon cœur tressaille à ta voix',140,'140_CE_fr','1.\nMon coeur tressaille à ta voix,\nBon Sauveur, conduis mes pas.\nJe suis tes divines lois\nEn m\'appuyant sur ton bras.\n\nRefrain\nGuide, ô Sauveur!\nTous mes pas en ces bas lieux,\nJusqu\'au jour où ta faveur\nEnfin m\'ouvrira les cieux.\n\n2.\nSeul, je ne puis avancer,\nJe vois l\'abîme et j\'ai peur;\nTremblant, mon pied peut glisser\nSous l\'oeil d\'un monde moqueur.\n\n3.\nMon refuge, c\'est toi seul;\nSi, débordant, le mal vient\nCouvrir tout de son linceul,\nÀ toi mon âme appartient.\n\n4.\nA travers la sombre nuit,\nLes dangers, les grandes eaux,\nTon amour seul me conduit\nVers le glorieux repos.','<p><strong>1.</strong> </p>\n<p>Mon coeur tressaille à ta voix, </p>\n<p>Bon Sauveur, conduis mes pas. </p>\n<p>Je suis tes divines lois </p>\n<p>En m&#39;appuyant sur ton bras. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Guide, ô Sauveur!</strong> </p>\n<p><strong>Tous mes pas en ces bas lieux,</strong> </p>\n<p><strong>Jusqu&#39;au jour où ta faveur</strong> </p>\n<p><strong>Enfin m&#39;ouvrira les cieux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seul, je ne puis avancer, </p>\n<p>Je vois l&#39;abîme et j&#39;ai peur; </p>\n<p>Tremblant, mon pied peut glisser </p>\n<p>Sous l&#39;oeil d&#39;un monde moqueur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon refuge, c&#39;est toi seul; </p>\n<p>Si, débordant, le mal vient </p>\n<p>Couvrir tout de son linceul, </p>\n<p>À toi mon âme appartient. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A travers la sombre nuit, </p>\n<p>Les dangers, les grandes eaux, </p>\n<p>Ton amour seul me conduit </p>\n<p>Vers le glorieux repos. </p>\n','Mon-coeur-tressaille-a-ta-voix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1412,'1688f3bc-3575-4cfc-96ed-da887715d4da',0,0,'Humblement Prosterne, Seigneur',160,'160_CE_fr','1.\nHumblement prosterné, Seigneur,\nDevant ta majesté,\nJe viens implorer la faveur\nD\'un coeur purifié.\n\nRefrain\nJe veux un coeur nouveau,\nLe baptême d\'en haut,\nDe feu, de foi, de charité,\nUn coeur régénéré.\n\n2.\nUn coeur patient, humble et joyeux,\nUn coeur semblable au tien,\nUn coeur content, toujours heureux,\nUn coeur aimant le bien.\n\n3.\nUn coeur aimant ta volonté,\nVrai, droit, prêt à servir,\nUn coeur soumit, plein de bonté,\nUn coeur sachant souffrir.','<p><strong>1.</strong> </p>\n<p>Humblement prosterné, Seigneur, </p>\n<p>Devant ta majesté, </p>\n<p>Je viens implorer la faveur </p>\n<p>D&#39;un coeur purifié. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je veux un coeur nouveau,</strong> </p>\n<p><strong>Le baptême d&#39;en haut,</strong> </p>\n<p><strong>De feu, de foi, de charité,</strong> </p>\n<p><strong>Un coeur régénéré.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Un coeur patient, humble et joyeux, </p>\n<p>Un coeur semblable au tien, </p>\n<p>Un coeur content, toujours heureux, </p>\n<p>Un coeur aimant le bien. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Un coeur aimant ta volonté, </p>\n<p>Vrai, droit, prêt à servir, </p>\n<p>Un coeur soumit, plein de bonté, </p>\n<p>Un coeur sachant souffrir. </p>\n','Humblement-Prosterne-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1413,'a009b470-8b08-464a-bf76-e0e89b840417',0,0,'Entre tes mains, j’abandonne',139,'139_CE_fr','1.\nEntre tes mains, j\'abandonne\nTout ce que j\'appelle mien.\nOh!  Que permets à personne,\nSeigneur, d\'en reprendre rien!\n\nRefrain\nOui, prends tout, Seigneur!   (bis)\nEntre tes mains, j\'abandonne\nTout avec bonheur.\n\n2.\nJe n\'ai pas peur de te suivre\nSur le chemin de la croix.\nC\'est pour toi que je veux vivre,\nJe connais, j\'aime ta voix.\n\n3.\nTu connais mieux que moi-même\nTous les besoins de mon  coeur.\nEt, pour mon bonheur suprême,\nTu peux me rendre vainqueur.\n\n4.\nPrends mon corps et prends mon âme;\nQue tout en moi soit à Toi!\nQue par ta divine flamme\nTout mal soit détruit en moi','<p><strong>1.</strong> </p>\n<p>Entre tes mains, j&#39;abandonne </p>\n<p>Tout ce que j&#39;appelle mien. </p>\n<p>Oh!  Que permets à personne, </p>\n<p>Seigneur, d&#39;en reprendre rien! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, prends tout, Seigneur!   (bis)</strong> </p>\n<p><strong>Entre tes mains, j&#39;abandonne</strong> </p>\n<p><strong>Tout avec bonheur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je n&#39;ai pas peur de te suivre </p>\n<p>Sur le chemin de la croix. </p>\n<p>C&#39;est pour toi que je veux vivre, </p>\n<p>Je connais, j&#39;aime ta voix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu connais mieux que moi-même </p>\n<p>Tous les besoins de mon  coeur. </p>\n<p>Et, pour mon bonheur suprême, </p>\n<p>Tu peux me rendre vainqueur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Prends mon corps et prends mon âme; </p>\n<p>Que tout en moi soit à Toi! </p>\n<p>Que par ta divine flamme </p>\n<p>Tout mal soit détruit en moi </p>\n','Entre-tes-mains-j\'abandonne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1414,'1eec9236-08fd-41ff-b63b-cc0d9e08e3ab',0,0,'Jésus, je te suivrai partout',152,'152_CE_fr','1.\nJésus, je te suivrai partout,  (bis)\nSoit dans la tristesse,\nSoit dans l\'allégresse,\nJésus, je te suivrai partout!\n\n\n2.\nJésus, je te suivrai toujours!  (bis)\nSi l\'épreuve abonde,\nTa paix est profonde,\nJésus, je te suivrai toujours!\n\n\n3.\nJe te suivrai dès aujourd\'hui!  (bis)\nMon âme affranchie,\nVivra de ta vie,\nJe te suivrai dès aujourd\'hui!\n\n\n4.\nJe te suivrai jusques au bout!  (bis)\nSi pour moi tu plaides,\nSeigneur, si tu m\'aides,\nJe te suivrai jusques au bout.','<p><strong>1.</strong> </p>\n<p>Jésus, je te suivrai partout,  (bis) </p>\n<p>Soit dans la tristesse, </p>\n<p>Soit dans l&#39;allégresse, </p>\n<p>Jésus, je te suivrai partout! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, je te suivrai toujours!  (bis) </p>\n<p>Si l&#39;épreuve abonde, </p>\n<p>Ta paix est profonde, </p>\n<p>Jésus, je te suivrai toujours! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je te suivrai dès aujourd&#39;hui!  (bis) </p>\n<p>Mon âme affranchie, </p>\n<p>Vivra de ta vie, </p>\n<p>Je te suivrai dès aujourd&#39;hui! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je te suivrai jusques au bout!  (bis) </p>\n<p>Si pour moi tu plaides, </p>\n<p>Seigneur, si tu m&#39;aides, </p>\n<p>Je te suivrai jusques au bout. </p>\n','Jesus-je-te-suivrai-partout',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1415,'32dc9bbd-2b65-474d-ba3f-b1d840f0d893',0,0,'Abandonne ta vie',156,'156_CE_fr','1.\nAbandonne ta vie,\nTes craintes et tes voeux,\nA la grâce infinie\nDu Souverain des cieux.\nLui, qui trace la route\nAux monde comme aux vents,\nConduira sans nul doutes,\nLes pas de ses enfants.\n\n\n2.\nQu\'il soit ton espérance\nAux bons, aux mauvais jours;\nQu\'il soit dans la souffrance,\nTon unique recours.\nDis-lui toutes tes peines,\nRemets-lui tes douleurs;\nIl a brisé tes chaînes,\nIl séchera tes pleurs.\n\n\n3.\nEspère, âme angoissée,\nEspère en ton Sauveur!\nIl connaît ta pensée,\nIl a lu dans ton coeur.\nIl est l\'amour suprême,\nIl est le vrai chemin;\nViens à Lui, car il t\'aime,\nViens!  Il te tend la main.\n\n\n4.\nA ta voix qui m\'appelle,\nJe t\'attendrai, Seigneur,\nJe veux, humble et fidèle,\nTe consacrer mon coeur.\nPour moi plus de tristesse!\nJe vivrai par la foi,\nEn célébrant sans cesse\nLe bonheur est à toi.','<p><strong>1.</strong> </p>\n<p>Abandonne ta vie, </p>\n<p>Tes craintes et tes voeux, </p>\n<p>A la grâce infinie </p>\n<p>Du Souverain des cieux. </p>\n<p>Lui, qui trace la route </p>\n<p>Aux monde comme aux vents, </p>\n<p>Conduira sans nul doutes, </p>\n<p>Les pas de ses enfants. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;il soit ton espérance </p>\n<p>Aux bons, aux mauvais jours; </p>\n<p>Qu&#39;il soit dans la souffrance, </p>\n<p>Ton unique recours. </p>\n<p>Dis-lui toutes tes peines, </p>\n<p>Remets-lui tes douleurs; </p>\n<p>Il a brisé tes chaînes, </p>\n<p>Il séchera tes pleurs. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Espère, âme angoissée, </p>\n<p>Espère en ton Sauveur! </p>\n<p>Il connaît ta pensée, </p>\n<p>Il a lu dans ton coeur. </p>\n<p>Il est l&#39;amour suprême, </p>\n<p>Il est le vrai chemin; </p>\n<p>Viens à Lui, car il t&#39;aime, </p>\n<p>Viens!  Il te tend la main. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A ta voix qui m&#39;appelle, </p>\n<p>Je t&#39;attendrai, Seigneur, </p>\n<p>Je veux, humble et fidèle, </p>\n<p>Te consacrer mon coeur. </p>\n<p>Pour moi plus de tristesse! </p>\n<p>Je vivrai par la foi, </p>\n<p>En célébrant sans cesse </p>\n<p>Le bonheur est à toi. </p>\n','Abandonne-ta-vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1416,'677ee539-a9a4-45d7-b84d-f91a2dff4abd',0,0,'À celui qui sera vainqueur',219,'219_CE_fr','1.\nA celui qui sera vainqueur,\nEt qui me glorifie,\nJe donnerai, dit le Seigneur,\nAu ciel l\'arbre de vie!\n\nRefrain\nVictoire, force, honneur et louanges,\nGloire, gloire, puissance à toi Jésus!\n\n\n2.\nUn caillou blanc, un nom nouveau,\nEt la manne cachée,\nLui seront donnés par l\'Agneau,\nSa promesse est scellée!\n\n\n3.\nRevêtu d\'un vêtement blanc,\nResplendissant de gloire!\nSera celui qui par le sang\nRemporta la victoire!\n\n\n4.\nAvec Christ, celui qui vaincra\nS\'assiéra sur son trône,\nEt de ses mains il recevra\nL\'immortelle couronne.','<p><strong>1.</strong> </p>\n<p>A celui qui sera vainqueur, </p>\n<p>Et qui me glorifie, </p>\n<p>Je donnerai, dit le Seigneur, </p>\n<p>Au ciel l&#39;arbre de vie! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Victoire, force, honneur et louanges,</strong> </p>\n<p><strong>Gloire, gloire, puissance à toi Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Un caillou blanc, un nom nouveau, </p>\n<p>Et la manne cachée, </p>\n<p>Lui seront donnés par l&#39;Agneau, </p>\n<p>Sa promesse est scellée! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Revêtu d&#39;un vêtement blanc, </p>\n<p>Resplendissant de gloire! </p>\n<p>Sera celui qui par le sang </p>\n<p>Remporta la victoire! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Avec Christ, celui qui vaincra </p>\n<p>S&#39;assiéra sur son trône, </p>\n<p>Et de ses mains il recevra </p>\n<p>L&#39;immortelle couronne. </p>\n','A-celui-qui-sera-vainqueur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1417,'5fd1c033-ec7e-4788-a970-800324d66e73',0,0,'Sur De La Victoire',220,'220_CE_fr','1.\nSûr de la victoire,\nSoldat du Seigneur,\nMarche, chante: « Gloire! »\nTu seras vainqueur.\nLe grand Capitaine\nQui va devant toi\nTe la rend certaine;\nCombats avec foi.\n\nRefrain\nOui, la victoire, - Tu l\'auras!\nChante donc: « Gloire! » - Tu verras\nTon adversaire – Fort, vaillant,\nCouché par terre – A l\'instant!\n\n\n2.\nL\'ennemi s\'avance\nRedoublant le pas\nEt plein d\'assurance,\nNe s\'arrête pas;\nMais, Christ va combattre,\nReste près de lui;\nDéjà pour l\'abattre,\nSon épée à lui.\n\nRefrain: Et la victoire, etc…\n\n\n3.\nDieu tient la couronne\nPromise au vainqueur;\nC\'est lui qui la donne\nAvec le bonheur;\nBientôt sur ta tête\nIl va la poser;\nDans sa paix parfaite\nTu va reposer.\n\nRefrain: Car la victoire, etc…','<p><strong>1.</strong> </p>\n<p>Sûr de la victoire, </p>\n<p>Soldat du Seigneur, </p>\n<p>Marche, chante: « Gloire! » </p>\n<p>Tu seras vainqueur. </p>\n<p>Le grand Capitaine </p>\n<p>Qui va devant toi </p>\n<p>Te la rend certaine; </p>\n<p>Combats avec foi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, la victoire, - Tu l&#39;auras!</strong> </p>\n<p><strong>Chante donc: « Gloire! » - Tu verras</strong> </p>\n<p><strong>Ton adversaire – Fort, vaillant,</strong> </p>\n<p><strong>Couché par terre – A l&#39;instant!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>L&#39;ennemi s&#39;avance </p>\n<p>Redoublant le pas </p>\n<p>Et plein d&#39;assurance, </p>\n<p>Ne s&#39;arrête pas; </p>\n<p>Mais, Christ va combattre, </p>\n<p>Reste près de lui; </p>\n<p>Déjà pour l&#39;abattre, </p>\n<p>Son épée à lui. </p>\n<p>Refrain: Et la victoire, etc… </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dieu tient la couronne </p>\n<p>Promise au vainqueur; </p>\n<p>C&#39;est lui qui la donne </p>\n<p>Avec le bonheur; </p>\n<p>Bientôt sur ta tête </p>\n<p>Il va la poser; </p>\n<p>Dans sa paix parfaite </p>\n<p>Tu va reposer. </p>\n<p>Refrain: Car la victoire, etc… </p>\n','Sur-De-La-Victoire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1418,'16398528-95d2-42b7-b2ec-1ddc952df165',0,0,'Écoutez l’appel du Berger',230,'230_CE_fr','1.\nÉcoutez l\'appel du Berger!\nIl sait ses brebis en danger;\nIl les appelle avec amour,\nEspérant toujours leur retour.\n\nRefrain\nCherchons-les!  Cherchons-les!\nSavons-nous le prix d\'une âme?\nCherchons-les!  Cherchons-les!\nLe Bon Berger les réclame.\n\n\n2.\nMourant de froid, de soif, de faim,\nLes brebis appellent en vain.\nJésus veut pour les sauver:\nQui va l\'aider à les trouver!\n\n\n3.\nNe peut-il pas compter sur nous?\nNe voulons-nous pas aller tous\nDire à tous ceux qui sont perdus\nQue nous les voulons pour Jésus?','<p><strong>1.</strong> </p>\n<p>Écoutez l&#39;appel du Berger! </p>\n<p>Il sait ses brebis en danger; </p>\n<p>Il les appelle avec amour, </p>\n<p>Espérant toujours leur retour. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Cherchons-les!  Cherchons-les!</strong> </p>\n<p><strong>Savons-nous le prix d&#39;une âme?</strong> </p>\n<p><strong>Cherchons-les!  Cherchons-les!</strong> </p>\n<p><strong>Le Bon Berger les réclame.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mourant de froid, de soif, de faim, </p>\n<p>Les brebis appellent en vain. </p>\n<p>Jésus veut pour les sauver: </p>\n<p>Qui va l&#39;aider à les trouver! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ne peut-il pas compter sur nous? </p>\n<p>Ne voulons-nous pas aller tous </p>\n<p>Dire à tous ceux qui sont perdus </p>\n<p>Que nous les voulons pour Jésus? </p>\n','Ecoutez-l\'appel-du-Berger',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1419,'c358a081-b8ee-43ef-b136-822fa0b3617f',0,0,'Seigneur, que la terre entière',241,'241_CE_fr','1.\nSeigneur, que la terre entière\nS\'éveille à ta grande voix,\nEt se lève à la lumière\nQui rayonne de ta croix,\nQui rayonne de ta croix!\n\nRefrain\nAprès la nuit viens l\'aurore:\nVoici l\'heure du réveil.\nQue toute âme, ô divin soleil,\nTe contemple et t\'adore!\nAprès la nuit vient l\'aurore:\nVoici l\'heure du réveil.\nQue toute âme, ô divin soleil,\nTe contemple et t\'adore!\n\n\n2.\nTon sang lave notre crime\nEt dissipe nos remord;\nEn mourant, sainte Victime,\nTu fais renaître les morts,\nTu fais renaître les morts.\n\n\n3.\nArrache à l\'enfer sa proie,\nTout-puissant Ressuscité!\nSème à pleine main la joie\nSur ce monde dévasté,\nSur ce monde dévasté.\n\n\n4.\nQue la source soit tarie\nDe vos pleurs, vous qui pleurez,\nPar l\'espoir de la patrie\nOù bientôt vous entrerez,\nOù bientôt vous entrerez!\n\n\n5.\nErreur, péché, nuit mortelle,\nFuyez dans le noir passé!\nPour moi la vie éternelle\nPar Jésus a commencé,\nPar Jésus a commencé.','<p><strong>1.</strong> </p>\n<p>Seigneur, que la terre entière </p>\n<p>S&#39;éveille à ta grande voix, </p>\n<p>Et se lève à la lumière </p>\n<p>Qui rayonne de ta croix, </p>\n<p>Qui rayonne de ta croix! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Après la nuit viens l&#39;aurore:</strong> </p>\n<p><strong>Voici l&#39;heure du réveil.</strong> </p>\n<p><strong>Que toute âme, ô divin soleil,</strong> </p>\n<p><strong>Te contemple et t&#39;adore!</strong> </p>\n<p><strong>Après la nuit vient l&#39;aurore:</strong> </p>\n<p><strong>Voici l&#39;heure du réveil.</strong> </p>\n<p><strong>Que toute âme, ô divin soleil,</strong> </p>\n<p><strong>Te contemple et t&#39;adore!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton sang lave notre crime </p>\n<p>Et dissipe nos remord; </p>\n<p>En mourant, sainte Victime, </p>\n<p>Tu fais renaître les morts, </p>\n<p>Tu fais renaître les morts. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Arrache à l&#39;enfer sa proie, </p>\n<p>Tout-puissant Ressuscité! </p>\n<p>Sème à pleine main la joie </p>\n<p>Sur ce monde dévasté, </p>\n<p>Sur ce monde dévasté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que la source soit tarie </p>\n<p>De vos pleurs, vous qui pleurez, </p>\n<p>Par l&#39;espoir de la patrie </p>\n<p>Où bientôt vous entrerez, </p>\n<p>Où bientôt vous entrerez! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Erreur, péché, nuit mortelle, </p>\n<p>Fuyez dans le noir passé! </p>\n<p>Pour moi la vie éternelle </p>\n<p>Par Jésus a commencé, </p>\n<p>Par Jésus a commencé. </p>\n','Seigneur-que-la-terre-entiere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1420,'1922b65d-e74c-4484-92c3-051c39977365',0,0,'Voici, je me tiens',276,'276_CE_fr','1.\nVoici, je me tiens\nA la porte de l\'âme,\nJe frappe sans cesse\nAvec divin espoir.\nSi quelqu\'un m\'entend,\nS\'il m\'ouvre et me réclame,\nChez lui j\'entrerai,\nJ\'y souperai ce soir.\n\nRefrain\nOuvre, ouvre-moi!\nSi quelqu\'un m\'ouvre la porte,\nJe soupe avec lui ce soir.  (bis 2 lignes)\n\n\n2.\nJe frappe au matin,\nQuand toute la nature\nNous parle d\'un Père\nEt tendre et tout-puissant.\nJe frappe à midi,\nQuand la moisson est mûr,\nRévélant l\'amour\nD\'un Dieu si prévoyant.','<p><strong>1.</strong> </p>\n<p>Voici, je me tiens </p>\n<p>A la porte de l&#39;âme, </p>\n<p>Je frappe sans cesse </p>\n<p>Avec divin espoir. </p>\n<p>Si quelqu&#39;un m&#39;entend, </p>\n<p>S&#39;il m&#39;ouvre et me réclame, </p>\n<p>Chez lui j&#39;entrerai, </p>\n<p>J&#39;y souperai ce soir. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Ouvre, ouvre-moi!</strong> </p>\n<p><strong>Si quelqu&#39;un m&#39;ouvre la porte,</strong> </p>\n<p><strong>Je soupe avec lui ce soir.  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je frappe au matin, </p>\n<p>Quand toute la nature </p>\n<p>Nous parle d&#39;un Père </p>\n<p>Et tendre et tout-puissant. </p>\n<p>Je frappe à midi, </p>\n<p>Quand la moisson est mûr, </p>\n<p>Révélant l&#39;amour </p>\n<p>D&#39;un Dieu si prévoyant. </p>\n','Voici-je-me-tiens',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1421,'b8ef7618-a8a2-4ed1-897c-40eb7ef67f82',0,0,'Seigneur, je n’ai rien à t’offrir',296,'296_CE_fr','1.\nSeigneur, je n\'ai rien à t\'offrir\nQu\'un coeur fatigué de souffrir,\nEt qui, sans toi, ne peut guérir:\nJe n\'ai que ma misère.\n\nRefrain\nPrends-moi tel que je suis,\nSans vertus, sans appuis,\nTel que je suis, tel que je suis,\nO mon céleste Frère!\n\n\n2.\nJ\'ai transgressé ta sainte loi;\nLe péché vainqueur règne en moi;\nPour me présenté devant Toi,\nJe n\'ai que ma souillure.\n\nRefrain\nPrends-moi tel que je suis,\nSans vertus, sans appuis,\nTel que je suis, tel que je suis,\nLave mon âme impure!\n\n\n3.\nFaible est ma chair, faible est mon coeur;\nPour repousser le tentateur,\nO mon divin Libérateur!\nJe n\'ai que ma faiblesse.\n\nRefrain\nPrends-moi tel que je suis,\nSans vertus, sans appuis,\nTel que je suis, tel que je suis,\nSubviens à ma détresse!\n\n\n4.\nTon sang versé me blanchira,\nTon Saint-Esprit m\'affranchira,\nTa richesse m\'enrichira,\nO mon céleste Maître!\n\nRefrain\nPrends-moi, faible et pécheur,\nSans vertus ni vigueur;\nO mon Sauveur,\nRends-moi vainqueur,\nEt tel que je dois être!','<p><strong>1.</strong> </p>\n<p>Seigneur, je n&#39;ai rien à t&#39;offrir </p>\n<p>Qu&#39;un coeur fatigué de souffrir, </p>\n<p>Et qui, sans toi, ne peut guérir: </p>\n<p>Je n&#39;ai que ma misère. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prends-moi tel que je suis,</strong> </p>\n<p><strong>Sans vertus, sans appuis,</strong> </p>\n<p><strong>Tel que je suis, tel que je suis,</strong> </p>\n<p><strong>O mon céleste Frère!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai transgressé ta sainte loi; </p>\n<p>Le péché vainqueur règne en moi; </p>\n<p>Pour me présenté devant Toi, </p>\n<p>Je n&#39;ai que ma souillure. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prends-moi tel que je suis,</strong> </p>\n<p><strong>Sans vertus, sans appuis,</strong> </p>\n<p><strong>Tel que je suis, tel que je suis,</strong> </p>\n<p><strong>Lave mon âme impure!</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Faible est ma chair, faible est mon coeur; </p>\n<p>Pour repousser le tentateur, </p>\n<p>O mon divin Libérateur! </p>\n<p>Je n&#39;ai que ma faiblesse. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prends-moi tel que je suis,</strong> </p>\n<p><strong>Sans vertus, sans appuis,</strong> </p>\n<p><strong>Tel que je suis, tel que je suis,</strong> </p>\n<p><strong>Subviens à ma détresse!</strong> </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ton sang versé me blanchira, </p>\n<p>Ton Saint-Esprit m&#39;affranchira, </p>\n<p>Ta richesse m&#39;enrichira, </p>\n<p>O mon céleste Maître! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Prends-moi, faible et pécheur,</strong> </p>\n<p><strong>Sans vertus ni vigueur;</strong> </p>\n<p><strong>O mon Sauveur,</strong> </p>\n<p><strong>Rends-moi vainqueur,</strong> </p>\n<p><strong>Et tel que je dois être!</strong> </p>\n','Seigneur-je-n\'ai-rien-a-t\'offrir',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1422,'0ec9591b-3d7d-4f44-be2e-45518617921a',0,0,'O jour béni, jour de victoire',155,'155_CE_fr','1.\nO jour béni, jour de victoire\nQue je ne saurais oublier\nJ\'ai vu, j\'ai vu le Roi de gloire\nApparaissant sur mon sentier\nSa beauté, sa gloire infinie\nDe tous les côtés m\'entourait\nSon regard, qui porte la vie\nSur ma pauvre âme s\'abaissait\n\n2.\nSon manteau couvrait ma misère\nSes bras me serraient sur son cœur\nIl me portait dans sa lumière\nLoin du péché, de la douleur\nDe sa main essuyant mes larmes\nIl me parlait de son amour\n«Viens mon enfant, sois sans alarmes\nJe te prends à Moi sans retour»\n\n3.\nEt je suis dans cette retraite\nDont je ne sortirai jamais\nEt je goûte une paix parfaite\nOù ma foi s\'abreuve à longs traits\nNon, tout ceci n\'est point un rêve\nMais la grande réalité\nC\'est un jour nouveau qui se lève\nQui doit durer l\'éternité\n\n4.\nEn avant donc, avec courage\nAvec  espoir, avec bonheur\nJe me consacre sans partage\nÀ mon Dieu, mon Roi, mon Sauveur\nIl dit à mon âme ravie\nNe t\'occupe plus que de moi\nEt je dirigerai ta vie\nEt je m\'occuperai de toi\n\n5.\nIl a saisi mes mains tremblantes\nJ\'ai dit amen à ce contrat\nIl étend ses mains bénissantes\nC\'est en effet Lui qui combat\nEt les yeux fermés, je m\'avance\nTranquille, sur le droit chemin\nJ\'entonne un chant de délivrance\nIl peut tout, car je ne suis rien','<p><strong>1.</strong> </p>\n<p>O jour béni, jour de victoire </p>\n<p>Que je ne saurais oublier </p>\n<p>J&#39;ai vu, j&#39;ai vu le Roi de gloire </p>\n<p>Apparaissant sur mon sentier </p>\n<p>Sa beauté, sa gloire infinie </p>\n<p>De tous les côtés m&#39;entourait </p>\n<p>Son regard, qui porte la vie </p>\n<p>Sur ma pauvre âme s&#39;abaissait </p>\n<p><br/> <strong>2.</strong></p>\n<p>Son manteau couvrait ma misère </p>\n<p>Ses bras me serraient sur son cœur </p>\n<p>Il me portait dans sa lumière </p>\n<p>Loin du péché, de la douleur </p>\n<p>De sa main essuyant mes larmes </p>\n<p>Il me parlait de son amour </p>\n<p>«Viens mon enfant, sois sans alarmes </p>\n<p>Je te prends à Moi sans retour» </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et je suis dans cette retraite </p>\n<p>Dont je ne sortirai jamais </p>\n<p>Et je goûte une paix parfaite </p>\n<p>Où ma foi s&#39;abreuve à longs traits </p>\n<p>Non, tout ceci n&#39;est point un rêve </p>\n<p>Mais la grande réalité </p>\n<p>C&#39;est un jour nouveau qui se lève </p>\n<p>Qui doit durer l&#39;éternité </p>\n<p><br/> <strong>4.</strong></p>\n<p>En avant donc, avec courage </p>\n<p>Avec  espoir, avec bonheur </p>\n<p>Je me consacre sans partage </p>\n<p>À mon Dieu, mon Roi, mon Sauveur </p>\n<p>Il dit à mon âme ravie </p>\n<p>Ne t&#39;occupe plus que de moi </p>\n<p>Et je dirigerai ta vie </p>\n<p>Et je m&#39;occuperai de toi </p>\n<p><br/> <strong>5.</strong></p>\n<p>Il a saisi mes mains tremblantes </p>\n<p>J&#39;ai dit amen à ce contrat </p>\n<p>Il étend ses mains bénissantes </p>\n<p>C&#39;est en effet Lui qui combat </p>\n<p>Et les yeux fermés, je m&#39;avance </p>\n<p>Tranquille, sur le droit chemin </p>\n<p>J&#39;entonne un chant de délivrance </p>\n<p>Il peut tout, car je ne suis rien </p>\n','O-jour-beni-jour-de-victoire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1423,'d818db36-bd2c-488b-8fb6-e86f0c20caea',0,0,'Seigneur, à ton regard de flamme',162,'162_CE_fr','1.\nSeigneur, à ton regard de flamme\nRien ne t\'est couvert, rien n\'est caché;\nQu\'il pénètre au fond de notre âme,\nEt qu\'il juge en nous le péché,\nQu\'il juge le péché!\n\n\n2.\nSonde nos coeurs et nos pensées,\nNos plus intimes souvenirs,\nNos oeuvres présentes, passées,\nSondes nos plus secrets désirs,\nNos plus secrets désirs!\n\n\n3.\nQu\'à la lumière tout paraisse\nPour que tout soit purifié,\nEt qu\'en nous ton Esprit ne laisse\nRien qui ne soit sanctifié,\nVraiment sanctifié!\n\n\n4.\nAlors, brûlant d\'un nouveau zèle,\nSeigneur, nous pourrons t\'obéir.\nOh!  Que la vie est grande et belle\nPour ceux qui veulent te servir,\nQui veulent te servir!','<p><strong>1.</strong> </p>\n<p>Seigneur, à ton regard de flamme </p>\n<p>Rien ne t&#39;est couvert, rien n&#39;est caché; </p>\n<p>Qu&#39;il pénètre au fond de notre âme, </p>\n<p>Et qu&#39;il juge en nous le péché, </p>\n<p>Qu&#39;il juge le péché! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sonde nos coeurs et nos pensées, </p>\n<p>Nos plus intimes souvenirs, </p>\n<p>Nos oeuvres présentes, passées, </p>\n<p>Sondes nos plus secrets désirs, </p>\n<p>Nos plus secrets désirs! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;à la lumière tout paraisse </p>\n<p>Pour que tout soit purifié, </p>\n<p>Et qu&#39;en nous ton Esprit ne laisse </p>\n<p>Rien qui ne soit sanctifié, </p>\n<p>Vraiment sanctifié! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Alors, brûlant d&#39;un nouveau zèle, </p>\n<p>Seigneur, nous pourrons t&#39;obéir. </p>\n<p>Oh!  Que la vie est grande et belle </p>\n<p>Pour ceux qui veulent te servir, </p>\n<p>Qui veulent te servir! </p>\n','Seigneur-a-ton-regard-de-flamme',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1424,'f64daf8c-7f6f-4f9d-9aa0-293b4a2aa58f',0,0,'Moment si doux de la prière',193,'193_CE_fr','1.\nMoment si doux de la prière,\nOù Dieu, m\'élevant jusqu\'à lui,\nSe révèle à moi comme un Père,\nComme un Sauveur, comme un appui;\nOh! Oui, je t\'aime, heure bénie.\nJe te désire avec ardeur,\nCar déjà souvent dans la vie,\nTu m\'as sauvé du tentateur.  (bis 2 lignes)\n\n2.\nDoux moment de paix, heure sainte,\nOù, sur les ailes de la foi,\nMon coeur s\'élève sans contrainte,\nJe ne saurais vivre sans toi.\nSois donc toujours toute ma joie,\nTout mon refuge et mon secours,\nEt que jamais Dieu ne me voie\nPasser sans toi l\'un de mes jours!  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Moment si doux de la prière, </p>\n<p>Où Dieu, m&#39;élevant jusqu&#39;à lui, </p>\n<p>Se révèle à moi comme un Père, </p>\n<p>Comme un Sauveur, comme un appui; </p>\n<p>Oh! Oui, je t&#39;aime, heure bénie. </p>\n<p>Je te désire avec ardeur, </p>\n<p>Car déjà souvent dans la vie, </p>\n<p>Tu m&#39;as sauvé du tentateur.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Doux moment de paix, heure sainte, </p>\n<p>Où, sur les ailes de la foi, </p>\n<p>Mon coeur s&#39;élève sans contrainte, </p>\n<p>Je ne saurais vivre sans toi. </p>\n<p>Sois donc toujours toute ma joie, </p>\n<p>Tout mon refuge et mon secours, </p>\n<p>Et que jamais Dieu ne me voie </p>\n<p>Passer sans toi l&#39;un de mes jours!  (bis 2 lignes) </p>\n','Moment-si-doux-de-la-priere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1425,'7c695c5d-71ac-48cd-bf11-9855daeea98c',0,0,'Dans le jardin ou j’aime entrer',207,'207_CE_fr','1.\nDans le jardin où j\'aime entrer\nA l\'heure douce de l\'aurore,\nJe me rends seul pour rencontrer\nCelui que mon âme adore.\n\nRefrain\nIl marche avec moi,\nMon Sauveur, mon Roi,\nIl me dit que je suis à lui;\nIl est mon soutien,\nIl est tout mon bien,\nMon salut, mon divin appui.\n\n\n2.\nIl parle, et dans mon coeur, sa voix\nFait naître une joie infinie,\nLes oiseaux même, au fond du bois,\nSuspendent leur mélodie.\n\n\n3.\nLe don que tu cherches, Seigneur,\nMeilleur que les fruits de la terre,\nC\'est l\'offrande de tout mon coeur;\nElle, seule, peut te plaire.','<p><strong>1.</strong> </p>\n<p>Dans le jardin où j&#39;aime entrer </p>\n<p>A l&#39;heure douce de l&#39;aurore, </p>\n<p>Je me rends seul pour rencontrer </p>\n<p>Celui que mon âme adore. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il marche avec moi,</strong> </p>\n<p><strong>Mon Sauveur, mon Roi,</strong> </p>\n<p><strong>Il me dit que je suis à lui;</strong> </p>\n<p><strong>Il est mon soutien,</strong> </p>\n<p><strong>Il est tout mon bien,</strong> </p>\n<p><strong>Mon salut, mon divin appui.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il parle, et dans mon coeur, sa voix </p>\n<p>Fait naître une joie infinie, </p>\n<p>Les oiseaux même, au fond du bois, </p>\n<p>Suspendent leur mélodie. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le don que tu cherches, Seigneur, </p>\n<p>Meilleur que les fruits de la terre, </p>\n<p>C&#39;est l&#39;offrande de tout mon coeur; </p>\n<p>Elle, seule, peut te plaire. </p>\n','Dans-le-jardin-ou-j\'aime-entrer',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1426,'e8c3f490-c720-4b66-b873-28d0340619f8',0,0,'Triomphons, Chantons D’allégresse',213,'213_CE_fr','1.\nTriomphant, chantons d\'allégresse,\nRéjouissons-nous devant Dieu!\nQu\'un chant de louanges sans cesse\nA sa gloire monte en tout lieu!\n\nRefrain\nTriomphe, triomphe,\nTressaille de gloire en ton Dieu!\nTriomphe, triomphe,\nTressaille de joie en ton Dieu!\n\n\n2.\nChantons à Celui qui s\'avance\nDans les cieux, les cieux éternels!\nIl fait entendre avec puissance\nSa voix, c\'est le Dieu d\'Israël!\n\n\n3.\nIl donne à son peuple victoire,\nPuissance, force, liberté,\nA notre Dieu rendons la gloire,\nLouons sa sainte majesté!\n\n\n4.\nDe concert avec les saints anges\nFaisons retentir notre voix,\nChantons à jamais les louanges\nDe l\'Agneau divin mis en croix.','<p><strong>1.</strong> </p>\n<p>Triomphant, chantons d&#39;allégresse, </p>\n<p>Réjouissons-nous devant Dieu! </p>\n<p>Qu&#39;un chant de louanges sans cesse </p>\n<p>A sa gloire monte en tout lieu! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Triomphe, triomphe,</strong> </p>\n<p><strong>Tressaille de gloire en ton Dieu!</strong> </p>\n<p><strong>Triomphe, triomphe,</strong> </p>\n<p><strong>Tressaille de joie en ton Dieu!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chantons à Celui qui s&#39;avance </p>\n<p>Dans les cieux, les cieux éternels! </p>\n<p>Il fait entendre avec puissance </p>\n<p>Sa voix, c&#39;est le Dieu d&#39;Israël! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il donne à son peuple victoire, </p>\n<p>Puissance, force, liberté, </p>\n<p>A notre Dieu rendons la gloire, </p>\n<p>Louons sa sainte majesté! </p>\n<p><br/> <strong>4.</strong></p>\n<p>De concert avec les saints anges </p>\n<p>Faisons retentir notre voix, </p>\n<p>Chantons à jamais les louanges </p>\n<p>De l&#39;Agneau divin mis en croix. </p>\n','Triomphons-Chantons-D\'allegresse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1427,'aa4046f0-8eff-44df-90e5-1144e1bdc47a',0,0,'C’est Jésus, quand je chancelle',214,'214_CE_fr','1.\nC\'est Jésus, quand je chancelle\nSeul en mon chemin,\nQui vient prendre ma main frêle\nDans sa forte main.\n\nRefrain\nTenu par sa main,  (bis)\nOui, je suis, quand je chancelle,\nTenu par sa main.\n\n\n2.\nIl connaît bien ma faiblesse,\nMon indignité,\nMais Il me bénit sans cesse;\nGrande est sa bonté!\n\nRefrain\nGrande est sa bonté!  (bis)\nIl supplée à ma faiblesse;\nGrande est sa bonté!\n\n3.\nJ\'ai dans ma misère extrême\nLes yeux sur Jésus.\nJe suis assuré qu\'Il m\'aime,\nQue faut-il de plus?\n\nRefrain\nLes yeux sur Jésus,  (bis)\nGloire à son amour suprême!\nQue faut-il de plus!\n\n\n4.\nTon sang, sur la croix bénie\nA coulé pour moi,\nEt je ne me glorifie\nQu\'en toi, divin Roi!\n\nRefrain\nOui, ce n\'est qu\'en Toi,  (bis)\nQue j\'ai le salut, la vie,\nO Jésus, mon Roi!','<p><strong>1.</strong> </p>\n<p>C&#39;est Jésus, quand je chancelle </p>\n<p>Seul en mon chemin, </p>\n<p>Qui vient prendre ma main frêle </p>\n<p>Dans sa forte main. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Tenu par sa main,  (bis)</strong> </p>\n<p><strong>Oui, je suis, quand je chancelle,</strong> </p>\n<p><strong>Tenu par sa main.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il connaît bien ma faiblesse, </p>\n<p>Mon indignité, </p>\n<p>Mais Il me bénit sans cesse; </p>\n<p>Grande est sa bonté! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Grande est sa bonté!  (bis)</strong> </p>\n<p><strong>Il supplée à ma faiblesse;</strong> </p>\n<p><strong>Grande est sa bonté!</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;ai dans ma misère extrême </p>\n<p>Les yeux sur Jésus. </p>\n<p>Je suis assuré qu&#39;Il m&#39;aime, </p>\n<p>Que faut-il de plus? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Les yeux sur Jésus,  (bis)</strong> </p>\n<p><strong>Gloire à son amour suprême!</strong> </p>\n<p><strong>Que faut-il de plus!</strong> </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ton sang, sur la croix bénie </p>\n<p>A coulé pour moi, </p>\n<p>Et je ne me glorifie </p>\n<p>Qu&#39;en toi, divin Roi! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, ce n&#39;est qu&#39;en Toi,  (bis)</strong> </p>\n<p><strong>Que j&#39;ai le salut, la vie,</strong> </p>\n<p><strong>O Jésus, mon Roi!</strong> </p>\n','C\'est-Jesus-quand-je-chancelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1428,'58e6dc65-1d59-4fbf-b944-35d1af6468d8',0,0,'Le signal de la victoire',216,'216_CE_fr','1.\nLe signal de la victoire\nDéjà brille aux cieux;\nLa couronne de la victoire\nParaît à nos yeux.\n\nRefrain\nJe viens, combattez encore!\nDit Jésus à tous.\nOui, mon Sauveur, je t\'implore,\nJe lutte à genoux.\n\n\n2.\nQue l\'ennemi, plein de rage,\nRedouble ses coups,\nNous ne perdons point courage:\nChrist est avec nous.\n\n\n3.\nSuivons, amis, la bannière\nDu Sauveur en croix,\nEt que notre armée entière\nSe range à sa voix.\n\n\n4.\nRude et longue est la mêlée:\nVoici le secours!\nDans nos mains prenons l\'épée\nQui vainquit toujours!','<p><strong>1.</strong> </p>\n<p>Le signal de la victoire </p>\n<p>Déjà brille aux cieux; </p>\n<p>La couronne de la victoire </p>\n<p>Paraît à nos yeux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je viens, combattez encore!</strong> </p>\n<p><strong>Dit Jésus à tous.</strong> </p>\n<p><strong>Oui, mon Sauveur, je t&#39;implore,</strong> </p>\n<p><strong>Je lutte à genoux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que l&#39;ennemi, plein de rage, </p>\n<p>Redouble ses coups, </p>\n<p>Nous ne perdons point courage: </p>\n<p>Christ est avec nous. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Suivons, amis, la bannière </p>\n<p>Du Sauveur en croix, </p>\n<p>Et que notre armée entière </p>\n<p>Se range à sa voix. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Rude et longue est la mêlée: </p>\n<p>Voici le secours! </p>\n<p>Dans nos mains prenons l&#39;épée </p>\n<p>Qui vainquit toujours! </p>\n','Le-signal-de-la-victoire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1429,'a816d1c3-096d-463f-93e3-ba05d66c4feb',0,0,'Seigneur Jésus, céleste Frère',311,'311_CE_fr','1.\nSeigneur Jésus, céleste Frère,\nBénis-nous en ce jour;\nToi qui descendis sur la terre\nPour montrer Ton amour\nSur ces époux fais pour toujours\nRayonner ta lumière,\nEt qu\'elle éclaire le chemin\nDevant les pèlerins.\n\n\n2.\nIls ont en Toi leur espérance,\nJésus, parfait Sauveur.\nSois leur gardien, leur délivrance,\nLeur puissant protecteur.\nRépands sur eux le vrai bonheur\nPar ta sainte présence.\nSeigneur, exauce maintenant\nLes voeux de tes enfants.','<p><strong>1.</strong> </p>\n<p>Seigneur Jésus, céleste Frère, </p>\n<p>Bénis-nous en ce jour; </p>\n<p>Toi qui descendis sur la terre </p>\n<p>Pour montrer Ton amour </p>\n<p>Sur ces époux fais pour toujours </p>\n<p>Rayonner ta lumière, </p>\n<p>Et qu&#39;elle éclaire le chemin </p>\n<p>Devant les pèlerins. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ils ont en Toi leur espérance, </p>\n<p>Jésus, parfait Sauveur. </p>\n<p>Sois leur gardien, leur délivrance, </p>\n<p>Leur puissant protecteur. </p>\n<p>Répands sur eux le vrai bonheur </p>\n<p>Par ta sainte présence. </p>\n<p>Seigneur, exauce maintenant </p>\n<p>Les voeux de tes enfants. </p>\n','Seigneur-Jesus-celeste-Frere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1430,'6eed2d40-8c05-4625-b39b-0b74dedd72b3',0,0,'Mon Dieu, Plus Près De Toi',148,'148_CE_fr','1.\nMon Dieu, plus près de Toi,\nPlus près de Toi!\nC\'est le mot de ma foi:\nPlus près de Toi!\nDans le jour où l\'épreuve\nDéborde comme un fleuve,\nGarde-moi près de Toi,\nPlus près de Toi!\n\n\n2.\nPlus près de Toi, Seigneur,\nPlus près de Toi!\nTiens-moi dans ma douleur,\nTout près de Toi!\nAlors que la souffrance\nFait son oeuvre en silence,\nToujours plus près de Toi,\nSeigneur, tiens-moi!\n\n\n3.\nPlus près de Toi, toujours,\nPlus près de Toi!\nDonne-moi ton secours,\nSoutiens ma foi!\nQue Satan se déchaîne,\nTon amour me ramène\nToujours plus près de Toi,\nPlus près de Toi!\n\n\n4.\nMon Dieu, plus près de Toi!\nDans le désert\nJ\'ai vu, plus près de Toi,\nTon ciel ouvert.\nPèlerin, bon courage!\nTon chant brave l\'orage.\nMon Dieu, plus près de Toi,\nPlus près de Toi!','<p><strong>1.</strong> </p>\n<p>Mon Dieu, plus près de Toi, </p>\n<p>Plus près de Toi! </p>\n<p>C&#39;est le mot de ma foi: </p>\n<p>Plus près de Toi! </p>\n<p>Dans le jour où l&#39;épreuve </p>\n<p>Déborde comme un fleuve, </p>\n<p>Garde-moi près de Toi, </p>\n<p>Plus près de Toi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Plus près de Toi, Seigneur, </p>\n<p>Plus près de Toi! </p>\n<p>Tiens-moi dans ma douleur, </p>\n<p>Tout près de Toi! </p>\n<p>Alors que la souffrance </p>\n<p>Fait son oeuvre en silence, </p>\n<p>Toujours plus près de Toi, </p>\n<p>Seigneur, tiens-moi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Plus près de Toi, toujours, </p>\n<p>Plus près de Toi! </p>\n<p>Donne-moi ton secours, </p>\n<p>Soutiens ma foi! </p>\n<p>Que Satan se déchaîne, </p>\n<p>Ton amour me ramène </p>\n<p>Toujours plus près de Toi, </p>\n<p>Plus près de Toi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mon Dieu, plus près de Toi! </p>\n<p>Dans le désert </p>\n<p>J&#39;ai vu, plus près de Toi, </p>\n<p>Ton ciel ouvert. </p>\n<p>Pèlerin, bon courage! </p>\n<p>Ton chant brave l&#39;orage. </p>\n<p>Mon Dieu, plus près de Toi, </p>\n<p>Plus près de Toi! </p>\n','Mon-Dieu-Plus-Pres-De-Toi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1431,'6a6897d1-4557-47fd-abc3-a90889a87b51',0,0,'Miséricorde Insondable',150,'150_CE_fr','1.\nMiséricorde insondable!\nDieu peut-il tout pardonné?\nAbsoudre un si grand coupable,\nEt mes péchés oublier?\n\nRefrain\nJésus, je viens!  Je viens à Toi!\nTel que je suis, je viens à Toi!\nJésus, je viens, je viens à Toi!\nTel que je suis, prends-moi.\n\n\n2.\nLongtemps j\'ai, loin de sa face,\nProvoqué son saint courroux,\nFermé mon coeur à sa grâce,\nBlessé le sien devant tous.\n\n\n3.\nO Jésus!  À Toi je cède,\nJe veux être libéré;\nDe tout péché qui m\'obsède\nÊtre à jamais délivré.\n\n\n4.\nAlléluia!  Plus de doute,\nMon fardeau est enlevé;\nPour le ciel je suis en route,\nHeureux pour l\'éternité','<p><strong>1.</strong> </p>\n<p>Miséricorde insondable! </p>\n<p>Dieu peut-il tout pardonné? </p>\n<p>Absoudre un si grand coupable, </p>\n<p>Et mes péchés oublier? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus, je viens!  Je viens à Toi!</strong> </p>\n<p><strong>Tel que je suis, je viens à Toi!</strong> </p>\n<p><strong>Jésus, je viens, je viens à Toi!</strong> </p>\n<p><strong>Tel que je suis, prends-moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Longtemps j&#39;ai, loin de sa face, </p>\n<p>Provoqué son saint courroux, </p>\n<p>Fermé mon coeur à sa grâce, </p>\n<p>Blessé le sien devant tous. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O Jésus!  À Toi je cède, </p>\n<p>Je veux être libéré; </p>\n<p>De tout péché qui m&#39;obsède </p>\n<p>Être à jamais délivré. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Alléluia!  Plus de doute, </p>\n<p>Mon fardeau est enlevé; </p>\n<p>Pour le ciel je suis en route, </p>\n<p>Heureux pour l&#39;éternité </p>\n','Misericorde-Insondable',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1432,'c1e60583-9dbe-4541-98fd-19ace2633840',0,0,'Plus haut, plus haut!',164,'164_CE_fr','1.\nPlus haut, plus haut!\nC\'est le cri de ma foi:\nS\'il faut courber\nLa tête sous le glaive,\nJe veux encore\nQue mon âme s\'élève\nPlus près de Toi, mon Dieu,\nPlus près de Toi!\n\n\n2.\nLorsque la nuit\nSe fait autour de moi,\nQuand j\'erre seul\nDans le désert immense,\nQue de mon âme\nEncore ce cri s\'élance:\nPlus près de Toi, mon Dieu,\nPlus près de Toi!\n\n\n3.\nPrends, ô mon coeur,\nLes ailes de la foi,\nVole au-dessus\nDes monts et des vallées,\nChante, au travers\nDes plaines étoilées:\nPlus près de Toi, mon Dieu,\nPlus près de Toi!\n\n\n4.\nQuand tu viendras,\nO mon céleste Roi,\nMe recueillir\nDans ta pure lumière,\nQue je redise\nA mon heure dernière:\nPlus près de Toi, mon Dieu,\nPlus près de Toi!','<p><strong>1.</strong> </p>\n<p>Plus haut, plus haut! </p>\n<p>C&#39;est le cri de ma foi: </p>\n<p>S&#39;il faut courber </p>\n<p>La tête sous le glaive, </p>\n<p>Je veux encore </p>\n<p>Que mon âme s&#39;élève </p>\n<p>Plus près de Toi, mon Dieu, </p>\n<p>Plus près de Toi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lorsque la nuit </p>\n<p>Se fait autour de moi, </p>\n<p>Quand j&#39;erre seul </p>\n<p>Dans le désert immense, </p>\n<p>Que de mon âme </p>\n<p>Encore ce cri s&#39;élance: </p>\n<p>Plus près de Toi, mon Dieu, </p>\n<p>Plus près de Toi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prends, ô mon coeur, </p>\n<p>Les ailes de la foi, </p>\n<p>Vole au-dessus </p>\n<p>Des monts et des vallées, </p>\n<p>Chante, au travers </p>\n<p>Des plaines étoilées: </p>\n<p>Plus près de Toi, mon Dieu, </p>\n<p>Plus près de Toi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand tu viendras, </p>\n<p>O mon céleste Roi, </p>\n<p>Me recueillir </p>\n<p>Dans ta pure lumière, </p>\n<p>Que je redise </p>\n<p>A mon heure dernière: </p>\n<p>Plus près de Toi, mon Dieu, </p>\n<p>Plus près de Toi! </p>\n','Plus-haut-plus-haut!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1433,'3c92f4a0-83b1-4ef8-9631-438de11976be',0,0,'O Dieu de vérité',199,'199_CE_fr','1.\nO Dieu de vérité,\nPour qui seul je soupire,\nUnis mon coeur à Toi\nPar de forts et doux noeuds!\nJe me lasse d\'ouïr,\nJe me lasse de lire,\nMais non pas de te dire:\nC\'est Toi seul que je veux!\nMais non pas de te dire:\nC\'est Toi seul que je veux!\n\n\n2.\nParle seul à mon coeur,\nEt qu\'aucune prudence,\nQu\'aucun autre docteur\nNe m\'explique tes lois;\nQue toute créature,\nEn ta sainte présence,\nS\'impose le silence\nEt laisse agir ta voix!\nS\'impose le silence\nEt laisse agir ta voix!\n\n\n3.\nTiens-toi près de mon âme,\nEt, dans ma solitude,\nViens remplir de ta paix\nLe vide de mon coeur.\nDissipe mes ennuis\nEt toute inquiétude,\nEt que ma seule étude\nSoit de t\'aimer, Seigneur!\nEt que ma seule étude\nSoit de t\'aimer, Seigneur!\n\n\n4.\nTu me réponds, mon Dieu!\nMais encore des nuages\nMe voilent tes splendeurs,\nCéleste Vérité!\nQue ne puis-je bientôt,\nSur de plus purs rivages\nPar delà tous les âges\nContempler ta bonté!\nPar delà tous les âges\nContempler ta bonté!','<p><strong>1.</strong> </p>\n<p>O Dieu de vérité, </p>\n<p>Pour qui seul je soupire, </p>\n<p>Unis mon coeur à Toi </p>\n<p>Par de forts et doux noeuds! </p>\n<p>Je me lasse d&#39;ouïr, </p>\n<p>Je me lasse de lire, </p>\n<p>Mais non pas de te dire: </p>\n<p>C&#39;est Toi seul que je veux! </p>\n<p>Mais non pas de te dire: </p>\n<p>C&#39;est Toi seul que je veux! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Parle seul à mon coeur, </p>\n<p>Et qu&#39;aucune prudence, </p>\n<p>Qu&#39;aucun autre docteur </p>\n<p>Ne m&#39;explique tes lois; </p>\n<p>Que toute créature, </p>\n<p>En ta sainte présence, </p>\n<p>S&#39;impose le silence </p>\n<p>Et laisse agir ta voix! </p>\n<p>S&#39;impose le silence </p>\n<p>Et laisse agir ta voix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tiens-toi près de mon âme, </p>\n<p>Et, dans ma solitude, </p>\n<p>Viens remplir de ta paix </p>\n<p>Le vide de mon coeur. </p>\n<p>Dissipe mes ennuis </p>\n<p>Et toute inquiétude, </p>\n<p>Et que ma seule étude </p>\n<p>Soit de t&#39;aimer, Seigneur! </p>\n<p>Et que ma seule étude </p>\n<p>Soit de t&#39;aimer, Seigneur! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tu me réponds, mon Dieu! </p>\n<p>Mais encore des nuages </p>\n<p>Me voilent tes splendeurs, </p>\n<p>Céleste Vérité! </p>\n<p>Que ne puis-je bientôt, </p>\n<p>Sur de plus purs rivages </p>\n<p>Par delà tous les âges </p>\n<p>Contempler ta bonté! </p>\n<p>Par delà tous les âges </p>\n<p>Contempler ta bonté! </p>\n','O-Dieu-de-verite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1434,'91907297-2328-4257-a071-46c5a4eab3de',0,0,'Je M’approche De Toi',202,'202_CE_fr','1.\nJe m\'approche de Toi, (bis)\nSource de vie,  (bis)\nMon Sauveur, bénis-moi!\n\n\n2.\nFais silence en mon coeur,  (bis)\nViens et me parle,  (bis)\nO mon divin Sauveur!\n\n\n3.\nRends-moi conforme à Toi,  (bis)\nDivin Modèle,  (bis)\nMets ton image ne moi!\n\n\n4.\nRends-moi brouillant, Seigneur.  (bis)\nPour ton service,  (bis)\nQue je parte en vainqueur!\n\n\n5.\nEt, quand tu reviendras,  (bis)\nDans tes milices,  (bis)\nTu me retrouveras!','<p><strong>1.</strong> </p>\n<p>Je m&#39;approche de Toi, (bis) </p>\n<p>Source de vie,  (bis) </p>\n<p>Mon Sauveur, bénis-moi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fais silence en mon coeur,  (bis) </p>\n<p>Viens et me parle,  (bis) </p>\n<p>O mon divin Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rends-moi conforme à Toi,  (bis) </p>\n<p>Divin Modèle,  (bis) </p>\n<p>Mets ton image ne moi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Rends-moi brouillant, Seigneur.  (bis) </p>\n<p>Pour ton service,  (bis) </p>\n<p>Que je parte en vainqueur! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Et, quand tu reviendras,  (bis) </p>\n<p>Dans tes milices,  (bis) </p>\n<p>Tu me retrouveras! </p>\n','Je-M\'approche-De-Toi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1435,'8c68ffdb-dfda-40df-8fb3-b0724b3fa14d',0,0,'Jésus t’appelle, oh! viens et vois',292,'292_CE_fr','1.\nJésus t\'appelle, oh! Viens et vois\nTon Sauveur cloué sur la croix;\nPour toi, le Souverain des cieux\nVint verser son sang précieux.\n\nRefrain\nJésus sauve, Jésus sauve,\nJésus sauve aujourd\'hui,\nTous ceux qui viennent,  (bis)\nTous ceux qui viennent à Lui.  (bis 4 lignes)\n\n\n2.\nJésus t\'appelle: oh! Cette voix,\nN\'a-t-elle aucun charme pour toi?\nNe veux-tu donc pas revenir\nA Celui qui veut te bénir?\n\n\n3.\nJésus t\'appelle, Il te connaît;\nTu fus rebelle, Il le savait\nIl veut purifier ton coeur\nEt te donner le vrai bonheur.\n\n\n4.\nJésus t\'appelle, oh! Réponds-Lui;\nQue ton coeur s\'ouvre à ton Ami;\nPour te sauver, Il vint mourir,\nIl veut t\'aimer et te bénir!','<p><strong>1.</strong> </p>\n<p>Jésus t&#39;appelle, oh! Viens et vois </p>\n<p>Ton Sauveur cloué sur la croix; </p>\n<p>Pour toi, le Souverain des cieux </p>\n<p>Vint verser son sang précieux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus sauve, Jésus sauve,</strong> </p>\n<p><strong>Jésus sauve aujourd&#39;hui,</strong> </p>\n<p><strong>Tous ceux qui viennent,  (bis)</strong> </p>\n<p><strong>Tous ceux qui viennent à Lui.  (bis 4 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus t&#39;appelle: oh! Cette voix, </p>\n<p>N&#39;a-t-elle aucun charme pour toi? </p>\n<p>Ne veux-tu donc pas revenir </p>\n<p>A Celui qui veut te bénir? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus t&#39;appelle, Il te connaît; </p>\n<p>Tu fus rebelle, Il le savait </p>\n<p>Il veut purifier ton coeur </p>\n<p>Et te donner le vrai bonheur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus t&#39;appelle, oh! Réponds-Lui; </p>\n<p>Que ton coeur s&#39;ouvre à ton Ami; </p>\n<p>Pour te sauver, Il vint mourir, </p>\n<p>Il veut t&#39;aimer et te bénir! </p>\n','Jesus-t\'appelle-oh!-viens-et-vois',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1436,'22f7fab7-c1c2-4e88-98e7-3ab85ee64ec5',0,0,'La Voix Du Seigneur m’appelle',134,'134_CE_fr','1.\nLa voix du Seigneur m\'appelle:\n«Prends ta croix, et viens, suis-moi!»\nJe réponds: «Sauveur fidèle,\nMe voici, je viens à Toi!»\n\nRefrain\nJusqu\'au bout, je veux te suivre,\nDans les bons, les mauvais jours;\nÀ Toi pour mourir et vivre,\nÀ Toi, Jésus, pour toujours!\n\n2.\nMais le chemin du Calvaire\nEst étroite et périlleux,\nC\'est un chemin solitaire,\nDifficile et ténébreux.\n\n3.\nIl faut quitter ceux qu\'on aime,\nSavoir être mal jugé,\nEndurer l\'injure même,\nDu monde être méprisé.\n\n4.\nOui, perdre sa propre vie,\nConsentir à n\'être rien,\nN\'avoir qu\'une seule vie,\nAimer Jésus, le seul bien!\n\n5.\nJésus donne grâce et gloire\nPour le suivre pas à pas,\nAvec lui, joie et victoire,\nPaix et bonheur ici-bas!','<p><strong>1.</strong> </p>\n<p>La voix du Seigneur m&#39;appelle: </p>\n<p>«Prends ta croix, et viens, suis-moi!» </p>\n<p>Je réponds: «Sauveur fidèle, </p>\n<p>Me voici, je viens à Toi!» </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jusqu&#39;au bout, je veux te suivre,</strong> </p>\n<p><strong>Dans les bons, les mauvais jours;</strong> </p>\n<p><strong>À Toi pour mourir et vivre,</strong> </p>\n<p><strong>À Toi, Jésus, pour toujours!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais le chemin du Calvaire </p>\n<p>Est étroite et périlleux, </p>\n<p>C&#39;est un chemin solitaire, </p>\n<p>Difficile et ténébreux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il faut quitter ceux qu&#39;on aime, </p>\n<p>Savoir être mal jugé, </p>\n<p>Endurer l&#39;injure même, </p>\n<p>Du monde être méprisé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, perdre sa propre vie, </p>\n<p>Consentir à n&#39;être rien, </p>\n<p>N&#39;avoir qu&#39;une seule vie, </p>\n<p>Aimer Jésus, le seul bien! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Jésus donne grâce et gloire </p>\n<p>Pour le suivre pas à pas, </p>\n<p>Avec lui, joie et victoire, </p>\n<p>Paix et bonheur ici-bas! </p>\n','La-Voix-Du-Seigneur-m\'appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1437,'98296f7b-516f-4dbd-9506-c9344acc3cf5',0,0,'Sur les pas du Saint Modèle',131,'131_CE_fr','1.\nSur les pas du Saint Modèle,\nNous marchons, comptant sur Lui,\nEt son coeur toujours fidèle\nNous accorde son appui.\nIl connaît notre faiblesse;\nLui toujours demeure fort,\nEt nous livre avec largesse\nLe meilleur de son trésor.\n\n2.\nIl nous donne sa vaillance\nPour monter vers les sommets;\nSi l\'un tombe en défaillance,\nDe ce faible Il vient plus près.\nSur le bord du précipice\nIl nous tient plus fortement.\nC\'est ainsi que nul n\'y glisse\nQui sur Lui compte vraiment.\n\n3.\nAvec nous venez sans crainte\nSur les pas de ce Sauveur;\nSuivons tous la route sainte,\nCar déjà c\'est le bonheur.\nEt plus haut, plus loin des plaines,\nAvec Lui montons toujours,\nCar ses mains sont toujours pleines\nPour qui veut son grand secours.','<p><strong>1.</strong> </p>\n<p>Sur les pas du Saint Modèle, </p>\n<p>Nous marchons, comptant sur Lui, </p>\n<p>Et son coeur toujours fidèle </p>\n<p>Nous accorde son appui. </p>\n<p>Il connaît notre faiblesse; </p>\n<p>Lui toujours demeure fort, </p>\n<p>Et nous livre avec largesse </p>\n<p>Le meilleur de son trésor. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il nous donne sa vaillance </p>\n<p>Pour monter vers les sommets; </p>\n<p>Si l&#39;un tombe en défaillance, </p>\n<p>De ce faible Il vient plus près. </p>\n<p>Sur le bord du précipice </p>\n<p>Il nous tient plus fortement. </p>\n<p>C&#39;est ainsi que nul n&#39;y glisse </p>\n<p>Qui sur Lui compte vraiment. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Avec nous venez sans crainte </p>\n<p>Sur les pas de ce Sauveur; </p>\n<p>Suivons tous la route sainte, </p>\n<p>Car déjà c&#39;est le bonheur. </p>\n<p>Et plus haut, plus loin des plaines, </p>\n<p>Avec Lui montons toujours, </p>\n<p>Car ses mains sont toujours pleines </p>\n<p>Pour qui veut son grand secours. </p>\n','Sur-les-pas-du-Saint-Modele',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1438,'583df62c-934c-46e7-9d67-abd587f781c7',0,0,'Pour toi seul en qui j’espère',145,'145_CE_fr','1.\nPour Toi seule, en qui j\'espère,\nPour Toi seul, d\'un coeur joyeux,\nJe fais monter de la terre\nMon cantique vers les cieux.\nPour Toi seul,  (bis)\nOui, mon Sauveur, pour Toi seul.\n\n\n2.\nA Toi seul, sainte Victime,\nAgneau mis à mort pour moi,\nDont le sang lava mon crime,\nÀ Toi seul s\'attend ma foi.\nÀ Toi seul,  (bis)\nOui, mon Sauveur, à Toi seul.\n\n\n3.\nA Toi seul, dans la détresse,\nÀ Toi seul j\'aurais recours,\nÀ Toi seul, ma forteresse,\nLe rocher de mon secours,\nÀ Toi seul,  (bis)\nOui, mon Sauveur, à Toi seul.','<p><strong>1.</strong> </p>\n<p>Pour Toi seule, en qui j&#39;espère, </p>\n<p>Pour Toi seul, d&#39;un coeur joyeux, </p>\n<p>Je fais monter de la terre </p>\n<p>Mon cantique vers les cieux. </p>\n<p>Pour Toi seul,  (bis) </p>\n<p>Oui, mon Sauveur, pour Toi seul. </p>\n<p><br/> <strong>2.</strong></p>\n<p>A Toi seul, sainte Victime, </p>\n<p>Agneau mis à mort pour moi, </p>\n<p>Dont le sang lava mon crime, </p>\n<p>À Toi seul s&#39;attend ma foi. </p>\n<p>À Toi seul,  (bis) </p>\n<p>Oui, mon Sauveur, à Toi seul. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A Toi seul, dans la détresse, </p>\n<p>À Toi seul j&#39;aurais recours, </p>\n<p>À Toi seul, ma forteresse, </p>\n<p>Le rocher de mon secours, </p>\n<p>À Toi seul,  (bis) </p>\n<p>Oui, mon Sauveur, à Toi seul. </p>\n','Pour-toi-seul-en-qui-j\'espere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1439,'e1ed5f7c-8336-422c-bb9c-634d4e02e8b2',0,0,'Prend ma vie, elle doit être',154,'154_CE_fr','1.\nPrends ma vie, elle doit être\nA Toi seul, ô divin Maître.\nQue sur le flot de mes jours,\nTon regard brille toujours!\n\n\n2.\nQue mes mains à ton service\nS\'offrent pour le sacrifice,\nQu\'à te suivre pas à pas\nMes pieds ne faiblissent pas!\n\n\n3.\nPrends ma voix et qu\'elle chante\nTa grâce auguste et touchante;\nPar mes lèvres que ton nom\nParle aux pécheurs de pardon!\n\n\n4.\nQue mon esprit s\'illumine\nDe ta sagesse divine;\nPrends mon argent et mon or\nEt toi seul, sois mon trésor!\n\n\n5.\nQue ma volonté devienne\nLa servante de la tienne;\nFais ton trône de mon coeur:\nIl t\'appartient, bon Sauveur!\n\n\n6.\nQu\'ainsi mon amour répande\nA tes pieds son humble offrande;\nPrends-moi, dès mes premier jours!\nTout à toi seul, pour toujours!','<p><strong>1.</strong> </p>\n<p>Prends ma vie, elle doit être </p>\n<p>A Toi seul, ô divin Maître. </p>\n<p>Que sur le flot de mes jours, </p>\n<p>Ton regard brille toujours! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que mes mains à ton service </p>\n<p>S&#39;offrent pour le sacrifice, </p>\n<p>Qu&#39;à te suivre pas à pas </p>\n<p>Mes pieds ne faiblissent pas! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prends ma voix et qu&#39;elle chante </p>\n<p>Ta grâce auguste et touchante; </p>\n<p>Par mes lèvres que ton nom </p>\n<p>Parle aux pécheurs de pardon! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que mon esprit s&#39;illumine </p>\n<p>De ta sagesse divine; </p>\n<p>Prends mon argent et mon or </p>\n<p>Et toi seul, sois mon trésor! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Que ma volonté devienne </p>\n<p>La servante de la tienne; </p>\n<p>Fais ton trône de mon coeur: </p>\n<p>Il t&#39;appartient, bon Sauveur! </p>\n<p><br/> <strong>6.</strong></p>\n<p>Qu&#39;ainsi mon amour répande </p>\n<p>A tes pieds son humble offrande; </p>\n<p>Prends-moi, dès mes premier jours! </p>\n<p>Tout à toi seul, pour toujours! </p>\n','Prend-ma-vie-elle-doit-etre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1440,'cfa6eed1-b18d-4cc8-b321-54efb3f86676',0,0,'Jusqu\'à La Mort',146,'146_CE_fr','1.\nJusqu\'à la mort,\nNous te serons fidèles,\nJusqu\'à la mort,\nTu seras notre Roi;\nSous ton drapeau,\nJésus, tu nous appelles;\nNous y mourons\nEn luttant avec foi!\n\nRefrain\nJusqu\'à la mort!\nC\'est notre cri de guerre,\nLe libre cri d\'un peuple racheté;\nJusqu\'à la mort,\nNous aurons pour bannière\nTa croix sanglante,\nÔ Christ ressuscité!\n\n\n2.\nPour toi, Jésus,\nOn est heureux de vivre;\nTous les chemins\nAvec toi semblent doux.\nAgneau de Dieu,\nQui ne voudrait te suivre\nJusqu\'à la mort,\nToi qui mourus pour nous?\n\n\n3.\nJusqu\'à la mort,\nSoumis à ta puissance,\nNous voulons vivre\nEt mourir sous tes lois,\nToi qui pour nous\nPoussas l\'obéissance\nJusqu\'à la mort,\nEt la mort de la croix.\n\n\n4.\nMais, ô Sauveur!\nTu sais notre faiblesse;\nNous tomberons\nSûrement en chemin\nSi tu ne viens\nAccomplir ta promesse:\nJusqu\'à la mort,\nNous tenir par la main.\n\n\n5.\nQue ton Esprit\nNous guide et nous anime!\nEt que sa flamme,\nEmbrasant tous nos coeurs,\nNous devenions\nPar toi, sainte victime,\nSur la mort même\nUn peuple de Vainqueurs!','<p><strong>1.</strong> </p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Nous te serons fidèles, </p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Tu seras notre Roi; </p>\n<p>Sous ton drapeau, </p>\n<p>Jésus, tu nous appelles; </p>\n<p>Nous y mourons </p>\n<p>En luttant avec foi! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jusqu&#39;à la mort!</strong> </p>\n<p><strong>C&#39;est notre cri de guerre,</strong> </p>\n<p><strong>Le libre cri d&#39;un peuple racheté;</strong> </p>\n<p><strong>Jusqu&#39;à la mort,</strong> </p>\n<p><strong>Nous aurons pour bannière</strong> </p>\n<p><strong>Ta croix sanglante,</strong> </p>\n<p><strong>Ô Christ ressuscité!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour toi, Jésus, </p>\n<p>On est heureux de vivre; </p>\n<p>Tous les chemins </p>\n<p>Avec toi semblent doux. </p>\n<p>Agneau de Dieu, </p>\n<p>Qui ne voudrait te suivre </p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Toi qui mourus pour nous? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Soumis à ta puissance, </p>\n<p>Nous voulons vivre </p>\n<p>Et mourir sous tes lois, </p>\n<p>Toi qui pour nous </p>\n<p>Poussas l&#39;obéissance </p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Et la mort de la croix. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais, ô Sauveur! </p>\n<p>Tu sais notre faiblesse; </p>\n<p>Nous tomberons </p>\n<p>Sûrement en chemin </p>\n<p>Si tu ne viens </p>\n<p>Accomplir ta promesse: </p>\n<p>Jusqu&#39;à la mort, </p>\n<p>Nous tenir par la main. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Que ton Esprit </p>\n<p>Nous guide et nous anime! </p>\n<p>Et que sa flamme, </p>\n<p>Embrasant tous nos coeurs, </p>\n<p>Nous devenions </p>\n<p>Par toi, sainte victime, </p>\n<p>Sur la mort même </p>\n<p>Un peuple de Vainqueurs! </p>\n','Jusqu\'a-La-Mort',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1441,'4286cfdb-83b4-4fc6-aad7-2cdfb60cfe67',0,0,'A Tes Pieds, O Divin Maître',168,'168_CE_fr','1.\nA tes pieds, ô divin Maître,\nJe me place avec bonheur!\nEt t\'adorant, je veux être\nTon disciple, ô mon Sauveur!\nParle! J\'ai soif de t\'entendre;\nParle! Je n\'ai qu\'un désir:\nQue ta voix puissante et tendre\nA mon coeur se fasse ouïr.   (bis 4 lignes)\n\n\n2.\nFaites place, ô voix humaines,\nA la voix de mon Sauveur!\nPenseurs vains et choses vaines,\nEloignez-vous de mon coeur!\nTous les bruits, faites silence!\nToi, mon Maître, parle-moi!\nRecueille en ta présence,\nMon âme n\'entend que Toi.  (bis 4 lignes)\n\n\n3.\nParle-moi, témoin fidèle!\nDécouvre-moi le trésor\nDe ta sagesse éternelle;\nParle, ô Maître, parle encore.\nTa Parole, c\'est la vie,\nC\'est la lumière et la paix;\nA ton école bénie\nJe veux rester à jamais.  (bis)\n\n\n4.\nParle, et pour que je comprenne\nTon enseignement divin,\nDans ta grâce souveraine,\nInstruit-moi par le Esprit-Saint.\nParle et pour que j\'obéisse\nA tes ordres aussitôt,\nForme-moi pour ton service,\nPour te suivre, saint Agneau!  (bis 4 lignes)','<p><strong>1.</strong> </p>\n<p>A tes pieds, ô divin Maître, </p>\n<p>Je me place avec bonheur! </p>\n<p>Et t&#39;adorant, je veux être </p>\n<p>Ton disciple, ô mon Sauveur! </p>\n<p>Parle! J&#39;ai soif de t&#39;entendre; </p>\n<p>Parle! Je n&#39;ai qu&#39;un désir: </p>\n<p>Que ta voix puissante et tendre </p>\n<p>A mon coeur se fasse ouïr.   (bis 4 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Faites place, ô voix humaines, </p>\n<p>A la voix de mon Sauveur! </p>\n<p>Penseurs vains et choses vaines, </p>\n<p>Eloignez-vous de mon coeur! </p>\n<p>Tous les bruits, faites silence! </p>\n<p>Toi, mon Maître, parle-moi! </p>\n<p>Recueille en ta présence, </p>\n<p>Mon âme n&#39;entend que Toi.  (bis 4 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Parle-moi, témoin fidèle! </p>\n<p>Découvre-moi le trésor </p>\n<p>De ta sagesse éternelle; </p>\n<p>Parle, ô Maître, parle encore. </p>\n<p>Ta Parole, c&#39;est la vie, </p>\n<p>C&#39;est la lumière et la paix; </p>\n<p>A ton école bénie </p>\n<p>Je veux rester à jamais.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Parle, et pour que je comprenne </p>\n<p>Ton enseignement divin, </p>\n<p>Dans ta grâce souveraine, </p>\n<p>Instruit-moi par le Esprit-Saint. </p>\n<p>Parle et pour que j&#39;obéisse </p>\n<p>A tes ordres aussitôt, </p>\n<p>Forme-moi pour ton service, </p>\n<p>Pour te suivre, saint Agneau!  (bis 4 lignes) </p>\n','A-Tes-Pieds-O-Divin-Maitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1442,'acb519d9-3b81-4449-af6c-1463edd7273a',0,0,'Seigneur, sanctifie',171,'171_CE_fr','1.\nSeigneur!  Sanctifie\nNos jours, nos moments;\nFais que notre vie\nT\'honore en tout temps.\nQue de ta présence,\nAu milieu de nous,\nL\'heureuse influence\nNous pénètre tous.\n\n\n2.\nNous voulons sans cesse\nMarcher par la foi,\nEt, dans la détresse,\nRegarder à Toi.\nHeureux qui repose\nSur ton bras puissant!\nOn a toute chose\nEn te possédant.','<p><strong>1.</strong> </p>\n<p>Seigneur!  Sanctifie </p>\n<p>Nos jours, nos moments; </p>\n<p>Fais que notre vie </p>\n<p>T&#39;honore en tout temps. </p>\n<p>Que de ta présence, </p>\n<p>Au milieu de nous, </p>\n<p>L&#39;heureuse influence </p>\n<p>Nous pénètre tous. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nous voulons sans cesse </p>\n<p>Marcher par la foi, </p>\n<p>Et, dans la détresse, </p>\n<p>Regarder à Toi. </p>\n<p>Heureux qui repose </p>\n<p>Sur ton bras puissant! </p>\n<p>On a toute chose </p>\n<p>En te possédant. </p>\n','Seigneur-sanctifie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1443,'0dfad187-4a94-4fcf-b700-ea54353cb7cb',0,0,'Que faut-il faire quand l’adversaire',198,'198_CE_fr','1.\nQue faut-il faire\nQuand l\'adversaire\nSe dresse soudain devant mes pas,\nLançant ses flèches,\nOuvrants des brèches,\nM\'entraînant où je ne voudrais pas?\n\nRefrain\nDis tout à Jésus, dis tout à Jésus,\nTu ne peux porter seul tes fardeaux;\nDis tout à Jésus, dis tout à Jésus,\nC\'est lui qui donne le vrai repos.\n\n\n2.\nQuand la misère,\nL\'épreuve amère,\nSont la part, Seigneur, de ton enfant;\nQue puis-je faire,\nComment te plaire\nSur ce chemin si sombre et glissant?\n\n\n3.\nQuand dans mon âme\nLa sainte flamme\nDe ton Esprit s\'éteint lentement,\nPuis-je encore croire\nA la victoire?\nMon coeur, hélas! Est indifférent!\n\n\n4.\nQuand la faiblesse,\nQuand la tristesse\nMe voilent la face de Jésus,\nLe sanctuaire\nDe la prière\nEst-il ouvert aux pauvres vaincus?','<p><strong>1.</strong> </p>\n<p>Que faut-il faire </p>\n<p>Quand l&#39;adversaire </p>\n<p>Se dresse soudain devant mes pas, </p>\n<p>Lançant ses flèches, </p>\n<p>Ouvrants des brèches, </p>\n<p>M&#39;entraînant où je ne voudrais pas? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dis tout à Jésus, dis tout à Jésus,</strong> </p>\n<p><strong>Tu ne peux porter seul tes fardeaux;</strong> </p>\n<p><strong>Dis tout à Jésus, dis tout à Jésus,</strong> </p>\n<p><strong>C&#39;est lui qui donne le vrai repos.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand la misère, </p>\n<p>L&#39;épreuve amère, </p>\n<p>Sont la part, Seigneur, de ton enfant; </p>\n<p>Que puis-je faire, </p>\n<p>Comment te plaire </p>\n<p>Sur ce chemin si sombre et glissant? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand dans mon âme </p>\n<p>La sainte flamme </p>\n<p>De ton Esprit s&#39;éteint lentement, </p>\n<p>Puis-je encore croire </p>\n<p>A la victoire? </p>\n<p>Mon coeur, hélas! Est indifférent! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand la faiblesse, </p>\n<p>Quand la tristesse </p>\n<p>Me voilent la face de Jésus, </p>\n<p>Le sanctuaire </p>\n<p>De la prière </p>\n<p>Est-il ouvert aux pauvres vaincus? </p>\n','Que-faut-il-faire-quand-l\'adversaire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1444,'14f8870d-f5e2-4e0f-b1e2-93f8d519ed5f',0,0,'Le Tout-Puissant est mon Berger',186,'186_CE_fr','1.\nLe Tout-Puissant est mon Berger;\nSon amour me fait vivre;\nIl n\'est disette ni danger\nDont Il ne me délivre.\n\n\n2.\nChaque matin Il me conduit\nPrès des sources d\'eau vive,\nEt je m\'endors, quand vient la nuit,\nSous sa garde attentive.\n\n\n3.\nJe m\'égarais, folle brebis,\nMais tu suivis ma trace!\nEt dans tes bras tu me repris,\nAmour que rien ne lasse!\n\n\n4.\nEt maintenant, je ne veux plus\nSans toi marcher une heure;\nPour vivre et mourir, ô Jésus,\nSous ta croix je demeure.\n\n\n5.\nCar seule elle me guidera\nDans le sombre passage;\nMon âme, ô Sauveur, bénira\nTon amour d\'âge en d\'âge!','<p><strong>1.</strong> </p>\n<p>Le Tout-Puissant est mon Berger; </p>\n<p>Son amour me fait vivre; </p>\n<p>Il n&#39;est disette ni danger </p>\n<p>Dont Il ne me délivre. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chaque matin Il me conduit </p>\n<p>Près des sources d&#39;eau vive, </p>\n<p>Et je m&#39;endors, quand vient la nuit, </p>\n<p>Sous sa garde attentive. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je m&#39;égarais, folle brebis, </p>\n<p>Mais tu suivis ma trace! </p>\n<p>Et dans tes bras tu me repris, </p>\n<p>Amour que rien ne lasse! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et maintenant, je ne veux plus </p>\n<p>Sans toi marcher une heure; </p>\n<p>Pour vivre et mourir, ô Jésus, </p>\n<p>Sous ta croix je demeure. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Car seule elle me guidera </p>\n<p>Dans le sombre passage; </p>\n<p>Mon âme, ô Sauveur, bénira </p>\n<p>Ton amour d&#39;âge en d&#39;âge! </p>\n','Le-Tout-Puissant-est-mon-Berger',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1445,'62e87a34-f4e1-426d-ad13-538e9d802dca',0,0,'Souvent, Seigneur, en sa détresse',209,'209_CE_fr','1.\nSouvent, Seigneur, en sa détresse,\nUn pauvre pécheur ne t\'adresse\nPour prières que des soupirs.\nVers lui, plein d\'amour, tu t\'inclines;\nQuoiqu\'il se taise, tu devines\nLe secret de tous ses désirs.\n\n\n2.\nMais, ô Dieu! Ces élans de l\'âme,\nCe cri d\'un coeur qui te réclame,\nJe ne les trouve pas en moi:\nToujours occupé de la terre,\nQuoique de tout je désespère,\nJe ne sais m\'élever à Toi.\n\n\n3.\nMon coeur se tait comme la lyre,\nDont Saül a, dans son délire,\nInterrompu les doux accords.\nSeigneur, fais-en vibrer les cordes,\nPour que de tes miséricordes\nJe parles avec de saints transports.\n\n\n4.\nMais quoi! Ce désir que j\'éprouve,\nCe souhait qu\'en mon coeur je trouve,\nNe me viendraient-ils pas de Dieu?\nJe disais: dicte ma prière!\nEt tu m\'avais, ô tendre Père,\nDéjà dicté ce premier voeu.\n\n\n5.\nDésormais donc, ô Dieu suprême!\nPourquoi chercherais-je en moi-même\nLe prière qu\'il faut t\'offrir?\nJ\'attends toute sainte pensée\nDu ciel, d\'où descend la rosée\nQue le soleil doit recueillir.','<p><strong>1.</strong> </p>\n<p>Souvent, Seigneur, en sa détresse, </p>\n<p>Un pauvre pécheur ne t&#39;adresse </p>\n<p>Pour prières que des soupirs. </p>\n<p>Vers lui, plein d&#39;amour, tu t&#39;inclines; </p>\n<p>Quoiqu&#39;il se taise, tu devines </p>\n<p>Le secret de tous ses désirs. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais, ô Dieu! Ces élans de l&#39;âme, </p>\n<p>Ce cri d&#39;un coeur qui te réclame, </p>\n<p>Je ne les trouve pas en moi: </p>\n<p>Toujours occupé de la terre, </p>\n<p>Quoique de tout je désespère, </p>\n<p>Je ne sais m&#39;élever à Toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon coeur se tait comme la lyre, </p>\n<p>Dont Saül a, dans son délire, </p>\n<p>Interrompu les doux accords. </p>\n<p>Seigneur, fais-en vibrer les cordes, </p>\n<p>Pour que de tes miséricordes </p>\n<p>Je parles avec de saints transports. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais quoi! Ce désir que j&#39;éprouve, </p>\n<p>Ce souhait qu&#39;en mon coeur je trouve, </p>\n<p>Ne me viendraient-ils pas de Dieu? </p>\n<p>Je disais: dicte ma prière! </p>\n<p>Et tu m&#39;avais, ô tendre Père, </p>\n<p>Déjà dicté ce premier voeu. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Désormais donc, ô Dieu suprême! </p>\n<p>Pourquoi chercherais-je en moi-même </p>\n<p>Le prière qu&#39;il faut t&#39;offrir? </p>\n<p>J&#39;attends toute sainte pensée </p>\n<p>Du ciel, d&#39;où descend la rosée </p>\n<p>Que le soleil doit recueillir. </p>\n','Souvent-Seigneur-en-sa-detresse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1446,'cd6e7066-578a-4b3e-900c-9b651760b34f',0,0,'T\'aimer Jésus! Te Connaitre',188,'188_CE_fr','1.\nT\'aimer, Jésus! Te connaître,\nSe reposer sur ton sein,\nT\'avoir pour son Roi, son Maître,\nPour son breuvage et son pain;\nSavourer en paix ta grâce;\nDe ta mort, puissant Sauveur,\nGoûter la sainte efficace,\nQuelle ineffable douceur!\n\n\n2.\nO bonheur inexprimable!\nJ\'ai l\'Éternel pour berger:\nToujours tendre et secourable,\nSon coeur ne saurait changer.\nDans sa charité suprême,\nIl descendit ici-bas\nChercher sa brebis qu\'Il aime,\nEt la prendre dans ses bras.\n\n\n3.\nIl donna pour moi sa vie,\nIl me connaît pas mon nom;\nA sa table Il me convie,\nJ\'ai ma place en sa maison.\nIl veut bien, de ma faiblesse,\nDe tous mes maux s\'enquérir…\nQu\'Il est bon! Il veut sans cesse\nMe pardonner, me guérir.\n\n\n4.\nRien, ô Jésus, que ta grâce,\nRien que ton sang précieux,\nQui seul mes péchés efface,\nNe me rend saint, juste, heureux,\nNe me dites autre chose,\nSinon qu\'Il est mon Sauveur.\nL\'auteur, la source et la cause\nDe mon éternel bonheur!','<p><strong>1.</strong> </p>\n<p>T&#39;aimer, Jésus! Te connaître, </p>\n<p>Se reposer sur ton sein, </p>\n<p>T&#39;avoir pour son Roi, son Maître, </p>\n<p>Pour son breuvage et son pain; </p>\n<p>Savourer en paix ta grâce; </p>\n<p>De ta mort, puissant Sauveur, </p>\n<p>Goûter la sainte efficace, </p>\n<p>Quelle ineffable douceur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>O bonheur inexprimable! </p>\n<p>J&#39;ai l&#39;Éternel pour berger: </p>\n<p>Toujours tendre et secourable, </p>\n<p>Son coeur ne saurait changer. </p>\n<p>Dans sa charité suprême, </p>\n<p>Il descendit ici-bas </p>\n<p>Chercher sa brebis qu&#39;Il aime, </p>\n<p>Et la prendre dans ses bras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il donna pour moi sa vie, </p>\n<p>Il me connaît pas mon nom; </p>\n<p>A sa table Il me convie, </p>\n<p>J&#39;ai ma place en sa maison. </p>\n<p>Il veut bien, de ma faiblesse, </p>\n<p>De tous mes maux s&#39;enquérir… </p>\n<p>Qu&#39;Il est bon! Il veut sans cesse </p>\n<p>Me pardonner, me guérir. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Rien, ô Jésus, que ta grâce, </p>\n<p>Rien que ton sang précieux, </p>\n<p>Qui seul mes péchés efface, </p>\n<p>Ne me rend saint, juste, heureux, </p>\n<p>Ne me dites autre chose, </p>\n<p>Sinon qu&#39;Il est mon Sauveur. </p>\n<p>L&#39;auteur, la source et la cause </p>\n<p>De mon éternel bonheur! </p>\n','T\'aimer-Jesus!-Te-Connaitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1447,'e211bf52-2514-4945-ab02-9f56475e2780',0,0,'Nous voguons vers un beau rivage',236,'236_CE_fr','1.\nNous voguons vers un beau rivage\nQue Jésus nous prépara;\nNous ne craignons aucun naufrage,\nSa grâce nous conduira.\n\nRefrain\nViens avec nous, la voile est prête;\nFrère, viens, que rien ne t\'arrête.\nPlus d\'ouragan, de tempête,\nAu séjour du Dieu d\'amour.  (bis 2 lignes)\n\n\n2.\nPour notre céleste patrie\nVoici l\'heure du départ;\nA venir Jésus te convie;\nDemain il serait trop tard.\n\n\n3.\nSur cette rive hospitalière\nDieu réunit ses enfants;\nAprès les luttes de la terre,\nNous aurons grossir leurs rangs.\n\n\n4.\nDevant nous, de la cité sainte\nBientôt va s\'ouvrir le port;\nDe nos coeur bannissant la crainte,\nChantons dans un saint transport:','<p><strong>1.</strong> </p>\n<p>Nous voguons vers un beau rivage </p>\n<p>Que Jésus nous prépara; </p>\n<p>Nous ne craignons aucun naufrage, </p>\n<p>Sa grâce nous conduira. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Viens avec nous, la voile est prête;</strong> </p>\n<p><strong>Frère, viens, que rien ne t&#39;arrête.</strong> </p>\n<p><strong>Plus d&#39;ouragan, de tempête,</strong> </p>\n<p><strong>Au séjour du Dieu d&#39;amour.  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour notre céleste patrie </p>\n<p>Voici l&#39;heure du départ; </p>\n<p>A venir Jésus te convie; </p>\n<p>Demain il serait trop tard. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sur cette rive hospitalière </p>\n<p>Dieu réunit ses enfants; </p>\n<p>Après les luttes de la terre, </p>\n<p>Nous aurons grossir leurs rangs. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Devant nous, de la cité sainte </p>\n<p>Bientôt va s&#39;ouvrir le port; </p>\n<p>De nos coeur bannissant la crainte, </p>\n<p>Chantons dans un saint transport: </p>\n','Nous-voguons-vers-un-beau-rivage',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1448,'d3fb0496-e050-4b7c-bde5-faf6e42d994a',0,0,'Semons dès l’aurore',226,'226_CE_fr','1.\nSemons dès l\'aurore,  (bis)\nQuand le soleil luit,  (bis)\nEt semons encore\nLorsque vient la nuit;\nDieu peut faire éclore\nLa fleur et le fruit.\n\nRefrain\nBon courage, amis!  (bis)\nNous irons joyeux\nCueillir les épis.  (bis 3 lignes)\n\n\n2.\nSemons sur le Maître,  (bis)\nParlons du Sauveur,  (bis)\nSemons, car peut-être\nUn pauvre pécheur\nPar nous pourra naître\nAu seul vrai bonheur.\n\n\n3.\nLa tâche est immense,  (bis)\nEt dur le terrain,  (bis)\nMais, bonne espérance!\nNul travail n\'est vain;\nDe Dieu la puissance\nFait germer le grain.','<p><strong>1.</strong> </p>\n<p>Semons dès l&#39;aurore,  (bis) </p>\n<p>Quand le soleil luit,  (bis) </p>\n<p>Et semons encore </p>\n<p>Lorsque vient la nuit; </p>\n<p>Dieu peut faire éclore </p>\n<p>La fleur et le fruit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Bon courage, amis!  (bis)</strong> </p>\n<p><strong>Nous irons joyeux</strong> </p>\n<p><strong>Cueillir les épis.  (bis 3 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Semons sur le Maître,  (bis) </p>\n<p>Parlons du Sauveur,  (bis) </p>\n<p>Semons, car peut-être </p>\n<p>Un pauvre pécheur </p>\n<p>Par nous pourra naître </p>\n<p>Au seul vrai bonheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La tâche est immense,  (bis) </p>\n<p>Et dur le terrain,  (bis) </p>\n<p>Mais, bonne espérance! </p>\n<p>Nul travail n&#39;est vain; </p>\n<p>De Dieu la puissance </p>\n<p>Fait germer le grain. </p>\n','Semons-des-l\'aurore',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1449,'d4ce9088-e0cf-4d3c-b6cf-1e647dca2511',0,0,'Gémissant sous l’esclavage',237,'237_CE_fr','1.\nGémissant sous l\'esclavage,\nDans la sombre nuit,\nAmis, reprenez courage:\nDéjà l\'aube lui.\n\nRefrain\nOh! Joyeuse espérance!\nVoici la délivrance!\nNous connaissons la puissance\nDe notre grand Libérateur,\nEt nous vous disons: Confiance!\nVenez, donnez-Lui votre coeur!\n\n\n2.\nQue de larmes, que de chutes\nDans la sombre nuit!\nVoici la fin de vos luttes:\nDéjà l\'aube luit.\n\n\n3.\nPrenant en dégoût la vie,\nCoeurs découragés!\nQue pourrait votre énergie,\nPauvres naufragés?\n\n\n4.\nLuit, qui fit tomber nos chaînes\nEt nous affranchit,\nVoit aussi vos luttes vaines\nDans la sombre nuit…\n\n\nRefrain\nOh! Joyeuse espérance!\nVoici la délivrance!\nCroyez donc à la puissance\nDe notre grand Libérateur,\nEt venez avec confiance;\nVenez, donnez-Lui votre coeur!','<p><strong>1.</strong> </p>\n<p>Gémissant sous l&#39;esclavage, </p>\n<p>Dans la sombre nuit, </p>\n<p>Amis, reprenez courage: </p>\n<p>Déjà l&#39;aube lui. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Joyeuse espérance!</strong> </p>\n<p><strong>Voici la délivrance!</strong> </p>\n<p><strong>Nous connaissons la puissance</strong> </p>\n<p><strong>De notre grand Libérateur,</strong> </p>\n<p><strong>Et nous vous disons: Confiance!</strong> </p>\n<p><strong>Venez, donnez-Lui votre coeur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que de larmes, que de chutes </p>\n<p>Dans la sombre nuit! </p>\n<p>Voici la fin de vos luttes: </p>\n<p>Déjà l&#39;aube luit. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prenant en dégoût la vie, </p>\n<p>Coeurs découragés! </p>\n<p>Que pourrait votre énergie, </p>\n<p>Pauvres naufragés? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Luit, qui fit tomber nos chaînes </p>\n<p>Et nous affranchit, </p>\n<p>Voit aussi vos luttes vaines </p>\n<p>Dans la sombre nuit… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Joyeuse espérance!</strong> </p>\n<p><strong>Voici la délivrance!</strong> </p>\n<p><strong>Croyez donc à la puissance</strong> </p>\n<p><strong>De notre grand Libérateur,</strong> </p>\n<p><strong>Et venez avec confiance;</strong> </p>\n<p><strong>Venez, donnez-Lui votre coeur!</strong> </p>\n','Gemissant-sous-l\'esclavage',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1450,'9a73fe87-eed3-4332-9719-e9be147ab538',0,0,'Publiez bien haut la grande nouvelle',228,'228_CE_fr','1.\nPubliez bien haut la grande nouvelle:\nLe ciel est ouvert à tout être humain!\nLa route est tracée, un guide fidèle\nVous conduira par la main.\n\nRefrain\nLe salut pour tous, le salut par grâce,\nA tous est offert, à tous est donné.\nOh!  Venez, pécheurs,\nVenez, le temps passe,\nEt vous serez pardonnés.\n\n\n2.\nPubliez bien haut la grande nouvelle:\nLe sang de Jésus a tout effacé.\nOù que vous soyez, c\'est vous qu\'Il appelle,\nVous qui l\'avez offensé.\n\n\n3.\nPubliez bien haut la grande nouvelle!\nAu loin comme au près faites-la courir,\nPartout où se trouve une âme rebelle,\nUn pécheur à secourir.','<p><strong>1.</strong> </p>\n<p>Publiez bien haut la grande nouvelle: </p>\n<p>Le ciel est ouvert à tout être humain! </p>\n<p>La route est tracée, un guide fidèle </p>\n<p>Vous conduira par la main. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Le salut pour tous, le salut par grâce,</strong> </p>\n<p><strong>A tous est offert, à tous est donné.</strong> </p>\n<p><strong>Oh!  Venez, pécheurs,</strong> </p>\n<p><strong>Venez, le temps passe,</strong> </p>\n<p><strong>Et vous serez pardonnés.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Publiez bien haut la grande nouvelle: </p>\n<p>Le sang de Jésus a tout effacé. </p>\n<p>Où que vous soyez, c&#39;est vous qu&#39;Il appelle, </p>\n<p>Vous qui l&#39;avez offensé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Publiez bien haut la grande nouvelle! </p>\n<p>Au loin comme au près faites-la courir, </p>\n<p>Partout où se trouve une âme rebelle, </p>\n<p>Un pécheur à secourir. </p>\n','Publiez-bien-haut-la-grande-nouvelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1451,'d9daf0a7-62c8-4627-acbe-5b94729ac62f',0,0,'Regarde à Jésus, c’est la vie',249,'249_CE_fr','1.\nRegarde à Jésus, c\'est la vie,\nContemple sa mort sur la croix;\nAccepte de sa main meurtrie\nTon salut, ô mon frère, et crois.\n\n\n2.\nMets en Lui seul ta confiance,\nCrois à la force de son bras,\nIl te donnera sa puissance,\nEt par son Esprit tu vaincras.\n\n\n3.\nCette paix que ton coeur réclame\nSe trouve à l\'ombre de la croix:\nC\'est un sûr abri pour ton âme\nQue t\'offre encore le Roi des rois.\n\n\n4.\nRegarde à Jésus, c\'est la vie;\nSaisis ton salut éternel,\nC\'est à l\'âme la plus flétrie\nQu\'Il ouvre la porte du ciel.','<p><strong>1.</strong> </p>\n<p>Regarde à Jésus, c&#39;est la vie, </p>\n<p>Contemple sa mort sur la croix; </p>\n<p>Accepte de sa main meurtrie </p>\n<p>Ton salut, ô mon frère, et crois. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mets en Lui seul ta confiance, </p>\n<p>Crois à la force de son bras, </p>\n<p>Il te donnera sa puissance, </p>\n<p>Et par son Esprit tu vaincras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Cette paix que ton coeur réclame </p>\n<p>Se trouve à l&#39;ombre de la croix: </p>\n<p>C&#39;est un sûr abri pour ton âme </p>\n<p>Que t&#39;offre encore le Roi des rois. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Regarde à Jésus, c&#39;est la vie; </p>\n<p>Saisis ton salut éternel, </p>\n<p>C&#39;est à l&#39;âme la plus flétrie </p>\n<p>Qu&#39;Il ouvre la porte du ciel. </p>\n','Regarde-a-Jesus-c\'est-la-vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1452,'97c8a0b7-766a-4160-99d3-ed5bd9c3ca14',0,0,'Messagers du grand Roi',232,'232_CE_fr','1.\nMessagers du grand Roi.\nProclamons avec foi\nLa joyeuse nouvelle\nDe la vie éternelle,\nAnnonçons au pécheur\nL\'amour du Dieu Sauveur,\nMontrons la croix du Rédempteur!\nAmbassadeurs de Jésus-Christ,\nPar la puissance de l\'Esprit,\nOffrez la paix de Dieu,\nPubliez le salut\nAu monde rebelle et perdu!\n\n\n2.\nVous parlerez pour moi,\nTel est l\'ordre du Roi;\nAvec joie et courage\nDélivrons son message:\nPécheurs, répentez-vous,\nDieu vous appelle tous,\nTombez, tombez à ses genoux.\nAmbassadeurs de Jésus-Christ,\nPar la puissance de l\'Esprit,\nOffrez la paix de Dieu\nPubliez le salut\nAu monde rebelle et perdu!\n\n\n3.\nÉcoute notre voix,\nPécheur, viens à la croix\nEt là, dans la poussière,\nDis à Dieu ta misère\nJésus te recevra,\nLe sang de Golgotha,\nLe sang de Christ te lavera.\nEt racheté de Jésus-Christ,\nPar la puissance de l\'Esprit,\nTu dira: Gloire à Dieu,\nJ\'étais mort et perdu,\nIl m\'a donné son grand salut!','<p><strong>1.</strong> </p>\n<p>Messagers du grand Roi. </p>\n<p>Proclamons avec foi </p>\n<p>La joyeuse nouvelle </p>\n<p>De la vie éternelle, </p>\n<p>Annonçons au pécheur </p>\n<p>L&#39;amour du Dieu Sauveur, </p>\n<p>Montrons la croix du Rédempteur! </p>\n<p>Ambassadeurs de Jésus-Christ, </p>\n<p>Par la puissance de l&#39;Esprit, </p>\n<p>Offrez la paix de Dieu, </p>\n<p>Publiez le salut </p>\n<p>Au monde rebelle et perdu! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vous parlerez pour moi, </p>\n<p>Tel est l&#39;ordre du Roi; </p>\n<p>Avec joie et courage </p>\n<p>Délivrons son message: </p>\n<p>Pécheurs, répentez-vous, </p>\n<p>Dieu vous appelle tous, </p>\n<p>Tombez, tombez à ses genoux. </p>\n<p>Ambassadeurs de Jésus-Christ, </p>\n<p>Par la puissance de l&#39;Esprit, </p>\n<p>Offrez la paix de Dieu </p>\n<p>Publiez le salut </p>\n<p>Au monde rebelle et perdu! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Écoute notre voix, </p>\n<p>Pécheur, viens à la croix </p>\n<p>Et là, dans la poussière, </p>\n<p>Dis à Dieu ta misère </p>\n<p>Jésus te recevra, </p>\n<p>Le sang de Golgotha, </p>\n<p>Le sang de Christ te lavera. </p>\n<p>Et racheté de Jésus-Christ, </p>\n<p>Par la puissance de l&#39;Esprit, </p>\n<p>Tu dira: Gloire à Dieu, </p>\n<p>J&#39;étais mort et perdu, </p>\n<p>Il m&#39;a donné son grand salut! </p>\n','Messagers-du-grand-Roi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1453,'c1a0c68c-6341-4498-8524-4ef9340d5d9f',0,0,'Contempler mon Dieu sur son trône',256,'256_CE_fr','1.\nContempler mon Dieu sur son trône,\nVivre avec Jésus dans le ciel,\nJeter à ses pieds ma couronne,\nC\'est là le bonheur éternel.\n\nRefrain\nDans le ciel  (bis)\nVivre avec Jésus dans le ciel,\nDans le ciel  (ciel)\nC\'est là le bonheur éternel.\n\n\n2.\nUnir ma voix aux choeurs des anges,\nBénir, louer Emmanuel,\nChanter à jamais ses louanges,\nC\'est là le bonheur éternel.\n\n\n3.\nRéjouir d\'une paix infinie,\nRevoir mes amis dans le ciel,\nPosséder l\'immortelle vie,\nC\'est là le bonheur éternel.\n\n\n4.\nRetrouver les saints dans la gloire,\nPrès du trône de l\'Éternel\nCélébrer la même victoire,\nC\'est là le bonheur éternel.','<p><strong>1.</strong> </p>\n<p>Contempler mon Dieu sur son trône, </p>\n<p>Vivre avec Jésus dans le ciel, </p>\n<p>Jeter à ses pieds ma couronne, </p>\n<p>C&#39;est là le bonheur éternel. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dans le ciel  (bis)</strong> </p>\n<p><strong>Vivre avec Jésus dans le ciel,</strong> </p>\n<p><strong>Dans le ciel  (ciel)</strong> </p>\n<p><strong>C&#39;est là le bonheur éternel.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Unir ma voix aux choeurs des anges, </p>\n<p>Bénir, louer Emmanuel, </p>\n<p>Chanter à jamais ses louanges, </p>\n<p>C&#39;est là le bonheur éternel. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Réjouir d&#39;une paix infinie, </p>\n<p>Revoir mes amis dans le ciel, </p>\n<p>Posséder l&#39;immortelle vie, </p>\n<p>C&#39;est là le bonheur éternel. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Retrouver les saints dans la gloire, </p>\n<p>Près du trône de l&#39;Éternel </p>\n<p>Célébrer la même victoire, </p>\n<p>C&#39;est là le bonheur éternel. </p>\n','Contempler-mon-Dieu-sur-son-trone',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1454,'c8898b76-820c-41d2-b0f4-57e14f57de59',0,0,'Étranger sur la terre',257,'257_CE_fr','1.\nÉtranger sur la terre,\nJe marche avec bonheur\nVers la maison du Père,\nVers la maison où m\'attend le Seigneur.  (bis 2 lignes)\n\nRefrain\nOh! Quand sera-ce\nQue, face à face,\nPour toujours près de toi,\nJe te verrai, mon Roi!\n\n\n2.\nC\'est là qu\'au choeur des anges,\nPendant l\'éternité,\nJ\'unirai mes louanges\nPour donner gloire à ses fidélité.  (bis 2 lignes)\n\n\n3.\nC\'est là, devant le trône,\nQu\'avec tous les élus\nJe prendrai ma couronne,\nPour la jeter à tes pieds, ô Jésus!  (bis 2 lignes)\n\n\n4.\nAinsi, plein d\'allégresse,\nConduit par ton amour,\nJe veux marcher sans cesse\nO mon Sauveur, vers ton divin séjour.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Étranger sur la terre, </p>\n<p>Je marche avec bonheur </p>\n<p>Vers la maison du Père, </p>\n<p>Vers la maison où m&#39;attend le Seigneur.  (bis 2 lignes) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Quand sera-ce</strong> </p>\n<p><strong>Que, face à face,</strong> </p>\n<p><strong>Pour toujours près de toi,</strong> </p>\n<p><strong>Je te verrai, mon Roi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est là qu&#39;au choeur des anges, </p>\n<p>Pendant l&#39;éternité, </p>\n<p>J&#39;unirai mes louanges </p>\n<p>Pour donner gloire à ses fidélité.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est là, devant le trône, </p>\n<p>Qu&#39;avec tous les élus </p>\n<p>Je prendrai ma couronne, </p>\n<p>Pour la jeter à tes pieds, ô Jésus!  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ainsi, plein d&#39;allégresse, </p>\n<p>Conduit par ton amour, </p>\n<p>Je veux marcher sans cesse </p>\n<p>O mon Sauveur, vers ton divin séjour.  (bis 2 lignes) </p>\n','Etranger-sur-la-terre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1455,'fd2bd09c-6db2-4a1c-af26-68bb69799893',0,0,'L’aube naît, sourit et passe',261,'261_CE_fr','1.\nL\'aube naît, sourit et passe;\nDe ses feux, le Roi du jour\nInonde un instant l\'espace\nPour disparaître à son tour.\nMais, dans la nuit solennelle,\nUn désir s\'éveille en moi:\nQuand luira l\'aube éternelle?\nDivin soleil, lève-toi!\n\n\n2.\nIci-bas, toute espérance\nCache derrière elle un deuil;\nToute joie, une souffrance\nEt toute vertu, l\'orgueil.\nMais au ciel, bonheur suprême!\nAu ciel plus d\'espoirs déçus!\nJe verrai le Dieu que j\'aime,\nEt ne l\'offenserai plus.\n\n\n3.\nAh!  Déchire tous les voiles\nQui te cachent à mes yeux,\nEt d\'étoiles en étoiles\nMonteront mes chants joyeux!\nMais qu\'ici-bas j\'abandonne\nMon âme à tes douce lois,\nQu\'en attendant la couronne,\nJe sache porter la croix.','<p><strong>1.</strong> </p>\n<p>L&#39;aube naît, sourit et passe; </p>\n<p>De ses feux, le Roi du jour </p>\n<p>Inonde un instant l&#39;espace </p>\n<p>Pour disparaître à son tour. </p>\n<p>Mais, dans la nuit solennelle, </p>\n<p>Un désir s&#39;éveille en moi: </p>\n<p>Quand luira l&#39;aube éternelle? </p>\n<p>Divin soleil, lève-toi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ici-bas, toute espérance </p>\n<p>Cache derrière elle un deuil; </p>\n<p>Toute joie, une souffrance </p>\n<p>Et toute vertu, l&#39;orgueil. </p>\n<p>Mais au ciel, bonheur suprême! </p>\n<p>Au ciel plus d&#39;espoirs déçus! </p>\n<p>Je verrai le Dieu que j&#39;aime, </p>\n<p>Et ne l&#39;offenserai plus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ah!  Déchire tous les voiles </p>\n<p>Qui te cachent à mes yeux, </p>\n<p>Et d&#39;étoiles en étoiles </p>\n<p>Monteront mes chants joyeux! </p>\n<p>Mais qu&#39;ici-bas j&#39;abandonne </p>\n<p>Mon âme à tes douce lois, </p>\n<p>Qu&#39;en attendant la couronne, </p>\n<p>Je sache porter la croix. </p>\n','L\'aube-nait-sourit-et-passe',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1456,'e0ba11c8-cf16-4f31-ae2c-57281aa6f23f',0,0,'Frère, quand ton âme lassée',259,'259_CE_fr','1.\nFrère, quand ton âme est lassée,\nOh! Pense au royaume des cieux!\nÉlève, élève ta pensée\nVers ce séjour des bienheureux.\n\nRefrain\nVers les cieux,  (bis)\nOh! Pense au royaume des cieux.\nVers les cieux,  (bis)\nOh! Pense au royaume des cieux.\n\n\n2.\nCe royaume, c\'est ta patrie,\nJésus-Christ t\'en fit citoyen;\nElle ne peut être ravie,\nMarche donc en paix, ô chrétien.\n\n\n3.\nLà-haut s\'épanouit la vie\nAu sein de l\'immortalité.\nLa justice, au bonheur unie,\nFleurit toute l\'éternité.\n\nRefrain\nDans les cieux,  (bis)\nOh! Pense au royaume des cieux!\nDans les cieux,  (bis)\nOh! Pense au royaume des cieux.\n\n\n4.\nQuelques jours d\'épreuves encore,\nEt puis, traversant le tombeau,\nTu verras resplendir l\'aurore\nD\'un jour sans fin, d\'un jour nouveau.\n\nRefrain\nDans les cieux,  (bis)  etc.','<p><strong>1.</strong> </p>\n<p>Frère, quand ton âme est lassée, </p>\n<p>Oh! Pense au royaume des cieux! </p>\n<p>Élève, élève ta pensée </p>\n<p>Vers ce séjour des bienheureux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Vers les cieux,  (bis)</strong> </p>\n<p><strong>Oh! Pense au royaume des cieux.</strong> </p>\n<p><strong>Vers les cieux,  (bis)</strong> </p>\n<p><strong>Oh! Pense au royaume des cieux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ce royaume, c&#39;est ta patrie, </p>\n<p>Jésus-Christ t&#39;en fit citoyen; </p>\n<p>Elle ne peut être ravie, </p>\n<p>Marche donc en paix, ô chrétien. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Là-haut s&#39;épanouit la vie </p>\n<p>Au sein de l&#39;immortalité. </p>\n<p>La justice, au bonheur unie, </p>\n<p>Fleurit toute l&#39;éternité. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dans les cieux,  (bis)</strong> </p>\n<p><strong>Oh! Pense au royaume des cieux!</strong> </p>\n<p><strong>Dans les cieux,  (bis)</strong> </p>\n<p><strong>Oh! Pense au royaume des cieux.</strong> </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quelques jours d&#39;épreuves encore, </p>\n<p>Et puis, traversant le tombeau, </p>\n<p>Tu verras resplendir l&#39;aurore </p>\n<p>D&#39;un jour sans fin, d&#39;un jour nouveau. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dans les cieux,  (bis)  etc.</strong> </p>\n','Frere-quand-ton-ame-lassee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1457,'eb29a2d3-927f-4966-b4f3-88cf0d481556',0,0,'Voici de tes enfants',265,'265_CE_fr','1.\nVoici de tes enfants,\nSeigneur, une poignée.\nSeuls, nous ne pouvons rien\nContre tes ennemis,\nMais, au contact du mal\nNotre âme est indignée;\nNous voulons voir ta cause\nEn tous les coeur gagnée\nEt sous ton étendard\nLe monde entier soumis.\n\n\n2.\nNous avons dans nos coeurs\nUne vive espérance,\nOui, nous croyons aux temps\nOù nul ne connaîtra\nNi larmes, ni souffrance,\nEt nous voulons hâter\nCe jour de délivrance:\nO Dieu, daigne bénir\nNos voeux et nos travaux!\n\n\n3.\nViens, et nous te suivrons,\nO notre Capitaine!\nViens, et de ton Esprit\nPar Toi-même embrasés,\nUnis dans ton amour,\nNous marcherons sans peine.\nLa victoire avec Toi\nNous paraîtra certaine,\nLes sentiers les plus durs\nNous deviendrons aisés!','<p><strong>1.</strong> </p>\n<p>Voici de tes enfants, </p>\n<p>Seigneur, une poignée. </p>\n<p>Seuls, nous ne pouvons rien </p>\n<p>Contre tes ennemis, </p>\n<p>Mais, au contact du mal </p>\n<p>Notre âme est indignée; </p>\n<p>Nous voulons voir ta cause </p>\n<p>En tous les coeur gagnée </p>\n<p>Et sous ton étendard </p>\n<p>Le monde entier soumis. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nous avons dans nos coeurs </p>\n<p>Une vive espérance, </p>\n<p>Oui, nous croyons aux temps </p>\n<p>Où nul ne connaîtra </p>\n<p>Ni larmes, ni souffrance, </p>\n<p>Et nous voulons hâter </p>\n<p>Ce jour de délivrance: </p>\n<p>O Dieu, daigne bénir </p>\n<p>Nos voeux et nos travaux! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Viens, et nous te suivrons, </p>\n<p>O notre Capitaine! </p>\n<p>Viens, et de ton Esprit </p>\n<p>Par Toi-même embrasés, </p>\n<p>Unis dans ton amour, </p>\n<p>Nous marcherons sans peine. </p>\n<p>La victoire avec Toi </p>\n<p>Nous paraîtra certaine, </p>\n<p>Les sentiers les plus durs </p>\n<p>Nous deviendrons aisés! </p>\n','Voici-de-tes-enfants',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1458,'d89f092a-3706-4bcd-9647-1a1b5d518090',0,0,'Ah! Qu’il est beau de voir des frères',263,'263_CE_fr','1.\nAh! Qu\'il est beau de voir des frères\nD\'un même amour unis entre eux!\nEsprit de Dieu, tu les éclaires,\nTu les embrases de tes feux.\nLeurs chants pieux et leurs prières\nComme un encens montent aux cieux.  (bis 2 lignes)\n\n\n2.\nO Rédempteur, en ta présence,\nDans ta sainte communion,\nIls savourent la jouissance\nD\'un céleste affection.\nAussi leurs coeurs en assurance\nT\'offrent leur adoration.  (bis 2 lignes)\n\n\n3.\nDans tous le lieux la même vie\nAnime tous tes rachetés,\nPartout leur âme est réjouie\nDe tes douces gratuités.\nOui, ton Église est enrichie\nDe tes magnifiques bontés.  (bis 2 lignes)\n\n\n4.\nRépands sur nous, Dieu charitable!\nTon Esprit de grâce et de paix.\nAccueillis à la même table\nEt goûtant les mêmes bienfaits;\nQu\'un amour saint et véritable\nNous unisse en toi pour jamais.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Ah! Qu&#39;il est beau de voir des frères </p>\n<p>D&#39;un même amour unis entre eux! </p>\n<p>Esprit de Dieu, tu les éclaires, </p>\n<p>Tu les embrases de tes feux. </p>\n<p>Leurs chants pieux et leurs prières </p>\n<p>Comme un encens montent aux cieux.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>O Rédempteur, en ta présence, </p>\n<p>Dans ta sainte communion, </p>\n<p>Ils savourent la jouissance </p>\n<p>D&#39;un céleste affection. </p>\n<p>Aussi leurs coeurs en assurance </p>\n<p>T&#39;offrent leur adoration.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans tous le lieux la même vie </p>\n<p>Anime tous tes rachetés, </p>\n<p>Partout leur âme est réjouie </p>\n<p>De tes douces gratuités. </p>\n<p>Oui, ton Église est enrichie </p>\n<p>De tes magnifiques bontés.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Répands sur nous, Dieu charitable! </p>\n<p>Ton Esprit de grâce et de paix. </p>\n<p>Accueillis à la même table </p>\n<p>Et goûtant les mêmes bienfaits; </p>\n<p>Qu&#39;un amour saint et véritable </p>\n<p>Nous unisse en toi pour jamais.  (bis 2 lignes) </p>\n','Ah!-Qu\'il-est-beau-de-voir-des-freres',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1459,'4fd35035-6b60-410a-9791-5da6be40a1ef',0,0,'Oh! que ton service est aimable',268,'268_CE_fr','1.\nOh!  Que ton service est aimable,\nSeigneur, mon Dieu, mon Rédempteur!\nOh! Qu\'il m\'est cher et désirable!\nIl est ma joie et ma douceur.\nMon âme ici dans la silence\nEn adorant trouve la paix;\nEt ton Esprit, de ta présence,\nMe fait sentir les saints effets.\n\n\n2.\nPuissant Sauveur, tu te rappelles\nQue tu promis d\'être en tout lieu\nOù quelques-uns de tes fidèles\nS\'assemblent au nom de leur Dieu.\nNous sommes donc devant ta face:\nOui, tu nous vois, tu nous entends;\nAh! Que le regard de ta grâce\nRepose sur nous tes enfants!\n\n\n3.\nQuoi! Je me trouve en ta lumière!\nTes yeux, ô mon Dieu, sont sur moi;\nTon oreille entend ma prière\nEt mon chant monte jusqu\'à Toi!\nOh! Quels transports donne à mon âme\nLe sentiment de ta bonté!\nAh! Que mon coeur aussi s\'enflamme\nDes saints feux de ta charité.\n\n\n4.\nOui, dans mon âme je t\'adore,\nMon Dieu, mon Seigneur, mon Rocher;\nJe t\'ai trouvé, je veux encore\nDe ton regard me rapprocher.\nQuel autre au ciel pourrait me plaire\nQue toi, mon fidèle Sauveur?\nQuel autre pourrait sur la terre\nRépondre aux besoins de mon coeur?','<p><strong>1.</strong> </p>\n<p>Oh!  Que ton service est aimable, </p>\n<p>Seigneur, mon Dieu, mon Rédempteur! </p>\n<p>Oh! Qu&#39;il m&#39;est cher et désirable! </p>\n<p>Il est ma joie et ma douceur. </p>\n<p>Mon âme ici dans la silence </p>\n<p>En adorant trouve la paix; </p>\n<p>Et ton Esprit, de ta présence, </p>\n<p>Me fait sentir les saints effets. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Puissant Sauveur, tu te rappelles </p>\n<p>Que tu promis d&#39;être en tout lieu </p>\n<p>Où quelques-uns de tes fidèles </p>\n<p>S&#39;assemblent au nom de leur Dieu. </p>\n<p>Nous sommes donc devant ta face: </p>\n<p>Oui, tu nous vois, tu nous entends; </p>\n<p>Ah! Que le regard de ta grâce </p>\n<p>Repose sur nous tes enfants! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quoi! Je me trouve en ta lumière! </p>\n<p>Tes yeux, ô mon Dieu, sont sur moi; </p>\n<p>Ton oreille entend ma prière </p>\n<p>Et mon chant monte jusqu&#39;à Toi! </p>\n<p>Oh! Quels transports donne à mon âme </p>\n<p>Le sentiment de ta bonté! </p>\n<p>Ah! Que mon coeur aussi s&#39;enflamme </p>\n<p>Des saints feux de ta charité. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, dans mon âme je t&#39;adore, </p>\n<p>Mon Dieu, mon Seigneur, mon Rocher; </p>\n<p>Je t&#39;ai trouvé, je veux encore </p>\n<p>De ton regard me rapprocher. </p>\n<p>Quel autre au ciel pourrait me plaire </p>\n<p>Que toi, mon fidèle Sauveur? </p>\n<p>Quel autre pourrait sur la terre </p>\n<p>Répondre aux besoins de mon coeur? </p>\n','Oh!-que-ton-service-est-aimable',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1460,'414dcf4c-0be6-40a3-afa2-abd68fe58000',0,0,'De Jésus, entends-tu la voix',280,'280_CE_fr','1.\nDe Jésus, entends-tu la voix,\nDivin appel d\'amour?\nO mon frère, Il s\'adresse à toi,\nDivin appel d\'amour!\nDoux message de grâce,\nChrist tes péchés efface:\n\nRefrain\nJoyeux appel, divin appel,\nDivin appel d\'amour!  (bis 2 lignes)\n\n\n2.\nIl t\'appelle encore, ô pécheur!\nDivin appel d\'amour!\nViens, mon fils, viens à ton Sauveur:\nDivin appel d\'amour!\nSon amour te réclame,\nIl veut guérir ton âme;\n\n\n3.\nA sa voix, qui peut résister?\nDivin appel d\'amour!\nIl s\'est donné pour nous sauver,\nDivin appel d\'amour!\nO Jésus, notre Maître,\nA toi nous voulons être;','<p><strong>1.</strong> </p>\n<p>De Jésus, entends-tu la voix, </p>\n<p>Divin appel d&#39;amour? </p>\n<p>O mon frère, Il s&#39;adresse à toi, </p>\n<p>Divin appel d&#39;amour! </p>\n<p>Doux message de grâce, </p>\n<p>Christ tes péchés efface: </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Joyeux appel, divin appel,</strong> </p>\n<p><strong>Divin appel d&#39;amour!  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il t&#39;appelle encore, ô pécheur! </p>\n<p>Divin appel d&#39;amour! </p>\n<p>Viens, mon fils, viens à ton Sauveur: </p>\n<p>Divin appel d&#39;amour! </p>\n<p>Son amour te réclame, </p>\n<p>Il veut guérir ton âme; </p>\n<p><br/> <strong>3.</strong></p>\n<p>A sa voix, qui peut résister? </p>\n<p>Divin appel d&#39;amour! </p>\n<p>Il s&#39;est donné pour nous sauver, </p>\n<p>Divin appel d&#39;amour! </p>\n<p>O Jésus, notre Maître, </p>\n<p>A toi nous voulons être; </p>\n','De-Jesus-entends-tu-la-voix',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1461,'f31f4dc0-0056-4df1-9dd4-f56d866bf8b3',0,0,'Jour de Seigneur',269,'269_CE_fr','1.\nJour du Seigneur,\nJ\'ouvre mon coeur\nA ta douce lumière:\nJour solennel,\nA l\'Éternel\nConsacre ma prière.\n\n\n2.\nDieu tout-puissant,\nDieu bienfaisant,\nJ\'ai besoin de ta grâce.\nÉclaire-moi,\nSoutiens ma foi;\nJe viens chercher ta face.\n\n\n3.\nTa vérité,\nTa charité,\nBrillent dans ta Parole.\nSeule elle instruit,\nGuide et conduit\nNotre âme, et la console.\n\n\n4.\nJ\'entends ta voix;\nTes saintes lois\nNe sont pas difficiles.\nViens les graver,\nLes conserver\nDans des âmes dociles.\n\n\n5.\nQue ton Esprit,\nO Jésus-Christ,\nRègne seul en notre âme!\nQue ton amour,\nEt nuit et jour,\nL\'embrase de sa flamme!','<p><strong>1.</strong> </p>\n<p>Jour du Seigneur, </p>\n<p>J&#39;ouvre mon coeur </p>\n<p>A ta douce lumière: </p>\n<p>Jour solennel, </p>\n<p>A l&#39;Éternel </p>\n<p>Consacre ma prière. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dieu tout-puissant, </p>\n<p>Dieu bienfaisant, </p>\n<p>J&#39;ai besoin de ta grâce. </p>\n<p>Éclaire-moi, </p>\n<p>Soutiens ma foi; </p>\n<p>Je viens chercher ta face. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ta vérité, </p>\n<p>Ta charité, </p>\n<p>Brillent dans ta Parole. </p>\n<p>Seule elle instruit, </p>\n<p>Guide et conduit </p>\n<p>Notre âme, et la console. </p>\n<p><br/> <strong>4.</strong></p>\n<p>J&#39;entends ta voix; </p>\n<p>Tes saintes lois </p>\n<p>Ne sont pas difficiles. </p>\n<p>Viens les graver, </p>\n<p>Les conserver </p>\n<p>Dans des âmes dociles. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Que ton Esprit, </p>\n<p>O Jésus-Christ, </p>\n<p>Règne seul en notre âme! </p>\n<p>Que ton amour, </p>\n<p>Et nuit et jour, </p>\n<p>L&#39;embrase de sa flamme! </p>\n','Jour-de-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1462,'7e3f5c53-fce8-49ab-887b-ec5036ce8c0d',0,0,'Qui Sont Ces Gens Au Radieux Visage',318,'318_CE_fr','1.\nQui sont ces gens au radieux visage\nQue, par delà les flots tumultueux,\nJe vois là-bas, sur le rivage,\nS\'assembler pour monter aux cieux?\n\nRefrain\nDes palmes à la main,\nEt couronnés de gloire,\nIls vont chantant\nLe cantique nouveau:\n« Heureux qui par la foi\nRemporta la victoire,\nLavé dans le sang de l\'Agneau! »\n\n\n2.\nCe sont des rois, jadis pauvres esclaves,\nDont Jésus-Christ a fait tomber les fers;\nLibres enfin de leurs entraves,\nIls vont régner sur l\'univers.\n\n\n3.\nAux jours mauvais, aux heures solennelles,\nPendant l\'épreuve ou la tentation,\nToujours ils restèrent fidèles\nA leur noble vocation.\n\n\n4.\nQu\'ils sont heureux! L\'épreuve est terminée.\nDu triste mal ils ne souffriront plus,\nEt désormais leur destinée\nC\'est de régner avec Jésus!','<p><strong>1.</strong> </p>\n<p>Qui sont ces gens au radieux visage </p>\n<p>Que, par delà les flots tumultueux, </p>\n<p>Je vois là-bas, sur le rivage, </p>\n<p>S&#39;assembler pour monter aux cieux? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Des palmes à la main,</strong> </p>\n<p><strong>Et couronnés de gloire,</strong> </p>\n<p><strong>Ils vont chantant</strong> </p>\n<p><strong>Le cantique nouveau:</strong> </p>\n<p><strong>« Heureux qui par la foi</strong> </p>\n<p><strong>Remporta la victoire,</strong> </p>\n<p><strong>Lavé dans le sang de l&#39;Agneau! »</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ce sont des rois, jadis pauvres esclaves, </p>\n<p>Dont Jésus-Christ a fait tomber les fers; </p>\n<p>Libres enfin de leurs entraves, </p>\n<p>Ils vont régner sur l&#39;univers. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Aux jours mauvais, aux heures solennelles, </p>\n<p>Pendant l&#39;épreuve ou la tentation, </p>\n<p>Toujours ils restèrent fidèles </p>\n<p>A leur noble vocation. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Qu&#39;ils sont heureux! L&#39;épreuve est terminée. </p>\n<p>Du triste mal ils ne souffriront plus, </p>\n<p>Et désormais leur destinée </p>\n<p>C&#39;est de régner avec Jésus! </p>\n','Qui-Sont-Ces-Gens-Au-Radieux-Visage',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1463,'832a8a1a-8da3-4a1d-b6ee-d0622904e528',0,0,'Paix, paix parfaite',179,'179_CE_fr','1.\nPaix, paix parfaite\nEn Jésus le Sauveur,\nParfaite paix,\nMême pour moi, pécheur!\n\n\n2.\nPaix, paix parfaite!\nOù sont tous mes péchés?…\nLe sang de Christ\nLes a tous effacés!\n\n\n3.\nPaix, paix parfaite!\nAu fort de ma douleur\nL\'amour de Christ\nMet la joie en mon coeur!\n\n\n4.\nPaix, paix parfaite!\nEn face de la mort,\nCar mon Sauveur\nEst Maître de mon sort!\n\n\n5.\nPaix, paix parfaite!\nEn Christ, ô doux espoir!\nLe triste « adieu »\nSe change en « au revoir! »\n\n\n6.\nPaix, paix parfaite\nEn notre Emmanuel,\nPaix sur la terre\nEt gloire dans le ciel!','<p><strong>1.</strong> </p>\n<p>Paix, paix parfaite </p>\n<p>En Jésus le Sauveur, </p>\n<p>Parfaite paix, </p>\n<p>Même pour moi, pécheur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Paix, paix parfaite! </p>\n<p>Où sont tous mes péchés?… </p>\n<p>Le sang de Christ </p>\n<p>Les a tous effacés! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Paix, paix parfaite! </p>\n<p>Au fort de ma douleur </p>\n<p>L&#39;amour de Christ </p>\n<p>Met la joie en mon coeur! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Paix, paix parfaite! </p>\n<p>En face de la mort, </p>\n<p>Car mon Sauveur </p>\n<p>Est Maître de mon sort! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Paix, paix parfaite! </p>\n<p>En Christ, ô doux espoir! </p>\n<p>Le triste « adieu » </p>\n<p>Se change en « au revoir! » </p>\n<p><br/> <strong>6.</strong></p>\n<p>Paix, paix parfaite </p>\n<p>En notre Emmanuel, </p>\n<p>Paix sur la terre </p>\n<p>Et gloire dans le ciel! </p>\n','Paix-paix-parfaite',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1464,'579b1156-adcc-454b-9a00-f09125b1cbd1',0,0,'Plus que vainqueurs! Telle est notre devise',210,'210_CE_fr','1.\nPlus que vainqueurs!  Telle est notre devise\nPlus que vainqueurs, bien que persécutés;\nCar la victoire à la fois fut acquise\nPar le Sauveur qui nous a rachetés.  (bis 2 lignes)\n\n\n2.\nSuivons le Christ jusque sur le Calvaire;\nAyons toujours sa mort devant les yeux;\nSi nous souffrons avec Lui sur la terre,\nNous règnerons avec Lui dans les cieux.  (bis 2 lignes)\n\n\n3.\nOsons braver les injures du monde,\nPour confesser le beau nom de Jésus.\nQue sur Lui seul tout notre espoir se fonde\nEt notre espoir ne sera pas confus.  (bis 2 lignes)\n\n\n4.\nAmis, croyons au pouvoir invisible\nQue le Sauveur a caché dans sa croix;\nSaisissons-la comme une arme invincible\nPour triompher au nom du Roi des rois.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Plus que vainqueurs!  Telle est notre devise </p>\n<p>Plus que vainqueurs, bien que persécutés; </p>\n<p>Car la victoire à la fois fut acquise </p>\n<p>Par le Sauveur qui nous a rachetés.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Suivons le Christ jusque sur le Calvaire; </p>\n<p>Ayons toujours sa mort devant les yeux; </p>\n<p>Si nous souffrons avec Lui sur la terre, </p>\n<p>Nous règnerons avec Lui dans les cieux.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Osons braver les injures du monde, </p>\n<p>Pour confesser le beau nom de Jésus. </p>\n<p>Que sur Lui seul tout notre espoir se fonde </p>\n<p>Et notre espoir ne sera pas confus.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Amis, croyons au pouvoir invisible </p>\n<p>Que le Sauveur a caché dans sa croix; </p>\n<p>Saisissons-la comme une arme invincible </p>\n<p>Pour triompher au nom du Roi des rois.  (bis 2 lignes) </p>\n','Plus-que-vainqueurs!-Telle-est-notre-devise',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1465,'b480ca81-a312-4523-a3f8-82805ea794b9',0,0,'À moi, les coeurs braves',217,'217_CE_fr','1.\n« A moi les coeurs braves! »\nA dit le vainqueur\nQui rompt les entraves\nDu pauvre pécheur.\n« Noble est la carrière:\nQui veut y courir?\nEt, sous ma bannière,\nCombattre et mourir? »\n\nRefrain\nA Toi, divin Maître, mon coeur et mon bras:\nJésus, je veux être l\'un de tes soldats!\n\n\n2.\nL\'ennemi fait rage:\nJe sens ses fureurs;\nComme un bruit d\'orage,\nJ\'entends ses clameurs,\nQuand Satan déchaîne\nTous ses alliés:\nMais ce flots de haine\nExpire à tes pieds.\n\n\n3.\nMa couronne est prête:\nTu m\'as racheté!\nMa justice est faite\nDe ta sainteté.\nTa grâce infinie\nCouvre mes péchés;\nA ta croix bénie\nIls sont attachés.\n\n\n\n\n\n\n\n\n\n4.\nAprès tant de luttes,\nLassés mais vainqueurs,\nRelevés des chutes,\nGuéris des douleurs,\nGardés sous ton aile,\nNous irons goûter\nLa paix éternelle,\nEt pourrons chanter:\n\nRefrain\n« A toi la couronnes de tous les élus!\nC\'est qui leur donnes ton ciel, ô Jésus! »','<p><strong>1.</strong> </p>\n<p>« A moi les coeurs braves! » </p>\n<p>A dit le vainqueur </p>\n<p>Qui rompt les entraves </p>\n<p>Du pauvre pécheur. </p>\n<p>« Noble est la carrière: </p>\n<p>Qui veut y courir? </p>\n<p>Et, sous ma bannière, </p>\n<p>Combattre et mourir? » </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>A Toi, divin Maître, mon coeur et mon bras:</strong> </p>\n<p><strong>Jésus, je veux être l&#39;un de tes soldats!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>L&#39;ennemi fait rage: </p>\n<p>Je sens ses fureurs; </p>\n<p>Comme un bruit d&#39;orage, </p>\n<p>J&#39;entends ses clameurs, </p>\n<p>Quand Satan déchaîne </p>\n<p>Tous ses alliés: </p>\n<p>Mais ce flots de haine </p>\n<p>Expire à tes pieds. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ma couronne est prête: </p>\n<p>Tu m&#39;as racheté! </p>\n<p>Ma justice est faite </p>\n<p>De ta sainteté. </p>\n<p>Ta grâce infinie </p>\n<p>Couvre mes péchés; </p>\n<p>A ta croix bénie </p>\n<p>Ils sont attachés. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Après tant de luttes, </p>\n<p>Lassés mais vainqueurs, </p>\n<p>Relevés des chutes, </p>\n<p>Guéris des douleurs, </p>\n<p>Gardés sous ton aile, </p>\n<p>Nous irons goûter </p>\n<p>La paix éternelle, </p>\n<p>Et pourrons chanter: </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>« A toi la couronnes de tous les élus!</strong> </p>\n<p><strong>C&#39;est qui leur donnes ton ciel, ô Jésus! »</strong> </p>\n','A-moi-les-coeurs-braves',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1466,'f43ce689-d0f4-4695-a7f7-668c01c5bf8f',0,0,'Avançons-nous joyeux, toujours joyeux',235,'235_CE_fr','1.\nAvançons-nous joyeux, toujours joyeux,\nVers le pays des esprits bienheureux.\nVers la demeure où Jésus pour nous prie.\nMarchons joyeux, c\'est là notre patrie.\nAvançons-nous joyeux, toujours joyeux,\nVers le pays des esprits bienheureux.\n\n\n2.\nDes chants d\'amour retentissent aux cieux\nQuels doux concerts! Harpes des bienheureux,\nNous entendrons votre sainte harmonie\nQuand nous aurons atteint notre patrie.\nAvançons-nous joyeux, toujours joyeux,\nVers le pays des esprits bienheureux.\n\n\n3.\nTon aiguillon, ô mort, tu ne l\'as plus!\nTombeau, déjà nous ne te craignons plus;\nSur toi Jésus remporta la victoire;\nIl nous ouvrit le chemin de la gloire.\nLui-même dit: « Accourez tous joyeux\nVers le pays des esprits des bienheureux. »','<p><strong>1.</strong> </p>\n<p>Avançons-nous joyeux, toujours joyeux, </p>\n<p>Vers le pays des esprits bienheureux. </p>\n<p>Vers la demeure où Jésus pour nous prie. </p>\n<p>Marchons joyeux, c&#39;est là notre patrie. </p>\n<p>Avançons-nous joyeux, toujours joyeux, </p>\n<p>Vers le pays des esprits bienheureux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Des chants d&#39;amour retentissent aux cieux </p>\n<p>Quels doux concerts! Harpes des bienheureux, </p>\n<p>Nous entendrons votre sainte harmonie </p>\n<p>Quand nous aurons atteint notre patrie. </p>\n<p>Avançons-nous joyeux, toujours joyeux, </p>\n<p>Vers le pays des esprits bienheureux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ton aiguillon, ô mort, tu ne l&#39;as plus! </p>\n<p>Tombeau, déjà nous ne te craignons plus; </p>\n<p>Sur toi Jésus remporta la victoire; </p>\n<p>Il nous ouvrit le chemin de la gloire. </p>\n<p>Lui-même dit: « Accourez tous joyeux </p>\n<p>Vers le pays des esprits des bienheureux. » </p>\n','Avancons-nous-joyeux-toujours-joyeux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1467,'2654f76e-bab1-4b70-90a6-383953d6e33e',0,0,'Possède-tu pauvre pécheur',239,'239_CE_fr','1.\nPossèdes-tu, pauvre pécheur,\nLa vive et joyeuse espérance?\nAs-tu trouvé, plein d\'assurance,\nUn appui ferme pour ton coeur?\n\nRefrain\nPour moi, j\'ai mon Sauveur!\nMon Sauveur, mon Sauveur!\nPour moi, j\'ai mon Sauveur,\n(1) En Lui j\'ai mis ma confiance.\n(2) Oh! Quel est douce sa parole!\n(3) Sa grâce au repos me convie.\n(4) Du matin, la brillante Étoile!\n(5) Heureux qui contemple sa face!\n\n\n2.\nQuand ton esprit est abattu,\nQuand ta vaine gaieté s\'envole,\nQuelle voix alors te console?\nEt pour ami, qui donc as-tu?\n\n\n3.\nDe ton présent, de ton passé,\nQuand tu sens la triste folie,\nQuand tu prends en dégoût la vie,\nQui soutient ton coeur oppressé?\n\n\n4.\nEt quand la nuit de l\'avenir\nSur toi déjà jette son voile,\nQuand l\'horizon pour toi se voile,\nD\'où le jour te peut-il venir?\n\n\n5.\nOh! Si tu voulais dans les bras\nDe Jésus aussi prendre place!\nSi tu voulais saisir sa grâce!…\nViens à Lui, frère, et tu vivras!','<p><strong>1.</strong> </p>\n<p>Possèdes-tu, pauvre pécheur, </p>\n<p>La vive et joyeuse espérance? </p>\n<p>As-tu trouvé, plein d&#39;assurance, </p>\n<p>Un appui ferme pour ton coeur? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Pour moi, j&#39;ai mon Sauveur!</strong> </p>\n<p><strong>Mon Sauveur, mon Sauveur!</strong> </p>\n<p><strong>Pour moi, j&#39;ai mon Sauveur,</strong> </p>\n<p><strong>(1) En Lui j&#39;ai mis ma confiance.</strong> </p>\n<p><strong>(2) Oh! Quel est douce sa parole!</strong> </p>\n<p><strong>(3) Sa grâce au repos me convie.</strong> </p>\n<p><strong>(4) Du matin, la brillante Étoile!</strong> </p>\n<p><strong>(5) Heureux qui contemple sa face!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand ton esprit est abattu, </p>\n<p>Quand ta vaine gaieté s&#39;envole, </p>\n<p>Quelle voix alors te console? </p>\n<p>Et pour ami, qui donc as-tu? </p>\n<p><br/> <strong>3.</strong></p>\n<p>De ton présent, de ton passé, </p>\n<p>Quand tu sens la triste folie, </p>\n<p>Quand tu prends en dégoût la vie, </p>\n<p>Qui soutient ton coeur oppressé? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et quand la nuit de l&#39;avenir </p>\n<p>Sur toi déjà jette son voile, </p>\n<p>Quand l&#39;horizon pour toi se voile, </p>\n<p>D&#39;où le jour te peut-il venir? </p>\n<p><br/> <strong>5.</strong></p>\n<p>Oh! Si tu voulais dans les bras </p>\n<p>De Jésus aussi prendre place! </p>\n<p>Si tu voulais saisir sa grâce!… </p>\n<p>Viens à Lui, frère, et tu vivras! </p>\n','Possede-tu-pauvre-pecheur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1468,'d3ff47dd-ed68-4874-9723-332b08ef2834',0,0,'Proclamez par tout le monde',240,'240_CE_fr','1.\nProclamez par tout le monde\nQue Jésus est Roi,\nIl est Roi,  (bis)\nEt que partout on réponde,\nLe coeur plein d\'émoi:\nIl est Roi,  (bis)\nEn tout lieu faites connaître\nQu\'Il règne à jamais,\nEt que ce grand Dieu, ce Maître,\nEst le Roi de paix!\nProclamez son nom sublime!\nCriez à tous vents\nQu\'Il règne sur les abîmes\nEt les flots mouvants!\n\nRefrain\nProclamez par tout le monde\nQue Jésus est Roi,\nIl est Roi,  (bis)\nEt que partout on réponde,\nLe coeur plein d\'émoi:\nIl est Roi!  (bis)\n\n\n2.\nProclamez par tout le monde\nQue Jésus est Roi,\nIl est Roi,  (bis)\nEt que partout on réponde,\nLe coeur plein d\'émoi:\nIl est Roi!  (bis)\nA ceux que la vie accable\nDites qu\'aujourd\'hui\nIl reçoit le misérable\nQui regarde à Lui.\nA toute âme qui succombe\nDites qu\'Il est fort!\nA ceux qui vont vers la tombe,\nQu\'Il vainquit la mort.\n\n3.\nProclamez par tout le monde\nQue Jésus est Roi,\nIl est Roi,  (bis)\nEt que partout on réponde,\nLe coeur plein d\'émoi:\nIl est Roi!  (bis)\nParcourez monts et campagnes,\nVilles et déserts!\nQue l\'Esprit vous accompagne\nDans tout l\'univers.\nBravez les fureurs, la haine,\nBravez les mépris\nPour que toute langue humaine\nRépète ce cri:','<p><strong>1.</strong> </p>\n<p>Proclamez par tout le monde </p>\n<p>Que Jésus est Roi, </p>\n<p>Il est Roi,  (bis) </p>\n<p>Et que partout on réponde, </p>\n<p>Le coeur plein d&#39;émoi: </p>\n<p>Il est Roi,  (bis) </p>\n<p>En tout lieu faites connaître </p>\n<p>Qu&#39;Il règne à jamais, </p>\n<p>Et que ce grand Dieu, ce Maître, </p>\n<p>Est le Roi de paix! </p>\n<p>Proclamez son nom sublime! </p>\n<p>Criez à tous vents </p>\n<p>Qu&#39;Il règne sur les abîmes </p>\n<p>Et les flots mouvants! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Proclamez par tout le monde</strong> </p>\n<p><strong>Que Jésus est Roi,</strong> </p>\n<p><strong>Il est Roi,  (bis)</strong> </p>\n<p><strong>Et que partout on réponde,</strong> </p>\n<p><strong>Le coeur plein d&#39;émoi:</strong> </p>\n<p><strong>Il est Roi!  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Proclamez par tout le monde </p>\n<p>Que Jésus est Roi, </p>\n<p>Il est Roi,  (bis) </p>\n<p>Et que partout on réponde, </p>\n<p>Le coeur plein d&#39;émoi: </p>\n<p>Il est Roi!  (bis) </p>\n<p>A ceux que la vie accable </p>\n<p>Dites qu&#39;aujourd&#39;hui </p>\n<p>Il reçoit le misérable </p>\n<p>Qui regarde à Lui. </p>\n<p>A toute âme qui succombe </p>\n<p>Dites qu&#39;Il est fort! </p>\n<p>A ceux qui vont vers la tombe, </p>\n<p>Qu&#39;Il vainquit la mort. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Proclamez par tout le monde </p>\n<p>Que Jésus est Roi, </p>\n<p>Il est Roi,  (bis) </p>\n<p>Et que partout on réponde, </p>\n<p>Le coeur plein d&#39;émoi: </p>\n<p>Il est Roi!  (bis) </p>\n<p>Parcourez monts et campagnes, </p>\n<p>Villes et déserts! </p>\n<p>Que l&#39;Esprit vous accompagne </p>\n<p>Dans tout l&#39;univers. </p>\n<p>Bravez les fureurs, la haine, </p>\n<p>Bravez les mépris </p>\n<p>Pour que toute langue humaine </p>\n<p>Répète ce cri: </p>\n','Proclamez-par-tout-le-monde',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1469,'6ee5a7c0-1f0d-4191-983f-65e6459cae0f',0,0,'Plus de pleurs, pleurs, plus de faux sourire',253,'253_CE_fr','1.\nPlus de pleurs, plus de faux sourire\nSur l\'autre bord!  (bis)\nPlus de terreurs, plus de délire,\nPlus de luttes, plus de martyre,\nAprès la mort!  (bis)\n\nRefrain\nRepos, amour au doux séjour!\nDe ta venue, ô Christ, hâte le jour!\n\n\n2.\nPlus de foudre, plus de nuage\nSur l\'autre bord;  (bis)\nPlus de fleurs que brise l\'orage,\nPlus de bonheurs qui font naufrage,\nAprès la mort!  (bis)\n\n\n3.\nPlus d\'hiver, plus de froide bise\nSur l\'autre bord;  (bis)\nPlus d\'adieux où le coeur se brise,\nPlus d\'amis que le temps divise,\nAprès la mort!  (bis)\n\n\n4.\nBientôt, Seigneur, ce soir peut-être,\nSur l\'autre bord,  (bis)\nDe loin nous te verrons paraître\nPour nous recueillir, tendre Maître,\nAvant la mort!  (bis)','<p><strong>1.</strong> </p>\n<p>Plus de pleurs, plus de faux sourire </p>\n<p>Sur l&#39;autre bord!  (bis) </p>\n<p>Plus de terreurs, plus de délire, </p>\n<p>Plus de luttes, plus de martyre, </p>\n<p>Après la mort!  (bis) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Repos, amour au doux séjour!</strong> </p>\n<p><strong>De ta venue, ô Christ, hâte le jour!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Plus de foudre, plus de nuage </p>\n<p>Sur l&#39;autre bord;  (bis) </p>\n<p>Plus de fleurs que brise l&#39;orage, </p>\n<p>Plus de bonheurs qui font naufrage, </p>\n<p>Après la mort!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Plus d&#39;hiver, plus de froide bise </p>\n<p>Sur l&#39;autre bord;  (bis) </p>\n<p>Plus d&#39;adieux où le coeur se brise, </p>\n<p>Plus d&#39;amis que le temps divise, </p>\n<p>Après la mort!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bientôt, Seigneur, ce soir peut-être, </p>\n<p>Sur l&#39;autre bord,  (bis) </p>\n<p>De loin nous te verrons paraître </p>\n<p>Pour nous recueillir, tendre Maître, </p>\n<p>Avant la mort!  (bis) </p>\n','Plus-de-pleurs-pleurs-plus-de-faux-sourire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1470,'4ed802f7-798c-4c8a-ad08-8b0d7e003ee4',0,0,'Pour moi chrétien, la terre est un exil',258,'258_CE_fr','1.\nPour moi chrétien, la terre est un exil\nMais tout est bien.  (bis)\nIl faut marcher de péril en péril;\nMais tout est bien.  (bis)\nPourquoi les pleurs, la terreur ou l\'ennui?\nChrist est à moi demain comme aujourd\'hui;\nAu ciel bientôt je serai tout à lui.\nOui, tout est bien.  (bis)\n\n\n2.\nLarmes, travail, deuil, tristesse ici-bas\nMais tout est bien.  (bis)\nDe Canaan j\'approche à chaque pas;\nOui, tout est bien.  (bis)\nEn vain le monde et son charme trompeur\nDans ses liens veut retenir mon coeur;\nJ\'avance en paix, les yeux sur mon sauveur,\nOui, tout est bien.  (bis)\n\n\n3.\nEncore un jour, et j\'atteindrai le but;\nOui, tout est bien.  (bis)\nEt je verrai le pays du salut;\nOui, tout est bien.  (bis)\nEncore un jour, le monde va passer.\nO pèlerin, marche sans te lasser;\nBientôt en Dieu tu vas te reposer.\nOui, tout est bien.  (bis)','<p><strong>1.</strong> </p>\n<p>Pour moi chrétien, la terre est un exil </p>\n<p>Mais tout est bien.  (bis) </p>\n<p>Il faut marcher de péril en péril; </p>\n<p>Mais tout est bien.  (bis) </p>\n<p>Pourquoi les pleurs, la terreur ou l&#39;ennui? </p>\n<p>Christ est à moi demain comme aujourd&#39;hui; </p>\n<p>Au ciel bientôt je serai tout à lui. </p>\n<p>Oui, tout est bien.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Larmes, travail, deuil, tristesse ici-bas </p>\n<p>Mais tout est bien.  (bis) </p>\n<p>De Canaan j&#39;approche à chaque pas; </p>\n<p>Oui, tout est bien.  (bis) </p>\n<p>En vain le monde et son charme trompeur </p>\n<p>Dans ses liens veut retenir mon coeur; </p>\n<p>J&#39;avance en paix, les yeux sur mon sauveur, </p>\n<p>Oui, tout est bien.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Encore un jour, et j&#39;atteindrai le but; </p>\n<p>Oui, tout est bien.  (bis) </p>\n<p>Et je verrai le pays du salut; </p>\n<p>Oui, tout est bien.  (bis) </p>\n<p>Encore un jour, le monde va passer. </p>\n<p>O pèlerin, marche sans te lasser; </p>\n<p>Bientôt en Dieu tu vas te reposer. </p>\n<p>Oui, tout est bien.  (bis) </p>\n','Pour-moi-chretien-la-terre-est-un-exil',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1471,'3dacd724-7ca1-4cc5-9f71-c5cf66f572bf',0,0,'Roc séculaire, Frappé pour moi',299,'299_CE_fr','1.\nRoc séculaire, - Frappé pour moi\nSur le Calvaire, - Je viens à Toi.\nTu sais mais chutes, - O mon Sauveur!\nTu vois mes luttes – Et ma douleur.\n\nRefrain\nRoc séculaire – Frappé pour moi\nSur le Calvaire, - Je viens à Toi.\n\n\n2.\nOh! Purifie, - Lave, Seigneur,\nEt sanctifie – Mon pauvre coeur.\nMa main tremblante – Ne t\'offre rien;\nTa croix sanglante – Est mon seul Bien.\n\n\n3.\nDans la détresse, - Sois mon berger,\nMa forteresse – Dans le danger.\nEt qu\'à toute heure, - Que chaque jour\nMon coeur demeure – En ton amour.','<p><strong>1.</strong> </p>\n<p>Roc séculaire, - Frappé pour moi </p>\n<p>Sur le Calvaire, - Je viens à Toi. </p>\n<p>Tu sais mais chutes, - O mon Sauveur! </p>\n<p>Tu vois mes luttes – Et ma douleur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Roc séculaire – Frappé pour moi</strong> </p>\n<p><strong>Sur le Calvaire, - Je viens à Toi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Purifie, - Lave, Seigneur, </p>\n<p>Et sanctifie – Mon pauvre coeur. </p>\n<p>Ma main tremblante – Ne t&#39;offre rien; </p>\n<p>Ta croix sanglante – Est mon seul Bien. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans la détresse, - Sois mon berger, </p>\n<p>Ma forteresse – Dans le danger. </p>\n<p>Et qu&#39;à toute heure, - Que chaque jour </p>\n<p>Mon coeur demeure – En ton amour. </p>\n','Roc-seculaire-Frappe-pour-moi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1472,'b1163f7e-f758-4566-965a-9e5f76932e0a',0,0,'Ah! Pourquoi l’amitié',314,'314_CE_fr','1.\nAh! Pourquoi l\'amitié\nGémirait-elle encore\nSur ceux qui dans exil\nComme nous dispersés,\nD\'un jour consolateur\nOnt vu briller l\'aurore,\nEt que vers Canaan\nDieu lui-même a poussés?\nAffranchis avant nous\nDu mal qui nous dévore,\nIls ne sont pas perdus,\nIls nous ont devancés.  (bis 2 lignes)\n\n\n2.\nOh! Combien ici-bas\nPesait à leur faiblesse\nCe fardeau de chagrins,\nSur leur tête amassés!\nEt que leur pauvre coeur\nComptait avec tristesse\nTant d\'heures, tant de jours\nDans la douleur passés!\nNouveau-nés de la tombe,\nEt parés de jeunesse,\nElles ne sont pas perdus,\nIls nous ont devancés.  (bis 2 lignes)\n\n\n\n\n\n3.\nPuisse la même foi\nQui consola leur vie,\nNous ouvrir les sentiers\nQue leurs pas ont pressés,\nEt, dirigeant nos pas\nVers la sainte patrie\nOù leur bonheur s\'accroît\nDe leurs travaux passés,\nNos rendre ces objets\nDe tendresse et d\'envie,\nQui ne sont pas perdu,\nMais nous ont devancés.  (bis 2 lignes)\n\n\n4.\nQuand le bruit de tes flots,\nL\'aspect de ton rivage,\nO Jourdain! Nous diront:\n« Vos travaux ont cessés, »\nAu pays du salut,\nConquis par son courage,\nJésus nous recevra,\nTriomphants et lassés,\nPrès de ces compagnons\nD\'exil et d\'héritage,\nQui ne sont pas perdu,\nMais nous ont devancés.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Ah! Pourquoi l&#39;amitié </p>\n<p>Gémirait-elle encore </p>\n<p>Sur ceux qui dans exil </p>\n<p>Comme nous dispersés, </p>\n<p>D&#39;un jour consolateur </p>\n<p>Ont vu briller l&#39;aurore, </p>\n<p>Et que vers Canaan </p>\n<p>Dieu lui-même a poussés? </p>\n<p>Affranchis avant nous </p>\n<p>Du mal qui nous dévore, </p>\n<p>Ils ne sont pas perdus, </p>\n<p>Ils nous ont devancés.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Combien ici-bas </p>\n<p>Pesait à leur faiblesse </p>\n<p>Ce fardeau de chagrins, </p>\n<p>Sur leur tête amassés! </p>\n<p>Et que leur pauvre coeur </p>\n<p>Comptait avec tristesse </p>\n<p>Tant d&#39;heures, tant de jours </p>\n<p>Dans la douleur passés! </p>\n<p>Nouveau-nés de la tombe, </p>\n<p>Et parés de jeunesse, </p>\n<p>Elles ne sont pas perdus, </p>\n<p>Ils nous ont devancés.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Puisse la même foi </p>\n<p>Qui consola leur vie, </p>\n<p>Nous ouvrir les sentiers </p>\n<p>Que leurs pas ont pressés, </p>\n<p>Et, dirigeant nos pas </p>\n<p>Vers la sainte patrie </p>\n<p>Où leur bonheur s&#39;accroît </p>\n<p>De leurs travaux passés, </p>\n<p>Nos rendre ces objets </p>\n<p>De tendresse et d&#39;envie, </p>\n<p>Qui ne sont pas perdu, </p>\n<p>Mais nous ont devancés.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand le bruit de tes flots, </p>\n<p>L&#39;aspect de ton rivage, </p>\n<p>O Jourdain! Nous diront: </p>\n<p>« Vos travaux ont cessés, » </p>\n<p>Au pays du salut, </p>\n<p>Conquis par son courage, </p>\n<p>Jésus nous recevra, </p>\n<p>Triomphants et lassés, </p>\n<p>Près de ces compagnons </p>\n<p>D&#39;exil et d&#39;héritage, </p>\n<p>Qui ne sont pas perdu, </p>\n<p>Mais nous ont devancés.  (bis 2 lignes) </p>\n','Ah!-Pourquoi-l\'amitie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1473,'55597444-bf50-4612-a2cc-995a7e9eeeb5',0,0,'Comme une terre altérée',161,'161_CE_fr','1.\nComme une terre altérée\nSoupire après l\'eau du ciel,\nNous appelons la rosée\nDe ta grâce, Emmanuel!\n\nRefrain\nFraîches, fraîches rosées,\nDescendez sur nous tous!\nÔ divine ondées,\nVenez, arrosez-nous!\n\n2.\nDescends, ô pluie abondante,\nCoule à flots dans notre coeur,\nDonne à l\'âme languissante\nUne nouvelle fraîcheur.\n\n\n3.\nNe laisse en nous rien d\'aride\nQui ne soit fertilisé;\nQue le coeur le plus avide\nSoit pleinement arrosé!\n\n\n4.\nOui, que les désert fleurissent\nSous tes bienfaisantes aux;\nQue les lieux secs reverdissent\nEt portant des fruits nouveaux.','<p><strong>1.</strong> </p>\n<p>Comme une terre altérée </p>\n<p>Soupire après l&#39;eau du ciel, </p>\n<p>Nous appelons la rosée </p>\n<p>De ta grâce, Emmanuel! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Fraîches, fraîches rosées,</strong> </p>\n<p><strong>Descendez sur nous tous!</strong> </p>\n<p><strong>Ô divine ondées,</strong> </p>\n<p><strong>Venez, arrosez-nous!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Descends, ô pluie abondante, </p>\n<p>Coule à flots dans notre coeur, </p>\n<p>Donne à l&#39;âme languissante </p>\n<p>Une nouvelle fraîcheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ne laisse en nous rien d&#39;aride </p>\n<p>Qui ne soit fertilisé; </p>\n<p>Que le coeur le plus avide </p>\n<p>Soit pleinement arrosé! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, que les désert fleurissent </p>\n<p>Sous tes bienfaisantes aux; </p>\n<p>Que les lieux secs reverdissent </p>\n<p>Et portant des fruits nouveaux. </p>\n','Comme-une-terre-alteree',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1474,'3d93d67f-b123-4813-a266-05f70f56e0a3',0,0,'Jésus ta sainte présence',163,'163_CE_fr','1.\nJésus, ta sainte présence\nEst la source du bonheur.\nDans la joie et la souffrance\nElle est le repos du coeur.\n\nRefrain\nFais briller sur moi ta face,\nO Jésus, Agneau de Dieu!\nA tes pieds, c\'est là ma place,\nPrès de Toi, mon ciel est bleu.\n\n\n2.\nSur celui qui te contemple\nDescend un rayon divin;\nTu fais de son coeur ton temple,\nTon doigt pointe son chemin.\n\n\n3.\nAh!  Que mon âme ravie\nTe rencontre, ô mon Sauveur!\nCar ton regard c\'est la vie,\nC\'est la richesse du coeur.\n\n\n4.\nIl faut aussi ta présence\nPour ton service, ô Jésus!\nCar elle est une puissance\nPour relever les perdus!\n\n\n5.\nQuand, dans l\'étendue immense,\nO Jésus, tu paraîtras,\nC\'est encore par ta présence\nQue tu nous attireras!','<p><strong>1.</strong> </p>\n<p>Jésus, ta sainte présence </p>\n<p>Est la source du bonheur. </p>\n<p>Dans la joie et la souffrance </p>\n<p>Elle est le repos du coeur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Fais briller sur moi ta face,</strong> </p>\n<p><strong>O Jésus, Agneau de Dieu!</strong> </p>\n<p><strong>A tes pieds, c&#39;est là ma place,</strong> </p>\n<p><strong>Près de Toi, mon ciel est bleu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sur celui qui te contemple </p>\n<p>Descend un rayon divin; </p>\n<p>Tu fais de son coeur ton temple, </p>\n<p>Ton doigt pointe son chemin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ah!  Que mon âme ravie </p>\n<p>Te rencontre, ô mon Sauveur! </p>\n<p>Car ton regard c&#39;est la vie, </p>\n<p>C&#39;est la richesse du coeur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il faut aussi ta présence </p>\n<p>Pour ton service, ô Jésus! </p>\n<p>Car elle est une puissance </p>\n<p>Pour relever les perdus! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Quand, dans l&#39;étendue immense, </p>\n<p>O Jésus, tu paraîtras, </p>\n<p>C&#39;est encore par ta présence </p>\n<p>Que tu nous attireras! </p>\n','Jesus-ta-sainte-presence',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1475,'ae7efbb0-6ac6-47ee-a25e-11b213b4be23',0,0,'Je vis D’espérance',187,'187_CE_fr','1.\nJe vis d\'espérance,\nD\'amour et de foi,\nEt mon chant s\'élance\nVers Toi, divin Roi.\nGrande est ma faiblesse,\nMon indignité;\nMais dans ta tendresse,\nTu m\'as racheté.\n\nRefrain\nOui, mon coeur chante,\nMon âme est contente;\nMon Jésus est tout mon bien,\nJe ne craindrai rien!  (bis 4 lignes)\n\n\n2.\nOh! Quelle tendresse,\nQuel immense amour!\nCet amour me presse\nD\'aimer à mon tour:\nAimer, c\'est te suivre\nEt c\'est t\'obéir.\nAimer, oui, c\'est vivre\nEt s\'épanouir.\n\n\n3.\nQue le coeur en fête\nLa nuit et le jour\nToujours je reflète\nTon immense amour!\nL\'amour qui rayonne,\nTe gagnant les coeurs,\nVoilà la couronne\nAu front des vainqueurs!','<p><strong>1.</strong> </p>\n<p>Je vis d&#39;espérance, </p>\n<p>D&#39;amour et de foi, </p>\n<p>Et mon chant s&#39;élance </p>\n<p>Vers Toi, divin Roi. </p>\n<p>Grande est ma faiblesse, </p>\n<p>Mon indignité; </p>\n<p>Mais dans ta tendresse, </p>\n<p>Tu m&#39;as racheté. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, mon coeur chante,</strong> </p>\n<p><strong>Mon âme est contente;</strong> </p>\n<p><strong>Mon Jésus est tout mon bien,</strong> </p>\n<p><strong>Je ne craindrai rien!  (bis 4 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Quelle tendresse, </p>\n<p>Quel immense amour! </p>\n<p>Cet amour me presse </p>\n<p>D&#39;aimer à mon tour: </p>\n<p>Aimer, c&#39;est te suivre </p>\n<p>Et c&#39;est t&#39;obéir. </p>\n<p>Aimer, oui, c&#39;est vivre </p>\n<p>Et s&#39;épanouir. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que le coeur en fête </p>\n<p>La nuit et le jour </p>\n<p>Toujours je reflète </p>\n<p>Ton immense amour! </p>\n<p>L&#39;amour qui rayonne, </p>\n<p>Te gagnant les coeurs, </p>\n<p>Voilà la couronne </p>\n<p>Au front des vainqueurs! </p>\n','Je-vis-D\'esperance',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1476,'572e0251-9468-4170-a7f2-856f218dba08',0,0,'Oui, selon ta promesse',189,'189_CE_fr','1.\nOui, selon ta promesse,\nO Jésus, mon Sauveur,\nJ\'ai trouvé l\'allégresse\nAuprès de la douleur,\nLe rayon qui rassure\nDans la plus sombre nuit,\nUn baume à la blessure,\nDans l\'épreuve un appui;\nUn baume à la blessure,\nDans l\'épreuve un appui.\n\n\n2.\nDans le désert aride,\nEt sous un ciel brûlant,\nUne eau fraîche et limpide\nDu rocher jaillissant,\nL\'ombre de la nuée\nAbritant le chemin,\nLa céleste rosée\nQui remplace le pain.\nLa céleste rosée\nQui remplace le pain.\n\n\n3.\nJésus, mon Roi, mon Maître,\nN\'es-tu pas tout pour moi?\nLa source de mon être,\nLe rocher de ma foi,\nLe soleil qui m\'éclaire,\nLe ciel qui me sourit,\nL\'eau qui me désaltère,\nLe pain qui me nourrit!\nL\'eau qui me désaltère,\nLe pain qui me nourrit!','<p><strong>1.</strong> </p>\n<p>Oui, selon ta promesse, </p>\n<p>O Jésus, mon Sauveur, </p>\n<p>J&#39;ai trouvé l&#39;allégresse </p>\n<p>Auprès de la douleur, </p>\n<p>Le rayon qui rassure </p>\n<p>Dans la plus sombre nuit, </p>\n<p>Un baume à la blessure, </p>\n<p>Dans l&#39;épreuve un appui; </p>\n<p>Un baume à la blessure, </p>\n<p>Dans l&#39;épreuve un appui. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans le désert aride, </p>\n<p>Et sous un ciel brûlant, </p>\n<p>Une eau fraîche et limpide </p>\n<p>Du rocher jaillissant, </p>\n<p>L&#39;ombre de la nuée </p>\n<p>Abritant le chemin, </p>\n<p>La céleste rosée </p>\n<p>Qui remplace le pain. </p>\n<p>La céleste rosée </p>\n<p>Qui remplace le pain. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus, mon Roi, mon Maître, </p>\n<p>N&#39;es-tu pas tout pour moi? </p>\n<p>La source de mon être, </p>\n<p>Le rocher de ma foi, </p>\n<p>Le soleil qui m&#39;éclaire, </p>\n<p>Le ciel qui me sourit, </p>\n<p>L&#39;eau qui me désaltère, </p>\n<p>Le pain qui me nourrit! </p>\n<p>L&#39;eau qui me désaltère, </p>\n<p>Le pain qui me nourrit! </p>\n','Oui-selon-ta-promesse',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1477,'fbc380d9-f49e-4f86-9ec6-5b710cdfa2f5',0,0,'Joie au ciel!',190,'190_CE_fr','1.\nJoie au ciel!\nÉcoutez, écoutez ces cantiques:\nLe prodigue est de retour!\nIl revient, il revient,\nOuvrez-vous, saint portiques!\nIl revient, affamé de pardon et d\'amour.\nDebout sur la route,\nSon Père l\'attend:\n« C\'est lui, plus de doute,\nVoici mon enfant! »\nPlein de tendresse,\nIl court, il presse\nDans ses bras le fils repentant.\nJoie au ciel!\nÉcoutez, écoutez ces cantiques:\nLe prodigue est de retour!\n\n\n2.\nJoie au ciel!\nÉcoutez, écoutez sa prière,\nCar il pleure et se repent:\n« J\'ai péché, j\'ai péché\nContre toi, tendre Père,\nEt je n\'ose espérer d\'être encore ton enfant. »\nLe Père s\'écrie:\n« Mon fils est sauvé!\nIl vient à la vie,\nIl est retrouvé! »\nLe fils écoute,\nEt son coeur doute\nQu\'un si beau jour se soit levé.\nJoie au ciel!\nÉcoutez, écoutez sa prière,\nCar il pleure et se repent.\n\n\n3.\nJoie au ciel!\n« Apportez des vêtements de fête\nPour le voyageur lassé.\nQu\'au banquet de l\'amour\nMa demeure s\'apprête\nEt dans son chants joyeux oublions le passé!\nOui, que tout s\'efface,\nQu\'il ne reste plus\nMémoire ni trace\nDes jours disparus.\nAu fils que j\'aime,\nAujourd\'hui même,\nMon coeur et mes biens sont rendus. »\nJoie au ciel!\n« Apportez des vêtements de fête\npour le voyageur lassé. »','<p><strong>1.</strong> </p>\n<p>Joie au ciel! </p>\n<p>Écoutez, écoutez ces cantiques: </p>\n<p>Le prodigue est de retour! </p>\n<p>Il revient, il revient, </p>\n<p>Ouvrez-vous, saint portiques! </p>\n<p>Il revient, affamé de pardon et d&#39;amour. </p>\n<p>Debout sur la route, </p>\n<p>Son Père l&#39;attend: </p>\n<p>« C&#39;est lui, plus de doute, </p>\n<p>Voici mon enfant! » </p>\n<p>Plein de tendresse, </p>\n<p>Il court, il presse </p>\n<p>Dans ses bras le fils repentant. </p>\n<p>Joie au ciel! </p>\n<p>Écoutez, écoutez ces cantiques: </p>\n<p>Le prodigue est de retour! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Joie au ciel! </p>\n<p>Écoutez, écoutez sa prière, </p>\n<p>Car il pleure et se repent: </p>\n<p>« J&#39;ai péché, j&#39;ai péché </p>\n<p>Contre toi, tendre Père, </p>\n<p>Et je n&#39;ose espérer d&#39;être encore ton enfant. » </p>\n<p>Le Père s&#39;écrie: </p>\n<p>« Mon fils est sauvé! </p>\n<p>Il vient à la vie, </p>\n<p>Il est retrouvé! » </p>\n<p>Le fils écoute, </p>\n<p>Et son coeur doute </p>\n<p>Qu&#39;un si beau jour se soit levé. </p>\n<p>Joie au ciel! </p>\n<p>Écoutez, écoutez sa prière, </p>\n<p>Car il pleure et se repent. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Joie au ciel! </p>\n<p>« Apportez des vêtements de fête </p>\n<p>Pour le voyageur lassé. </p>\n<p>Qu&#39;au banquet de l&#39;amour </p>\n<p>Ma demeure s&#39;apprête </p>\n<p>Et dans son chants joyeux oublions le passé! </p>\n<p>Oui, que tout s&#39;efface, </p>\n<p>Qu&#39;il ne reste plus </p>\n<p>Mémoire ni trace </p>\n<p>Des jours disparus. </p>\n<p>Au fils que j&#39;aime, </p>\n<p>Aujourd&#39;hui même, </p>\n<p>Mon coeur et mes biens sont rendus. » </p>\n<p>Joie au ciel! </p>\n<p>« Apportez des vêtements de fête </p>\n<p>pour le voyageur lassé. » </p>\n','Joie-au-ciel!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1478,'b1daf4a8-92b8-48fa-8a4e-4cc6863773a7',0,0,'Béni soit le lien',264,'264_CE_fr','1.\nBéni soit le lien\nQui nous unit en Christ,\nLe saint amour, l\'amour divin\nQue verse en nous l\'Esprit!\n\n\n2.\nAu ciel, vers notre Dieu,\nAvec joie et ferveur,\nS\'élèvent nos chants et nos voeux,\nParfum doux au Seigneur.\n\n\n3.\nNous mettons en commun\nNos fardeaux, nos labeurs;\nEn Jésus nous ne sommes qu\'un\nDans la joie et les pleurs!\n\n\n4.\nSi nous devons bientôt\nQuitter ces lieux bénis,\nNous nous retrouverons là-haut,\nPour toujours réunis.','<p><strong>1.</strong> </p>\n<p>Béni soit le lien </p>\n<p>Qui nous unit en Christ, </p>\n<p>Le saint amour, l&#39;amour divin </p>\n<p>Que verse en nous l&#39;Esprit! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Au ciel, vers notre Dieu, </p>\n<p>Avec joie et ferveur, </p>\n<p>S&#39;élèvent nos chants et nos voeux, </p>\n<p>Parfum doux au Seigneur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous mettons en commun </p>\n<p>Nos fardeaux, nos labeurs; </p>\n<p>En Jésus nous ne sommes qu&#39;un </p>\n<p>Dans la joie et les pleurs! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si nous devons bientôt </p>\n<p>Quitter ces lieux bénis, </p>\n<p>Nous nous retrouverons là-haut, </p>\n<p>Pour toujours réunis. </p>\n','Beni-soit-le-lien',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1479,'424192be-ee6c-476e-a3a5-c0ede7daca74',0,0,'Venez, cœurs souffrants et meurtris',291,'291_CE_fr','1.\nVenez, coeurs souffrants et meurtris;\nAu Médecin de l\'âme.\nPar Jésus vous serez guéris;\nQue votre voix l\'acclame:\n\nRefrain\nNom célébré par les élus,\nAdoré par les anges,\nThème éternel de nos louanges,\nJésus!  Jésus!  Jésus!\n\n\n2.\nEn Christ nous sommes pardonnés;\nC\'est la Bonne Nouvelle.\nPar Lui les cieux nous sont donnés,\nEt la vie éternelle.\n\n\n3.\nVous tous qui souffrez isolés,\nVenez, Jésus vous aime;\nPour le troupeaux des désolés,\nIl s\'est offert Lui-même.\n\n\n4.\nD\'un seul coeur, d\'une seule voix,\nExaltons sa victoire;\nEt, dans le ciel, du Roi des rois\nNous redirons la gloire.','<p><strong>1.</strong> </p>\n<p>Venez, coeurs souffrants et meurtris; </p>\n<p>Au Médecin de l&#39;âme. </p>\n<p>Par Jésus vous serez guéris; </p>\n<p>Que votre voix l&#39;acclame: </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nom célébré par les élus,</strong> </p>\n<p><strong>Adoré par les anges,</strong> </p>\n<p><strong>Thème éternel de nos louanges,</strong> </p>\n<p><strong>Jésus!  Jésus!  Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>En Christ nous sommes pardonnés; </p>\n<p>C&#39;est la Bonne Nouvelle. </p>\n<p>Par Lui les cieux nous sont donnés, </p>\n<p>Et la vie éternelle. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vous tous qui souffrez isolés, </p>\n<p>Venez, Jésus vous aime; </p>\n<p>Pour le troupeaux des désolés, </p>\n<p>Il s&#39;est offert Lui-même. </p>\n<p><br/> <strong>4.</strong></p>\n<p>D&#39;un seul coeur, d&#39;une seule voix, </p>\n<p>Exaltons sa victoire; </p>\n<p>Et, dans le ciel, du Roi des rois </p>\n<p>Nous redirons la gloire. </p>\n','Venez-coeurs-souffrants-et-meurtris',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1480,'23318c68-f737-4c28-8e4f-8b3f7a3466b9',0,0,'Coeur fatigués et lasses du péché',294,'294_CE_fr','1.\nCoeurs fatigués et lassés du péché,\nAssez souffert! Assez cherché!\nJe puis donner la paix et le repos,\nMe charger de vos fardeaux.\n\nRefrain\nVenez à Lui…(bis)\nVenez à Jésus,\nVenez à Jésus aujourd\'hui!\n\n\n2.\nVenez tous, pécheurs condamnés par la loi,\nVenez à moi!  Venez à moi!\nVous recevrez en vos coeurs dès ce jour\nLe pardon du Dieu d\'amour.\n\n\n3.\nVous qui souffrez, par le mal asservis,\nSoyez par moi, tous affranchis,\nOh!  Venez tous, travaillés et chargés,\nEt vous serez soulagés.','<p><strong>1.</strong> </p>\n<p>Coeurs fatigués et lassés du péché, </p>\n<p>Assez souffert! Assez cherché! </p>\n<p>Je puis donner la paix et le repos, </p>\n<p>Me charger de vos fardeaux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Venez à Lui…(bis)</strong> </p>\n<p><strong>Venez à Jésus,</strong> </p>\n<p><strong>Venez à Jésus aujourd&#39;hui!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Venez tous, pécheurs condamnés par la loi, </p>\n<p>Venez à moi!  Venez à moi! </p>\n<p>Vous recevrez en vos coeurs dès ce jour </p>\n<p>Le pardon du Dieu d&#39;amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vous qui souffrez, par le mal asservis, </p>\n<p>Soyez par moi, tous affranchis, </p>\n<p>Oh!  Venez tous, travaillés et chargés, </p>\n<p>Et vous serez soulagés. </p>\n','Coeur-fatigues-et-lasses-du-peche',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1481,'5387bd40-333f-4379-b68e-e92849248fbc',0,0,'Tel que je suis, sans rien à moi',295,'295_CE_fr','1.\nTel que je suis, sans rien à moi,\nSinon ton sang, versé pour moi,\nEt ta voix qui m\'appelle à toi,\nAgneau de Dieu, je viens! Je viens!\n\n\n2.\nTel que je suis, bien vacillant,\nEn proie au doute à chaque instant,\nLutte au dehors, crainte au dedans,\nAgneau de Dieu, je viens! Je viens!\n\n\n3.\nTel que je suis, ton coeur est prêt\nA prendre le mien tel qu\'il est,\nPour tout changer, Sauveur parfait!\nAgneau de Dieu, je viens! Je viens!\n\n\n4.\nTel que je suis, ton grand amour\nA tout pardonné sans retour,\nJe veux être à toi dès ce jour;\nAgneau de Dieu, je viens! Je viens!','<p><strong>1.</strong> </p>\n<p>Tel que je suis, sans rien à moi, </p>\n<p>Sinon ton sang, versé pour moi, </p>\n<p>Et ta voix qui m&#39;appelle à toi, </p>\n<p>Agneau de Dieu, je viens! Je viens! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tel que je suis, bien vacillant, </p>\n<p>En proie au doute à chaque instant, </p>\n<p>Lutte au dehors, crainte au dedans, </p>\n<p>Agneau de Dieu, je viens! Je viens! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tel que je suis, ton coeur est prêt </p>\n<p>A prendre le mien tel qu&#39;il est, </p>\n<p>Pour tout changer, Sauveur parfait! </p>\n<p>Agneau de Dieu, je viens! Je viens! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tel que je suis, ton grand amour </p>\n<p>A tout pardonné sans retour, </p>\n<p>Je veux être à toi dès ce jour; </p>\n<p>Agneau de Dieu, je viens! Je viens! </p>\n','Tel-que-je-suis-sans-rien-a-moi',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1482,'cbcf3a05-a4ad-4de6-b8c8-74eb5e607c78',0,0,'Seigneur, Tu Donnes Ta Grâce',303,'303_CE_fr','1.\nSeigneur, tu donnes ta grâce\nAu coeur qui regarde à Toi.\nAh! Que sa douce efficace\nSe répandre aussi sur moi!\nOui, sur moi  (bis)\nSe répandre aussi sur moi!\n\n\n2.\nPère tendre et secourable,\nJe fus rebelle à ta loi;\nQuoiqu\'impur et misérable,\nOh! Pardonne et bénis-moi!\nBénis-moi!  (bis)\nOh! Pardonne et bénis-moi!\n\n\n3.\nRédempteur toujours propice,\nJe veux m\'attacher à Toi;\nJ\'ai faim, j\'ai soif de ta justice:\nJe t\'appelle, réponds-moi!\nRéponds-moi!  (bis)\nJe t\'appelle, réponds-moi!\n\n\n4.\nSaint-Esprit, souffle de vie,\nViens en mon coeur par la foi!\nDans le sang qui purifie,\nDe tout péché lave-moi!\nLave-moi!  (bis)\nDe tout péché lave-moi!  (bis)\n\n\n5.\nPar ton amour, ô bon Père,\nPar le sang versé pour moi,\nPar l\'Esprit qui seul opère,\nDieu trois fois saint, sauve-moi!\nSauve-moi,  (bis)\nDieu trois fois saint, sauve-moi!','<p><strong>1.</strong> </p>\n<p>Seigneur, tu donnes ta grâce </p>\n<p>Au coeur qui regarde à Toi. </p>\n<p>Ah! Que sa douce efficace </p>\n<p>Se répandre aussi sur moi! </p>\n<p>Oui, sur moi  (bis) </p>\n<p>Se répandre aussi sur moi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Père tendre et secourable, </p>\n<p>Je fus rebelle à ta loi; </p>\n<p>Quoiqu&#39;impur et misérable, </p>\n<p>Oh! Pardonne et bénis-moi! </p>\n<p>Bénis-moi!  (bis) </p>\n<p>Oh! Pardonne et bénis-moi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rédempteur toujours propice, </p>\n<p>Je veux m&#39;attacher à Toi; </p>\n<p>J&#39;ai faim, j&#39;ai soif de ta justice: </p>\n<p>Je t&#39;appelle, réponds-moi! </p>\n<p>Réponds-moi!  (bis) </p>\n<p>Je t&#39;appelle, réponds-moi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Saint-Esprit, souffle de vie, </p>\n<p>Viens en mon coeur par la foi! </p>\n<p>Dans le sang qui purifie, </p>\n<p>De tout péché lave-moi! </p>\n<p>Lave-moi!  (bis) </p>\n<p>De tout péché lave-moi!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Par ton amour, ô bon Père, </p>\n<p>Par le sang versé pour moi, </p>\n<p>Par l&#39;Esprit qui seul opère, </p>\n<p>Dieu trois fois saint, sauve-moi! </p>\n<p>Sauve-moi,  (bis) </p>\n<p>Dieu trois fois saint, sauve-moi! </p>\n','Seigneur-Tu-Donnes-Ta-Grace',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1483,'a0533f11-d863-499a-a1e0-4b4a33000a5a',0,0,'Je Suis à Toi, Gloire à Ton Nom Suprême',304,'304_CE_fr','1.\nJe suis à toi, gloire à ton nom suprême!\nO mon Sauveur, je fléchis sous ta loi.\nJe suis à toi, je t\'adore, je t\'aime;\nJe suis à toi.  (bis)\n\n\n2.\nJ\'errais perdu dans les sentiers du doute,\nLe vide au coeur et la mort devant moi,\nLorsque tu vins resplendir sur ma route;\nJe suis à toi!  (bis)\n\n\n3.\nJadis j\'étais sous l\'empire du monde,\nMais aujourd\'hui Jésus-Christ est mon Roi,\nTon joug est doux et ta paix est profonde;\nJe suis à toi!  (bis)\n\n\n4.\nEn te trouvant j\'ai trouvé toute chose,\nEt ce bonheur m\'est venu par la foi.\nC\'est sur ton sein qu\'en paix je me repose;\nJe suis à toi!  (bis)\n\n\n5.\nNul ne saurait m\'effacer de ton livre;\nNul ne saurait me soustraire à ta loi.\nC\'est ton regard qui fait mourir et vivre;\nJe suis à toi!  (bis)','<p><strong>1.</strong> </p>\n<p>Je suis à toi, gloire à ton nom suprême! </p>\n<p>O mon Sauveur, je fléchis sous ta loi. </p>\n<p>Je suis à toi, je t&#39;adore, je t&#39;aime; </p>\n<p>Je suis à toi.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;errais perdu dans les sentiers du doute, </p>\n<p>Le vide au coeur et la mort devant moi, </p>\n<p>Lorsque tu vins resplendir sur ma route; </p>\n<p>Je suis à toi!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jadis j&#39;étais sous l&#39;empire du monde, </p>\n<p>Mais aujourd&#39;hui Jésus-Christ est mon Roi, </p>\n<p>Ton joug est doux et ta paix est profonde; </p>\n<p>Je suis à toi!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>En te trouvant j&#39;ai trouvé toute chose, </p>\n<p>Et ce bonheur m&#39;est venu par la foi. </p>\n<p>C&#39;est sur ton sein qu&#39;en paix je me repose; </p>\n<p>Je suis à toi!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Nul ne saurait m&#39;effacer de ton livre; </p>\n<p>Nul ne saurait me soustraire à ta loi. </p>\n<p>C&#39;est ton regard qui fait mourir et vivre; </p>\n<p>Je suis à toi!  (bis) </p>\n','Je-Suis-a-Toi-Gloire-a-Ton-Nom-Supreme',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1484,'6980209d-301a-4f69-a603-eba6d04dd3b2',0,0,'Source Féconde, Salut Du Monde',305,'305_CE_fr','1.\nSource féconde, - Salut du monde,\nLe sang de Christ est répandu.\nCe divin frère, - Sur le Calvaire,\nEst mort pour l\'homme perdu.\n\nRefrain\nOui, je puis croire, - Oui, je veux croire\nQue Jésus-Christ est mort pour moi!\nSa mort sanglante – Et triomphante\nMe rend libre par la foi.\n\n\n2.\nEn Jésus joie! – il est la voie\nQui nous mène toujours au but.\nJésus pardonne, - Il n\'est personne\nQu\'Il repousse du salut.\n\n\n3.\nJour mémorable – Pour le coupable!\nLe malfaiteur près de Jésus\nTrouve sa grâce, - Sainte, efficace,\nSa part avec les élus.\n\n\n4.\nDu fils la fête – Est toujours prête;\nLe festin de noce est pour nous.\nIl nous invite, - Entrons de suite,\nGoûtons son accueil si doux.','<p><strong>1.</strong> </p>\n<p>Source féconde, - Salut du monde, </p>\n<p>Le sang de Christ est répandu. </p>\n<p>Ce divin frère, - Sur le Calvaire, </p>\n<p>Est mort pour l&#39;homme perdu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oui, je puis croire, - Oui, je veux croire</strong> </p>\n<p><strong>Que Jésus-Christ est mort pour moi!</strong> </p>\n<p><strong>Sa mort sanglante – Et triomphante</strong> </p>\n<p><strong>Me rend libre par la foi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>En Jésus joie! – il est la voie </p>\n<p>Qui nous mène toujours au but. </p>\n<p>Jésus pardonne, - Il n&#39;est personne </p>\n<p>Qu&#39;Il repousse du salut. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jour mémorable – Pour le coupable! </p>\n<p>Le malfaiteur près de Jésus </p>\n<p>Trouve sa grâce, - Sainte, efficace, </p>\n<p>Sa part avec les élus. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Du fils la fête – Est toujours prête; </p>\n<p>Le festin de noce est pour nous. </p>\n<p>Il nous invite, - Entrons de suite, </p>\n<p>Goûtons son accueil si doux. </p>\n','Source-Feconde-Salut-Du-Monde',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1485,'efb34441-07d4-4efb-b8ef-d67a2f75be96',0,0,'Voici Jésus, notre Sauveur',309,'309_CE_fr','1.\nVoici Jésus, notre Sauveur,\nQui nous ouvre une porte.\n« Suis-moi! »  dit-Il\nA tout pécheur,\nDe sa voix tendre et forte.\n\nRefrain\nSeigneur Jésus, j\'espère en Toi,\nLa porte est ouverte pour moi,\nPour moi, pour moi, est ouverte pour moi!\n\n\n2.\nLa porte est ouverte à tous ceux\nQui cherche paix et joie;\nElle introduit les malheureux\nSur la céleste voie.\n\n\n3.\nLa sainte croix du Rédempteur\nVient éclairer la route;\nSous cette croix, plus de terreur,\nD\'angoisse, ni de doute.\n\n\n4.\nRépondons au divin appel\nAvec reconnaissance,\nEt vers le séjour éternel\nMarchons en assurance!','<p><strong>1.</strong> </p>\n<p>Voici Jésus, notre Sauveur, </p>\n<p>Qui nous ouvre une porte. </p>\n<p>« Suis-moi! »  dit-Il </p>\n<p>A tout pécheur, </p>\n<p>De sa voix tendre et forte. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Seigneur Jésus, j&#39;espère en Toi,</strong> </p>\n<p><strong>La porte est ouverte pour moi,</strong> </p>\n<p><strong>Pour moi, pour moi, est ouverte pour moi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La porte est ouverte à tous ceux </p>\n<p>Qui cherche paix et joie; </p>\n<p>Elle introduit les malheureux </p>\n<p>Sur la céleste voie. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La sainte croix du Rédempteur </p>\n<p>Vient éclairer la route; </p>\n<p>Sous cette croix, plus de terreur, </p>\n<p>D&#39;angoisse, ni de doute. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Répondons au divin appel </p>\n<p>Avec reconnaissance, </p>\n<p>Et vers le séjour éternel </p>\n<p>Marchons en assurance! </p>\n','Voici-Jesus-notre-Sauveur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1486,'17ae7708-1b3d-4149-a8b5-59d876f4489f',0,0,'Au pied de la croix sanglante',142,'142_CE_fr','1.\nAu pied de la croix sanglante,\nOù tu t\'es donné pour moi,\nMon âme émue et tremblante,\nÔ Jésus, se livre à Toi.\n\nRefrain\nLe parfait bonheur, (bis) C\'est de mettre\nTout mon être – A tes pieds, Seigneur!\n\n\n2.\nMe voici pour ton service,\nJe ne garde rien pour moi,\nSur l\'autel du sacrifice\nJe me place par la foi.\n\n\n3.\nPrends, Seigneur, ma vie entière,\nMets sur moi ton divin sceau;\nFais d\'un fils de la poussière,\nUn enfant du Dieu très haut.\n\n\n4.\nA la gloire, aux biens du monde,\nJe renonce pour jamais;\nQue le Saint-Esprit m\'inonde\nDe ta joie et de ta paix.\n\n\n5.\nSi ma faiblesse est bien grande,\nTa force est plus grande encore,\nÔ Jésus, qu\'elle me rende\nFidèle jusqu\'à la mort!\n\n\n6.\nO félicité suprême!\nTa grâce est mon bouclier,\nEt je t\'appartiens, je t\'aime,\nToi qui m\'aimas le premier.','<p><strong>1.</strong> </p>\n<p>Au pied de la croix sanglante, </p>\n<p>Où tu t&#39;es donné pour moi, </p>\n<p>Mon âme émue et tremblante, </p>\n<p>Ô Jésus, se livre à Toi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Le parfait bonheur, (bis) C&#39;est de mettre</strong> </p>\n<p><strong>Tout mon être – A tes pieds, Seigneur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Me voici pour ton service, </p>\n<p>Je ne garde rien pour moi, </p>\n<p>Sur l&#39;autel du sacrifice </p>\n<p>Je me place par la foi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prends, Seigneur, ma vie entière, </p>\n<p>Mets sur moi ton divin sceau; </p>\n<p>Fais d&#39;un fils de la poussière, </p>\n<p>Un enfant du Dieu très haut. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A la gloire, aux biens du monde, </p>\n<p>Je renonce pour jamais; </p>\n<p>Que le Saint-Esprit m&#39;inonde </p>\n<p>De ta joie et de ta paix. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Si ma faiblesse est bien grande, </p>\n<p>Ta force est plus grande encore, </p>\n<p>Ô Jésus, qu&#39;elle me rende </p>\n<p>Fidèle jusqu&#39;à la mort! </p>\n<p><br/> <strong>6.</strong></p>\n<p>O félicité suprême! </p>\n<p>Ta grâce est mon bouclier, </p>\n<p>Et je t&#39;appartiens, je t&#39;aime, </p>\n<p>Toi qui m&#39;aimas le premier. </p>\n','Au-pied-de-la-croix-sanglante',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1487,'993a279f-922f-491c-977c-2cd4d5b9bdbe',0,0,'Je la connais cette joie excellente',184,'184_CE_fr','1.\nJe la connais cette joie excellente,\nQue ton Esprit, Jésus, met dans un coeur;\nJe suis heureux, oui, mon âme est contente\nPuisque je sais qu\'en toi j\'ai mon Sauveur.\n\n\n2.\nTu m\'as aimé, moi, vile créature,\nJusqu\'à t\'offrir en victime pour moi;\nTon propre sans a lavé ma souillure,\nEt par ta mort je suis vivant pour toi.\n\n\n3.\nQue puis-je donc désirer sur la terre,\nPuisque je suis l\'objet de ton amour;\nPuisque ta grâce, ô Sauveur débonnaire,\nDès le matin me prévient chaque jour?\n\n\n4.\nAh! Que mon âme, en parcourant sa voie,\nS\'égaye, ô Dieu, dans ta communion,\nEt que mon coeur, plein de force en ta joie,\nDe ton Esprit garde en paix l\'onction!','<p><strong>1.</strong> </p>\n<p>Je la connais cette joie excellente, </p>\n<p>Que ton Esprit, Jésus, met dans un coeur; </p>\n<p>Je suis heureux, oui, mon âme est contente </p>\n<p>Puisque je sais qu&#39;en toi j&#39;ai mon Sauveur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu m&#39;as aimé, moi, vile créature, </p>\n<p>Jusqu&#39;à t&#39;offrir en victime pour moi; </p>\n<p>Ton propre sans a lavé ma souillure, </p>\n<p>Et par ta mort je suis vivant pour toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que puis-je donc désirer sur la terre, </p>\n<p>Puisque je suis l&#39;objet de ton amour; </p>\n<p>Puisque ta grâce, ô Sauveur débonnaire, </p>\n<p>Dès le matin me prévient chaque jour? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ah! Que mon âme, en parcourant sa voie, </p>\n<p>S&#39;égaye, ô Dieu, dans ta communion, </p>\n<p>Et que mon coeur, plein de force en ta joie, </p>\n<p>De ton Esprit garde en paix l&#39;onction! </p>\n','Je-la-connais-cette-joie-excellente',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1488,'72e08ab3-1d78-45cb-8077-6dfd082faa38',0,0,'Le Cri De Mon âme',204,'204_CE_fr','1.\nLe cri de mon âme\nS\'élève vers Toi;\nElle te réclame,\nJésus, pour son Roi.\nTon joug est facile,\nTon fardeau léger;\nSur mon coeur docile,\nRègne, ô Bon Berger!\n\n\n2.\nTrop longtemps le monde,\nTrompa mes désirs.\nChangeants comme l\'onde\nSont tous ses plaisirs.\nSa joie est frivole,\nTout est vanité;\nSeule, ta Parole\nEst la vérité.\n\n\n3.\nParole éternelle,\nO Christ Rédempteur,\nPrends-moi sous ton aile,\nHabite en mon coeur.\nDans la nuit profonde,\nTiens-moi par la main;\nLumière du monde\nLuis sur mon chemin!\n\n\n4.\nSource de l\'eau vive,\nPain venu des cieux,\nQue par Toi je vive\nPaisible et joyeux!\nQuand luira l\'aurore\nDu jour éternel,\nQue je vive encore\nPour Toi, dans le ciel!','<p><strong>1.</strong> </p>\n<p>Le cri de mon âme </p>\n<p>S&#39;élève vers Toi; </p>\n<p>Elle te réclame, </p>\n<p>Jésus, pour son Roi. </p>\n<p>Ton joug est facile, </p>\n<p>Ton fardeau léger; </p>\n<p>Sur mon coeur docile, </p>\n<p>Règne, ô Bon Berger! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Trop longtemps le monde, </p>\n<p>Trompa mes désirs. </p>\n<p>Changeants comme l&#39;onde </p>\n<p>Sont tous ses plaisirs. </p>\n<p>Sa joie est frivole, </p>\n<p>Tout est vanité; </p>\n<p>Seule, ta Parole </p>\n<p>Est la vérité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Parole éternelle, </p>\n<p>O Christ Rédempteur, </p>\n<p>Prends-moi sous ton aile, </p>\n<p>Habite en mon coeur. </p>\n<p>Dans la nuit profonde, </p>\n<p>Tiens-moi par la main; </p>\n<p>Lumière du monde </p>\n<p>Luis sur mon chemin! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Source de l&#39;eau vive, </p>\n<p>Pain venu des cieux, </p>\n<p>Que par Toi je vive </p>\n<p>Paisible et joyeux! </p>\n<p>Quand luira l&#39;aurore </p>\n<p>Du jour éternel, </p>\n<p>Que je vive encore </p>\n<p>Pour Toi, dans le ciel! </p>\n','Le-Cri-De-Mon-ame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1489,'a5282ecd-9619-41d0-a1ee-2157f5310ba8',0,0,'Choeur des bienheureux',229,'229_CE_fr','1.\nChoeur des bienheureux,\nCélestes armées,\nChantez, voici l\'enfant perdu!\nLes portes du ciel\nNe sont fermées,\nIl revient, le fils attendu!\n\nRefrain\nGloire, gloire à Dieu dans les hauts cieux!\nQuels doux accords retentissent!\nQue nos voix s\'unissent\nA ces chants joyeux:\n\n\n2.\n« J\'ai trouvé mon fils! »\nDit le tendre Père\nEn l\'embrassant avec amour.\n« D\'un manteau royal\nCouvrez sa misère,\nEt que tout chante son retour. »\n\n\n3.\nFrères, accourez\nAu Dieu qui vous aime!\nDebout encore sur le seuil,\nIl vous fait entendre\nUn appel suprême\nEt vous réserve un doux accueil!','<p><strong>1.</strong> </p>\n<p>Choeur des bienheureux, </p>\n<p>Célestes armées, </p>\n<p>Chantez, voici l&#39;enfant perdu! </p>\n<p>Les portes du ciel </p>\n<p>Ne sont fermées, </p>\n<p>Il revient, le fils attendu! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire, gloire à Dieu dans les hauts cieux!</strong> </p>\n<p><strong>Quels doux accords retentissent!</strong> </p>\n<p><strong>Que nos voix s&#39;unissent</strong> </p>\n<p><strong>A ces chants joyeux:</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>« J&#39;ai trouvé mon fils! » </p>\n<p>Dit le tendre Père </p>\n<p>En l&#39;embrassant avec amour. </p>\n<p>« D&#39;un manteau royal </p>\n<p>Couvrez sa misère, </p>\n<p>Et que tout chante son retour. » </p>\n<p><br/> <strong>3.</strong></p>\n<p>Frères, accourez </p>\n<p>Au Dieu qui vous aime! </p>\n<p>Debout encore sur le seuil, </p>\n<p>Il vous fait entendre </p>\n<p>Un appel suprême </p>\n<p>Et vous réserve un doux accueil! </p>\n','Choeur-des-bienheureux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1490,'992483af-918b-4f2e-b2a0-6ae25b5fef08',0,0,'Ô Seigneur Eternel',244,'244_CE_fr','1.\nO Seigneur Éternel,\nUne nouvelle année\nPar ta grande bonté\nNous est encore donné.\nAh! Donne-nous aussi\nD\'y vivre par la foi,\nEt de la consacrer\nUniquement à Toi.\n\n\n2.\nQue ce soit pour nous tous\nUn an de bienveillance!\nDe grâce, de progrès,\nD\'amour, de délivrance!\nQue notre âme, docile\nA ta puissante voix,\nJésus, trouve la paix\nA l\'ombre de ta croix!\n\n\n3.\nNos jours sont en tes mains,\nNotre course est bornée,\nEt plusieurs sont entrés\nDans leur dernière année.\nVeillons donc et prions,\nEt s\'il faut déloger,\nNous irons, pleins de joie\nAuprès du Bon Berger.\n\n\n4.\nPuissions-nous, rachetés\nPar ton grand sacrifice,\nScellés de ton Esprit,\nCouverts de ta justice,\nNous réjouir en Toi,\nGarder le bon dépôt!\nOui, Seigneur Jésus, viens!\nOui, Seigneur, viens bientôt.','<p><strong>1.</strong> </p>\n<p>O Seigneur Éternel, </p>\n<p>Une nouvelle année </p>\n<p>Par ta grande bonté </p>\n<p>Nous est encore donné. </p>\n<p>Ah! Donne-nous aussi </p>\n<p>D&#39;y vivre par la foi, </p>\n<p>Et de la consacrer </p>\n<p>Uniquement à Toi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que ce soit pour nous tous </p>\n<p>Un an de bienveillance! </p>\n<p>De grâce, de progrès, </p>\n<p>D&#39;amour, de délivrance! </p>\n<p>Que notre âme, docile </p>\n<p>A ta puissante voix, </p>\n<p>Jésus, trouve la paix </p>\n<p>A l&#39;ombre de ta croix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nos jours sont en tes mains, </p>\n<p>Notre course est bornée, </p>\n<p>Et plusieurs sont entrés </p>\n<p>Dans leur dernière année. </p>\n<p>Veillons donc et prions, </p>\n<p>Et s&#39;il faut déloger, </p>\n<p>Nous irons, pleins de joie </p>\n<p>Auprès du Bon Berger. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Puissions-nous, rachetés </p>\n<p>Par ton grand sacrifice, </p>\n<p>Scellés de ton Esprit, </p>\n<p>Couverts de ta justice, </p>\n<p>Nous réjouir en Toi, </p>\n<p>Garder le bon dépôt! </p>\n<p>Oui, Seigneur Jésus, viens! </p>\n<p>Oui, Seigneur, viens bientôt. </p>\n','O-Seigneur-Eternel',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1491,'d09df6e2-2d45-47c5-898e-7e588a9b6824',0,0,'Sentinelle vigilante',247,'247_CE_fr','1.\n« Sentinelle vigilante,\nQu\'en est-il donc de la nuit?\nDis à l\'âme somnolente\nQue déjà le matin luit! »\n\nRefrain\nLa nuit passe, le matin du grand jour luit!\nSentinelle, sois au poste jour et nuit!  (bis)\n\n\n2.\nLes gardes sur la muraille\nNous l\'ont dit, entendez-vous?\nAu loin gronde la bataille,\nTout est sombre autour de nous.\n\n\n3.\nPoint de repos, de relâche,\nRachetés de l\'Éternel.\nTravaillez à votre tâche,\nCar Jésus revient du ciel!','<p><strong>1.</strong> </p>\n<p>« Sentinelle vigilante, </p>\n<p>Qu&#39;en est-il donc de la nuit? </p>\n<p>Dis à l&#39;âme somnolente </p>\n<p>Que déjà le matin luit! » </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>La nuit passe, le matin du grand jour luit!</strong> </p>\n<p><strong>Sentinelle, sois au poste jour et nuit!  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les gardes sur la muraille </p>\n<p>Nous l&#39;ont dit, entendez-vous? </p>\n<p>Au loin gronde la bataille, </p>\n<p>Tout est sombre autour de nous. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Point de repos, de relâche, </p>\n<p>Rachetés de l&#39;Éternel. </p>\n<p>Travaillez à votre tâche, </p>\n<p>Car Jésus revient du ciel! </p>\n','Sentinelle-vigilante',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1492,'db15beca-1c17-49ca-a910-633eb2d74248',0,0,'Rois des rois, Eternel, mon Dieu!',271,'271_CE_fr','1.\nRoi des rois, Éternel, mon Dieu!\nQue ton tabernacle est un lieu\nSur tous les autres lieux aimable!\nMon coeur languit, mes sens ravis\nNe respirent que tes parvis\nEt que ta présence adorable;\nMon âme, vers Toi s\'élevant,\nCherche ta face, ô Dieu vivant!\n\n\n2.\nHélas! Seigneur, le moindre oiseau,\nL\'hirondelle ou le passereau,\nTrouve son nid et sa retraite;\nEt moi, dans mes ennuis mortels,\nJe languis loin de tes autels;\nC\'est en vain que je m\'y souhaite.\nHeureux qui peut, dans ta maison,\nTe louer en toute saison!\n\n\n3.\nQui veut en toi se confier\nT\'a pour soleil, pour bouclier;\nTu donnes la grâce et la gloire;\nTu couronnes l\'intégrité\nD\'honneur et de félicité,\nAu delà de ce qu\'on peut croire.\nOh! Mille et mille fois heureux\nCelui qui t\'adresse ses voeux!','<p><strong>1.</strong> </p>\n<p>Roi des rois, Éternel, mon Dieu! </p>\n<p>Que ton tabernacle est un lieu </p>\n<p>Sur tous les autres lieux aimable! </p>\n<p>Mon coeur languit, mes sens ravis </p>\n<p>Ne respirent que tes parvis </p>\n<p>Et que ta présence adorable; </p>\n<p>Mon âme, vers Toi s&#39;élevant, </p>\n<p>Cherche ta face, ô Dieu vivant! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Hélas! Seigneur, le moindre oiseau, </p>\n<p>L&#39;hirondelle ou le passereau, </p>\n<p>Trouve son nid et sa retraite; </p>\n<p>Et moi, dans mes ennuis mortels, </p>\n<p>Je languis loin de tes autels; </p>\n<p>C&#39;est en vain que je m&#39;y souhaite. </p>\n<p>Heureux qui peut, dans ta maison, </p>\n<p>Te louer en toute saison! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui veut en toi se confier </p>\n<p>T&#39;a pour soleil, pour bouclier; </p>\n<p>Tu donnes la grâce et la gloire; </p>\n<p>Tu couronnes l&#39;intégrité </p>\n<p>D&#39;honneur et de félicité, </p>\n<p>Au delà de ce qu&#39;on peut croire. </p>\n<p>Oh! Mille et mille fois heureux </p>\n<p>Celui qui t&#39;adresse ses voeux! </p>\n','Rois-des-rois-Eternel-mon-Dieu!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1493,'a99e19d7-320e-4585-bb50-345f462a3ad9',0,0,'Espère en Dieu quand la nuit sombre',132,'132_CE_fr','1.\nEspère en Dieu quand la nuit sombre\nVoile le ciel et l\'horizon;\nJamais là-haut ne règne l\'ombre,\nLà-haut t\'attend une maison.\nEspère en Dieu quand la tempête\nContre la nef jette ses flots.\nUn mot vainqueur déjà s\'apprête,\nÀ commander paix et repos.\n\n2.\nEspère en Dieu quand la souffrance\nBrisant ton corps, trouble ton coeur.\nChez Lui jamais l\'indifférence\nNe le distrait de ton malheur.\nEspère en Dieu quand sonne l\'heure\nD\'abandonner les biens d\'en-bas.\nCrois aux trésors de sa demeure,\nCar son amour t\'ouvre ses bras.\n\n3.\nEspère en Dieu quand on t\'oublie\nOu qu\'on te raille avec dédain.\nPour te sauver, jamais ne plie!\nVa plutôt seul sur ton chemin.\nEspère en Dieu quand ton pied glisse\nSous les efforts du Tentateur.\nSaisis la main libératrice\nQui te rendra toujours vainqueur.','<p><strong>1.</strong> </p>\n<p>Espère en Dieu quand la nuit sombre </p>\n<p>Voile le ciel et l&#39;horizon; </p>\n<p>Jamais là-haut ne règne l&#39;ombre, </p>\n<p>Là-haut t&#39;attend une maison. </p>\n<p>Espère en Dieu quand la tempête </p>\n<p>Contre la nef jette ses flots. </p>\n<p>Un mot vainqueur déjà s&#39;apprête, </p>\n<p>À commander paix et repos. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Espère en Dieu quand la souffrance </p>\n<p>Brisant ton corps, trouble ton coeur. </p>\n<p>Chez Lui jamais l&#39;indifférence </p>\n<p>Ne le distrait de ton malheur. </p>\n<p>Espère en Dieu quand sonne l&#39;heure </p>\n<p>D&#39;abandonner les biens d&#39;en-bas. </p>\n<p>Crois aux trésors de sa demeure, </p>\n<p>Car son amour t&#39;ouvre ses bras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Espère en Dieu quand on t&#39;oublie </p>\n<p>Ou qu&#39;on te raille avec dédain. </p>\n<p>Pour te sauver, jamais ne plie! </p>\n<p>Va plutôt seul sur ton chemin. </p>\n<p>Espère en Dieu quand ton pied glisse </p>\n<p>Sous les efforts du Tentateur. </p>\n<p>Saisis la main libératrice </p>\n<p>Qui te rendra toujours vainqueur. </p>\n','Espere-en-Dieu-quand-la-nuit-sombre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1494,'e3983582-9f8a-45e4-a957-e36ec227b7d9',0,0,'Comme une tige légère',135,'135_CE_fr','1.\nComme un tige légère\nS\'incline au souffle du vent,\nQu\'ainsi mon âme, ô mon Père,\nPlie à ton commandement.  (bis)\n\n2.\nMa volonté, c\'est la tienne;\nOrdonne: j\'attends ta voix;\nOui, Seigneur, quoiqu\'il m\'advienne,\nJe suis soumis à tes lois.  (bis)\n\n3.\nJe t\'aime, quoi que tu fasses!\nDonne, ôte, rends ou reprends;\nTous tes ordres sont des grâces\nPour tes bienheureux enfants.  (bis)\n\n4.\nOh!  Détache de la terre\nTous mes désirs, tous mes voeux!\nT\'aimer, te servir, mon Père,\nAh!  C\'est déjà vivre aux cieux.  (bis)','<p><strong>1.</strong> </p>\n<p>Comme un tige légère </p>\n<p>S&#39;incline au souffle du vent, </p>\n<p>Qu&#39;ainsi mon âme, ô mon Père, </p>\n<p>Plie à ton commandement.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ma volonté, c&#39;est la tienne; </p>\n<p>Ordonne: j&#39;attends ta voix; </p>\n<p>Oui, Seigneur, quoiqu&#39;il m&#39;advienne, </p>\n<p>Je suis soumis à tes lois.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je t&#39;aime, quoi que tu fasses! </p>\n<p>Donne, ôte, rends ou reprends; </p>\n<p>Tous tes ordres sont des grâces </p>\n<p>Pour tes bienheureux enfants.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh!  Détache de la terre </p>\n<p>Tous mes désirs, tous mes voeux! </p>\n<p>T&#39;aimer, te servir, mon Père, </p>\n<p>Ah!  C&#39;est déjà vivre aux cieux.  (bis) </p>\n','Comme-une-tige-legere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1495,'fc774fb2-7440-47db-895c-8a4cdf752482',0,0,'Sois sans alarmes, sans frayeur',133,'133_CE_fr','1.\nSois sans alarmes, sans frayeur,\nDieu prendra soin de toi;\nIl est ton Père et ton Sauveur,\nDieu prendra soin de toi.\n\nRefrain\nDieu prendra soin de toi,\nDans le chemin jusqu\'à la fin,\nIl prendra soin de toi,\nTous les jours jusqu\'à la fin.\n\n2.\nDans les jours de l\'adversité,\nDieu prendra soin de toi;\nLorsque pour toi, tout est danger,\nDieu prendra soin de toi.\n\n3.\nSi tu es seul, découragé,\nDieu prendra soin de toi;\nIl est toujours ton Bon Berger,\nDieu prendra soin de toi.\n\n4.\nNe crains donc pas le lendemain,\nDieu prendra soin de toi;\nPas à pas, ta main dans sa main,\nDieu prendra soin de toi.','<p><strong>1.</strong> </p>\n<p>Sois sans alarmes, sans frayeur, </p>\n<p>Dieu prendra soin de toi; </p>\n<p>Il est ton Père et ton Sauveur, </p>\n<p>Dieu prendra soin de toi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dieu prendra soin de toi,</strong> </p>\n<p><strong>Dans le chemin jusqu&#39;à la fin,</strong> </p>\n<p><strong>Il prendra soin de toi,</strong> </p>\n<p><strong>Tous les jours jusqu&#39;à la fin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans les jours de l&#39;adversité, </p>\n<p>Dieu prendra soin de toi; </p>\n<p>Lorsque pour toi, tout est danger, </p>\n<p>Dieu prendra soin de toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si tu es seul, découragé, </p>\n<p>Dieu prendra soin de toi; </p>\n<p>Il est toujours ton Bon Berger, </p>\n<p>Dieu prendra soin de toi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ne crains donc pas le lendemain, </p>\n<p>Dieu prendra soin de toi; </p>\n<p>Pas à pas, ta main dans sa main, </p>\n<p>Dieu prendra soin de toi. </p>\n','Sois-sans-alarmes-sans-frayeur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1496,'3f9b3731-00b5-4123-8712-46e3ef9bed46',0,0,'A toi, mon Dieu! je me donne',138,'138_CE_fr','1.\nA Toi, mon Dieu!  Je me donne,\nJe me donne tout entier!\nTon amour est ma couronne,\nTa force est mon bouclier.\n\n2.\nJe te donne mes journées,\nMes succès ou mes revers;\nJe te donne mes années,\nMes printemps et mes hivers.\n\n3.\nMes désirs, avec leur flamme\nQue tu peux seul apaiser,\nEt les rêves de mon âme,\nQue tu veux réaliser.\n\n4.\nToutes les fleurs de ma route,\nViens les cueillir de ta main;\nTous les pleurs, goutte après goutte,\nLes recueillir dans ton sein.\n\n5.\nDans la joie ou la souffrance\nJe veux te suivre en tout lieu;\nToute ma vie à l\'avance,\nJe te l\'apporte, ô mon Dieu!','<p><strong>1.</strong> </p>\n<p>A Toi, mon Dieu!  Je me donne, </p>\n<p>Je me donne tout entier! </p>\n<p>Ton amour est ma couronne, </p>\n<p>Ta force est mon bouclier. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je te donne mes journées, </p>\n<p>Mes succès ou mes revers; </p>\n<p>Je te donne mes années, </p>\n<p>Mes printemps et mes hivers. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mes désirs, avec leur flamme </p>\n<p>Que tu peux seul apaiser, </p>\n<p>Et les rêves de mon âme, </p>\n<p>Que tu veux réaliser. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Toutes les fleurs de ma route, </p>\n<p>Viens les cueillir de ta main; </p>\n<p>Tous les pleurs, goutte après goutte, </p>\n<p>Les recueillir dans ton sein. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Dans la joie ou la souffrance </p>\n<p>Je veux te suivre en tout lieu; </p>\n<p>Toute ma vie à l&#39;avance, </p>\n<p>Je te l&#39;apporte, ô mon Dieu! </p>\n','A-toi-mon-Dieu!-je-me-donne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1497,'ae15d3a8-750b-47ff-8ec6-0774dffd47ac',0,0,'A Jésus je m’abandonne',141,'141_CE_fr','1.A Jésus je m\'abandonne;Ce qu\'il me dit, je le crois,Et je prends ce qu\'il me donne,La couronne avec la croix.RefrainCompter sur lui d\'heure en heure,Tant que dure le combat;Que l\'on vive ou que l\'on meure,Compter sur lui, tout est là.  (bis 2 lignes)2.Que si l\'ennemi se monte,Mon coeur n\'en est point troublé;Avec Christ à sa rencontreJe puis aller sans trembler.3.Suis-je en paix? Vers la lumièreMon chant s\'élève, attendri,Pour se changer en prièreSi l\'horizon s\'assombrit.4.Qu\'on m\'approuve ou qu\'on me blâme,Et demain comme aujourd\'hui,Je ne veux quoiqu\'on réclame,Jamais compter que sur Lui.','<p><strong>1.</strong> </p>\n<p>A Jésus je m&#39;abandonne; </p>\n<p>Ce qu&#39;il me dit, je le crois, </p>\n<p>Et je prends ce qu&#39;il me donne, </p>\n<p>La couronne avec la croix. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Compter sur lui d&#39;heure en heure,</strong> </p>\n<p><strong>Tant que dure le combat;</strong> </p>\n<p><strong>Que l&#39;on vive ou que l&#39;on meure,</strong> </p>\n<p><strong>Compter sur lui, tout est là.  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que si l&#39;ennemi se montre, </p>\n<p>Mon coeur n&#39;en est point troublé; </p>\n<p>Avec Christ à sa rencontre </p>\n<p>Je puis aller sans trembler. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Suis-je en paix? Vers la lumière </p>\n<p>Mon chant s&#39;élève, attendri, </p>\n<p>Pour se changer en prière </p>\n<p>Si l&#39;horizon s&#39;assombrit. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Qu&#39;on m&#39;approuve ou qu&#39;on me blâme, </p>\n<p>Et demain comme aujourd&#39;hui, </p>\n<p>Je ne veux quoiqu&#39;on réclame, </p>\n<p>Jamais compter que sur Lui. </p>\n','A-Jesus-je-m\'abandonne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1498,'78eba39c-d0cc-4e6f-b30d-64e12db5753f',0,0,'J’ai trouvé, j’ai trouvé la voie',167,'167_CE_fr','1.\nJ\'ai trouvé, j\'ai trouvé la voie\nQui conduit au repos du coeur;\nJ\'ai trouvé la paix et la joie\nEn Jésus, le Libérateur.\nO mes compagnons de misère,\nEnsemble invoquons son secours:\nIl m\'attend que notre prière,\nLe Sauveur qui sauve toujours.  (bis 2 lignes)\n\n\n2.\nPar son sang, sa miséricorde\nEfface nos iniquités;\nPar son Esprit, qu\'Il nous accorde,\nIl guérit nos infirmités;\nEt plus le mal incurable,\nEt plus les fardeaux semblent lourds,\nPlus Il se montre secourable,\nLe Sauveur qui sauve toujours.  (bis 2 lignes)\n\n\n3.\nHélas! Ma trop longue ignorance\nNe connaissait auparavant,\nDans la lutte ou dans la souffrance,\nQu\'un Sauveur qui sauve souvent;\nMais de ma pauvre et triste vie\nJ\'ai vu se transformer le cours,\nDepuis que mon coeur se confie\nAu Sauveur qui sauve toujours.  (bis 2 lignes)\n\n\n4.\nIl est ma force et ma victoire,\nL\'Ami qui me guide en tout lieu;\nIl est ma lumière et ma gloire;\nIl est mon frère; Il est mon Dieu.\nSoit que je vive ou que je meure,\nIl est mon unique recours…\nTu me sauveras d\'heure en heure,\nO Sauveur qui sauves toujours!  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>J&#39;ai trouvé, j&#39;ai trouvé la voie </p>\n<p>Qui conduit au repos du coeur; </p>\n<p>J&#39;ai trouvé la paix et la joie </p>\n<p>En Jésus, le Libérateur. </p>\n<p>O mes compagnons de misère, </p>\n<p>Ensemble invoquons son secours: </p>\n<p>Il m&#39;attend que notre prière, </p>\n<p>Le Sauveur qui sauve toujours.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Par son sang, sa miséricorde </p>\n<p>Efface nos iniquités; </p>\n<p>Par son Esprit, qu&#39;Il nous accorde, </p>\n<p>Il guérit nos infirmités; </p>\n<p>Et plus le mal incurable, </p>\n<p>Et plus les fardeaux semblent lourds, </p>\n<p>Plus Il se montre secourable, </p>\n<p>Le Sauveur qui sauve toujours.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Hélas! Ma trop longue ignorance </p>\n<p>Ne connaissait auparavant, </p>\n<p>Dans la lutte ou dans la souffrance, </p>\n<p>Qu&#39;un Sauveur qui sauve souvent; </p>\n<p>Mais de ma pauvre et triste vie </p>\n<p>J&#39;ai vu se transformer le cours, </p>\n<p>Depuis que mon coeur se confie </p>\n<p>Au Sauveur qui sauve toujours.  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il est ma force et ma victoire, </p>\n<p>L&#39;Ami qui me guide en tout lieu; </p>\n<p>Il est ma lumière et ma gloire; </p>\n<p>Il est mon frère; Il est mon Dieu. </p>\n<p>Soit que je vive ou que je meure, </p>\n<p>Il est mon unique recours… </p>\n<p>Tu me sauveras d&#39;heure en heure, </p>\n<p>O Sauveur qui sauves toujours!  (bis 2 lignes) </p>\n','J\'ai-trouve-j\'ai-trouve-la-voie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1499,'53f49fb8-65c6-4b7c-ba5e-90d8d96ae706',0,0,'J’ai dans mon cœur une mélodie',182,'182_CE_fr','1.\nJ\'ai dans mon coeur une mélodie\nQui me vient de mon Sauveur;\nC\'est une joie intense et bénie\nQui vient réjouir mon coeur.\n\nRefrain\nDans mon coeur chante une mélodie,\nDouce mélodie, céleste harmonie;\nDans mon coeur chante une mélodie\nA la gloire du Sauveur.\n\n\n2.\nC\'est à la croix que je l\'ai trouvée\nDans l\'amour de mon Sauveur.\nC\'est mon âme qu\'il a rachetée\nDu fardeau de sa douleur.\n\n\n3.\nDepuis ce temps, mon âme joyeuse\nChante un doux refrain d\'amour;\nElle est paix, elle est si heureuse,\nQu\'elle redit nuit et jour:','<p><strong>1.</strong> </p>\n<p>J&#39;ai dans mon coeur une mélodie </p>\n<p>Qui me vient de mon Sauveur; </p>\n<p>C&#39;est une joie intense et bénie </p>\n<p>Qui vient réjouir mon coeur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dans mon coeur chante une mélodie,</strong> </p>\n<p><strong>Douce mélodie, céleste harmonie;</strong> </p>\n<p><strong>Dans mon coeur chante une mélodie</strong> </p>\n<p><strong>A la gloire du Sauveur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est à la croix que je l&#39;ai trouvée </p>\n<p>Dans l&#39;amour de mon Sauveur. </p>\n<p>C&#39;est mon âme qu&#39;il a rachetée </p>\n<p>Du fardeau de sa douleur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Depuis ce temps, mon âme joyeuse </p>\n<p>Chante un doux refrain d&#39;amour; </p>\n<p>Elle est paix, elle est si heureuse, </p>\n<p>Qu&#39;elle redit nuit et jour: </p>\n','J\'ai-dans-mon-coeur-une-melodie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1500,'bd54950c-57a4-4817-b1ef-51f127b33583',0,0,'Maitre, entends-tu la tempête',191,'191_CE_fr','1.\nMaître, entends-tu la tempête\nEt ses terrible accents!\nParle, Seigneur, et l\'arrête;\nSauve, sauve tes enfants!\nMais aucun bruit ne n\'éveille;\nIl dort et nous périssons.\nA nos cris prête l\'oreille,\nSauve-nous, nous t\'en prions!\n\nRefrain\nIl parle aux flots en démence:\nPaix vous soit!\nLa mer, les vents font silence;\nPaix vous soit! Paix vous soit!\nIl est notre délivrance;\nPaix vous soit!\n\n\n2.\nMaître, mon coeur qui chancelle\nTraverse les grandes eaux,\nEt l\'orage, à ma nacelle,\nLivre de rudes assauts.\nOh! Qui verra ma détresse?\nD\'où me viendra le secours?\nA Toi, Seigneur, je m\'adresse,\nC\'est à Toi que j\'ai recours.\n\n\n3.\nMaître, l\'onde est apaisée,\nLe doute a quitté mon coeur,\nEt mon âme consolée\nA retrouvé son Sauveur.\nOh! Prends dans ta main ma vie,\nJusques au jours de ma mort!\nEn Toi seul je me confie;\nTu me conduiras au port.','<p><strong>1.</strong> </p>\n<p>Maître, entends-tu la tempête </p>\n<p>Et ses terrible accents! </p>\n<p>Parle, Seigneur, et l&#39;arrête; </p>\n<p>Sauve, sauve tes enfants! </p>\n<p>Mais aucun bruit ne n&#39;éveille; </p>\n<p>Il dort et nous périssons. </p>\n<p>A nos cris prête l&#39;oreille, </p>\n<p>Sauve-nous, nous t&#39;en prions! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il parle aux flots en démence:</strong> </p>\n<p><strong>Paix vous soit!</strong> </p>\n<p><strong>La mer, les vents font silence;</strong> </p>\n<p><strong>Paix vous soit! Paix vous soit!</strong> </p>\n<p><strong>Il est notre délivrance;</strong> </p>\n<p><strong>Paix vous soit!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Maître, mon coeur qui chancelle </p>\n<p>Traverse les grandes eaux, </p>\n<p>Et l&#39;orage, à ma nacelle, </p>\n<p>Livre de rudes assauts. </p>\n<p>Oh! Qui verra ma détresse? </p>\n<p>D&#39;où me viendra le secours? </p>\n<p>A Toi, Seigneur, je m&#39;adresse, </p>\n<p>C&#39;est à Toi que j&#39;ai recours. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Maître, l&#39;onde est apaisée, </p>\n<p>Le doute a quitté mon coeur, </p>\n<p>Et mon âme consolée </p>\n<p>A retrouvé son Sauveur. </p>\n<p>Oh! Prends dans ta main ma vie, </p>\n<p>Jusques au jours de ma mort! </p>\n<p>En Toi seul je me confie; </p>\n<p>Tu me conduiras au port. </p>\n','Maitre-entends-tu-la-tempete',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1501,'cb1b102d-bab3-4638-87fb-a25336979ad7',0,0,'Mon Dieu, mon Père',196,'196_CE_fr','1.\nMon Dieu, mon Père,\nÉcoute-moi,\nCar ma prière\nS\'élève à toi.\nEn Jésus-Christ,\nTu nous l\'as dit,\nJe puis, Seigneur,\nT\'ouvrir mon coeur.\nAh! Fais-moi grâce,\nDieu tout-puissant!\nTourne ta face\nVers ton enfant.\n\n\n2.\nViens, je te prie,\nChange mon coeur;\nGuide ma vie\nLoin de l\'erreur.\nMon seul désir\nEst de choisir\nLa bonne part\nSous ton regard.\nQue mon offense\nNe lasse plus\nTa patience,\nSeigneur Jésus!\n\n\n3.\nFais-moi comprendre\nTa charité\nEt bien entendre\nTa vérité.\nOui, que ta main,\nSur mon chemin,\nSoit, ô Dieu fort,\nMon seul support!\nQue ta puissance\nSoit chaque jour\nMa délivrance,\nO Dieu d\'amour!\n\n\n4.\nRends-moi fidèle\nPar ton secours,\nEt sous ton aile\nTiens-moi toujours;\nLoin du danger,\nO Bon Berger!\nConduis mes pas\nJusqu\'au trépas.\nVois ma faiblesse\nEt mes soutiens\nPar ta tendresse:\nJe t\'appartiens.','<p><strong>1.</strong> </p>\n<p>Mon Dieu, mon Père, </p>\n<p>Écoute-moi, </p>\n<p>Car ma prière </p>\n<p>S&#39;élève à toi. </p>\n<p>En Jésus-Christ, </p>\n<p>Tu nous l&#39;as dit, </p>\n<p>Je puis, Seigneur, </p>\n<p>T&#39;ouvrir mon coeur. </p>\n<p>Ah! Fais-moi grâce, </p>\n<p>Dieu tout-puissant! </p>\n<p>Tourne ta face </p>\n<p>Vers ton enfant. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Viens, je te prie, </p>\n<p>Change mon coeur; </p>\n<p>Guide ma vie </p>\n<p>Loin de l&#39;erreur. </p>\n<p>Mon seul désir </p>\n<p>Est de choisir </p>\n<p>La bonne part </p>\n<p>Sous ton regard. </p>\n<p>Que mon offense </p>\n<p>Ne lasse plus </p>\n<p>Ta patience, </p>\n<p>Seigneur Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fais-moi comprendre </p>\n<p>Ta charité </p>\n<p>Et bien entendre </p>\n<p>Ta vérité. </p>\n<p>Oui, que ta main, </p>\n<p>Sur mon chemin, </p>\n<p>Soit, ô Dieu fort, </p>\n<p>Mon seul support! </p>\n<p>Que ta puissance </p>\n<p>Soit chaque jour </p>\n<p>Ma délivrance, </p>\n<p>O Dieu d&#39;amour! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Rends-moi fidèle </p>\n<p>Par ton secours, </p>\n<p>Et sous ton aile </p>\n<p>Tiens-moi toujours; </p>\n<p>Loin du danger, </p>\n<p>O Bon Berger! </p>\n<p>Conduis mes pas </p>\n<p>Jusqu&#39;au trépas. </p>\n<p>Vois ma faiblesse </p>\n<p>Et mes soutiens </p>\n<p>Par ta tendresse: </p>\n<p>Je t&#39;appartiens. </p>\n','Mon-Dieu-mon-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1502,'6813af20-51a8-4223-9e9f-069d53041248',0,0,'Seigneur, Ce Que Je Réclame',203,'203_CE_fr','1.\nSeigneur, ce que je réclame,\nC\'est ce riche don d\'amour!\nQue cette céleste flamme\nEn moi brûle nuit et jour!\nCet amour, si fort, si tendre,\nAmour qui supporte tout,\nQui ne veut pas se défendre,\nPrêt à souffrir jusqu\'au bout.\n\n\n2.\nPour les autres il s\'oublie,\nIl ne peut être envieux,\nTout éloge l\'humilie,\nJamais il n\'est orgueilleux.\nCet amour peut vaincre et fondre\nLes coeurs méchants, durs, glacés;\nIls ne peuvent rien répondre,\nL\'amour les a terrassés.\n\n\n3.\nDevant la vaste souffrance\nQui s\'étend de jour en jour,\nIl faut un remède immense,\nIl faut un immense amour.\nQue jusqu\'à la mort, fidèle,\nPriant, luttant en tout lieu,\nMa vie entière révèle\nL\'amour sublime de Dieu!','<p><strong>1.</strong> </p>\n<p>Seigneur, ce que je réclame, </p>\n<p>C&#39;est ce riche don d&#39;amour! </p>\n<p>Que cette céleste flamme </p>\n<p>En moi brûle nuit et jour! </p>\n<p>Cet amour, si fort, si tendre, </p>\n<p>Amour qui supporte tout, </p>\n<p>Qui ne veut pas se défendre, </p>\n<p>Prêt à souffrir jusqu&#39;au bout. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour les autres il s&#39;oublie, </p>\n<p>Il ne peut être envieux, </p>\n<p>Tout éloge l&#39;humilie, </p>\n<p>Jamais il n&#39;est orgueilleux. </p>\n<p>Cet amour peut vaincre et fondre </p>\n<p>Les coeurs méchants, durs, glacés; </p>\n<p>Ils ne peuvent rien répondre, </p>\n<p>L&#39;amour les a terrassés. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Devant la vaste souffrance </p>\n<p>Qui s&#39;étend de jour en jour, </p>\n<p>Il faut un remède immense, </p>\n<p>Il faut un immense amour. </p>\n<p>Que jusqu&#39;à la mort, fidèle, </p>\n<p>Priant, luttant en tout lieu, </p>\n<p>Ma vie entière révèle </p>\n<p>L&#39;amour sublime de Dieu! </p>\n','Seigneur-Ce-Que-Je-Reclame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1503,'41077bc0-85cb-4df8-a88b-9bcb0b947cb5',0,0,'J’apportai ma détresse à Jésus',238,'238_CE_fr','1.\nJ\'apportai ma détresse à Jésus;\nMon péché, ma tristesse ne sont plus.\nCombien douce à mon âme fut sa voix,\nQuand, sur le bois infâme de la croix,\nIl murmura: « Je t\'aime,\nEt j\'expire pour toi;\nPécheur, aujourd\'hui même,\nTu vivras avec moi! »\n\n\n2.\nQuand mon âme est lassée, au Sauveur\nJ\'élève ma pensée et mon coeur.\nJe le vois: plus de larmes dans mes yeux!\nJe suis libre d\'alarmes et joyeux.\nSa force en ma faiblesse\nS\'accomplit tous les jours;\nNul fardeau ne m\'oppresse\nCar Il est mon recours.\n\n\n3.\nViens à Jésus, mon frère, aujourd\'hui.\nAvec ta peine amère, viens à Lui.\nLe Fils de Dieu t\'appelle; viens et crois!\nPourquoi rester rebelle à sa voix?\nLa voix qui fit le monde\nTe supplie, ô pécheur.\nQue ton âme réponde\nEt trouve le bonheur.','<p><strong>1.</strong> </p>\n<p>J&#39;apportai ma détresse à Jésus; </p>\n<p>Mon péché, ma tristesse ne sont plus. </p>\n<p>Combien douce à mon âme fut sa voix, </p>\n<p>Quand, sur le bois infâme de la croix, </p>\n<p>Il murmura: « Je t&#39;aime, </p>\n<p>Et j&#39;expire pour toi; </p>\n<p>Pécheur, aujourd&#39;hui même, </p>\n<p>Tu vivras avec moi! » </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand mon âme est lassée, au Sauveur </p>\n<p>J&#39;élève ma pensée et mon coeur. </p>\n<p>Je le vois: plus de larmes dans mes yeux! </p>\n<p>Je suis libre d&#39;alarmes et joyeux. </p>\n<p>Sa force en ma faiblesse </p>\n<p>S&#39;accomplit tous les jours; </p>\n<p>Nul fardeau ne m&#39;oppresse </p>\n<p>Car Il est mon recours. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Viens à Jésus, mon frère, aujourd&#39;hui. </p>\n<p>Avec ta peine amère, viens à Lui. </p>\n<p>Le Fils de Dieu t&#39;appelle; viens et crois! </p>\n<p>Pourquoi rester rebelle à sa voix? </p>\n<p>La voix qui fit le monde </p>\n<p>Te supplie, ô pécheur. </p>\n<p>Que ton âme réponde </p>\n<p>Et trouve le bonheur. </p>\n','J\'apportai-ma-detresse-a-Jesus',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1504,'9532682a-5bf5-4112-b98c-acb7082effeb',0,0,'Toi dont l’âme est tourmentée',274,'274_CE_fr','1.\nToi dont l\'âme est tourmentée\nAux approches de la mort,\nToi dont la nef ballottée\nNe sait où trouver le port,\nRegarde à travers tes larmes,\nCe phare, qui tant de fois\nA brillé dans tes alarmes:\nC\'est la croix! C\'est la croix!\n\n\n2.\nO toi qu\'assaille le doute,\nToi que le monde a séduit,\nToi qui marches sur la route\nDans la nuit et vers la nuit,\nMême en doutant, prie, adore\nCelui qui meurt sur le bois,\nRegarde, oh! Regarde encore\nVers la croix! Vers la croix!\n\n\n3.\nSur la croix où Christ expire,\nLa mort succombe avec lui:\nC\'en est fait de son empire,\nLe jour de la vie a lui!\nPéchés, doutes et souffrance\nDemeurent cloués au bois:\nO sublime délivrance\nDe la croix! De la croix!\n\n\n4.\nA tes pieds, ô croix bénie,\nSigne auguste et méprisé\nDe triomphe et d\'agonie,\nJ\'apporte mon coeur brisé.\nDésormais, sois ma bannière!\nJe veux vivre sous tes lois\nEt mourir sous ta lumière,\nSainte croix! Sainte croix!','<p><strong>1.</strong> </p>\n<p>Toi dont l&#39;âme est tourmentée </p>\n<p>Aux approches de la mort, </p>\n<p>Toi dont la nef ballottée </p>\n<p>Ne sait où trouver le port, </p>\n<p>Regarde à travers tes larmes, </p>\n<p>Ce phare, qui tant de fois </p>\n<p>A brillé dans tes alarmes: </p>\n<p>C&#39;est la croix! C&#39;est la croix! </p>\n<p><br/> <strong>2.</strong></p>\n<p>O toi qu&#39;assaille le doute, </p>\n<p>Toi que le monde a séduit, </p>\n<p>Toi qui marches sur la route </p>\n<p>Dans la nuit et vers la nuit, </p>\n<p>Même en doutant, prie, adore </p>\n<p>Celui qui meurt sur le bois, </p>\n<p>Regarde, oh! Regarde encore </p>\n<p>Vers la croix! Vers la croix! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sur la croix où Christ expire, </p>\n<p>La mort succombe avec lui: </p>\n<p>C&#39;en est fait de son empire, </p>\n<p>Le jour de la vie a lui! </p>\n<p>Péchés, doutes et souffrance </p>\n<p>Demeurent cloués au bois: </p>\n<p>O sublime délivrance </p>\n<p>De la croix! De la croix! </p>\n<p><br/> <strong>4.</strong></p>\n<p>A tes pieds, ô croix bénie, </p>\n<p>Signe auguste et méprisé </p>\n<p>De triomphe et d&#39;agonie, </p>\n<p>J&#39;apporte mon coeur brisé. </p>\n<p>Désormais, sois ma bannière! </p>\n<p>Je veux vivre sous tes lois </p>\n<p>Et mourir sous ta lumière, </p>\n<p>Sainte croix! Sainte croix! </p>\n','Toi-dont-l\'ame-est-tourmentee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1505,'d730ff5c-9792-4e11-92c5-e4b7016d882c',0,0,'Écoutez la Bonne Nouvelle',282,'282_CE_fr','1.\nÉcoutez la Bonne Nouvelle,\nVous qui n\'espérez plus!\nDieu donne la vie éternelle\nPar le sang de Jésus.\n\nRefrain\nAmour divin, amour sublime!\nPour nous le Roi des rois\nQuitta les cieux, sainte victime,\nEt mourut sur la croix.\n\n\n2.\nLa coupe amère de souffrance\nQue le Rédempteur\nEst la coupe de délivrance\nPour le pauvre pécheur.\n\n\n3.\nIl a remporté la victoire\nPour nous rendre vainqueurs,\nPlutôt qu\'au ciel, Il met sa gloire\nA vivre dans nos coeurs.\n\n\n4.\nPourquoi, mon frère, craindre encore?\nVers Christ, lève les yeux!\nSur nous, Il fait briller l\'aurore\nDu matin glorieux.','<p><strong>1.</strong> </p>\n<p>Écoutez la Bonne Nouvelle, </p>\n<p>Vous qui n&#39;espérez plus! </p>\n<p>Dieu donne la vie éternelle </p>\n<p>Par le sang de Jésus. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Amour divin, amour sublime!</strong> </p>\n<p><strong>Pour nous le Roi des rois</strong> </p>\n<p><strong>Quitta les cieux, sainte victime,</strong> </p>\n<p><strong>Et mourut sur la croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La coupe amère de souffrance </p>\n<p>Que le Rédempteur </p>\n<p>Est la coupe de délivrance </p>\n<p>Pour le pauvre pécheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il a remporté la victoire </p>\n<p>Pour nous rendre vainqueurs, </p>\n<p>Plutôt qu&#39;au ciel, Il met sa gloire </p>\n<p>A vivre dans nos coeurs. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pourquoi, mon frère, craindre encore? </p>\n<p>Vers Christ, lève les yeux! </p>\n<p>Sur nous, Il fait briller l&#39;aurore </p>\n<p>Du matin glorieux. </p>\n','Ecoutez-la-Bonne-Nouvelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1506,'be17bc42-6acb-4aab-bdc9-94fd299c269c',0,0,'Tel que je suis, pécheur rebelle',297,'297_CE_fr','1.\nTel que je suis, pécheur rebelle,\nAu nom du sang versé pour moi,\nAu nom de ta voix qui m\'appelle,\nJésus je viens à Toi!\n\n\n2.\nTel que je suis, dans ma souillure,\nNe cherchant nul remède en moi,\nTon sang lave mon âme impure;\nJésus, je viens à Toi!\n\n\n3.\nTel que je suis, avec mes luttes,\nMes craintes, ma timide foi,\nAvec mes doutes et mes chutes,\nJésus, je viens à Toi!\n\n\n4.\nTel que je suis, je me réclame\nDe ta promesse, par la foi;\nAu ciel tu recevras mon âme;\nJésus, je viens à Toi!\n\n\n5.\nTel que je suis, ton sacrifice\nA ma place accomplit la loi;\nJustifié par ta justice,\nJésus, je viens à Toi!\n\n\n6.\nTel que je suis, Dieu me convie,\nO mon Sauveur, pour être à Toi;\nA Toi dans la mort, dans la vie;\nJésus, je viens à Toi!','<p><strong>1.</strong> </p>\n<p>Tel que je suis, pécheur rebelle, </p>\n<p>Au nom du sang versé pour moi, </p>\n<p>Au nom de ta voix qui m&#39;appelle, </p>\n<p>Jésus je viens à Toi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tel que je suis, dans ma souillure, </p>\n<p>Ne cherchant nul remède en moi, </p>\n<p>Ton sang lave mon âme impure; </p>\n<p>Jésus, je viens à Toi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tel que je suis, avec mes luttes, </p>\n<p>Mes craintes, ma timide foi, </p>\n<p>Avec mes doutes et mes chutes, </p>\n<p>Jésus, je viens à Toi! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tel que je suis, je me réclame </p>\n<p>De ta promesse, par la foi; </p>\n<p>Au ciel tu recevras mon âme; </p>\n<p>Jésus, je viens à Toi! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Tel que je suis, ton sacrifice </p>\n<p>A ma place accomplit la loi; </p>\n<p>Justifié par ta justice, </p>\n<p>Jésus, je viens à Toi! </p>\n<p><br/> <strong>6.</strong></p>\n<p>Tel que je suis, Dieu me convie, </p>\n<p>O mon Sauveur, pour être à Toi; </p>\n<p>A Toi dans la mort, dans la vie; </p>\n<p>Jésus, je viens à Toi! </p>\n','Tel-que-je-suis-pecheur-rebelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1507,'bb8b9c5c-53e0-43ad-802d-e05ef411d378',0,0,'C’est Toi Jésus',300,'300_CE_fr','1.\nC\'est toi, Jésus!\nQue recherche mon âme;\nA te trouver\nSe bornent mes souhaits;\nC\'est ton regard\nQue sur moi je réclame;\nRends-moi, Seigneur,\nRends-moi ta douce paix.\n\n\n2.\nLongtemps j\'errai\nDans les sentiers du monde,\nNe connaissant\nNi ton nom ni ta loi;\nTu me cherchas\nEn cette nuit profonde,\nEt, pour toujours,\nM\'en tiras par la foi.\n\n\n3.\nDe ton amour\nLa voix se fit entendre;\nJ\'appris alors\nQue tu m\'as racheté,\nEt ton Esprit\nA mon coeur fit comprendre\nCe qu\'est pour nous,\nO Dieu!  Ta charité!\n\n\n4.\nPrends donc pitié\nDe ma grande misère!\nSoumets mon coeur,\nBrise sa dureté.\nA Golgotha\nMon âme te fut chère:\nJe compte, ô Dieu!\nSur ta fidélité.','<p><strong>1.</strong> </p>\n<p>C&#39;est toi, Jésus! </p>\n<p>Que recherche mon âme; </p>\n<p>A te trouver </p>\n<p>Se bornent mes souhaits; </p>\n<p>C&#39;est ton regard </p>\n<p>Que sur moi je réclame; </p>\n<p>Rends-moi, Seigneur, </p>\n<p>Rends-moi ta douce paix. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Longtemps j&#39;errai </p>\n<p>Dans les sentiers du monde, </p>\n<p>Ne connaissant </p>\n<p>Ni ton nom ni ta loi; </p>\n<p>Tu me cherchas </p>\n<p>En cette nuit profonde, </p>\n<p>Et, pour toujours, </p>\n<p>M&#39;en tiras par la foi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>De ton amour </p>\n<p>La voix se fit entendre; </p>\n<p>J&#39;appris alors </p>\n<p>Que tu m&#39;as racheté, </p>\n<p>Et ton Esprit </p>\n<p>A mon coeur fit comprendre </p>\n<p>Ce qu&#39;est pour nous, </p>\n<p>O Dieu!  Ta charité! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Prends donc pitié </p>\n<p>De ma grande misère! </p>\n<p>Soumets mon coeur, </p>\n<p>Brise sa dureté. </p>\n<p>A Golgotha </p>\n<p>Mon âme te fut chère: </p>\n<p>Je compte, ô Dieu! </p>\n<p>Sur ta fidélité. </p>\n','C\'est-Toi-Jesus',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1508,'8d0f5fba-9c3d-479b-ab4a-3c841d02b284',0,0,'Est-il Bien Vrai, Seigneur',301,'301_CE_fr','1.\nEst-il bien vrai, Seigneur,\nQu\'un fils de la poussière\nA ton festin d\'amour,\nPar toi soit invité?\nPour titre à tes faveurs,\nJe n\'ai que ma misère;\nMon seul droit,\nC\'est ta charité!  (bis 2 lignes)\n\n\n2.\nJe viens donc, altéré\nDe pardon, de justice,\nRecevoir de ta main\nLes symboles touchants\nQue retracent ici\nTon sanglant sacrifice\nAu souvenir\nDe tes enfants.  (bis 2 lignes)\n\n\n3.\nToi qui m\'as tant aimé,\nQui lavas ma souillure,\nQui, dans mon coeur troublé\nFis descendre la paix,\nO Jésus, pain du ciel,\nDeviens ma nourriture,\nEt qu\'en Toi,\nJe vive à jamais!  (bis 2 lignes)\n\n\n4.\nOui, Seigneur, en Toi seul\nJe veux trouver ma vie;\nJ\'ai vécu trop longtemps\nDu monde et du péché;\nA ta brebis lassée\nOuvre la bergerie,\nEt dans ton sein\nTiens-moi caché.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Est-il bien vrai, Seigneur, </p>\n<p>Qu&#39;un fils de la poussière </p>\n<p>A ton festin d&#39;amour, </p>\n<p>Par toi soit invité? </p>\n<p>Pour titre à tes faveurs, </p>\n<p>Je n&#39;ai que ma misère; </p>\n<p>Mon seul droit, </p>\n<p>C&#39;est ta charité!  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je viens donc, altéré </p>\n<p>De pardon, de justice, </p>\n<p>Recevoir de ta main </p>\n<p>Les symboles touchants </p>\n<p>Que retracent ici </p>\n<p>Ton sanglant sacrifice </p>\n<p>Au souvenir </p>\n<p>De tes enfants.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Toi qui m&#39;as tant aimé, </p>\n<p>Qui lavas ma souillure, </p>\n<p>Qui, dans mon coeur troublé </p>\n<p>Fis descendre la paix, </p>\n<p>O Jésus, pain du ciel, </p>\n<p>Deviens ma nourriture, </p>\n<p>Et qu&#39;en Toi, </p>\n<p>Je vive à jamais!  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, Seigneur, en Toi seul </p>\n<p>Je veux trouver ma vie; </p>\n<p>J&#39;ai vécu trop longtemps </p>\n<p>Du monde et du péché; </p>\n<p>A ta brebis lassée </p>\n<p>Ouvre la bergerie, </p>\n<p>Et dans ton sein </p>\n<p>Tiens-moi caché.  (bis 2 lignes) </p>\n','Est-il-Bien-Vrai-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1509,'121396af-32a9-4cdf-97c7-cd0d29d90849',0,0,'Epoux chrétiens, que le ciel vous sourie',313,'313_CE_fr','1.\nÉpoux chrétiens, que le ciel vous sourie\nDans ce beau jour où vous êtes unis;\nPour vous, ici, chacun de nos coeurs prie.\nSoyez heureux, amis, soyez bénis!\n\n\n2.\nOh! Que Jésus, dont la douce présence\nVoulut orner les noces de Cana,\nPréside encore, dans sa condescendance,\nA ce bonheur que Lui-même donna!\n\n\n3.\nQu\'il fait chez vous un autre Béthanie,\nOù l\'on accueille avec empressement\nSon coeur aimant, sa parole bénie,\nOù l\'on s\'entr\'aime, à ses pieds, doucement.','<p><strong>1.</strong> </p>\n<p>Époux chrétiens, que le ciel vous sourie </p>\n<p>Dans ce beau jour où vous êtes unis; </p>\n<p>Pour vous, ici, chacun de nos coeurs prie. </p>\n<p>Soyez heureux, amis, soyez bénis! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh! Que Jésus, dont la douce présence </p>\n<p>Voulut orner les noces de Cana, </p>\n<p>Préside encore, dans sa condescendance, </p>\n<p>A ce bonheur que Lui-même donna! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;il fait chez vous un autre Béthanie, </p>\n<p>Où l&#39;on accueille avec empressement </p>\n<p>Son coeur aimant, sa parole bénie, </p>\n<p>Où l&#39;on s&#39;entr&#39;aime, à ses pieds, doucement. </p>\n','Epoux-chretiens-que-le-ciel-vous-sourie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1510,'5d1912ea-d043-4bf8-be4f-bcec4060a9f0',0,0,'Nous mourrons, mais pour renaître',316,'316_CE_fr','1.\nNous mourrons, mais pour renaître;\nLa mort n\'est qu\'un doux sommeil;\nBientôt, Jésus va paraître:\nCe sera le grand réveil!\nDes profondeurs de l\'abîme,\nDe la tombe, noir séjour,\nDe la plaine et de la cime,\nTous se lèveront un jour!\n\nRefrain\nNous mourrons, mais pour renaître;\nLa mort n\'est qu\'un doux sommeil,\nBientôt Jésus va paraître;\nCe sera le grand réveil!\n\n\n2.\nTu pleures, ô tendre mère;\nTu vis ton enfant mourir,\nTa douce fleur éphémère,\nUn souffle a su la flétrir…\nMais si ta douleur est vive,\nCrois, espère au Dieu d\'amour,\nTa fleur vit, il la cultive,\nIl veut te la rendre un jour!\n\n\n3.\nNous mourrons, mais pour renaître,\nEt, quittant le froid tombeau,\nNous saluerons notre Maître\nAu seuil d\'un monde nouveau,\nLa mort, délaissant sa proie,\nFuira devant Son retour,\nEt de l\'éternelle joie,\nPour nous brillera le jour!','<p><strong>1.</strong> </p>\n<p>Nous mourrons, mais pour renaître; </p>\n<p>La mort n&#39;est qu&#39;un doux sommeil; </p>\n<p>Bientôt, Jésus va paraître: </p>\n<p>Ce sera le grand réveil! </p>\n<p>Des profondeurs de l&#39;abîme, </p>\n<p>De la tombe, noir séjour, </p>\n<p>De la plaine et de la cime, </p>\n<p>Tous se lèveront un jour! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nous mourrons, mais pour renaître;</strong> </p>\n<p><strong>La mort n&#39;est qu&#39;un doux sommeil,</strong> </p>\n<p><strong>Bientôt Jésus va paraître;</strong> </p>\n<p><strong>Ce sera le grand réveil!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu pleures, ô tendre mère; </p>\n<p>Tu vis ton enfant mourir, </p>\n<p>Ta douce fleur éphémère, </p>\n<p>Un souffle a su la flétrir… </p>\n<p>Mais si ta douleur est vive, </p>\n<p>Crois, espère au Dieu d&#39;amour, </p>\n<p>Ta fleur vit, il la cultive, </p>\n<p>Il veut te la rendre un jour! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous mourrons, mais pour renaître, </p>\n<p>Et, quittant le froid tombeau, </p>\n<p>Nous saluerons notre Maître </p>\n<p>Au seuil d&#39;un monde nouveau, </p>\n<p>La mort, délaissant sa proie, </p>\n<p>Fuira devant Son retour, </p>\n<p>Et de l&#39;éternelle joie, </p>\n<p>Pour nous brillera le jour! </p>\n','Nous-mourrons-mais-pour-renaitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1511,'3e9c4bc6-2f44-42b2-9d9e-722347d374e0',0,0,'Viens m’apprendre à t’aimer',208,'208_CE_fr','1.\nViens m\'apprendre à t\'aimer,\nC\'est en toi qu\'est la vie,\nEn toi qu\'est le bonheur\nEt la félicité.\nJésus, étends sur moi\nTa main, ta main meurtrie,\nEt dis-moi: j\'ai souffert,\nJ\'ai porté ton péché.\n\n\n2.\nVois, je suis à tes pieds,\nJe pleure et m\'humilie,\nA l\'ombre de ta croix\nJe me tiens prosterné.\nQue ton divin amour\nMe retienne et me lie,\nEt m\'apprenne à t\'aimer,\nToi qui m\'as tout donné.\n\n\n3.\nQue ton sang répandu\nM\'ôte toute souillure,\nViens me donner un coeur\nQui ressemble à ton coeur.\nRends-moi participant\nDe ta sainte nature,\nJe ne veux que toi seul\nPour Maître et pour Vainqueur.\n\n\n4.\nJe ne t\'apporte rien,\nQu\'un pauvre coeur qui souffre\nDe ne pouvoir t\'offrir\nQue son trop faible amour.\nEn venant me chercher\nEt me tirer du gouffre,\nTu m\'as sauvé, Seigneur,\nMe voici dès ce jour!','<p><strong>1.</strong> </p>\n<p>Viens m&#39;apprendre à t&#39;aimer, </p>\n<p>C&#39;est en toi qu&#39;est la vie, </p>\n<p>En toi qu&#39;est le bonheur </p>\n<p>Et la félicité. </p>\n<p>Jésus, étends sur moi </p>\n<p>Ta main, ta main meurtrie, </p>\n<p>Et dis-moi: j&#39;ai souffert, </p>\n<p>J&#39;ai porté ton péché. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois, je suis à tes pieds, </p>\n<p>Je pleure et m&#39;humilie, </p>\n<p>A l&#39;ombre de ta croix </p>\n<p>Je me tiens prosterné. </p>\n<p>Que ton divin amour </p>\n<p>Me retienne et me lie, </p>\n<p>Et m&#39;apprenne à t&#39;aimer, </p>\n<p>Toi qui m&#39;as tout donné. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que ton sang répandu </p>\n<p>M&#39;ôte toute souillure, </p>\n<p>Viens me donner un coeur </p>\n<p>Qui ressemble à ton coeur. </p>\n<p>Rends-moi participant </p>\n<p>De ta sainte nature, </p>\n<p>Je ne veux que toi seul </p>\n<p>Pour Maître et pour Vainqueur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je ne t&#39;apporte rien, </p>\n<p>Qu&#39;un pauvre coeur qui souffre </p>\n<p>De ne pouvoir t&#39;offrir </p>\n<p>Que son trop faible amour. </p>\n<p>En venant me chercher </p>\n<p>Et me tirer du gouffre, </p>\n<p>Tu m&#39;as sauvé, Seigneur, </p>\n<p>Me voici dès ce jour! </p>\n','Viens-m\'apprendre-a-t\'aimer',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1512,'d4805785-2c6f-4fb2-a3c7-cb8832462ee8',0,0,'Travaillons et Luttons',211,'211_CE_fr','1.\nTravaillons et luttons!\nNous sommes au Seigneur,\nSuivons l\'étroit sentier\nQui conduit à la vie!\nJésus marche avec nous,\nAvançons sans frayeur,\nIl nous garde, et son bras\nToujours nous fortifie.\n\nRefrain\nTravaillons et luttons,  (bis)\nSoyons prêts et prions,\nBientôt le Maître va venir!\n\n\n2.\nTravaillons et luttons!\nQue les coeurs affligés,\nLes perdus loin de Dieu\nRetrouvent l\'espérance!\nVers la croix, dirigeons\nLeurs regards angoissés,\nPressons-les d\'accepter\nJésus, leur délivrance.\n\n\n3.\nTravaillons et luttons!\nIl nous appelle tous,\nLe champ nous est ouvert\nEt la moisson est grande!\nPour servir notre Chef,\nNe pensons plus à nous,\nEn avant vers le but!\nLe Maître le demande.\n\n\n4.\nTravaillons et luttons!\nSans jamais nous lasser,\nDe notre Rédempteur\nÉlevons la bannière,\nFidèles jusqu\'au bout,\nSachons persévérer,\nLe repos nous attend\nDans la pleine lumière!','<p><strong>1.</strong> </p>\n<p>Travaillons et luttons! </p>\n<p>Nous sommes au Seigneur, </p>\n<p>Suivons l&#39;étroit sentier </p>\n<p>Qui conduit à la vie! </p>\n<p>Jésus marche avec nous, </p>\n<p>Avançons sans frayeur, </p>\n<p>Il nous garde, et son bras </p>\n<p>Toujours nous fortifie. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Travaillons et luttons,  (bis)</strong> </p>\n<p><strong>Soyons prêts et prions,</strong> </p>\n<p><strong>Bientôt le Maître va venir!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Travaillons et luttons! </p>\n<p>Que les coeurs affligés, </p>\n<p>Les perdus loin de Dieu </p>\n<p>Retrouvent l&#39;espérance! </p>\n<p>Vers la croix, dirigeons </p>\n<p>Leurs regards angoissés, </p>\n<p>Pressons-les d&#39;accepter </p>\n<p>Jésus, leur délivrance. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Travaillons et luttons! </p>\n<p>Il nous appelle tous, </p>\n<p>Le champ nous est ouvert </p>\n<p>Et la moisson est grande! </p>\n<p>Pour servir notre Chef, </p>\n<p>Ne pensons plus à nous, </p>\n<p>En avant vers le but! </p>\n<p>Le Maître le demande. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Travaillons et luttons! </p>\n<p>Sans jamais nous lasser, </p>\n<p>De notre Rédempteur </p>\n<p>Élevons la bannière, </p>\n<p>Fidèles jusqu&#39;au bout, </p>\n<p>Sachons persévérer, </p>\n<p>Le repos nous attend </p>\n<p>Dans la pleine lumière! </p>\n','Travaillons-et-Luttons',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1513,'f3646ae6-ae06-4a28-9089-5a8e9f8a2519',0,0,'Il est une sainte guerre',222,'222_CE_fr','1.\nIl est une sainte guerre,\nIl est un combat divin,\nEntre le ciel et la terre,\nEntre le mal et le bien.\n\n\n2.\nCertaine est notre victoire,\nLuttons sans crainte et sans peur;\nNous combattons pour la gloire\nDe Jésus, le Rédempteur.\n\n\n3.\nLa justice est notre armure,\nLa foi, notre bouclier,\nLa vérité, pour ceinture,\nDoit à jamais nous lier.\n\n\n4.\nC\'est une épée invincible\nQu\'on voit briller dans nos mains,\nC\'est la Parole infaillible\nDu Dieu Sauveur des humains.\n\n\n5.\nSous la bannière immortelle\nLes coeurs en haut, nous marchons;\nDe la patrie éternelle\nPas à pas nous approchons.\n\n\n6.\nAprès la lutte suprême\nLa couronne nous attend;\nJésus la mettra Lui-même\nSur le front du combattant!','<p><strong>1.</strong> </p>\n<p>Il est une sainte guerre, </p>\n<p>Il est un combat divin, </p>\n<p>Entre le ciel et la terre, </p>\n<p>Entre le mal et le bien. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Certaine est notre victoire, </p>\n<p>Luttons sans crainte et sans peur; </p>\n<p>Nous combattons pour la gloire </p>\n<p>De Jésus, le Rédempteur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La justice est notre armure, </p>\n<p>La foi, notre bouclier, </p>\n<p>La vérité, pour ceinture, </p>\n<p>Doit à jamais nous lier. </p>\n<p><br/> <strong>4.</strong></p>\n<p>C&#39;est une épée invincible </p>\n<p>Qu&#39;on voit briller dans nos mains, </p>\n<p>C&#39;est la Parole infaillible </p>\n<p>Du Dieu Sauveur des humains. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Sous la bannière immortelle </p>\n<p>Les coeurs en haut, nous marchons; </p>\n<p>De la patrie éternelle </p>\n<p>Pas à pas nous approchons. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Après la lutte suprême </p>\n<p>La couronne nous attend; </p>\n<p>Jésus la mettra Lui-même </p>\n<p>Sur le front du combattant! </p>\n','Il-est-une-sainte-guerre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1514,'1ed02f21-3690-4b72-8728-95c5ff45abf5',0,0,'La voix de Christ nous appelle',231,'231_CE_fr','1.\nLa voix de Christ nous appelle;\nIl est temps de s\'éveiller:\n« La moisson est vaste et belle!\nQui veut pour moi travailler? »\nC\'est ton Sauveur, ô mon frère,\nDon\'t l\'appel s\'adresse à toi;\nRéponds-lui d\'un coeur sincère:\n« Me voici, Maître, prends-moi! »\n\n\n2.\nSans franchir les mers bruyantes,\nTu peux annoncer Jésus.\nQue d\'âmes insouciantes,\nDe coeurs souffrant et perdus!\nAutour de nous l\'oeuvre est grande,\nMais petite est notre foi.\nA Jésus qui nous commande\nRépondons: « Maître, aide-moi! »\n\n\n3.\nSi d\'un sublime langage\nTu n\'as pas reçu le don,\nTu peux rendre témoignage\nQu\'en Jésus est le pardon;\nA ton frère, tu peux dire\nCe que Christ a fait pour toi.\nPour que lui-même t\'inspire,\nDis-Lui: « Maître, enseigne-moi! »\n\n\n4.\nMais que nul ne nous entende\nDire encore: « Je ne puis rien! »\nLorsque Jésus nous commande\nDe faire et d\'aimer le bien.\nPoursuivons l\'oeuvre bénie\nAvec zèle, amour et foi,\nPuis, notre tâche, finie,\nNous dirons: « Maître, prends-moi! »','<p><strong>1.</strong> </p>\n<p>La voix de Christ nous appelle; </p>\n<p>Il est temps de s&#39;éveiller: </p>\n<p>« La moisson est vaste et belle! </p>\n<p>Qui veut pour moi travailler? » </p>\n<p>C&#39;est ton Sauveur, ô mon frère, </p>\n<p>Don&#39;t l&#39;appel s&#39;adresse à toi; </p>\n<p>Réponds-lui d&#39;un coeur sincère: </p>\n<p>« Me voici, Maître, prends-moi! » </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sans franchir les mers bruyantes, </p>\n<p>Tu peux annoncer Jésus. </p>\n<p>Que d&#39;âmes insouciantes, </p>\n<p>De coeurs souffrant et perdus! </p>\n<p>Autour de nous l&#39;oeuvre est grande, </p>\n<p>Mais petite est notre foi. </p>\n<p>A Jésus qui nous commande </p>\n<p>Répondons: « Maître, aide-moi! » </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si d&#39;un sublime langage </p>\n<p>Tu n&#39;as pas reçu le don, </p>\n<p>Tu peux rendre témoignage </p>\n<p>Qu&#39;en Jésus est le pardon; </p>\n<p>A ton frère, tu peux dire </p>\n<p>Ce que Christ a fait pour toi. </p>\n<p>Pour que lui-même t&#39;inspire, </p>\n<p>Dis-Lui: « Maître, enseigne-moi! » </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais que nul ne nous entende </p>\n<p>Dire encore: « Je ne puis rien! » </p>\n<p>Lorsque Jésus nous commande </p>\n<p>De faire et d&#39;aimer le bien. </p>\n<p>Poursuivons l&#39;oeuvre bénie </p>\n<p>Avec zèle, amour et foi, </p>\n<p>Puis, notre tâche, finie, </p>\n<p>Nous dirons: « Maître, prends-moi! » </p>\n','La-voix-de-Christ-nous-appelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1515,'b00256c8-9faa-4ad6-8c36-556b95dc7afe',0,0,'Le temps est court pour accomplir la tâche',242,'242_CE_fr','1.\nLe temps est court pour accomplir la tâche\nEnvers soi-même, envers l\'humanité.\nIl faut agir sans trêve et sans relâche,\nIl faut semer pour l\'immortalité.\n\nRefrain\nCar doucement, mais sûrement,\nMa barque vogue au port.\nJe m\'en vais vers les cieux,\nJe m\'en vais vers mon Dieu;\nCar doucement, mais sûrement,\nMa barque vogue au port,\nJ\'entends déjà l\'écho\nDes chants de l\'autre bord.\n\n\n2.\nLe temps est court pour dépouiller notre âme\nDe tout fardeau provenant du péché,\nEt ranimer en elle enfin la flamme,\nAutel divin d\'un coeur humble et brisé.\n\n\n3.\nLe temps est court pour aider notre frère;\nTendons-lui donc la main d\'amour, de foi.\nOh! Pardonnons, et semons sur la terre\nLe grain qui meurt et qui renaît cent fois.\n\n\n4.\nLe temps est court, les vagues de la vie\nAuront bientôt poussé ma barque au port.\nQue d\'âmes souffrant loin de la patrie,\nIndiquons-leur l\'unique entrée au port.','<p><strong>1.</strong> </p>\n<p>Le temps est court pour accomplir la tâche </p>\n<p>Envers soi-même, envers l&#39;humanité. </p>\n<p>Il faut agir sans trêve et sans relâche, </p>\n<p>Il faut semer pour l&#39;immortalité. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Car doucement, mais sûrement,</strong> </p>\n<p><strong>Ma barque vogue au port.</strong> </p>\n<p><strong>Je m&#39;en vais vers les cieux,</strong> </p>\n<p><strong>Je m&#39;en vais vers mon Dieu;</strong> </p>\n<p><strong>Car doucement, mais sûrement,</strong> </p>\n<p><strong>Ma barque vogue au port,</strong> </p>\n<p><strong>J&#39;entends déjà l&#39;écho</strong> </p>\n<p><strong>Des chants de l&#39;autre bord.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le temps est court pour dépouiller notre âme </p>\n<p>De tout fardeau provenant du péché, </p>\n<p>Et ranimer en elle enfin la flamme, </p>\n<p>Autel divin d&#39;un coeur humble et brisé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le temps est court pour aider notre frère; </p>\n<p>Tendons-lui donc la main d&#39;amour, de foi. </p>\n<p>Oh! Pardonnons, et semons sur la terre </p>\n<p>Le grain qui meurt et qui renaît cent fois. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le temps est court, les vagues de la vie </p>\n<p>Auront bientôt poussé ma barque au port. </p>\n<p>Que d&#39;âmes souffrant loin de la patrie, </p>\n<p>Indiquons-leur l&#39;unique entrée au port. </p>\n','Le-temps-est-court-pour-accomplir-la-tache',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1516,'6ce0b946-f2ab-4159-b7c4-73e4c84e098c',0,0,'Venez au Sauveur qui vous aime',248,'248_CE_fr','1.\nVenez au Sauveur qui vous aime,\nVenez, Il a brisé vos fer;\nIl veut vous recevoir Lui-même,\nSes bras vous sont ouverts.\n\nRefrain\nOh!  Quel beau jour, Sauveur fidèle,\nQuand, nous appuyant sur ton bras,\nDans la demeure paternelle\nNous porterons nos pas!\n\n\n2.\nVenez, pécheurs, Il vous appelle,\nLe bonheur est dans son amour.\nAh!  Donnez-lui ce coeur rebelle,\nDonnez-le sans retour.\n\n\n3.\nLe temps s\'enfuit, l\'heure s\'écoule,\nQui sait si nous vivrons demain?\nJésus est ici dans la foule;\nAh!  Saisissez sa main!','<p><strong>1.</strong> </p>\n<p>Venez au Sauveur qui vous aime, </p>\n<p>Venez, Il a brisé vos fer; </p>\n<p>Il veut vous recevoir Lui-même, </p>\n<p>Ses bras vous sont ouverts. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh!  Quel beau jour, Sauveur fidèle,</strong> </p>\n<p><strong>Quand, nous appuyant sur ton bras,</strong> </p>\n<p><strong>Dans la demeure paternelle</strong> </p>\n<p><strong>Nous porterons nos pas!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Venez, pécheurs, Il vous appelle, </p>\n<p>Le bonheur est dans son amour. </p>\n<p>Ah!  Donnez-lui ce coeur rebelle, </p>\n<p>Donnez-le sans retour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le temps s&#39;enfuit, l&#39;heure s&#39;écoule, </p>\n<p>Qui sait si nous vivrons demain? </p>\n<p>Jésus est ici dans la foule; </p>\n<p>Ah!  Saisissez sa main! </p>\n','Venez-au-Sauveur-qui-vous-aime',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1517,'16840ade-e528-48ae-8a96-acc509e27214',0,0,'Ô seigneur, bénis la parole',266,'266_CE_fr','1.\nO Seigneur, bénis la parole\nQue nous venons d\'ouïr.\nNe permets pas qu\'elle s\'envole\nDe notre souvenir!\n\n\n2.\nJaloux de ta sainte influence,\nTrop souvent le Malin\nAccourt, et ravit la semence\nQue répandit ta main.\n\n\n3.\nTrop souvent elle est sans racine\nEt un terrain pierreux;\nLe soleil vient: elle décline\nSous l\'ardeur de ses feux.\n\n\n4.\nTrop souvent les soucis du monde\nOu ses biens sans valeur\nDétruisant sa vertu féconde,\nL\'étouffent dans le coeur.\n\n\n5.\nAh! Plutôt que ton Évangile\nS\'emparant de nos coeurs,\nCes quelques grains en donnent mille\nA ta gloire, ô Seigneur!','<p><strong>1.</strong> </p>\n<p>O Seigneur, bénis la parole </p>\n<p>Que nous venons d&#39;ouïr. </p>\n<p>Ne permets pas qu&#39;elle s&#39;envole </p>\n<p>De notre souvenir! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jaloux de ta sainte influence, </p>\n<p>Trop souvent le Malin </p>\n<p>Accourt, et ravit la semence </p>\n<p>Que répandit ta main. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Trop souvent elle est sans racine </p>\n<p>Et un terrain pierreux; </p>\n<p>Le soleil vient: elle décline </p>\n<p>Sous l&#39;ardeur de ses feux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Trop souvent les soucis du monde </p>\n<p>Ou ses biens sans valeur </p>\n<p>Détruisant sa vertu féconde, </p>\n<p>L&#39;étouffent dans le coeur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ah! Plutôt que ton Évangile </p>\n<p>S&#39;emparant de nos coeurs, </p>\n<p>Ces quelques grains en donnent mille </p>\n<p>A ta gloire, ô Seigneur! </p>\n','O-seigneur-benis-la-parole',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1518,'f535f993-85e0-4398-a2e7-1c23eaf16758',0,0,'Venez à Celui qui pardonne',278,'278_CE_fr','1.\nVenez à Celui qui pardonne\nAux pécheurs qui n\'espéraient plus!\nC\'est aux plus pauvres qu\'Il se donne.\nIl peut sauver les plus perdus.\nSon sang, répandu pour le monde,\nPeut seul, effacer et blanchir;\nCe que l\'océan, sous son onde,\nNe ferait pas même pâlir!\n\n\n2.\nVenez à Celui qui relève\nCe qu\'on vit descendre si bas\nQue leur salut paraît un rêve\nDont on rit et qu\'on ne croit pas.\nSur la pécheresse qui pleure,\nIl imprima son sceau divin;\nLe brigand, à la dernière heure,\nN\'implora pas sa grâce en vain.\n\n\n3.\nVenez à Celui qui console\nLes inconsolables douleurs.\nVenez apprendre à son école\nL\'art divin de sécher les pleurs.\nComme Il a répandu des larmes,\nIl peut aussi les essuyer;\nEt la douleur lui rend les armes,\nCar Il a souffert le premier.','<p><strong>1.</strong> </p>\n<p>Venez à Celui qui pardonne </p>\n<p>Aux pécheurs qui n&#39;espéraient plus! </p>\n<p>C&#39;est aux plus pauvres qu&#39;Il se donne. </p>\n<p>Il peut sauver les plus perdus. </p>\n<p>Son sang, répandu pour le monde, </p>\n<p>Peut seul, effacer et blanchir; </p>\n<p>Ce que l&#39;océan, sous son onde, </p>\n<p>Ne ferait pas même pâlir! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Venez à Celui qui relève </p>\n<p>Ce qu&#39;on vit descendre si bas </p>\n<p>Que leur salut paraît un rêve </p>\n<p>Dont on rit et qu&#39;on ne croit pas. </p>\n<p>Sur la pécheresse qui pleure, </p>\n<p>Il imprima son sceau divin; </p>\n<p>Le brigand, à la dernière heure, </p>\n<p>N&#39;implora pas sa grâce en vain. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Venez à Celui qui console </p>\n<p>Les inconsolables douleurs. </p>\n<p>Venez apprendre à son école </p>\n<p>L&#39;art divin de sécher les pleurs. </p>\n<p>Comme Il a répandu des larmes, </p>\n<p>Il peut aussi les essuyer; </p>\n<p>Et la douleur lui rend les armes, </p>\n<p>Car Il a souffert le premier. </p>\n','Venez-a-Celui-qui-pardonne',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1519,'6f2eedb3-33aa-4c8e-9748-1985ed2ab6a2',0,0,'Viens, âme qui pleures',288,'288_CE_fr','1.\nViens, âme qui pleures,\nViens à ton Sauveur;\nDans tes tristes heures,\nDis-lui ta douleur…\nDis tout bas ta plainte\nAu Seigneur Jésus…\nParle-lui sans crainte,\nEt ne pleure plus!\n\n\n2.\nSis tout à ce frère,\nA ce tendre ami,\nTon épreuve amère,\nTon deuil, ton souci.\nIl aime, Il console\nLes coeurs abattus;\nCrois à sa parole\nEt ne pleure plus!\n\n\n3.\nDis à d\'autres âmes\nPloyant sous le fait,\nQue tu les réclames\nPour le Dieu de paix.\nCalme leurs alarmes,\nMontre-leur Jésus;\nVa sécher leurs larmes,\nEt ne pleure plus!','<p><strong>1.</strong> </p>\n<p>Viens, âme qui pleures, </p>\n<p>Viens à ton Sauveur; </p>\n<p>Dans tes tristes heures, </p>\n<p>Dis-lui ta douleur… </p>\n<p>Dis tout bas ta plainte </p>\n<p>Au Seigneur Jésus… </p>\n<p>Parle-lui sans crainte, </p>\n<p>Et ne pleure plus! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sis tout à ce frère, </p>\n<p>A ce tendre ami, </p>\n<p>Ton épreuve amère, </p>\n<p>Ton deuil, ton souci. </p>\n<p>Il aime, Il console </p>\n<p>Les coeurs abattus; </p>\n<p>Crois à sa parole </p>\n<p>Et ne pleure plus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dis à d&#39;autres âmes </p>\n<p>Ployant sous le fait, </p>\n<p>Que tu les réclames </p>\n<p>Pour le Dieu de paix. </p>\n<p>Calme leurs alarmes, </p>\n<p>Montre-leur Jésus; </p>\n<p>Va sécher leurs larmes, </p>\n<p>Et ne pleure plus! </p>\n','Viens-ame-qui-pleures',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1520,'239444ee-ee7e-4a18-ba15-8257766bbe2c',0,0,'On frappe, on frappe, entends-tu?',298,'298_CE_fr','1.\nOn frappe… on frappe… entends-tu?\nPassant, qui donc cherches-tu?\nPourquoi, voyageur étranger,\nA ma porte es-tu venu?\nN\'es-tu pas quelque saint ange\nSur la terre descendu?\n\n\n2.\nC\'est Moi, c\'est Moi, ton Sauveur!\nJe veux entrer dans ton coeur.\nPourquoi, me laisser attendre?\nOuvre, c\'est un bienfaiteur\nQui chez toi s\'offre à répandre\nTous les trésors du bonheur.\n\n\n3.\nVraiment! Vraiment! C\'est sa voix!\nC\'est Lui! C\'est Lui! Je le vois.\nOh! Ta grâce est la plus forte,\nMon Sauveur, je te reçois!\nJe ne puis fermer la porte\nQuand tu me montres ta croix!','<p><strong>1.</strong> </p>\n<p>On frappe… on frappe… entends-tu? </p>\n<p>Passant, qui donc cherches-tu? </p>\n<p>Pourquoi, voyageur étranger, </p>\n<p>A ma porte es-tu venu? </p>\n<p>N&#39;es-tu pas quelque saint ange </p>\n<p>Sur la terre descendu? </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est Moi, c&#39;est Moi, ton Sauveur! </p>\n<p>Je veux entrer dans ton coeur. </p>\n<p>Pourquoi, me laisser attendre? </p>\n<p>Ouvre, c&#39;est un bienfaiteur </p>\n<p>Qui chez toi s&#39;offre à répandre </p>\n<p>Tous les trésors du bonheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vraiment! Vraiment! C&#39;est sa voix! </p>\n<p>C&#39;est Lui! C&#39;est Lui! Je le vois. </p>\n<p>Oh! Ta grâce est la plus forte, </p>\n<p>Mon Sauveur, je te reçois! </p>\n<p>Je ne puis fermer la porte </p>\n<p>Quand tu me montres ta croix! </p>\n','On-frappe-on-frappe-entends-tu',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1521,'92522605-f158-489d-b8ed-25d62dbce854',0,0,'Bien Loin De Toi, Mon Père',302,'302_CE_fr','1.\nBien loin de toi, mon Père,\nJ\'ai dissipé mes biens;\nDans ma douleur amère,\nJe reviens, je reviens.\n\n\n2.\nVois, mon âme est souffrante,\nEt mon corps épuisé;\nSaisis ma main tremblante,\nGuéris mon coeur brisé!\n\n\n3.\nLa honte et la misère\nOnt sillonné mon front;\nJ\'ai péché, tendre Père,\nJ\'implore ton pardon!\n\n\n4.\nNon, je ne suis pas digne\nD\'être appelé ton fils;\nDe tes grâces, indigne,\nJ\'ai méconnu le prix.\n\n\n5.\nMais, que vois-je à cette heure?\nPère, ô Père, c\'est Toi!\nDu seuil de ta demeure,\nTu t\'avances vers moi!\n\n\n6.\nO bonheur! Tu me donnes\nLe baiser paternel,\nO bonheur! Tu pardonnes:\nEt tu m\'ouvres le ciel!','<p><strong>1.</strong> </p>\n<p>Bien loin de toi, mon Père, </p>\n<p>J&#39;ai dissipé mes biens; </p>\n<p>Dans ma douleur amère, </p>\n<p>Je reviens, je reviens. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois, mon âme est souffrante, </p>\n<p>Et mon corps épuisé; </p>\n<p>Saisis ma main tremblante, </p>\n<p>Guéris mon coeur brisé! </p>\n<p><br/> <strong>3.</strong></p>\n<p>La honte et la misère </p>\n<p>Ont sillonné mon front; </p>\n<p>J&#39;ai péché, tendre Père, </p>\n<p>J&#39;implore ton pardon! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Non, je ne suis pas digne </p>\n<p>D&#39;être appelé ton fils; </p>\n<p>De tes grâces, indigne, </p>\n<p>J&#39;ai méconnu le prix. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mais, que vois-je à cette heure? </p>\n<p>Père, ô Père, c&#39;est Toi! </p>\n<p>Du seuil de ta demeure, </p>\n<p>Tu t&#39;avances vers moi! </p>\n<p><br/> <strong>6.</strong></p>\n<p>O bonheur! Tu me donnes </p>\n<p>Le baiser paternel, </p>\n<p>O bonheur! Tu pardonnes: </p>\n<p>Et tu m&#39;ouvres le ciel! </p>\n','Bien-Loin-De-Toi-Mon-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1522,'df08c38c-e0ba-4bf2-bced-6b66b47231f1',0,0,'Dors, bien-aimé, ton œuvre est terminée',315,'315_CE_fr','1.\nDors, bien-aimé(e), ton heure est terminée,\nVoici pour toi le soir de la journée.\nDu grand repos l\'heure est enfin sonnée,\nA Dieu, mon frère (ma soeur), à Dieu!\n\n\n2.\nSi nous t\'aimons, Christ t\'aime davantage,\nIl t\'a conduit dans ton pèlerinage,\nIl te soutient dans le sombre passage,\nA Dieu, mon frère (ma soeur), à Dieu!\n\n\n3.\nÂme fidèle, entre dans la patrie\nD\'où la souffrance est à jamais bannie;\nBois à longs traits au fleuve de la Vie,\nÀ Dieu, mon frère (ma soeur), à Dieu!\n\n\n4.\nSublime espoir! Jésus-Christ va paraître;\nPour saluer le retour de son Maître,\nTon corps, bientôt, glorieux va renaître,\nÀ Dieu, mon frère (ma soeur), à Dieu!','<p><strong>1.</strong> </p>\n<p>Dors, bien-aimé(e), ton heure est terminée, </p>\n<p>Voici pour toi le soir de la journée. </p>\n<p>Du grand repos l&#39;heure est enfin sonnée, </p>\n<p>A Dieu, mon frère (ma soeur), à Dieu! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si nous t&#39;aimons, Christ t&#39;aime davantage, </p>\n<p>Il t&#39;a conduit dans ton pèlerinage, </p>\n<p>Il te soutient dans le sombre passage, </p>\n<p>A Dieu, mon frère (ma soeur), à Dieu! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Âme fidèle, entre dans la patrie </p>\n<p>D&#39;où la souffrance est à jamais bannie; </p>\n<p>Bois à longs traits au fleuve de la Vie, </p>\n<p>À Dieu, mon frère (ma soeur), à Dieu! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sublime espoir! Jésus-Christ va paraître; </p>\n<p>Pour saluer le retour de son Maître, </p>\n<p>Ton corps, bientôt, glorieux va renaître, </p>\n<p>À Dieu, mon frère (ma soeur), à Dieu! </p>\n','Dors-bien-aime-ton-oeuvre-est-terminee',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1523,'edbe10d8-83c4-487f-bbf5-3643caf6acfd',0,0,'Tout joyeux bénissons le Seigneur',136,'136_CE_fr','1.\nTout joyeux bénissons le Seigneur,\nChantons et célébrons ses louanges,\nAdorons avec foi le Sauveur,\nNous joignant aux céleste phalanges.\n\nRefrain\nGloire à Dieu!  Gloire à Dieu!\nQue ce chant retentisse en tout lieu;\nGloire à Dieu!  Gloire à Dieu!\nQue ce chant retentisse en tout lieu!\n\n2.\nDieu, dans son incomparable amour,\nDu ciel envoya son Fils unique,\nEt la terre et les cieux, dans ce jour,\nS\'unissent pour chanter ce cantique:\n\n3.\nLe châtiment qui produit la paix,\nJésus-Christ la subi pour mon âme;\nIl voulut expier nos forfaits,\nEn mourant, lui, sur le bois infâme.\n\n4.\nNous voulons en retour, bon Sauveur,\nT\'aimer par-dessus tout autre chose;\nForme ton amour dans notre coeur,\nEt puis, de chacun de nous, dispose!','<p><strong>1.</strong> </p>\n<p>Tout joyeux bénissons le Seigneur, </p>\n<p>Chantons et célébrons ses louanges, </p>\n<p>Adorons avec foi le Sauveur, </p>\n<p>Nous joignant aux céleste phalanges. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à Dieu!  Gloire à Dieu!</strong> </p>\n<p><strong>Que ce chant retentisse en tout lieu;</strong> </p>\n<p><strong>Gloire à Dieu!  Gloire à Dieu!</strong> </p>\n<p><strong>Que ce chant retentisse en tout lieu!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dieu, dans son incomparable amour, </p>\n<p>Du ciel envoya son Fils unique, </p>\n<p>Et la terre et les cieux, dans ce jour, </p>\n<p>S&#39;unissent pour chanter ce cantique: </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le châtiment qui produit la paix, </p>\n<p>Jésus-Christ la subi pour mon âme; </p>\n<p>Il voulut expier nos forfaits, </p>\n<p>En mourant, lui, sur le bois infâme. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nous voulons en retour, bon Sauveur, </p>\n<p>T&#39;aimer par-dessus tout autre chose; </p>\n<p>Forme ton amour dans notre coeur, </p>\n<p>Et puis, de chacun de nous, dispose! </p>\n','Tout-joyeux-benissons-le-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1524,'b7066414-73b0-48ce-b725-8ec9608eb456',0,0,'Marchons Avec Joie',147,'147_CE_fr','1.\nMarchons avec joie\nDans le bon chemin,\nDans l\'étroite voie\nDu bonheur sans fin.\nLaissons en arrière\nLes biens d\'ici-bas;\nPrions notre Père\nDe guider nos pas.\nComptant sur sa grâce,\nRemplis de sa paix,\nQue rien n\'embarrasse\nNos pas désormais.\n\n\n2.\nSi rude est la route,\nGlissant le sentier,\nLe Seigneur écoute\nQui le sait prier.\nIl est la lumière,\nLe Libérateur\nDu coeur qui n\'espère\nQu\'en son Dieu-Sauveur.\nSa toute-puissance\nDéfend le croyant;\nSon amour immense\nSauve son enfant.\n\n\n3.\nEt quand sonne l\'heure\nDe quitter ces lieux,\nIl a pour demeure\nLe palais des cieux!\nVêtements de gloire,\nD\'immortalité,\nPalmes de victoire\nPour l\'éternité!\nVoilà ce que donne\nLe Seigneur Jésus!\nVoilà la couronne\nDe tous les élus.','<p><strong>1.</strong> </p>\n<p>Marchons avec joie </p>\n<p>Dans le bon chemin, </p>\n<p>Dans l&#39;étroite voie </p>\n<p>Du bonheur sans fin. </p>\n<p>Laissons en arrière </p>\n<p>Les biens d&#39;ici-bas; </p>\n<p>Prions notre Père </p>\n<p>De guider nos pas. </p>\n<p>Comptant sur sa grâce, </p>\n<p>Remplis de sa paix, </p>\n<p>Que rien n&#39;embarrasse </p>\n<p>Nos pas désormais. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si rude est la route, </p>\n<p>Glissant le sentier, </p>\n<p>Le Seigneur écoute </p>\n<p>Qui le sait prier. </p>\n<p>Il est la lumière, </p>\n<p>Le Libérateur </p>\n<p>Du coeur qui n&#39;espère </p>\n<p>Qu&#39;en son Dieu-Sauveur. </p>\n<p>Sa toute-puissance </p>\n<p>Défend le croyant; </p>\n<p>Son amour immense </p>\n<p>Sauve son enfant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et quand sonne l&#39;heure </p>\n<p>De quitter ces lieux, </p>\n<p>Il a pour demeure </p>\n<p>Le palais des cieux! </p>\n<p>Vêtements de gloire, </p>\n<p>D&#39;immortalité, </p>\n<p>Palmes de victoire </p>\n<p>Pour l&#39;éternité! </p>\n<p>Voilà ce que donne </p>\n<p>Le Seigneur Jésus! </p>\n<p>Voilà la couronne </p>\n<p>De tous les élus. </p>\n','Marchons-Avec-Joie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1525,'01c55d32-7c4b-4fa1-bfa3-2ce9b950b94c',0,0,'Veille au matin',157,'157_CE_fr','1.\nVeille au matin,\nQuand un ciel sans nuage\nSemble annoncer\nUn jour calme et serein;\nC\'est dans ton coeur\nQue peut gronder l\'orage\nQui fait tomber le pèlerin.\n\nRefrain\nVeille au matin, veille le soir,\nVeille et prier toujours.\n\n\n2.\nVeille à midi,\nQuand les bruits de la terre\nFont oublier\nLe céleste séjours;\nTrouve un instant\nPour être solitaire\nDans la prière et dans l\'amour.\n\n\n3.\nVeille le soir,\nQuand se fait le silence;\nPense aux bienfaits\nDe ton céleste Ami,\nCherche avec soin\nSa divine présence,\nVerse en son coeur tout ton souci.\n\n\n4.\nVeille toujours,\nEn tous lieux, à toute heure,\nCar l\'ennemi\nTe guette à chaque instant,\nPour se glisser\nDans la sainte demeure\nOù doit régner le Tout-Puissant.','<p><strong>1.</strong> </p>\n<p>Veille au matin, </p>\n<p>Quand un ciel sans nuage </p>\n<p>Semble annoncer </p>\n<p>Un jour calme et serein; </p>\n<p>C&#39;est dans ton coeur </p>\n<p>Que peut gronder l&#39;orage </p>\n<p>Qui fait tomber le pèlerin. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Veille au matin, veille le soir,</strong> </p>\n<p><strong>Veille et prier toujours.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Veille à midi, </p>\n<p>Quand les bruits de la terre </p>\n<p>Font oublier </p>\n<p>Le céleste séjours; </p>\n<p>Trouve un instant </p>\n<p>Pour être solitaire </p>\n<p>Dans la prière et dans l&#39;amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Veille le soir, </p>\n<p>Quand se fait le silence; </p>\n<p>Pense aux bienfaits </p>\n<p>De ton céleste Ami, </p>\n<p>Cherche avec soin </p>\n<p>Sa divine présence, </p>\n<p>Verse en son coeur tout ton souci. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Veille toujours, </p>\n<p>En tous lieux, à toute heure, </p>\n<p>Car l&#39;ennemi </p>\n<p>Te guette à chaque instant, </p>\n<p>Pour se glisser </p>\n<p>Dans la sainte demeure </p>\n<p>Où doit régner le Tout-Puissant. </p>\n','Veille-au-matin',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1526,'7a9a9f71-5f6b-4aee-8863-0861c9d8ebb7',0,0,'Repose ton âme',173,'173_CE_fr','1.\nRepose ton âme\nDans la sainteté,\nDe ton Dieu proclame\nLa fidélité;\nLa sainte Parole\nDont il te nourrit\nCondamne et console,\nBlesse, mais guérit.\n\n\n2.\nRepose ton âme\nDans la sainteté,\nNe crains ni le blâme,\nNi l\'adversité;\nTout près de ton guide,\nLa main dans sa main,\nLe désert aride\nFleurira soudain.\n\n\n3.\nRepose ton âme\nDans la sainteté,\nL\'Esprit Saint réclame\nTon activité;\nJésus, par sa grâce,\nDemeurant en toi,\nChoisira ta place\nPour l\'oeuvre du Roi.\n\n\n4.\nRepose ton âme\nDans la sainteté,\nEntretiens la flamme\nDe la charité.\nEn suivant le Maître\nDoux, humble de coeur,\nTu feras connaître,\nSa grâce au pécheur.\n\n\n5.\nRepose ton âme\nDans la sainteté,\nQuand la foule acclame\nLa mondanité;\nPense à la couronne\nPromise au vainqueur.\nVa, le ciel l\'ordonne,\nServir ton Sauveur!','<p><strong>1.</strong> </p>\n<p>Repose ton âme </p>\n<p>Dans la sainteté, </p>\n<p>De ton Dieu proclame </p>\n<p>La fidélité; </p>\n<p>La sainte Parole </p>\n<p>Dont il te nourrit </p>\n<p>Condamne et console, </p>\n<p>Blesse, mais guérit. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Repose ton âme </p>\n<p>Dans la sainteté, </p>\n<p>Ne crains ni le blâme, </p>\n<p>Ni l&#39;adversité; </p>\n<p>Tout près de ton guide, </p>\n<p>La main dans sa main, </p>\n<p>Le désert aride </p>\n<p>Fleurira soudain. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Repose ton âme </p>\n<p>Dans la sainteté, </p>\n<p>L&#39;Esprit Saint réclame </p>\n<p>Ton activité; </p>\n<p>Jésus, par sa grâce, </p>\n<p>Demeurant en toi, </p>\n<p>Choisira ta place </p>\n<p>Pour l&#39;oeuvre du Roi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Repose ton âme </p>\n<p>Dans la sainteté, </p>\n<p>Entretiens la flamme </p>\n<p>De la charité. </p>\n<p>En suivant le Maître </p>\n<p>Doux, humble de coeur, </p>\n<p>Tu feras connaître, </p>\n<p>Sa grâce au pécheur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Repose ton âme </p>\n<p>Dans la sainteté, </p>\n<p>Quand la foule acclame </p>\n<p>La mondanité; </p>\n<p>Pense à la couronne </p>\n<p>Promise au vainqueur. </p>\n<p>Va, le ciel l&#39;ordonne, </p>\n<p>Servir ton Sauveur! </p>\n','Repose-ton-ame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1527,'b475c802-921d-471e-9063-c41ff6bfe25d',0,0,'Par ce chemin solitaire',178,'178_CE_fr','1.\nPar ce chemin solitaire,\nVoyageurs, où courez-vous?\nUne couronne immortelle\nQue Christ prépara pour nous.\nPar delà plaines cimes,\nVers ces demeures sublimes,\nVers  ces demeures sublimes,\nAmis, venez avec nous!  (bis 2 lignes)\n\n\n2.\nDans cette terre nouvelle,\nVoyageurs, qu\'espérez-vous?\nUne couronne immortelle\nQue Christ mérita pour nous.\nLà, toujours en sa présence,\nPlus de larmes, de souffrance,\nPlus de larmes, de souffrance,\nAmis, venez avec nous!  (bis 2 lignes)\n\n\n3.\nBien faible est votre cortège:\nQuels dangers affrondez-vous?\nLe Tout-Puissant nous protège\nEt son Ange est avec nous.\nL\'Éternel est notre égide;\nNous avons Jésus pour guide,\nNous avons Jésus pour guide,\nAmis, venez avec nous!  (bis 2 lignes)\n\n\n4.\nTrouverons-nous de la place?\nCe bonheur est-il pour nous?\nVenez! En ce jour de grâce,\nLe ciel est ouvert pour tous.\nA la source de la vie\nDieu lui-même vous convie,\nDieu lui-même vous convie;\nAmis, venez avec nous!  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Par ce chemin solitaire, </p>\n<p>Voyageurs, où courez-vous? </p>\n<p>Une couronne immortelle </p>\n<p>Que Christ prépara pour nous. </p>\n<p>Par delà plaines cimes, </p>\n<p>Vers ces demeures sublimes, </p>\n<p>Vers  ces demeures sublimes, </p>\n<p>Amis, venez avec nous!  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans cette terre nouvelle, </p>\n<p>Voyageurs, qu&#39;espérez-vous? </p>\n<p>Une couronne immortelle </p>\n<p>Que Christ mérita pour nous. </p>\n<p>Là, toujours en sa présence, </p>\n<p>Plus de larmes, de souffrance, </p>\n<p>Plus de larmes, de souffrance, </p>\n<p>Amis, venez avec nous!  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bien faible est votre cortège: </p>\n<p>Quels dangers affrondez-vous? </p>\n<p>Le Tout-Puissant nous protège </p>\n<p>Et son Ange est avec nous. </p>\n<p>L&#39;Éternel est notre égide; </p>\n<p>Nous avons Jésus pour guide, </p>\n<p>Nous avons Jésus pour guide, </p>\n<p>Amis, venez avec nous!  (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Trouverons-nous de la place? </p>\n<p>Ce bonheur est-il pour nous? </p>\n<p>Venez! En ce jour de grâce, </p>\n<p>Le ciel est ouvert pour tous. </p>\n<p>A la source de la vie </p>\n<p>Dieu lui-même vous convie, </p>\n<p>Dieu lui-même vous convie; </p>\n<p>Amis, venez avec nous!  (bis 2 lignes) </p>\n','Par-ce-chemin-solitaire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1528,'6c0af051-5dc5-475c-897c-e1e4120db9b9',0,0,'Mort avec Christ d\'une mort volontaire',183,'183_CE_fr','1.\nMort avec Christ d\'une mort volontaire,\nJe vis au ciel déjà sur cette terre;\nEn attendant son glorieux retour,\nJe suis gardé par Lui de jour en jour.\n\nRefrain\nJour après, gardé par son amour,\nJour après jour à l\'abri de son aile;\nC\'est le repos et la vie éternelle…\nJe t\'appartiens, ô Sauveur, pour toujours!\n\n\n2.\nIl n\'est douleur que son coeur ne partage,\nIl n\'est fardeaux dont Il ne me soulage;\nIl n\'est de pleurs qu\'Il ne daigne tarir,\nIl n\'est de maux qu\'Il ne puisse guérir.\n\n\n3.\nA mes soupirs s\'incline son oreille;\nLorsque je dors, près de moi son coeur veille;\nQuand pour toujours mes yeux se fermeront,\nSes bras puissants au ciel me porteront.','<p><strong>1.</strong> </p>\n<p>Mort avec Christ d&#39;une mort volontaire, </p>\n<p>Je vis au ciel déjà sur cette terre; </p>\n<p>En attendant son glorieux retour, </p>\n<p>Je suis gardé par Lui de jour en jour. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jour après, gardé par son amour,</strong> </p>\n<p><strong>Jour après jour à l&#39;abri de son aile;</strong> </p>\n<p><strong>C&#39;est le repos et la vie éternelle…</strong> </p>\n<p><strong>Je t&#39;appartiens, ô Sauveur, pour toujours!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il n&#39;est douleur que son coeur ne partage, </p>\n<p>Il n&#39;est fardeaux dont Il ne me soulage; </p>\n<p>Il n&#39;est de pleurs qu&#39;Il ne daigne tarir, </p>\n<p>Il n&#39;est de maux qu&#39;Il ne puisse guérir. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A mes soupirs s&#39;incline son oreille; </p>\n<p>Lorsque je dors, près de moi son coeur veille; </p>\n<p>Quand pour toujours mes yeux se fermeront, </p>\n<p>Ses bras puissants au ciel me porteront. </p>\n','Mort-avec-Christ-d\'une-mort-volontaire',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1529,'82fbb123-52be-4df8-8882-1704d0495212',0,0,'Jésus, Ami de mon âme',192,'192_CE_fr','1.\nJésus, Ami de mon âme,\nSauve-moi des grandes eaux!\nC\'est toi seul que je réclame,\nEn toi j\'ai le vrai repos.\n\nRefrain\nTa puissance est efficace,\nTon amour est infini;\nEn toi seul j\'ai toute grâce,\nTu seras mon ferme appui.\n\n\n2.\nTon nom seul est saint et juste,\nJe ne suis qu\'iniquité;\nEn Toi, Rédempteur auguste,\nTout est grâce et sainteté.\n\n\n3.\nGarde, ô Jésus, ma nacelle,\nViens la guider vers le port;\nPuis, dans ton amour fidèle,\nReçois mon âme à la mort.','<p><strong>1.</strong> </p>\n<p>Jésus, Ami de mon âme, </p>\n<p>Sauve-moi des grandes eaux! </p>\n<p>C&#39;est toi seul que je réclame, </p>\n<p>En toi j&#39;ai le vrai repos. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Ta puissance est efficace,</strong> </p>\n<p><strong>Ton amour est infini;</strong> </p>\n<p><strong>En toi seul j&#39;ai toute grâce,</strong> </p>\n<p><strong>Tu seras mon ferme appui.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton nom seul est saint et juste, </p>\n<p>Je ne suis qu&#39;iniquité; </p>\n<p>En Toi, Rédempteur auguste, </p>\n<p>Tout est grâce et sainteté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Garde, ô Jésus, ma nacelle, </p>\n<p>Viens la guider vers le port; </p>\n<p>Puis, dans ton amour fidèle, </p>\n<p>Reçois mon âme à la mort. </p>\n','Jesus-Ami-de-mon-ame',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1530,'cd2827cd-c63f-49fd-aac5-bbd0a20505a4',0,0,'Qu’il sont beaux sur les montagnes',225,'225_CE_fr','1.\nQu\'ils sont beaux sur les montagnes\nLes pieds de tes serviteurs,\nQui parcourent les campagnes,\nPrêchant la grâce aux pécheurs!\nO délicieuse vie\nD\'un serviteur de Jésus,\nQui pour son Maître s\'oublie,\nEn annonçant ses vertus!\n\n\n2.\nLibre de toute autre chaîne,\nLe chrétien qui sert son Dieu,\nDans la souffrance et la peine\nSuit son modèle en tout lieu.\nIl faut qu\'en vivante offrande\nIl s\'offre pour son Sauveur:\nC\'est là ce que Dieu demande\nD\'un fidèle serviteur.\n\n\n3.\nDites au coeur débonnaire\nQue Christ est sa guérison,\nEt que sa mort salutaire\nDétruit le mortel poison.\nAnnoncez au coeur timide,\nAu pécheur contrit, brisé,\nQue Christ fait d\'un coeur aride\nUn coeur de grâce arrosé.\n\n\n4.\nAu coeur accablé de peines\nQui tremble au seul nom de mort,\nAu captif chargé de chaînes\nQui n\'attend qu\'un triste sort,\nDites que Dieu, dans sa grâce,\nDonna son Fils au pécheur,\nEt que sa mort efficace\nNous mérita sa faveur.','<p><strong>1.</strong> </p>\n<p>Qu&#39;ils sont beaux sur les montagnes </p>\n<p>Les pieds de tes serviteurs, </p>\n<p>Qui parcourent les campagnes, </p>\n<p>Prêchant la grâce aux pécheurs! </p>\n<p>O délicieuse vie </p>\n<p>D&#39;un serviteur de Jésus, </p>\n<p>Qui pour son Maître s&#39;oublie, </p>\n<p>En annonçant ses vertus! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Libre de toute autre chaîne, </p>\n<p>Le chrétien qui sert son Dieu, </p>\n<p>Dans la souffrance et la peine </p>\n<p>Suit son modèle en tout lieu. </p>\n<p>Il faut qu&#39;en vivante offrande </p>\n<p>Il s&#39;offre pour son Sauveur: </p>\n<p>C&#39;est là ce que Dieu demande </p>\n<p>D&#39;un fidèle serviteur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dites au coeur débonnaire </p>\n<p>Que Christ est sa guérison, </p>\n<p>Et que sa mort salutaire </p>\n<p>Détruit le mortel poison. </p>\n<p>Annoncez au coeur timide, </p>\n<p>Au pécheur contrit, brisé, </p>\n<p>Que Christ fait d&#39;un coeur aride </p>\n<p>Un coeur de grâce arrosé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Au coeur accablé de peines </p>\n<p>Qui tremble au seul nom de mort, </p>\n<p>Au captif chargé de chaînes </p>\n<p>Qui n&#39;attend qu&#39;un triste sort, </p>\n<p>Dites que Dieu, dans sa grâce, </p>\n<p>Donna son Fils au pécheur, </p>\n<p>Et que sa mort efficace </p>\n<p>Nous mérita sa faveur. </p>\n','Qu\'il-sont-beaux-sur-les-montagnes',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1531,'5c948dff-965e-4a34-b4cb-878e8e4e7003',0,0,'Voyez, voyez! Les voici!',243,'243_CE_fr','1.\nVoyez, voyez!  Les voici!\nTous les peuples sont ici:\nC\'est la croix qui les attire…\nLes pécheurs, les malheureux,\nTous viennent grandir l\'empire\nDu Christ qui mourut pour eux.\n\n\n2.\nVoyez, voyez!  Les voici!\nEn foule accourent ici:\nDe leurs cités innombrables\nLes mystérieux Chinois,\nEt les Hindous misérables…\nTous regardent vers la croix.\n\n\n3.\nVoyez, voyez!  Les voici!\nLentement s\'approche aussi\nL\'Africain noir et sauvage.\nAux pieds du Crucifié,\nSon long et dur esclavage\nSera bientôt oublié!\n\n\n4.\nVoyez, voyez!  Les voici!\nLes enfants du Nord transi,\nLes fils de la steppe aride;\nL\'heure est sonné en tout lieu;\nLe coeur de l\'homme est avide\nDe te voir, ô Fils de Dieu!\n\n\n5.\nVoyez, voyez!  Les voici!\nTous sont réunis ici!\nC\'est la famille immortelle,\nEt tous chante d\'un seul coeur\nCette hymne toujours nouvelle:\nGloire, gloire au Rédempteur!','<p><strong>1.</strong> </p>\n<p>Voyez, voyez!  Les voici! </p>\n<p>Tous les peuples sont ici: </p>\n<p>C&#39;est la croix qui les attire… </p>\n<p>Les pécheurs, les malheureux, </p>\n<p>Tous viennent grandir l&#39;empire </p>\n<p>Du Christ qui mourut pour eux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voyez, voyez!  Les voici! </p>\n<p>En foule accourent ici: </p>\n<p>De leurs cités innombrables </p>\n<p>Les mystérieux Chinois, </p>\n<p>Et les Hindous misérables… </p>\n<p>Tous regardent vers la croix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Voyez, voyez!  Les voici! </p>\n<p>Lentement s&#39;approche aussi </p>\n<p>L&#39;Africain noir et sauvage. </p>\n<p>Aux pieds du Crucifié, </p>\n<p>Son long et dur esclavage </p>\n<p>Sera bientôt oublié! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Voyez, voyez!  Les voici! </p>\n<p>Les enfants du Nord transi, </p>\n<p>Les fils de la steppe aride; </p>\n<p>L&#39;heure est sonné en tout lieu; </p>\n<p>Le coeur de l&#39;homme est avide </p>\n<p>De te voir, ô Fils de Dieu! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Voyez, voyez!  Les voici! </p>\n<p>Tous sont réunis ici! </p>\n<p>C&#39;est la famille immortelle, </p>\n<p>Et tous chante d&#39;un seul coeur </p>\n<p>Cette hymne toujours nouvelle: </p>\n<p>Gloire, gloire au Rédempteur! </p>\n','Voyez-voyez!-Les-voici!',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1532,'5d9e1759-aa9c-4058-8d0d-59f626f9e942',0,0,'Je ne sais pas le jour',251,'251_CE_fr','1.\nJe ne sais pas le jour\nOù je verrai mon Roi,\nMais je sais qu\'Il me veut\nDans sa sainte demeure;\nLa lumière vaincra\nLes ombres à cette heure;\nCe sera la gloire pour moi…\n\nRefrain\nCe sera la gloire pour moi…(bis)\nLa lumière vaincra\nLes ombres à cette heure;\nCe sera la gloire pour moi.\n\n\n2.\nJe ne sais quels seront\nLes chants des bienheureux,\nLes accents, les accords\nDes hymnes angéliques,\nMais je sais que, joignant\nMa voix aux saints cantiques,\nBientôt j\'adorerai comme eux…\n\nRefrain\nBientôt j\'adorerai comme eux…(bis)\nMais je sais que, joignant\nMa voix aux saints cantiques,\nBientôt j\'adorerai comme eux.\n\n\n3.\nJe ne sais quel sera\nLe palais éternel,\nMais je sais que mon âme\nY sera reconnue.\nUn regard de Jésus\nSera ma bienvenue.\nPour moi, pour moi s\'ouvre le ciel…\n\nRefrain\nPour moi, pour moi s\'ouvre le ciel…(bis)\nUn regard de Jésus\nSera ma bienvenue.\nPour moi, pour moi s\'ouvre le ciel.','<p><strong>1.</strong> </p>\n<p>Je ne sais pas le jour </p>\n<p>Où je verrai mon Roi, </p>\n<p>Mais je sais qu&#39;Il me veut </p>\n<p>Dans sa sainte demeure; </p>\n<p>La lumière vaincra </p>\n<p>Les ombres à cette heure; </p>\n<p>Ce sera la gloire pour moi… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Ce sera la gloire pour moi…(bis)</strong> </p>\n<p><strong>La lumière vaincra</strong> </p>\n<p><strong>Les ombres à cette heure;</strong> </p>\n<p><strong>Ce sera la gloire pour moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je ne sais quels seront </p>\n<p>Les chants des bienheureux, </p>\n<p>Les accents, les accords </p>\n<p>Des hymnes angéliques, </p>\n<p>Mais je sais que, joignant </p>\n<p>Ma voix aux saints cantiques, </p>\n<p>Bientôt j&#39;adorerai comme eux… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Bientôt j&#39;adorerai comme eux…(bis)</strong> </p>\n<p><strong>Mais je sais que, joignant</strong> </p>\n<p><strong>Ma voix aux saints cantiques,</strong> </p>\n<p><strong>Bientôt j&#39;adorerai comme eux.</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je ne sais quel sera </p>\n<p>Le palais éternel, </p>\n<p>Mais je sais que mon âme </p>\n<p>Y sera reconnue. </p>\n<p>Un regard de Jésus </p>\n<p>Sera ma bienvenue. </p>\n<p>Pour moi, pour moi s&#39;ouvre le ciel… </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Pour moi, pour moi s&#39;ouvre le ciel…(bis)</strong> </p>\n<p><strong>Un regard de Jésus</strong> </p>\n<p><strong>Sera ma bienvenue.</strong> </p>\n<p><strong>Pour moi, pour moi s&#39;ouvre le ciel.</strong> </p>\n','Je-ne-sais-pas-le-jour',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1533,'0a47d8cc-be89-4b25-a6a4-6569c345cc21',0,0,'Oh! quel beau jour, o, devant ta face',260,'260_CE_fr','1.\nOh! Quel beau jour, où, devant sa face,\nTous tes rachetés apparaîtront,\nTu verras resplendir l\'aurore\nD\'un jour sans fin, d\'un jour nouveau.\n\nRefrain\nNombreux comme le sable des plages!  (bis)\nOh! Que ce sera beau,\nLorsque nous irons là-haut,\nAussi nombreux que le sable des plages!\n\n\n2.\nJe la vois, cette armée innombrable,\nSes rangs reflétant l\'éclat des cieux,\nTressaillant d\'un bonheur ineffable,\nSur le seuil du séjour radieux.\n\n\n3.\nDe son trône éclatant de lumière,\nJ\'entends la douce voix du Seigneur:\n« Venez, vous bien-aimés de mon Père,\nLa couronne est à chaque vainqueur. »\n\n\n4.\nPuis, au sein du vaste océan d\'anges,\nNos cohortes déversent leurs flots;\nL\'univers tremble au son des louanges;\nPlus de combats, de pleurs, de sanglots.','<p><strong>1.</strong> </p>\n<p>Oh! Quel beau jour, où, devant sa face, </p>\n<p>Tous tes rachetés apparaîtront, </p>\n<p>Tu verras resplendir l&#39;aurore </p>\n<p>D&#39;un jour sans fin, d&#39;un jour nouveau. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nombreux comme le sable des plages!  (bis)</strong> </p>\n<p><strong>Oh! Que ce sera beau,</strong> </p>\n<p><strong>Lorsque nous irons là-haut,</strong> </p>\n<p><strong>Aussi nombreux que le sable des plages!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je la vois, cette armée innombrable, </p>\n<p>Ses rangs reflétant l&#39;éclat des cieux, </p>\n<p>Tressaillant d&#39;un bonheur ineffable, </p>\n<p>Sur le seuil du séjour radieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>De son trône éclatant de lumière, </p>\n<p>J&#39;entends la douce voix du Seigneur: </p>\n<p>« Venez, vous bien-aimés de mon Père, </p>\n<p>La couronne est à chaque vainqueur. » </p>\n<p><br/> <strong>4.</strong></p>\n<p>Puis, au sein du vaste océan d&#39;anges, </p>\n<p>Nos cohortes déversent leurs flots; </p>\n<p>L&#39;univers tremble au son des louanges; </p>\n<p>Plus de combats, de pleurs, de sanglots. </p>\n','Oh!-quel-beau-jour-o-devant-ta-face',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1534,'544a8086-edf3-4d0b-b8a2-bb77736492d5',0,0,'Sans un Dieu puissant pour Père',277,'277_CE_fr','1.\nSans un Dieu puissant pour Père,\nQue ferez-vous?  (bis)\nSans un Dieu Sauveur pour Frère,\nQue ferez-vous?  (bis)\nPécheurs, l\'âme est immortelle,\nSans Dieu que deviendra-t-elle?\nVoici l\'heure solennelle:\nQue ferez-vous?  (bis)\n\n\n2.\nPossédez-vous un refuge?\nQue ferez-vous?  (bis)\nEn présence du grand Juge,\nQue ferez-vous?  (bis)\nVieillards que la mort talonne,\nJeunes gens que rien m\'étonne,\nPécheurs que la mort moissonne,\nQue ferez-vous?   (bis)\n\n\n3.\nFrères, reprenez courage,\nEspérez tous!  (bis)\nJésus apporte un message,\nIl est pour vous.  (bis)\nQue la mort se change en fête,\nChrist a payé votre dette!\nPour célébrer sa conquête,\nUnissons-nous, et chantons tous:\n\n\n4.\nPar l\'amour de Dieu le Père,\nJe suis sauvé!  (bis)\nPar le sang de Christ, mon Frère,\nJe suis lavé!  (bis)\nPlus de douleur, plus de plainte,\nPlus de chagrin, de contrainte.\nChante, mon coeur! Sois sans crainte!\nJe suis sauvé!  (bis)','<p><strong>1.</strong> </p>\n<p>Sans un Dieu puissant pour Père, </p>\n<p>Que ferez-vous?  (bis) </p>\n<p>Sans un Dieu Sauveur pour Frère, </p>\n<p>Que ferez-vous?  (bis) </p>\n<p>Pécheurs, l&#39;âme est immortelle, </p>\n<p>Sans Dieu que deviendra-t-elle? </p>\n<p>Voici l&#39;heure solennelle: </p>\n<p>Que ferez-vous?  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Possédez-vous un refuge? </p>\n<p>Que ferez-vous?  (bis) </p>\n<p>En présence du grand Juge, </p>\n<p>Que ferez-vous?  (bis) </p>\n<p>Vieillards que la mort talonne, </p>\n<p>Jeunes gens que rien m&#39;étonne, </p>\n<p>Pécheurs que la mort moissonne, </p>\n<p>Que ferez-vous?   (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Frères, reprenez courage, </p>\n<p>Espérez tous!  (bis) </p>\n<p>Jésus apporte un message, </p>\n<p>Il est pour vous.  (bis) </p>\n<p>Que la mort se change en fête, </p>\n<p>Christ a payé votre dette! </p>\n<p>Pour célébrer sa conquête, </p>\n<p>Unissons-nous, et chantons tous: </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par l&#39;amour de Dieu le Père, </p>\n<p>Je suis sauvé!  (bis) </p>\n<p>Par le sang de Christ, mon Frère, </p>\n<p>Je suis lavé!  (bis) </p>\n<p>Plus de douleur, plus de plainte, </p>\n<p>Plus de chagrin, de contrainte. </p>\n<p>Chante, mon coeur! Sois sans crainte! </p>\n<p>Je suis sauvé!  (bis) </p>\n','Sans-un-Dieu-puissant-pour-Pere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1535,'155dc6ba-9f83-48f0-9f9b-21106e9409e1',0,0,'Sans attendre, Je veux tendre',310,'310_CE_fr','1.\nSans attendre, - Je veux tendre\nAu bonheur promis; - Qui s\'élance,\nQui s\'avance – Obtiendra le prix.\nDe mon Dieu je suis l\'enfant,\nEt c\'est Lui qui me défend.\nDonc en route! – Point de doute,\nLe but est si grand!\n\n\n2.\nPrès du trône, - La couronne\nAttend le vainqueur. – Nulle trêve!\nQu\'on se lève! – A dit le Seigneur.\nD\'obéir, soyons heureux;\nPoint de tièdes, de peureux;\nQui se lasse, - Perd sa place\nAu banquet des cieux!\n\n\n3.\nD\'un pas ferme, - Jusqu\'au terme\nIl faut s\'avancer! – Dieu m\'observe,\nQu\'Il préserve – Mon pied de glisser!\nQue ce monde et ses attraits\nNe me séduisent jamais!\nSi sa haine – Se déchaîne,\nQue je sois en paix!\n\n\n4.\nDieu de grâce, - Que ta face\nLuise en mon chemin! – Père tendre,\nViens me prendre – Par ta forte main!\nToute puissance est à Toi,\nSubviens à ma faible foi;\nMa victoire, - C\'est ta gloire,\nO mon Dieu, mon Roi!','<p><strong>1.</strong> </p>\n<p>Sans attendre, - Je veux tendre </p>\n<p>Au bonheur promis; - Qui s&#39;élance, </p>\n<p>Qui s&#39;avance – Obtiendra le prix. </p>\n<p>De mon Dieu je suis l&#39;enfant, </p>\n<p>Et c&#39;est Lui qui me défend. </p>\n<p>Donc en route! – Point de doute, </p>\n<p>Le but est si grand! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Près du trône, - La couronne </p>\n<p>Attend le vainqueur. – Nulle trêve! </p>\n<p>Qu&#39;on se lève! – A dit le Seigneur. </p>\n<p>D&#39;obéir, soyons heureux; </p>\n<p>Point de tièdes, de peureux; </p>\n<p>Qui se lasse, - Perd sa place </p>\n<p>Au banquet des cieux! </p>\n<p><br/> <strong>3.</strong></p>\n<p>D&#39;un pas ferme, - Jusqu&#39;au terme </p>\n<p>Il faut s&#39;avancer! – Dieu m&#39;observe, </p>\n<p>Qu&#39;Il préserve – Mon pied de glisser! </p>\n<p>Que ce monde et ses attraits </p>\n<p>Ne me séduisent jamais! </p>\n<p>Si sa haine – Se déchaîne, </p>\n<p>Que je sois en paix! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dieu de grâce, - Que ta face </p>\n<p>Luise en mon chemin! – Père tendre, </p>\n<p>Viens me prendre – Par ta forte main! </p>\n<p>Toute puissance est à Toi, </p>\n<p>Subviens à ma faible foi; </p>\n<p>Ma victoire, - C&#39;est ta gloire, </p>\n<p>O mon Dieu, mon Roi! </p>\n','Sans-attendre-Je-veux-tendre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1536,'68c04dc2-e664-412a-8279-d3f8fe489379',0,0,'Gen lumyè nan nanm mwen jodi ya',37,'37_CE_ht','1.\nGin lumiè nan nan-m mouin jodi a,\nAvèk gloua pi gran pasé\nMin-m solèy la n-apé ouè nan sièl,\nKa Jézu té kléré-m.\n\nKè:\nOui, gin lumiè, gin bèl lumiè;\nMouin kontan, mouin vlé toujou chanté;\nLè Jézu montré amou li nan kè mouin\nLumiè sa toujou rété.\n\n\n2.\nGin kantik nan nan-m mouin jodi a,\nSé gnou kantik pou Jézu;\nÉ li konnin sa mouin pakab di,\nKa amou-l san mézi.\n\n\n3.\nGin la joua nan nan-m mouin jodi a,\nAk éspoua, louanj, amou,\nPou bi-infè li ban mouin tou lé jou,\nPou siè l-a ban mouin tou.','<p><strong>1.</strong> </p>\n<p>Gin lumiè nan nan-m mouin jodi a, </p>\n<p>Avèk gloua pi gran pasé </p>\n<p>Min-m solèy la n-apé ouè nan sièl, </p>\n<p>Ka Jézu té kléré-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui, gin lumiè, gin bèl lumiè;</strong> </p>\n<p><strong>Mouin kontan, mouin vlé toujou chanté;</strong> </p>\n<p><strong>Lè Jézu montré amou li nan kè mouin</strong> </p>\n<p><strong>Lumiè sa toujou rété.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin kantik nan nan-m mouin jodi a, </p>\n<p>Sé gnou kantik pou Jézu; </p>\n<p>É li konnin sa mouin pakab di, </p>\n<p>Ka amou-l san mézi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin la joua nan nan-m mouin jodi a, </p>\n<p>Ak éspoua, louanj, amou, </p>\n<p>Pou bi-infè li ban mouin tou lé jou, </p>\n<p>Pou siè l-a ban mouin tou. </p>\n','Gen-lumye-nan-nanm-mwen-jodi-ya',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1537,'3907479f-1afd-47c0-9a69-9ea24034cac0',0,0,'Ségné, Angnin M-Pa Ka Ba Ou',6,'6_CE_ht','1.\nSégnè angnin m-pa ka ba ou,\nSèl gnou kè ki bouké soufri,\nÉ san ou mouin pa kab géri,\nMizè mouin dévan ou.\n\nKè\nPran mouin jan ou ouè-m la,\nSan fòs é san souti-in\nJan ou ouè-m la,  (2 foua)\nO Jézu gran frè mouin.\n\n\n2.\nMouin pa janmin obéi ou,\nPéché kouvri-m la tèt o pié,\nÉ pou-m prézanté dévan ou\nPéché sèlman m-poté.\n\n\n3.\nMouin fèb nan kè, mouin fèb nan chè,\nPou-m rézisté dévan Satan;\nO Ségnè mouin, mouin fèb tout tan,\nMap rélé: ban-m lumiè.\n\n\n4.\nSan li koulé a va blanchi-m,\nSint-Espri ou va purifié-m\nRichès ou yo va anrichi-m,\nMon mouin! Vini sové-m.','<p><strong>1.</strong> </p>\n<p>Ségnè angnin m-pa ka ba ou, </p>\n<p>Sèl gnou kè ki bouké soufri, </p>\n<p>É san ou mouin pa kab géri, </p>\n<p>Mizè mouin dévan ou. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Pran mouin jan ou ouè-m la,</strong> </p>\n<p><strong>San fòs é san souti-in</strong> </p>\n<p><strong>Jan ou ouè-m la,  (2 foua)</strong> </p>\n<p><strong>O Jézu gran frè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin pa janmin obéi ou, </p>\n<p>Péché kouvri-m la tèt o pié, </p>\n<p>É pou-m prézanté dévan ou </p>\n<p>Péché sèlman m-poté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin fèb nan kè, mouin fèb nan chè, </p>\n<p>Pou-m rézisté dévan Satan; </p>\n<p>O Ségnè mouin, mouin fèb tout tan, </p>\n<p>Map rélé: ban-m lumiè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>San li koulé a va blanchi-m, </p>\n<p>Sint-Espri ou va purifié-m </p>\n<p>Richès ou yo va anrichi-m, </p>\n<p>Mon mouin! Vini sové-m. </p>\n','Segne-Angnin-M-Pa-Ka-Ba-Ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1538,'9341995d-9178-45f8-a291-b350cb881f35',0,0,'Accourez Au Baptême',334,'334_CE_fr','1.\nAccourez au baptême,\nA ces eaux salutaires,\nAccourez, ô chrétiens,\nFidèles et fervents,\nEt que vos saints accents  (bis)\nRetentissent dans l\'air,\nAinsi que vos prières!\n\n\n2.\nAh! Louez le Seigneur,\nCe Dieu bon, tutélaire,\nQui vous accorde, en Père,\nUn bien si précieux;\nEt que vos coeurs joyeux,  (bis)\nEn sortant de cette onde,\nAdorent votre Père!\n\n\n3.\nChrétiens, souvenez-vous\nQue Jésus-Christ lui-même\nY fut plongé par Jean;\nSatisfait, glorieux,\nIl vit s\'ouvrir les cieux,  (bis)\nAlors qu\'au sein des flots\nIl reçut le baptême.\n\n\n4.\nTelle qu\'une rapide,\nUne invisible flamme,\nQui d\'ici-bas s\'élance\nAu céleste séjour,\nCet acte heureux d\'amour  (bis)\nVa porter jusqu\'à Dieu\nL\'hommage de notre âme\n\n5.\nOui, Dieu grave lui-même\nAux portes éternelles\nCe zèle, cette foi,\nCes pieux sentiments;\nEt sa grâce en torrents  (bis)\nCoule toujours sur ceux\nQui lui restent fidèles.','<p><strong>1.</strong> </p>\n<p>Accourez au baptême, </p>\n<p>A ces eaux salutaires, </p>\n<p>Accourez, ô chrétiens, </p>\n<p>Fidèles et fervents, </p>\n<p>Et que vos saints accents  (bis) </p>\n<p>Retentissent dans l&#39;air, </p>\n<p>Ainsi que vos prières! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ah! Louez le Seigneur, </p>\n<p>Ce Dieu bon, tutélaire, </p>\n<p>Qui vous accorde, en Père, </p>\n<p>Un bien si précieux; </p>\n<p>Et que vos coeurs joyeux,  (bis) </p>\n<p>En sortant de cette onde, </p>\n<p>Adorent votre Père! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chrétiens, souvenez-vous </p>\n<p>Que Jésus-Christ lui-même </p>\n<p>Y fut plongé par Jean; </p>\n<p>Satisfait, glorieux, </p>\n<p>Il vit s&#39;ouvrir les cieux,  (bis) </p>\n<p>Alors qu&#39;au sein des flots </p>\n<p>Il reçut le baptême. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Telle qu&#39;une rapide, </p>\n<p>Une invisible flamme, </p>\n<p>Qui d&#39;ici-bas s&#39;élance </p>\n<p>Au céleste séjour, </p>\n<p>Cet acte heureux d&#39;amour  (bis) </p>\n<p>Va porter jusqu&#39;à Dieu </p>\n<p>L&#39;hommage de notre âme </p>\n<p><br/> <strong>5.</strong></p>\n<p>Oui, Dieu grave lui-même </p>\n<p>Aux portes éternelles </p>\n<p>Ce zèle, cette foi, </p>\n<p>Ces pieux sentiments; </p>\n<p>Et sa grâce en torrents  (bis) </p>\n<p>Coule toujours sur ceux </p>\n<p>Qui lui restent fidèles. </p>\n','Accourez-Au-Bapteme',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1539,'1807e3e4-82ec-47b0-bbb3-7cad6c81ddbf',0,0,'Deux Mains Pour Servir Le Maître',331,'331_CE_fr','1.\nDeux mains pour servir le Maître,\nDeux pieds pour aller partout\nDire à tous: nous voulons être\nAu bon Sauveur humble et doux.\n\nRefrain\nSeigneur, prends toute ma vie,\nMon coeur et ma volonté;\nQue jamais je ne t\'oublie,\nDieu de grâce et de bonté!\n\n\n2.\nDeux oreilles pour entendre\nLe vrai message du ciel;\nLa mémoire pour apprendre\nLes leçons de l\'Éternel.\n\n\n3.\nDeux yeux pour lire le Livre\nQu\'il faut aimer toujours plus;\nC\'est le pain qui nous fait vivre,\nQui nous rend forts en Jésus.\n\n\n4.\nUn coeur pour être le trône,\nOù doit régner le Seigneur,\nUne vie qui se donne\nToute entière au Créateur.','<p><strong>1.</strong> </p>\n<p>Deux mains pour servir le Maître, </p>\n<p>Deux pieds pour aller partout </p>\n<p>Dire à tous: nous voulons être </p>\n<p>Au bon Sauveur humble et doux. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Seigneur, prends toute ma vie,</strong> </p>\n<p><strong>Mon coeur et ma volonté;</strong> </p>\n<p><strong>Que jamais je ne t&#39;oublie,</strong> </p>\n<p><strong>Dieu de grâce et de bonté!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Deux oreilles pour entendre </p>\n<p>Le vrai message du ciel; </p>\n<p>La mémoire pour apprendre </p>\n<p>Les leçons de l&#39;Éternel. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Deux yeux pour lire le Livre </p>\n<p>Qu&#39;il faut aimer toujours plus; </p>\n<p>C&#39;est le pain qui nous fait vivre, </p>\n<p>Qui nous rend forts en Jésus. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Un coeur pour être le trône, </p>\n<p>Où doit régner le Seigneur, </p>\n<p>Une vie qui se donne </p>\n<p>Toute entière au Créateur. </p>\n','Deux-Mains-Pour-Servir-Le-Maitre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1540,'94a23123-ca50-4bbc-accf-15aec27966e9',0,0,'Jézu, se nan prezans ou nanm mwen pran plezi',41,'41_CE_ht','1.\nJézu, sé nan prézans ou nan-m mouin pran plézi,\nOu konsolé-m nan gran tristès.\nOu sé joua-m la jounin, é chan mouin la nuit.\nOu ban-m éspoua, salu, richès.\n\n\n2.\nBon Bèjé, koté ou gadé mouton ou\nPou bay yo paturaj lamou?\nPouki sa mouin krié nan santié lan mò\nOu rété nan dézè san ou?\n\n\n3.\nPouki sa-m bézouin gayé isit san ou\nPou krié nan dézè pou pin?\nDétrès mouin va gnou la joua pou énmi ou\nÉ yo va réjoui nan pin-n mouin.\n\n\n4.\nLè-l gadé gingnin di mil zanj ki réjoui,\nÉ anpil ap tan-n parol li.\nLè-l palé tout étènité anvai\nAvèk louanj dé Jézu-Kri.\n\n\n5.\nBon Bèjé, mouin tandé é mouin va suiv ou,\nMa maché ak ou pa la foua.\nKinbé-m é défan-n mouin, mouin pou ou toujou\nÉ nan ou ma toujou gin joua','<p><strong>1.</strong> </p>\n<p>Jézu, sé nan prézans ou nan-m mouin pran plézi, </p>\n<p>Ou konsolé-m nan gran tristès. </p>\n<p>Ou sé joua-m la jounin, é chan mouin la nuit. </p>\n<p>Ou ban-m éspoua, salu, richès. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bon Bèjé, koté ou gadé mouton ou </p>\n<p>Pou bay yo paturaj lamou? </p>\n<p>Pouki sa mouin krié nan santié lan mò </p>\n<p>Ou rété nan dézè san ou? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pouki sa-m bézouin gayé isit san ou </p>\n<p>Pou krié nan dézè pou pin? </p>\n<p>Détrès mouin va gnou la joua pou énmi ou </p>\n<p>É yo va réjoui nan pin-n mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè-l gadé gingnin di mil zanj ki réjoui, </p>\n<p>É anpil ap tan-n parol li. </p>\n<p>Lè-l palé tout étènité anvai </p>\n<p>Avèk louanj dé Jézu-Kri. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Bon Bèjé, mouin tandé é mouin va suiv ou, </p>\n<p>Ma maché ak ou pa la foua. </p>\n<p>Kinbé-m é défan-n mouin, mouin pou ou toujou </p>\n<p>É nan ou ma toujou gin joua </p>\n','Jezu-se-nan-prezans-ou-nanm-mwen-pran-plezi',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1541,'58b8af1b-81cf-4253-94b3-7b3127f5e9de',0,0,'Dans Mon Ame Un Beau Soleil Brille',329,'329_CE_fr','1.\nDans mon âme un beau soleil brille;\nSon rayon doux et joyeux\nRépand un éclat qui scintille;\nC\'est le sourire de Dieu.\n\nRefrain\nOh! Quel beau soleil dans mon âme!\nIl éclaire, il illumine tout,\nA ses rayons mon coeur s\'enflamme,\nEt je vais chantant partout.\n\n\n2.\nMon coeur était plein de ténèbres\nQuand parut un jour nouveau,\nAu loin, fuyez ombres funèbres,\nDevant un soleil si beau!\n\n\n3.\nNuages des plaintes, du doute,\nGaîment je vous dis adieu!\nVoici resplendir sur ma route\nLe soleil dans un ciel bleu!\n\n\n4.\nO mon coeur, éclate en louanges,\nPour toi le soleil a lui;\nJe serai parmi les phalanges\nQui loueront Dieu jour et nuit!','<p><strong>1.</strong> </p>\n<p>Dans mon âme un beau soleil brille; </p>\n<p>Son rayon doux et joyeux </p>\n<p>Répand un éclat qui scintille; </p>\n<p>C&#39;est le sourire de Dieu. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Quel beau soleil dans mon âme!</strong> </p>\n<p><strong>Il éclaire, il illumine tout,</strong> </p>\n<p><strong>A ses rayons mon coeur s&#39;enflamme,</strong> </p>\n<p><strong>Et je vais chantant partout.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon coeur était plein de ténèbres </p>\n<p>Quand parut un jour nouveau, </p>\n<p>Au loin, fuyez ombres funèbres, </p>\n<p>Devant un soleil si beau! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nuages des plaintes, du doute, </p>\n<p>Gaîment je vous dis adieu! </p>\n<p>Voici resplendir sur ma route </p>\n<p>Le soleil dans un ciel bleu! </p>\n<p><br/> <strong>4.</strong></p>\n<p>O mon coeur, éclate en louanges, </p>\n<p>Pour toi le soleil a lui; </p>\n<p>Je serai parmi les phalanges </p>\n<p>Qui loueront Dieu jour et nuit! </p>\n','Dans-Mon-Ame-Un-Beau-Soleil-Brille',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1542,'0653d1cc-111f-446c-9f06-f00050548eb1',0,0,'Je Suis La Lumière',324,'324_CE_fr','1.\n« Je suis la lumière »\nA dit le Seigneur;\nAvec moi, mon frère,\nOuvre-lui ton coeur.\n\nRefrain\nLe monde est plein d\'ombre;\nBrillons, brillons bien,\nToi dans ton coin sombre\nEt moi dans le mien.\n\n\n2.\nSi la pure flamme\nParfois baisse un peu,\nVeillons sur notre âme,\nRavivons le feu.\n\n\n3.\nQuand le jour se voile\nAu clair firmament,\nLa plus humble étoile\nBrille doucement.','<p><strong>1.</strong> </p>\n<p>« Je suis la lumière » </p>\n<p>A dit le Seigneur; </p>\n<p>Avec moi, mon frère, </p>\n<p>Ouvre-lui ton coeur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Le monde est plein d&#39;ombre;</strong> </p>\n<p><strong>Brillons, brillons bien,</strong> </p>\n<p><strong>Toi dans ton coin sombre</strong> </p>\n<p><strong>Et moi dans le mien.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si la pure flamme </p>\n<p>Parfois baisse un peu, </p>\n<p>Veillons sur notre âme, </p>\n<p>Ravivons le feu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand le jour se voile </p>\n<p>Au clair firmament, </p>\n<p>La plus humble étoile </p>\n<p>Brille doucement. </p>\n','Je-Suis-La-Lumiere',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1543,'7617902d-a84a-4248-bcfe-328b817a46b8',0,0,'Nul Enfant N’est Trop Petit',323,'323_CE_fr','1.\nNul enfant n\'est trop petit\nPour la route étroite,\nQuand le Seigneur l\'y conduit,\nMarchant à sa droite.\nMême le plus jeune coeur\nPeut être un temple au Seigneur.  (bis 2 lignes)\n\n\n2.\nCe sont aussi les petits\nQue Jésus convie,\nEt don\'t les noms sont écrits\nAu Livre de vie,\nDans les parvis éternels,\nDieu reçoit ceux qui sont tels.  (bis 2 lignes)\n\n\n3.\nPuis, dans les concerts des cieux,\nLes voix enfantines\nUniront leurs chants joyeux\nAux hymnes divines.\nPetits et grands, d\'un seul coeur,\nLoueront alors le Seigneur.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Nul enfant n&#39;est trop petit </p>\n<p>Pour la route étroite, </p>\n<p>Quand le Seigneur l&#39;y conduit, </p>\n<p>Marchant à sa droite. </p>\n<p>Même le plus jeune coeur </p>\n<p>Peut être un temple au Seigneur.  (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ce sont aussi les petits </p>\n<p>Que Jésus convie, </p>\n<p>Et don&#39;t les noms sont écrits </p>\n<p>Au Livre de vie, </p>\n<p>Dans les parvis éternels, </p>\n<p>Dieu reçoit ceux qui sont tels.  (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Puis, dans les concerts des cieux, </p>\n<p>Les voix enfantines </p>\n<p>Uniront leurs chants joyeux </p>\n<p>Aux hymnes divines. </p>\n<p>Petits et grands, d&#39;un seul coeur, </p>\n<p>Loueront alors le Seigneur.  (bis 2 lignes) </p>\n','Nul-Enfant-N\'est-Trop-Petit',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1544,'1d18c459-d5f9-4619-940f-25a764db46cc',0,0,'Mwen pa bezouin pè',48,'48_CE_ht','1.\nMouin pa bézouin pè, Sovè mouin tou pré,\nÉ pou sové mouin Li prèt pou parèt.\nMa va prié anpil, Sé Kris kap aji,\nLè Li nan kannot mouin, mouin pap pè tanpèt.\n\n\n2.\nSi chémin mouin noua, sé Kris ki gid mouin\nMap obéi Li, L-a protéjé mouin.\nMin-m si tout moun kont mouin, é tout bagay noua,\nParòl Li pap manké, li gingnin pou fèt.\n\n\n3.\nAmou Li pou mouin, sé pa bagay yè,\nSé pouki m-konnin Li pap kité mouin.\nMouin pa kapab blié tout délivrans mouin;\nPou tèt sa m-konnin l-a édé-m jouk la fin.\n\n\n4.\nMouin pa pou plingnin soufrans ni bézouin\nKris té avèti-m sa gin pou vini.\nParòl li anségné ké moun kap suiv Li\nDoué subi tantasion ak pèsékusion.\n\n\n5.\nTou sa kap rivé-m, va sèvi gnou bi-in,\nM-aksépté soufrans kòm si li té dou.\nLi pap duré lontan, bi-into l-a suspan-n.\nAn Kris ma va kontan, ma plus ké vinkè.','<p><strong>1.</strong> </p>\n<p>Mouin pa bézouin pè, Sovè mouin tou pré, </p>\n<p>É pou sové mouin Li prèt pou parèt. </p>\n<p>Ma va prié anpil, Sé Kris kap aji, </p>\n<p>Lè Li nan kannot mouin, mouin pap pè tanpèt. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si chémin mouin noua, sé Kris ki gid mouin </p>\n<p>Map obéi Li, L-a protéjé mouin. </p>\n<p>Min-m si tout moun kont mouin, é tout bagay noua, </p>\n<p>Paròl Li pap manké, li gingnin pou fèt. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Amou Li pou mouin, sé pa bagay yè, </p>\n<p>Sé pouki m-konnin Li pap kité mouin. </p>\n<p>Mouin pa kapab blié tout délivrans mouin; </p>\n<p>Pou tèt sa m-konnin l-a édé-m jouk la fin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin pa pou plingnin soufrans ni bézouin </p>\n<p>Kris té avèti-m sa gin pou vini. </p>\n<p>Paròl li anségné ké moun kap suiv Li </p>\n<p>Doué subi tantasion ak pèsékusion. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Tou sa kap rivé-m, va sèvi gnou bi-in, </p>\n<p>M-aksépté soufrans kòm si li té dou. </p>\n<p>Li pap duré lontan, bi-into l-a suspan-n. </p>\n<p>An Kris ma va kontan, ma plus ké vinkè. </p>\n','Mwen-pa-bezouin-pe',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1545,'38c4ed3a-81fe-4cb5-8907-9da0e961b542',0,0,'Sa-k kapab lave péché-m?',55,'55_CE_ht','1.\nSa-k kapab lavé péché?\nAngnin pasé san Jézu-Kri.\nSa-k kapab géri tout kè-m?\nAngnin pasé san Jézu-Kri.\n\nKè:\nSan ki chè pasé lò\nKi ouété-m nan lan mò;\nNan pouin lòt sous ankò,\nAngnin pasé san Jézu-Kri.\n\n\n2.\nPou padon mouin, sa mouin ouè?\nAngnin pasé san Jézu-Kri.\nPou nétouayé-m sa-m mandé?\nAngnin pasé san Jézu-Kri.\n\n\n3.\nSa-k pou péyé dèt péché?\nAngnin pasé san Jézu-Kri.\nSé pat bon zèv mouin té fè;\nAngnin pasè san Jézu-Kri.\n\n\n4.\nSa-k ban-m éspoua ak la pè?\nAngnin pasé san Jézu-Kri.\nSa-k fè justis mouin asé?\nAngnin pasé san Jézu-Kri.','<p><strong>1.</strong> </p>\n<p>Sa-k kapab lavé péché? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p>Sa-k kapab géri tout kè-m? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>San ki chè pasé lò</strong> </p>\n<p><strong>Ki ouété-m nan lan mò;</strong> </p>\n<p><strong>Nan pouin lòt sous ankò,</strong> </p>\n<p><strong>Angnin pasé san Jézu-Kri.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou padon mouin, sa mouin ouè? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p>Pou nétouayé-m sa-m mandé? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa-k pou péyé dèt péché? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p>Sé pat bon zèv mouin té fè; </p>\n<p>Angnin pasè san Jézu-Kri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sa-k ban-m éspoua ak la pè? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n<p>Sa-k fè justis mouin asé? </p>\n<p>Angnin pasé san Jézu-Kri. </p>\n','Sa-k-kapab-lave-peche-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1546,'eb538704-d29e-484e-a068-275e5f0c2868',0,0,'A la gnou bèl bagay',63,'63_CE_ht','1.\nA la gnou bèl bagay ki nan vi mouin rivé\nLè Jézu vini nan kè mouin.\nMouin gin lumiè nan kè-m, sa-m té toujou chaché\nLè Jézu vini nan kè mouin.\n\nKè:\nLè Jézu vini nan kè mouin,  (2 foua)\nLa joua mouin débòdé pasé gnou gran riviè\nLè Jézu vini nan kè mouin.\n\n\n2.\nMouin suspan-n voyajé nan gnou mové chémin\nLè Jézu vini nan kè mouin.\nSan Jézu éfasé tout péché m-té gingnin\nLè Jézu vini nan kè mouin.\n\n\n3.\nMouin té jouin-n gnou éspoua ki va duré tou tan\nLè Jézu vini nan kè mouin.\nÉ nan pouin dout ankò nan kè mouin kòm avan\nLè Jézu vini nan kè mouin.\n\n\n4.\nMouin gin gnou lumiè ki kléré santié lan mò\nLè Jézu vini nan kè mouin.\nÉ m-kapab ouè pòt sièl la ki louvri lòt bò\nLè Jézu vini nan kè mouin.\n\n\n5.\nMouin gingnin nan sièl la gnou kay pou mouin rété\nLè Jézu vini nan kè mouin.\nMouin kontan, bi-in kontan, bonè mouin asuré\nLè Jézu vini nan kè mouin.','<p><strong>1.</strong> </p>\n<p>A la gnou bèl bagay ki nan vi mouin rivé </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p>Mouin gin lumiè nan kè-m, sa-m té toujou chaché </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè Jézu vini nan kè mouin,  (2 foua)</strong> </p>\n<p><strong>La joua mouin débòdé pasé gnou gran riviè</strong> </p>\n<p><strong>Lè Jézu vini nan kè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin suspan-n voyajé nan gnou mové chémin </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p>San Jézu éfasé tout péché m-té gingnin </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin té jouin-n gnou éspoua ki va duré tou tan </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p>É nan pouin dout ankò nan kè mouin kòm avan </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin gin gnou lumiè ki kléré santié lan mò </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p>É m-kapab ouè pòt sièl la ki louvri lòt bò </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mouin gingnin nan sièl la gnou kay pou mouin rété </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n<p>Mouin kontan, bi-in kontan, bonè mouin asuré </p>\n<p>Lè Jézu vini nan kè mouin. </p>\n','A-la-gnou-bel-bagay',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1547,'a7453352-fcab-477c-9cce-dd6b88630bbd',0,0,'Mouin pito gin Jézu pasé gro lajan',95,'95_CE_ht','1.\nMouin pito gin Jézu pasé gro lajan;\nMouin pito pa Li, pasé m-ta rich tout tan,\nMouin pito gin Jézu pasé bèl kay;\nPito li gidé mouin nan tout bagay.\n\nKè:\nPasé pou-m ta roua nan gnou gran péi,\nLè péché-m ta fè-m soufri;\nPito m-gin Jézu pasé tout bagay\nKé lé mon-n ta kapab bay.\n\n\n2.\nMouin pito gin Jézu pasé vanité;\nMouin pito fidèl; pou Li mapé luté;\nMouin pito gin Jézu pasé rénonmé:\nMouin pito kinbé-L, é viv nan la pè.\n\n\n3.\nJustis Jézu-Kri sé gnou gran mèvèy;\nParol li pi dous pasé gnou gato mièl;\nMouin pito gin Jézu – Li kon-n satisfè-m;\nMouin pito suiv li – Li kon-n dirijé-m.','<p><strong>1.</strong> </p>\n<p>Mouin pito gin Jézu pasé gro lajan; </p>\n<p>Mouin pito pa Li, pasé m-ta rich tout tan, </p>\n<p>Mouin pito gin Jézu pasé bèl kay; </p>\n<p>Pito li gidé mouin nan tout bagay. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pasé pou-m ta roua nan gnou gran péi,</strong> </p>\n<p><strong>Lè péché-m ta fè-m soufri;</strong> </p>\n<p><strong>Pito m-gin Jézu pasé tout bagay</strong> </p>\n<p><strong>Ké lé mon-n ta kapab bay.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin pito gin Jézu pasé vanité; </p>\n<p>Mouin pito fidèl; pou Li mapé luté; </p>\n<p>Mouin pito gin Jézu pasé rénonmé: </p>\n<p>Mouin pito kinbé-L, é viv nan la pè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Justis Jézu-Kri sé gnou gran mèvèy; </p>\n<p>Parol li pi dous pasé gnou gato mièl; </p>\n<p>Mouin pito gin Jézu – Li kon-n satisfè-m; </p>\n<p>Mouin pito suiv li – Li kon-n dirijé-m. </p>\n','Mouin-pito-gin-Jezu-pase-gro-lajan',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1548,'c79e9aad-1852-46d2-9fbb-172549eb4efc',0,0,'Ogmanté dézi mouin pou ou',131,'131_CE_ht','1.\nOgmanté dézi mouin pou ou,\nÉ louvri kè-m, Ségnè;\nPou-m kapab ranpli ak Léspri ou;\nPou-m konnin sa ou vlé pou-m fè.\nFè la foua-m vini pi gran,\nFè li gin gnou bèl kapasité.\nAlò, ranpli nan-m mouin pou toujou\nAk tout richès yo ki nan ou.','<p><strong>1.</strong> </p>\n<p>Ogmanté dézi mouin pou ou, </p>\n<p>É louvri kè-m, Ségnè; </p>\n<p>Pou-m kapab ranpli ak Léspri ou; </p>\n<p>Pou-m konnin sa ou vlé pou-m fè. </p>\n<p>Fè la foua-m vini pi gran, </p>\n<p>Fè li gin gnou bèl kapasité. </p>\n<p>Alò, ranpli nan-m mouin pou toujou </p>\n<p>Ak tout richès yo ki nan ou. </p>\n','Ogmante-dezi-mouin-pou-ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1549,'8c3c3233-9a53-46e8-b0cf-439a9f7d41b3',0,0,'Mouin té pèdu nan vil péché',132,'132_CE_ht','1.\nMouin té pèdu nan vil péché\nMouin t-alé louin Bon-Dieu.\nMin jou ké Kris té vi-n jouin-n mouin,\nSé Li ki viv nan mouin.\n\nKè\nJézu viv nan mouin,  (2 foua)\nA la bèl bagay pou mouin,\nSé Kris ki viv nan mouin.\n\n\n2.\nMin-n jan solèy la bay la vi\nTout flè yo sou la tè,\nJézu-Kri sé solèy pou mouin,\nKi té vi-n ban-m la vi.\n\n\n3.\nMouin vlé, Ségnè, é sé dézi-m\nAvan tout lot bagay,\nViv la vi ki nan ou, Ségnè,\nTout tan ouap viv nan mouin.','<p><strong>1.</strong> </p>\n<p>Mouin té pèdu nan vil péché </p>\n<p>Mouin t-alé louin Bon-Dieu. </p>\n<p>Min jou ké Kris té vi-n jouin-n mouin, </p>\n<p>Sé Li ki viv nan mouin. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Jézu viv nan mouin,  (2 foua)</strong> </p>\n<p><strong>A la bèl bagay pou mouin,</strong> </p>\n<p><strong>Sé Kris ki viv nan mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-n jan solèy la bay la vi </p>\n<p>Tout flè yo sou la tè, </p>\n<p>Jézu-Kri sé solèy pou mouin, </p>\n<p>Ki té vi-n ban-m la vi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vlé, Ségnè, é sé dézi-m </p>\n<p>Avan tout lot bagay, </p>\n<p>Viv la vi ki nan ou, Ségnè, </p>\n<p>Tout tan ouap viv nan mouin. </p>\n','Mouin-te-pedu-nan-vil-peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1550,'eee26022-ba91-4131-8022-a859c3eb5269',0,0,'Nan pouin angnin ant nan-m mouin é Sovè-m',141,'141_CE_ht','1.\nNan pouin angnin ant nan-m mouin é Sovè-m,\nAngnin nan lé mon-n dépravé sa,\nMouin té rénonsé tout plézi la chè-m,\nJézu pou mouin, pa gin bariè la.\n\nKè:\nNan pouin angnin ant nan-m mouin é Sovè-m,\nPou anpéché-m ouè Li fas a fas;\nAngnin pou détouné favè Li anvè-m,\nOuété tout bò-n!  pa gin bariè la.\n\n\n2.\nNan pouin plézi mon-n sa ant mouin ak Kris;\nKoun yé a vis la vi vi-n san ka;\nYo pa kab séparé-m ak Li non plis,\nJézu pou mouin, nan pouin bariè la.\n\n\n3.\nMalgré m-a gin éprèv ki du pépèt,\nMalgré lé mon-n méprizé la Koua;\nMa-p tan-n nan priè jouk Ségnè-m parèt,\nSé va trionf, nan pouin bariè la.','<p><strong>1.</strong> </p>\n<p>Nan pouin angnin ant nan-m mouin é Sovè-m, </p>\n<p>Angnin nan lé mon-n dépravé sa, </p>\n<p>Mouin té rénonsé tout plézi la chè-m, </p>\n<p>Jézu pou mouin, pa gin bariè la. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan pouin angnin ant nan-m mouin é Sovè-m,</strong> </p>\n<p><strong>Pou anpéché-m ouè Li fas a fas;</strong> </p>\n<p><strong>Angnin pou détouné favè Li anvè-m,</strong> </p>\n<p><strong>Ouété tout bò-n!  pa gin bariè la.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan pouin plézi mon-n sa ant mouin ak Kris; </p>\n<p>Koun yé a vis la vi vi-n san ka; </p>\n<p>Yo pa kab séparé-m ak Li non plis, </p>\n<p>Jézu pou mouin, nan pouin bariè la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Malgré m-a gin éprèv ki du pépèt, </p>\n<p>Malgré lé mon-n méprizé la Koua; </p>\n<p>Ma-p tan-n nan priè jouk Ségnè-m parèt, </p>\n<p>Sé va trionf, nan pouin bariè la. </p>\n','Nan-pouin-angnin-ant-nan-m-mouin-e-Sove-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1551,'afd50a4e-e85e-41dd-8774-1337ed5b9281',0,0,'Yon Vi Pou Jézu Bay La Pè',32,'32_MJ_ht','1.\nGnou vi pou Jézu bay la pè\nKou gnou riviè ki pa rété;\nDifikulté yo kon-n vi-ni –\nMin Jézu la, é sa sufi.\n\nKè:\nÉdé-m sèvi Ou plus chak jou,\nPou-m rété toujou avèk Ou;\nM-rinmin Ou, Kris, plus ké janmin;\nTan pri, pa kité-m égaré.\n\n2.\nGnou vi pou Jézu bay répo,\nLi ouété nan mouin sa ki fo;\nParol Li sé la vérité;\nL-a gidé-m pou létènité.\n\n3.\nGnou vi pou Jézu bay la joua\nDèské mouin sové pa la foua;\nZanmi yo mèt abandonnin-m,\nJézu fidèl, Li va kinbé-m.\n\n4.\nGnou vi pou Jézu bay viktoua,\nAk fòs Li m-maché pa-za-pa;\nKris té vink diab, lé mon-n, la chè;\nKoun-yé-a mouin plus ké vinkè.','<p><strong>1.</strong> </p>\n<p>Gnou vi pou Jézu bay la pè </p>\n<p>Kou gnou riviè ki pa rété; </p>\n<p>Difikulté yo kon-n vi-ni – </p>\n<p>Min Jézu la, é sa sufi. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Édé-m sèvi Ou plus chak jou,</strong> </p>\n<p><strong>Pou-m rété toujou avèk Ou;</strong> </p>\n<p><strong>M-rinmin Ou, Kris, plus ké janmin;</strong> </p>\n<p><strong>Tan pri, pa kité-m égaré.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou vi pou Jézu bay répo, </p>\n<p>Li ouété nan mouin sa ki fo; </p>\n<p>Parol Li sé la vérité; </p>\n<p>L-a gidé-m pou létènité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou vi pou Jézu bay la joua </p>\n<p>Dèské mouin sové pa la foua; </p>\n<p>Zanmi yo mèt abandonnin-m, </p>\n<p>Jézu fidèl, Li va kinbé-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou vi pou Jézu bay viktoua, </p>\n<p>Ak fòs Li m-maché pa-za-pa; </p>\n<p>Kris té vink diab, lé mon-n, la chè; </p>\n<p>Koun-yé-a mouin plus ké vinkè. </p>\n','Yon-Vi-Pou-Jezu-Bay-La-Pe',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1552,'4370e678-a2c6-4bea-bab7-a849806e7085',0,0,'Pawol Bondié Ban Nou Lumiè',35,'35_MJ_ht','1.\nParòl Bon-Dieu ban nou lumiè\nPou kléré chémin salu;\nPou établi li kon Ségnè\nNan kè mouin, sa fè-m réjoui.\n\nKè:\nM-ap séré Paròl Ou nan kè-m\nPou mouin pa fè péché kont Ou,\nPou mouin pa péché  (2 foua)\nM-ap séré Paròol Ou nan kè-m.\n\n2.\nParòl Ou, Ségnè, sé sètin,\nÉtabli pou pa fayi;\nFidélité Ou toujou min-m,\nGnou sous ki vrèman jayi.\n\n3.\nChak matin, midi é asouè\nM-ap di: Mèsi, chè Ségnè;\nPaské Ou-min’m gnou dou Sovè,\nM-ap sèvi Ou byin sinsè.\n\n4.\nParòl Ou ban nou asurans\nKé Jézu va rétounin.\nNou-min’m va chanjé aparans;\nAvèk Jézu n-a rété.','<p><strong>1.</strong> </p>\n<p>Paròl Bon-Dieu ban nou lumiè </p>\n<p>Pou kléré chémin salu; </p>\n<p>Pou établi li kon Ségnè </p>\n<p>Nan kè mouin, sa fè-m réjoui. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-ap séré Paròl Ou nan kè-m</strong> </p>\n<p><strong>Pou mouin pa fè péché kont Ou,</strong> </p>\n<p><strong>Pou mouin pa péché  (2 foua)</strong> </p>\n<p><strong>M-ap séré Paròol Ou nan kè-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Paròl Ou, Ségnè, sé sètin, </p>\n<p>Établi pou pa fayi; </p>\n<p>Fidélité Ou toujou min-m, </p>\n<p>Gnou sous ki vrèman jayi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chak matin, midi é asouè </p>\n<p>M-ap di: Mèsi, chè Ségnè; </p>\n<p>Paské Ou-min’m gnou dou Sovè, </p>\n<p>M-ap sèvi Ou byin sinsè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Paròl Ou ban nou asurans </p>\n<p>Ké Jézu va rétounin. </p>\n<p>Nou-min’m va chanjé aparans; </p>\n<p>Avèk Jézu n-a rété. </p>\n','Pawol-Bondie-Ban-Nou-Lumie',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1553,'2e4e625e-ccb8-4235-baca-6878da9362f3',0,0,'Lè Kè Ou Fè Tris, O Gadé Jézu',80,'80_MJ_ht','1.\nLè kè ou fè tris, O gadé Jézu,\nLi sé pi bon zanmi pasé tout,\nOu va jouin-n Jézu kanpé tou pré ou,\nPou konsolé ou chak lè, chak jou.\n\nKè:\nKè tris, di tou sa a Jézu,\nAlé koun-yé a,\nPa mizé pou di Li tout,\nKè tris, bay tout chay a Jézu,\nLi va ranpli kè ou avèk joua.\n\n2.\nJézu va bay ou joua pou chak tristès,\nBèl soulajman L-a bay pou la pin-n,\nLè ou chèché-L,\nOu-a jouin-n Li san dout.\n\n3.\nJézu konnin ninpòt traka ou gin,\nÉ Li vlé géri nan-m ou troublé,\nEsk ou va gin konfians nan amou Li?\nJézu sèl kap géri léspri ou.','<p><strong>1.</strong> </p>\n<p>Lè kè ou fè tris, O gadé Jézu, </p>\n<p>Li sé pi bon zanmi pasé tout, </p>\n<p>Ou va jouin-n Jézu kanpé tou pré ou, </p>\n<p>Pou konsolé ou chak lè, chak jou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kè tris, di tou sa a Jézu,</strong> </p>\n<p><strong>Alé koun-yé a,</strong> </p>\n<p><strong>Pa mizé pou di Li tout,</strong> </p>\n<p><strong>Kè tris, bay tout chay a Jézu,</strong> </p>\n<p><strong>Li va ranpli kè ou avèk joua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu va bay ou joua pou chak tristès, </p>\n<p>Bèl soulajman L-a bay pou la pin-n, </p>\n<p>Lè ou chèché-L, </p>\n<p>Ou-a jouin-n Li san dout. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu konnin ninpòt traka ou gin, </p>\n<p>É Li vlé géri nan-m ou troublé, </p>\n<p>Esk ou va gin konfians nan amou Li? </p>\n<p>Jézu sèl kap géri léspri ou. </p>\n','Le-Ke-Ou-Fe-Tris-O-Gade-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1554,'5cccf26a-f4cb-4def-bac5-8ae8974d89a3',0,0,'A de Nouveau combats',16,'16_VR_fr','1.	\nA de nouveaux combats, Jésus tu nous appelles\nEt nous voici fidèles, prêts à suivre tes pas \nConduits nous aux combats, nous sommes tes soldats\n\nChoeur\nArme nos bras, arme nos coeurs\nEt nous serons plus que vainqueurs  (bis)\nArme nos bras, arme nos coeurs.\n\n2.	\nArme-nous O Seigneur, de foi, de hardiesse,\nDe force, de sagesse, d\'amour et de ferveur\nEmbrasse-nous Seigneur, d\'une invincible ardeur\n\n3.	\nNous tiendrons ferme et haut,\nLa croix, notre bannière\nPour vaincre l\'adversaire, par le sang de l\'Agneau\nOui! Nous tiendrons bien haut, ta croix, notre drapeau.\n\n4.	\nLuttons, prions, souffrons,\nNous aurons la victoire; la couronne de gloire\nUn jour ceindra nos fronts, luttons, prions,\nSouffrons; bientôt nous règnerons.','<p><strong>1.</strong> </p>\n<p>A de nouveaux combats, Jésus tu nous appelles </p>\n<p>Et nous voici fidèles, prêts à suivre tes pas </p>\n<p>Conduits nous aux combats, nous sommes tes soldats </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Arme nos bras, arme nos coeurs</strong> </p>\n<p><strong>Et nous serons plus que vainqueurs  (bis)</strong> </p>\n<p><strong>Arme nos bras, arme nos coeurs.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Arme-nous O Seigneur, de foi, de hardiesse, </p>\n<p>De force, de sagesse, d&#39;amour et de ferveur </p>\n<p>Embrasse-nous Seigneur, d&#39;une invincible ardeur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous tiendrons ferme et haut, </p>\n<p>La croix, notre bannière </p>\n<p>Pour vaincre l&#39;adversaire, par le sang de l&#39;Agneau </p>\n<p>Oui! Nous tiendrons bien haut, ta croix, notre drapeau. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Luttons, prions, souffrons, </p>\n<p>Nous aurons la victoire; la couronne de gloire </p>\n<p>Un jour ceindra nos fronts, luttons, prions, </p>\n<p>Souffrons; bientôt nous règnerons. </p>\n','A-de-Nouveau-combats',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1555,'d4b9576b-0d3b-418d-8378-902970bdec07',0,0,'Guide-moi,  ô mon Sauveur',13,'13_VR_fr','1.	\nGuide-moi ô mon Sauveur,\nSur le chemin du salut.\nA tes côtés point de peur,\nC\'est la joie pour tes élus.\n\nChoeur\nJésus, Jésus,\nDivin Époux de mon âme,\nGarde-moi fidèle à Toi,\nToujours docile à ta voix.\n\n2.	\nPréserve mon coeur du monde\nEt des pièges de Satan\nLorsque le doute m\'inonde\nRends victorieux ton enfant.\n\n3.	\nTu ne m\'abandonne jamais\nDans les luttes d\'ici-bas,\nTu me remplis de ta paix\nEt m\'entoure de tes bras.\n\n4.	\nEt dans la détresse extrême\nJe trouve en toi un appui,\nLe seul remède suprême\nQui fortifie et guérit.','<p><strong>1.</strong> </p>\n<p>Guide-moi ô mon Sauveur, </p>\n<p>Sur le chemin du salut. </p>\n<p>A tes côtés point de peur, </p>\n<p>C&#39;est la joie pour tes élus. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Jésus, Jésus,</strong> </p>\n<p><strong>Divin Époux de mon âme,</strong> </p>\n<p><strong>Garde-moi fidèle à Toi,</strong> </p>\n<p><strong>Toujours docile à ta voix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Préserve mon coeur du monde </p>\n<p>Et des pièges de Satan </p>\n<p>Lorsque le doute m&#39;inonde </p>\n<p>Rends victorieux ton enfant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu ne m&#39;abandonne jamais </p>\n<p>Dans les luttes d&#39;ici-bas, </p>\n<p>Tu me remplis de ta paix </p>\n<p>Et m&#39;entoure de tes bras. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et dans la détresse extrême </p>\n<p>Je trouve en toi un appui, </p>\n<p>Le seul remède suprême </p>\n<p>Qui fortifie et guérit. </p>\n','Guide-moi-o-mon-Sauveur',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1556,'69c55f0c-2aa9-4069-9180-88c0d1f95544',0,0,'Map Gadé Ou Ak Foua',42,'42_CE_ht','1.\nMap gadé ou ak foua,\nAgno Bon-Dieu sou boua,\nSovè si dou.\nKouté mouin lè-m prié,\nLavé-m nan tout péché;\nDépi jodi mouin vlé\nMouin tout pou ou.\n\n\n2.\nBan-m gras pou rafréchi-m;\nFòs kè mouin afébli\nDé tan-zan-tan.\nKòm ou mouri pou muoin,\nSé pou lamou mouin gin\nPou ou, rété san fin\nGnou flan-m vivan.\n\n\n3.\nTénèb kouvri chémin-m,\nDoulè patou koté-m;\nGid mouin sé ou.\nFè noua tounin lumiè;\nOuété krié doulè,\nPa jan-m lésé-m kité\nBò koté ou.','<p><strong>1.</strong> </p>\n<p>Map gadé ou ak foua, </p>\n<p>Agno Bon-Dieu sou boua, </p>\n<p>Sovè si dou. </p>\n<p>Kouté mouin lè-m prié, </p>\n<p>Lavé-m nan tout péché; </p>\n<p>Dépi jodi mouin vlé </p>\n<p>Mouin tout pou ou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ban-m gras pou rafréchi-m; </p>\n<p>Fòs kè mouin afébli </p>\n<p>Dé tan-zan-tan. </p>\n<p>Kòm ou mouri pou muoin, </p>\n<p>Sé pou lamou mouin gin </p>\n<p>Pou ou, rété san fin </p>\n<p>Gnou flan-m vivan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ténèb kouvri chémin-m, </p>\n<p>Doulè patou koté-m; </p>\n<p>Gid mouin sé ou. </p>\n<p>Fè noua tounin lumiè; </p>\n<p>Ouété krié doulè, </p>\n<p>Pa jan-m lésé-m kité </p>\n<p>Bò koté ou. </p>\n','Map-Gade-Ou-Ak-Foua',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1557,'18b24d7a-2f27-4e76-a380-5705439aafdd',0,0,'M-bézouin Jézu, sé sa kè-m réko-nèt',116,'116_CE_ht','1.M-bézouin Jézu, sé sa kè-m réko-nèt;Nan pouin zanmi kon Li lè-m nan tristès;M-bézouin Jézu, mouin bi-in kontan admèt;Lè zòt kab pòté fado yo,Min m-bézouin Jézu.Kè:  M-bézouin Jézu, m-bézouin Jézu,M-bézouin Jézu koun yé a;M-bézouin-l kè solèy kléré,M-bézouin-l lè gran van souflé;Chak moman sou rout sièl laMouin bézouin Jézu.2.M-bézouin Jézu, gnou zanmi kon Li-min’m,Pou gidé-m lè fè noua vi-n sou chémin;M-bézouin Jézu, lè lènmi nan-m mouin pré;Mouin-min’m tou sèl, m-ta fayi nèt,Mouin bézouin Jézu.3.M-bézouin Jézu, mouin bézouin-L jouk la fin;Péchè yo pa gin gnou lot vrè zanmi;M-bézouin Jézu, lot yo pa kab sufi;Oui, m-bézouin Jézu.','<p><strong>1.</strong> </p>\n<p>M-bézouin Jézu, sé sa kè-m réko-nèt; </p>\n<p>Nan pouin zanmi kon Li lè-m nan tristès; </p>\n<p>M-bézouin Jézu, mouin bi-in kontan admèt; </p>\n<p>Lè zòt kab pòté fado yo, </p>\n<p>Min m-bézouin Jézu. </p>\n<p><br/><br><strong>Kè</strong>  </p>\n<p>M-bézouin Jézu, m-bézouin Jézu, </p>\n<p>M-bézouin Jézu koun yé a; </p>\n<p>M-bézouin-l kè solèy kléré, </p>\n<p>M-bézouin-l lè gran van souflé; </p>\n<p>Chak moman sou rout sièl la </p>\n<p>Mouin bézouin Jézu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-bézouin Jézu, gnou zanmi kon Li-min’m, </p>\n<p>Pou gidé-m lè fè noua vi-n sou chémin; </p>\n<p>M-bézouin Jézu, lè lènmi nan-m mouin pré; </p>\n<p>Mouin-min’m tou sèl, m-ta fayi nèt, </p>\n<p>Mouin bézouin Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>M-bézouin Jézu, mouin bézouin-L jouk la fin; </p>\n<p>Péchè yo pa gin gnou lot vrè zanmi; </p>\n<p>M-bézouin Jézu, lot yo pa kab sufi; </p>\n<p>Oui, m-bézouin Jézu. </p>\n','M-bezouin-Jezu-se-sa-ke-m-reko-net',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1558,'284f8518-3129-4978-a100-cfb9b2890432',0,0,'Konfié Nan Jézu Pa-za-pa',19,'19_MJ_ht','1.\nKonfié nan Jézu pa-za-pa\nSé gnou zanmi toujou\nNinpòt koté nou vouyajé\nJézu va gidé nou.\nChanté nan tan solèy,\nChanté lè li fè noua,\nOui, chak jou, tou patou\nO, chanté!  Chanté!','<p><strong>1.</strong> </p>\n<p>Konfié nan Jézu pa-za-pa </p>\n<p>Sé gnou zanmi toujou </p>\n<p>Ninpòt koté nou vouyajé </p>\n<p>Jézu va gidé nou. </p>\n<p>Chanté nan tan solèy, </p>\n<p>Chanté lè li fè noua, </p>\n<p>Oui, chak jou, tou patou </p>\n<p>O, chanté!  Chanté! </p>\n','Konfie-Nan-Jezu-Pa-za-pa',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1559,'9a5c1c87-17f6-4646-9f9c-9469be4561bb',0,0,'Lot Bo Bèl Mon Yo La Ba',22,'22_MJ_ht','1.\nLòt bò bèl mòn yo la ba,\nGnou jou mouin va alé,\nPou-m rankontré ak Jézu\nJézu-Kri ki rinmin mouin.\n\nKè:\nLòt bò bèl mòn yo la ba\nSièl la apé tan-n mouin.\nLòt bò bèl mòn yo la ba\nJézu-Kri Ségnè-m ma ouè.','<p><strong>1.</strong> </p>\n<p>Lòt bò bèl mòn yo la ba, </p>\n<p>Gnou jou mouin va alé, </p>\n<p>Pou-m rankontré ak Jézu </p>\n<p>Jézu-Kri ki rinmin mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lòt bò bèl mòn yo la ba</strong> </p>\n<p><strong>Sièl la apé tan-n mouin.</strong> </p>\n<p><strong>Lòt bò bèl mòn yo la ba</strong> </p>\n<p><strong>Jézu-Kri Ségnè-m ma ouè.</strong> </p>\n','Lot-Bo-Bel-Mon-Yo-La-Ba',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1560,'4b454b1b-d83c-4e56-90e2-13c9353aee2b',0,0,'Gin Moun Ki Supozé',50,'50_MJ_ht','1.\nGin moun ki supòzé,\nYo souété, yo ta vlé,\nMin mouin, mouin konnin m-sové.\nYo souété gnou jou\nY-a rivé nan sièl la,\nMin, mouin min-m,\nMouin konnin m-sové.\nMouin té louvri pòt\nKè mouin é Jézu antré\nMouin sètin ké Li sové-m,\nLi fè-m pa péché.\nÉ Léspri Bon-Dieu\nRan-n témouayaj avèk mouin\nÉ mouin min-m, mouin konnin m-sové.','<p><strong>1.</strong> </p>\n<p>Gin moun ki supòzé, </p>\n<p>Yo souété, yo ta vlé, </p>\n<p>Min mouin, mouin konnin m-sové. </p>\n<p>Yo souété gnou jou </p>\n<p>Y-a rivé nan sièl la, </p>\n<p>Min, mouin min-m, </p>\n<p>Mouin konnin m-sové. </p>\n<p>Mouin té louvri pòt </p>\n<p>Kè mouin é Jézu antré </p>\n<p>Mouin sètin ké Li sové-m, </p>\n<p>Li fè-m pa péché. </p>\n<p>É Léspri Bon-Dieu </p>\n<p>Ran-n témouayaj avèk mouin </p>\n<p>É mouin min-m, mouin konnin m-sové. </p>\n','Gin-Moun-Ki-Supoze',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1561,'b5dfc409-5f58-43cc-9fb7-873f34eb6db2',0,0,'Eské Ou Konin Ki Lè Li Yé',59,'59_MJ_ht','1.\nEské ou konnin ki lè li yé? (prémié pati)\nSé lè sèvi Bon-dieu.  (dézièm pati)\nSé rachté lé tan jodi a\nSélon Paròl Bon-Dieu.\nSoua jou, soua nuit,\nBon tan, mové tan,\nNinpòt tan sé bon tan sèvi Bon-Dieu.','<p><strong>1.</strong> </p>\n<p>Eské ou konnin ki lè li yé? (prémié pati) </p>\n<p>Sé lè sèvi Bon-dieu.  (dézièm pati) </p>\n<p>Sé rachté lé tan jodi a </p>\n<p>Sélon Paròl Bon-Dieu. </p>\n<p>Soua jou, soua nuit, </p>\n<p>Bon tan, mové tan, </p>\n<p>Ninpòt tan sé bon tan sèvi Bon-Dieu. </p>\n','Eske-Ou-Konin-Ki-Le-Li-Ye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1562,'94d84d40-8a91-4887-bc3c-2c4e0d84f786',0,0,'A La Yon Mèvéyé, Mèvéyé Jou',66,'66_MJ_ht','1.\nA la gnou mèvéyé, mèvéyé jou\nJou m-pa ka jan-m blié,\nLè-m té nan ténèb, pèdu nan la bou,\nJézu Sovè-m mouin kontré;\nA la gnou zanmi ki gin charité,\nLi satisfè-m ak gras Li,\nLi pran tristès mouin, Li ba-m alégrès,\nÉ Li fè tout ténèb mouin pati.\n\nKè:\nSièl la dé san-n, plin nan-m mouin ak la joua\nLè Sovè mouin té sové-m a la koua.\nPéché-m tout padonné,\nTénèb nan lumiè touné\nSièl la désan’n, plin nan-m mouin ak la joua.\n\n2.\nFèt dé nouvo pa puisans Léspri Sin,\nLi fè-m gnou pitit Bon-Dieu,\nAmou Kalvè justifié-m, sé sètin,\nLi ban-m gnou chan glorié;\nTravay sa-a fèt nan gnou ti moman\nLè-m répanti nan péché-m,\nLè-m aksépté Li é lè-m konfésé Li,\nO béni non Li, Li sové-m.\n\n3.\nA-lè-ki-lé, lèspérans ké-m trouvé\nLa duré jouk la vi sa-a\nVa fini gnou jou, pui, m-tout asuré\nMa jouin-n gnou plas nan sièl la.\nTou sa-a rivé-m paské gnou bèl jou\nMouin té kouè nan Jézu-Kri,\nByinfè yo si bèl ak salu étènèl\nMouin résévoua tout nan min Li.','<p><strong>1.</strong> </p>\n<p>A la gnou mèvéyé, mèvéyé jou </p>\n<p>Jou m-pa ka jan-m blié, </p>\n<p>Lè-m té nan ténèb, pèdu nan la bou, </p>\n<p>Jézu Sovè-m mouin kontré; </p>\n<p>A la gnou zanmi ki gin charité, </p>\n<p>Li satisfè-m ak gras Li, </p>\n<p>Li pran tristès mouin, Li ba-m alégrès, </p>\n<p>É Li fè tout ténèb mouin pati. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sièl la dé san-n, plin nan-m mouin ak la joua</strong> </p>\n<p><strong>Lè Sovè mouin té sové-m a la koua.</strong> </p>\n<p><strong>Péché-m tout padonné,</strong> </p>\n<p><strong>Ténèb nan lumiè touné</strong> </p>\n<p><strong>Sièl la désan’n, plin nan-m mouin ak la joua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fèt dé nouvo pa puisans Léspri Sin, </p>\n<p>Li fè-m gnou pitit Bon-Dieu, </p>\n<p>Amou Kalvè justifié-m, sé sètin, </p>\n<p>Li ban-m gnou chan glorié; </p>\n<p>Travay sa-a fèt nan gnou ti moman </p>\n<p>Lè-m répanti nan péché-m, </p>\n<p>Lè-m aksépté Li é lè-m konfésé Li, </p>\n<p>O béni non Li, Li sové-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A-lè-ki-lé, lèspérans ké-m trouvé </p>\n<p>La duré jouk la vi sa-a </p>\n<p>Va fini gnou jou, pui, m-tout asuré </p>\n<p>Ma jouin-n gnou plas nan sièl la. </p>\n<p>Tou sa-a rivé-m paské gnou bèl jou </p>\n<p>Mouin té kouè nan Jézu-Kri, </p>\n<p>Byinfè yo si bèl ak salu étènèl </p>\n<p>Mouin résévoua tout nan min Li. </p>\n','A-La-Yon-Meveye-Meveye-Jou',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1563,'8354c7d9-657e-4c42-a3a5-ab98a3064a9d',0,0,'Eské Ou Gen Tan Kité Souin',69,'69_MJ_ht','1.\nEské ou gin tan kité souin\nPapa ou, ou régrèt ké ou\nEgaré louin, gin gnou voua dou\nE tand kap mandé pou ou\n\nKè: Pitit mouin, pitit mouin, rétounin\nPitit mouin, pitit mouin,\nO pitit ou égaré, Papa ou apé plédé\nPitit mouin tounin, tounin\n\n2.\nEské ou san sékou nan loraj la vi\nBato ou prèt pou plonjé anfin\nMin! Lumiè lévanjil, antré nan labri\n\n3.\nJodi a Lap plédé avèk ou anko\nAvantaj lé monn pa vo angnin\nAntré vit pa négligé gnou vré trézo','<p><strong>1.</strong> </p>\n<p>Eské ou gin tan kité souin </p>\n<p>Papa ou, ou régrèt ké ou </p>\n<p>Egaré louin, gin gnou voua dou </p>\n<p>E tand kap mandé pou ou </p>\n<p>Kè: Pitit mouin, pitit mouin, rétounin </p>\n<p>Pitit mouin, pitit mouin, </p>\n<p>O pitit ou égaré, Papa ou apé plédé </p>\n<p>Pitit mouin tounin, tounin </p>\n<p><br/> <strong>2.</strong></p>\n<p>Eské ou san sékou nan loraj la vi </p>\n<p>Bato ou prèt pou plonjé anfin </p>\n<p>Min! Lumiè lévanjil, antré nan labri </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jodi a Lap plédé avèk ou anko </p>\n<p>Avantaj lé monn pa vo angnin </p>\n<p>Antré vit pa négligé gnou vré trézo </p>\n','Eske-Ou-Gen-Tan-Kite-Souin',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1564,'b9e80d2f-f6c7-4aef-a964-1fee31a0f72b',0,0,'Ah! Rentrons Au Bercail',333,'333_CE_fr','1.\nAh!  Rentrons au bercail\nDans le sein du Seingneur,\nCélébrons l\'Éternel\nPlein de zèle et d\'ardeur,\nExaltons son saint nom,\nEntonnons ses louanges,\nEt chantons ses bienfaits,\nDe même que les anges!\n\n\n2.\nFidèles, un moment\nSuspendons nos travaux;\nPortons nos pas tremblants,\nApprochons vers ces eaux\nOù Jésus, radieux,\nÉtant plongé lui-même\nAu monde entier donna\nL\'exemple du baptême!\n\n\n3.\nO chrétiens, pratiquez\nEt soutenez la loi,\nSoutenez dignement\nLe drapeau de la foi:\nDieu veut nous voir entrer,\nCombattre dans la lice,\nPour atteindre le but\nFondé par sa justice.\n\n\n\n\n\n\n4.\nA travers les périls,\nNe les redoutant pas,\nSur l\'obstacle sans cesse\nAffermissez vos pas;\nImitez le Sauveur\nEn son brûlant passage,\nCar lui-même en nos coeurs\nA gravé  son image.\n\n\n5.\nLe phare bienfaiteur\nBrille dans le lointain,\nL\'Éternel vous prépare\nUn triomphe certain;\nMarchez, persévérez\nAu milieu des tempêtes,\nLe mystique laurier\nCouronnera vos têtes.','<p><strong>1.</strong> </p>\n<p>Ah!  Rentrons au bercail </p>\n<p>Dans le sein du Seingneur, </p>\n<p>Célébrons l&#39;Éternel </p>\n<p>Plein de zèle et d&#39;ardeur, </p>\n<p>Exaltons son saint nom, </p>\n<p>Entonnons ses louanges, </p>\n<p>Et chantons ses bienfaits, </p>\n<p>De même que les anges! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fidèles, un moment </p>\n<p>Suspendons nos travaux; </p>\n<p>Portons nos pas tremblants, </p>\n<p>Approchons vers ces eaux </p>\n<p>Où Jésus, radieux, </p>\n<p>Étant plongé lui-même </p>\n<p>Au monde entier donna </p>\n<p>L&#39;exemple du baptême! </p>\n<p><br/> <strong>3.</strong></p>\n<p>O chrétiens, pratiquez </p>\n<p>Et soutenez la loi, </p>\n<p>Soutenez dignement </p>\n<p>Le drapeau de la foi: </p>\n<p>Dieu veut nous voir entrer, </p>\n<p>Combattre dans la lice, </p>\n<p>Pour atteindre le but </p>\n<p>Fondé par sa justice. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A travers les périls, </p>\n<p>Ne les redoutant pas, </p>\n<p>Sur l&#39;obstacle sans cesse </p>\n<p>Affermissez vos pas; </p>\n<p>Imitez le Sauveur </p>\n<p>En son brûlant passage, </p>\n<p>Car lui-même en nos coeurs </p>\n<p>A gravé  son image. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Le phare bienfaiteur </p>\n<p>Brille dans le lointain, </p>\n<p>L&#39;Éternel vous prépare </p>\n<p>Un triomphe certain; </p>\n<p>Marchez, persévérez </p>\n<p>Au milieu des tempêtes, </p>\n<p>Le mystique laurier </p>\n<p>Couronnera vos têtes. </p>\n','Ah!-Rentrons-Au-Bercail',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1565,'e862c239-2530-4381-8450-5d3e3ecffda0',0,0,'Té Gin Katré-vin-diz-néf Mouton Yo',25,'25_CE_ht','1.\nTé gin katré-vin-diz-nèf mouton yo\nBin-in gadé nan pak la;\nMin gin gnoun ki té pèdu nan mò-n yo,\nLouin kay kap protéjé-l,\nLouin, louin nan mò-n yo sovaj é san non,\nBi-in louin souin Bon Bèjé ki rinmin li,\nBi-in louin souin Bon Bèjé ki rinmin li.\n\n\n2.\nSégnè, ou gin la katré-vin-diz-nèf,\nSa pa asé pou ou?\nMin Bèjé a té répon-n: Mouton pa-m\nTé égaré louin mouin.\nMin-m si chémin an du anpil, anpil,\nM-alé nan dézè a, pou jouin-n mouton mouin,\nM-alé nan dézè a, pou jouin-n mouton mouin.\n\n\n3.\nPa gnoun pami rachté yo jan-m konpran-n\nJan dlo a danjéré,\nNuit lè lé Ségnè tap travèsé-l,\nPou jouin-n mouton pèdu.\nLouin nan dézè a, li tandé-l krié,\nMalad, san sékou, tou pré pou mouri,\nMalad, san sékou, tou pré pou mouri.\n\n\n4.\nSégnè, pouki gin tach san tou patou\nNan tout rout nan mò-n yo?\nSan sa a té vèsé pou gnou mouton\nPèdu malgré Bèjé-l.\nSégnè, pouki min ou yo tout blésé?\nAsouè a anpil pikan pèsé yo!\nAsouè a anpil pikan pèsé yo!\n\n\n5.\nPatout nan mò-n yo, nan mitan ton-nè,\nÉpi nan chémin ròch,\nYo tandé gnou rèl sòt jouk nan sièl la:\nRéjoui, mouin jouin-n mouton-m.\nAnj yo répété dévan tròn Bon-Dieu:\nRéjoui! Lé Ségnè tounin ak mouton-l,\nRéjoui! Lé Ségnè tounin ak mouton-l.','<p><strong>1.</strong> </p>\n<p>Té gin katré-vin-diz-nèf mouton yo </p>\n<p>Bin-in gadé nan pak la; </p>\n<p>Min gin gnoun ki té pèdu nan mò-n yo, </p>\n<p>Louin kay kap protéjé-l, </p>\n<p>Louin, louin nan mò-n yo sovaj é san non, </p>\n<p>Bi-in louin souin Bon Bèjé ki rinmin li, </p>\n<p>Bi-in louin souin Bon Bèjé ki rinmin li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ségnè, ou gin la katré-vin-diz-nèf, </p>\n<p>Sa pa asé pou ou? </p>\n<p>Min Bèjé a té répon-n: Mouton pa-m </p>\n<p>Té égaré louin mouin. </p>\n<p>Min-m si chémin an du anpil, anpil, </p>\n<p>M-alé nan dézè a, pou jouin-n mouton mouin, </p>\n<p>M-alé nan dézè a, pou jouin-n mouton mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pa gnoun pami rachté yo jan-m konpran-n </p>\n<p>Jan dlo a danjéré, </p>\n<p>Nuit lè lé Ségnè tap travèsé-l, </p>\n<p>Pou jouin-n mouton pèdu. </p>\n<p>Louin nan dézè a, li tandé-l krié, </p>\n<p>Malad, san sékou, tou pré pou mouri, </p>\n<p>Malad, san sékou, tou pré pou mouri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ségnè, pouki gin tach san tou patou </p>\n<p>Nan tout rout nan mò-n yo? </p>\n<p>San sa a té vèsé pou gnou mouton </p>\n<p>Pèdu malgré Bèjé-l. </p>\n<p>Ségnè, pouki min ou yo tout blésé? </p>\n<p>Asouè a anpil pikan pèsé yo! </p>\n<p>Asouè a anpil pikan pèsé yo! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Patout nan mò-n yo, nan mitan ton-nè, </p>\n<p>Épi nan chémin ròch, </p>\n<p>Yo tandé gnou rèl sòt jouk nan sièl la: </p>\n<p>Réjoui, mouin jouin-n mouton-m. </p>\n<p>Anj yo répété dévan tròn Bon-Dieu: </p>\n<p>Réjoui! Lé Ségnè tounin ak mouton-l, </p>\n<p>Réjoui! Lé Ségnè tounin ak mouton-l. </p>\n','Te-Gin-Katre-vin-diz-nef-Mouton-Yo',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1566,'2e619c5a-5819-4f7d-8b24-5851a0afa596',0,0,'Mèt, Palé! Sévant Ou Kouté!',32,'32_CE_ht','1.\nMèt, palé!  Sèvant ou kouté!\nMap tan-n bon paròl bouch ou;\nVoua ou ki fè mouin si kontan\nKè-m souaf tandé li toujou.\nMapé kouté ou, Ségnè,\nKi sa ou gin pou di mouin.\n\n\n2.\nMèt, rélé mouin non pa mouin-min’m,\nDi-m sé ak mouin ou palé,\nPou mouin kapab suiv ou pi fèm;\nPou mouin maché san tranblé.\nPalé! Ou sé Bon Bèjé,\nMouin, mouton ou té rachté.\n\n\n3.\nKouté mouin avan mouin alé,\nMouin tankou gnou moun pèdi.\nMèt, palé! Sé ou ki konnin\nTout bagay kè mouin anvi,\nTou sa nan-m mouin bézouin vré;\nVini, é nan kè-m palé.\n\n\n4.\nMèt, palé!  É fè mouin paré\nLè-m tandé bi-in sa ou di,\nAk vitès, épi kè kontan\nPou-m suiv tou sa ou té di,\nMapé kouté ou, Mèt mouin;\nPalé, Jézu! Palé mouin!','<p><strong>1.</strong> </p>\n<p>Mèt, palé!  Sèvant ou kouté! </p>\n<p>Map tan-n bon paròl bouch ou; </p>\n<p>Voua ou ki fè mouin si kontan </p>\n<p>Kè-m souaf tandé li toujou. </p>\n<p>Mapé kouté ou, Ségnè, </p>\n<p>Ki sa ou gin pou di mouin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mèt, rélé mouin non pa mouin-min’m, </p>\n<p>Di-m sé ak mouin ou palé, </p>\n<p>Pou mouin kapab suiv ou pi fèm; </p>\n<p>Pou mouin maché san tranblé. </p>\n<p>Palé! Ou sé Bon Bèjé, </p>\n<p>Mouin, mouton ou té rachté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kouté mouin avan mouin alé, </p>\n<p>Mouin tankou gnou moun pèdi. </p>\n<p>Mèt, palé! Sé ou ki konnin </p>\n<p>Tout bagay kè mouin anvi, </p>\n<p>Tou sa nan-m mouin bézouin vré; </p>\n<p>Vini, é nan kè-m palé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mèt, palé!  É fè mouin paré </p>\n<p>Lè-m tandé bi-in sa ou di, </p>\n<p>Ak vitès, épi kè kontan </p>\n<p>Pou-m suiv tou sa ou té di, </p>\n<p>Mapé kouté ou, Mèt mouin; </p>\n<p>Palé, Jézu! Palé mouin! </p>\n','Met-Pale!-Sevant-Ou-Koute!',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1567,'696d0762-9372-4b39-82d9-7275e0107be6',0,0,'Vini Chak Moun Ki Plin Péché',44,'44_CE_ht','1.\nVini chak moun ki gin péché,\nKris gin mizèrikòd\nLi di li va ba ou la pè,\nL-a mét kè ou ann òd.\n\nKè:\nSèlman kouè li,  (2 foua)\nSèlman kouè Jézu;\nL-a sové ou,  (2 foua)\nL-a sové ou tou.\n\n\n2.\nPaské Kris té vèsé san li,\nKi gran bénédiksion!\nPlonjé ou nan san Jézu Ki\nLavé tout transgrésion.\n\n\n3.\nOui, jézu sé chémin la vi\nKi minnin ou nan sièl;\nVi-n mét tout konfians ou nan li;\nOu pap maché tou sèl.\n\n\n4.\nVi-n non, uni ou avèk nou\nKi pralé nan la gloua,\nPou abité la tou lé jou\nKoté tout moun an joua.','<p><strong>1.</strong> </p>\n<p>Vini chak moun ki gin péché, </p>\n<p>Kris gin mizèrikòd </p>\n<p>Li di li va ba ou la pè, </p>\n<p>L-a mét kè ou ann òd. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sèlman kouè li,  (2 foua)</strong> </p>\n<p><strong>Sèlman kouè Jézu;</strong> </p>\n<p><strong>L-a sové ou,  (2 foua)</strong> </p>\n<p><strong>L-a sové ou tou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Paské Kris té vèsé san li, </p>\n<p>Ki gran bénédiksion! </p>\n<p>Plonjé ou nan san Jézu Ki </p>\n<p>Lavé tout transgrésion. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, jézu sé chémin la vi </p>\n<p>Ki minnin ou nan sièl; </p>\n<p>Vi-n mét tout konfians ou nan li; </p>\n<p>Ou pap maché tou sèl. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vi-n non, uni ou avèk nou </p>\n<p>Ki pralé nan la gloua, </p>\n<p>Pou abité la tou lé jou </p>\n<p>Koté tout moun an joua. </p>\n','Vini-Chak-Moun-Ki-Plin-Peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1568,'5ddafb7f-f0bd-4084-aaf6-a777b070edf5',0,0,'O Gran Amou Kité Rachté-m',52,'52_CE_ht','1.\nO gran amou ki té rachté-m\nNan péché a,\nKoupab é sal jan mouin té yé\nM-pat gin éspoua.\nLè sa m-té nan gnou gran fènoua\nO ki gran jou!\nLi lévé nan-m mouin nan lan mò,\nKi gran amou!\n\nKè:\nKi gran amou pou rachté nan-m mouin,\nKi gran amou pou géri kè mouin.\nAmou sa voyé Ségnè-m mouri.\nKi gran amou! Adoré sin non Li.\n\n\n2.\nAmou sa fè Sovè-m mouri\nSou boua Kalvè\nPou péchè ki rébèl kont Li.\nKouman sa yé!\nAmou fè pon ant mouin ak sièl,\nLi pou mouin tou,\nMouin padonnin, mouin pa tou sèl\nKi gran amou!\n\n\n3.\nAmou té louvri pòt lumiè\nRouayom sièl la.\nJézu va parèt la an lè,\nL-a résévoua-m.\nAmou té lévé-m nan malè.\nA la gnou jou!\nPat gin lot èd sou tout la tè,\nKi gran amou!','<p><strong>1.</strong> </p>\n<p>O gran amou ki té rachté-m </p>\n<p>Nan péché a, </p>\n<p>Koupab é sal jan mouin té yé </p>\n<p>M-pat gin éspoua. </p>\n<p>Lè sa m-té nan gnou gran fènoua </p>\n<p>O ki gran jou! </p>\n<p>Li lévé nan-m mouin nan lan mò, </p>\n<p>Ki gran amou! </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ki gran amou pou rachté nan-m mouin,</strong> </p>\n<p><strong>Ki gran amou pou géri kè mouin.</strong> </p>\n<p><strong>Amou sa voyé Ségnè-m mouri.</strong> </p>\n<p><strong>Ki gran amou! Adoré sin non Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Amou sa fè Sovè-m mouri </p>\n<p>Sou boua Kalvè </p>\n<p>Pou péchè ki rébèl kont Li. </p>\n<p>Kouman sa yé! </p>\n<p>Amou fè pon ant mouin ak sièl, </p>\n<p>Li pou mouin tou, </p>\n<p>Mouin padonnin, mouin pa tou sèl </p>\n<p>Ki gran amou! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Amou té louvri pòt lumiè </p>\n<p>Rouayom sièl la. </p>\n<p>Jézu va parèt la an lè, </p>\n<p>L-a résévoua-m. </p>\n<p>Amou té lévé-m nan malè. </p>\n<p>A la gnou jou! </p>\n<p>Pat gin lot èd sou tout la tè, </p>\n<p>Ki gran amou! </p>\n','O-Gran-Amou-Kite-Rachte-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1569,'d265c454-bf00-4a25-8c89-32c4e414ef39',0,0,'Zanj ap chanté an lè',83,'83_CE_ht','1.\nZanj ap chanté an lè;\nMin bèl zétoual nan sièl;\nGin pitit pou nou ouè,\nJézu Éma-nuèl.\nKité mouton nan chan,\nVi-ni ouè mèvèy sa,\nPaské krèch Bétléèm\nRésévoua Roua dè roua.\n\n\n2.\nMin-m zétoual yo an joua\nPou nésans mèvéyé;\nPaské Pitit vièj la\nSé Ségnè tout la tè.\nMaj, kité péi ou\nVi-ni ouè mèvèy sa,\nPaské krèch Bétléèm\nRésévoua Roua dè roua.\n\n\n3.\nTi Pitit ki kouché\nNan lumiè zétoual la,\nSé Fis létènité,\nSé pou li tout la gloua.\nNou tout zanj nan sièl la\nVi-ni ouè mèvèy sa,\nPaské krèch Bétléèm\nRésévoua Roua dè roua.\n\n\n4.\nTout moun ki nan fè noua,\nYo tout anba malè,\nSé pou nou bèl chan sa,\nBon-Dieu ban nou lumiè.\nKouri vi-n jouin-n Jézu,\nSé li-min’m ki Sovè-n;\nKa Pitit nan krèch la\nSé Jézu, Rédanmtè-n.','<p><strong>1.</strong> </p>\n<p>Zanj ap chanté an lè; </p>\n<p>Min bèl zétoual nan sièl; </p>\n<p>Gin pitit pou nou ouè, </p>\n<p>Jézu Éma-nuèl. </p>\n<p>Kité mouton nan chan, </p>\n<p>Vi-ni ouè mèvèy sa, </p>\n<p>Paské krèch Bétléèm </p>\n<p>Résévoua Roua dè roua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m zétoual yo an joua </p>\n<p>Pou nésans mèvéyé; </p>\n<p>Paské Pitit vièj la </p>\n<p>Sé Ségnè tout la tè. </p>\n<p>Maj, kité péi ou </p>\n<p>Vi-ni ouè mèvèy sa, </p>\n<p>Paské krèch Bétléèm </p>\n<p>Résévoua Roua dè roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ti Pitit ki kouché </p>\n<p>Nan lumiè zétoual la, </p>\n<p>Sé Fis létènité, </p>\n<p>Sé pou li tout la gloua. </p>\n<p>Nou tout zanj nan sièl la </p>\n<p>Vi-ni ouè mèvèy sa, </p>\n<p>Paské krèch Bétléèm </p>\n<p>Résévoua Roua dè roua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tout moun ki nan fè noua, </p>\n<p>Yo tout anba malè, </p>\n<p>Sé pou nou bèl chan sa, </p>\n<p>Bon-Dieu ban nou lumiè. </p>\n<p>Kouri vi-n jouin-n Jézu, </p>\n<p>Sé li-min’m ki Sovè-n; </p>\n<p>Ka Pitit nan krèch la </p>\n<p>Sé Jézu, Rédanmtè-n. </p>\n','Zanj-ap-chante-an-le',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1570,'ea18fc7c-791a-4e37-8b97-001d0dfcdf4e',0,0,'Gnou pov té sèl, a koté gran chémin a',123,'123_CE_ht','1.\nGnou pov té sèl, a koté gran chémin a,\nJé-l té avèg, li pa té kapab ouè;\nRad li chiré, li tranblé nan lonbraj la,\nJézu vini, ténèb li vi-n lumiè.\n\nKè:\nLè Kris vini, puisans Satan fi-n brizé;\nLè Kris vini, dlo nan jé yo suiyé.\nLi pran tristès,\nLa vi nou plin ak la gloua;\nNou vi-n chanjé, lè Kris vini rété.\n\n\n2.\nMové léspri té chasé-l louin lakay li,\nPami ton-m yo, l-té rété nan mizè.\nLi koupé kò-l lè démon yo anparé-l,\nJézu vini é lagé li min-m lè.\n\n\n3.\nInpu! Inpu! Lépré a té nan touman,\nSoud é bèbè, yo pa ba-l èd du tout,\nLa fièv nan zò-l, maladi fi-n kaba yo;\nJézu vi-ni, Li ouété tout fréyè.\n\n\n4.\nKè yo té tris lè yo mété-l nan ton-m la,\nPaské lan mò té ouété tout éspoua;\nYo té an dèy é jé yo té tou plin dlo,\nJézu vini, pin-n yo chanjé an joua.\n\n\n5.\nInsi lè zom trouvé Sovè a gin fòs;\nYo pat kapab vink pasion é péché,\nKè yo brizé, kité yo tris é tou sèl,\nJézu vini, Li vini pou rété.','<p><strong>1.</strong> </p>\n<p>Gnou pov té sèl, a koté gran chémin a, </p>\n<p>Jé-l té avèg, li pa té kapab ouè; </p>\n<p>Rad li chiré, li tranblé nan lonbraj la, </p>\n<p>Jézu vini, ténèb li vi-n lumiè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè Kris vini, puisans Satan fi-n brizé;</strong> </p>\n<p><strong>Lè Kris vini, dlo nan jé yo suiyé.</strong> </p>\n<p><strong>Li pran tristès,</strong> </p>\n<p><strong>La vi nou plin ak la gloua;</strong> </p>\n<p><strong>Nou vi-n chanjé, lè Kris vini rété.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mové léspri té chasé-l louin lakay li, </p>\n<p>Pami ton-m yo, l-té rété nan mizè. </p>\n<p>Li koupé kò-l lè démon yo anparé-l, </p>\n<p>Jézu vini é lagé li min-m lè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Inpu! Inpu! Lépré a té nan touman, </p>\n<p>Soud é bèbè, yo pa ba-l èd du tout, </p>\n<p>La fièv nan zò-l, maladi fi-n kaba yo; </p>\n<p>Jézu vi-ni, Li ouété tout fréyè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kè yo té tris lè yo mété-l nan ton-m la, </p>\n<p>Paské lan mò té ouété tout éspoua; </p>\n<p>Yo té an dèy é jé yo té tou plin dlo, </p>\n<p>Jézu vini, pin-n yo chanjé an joua. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Insi lè zom trouvé Sovè a gin fòs; </p>\n<p>Yo pat kapab vink pasion é péché, </p>\n<p>Kè yo brizé, kité yo tris é tou sèl, </p>\n<p>Jézu vini, Li vini pou rété. </p>\n','Gnou-pov-te-sel-a-kote-gran-chemin-a',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1571,'939d9972-5ccf-472c-b167-d7d13131900a',0,0,'Mouin kanpé sou tout promès',10,'10_MJ_ht','1.\nMouin kanpé sou tout promès\nDé Kris, roua mouin,\nPandan létènité\nMap chanté louanj Li,\nGloua nan tout sièl la\nSa va sujè chan mouin,\nMouin kanpé sou tou promès Bon-Dieu.\n\nKè:\nKanpé, kanpé, mouin kanpé sou\nTou promès ké Bon-Dieu fè nou;\nKanpé, kanpé, mapé kanpé\nSou tout promès Bon-Dieu.\n\n2.\nMouin kanpé sou tout promès ki pa fayi,\nLè mové tanpèt\nÉ tout krint vlé koulé-m,\nPa voua Paròl Bon-Dieu\nM-a sòti vinkè,\nMouin kanpé sou tout promès Bon-Dieu.','<p><strong>1.</strong> </p>\n<p>Mouin kanpé sou tout promès </p>\n<p>Dé Kris, roua mouin, </p>\n<p>Pandan létènité </p>\n<p>Map chanté louanj Li, </p>\n<p>Gloua nan tout sièl la </p>\n<p>Sa va sujè chan mouin, </p>\n<p>Mouin kanpé sou tou promès Bon-Dieu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kanpé, kanpé, mouin kanpé sou</strong> </p>\n<p><strong>Tou promès ké Bon-Dieu fè nou;</strong> </p>\n<p><strong>Kanpé, kanpé, mapé kanpé</strong> </p>\n<p><strong>Sou tout promès Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin kanpé sou tout promès ki pa fayi, </p>\n<p>Lè mové tanpèt </p>\n<p>É tout krint vlé koulé-m, </p>\n<p>Pa voua Paròl Bon-Dieu </p>\n<p>M-a sòti vinkè, </p>\n<p>Mouin kanpé sou tout promès Bon-Dieu. </p>\n','Mouin-kanpe-sou-tout-promes',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1572,'96391549-601c-44b2-99b0-5f27f2fa17bc',0,0,'Atansion Ti Jé Mouin Sa Ou Gadé',29,'29_MJ_ht','1.\nAtansion ti jé mouin sa ou gadé (2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion ti jé mouin sa ou gadé.\n\n2.\nAtansion zorèy mouin sa ou kouté (2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion zorèy mouin sa ou kouté.\n\n3.\nAtansion bouch mouin tou sa ou palé,(2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion bouch mouin tou sa ou palé.\n\n4.\nAtansion ti min mouin tou sa ou fè, (2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion ti min mouin tou sa ou fè.\n\n5.\nAtansion pié mouin koté ou pralé, (2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion pié mouin koté ou pralé.\n\n6.\nAtansion kè mouin ki sa ou rinmin, (2 foua)\nPaské Bon-Dieu nan sièl la\nApé gadé nou an-ba,\nAtansion kè mouin ki sa ou rinmin.','<p><strong>1.</strong> </p>\n<p>Atansion ti jé mouin sa ou gadé (2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion ti jé mouin sa ou gadé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Atansion zorèy mouin sa ou kouté (2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion zorèy mouin sa ou kouté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Atansion bouch mouin tou sa ou palé,(2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion bouch mouin tou sa ou palé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Atansion ti min mouin tou sa ou fè, (2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion ti min mouin tou sa ou fè. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Atansion pié mouin koté ou pralé, (2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion pié mouin koté ou pralé. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Atansion kè mouin ki sa ou rinmin, (2 foua) </p>\n<p>Paské Bon-Dieu nan sièl la </p>\n<p>Apé gadé nou an-ba, </p>\n<p>Atansion kè mouin ki sa ou rinmin. </p>\n','Atansion-Ti-Je-Mouin-Sa-Ou-Gade',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1573,'af0583ef-1ce9-4f3b-8c96-07f009a97ca9',0,0,'M-ap Chanté Tout Byin-fè lé Ségnè pou toujou',30,'30_MJ_ht','1.\nM-ap chanté tout byinfè lé Ségnè pou toujou;\nM-ap chanté, m-ap chanté;\nM-ap chanté tout byinfè lé Ségnè pou toujou;\nM-ap chanté tout byinfè lé Ségnè.\nAk bouch mouin, m-ap a-nonsé\nFidélité-l,  fidélité-l;\nAk bouch mouin, m-ap a-nonsé\nFidélité lé Ségnè pou tout moun.\n(bis 4 prémié lign)','<p><strong>1.</strong> </p>\n<p>M-ap chanté tout byinfè lé Ségnè pou toujou; </p>\n<p>M-ap chanté, m-ap chanté; </p>\n<p>M-ap chanté tout byinfè lé Ségnè pou toujou; </p>\n<p>M-ap chanté tout byinfè lé Ségnè. </p>\n<p>Ak bouch mouin, m-ap a-nonsé </p>\n<p>Fidélité-l,  fidélité-l; </p>\n<p>Ak bouch mouin, m-ap a-nonsé </p>\n<p>Fidélité lé Ségnè pou tout moun. </p>\n<p>(bis 4 prémié lign) </p>\n','M-ap-Chante-Tout-Byin-fe-le-Segne-pou-toujou',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1574,'61a7c1d3-f006-4c3e-9c2a-a6459d685881',0,0,'Gen Yon Non Mwen Renmen Chanté',42,'42_MJ_ht','1.\nGin gnou non mouin rinmin chanté,\nSé gnou non mouin p-ap jan-m blié,\nPaské sé pa li mouin sové,\nSé présié non dé Jézu.\n\nKè:\nO, oui, mouin rinmin Jézu (3 foua)\nPaské Li rinmin-m avan.\n\n2.\nBoua Kalvè mouin p-ap jan-m blié,\nSé la Li mouri pousové\nTout péchè ki vlé vi-n lavé\nNan san Li ki koulé la.\n\n3.\nParol Bon-Dieu m-apé séré\nNan kè mouin pou mouin pa péché\nKont Jézu ki té vlé sové\nNou, lè Li mouri pou nou.','<p><strong>1.</strong> </p>\n<p>Gin gnou non mouin rinmin chanté, </p>\n<p>Sé gnou non mouin p-ap jan-m blié, </p>\n<p>Paské sé pa li mouin sové, </p>\n<p>Sé présié non dé Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O, oui, mouin rinmin Jézu (3 foua)</strong> </p>\n<p><strong>Paské Li rinmin-m avan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Boua Kalvè mouin p-ap jan-m blié, </p>\n<p>Sé la Li mouri pousové </p>\n<p>Tout péchè ki vlé vi-n lavé </p>\n<p>Nan san Li ki koulé la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Parol Bon-Dieu m-apé séré </p>\n<p>Nan kè mouin pou mouin pa péché </p>\n<p>Kont Jézu ki té vlé sové </p>\n<p>Nou, lè Li mouri pou nou. </p>\n','Gen-Yon-Non-Mwen-Renmen-Chante',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1575,'d113cd86-2f72-4e17-8856-51a10cd317d0',0,0,'Jézu Sové-m É Li Toujou Rinmin-m',45,'45_MJ_ht','1.\nJézu sové-m é Li toujou rinmin-m;\nNan pouin angnin ki kapab séparé-m.\nLi té mouri pou rachté nan-m mouin;\nKoun-yé-a mouin pou Li.\n\nKè:\nKoun-yé-a mouin pou Jézu,\nJézu pou mouin-min’m tou;\nPa sèlman pou la vi sa a,\nLi kinbé-m pou toujou.\n\n2.\nMouin-min’m té pèdu sou la pin-n péché-m;\nJézu désan-n sou tè sa a pou édé-m;\nLi ouété mouin nan ront é mizè;\nKoun-yé-a mouin pou Li.\n\n3.\nLa joua apé débòdé nan kè mouin;\nKris, Pitit Bon-Dieu, sé Rédanmtè mouin,\nPa mouayin san Li mouin afranchi;\nKoun-yé-a mouin pou Li.','<p><strong>1.</strong> </p>\n<p>Jézu sové-m é Li toujou rinmin-m; </p>\n<p>Nan pouin angnin ki kapab séparé-m. </p>\n<p>Li té mouri pou rachté nan-m mouin; </p>\n<p>Koun-yé-a mouin pou Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Koun-yé-a mouin pou Jézu,</strong> </p>\n<p><strong>Jézu pou mouin-min’m tou;</strong> </p>\n<p><strong>Pa sèlman pou la vi sa a,</strong> </p>\n<p><strong>Li kinbé-m pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin-min’m té pèdu sou la pin-n péché-m; </p>\n<p>Jézu désan-n sou tè sa a pou édé-m; </p>\n<p>Li ouété mouin nan ront é mizè; </p>\n<p>Koun-yé-a mouin pou Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La joua apé débòdé nan kè mouin; </p>\n<p>Kris, Pitit Bon-Dieu, sé Rédanmtè mouin, </p>\n<p>Pa mouayin san Li mouin afranchi; </p>\n<p>Koun-yé-a mouin pou Li. </p>\n','Jezu-Sove-m-E-Li-Toujou-Rinmin-m',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1576,'218d577d-2c15-4988-98d3-8b72173121dc',0,0,'Jézu La Ak Mwen Tout Koté-m Pasé',52,'52_MJ_ht','1.\nJézu la ak mouin tout koté-m pasé,\nJézu la ak mouin sé vré;\nSi-m maché sou tè sa a,\nOu janbé la mè,\nJézu la, m-pa bézouin pè.\nMin-m nan la valé,\nNan ténèb ou danjé,\nLi kinbé-m nan min Li,\nM-konnin sé vré;\nJézu la ak mouin tout koté-m pasé,\nJézu la ak mouin, sévré!','<p><strong>1.</strong> </p>\n<p>Jézu la ak mouin tout koté-m pasé, </p>\n<p>Jézu la ak mouin sé vré; </p>\n<p>Si-m maché sou tè sa a, </p>\n<p>Ou janbé la mè, </p>\n<p>Jézu la, m-pa bézouin pè. </p>\n<p>Min-m nan la valé, </p>\n<p>Nan ténèb ou danjé, </p>\n<p>Li kinbé-m nan min Li, </p>\n<p>M-konnin sé vré; </p>\n<p>Jézu la ak mouin tout koté-m pasé, </p>\n<p>Jézu la ak mouin, sévré! </p>\n','Jezu-La-Ak-Mwen-Tout-Kote-m-Pase',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1577,'384ae3ca-3c2f-481b-89dc-917c0a0c6bc5',0,0,'Chémin An Pa Fasil',67,'67_MJ_ht','1.\nChémin an pa fasil\nJouk nou rivé nan sièl a\nLi gignin pikan é pièj anpil\nChémin an pa fasil\nMin Jézu maché ak nou\nLi rann kè nou tout a fè trankil\n\nRefrain:\nNon, non, chémin an pa fasil (bis)\nMin Jézu maché ak nou\nLi poté fado nou\nGras li fè kè nou réjoui anpil\n\n2.\nChémin an pas fasil\nGingnin zéprèv, gin traka\nTout koté nou pasé gin danjé\nRout la mèt difisil\nSi Jézu kinbé min nou\nNa rété fèm lè pié nou glisé\n\n3.\nKo nou konn fatigé\nDékourajé nan rout la\nEpi Satan konn vi-n tanté nou\nMin n-apé tann lé Ségnè\nK-ap parèt nan glou Li\nL-apé pran nou ak Li gnou bèl jou','<p><strong>1.</strong> </p>\n<p>Chémin an pa fasil </p>\n<p>Jouk nou rivé nan sièl a </p>\n<p>Li gignin pikan é pièj anpil </p>\n<p>Chémin an pa fasil </p>\n<p>Min Jézu maché ak nou </p>\n<p>Li rann kè nou tout a fè trankil </p>\n<p>Refrain: </p>\n<p>Non, non, chémin an pa fasil (bis) </p>\n<p>Min Jézu maché ak nou </p>\n<p>Li poté fado nou </p>\n<p>Gras li fè kè nou réjoui anpil </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chémin an pas fasil </p>\n<p>Gingnin zéprèv, gin traka </p>\n<p>Tout koté nou pasé gin danjé </p>\n<p>Rout la mèt difisil </p>\n<p>Si Jézu kinbé min nou </p>\n<p>Na rété fèm lè pié nou glisé </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ko nou konn fatigé </p>\n<p>Dékourajé nan rout la </p>\n<p>Epi Satan konn vi-n tanté nou </p>\n<p>Min n-apé tann lé Ségnè </p>\n<p>K-ap parèt nan glou Li </p>\n<p>L-apé pran nou ak Li gnou bèl jou </p>\n','Chemin-An-Pa-Fasil',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1578,'c457ac3d-2d74-4a20-992d-3863260509ea',0,0,'Sièl La Sé La M-Pralé',85,'85_MJ_ht','1.\nSièl la sé la m-pralé\nPou tout létènité.\nSé lè sa gloua-L va révélé\nLòt bò bèl pòt sa, Jézu kap tan-n mouin la.\n\nKè:\nMouin va rankontré tout krétyin yo,\nTout sa yo ki kon-n lagno.\nÉ sé la m-a viv toujou.\nMin plus pasé sa m-a ouè Jézu,\nM-a réjoui nan prézans Li.\nSé pou sa mouin vlé alé.\n\n2.\nGin la, bèl flèv la vi,\nLa joua nan Jézu Kri.\nKonbyin mouin ta vlé ouè ak Li!\nSé la lumiè-l kléré, pou tout létènité.\nSé pou sa mouin vlé alé.','<p><strong>1.</strong> </p>\n<p>Sièl la sé la m-pralé </p>\n<p>Pou tout létènité. </p>\n<p>Sé lè sa gloua-L va révélé </p>\n<p>Lòt bò bèl pòt sa, Jézu kap tan-n mouin la. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin va rankontré tout krétyin yo,</strong> </p>\n<p><strong>Tout sa yo ki kon-n lagno.</strong> </p>\n<p><strong>É sé la m-a viv toujou.</strong> </p>\n<p><strong>Min plus pasé sa m-a ouè Jézu,</strong> </p>\n<p><strong>M-a réjoui nan prézans Li.</strong> </p>\n<p><strong>Sé pou sa mouin vlé alé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin la, bèl flèv la vi, </p>\n<p>La joua nan Jézu Kri. </p>\n<p>Konbyin mouin ta vlé ouè ak Li! </p>\n<p>Sé la lumiè-l kléré, pou tout létènité. </p>\n<p>Sé pou sa mouin vlé alé. </p>\n','Siel-La-Se-La-M-Prale',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1579,'1704aa53-04f6-4a75-85f1-7c9e562bdd19',0,0,'J’accours Et Me Rends Au Seigneur',332,'332_CE_fr','1.\nJ\'accours et me rends au Seigneur!\nBénissant son pourvoir suprême,\nJe viens recevoir le baptême,\nRempli de zèle et de ferveur!  (bis)\n\n\n2.\nAssistez donc, Anges des Cieux.\nSoyez témoins de ma promesse!\nElle me remplit d\'allégresse,\nEt désormais je suis heureux!  (bis)\n\n\n3.\nJésus l\'a prescrit aux humains,\nS\'y soumettant aussi lui-même,\nDans les flots reçut le baptême:\nQuel exemple pour les Chrétiens!  (bis)\n\n\n4.\nAccourez donc, ô coeurs fervents,\nQue remplit la foi de ses flammes;\nÉlevez donc vers Dieu vos âmes,\nEt soyez désormais constants!  (bis)\n\n\n5.\nL\'Éternel au sein des élus\nBénira votre ardeur sublime;\nEn reniant toujours le crime,\nMarchez au sentier des vertus.  (bis)','<p><strong>1.</strong> </p>\n<p>J&#39;accours et me rends au Seigneur! </p>\n<p>Bénissant son pourvoir suprême, </p>\n<p>Je viens recevoir le baptême, </p>\n<p>Rempli de zèle et de ferveur!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Assistez donc, Anges des Cieux. </p>\n<p>Soyez témoins de ma promesse! </p>\n<p>Elle me remplit d&#39;allégresse, </p>\n<p>Et désormais je suis heureux!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus l&#39;a prescrit aux humains, </p>\n<p>S&#39;y soumettant aussi lui-même, </p>\n<p>Dans les flots reçut le baptême: </p>\n<p>Quel exemple pour les Chrétiens!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Accourez donc, ô coeurs fervents, </p>\n<p>Que remplit la foi de ses flammes; </p>\n<p>Élevez donc vers Dieu vos âmes, </p>\n<p>Et soyez désormais constants!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>L&#39;Éternel au sein des élus </p>\n<p>Bénira votre ardeur sublime; </p>\n<p>En reniant toujours le crime, </p>\n<p>Marchez au sentier des vertus.  (bis) </p>\n','J\'accours-Et-Me-Rends-Au-Seigneur',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1580,'63871108-622d-44a7-819e-81ddaf9bf7f8',0,0,'San Sovè, San Bon-Dieu',8,'8_CE_ht','1.\nSan Sovè, san Bon-Dieu, sa ou va fè?\nLè ou apé mouri, sa ou va fè?\nLè sa li sérié, nan-m ou pralé;\nKi sa ou va tounin; sa ou va fè?\n\n\n2.\nOu-min’m kap ri tout tan; sa ou va fè?\nJou sa ou va tou sèl; sa ou va fé?\nSi lè lan mò vini, ou pa paré,\nLa pè va sézi ou; sa ou va fè?\n\n\n3.\nEské gin gnou koté pou oukaché?\nLè Bon-Dieu rélé ou, sa ou va fè?\nGran moun tan ap pasé; jèn jan; jèn fi;\nMoun ki rinmin péché; sa ou va fè?\n\n\n4.\nMoun ki rinmin Jézu, al jouin Bon-Dieu;\nLi bon, li patènel; li sové yo;\nLan mò pa fò yo pè, Jézu ak yo;\nLi kaché yo non bra-l, li sové yo.','<p><strong>1.</strong> </p>\n<p>San Sovè, san Bon-Dieu, sa ou va fè? </p>\n<p>Lè ou apé mouri, sa ou va fè? </p>\n<p>Lè sa li sérié, nan-m ou pralé; </p>\n<p>Ki sa ou va tounin; sa ou va fè? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou-min’m kap ri tout tan; sa ou va fè? </p>\n<p>Jou sa ou va tou sèl; sa ou va fé? </p>\n<p>Si lè lan mò vini, ou pa paré, </p>\n<p>La pè va sézi ou; sa ou va fè? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Eské gin gnou koté pou oukaché? </p>\n<p>Lè Bon-Dieu rélé ou, sa ou va fè? </p>\n<p>Gran moun tan ap pasé; jèn jan; jèn fi; </p>\n<p>Moun ki rinmin péché; sa ou va fè? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Moun ki rinmin Jézu, al jouin Bon-Dieu; </p>\n<p>Li bon, li patènel; li sové yo; </p>\n<p>Lan mò pa fò yo pè, Jézu ak yo; </p>\n<p>Li kaché yo non bra-l, li sové yo. </p>\n','San-Sove-San-Bon-Dieu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1581,'a6b3d892-6f2d-4709-9465-0691de7e95c5',0,0,'Ségnè, palé mouin pou-m pale',33,'33_CE_ht','1.\nSégnè, palé mouin pou-m palé\nTankou gnou éko voua ou-minm\nKité-m chaché, minm jan ak ou\nPitit ou ki pèdu,ki sèl\n\nKè\nGidé-m, Ségnè, pou-m ka gidé\nPié moun ki pran mové chémin\nBan-m manjé, pou-m kapab nouri\nMoun ki grangou, ak la ma-n ou.\n\n2.\nBan mouin fòs pou-m rété fidèl\nKampé sou gnou rok, fèm ak ou\nPouin mouin kapab lonjé min mouin\nÉdé moun kap bat nan la vi.\n\n3.\nSégnè montré-m tout sa ki bon\nPou mouin  montré lè zòt yo tou\nInpiré sa mouin di, é fè\nParòl mouin touché kè yo\n\n4.\nSèvi ak mouin, min mouin Ségnè\nKondui mouin tout koté ou vlé\nJouk tan mouin ouè fas béni ou\nPou-m patajé gloua ak joua ou.','<p><strong>1.</strong> </p>\n<p>Ségnè, palé mouin pou-m palé </p>\n<p>Tankou gnou éko voua ou-minm </p>\n<p>Kité-m chaché, minm jan ak ou </p>\n<p>Pitit ou ki pèdu,ki sèl </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Gidé-m, Ségnè, pou-m ka gidé</strong> </p>\n<p><strong>Pié moun ki pran mové chémin</strong> </p>\n<p><strong>Ban-m manjé, pou-m kapab nouri</strong> </p>\n<p><strong>Moun ki grangou, ak la ma-n ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ban mouin fòs pou-m rété fidèl </p>\n<p>Kampé sou gnou rok, fèm ak ou </p>\n<p>Pouin mouin kapab lonjé min mouin </p>\n<p>Édé moun kap bat nan la vi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ségnè montré-m tout sa ki bon </p>\n<p>Pou mouin  montré lè zòt yo tou </p>\n<p>Inpiré sa mouin di, é fè </p>\n<p>Paròl mouin touché kè yo </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sèvi ak mouin, min mouin Ségnè </p>\n<p>Kondui mouin tout koté ou vlé </p>\n<p>Jouk tan mouin ouè fas béni ou </p>\n<p>Pou-m patajé gloua ak joua ou. </p>\n','Segne-pale-mouin-pou-m-pale',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1582,'719c4893-9715-4feb-90e9-3b60fd9fcf0e',0,0,'Gnou jou avan kouché solèy',53,'53_CE_ht','1.\nGnou jou avan kouché solèy,\nAnpil malad té résté la\nDévan Jézu, nan gran doulè.\nBi-into yo alé avèk joua.\n\n\n2.\nGnou foua anko sé asouè tou.\nÉ nou proché bò Jézu-Kri.\nSégnè si nou pa ouè kò Ou,\nNou kouè Ou isit an Éspri.\n\n\n3.\nO Sovè, chasé tout malè.\nGin pami nou moun ki soufran,\nÉ dòt ki tris, ou nan mizè;\nDòt kap soufri nan min Satan.\n\n\n4.\nLé mon-n tronpé jin-n jan ak fi,\nÉ yo rété la dan toujou!\nGin moun ki gin mové zanmi,\nPoutan yo pa vlé vi-n jouin-n Ou!\n\n\n5.\nE tout moun yo bézouin la pè,\nSové dé tout bagay ki fo.\nMin moun yo kap suiv Ou tou pré,\nSanti péché andan kè yo!\n\n\n6.\nO Sovè Kris, Ou sé moun tou.\nLè sou la tè Ou té gnou non-m,\nOu té grangou ak tanté tou.\nOu kon-n sa ki nan kè lè zòm.\n\n\n7.\nMin, Ou gin toujou min-m puisans,\nParòl Ou gingnin fòs toujou.\nBéni nou tout an abondans,\nÉ asouè a vi-n géri nou.','<p><strong>1.</strong> </p>\n<p>Gnou jou avan kouché solèy, </p>\n<p>Anpil malad té résté la </p>\n<p>Dévan Jézu, nan gran doulè. </p>\n<p>Bi-into yo alé avèk joua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou foua anko sé asouè tou. </p>\n<p>É nou proché bò Jézu-Kri. </p>\n<p>Ségnè si nou pa ouè kò Ou, </p>\n<p>Nou kouè Ou isit an Éspri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O Sovè, chasé tout malè. </p>\n<p>Gin pami nou moun ki soufran, </p>\n<p>É dòt ki tris, ou nan mizè; </p>\n<p>Dòt kap soufri nan min Satan. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lé mon-n tronpé jin-n jan ak fi, </p>\n<p>É yo rété la dan toujou! </p>\n<p>Gin moun ki gin mové zanmi, </p>\n<p>Poutan yo pa vlé vi-n jouin-n Ou! </p>\n<p><br/> <strong>5.</strong></p>\n<p>E tout moun yo bézouin la pè, </p>\n<p>Sové dé tout bagay ki fo. </p>\n<p>Min moun yo kap suiv Ou tou pré, </p>\n<p>Santi péché andan kè yo! </p>\n<p><br/> <strong>6.</strong></p>\n<p>O Sovè Kris, Ou sé moun tou. </p>\n<p>Lè sou la tè Ou té gnou non-m, </p>\n<p>Ou té grangou ak tanté tou. </p>\n<p>Ou kon-n sa ki nan kè lè zòm. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Min, Ou gin toujou min-m puisans, </p>\n<p>Paròl Ou gingnin fòs toujou. </p>\n<p>Béni nou tout an abondans, </p>\n<p>É asouè a vi-n géri nou. </p>\n','Gnou-jou-avan-kouche-soley',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1583,'7c9dcc6d-0dcb-4004-af66-6d85bbfa6d8e',0,0,'Kè nou pa bézouin troublé plis',73,'73_CE_ht','1.\nKè nou pa bézouin troublé plis,\nBon-Dieu ban nou la pè  nan Kris.\nSé sèlman pou nou kouè Bon-Dieu,\nAk Jézu-Kri, la vérité.\n\n\n2.\nLa foua nan Li, sé gnou rémèd;\nLi chanjé troub yo ki pi rèd!\nMoun yo ki gin anpil la foua,\nDoulè yo va tounin nan joua.\n\n\n3.\nGin anpil moun ki pè lan mò;\nYo krin-n sa kap tan-n yo lòt bò!\nJézu di ké nan kay Papa-l,\nLi va préparé pliziè sal.\n\n\n4.\nSé té pou tout disip li yo,\nLi té palé gnou si bèl mo.\nLi vlé disip li yo rété\nNan min-m koté li va yé.\n\n\n5.\nSé Jézu-Kri, la vérité,\nKi promèt li ban nou la pè;\nSa li ban nou, sé pou tout tan;\nSan krint ni troub, kè nou kontan.','<p><strong>1.</strong> </p>\n<p>Kè nou pa bézouin troublé plis, </p>\n<p>Bon-Dieu ban nou la pè  nan Kris. </p>\n<p>Sé sèlman pou nou kouè Bon-Dieu, </p>\n<p>Ak Jézu-Kri, la vérité. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La foua nan Li, sé gnou rémèd; </p>\n<p>Li chanjé troub yo ki pi rèd! </p>\n<p>Moun yo ki gin anpil la foua, </p>\n<p>Doulè yo va tounin nan joua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin anpil moun ki pè lan mò; </p>\n<p>Yo krin-n sa kap tan-n yo lòt bò! </p>\n<p>Jézu di ké nan kay Papa-l, </p>\n<p>Li va préparé pliziè sal. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sé té pou tout disip li yo, </p>\n<p>Li té palé gnou si bèl mo. </p>\n<p>Li vlé disip li yo rété </p>\n<p>Nan min-m koté li va yé. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Sé Jézu-Kri, la vérité, </p>\n<p>Ki promèt li ban nou la pè; </p>\n<p>Sa li ban nou, sé pou tout tan; </p>\n<p>San krint ni troub, kè nou kontan. </p>\n','Ke-nou-pa-bezouin-trouble-plis',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1584,'809bb144-064d-493b-a619-dd13ca7ea9f1',0,0,'Lé monn antié pèdu nan ténèb péché',78,'78_CE_ht','1.\nLé mon-n antié pèdu nan ténèb péché;\nSèl lumiè lé mon-n sé Jézu!\nKon solèy midi Lévanjil la briyé;\nSèl lumiè lé mon-n sé Jézu!\n\nKè:\nVi-n jouin-n Jézu, La ba ou lumiè;\nLumiè a dous, li bèl, sé Jézu!\nMouin té avèg, koul yé a mouin ouè\nSèl lumiè lé mon-n sé Jézu!\n\n\n2.\nMoun ki gin Jézu nan kè yo, gin lumiè;\nSèl lumiè lé mon-n sé Jézu!\nSi ou suiv Jézu chak jou, ou-a gin lumiè;\nSèl lumiè lé mon-n sé Jézu!\n\n\n3.\nMoun kapé viv nan péché, yo nan ténèb;\nSèl lumiè lé mon-n sé Jézu!\nPa konté sous tradision yo, sa trò fèb;\nSèl lumiè lé mon-n sé Jézu!\n\n\n4.\nSèl lumiè ki va nan sièl la, sé Jézu;\nSèl lumiè lé mon-n sé Jézu!\nLumiè na gin nan vil dò a sé Jézu;\nSèl lumiè lé mon-n sé Jézu!','<p><strong>1.</strong> </p>\n<p>Lé mon-n antié pèdu nan ténèb péché; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p>Kon solèy midi Lévanjil la briyé; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Vi-n jouin-n Jézu, La ba ou lumiè;</strong> </p>\n<p><strong>Lumiè a dous, li bèl, sé Jézu!</strong> </p>\n<p><strong>Mouin té avèg, koul yé a mouin ouè</strong> </p>\n<p><strong>Sèl lumiè lé mon-n sé Jézu!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moun ki gin Jézu nan kè yo, gin lumiè; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p>Si ou suiv Jézu chak jou, ou-a gin lumiè; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Moun kapé viv nan péché, yo nan ténèb; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p>Pa konté sous tradision yo, sa trò fèb; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sèl lumiè ki va nan sièl la, sé Jézu; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n<p>Lumiè na gin nan vil dò a sé Jézu; </p>\n<p>Sèl lumiè lé mon-n sé Jézu! </p>\n','Le-monn-antie-pedu-nan-teneb-peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1585,'720be493-98f8-41f8-8026-d589995fdbb9',0,0,'Jézu rélé-n nan tout touman',80,'80_CE_ht','1.\nJézu rélé-n nan tout touman\nLa vi nou ki kou tanpèt,\nTou lé jou voua Li rézonné,\nLi di: O Kréti-in, suiv mouin.\n\n\n2.\nLè apot yo té kon-n tandé-l\nKoté la mè Galilé,\nYo té kité kay ak zanmi,\nTout bagay pou tèt non Li.\n\n\n3.\nJézu rélé-n pou nou kité\nLé mon-n ak tout vanité.\nTout idòl avèk tout malis,\nLi di: Kréti-in, rinmin-m plus.\n\n\n4.\nNan la joua é nan la tristès,\nLè nou jinnin ou a lèz,\nNinpòt lè, é nan ninpòt rout\nLi di-m: Pran ka-m pasé tout.\n\n\n5.\nJézu rélé-n! Nan pitié ou,\nSové, fè-n tandé voua ou;\nBan nou kè ki obéisan\nPou sèvi, rinmin tout tan.','<p><strong>1.</strong> </p>\n<p>Jézu rélé-n nan tout touman </p>\n<p>La vi nou ki kou tanpèt, </p>\n<p>Tou lé jou voua Li rézonné, </p>\n<p>Li di: O Kréti-in, suiv mouin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè apot yo té kon-n tandé-l </p>\n<p>Koté la mè Galilé, </p>\n<p>Yo té kité kay ak zanmi, </p>\n<p>Tout bagay pou tèt non Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu rélé-n pou nou kité </p>\n<p>Lé mon-n ak tout vanité. </p>\n<p>Tout idòl avèk tout malis, </p>\n<p>Li di: Kréti-in, rinmin-m plus. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan la joua é nan la tristès, </p>\n<p>Lè nou jinnin ou a lèz, </p>\n<p>Ninpòt lè, é nan ninpòt rout </p>\n<p>Li di-m: Pran ka-m pasé tout. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Jézu rélé-n! Nan pitié ou, </p>\n<p>Sové, fè-n tandé voua ou; </p>\n<p>Ban nou kè ki obéisan </p>\n<p>Pou sèvi, rinmin tout tan. </p>\n','Jezu-rele-n-nan-tout-touman',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1586,'0dc15d0c-fe15-4913-bfe9-c7b3f3009104',0,0,'Map viv pou Jézu gnou vi ki sinsè',84,'84_CE_ht','1.\nMap viv pou Jézu gnou vi ki sinsè,\nGnou vi plin obéisans ak tout kè;\nMap fè tout éfò pou fè li plézi,\nKon sa l-a béni-m toujou nan la vi.\n\nKè:  Jézu, Ségnè ak Sovè-m,\nMouin ba ou tout la vi-m,\nPaské sou la koua Kalvè,\nSé pou mouin ou mouri.\nSé ou-min’m sèl ki Mèt mouin,\nPran kè-m kon tròn pa ou;\nMap bay la vi mouin pou-m sèvi\nOu-min’m, Kris, pou toujou.\n\n\n2.\nMap viv pou Jézu ki ouété péché-m;\nLi mouri sou boua pou-l té kab sové-m.\nAmou sa oblijé-m répon-n voua li;\nMa ba-l sa-m gingnin, é ma va suiv li.\n\n\n3.\nMap viv pou Jézu tout koté m-pasé;\nMap fè tou sa-m fè pou la gloua Bon-Dieu.\nMouin prèt pou soufri afliksion ou pèd,\nMalgré tout zéprèv mouin pap jan-m régnèt.\n\n\n4.\nMap viv pou Jézu ti tan la vi sa,\nPi gran trézò mouin sé souri Mèt la.\nTravay mouin sé chaché péchè pèdi,\nFè moun ki bouké jouin-n répo nan li.','<p><strong>1.</strong> </p>\n<p>Map viv pou Jézu gnou vi ki sinsè, </p>\n<p>Gnou vi plin obéisans ak tout kè; </p>\n<p>Map fè tout éfò pou fè li plézi, </p>\n<p>Kon sa l-a béni-m toujou nan la vi. </p>\n<p>Kè:  Jézu, Ségnè ak Sovè-m, </p>\n<p>Mouin ba ou tout la vi-m, </p>\n<p>Paské sou la koua Kalvè, </p>\n<p>Sé pou mouin ou mouri. </p>\n<p>Sé ou-min’m sèl ki Mèt mouin, </p>\n<p>Pran kè-m kon tròn pa ou; </p>\n<p>Map bay la vi mouin pou-m sèvi </p>\n<p>Ou-min’m, Kris, pou toujou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Map viv pou Jézu ki ouété péché-m; </p>\n<p>Li mouri sou boua pou-l té kab sové-m. </p>\n<p>Amou sa oblijé-m répon-n voua li; </p>\n<p>Ma ba-l sa-m gingnin, é ma va suiv li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Map viv pou Jézu tout koté m-pasé; </p>\n<p>Map fè tou sa-m fè pou la gloua Bon-Dieu. </p>\n<p>Mouin prèt pou soufri afliksion ou pèd, </p>\n<p>Malgré tout zéprèv mouin pap jan-m régnèt. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Map viv pou Jézu ti tan la vi sa, </p>\n<p>Pi gran trézò mouin sé souri Mèt la. </p>\n<p>Travay mouin sé chaché péchè pèdi, </p>\n<p>Fè moun ki bouké jouin-n répo nan li. </p>\n','Map-viv-pou-Jezu-gnou-vi-ki-sinse',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1587,'c05c56b3-739b-4471-9c5d-51fe81eddf7d',0,0,'Jézu té fèt nan gnou krèch mizérab',88,'88_CE_ht','1.Jézu té fèt nan gnou krèch mizérab;Kè li té dou, é paròl li émab;Maltrété, li mouri kon gnou koupab,Jézu, présié Ségnè.Kè Présié, présié Jézu,Zanmi sètin, juska la fin;Li ofri kò-l pou mouin gingnin la vi,Jézu, présié Ségnè.2.Nan loraj, sé li sèl ki bay la pè;Grangou ak souaf, sé li ki satisfè;Lè tantasion, li bay gras nésésè,Jézu, présié Ségnè.3.Sé  zanmi tout moun, min anpil rénié-l;Gnoun èd pou fèb yo, li té umilié;Lumiè lé mon-n, - sé li yo krusifié;Jézu, présié Ségnè.','<p><strong>1.</strong> </p>\n<p>Jézu té fèt nan gnou krèch mizérab; </p>\n<p>Kè li té dou, é paròl li émab; </p>\n<p>Maltrété, li mouri kon gnou koupab, </p>\n<p>Jézu, présié Ségnè. </p>\n<p>Kè</p>\n<p>Présié, présié Jézu, </p>\n<p>Zanmi sètin, juska la fin; </p>\n<p>Li ofri kò-l pou mouin gingnin la vi, </p>\n<p>Jézu, présié Ségnè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan loraj, sé li sèl ki bay la pè; </p>\n<p>Grangou ak souaf, sé li ki satisfè; </p>\n<p>Lè tantasion, li bay gras nésésè, </p>\n<p>Jézu, présié Ségnè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé  zanmi tout moun, min anpil rénié-l; </p>\n<p>Gnoun èd pou fèb yo, li té umilié; </p>\n<p>Lumiè lé mon-n, - sé li yo krusifié; </p>\n<p>Jézu, présié Ségnè. </p>\n','Jezu-te-fet-nan-gnou-krech-mizerab',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1588,'62877f8f-9583-4aac-84e7-7d421446cb9a',0,0,'An nou chanté la viktoua',12,'12_MJ_ht','1.\nAn nou chanté viktoua\nChanté fò pou moun-n tandé li.\nAn nou chanté la viktoua\nSé pou moun-n ki nan Jézu-Kri.\nMalgré Satan vlé détounin nou\nAvèk Kris nou fidèl toujou\nAn nou chanté la viktoua\nLa viktoua nan Jézu-Kri.','<p><strong>1.</strong> </p>\n<p>An nou chanté viktoua </p>\n<p>Chanté fò pou moun-n tandé li. </p>\n<p>An nou chanté la viktoua </p>\n<p>Sé pou moun-n ki nan Jézu-Kri. </p>\n<p>Malgré Satan vlé détounin nou </p>\n<p>Avèk Kris nou fidèl toujou </p>\n<p>An nou chanté la viktoua </p>\n<p>La viktoua nan Jézu-Kri. </p>\n','An-nou-chante-la-viktoua',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1589,'5db19671-c8e2-47f8-9fde-0eb85ac70cb1',0,0,'O frè é sè mouin, lè lé mond ap brulé',16,'16_MJ_ht','1.\nO frè é sè mouin, lè lé mond ap  brulé\nÈské ou pa ta vlé\nKouché nan bra Jézu?\nKaché nan-m ou nan abri sur sa\nRòk ki té frapé pou nou.','<p><strong>1.</strong> </p>\n<p>O frè é sè mouin, lè lé mond ap  brulé </p>\n<p>Èské ou pa ta vlé </p>\n<p>Kouché nan bra Jézu? </p>\n<p>Kaché nan-m ou nan abri sur sa </p>\n<p>Ròk ki té frapé pou nou. </p>\n','O-fre-e-se-mouin-le-le-mond-ap-brule',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1590,'bbb997ce-4a65-497e-9e22-98027cacd935',0,0,'Lé Mond Plen Tristès Ak Mizè',34,'34_MJ_ht','1.\nLé mon-n plin ak tristès é mizè\nKè nou chajé chak jou.\nJézu té ouété tout fado yo\nSou mòn Kalvè pou nou.\n\nKè:\nLi vlé ouété tout fado pou ou\nBo Kalvè, bo Kalvè.\nLi vlé ouété tout fado pou ou\nLi tou pré nou toujou.\n\n2.\nLagé tout chaj ou sou Jézu\nKité krint ak sousi\nKà Jézu té ouété fado nou,\nSou Kalvè Li mouri.\n\n3.\nNan-m troublé, lé Ségnè konnin\nChagrin ki nan kè ou\nMin Li vlé ouété tout fado yo\nSou mòn Kalvè pou ou.','<p><strong>1.</strong> </p>\n<p>Lé mon-n plin ak tristès é mizè </p>\n<p>Kè nou chajé chak jou. </p>\n<p>Jézu té ouété tout fado yo </p>\n<p>Sou mòn Kalvè pou nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li vlé ouété tout fado pou ou</strong> </p>\n<p><strong>Bo Kalvè, bo Kalvè.</strong> </p>\n<p><strong>Li vlé ouété tout fado pou ou</strong> </p>\n<p><strong>Li tou pré nou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lagé tout chaj ou sou Jézu </p>\n<p>Kité krint ak sousi </p>\n<p>Kà Jézu té ouété fado nou, </p>\n<p>Sou Kalvè Li mouri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan-m troublé, lé Ségnè konnin </p>\n<p>Chagrin ki nan kè ou </p>\n<p>Min Li vlé ouété tout fado yo </p>\n<p>Sou mòn Kalvè pou ou. </p>\n','Le-Mond-Plen-Tristes-Ak-Mize',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1591,'600d9b2a-6d38-474a-ac8e-0209c7b036f5',0,0,'La Koua Kanpé Solid',49,'49_MJ_ht','1.\nLa koua kanpé solid,\nAlélouya,  Alélouya;\nDékourajé janmin –\nAlélouya,  Alélouya.\nTout van lanfè vanté,\nLé mon-n tout riz montré,\nMin la koua rété solid,\nAlélouya pou la koua.\n\nKè:\nAlélouya,  Alélouya,\nAlélouya pou la koua,\nAlélouya,  Alélouya;\nLi sové sak gin la foua.\n\n2.\nSé toujou la koua Jézu,\nAlélouya,  Alélouya;\nLi pòté la viktoua –\nAlélouya,  Alélouya.\nLa koua montré gras Li,\nNan Kris, Pitit Bon-Dieu,\nKi té mouri pou sové nou,\nAlélouya pou la koua.\n\n3.\nJézu té péyé dèt péché,\nAlélouya,  Alélouya;\nLi pòté péché nou,\nAlélouya,  Alélouya.\nSé pou la koua nou chanté,\nDé Kris, ofran-n pou nou,\nDé Kris, Roua nou vivan,\nAlélouya, pou la koua.','<p><strong>1.</strong> </p>\n<p>La koua kanpé solid, </p>\n<p>Alélouya,  Alélouya; </p>\n<p>Dékourajé janmin – </p>\n<p>Alélouya,  Alélouya. </p>\n<p>Tout van lanfè vanté, </p>\n<p>Lé mon-n tout riz montré, </p>\n<p>Min la koua rété solid, </p>\n<p>Alélouya pou la koua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alélouya,  Alélouya,</strong> </p>\n<p><strong>Alélouya pou la koua,</strong> </p>\n<p><strong>Alélouya,  Alélouya;</strong> </p>\n<p><strong>Li sové sak gin la foua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé toujou la koua Jézu, </p>\n<p>Alélouya,  Alélouya; </p>\n<p>Li pòté la viktoua – </p>\n<p>Alélouya,  Alélouya. </p>\n<p>La koua montré gras Li, </p>\n<p>Nan Kris, Pitit Bon-Dieu, </p>\n<p>Ki té mouri pou sové nou, </p>\n<p>Alélouya pou la koua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu té péyé dèt péché, </p>\n<p>Alélouya,  Alélouya; </p>\n<p>Li pòté péché nou, </p>\n<p>Alélouya,  Alélouya. </p>\n<p>Sé pou la koua nou chanté, </p>\n<p>Dé Kris, ofran-n pou nou, </p>\n<p>Dé Kris, Roua nou vivan, </p>\n<p>Alélouya, pou la koua. </p>\n','La-Koua-Kanpe-Solid',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1592,'9d89a427-d2b2-4924-9710-0aae03814e9c',0,0,'Jézu ouè sa ou fè',61,'61_MJ_ht','1.\nJézu ouè sa ou fè, Li tandé sa ou di,\nLé Ségnè ap kouté tout tan, tout tan.\nJézu ouè sa ou fè, Li tandé sa ou di,\nLé Ségnè ap kouté tout tan.','<p><strong>1.</strong> </p>\n<p>Jézu ouè sa ou fè, Li tandé sa ou di, </p>\n<p>Lé Ségnè ap kouté tout tan, tout tan. </p>\n<p>Jézu ouè sa ou fè, Li tandé sa ou di, </p>\n<p>Lé Ségnè ap kouté tout tan. </p>\n','Jezu-oue-sa-ou-fe',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1593,'70e5fbc9-85e8-4622-94ce-44b1760841fc',0,0,'M-ap Chanté! M-ap Chanté',65,'65_MJ_ht','1.\nM-ap chanté!  M-ap chanté!\nPaské mouin gin gnou chan nan kè-m,\nPrézans Jézu ranpli-m ak joua é la viktoua\nM-ap adoré-L, adoré-L,\nPaské amou Li si gran,\nÉ si-m pa kab ouè Li isit\nGnou jou ma ouè Li fas a fas!','<p><strong>1.</strong> </p>\n<p>M-ap chanté!  M-ap chanté! </p>\n<p>Paské mouin gin gnou chan nan kè-m, </p>\n<p>Prézans Jézu ranpli-m ak joua é la viktoua </p>\n<p>M-ap adoré-L, adoré-L, </p>\n<p>Paské amou Li si gran, </p>\n<p>É si-m pa kab ouè Li isit </p>\n<p>Gnou jou ma ouè Li fas a fas! </p>\n','M-ap-Chante!-M-ap-Chante',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1594,'2dbce946-e9e2-4f8f-ad91-baebb2c8c482',0,0,'Pi Fon, Pi Fon Mwen Pralé',83,'83_MJ_ht','1.\nPi fon, pi fon mouin pralé\nNan lamou Jézu – Sovè-m,\nMap chanté konnin pouki\nLi té tèlman rinmin-m,\nPouki Li lonjé min Li\nOuété-m nan gran péché,\nLi té sové-m, Li té géri-m\nMalgré-m té byin égaré.\n\n2.\nPi fon, pi fon mouin pralé\nNan la soufrans dé Jézu,\nMap suiv Li nan jadin an\nKoté-l té soué san Li,\nLa Li pran fado doulè,\nPéché lé mon-n antié,\nO chè Ségnè, Ségnè édé-m,\nPa kité-m dékourajé.\n\n3.\nPi fon, pi fon mouin pralé\nNan volonté Jézu-Kri,\nMap prié, konnin suiv Li\nPou fè Ségnè-m plézi\nMouin vlé bésé dévan Li,\nProstèné nan pié Li,\nKris, fè sa-ou vlé,\nKasé kè mouin,\nFè-m gnou bon vèso, tanpri.','<p><strong>1.</strong> </p>\n<p>Pi fon, pi fon mouin pralé </p>\n<p>Nan lamou Jézu – Sovè-m, </p>\n<p>Map chanté konnin pouki </p>\n<p>Li té tèlman rinmin-m, </p>\n<p>Pouki Li lonjé min Li </p>\n<p>Ouété-m nan gran péché, </p>\n<p>Li té sové-m, Li té géri-m </p>\n<p>Malgré-m té byin égaré. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pi fon, pi fon mouin pralé </p>\n<p>Nan la soufrans dé Jézu, </p>\n<p>Map suiv Li nan jadin an </p>\n<p>Koté-l té soué san Li, </p>\n<p>La Li pran fado doulè, </p>\n<p>Péché lé mon-n antié, </p>\n<p>O chè Ségnè, Ségnè édé-m, </p>\n<p>Pa kité-m dékourajé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pi fon, pi fon mouin pralé </p>\n<p>Nan volonté Jézu-Kri, </p>\n<p>Map prié, konnin suiv Li </p>\n<p>Pou fè Ségnè-m plézi </p>\n<p>Mouin vlé bésé dévan Li, </p>\n<p>Prostèné nan pié Li, </p>\n<p>Kris, fè sa-ou vlé, </p>\n<p>Kasé kè mouin, </p>\n<p>Fè-m gnou bon vèso, tanpri. </p>\n','Pi-Fon-Pi-Fon-Mwen-Prale',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1595,'4949698d-ed48-438d-9b84-78ff62476a9f',0,0,'Le Monn Vlé Mouin Suiv Plézi Li',49,'49_CE_ht','1.\nLé mon-n vlé mouin suiv plézi li,\nMin pito m-suiv Jézu;\nLé mon-n pa kap ban mouin la vi,\nMouin vlé sanblé Jézu.\n\nKè:\nSanblé Jézu, sé dézi-m,\nLè mouin sèl ou pami moun;\nSanblé Jézu nan tout vi-m,\nMouin vlé sanbléJézu.\n\n\n2.\nLi té krazé chin-n péché-m yo;\nMouin vlé sanblé Jézu,\nPou nan-m mouin kab sèvi-l pi fò,\nMouin vlé sanblé Jézu.\n\n\n3.\nMouin vlé ouè Li nan Paradi\nPami anj avèk sin;\nMouin vlé tandé bèl paròl Li\nLè-l di: « Ou té fè bi-in. »','<p><strong>1.</strong> </p>\n<p>Lé mon-n vlé mouin suiv plézi li, </p>\n<p>Min pito m-suiv Jézu; </p>\n<p>Lé mon-n pa kap ban mouin la vi, </p>\n<p>Mouin vlé sanblé Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sanblé Jézu, sé dézi-m,</strong> </p>\n<p><strong>Lè mouin sèl ou pami moun;</strong> </p>\n<p><strong>Sanblé Jézu nan tout vi-m,</strong> </p>\n<p><strong>Mouin vlé sanbléJézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té krazé chin-n péché-m yo; </p>\n<p>Mouin vlé sanblé Jézu, </p>\n<p>Pou nan-m mouin kab sèvi-l pi fò, </p>\n<p>Mouin vlé sanblé Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vlé ouè Li nan Paradi </p>\n<p>Pami anj avèk sin; </p>\n<p>Mouin vlé tandé bèl paròl Li </p>\n<p>Lè-l di: « Ou té fè bi-in. » </p>\n','Le-Monn-Vle-Mouin-Suiv-Plezi-Li',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1596,'c8bed380-6c68-4d4f-aee2-d379555e8766',0,0,'Jézu, mouin té pran la koua mouin',57,'57_CE_ht','1.\nJézu, mouin té pran la koua mouin\nPou-m kité tout é pou-m suiv Ou.\nAbandonnin é méprizé,\nSé Ou sèl pou mouin toujou.\nSé pou-m pèdu tout anbision,\nTou sa m-té chaché é konnin.\nMin richès mouin sou kondision\nKé Bon-Dieu ak sièl pou mouin.\n\n\n2.\nLé mon-n mèt kité é rai mouin.\nYo té kité Sovè-m tou.\nKè umin-n kapab tronpé mouin,\nLè zòm pa fidèl kòm Ou.\nÉ pandan Ou kontan avè-m,\nBon-Dieu sajès é amou,\nYo mèt rai é kité mouin,\nMouin vlé rété avèk Ou.\n\n\n3.\nLè zòm mèt pèsékuté mouin,\nSa va fè-m proché pi pré Ou.\nLa vi du kapab avèk mouin,\nPou mouin, sièl la va pi dous.\nOkin mal pa kab fè-m soufri\nPandan amou Ou pou mouin.\nPlézi tè sa pa fè-m réjoui.\nJoua Ou bay, sé joua san fin.\n\n\n4.\nSé pou-n vouayajé pou la gloua\nPa la foua é la priè.\nNou va konbat pou la viktoua.\nAk Bon-Dieu nou gin la pè.\nBi-into sé pou-n kité tè sa.\nPélérinaj nou va fini.\nNou va ouè foui tout travay la.\nRékonpans nou, sé Jézu.','<p><strong>1.</strong> </p>\n<p>Jézu, mouin té pran la koua mouin </p>\n<p>Pou-m kité tout é pou-m suiv Ou. </p>\n<p>Abandonnin é méprizé, </p>\n<p>Sé Ou sèl pou mouin toujou. </p>\n<p>Sé pou-m pèdu tout anbision, </p>\n<p>Tou sa m-té chaché é konnin. </p>\n<p>Min richès mouin sou kondision </p>\n<p>Ké Bon-Dieu ak sièl pou mouin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mon-n mèt kité é rai mouin. </p>\n<p>Yo té kité Sovè-m tou. </p>\n<p>Kè umin-n kapab tronpé mouin, </p>\n<p>Lè zòm pa fidèl kòm Ou. </p>\n<p>É pandan Ou kontan avè-m, </p>\n<p>Bon-Dieu sajès é amou, </p>\n<p>Yo mèt rai é kité mouin, </p>\n<p>Mouin vlé rété avèk Ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè zòm mèt pèsékuté mouin, </p>\n<p>Sa va fè-m proché pi pré Ou. </p>\n<p>La vi du kapab avèk mouin, </p>\n<p>Pou mouin, sièl la va pi dous. </p>\n<p>Okin mal pa kab fè-m soufri </p>\n<p>Pandan amou Ou pou mouin. </p>\n<p>Plézi tè sa pa fè-m réjoui. </p>\n<p>Joua Ou bay, sé joua san fin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sé pou-n vouayajé pou la gloua </p>\n<p>Pa la foua é la priè. </p>\n<p>Nou va konbat pou la viktoua. </p>\n<p>Ak Bon-Dieu nou gin la pè. </p>\n<p>Bi-into sé pou-n kité tè sa. </p>\n<p>Pélérinaj nou va fini. </p>\n<p>Nou va ouè foui tout travay la. </p>\n<p>Rékonpans nou, sé Jézu. </p>\n','Jezu-mouin-te-pran-la-koua-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1597,'55cc3e05-048e-473d-abe4-bd82a77d8fb9',0,0,'Peyi Lwen',59,'59_CE_ht','1.\nPéi louin, louin isit, sé la mouin doué alé,\nPéi sa gin anpil moun pèdu.\nMoun la yo pa konnin Jézu-Kri sé koulé,\nLi rinmin nou anpil, li mouri.\n\nKè:\nPéi louin…louin isit…\nMouin doué alé…mouin doué alé…\nJouk tout moun…fi-n konnin…\nBòn nouvèl…Jézu-Kri.\n\n\n2.\nLi voyé mouin alé nan gnou lié  difisil,\nÉ lié sa sé gnou lié trè, trèdu.\nMoun nan yo gin kè du, yo papé vlé kouté,\nMin kan min-m yo bézouin-m, mouin pralé.\n\n\n3.\nGin anpil, anpil moun ki toujou yo déro.\nJézu-Kri vlé yo vi-n jodi a.\nLi voyé mouin alé nan péi louin ankò,\nRanmasé yo, pòté yo bay Li.','<p><strong>1.</strong> </p>\n<p>Péi louin, louin isit, sé la mouin doué alé, </p>\n<p>Péi sa gin anpil moun pèdu. </p>\n<p>Moun la yo pa konnin Jézu-Kri sé koulé, </p>\n<p>Li rinmin nou anpil, li mouri. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Péi louin…louin isit…</strong> </p>\n<p><strong>Mouin doué alé…mouin doué alé…</strong> </p>\n<p><strong>Jouk tout moun…fi-n konnin…</strong> </p>\n<p><strong>Bòn nouvèl…Jézu-Kri.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li voyé mouin alé nan gnou lié  difisil, </p>\n<p>É lié sa sé gnou lié trè, trèdu. </p>\n<p>Moun nan yo gin kè du, yo papé vlé kouté, </p>\n<p>Min kan min-m yo bézouin-m, mouin pralé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin anpil, anpil moun ki toujou yo déro. </p>\n<p>Jézu-Kri vlé yo vi-n jodi a. </p>\n<p>Li voyé mouin alé nan péi louin ankò, </p>\n<p>Ranmasé yo, pòté yo bay Li. </p>\n','Peyi-Lwen',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1598,'65698c4b-b5be-4bf4-b1aa-d537e06e1e02',0,0,'Maten an avan ou sòti',66,'66_CE_ht','1.\nMatin an avan ou sòti,\nÈské outé prié?\nO non dé Jézu, Sovè nou,\nÈské ou mandé favè li\nPou protéjé ou?\n\nKè:\nLa priè va ba ou répo,\nLa priè chanjé tout bagay;\nLè chémin la sanblé trò long,\nPa blié prié.\n\n\n2.\nLè kè ou té ranpli kolè,\nÈské outé prié?\nÈské outé mandé gras li\nPou padonnin lè zòt yo\nKi té blésé ou.\n\n\n3.\nLè zéprèv anpil té vini,\nÈské outé prié?\nLè nan-m ou té gingnin tristès,\nÈské outé chaché joua Bon-Dieu\nDépi gran matin?','<p><strong>1.</strong> </p>\n<p>Matin an avan ou sòti, </p>\n<p>Èské outé prié? </p>\n<p>O non dé Jézu, Sovè nou, </p>\n<p>Èské ou mandé favè li </p>\n<p>Pou protéjé ou? </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>La priè va ba ou répo,</strong> </p>\n<p><strong>La priè chanjé tout bagay;</strong> </p>\n<p><strong>Lè chémin la sanblé trò long,</strong> </p>\n<p><strong>Pa blié prié.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè kè ou té ranpli kolè, </p>\n<p>Èské outé prié? </p>\n<p>Èské outé mandé gras li </p>\n<p>Pou padonnin lè zòt yo </p>\n<p>Ki té blésé ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè zéprèv anpil té vini, </p>\n<p>Èské outé prié? </p>\n<p>Lè nan-m ou té gingnin tristès, </p>\n<p>Èské outé chaché joua Bon-Dieu </p>\n<p>Dépi gran matin? </p>\n','Maten-an-avan-ou-soti',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1599,'1f8ccee5-2d33-40e3-8d60-ab57095f6fcc',0,0,'Kouté!Anj yo ap chanté',82,'82_CE_ht','1.\nKouté!  Anj yo ap chanté,\nGloua a Jézu, nouvo né!\nSé la pè é sé la joua;\nMoun va sové pa la foua.\nVi-ni tout moun, chanté tou,\nAnj yo ap chanté ak nou.\nJézu fèt nan Bétléèm,\nLi sé pitit Bon-Dieu min-m!\n\nKè:\nKouté! Anj yo ap chanté,\nGloua a Jézu, nouvo né!\n\n\n2.\nNan sièl la yo adoré-l,\nLi sé Kris, li étènèl,\nMin, kon lè jus té rivé\nLi té vi-ni kou Sovè\nGadé Bon-Dieu nan la chè!\nAdoré Li sou la tè.\nSa sé bagay ki pi bèl!\nJézu sé Éma-nuèl.\n\n\n3.\nLi té kité tout gloua li,\nLi té vi-ni pou mouri,\nPou nou fèt gnou dézièm foua,\nPou nou antré nan la gloua.\nNou prostèné dévan li\nKi sous justis é la vi.\nGloua, ònè, louanj, viktoua,\nBa li tout, sé li ki Roua.','<p><strong>1.</strong> </p>\n<p>Kouté!  Anj yo ap chanté, </p>\n<p>Gloua a Jézu, nouvo né! </p>\n<p>Sé la pè é sé la joua; </p>\n<p>Moun va sové pa la foua. </p>\n<p>Vi-ni tout moun, chanté tou, </p>\n<p>Anj yo ap chanté ak nou. </p>\n<p>Jézu fèt nan Bétléèm, </p>\n<p>Li sé pitit Bon-Dieu min-m! </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kouté! Anj yo ap chanté,</strong> </p>\n<p><strong>Gloua a Jézu, nouvo né!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan sièl la yo adoré-l, </p>\n<p>Li sé Kris, li étènèl, </p>\n<p>Min, kon lè jus té rivé </p>\n<p>Li té vi-ni kou Sovè </p>\n<p>Gadé Bon-Dieu nan la chè! </p>\n<p>Adoré Li sou la tè. </p>\n<p>Sa sé bagay ki pi bèl! </p>\n<p>Jézu sé Éma-nuèl. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li té kité tout gloua li, </p>\n<p>Li té vi-ni pou mouri, </p>\n<p>Pou nou fèt gnou dézièm foua, </p>\n<p>Pou nou antré nan la gloua. </p>\n<p>Nou prostèné dévan li </p>\n<p>Ki sous justis é la vi. </p>\n<p>Gloua, ònè, louanj, viktoua, </p>\n<p>Ba li tout, sé li ki Roua. </p>\n','Koute!Anj-yo-ap-chante',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1600,'4fe6cca2-7144-4c2f-94de-2cab61ce7d4f',0,0,'Mouin tap néyé nan péché',89,'89_CE_ht','1.\nMouin tap néyé nan péché\nBi-in louin la pè Bon-dieu;\nNan-m mouin ki té tout taché\nTap koulé san lévé.\nJézu ki tapé véyé-m\nLè li tandé rèl mouin\nLi vi-ni tout suit édé-m,\nLi sové mouin.\n\nKè:\nJézu sové-m!  Jézu sové-m!\nLè mouin té san sékou, Jézu sové-m!\nJézu sové-m!  Jézu sové-m!\nLè mouin té san sékou, Jézu sovè-m!\n\n\n2.\nOui, tout kè-m mouin vlé bay li,\nPou-m toujou avèk li,\nPou mouin viv nan prézans li,\nPou-m chanté louanj li,\nAmou li si bon, si gran!\nLi mérité tout vi-m,\nPou mouin sèvi li tout tan\nMap bay tout vi-m.\n\n\n3.\nMoun ki nan péché chaché-l,\nJézu vlé sové ou;\nPou-l pòté, avèk amou-l,\nTout fado ki trò lou.\nL-a sové ou nan tanpèt,\nL-a kaché ou toujou,\nFòtifié ou nan dizèt,\nL-a sové ou.','<p><strong>1.</strong> </p>\n<p>Mouin tap néyé nan péché </p>\n<p>Bi-in louin la pè Bon-dieu; </p>\n<p>Nan-m mouin ki té tout taché </p>\n<p>Tap koulé san lévé. </p>\n<p>Jézu ki tapé véyé-m </p>\n<p>Lè li tandé rèl mouin </p>\n<p>Li vi-ni tout suit édé-m, </p>\n<p>Li sové mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sové-m!  Jézu sové-m!</strong> </p>\n<p><strong>Lè mouin té san sékou, Jézu sové-m!</strong> </p>\n<p><strong>Jézu sové-m!  Jézu sové-m!</strong> </p>\n<p><strong>Lè mouin té san sékou, Jézu sovè-m!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, tout kè-m mouin vlé bay li, </p>\n<p>Pou-m toujou avèk li, </p>\n<p>Pou mouin viv nan prézans li, </p>\n<p>Pou-m chanté louanj li, </p>\n<p>Amou li si bon, si gran! </p>\n<p>Li mérité tout vi-m, </p>\n<p>Pou mouin sèvi li tout tan </p>\n<p>Map bay tout vi-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Moun ki nan péché chaché-l, </p>\n<p>Jézu vlé sové ou; </p>\n<p>Pou-l pòté, avèk amou-l, </p>\n<p>Tout fado ki trò lou. </p>\n<p>L-a sové ou nan tanpèt, </p>\n<p>L-a kaché ou toujou, </p>\n<p>Fòtifié ou nan dizèt, </p>\n<p>L-a sové ou. </p>\n','Mouin-tap-neye-nan-peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1601,'ddfc8de0-187f-49b2-8891-f9050f2639cc',0,0,'Papa mouin gin kay, avèk anpil tè',101,'101_CE_ht','1.\nPapa mouin gin kay, avèk anpil tè,\nPla min ni kinbin tout fòtu-n ké li fè,\nPiè présié, diaman, ak lajan ak lò,\nTout bouat li yo plin, nan richès li pi fò.\n\nKè:  Mouin sé pitit Roua a,\nGnou pitit Roua a;\nJézu sé Sovè mouin,\nMouin sé pitit Roua a.\n\n\n2.\nSèl Pitit Bon-Dieu ki Sovè lé mon-n,\nGnou jou té maché sou la tè kon gnou non-m:\nKoun yé a, lap plédé padon nou nan sièl;\nLè L-a vi-n chaché nou, n-a rantré avè-l.\n\n\n3.\nLontan mouin maché, gnou non-m égaré;\nM-té pito péché pasé konnin Bon-Dieu;\nMin Li adopté-m, é Li ban mouin non-m;\nMouin sé éritié gnou palè ak kourò-n.\n\n\n4.\nJoupa ou bi-in kay, sa pa fè-m angnin;\nAn ro a, yap bati gnou palè pou mouin;\nPandan map tan-n li, m-a chanté chan sa a:\nTout gloua pou Bon-Dieu, mouin sé pitit Roua a.','<p><strong>1.</strong> </p>\n<p>Papa mouin gin kay, avèk anpil tè, </p>\n<p>Pla min ni kinbin tout fòtu-n ké li fè, </p>\n<p>Piè présié, diaman, ak lajan ak lò, </p>\n<p>Tout bouat li yo plin, nan richès li pi fò. </p>\n<p>Kè:  Mouin sé pitit Roua a, </p>\n<p>Gnou pitit Roua a; </p>\n<p>Jézu sé Sovè mouin, </p>\n<p>Mouin sé pitit Roua a. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sèl Pitit Bon-Dieu ki Sovè lé mon-n, </p>\n<p>Gnou jou té maché sou la tè kon gnou non-m: </p>\n<p>Koun yé a, lap plédé padon nou nan sièl; </p>\n<p>Lè L-a vi-n chaché nou, n-a rantré avè-l. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lontan mouin maché, gnou non-m égaré; </p>\n<p>M-té pito péché pasé konnin Bon-Dieu; </p>\n<p>Min Li adopté-m, é Li ban mouin non-m; </p>\n<p>Mouin sé éritié gnou palè ak kourò-n. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Joupa ou bi-in kay, sa pa fè-m angnin; </p>\n<p>An ro a, yap bati gnou palè pou mouin; </p>\n<p>Pandan map tan-n li, m-a chanté chan sa a: </p>\n<p>Tout gloua pou Bon-Dieu, mouin sé pitit Roua a. </p>\n','Papa-mouin-gin-kay-avek-anpil-te',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1602,'a5e7830f-5edf-470e-8699-c5b305360bba',0,0,'Adoré Li! Jézu, Rédanmtè, béni nou',103,'103_CE_ht','1.\nAdoré Li! Jézu, Rédanmtè, béni nou;\nChanté amou-L, anonsé-l tou patou.\nAdoré Li! Zanj yo ki pi ro nan sièl la;\nFòs é lonnè pou Jézu, Sovè a.\nKou gnou Bèjé, Jézu véyé pitit Li,\nToujou lapé pòté yo nan bra Li.\nAdoré Li! Palé sou gras Li montré nou;\nAdoré Li! Nan chan jouayé chak jou.\n\n\n2.\nAdoré Li! Jézu, Rédanmtè nap béni;\nPou péché nou, Li té soufri, mouri.\nLi sé Ròch nou, éspoua la vi étènèl la;\nAdoré Li! Jézu krusifié a.\nChanté louanj Li, Jézu pòté tristès nou.\nAmou Jézu mèvéyé, san fin tou.\nAdoré Li! Palé sou gras Li montré nou;\nAdoré Li! Nan chan jouayé chak jou.\n\n\n3.\nAdoré Li! Nap béni sèl Rédanmtè a;\nJouk pòt sièl la yap tandé: Ozanna!\nJézu, Sovè, va régné pandan tout sièk yo,\nKouroné Li Profòt é Prèt é Roua.\nKris ap vi-ni, Gran Chèf sou tout la tè a;\nPou Jézu, tout puisans avèk tout gloua.\nAdoré Li! Palé sou gras Li montré nou;\nAdoré Li! Nan chan jouayé chak jou.','<p><strong>1.</strong> </p>\n<p>Adoré Li! Jézu, Rédanmtè, béni nou; </p>\n<p>Chanté amou-L, anonsé-l tou patou. </p>\n<p>Adoré Li! Zanj yo ki pi ro nan sièl la; </p>\n<p>Fòs é lonnè pou Jézu, Sovè a. </p>\n<p>Kou gnou Bèjé, Jézu véyé pitit Li, </p>\n<p>Toujou lapé pòté yo nan bra Li. </p>\n<p>Adoré Li! Palé sou gras Li montré nou; </p>\n<p>Adoré Li! Nan chan jouayé chak jou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Adoré Li! Jézu, Rédanmtè nap béni; </p>\n<p>Pou péché nou, Li té soufri, mouri. </p>\n<p>Li sé Ròch nou, éspoua la vi étènèl la; </p>\n<p>Adoré Li! Jézu krusifié a. </p>\n<p>Chanté louanj Li, Jézu pòté tristès nou. </p>\n<p>Amou Jézu mèvéyé, san fin tou. </p>\n<p>Adoré Li! Palé sou gras Li montré nou; </p>\n<p>Adoré Li! Nan chan jouayé chak jou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Adoré Li! Nap béni sèl Rédanmtè a; </p>\n<p>Jouk pòt sièl la yap tandé: Ozanna! </p>\n<p>Jézu, Sovè, va régné pandan tout sièk yo, </p>\n<p>Kouroné Li Profòt é Prèt é Roua. </p>\n<p>Kris ap vi-ni, Gran Chèf sou tout la tè a; </p>\n<p>Pou Jézu, tout puisans avèk tout gloua. </p>\n<p>Adoré Li! Palé sou gras Li montré nou; </p>\n<p>Adoré Li! Nan chan jouayé chak jou. </p>\n','Adore-Li!-Jezu-Redanmte-beni-nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1603,'1872859f-6f6b-44c2-897e-16f7537376cf',0,0,'A la bèl gloua, maché ak Jézu',105,'105_CE_ht','1.\nA la bèl gloua, maché ak Jézu!\nLi vèsé san-l pou mouin;\nNan-m mouin pran plézi li chak jou,\nSé gran joua lè Jézu koté mouin,\nLi ak mouin tou patou.\nGloua a Dieu!\nSé gnou bèl gloua pou mouin.\n\nKè:\nA la bèl gloua, maché ak Jézu!  (2 foua)\nLi kondui-m nan tout chémin,\nLi kinbin-m, sa sé sètin;\nA la bèl gloua, maché ak Jézu.\n\n\n2.\nA la bè gloua, min-m lè li fè noua,\nPou-m konnin li tou pré;\nAlò m-prié ak konfians, ak foua;\nA la bèl gloua, santi li tou pré\nLè chémin mouin plin joua.\nGloua a Dieu!\nSé gnou bèl gloua pou mouin.\n\n\n3.\nA la bèl gloua, lè, la kay Bon-Dieu,\nAk li m-a va maché,\nPou toujou koté-l m-a va rété;\nSa va gnou mèvèy lè map chanté\nPou tou létènité.\nGloua a Dieu!\nSé gnou bèl gloua pou mouin.','<p><strong>1.</strong> </p>\n<p>A la bèl gloua, maché ak Jézu! </p>\n<p>Li vèsé san-l pou mouin; </p>\n<p>Nan-m mouin pran plézi li chak jou, </p>\n<p>Sé gran joua lè Jézu koté mouin, </p>\n<p>Li ak mouin tou patou. </p>\n<p>Gloua a Dieu! </p>\n<p>Sé gnou bèl gloua pou mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la bèl gloua, maché ak Jézu!  (2 foua)</strong> </p>\n<p><strong>Li kondui-m nan tout chémin,</strong> </p>\n<p><strong>Li kinbin-m, sa sé sètin;</strong> </p>\n<p><strong>A la bèl gloua, maché ak Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A la bè gloua, min-m lè li fè noua, </p>\n<p>Pou-m konnin li tou pré; </p>\n<p>Alò m-prié ak konfians, ak foua; </p>\n<p>A la bèl gloua, santi li tou pré </p>\n<p>Lè chémin mouin plin joua. </p>\n<p>Gloua a Dieu! </p>\n<p>Sé gnou bèl gloua pou mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A la bèl gloua, lè, la kay Bon-Dieu, </p>\n<p>Ak li m-a va maché, </p>\n<p>Pou toujou koté-l m-a va rété; </p>\n<p>Sa va gnou mèvèy lè map chanté </p>\n<p>Pou tou létènité. </p>\n<p>Gloua a Dieu! </p>\n<p>Sé gnou bèl gloua pou mouin. </p>\n','A-la-bel-gloua-mache-ak-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1604,'9c9a37d5-f774-4b6b-93dd-0ba1b50a047d',0,0,'Min gnou bèl istoua: Kris té kité tout gloua',106,'106_CE_ht','1.\nMin gnou bèl istoua: Kris té kité tout gloua\nLi té konnin nan sièl; Non Li: Éma-nuèl.\nLè-l fèt nan krèch la,\nMoun yo pat konpran-n sa;\nSou la tè li pasé anpil traka.\n\nKè:\nA la m-rinmin Li! Mouin adoré Li!\nJézu sé tou sa kè mouin bézouin.\nGran Kréatè a té vi-n sové mouin;\nMouin konnin Bon-Dieu nan Jézu-Kri.\n\n\n2.\nLi té umilié-l pou nou kapab rachté;\nLè nou té nan fè noua, lè nou té san éspoua,\nBon-Dieu nan gras li\nTé kité tout gloua li;\nLi té vi-n chaché é sové nan-m mouin.\n\n\n3.\nNan gran bonté li, pou nou Kris té soufri;\nSou fòm gnou non-m li vi-n pou montré plan salu;\nSé gnou gran mistè…\nSakrifis sou Kalvè;\nKris sèl kon-n satisfè bézouin kè mouin.','<p><strong>1.</strong> </p>\n<p>Min gnou bèl istoua: Kris té kité tout gloua </p>\n<p>Li té konnin nan sièl; Non Li: Éma-nuèl. </p>\n<p>Lè-l fèt nan krèch la, </p>\n<p>Moun yo pat konpran-n sa; </p>\n<p>Sou la tè li pasé anpil traka. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la m-rinmin Li! Mouin adoré Li!</strong> </p>\n<p><strong>Jézu sé tou sa kè mouin bézouin.</strong> </p>\n<p><strong>Gran Kréatè a té vi-n sové mouin;</strong> </p>\n<p><strong>Mouin konnin Bon-Dieu nan Jézu-Kri.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té umilié-l pou nou kapab rachté; </p>\n<p>Lè nou té nan fè noua, lè nou té san éspoua, </p>\n<p>Bon-Dieu nan gras li </p>\n<p>Té kité tout gloua li; </p>\n<p>Li té vi-n chaché é sové nan-m mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan gran bonté li, pou nou Kris té soufri; </p>\n<p>Sou fòm gnou non-m li vi-n pou montré plan salu; </p>\n<p>Sé gnou gran mistè… </p>\n<p>Sakrifis sou Kalvè; </p>\n<p>Kris sèl kon-n satisfè bézouin kè mouin. </p>\n','Min-gnou-bel-istoua:-Kris-te-kite-tout-gloua',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1605,'c29005e5-63e5-4d32-a628-5be1c314d045',0,0,'Mouin té fi-n pèdu',124,'124_CE_ht','1.\nMouin té fi-n pèdu\nMin Jézu té sové-m,\nSé gnou mèvéyé Sovè pou mouin;\nPéché-m té maré-m,\nMin Jézu délivré-m,\nSé gnou mèvéyé Sovè pou mouin.\n\nKè:\nLi sé gnou mèvéyé Sovè pou mouin,\nSé gnou mèvéyé Sovè pou mouin,\nMouin té fi-n pèdu, min Jézu té sové-m,\nSé gnou mèvéyé Sovè pou mouin.\n\n\n2.\nLi gnou zanmi vré,\nSi pasian é trankil,\nSé gnou mèvéyé Sovè pou mouin;\nTout bagay m-bézouin,\nMouin toujou jouin-n nan Li,\nSé gnou mèvéyé Sovè pou mouin.\n\n\n3.\nJézu konsolé-m,\nÉ Li ankourajé-m\nSé gnou mèvéyé Sovè pou mouin;\nLi padonnin mouin,\nLi suiyé dlo nan jé-m,\nSé gnou mèvéyé Sovè pou mouin.\n\n\n4.\nLamou Jézu vi-n\nPi présié tou lé jou,\nSé gnou mèvéyé Sovè pou mouin;\nLè-m rété fidèl,\nGras Li dous toujou,\nSé gnou mèvéyé Sovè pou mouin.','<p><strong>1.</strong> </p>\n<p>Mouin té fi-n pèdu </p>\n<p>Min Jézu té sové-m, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin; </p>\n<p>Péché-m té maré-m, </p>\n<p>Min Jézu délivré-m, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li sé gnou mèvéyé Sovè pou mouin,</strong> </p>\n<p><strong>Sé gnou mèvéyé Sovè pou mouin,</strong> </p>\n<p><strong>Mouin té fi-n pèdu, min Jézu té sové-m,</strong> </p>\n<p><strong>Sé gnou mèvéyé Sovè pou mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li gnou zanmi vré, </p>\n<p>Si pasian é trankil, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin; </p>\n<p>Tout bagay m-bézouin, </p>\n<p>Mouin toujou jouin-n nan Li, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu konsolé-m, </p>\n<p>É Li ankourajé-m </p>\n<p>Sé gnou mèvéyé Sovè pou mouin; </p>\n<p>Li padonnin mouin, </p>\n<p>Li suiyé dlo nan jé-m, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lamou Jézu vi-n </p>\n<p>Pi présié tou lé jou, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin; </p>\n<p>Lè-m rété fidèl, </p>\n<p>Gras Li dous toujou, </p>\n<p>Sé gnou mèvéyé Sovè pou mouin. </p>\n','Mouin-te-fi-n-pedu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1606,'adf06b9c-18c6-48cb-9d9d-389b245254b9',0,0,'Kouté Jézu apé rélé',130,'130_CE_ht','1.\nKouté Jézu apé rélé,\nLi kon-n mouton-l yo an danjé,\nLi palé yo avèk amou,\nLi gin éspoua jouin-n yo gnou jou.\n\nKè:\nChaché yo, chaché yo!\nÈské ou konnin pri gnou nan-m?\nChaché yo, chaché yo!\nKris mandé pou nou tout ansan-m.\n\n\n2.\nAnba soufrans, anba péché\nMouton yo santi y-antravé;\nJézu mandé nou édé Li\nDélivré gran avèk piti!\n\n\n3.\nEské Kris ka konté sou nou?\nÈské nou vlé sèvi Li tou\nPou rakonté tout moun pèdu\nGin délivrans la nan Jézu!','<p><strong>1.</strong> </p>\n<p>Kouté Jézu apé rélé, </p>\n<p>Li kon-n mouton-l yo an danjé, </p>\n<p>Li palé yo avèk amou, </p>\n<p>Li gin éspoua jouin-n yo gnou jou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chaché yo, chaché yo!</strong> </p>\n<p><strong>Èské ou konnin pri gnou nan-m?</strong> </p>\n<p><strong>Chaché yo, chaché yo!</strong> </p>\n<p><strong>Kris mandé pou nou tout ansan-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Anba soufrans, anba péché </p>\n<p>Mouton yo santi y-antravé; </p>\n<p>Jézu mandé nou édé Li </p>\n<p>Délivré gran avèk piti! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Eské Kris ka konté sou nou? </p>\n<p>Èské nou vlé sèvi Li tou </p>\n<p>Pou rakonté tout moun pèdu </p>\n<p>Gin délivrans la nan Jézu! </p>\n','Koute-Jezu-ape-rele',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1607,'abe67343-31dd-48c8-80eb-7d24c64f0f14',0,0,'Ti réyon bèl solèy la',135,'135_CE_ht','1.\nTi réyon bèl solèy la,\nKi moun fè ou kléré kon sa?\nSé Bon Papa nou.  (2 foua)\nTi zouazo ak bèl koulè,\nKi moun fè ou chanté tout lè?\nSé Bon Papa nou.  (2 foua)\n\nKè:\nSé Bon-Dieu nan sièl ki Papa nou,\nSé Li ki fè nou – Li plin lamou.\n\n\n2.\nBèl ti flè kap réjoui nou,\nSé ki moun ki konnin fè ou?\nSé Bon Papa nou.  (2 foua)\nBèl ti riviè kap koulé,\nKi moun fè ou, èské ou konnin?\nSé bon Papa nou.  (2 foua)\n\n\n3.\nTi moun ak figu briyé,\nKi moun fè kè ou si léjé?\nSé Bon Papa nou.  (2 foua)\nKi moun montré ou chanté\nKou ti zouazo ki toujou gé?\nSé Bon Papa nou.  (2 foua)','<p><strong>1.</strong> </p>\n<p>Ti réyon bèl solèy la, </p>\n<p>Ki moun fè ou kléré kon sa? </p>\n<p>Sé Bon Papa nou.  (2 foua) </p>\n<p>Ti zouazo ak bèl koulè, </p>\n<p>Ki moun fè ou chanté tout lè? </p>\n<p>Sé Bon Papa nou.  (2 foua) </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé Bon-Dieu nan sièl ki Papa nou,</strong> </p>\n<p><strong>Sé Li ki fè nou – Li plin lamou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bèl ti flè kap réjoui nou, </p>\n<p>Sé ki moun ki konnin fè ou? </p>\n<p>Sé Bon Papa nou.  (2 foua) </p>\n<p>Bèl ti riviè kap koulé, </p>\n<p>Ki moun fè ou, èské ou konnin? </p>\n<p>Sé bon Papa nou.  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ti moun ak figu briyé, </p>\n<p>Ki moun fè kè ou si léjé? </p>\n<p>Sé Bon Papa nou.  (2 foua) </p>\n<p>Ki moun montré ou chanté </p>\n<p>Kou ti zouazo ki toujou gé? </p>\n<p>Sé Bon Papa nou.  (2 foua) </p>\n','Ti-reyon-bel-soley-la',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1608,'44231834-5606-4aff-9137-9668ec89e8be',0,0,'Bon-Dieu damou té rinmin',6,'6_MJ_ht','1.\nBon-Dieu damou té rinmin\nTout péchè yo anpil\nÉ Li té bay sèl pitit Li pou sové yo.\nSé sou la koua\nJézu té mouri gnou jou\nÉ Li té vèsé san-ni la pou nou.\n\nKè:\nO alélouya, O gloua a Jézu!\nPaské mouin sové\nNan gran gras Bon-Dieu.\nLi té pran péché-m,\nLi ban mouin la vi,\nKoun yé-a m-sové pou létènité.\n\n2.\nM-té kon-n maché nan péché\nRouj mouin yo san frin\nMin lumiè Bon-Dieu\nTé ékléré mouin-min’m tou.\nSint-Éspri Li té\nFè-m ouè jan-m té pèdu\nÉ sa-m doué fè pou kité rout lanfè.\n\n3.\nTout gras Bon-Dieu gingnin\nLi pa ka jan-m fini\nLi gin kont lamou\nKi kap sové nou-min’m tou\nVi-n ouvri kè ou pou aksépté Jézu\nÉ ou va réjoui nan sièl la ak nou.','<p><strong>1.</strong> </p>\n<p>Bon-Dieu damou té rinmin </p>\n<p>Tout péchè yo anpil </p>\n<p>É Li té bay sèl pitit Li pou sové yo. </p>\n<p>Sé sou la koua </p>\n<p>Jézu té mouri gnou jou </p>\n<p>É Li té vèsé san-ni la pou nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O alélouya, O gloua a Jézu!</strong> </p>\n<p><strong>Paské mouin sové</strong> </p>\n<p><strong>Nan gran gras Bon-Dieu.</strong> </p>\n<p><strong>Li té pran péché-m,</strong> </p>\n<p><strong>Li ban mouin la vi,</strong> </p>\n<p><strong>Koun yé-a m-sové pou létènité.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-té kon-n maché nan péché </p>\n<p>Rouj mouin yo san frin </p>\n<p>Min lumiè Bon-Dieu </p>\n<p>Té ékléré mouin-min’m tou. </p>\n<p>Sint-Éspri Li té </p>\n<p>Fè-m ouè jan-m té pèdu </p>\n<p>É sa-m doué fè pou kité rout lanfè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout gras Bon-Dieu gingnin </p>\n<p>Li pa ka jan-m fini </p>\n<p>Li gin kont lamou </p>\n<p>Ki kap sové nou-min’m tou </p>\n<p>Vi-n ouvri kè ou pou aksépté Jézu </p>\n<p>É ou va réjoui nan sièl la ak nou. </p>\n','Bon-Dieu-damou-te-rinmin',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1609,'2bfb4add-24b8-472d-84ff-8b3b0e7538a5',0,0,'Nan Bèl Paturaj Bon-Dieu',25,'25_MJ_ht','1.\nNan bèl paturaj Bon-Dieu,\nKoté dlo fré koulé,\nDousman a-souè m-ap maché ak Bèjé-m\nTout troupo paturaj\nLi apé répozé la,\nMouin sé mouton-L.\n\nKè:\nBèl dlo fré  (nan valé-a)\nPaturaj  (sou montagn la)\nLè-l fè noua (oui lé soua)\nM-ap maché ak Ségnè-m.\nN-ap désan-n  (nan valé-a)\nPa-za-pa  (pa-za-pa)\nAk Bèjé mouin.','<p><strong>1.</strong> </p>\n<p>Nan bèl paturaj Bon-Dieu, </p>\n<p>Koté dlo fré koulé, </p>\n<p>Dousman a-souè m-ap maché ak Bèjé-m </p>\n<p>Tout troupo paturaj </p>\n<p>Li apé répozé la, </p>\n<p>Mouin sé mouton-L. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bèl dlo fré  (nan valé-a)</strong> </p>\n<p><strong>Paturaj  (sou montagn la)</strong> </p>\n<p><strong>Lè-l fè noua (oui lé soua)</strong> </p>\n<p><strong>M-ap maché ak Ségnè-m.</strong> </p>\n<p><strong>N-ap désan-n  (nan valé-a)</strong> </p>\n<p><strong>Pa-za-pa  (pa-za-pa)</strong> </p>\n<p><strong>Ak Bèjé mouin.</strong> </p>\n','Nan-Bel-Paturaj-Bon-Dieu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1610,'92b59d4c-2569-4ea6-86aa-7ba1d6b18068',0,0,'Kè Mwen Si Kontan Ké Tas Mwen Débodé',44,'44_MJ_ht','1.\nKè mouin si kontan ké tas mouin\nJoua sa a ki transpòté-m   (débòdé,\nMouin pa-t jan-m konnin;\nLi vi-ni nan kè mouin\nDépi mouin sové,\nKa mouin té fèt dé nouvo.\n\nKè:\nM-a chanté, rakonté,\nMouin vlé tout moun tandé,\nMouin vlé yo konnin,\nJoua sa a ki nan kè mouin\nKi fè-l débòdé,\nKa mouin té fèt dé nouvo.\n\n2.\nMouin konnin mouin rachté,\nLéspri Sin ap di-m\nKé Jézu té sové-m,\nLi lavé tout vi-m.\nM-ap réjoui, m-ap viv nan Jézu ki zanmi-m.\nKa mouin té fét dé nouvo.\n\n3.\nMouin rinmin chè Sovè-m,\nTravay L m-ap fè;\nMouin rinmin pèp Bon-Dieu,\nÉ moun ki péchè;\nMouin rinmin koun-yé-a\nSuiv chémin Kalvè,\nKa mouin té fèt dé nouvo.','<p><strong>1.</strong> </p>\n<p>Kè mouin si kontan ké tas mouin </p>\n<p>Joua sa a ki transpòté-m   (débòdé, </p>\n<p>Mouin pa-t jan-m konnin; </p>\n<p>Li vi-ni nan kè mouin </p>\n<p>Dépi mouin sové, </p>\n<p>Ka mouin té fèt dé nouvo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-a chanté, rakonté,</strong> </p>\n<p><strong>Mouin vlé tout moun tandé,</strong> </p>\n<p><strong>Mouin vlé yo konnin,</strong> </p>\n<p><strong>Joua sa a ki nan kè mouin</strong> </p>\n<p><strong>Ki fè-l débòdé,</strong> </p>\n<p><strong>Ka mouin té fèt dé nouvo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin konnin mouin rachté, </p>\n<p>Léspri Sin ap di-m </p>\n<p>Ké Jézu té sové-m, </p>\n<p>Li lavé tout vi-m. </p>\n<p>M-ap réjoui, m-ap viv nan Jézu ki zanmi-m. </p>\n<p>Ka mouin té fét dé nouvo. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin rinmin chè Sovè-m, </p>\n<p>Travay L m-ap fè; </p>\n<p>Mouin rinmin pèp Bon-Dieu, </p>\n<p>É moun ki péchè; </p>\n<p>Mouin rinmin koun-yé-a </p>\n<p>Suiv chémin Kalvè, </p>\n<p>Ka mouin té fèt dé nouvo. </p>\n','Ke-Mwen-Si-Kontan-Ke-Tas-Mwen-Debode',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1611,'4ae0d0c2-9f23-425f-ba2e-9bff498148e6',0,0,'Ak Kris Nan Bak Mwen',75,'75_MJ_ht','1.\nAk Kris nan bak mouin\nMouin  [pa pè tanpèt la (3 foua)]\nAk Kris nan bak mouin\nMouin pa pè tanpèt-la\nLè m-ap vouayajé la kay\nVouayajé la kay  (2 foua)\nAk Kris nan bak mouin,\nMouin pa pè tanpèt-la\nLè m-ap vouayajé la kay.','<p><strong>1.</strong> </p>\n<p>Ak Kris nan bak mouin </p>\n<p>Mouin  [pa pè tanpèt la (3 foua)] </p>\n<p>Ak Kris nan bak mouin </p>\n<p>Mouin pa pè tanpèt-la </p>\n<p>Lè m-ap vouayajé la kay </p>\n<p>Vouayajé la kay  (2 foua) </p>\n<p>Ak Kris nan bak mouin, </p>\n<p>Mouin pa pè tanpèt-la </p>\n<p>Lè m-ap vouayajé la kay. </p>\n','Ak-Kris-Nan-Bak-Mwen',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1612,'8915e9cc-4183-4b32-9446-9b76d62088ff',0,0,'Es-tu prêt?',9,'9_VR_fr','1.	\nBientôt le Seigneur va venir, Es-tu prêt?\nBientôt le ciel Il veut t\'ouvrir, Es-tu prêt?\n\nChoeur\nEs-tu prêt?  Es-tu prêt?\nEs-tu prêt pour l\'éternité?\nEs-tu prêt? Es-tu prêt?\nLe Sauveur t\'a-t-il racheté?\n\n2.	\nBientôt l\'appel retentira, Es-tu prêt?\nBientôt la mort te surprendra, Es-tu prêt?\n\n3.	\nBientôt Jésus va te juger, Es-tu prêt?\nOh! Devra-t-il te condamner, Es-tu prêt? \n\n4.	\nBientôt dans le ciel élevé, Es-tu prêt?\nTous les élus seront sauvés, Es-tu prêt?','<p><strong>1.</strong> </p>\n<p>Bientôt le Seigneur va venir, Es-tu prêt? </p>\n<p>Bientôt le ciel Il veut t&#39;ouvrir, Es-tu prêt? </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Es-tu prêt?  Es-tu prêt?</strong> </p>\n<p><strong>Es-tu prêt pour l&#39;éternité?</strong> </p>\n<p><strong>Es-tu prêt? Es-tu prêt?</strong> </p>\n<p><strong>Le Sauveur t&#39;a-t-il racheté?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bientôt l&#39;appel retentira, Es-tu prêt? </p>\n<p>Bientôt la mort te surprendra, Es-tu prêt? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientôt Jésus va te juger, Es-tu prêt? </p>\n<p>Oh! Devra-t-il te condamner, Es-tu prêt? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bientôt dans le ciel élevé, Es-tu prêt? </p>\n<p>Tous les élus seront sauvés, Es-tu prêt? </p>\n','Es-tu-pret',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1613,'a531ccfb-ea1f-42dc-a021-c3a48642941f',0,0,'San pa Jézu gin gran vale',2,'2_CE_ht','1.\nSan pa Jézu gin gran valè,\nLi ban nou padon;\nJézu vlé sové tout péchè,\nDélivré-l tout bon.\n\nKè\nSan an koulé pou tout péchè,\nRébèl kont Bon-Dieu;\nSan pa Jézu gin gran valé,\nSé sa ki rachté-m.\n\n\n2.\nEské chay péché ou trè lou,\nRouj kon kramouazi?\nJézu kab nétouayé kè ou,\nFè ou pu kou li.\n\n\n3.\nKoun yé a nou kapab antré\nNan prézans Bon-Dieu,\nAsuré ké san pa Jézu\nOuété tout péché.\n\n\n4.\nAk san an nou kapab brizé\nTout puisans Satan;\nAk san an nou kapab trouvé\nLa pè pou tout tan.','<p><strong>1.</strong> </p>\n<p>San pa Jézu gin gran valè, </p>\n<p>Li ban nou padon; </p>\n<p>Jézu vlé sové tout péchè, </p>\n<p>Délivré-l tout bon. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>San an koulé pou tout péchè,</strong> </p>\n<p><strong>Rébèl kont Bon-Dieu;</strong> </p>\n<p><strong>San pa Jézu gin gran valé,</strong> </p>\n<p><strong>Sé sa ki rachté-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Eské chay péché ou trè lou, </p>\n<p>Rouj kon kramouazi? </p>\n<p>Jézu kab nétouayé kè ou, </p>\n<p>Fè ou pu kou li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Koun yé a nou kapab antré </p>\n<p>Nan prézans Bon-Dieu, </p>\n<p>Asuré ké san pa Jézu </p>\n<p>Ouété tout péché. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ak san an nou kapab brizé </p>\n<p>Tout puisans Satan; </p>\n<p>Ak san an nou kapab trouvé </p>\n<p>La pè pou tout tan. </p>\n','San-pa-Jezu-gin-gran-vale',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1614,'36aaca8b-9387-4ac9-afba-1de68fdd1d0f',0,0,'Lè Nap Maché Ak Dieu',13,'13_CE_ht','1.\nLè nap maché ak Dieu, ann lumiè Parol li,\nKi bèl gloua li voyé sou rout nou!\nLè nap fè volonté-l, li rété koté nou,\nÉ ak tout moun ki « Obéi-kouè ».\n\nKè:\n« Obéi-kouè », sé tou sa pou ou fè,\nPou ou kontan nan Jézu, sé pou «obéi-kouè».\n\n\n2.\nLonbraj ki va parèt, tout nuaj ka nan sièl la,\nSouri Jézu va fè-l disparèt;\nPa gnou dout, pa gnou pè, ni soupi, ni gnou kri,\nKa rété lè nou « Obéi-kouè »\n\n\n3.\nNou pap pòté gnou chaj pou lè zòt kap soufri,\nSan Jézu pa béni nou pou sa.\nNou pap subi gnou pèd, nou pap pòté gnou koua,\nSan-n pa béni, si-n «obéi-kouè».\n\n\n4.\nMin nou pa ka konnin tout mèvèy amou li\nAvan nou konsakré tout bagay;\nPaské bi-infè li yo é la joua li ban nou,\nSé pou moun ki vlé «obéi-kouè».\n\n\n5.\nKon sa nan prézans li, na chita nan pié li,\nOu nou va maché nan rout avè-l.\nSa li di, nou va fè.  Nou pral koté li vlé;\nSan pè angnin, na «obéi-kouè».','<p><strong>1.</strong> </p>\n<p>Lè nap maché ak Dieu, ann lumiè Parol li, </p>\n<p>Ki bèl gloua li voyé sou rout nou! </p>\n<p>Lè nap fè volonté-l, li rété koté nou, </p>\n<p>É ak tout moun ki « Obéi-kouè ». </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>« Obéi-kouè », sé tou sa pou ou fè,</strong> </p>\n<p><strong>Pou ou kontan nan Jézu, sé pou «obéi-kouè».</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lonbraj ki va parèt, tout nuaj ka nan sièl la, </p>\n<p>Souri Jézu va fè-l disparèt; </p>\n<p>Pa gnou dout, pa gnou pè, ni soupi, ni gnou kri, </p>\n<p>Ka rété lè nou « Obéi-kouè » </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nou pap pòté gnou chaj pou lè zòt kap soufri, </p>\n<p>San Jézu pa béni nou pou sa. </p>\n<p>Nou pap subi gnou pèd, nou pap pòté gnou koua, </p>\n<p>San-n pa béni, si-n «obéi-kouè». </p>\n<p><br/> <strong>4.</strong></p>\n<p>Min nou pa ka konnin tout mèvèy amou li </p>\n<p>Avan nou konsakré tout bagay; </p>\n<p>Paské bi-infè li yo é la joua li ban nou, </p>\n<p>Sé pou moun ki vlé «obéi-kouè». </p>\n<p><br/> <strong>5.</strong></p>\n<p>Kon sa nan prézans li, na chita nan pié li, </p>\n<p>Ou nou va maché nan rout avè-l. </p>\n<p>Sa li di, nou va fè.  Nou pral koté li vlé; </p>\n<p>San pè angnin, na «obéi-kouè». </p>\n','Le-Nap-Mache-Ak-Dieu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1615,'f7488958-4ac3-434a-88f0-94b951c98eb0',0,0,'Ou Pa Doue Pe Angnin',16,'16_CE_ht','1.\nOu pa doué pè angnin;\nMouin toujou ak ou:\nMin promès la, krétiin,\nKi soutni kè nou.\nRout sou té a mèt noua,\nSa pap fè mouin pè;\nNan-m mouin plin ak éspoua,\nM-ap maché ak bon Sovè-m.\n\nKè:\nMouin pa janmin sèl;  (2 foua)\nJézu, Sovè-m, ap véyé mouin,\nLi pa jan-m kité mouin sèl.\nMouin pa janmin sèl;  (2 foua)\nJézu, Sovè-m, ap véyé mouin,\nMouin pa jan-m rété tou sèl.\n\n\n2.\nSolèy la kléré bi-in,\nLè bèl tan sèlman;\nMin Jézu, lumiè mouin,\nLi kléré tout tan.\nLè mouin pa ouè klèté\nSolèy ki si bèl,\nNan ninpòt ki koté\nJézu parèt – mouin ouè sièl.','<p><strong>1.</strong> </p>\n<p>Ou pa doué pè angnin; </p>\n<p>Mouin toujou ak ou: </p>\n<p>Min promès la, krétiin, </p>\n<p>Ki soutni kè nou. </p>\n<p>Rout sou té a mèt noua, </p>\n<p>Sa pap fè mouin pè; </p>\n<p>Nan-m mouin plin ak éspoua, </p>\n<p>M-ap maché ak bon Sovè-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin pa janmin sèl;  (2 foua)</strong> </p>\n<p><strong>Jézu, Sovè-m, ap véyé mouin,</strong> </p>\n<p><strong>Li pa jan-m kité mouin sèl.</strong> </p>\n<p><strong>Mouin pa janmin sèl;  (2 foua)</strong> </p>\n<p><strong>Jézu, Sovè-m, ap véyé mouin,</strong> </p>\n<p><strong>Mouin pa jan-m rété tou sèl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Solèy la kléré bi-in, </p>\n<p>Lè bèl tan sèlman; </p>\n<p>Min Jézu, lumiè mouin, </p>\n<p>Li kléré tout tan. </p>\n<p>Lè mouin pa ouè klèté </p>\n<p>Solèy ki si bèl, </p>\n<p>Nan ninpòt ki koté </p>\n<p>Jézu parèt – mouin ouè sièl. </p>\n','Ou-Pa-Doue-Pe-Angnin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1616,'9c779314-21bc-4237-8ff8-a41919b1ac67',0,0,'Mwenn byen kontan ke',17,'17_CE_ht','1.\nMouin bi-in kontan ké Papa mouin nan sièl,\nPalé nan liv li, pou di-m li rinmin-m.\nNan Parol li, m-jouin’n anpil bèl bagay;\nSa ki pi bèl, sè ké Jézu rinmin-m.\n\nKè:\nA la m-kontan ouè Jézu rinmin-m,\nJézu rinmin-m, Jézu rinmin-m,\nA la m-kontan ouè Jézu rinmin-m,\nJézu rinmin-m tout tan.\n\n\n2.\nMin-m si mouin blié-l, pou-m pèdu chémin,\nNinpòt ki koté m-alé, li rinmin-m.\nMa tounin kay li, kaché-m nan bra-l yo,\nLè ma sonjé ki jan Jézu rinmin-m.\n\n\n3.\nJézu rinmin-m é m-konnin m-rinmin-l tou,\nSé amou li ki fè-l vi-n pou rachté-m.\nSé amou li ki té fè-l mouri tou;\nDonk mouin asuré ké Jézu rinmin-m.\n\n\n4.\nSi moun ta mandé-m kouman mouin konn sa,\nGloua a Jézu, mouin konnin sa trè bi-in.\nSint-Espri Bon-Dieu palé nan kè mouin\nPou témouagné ké Jézu rinmin mouin.\n\n\n5.\nLè-m rivé nan sièl la, nan bonté li;\nSi sé gnou sèl chan mouin kapab chanté,\nMin ki chan mouin pou tout létènité:\nJézu rinmin-m, sé bagay mèvéyé.','<p><strong>1.</strong> </p>\n<p>Mouin bi-in kontan ké Papa mouin nan sièl, </p>\n<p>Palé nan liv li, pou di-m li rinmin-m. </p>\n<p>Nan Parol li, m-jouin’n anpil bèl bagay; </p>\n<p>Sa ki pi bèl, sè ké Jézu rinmin-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la m-kontan ouè Jézu rinmin-m,</strong> </p>\n<p><strong>Jézu rinmin-m, Jézu rinmin-m,</strong> </p>\n<p><strong>A la m-kontan ouè Jézu rinmin-m,</strong> </p>\n<p><strong>Jézu rinmin-m tout tan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m si mouin blié-l, pou-m pèdu chémin, </p>\n<p>Ninpòt ki koté m-alé, li rinmin-m. </p>\n<p>Ma tounin kay li, kaché-m nan bra-l yo, </p>\n<p>Lè ma sonjé ki jan Jézu rinmin-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu rinmin-m é m-konnin m-rinmin-l tou, </p>\n<p>Sé amou li ki fè-l vi-n pou rachté-m. </p>\n<p>Sé amou li ki té fè-l mouri tou; </p>\n<p>Donk mouin asuré ké Jézu rinmin-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si moun ta mandé-m kouman mouin konn sa, </p>\n<p>Gloua a Jézu, mouin konnin sa trè bi-in. </p>\n<p>Sint-Espri Bon-Dieu palé nan kè mouin </p>\n<p>Pou témouagné ké Jézu rinmin mouin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Lè-m rivé nan sièl la, nan bonté li; </p>\n<p>Si sé gnou sèl chan mouin kapab chanté, </p>\n<p>Min ki chan mouin pou tout létènité: </p>\n<p>Jézu rinmin-m, sé bagay mèvéyé. </p>\n','Mwenn-byen-kontan-ke',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1617,'b552cbd8-697e-4d14-a40a-06ee9df62cf0',0,0,'Esk Ou Ta Vlé Viv Pou Jézu Nét Epi Toujou?',34,'34_CE_ht','1.\nEsk-ou ta vlé viv pou Jézu nèt épi toujou?\nEsk-ou vlé maché avèk li tou lè jou?\nEsk-ou vlé li pòté chay ou, soutni kè ou tou?\nKité-l fè ou kon li vlé.\n\nKè:\nPuisans li kab fè ou sa ou doué yé,\nSan-l kab nétouayé kè ou kon li yé,\nAmou li va plin na-m ou, si ou kouè.\nPito kité-l kondui ou kon li vlé.\n\n\n2.\nEsk-ou vlé li fè ou lib pou suiv li kout ké kout?\nEsk-ou vlé kouraj pou fè nonpòt ki rout?\nEsk-ou vlé délivré ou nan tout pièj, tout dout?\nKité-l fè ou kon li vlé.\n\n\n3.\nEsk-ou ou chaché gnou répo pou nan-m ou nan la pè?\nEsk-ou vlé Kris la avèk ou nan léprèv?\nEsk-ou vlé obéi li é pòté foui bon zèv?\nKité-l fè ou kon li vlé.','<p><strong>1.</strong> </p>\n<p>Esk-ou ta vlé viv pou Jézu nèt épi toujou? </p>\n<p>Esk-ou vlé maché avèk li tou lè jou? </p>\n<p>Esk-ou vlé li pòté chay ou, soutni kè ou tou? </p>\n<p>Kité-l fè ou kon li vlé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Puisans li kab fè ou sa ou doué yé,</strong> </p>\n<p><strong>San-l kab nétouayé kè ou kon li yé,</strong> </p>\n<p><strong>Amou li va plin na-m ou, si ou kouè.</strong> </p>\n<p><strong>Pito kité-l kondui ou kon li vlé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Esk-ou vlé li fè ou lib pou suiv li kout ké kout? </p>\n<p>Esk-ou vlé kouraj pou fè nonpòt ki rout? </p>\n<p>Esk-ou vlé délivré ou nan tout pièj, tout dout? </p>\n<p>Kité-l fè ou kon li vlé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Esk-ou ou chaché gnou répo pou nan-m ou nan la pè? </p>\n<p>Esk-ou vlé Kris la avèk ou nan léprèv? </p>\n<p>Esk-ou vlé obéi li é pòté foui bon zèv? </p>\n<p>Kité-l fè ou kon li vlé. </p>\n','Esk-Ou-Ta-Vle-Viv-Pou-Jezu-Net-Epi-Toujou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1618,'31bc03d3-4549-4377-b874-4b1d1eee2e5c',0,0,'Bèje mwen, se gnou Rwa damou',36,'36_CE_ht','1.\nBèjé mouin, sé gnou Roua damou,\nBonté li pap jan-m fini.\nSi sé li-min’m ki Sovè mouin,\nMouin pap jan-m manké angnin.\n\n\n2.\nKoté dlo a koulé pi fré,\nSé la li va kondui mouin,\nÉ sé koté zèb yo pi bèl\nLi mété paturaj mouin.\n\n\n3.\nMouin té fou, lè mouin té kité-l;\nMin, li-min’m alé chaché-m,\nÉ sou zépòl li, li pòté-m\nBi-in kontan, ak li m-tounin.\n\n\n4.\nMin-m si mouin ouè lan mò an fas,\nMouin papé pè gnou ti kras.\nGran baton ou ap protéjé-m,\nKoua ou dévan ap gidé-m.\n\n\n5.\nOu ranjé gnou supèb kouvè\nPou mouin, dévan tout lènmi-m,\nOu manjé ak mouin, Ou ban-m bouè\nDlo ké sièl la sèl gingnin.\n\n\n6.\nNinpò ki sa ki kab rivé,\nJuska la fin ma suiv ou.\nÉpi nan sièl la ma chanté\nGras ou ki pa jan-m fini.','<p><strong>1.</strong> </p>\n<p>Bèjé mouin, sé gnou Roua damou, </p>\n<p>Bonté li pap jan-m fini. </p>\n<p>Si sé li-min’m ki Sovè mouin, </p>\n<p>Mouin pap jan-m manké angnin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Koté dlo a koulé pi fré, </p>\n<p>Sé la li va kondui mouin, </p>\n<p>É sé koté zèb yo pi bèl </p>\n<p>Li mété paturaj mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin té fou, lè mouin té kité-l; </p>\n<p>Min, li-min’m alé chaché-m, </p>\n<p>É sou zépòl li, li pòté-m </p>\n<p>Bi-in kontan, ak li m-tounin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Min-m si mouin ouè lan mò an fas, </p>\n<p>Mouin papé pè gnou ti kras. </p>\n<p>Gran baton ou ap protéjé-m, </p>\n<p>Koua ou dévan ap gidé-m. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ou ranjé gnou supèb kouvè </p>\n<p>Pou mouin, dévan tout lènmi-m, </p>\n<p>Ou manjé ak mouin, Ou ban-m bouè </p>\n<p>Dlo ké sièl la sèl gingnin. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Ninpò ki sa ki kab rivé, </p>\n<p>Juska la fin ma suiv ou. </p>\n<p>Épi nan sièl la ma chanté </p>\n<p>Gras ou ki pa jan-m fini. </p>\n','Beje-mwen-se-gnou-Rwa-damou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1619,'5e158f08-2f87-4421-90a5-2cb8a4e28f6d',0,0,'Moun Yo Ki Obéi',39,'39_CE_ht','1.\nMoun yo ki obéi,\nÉ kap suiv Jézu-Kri,\nYo kontan é trézò yo an ro.\nLé zòt yo pa konnin\nNi la joua ni la pè\nNan kè tout moun kap suiv Sovè yo.  (2 foua)\n\n\n2.\nPandan tout la jounnin,\nSé non Kris map chanté.\nMouin ta souété tout moun konnin Li.\nMouin sètin li rinmin-m;\nLi mouri pou sové-m\nAvèk tout moun kap vini jouin-n Li.  (2 foua)\n\n\n3.\nSé gnou favè divin,\nSé gnou gran joua san fin;\nJézu rété nan kè-m, mouin kon-n sa.\nMouin posédé Sovè-m.\nMouin konnin li béni-m\nKòm si tout amou Bon-Dieu té la.  (2 foua)','<p><strong>1.</strong> </p>\n<p>Moun yo ki obéi, </p>\n<p>É kap suiv Jézu-Kri, </p>\n<p>Yo kontan é trézò yo an ro. </p>\n<p>Lé zòt yo pa konnin </p>\n<p>Ni la joua ni la pè </p>\n<p>Nan kè tout moun kap suiv Sovè yo.  (2 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pandan tout la jounnin, </p>\n<p>Sé non Kris map chanté. </p>\n<p>Mouin ta souété tout moun konnin Li. </p>\n<p>Mouin sètin li rinmin-m; </p>\n<p>Li mouri pou sové-m </p>\n<p>Avèk tout moun kap vini jouin-n Li.  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé gnou favè divin, </p>\n<p>Sé gnou gran joua san fin; </p>\n<p>Jézu rété nan kè-m, mouin kon-n sa. </p>\n<p>Mouin posédé Sovè-m. </p>\n<p>Mouin konnin li béni-m </p>\n<p>Kòm si tout amou Bon-Dieu té la.  (2 foua) </p>\n','Moun-Yo-Ki-Obei',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1620,'0518fe37-ba8c-4f80-a8d5-784c25bd2861',0,0,'Ségnè Jézu, konvéti nou',60,'60_CE_ht','1.\nSégnè Jézu, konvèti nou,\nTravay nou pa angnin,\nSégnè Jézu, sé ak fòs Ou\nNou kab fè sa ki bi-in.\n\nKè:\nChanjé ak gras Bon-Dieu,\nTout gloua pou Ou, Roua mouin,\nPou-m fè volonté Ou, Ségnè,\nSé sèl bu mouin gingnin.\n\n\n2.\nKonsolé chagrin ak tristès\nNan chak kè ki chanjé.\nBan nou la joua nan la détrès,\nÉ lè nan-m nou troublé.\n\n\n3.\nOuété tout chut ak tout érè\nKaché nan la vi nou;\nNan non Ou, Ségnè, na vinkè.\nLouanj pou Ou toujou.','<p><strong>1.</strong> </p>\n<p>Ségnè Jézu, konvèti nou, </p>\n<p>Travay nou pa angnin, </p>\n<p>Ségnè Jézu, sé ak fòs Ou </p>\n<p>Nou kab fè sa ki bi-in. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chanjé ak gras Bon-Dieu,</strong> </p>\n<p><strong>Tout gloua pou Ou, Roua mouin,</strong> </p>\n<p><strong>Pou-m fè volonté Ou, Ségnè,</strong> </p>\n<p><strong>Sé sèl bu mouin gingnin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Konsolé chagrin ak tristès </p>\n<p>Nan chak kè ki chanjé. </p>\n<p>Ban nou la joua nan la détrès, </p>\n<p>É lè nan-m nou troublé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ouété tout chut ak tout érè </p>\n<p>Kaché nan la vi nou; </p>\n<p>Nan non Ou, Ségnè, na vinkè. </p>\n<p>Louanj pou Ou toujou. </p>\n','Segne-Jezu-konveti-nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1621,'504541de-6d5e-40de-a13e-9a74bfcc6cd2',0,0,'Pi bon zanmi mouin gingnin sé Jézu',86,'86_CE_ht','1.\nPi bon zanmi mouin gingnin sé Jézu,\nAmou-l si gran, si vré;\nJanmin Li abandonnin-m\nNan tou sa mapé fè.\nSouvan mouin fè-l la pin-n nan vi-m;\nPou mouin li bon Sovè;\nLè-m répanti, li padonnin-m;\nAlò mouin gin la pè.\n\nKè:\nPi bon zanmi mouin sou la tè, sé Jézu,\nPi bon zanmin mouin sou la tè, sé Jézu,\nLi lavé péché-m pou-l minnin-m nan sièl;\nAmou Jézu si bèl.\n\n\n2.\nPafoua sièl mouin plin ak nuaj,\nTout chémin mouin fè noua,\nFas li kaché ak lonbraj\nLè mouin pa gin la foua.\nNan lamou li prié pou mouin,\nKè mouin vi-n soulajé\nPaské li ouété tout chagrin;\nTout la pin-n mouin alé.\n\n\n3.\nMouin vlé chanté amou li,\nAmou Jézu pou mouin,\nBonté-l, fidélité li,\nPou gnou péchè kon mouin.\nKouman m-kapab rémèsié li\nPou amou-l anvè mouin?\nSé pou-m rinmin-l é sèvi li,\nSuiv li pré , janmin louin.','<p><strong>1.</strong> </p>\n<p>Pi bon zanmi mouin gingnin sé Jézu, </p>\n<p>Amou-l si gran, si vré; </p>\n<p>Janmin Li abandonnin-m </p>\n<p>Nan tou sa mapé fè. </p>\n<p>Souvan mouin fè-l la pin-n nan vi-m; </p>\n<p>Pou mouin li bon Sovè; </p>\n<p>Lè-m répanti, li padonnin-m; </p>\n<p>Alò mouin gin la pè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pi bon zanmi mouin sou la tè, sé Jézu,</strong> </p>\n<p><strong>Pi bon zanmin mouin sou la tè, sé Jézu,</strong> </p>\n<p><strong>Li lavé péché-m pou-l minnin-m nan sièl;</strong> </p>\n<p><strong>Amou Jézu si bèl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pafoua sièl mouin plin ak nuaj, </p>\n<p>Tout chémin mouin fè noua, </p>\n<p>Fas li kaché ak lonbraj </p>\n<p>Lè mouin pa gin la foua. </p>\n<p>Nan lamou li prié pou mouin, </p>\n<p>Kè mouin vi-n soulajé </p>\n<p>Paské li ouété tout chagrin; </p>\n<p>Tout la pin-n mouin alé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vlé chanté amou li, </p>\n<p>Amou Jézu pou mouin, </p>\n<p>Bonté-l, fidélité li, </p>\n<p>Pou gnou péchè kon mouin. </p>\n<p>Kouman m-kapab rémèsié li </p>\n<p>Pou amou-l anvè mouin? </p>\n<p>Sé pou-m rinmin-l é sèvi li, </p>\n<p>Suiv li pré , janmin louin. </p>\n','Pi-bon-zanmi-mouin-gingnin-se-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1622,'ff449f97-8f95-4f93-ba36-f43e4b4e3fbd',0,0,'Mapé maché sèl ak Jézu',93,'93_CE_ht','1.\nMapé maché sèl ak Jézu\nNan gnou komunion divin;\nLè mon-n pa kapab fè-m plézi,\nMouin pou Kris, é Kris pou mouin.\n\nKè:  Mouin té ouè li, mouin konnin li,\nLi fè mouin maché avè-l;\nÉ li fè-m kado prézans li,\nKi gran gloua, divin prézans li,\nKi gran joua, ouè-l fas a fas!\nMouin ba li la vi-m pou tout tan,\nLi té gingnin-m pa la gras.\n\n\n2.\nSou montagn la mouin té ouè li;\nKris, Konsolatè, Zanmi;\nLa gloua vizion sa va rété\nJouk tan la vi mouin fi-ni.\n\n\n3.\nNan tout doulè ak féblès mouin,\nMalgré kè-m té fi-n krazé,\nToujou mouin santi prézans li,\nLi té ouété-m nan péché.\n\n\n4.\nNan ténèb la, nan lonbraj la\nAk Sovè la mouin fè rout;\nÉ léson lap ansingnin mouin\nFè-m vlé suiv li kout ké kout.','<p><strong>1.</strong> </p>\n<p>Mapé maché sèl ak Jézu </p>\n<p>Nan gnou komunion divin; </p>\n<p>Lè mon-n pa kapab fè-m plézi, </p>\n<p>Mouin pou Kris, é Kris pou mouin. </p>\n<p>Kè:  Mouin té ouè li, mouin konnin li, </p>\n<p>Li fè mouin maché avè-l; </p>\n<p>É li fè-m kado prézans li, </p>\n<p>Ki gran gloua, divin prézans li, </p>\n<p>Ki gran joua, ouè-l fas a fas! </p>\n<p>Mouin ba li la vi-m pou tout tan, </p>\n<p>Li té gingnin-m pa la gras. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sou montagn la mouin té ouè li; </p>\n<p>Kris, Konsolatè, Zanmi; </p>\n<p>La gloua vizion sa va rété </p>\n<p>Jouk tan la vi mouin fi-ni. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan tout doulè ak féblès mouin, </p>\n<p>Malgré kè-m té fi-n krazé, </p>\n<p>Toujou mouin santi prézans li, </p>\n<p>Li té ouété-m nan péché. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan ténèb la, nan lonbraj la </p>\n<p>Ak Sovè la mouin fè rout; </p>\n<p>É léson lap ansingnin mouin </p>\n<p>Fè-m vlé suiv li kout ké kout. </p>\n','Mape-mache-sel-ak-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1623,'bb5b94f3-8c6b-48d2-83f5-79693ee8425f',0,0,'An nou travay pou lé Ségnè avan solèy va kouché',111,'111_CE_ht','1.\nAn nou travay pou lé Ségnè avan solèy va kouché\nPou a-nonsé bòn nouvèl li tou patou,\nÉ lè Jézu va tounin pou chaché tout fidèl li yo,\nLè lapèl va fèt nan sièl la, nou va la.\n\nKè:\nLè lapèl va fèt nan sièl la  (4 foua)\nNou va la.\n\n\n2.\nNou pa gin pou dékourajé malgré chémin difisil,\nÉ tristès pap fè nou maché tèt an ba.\nToutfoua napé tan-n lé Ségnè ki va parèt an lè a,\nLè lapèl va fèt nan sièl la nou va la.\n\n\n3.\nGin anpil mouton ki pèdu, ki égaré nan péché;\nYo pa min-m konnin nou gin gnou bon Bèjé.\nAn nou chaché yo pou Jézu, fè yo antré nan pak li,\nLè lapèl la fèt nan sièl la, yo doué la.','<p><strong>1.</strong> </p>\n<p>An nou travay pou lé Ségnè avan solèy va kouché </p>\n<p>Pou a-nonsé bòn nouvèl li tou patou, </p>\n<p>É lè Jézu va tounin pou chaché tout fidèl li yo, </p>\n<p>Lè lapèl va fèt nan sièl la, nou va la. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè lapèl va fèt nan sièl la  (4 foua)</strong> </p>\n<p><strong>Nou va la.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou pa gin pou dékourajé malgré chémin difisil, </p>\n<p>É tristès pap fè nou maché tèt an ba. </p>\n<p>Toutfoua napé tan-n lé Ségnè ki va parèt an lè a, </p>\n<p>Lè lapèl va fèt nan sièl la nou va la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin anpil mouton ki pèdu, ki égaré nan péché; </p>\n<p>Yo pa min-m konnin nou gin gnou bon Bèjé. </p>\n<p>An nou chaché yo pou Jézu, fè yo antré nan pak li, </p>\n<p>Lè lapèl la fèt nan sièl la, yo doué la. </p>\n','An-nou-travay-pou-le-Segne-avan-soley-va-kouche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1624,'a0ee63a1-c0e2-4c53-9a7c-17badca6833b',0,0,'Sou mòn Kalvè, Ségnè jézu',113,'113_CE_ht','1.\nSou mòn Kalvè, Ségnè jézu,\nJé mouin ouè la koua ou,\nPéchè sa a gin gnou dézi:\nSégnè, sèvi ak mouin.\nSégnè, sa rèd po moun péri\nTandis ké mouin fè kè mouin du.\nSégnè Jézu, mouin prié ou,\nSèvi ak mouin.\n\n\n2.\nSégnè Jézu, vè Bétani\nMouin ouè dèt mouin réglé.\nOu-min’m ki klouré é mouri\nMin ki résusité.\nJézu, ou gin démè nan sièl,\nParé pou tou sa yo li kouè-l.\nSégnè Jézu, nétouayé mouin,\nSèvi ak mouin.\n\n\n3.\nSégnè, lè mon-n ki plin péché\nAnpéché-m gin la foua,\nFè-m gras pou-m jouin-n nan-m égaré;\nPou-m séyé, ban-m gras sa.\nLè la vi-m fi-ni, ma pralé\nKontré ak nan-m ké m-té gingnin\nMaptannou di: «Ou té fè bi-in!»\nSèvi ak mouin.','<p><strong>1.</strong> </p>\n<p>Sou mòn Kalvè, Ségnè jézu, </p>\n<p>Jé mouin ouè la koua ou, </p>\n<p>Péchè sa a gin gnou dézi: </p>\n<p>Ségnè, sèvi ak mouin. </p>\n<p>Ségnè, sa rèd po moun péri </p>\n<p>Tandis ké mouin fè kè mouin du. </p>\n<p>Ségnè Jézu, mouin prié ou, </p>\n<p>Sèvi ak mouin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ségnè Jézu, vè Bétani </p>\n<p>Mouin ouè dèt mouin réglé. </p>\n<p>Ou-min’m ki klouré é mouri </p>\n<p>Min ki résusité. </p>\n<p>Jézu, ou gin démè nan sièl, </p>\n<p>Paré pou tou sa yo li kouè-l. </p>\n<p>Ségnè Jézu, nétouayé mouin, </p>\n<p>Sèvi ak mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ségnè, lè mon-n ki plin péché </p>\n<p>Anpéché-m gin la foua, </p>\n<p>Fè-m gras pou-m jouin-n nan-m égaré; </p>\n<p>Pou-m séyé, ban-m gras sa. </p>\n<p>Lè la vi-m fi-ni, ma pralé </p>\n<p>Kontré ak nan-m ké m-té gingnin </p>\n<p>Maptannou di: «Ou té fè bi-in!» </p>\n<p>Sèvi ak mouin. </p>\n','Sou-mon-Kalve-Segne-jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1625,'60521b94-f10c-4c2c-81a8-fafda9a4a183',0,0,'Ségnè Jézu ki Sovè nou',125,'125_CE_ht','1.\nSégnè Jézu ki Sovè nou,\nNinpòt ki ras nou yé!\nNou rékonnèt sé ou ki Mèt,\nSé ou ki modèl nou.\n\n\n2.\nNan présans ou, nou rékonnèt\nKi jan nou gingnin défo.\nOu rinmin nou, min an min-m tan\nOu rai péché nou.\n\n\n3.\nTout pansé yo ki nan tout kè\nPa kaché dévan ou,\nOu konnin tout, ou ouè tout klè,\nPa gin sékrè pou ou.\n\n\n4.\nMalgré nou fèb, pa bon du tou,\nOu kité-n sèvi ou.\nTout kado nou ofri ba ou,\nOu pa réfuzé yo.\n\n\n5.\nAngnin pa bon, lè ou pa la,\nSan ou tout travay vin.\nLonbraj koua ou pi bèl pou nou\nPasé lumiè solèy.\n\n\n6.\nOu sé zanmi-n, frè nou, Mèt nou,\nKi sa ou vlé pou-n fè?\nSé pa nan rit ni fòm spésial,\nSé sèlman pou suiv ou.\n\n\n7.\nNou pa ouè bi-in, ni tandé bi-in.\nNou pa kon-n prié bi-in.\nMin tout, nou rékonnèt nan ou\nLa vérité, la vi.','<p><strong>1.</strong> </p>\n<p>Ségnè Jézu ki Sovè nou, </p>\n<p>Ninpòt ki ras nou yé! </p>\n<p>Nou rékonnèt sé ou ki Mèt, </p>\n<p>Sé ou ki modèl nou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan présans ou, nou rékonnèt </p>\n<p>Ki jan nou gingnin défo. </p>\n<p>Ou rinmin nou, min an min-m tan </p>\n<p>Ou rai péché nou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout pansé yo ki nan tout kè </p>\n<p>Pa kaché dévan ou, </p>\n<p>Ou konnin tout, ou ouè tout klè, </p>\n<p>Pa gin sékrè pou ou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Malgré nou fèb, pa bon du tou, </p>\n<p>Ou kité-n sèvi ou. </p>\n<p>Tout kado nou ofri ba ou, </p>\n<p>Ou pa réfuzé yo. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Angnin pa bon, lè ou pa la, </p>\n<p>San ou tout travay vin. </p>\n<p>Lonbraj koua ou pi bèl pou nou </p>\n<p>Pasé lumiè solèy. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Ou sé zanmi-n, frè nou, Mèt nou, </p>\n<p>Ki sa ou vlé pou-n fè? </p>\n<p>Sé pa nan rit ni fòm spésial, </p>\n<p>Sé sèlman pou suiv ou. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Nou pa ouè bi-in, ni tandé bi-in. </p>\n<p>Nou pa kon-n prié bi-in. </p>\n<p>Min tout, nou rékonnèt nan ou </p>\n<p>La vérité, la vi. </p>\n','Segne-Jezu-ki-Sove-nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1626,'9c86c91e-6946-4603-9489-4620673d8974',0,0,'Ségnè, kondui mouin chak jou',128,'128_CE_ht','1.\nSégnè, kondui mouin chak jou\nKoté ou vlé pou-m alé;\nFè-m konnin volonté ou,\nMontré mouin sa-m gin pou fè.\n\n\n2.\nBan mouin kouraj nan danjé,\nMontré-m chaché sékou ou,\nFè-m kon-n ou kapab sové,\nGadé mouin nan amou ou.\n\n\n3.\nLè-m tanté pou fè lé mal,\nFè mouin rété fò é fèm,\nÉ lè mouin kanpé tou sèl,\nAvèk min ou protéjé-m.\n\n\n4.\nLè kè mouin ranpli la joua,\nÉdé mouin pou-m sonjé ou.\nSa ki fè mouin pi kontan\nSé amou ou gin pou mouin.\n\n\n5.\nLè travay mouin sanblé di\nÉdé-m gin pèsévérans.\nNan soufrans, nan maladi\nBan-m kouraj ak pasians.\n\n\n6.\nEdé-m fè tou sa ki bon,\nKòm pitit ou sou la tè.\nLè la vi mouin doué fini,\nKité-m al nan sièl ak ou.','<p><strong>1.</strong> </p>\n<p>Ségnè, kondui mouin chak jou </p>\n<p>Koté ou vlé pou-m alé; </p>\n<p>Fè-m konnin volonté ou, </p>\n<p>Montré mouin sa-m gin pou fè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ban mouin kouraj nan danjé, </p>\n<p>Montré-m chaché sékou ou, </p>\n<p>Fè-m kon-n ou kapab sové, </p>\n<p>Gadé mouin nan amou ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè-m tanté pou fè lé mal, </p>\n<p>Fè mouin rété fò é fèm, </p>\n<p>É lè mouin kanpé tou sèl, </p>\n<p>Avèk min ou protéjé-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè kè mouin ranpli la joua, </p>\n<p>Édé mouin pou-m sonjé ou. </p>\n<p>Sa ki fè mouin pi kontan </p>\n<p>Sé amou ou gin pou mouin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Lè travay mouin sanblé di </p>\n<p>Édé-m gin pèsévérans. </p>\n<p>Nan soufrans, nan maladi </p>\n<p>Ban-m kouraj ak pasians. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Edé-m fè tou sa ki bon, </p>\n<p>Kòm pitit ou sou la tè. </p>\n<p>Lè la vi mouin doué fini, </p>\n<p>Kité-m al nan sièl ak ou. </p>\n','Segne-kondui-mouin-chak-jou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1627,'b144cf5d-79ac-4322-894a-1037429e68c1',0,0,'Min Gnou Bèl Ti Nouvel',26,'26_MJ_ht','1.\nMin gnou bèl ti nouvèl\nKi sòt nan sièl\nPéchè Jézu rélé ou, lévé jé ou.\nOu mèt kouvri ak mizè,\nPéché mèt dégizé ou\nVi-n jouin-n Li!\n\n2.\nJézu pitit Bon-dieu\nMouri pou nou;\nLi montré-n amou Li\nSou bou Kalvè\nNan san ki kap lavé\nTout kè ki plin péché,\nVi-n mèt tout konfians ou;\nVi-n jouin-n Jézu!\n\n3.\nLè Jézu la vé ou\nAvèk san Li\nLi délivré ou nèt nan min satan,\nPéché pa gin puisans\nNan kè ou kòn avan\nLéspri sanktifié ou,\nVini jouin-n Li.\n\n4.\nVini koun yé-a min-m,\nPiga ou rété\nOu prèt pou délivré, si ou vini\nSi ou kouè nan Jézu-Kri,\nPéché ou ap éfasé\nÉ na chanté ansan-m.\nGloua a Jézu.','<p><strong>1.</strong> </p>\n<p>Min gnou bèl ti nouvèl </p>\n<p>Ki sòt nan sièl </p>\n<p>Péchè Jézu rélé ou, lévé jé ou. </p>\n<p>Ou mèt kouvri ak mizè, </p>\n<p>Péché mèt dégizé ou </p>\n<p>Vi-n jouin-n Li! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu pitit Bon-dieu </p>\n<p>Mouri pou nou; </p>\n<p>Li montré-n amou Li </p>\n<p>Sou bou Kalvè </p>\n<p>Nan san ki kap lavé </p>\n<p>Tout kè ki plin péché, </p>\n<p>Vi-n mèt tout konfians ou; </p>\n<p>Vi-n jouin-n Jézu! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè Jézu la vé ou </p>\n<p>Avèk san Li </p>\n<p>Li délivré ou nèt nan min satan, </p>\n<p>Péché pa gin puisans </p>\n<p>Nan kè ou kòn avan </p>\n<p>Léspri sanktifié ou, </p>\n<p>Vini jouin-n Li. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vini koun yé-a min-m, </p>\n<p>Piga ou rété </p>\n<p>Ou prèt pou délivré, si ou vini </p>\n<p>Si ou kouè nan Jézu-Kri, </p>\n<p>Péché ou ap éfasé </p>\n<p>É na chanté ansan-m. </p>\n<p>Gloua a Jézu. </p>\n','Min-Gnou-Bel-Ti-Nouvel',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1628,'8cccf632-c88c-474f-b8e0-971b3642511c',0,0,'Sou Yon Mon Bandonné',41,'41_MJ_ht','1.\nSou gnou mòn badonné,\nGnou vié koua té planté,\nKoté brigan yo gin pou mouri.\nMin sé sou min-m koua sa a,\nJézu mouri pou mouin,\nPou-m kapab patajé tout gloua Li.\n\nKè:\nYo té kloué Jézu sou la koua;\nÉ sé la Li té mouri pou mouin.\nM-apé viv pou gloua Li sou tè sa a,\nÉ gnou jou m-a va ouè Li nan sièl.\n\n2.\nPou tou sa-k té rivé,\nSou la koua nan jou sa a,\nM-a va toujou gin rékonnésans;\nPaské tout Jézu fè,\nLi té fè-l nan plas mouin,\nPou ban mouin la vi nan abondans.\n\n3.\nKon yo tué Jézu,\nKi té Pitit Bon-Dieu,\nYo pa vlé ouè mouin-min’m non plus.\nMin mouin pito suiv Li,\nNan tou sa k-a rivé,\nPasé tou sa-m ta jouin-n sou tè sa a.','<p><strong>1.</strong> </p>\n<p>Sou gnou mòn badonné, </p>\n<p>Gnou vié koua té planté, </p>\n<p>Koté brigan yo gin pou mouri. </p>\n<p>Min sé sou min-m koua sa a, </p>\n<p>Jézu mouri pou mouin, </p>\n<p>Pou-m kapab patajé tout gloua Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Yo té kloué Jézu sou la koua;</strong> </p>\n<p><strong>É sé la Li té mouri pou mouin.</strong> </p>\n<p><strong>M-apé viv pou gloua Li sou tè sa a,</strong> </p>\n<p><strong>É gnou jou m-a va ouè Li nan sièl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou tou sa-k té rivé, </p>\n<p>Sou la koua nan jou sa a, </p>\n<p>M-a va toujou gin rékonnésans; </p>\n<p>Paské tout Jézu fè, </p>\n<p>Li té fè-l nan plas mouin, </p>\n<p>Pou ban mouin la vi nan abondans. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kon yo tué Jézu, </p>\n<p>Ki té Pitit Bon-Dieu, </p>\n<p>Yo pa vlé ouè mouin-min’m non plus. </p>\n<p>Min mouin pito suiv Li, </p>\n<p>Nan tou sa k-a rivé, </p>\n<p>Pasé tou sa-m ta jouin-n sou tè sa a. </p>\n','Sou-Yon-Mon-Bandonne',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1629,'2713070d-7aaf-4dbf-81c1-0db276e94752',0,0,'De Canaan quand verrons-nous',44,'44_VR_fr','1.	\nDe Canaan quand verrons-nous\nLe céleste rivage\nVers le Jourdain entendez-vous\nChrist nous appelle tous.\nPrès de Lui doux partage\nA l\'abrit de l\'orage\nNous pourrons chanter à jamais\nLe cantique de paix.\n\nChoeur\nOh! Quel parfait bonheur\nQuel bonheur! Quel bonheur\nOh! Quel parfait bonheur\nAprès tant de labeur\nPour toujours réunie\nL\'Église en sa patrie\nEntonnera Alléluia\nGloire à Toi Jéhova.\n\n2.	\n«Vêtu» de blanc les rachetés\nDe Christ verront la gloire\nPar sa vertue ressuscité\nIls diront ses bontés\nCélébrant sa victoire\nSon oeuvre expiatoire\nAutour de son trône avec eux\nNous Lui rendrons nos voeux.\n\n3.	\nDu grand jour de l\'éternité\nQuand brillera l\'aurore\nTous consommés dans l\'unité\nEt dans la charité\nA Celui qu\'on adore\nNous redirons encore\nForce, empire et pouvoir.','<p><strong>1.</strong> </p>\n<p>De Canaan quand verrons-nous </p>\n<p>Le céleste rivage </p>\n<p>Vers le Jourdain entendez-vous </p>\n<p>Christ nous appelle tous. </p>\n<p>Près de Lui doux partage </p>\n<p>A l&#39;abrit de l&#39;orage </p>\n<p>Nous pourrons chanter à jamais </p>\n<p>Le cantique de paix. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Oh! Quel parfait bonheur</strong> </p>\n<p><strong>Quel bonheur! Quel bonheur</strong> </p>\n<p><strong>Oh! Quel parfait bonheur</strong> </p>\n<p><strong>Après tant de labeur</strong> </p>\n<p><strong>Pour toujours réunie</strong> </p>\n<p><strong>L&#39;Église en sa patrie</strong> </p>\n<p><strong>Entonnera Alléluia</strong> </p>\n<p><strong>Gloire à Toi Jéhova.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>«Vêtu» de blanc les rachetés </p>\n<p>De Christ verront la gloire </p>\n<p>Par sa vertue ressuscité </p>\n<p>Ils diront ses bontés </p>\n<p>Célébrant sa victoire </p>\n<p>Son oeuvre expiatoire </p>\n<p>Autour de son trône avec eux </p>\n<p>Nous Lui rendrons nos voeux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Du grand jour de l&#39;éternité </p>\n<p>Quand brillera l&#39;aurore </p>\n<p>Tous consommés dans l&#39;unité </p>\n<p>Et dans la charité </p>\n<p>A Celui qu&#39;on adore </p>\n<p>Nous redirons encore </p>\n<p>Force, empire et pouvoir. </p>\n','De-Canaan-quand-verrons-nous',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1630,'d74e44b9-f032-4b6f-91d1-88f968b9d29a',0,0,'Béni Kòd Ki Maré',21,'21_CE_ht','1.\nBéni kòd ki maré\nkè nou nan amou Kris,\nFratènité moun ki uni\nFè nou konpran-n sièl la.\n\n\n2.\nDévan trò-n Papa nou\nNou prié tout ansan-m,\nNou gin min-m krint ak min-m éspoua,\nNou vlé min-m délivrans.\n\n\n3.\nPatajé mizè nou,\nGnoun pòté pakèt lòt,\nLè moun soufri, krié ak yo,\nNou fè tou sa pou Kris.\n\n\n4.\nLè nou gnoun kité lòt,\nSa fè nou tout la pin-n.\nNou rété uni nan léspri,\nNou tout sé gnoun nan Kris.','<p><strong>1.</strong> </p>\n<p>Béni kòd ki maré </p>\n<p>kè nou nan amou Kris, </p>\n<p>Fratènité moun ki uni </p>\n<p>Fè nou konpran-n sièl la. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dévan trò-n Papa nou </p>\n<p>Nou prié tout ansan-m, </p>\n<p>Nou gin min-m krint ak min-m éspoua, </p>\n<p>Nou vlé min-m délivrans. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Patajé mizè nou, </p>\n<p>Gnoun pòté pakèt lòt, </p>\n<p>Lè moun soufri, krié ak yo, </p>\n<p>Nou fè tou sa pou Kris. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè nou gnoun kité lòt, </p>\n<p>Sa fè nou tout la pin-n. </p>\n<p>Nou rété uni nan léspri, </p>\n<p>Nou tout sé gnoun nan Kris. </p>\n','Beni-Kod-Ki-Mare',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1631,'a9b2ac24-20e6-4e2e-a676-fd9e49b87c6f',0,0,'Jézu se tout bagay pou mwen',24,'24_CE_ht','1.\nJézu sé tout bagay pou mouin;\nLa pè, la joua, la vi;\nSé li ki fòs mouin tou lè jou,\nMouin ta tonbé san li.\nLè m-santi’m tris, sé li m-chaché;\nSi-l pa té la, sa mouin ta fè?\nLè m-santi’m tris, li fè mouin gué;\nLi rinmin-m.\n\n\n2.\nJézu sé tout bagay pou mouin;\nNan tout éprèv chak jou.\nM-alé koté-l pou bi-infè,\nÉ li ban mouin yo toujou.\nLi voyé solèy ak la pli,\nLi ban nou rékolt avèk foui;\nSolèy, la pli, rékolt ak foui;\nLi rinmin-m.\n\n\n3.\nJézu sé tout bagay pou mouin;\nMouin va rété fidèl;\nLi si fidèl pou mouin toujou,\nMouin pa kapab nié-l.\nLè map suiv li, mouin pap pèdu;\nLi véyé mouin jou avèk nuit;\nMouin pou suiv li jou avèk nuit;\nLi rinmin-m.\n\n\n4.\nJézu sé tout bagay pou mouin;\nPa gin pi bon zanmi;\nMa mété pou létènité\nTout konfians mouin nan li.\nLé li rinmin-m, sé bèl la vi;\nLi bèl, li papé jan-m fini;\nVi étènèl! Joua étènèl!\nLi rinmin-m.','<p><strong>1.</strong> </p>\n<p>Jézu sé tout bagay pou mouin; </p>\n<p>La pè, la joua, la vi; </p>\n<p>Sé li ki fòs mouin tou lè jou, </p>\n<p>Mouin ta tonbé san li. </p>\n<p>Lè m-santi’m tris, sé li m-chaché; </p>\n<p>Si-l pa té la, sa mouin ta fè? </p>\n<p>Lè m-santi’m tris, li fè mouin gué; </p>\n<p>Li rinmin-m. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu sé tout bagay pou mouin; </p>\n<p>Nan tout éprèv chak jou. </p>\n<p>M-alé koté-l pou bi-infè, </p>\n<p>É li ban mouin yo toujou. </p>\n<p>Li voyé solèy ak la pli, </p>\n<p>Li ban nou rékolt avèk foui; </p>\n<p>Solèy, la pli, rékolt ak foui; </p>\n<p>Li rinmin-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu sé tout bagay pou mouin; </p>\n<p>Mouin va rété fidèl; </p>\n<p>Li si fidèl pou mouin toujou, </p>\n<p>Mouin pa kapab nié-l. </p>\n<p>Lè map suiv li, mouin pap pèdu; </p>\n<p>Li véyé mouin jou avèk nuit; </p>\n<p>Mouin pou suiv li jou avèk nuit; </p>\n<p>Li rinmin-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu sé tout bagay pou mouin; </p>\n<p>Pa gin pi bon zanmi; </p>\n<p>Ma mété pou létènité </p>\n<p>Tout konfians mouin nan li. </p>\n<p>Lé li rinmin-m, sé bèl la vi; </p>\n<p>Li bèl, li papé jan-m fini; </p>\n<p>Vi étènèl! Joua étènèl! </p>\n<p>Li rinmin-m. </p>\n','Jezu-se-tout-bagay-pou-mwen',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1632,'b2c5d5c9-c01e-444b-a365-a0a5bee3e013',0,0,'Mouin Louvri Kè Mouin',29,'29_CE_ht','1.\nMouin louvri kè mouin,\nPou-m résévoua ou.\nJézu, Sovè mouin,\nKè mouin bézouin ou;\nMouin santi mouin sèl;\nKè mouin fatigé;\nViv ak ou ta bèl,\nOu kab ban-m la pè.\n\n\n2.\nMouin louvri léspri-m,\nPou-m résévoua ou.\nJézu-Kri, Ségnè-m,\nRété la toujou.\nVini pou régné,\nGidé tout bagay.\nPou dirijé-m bi-in\nMa suiv lòd ou bay.\n\n\n3.\nMouin louvri nan-m mouin\nPou-m résévoua ou.\nBon Rédanmtè mouin,\nKò mouin pou ou tou.\nPou ban mouin la vi,\nPa manké angnin,\nPou mouin ou mouri!\nPran kò-m, vi-m, nan-m mouin.','<p><strong>1.</strong> </p>\n<p>Mouin louvri kè mouin, </p>\n<p>Pou-m résévoua ou. </p>\n<p>Jézu, Sovè mouin, </p>\n<p>Kè mouin bézouin ou; </p>\n<p>Mouin santi mouin sèl; </p>\n<p>Kè mouin fatigé; </p>\n<p>Viv ak ou ta bèl, </p>\n<p>Ou kab ban-m la pè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin louvri léspri-m, </p>\n<p>Pou-m résévoua ou. </p>\n<p>Jézu-Kri, Ségnè-m, </p>\n<p>Rété la toujou. </p>\n<p>Vini pou régné, </p>\n<p>Gidé tout bagay. </p>\n<p>Pou dirijé-m bi-in </p>\n<p>Ma suiv lòd ou bay. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin louvri nan-m mouin </p>\n<p>Pou-m résévoua ou. </p>\n<p>Bon Rédanmtè mouin, </p>\n<p>Kò mouin pou ou tou. </p>\n<p>Pou ban mouin la vi, </p>\n<p>Pa manké angnin, </p>\n<p>Pou mouin ou mouri! </p>\n<p>Pran kò-m, vi-m, nan-m mouin. </p>\n','Mouin-Louvri-Ke-Mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1633,'7e8f3651-1091-420b-a310-ad705dd73c81',0,0,'Mouin-minmsé Gnou Moun Télman Mizérab',30,'30_CE_ht','1.\nMouin-min’m sé gnou moun tèlman mizérab,\nKòman m-kab prézanté dévan Bon-Dieu-m?\nParèt dévan tròn li konman m-kapab?\nSépandan, min-l yo lonjé pou rélé-m.\n\n\n2.\nLè mouin vlé maché nan chémin lé sièl,\nSé lè péché vini pou mouin troublé;\nPoutan, mouin tandé gnou mo ki si bèl:\nRépanti, kouè, é ou va délivré.\n\n\n3.\nVoua m–tandé la, sé voua Jézu li-min’m,\nSé min li yo ki lonjé pou sové-m;\nPou nou kab padonnin sé nan li-min’m,\nÉ sé li-min’m tou ki kab purifié-m.\n\n\n4.\nMouin pa gin angnin, é m-doué ou tèlman!\nPou tèt sa mouin gin kichòy pou ba ou:\nMin-m jan ak Mari, ma montré kouman\nM-rinmin ou paské ou padonnin-m tou.','<p><strong>1.</strong> </p>\n<p>Mouin-min’m sé gnou moun tèlman mizérab, </p>\n<p>Kòman m-kab prézanté dévan Bon-Dieu-m? </p>\n<p>Parèt dévan tròn li konman m-kapab? </p>\n<p>Sépandan, min-l yo lonjé pou rélé-m. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè mouin vlé maché nan chémin lé sièl, </p>\n<p>Sé lè péché vini pou mouin troublé; </p>\n<p>Poutan, mouin tandé gnou mo ki si bèl: </p>\n<p>Répanti, kouè, é ou va délivré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Voua m–tandé la, sé voua Jézu li-min’m, </p>\n<p>Sé min li yo ki lonjé pou sové-m; </p>\n<p>Pou nou kab padonnin sé nan li-min’m, </p>\n<p>É sé li-min’m tou ki kab purifié-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin pa gin angnin, é m-doué ou tèlman! </p>\n<p>Pou tèt sa mouin gin kichòy pou ba ou: </p>\n<p>Min-m jan ak Mari, ma montré kouman </p>\n<p>M-rinmin ou paské ou padonnin-m tou. </p>\n','Mouin-minmse-Gnou-Moun-Telman-Mizerab',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1634,'33c60467-af50-4872-8a01-ccceb7f4c9cf',0,0,'An nou chanté amou Jézu',46,'46_CE_ht','1.\nAn nou chanté  amou Jézu,\nChanté tandrès ak gras tou,\nNan bèl kay yo ki nan sièl la\nLap paré gnou plas pou nou.\n\nKè:\nLè nou tout al nan sièl la,\nKi gran jou réjouisans sa va pou nou.\nLè nou tout ouè Jézu\nNa chanté la viktoua toujou.\n\n\n2.\nTout lè nap maché sou tè sa,\nDoulè kapab nui nou fò,\nMin lè vouayaj nou va fini\nNan pouin dlo nan jé ankò.\n\n\n3.\nAn-n chaché pou nou ak Jézu,\nTou suit na ouè jan li bèl.\nTou suit pòt sièl la va louvri,\nNa maché an ro avè-L.','<p><strong>1.</strong> </p>\n<p>An nou chanté  amou Jézu, </p>\n<p>Chanté tandrès ak gras tou, </p>\n<p>Nan bèl kay yo ki nan sièl la </p>\n<p>Lap paré gnou plas pou nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè nou tout al nan sièl la,</strong> </p>\n<p><strong>Ki gran jou réjouisans sa va pou nou.</strong> </p>\n<p><strong>Lè nou tout ouè Jézu</strong> </p>\n<p><strong>Na chanté la viktoua toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout lè nap maché sou tè sa, </p>\n<p>Doulè kapab nui nou fò, </p>\n<p>Min lè vouayaj nou va fini </p>\n<p>Nan pouin dlo nan jé ankò. </p>\n<p><br/> <strong>3.</strong></p>\n<p>An-n chaché pou nou ak Jézu, </p>\n<p>Tou suit na ouè jan li bèl. </p>\n<p>Tou suit pòt sièl la va louvri, </p>\n<p>Na maché an ro avè-L. </p>\n','An-nou-chante-amou-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1635,'ac22238c-52e0-424b-8458-67ff2489ff15',0,0,'Gingnin Gnou Sous Tout Plin Ak San',51,'51_CE_ht','1.\nGingnin gnou sous tou plin ak san,\nSé san dé Jézu-Kri.\nPéchè yo ki bingnin la dan\nSòti san tach é pu.  (3 foua)\nPéchè yo ki bingnin la dan\nSòti san tach é pu.\n\n\n2.\nVolè a ki tapé mouri,\nLi té réjoui nan jou sa.\nÉ nan-m mouin ki koupab kon li\nJouin padon nan sous la.  (3 foua)\nÉ nan-m mouin  ki koupab kon li\nJouin padon nan sous la.\n\n\n3.\nSovè-m, san ou ki té koulé\nPap jan-m pèdu puisans\nJouk lè ké tout légliz Bon-Dieu\nRéjoui nan délivrans.  (3 foua)\nJouk lè ké tout légliz Bon-dieu\nRéjoui nan délivrans.\n\n\n4.\nDépi mouin té ouè tout bonté\nOu montré-m nan sali,\nAmou map chanté san rété\nJouk lè ma va mouri.  (3 foua)\nAmou map chanté san rété\nJouk lè ma va mouri.\n\n\n5.\nLè lang pa mouin va fi-n palé,\nÉ kò mouin nan tonbo;\nLouanj Jézu ma va chanté\nAk rachté yo an ro.  (3 foua)\nLouanj Jézu ma va chanté\nAk rachté yo an ro.','<p><strong>1.</strong> </p>\n<p>Gingnin gnou sous tou plin ak san, </p>\n<p>Sé san dé Jézu-Kri. </p>\n<p>Péchè yo ki bingnin la dan </p>\n<p>Sòti san tach é pu.  (3 foua) </p>\n<p>Péchè yo ki bingnin la dan </p>\n<p>Sòti san tach é pu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Volè a ki tapé mouri, </p>\n<p>Li té réjoui nan jou sa. </p>\n<p>É nan-m mouin ki koupab kon li </p>\n<p>Jouin padon nan sous la.  (3 foua) </p>\n<p>É nan-m mouin  ki koupab kon li </p>\n<p>Jouin padon nan sous la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sovè-m, san ou ki té koulé </p>\n<p>Pap jan-m pèdu puisans </p>\n<p>Jouk lè ké tout légliz Bon-Dieu </p>\n<p>Réjoui nan délivrans.  (3 foua) </p>\n<p>Jouk lè ké tout légliz Bon-dieu </p>\n<p>Réjoui nan délivrans. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dépi mouin té ouè tout bonté </p>\n<p>Ou montré-m nan sali, </p>\n<p>Amou map chanté san rété </p>\n<p>Jouk lè ma va mouri.  (3 foua) </p>\n<p>Amou map chanté san rété </p>\n<p>Jouk lè ma va mouri. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Lè lang pa mouin va fi-n palé, </p>\n<p>É kò mouin nan tonbo; </p>\n<p>Louanj Jézu ma va chanté </p>\n<p>Ak rachté yo an ro.  (3 foua) </p>\n<p>Louanj Jézu ma va chanté </p>\n<p>Ak rachté yo an ro. </p>\n','Gingnin-Gnou-Sous-Tout-Plin-Ak-San',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1636,'0a90a27a-9839-4c78-adf1-732a1141e53a',0,0,'Nan fon labim map rélé Ou',76,'76_CE_ht','1.\nNan fon labim map rélé Ou,\nO kouté mouin, Ségnè!\nSé nan Ou sèl mouin jouin-n sékou\nPou-m pa dézéspéré!\nSi ou ta sonjé tout péché\nPou puni chak, kon sa doué yé,\nKi moun kab rézisté Ou?\n\n\n2.\nGras a lamou, nou jouin-n nan Ou\nPadon péché nou yo.\nJustis nou-min’m pa bon du tou,\nZèv nou sé bagay fo.\nNou pa gin doua vanté tèt nou\nPou angnin bon kap fèt nan nou;\nSé gras Ou sèl kap fè li.\n\n\n3.\nTout éspoua-m sé lé Ségnè,\nZèv mouin pa vo angnin,\nSé pa sou yo mouin kalkulé,\nTout foua-m sé nan Li-min’m.\nMouin kité tout choz nan min Li,\nLi sé fòs mouin, Li sé la vi,\nMap tan-n ak foua sékou Li.\n\n\n4.\nMin-m si Li pa édé-m tou suit,\nSi m-oblijé tan-n lontan -\nDé jou pasé é min-m dé nuit,\nSan m-pa kapab konpran-n;\nKè mouin pap jan-m pèdu la foua,\nLi pap bandonné mouin nan noua:\nMap tan-n sékou-L ak konfians.\n\n\n5.\nPéché nou yo, yo tèlman gran,\nChut nou té tèlman fon!\nMin gras Li sé pi konsékan –\nAmou Li gran san non!\nBèjé nou, Sovè nou, sé Li\nKi vlé bay tout moun yo la vi –\nSové yo nan min Satan.','<p><strong>1.</strong> </p>\n<p>Nan fon labim map rélé Ou, </p>\n<p>O kouté mouin, Ségnè! </p>\n<p>Sé nan Ou sèl mouin jouin-n sékou </p>\n<p>Pou-m pa dézéspéré! </p>\n<p>Si ou ta sonjé tout péché </p>\n<p>Pou puni chak, kon sa doué yé, </p>\n<p>Ki moun kab rézisté Ou? </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gras a lamou, nou jouin-n nan Ou </p>\n<p>Padon péché nou yo. </p>\n<p>Justis nou-min’m pa bon du tou, </p>\n<p>Zèv nou sé bagay fo. </p>\n<p>Nou pa gin doua vanté tèt nou </p>\n<p>Pou angnin bon kap fèt nan nou; </p>\n<p>Sé gras Ou sèl kap fè li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout éspoua-m sé lé Ségnè, </p>\n<p>Zèv mouin pa vo angnin, </p>\n<p>Sé pa sou yo mouin kalkulé, </p>\n<p>Tout foua-m sé nan Li-min’m. </p>\n<p>Mouin kité tout choz nan min Li, </p>\n<p>Li sé fòs mouin, Li sé la vi, </p>\n<p>Map tan-n ak foua sékou Li. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Min-m si Li pa édé-m tou suit, </p>\n<p>Si m-oblijé tan-n lontan - </p>\n<p>Dé jou pasé é min-m dé nuit, </p>\n<p>San m-pa kapab konpran-n; </p>\n<p>Kè mouin pap jan-m pèdu la foua, </p>\n<p>Li pap bandonné mouin nan noua: </p>\n<p>Map tan-n sékou-L ak konfians. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Péché nou yo, yo tèlman gran, </p>\n<p>Chut nou té tèlman fon! </p>\n<p>Min gras Li sé pi konsékan – </p>\n<p>Amou Li gran san non! </p>\n<p>Bèjé nou, Sovè nou, sé Li </p>\n<p>Ki vlé bay tout moun yo la vi – </p>\n<p>Sové yo nan min Satan. </p>\n','Nan-fon-labim-map-rele-Ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1637,'d205bde7-d938-4e89-8ded-82d49fd9d273',0,0,'Mouin ta vlé di ou sa Jézu fè pou kè mouin',94,'94_CE_ht','1.\nMouin ta vlé di ou sa Jézu fè pou kè mouin,\nDépi mouin té jouin-n li kou bon Zanmi mouin;\nMouin ta di jan la vi-m té chanjé konplètman,\nÉ kè mouin té ranpli avèk kontantman.\n\nKè:\nPèsòn patjan-m rinmin mouin kou Jézu,\nPa gin gnou lot bon Zanmi kon Li.\nPa gin gnou lot kab blachi péché-m ki rouj anpil!\nO, a la li té rinmin-m!\n\n\n2.\nVi-m té tou sal ak péché lè Jézu jouin-n mouin,\nKè-m té plin ak mizè, tèt mouin té chajé,\nMin avèk amou Li Jézu té vlopé mouin,\nÉ li kondui mouin koté m-té doué alé.\n\n\n3.\nChak jou li vi-n jouin-n mouin pou ban mouin asurans;\nJou apré jou mouin pi konnin amou li;\nMouin pap jan-m konpran pouki li té vi-n sové-m\nJouk lò nan sièl la ma va kontré ak li.','<p><strong>1.</strong> </p>\n<p>Mouin ta vlé di ou sa Jézu fè pou kè mouin, </p>\n<p>Dépi mouin té jouin-n li kou bon Zanmi mouin; </p>\n<p>Mouin ta di jan la vi-m té chanjé konplètman, </p>\n<p>É kè mouin té ranpli avèk kontantman. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pèsòn patjan-m rinmin mouin kou Jézu,</strong> </p>\n<p><strong>Pa gin gnou lot bon Zanmi kon Li.</strong> </p>\n<p><strong>Pa gin gnou lot kab blachi péché-m ki rouj anpil!</strong> </p>\n<p><strong>O, a la li té rinmin-m!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vi-m té tou sal ak péché lè Jézu jouin-n mouin, </p>\n<p>Kè-m té plin ak mizè, tèt mouin té chajé, </p>\n<p>Min avèk amou Li Jézu té vlopé mouin, </p>\n<p>É li kondui mouin koté m-té doué alé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chak jou li vi-n jouin-n mouin pou ban mouin asurans; </p>\n<p>Jou apré jou mouin pi konnin amou li; </p>\n<p>Mouin pap jan-m konpran pouki li té vi-n sové-m </p>\n<p>Jouk lò nan sièl la ma va kontré ak li. </p>\n','Mouin-ta-vle-di-ou-sa-Jezu-fe-pou-ke-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1638,'408a2210-5ed5-4f20-bfca-916756831f37',0,0,'Ti moun yo nan Jéruzalèm',110,'110_CE_ht','1.\nTi moun yo nan Jéruzalèm\nTé chanté gloua non Jézu.\nÉ ti moun yo jodi a\nKapab chanté non li tou.\n\nKè:\nKouté bi-in, ti moun yo ap chanté:  (2 foua)\nOzanna  (3 foua)  pou lé roua Jézu.\n\n\n2.\nNap apran-n rinmin Jézu,\nNap apran-n li Parol Li,\nNou kap suiv lé bon chémin;\nGloua a Jézu, lé Ségnè!\n\n\n3.\nGran moun avèk ti moun yo,\nSé pou nou chanté ansan-m.\nChanté avèk anj yo tou,\nOzanna, gloua a Jézu.','<p><strong>1.</strong> </p>\n<p>Ti moun yo nan Jéruzalèm </p>\n<p>Té chanté gloua non Jézu. </p>\n<p>É ti moun yo jodi a </p>\n<p>Kapab chanté non li tou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kouté bi-in, ti moun yo ap chanté:  (2 foua)</strong> </p>\n<p><strong>Ozanna  (3 foua)  pou lé roua Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nap apran-n rinmin Jézu, </p>\n<p>Nap apran-n li Parol Li, </p>\n<p>Nou kap suiv lé bon chémin; </p>\n<p>Gloua a Jézu, lé Ségnè! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gran moun avèk ti moun yo, </p>\n<p>Sé pou nou chanté ansan-m. </p>\n<p>Chanté avèk anj yo tou, </p>\n<p>Ozanna, gloua a Jézu. </p>\n','Ti-moun-yo-nan-Jeruzalem',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1639,'1aaafd2f-427a-4850-a5ca-083c591ff676',0,0,'O Sovè, ou té pran kè mouin',112,'112_CE_ht','1.\nO Sovè, ou té pran kè mouin,\nKoun yé a, mouin pou ou.\nLa vi-m, la joua, la pè-m, tout choz\nMouin jouin-n nan ou toujou.\n\nKè:\nKè mouin ap chanté tou lé jou,\nPaské léspri mouin lib nan ou.\nNan-m mouin plin ak lamou divin,\nSégnè, mouin jouin-n répo chak jou.\n\n\n2.\nSint-Éspri abité nan kè-m,\nMouin pa gin krin non plus.\nFado péché-m té fi-n alé,\nMouin fondé sou lé Kris.\n\n\n3.\nKè mouin débòdé ak lamou,\nMouin konnin mouin pou ou.\nDévan tròn gras ou m-ajénou\nPou-m prié ou chak jou.','<p><strong>1.</strong> </p>\n<p>O Sovè, ou té pran kè mouin, </p>\n<p>Koun yé a, mouin pou ou. </p>\n<p>La vi-m, la joua, la pè-m, tout choz </p>\n<p>Mouin jouin-n nan ou toujou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kè mouin ap chanté tou lé jou,</strong> </p>\n<p><strong>Paské léspri mouin lib nan ou.</strong> </p>\n<p><strong>Nan-m mouin plin ak lamou divin,</strong> </p>\n<p><strong>Ségnè, mouin jouin-n répo chak jou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sint-Éspri abité nan kè-m, </p>\n<p>Mouin pa gin krin non plus. </p>\n<p>Fado péché-m té fi-n alé, </p>\n<p>Mouin fondé sou lé Kris. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kè mouin débòdé ak lamou, </p>\n<p>Mouin konnin mouin pou ou. </p>\n<p>Dévan tròn gras ou m-ajénou </p>\n<p>Pou-m prié ou chak jou. </p>\n','O-Sove-ou-te-pran-ke-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1640,'99d91735-1444-457e-9268-c5a62279b4a4',0,0,'O Ségnè, nou pòté rémèsiman',119,'119_CE_ht','1.\nO Ségnè, nou pòté rémèsiman\nPou bi-infè ou ki anpil:\nOu fè tè Daiti-a trè fètil\nPou pa manké aliman.\nO Bon Papa!  (2 foua)\nPitit ou yo ran-n ou gloua!\nAlélouya!\n\n\n2.\nO gadé ki bi-infè Bon-Dieu ban nou:\nNou jouin-n rékolt san konpran-n\nSa-k pasé nan pié kafé, poua, bannan-n,\nNan pié flè nou ouè patou.\nA la mèvèy!  (2 foua)\nA la Bon-Dieu bon papa!\nAlélouya!\n\n\n3.\nMin Dieu di:  Pin sèl pa kab bay la vi.\nGnou lè nou pap manjé, bouè,.\nLi vlé pran souin nou isit sou la tè\nPou nou viv nan Jézu-Kri.\nO pran kè nou!  (2 foua)\nBon-Papa ki rinmin nou!\nAlélouya!','<p><strong>1.</strong> </p>\n<p>O Ségnè, nou pòté rémèsiman </p>\n<p>Pou bi-infè ou ki anpil: </p>\n<p>Ou fè tè Daiti-a trè fètil </p>\n<p>Pou pa manké aliman. </p>\n<p>O Bon Papa!  (2 foua) </p>\n<p>Pitit ou yo ran-n ou gloua! </p>\n<p>Alélouya! </p>\n<p><br/> <strong>2.</strong></p>\n<p>O gadé ki bi-infè Bon-Dieu ban nou: </p>\n<p>Nou jouin-n rékolt san konpran-n </p>\n<p>Sa-k pasé nan pié kafé, poua, bannan-n, </p>\n<p>Nan pié flè nou ouè patou. </p>\n<p>A la mèvèy!  (2 foua) </p>\n<p>A la Bon-Dieu bon papa! </p>\n<p>Alélouya! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Min Dieu di:  Pin sèl pa kab bay la vi. </p>\n<p>Gnou lè nou pap manjé, bouè,. </p>\n<p>Li vlé pran souin nou isit sou la tè </p>\n<p>Pou nou viv nan Jézu-Kri. </p>\n<p>O pran kè nou!  (2 foua) </p>\n<p>Bon-Papa ki rinmin nou! </p>\n<p>Alélouya! </p>\n','O-Segne-nou-pote-remesiman',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1641,'30f558af-07d2-4e15-af4d-2ff2b064c3fa',0,0,'Prémié Noèl zanj la té palé',121,'121_CE_ht','1.\nPrémié Noèl zanj la té palé\nBèjé yo nan plèn yo rété,\nNan plèn koté yo gadé mouton,\nGnou nuit frédi ki té fè noua.\n\nKè:  Noèl, Noèl, Noèl, Noèl,\nJézu té fèt roua Izraèl.\n\n\n2.\nYo gadé sièl la é yo ouè\nGnou foul bèl zanj yo ki tapé chanté:\nTout gloua pou Bon-Dieu nan lié trè ro,\nÉ pè sou la tè é bòn volonté.\n\n\n3.\nAvèk lumiè gnou bèl zétoual,\nToua zòm saj sèti nan  gnou péitrèlouin,\nAk intansion pou yo chaché gnou roua,\nÉ yo suiv zétoual la koté li alé.\n\n\n4.\nZétoual la maché tout la nui,\nRivé Bétléèm, li pa konto-nué.\nLi rété la é li pozé\nTou jus koté Jézu té kouché.\n\n\n5.\nAlò, toua zòm saj yo vi-n pou antré,\nAk gran réspè yo mété a jénou,\nÉ yo ofri Li tou sa o pòté:\nAnpli lò, la mi ak lansan.\n\n\n6.\nNou-min’m tou, sé pou nou mété ansan-m\nPou chanté louanj Ségnè nou ki nan sièl,\nKi pat bézouin angnin pou fè sièl ak tè\nÉ ki vini pou sovè nou.','<p><strong>1.</strong> </p>\n<p>Prémié Noèl zanj la té palé </p>\n<p>Bèjé yo nan plèn yo rété, </p>\n<p>Nan plèn koté yo gadé mouton, </p>\n<p>Gnou nuit frédi ki té fè noua. </p>\n<p>Kè:  Noèl, Noèl, Noèl, Noèl, </p>\n<p>Jézu té fèt roua Izraèl. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Yo gadé sièl la é yo ouè </p>\n<p>Gnou foul bèl zanj yo ki tapé chanté: </p>\n<p>Tout gloua pou Bon-Dieu nan lié trè ro, </p>\n<p>É pè sou la tè é bòn volonté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Avèk lumiè gnou bèl zétoual, </p>\n<p>Toua zòm saj sèti nan  gnou péitrèlouin, </p>\n<p>Ak intansion pou yo chaché gnou roua, </p>\n<p>É yo suiv zétoual la koté li alé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zétoual la maché tout la nui, </p>\n<p>Rivé Bétléèm, li pa konto-nué. </p>\n<p>Li rété la é li pozé </p>\n<p>Tou jus koté Jézu té kouché. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Alò, toua zòm saj yo vi-n pou antré, </p>\n<p>Ak gran réspè yo mété a jénou, </p>\n<p>É yo ofri Li tou sa o pòté: </p>\n<p>Anpli lò, la mi ak lansan. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Nou-min’m tou, sé pou nou mété ansan-m </p>\n<p>Pou chanté louanj Ségnè nou ki nan sièl, </p>\n<p>Ki pat bézouin angnin pou fè sièl ak tè </p>\n<p>É ki vini pou sovè nou. </p>\n','Premie-Noel-zanj-la-te-pale',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1642,'4a454ffe-7f66-4543-88ce-38d1d43c595a',0,0,'Tou patou sou la tè gin ti moun yo',140,'140_CE_ht','1.\nTou patou sou la tè gin ti moun yo\nKap chanté gloua a Jézu ak tout kè.\nSé li ki bay yo fòs é la joua tou,\nSé li ki Sovè, Gid ak Zanmi a tout è.\n\n\n2.\nGro la mè a touché anpil péï;\nNan tout péï sa yo anpil ti moun\nAp chanté gloua a Jézu, ap loué-l\nPou amou-l kap antouré yo kou gro la mè.','<p><strong>1.</strong> </p>\n<p>Tou patou sou la tè gin ti moun yo </p>\n<p>Kap chanté gloua a Jézu ak tout kè. </p>\n<p>Sé li ki bay yo fòs é la joua tou, </p>\n<p>Sé li ki Sovè, Gid ak Zanmi a tout è. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gro la mè a touché anpil péï; </p>\n<p>Nan tout péï sa yo anpil ti moun </p>\n<p>Ap chanté gloua a Jézu, ap loué-l </p>\n<p>Pou amou-l kap antouré yo kou gro la mè. </p>\n','Tou-patou-sou-la-te-gin-ti-moun-yo',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1643,'d770f2cf-8943-416e-b61c-a6b9693e7c90',0,0,'Le Ségnè Konnin Chemin Nan Dézè A',47,'47_MJ_ht','1.\nLé Ségnè konnin chémin nan dézè a;\nSa mouin gin pou fè sé suiv Li. (bis 2 lign)\nLi ban-m la fòs pou jodi a;\nNan tout rout la mouin sou kont Li.\nLé Ségnè konnin chémin nan dézè a;\nSa mouin gin pou fè, sé suiv Li.','<p><strong>1.</strong> </p>\n<p>Lé Ségnè konnin chémin nan dézè a; </p>\n<p>Sa mouin gin pou fè sé suiv Li. (bis 2 lign) </p>\n<p>Li ban-m la fòs pou jodi a; </p>\n<p>Nan tout rout la mouin sou kont Li. </p>\n<p>Lé Ségnè konnin chémin nan dézè a; </p>\n<p>Sa mouin gin pou fè, sé suiv Li. </p>\n','Le-Segne-Konnin-Chemin-Nan-Deze-A',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1644,'e469c83a-b289-4ef2-b0b6-8007b29b35e2',0,0,'Muzik Nan Kè Mwen Ap Soné',68,'68_MJ_ht','1.\nMuzik nan kè mouin ap soné\nMélodi divin\nNam mouin réjoui é mouin chanté\nKa amou-l pou mouin\n\nKè:\nTout la jounin kè-m ap chanté\nKa mouin konnin Kris pou mouin\nMouin sé gnou nouvo kréatu\nKoun-yé a mouin pitit Bon-Dieu\nMouin pa pè é mouin pa douté\nMouin sové pa gras divin\n\n2.\nKris sé vréman pa-m pou janmin\nSovè, Mèt, é Gid\nNi dout ni pérèz kab nui mouin\nLè-m kaché nan Li','<p><strong>1.</strong> </p>\n<p>Muzik nan kè mouin ap soné </p>\n<p>Mélodi divin </p>\n<p>Nam mouin réjoui é mouin chanté </p>\n<p>Ka amou-l pou mouin </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Tout la jounin kè-m ap chanté</strong> </p>\n<p><strong>Ka mouin konnin Kris pou mouin</strong> </p>\n<p><strong>Mouin sé gnou nouvo kréatu</strong> </p>\n<p><strong>Koun-yé a mouin pitit Bon-Dieu</strong> </p>\n<p><strong>Mouin pa pè é mouin pa douté</strong> </p>\n<p><strong>Mouin sové pa gras divin</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Kris sé vréman pa-m pou janmin </p>\n<p>Sovè, Mèt, é Gid </p>\n<p>Ni dout ni pérèz kab nui mouin </p>\n<p>Lè-m kaché nan Li </p>\n','Muzik-Nan-Ke-Mwen-Ap-Sone',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1645,'d12a3b51-09c0-4913-a2ca-ac6e2313ccba',0,0,'Sa Sa Fè Si Rout La Mové',81,'81_MJ_ht','1.	\nSa sa fè si rout la mové?\nMin-m si gin mové pa?\nLi konnin byin tou sa-m kap fè,\nPapa-m ranjé-l konsa.\n\nKè:\nMap chanté nan solèy, nan fè noua,\nNinpòt sak rivé ma kontan,\nFò-m chanté, mouin pa kapab rété,\nPapa-m ranjé-l konsa.\n\n2.	\nSikonstans yo kapab chanjé,\nSa pa fè mouin angnin,\nDèské sé konsa Li chouazi,\nPlan Papa-m bon pou mouin.\n\n3.	\nLi té déjà pasé rout sa,\nMap suiv Li pa-za-pa,\nMouin gin konfians ké lé Ségnè,\nAp minnin-m nan sièl la.\n\n4.	\nSèlman lumiè a réjouisans,\nAp tan-n mouin a la fin,\nDonk, mouin pa pè gnou ti soufrans,\nPapa-m ranjé-l konsa.','<p><strong>1.</strong> </p>\n<p>Sa sa fè si rout la mové? </p>\n<p>Min-m si gin mové pa? </p>\n<p>Li konnin byin tou sa-m kap fè, </p>\n<p>Papa-m ranjé-l konsa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Map chanté nan solèy, nan fè noua,</strong> </p>\n<p><strong>Ninpòt sak rivé ma kontan,</strong> </p>\n<p><strong>Fò-m chanté, mouin pa kapab rété,</strong> </p>\n<p><strong>Papa-m ranjé-l konsa.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sikonstans yo kapab chanjé, </p>\n<p>Sa pa fè mouin angnin, </p>\n<p>Dèské sé konsa Li chouazi, </p>\n<p>Plan Papa-m bon pou mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li té déjà pasé rout sa, </p>\n<p>Map suiv Li pa-za-pa, </p>\n<p>Mouin gin konfians ké lé Ségnè, </p>\n<p>Ap minnin-m nan sièl la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sèlman lumiè a réjouisans, </p>\n<p>Ap tan-n mouin a la fin, </p>\n<p>Donk, mouin pa pè gnou ti soufrans, </p>\n<p>Papa-m ranjé-l konsa. </p>\n','Sa-Sa-Fe-Si-Rout-La-Move',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1646,'8acaf561-87e4-451e-98c1-1af28a7af89f',2,0,'C’est Toi Jésus Pain de vie',3,'3_VR_fr','1.	\nC\'est Toi Jésus Pain de vie\nQui me nourris jour par jour\nManne céleste et bénie\nC\'est ton merveilleux amour.\n\nChoeur\nPain de vie, Pain de vie, Pain de vie\nDon du ciel toujours nouveau\nTu me nourris Pain d\'En-Haut.\n\n2.	\nC\'est encore Toi source vive\nLe secours du Pèlerin\nIl puise il boit et ravive\nSon coeur le long du chemin.\n\nChoeur\nSource vive, Source vive, Source vive\nDon du ciel toujours nouveau\nTu jaillis sur moi d\'En-Haut.\n\n3.	\nÂmes toujours altérées\nQui cherchez de fraîches eaux\nAux citernes crevassées\nPréférez les clairs ruisseaux.\n\nChoeur\nO Rosées, O Rosées, O Rosées,\nDon du ciel toujours nouveau\nDescendez sur moi d\'En-Haut.','<p><strong>1.</strong> </p>\n<p>C&#39;est Toi Jésus Pain de vie </p>\n<p>Qui me nourris jour par jour </p>\n<p>Manne céleste et bénie </p>\n<p>C&#39;est ton merveilleux amour. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Pain de vie, Pain de vie, Pain de vie</strong> </p>\n<p><strong>Don du ciel toujours nouveau</strong> </p>\n<p><strong>Tu me nourris Pain d&#39;En-Haut.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est encore Toi source vive </p>\n<p>Le secours du Pèlerin </p>\n<p>Il puise il boit et ravive </p>\n<p>Son coeur le long du chemin. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Source vive, Source vive, Source vive</strong> </p>\n<p><strong>Don du ciel toujours nouveau</strong> </p>\n<p><strong>Tu jaillis sur moi d&#39;En-Haut.</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Âmes toujours altérées </p>\n<p>Qui cherchez de fraîches eaux </p>\n<p>Aux citernes crevassées </p>\n<p>Préférez les clairs ruisseaux. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>O Rosées, O Rosées, O Rosées,</strong> </p>\n<p><strong>Don du ciel toujours nouveau</strong> </p>\n<p><strong>Descendez sur moi d&#39;En-Haut.</strong> </p>\n','C\'est-Toi-Jesus-Pain-de-vie',NULL,'fr',2,'2023-07-22 22:41:15','2023-10-03 20:22:08'),(1647,'7d1b6d99-06a7-4dd2-8729-fce080721327',0,0,'Quel Bonheur Quand, Faisant Trêve',327,'327_CE_fr','1.\nQuel bonheur quand, faisant trêve\nUn moment à nos labeurs,\nL\'aube du saint jour se lève\nDans le ciel et dans nos coeurs!\nDès le matin, plus légère,\nL\'âme s\'élève au Seigneur\nSur l\'aile de la prière:\nQuel bonheur!  Quel bonheur!\n\n\n2.\nL\'été, la nature en fête\nÉlève ensuite sa voix;\nOn l\'entend, où qu\'on s\'arrête,\nDans les champs et dans les bois.\nL\'oiseau, la fleur, le brin d\'herbe\nChante aussi bien son Auteur\nQue la montagne superbe.\nQuel bonheur!  Quel bonheur!\n\n\n3.\nL\'hiver, voici la demeure\nDu pauvre et l\'orphelin,\nOù l\'on a froid, ou l\'on pleure:\nOh! Prenons-en le chemin!\nA soulager la souffrance,\nA faire en leur triste coeur\nLuire un rayon d\'espérance,\nQuel bonheur!  Quel bonheur!\n\n\n4.\nLe soir, quand la lampe brille\nOn lit ou l\'on cause un peu,\nPuis le père de famille\nOuvre le livre de Dieu;\nEt, sous la garde invisible\nDe son ange protecteur,\nLe foyer s\'endort paisible.\nQuel bonheur!  Quel bonheur!','<p><strong>1.</strong> </p>\n<p>Quel bonheur quand, faisant trêve </p>\n<p>Un moment à nos labeurs, </p>\n<p>L&#39;aube du saint jour se lève </p>\n<p>Dans le ciel et dans nos coeurs! </p>\n<p>Dès le matin, plus légère, </p>\n<p>L&#39;âme s&#39;élève au Seigneur </p>\n<p>Sur l&#39;aile de la prière: </p>\n<p>Quel bonheur!  Quel bonheur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>L&#39;été, la nature en fête </p>\n<p>Élève ensuite sa voix; </p>\n<p>On l&#39;entend, où qu&#39;on s&#39;arrête, </p>\n<p>Dans les champs et dans les bois. </p>\n<p>L&#39;oiseau, la fleur, le brin d&#39;herbe </p>\n<p>Chante aussi bien son Auteur </p>\n<p>Que la montagne superbe. </p>\n<p>Quel bonheur!  Quel bonheur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>L&#39;hiver, voici la demeure </p>\n<p>Du pauvre et l&#39;orphelin, </p>\n<p>Où l&#39;on a froid, ou l&#39;on pleure: </p>\n<p>Oh! Prenons-en le chemin! </p>\n<p>A soulager la souffrance, </p>\n<p>A faire en leur triste coeur </p>\n<p>Luire un rayon d&#39;espérance, </p>\n<p>Quel bonheur!  Quel bonheur! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le soir, quand la lampe brille </p>\n<p>On lit ou l&#39;on cause un peu, </p>\n<p>Puis le père de famille </p>\n<p>Ouvre le livre de Dieu; </p>\n<p>Et, sous la garde invisible </p>\n<p>De son ange protecteur, </p>\n<p>Le foyer s&#39;endort paisible. </p>\n<p>Quel bonheur!  Quel bonheur! </p>\n','Quel-Bonheur-Quand-Faisant-Treve',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1648,'bc823743-1fed-4491-a89c-1e7c919e7277',0,0,'Rédanmtè mouin mouri sou la koua',1,'1_CE_ht','1.\nRédanmtè mouin mouri sou la koua\nPou moun kapab viv ki résévoua-l.\nLi vi-n rachté tout moun nan péché,\nSi nou kouè, nou papé kondanné.\n\nKè\nLè Bon-Dieu ouè gnou péchè  (2 foua)\nTout kouvri avèk péché-l.\nMét la foua nan Jézu, li padonnin-l.\n\n\n2.\nA! ou mèt pi méchan sou la tè,\nKè ou mèt du tankou gnou bout fè,\nSi ou vi-n jouin-n Jézu pou Sovè\nTout sièl la kontan ou délivré.\n\n\n3.\nBon-Dieu bon, li ban nou piti li,\nPandan nou gin la vi, répanti,\nPaské kolè Bon-Dieu sé di fé;\nKouri, vi-n soti anba malè.\n\n\n4.\nVi ou doué pròp tankou Jézu-Kri,\nSan-l va lavé ou, oua gin Léspri.\nVinni! Vini! Tan an ap pasé;\nSi ou mizé tròp, sa ou va fè?','<p><strong>1.</strong> </p>\n<p>Rédanmtè mouin mouri sou la koua </p>\n<p>Pou moun kapab viv ki résévoua-l. </p>\n<p>Li vi-n rachté tout moun nan péché, </p>\n<p>Si nou kouè, nou papé kondanné. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Lè Bon-Dieu ouè gnou péchè  (2 foua)</strong> </p>\n<p><strong>Tout kouvri avèk péché-l.</strong> </p>\n<p><strong>Mét la foua nan Jézu, li padonnin-l.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A! ou mèt pi méchan sou la tè, </p>\n<p>Kè ou mèt du tankou gnou bout fè, </p>\n<p>Si ou vi-n jouin-n Jézu pou Sovè </p>\n<p>Tout sièl la kontan ou délivré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bon-Dieu bon, li ban nou piti li, </p>\n<p>Pandan nou gin la vi, répanti, </p>\n<p>Paské kolè Bon-Dieu sé di fé; </p>\n<p>Kouri, vi-n soti anba malè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vi ou doué pròp tankou Jézu-Kri, </p>\n<p>San-l va lavé ou, oua gin Léspri. </p>\n<p>Vinni! Vini! Tan an ap pasé; </p>\n<p>Si ou mizé tròp, sa ou va fè? </p>\n','Redanmte-mouin-mouri-sou-la-koua',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1649,'fd5fa7cc-feca-4bfe-8c67-2da5eeea53c5',0,0,'Jézu sèl unik éspérans',7,'7_CE_ht','1.\nJézu, sèl unik éspérans,\nSé li sèl ki bay asurans,\nLi sé souti-in’m, li sé fòs mouin\nÉ mouin pa rété nan chémin.\n\nKè\nJézu, sé ou ki délivré-m;\nSé ou, Sovè-m, kapé kinbé-m.  (2 foua)\n\n\n2.\nOkin-n lòt plas pa kab kaché-m,\nOkin-n lòt non pap délivré-m,\nMoun ki chouazi lòt diréksion,\nSoufri, péri nan rémision.\n\n\n3.\nMin, lè tout bagay vlé sombré.\nMouin gadé Jézu, mouin kanpé;\nLè kouraj mouin ta vlé fébli,\nMouin jouin-n sékou nan prézans li.\n\n\n4.\nKon sa lan mò gin pou-l vini,\nMin, toutfoua sa sé pa mouri,\nAvèk Jézu pa gin lan mò,\nSé fèmin jé, louvri-l lòt bò.','<p><strong>1.</strong> </p>\n<p>Jézu, sèl unik éspérans, </p>\n<p>Sé li sèl ki bay asurans, </p>\n<p>Li sé souti-in’m, li sé fòs mouin </p>\n<p>É mouin pa rété nan chémin. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Jézu, sé ou ki délivré-m;</strong> </p>\n<p><strong>Sé ou, Sovè-m, kapé kinbé-m.  (2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Okin-n lòt plas pa kab kaché-m, </p>\n<p>Okin-n lòt non pap délivré-m, </p>\n<p>Moun ki chouazi lòt diréksion, </p>\n<p>Soufri, péri nan rémision. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Min, lè tout bagay vlé sombré. </p>\n<p>Mouin gadé Jézu, mouin kanpé; </p>\n<p>Lè kouraj mouin ta vlé fébli, </p>\n<p>Mouin jouin-n sékou nan prézans li. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kon sa lan mò gin pou-l vini, </p>\n<p>Min, toutfoua sa sé pa mouri, </p>\n<p>Avèk Jézu pa gin lan mò, </p>\n<p>Sé fèmin jé, louvri-l lòt bò. </p>\n','Jezu-sel-unik-esperans',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1650,'23eef9e8-d3a1-4976-8d32-b80da6615f60',0,0,'Pandan La Nuit',12,'12_CE_ht','1.\nPandan la nuit, lè bèjé yo\nOkupé mouton yo,\nAnj lé Ségnè vini désan-n,\nLa gloua briyé patou,\nLi di: « Pa pè », paské yo tout\nTé trablé, tou sézi,\nMa póté ba nou bon nouvèl!\nPou nou ak tou lè zòm.\n\n\n2.\nNan la vil David, joda a,\nGnou pitit vini fèt,\nSé li ki Kris, Sovè, Ségnè,\nMin ki sign ma ba nou:\nNou va jouin-n pitit lè sièl la,\nLi la dévan jé moun,\nVlopé nan rad tout òdi-nè,\nÉ kouché nan gnou krèch.\n\n\n3.\nLè anj la té palé kon sa,\nGnou foul parèt tou suit,\nSé té lamé anj yo lé sièl,\nPou chanté kantik sa:\nTout gloua pou Bon-Dieu nan lé sièl,\nÉ la pè sou la tè,\nBon-Dieu voyé bòn-n volonté,\nPou lè zòm pou tout tan.','<p><strong>1.</strong> </p>\n<p>Pandan la nuit, lè bèjé yo </p>\n<p>Okupé mouton yo, </p>\n<p>Anj lé Ségnè vini désan-n, </p>\n<p>La gloua briyé patou, </p>\n<p>Li di: « Pa pè », paské yo tout </p>\n<p>Té trablé, tou sézi, </p>\n<p>Ma póté ba nou bon nouvèl! </p>\n<p>Pou nou ak tou lè zòm. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan la vil David, joda a, </p>\n<p>Gnou pitit vini fèt, </p>\n<p>Sé li ki Kris, Sovè, Ségnè, </p>\n<p>Min ki sign ma ba nou: </p>\n<p>Nou va jouin-n pitit lè sièl la, </p>\n<p>Li la dévan jé moun, </p>\n<p>Vlopé nan rad tout òdi-nè, </p>\n<p>É kouché nan gnou krèch. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè anj la té palé kon sa, </p>\n<p>Gnou foul parèt tou suit, </p>\n<p>Sé té lamé anj yo lé sièl, </p>\n<p>Pou chanté kantik sa: </p>\n<p>Tout gloua pou Bon-Dieu nan lé sièl, </p>\n<p>É la pè sou la tè, </p>\n<p>Bon-Dieu voyé bòn-n volonté, </p>\n<p>Pou lè zòm pou tout tan. </p>\n','Pandan-La-Nuit',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1651,'43bfa5f7-f3ab-4b5b-9953-5ddd32d478b1',0,0,'Amou Bondye pi gran pase',28,'28_CE_ht','1.\nAmou Bon-dieu pi gran pasé\nTou sa lang moun ak zanj kab di;\nL-alé pi louin pasé zétoual,\nLi jouin-n moun ki té fi-n pèdu.\nPitit Bon-Dieu chaché moun ki\nNan la pin-n ak péché;\nSi yo rékonnèt péché yo,\nLi prèt pou padonnin.\n\nKè:\nAmou Bon-Dieu sé rich, sé pu;\nLi san mézi, li fò;\nAmou sa a duré tout tan,\nSé kantik tout zanj yo.\n\n\n2.\nLè tout jou ak lanné pasé,\nLè tout tròn, tout roua fi-n tonbé;\nLè moun ki pa prié jodi\nVa chaché mouayin pou sové;\nLamou Bon-Dieu va la toujou,\nL-a san mézi, l-a fò;\nL-a fòs Bon-Dieu pou sové nou,\nL-a kantik tout zanj yo.\n\n\n3.\nSi nou té kab chanjé la mè\nKon lank, nan sièl la pou n-ékri;\nSi tout boua sou la tè té plum,\nÉ tout moun té kapab ékri;\nPou yo ékri lamou Bon-Dieu\nTa fi-n séché la mè,\nÉ sièl la pa ta kab kinbé\nTout zèv lamou-l té fè.','<p><strong>1.</strong> </p>\n<p>Amou Bon-dieu pi gran pasé </p>\n<p>Tou sa lang moun ak zanj kab di; </p>\n<p>L-alé pi louin pasé zétoual, </p>\n<p>Li jouin-n moun ki té fi-n pèdu. </p>\n<p>Pitit Bon-Dieu chaché moun ki </p>\n<p>Nan la pin-n ak péché; </p>\n<p>Si yo rékonnèt péché yo, </p>\n<p>Li prèt pou padonnin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Amou Bon-Dieu sé rich, sé pu;</strong> </p>\n<p><strong>Li san mézi, li fò;</strong> </p>\n<p><strong>Amou sa a duré tout tan,</strong> </p>\n<p><strong>Sé kantik tout zanj yo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè tout jou ak lanné pasé, </p>\n<p>Lè tout tròn, tout roua fi-n tonbé; </p>\n<p>Lè moun ki pa prié jodi </p>\n<p>Va chaché mouayin pou sové; </p>\n<p>Lamou Bon-Dieu va la toujou, </p>\n<p>L-a san mézi, l-a fò; </p>\n<p>L-a fòs Bon-Dieu pou sové nou, </p>\n<p>L-a kantik tout zanj yo. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si nou té kab chanjé la mè </p>\n<p>Kon lank, nan sièl la pou n-ékri; </p>\n<p>Si tout boua sou la tè té plum, </p>\n<p>É tout moun té kapab ékri; </p>\n<p>Pou yo ékri lamou Bon-Dieu </p>\n<p>Ta fi-n séché la mè, </p>\n<p>É sièl la pa ta kab kinbé </p>\n<p>Tout zèv lamou-l té fè. </p>\n','Amou-Bondye-pi-gran-pase',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1652,'4587349b-1ef0-4ad8-8906-e3bae0b03fc7',0,0,'Jézu, kinbin-m pré la koua',43,'43_CE_ht','1.\nJézu, kinbé-m pré la koua;\nSan ou té vèsé la,\nPou-l kab purifié tout nan-m\nKi ta plonjé ladan.\n\nKè:\nPré la koua,  (2 foua)\nMouin vlé toujou rété,\nJouk Jézu vini chaché-m\nPou-l sa ban mouin la joua.\n\n\n2.\nPré la koua, nan-m mouin tranblé;\nAmou li té jouin-n mouin,\nLa lumiè li té antré,\nLi té chanjé kè mouin.\n\n\n3.\nPré la koua, O Bon Sovè!\nFè-m sonjé soufrans ou,\nÉdé-m maché jou an jou\nAn ba lonbraj kalvè.\n\n\n4.\nPré la koua mapé véyé,\nKouè, obéi, prié,\nJouk tan lè la va sonnin\nPou li vi-n chaché mouin.','<p><strong>1.</strong> </p>\n<p>Jézu, kinbé-m pré la koua; </p>\n<p>San ou té vèsé la, </p>\n<p>Pou-l kab purifié tout nan-m </p>\n<p>Ki ta plonjé ladan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pré la koua,  (2 foua)</strong> </p>\n<p><strong>Mouin vlé toujou rété,</strong> </p>\n<p><strong>Jouk Jézu vini chaché-m</strong> </p>\n<p><strong>Pou-l sa ban mouin la joua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pré la koua, nan-m mouin tranblé; </p>\n<p>Amou li té jouin-n mouin, </p>\n<p>La lumiè li té antré, </p>\n<p>Li té chanjé kè mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pré la koua, O Bon Sovè! </p>\n<p>Fè-m sonjé soufrans ou, </p>\n<p>Édé-m maché jou an jou </p>\n<p>An ba lonbraj kalvè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pré la koua mapé véyé, </p>\n<p>Kouè, obéi, prié, </p>\n<p>Jouk tan lè la va sonnin </p>\n<p>Pou li vi-n chaché mouin. </p>\n','Jezu-kinbin-m-pre-la-koua',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1653,'e9177ff9-61af-4d38-826d-8ab31923f7ab',0,0,'Ban mouin kè ou, di Bon-Dieu na sièl la',68,'68_CE_ht','1.\nBan mouin kè ou, di Bon-Dieu nan sièl la,\nPa gin lòt kado pi bon pasé sa,\nDousman Li té rélé ou koté ou yé;\nMété konfians nan mouin, ban mouin kè ou.\n\nKè:\nBan mouin kè ou,  (2 foua)\nDousman Li  palé ou koté ou yé,\nNan lé mon-n ténèb Li vlé minnin ou,\nDousman Li palé ou:\nBan mouin kè ou.\n\n\n2.\nBan mouin kè ou, di Jézu, Sovè nou,\nNan mizèrikòd Li rélé ankò:\nKité péché ou é tout mové zèv,\nMouin té mouri pou ou, ban mouin kè ou.\n\n\n3.\nBan mouin kè ou, di Léspri, trè dousman,\nTou sa ou gingnin ban-m kinbé-l pou ou,\nMouin gin anpil gras pou ba ou chak jou.\nKonsakré tout bagay, ban mouin kè ou.','<p><strong>1.</strong> </p>\n<p>Ban mouin kè ou, di Bon-Dieu nan sièl la, </p>\n<p>Pa gin lòt kado pi bon pasé sa, </p>\n<p>Dousman Li té rélé ou koté ou yé; </p>\n<p>Mété konfians nan mouin, ban mouin kè ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ban mouin kè ou,  (2 foua)</strong> </p>\n<p><strong>Dousman Li  palé ou koté ou yé,</strong> </p>\n<p><strong>Nan lé mon-n ténèb Li vlé minnin ou,</strong> </p>\n<p><strong>Dousman Li palé ou:</strong> </p>\n<p><strong>Ban mouin kè ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ban mouin kè ou, di Jézu, Sovè nou, </p>\n<p>Nan mizèrikòd Li rélé ankò: </p>\n<p>Kité péché ou é tout mové zèv, </p>\n<p>Mouin té mouri pou ou, ban mouin kè ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ban mouin kè ou, di Léspri, trè dousman, </p>\n<p>Tou sa ou gingnin ban-m kinbé-l pou ou, </p>\n<p>Mouin gin anpil gras pou ba ou chak jou. </p>\n<p>Konsakré tout bagay, ban mouin kè ou. </p>\n','Ban-mouin-ke-ou-di-Bon-Dieu-na-siel-la',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1654,'73f69ab9-5bc6-49ac-81a5-563a81b67fda',0,0,'Bon Dié Voyé pitit li, rélé’l Jézu',69,'69_CE_ht','1.\nLè ou konnin bon Sovè mouin,\nLè ou aksépté amou li,\nKè ou va chanté avèk joua,\nLè ou konnin Jézu.\n\nKè:\nLè ou konnin-L,  (2 foua)\nOu-a rinmin-l kòm nou zòt yo, tou;\nKè ou va chanté louanj Li,\nLè ou konnin Jézu nou.\n\n\n2.\nJoua Li va réjoui ou chak jou,\nLumiè Li tonbé sou rout ou,\nÉ promès Li va dous pou ou,\nLè ou konnin Jézu.\n\n\n3.\nOu-a konpran mizèrikòd Li,\nOu-a santi la pè Li toujou,\nLan mò pa gingnin pè pou ou,\nLè ou konnin Jézu.\n\n\n4.\nOu-a ouè Li kondui nan bon rout,\nOu-a kité-L kondui ou toujou,\nOu-a rakonté tou sa Li fè,\nLè ou konnin Jézu.','<p><strong>1.</strong> </p>\n<p>Lè ou konnin bon Sovè mouin, </p>\n<p>Lè ou aksépté amou li, </p>\n<p>Kè ou va chanté avèk joua, </p>\n<p>Lè ou konnin Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè ou konnin-L,  (2 foua)</strong> </p>\n<p><strong>Ou-a rinmin-l kòm nou zòt yo, tou;</strong> </p>\n<p><strong>Kè ou va chanté louanj Li,</strong> </p>\n<p><strong>Lè ou konnin Jézu nou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Joua Li va réjoui ou chak jou, </p>\n<p>Lumiè Li tonbé sou rout ou, </p>\n<p>É promès Li va dous pou ou, </p>\n<p>Lè ou konnin Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou-a konpran mizèrikòd Li, </p>\n<p>Ou-a santi la pè Li toujou, </p>\n<p>Lan mò pa gingnin pè pou ou, </p>\n<p>Lè ou konnin Jézu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ou-a ouè Li kondui nan bon rout, </p>\n<p>Ou-a kité-L kondui ou toujou, </p>\n<p>Ou-a rakonté tou sa Li fè, </p>\n<p>Lè ou konnin Jézu. </p>\n','Bon-Die-Voye-pitit-li-rele\'l-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1655,'8714778f-312d-4638-a18a-061c8688d6f5',0,0,'An ro mòn Kalvè yo té planté gnou koua',71,'71_CE_ht','1.\nAn ro mòn Kalvè yo té planté gnou koua,\nKi té lévé gnou nòm méprizé.\nMin nòm sa a té pitit unik Bon-Dieu,\nKi mouri pou nou kab sové.\n\nKè:\nKris pan-n sou la koua,\nSa sé sujè chan mouin,\nKris sa a li bi-in mèvéyé.\nLi rachté sa-k pèdi,\nLi té péyé tout pri.\nGloua a Jézu, kloué sou la koua.\n\n\n2.\nLè zòm pat konnin sa yo tap fè lè sa;\nKris di: Padonnin yo, souplé.\nYo pa té konnin min-m koua ki méprizé,\nSé té la koua Kris glorifié.\n\n\n3.\nLè zòt yo adoré la koua Kris sèlman,\nSujè mouin, sé Kris sou la koua.\nSé pa nan boua la koua puisans la rété,\nMin sé Kris sèl ki bay viktoua.','<p><strong>1.</strong> </p>\n<p>An ro mòn Kalvè yo té planté gnou koua, </p>\n<p>Ki té lévé gnou nòm méprizé. </p>\n<p>Min nòm sa a té pitit unik Bon-Dieu, </p>\n<p>Ki mouri pou nou kab sové. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kris pan-n sou la koua,</strong> </p>\n<p><strong>Sa sé sujè chan mouin,</strong> </p>\n<p><strong>Kris sa a li bi-in mèvéyé.</strong> </p>\n<p><strong>Li rachté sa-k pèdi,</strong> </p>\n<p><strong>Li té péyé tout pri.</strong> </p>\n<p><strong>Gloua a Jézu, kloué sou la koua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè zòm pat konnin sa yo tap fè lè sa; </p>\n<p>Kris di: Padonnin yo, souplé. </p>\n<p>Yo pa té konnin min-m koua ki méprizé, </p>\n<p>Sé té la koua Kris glorifié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè zòt yo adoré la koua Kris sèlman, </p>\n<p>Sujè mouin, sé Kris sou la koua. </p>\n<p>Sé pa nan boua la koua puisans la rété, </p>\n<p>Min sé Kris sèl ki bay viktoua. </p>\n','An-ro-mon-Kalve-yo-te-plante-gnou-koua',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1656,'675a5503-8679-4bde-8a4e-f33fc51f24ee',0,0,'Mouin té tandé la voua Jézu',72,'72_CE_ht','1.\nMouin té tandé la voua Jézu\nKi palé mouin pou di:\nNou ki chajé, vini jouin-n mouin,\nRépozé ou sou mouin.\nMouin té vini, kòm mouin té yé,\nMizérab, fatigé!\nMouin jouin-n nan Li répo, la pè,\nKè-m vi-n kontan é gé.\n\n\n2.\nMouin té tandé la voua Jézu\nKi palé mouin pou di:\nMin ni, m-ofri ou dlo vivan,\nSé pou ou, vini pran!\nMouin té vini, é mouin té bouè\nDlo a dé mon Sovè;\nNan-m mouin té vini rafréchi,\nÉ mapé viv nan li.\n\n\n3.\nMouin té tandé la voua Jézu\nKi palé mouin pou di:\nSé mouin ki vrè lumiè lè zòm,\nGadé bò mouin tout moun.\nMouin té gadé bò Jézu-Kri,\nMouin jouin-n lumiè nan li,\nÉ sé li ki va kléré mouin\nTout vi-m juska la fin.','<p><strong>1.</strong> </p>\n<p>Mouin té tandé la voua Jézu </p>\n<p>Ki palé mouin pou di: </p>\n<p>Nou ki chajé, vini jouin-n mouin, </p>\n<p>Répozé ou sou mouin. </p>\n<p>Mouin té vini, kòm mouin té yé, </p>\n<p>Mizérab, fatigé! </p>\n<p>Mouin jouin-n nan Li répo, la pè, </p>\n<p>Kè-m vi-n kontan é gé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té tandé la voua Jézu </p>\n<p>Ki palé mouin pou di: </p>\n<p>Min ni, m-ofri ou dlo vivan, </p>\n<p>Sé pou ou, vini pran! </p>\n<p>Mouin té vini, é mouin té bouè </p>\n<p>Dlo a dé mon Sovè; </p>\n<p>Nan-m mouin té vini rafréchi, </p>\n<p>É mapé viv nan li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin té tandé la voua Jézu </p>\n<p>Ki palé mouin pou di: </p>\n<p>Sé mouin ki vrè lumiè lè zòm, </p>\n<p>Gadé bò mouin tout moun. </p>\n<p>Mouin té gadé bò Jézu-Kri, </p>\n<p>Mouin jouin-n lumiè nan li, </p>\n<p>É sé li ki va kléré mouin </p>\n<p>Tout vi-m juska la fin. </p>\n','Mouin-te-tande-la-voua-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1657,'6ea55807-bfcc-4968-8686-00d6e91cc5f6',0,0,'Nan bonte li, Jézu vi-ni',75,'75_CE_ht','1.\nNan bonté li, Jézu vi-ni\nPou rachté nan-m mouin pa la gras;\nNan bafon péché ak la ront\nKonpasion-l lévé mouin.\n\nKè:\nNan té danjé li soutni mouin,\nAk min-l bi-in dous li lévé mouin\nSòt nan nouasè, vi-n nan lumiè,\nMap adoré-l li lévé mouin.\n\n\n2.\nDépi lontan li rélé mouin,\nAvan kè mouin té répanti.\nMin lè-m kinbé-l nan paròl li\nAk padon L-lévé mouin.\n\n\n3.\nAnpil pikan té pèsé fron-l,\nDé klou térib té pèsé min-l;\nMin lè mouin bandonné péché\nAmou li lévé ouin.\n\n\n4.\nKoul yé a min-m, an lè bi-in ro\nNan-m mouin apé joui gnou répo.\nKi jan? pouki? Mouin pa konnin\nKouman li lévé mouin.','<p><strong>1.</strong> </p>\n<p>Nan bonté li, Jézu vi-ni </p>\n<p>Pou rachté nan-m mouin pa la gras; </p>\n<p>Nan bafon péché ak la ront </p>\n<p>Konpasion-l lévé mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan té danjé li soutni mouin,</strong> </p>\n<p><strong>Ak min-l bi-in dous li lévé mouin</strong> </p>\n<p><strong>Sòt nan nouasè, vi-n nan lumiè,</strong> </p>\n<p><strong>Map adoré-l li lévé mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dépi lontan li rélé mouin, </p>\n<p>Avan kè mouin té répanti. </p>\n<p>Min lè-m kinbé-l nan paròl li </p>\n<p>Ak padon L-lévé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Anpil pikan té pèsé fron-l, </p>\n<p>Dé klou térib té pèsé min-l; </p>\n<p>Min lè mouin bandonné péché </p>\n<p>Amou li lévé ouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Koul yé a min-m, an lè bi-in ro </p>\n<p>Nan-m mouin apé joui gnou répo. </p>\n<p>Ki jan? pouki? Mouin pa konnin </p>\n<p>Kouman li lévé mouin. </p>\n','Nan-bonte-li-Jezu-vi-ni',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1658,'dc7f86db-bf41-4830-8778-e69b975b132f',0,0,'Lè Jézu mouri sou Kalvè',100,'100_CE_ht','1.\nLè Jézu mouri sou Kalvè,\nLè Kris mouri sé té pou mouin.\nMouin mandé-m, èské m-a konnin\nKonbi-in mouin doué, konbi-in mouin doué?\n\nKè:\nKonbi-in mouin doué lamou san fin?\nKonbi-in mouin doué, ka Kris pou mouin?\nMouin konnin sa li fè pou mouin;\nMouin pa ka di konbi-in mouin doué.\n\n\n2.\nSovè mouin, nan Jétsémani,\nTé pasé pi gran agoni.\nSé pou mouin soufrans sa té yé,\nKouman pou-m di konbi-in mouin doué?\n\n\n3.\nSé té amou li gin pou mouin\nKi fè-l soufri gnou mò onté.\nLè tout san kò li té koulé,\nLi sové mouin, konbi-in mouin doué?\n\n\n4.\nLè m-a rivé nan sièl la tou,\nRété avèk li pou toujou,\nMouin mèt palé di mil anné;\nMouin pap fi-n di konbi-in mouin doué.','<p><strong>1.</strong> </p>\n<p>Lè Jézu mouri sou Kalvè, </p>\n<p>Lè Kris mouri sé té pou mouin. </p>\n<p>Mouin mandé-m, èské m-a konnin </p>\n<p>Konbi-in mouin doué, konbi-in mouin doué? </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konbi-in mouin doué lamou san fin?</strong> </p>\n<p><strong>Konbi-in mouin doué, ka Kris pou mouin?</strong> </p>\n<p><strong>Mouin konnin sa li fè pou mouin;</strong> </p>\n<p><strong>Mouin pa ka di konbi-in mouin doué.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sovè mouin, nan Jétsémani, </p>\n<p>Té pasé pi gran agoni. </p>\n<p>Sé pou mouin soufrans sa té yé, </p>\n<p>Kouman pou-m di konbi-in mouin doué? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé té amou li gin pou mouin </p>\n<p>Ki fè-l soufri gnou mò onté. </p>\n<p>Lè tout san kò li té koulé, </p>\n<p>Li sové mouin, konbi-in mouin doué? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè m-a rivé nan sièl la tou, </p>\n<p>Rété avèk li pou toujou, </p>\n<p>Mouin mèt palé di mil anné; </p>\n<p>Mouin pap fi-n di konbi-in mouin doué. </p>\n','Le-Jezu-mouri-sou-Kalve',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1659,'efc3f155-9864-4256-a819-53104756a3b5',0,0,'Gran matin nan jadin',139,'139_CE_ht','1.\nGran matin nan jadin,\nZouazo chanté, flè yo louvri.\nKi moun kap maché la?\nSé jézu, lé Ségnè.\n\n\n2.\nLi viv pou rinmin nou,\nAlélouya!  Alélouya!\nLi sé Roua nan sièl la,\nJézu-Kri, lé Ségnè.','<p><strong>1.</strong> </p>\n<p>Gran matin nan jadin, </p>\n<p>Zouazo chanté, flè yo louvri. </p>\n<p>Ki moun kap maché la? </p>\n<p>Sé jézu, lé Ségnè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li viv pou rinmin nou, </p>\n<p>Alélouya!  Alélouya! </p>\n<p>Li sé Roua nan sièl la, </p>\n<p>Jézu-Kri, lé Ségnè. </p>\n','Gran-matin-nan-jadin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1660,'88be55bd-e4d3-464b-8bcd-4e62b5ccd3a2',0,0,'Min Jézu yo janti pou fè tout moun biin',143,'143_CE_ht','1.\nMin Jézu yo janti pou fè tout moun biin\nBénie ti pitit yo,soulajé soufrans\nLavé pié ki bouké, kinbin sa ki fèb\nMin Jézu yo janti pou fè tout moun biin\n\n2.\nPran min mouin yo, Jézu, pou travay ou\nFè yo fò é janti, bon nan sa yo fè\nFè-m gadé ou, Jézu, pou-m vi-n janti tou\nJus tan mouin plin amou pou fè sèvis ou','<p><strong>1.</strong> </p>\n<p>Min Jézu yo janti pou fè tout moun biin </p>\n<p>Bénie ti pitit yo,soulajé soufrans </p>\n<p>Lavé pié ki bouké, kinbin sa ki fèb </p>\n<p>Min Jézu yo janti pou fè tout moun biin </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pran min mouin yo, Jézu, pou travay ou </p>\n<p>Fè yo fò é janti, bon nan sa yo fè </p>\n<p>Fè-m gadé ou, Jézu, pou-m vi-n janti tou </p>\n<p>Jus tan mouin plin amou pou fè sèvis ou </p>\n','Min-Jezu-yo-janti-pou-fe-tout-moun-biin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1661,'87621dc5-c6a9-42dd-82b5-ae70242817a4',0,0,'N-ap maché ak Jézu',7,'7_MJ_ht','1.\nN-ap maché ak Jézu,\nMaché nan chémin, juska la fin.\nN-ap maché ak Jézu,\nMaché ak Jézuz sèl.','<p><strong>1.</strong> </p>\n<p>N-ap maché ak Jézu, </p>\n<p>Maché nan chémin, juska la fin. </p>\n<p>N-ap maché ak Jézu, </p>\n<p>Maché ak Jézuz sèl. </p>\n','N-ap-mache-ak-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1662,'7f1f0b4d-d476-4b52-b89e-1a3662d837f5',0,0,'Nap kanpé douat pou nou chanté',13,'13_MJ_ht','1.\nNap kanpé douat pou nou chanté\nNap chita trankil pou kouté\nNap bésé tèt nou pou nou prié\nNou konnin nou pa doué palé.','<p><strong>1.</strong> </p>\n<p>Nap kanpé douat pou nou chanté </p>\n<p>Nap chita trankil pou kouté </p>\n<p>Nap bésé tèt nou pou nou prié </p>\n<p>Nou konnin nou pa doué palé. </p>\n','Nap-kanpe-douat-pou-nou-chante',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1663,'f48f61a3-cc20-44e5-9ed7-cdd2bc8be270',0,0,'Il Est Un Ami Fidèle',325,'325_CE_fr','1.\nIl est un Ami fidèle\nQui chérit les enfants,\nSans se lasser, appelle\nLes petits et les grands,\nQui sur eux à toute heure\nSe penche, plein d\'amour,\nUn Ami qui demeure\nLe même chaque jour…\n\n\n2.\nIl est un riant rivage\nOù nous attend Jésus,\nOù le bruit de l\'orage\nNe nous troublera plus.\nLà cessent nos alarmes;\nTout sera joie et paix;\nPlus de péché, les larmes\nN\'y couleront jamais.\n\n\n3.\nIl existe une couronne\nPour les enfants pieux,\nQui pour toujours rayonne\nEt resplendit sur eux,\nCéleste diadème\nAux éclatants fleurons\nQue le Seigneur Lui-même\nPosera sur leurs fronts!\n\n\n4.\nIl est un divin cantique\nAux sons mélodieux\nQue le choeur angélique\nEntonne dans les cieux;\nCe chant suave et tendre\nEn l\'honneur de l\'Agneau,\nQu\'il fera bon l\'entendre!\nC\'est de tous le plus beau!','<p><strong>1.</strong> </p>\n<p>Il est un Ami fidèle </p>\n<p>Qui chérit les enfants, </p>\n<p>Sans se lasser, appelle </p>\n<p>Les petits et les grands, </p>\n<p>Qui sur eux à toute heure </p>\n<p>Se penche, plein d&#39;amour, </p>\n<p>Un Ami qui demeure </p>\n<p>Le même chaque jour… </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il est un riant rivage </p>\n<p>Où nous attend Jésus, </p>\n<p>Où le bruit de l&#39;orage </p>\n<p>Ne nous troublera plus. </p>\n<p>Là cessent nos alarmes; </p>\n<p>Tout sera joie et paix; </p>\n<p>Plus de péché, les larmes </p>\n<p>N&#39;y couleront jamais. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il existe une couronne </p>\n<p>Pour les enfants pieux, </p>\n<p>Qui pour toujours rayonne </p>\n<p>Et resplendit sur eux, </p>\n<p>Céleste diadème </p>\n<p>Aux éclatants fleurons </p>\n<p>Que le Seigneur Lui-même </p>\n<p>Posera sur leurs fronts! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il est un divin cantique </p>\n<p>Aux sons mélodieux </p>\n<p>Que le choeur angélique </p>\n<p>Entonne dans les cieux; </p>\n<p>Ce chant suave et tendre </p>\n<p>En l&#39;honneur de l&#39;Agneau, </p>\n<p>Qu&#39;il fera bon l&#39;entendre! </p>\n<p>C&#39;est de tous le plus beau! </p>\n','Il-Est-Un-Ami-Fidele',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1664,'a9149abe-9699-40ea-b7c1-1b8a41c40b9d',0,0,'Nan Pouin Lan Mo! Krétiin Yo Pa Kab Pèdu',9,'9_CE_ht','1.\nNan pouin lan mò! Krétin-in yo pa kab pèdu;\nParol Bon-Dieu asuré salu mouin.\nMouin té pèdu, koul yé a mouin sové,\nOui, mouin sové nan Kris, Sovè divin.\n\nKè\nNan pouin lan mò! Sé la gloua, Alélouya!\nPitit Bon-Dieu nan plas mouin té soufri;\nLi té pran plas mouin pou mouin kapab sové,\nÉ li té sové mouin nan tout gras li.\n\n\n2.\nNan pouin lan mò! Péché mouin yo padonnin\nMouin asuré puiské Jézu mouri.\nFòs Satan ki té donté mouin fi-n krazé,\nJézu vivan pou kinbé-m avèk li.\n\n\n3.\nNan pouin lan mò, paské lè kò-m ap mouri,\nNan-m mouin apé monté tou douat nan sièl;\nAk tout frè yo ma rété nan prézans li,\nMouin té konfié-m nan li, Sovè étènèl.','<p><strong>1.</strong> </p>\n<p>Nan pouin lan mò! Krétin-in yo pa kab pèdu; </p>\n<p>Parol Bon-Dieu asuré salu mouin. </p>\n<p>Mouin té pèdu, koul yé a mouin sové, </p>\n<p>Oui, mouin sové nan Kris, Sovè divin. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Nan pouin lan mò! Sé la gloua, Alélouya!</strong> </p>\n<p><strong>Pitit Bon-Dieu nan plas mouin té soufri;</strong> </p>\n<p><strong>Li té pran plas mouin pou mouin kapab sové,</strong> </p>\n<p><strong>É li té sové mouin nan tout gras li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan pouin lan mò! Péché mouin yo padonnin </p>\n<p>Mouin asuré puiské Jézu mouri. </p>\n<p>Fòs Satan ki té donté mouin fi-n krazé, </p>\n<p>Jézu vivan pou kinbé-m avèk li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan pouin lan mò, paské lè kò-m ap mouri, </p>\n<p>Nan-m mouin apé monté tou douat nan sièl; </p>\n<p>Ak tout frè yo ma rété nan prézans li, </p>\n<p>Mouin té konfié-m nan li, Sovè étènèl. </p>\n','Nan-Pouin-Lan-Mo!-Kretiin-Yo-Pa-Kab-Pedu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1665,'7f78a797-1a2e-4243-868a-f6b6e39f7a33',0,0,'Si ou vlé kasé tout kòd vié péché',3,'3_CE_ht','1.\nSi ou vlé kasé tout kòd vié péché,\nGin fòs nan san Kris,  (2 foua)\nSi ou vlé vinkè sou tout méchansté,\nGin fòs nan gnou sèl sakrifis.\n\nKè\nOui, gin fòs, fòs, oui gin fòs anpil\nNan san Kris Krusifié,\nOui, gin fòs, fòs, oui gin fòs anpil\nNan présié san Kris, Lagno Bon-Dieu.\n\n\n2.\nSi ou vlé kité pasion ak lògèy,\nGin fòs nan san Kris,  (2 foua)\nSous Kalvè a kab lavé tout péché,\nGin fòs nan gnou sèl sakrifis.\n\n\n3.\nSi ou vlé jété tout féblès la chè,\nGin fòs nan san Kris,  (2 foua)\nBon zèv ou pap fè ou chapé lanfè,\nGin fòs nan gnou sèl sakrifis.\n\n\n4.\nSi ou vlé sèvi Bon-Dieu fidèlman,\nGin fòs nan san Kris,  (2 foua)\nMoun ki chanté gloua li étènelman,\nGin fòs nan gnou sèl sakrifis.','<p><strong>1.</strong> </p>\n<p>Si ou vlé kasé tout kòd vié péché, </p>\n<p>Gin fòs nan san Kris,  (2 foua) </p>\n<p>Si ou vlé vinkè sou tout méchansté, </p>\n<p>Gin fòs nan gnou sèl sakrifis. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Oui, gin fòs, fòs, oui gin fòs anpil</strong> </p>\n<p><strong>Nan san Kris Krusifié,</strong> </p>\n<p><strong>Oui, gin fòs, fòs, oui gin fòs anpil</strong> </p>\n<p><strong>Nan présié san Kris, Lagno Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si ou vlé kité pasion ak lògèy, </p>\n<p>Gin fòs nan san Kris,  (2 foua) </p>\n<p>Sous Kalvè a kab lavé tout péché, </p>\n<p>Gin fòs nan gnou sèl sakrifis. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ou vlé jété tout féblès la chè, </p>\n<p>Gin fòs nan san Kris,  (2 foua) </p>\n<p>Bon zèv ou pap fè ou chapé lanfè, </p>\n<p>Gin fòs nan gnou sèl sakrifis. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si ou vlé sèvi Bon-Dieu fidèlman, </p>\n<p>Gin fòs nan san Kris,  (2 foua) </p>\n<p>Moun ki chanté gloua li étènelman, </p>\n<p>Gin fòs nan gnou sèl sakrifis. </p>\n','Si-ou-vle-kase-tout-kod-vie-peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1666,'c0c02ff5-f3c1-4ef5-bb0c-bc1fd55b0c6f',0,0,'Segne, Fe Pitit Ou Pi Sanble Ou Chak Jou',19,'19_CE_ht','1.\nSégnè, fè pitit ou pi sanblé ou chak jou;\nBan mouin plus Sint-Espri, pou mouin pa fayi;\nBan mouin plus asurans, sé fòs mouin ou yé,\nKon sa ma rét kanpé dévan tout danjé.\n\n\n2.\nFè-m konpran-n Jézu-Kri, fè-m konpran-n lan mò li,\nSé li sèl pou mouin ouè nan tou sa map fè,\nJan li té obéi, sa li té soufri,\nTou sa li té subi pou-m té ka frè li.\n\n\n3.\nBan mouin pou-m kab fidèl, gnou kè ki pa rébèl,\nGnou kè toujou trankil, min-m éprèv anpil,\nGnou kè ki pa anflé, é ki pap tranblé,\nGnou kè sinp, gnou kè dou, ki plin ak amou.','<p><strong>1.</strong> </p>\n<p>Ségnè, fè pitit ou pi sanblé ou chak jou; </p>\n<p>Ban mouin plus Sint-Espri, pou mouin pa fayi; </p>\n<p>Ban mouin plus asurans, sé fòs mouin ou yé, </p>\n<p>Kon sa ma rét kanpé dévan tout danjé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fè-m konpran-n Jézu-Kri, fè-m konpran-n lan mò li, </p>\n<p>Sé li sèl pou mouin ouè nan tou sa map fè, </p>\n<p>Jan li té obéi, sa li té soufri, </p>\n<p>Tou sa li té subi pou-m té ka frè li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ban mouin pou-m kab fidèl, gnou kè ki pa rébèl, </p>\n<p>Gnou kè toujou trankil, min-m éprèv anpil, </p>\n<p>Gnou kè ki pa anflé, é ki pap tranblé, </p>\n<p>Gnou kè sinp, gnou kè dou, ki plin ak amou. </p>\n','Segne-Fe-Pitit-Ou-Pi-Sanble-Ou-Chak-Jou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1667,'6b414c94-a82f-42b9-9402-42172d1d4ba1',0,0,'Kè Mouin Ap Chanté Kantik Chak Jou',35,'35_CE_ht','1.\nKè mouin ap chanté kantik chak jou\nPaské Jézu pa pi louin;\nLi di mouin kon sa: Mouin avèk ou,\nOu pa gin doua pè angnin.\n\nKè:\nJézu, Jézu, Jézu,\nNon pi dous pou mouin,\nMa chanté amou li.\nKè-m kontan , m-pa pè angnin.\n\n\n2.\nMouin pa té manké kon-n fé rébèl\nKont tout pasians Jézu-Kri;\nJodi a mouin gingnin asurans,\nSé té nan plas mouin l-mouri.\n\n\n3.\nO, ki gran amou ki fè li vlé\nPadonnin mouin tout péché!\nO, ségnè, fè-m rai tout péché,\nDévan ou pou mouin maché.\n\n\n4.\nGnou jou l-a vini pou résévoua-m\nNan kay-l nan si bèl sièl la.\nMa kité tè sa ak gnou gran joua;\nMa ouè li an ro jou sa.','<p><strong>1.</strong> </p>\n<p>Kè mouin ap chanté kantik chak jou </p>\n<p>Paské Jézu pa pi louin; </p>\n<p>Li di mouin kon sa: Mouin avèk ou, </p>\n<p>Ou pa gin doua pè angnin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu, Jézu, Jézu,</strong> </p>\n<p><strong>Non pi dous pou mouin,</strong> </p>\n<p><strong>Ma chanté amou li.</strong> </p>\n<p><strong>Kè-m kontan , m-pa pè angnin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin pa té manké kon-n fé rébèl </p>\n<p>Kont tout pasians Jézu-Kri; </p>\n<p>Jodi a mouin gingnin asurans, </p>\n<p>Sé té nan plas mouin l-mouri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O, ki gran amou ki fè li vlé </p>\n<p>Padonnin mouin tout péché! </p>\n<p>O, ségnè, fè-m rai tout péché, </p>\n<p>Dévan ou pou mouin maché. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou jou l-a vini pou résévoua-m </p>\n<p>Nan kay-l nan si bèl sièl la. </p>\n<p>Ma kité tè sa ak gnou gran joua; </p>\n<p>Ma ouè li an ro jou sa. </p>\n','Ke-Mouin-Ap-Chante-Kantik-Chak-Jou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1668,'6a280c66-c4af-47c5-9bd4-0ad581cc9211',0,0,'Pa kite mouin, O bon Sove',38,'38_CE_ht','1.\nPa kité mouin, O bon Sovè,\nKouté krié mouin;\nKon ou okupé lé zòt yo,\nPa pasé louin mouin.\n\nKè:\nSovè, Sovè,\nKouté krié mouin;\nKon ou okupé lé zòt yo,\nPa pasé louin mouin.\n\n\n2.\nDévan tròn ou ban-m padon ou,\nBan-m gnou dous fréchè;\nMin mouin koubé nan prézans ou\nÉdé-m pou mouin kouè.\n\n\n3.\nMouin konfié-m sèl nan mérit ou;\nM-ta gadé ou nan fas;\nGéri kè mouin ki nan tristès,\nKinbé-m nan la gras.\n\n\n4.\nOu sé sous pou konsolé mouin,\nMouin vlé rét fidèl;\nMouin rinmin ou pasé tout moun\nSou tè é nan sièl.','<p><strong>1.</strong> </p>\n<p>Pa kité mouin, O bon Sovè, </p>\n<p>Kouté krié mouin; </p>\n<p>Kon ou okupé lé zòt yo, </p>\n<p>Pa pasé louin mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sovè, Sovè,</strong> </p>\n<p><strong>Kouté krié mouin;</strong> </p>\n<p><strong>Kon ou okupé lé zòt yo,</strong> </p>\n<p><strong>Pa pasé louin mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dévan tròn ou ban-m padon ou, </p>\n<p>Ban-m gnou dous fréchè; </p>\n<p>Min mouin koubé nan prézans ou </p>\n<p>Édé-m pou mouin kouè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin konfié-m sèl nan mérit ou; </p>\n<p>M-ta gadé ou nan fas; </p>\n<p>Géri kè mouin ki nan tristès, </p>\n<p>Kinbé-m nan la gras. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ou sé sous pou konsolé mouin, </p>\n<p>Mouin vlé rét fidèl; </p>\n<p>Mouin rinmin ou pasé tout moun </p>\n<p>Sou tè é nan sièl. </p>\n','Pa-kite-mouin-O-bon-Sove',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1669,'d6a469d3-c4af-4c32-aeb7-cdc1a4719e11',0,0,'Jézu mandé ou briyé pou li koté ou yé',91,'91_CE_ht','1.\nJézu mandé ou briyé pou li koté ou yé,\nTout kréti-in, bay lumiè tout patou;\nBriyé nan fanmi ou, nan travay, koté ou yé;\nBriyé pou Jézu koté ou yé.\n\nKè:\nBriyé pou Jézu koté ou yé,  (2 foua)\nLumiè ou kab édé gnou nan-m pèdu jouin-n Jézu;\nBriyé pou Jézu koté ou yé.\n\n\n2.\nMin-m lè sièl la noua, min-m lè chémin an pa trò klè,\nSé lè sa pou lumiè ou briyé.\nPou briyé, Jézu té lésé ou-min’m sou la tè;\nBriyé pou Jézu koté ou yé.\n\n\n3.\nSé gran gras ké Bon-Dieu fè ou vini gnou lumiè\nLè péché ténèb antouré ou,\nLévé lumiè a bi-in ro pou-l kab kléré tout kè;\nBriyé pou Jézu koté ou yé.\n\n\n4.\nSi ou vlé briyé gnou jou ak Jézu nan sièl la,\nLumiè ou doué lumin sou la tè;\nPaské Bon-Dieu plasé nou kou lumiè isiba;\nBriyé pou Jézu koté ou yé.','<p><strong>1.</strong> </p>\n<p>Jézu mandé ou briyé pou li koté ou yé, </p>\n<p>Tout kréti-in, bay lumiè tout patou; </p>\n<p>Briyé nan fanmi ou, nan travay, koté ou yé; </p>\n<p>Briyé pou Jézu koté ou yé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Briyé pou Jézu koté ou yé,  (2 foua)</strong> </p>\n<p><strong>Lumiè ou kab édé gnou nan-m pèdu jouin-n Jézu;</strong> </p>\n<p><strong>Briyé pou Jézu koté ou yé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m lè sièl la noua, min-m lè chémin an pa trò klè, </p>\n<p>Sé lè sa pou lumiè ou briyé. </p>\n<p>Pou briyé, Jézu té lésé ou-min’m sou la tè; </p>\n<p>Briyé pou Jézu koté ou yé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé gran gras ké Bon-Dieu fè ou vini gnou lumiè </p>\n<p>Lè péché ténèb antouré ou, </p>\n<p>Lévé lumiè a bi-in ro pou-l kab kléré tout kè; </p>\n<p>Briyé pou Jézu koté ou yé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si ou vlé briyé gnou jou ak Jézu nan sièl la, </p>\n<p>Lumiè ou doué lumin sou la tè; </p>\n<p>Paské Bon-Dieu plasé nou kou lumiè isiba; </p>\n<p>Briyé pou Jézu koté ou yé. </p>\n','Jezu-mande-ou-briye-pou-li-kote-ou-ye',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1670,'6fcfd706-4ca0-4ae7-8a8e-8a3488c56dc3',0,0,'Map travay pou Jézu nan vouayaj mouin',92,'92_CE_ht','1.\nMap travay pou Jézu nan vouayaj mouin;\nMap travay jouk la fin la jouné;\nMouin kab bronché souvan nan chémin mouin,\nMin l-a padonnin mouin si-m prié.\n\nKè:\nMap nan travay pou Jézu nan vouayaj mouin;\nMap travay ninpòt koté mouin yé;\nParol li sé lasurans mouin gingnin;\nNan travay la m-a toujou rété.\n\n\n2.\nGin dé lè kè mouin tris pasé yo tout\nA kòz danjé mouin ouè nan chémin;\nJézu di: Pa pè, mouin toujou ak ou;\nSé mouin kapab kinbé ou ak mouin.\n\n\n3.\nMouin kontan avèk Jézu, Sovè mouin;\nLap konsolé pov kè mouin toujou;\nZanmi mouin, vi-ni tou, kouè nan Jézu,\nOu-a jouin-n li fidèl pou sové ou.','<p><strong>1.</strong> </p>\n<p>Map travay pou Jézu nan vouayaj mouin; </p>\n<p>Map travay jouk la fin la jouné; </p>\n<p>Mouin kab bronché souvan nan chémin mouin, </p>\n<p>Min l-a padonnin mouin si-m prié. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Map nan travay pou Jézu nan vouayaj mouin;</strong> </p>\n<p><strong>Map travay ninpòt koté mouin yé;</strong> </p>\n<p><strong>Parol li sé lasurans mouin gingnin;</strong> </p>\n<p><strong>Nan travay la m-a toujou rété.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin dé lè kè mouin tris pasé yo tout </p>\n<p>A kòz danjé mouin ouè nan chémin; </p>\n<p>Jézu di: Pa pè, mouin toujou ak ou; </p>\n<p>Sé mouin kapab kinbé ou ak mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin kontan avèk Jézu, Sovè mouin; </p>\n<p>Lap konsolé pov kè mouin toujou; </p>\n<p>Zanmi mouin, vi-ni tou, kouè nan Jézu, </p>\n<p>Ou-a jouin-n li fidèl pou sové ou. </p>\n','Map-travay-pou-Jezu-nan-vouayaj-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1671,'2965416c-c7a2-481d-a98d-b5193afb86d2',0,0,'Ou pa bézouin gin krint ni pè',98,'98_CE_ht','1.\nOu pa bézouin gin krint ni pè,\nJézu va pran souin ou.\nPou ou, li sé gnou gran Sovè,\nJézu va pran souin ou.\n\nKè:  Jézu va pran souin ou,\nNan tout chémin, jusk a la fin,\nJézu va pran souin ou\nTou lé jou, jusk a la fin.\n\n\n2.\nLi konnin tou sa ou bézouin,\nJézu va pran souin ou;\nLi pap kité ou san angnin,\nJézu va pran souin ou.\n\n\n3.\nSi ou sèl é dékourajé,\nJézu va pran souin ou;\nLi sé toujou gnou Bon Bèjé,\nJézu va pran souin ou.\n\n\n4.\nOu pa bézouin pè pou chak jou,\nJézu va pran souin ou.\nLi la, lapé véyé sou ou,\nJézu va pran souin ou.','<p><strong>1.</strong> </p>\n<p>Ou pa bézouin gin krint ni pè, </p>\n<p>Jézu va pran souin ou. </p>\n<p>Pou ou, li sé gnou gran Sovè, </p>\n<p>Jézu va pran souin ou. </p>\n<p>Kè:  Jézu va pran souin ou, </p>\n<p>Nan tout chémin, jusk a la fin, </p>\n<p>Jézu va pran souin ou </p>\n<p>Tou lé jou, jusk a la fin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li konnin tou sa ou bézouin, </p>\n<p>Jézu va pran souin ou; </p>\n<p>Li pap kité ou san angnin, </p>\n<p>Jézu va pran souin ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ou sèl é dékourajé, </p>\n<p>Jézu va pran souin ou; </p>\n<p>Li sé toujou gnou Bon Bèjé, </p>\n<p>Jézu va pran souin ou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ou pa bézouin pè pou chak jou, </p>\n<p>Jézu va pran souin ou. </p>\n<p>Li la, lapé véyé sou ou, </p>\n<p>Jézu va pran souin ou. </p>\n','Ou-pa-bezouin-gin-krint-ni-pe',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1672,'96fb51fd-6a48-4095-bb56-9c8f42922055',0,0,'Mouin vlé rakonté tou patou',99,'99_CE_ht','1.\nMouin vlé rakonté tou patou\nKòman Jézu rinmin mouin;\nSou la tè li vi-ni gnou jou\nPou li bay vi li pou mouin.\n\nKè:\nSé gnou bèl istoua nap rakonté,\nJézu-Kri mouri pou nou.\nAn nou chanté, an nou adoré\nGnou Roua ki si rinmin nou!\n\n\n2.\nMouin té pèdi, li sové mouin,\nLi té ouè jan m-tap soufri;\nAk bra li, li antouré mouin,\nLi pran mouin sou zépol li.\n\n\n3.\nA! bi-in souvan mouin akablé –\nLa vi abat kouraj mouin;\nMin mouin pa pè, Jézu tou pré\nPou rékonfòté nan-m mouin.\n\n\n4.\nToujou Jézu sé Sovè mouin,\nÉ kan lè a va rivé\nPou-m alé ouè Rédanmtè mouin,\nSé avèk joua mouin pralé.','<p><strong>1.</strong> </p>\n<p>Mouin vlé rakonté tou patou </p>\n<p>Kòman Jézu rinmin mouin; </p>\n<p>Sou la tè li vi-ni gnou jou </p>\n<p>Pou li bay vi li pou mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé gnou bèl istoua nap rakonté,</strong> </p>\n<p><strong>Jézu-Kri mouri pou nou.</strong> </p>\n<p><strong>An nou chanté, an nou adoré</strong> </p>\n<p><strong>Gnou Roua ki si rinmin nou!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té pèdi, li sové mouin, </p>\n<p>Li té ouè jan m-tap soufri; </p>\n<p>Ak bra li, li antouré mouin, </p>\n<p>Li pran mouin sou zépol li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A! bi-in souvan mouin akablé – </p>\n<p>La vi abat kouraj mouin; </p>\n<p>Min mouin pa pè, Jézu tou pré </p>\n<p>Pou rékonfòté nan-m mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Toujou Jézu sé Sovè mouin, </p>\n<p>É kan lè a va rivé </p>\n<p>Pou-m alé ouè Rédanmtè mouin, </p>\n<p>Sé avèk joua mouin pralé. </p>\n','Mouin-vle-rakonte-tou-patou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1673,'b1acec88-bbe6-49ff-a2d5-12d579a190ca',0,0,'Anfin mouin livré volonté-m si rébè-l',102,'102_CE_ht','1.\nAnfin mouin livré volonté-m si rébè-l;\nMouin vlé pou ou, Sovè-m, pou ou-min’m sèl.\nKoul yé a, mouin vlé di nan priè-m:\nSégnè, fè volonté ou nan kè-m.\n\nKè:  Volonté Bon-Dieu dous;\nSé li min-m mouin kontan suiv;\nSégnè, volonté ou agréab;\nFè-m pi konpran-n li chak jou;\nFè-m konnin jan pou-m suiv ou;\nVolonté ou dous, li si émab.\n\n\n2.\nMouin té fi-ni bouké viv anba péché;\nChémin Satan tou rèd, tou noua li yé;\nMin bèl lumiè té kléré rout la\nLè-m té jouin-n ak Jézu pré la koua.\n\n\n3.\nO, volonté ou dous; ladan map rété;\nOu sèl konnin chémin mouin doué pasé.\nTout dézòd réglé, la pé ranpli-m;\nKon gnou ti zouazo, nan-m mouin vi-n lib.\n\n\n4.\nNan-m mouin kaché nan ou, Ségnè, pou tout tan;\nPié mouin pa pou suiv ankò tras Satan.\nKi puisans ki kab égaré mouin\nLè-m rété nan volonté Bon-Dieu?','<p><strong>1.</strong> </p>\n<p>Anfin mouin livré volonté-m si rébè-l; </p>\n<p>Mouin vlé pou ou, Sovè-m, pou ou-min’m sèl. </p>\n<p>Koul yé a, mouin vlé di nan priè-m: </p>\n<p>Ségnè, fè volonté ou nan kè-m. </p>\n<p>Kè:  Volonté Bon-Dieu dous; </p>\n<p>Sé li min-m mouin kontan suiv; </p>\n<p>Ségnè, volonté ou agréab; </p>\n<p>Fè-m pi konpran-n li chak jou; </p>\n<p>Fè-m konnin jan pou-m suiv ou; </p>\n<p>Volonté ou dous, li si émab. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té fi-ni bouké viv anba péché; </p>\n<p>Chémin Satan tou rèd, tou noua li yé; </p>\n<p>Min bèl lumiè té kléré rout la </p>\n<p>Lè-m té jouin-n ak Jézu pré la koua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O, volonté ou dous; ladan map rété; </p>\n<p>Ou sèl konnin chémin mouin doué pasé. </p>\n<p>Tout dézòd réglé, la pé ranpli-m; </p>\n<p>Kon gnou ti zouazo, nan-m mouin vi-n lib. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan-m mouin kaché nan ou, Ségnè, pou tout tan; </p>\n<p>Pié mouin pa pou suiv ankò tras Satan. </p>\n<p>Ki puisans ki kab égaré mouin </p>\n<p>Lè-m rété nan volonté Bon-Dieu? </p>\n','Anfin-mouin-livre-volonte-m-si-rebe-l',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1674,'f9de9d9c-0f55-4f60-8eaf-0d2b9e64a470',0,0,'Jézu, mouin té promèt Ou',109,'109_CE_ht','1.\nJézu, mouin té promèt Ou,\nPou-m suiv Ou jouk la fin;\nRété tou pré mouin tou tan,\nMèt mouin é Zanmi mouin.\nMouin pa pè sa kap rivé-m,\nLè Ou rété tou pré-m;\nMouin pap pèdu nan rout la,\nLè Ou maché avè-m.\n\n\n2.\nM-santi lé mon-n trò pré mouin;\nJézu, rété pi pré.\nGin dé bagay ki tanté-m;\nPou minnin-m nan péché.\nEnmi-m yo, yo toujou la,\nYo andan kè mouin tou;\nMin Jézu, Ou kab sové-m,\nRété pi pré toujou.\n\n\n3.\nKité-m tandé Ou palé\nBi-in klèman, pou-m kapab\nKonnin ké sé la voua Ou,\nMalgré tout voua péché.\nPalé pou ban-m plus konfians,\nPou-m fè tou sa Ou vlé.\nPalé, é fè-m kouté Ou\nSé Ou ki Gadi-in mouin.\n\n\n4.\nO Jézu, Ou té promèt\nTout moun yo kap suiv Ou,\nKoté Ou-a yé nan sièl la,\nSé la yo va yé tou.\nÉ Jézu, mouin té promèt\nPou-m suiv Ou tout tan,\nO ban mouin gras Ou anpil,\nMèt mouin é Zanmi mouin.\n\n\n5.\nKité-m ouè tout gras Ou yo,\nPou-m mét piè-m ladan.\nTout éspoua mouin pou suiv Ou\nSé nan fòs Ou sèlman.\nO gidé mouin, é rélé-m,\nSoutni-m jouk sa fi-ni;\nÉpi pran mouin nan sièl la,\nMèt mouin é Zanmi mouin.','<p><strong>1.</strong> </p>\n<p>Jézu, mouin té promèt Ou, </p>\n<p>Pou-m suiv Ou jouk la fin; </p>\n<p>Rété tou pré mouin tou tan, </p>\n<p>Mèt mouin é Zanmi mouin. </p>\n<p>Mouin pa pè sa kap rivé-m, </p>\n<p>Lè Ou rété tou pré-m; </p>\n<p>Mouin pap pèdu nan rout la, </p>\n<p>Lè Ou maché avè-m. </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-santi lé mon-n trò pré mouin; </p>\n<p>Jézu, rété pi pré. </p>\n<p>Gin dé bagay ki tanté-m; </p>\n<p>Pou minnin-m nan péché. </p>\n<p>Enmi-m yo, yo toujou la, </p>\n<p>Yo andan kè mouin tou; </p>\n<p>Min Jézu, Ou kab sové-m, </p>\n<p>Rété pi pré toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kité-m tandé Ou palé </p>\n<p>Bi-in klèman, pou-m kapab </p>\n<p>Konnin ké sé la voua Ou, </p>\n<p>Malgré tout voua péché. </p>\n<p>Palé pou ban-m plus konfians, </p>\n<p>Pou-m fè tou sa Ou vlé. </p>\n<p>Palé, é fè-m kouté Ou </p>\n<p>Sé Ou ki Gadi-in mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Jézu, Ou té promèt </p>\n<p>Tout moun yo kap suiv Ou, </p>\n<p>Koté Ou-a yé nan sièl la, </p>\n<p>Sé la yo va yé tou. </p>\n<p>É Jézu, mouin té promèt </p>\n<p>Pou-m suiv Ou tout tan, </p>\n<p>O ban mouin gras Ou anpil, </p>\n<p>Mèt mouin é Zanmi mouin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Kité-m ouè tout gras Ou yo, </p>\n<p>Pou-m mét piè-m ladan. </p>\n<p>Tout éspoua mouin pou suiv Ou </p>\n<p>Sé nan fòs Ou sèlman. </p>\n<p>O gidé mouin, é rélé-m, </p>\n<p>Soutni-m jouk sa fi-ni; </p>\n<p>Épi pran mouin nan sièl la, </p>\n<p>Mèt mouin é Zanmi mouin. </p>\n','Jezu-mouin-te-promet-Ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1675,'a28eeded-d355-4164-ad39-ec21b51840bf',0,0,'Kèk foua nou jouin-n tristès nan la vi nou',115,'115_CE_ht','1.\nKèk foua nou jouin-n tristès nan la vi nou,\nKèk foua kè nou gin gro fado tou;\nMin nou kab konnin ninpòt sa-k rivé,\nJézu la pou toujou.\n\nKè:\nNan pouin fado ké Jézu pa kab pòté,\nNan pouin tristès ké Li pat gingnin tou.\nMalgré jou yo kab bay la joua ou tristès,\nJézu la pou toujou.\n\n\n2.\nLè nou nan mitan problèm la vi sa,\nChajé ak travay, ak fado lou;\nNou gin konfò nan Jézu, Sovè a,\nJézu la pou toujou.\n\n\n3.\nLè nap maché nan bèl rout isi-ba,\nKon sa gin montagn difisil tou;\nNan promès Bon-Dieu nou jouin-n asurans,\nJézu la pou toujou.\n\n\n4.\nM-avèk ou toujou, Parol la di nou,\nDieu pa manké répon-n priè nou;\nKonfié ou nan Parol Li, promès Li,\nJézu la pou toujou.','<p><strong>1.</strong> </p>\n<p>Kèk foua nou jouin-n tristès nan la vi nou, </p>\n<p>Kèk foua kè nou gin gro fado tou; </p>\n<p>Min nou kab konnin ninpòt sa-k rivé, </p>\n<p>Jézu la pou toujou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan pouin fado ké Jézu pa kab pòté,</strong> </p>\n<p><strong>Nan pouin tristès ké Li pat gingnin tou.</strong> </p>\n<p><strong>Malgré jou yo kab bay la joua ou tristès,</strong> </p>\n<p><strong>Jézu la pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè nou nan mitan problèm la vi sa, </p>\n<p>Chajé ak travay, ak fado lou; </p>\n<p>Nou gin konfò nan Jézu, Sovè a, </p>\n<p>Jézu la pou toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè nap maché nan bèl rout isi-ba, </p>\n<p>Kon sa gin montagn difisil tou; </p>\n<p>Nan promès Bon-Dieu nou jouin-n asurans, </p>\n<p>Jézu la pou toujou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>M-avèk ou toujou, Parol la di nou, </p>\n<p>Dieu pa manké répon-n priè nou; </p>\n<p>Konfié ou nan Parol Li, promès Li, </p>\n<p>Jézu la pou toujou. </p>\n','Kek-foua-nou-jouin-n-tristes-nan-la-vi-nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1676,'35ad1251-820e-4c96-b3df-0450935865cd',0,0,'Gadé ti Jézu ki kouché nan krèch la',117,'117_CE_ht','1.\nGadé ti Jézu ki kouché nan krèch la,\nOtèl la réfuzé-l malgré Li té Roua;\nBèjé yo té vini pou adoré Li.\nBèl ti Ségnè Jézu ki t-apé dòmi.\n\n\n2.\nBèf yo t-apé béglé, ti Bébé lévé,\nLi ouvri bèl jé Li, min Li pat krié;\nZétoual nan sièl la té kléré figu Li;\nVi-n ouè Sovè ou ki té fèt pou mouri.\n\n\n3.\nMèsi, Ségnè Jézu, pou kado la vi,\nJou fèt ou, Noèl la, fè kè mouin réjoui;\nMouin rinmin ou, Jézu, pi plus ké janmin,\nMouin ba ou tout kè mouin pou létènité.','<p><strong>1.</strong> </p>\n<p>Gadé ti Jézu ki kouché nan krèch la, </p>\n<p>Otèl la réfuzé-l malgré Li té Roua; </p>\n<p>Bèjé yo té vini pou adoré Li. </p>\n<p>Bèl ti Ségnè Jézu ki t-apé dòmi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bèf yo t-apé béglé, ti Bébé lévé, </p>\n<p>Li ouvri bèl jé Li, min Li pat krié; </p>\n<p>Zétoual nan sièl la té kléré figu Li; </p>\n<p>Vi-n ouè Sovè ou ki té fèt pou mouri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mèsi, Ségnè Jézu, pou kado la vi, </p>\n<p>Jou fèt ou, Noèl la, fè kè mouin réjoui; </p>\n<p>Mouin rinmin ou, Jézu, pi plus ké janmin, </p>\n<p>Mouin ba ou tout kè mouin pou létènité. </p>\n','Gade-ti-Jezu-ki-kouche-nan-krech-la',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1677,'03c9c779-494d-45d9-b8f7-2c225dd51f12',0,0,'Souvan m-maché tris nan la vi-m',127,'127_CE_ht','1.\nSouvan m-maché tris nan la vi-m,\nChajé ak fado mouin;\nMin gnou moun té vi-n sékouri-m,\nMouin trouvé Bon-Dieu la.\n\nKè:\nBon-dieu la pou tandé mouin,\nPou pran fado-m ak tèt chajé-m,\nLi patajé chaj la avè-m,\nBon-Dieu la, oui, Bon-Dieu la.\n\n\n2.\nLè tout barriè ak pòt fèmin,\nNan pouin rout pou pasé;\nLè tout éspoua pèdu pou mouin,\nMouin trouvé Bon-Dieu la.\n\n\n3.\nMalgré m-gingnin anpil problèm,\nAk chaj pou mouin pòté;\nMouin gin konfians gras li kont, min-m,\nMouin konnin Bon-Dieu la.\n\nKè:\nBon-Dieu la pou fè-m kontan,\nPou chasé tout krint nan kè mouin;\nLè-m tandé voua-l mouin pa pantan,\nBon-Dieu la, Bon-dieu la.','<p><strong>1.</strong> </p>\n<p>Souvan m-maché tris nan la vi-m, </p>\n<p>Chajé ak fado mouin; </p>\n<p>Min gnou moun té vi-n sékouri-m, </p>\n<p>Mouin trouvé Bon-Dieu la. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bon-dieu la pou tandé mouin,</strong> </p>\n<p><strong>Pou pran fado-m ak tèt chajé-m,</strong> </p>\n<p><strong>Li patajé chaj la avè-m,</strong> </p>\n<p><strong>Bon-Dieu la, oui, Bon-Dieu la.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè tout barriè ak pòt fèmin, </p>\n<p>Nan pouin rout pou pasé; </p>\n<p>Lè tout éspoua pèdu pou mouin, </p>\n<p>Mouin trouvé Bon-Dieu la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Malgré m-gingnin anpil problèm, </p>\n<p>Ak chaj pou mouin pòté; </p>\n<p>Mouin gin konfians gras li kont, min-m, </p>\n<p>Mouin konnin Bon-Dieu la. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bon-Dieu la pou fè-m kontan,</strong> </p>\n<p><strong>Pou chasé tout krint nan kè mouin;</strong> </p>\n<p><strong>Lè-m tandé voua-l mouin pa pantan,</strong> </p>\n<p><strong>Bon-Dieu la, Bon-dieu la.</strong> </p>\n','Souvan-m-mache-tris-nan-la-vi-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1678,'0bb64943-659d-4c51-98ef-648537b66c9b',0,0,'An nou chanté ak tout kè',133,'133_CE_ht','1.\nAn nou chanté ak tout kè,\nGloua a Dieu, lé Kréatè.\n\nKè:  Bonté Li sé pou toujou;\nLi fidèl, Li plin amou.\n\n\n2.\nAn nou anonsé non Li,\nPaské Li sèl sé Bon-Dieu.\n\n\n3.\nSé Li ki fè solèy la\nPou bay tout moun bèl lumiè.\n\n\n4.\nBèt ak moun konté sou Li,\nMin Li bay yo nouriti.\n\n\n5.\nLi béni pèp Li chouazi\nNan dézè bò Si-naï.\n\n\n6.\nLi gingnin pitié pou nou,\nNan mizè ak féblès tou.\n\n\n7.\nAn nou chanté ak tout kè:\nGloua a Dieu, lé Kréatè.','<p><strong>1.</strong> </p>\n<p>An nou chanté ak tout kè, </p>\n<p>Gloua a Dieu, lé Kréatè. </p>\n<p>Kè:  Bonté Li sé pou toujou; </p>\n<p>Li fidèl, Li plin amou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>An nou anonsé non Li, </p>\n<p>Paské Li sèl sé Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé Li ki fè solèy la </p>\n<p>Pou bay tout moun bèl lumiè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bèt ak moun konté sou Li, </p>\n<p>Min Li bay yo nouriti. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Li béni pèp Li chouazi </p>\n<p>Nan dézè bò Si-naï. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Li gingnin pitié pou nou, </p>\n<p>Nan mizè ak féblès tou. </p>\n<p><br/> <strong>7.</strong></p>\n<p>An nou chanté ak tout kè: </p>\n<p>Gloua a Dieu, lé Kréatè. </p>\n','An-nou-chante-ak-tout-ke',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1679,'09f34c45-21d2-4f29-a443-f20674826359',0,0,'Jézu Sé Sovè Mwen',9,'9_MJ_ht','1.\nJézu sé Sovè mouin – mapé rété fèm\nMouin pap manké angnin –\nMa pé rété fèm\nKou gnou pié boua\nKi planté bò riviè-a,\nOui, mapé rété fèm.\n\nKè:\nMapé rété, mapé rété fèm  (bis)\nKou gnou pié boua\nKi planté bò riviè-a\nOui, mapé rété fèm.\n\n2.\nJézu ban mouin la vi –\nMapé rété fèm\nKaché an-ba zèl Li –\nMapé rété fèm\nKou gnou pié boua\nKi planté bò riviè-a,\nOui, mapé rété fèm.\n\n3.\nAk gnou foua ki vivan –\nMapé rété fèm\nGras Li ran-n mouin puisan –\nMapé rété fèm\nKou gnou pié boua\nKi planté bò riviè-a,\nOui, mapé rété fèm.\n\n4.\nNouri nan Paròl Li –\nMapé rété fèm\nA-lèv, jouk Li vini,\nMapé rété fèm\nKou gnou piè boua\nKi planté bò riviè-a,\nOui, mapé rété fèm.','<p><strong>1.</strong> </p>\n<p>Jézu sé Sovè mouin – mapé rété fèm </p>\n<p>Mouin pap manké angnin – </p>\n<p>Ma pé rété fèm </p>\n<p>Kou gnou pié boua </p>\n<p>Ki planté bò riviè-a, </p>\n<p>Oui, mapé rété fèm. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mapé rété, mapé rété fèm  (bis)</strong> </p>\n<p><strong>Kou gnou pié boua</strong> </p>\n<p><strong>Ki planté bò riviè-a</strong> </p>\n<p><strong>Oui, mapé rété fèm.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu ban mouin la vi – </p>\n<p>Mapé rété fèm </p>\n<p>Kaché an-ba zèl Li – </p>\n<p>Mapé rété fèm </p>\n<p>Kou gnou pié boua </p>\n<p>Ki planté bò riviè-a, </p>\n<p>Oui, mapé rété fèm. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ak gnou foua ki vivan – </p>\n<p>Mapé rété fèm </p>\n<p>Gras Li ran-n mouin puisan – </p>\n<p>Mapé rété fèm </p>\n<p>Kou gnou pié boua </p>\n<p>Ki planté bò riviè-a, </p>\n<p>Oui, mapé rété fèm. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nouri nan Paròl Li – </p>\n<p>Mapé rété fèm </p>\n<p>A-lèv, jouk Li vini, </p>\n<p>Mapé rété fèm </p>\n<p>Kou gnou piè boua </p>\n<p>Ki planté bò riviè-a, </p>\n<p>Oui, mapé rété fèm. </p>\n','Jezu-Se-Sove-Mwen',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1680,'edcc6649-f036-4399-b096-5de5ee394f21',0,0,'Mouin Maché Nan Gnou Dur Chémin',18,'18_MJ_ht','1.\nMouin maché nan gnou dur chémin,\nPèson-n pa ban-m sékou,\nFado mouin dékourajé mouin\nLi sanblé tèlman lou,\nMouin plingné dévan Jézu:\n«Mou yo pa gin amou»\nÉpi Li répon-n\nMouin ak voua si dous:\n«Mouin té bouké, mouin-min’m tou,\nLè sou Kalvè m-maché,\nKoua mouin té vini si lou\nMouin fini pa tonbé\nKrétyin, sé pou ou fidèl,\nRépo ou pa tro louin,\nPran koua ou, non,\nÉ maché avèk mouin.»\n\n2.\nSouvan sé mouin vanté tèt mouin,\nM-di: «Mouin fè gran sèvis,\nÉ pou-m suiv nan lé bon chémin\nM-fè gran sakrifis.\nMouin rénonsé si bèl bagay\nPou-m vini disip Kris»\nPui, m-tandé-L répon-n\nMouin ak voua byin tris:\n«Mouin kité bèl la gloua mouin,\nMouin rénonsé-l ak joua,\nLé mon-n pa résévoua mouin,\nYo klouré-m sou gnou koua.\nOu-min’m kab fè chémn-an\nAk min ou nan min mouin,\nPran koua ou, non,\nÉ maché avèk mouin.»\n\n3.\n«Koun-yé’a, Jézu, m-vlé suiv ou\nAvèk umilité,\nÉ si-m bézouin mouri pou ou\nMin-m sa-a pap jan-m péyé\nTout lamou ou, san mézi\nKé la mò ou montré-m»\nAk voua byin dous\nLi répon-n, Li mandé-m:\n«Si sé sèlman ti vè dlo\nMouin vlé ou bay pou mouin,\nOu byin, pòté gro fado\nKi pézé jouk la fin,\nMin-m si-m bézouin ou soufri\nÉ mouri pou mouin tou»\n«Jézu, ma pran koua mouin,\nMa fè-l pou ou!','<p><strong>1.</strong> </p>\n<p>Mouin maché nan gnou dur chémin, </p>\n<p>Pèson-n pa ban-m sékou, </p>\n<p>Fado mouin dékourajé mouin </p>\n<p>Li sanblé tèlman lou, </p>\n<p>Mouin plingné dévan Jézu: </p>\n<p>«Mou yo pa gin amou» </p>\n<p>Épi Li répon-n </p>\n<p>Mouin ak voua si dous: </p>\n<p>«Mouin té bouké, mouin-min’m tou, </p>\n<p>Lè sou Kalvè m-maché, </p>\n<p>Koua mouin té vini si lou </p>\n<p>Mouin fini pa tonbé </p>\n<p>Krétyin, sé pou ou fidèl, </p>\n<p>Répo ou pa tro louin, </p>\n<p>Pran koua ou, non, </p>\n<p>É maché avèk mouin.» </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan sé mouin vanté tèt mouin, </p>\n<p>M-di: «Mouin fè gran sèvis, </p>\n<p>É pou-m suiv nan lé bon chémin </p>\n<p>M-fè gran sakrifis. </p>\n<p>Mouin rénonsé si bèl bagay </p>\n<p>Pou-m vini disip Kris» </p>\n<p>Pui, m-tandé-L répon-n </p>\n<p>Mouin ak voua byin tris: </p>\n<p>«Mouin kité bèl la gloua mouin, </p>\n<p>Mouin rénonsé-l ak joua, </p>\n<p>Lé mon-n pa résévoua mouin, </p>\n<p>Yo klouré-m sou gnou koua. </p>\n<p>Ou-min’m kab fè chémn-an </p>\n<p>Ak min ou nan min mouin, </p>\n<p>Pran koua ou, non, </p>\n<p>É maché avèk mouin.» </p>\n<p><br/> <strong>3.</strong></p>\n<p>«Koun-yé’a, Jézu, m-vlé suiv ou </p>\n<p>Avèk umilité, </p>\n<p>É si-m bézouin mouri pou ou </p>\n<p>Min-m sa-a pap jan-m péyé </p>\n<p>Tout lamou ou, san mézi </p>\n<p>Ké la mò ou montré-m» </p>\n<p>Ak voua byin dous </p>\n<p>Li répon-n, Li mandé-m: </p>\n<p>«Si sé sèlman ti vè dlo </p>\n<p>Mouin vlé ou bay pou mouin, </p>\n<p>Ou byin, pòté gro fado </p>\n<p>Ki pézé jouk la fin, </p>\n<p>Min-m si-m bézouin ou soufri </p>\n<p>É mouri pou mouin tou» </p>\n<p>«Jézu, ma pran koua mouin, </p>\n<p>Ma fè-l pou ou! </p>\n','Mouin-Mache-Nan-Gnou-Dur-Chemin',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1681,'d84a3249-ceeb-486d-bb48-6ae2ff055996',0,0,'Nom Saj La Bati Kay Li Sou Roch La',31,'31_MJ_ht','1.\nNon-m saj la bati kay li sou roch la, (3 foua)\nÉ la pli kòmansé tonbé.\nLa pli désan-n, é flèv yo té monté (3 foua)\nÉ kay la sou roch la rété fè-m.\n\n2.\nNon-m fou a bati kay li sou sab la, (3 foua)\nÉ la pli kòmansé tonbé.\nLa pli désan-n é flèv yo té monté  (3 foua)\nÉ kay la sou sab tonbé PLAT.\n\n3.\nAn nou bati sou Ségnè Jézu-Kri (3 foua)\nÉ gras li kòmansé tonbé.\nGras li désan-n lè priè yo monté (3 foua)\nAn nou bati nou sou Kris.','<p><strong>1.</strong> </p>\n<p>Non-m saj la bati kay li sou roch la, (3 foua) </p>\n<p>É la pli kòmansé tonbé. </p>\n<p>La pli désan-n, é flèv yo té monté (3 foua) </p>\n<p>É kay la sou roch la rété fè-m. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non-m fou a bati kay li sou sab la, (3 foua) </p>\n<p>É la pli kòmansé tonbé. </p>\n<p>La pli désan-n é flèv yo té monté  (3 foua) </p>\n<p>É kay la sou sab tonbé PLAT. </p>\n<p><br/> <strong>3.</strong></p>\n<p>An nou bati sou Ségnè Jézu-Kri (3 foua) </p>\n<p>É gras li kòmansé tonbé. </p>\n<p>Gras li désan-n lè priè yo monté (3 foua) </p>\n<p>An nou bati nou sou Kris. </p>\n','Nom-Saj-La-Bati-Kay-Li-Sou-Roch-La',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1682,'f936d558-3fff-4be3-a8f6-52ff9b867494',0,0,'Ou Pa Kab Alé Nan Siel La',48,'48_MJ_ht','1.\nOu pa kab alé nan sièl\nSi ou viv nan péché isi ba. (bis 2 lign)\nSé pou ou vi-n jouin-n ak Jézu-Kri.\n\nKè:\nSé pou ou vi-n jouin-n Jézu-Kri  (3 foua)\n\n2.\nOu pa kab alé nan sièl la\nSi ou sèvi Satan isi-ba. (2 lign 2 foua)\nSé pou ou vi-n jouin-n ak Jézu-Kri.\n\n3.\nOu pa kab alé nan sièl la,\nSi ou pa lavé nan bèl sous la. (bis 2 lign)\nSé pou ou vi-n jouin-n ak Jézu-Kri.','<p><strong>1.</strong> </p>\n<p>Ou pa kab alé nan sièl </p>\n<p>Si ou viv nan péché isi ba. (bis 2 lign) </p>\n<p>Sé pou ou vi-n jouin-n ak Jézu-Kri. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé pou ou vi-n jouin-n Jézu-Kri  (3 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou pa kab alé nan sièl la </p>\n<p>Si ou sèvi Satan isi-ba. (2 lign 2 foua) </p>\n<p>Sé pou ou vi-n jouin-n ak Jézu-Kri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou pa kab alé nan sièl la, </p>\n<p>Si ou pa lavé nan bèl sous la. (bis 2 lign) </p>\n<p>Sé pou ou vi-n jouin-n ak Jézu-Kri. </p>\n','Ou-Pa-Kab-Ale-Nan-Siel-La',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1683,'e7e4b501-5399-4c1d-9792-e32da2cc9cb6',0,0,'Mèvéyé, Abondan Gras Bon-Dieu',57,'57_MJ_ht','1.\nMèvéyé, abandon gras Bon-Dieu,\nGras ki padonnin é purifié,\nGras, gras, gras Bon-Dieu,\nGras Li té vèsé la pou nou tout.\n\nKè:\nGras, gras, gras Bon-Dieu,\nGras ki padonnin é purifié,\nGras, gras, gras Bon-Dieu,\nGras ki pi gran pasé tout péché.\n\n2.\nPéché ki gran kòm gro vag la mè,\nMénasé nan-m nou pou nou pèdu,\nGras ki pi gran, oui, gras Li si chè,\nMontré nou gnou réfuj nan koua Li.\n\n3.\nMèvéyé gras Li ki san mézu,\nBon-Dieu bay li pou tout moun ki kouè.\nOu, ki gnou jou vlé ouè fas Jézu,\nÈské ou-a résévoua gras Kalvè.','<p><strong>1.</strong> </p>\n<p>Mèvéyé, abandon gras Bon-Dieu, </p>\n<p>Gras ki padonnin é purifié, </p>\n<p>Gras, gras, gras Bon-Dieu, </p>\n<p>Gras Li té vèsé la pou nou tout. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gras, gras, gras Bon-Dieu,</strong> </p>\n<p><strong>Gras ki padonnin é purifié,</strong> </p>\n<p><strong>Gras, gras, gras Bon-Dieu,</strong> </p>\n<p><strong>Gras ki pi gran pasé tout péché.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Péché ki gran kòm gro vag la mè, </p>\n<p>Ménasé nan-m nou pou nou pèdu, </p>\n<p>Gras ki pi gran, oui, gras Li si chè, </p>\n<p>Montré nou gnou réfuj nan koua Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mèvéyé gras Li ki san mézu, </p>\n<p>Bon-Dieu bay li pou tout moun ki kouè. </p>\n<p>Ou, ki gnou jou vlé ouè fas Jézu, </p>\n<p>Èské ou-a résévoua gras Kalvè. </p>\n','Meveye-Abondan-Gras-Bon-Dieu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1684,'b347776e-c9ca-4afb-9cc4-63559476c072',0,0,'Chak Moman Sovè Mouin Minnin-m',58,'58_MJ_ht','1.\nChak moman Sovè mouin minnin-m;\nKi sa mouin doué tan-n ankò?\nMouin pa jan-m kab douté amou-L,\nPou mouin, sa vo plus ké lò.\nLa pè sièl ak konsolasion\nAp la foua mouin jouin-n nan Li;\nÉ nan tout bagay ki rivé-m\nMouin toujou ouè Jézu-Kri.  (bis 2 lign yo)\n\n2.\nChak moman Sovè mouin minnin-m;\nSouri Li kléré chémin-m.\nLi ban-m gras pou tout éprèv mouin,\nPin ki sòti nan Li-min’m.\nMalgré pié mouin souvan glisé\nÉ nan-m mouin konnin souaf tou,\nSòti nan Ròch la dévan mouin\nSous la jayi nuit kon jou.  (bis 2 lign yo)\n\n3.\nChak moman Sovè mouin minnin-m,\nKè-m ranpli ak pè pafèt;\nAmou-L va toujou anbrasé-m\nMin-m nan soufrans ak dizèt;\nLè nan-m mouin monté nan sièl la\nKoté Jézu ap tan-n mouin,\nChan sa ma va chanté toujou:\nLi va minnin-m jouk la fin.  (bis 2 lign yo)','<p><strong>1.</strong> </p>\n<p>Chak moman Sovè mouin minnin-m; </p>\n<p>Ki sa mouin doué tan-n ankò? </p>\n<p>Mouin pa jan-m kab douté amou-L, </p>\n<p>Pou mouin, sa vo plus ké lò. </p>\n<p>La pè sièl ak konsolasion </p>\n<p>Ap la foua mouin jouin-n nan Li; </p>\n<p>É nan tout bagay ki rivé-m </p>\n<p>Mouin toujou ouè Jézu-Kri.  (bis 2 lign yo) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chak moman Sovè mouin minnin-m; </p>\n<p>Souri Li kléré chémin-m. </p>\n<p>Li ban-m gras pou tout éprèv mouin, </p>\n<p>Pin ki sòti nan Li-min’m. </p>\n<p>Malgré pié mouin souvan glisé </p>\n<p>É nan-m mouin konnin souaf tou, </p>\n<p>Sòti nan Ròch la dévan mouin </p>\n<p>Sous la jayi nuit kon jou.  (bis 2 lign yo) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chak moman Sovè mouin minnin-m, </p>\n<p>Kè-m ranpli ak pè pafèt; </p>\n<p>Amou-L va toujou anbrasé-m </p>\n<p>Min-m nan soufrans ak dizèt; </p>\n<p>Lè nan-m mouin monté nan sièl la </p>\n<p>Koté Jézu ap tan-n mouin, </p>\n<p>Chan sa ma va chanté toujou: </p>\n<p>Li va minnin-m jouk la fin.  (bis 2 lign yo) </p>\n','Chak-Moman-Sove-Mouin-Minnin-m',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1685,'c8701cff-6674-4cb9-a657-871d631e7e31',0,0,'Mwen Konnin Yon Non',71,'71_MJ_ht','1.\nMouin konnin gnou Non,\nGnou glorifié Non,\nKé zanj yo té pòté bay nou.\nSièl la tap kléré lè yo té palé.\nNan sa ki si bèl, si dou.\n\nKè:\nNan sa ki plin gloua,\nNon mèvéyé sa.\nNan péché kap délivré nou.\nKèl glorifié Non, kèl mèvéyé Non\nLé Non suprèm, sé Jézu.\n\n2.\nMouin rinmin Non sa,\nNon mèvéyé sa.\nKi pi bèl pasé tout non yo.\nDépi-m té konnin amou-L pou pèp Li,\nMouin té bay Li tout la vi-m.','<p><strong>1.</strong> </p>\n<p>Mouin konnin gnou Non, </p>\n<p>Gnou glorifié Non, </p>\n<p>Ké zanj yo té pòté bay nou. </p>\n<p>Sièl la tap kléré lè yo té palé. </p>\n<p>Nan sa ki si bèl, si dou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan sa ki plin gloua,</strong> </p>\n<p><strong>Non mèvéyé sa.</strong> </p>\n<p><strong>Nan péché kap délivré nou.</strong> </p>\n<p><strong>Kèl glorifié Non, kèl mèvéyé Non</strong> </p>\n<p><strong>Lé Non suprèm, sé Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin rinmin Non sa, </p>\n<p>Non mèvéyé sa. </p>\n<p>Ki pi bèl pasé tout non yo. </p>\n<p>Dépi-m té konnin amou-L pou pèp Li, </p>\n<p>Mouin té bay Li tout la vi-m. </p>\n','Mwen-Konnin-Yon-Non',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1686,'f6ac84d1-dfb1-4696-9a69-1276c593e5d1',0,0,'Amou Bon Dieu Gran Pasé Tout',84,'84_MJ_ht','1.\nAmou Bon Dieu gran pasé tout\nKon amou sous ki sin é pu\nLi soti fon nan kè Jézu\nÉ li papé jan-m fini.\n\nKè:\nLi va ouvri bèl pòt sièl la\nÉ sé sètin map antré\nPaské Jézu vèsé san Li\nPou sové mouin nan péché.\n\n2.\nMouin té kon gnou mouton blésé\nKon gnou zouazo nan pièj la\nKè-m té brizé min Li géri-m\nLi plin kè mouin ak la joua.\n\n3.\nLè vouayaj mouin va tèminé\nDévan pòt sièl ma rivé\nPa lamou Jézu si présié\nLi va di mouin vi-n antré.','<p><strong>1.</strong> </p>\n<p>Amou Bon Dieu gran pasé tout </p>\n<p>Kon amou sous ki sin é pu </p>\n<p>Li soti fon nan kè Jézu </p>\n<p>É li papé jan-m fini. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li va ouvri bèl pòt sièl la</strong> </p>\n<p><strong>É sé sètin map antré</strong> </p>\n<p><strong>Paské Jézu vèsé san Li</strong> </p>\n<p><strong>Pou sové mouin nan péché.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té kon gnou mouton blésé </p>\n<p>Kon gnou zouazo nan pièj la </p>\n<p>Kè-m té brizé min Li géri-m </p>\n<p>Li plin kè mouin ak la joua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè vouayaj mouin va tèminé </p>\n<p>Dévan pòt sièl ma rivé </p>\n<p>Pa lamou Jézu si présié </p>\n<p>Li va di mouin vi-n antré. </p>\n','Amou-Bon-Dieu-Gran-Pase-Tout',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1687,'1057118d-389d-416d-b6e6-58e772b0bb60',0,0,'Jézu, mwen renmen ou, Se ou ki Sovè-m',18,'18_CE_ht','1.\nJézu, mouin rinmin ou, sé ou ki Sovè-m,\nAmou ou fè-m kité plèzi péché mouin.\nRédanmtè béni mouin, sé ou ki Sovè-m,\nMouin rinmin ou, Ségnè, pi plus ké janmin.\n\n\n2.\nMouin rinmin ou paské ou rinmin-m avan,\nSou pié boua Kalvè ou té soufri pou mouin.\nMouin rinmin ou ki pòté kourò-n pikan;\nMouin rinmin ou, Ségnè, pi plus ké janmin.\n\n\n3.\nNan la vi, nan lan mò, mouin va rinmin ou.\nMa sèvi ou tout tan ou ban-m souf bouch mouin\nLè lonbraj lan mò vi-n sou mouin, ma di ou:\nMouin rinmin ou, Ségnè, pi plus ké janmin.\n\n\n4.\nNan kay Bon-Dieu, ak joua é gloua é la pè,\nNan sièl la ma va adoré ou san fin.\nDévan tout anj Bon-Dieu yo, ma va chanté:\nMouin rinmin ou, Ségnè, pi plus ké janmin.','<p><strong>1.</strong> </p>\n<p>Jézu, mouin rinmin ou, sé ou ki Sovè-m, </p>\n<p>Amou ou fè-m kité plèzi péché mouin. </p>\n<p>Rédanmtè béni mouin, sé ou ki Sovè-m, </p>\n<p>Mouin rinmin ou, Ségnè, pi plus ké janmin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin rinmin ou paské ou rinmin-m avan, </p>\n<p>Sou pié boua Kalvè ou té soufri pou mouin. </p>\n<p>Mouin rinmin ou ki pòté kourò-n pikan; </p>\n<p>Mouin rinmin ou, Ségnè, pi plus ké janmin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan la vi, nan lan mò, mouin va rinmin ou. </p>\n<p>Ma sèvi ou tout tan ou ban-m souf bouch mouin </p>\n<p>Lè lonbraj lan mò vi-n sou mouin, ma di ou: </p>\n<p>Mouin rinmin ou, Ségnè, pi plus ké janmin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan kay Bon-Dieu, ak joua é gloua é la pè, </p>\n<p>Nan sièl la ma va adoré ou san fin. </p>\n<p>Dévan tout anj Bon-Dieu yo, ma va chanté: </p>\n<p>Mouin rinmin ou, Ségnè, pi plus ké janmin. </p>\n','Jezu-mwen-renmen-ou-Se-ou-ki-Sove-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1688,'37d0e404-d9df-4318-98ac-20a2d656655a',0,0,'You jou ma suspann chante-m yo',20,'20_CE_ht','1.\nGnou jou ma suspan-n chanté-m yo,\nVoua-m a rété san-l pa chanté;\nMin ak sin ki nan la gloua yo,\nNan-m mouin va kontan pou-l mélé.\n\nKè:\nMa va ouè li min-m jan li yé,\nJézu, Rédanmtè, Fis Bon-Dieu.  (2 li-n 2 foua)\n\n\n2.\nGnou jou fòk kan min-m mouin kité\nAbitasion-m sou la tè a,\nMin pòt abitasion Bon-Dieu\nVa louvri pou mouin jou sa a.\n\n\n3.\nGnou jou fè noua va kouvri mouin,\nSolèy va kouché pou toujou;\nMin an ro Jézu va pran mouin,\nPap jan-m fè noua, min tout tan jou.\n\n\n4.\nFò-l vini, jou la gloua sa a,\nSé li ki asurans kè mouin,\nSégnè, kinbé-m paré pou sa,\nNan ou m-mété tout konfians mouin.','<p><strong>1.</strong> </p>\n<p>Gnou jou ma suspan-n chanté-m yo, </p>\n<p>Voua-m a rété san-l pa chanté; </p>\n<p>Min ak sin ki nan la gloua yo, </p>\n<p>Nan-m mouin va kontan pou-l mélé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ma va ouè li min-m jan li yé,</strong> </p>\n<p><strong>Jézu, Rédanmtè, Fis Bon-Dieu.  (2 li-n 2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou jou fòk kan min-m mouin kité </p>\n<p>Abitasion-m sou la tè a, </p>\n<p>Min pòt abitasion Bon-Dieu </p>\n<p>Va louvri pou mouin jou sa a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou jou fè noua va kouvri mouin, </p>\n<p>Solèy va kouché pou toujou; </p>\n<p>Min an ro Jézu va pran mouin, </p>\n<p>Pap jan-m fè noua, min tout tan jou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Fò-l vini, jou la gloua sa a, </p>\n<p>Sé li ki asurans kè mouin, </p>\n<p>Ségnè, kinbé-m paré pou sa, </p>\n<p>Nan ou m-mété tout konfians mouin. </p>\n','You-jou-ma-suspann-chante-m-yo',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1689,'4438cf8c-f5ff-4da1-92e6-2f188165fe0c',0,0,'Bon-Dieu té montré amou-l',26,'26_CE_ht','1.\nBon-Dieu té montré amou-l\nDé pi tròn li nan sièl la;\nMouin té jouin-n gnou plas répo\nAn ba zèl amou li a.\n\nKè:\nMapé pozé, pozé,\nPozé dousman an ba zèl amou-l,\nMapé pozé nan amou-l;\nPozé an ba zè amou li a.\n\nKè: pou ténò\nMapé pozé, dousman pozé an ba zèl amou li a,\nPozé an ba zèl amou li a.  (2 foua)\n\n\n2.\nLè léprèv la vi frapé-m,\nLè sièl la an ro tou noua,\nMouin kab toujou jouin-n répo\nAn ba zèl amou li a.\n\n\n3.\nA! si ou pat jan-m béni\nAvèk la pè nan kè a,\nGin pou ou gnou plas répo\nAn ba zèl amou li a.','<p><strong>1.</strong> </p>\n<p>Bon-Dieu té montré amou-l </p>\n<p>Dé pi tròn li nan sièl la; </p>\n<p>Mouin té jouin-n gnou plas répo </p>\n<p>An ba zèl amou li a. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mapé pozé, pozé,</strong> </p>\n<p><strong>Pozé dousman an ba zèl amou-l,</strong> </p>\n<p><strong>Mapé pozé nan amou-l;</strong> </p>\n<p><strong>Pozé an ba zè amou li a.</strong> </p>\n<p><strong>Kè: pou ténò</strong> </p>\n<p><strong>Mapé pozé, dousman pozé an ba zèl amou li a,</strong> </p>\n<p><strong>Pozé an ba zèl amou li a.  (2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè léprèv la vi frapé-m, </p>\n<p>Lè sièl la an ro tou noua, </p>\n<p>Mouin kab toujou jouin-n répo </p>\n<p>An ba zèl amou li a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A! si ou pat jan-m béni </p>\n<p>Avèk la pè nan kè a, </p>\n<p>Gin pou ou gnou plas répo </p>\n<p>An ba zèl amou li a. </p>\n','Bon-Dieu-te-montre-amou-l',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1690,'e6e4dd11-4d7a-4259-8a75-aad236245853',0,0,'Non Jézu Gin Gnou Son Si Dou',45,'45_CE_ht','1.\nNon Jézu gin gnou son si dou!\nLi chasé tristès nou.\nNon li, sé kou gnou gérizon,\nPa gin gnou pi bèl son!\n\n\n2.\nNon Jézu kalmé kè troublé,\nÉ léspri fatigé.\nLi kou gnou manjé tèlman dou\nPou nan-m yo ki grangou.\n\n\n3.\nNon Jézu sé kou gnou roché\nKi pat kab déplasé,\nLi protéjé-m, li kaché mouin,\nL-a sové-m jouk la fin.\n\n\n4.\nJézu sèvi pou mouin tankou\nBèjé-m, é zanmi-m tou,\nSé li ki chémin an, épi\nLa vérité, la vi.\n\n\n5.\nMouin pat kab fè anpil pou ou;\nMouin fèb é piti tou,\nMa di Ou mèsi pou tout gras,\nLè m-ouè Ou fas a fas.\n\n\n6.\nJouk lè sa a, mouin vlé sèvi\nAk tout souf mouin, pou di\nTout péché yo, sé nan non Ou\nYo kapab sové tou.','<p><strong>1.</strong> </p>\n<p>Non Jézu gin gnou son si dou! </p>\n<p>Li chasé tristès nou. </p>\n<p>Non li, sé kou gnou gérizon, </p>\n<p>Pa gin gnou pi bèl son! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non Jézu kalmé kè troublé, </p>\n<p>É léspri fatigé. </p>\n<p>Li kou gnou manjé tèlman dou </p>\n<p>Pou nan-m yo ki grangou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Non Jézu sé kou gnou roché </p>\n<p>Ki pat kab déplasé, </p>\n<p>Li protéjé-m, li kaché mouin, </p>\n<p>L-a sové-m jouk la fin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu sèvi pou mouin tankou </p>\n<p>Bèjé-m, é zanmi-m tou, </p>\n<p>Sé li ki chémin an, épi </p>\n<p>La vérité, la vi. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mouin pat kab fè anpil pou ou; </p>\n<p>Mouin fèb é piti tou, </p>\n<p>Ma di Ou mèsi pou tout gras, </p>\n<p>Lè m-ouè Ou fas a fas. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Jouk lè sa a, mouin vlé sèvi </p>\n<p>Ak tout souf mouin, pou di </p>\n<p>Tout péché yo, sé nan non Ou </p>\n<p>Yo kapab sové tou. </p>\n','Non-Jezu-Gin-Gnou-Son-Si-Dou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1691,'c028f823-13c2-4210-b1aa-e0a61a371bb4',0,0,'Pran La Vi-m, Ségnè Jézu',50,'50_CE_ht','1.\nPran la vi-m, Ségnè Jézu,\nMouin vlé konsakré-l ba Ou.\nPran chak è é pran chak jou,\nTout tan mouin vlé loué Ou.  (2 foua)\n\n\n2.\nPran min mouin yo, Jézu-Kri,\nÉ sèvi ak yo, tan pri.\nPran pié mouin yo tou, Ségnè,\nPou kouri pou Ou tout è.  (2 foua)\n\n\n3.\nPran voua mouin, é kité mouin\nChanté pou Ou, ki Roua mouin.\nPran lè mouin yo, ranpli yo\nAk mo ki sòti an ro.  (2 foua)\n\n\n4.\nPran lò mouin ak lajan tou,\nTou sa m-gingnin sé pou Ou.\nPran léspri-m ak tout fòs mouin,\nSèvi ak yo jouk la fin.  (2 foua)\n\n\n5.\nPran volonté pa-m, Ségnè;\nSa Ou vlé, sé sa ma fè.\nPran kè mouin, li pou Ou tou,\nLi va sèvi la kay Ou.  (2 foua)\n\n\n6.\nPran amou mouin, Ségnè mouin,\nFè-l pou Ou gnou bagay sin.\nPran mouin min-m, m-vlé viv toujou\nAk tout fòs sèlman pou Ou.  (2 foua)','<p><strong>1.</strong> </p>\n<p>Pran la vi-m, Ségnè Jézu, </p>\n<p>Mouin vlé konsakré-l ba Ou. </p>\n<p>Pran chak è é pran chak jou, </p>\n<p>Tout tan mouin vlé loué Ou.  (2 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pran min mouin yo, Jézu-Kri, </p>\n<p>É sèvi ak yo, tan pri. </p>\n<p>Pran pié mouin yo tou, Ségnè, </p>\n<p>Pou kouri pou Ou tout è.  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pran voua mouin, é kité mouin </p>\n<p>Chanté pou Ou, ki Roua mouin. </p>\n<p>Pran lè mouin yo, ranpli yo </p>\n<p>Ak mo ki sòti an ro.  (2 foua) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pran lò mouin ak lajan tou, </p>\n<p>Tou sa m-gingnin sé pou Ou. </p>\n<p>Pran léspri-m ak tout fòs mouin, </p>\n<p>Sèvi ak yo jouk la fin.  (2 foua) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Pran volonté pa-m, Ségnè; </p>\n<p>Sa Ou vlé, sé sa ma fè. </p>\n<p>Pran kè mouin, li pou Ou tou, </p>\n<p>Li va sèvi la kay Ou.  (2 foua) </p>\n<p><br/> <strong>6.</strong></p>\n<p>Pran amou mouin, Ségnè mouin, </p>\n<p>Fè-l pou Ou gnou bagay sin. </p>\n<p>Pran mouin min-m, m-vlé viv toujou </p>\n<p>Ak tout fòs sèlman pou Ou.  (2 foua) </p>\n','Pran-La-Vi-m-Segne-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1692,'a6b7d532-e318-4dba-8ef3-7b0cf4eb88a0',0,0,'A la gnou Zanmi se Jézu',74,'74_CE_ht','1.\nA la gnou Zanmi sé Jézu,\nTout péché nou sé chaj li!\nA la gnou bi-infè: pou pòté\nPin-n ba li nan la priè!\nKonbi-in foua la pè nou pèdi,\nKonbi-in chagrin nou subi?\nPouki? Paské nou pa pòté\nTout bali nan la priè.\n\n\n2.\nSi gin tantasion ak touman,\nOu traka ninpòt kouman,\nNou pa pou jan-m dékourajé:\nJézu la nan la priè.\nKi lès Zanmi ki si fidèl,\nKi fè nan-m blié doulè-l?\nJézu konnin sa féblès yé:\nJouin-n ak li nan la priè.\n\n\n3.\nNou santi-n fèb? Nou pa kapab?\nKi lès flo napé pasé?\nSovè a di li résponsab:\nJouin-n ak li nan la priè.\nSi zanmi ou yo blié ou,\nDi Jézu-l, li tandé ou.\nLapé tan-n ou, bra li louvri.\nJézu vlé ba ou la vi.','<p><strong>1.</strong> </p>\n<p>A la gnou Zanmi sé Jézu, </p>\n<p>Tout péché nou sé chaj li! </p>\n<p>A la gnou bi-infè: pou pòté </p>\n<p>Pin-n ba li nan la priè! </p>\n<p>Konbi-in foua la pè nou pèdi, </p>\n<p>Konbi-in chagrin nou subi? </p>\n<p>Pouki? Paské nou pa pòté </p>\n<p>Tout bali nan la priè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si gin tantasion ak touman, </p>\n<p>Ou traka ninpòt kouman, </p>\n<p>Nou pa pou jan-m dékourajé: </p>\n<p>Jézu la nan la priè. </p>\n<p>Ki lès Zanmi ki si fidèl, </p>\n<p>Ki fè nan-m blié doulè-l? </p>\n<p>Jézu konnin sa féblès yé: </p>\n<p>Jouin-n ak li nan la priè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nou santi-n fèb? Nou pa kapab? </p>\n<p>Ki lès flo napé pasé? </p>\n<p>Sovè a di li résponsab: </p>\n<p>Jouin-n ak li nan la priè. </p>\n<p>Si zanmi ou yo blié ou, </p>\n<p>Di Jézu-l, li tandé ou. </p>\n<p>Lapé tan-n ou, bra li louvri. </p>\n<p>Jézu vlé ba ou la vi. </p>\n','A-la-gnou-Zanmi-se-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1693,'ac032338-198e-4ef9-a07f-09adb11c4a02',0,0,'Jézu résusité',104,'104_CE_ht','1.\nJézu résusité!\nAk kè nou plin joua\nTou patou nap chanté;\nNap ran-n li gloua.\n\nKè:\nLi té sòti nan tonbo,\nGnou Sovè vivan é tout puisan!\nPou janmin, li gin la viktoua sou lan mò;\nÉtènèlman, L-a ré1gn ak sin li yo.\nKris vivan!  Kris vivan!\nAlélouya!  Kris vivan!\n\n\n2.\nJézu résusité!\nSé nan li toujou\nPou nou tout al chaché\nFòs ak sékou.\n\n\n3.\nJézu résusité!\nLi ban nou la vi;\nÉ nan létènité\nN-a avèk li.','<p><strong>1.</strong> </p>\n<p>Jézu résusité! </p>\n<p>Ak kè nou plin joua </p>\n<p>Tou patou nap chanté; </p>\n<p>Nap ran-n li gloua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li té sòti nan tonbo,</strong> </p>\n<p><strong>Gnou Sovè vivan é tout puisan!</strong> </p>\n<p><strong>Pou janmin, li gin la viktoua sou lan mò;</strong> </p>\n<p><strong>Étènèlman, L-a ré1gn ak sin li yo.</strong> </p>\n<p><strong>Kris vivan!  Kris vivan!</strong> </p>\n<p><strong>Alélouya!  Kris vivan!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu résusité! </p>\n<p>Sé nan li toujou </p>\n<p>Pou nou tout al chaché </p>\n<p>Fòs ak sékou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu résusité! </p>\n<p>Li ban nou la vi; </p>\n<p>É nan létènité </p>\n<p>N-a avèk li. </p>\n','Jezu-resusite',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1694,'4ee81453-4a09-40e3-9e39-76b742cff9ee',0,0,'Amou Kris chanté pou tout moun tandé',134,'134_CE_ht','1.\nAmou Kris chanté pou tout moun tandé,\nPréché nouvèl sa nan tout koté;\nBon-Dieu a té voyé sèl pitit Li,\nPou sa ki kouè nan Li kab sové.\n\nKè:\nAmou Li mèvéyé pou mouin,\nAmou Li mèvéyé pou mouin,\nJézu té si rinmin-m\nKé Li té mouri min-m;\nAmou Li mèvéyé pou mouin.\n\n\n2.\nAmou Bon-Dieu révélé nan Jézu\nLè Li té pré la mè Galilé,\nLi té péyé fot mouin sou boua Kalvè,\nMalgré Li pat janmin fè péché.\n\n\n3.\nAmou Kris ki soulajé tout tristès,\nAmou Kris sé protéksion nan-m mouin,\nPran la vi-m, kinbé tou sa mouin gingnin,\nMin mouin, Jézu, sé ou mouin rinmin.','<p><strong>1.</strong> </p>\n<p>Amou Kris chanté pou tout moun tandé, </p>\n<p>Préché nouvèl sa nan tout koté; </p>\n<p>Bon-Dieu a té voyé sèl pitit Li, </p>\n<p>Pou sa ki kouè nan Li kab sové. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Amou Li mèvéyé pou mouin,</strong> </p>\n<p><strong>Amou Li mèvéyé pou mouin,</strong> </p>\n<p><strong>Jézu té si rinmin-m</strong> </p>\n<p><strong>Ké Li té mouri min-m;</strong> </p>\n<p><strong>Amou Li mèvéyé pou mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Amou Bon-Dieu révélé nan Jézu </p>\n<p>Lè Li té pré la mè Galilé, </p>\n<p>Li té péyé fot mouin sou boua Kalvè, </p>\n<p>Malgré Li pat janmin fè péché. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Amou Kris ki soulajé tout tristès, </p>\n<p>Amou Kris sé protéksion nan-m mouin, </p>\n<p>Pran la vi-m, kinbé tou sa mouin gingnin, </p>\n<p>Min mouin, Jézu, sé ou mouin rinmin. </p>\n','Amou-Kris-chante-pou-tout-moun-tande',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1695,'2550e49b-b277-44db-8911-5252e22fe8cc',0,0,'Mouin santi-m bi-in kontan pou-m sèvi Roua a',136,'136_CE_ht','1.\nMouin santi-m bi-in kontan pou-m sèvi Roua a,\nMouin bi-in kontan, oui bi-in kontan;\nMouin gin la pè, la joua pasé tout bagay,\nLè map sèvi pi gran Roua.\n\nKè: Mapé sèvi pi gran Roua,\nChak talan-m Li résévoua;\nMouin gin la pè, la joua, tout bagay,\nLè-m sèvi pi gran Roua.\n\n\n2.\nMouin santi-m bi-in kontan pou-m sèvi Roua a,\nMouin bi-in kontan, oui bi-in kontan;\nMalgré solèy, malgré nuaj, m-a chanté\nLè map sèvi, pi gran Roua.\n\n\n3.\nMouin santi-m bi-in kontan pou-m sèvi Roua a,\nMouin bi-in kontan, oui bi-in kontan;\nMa kité min-l dirijé mouin pa-za-pa\nLè map sèvi pi gran Roua.\n\n\n4.\nMouin santi-m bi-in kontan pou-m sèvi Roua a,\nMouin bi-in kontan, oui bi-in kontan;\nTou sa-m gingnin, nan min Li ouin kontan bay,\nLè map sèvi pi gran Roua.','<p><strong>1.</strong> </p>\n<p>Mouin santi-m bi-in kontan pou-m sèvi Roua a, </p>\n<p>Mouin bi-in kontan, oui bi-in kontan; </p>\n<p>Mouin gin la pè, la joua pasé tout bagay, </p>\n<p>Lè map sèvi pi gran Roua. </p>\n<p>Kè: Mapé sèvi pi gran Roua, </p>\n<p>Chak talan-m Li résévoua; </p>\n<p>Mouin gin la pè, la joua, tout bagay, </p>\n<p>Lè-m sèvi pi gran Roua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin santi-m bi-in kontan pou-m sèvi Roua a, </p>\n<p>Mouin bi-in kontan, oui bi-in kontan; </p>\n<p>Malgré solèy, malgré nuaj, m-a chanté </p>\n<p>Lè map sèvi, pi gran Roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin santi-m bi-in kontan pou-m sèvi Roua a, </p>\n<p>Mouin bi-in kontan, oui bi-in kontan; </p>\n<p>Ma kité min-l dirijé mouin pa-za-pa </p>\n<p>Lè map sèvi pi gran Roua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin santi-m bi-in kontan pou-m sèvi Roua a, </p>\n<p>Mouin bi-in kontan, oui bi-in kontan; </p>\n<p>Tou sa-m gingnin, nan min Li ouin kontan bay, </p>\n<p>Lè map sèvi pi gran Roua. </p>\n','Mouin-santi-m-bi-in-kontan-pou-m-sevi-Roua-a',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1696,'2947a647-23f9-4b26-83a6-e2395ffdb1db',0,0,'Gin gnou foua nan la vil David',138,'138_CE_ht','1.\nGin gnou foua nan la vil David,\nNan gnou étab pov é ba,\nGnou manman kouché pitit li\nNan krèch la ranjé ak pay.\nMari té manman si janti,\nJézu-Kri té ti pitit li.\n\n\n2.\nLi té sòti min-m nan sièl la,\nLi ki Bon-Dieu é Ségnè,\nLi désan-n jouk nan étab la,\nSé nan krèch li dòmi.\nJézu, Sovè nou, té vini\nPami moun yo, pov é janti.\n\n\n3.\nÉ tout tan lè Li té piti\nLi té kouté manman li.\nLi rinmin gadé-l é sèvi-l,\nLi té soumi dévan li.\nTout ti moun yo ki vlé suiv li\nDoué toujou soumi é janti.\n\n\n4.\nJézu sé modèl ti moun yo,\nLi tap grandi tankou nou;\nLi té bézouin moun okupé-l,\nLi kon-n krié, li souri tou.\nLè nou tris, li soufri ak nou;\nLè nou jouayé, li kontan tou.','<p><strong>1.</strong> </p>\n<p>Gin gnou foua nan la vil David, </p>\n<p>Nan gnou étab pov é ba, </p>\n<p>Gnou manman kouché pitit li </p>\n<p>Nan krèch la ranjé ak pay. </p>\n<p>Mari té manman si janti, </p>\n<p>Jézu-Kri té ti pitit li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té sòti min-m nan sièl la, </p>\n<p>Li ki Bon-Dieu é Ségnè, </p>\n<p>Li désan-n jouk nan étab la, </p>\n<p>Sé nan krèch li dòmi. </p>\n<p>Jézu, Sovè nou, té vini </p>\n<p>Pami moun yo, pov é janti. </p>\n<p><br/> <strong>3.</strong></p>\n<p>É tout tan lè Li té piti </p>\n<p>Li té kouté manman li. </p>\n<p>Li rinmin gadé-l é sèvi-l, </p>\n<p>Li té soumi dévan li. </p>\n<p>Tout ti moun yo ki vlé suiv li </p>\n<p>Doué toujou soumi é janti. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu sé modèl ti moun yo, </p>\n<p>Li tap grandi tankou nou; </p>\n<p>Li té bézouin moun okupé-l, </p>\n<p>Li kon-n krié, li souri tou. </p>\n<p>Lè nou tris, li soufri ak nou; </p>\n<p>Lè nou jouayé, li kontan tou. </p>\n','Gin-gnou-foua-nan-la-vil-David',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1697,'dc4b1808-3cb0-4170-8f5f-3af1d699482a',0,0,'Moun yo ki manké dévoua yo',145,'145_CE_ht','1.\nMoun yo ki manké dévoua yo\nKi négligé travay chak jou\nFè minm bagay pou Bon-Dieu tou\nYa négligé travay sélès\nYo séparé sa-k doué ansanm\nLa piété ak la dilijans\n\n2.\nNou viv kontan lè Dieu ak nou\nNan travay nou nou mété zèl\nKa chak travay, si-l fèt pou li\nSanblé gnou sakrifis divin\nKa, sé gnou bagay ki biin vré\nPlus nap sévi, plus nap rinmin','<p><strong>1.</strong> </p>\n<p>Moun yo ki manké dévoua yo </p>\n<p>Ki négligé travay chak jou </p>\n<p>Fè minm bagay pou Bon-Dieu tou </p>\n<p>Ya négligé travay sélès </p>\n<p>Yo séparé sa-k doué ansanm </p>\n<p>La piété ak la dilijans </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou viv kontan lè Dieu ak nou </p>\n<p>Nan travay nou nou mété zèl </p>\n<p>Ka chak travay, si-l fèt pou li </p>\n<p>Sanblé gnou sakrifis divin </p>\n<p>Ka, sé gnou bagay ki biin vré </p>\n<p>Plus nap sévi, plus nap rinmin </p>\n','Moun-yo-ki-manke-devoua-yo',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1698,'6e9973b6-cf75-4f6f-a12f-77ea7107f034',0,0,'Le zeprev yo vi-n nou monde pouki',14,'14_MJ_ht','1.\nLè zéprèv yo vi-n nou mandé pouki\nNou bézouin soufri – kè atristé\nPoutan lè zòt yo ki antouré nou\nApé réjoui yo-min’m nan péché.\n\nKè:\nPi dévan na ouè Jézu nan glou Li\nPi dévan napé konpran-n pouki\nBon kouraj frè mouin\nFè kè ou kontan,\nPaské gnou jou\nNap konpran-n pouki.\n\n2.\nSouvan nou mandé\nPouki nou bézouin\nPasé nan rout sa pou suiv Jézu\nPandan lè zòt yo ap viv byin alèz\nNou bat pou chèché lèzam pèdu.\n\n3.\nRété fidèl juska la mò frè mouin\nLé Ségnè Jézu égzòté nou.\nNou va blié tout traka nou pasé\nLè nou antré nan sièl la gnou jou.\n\n4.\nByinto n-ap la ak Jézu Sovè nou\nNan bèl kay nou lè Li va vini.\nAnlè nap kontré byinn-émé nou yo\nLè sa napé konpran-n byin pouki.','<p><strong>1.</strong> </p>\n<p>Lè zéprèv yo vi-n nou mandé pouki </p>\n<p>Nou bézouin soufri – kè atristé </p>\n<p>Poutan lè zòt yo ki antouré nou </p>\n<p>Apé réjoui yo-min’m nan péché. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pi dévan na ouè Jézu nan glou Li</strong> </p>\n<p><strong>Pi dévan napé konpran-n pouki</strong> </p>\n<p><strong>Bon kouraj frè mouin</strong> </p>\n<p><strong>Fè kè ou kontan,</strong> </p>\n<p><strong>Paské gnou jou</strong> </p>\n<p><strong>Nap konpran-n pouki.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan nou mandé </p>\n<p>Pouki nou bézouin </p>\n<p>Pasé nan rout sa pou suiv Jézu </p>\n<p>Pandan lè zòt yo ap viv byin alèz </p>\n<p>Nou bat pou chèché lèzam pèdu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rété fidèl juska la mò frè mouin </p>\n<p>Lé Ségnè Jézu égzòté nou. </p>\n<p>Nou va blié tout traka nou pasé </p>\n<p>Lè nou antré nan sièl la gnou jou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Byinto n-ap la ak Jézu Sovè nou </p>\n<p>Nan bèl kay nou lè Li va vini. </p>\n<p>Anlè nap kontré byinn-émé nou yo </p>\n<p>Lè sa napé konpran-n byin pouki. </p>\n','Le-zeprev-yo-vi-n-nou-monde-pouki',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1699,'49b99b8e-aff6-4f9f-8a73-8cca1af3a09d',0,0,'Koté Min-d-èv Yo Pou Rékolté',21,'21_MJ_ht','1.\nKoté min-d-èv yo ki pou rékolté,\nGran mouason dé zam\nKi nan chan péché?\nFòk nou byin paré ak la vérité,\nPou rantré rékòt pou létènité.\n\nKè:\nKoté min-d-èv yo pou rékolté,\nGran mouason dé zam\nKi nan chan péché?\nKilès va édé-n zam\nNan travay Bon-Dieu,\nÉgzanté lé zam nan jujman dufé?\n\n2.\nTravay la va rèd é li fè byin cho,\nNa kéyi lé zam malgré rajé gro,\nPa kité gnou sèl,\nPaské tout pou Kris,\nTout éfò nou fè sonjé Li fè plus.\n\n3.\nMouason déjà prèt pou li rékolté,\nTravay la anpil, manké ouvrié.\nByinto l-a pèdu si nou fè réta,\nVini bay konkou\nNan gran travay sa.\n\n4.\nAn nou mété tout\nMin nou yo ansan-m,\nNou pa fi-n konnin\nValè gnou sèl nan-m\nByinto Mèt rékòt la va rétounin,\nLi vlé nou réjoui ak Li a la fin.','<p><strong>1.</strong> </p>\n<p>Koté min-d-èv yo ki pou rékolté, </p>\n<p>Gran mouason dé zam </p>\n<p>Ki nan chan péché? </p>\n<p>Fòk nou byin paré ak la vérité, </p>\n<p>Pou rantré rékòt pou létènité. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Koté min-d-èv yo pou rékolté,</strong> </p>\n<p><strong>Gran mouason dé zam</strong> </p>\n<p><strong>Ki nan chan péché?</strong> </p>\n<p><strong>Kilès va édé-n zam</strong> </p>\n<p><strong>Nan travay Bon-Dieu,</strong> </p>\n<p><strong>Égzanté lé zam nan jujman dufé?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Travay la va rèd é li fè byin cho, </p>\n<p>Na kéyi lé zam malgré rajé gro, </p>\n<p>Pa kité gnou sèl, </p>\n<p>Paské tout pou Kris, </p>\n<p>Tout éfò nou fè sonjé Li fè plus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouason déjà prèt pou li rékolté, </p>\n<p>Travay la anpil, manké ouvrié. </p>\n<p>Byinto l-a pèdu si nou fè réta, </p>\n<p>Vini bay konkou </p>\n<p>Nan gran travay sa. </p>\n<p><br/> <strong>4.</strong></p>\n<p>An nou mété tout </p>\n<p>Min nou yo ansan-m, </p>\n<p>Nou pa fi-n konnin </p>\n<p>Valè gnou sèl nan-m </p>\n<p>Byinto Mèt rékòt la va rétounin, </p>\n<p>Li vlé nou réjoui ak Li a la fin. </p>\n','Kote-Min-d-ev-Yo-Pou-Rekolte',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1700,'cd4cee00-1a61-4489-aee8-dbc0054e87c6',0,0,'Chak Promès Nan La Bib Sé Pou Mwen',37,'37_MJ_ht','1.\nChak promès nan la Bib sé pou mouin,\nChak chapit, chak vèsè é chak lign;\nTout byin-fè li sé amou divin;\nChak promès nan la Bib sé pou mouin.','<p><strong>1.</strong> </p>\n<p>Chak promès nan la Bib sé pou mouin, </p>\n<p>Chak chapit, chak vèsè é chak lign; </p>\n<p>Tout byin-fè li sé amou divin; </p>\n<p>Chak promès nan la Bib sé pou mouin. </p>\n','Chak-Promes-Nan-La-Bib-Se-Pou-Mwen',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1701,'3b793fd8-4fbd-416e-86a4-4fbc516d7049',0,0,'Lé Monn Sé Pa Lakay Mwen',39,'39_MJ_ht','1.\nLé mon-n sé pa lakay mouin,\nNan sièl map pralé;\nTrézò mouin ak kè mouin,\nAn-ro yo tout fiksé.\nMouin tandé zanj yo\nKap rélé mouin alé la,\nÉ mouin pa kab santi-m\nTròp a-lèz isi-ba.\n\nKè:\nJézu, ou konnin ou sé sèl zanmi mouin,\nSi ou pa pran-mouin nan sièl,\nO Ségnè-m sa mouin fè?\nMouin tandé zanj yo\nK-ap rélé mouin alé la,\nÉ mouin pa kab santi-m\nTrò a-lèz isi-ba.\n\n2.\nMouin konnin mouin pa dign,\nMouin sé gnou vil péchè;\nMin mouin gin tout éspoua\nKé Jézu sé Sovè-m;\nSé Jézu ki té mouri\nSou lé boua Kalvè\nÉ li té sové nan-m mouin pou létènité.\n\n3.\nMouin gin kèk byin-émé\nKi dévansé-m déjà;\nYo nan bra dé Jézu,\nÉ y-apé tan-n mouin la.\nMouin gin pou rété,\nLuté kon gnou bon solda\nMin Jézu, lè ou rélé-m,\nM-a vi-n avèk joua.','<p><strong>1.</strong> </p>\n<p>Lé mon-n sé pa lakay mouin, </p>\n<p>Nan sièl map pralé; </p>\n<p>Trézò mouin ak kè mouin, </p>\n<p>An-ro yo tout fiksé. </p>\n<p>Mouin tandé zanj yo </p>\n<p>Kap rélé mouin alé la, </p>\n<p>É mouin pa kab santi-m </p>\n<p>Tròp a-lèz isi-ba. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu, ou konnin ou sé sèl zanmi mouin,</strong> </p>\n<p><strong>Si ou pa pran-mouin nan sièl,</strong> </p>\n<p><strong>O Ségnè-m sa mouin fè?</strong> </p>\n<p><strong>Mouin tandé zanj yo</strong> </p>\n<p><strong>K-ap rélé mouin alé la,</strong> </p>\n<p><strong>É mouin pa kab santi-m</strong> </p>\n<p><strong>Trò a-lèz isi-ba.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin konnin mouin pa dign, </p>\n<p>Mouin sé gnou vil péchè; </p>\n<p>Min mouin gin tout éspoua </p>\n<p>Ké Jézu sé Sovè-m; </p>\n<p>Sé Jézu ki té mouri </p>\n<p>Sou lé boua Kalvè </p>\n<p>É li té sové nan-m mouin pou létènité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin gin kèk byin-émé </p>\n<p>Ki dévansé-m déjà; </p>\n<p>Yo nan bra dé Jézu, </p>\n<p>É y-apé tan-n mouin la. </p>\n<p>Mouin gin pou rété, </p>\n<p>Luté kon gnou bon solda </p>\n<p>Min Jézu, lè ou rélé-m, </p>\n<p>M-a vi-n avèk joua. </p>\n','Le-Monn-Se-Pa-Lakay-Mwen',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1702,'a2fd1b8b-cf4e-41da-897c-695cb6e4593e',0,0,'Pètèt Sé Pa Sou Gnou Gro Montay',40,'40_MJ_ht','1.\nPètèt sé pa sou gnou gro montagn,\nNi nan gnou péi byin louin;\nPètèt sé pa nan gnou gran batay,\nKé Ou va sèvi ak mouin;\nMin si sé voua pa Ou ki palé,\nMin-m si mouin pa gin angnin,\nM-ap répon-n, Bon-Dieu,\nAk min Ou sou mouin,\nMouin va alé koté Ou vlé.\n\nKè:\nMouin vlé alé koté Ou vlé, Bon-Dieu,\nNan mòn, sou la mè, nan valé.\nMouin va di sa Ou vlé mouin di Bon-Dieu,\nMouin va sa Ou vlé pou mouin yé.\n\n2.\nPètèt jodi a sé paròl dou\nKé Jézu vlé pou-m palé;\nSan dout doué gingnin sou rout la tou,\nMouin égaré pou-m chèché.\nO Bon-Sovè, si Ou va gidé-m,\nÉ si sou rout la Ou kinbé-m,\nMouin va répété mésaj la vi,\nMouin va di sa Ou vlé mouin di.\n\n3.\nSèrènman gingnin gnou vié ti plas\nNan gran rékolt mon-n sa a,\nKoté-m kab fè, min-m gnou ti kras\nPou Jézu, Krusifié a.\nM-a mété konfians mouin nan Ou, Ségnè-m\nPaské Ou té rinmin mouin vré;\nM-a fè volonté Ou, chè Sovè-m,\nMouin va sa Ou vlé pou mouin yé.','<p><strong>1.</strong> </p>\n<p>Pètèt sé pa sou gnou gro montagn, </p>\n<p>Ni nan gnou péi byin louin; </p>\n<p>Pètèt sé pa nan gnou gran batay, </p>\n<p>Ké Ou va sèvi ak mouin; </p>\n<p>Min si sé voua pa Ou ki palé, </p>\n<p>Min-m si mouin pa gin angnin, </p>\n<p>M-ap répon-n, Bon-Dieu, </p>\n<p>Ak min Ou sou mouin, </p>\n<p>Mouin va alé koté Ou vlé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin vlé alé koté Ou vlé, Bon-Dieu,</strong> </p>\n<p><strong>Nan mòn, sou la mè, nan valé.</strong> </p>\n<p><strong>Mouin va di sa Ou vlé mouin di Bon-Dieu,</strong> </p>\n<p><strong>Mouin va sa Ou vlé pou mouin yé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pètèt jodi a sé paròl dou </p>\n<p>Ké Jézu vlé pou-m palé; </p>\n<p>San dout doué gingnin sou rout la tou, </p>\n<p>Mouin égaré pou-m chèché. </p>\n<p>O Bon-Sovè, si Ou va gidé-m, </p>\n<p>É si sou rout la Ou kinbé-m, </p>\n<p>Mouin va répété mésaj la vi, </p>\n<p>Mouin va di sa Ou vlé mouin di. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sèrènman gingnin gnou vié ti plas </p>\n<p>Nan gran rékolt mon-n sa a, </p>\n<p>Koté-m kab fè, min-m gnou ti kras </p>\n<p>Pou Jézu, Krusifié a. </p>\n<p>M-a mété konfians mouin nan Ou, Ségnè-m </p>\n<p>Paské Ou té rinmin mouin vré; </p>\n<p>M-a fè volonté Ou, chè Sovè-m, </p>\n<p>Mouin va sa Ou vlé pou mouin yé. </p>\n','Petet-Se-Pa-Sou-Gnou-Gro-Montay',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1703,'bf1f3808-66aa-4ffb-8d82-48e213dfef23',0,0,'Tout La Vi Mwen',43,'43_MJ_ht','1.\nTout la vi mouin, mouin tap soufri\nM-tap chèché gnou dlo pou-m bouè\nKi tap soulajé-m pou tout tan\nSouaf la ki té plin kè mouin.\n\nKè:\nAlélouya mouin jouin-n Jézu\nKé lontan nan-m mouin chèché.\nJézu satisfè tout kè mouin\nSé pa san Li mouin sové.\n\n2.\nManjé lé mond té ranpli mouin\nMin mouin pat gin pè du tou\nMouin tap chèché sa ki pi bon\nNan-m mouin té toujou grangou.\n\n3.\nMouin té pòv, lajan m-tap chèché\nGnou bagay pou satisfè-m,\nMin tou sa mouin tap rasanblé\nPat fè angnin pou nan-m mouin.\n\n4.\nGnou sous dlo ki toujou koulé,\nPin la vi si bon san pri\nÉ richès ki satisfè mouin.\nMouin jouin-n tou sa nan Jézu.','<p><strong>1.</strong> </p>\n<p>Tout la vi mouin, mouin tap soufri </p>\n<p>M-tap chèché gnou dlo pou-m bouè </p>\n<p>Ki tap soulajé-m pou tout tan </p>\n<p>Souaf la ki té plin kè mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alélouya mouin jouin-n Jézu</strong> </p>\n<p><strong>Ké lontan nan-m mouin chèché.</strong> </p>\n<p><strong>Jézu satisfè tout kè mouin</strong> </p>\n<p><strong>Sé pa san Li mouin sové.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Manjé lé mond té ranpli mouin </p>\n<p>Min mouin pat gin pè du tou </p>\n<p>Mouin tap chèché sa ki pi bon </p>\n<p>Nan-m mouin té toujou grangou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin té pòv, lajan m-tap chèché </p>\n<p>Gnou bagay pou satisfè-m, </p>\n<p>Min tou sa mouin tap rasanblé </p>\n<p>Pat fè angnin pou nan-m mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou sous dlo ki toujou koulé, </p>\n<p>Pin la vi si bon san pri </p>\n<p>É richès ki satisfè mouin. </p>\n<p>Mouin jouin-n tou sa nan Jézu. </p>\n','Tout-La-Vi-Mwen',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1704,'b7858da0-803d-4004-9480-e19f6877b217',0,0,'M-ap Luté pou-m Rivé Laba',64,'64_MJ_ht','1.\nM-ap luté pou-m rivé laba\nKoté ki gin la pè, la joua,\nSé la mouin vlé rété tout tan,\nNan bèl péi dé Kanaan.\n\nKè:\nBèl Kanaan, bèl Kanaan,\nSé la mouin vlé rété tout tan\nKoté lèt la ak mièl koulé\nKoté ki pa jan-m gin péché,\nSé la m-ap rété viv san fin,\nPou toujou ak Kris, Sovè mouin.\n\n2.\nRout la mové min m-ap rivé\nSatan ap chèché fè-m tonbé,\nMin Jézu kinbé-m pa-za-pa\nJouk mouin rivé la nan la gloua.\n\n3.\nMouin p-ap murmuré, ni krié,\nMin m-ap toujou pèsévéré,\nKa Kris té soufri trop pou mouin\nFok mouin suiv Li juska la fin.','<p><strong>1.</strong> </p>\n<p>M-ap luté pou-m rivé laba </p>\n<p>Koté ki gin la pè, la joua, </p>\n<p>Sé la mouin vlé rété tout tan, </p>\n<p>Nan bèl péi dé Kanaan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bèl Kanaan, bèl Kanaan,</strong> </p>\n<p><strong>Sé la mouin vlé rété tout tan</strong> </p>\n<p><strong>Koté lèt la ak mièl koulé</strong> </p>\n<p><strong>Koté ki pa jan-m gin péché,</strong> </p>\n<p><strong>Sé la m-ap rété viv san fin,</strong> </p>\n<p><strong>Pou toujou ak Kris, Sovè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Rout la mové min m-ap rivé </p>\n<p>Satan ap chèché fè-m tonbé, </p>\n<p>Min Jézu kinbé-m pa-za-pa </p>\n<p>Jouk mouin rivé la nan la gloua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin p-ap murmuré, ni krié, </p>\n<p>Min m-ap toujou pèsévéré, </p>\n<p>Ka Kris té soufri trop pou mouin </p>\n<p>Fok mouin suiv Li juska la fin. </p>\n','M-ap-Lute-pou-m-Rive-Laba',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1705,'11bf29d9-9d88-49ab-9596-9300b6a4be93',0,0,'Mwen Kontan Ké Mwen Konvèti',70,'70_MJ_ht','1.\nMouin kontan ké mouin konvèti,\nOui, m-kontan.  (2 lign 2 foua)\nMouin kontan ké mouin konvèti\nTou lé lindi, amdi, mèkrédi,\nJédi, vandrédi, samdi,\nTout jounin dimanch la,\nOui, m-kontan.','<p><strong>1.</strong> </p>\n<p>Mouin kontan ké mouin konvèti, </p>\n<p>Oui, m-kontan.  (2 lign 2 foua) </p>\n<p>Mouin kontan ké mouin konvèti </p>\n<p>Tou lé lindi, amdi, mèkrédi, </p>\n<p>Jédi, vandrédi, samdi, </p>\n<p>Tout jounin dimanch la, </p>\n<p>Oui, m-kontan. </p>\n','Mwen-Kontan-Ke-Mwen-Konveti',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1706,'f18242d2-3853-428c-9dcb-ff98461e0781',0,0,'M-konfié Nan Dieu Ninpot Koté Mwen Yé',72,'72_MJ_ht','1.\nM-konfié nan Dieu ninpòt koté mouin yé.\nNi sou la tè, si sou la mè troublé;\nNan tout traka, nan la vi sa a\nPapa-m nan sièl la apé véyé mouin.\n\nKè:\nM-konfié nan Dieu,\nMouin konnin L-ap pran souin mouin,\nNan dézè a, ou nan mové chémin,\nLa mè pousé-m, Bon-Dieu kinbé-m\nPapa-m nan sièl la apé véyé mouin.\n\n2.\nLi gadé flè yo nan chan tou patou,\nLi pran souin zouazo kap chanté pou nou,\nJuska la fin, L-ap sonjé mouin,\nPapa-m nan sièl la apé véyé mouin.\n\n3.\nRout la mèt du, nuaj yo kouvri mouin,\nBèjé a gadé mouton-l jouk la fin\nApré mon-n sa, na ouè sièl la,\nPapa-m nan sièl la apé véyé mouin.','<p><strong>1.</strong> </p>\n<p>M-konfié nan Dieu ninpòt koté mouin yé. </p>\n<p>Ni sou la tè, si sou la mè troublé; </p>\n<p>Nan tout traka, nan la vi sa a </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-konfié nan Dieu,</strong> </p>\n<p><strong>Mouin konnin L-ap pran souin mouin,</strong> </p>\n<p><strong>Nan dézè a, ou nan mové chémin,</strong> </p>\n<p><strong>La mè pousé-m, Bon-Dieu kinbé-m</strong> </p>\n<p><strong>Papa-m nan sièl la apé véyé mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li gadé flè yo nan chan tou patou, </p>\n<p>Li pran souin zouazo kap chanté pou nou, </p>\n<p>Juska la fin, L-ap sonjé mouin, </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rout la mèt du, nuaj yo kouvri mouin, </p>\n<p>Bèjé a gadé mouton-l jouk la fin </p>\n<p>Apré mon-n sa, na ouè sièl la, </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n','M-konfie-Nan-Dieu-Ninpot-Kote-Mwen-Ye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1707,'14ac4628-8232-446b-8761-ebae288a82f9',0,0,'Pouki M\'doué Tris',73,'73_MJ_ht','1.\nPouki m-doué tris\nLè solèy pa briyé?\nJézu avè-m, toutla jounin,\nPouki m-doué tris\nLè tanpèt vi-n koté-m\nJézu avèk mouin tout tan.\nLi pa abandonnin mouin,\nPaské Li fidèl\nMalgré tout éprèv yo,\nLi va kondui-m nan sièl,\nMouin byin kontan ak Jézu\nÉ pouki m-doué tris\nLè solèy pa briyé?\nJézu avèk mouin toujou.','<p><strong>1.</strong> </p>\n<p>Pouki m-doué tris </p>\n<p>Lè solèy pa briyé? </p>\n<p>Jézu avè-m, toutla jounin, </p>\n<p>Pouki m-doué tris </p>\n<p>Lè tanpèt vi-n koté-m </p>\n<p>Jézu avèk mouin tout tan. </p>\n<p>Li pa abandonnin mouin, </p>\n<p>Paské Li fidèl </p>\n<p>Malgré tout éprèv yo, </p>\n<p>Li va kondui-m nan sièl, </p>\n<p>Mouin byin kontan ak Jézu </p>\n<p>É pouki m-doué tris </p>\n<p>Lè solèy pa briyé? </p>\n<p>Jézu avèk mouin toujou. </p>\n','Pouki-M\'doue-Tris',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1708,'6e45d6cb-591e-4a21-8e1d-85b96dc2abf2',0,0,'Sèlman Yon Gason David',79,'79_MJ_ht','1.\nSèlman gnou gason David,\nSèlman gnou ti flan-n,\nSèlman gnou gason David,\nMin li té prié é chanté\nSèlman gnou gason David,\nSèlman gnou ti riviè,\nSèlman gnou gason David,\nÉ sink ti roch li pran.\nGnou ti roch li mété nan flan-n\nÉ li balansé-l ron-n é ron-n,\nGnou ti roch li mété nan flan-n\nÉ li balansé-l ron-n é ron-n\nÉ ron-n, é ron-n, é ron-n, é ron-n\nÉ ron-n, é ron-n, é ron-n,\nGnou ti roch li voyé nan lè-a\nÉ Golia-t tonbé a tè.','<p><strong>1.</strong> </p>\n<p>Sèlman gnou gason David, </p>\n<p>Sèlman gnou ti flan-n, </p>\n<p>Sèlman gnou gason David, </p>\n<p>Min li té prié é chanté </p>\n<p>Sèlman gnou gason David, </p>\n<p>Sèlman gnou ti riviè, </p>\n<p>Sèlman gnou gason David, </p>\n<p>É sink ti roch li pran. </p>\n<p>Gnou ti roch li mété nan flan-n </p>\n<p>É li balansé-l ron-n é ron-n, </p>\n<p>Gnou ti roch li mété nan flan-n </p>\n<p>É li balansé-l ron-n é ron-n </p>\n<p>É ron-n, é ron-n, é ron-n, é ron-n </p>\n<p>É ron-n, é ron-n, é ron-n, </p>\n<p>Gnou ti roch li voyé nan lè-a </p>\n<p>É Golia-t tonbé a tè. </p>\n','Selman-Yon-Gason-David',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1709,'cfcfcf04-a291-41f2-b6bc-13e75be2f139',0,0,'Bénis Seigneur',26,'26_VR_fr','1.	\nBénis Seigneur\nBénis Seigneur devant ta face\nDeux jeunes époux Accomplissant par grâce\nLe lien de l\'amour\nQue ta sainte bénédiction\nDans leur vie, luise à jamais  (bis)\nBénis, bénis cette union\nPour une douce paix.','<p><strong>1.</strong> </p>\n<p>Bénis Seigneur </p>\n<p>Bénis Seigneur devant ta face </p>\n<p>Deux jeunes époux Accomplissant par grâce </p>\n<p>Le lien de l&#39;amour </p>\n<p>Que ta sainte bénédiction </p>\n<p>Dans leur vie, luise à jamais  (bis) </p>\n<p>Bénis, bénis cette union </p>\n<p>Pour une douce paix. </p>\n','Benis-Seigneur',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1710,'eef0a6d4-ab40-4d95-a3b1-804b966fc48e',0,0,'O Dieu De Nos Aïeux',321,'321_CE_fr','1.\nO Dieu de nos aïeux,\nDaigne entendre nos voeux\nPour la patrie.\nToi qui brisas nos fers,\nQue la terre chérie\nJamais par des revers\nNe soit flétrie!\n\n\n2.\nOui, tout brûlants d\'ardeur,\nNous t\'implorons, Seigneur,\nPour la patrie!\nMaintiens-la dans ses droits,\nSois sa force, sa vie,\nEt, qu\'à tes saintes lois,\nLa foi la lie!\n\n\n3.\nPère, Fils, Saint-Esprit,\nTrinité que tout craint,\nVois la Patrie!\nParle, et que converti\nPar ta voix obéie\nChaque enfant d\'Haïti\nTe glorifie!','<p><strong>1.</strong> </p>\n<p>O Dieu de nos aïeux, </p>\n<p>Daigne entendre nos voeux </p>\n<p>Pour la patrie. </p>\n<p>Toi qui brisas nos fers, </p>\n<p>Que la terre chérie </p>\n<p>Jamais par des revers </p>\n<p>Ne soit flétrie! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, tout brûlants d&#39;ardeur, </p>\n<p>Nous t&#39;implorons, Seigneur, </p>\n<p>Pour la patrie! </p>\n<p>Maintiens-la dans ses droits, </p>\n<p>Sois sa force, sa vie, </p>\n<p>Et, qu&#39;à tes saintes lois, </p>\n<p>La foi la lie! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Père, Fils, Saint-Esprit, </p>\n<p>Trinité que tout craint, </p>\n<p>Vois la Patrie! </p>\n<p>Parle, et que converti </p>\n<p>Par ta voix obéie </p>\n<p>Chaque enfant d&#39;Haïti </p>\n<p>Te glorifie! </p>\n','O-Dieu-De-Nos-Aieux',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1711,'a9136c1f-10c7-49c4-b6d1-2ae7b159765b',0,0,'Soldats de Christ et Haïtiens',320,'320_CE_fr','1.\nSoldats de Christ et Haïtiens,\nDu ciel nous sommes citoyens;\nDans la Parole du Seigneur,\nNous trouvons le seul vrai bonheur.\n\nRefrain\nSauve, Seigneur, bénis\nNotre chère Haïti!\nPetite nation,\nAvance vers Sion,\nA Dieu consacre-toi\nFais de Jésus ton Roi,\nSauve, Seigneur,\nBénis notre chère Haïti!\n\n\n2.\nSoldats de Christ et Haïtiens!\nSoyons unis à tous les siens,\nCar dans le Testament Nouveau,\nIl nous a marqués de son sceau.\n\n\n3.\nCompatriotes Haïtiens,\nDu ciel devenez citoyens.\nChantez avec nous désormais:\nHaïti pour Christ à jamais!','<p><strong>1.</strong> </p>\n<p>Soldats de Christ et Haïtiens, </p>\n<p>Du ciel nous sommes citoyens; </p>\n<p>Dans la Parole du Seigneur, </p>\n<p>Nous trouvons le seul vrai bonheur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Sauve, Seigneur, bénis</strong> </p>\n<p><strong>Notre chère Haïti!</strong> </p>\n<p><strong>Petite nation,</strong> </p>\n<p><strong>Avance vers Sion,</strong> </p>\n<p><strong>A Dieu consacre-toi</strong> </p>\n<p><strong>Fais de Jésus ton Roi,</strong> </p>\n<p><strong>Sauve, Seigneur,</strong> </p>\n<p><strong>Bénis notre chère Haïti!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Soldats de Christ et Haïtiens! </p>\n<p>Soyons unis à tous les siens, </p>\n<p>Car dans le Testament Nouveau, </p>\n<p>Il nous a marqués de son sceau. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Compatriotes Haïtiens, </p>\n<p>Du ciel devenez citoyens. </p>\n<p>Chantez avec nous désormais: </p>\n<p>Haïti pour Christ à jamais! </p>\n','Soldats-de-Christ-et-Haitiens',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1712,'62911e29-d098-4972-83e7-86ba6a7489b5',0,0,'Chaque Jour De Ma Vie',328,'328_CE_fr','1.\nChaque jour de ma vie,\nJe veux dire au Seigneur:\nApprends-moi, je te prie,\nA te donner mon coeur.\n\n\n2.\nDès que le jour commence,\nJe veux dire au Seigneur:\nTiens-moi dans ta présence,\nMon Dieu, mon Rédempteur!\n\n\n3.\nSouvent dans la journée,\nJe veux dire au Seigneur:\nToi qui me l\'as donnée,\nSois-en le bienfaiteur.\n\n\n4.\nEt quand vient la nuit sombre,\nJe veux dire au Seigneur:\nTu me vois sous ton ombre,\nGarde-moi ta faveur.','<p><strong>1.</strong> </p>\n<p>Chaque jour de ma vie, </p>\n<p>Je veux dire au Seigneur: </p>\n<p>Apprends-moi, je te prie, </p>\n<p>A te donner mon coeur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dès que le jour commence, </p>\n<p>Je veux dire au Seigneur: </p>\n<p>Tiens-moi dans ta présence, </p>\n<p>Mon Dieu, mon Rédempteur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Souvent dans la journée, </p>\n<p>Je veux dire au Seigneur: </p>\n<p>Toi qui me l&#39;as donnée, </p>\n<p>Sois-en le bienfaiteur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et quand vient la nuit sombre, </p>\n<p>Je veux dire au Seigneur: </p>\n<p>Tu me vois sous ton ombre, </p>\n<p>Garde-moi ta faveur. </p>\n','Chaque-Jour-De-Ma-Vie',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1713,'c12b9376-3dde-408c-875e-45db05acca05',0,0,'Tout Bonè Mouin Nan Jézu-Kri',10,'10_CE_ht','1.\nTout bonè mouin nan Jézu-Kri,\nKris pou mouin!  (2 foua)\nSan Jézu mouin pa gin la vi,\nKris pou mouin!\nSé li ki Roua-m, li sé Sovè-m,\nSé li-min’m tou ki bon doktè-m,\nSi-l pa té la, sa mouin ta fè?\nKris pou mouin!  (2 foua)\n\n\n2.\nMouin kité-n chaché lajan,\nKris pou mouin!  (2 foua)\nMouin gin kichoy pi konsékan,\nKris pou mouin!\nSi ou ban-m rad avèk bijou,\nSi ou fè-m rich nan gnou sèl jou,\nTou sa sé pa angnin toujou,\nKris pou mouin!  (2 foua)\n\n\n3.\nNan santé kou nan maladi,\nKris pou mouin!  (2 foua)\nLè kè-m kontan, lè m-ap soufri,\nKris pou mouin!\nLè tan-m rivé pou mouin mouri,\nLésé tout bagay nan la vi,\nMouin pap krié, mouin pap kouri,\nKris pou mouin!  (2 foua)','<p><strong>1.</strong> </p>\n<p>Tout bonè mouin nan Jézu-Kri, </p>\n<p>Kris pou mouin!  (2 foua) </p>\n<p>San Jézu mouin pa gin la vi, </p>\n<p>Kris pou mouin! </p>\n<p>Sé li ki Roua-m, li sé Sovè-m, </p>\n<p>Sé li-min’m tou ki bon doktè-m, </p>\n<p>Si-l pa té la, sa mouin ta fè? </p>\n<p>Kris pou mouin!  (2 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin kité-n chaché lajan, </p>\n<p>Kris pou mouin!  (2 foua) </p>\n<p>Mouin gin kichoy pi konsékan, </p>\n<p>Kris pou mouin! </p>\n<p>Si ou ban-m rad avèk bijou, </p>\n<p>Si ou fè-m rich nan gnou sèl jou, </p>\n<p>Tou sa sé pa angnin toujou, </p>\n<p>Kris pou mouin!  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan santé kou nan maladi, </p>\n<p>Kris pou mouin!  (2 foua) </p>\n<p>Lè kè-m kontan, lè m-ap soufri, </p>\n<p>Kris pou mouin! </p>\n<p>Lè tan-m rivé pou mouin mouri, </p>\n<p>Lésé tout bagay nan la vi, </p>\n<p>Mouin pap krié, mouin pap kouri, </p>\n<p>Kris pou mouin!  (2 foua) </p>\n','Tout-Bone-Mouin-Nan-Jezu-Kri',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1714,'e44671f6-0b9d-4886-b553-b6c5128142dd',0,0,'Minnin mouin, O Bon Sovè',64,'64_CE_ht','1.\nMinnin mouin, O Bon Sovè.\nKondui-m nan chémin sali.\nAvèk ou m-pa bézouin pè.\nNan ou sèl mouin jouin-n la vi.\n\nKè:Jézu, Jézu, pa kité-m, O Bon Sovè.\nFè-m soti nan tantasion.\nOu-a fè mouin plus ké vinkè.\n\n\n2.\nJézu, pa kité-m isi\nSou tè sa plin ak doulè.\nSan ou nan pouin lòt zanmi,\nMin nan ou mouin jouin-n bon-nè.\n\n\n3.\nOu té lévé-m nan la bou\nDé pi profon méchansté.\nOu-a fè-m rivé nan sièl tou.\nSé la gloua Ou ma chanté.','<p><strong>1.</strong> </p>\n<p>Minnin mouin, O Bon Sovè. </p>\n<p>Kondui-m nan chémin sali. </p>\n<p>Avèk ou m-pa bézouin pè. </p>\n<p>Nan ou sèl mouin jouin-n la vi. </p>\n<p>Kè:Jézu, Jézu, pa kité-m, O Bon Sovè. </p>\n<p>Fè-m soti nan tantasion. </p>\n<p>Ou-a fè mouin plus ké vinkè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu, pa kité-m isi </p>\n<p>Sou tè sa plin ak doulè. </p>\n<p>San ou nan pouin lòt zanmi, </p>\n<p>Min nan ou mouin jouin-n bon-nè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou té lévé-m nan la bou </p>\n<p>Dé pi profon méchansté. </p>\n<p>Ou-a fè-m rivé nan sièl tou. </p>\n<p>Sé la gloua Ou ma chanté. </p>\n','Minnin-mouin-O-Bon-Sove',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1715,'b1be5b25-d7cd-41d7-be05-8111968b3b52',0,0,'Nan lé mon-n mouin té pèdu nan péché',96,'96_CE_ht','1.\nNan lé mon-n mouin té pèdu nan péché;\nMin Jézu té sové-m, map ran-n gloua a li,\nAk kòd plézi lé mon-n, mouin té maré,\nKris té fè-m lib ak puisans li.\n\nKè:\nA la bèl jou, Jézu té jouin-n mouin !\nAk gran pitié li té lévé mouin;\nLi té ouété péché-m, ak li map maché;\nMouin gin la viktoua ak la joua nan nan-m mouin.\nA la bèl jou, O alélouya!\nJézu jouin-n sa-k tout bon pa Li;\nAlò m-a chanté: Ozanna!  Ozanna!\nLè m-ouè Jézu nan tout gloua li.\n\n\n2.\nSé té gnou jou, sièl la té plin joua,\nNan-m mouin-min’m tou tap santi tout joua sa-a;\nDépi lè a mouin toujou plin ak joua,\nPaské nan kè-m Jézu sé Roua.\n\n\n3.\nSa va gnou gran jou lè nou va ansan-m,\nNan sièl avèk Jézu pou létènité,\nPou nou rakonté istoua amou li\nPou-n toujou adoré non li.','<p><strong>1.</strong> </p>\n<p>Nan lé mon-n mouin té pèdu nan péché; </p>\n<p>Min Jézu té sové-m, map ran-n gloua a li, </p>\n<p>Ak kòd plézi lé mon-n, mouin té maré, </p>\n<p>Kris té fè-m lib ak puisans li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la bèl jou, Jézu té jouin-n mouin !</strong> </p>\n<p><strong>Ak gran pitié li té lévé mouin;</strong> </p>\n<p><strong>Li té ouété péché-m, ak li map maché;</strong> </p>\n<p><strong>Mouin gin la viktoua ak la joua nan nan-m mouin.</strong> </p>\n<p><strong>A la bèl jou, O alélouya!</strong> </p>\n<p><strong>Jézu jouin-n sa-k tout bon pa Li;</strong> </p>\n<p><strong>Alò m-a chanté: Ozanna!  Ozanna!</strong> </p>\n<p><strong>Lè m-ouè Jézu nan tout gloua li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé té gnou jou, sièl la té plin joua, </p>\n<p>Nan-m mouin-min’m tou tap santi tout joua sa-a; </p>\n<p>Dépi lè a mouin toujou plin ak joua, </p>\n<p>Paské nan kè-m Jézu sé Roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa va gnou gran jou lè nou va ansan-m, </p>\n<p>Nan sièl avèk Jézu pou létènité, </p>\n<p>Pou nou rakonté istoua amou li </p>\n<p>Pou-n toujou adoré non li. </p>\n','Nan-le-mon-n-mouin-te-pedu-nan-peche',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1716,'c2e57a20-8407-472f-a0bc-d357d1accac1',0,0,'Vini pou chanté',144,'144_CE_ht','1.\nVini pou chanté\nAvèk joua nan kè nou\nLè nou gingnin joua\nBon-Dieu, Li kontan tou\n\n2.\nSé Bon-Dieu ki fè\nTi zouazo yo chanté\nSé li ki mété\nLa joua nan kè nou tou\n\n3.\nLè gin nuaj nan siel\nLè loraj ap grondé\nSé pou nou sonjé\nTou sa gin pou fini\n\n4.\nChan té nan chemin\nAvèk kè nou kontan\nChanté jouk la fin\nTristès pa pou lontan','<p><strong>1.</strong> </p>\n<p>Vini pou chanté </p>\n<p>Avèk joua nan kè nou </p>\n<p>Lè nou gingnin joua </p>\n<p>Bon-Dieu, Li kontan tou </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé Bon-Dieu ki fè </p>\n<p>Ti zouazo yo chanté </p>\n<p>Sé li ki mété </p>\n<p>La joua nan kè nou tou </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè gin nuaj nan siel </p>\n<p>Lè loraj ap grondé </p>\n<p>Sé pou nou sonjé </p>\n<p>Tou sa gin pou fini </p>\n<p><br/> <strong>4.</strong></p>\n<p>Chan té nan chemin </p>\n<p>Avèk kè nou kontan </p>\n<p>Chanté jouk la fin </p>\n<p>Tristès pa pou lontan </p>\n','Vini-pou-chante',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1717,'3a1abba2-8f7e-4349-b4da-60c3e46d7293',0,0,'Tout kè ou, tout kè ou',1,'1_MJ_ht','1.\nTout kè ou, Tout kè ou,\nJézu vlé antré nan tout kè ou.\nTout kè ou, Tout kè ou,\nJézu vlé ou bay Li tout kè ou.','<p><strong>1.</strong> </p>\n<p>Tout kè ou, Tout kè ou, </p>\n<p>Jézu vlé antré nan tout kè ou. </p>\n<p>Tout kè ou, Tout kè ou, </p>\n<p>Jézu vlé ou bay Li tout kè ou. </p>\n','Tout-ke-ou-tout-ke-ou',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1718,'89bddaa6-69cf-4cc0-a80d-5aaf61b0212c',0,0,'Promès la yo byin dous',3,'3_MJ_ht','1.\nPromès la yo byin dou,\nParòl la bon,\nLi pi présié pasé\nNinpòt ki lòt mésaj;\nKris té gin pansé prop,\nLi san péché,\nLi sé pi gran égzanp ki janmin égzisté.\n\nKè:\nLè-L…kondui-m ma suiv Li,\nSuiv…Li pou toujou,\nLè-L…kondui-m ma suiv Li.\nMouin va suiv Li pou toujou.\n\n2.\nLamou Jézu montré-m, sé mèvéyé,\nPi dous pasé tou sa\nLèz òm kapab pansé,\nPou sa yo ki pèdu, L-apé plédé,\nLi sé pi gran égzanp ki janmin égzisté.\n\n3.\n«Vini jouin-n mouin» sé sa L-ap répété,\nPou ou gingnin répo ou-min’m ki fatigé,\nKonfié nan promès Li, sé asuré,\nLi sé pi gran égzanp ki janmin égzisté.','<p><strong>1.</strong> </p>\n<p>Promès la yo byin dou, </p>\n<p>Paròl la bon, </p>\n<p>Li pi présié pasé </p>\n<p>Ninpòt ki lòt mésaj; </p>\n<p>Kris té gin pansé prop, </p>\n<p>Li san péché, </p>\n<p>Li sé pi gran égzanp ki janmin égzisté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè-L…kondui-m ma suiv Li,</strong> </p>\n<p><strong>Suiv…Li pou toujou,</strong> </p>\n<p><strong>Lè-L…kondui-m ma suiv Li.</strong> </p>\n<p><strong>Mouin va suiv Li pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lamou Jézu montré-m, sé mèvéyé, </p>\n<p>Pi dous pasé tou sa </p>\n<p>Lèz òm kapab pansé, </p>\n<p>Pou sa yo ki pèdu, L-apé plédé, </p>\n<p>Li sé pi gran égzanp ki janmin égzisté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>«Vini jouin-n mouin» sé sa L-ap répété, </p>\n<p>Pou ou gingnin répo ou-min’m ki fatigé, </p>\n<p>Konfié nan promès Li, sé asuré, </p>\n<p>Li sé pi gran égzanp ki janmin égzisté. </p>\n','Promes-la-yo-byin-dous',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1719,'288a70ca-c0d0-461d-9f42-eff2cb6808a6',0,0,'Kè Mouin Ranpli Ak Joua, Lè-m Maché Isi-ba',20,'20_MJ_ht','1.\nKè mouin ranpli ak joua, lè-m maché isi-ba,\nJézu-Kri té sové-m\nLi ouété tout péché-m, kè mouin va loué Li\nNan sélès armoni,\nLè Jézu tounin chèché mouin.','<p><strong>1.</strong> </p>\n<p>Kè mouin ranpli ak joua, lè-m maché isi-ba, </p>\n<p>Jézu-Kri té sové-m </p>\n<p>Li ouété tout péché-m, kè mouin va loué Li </p>\n<p>Nan sélès armoni, </p>\n<p>Lè Jézu tounin chèché mouin. </p>\n','Ke-Mouin-Ranpli-Ak-Joua-Le-m-Mache-Isi-ba',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1720,'0915bde7-c458-49cb-8198-43ecdc04b0fa',0,0,'Gin Joua Lè N-apé Suiv Jézu',36,'36_MJ_ht','1.\nGin joua lè n-apé suiv Jézu,\nNan chak moman nan jou a:\nGin joua lè n-apé suiv Jézu\nNan tout chémin yo ki isi ba.\nOui, m-ap pral tout koté Li voyé-m\nSou tè a é sou la mè;\nGin joua lè n-apé suiv Jézu\nSé joua é bonnè.','<p><strong>1.</strong> </p>\n<p>Gin joua lè n-apé suiv Jézu, </p>\n<p>Nan chak moman nan jou a: </p>\n<p>Gin joua lè n-apé suiv Jézu </p>\n<p>Nan tout chémin yo ki isi ba. </p>\n<p>Oui, m-ap pral tout koté Li voyé-m </p>\n<p>Sou tè a é sou la mè; </p>\n<p>Gin joua lè n-apé suiv Jézu </p>\n<p>Sé joua é bonnè. </p>\n','Gin-Joua-Le-N-ape-Suiv-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1721,'4ef9525e-1a84-4740-a477-289a0c22e086',0,0,'Monn Sa Sé Pou Papa-m',51,'51_MJ_ht','1.\nMon-n sa sé pou papa-m\nSé Li ki té fè li\nZouazo nan boua chanté ak joua\nPou loué bonté Li.\n\nKè:\nMon-n sa sé pou papa-m\nSé Li ki Tou-Puisan\nSièl-la, tè-a ak la mè,\nTout di ki jan Li gran.\n\n2.\nMon-n sa sé pou papa-m\nMap kouté tou patou\nZétoual, solèy yo tout palé\nKi jan Li rinmin nou.\n\n3.\nMon-n sa sé pou papa-m\nA la kè mouin kontan;\nLi rinmin mouin jiska la fin,\nMouin pou Li pou tout tan.','<p><strong>1.</strong> </p>\n<p>Mon-n sa sé pou papa-m </p>\n<p>Sé Li ki té fè li </p>\n<p>Zouazo nan boua chanté ak joua </p>\n<p>Pou loué bonté Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mon-n sa sé pou papa-m</strong> </p>\n<p><strong>Sé Li ki Tou-Puisan</strong> </p>\n<p><strong>Sièl-la, tè-a ak la mè,</strong> </p>\n<p><strong>Tout di ki jan Li gran.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon-n sa sé pou papa-m </p>\n<p>Map kouté tou patou </p>\n<p>Zétoual, solèy yo tout palé </p>\n<p>Ki jan Li rinmin nou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon-n sa sé pou papa-m </p>\n<p>A la kè mouin kontan; </p>\n<p>Li rinmin mouin jiska la fin, </p>\n<p>Mouin pou Li pou tout tan. </p>\n','Monn-Sa-Se-Pou-Papa-m',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1722,'75214b1e-6d55-46ec-a814-3223ea39d32d',0,0,'Na Ouè Li Va La Pinn',54,'54_MJ_ht','1.\nNa ouè li vo la pin-n\nLè nou ouè Jézu\nTraka la vi sa-a sanblé piti\nNa gadé nan fas-Li\nTout chagrin va fini.\nDonk kouri ak kouraj\nJouk nou ouè Kris.','<p><strong>1.</strong> </p>\n<p>Na ouè li vo la pin-n </p>\n<p>Lè nou ouè Jézu </p>\n<p>Traka la vi sa-a sanblé piti </p>\n<p>Na gadé nan fas-Li </p>\n<p>Tout chagrin va fini. </p>\n<p>Donk kouri ak kouraj </p>\n<p>Jouk nou ouè Kris. </p>\n','Na-Oue-Li-Va-La-Pinn',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1723,'85843eb4-4a2d-439e-874f-dbee4486c727',0,0,'Bib La Kanpé Fèm Kom Gnou Roch Solid',56,'56_MJ_ht','1.\nBib la kanpé fèm kòm gnou roch solid\nMalgré lénmi ataké-l,\nMésaj li gravé nan kè péchè yo\nPou bay yo la vi étènèl.\n\nKè:\nBib la kanpé fè lè mòn yo tranblé,\nLap rété solid si tè a déplasé,\nAn nou bazé la foua nou sou li\nPaské  Bib la rété fèm.\n\n2.\nBib la kanpé, l-a kanpé pou toujou,\nLè lé mon-n gin tan pasé,\nMouin vlé obéi tout présèt li yo,\nSé sèl liv la ki inspiré.\n\n3.\nBib la pasé tout éprèv nou ba li\nPaské otè li divin.\nPa gras, mouin vlé viv dakò avèk li,\nOui, Paròl Bon-Dieu sé pou mouin.','<p><strong>1.</strong> </p>\n<p>Bib la kanpé fèm kòm gnou roch solid </p>\n<p>Malgré lénmi ataké-l, </p>\n<p>Mésaj li gravé nan kè péchè yo </p>\n<p>Pou bay yo la vi étènèl. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bib la kanpé fè lè mòn yo tranblé,</strong> </p>\n<p><strong>Lap rété solid si tè a déplasé,</strong> </p>\n<p><strong>An nou bazé la foua nou sou li</strong> </p>\n<p><strong>Paské  Bib la rété fèm.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bib la kanpé, l-a kanpé pou toujou, </p>\n<p>Lè lé mon-n gin tan pasé, </p>\n<p>Mouin vlé obéi tout présèt li yo, </p>\n<p>Sé sèl liv la ki inspiré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bib la pasé tout éprèv nou ba li </p>\n<p>Paské otè li divin. </p>\n<p>Pa gras, mouin vlé viv dakò avèk li, </p>\n<p>Oui, Paròl Bon-Dieu sé pou mouin. </p>\n','Bib-La-Kanpe-Fem-Kom-Gnou-Roch-Solid',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1724,'ee6db7f3-9990-4a3a-93a7-3592dcad912e',0,0,'Je Viens, Seigneur, A Ce Baptême',335,'335_CE_fr','1.\nJe viens, Seigneur, à ce baptême,\nOù tu descendis avant moi,\nHeureux de mourir à moi-même,\nAfin de vivre comme toi.\n\n\n2.\nAux plaisirs, aux gloires du monde,\nJe dis un éternel adieu;\nJe livre aux profondeurs de l\'onde\nTout ce qui déplaît à mon Dieu.\n\n\n3.\nAprès tant d\'erreurs et de chutes,\nTant de péchés et de remords,\nJe renonce à mes vaines luttes\nEt me réfugie en ta mort.\n\n\n4.\nC\'est pour moi que tu l\'as soufferte,\nVictime sainte, Agneau très doux!\nPour moi ton âme fut offerte\nEt ton corps meurtri par les clous.\n\n\n5.\nA ton baptême, à ton calice,\nJe veux avoir part en ce jour\nEt goûter, dans le sacrifice,\nLa douceur de ton grand amour!','<p><strong>1.</strong> </p>\n<p>Je viens, Seigneur, à ce baptême, </p>\n<p>Où tu descendis avant moi, </p>\n<p>Heureux de mourir à moi-même, </p>\n<p>Afin de vivre comme toi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Aux plaisirs, aux gloires du monde, </p>\n<p>Je dis un éternel adieu; </p>\n<p>Je livre aux profondeurs de l&#39;onde </p>\n<p>Tout ce qui déplaît à mon Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Après tant d&#39;erreurs et de chutes, </p>\n<p>Tant de péchés et de remords, </p>\n<p>Je renonce à mes vaines luttes </p>\n<p>Et me réfugie en ta mort. </p>\n<p><br/> <strong>4.</strong></p>\n<p>C&#39;est pour moi que tu l&#39;as soufferte, </p>\n<p>Victime sainte, Agneau très doux! </p>\n<p>Pour moi ton âme fut offerte </p>\n<p>Et ton corps meurtri par les clous. </p>\n<p><br/> <strong>5.</strong></p>\n<p>A ton baptême, à ton calice, </p>\n<p>Je veux avoir part en ce jour </p>\n<p>Et goûter, dans le sacrifice, </p>\n<p>La douceur de ton grand amour! </p>\n','Je-Viens-Seigneur-A-Ce-Bapteme',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1725,'f75fd6be-fd0b-4df0-9376-79020d536b15',0,0,'Oh! Que Ta Main Paternelle',326,'326_CE_fr','1.\nOh! Que ta main paternelle\nMe bénisse à mon coucher!\nEt que ce soit sous ton aile\nQue je dorme, ô mon Berger!  (bis)\n\n\n2.\nVeuille effacer, par ta grâce,\nLes péchés que j\'ai commis,\nEt que ton Esprit me fasse\nObéissant et soumis!  (bis)\n\n\n3.\nFais reposer sous ta garde\nMes amis et mes parents,\nEt que ton oeil les regarde,\nDe ton ciel, petits et grands!  (bis)\n\n\n4.\nQue ta faveur se déploie\nPour consoler l\'affligé;\nDonne au pauvre un peu de joie,\nAu malade la santé!  (bis)\n\n\n5.\nSeigneur, j\'ai fais ma prière;\nSous ton aile je m\'endors,\nHeureux de savoir qu\'un Père\nPlein d\'amour veille au dehors!  (bis)','<p><strong>1.</strong> </p>\n<p>Oh! Que ta main paternelle </p>\n<p>Me bénisse à mon coucher! </p>\n<p>Et que ce soit sous ton aile </p>\n<p>Que je dorme, ô mon Berger!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Veuille effacer, par ta grâce, </p>\n<p>Les péchés que j&#39;ai commis, </p>\n<p>Et que ton Esprit me fasse </p>\n<p>Obéissant et soumis!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fais reposer sous ta garde </p>\n<p>Mes amis et mes parents, </p>\n<p>Et que ton oeil les regarde, </p>\n<p>De ton ciel, petits et grands!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que ta faveur se déploie </p>\n<p>Pour consoler l&#39;affligé; </p>\n<p>Donne au pauvre un peu de joie, </p>\n<p>Au malade la santé!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Seigneur, j&#39;ai fais ma prière; </p>\n<p>Sous ton aile je m&#39;endors, </p>\n<p>Heureux de savoir qu&#39;un Père </p>\n<p>Plein d&#39;amour veille au dehors!  (bis) </p>\n','Oh!-Que-Ta-Main-Paternelle',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1726,'5d9c2a12-37a3-45cd-9dac-d26090cfdf1e',0,0,'San Jézu, mouin pa kapab viv',11,'11_CE_ht','1.\nSan Jézu mouin pa kapab viv,\nMouin pa sa fè gnou sèl pa;\nSé li-min’m sèl mouin ta vlé suiv\nMouin suiv li jouk sa kaba.\n\nKè:\nAngnin mouin pa gin pou-m krin-n,\nPuiské Sovè-m ap véyé;\nMa suiv li san mouin pa jan-m plin-n\nLi pap kité mouin néyé.\n\n\n2.\nAmou li avèk promès li\nPlin kè mouin ak gnou gran joua;\nMouin mèt tou fèb, mouin mèt kagou,\nSètin l-a ban-m la viktoua.\n\n\n3.\nLè fè noua vi-n pou fè-m tranblé,\nAvèk li mouin brav fasil;\nLè voua Satan apé grondé\nMouin rété dousman trankil.\n\n\n4.\nJézu, ouété tou sa ki tròp\nNan kè mouin, pou mouin maché,\nPurifié mouin, lavé-m, fè-m pròp\nJan Bon-Dieu vlé pou mouin yé.','<p><strong>1.</strong> </p>\n<p>San Jézu mouin pa kapab viv, </p>\n<p>Mouin pa sa fè gnou sèl pa; </p>\n<p>Sé li-min’m sèl mouin ta vlé suiv </p>\n<p>Mouin suiv li jouk sa kaba. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Angnin mouin pa gin pou-m krin-n,</strong> </p>\n<p><strong>Puiské Sovè-m ap véyé;</strong> </p>\n<p><strong>Ma suiv li san mouin pa jan-m plin-n</strong> </p>\n<p><strong>Li pap kité mouin néyé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Amou li avèk promès li </p>\n<p>Plin kè mouin ak gnou gran joua; </p>\n<p>Mouin mèt tou fèb, mouin mèt kagou, </p>\n<p>Sètin l-a ban-m la viktoua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè fè noua vi-n pou fè-m tranblé, </p>\n<p>Avèk li mouin brav fasil; </p>\n<p>Lè voua Satan apé grondé </p>\n<p>Mouin rété dousman trankil. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu, ouété tou sa ki tròp </p>\n<p>Nan kè mouin, pou mouin maché, </p>\n<p>Purifié mouin, lavé-m, fè-m pròp </p>\n<p>Jan Bon-Dieu vlé pou mouin yé. </p>\n','San-Jezu-mouin-pa-kapab-viv',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1727,'f3a6c26a-2bbd-4119-ab28-961c0e517f01',0,0,'Gen you zanmi, you bon zanmi',77,'77_CE_ht','1.\nGin gnou zanmi, gnou bon zanmi,\nLi rinmin-m dépi tout tan.\nLi gingnin kè mouin ak lamou,\nLi tankou chin-n pou kinbé-m.\nPèsòn pa kab kasé chin-n nan,\nLi kinbé-m pa lamou li.\nMouin pou Li-min’m, Li pou mouin tou,\nPou tout tan é pou tout tan.\n\nKè:\nMouin pou Li-min’m, Li pou mouin tou,\nPou tout tan é pou tout tan.  (2 li-n 2 foua)\n\n\n2.\nGin gnou zanmi, gnou bon zanmi.\nLi té mouri pou sové-m,\nLi té ban mouin, avèk la vi,\nGnou lot kado, sé Li-min’m!\nTou sa-m gingnin, sé pa pou mouin,\nTout bagay sé pou Zanmi-m;\nKè mouin, é vi-m, tout sé pou Li,\nTout sé pou Li pou tout tan.\n\n\n3.\nGin gnou zanmin, gnou bon zanmi,\nLi gin bon kè, Li janti.\nLi sé gnou gid, konséyè,\nÉpi Li kon-n défan-n mouin.\nKi moun kapab séparé mouin\nAvèk Li-min’m kap rinmin-m?\nNi nan lan mò, ni lanfè min-m!\nNon! Mouin pou Li pou tout tan.','<p><strong>1.</strong> </p>\n<p>Gin gnou zanmi, gnou bon zanmi, </p>\n<p>Li rinmin-m dépi tout tan. </p>\n<p>Li gingnin kè mouin ak lamou, </p>\n<p>Li tankou chin-n pou kinbé-m. </p>\n<p>Pèsòn pa kab kasé chin-n nan, </p>\n<p>Li kinbé-m pa lamou li. </p>\n<p>Mouin pou Li-min’m, Li pou mouin tou, </p>\n<p>Pou tout tan é pou tout tan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin pou Li-min’m, Li pou mouin tou,</strong> </p>\n<p><strong>Pou tout tan é pou tout tan.  (2 li-n 2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin gnou zanmi, gnou bon zanmi. </p>\n<p>Li té mouri pou sové-m, </p>\n<p>Li té ban mouin, avèk la vi, </p>\n<p>Gnou lot kado, sé Li-min’m! </p>\n<p>Tou sa-m gingnin, sé pa pou mouin, </p>\n<p>Tout bagay sé pou Zanmi-m; </p>\n<p>Kè mouin, é vi-m, tout sé pou Li, </p>\n<p>Tout sé pou Li pou tout tan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin gnou zanmin, gnou bon zanmi, </p>\n<p>Li gin bon kè, Li janti. </p>\n<p>Li sé gnou gid, konséyè, </p>\n<p>Épi Li kon-n défan-n mouin. </p>\n<p>Ki moun kapab séparé mouin </p>\n<p>Avèk Li-min’m kap rinmin-m? </p>\n<p>Ni nan lan mò, ni lanfè min-m! </p>\n<p>Non! Mouin pou Li pou tout tan. </p>\n','Gen-you-zanmi-you-bon-zanmi',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1728,'e2f9bd59-66d2-42ef-9198-8ad0abc0d58c',0,0,'Nou pa krin-n ankò',85,'85_CE_ht','1.\nNou pa krin-n ankò,\nNi troub ni lan mò!\nLéspri a vi-ni,\nJézu ki alé, voyé lé Sint-Éspri.\n\n\n2.\nJézu ki la pè,\nNan sièl la li yé.\nLi voyé gnou gaj\nKi palé nan kè nou gnou bèl témouagnaj.\n\n\n3.\nJézu rinmin nou;\nLi pran-n nan la bou;\nLi fè-n gnou kado,\nLi pap jan-m ouété Sint-Éspri a ankò.\n\n\n4.\nLi, ki avèk nou,\nSèvi kou gid tou.\nLi ban nou lamou\nLi tabli rouayom lamou li nan kè nou.\n\n\n5.\nTout moun yo ki kouè,\nYo va délivré:\nYo konnin la pè,\nAk puisans é la joua, yo va délivré.\n\n\n6.\nAn nou plin la joua,\nChanté ak tout voua,\nNap suiv lé Ségnè,\nNap vink avèk li juska la dèniè è.','<p><strong>1.</strong> </p>\n<p>Nou pa krin-n ankò, </p>\n<p>Ni troub ni lan mò! </p>\n<p>Léspri a vi-ni, </p>\n<p>Jézu ki alé, voyé lé Sint-Éspri. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu ki la pè, </p>\n<p>Nan sièl la li yé. </p>\n<p>Li voyé gnou gaj </p>\n<p>Ki palé nan kè nou gnou bèl témouagnaj. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu rinmin nou; </p>\n<p>Li pran-n nan la bou; </p>\n<p>Li fè-n gnou kado, </p>\n<p>Li pap jan-m ouété Sint-Éspri a ankò. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li, ki avèk nou, </p>\n<p>Sèvi kou gid tou. </p>\n<p>Li ban nou lamou </p>\n<p>Li tabli rouayom lamou li nan kè nou. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Tout moun yo ki kouè, </p>\n<p>Yo va délivré: </p>\n<p>Yo konnin la pè, </p>\n<p>Ak puisans é la joua, yo va délivré. </p>\n<p><br/> <strong>6.</strong></p>\n<p>An nou plin la joua, </p>\n<p>Chanté ak tout voua, </p>\n<p>Nap suiv lé Ségnè, </p>\n<p>Nap vink avèk li juska la dèniè è. </p>\n','Nou-pa-krin-n-anko',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1729,'2295dd6a-c847-47d2-8cde-d9672b66ea4a',0,0,'Chè Ségnè, kinbin min-m, Édé mouin rété fèm',114,'114_CE_ht','1.\nChè Ségnè, kinbin min-m, Édé mouin rété fèm;\nMouin bouké, fatigé, tèlman.\nNan loraj, nan fènoua, kléré rout la dévan;\nKinbin min-m, chè Ségnè, fè-m rivé.\n\n\n2.\nLè chémin difisil, É m-bézouin ou anpil,\nSégnè, rété la tou pré mouin.\nLè mouin prèt pou alé, Édé-m pou-m pa buté,\nKinbin min-m, chè Ségnè, fè-m rivé.\n\n\n3.\nLè solèy la kouché, É nuit la ap proché,\nÉ tout la vi mouin va fini,\nÉdé-m pa régrété, Nan ou sèl mouin konfié,\nKinbin min-m, chè Ségnè, fè-m rivé.','<p><strong>1.</strong> </p>\n<p>Chè Ségnè, kinbin min-m, Édé mouin rété fèm; </p>\n<p>Mouin bouké, fatigé, tèlman. </p>\n<p>Nan loraj, nan fènoua, kléré rout la dévan; </p>\n<p>Kinbin min-m, chè Ségnè, fè-m rivé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè chémin difisil, É m-bézouin ou anpil, </p>\n<p>Ségnè, rété la tou pré mouin. </p>\n<p>Lè mouin prèt pou alé, Édé-m pou-m pa buté, </p>\n<p>Kinbin min-m, chè Ségnè, fè-m rivé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè solèy la kouché, É nuit la ap proché, </p>\n<p>É tout la vi mouin va fini, </p>\n<p>Édé-m pa régrété, Nan ou sèl mouin konfié, </p>\n<p>Kinbin min-m, chè Ségnè, fè-m rivé. </p>\n','Che-Segne-kinbin-min-m-Ede-mouin-rete-fem',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1730,'dd5e678d-5b71-4f3a-a55e-0322f0919a98',0,0,'Mouin té chouazi nèt pou toujou',118,'118_CE_ht','1.\nMouin té chouazi nèt pou toujou\nAnt mon-n sa é Fis Bon-Dieu;\nAngnin pap chanjé mouin du tou,\nPaské Li fi-n pran kè-m vré.\nPran mon-n sa ak tout plézi li,\nPran yo, pran yo, kout ké kout,\nBan mouin Jézu, présié Sovè-m,\nLi pi dous pasé yo tout.\n\nKè:  Pi dous, pi dous pasé tout,\nPi dous, pi dous pasé tout.\nBan mouin Jézu, présié Sovè-m,\nLi pi dous pasé yo tout.\n\n\n2.\nDépi m-fè choua mouin pou toujou,\nDépi m-maché avèk Kris,\nVi mouin vi-ni pi rich chak jou;\nParol Li touché-m pi plus.\nPran mon-n sa ak tout richès li,\nPran yo, pran yo, kout ké kout,\nBan mouin Jézu, présié Sovè-m,\nLi pi dous pasé yo tout.\n\n\n3.\nDépi mouin fè choua pou sièl la,\nDépi Kris pi bon zanmi-m,\nTout moun kité-m sou tè sa,\nMin nan Kris mouin jouin-n apui-m.\nPran tout amitié sou tè a,\nPran yo, pran yo, kout ké kout,\nBan mouin Jézu, présié Sovè-m;\nLi pi dous pasé yo tout.\n\n\n4.\nLè zèv la vi mouin achévé,\nLè mouin pasé pòt lan mò,\nLè mouin monté koté Bon-Dieu,\nLè m-a maché bò lot bò,\nLè jé mouin gin tan ouè zanj yo,\nGran ak piti, m-a ouè tout.\nBan mouin Jézu, présié Sovè-m,\nLi pi dous pasé yo tout.','<p><strong>1.</strong> </p>\n<p>Mouin té chouazi nèt pou toujou </p>\n<p>Ant mon-n sa é Fis Bon-Dieu; </p>\n<p>Angnin pap chanjé mouin du tou, </p>\n<p>Paské Li fi-n pran kè-m vré. </p>\n<p>Pran mon-n sa ak tout plézi li, </p>\n<p>Pran yo, pran yo, kout ké kout, </p>\n<p>Ban mouin Jézu, présié Sovè-m, </p>\n<p>Li pi dous pasé yo tout. </p>\n<p>Kè:  Pi dous, pi dous pasé tout, </p>\n<p>Pi dous, pi dous pasé tout. </p>\n<p>Ban mouin Jézu, présié Sovè-m, </p>\n<p>Li pi dous pasé yo tout. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dépi m-fè choua mouin pou toujou, </p>\n<p>Dépi m-maché avèk Kris, </p>\n<p>Vi mouin vi-ni pi rich chak jou; </p>\n<p>Parol Li touché-m pi plus. </p>\n<p>Pran mon-n sa ak tout richès li, </p>\n<p>Pran yo, pran yo, kout ké kout, </p>\n<p>Ban mouin Jézu, présié Sovè-m, </p>\n<p>Li pi dous pasé yo tout. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dépi mouin fè choua pou sièl la, </p>\n<p>Dépi Kris pi bon zanmi-m, </p>\n<p>Tout moun kité-m sou tè sa, </p>\n<p>Min nan Kris mouin jouin-n apui-m. </p>\n<p>Pran tout amitié sou tè a, </p>\n<p>Pran yo, pran yo, kout ké kout, </p>\n<p>Ban mouin Jézu, présié Sovè-m; </p>\n<p>Li pi dous pasé yo tout. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè zèv la vi mouin achévé, </p>\n<p>Lè mouin pasé pòt lan mò, </p>\n<p>Lè mouin monté koté Bon-Dieu, </p>\n<p>Lè m-a maché bò lot bò, </p>\n<p>Lè jé mouin gin tan ouè zanj yo, </p>\n<p>Gran ak piti, m-a ouè tout. </p>\n<p>Ban mouin Jézu, présié Sovè-m, </p>\n<p>Li pi dous pasé yo tout. </p>\n','Mouin-te-chouazi-net-pou-toujou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1731,'c2eee1c7-e6d9-4d12-a20e-1e3ae366a6ef',0,0,'Nap chanté gnou kantik Noèl',120,'120_CE_ht','1.\nNap chanté gnou kantik Noèl,\nBèl fèt ki ran-n nou tout jouayé!\nJou Noèl nan tan lontan,\nJézu-Kri té vini sou la té.\nLi té désan’n – Pami lè zòm,\nKou gnou ti moun.\nÉ lap voyé:  Bòn volonté\nÉ la pé pou tout moun!\n\nKè:  Sonnin bi-in bèl\nTout klòch Noèl.\nLè Kris vini\nPou rét ak nou –\nÉma-nuèl, ki non si bèl.\n\n\n2.\nBéjè yo kouté – tout sézi,\nLè anj la di gnou si bèl mo.\nTout foul zanj yo tap chanté:\nTout gloua pou Bon-Dieu nan lié trè ro!\nKi gran lamou- Li gin pou nou,\nKi fè-l désan-n.\nPrémié Noèl – Jou la si bèl\nPou tou lè zòm!\n\n\n3.\nNap tandé bèl kantik ankò,\nÉ nap vini présé, présé.\nNou vlé ofri-L gnou kado,\nSé pa lò, min kè nou nap pòté.\nLi té vini – Li té soufri\nJusk a lan mò,\nPou minnin nou – Ak tout moun tou,\nNan kay-L an ro.','<p><strong>1.</strong> </p>\n<p>Nap chanté gnou kantik Noèl, </p>\n<p>Bèl fèt ki ran-n nou tout jouayé! </p>\n<p>Jou Noèl nan tan lontan, </p>\n<p>Jézu-Kri té vini sou la té. </p>\n<p>Li té désan’n – Pami lè zòm, </p>\n<p>Kou gnou ti moun. </p>\n<p>É lap voyé:  Bòn volonté </p>\n<p>É la pé pou tout moun! </p>\n<p>Kè:  Sonnin bi-in bèl </p>\n<p>Tout klòch Noèl. </p>\n<p>Lè Kris vini </p>\n<p>Pou rét ak nou – </p>\n<p>Éma-nuèl, ki non si bèl. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Béjè yo kouté – tout sézi, </p>\n<p>Lè anj la di gnou si bèl mo. </p>\n<p>Tout foul zanj yo tap chanté: </p>\n<p>Tout gloua pou Bon-Dieu nan lié trè ro! </p>\n<p>Ki gran lamou- Li gin pou nou, </p>\n<p>Ki fè-l désan-n. </p>\n<p>Prémié Noèl – Jou la si bèl </p>\n<p>Pou tou lè zòm! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nap tandé bèl kantik ankò, </p>\n<p>É nap vini présé, présé. </p>\n<p>Nou vlé ofri-L gnou kado, </p>\n<p>Sé pa lò, min kè nou nap pòté. </p>\n<p>Li té vini – Li té soufri </p>\n<p>Jusk a lan mò, </p>\n<p>Pou minnin nou – Ak tout moun tou, </p>\n<p>Nan kay-L an ro. </p>\n','Nap-chante-gnou-kantik-Noel',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1732,'b9f17817-679c-4172-a224-33de8377b193',0,0,'An-n chanté louanj Bon-Dieu papa nou',122,'122_CE_ht','1.\nAn-n chanté louanj Bon-Dieu papa nou\nNan gnou bèl kantik damou.\n\nKè:  Paské bonté li duré,\nToujou fidèl, asuré.\n\n\n2.\nSé li ki fè gnou si bèl solèy,\nLa lun, zétoual, tout mèmèy.\n\n\n3.\nLi voyé la rouzé ak la pli\nPou grin-n jèmin, plant grandi.\n\n\n4.\nLi kòmandé tout jadin fètil,\nPou fè rékolt yo anpil.\n\n\n5.\nRémèsié li pou viv tè ak foui\nTout danré li fè vini.\n\n\n6.\nRémèsié li pou tout zannimo,\nPouason nan dlo, tout zouazo.\n\n\n7.\nGras a li, la vi-n pa an danjé,\nLi louvri min-l, nou manjé.\n\n\n8.\nMin li ba nou manjé ki pi bèl,\nJézu, pin ki sòt nan sièl.\n\n\n9. Chanté, chanté louanj Papa sila,\nTout moun chanté ak tout voua.\n\nDènié Kè:\nDieu lé Pè, Fis, Sint-Éspri,\nGloua non ou pap jan-m fini.','<p><strong>1.</strong> </p>\n<p>An-n chanté louanj Bon-Dieu papa nou </p>\n<p>Nan gnou bèl kantik damou. </p>\n<p>Kè:  Paské bonté li duré, </p>\n<p>Toujou fidèl, asuré. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé li ki fè gnou si bèl solèy, </p>\n<p>La lun, zétoual, tout mèmèy. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li voyé la rouzé ak la pli </p>\n<p>Pou grin-n jèmin, plant grandi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li kòmandé tout jadin fètil, </p>\n<p>Pou fè rékolt yo anpil. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Rémèsié li pou viv tè ak foui </p>\n<p>Tout danré li fè vini. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Rémèsié li pou tout zannimo, </p>\n<p>Pouason nan dlo, tout zouazo. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Gras a li, la vi-n pa an danjé, </p>\n<p>Li louvri min-l, nou manjé. </p>\n<p><br/> <strong>8.</strong></p>\n<p>Min li ba nou manjé ki pi bèl, </p>\n<p>Jézu, pin ki sòt nan sièl. </p>\n<ol>\n<li>Chanté, chanté louanj Papa sila, </li>\n</ol>\n<p>Tout moun chanté ak tout voua. </p>\n<p>Dènié Kè: </p>\n<p>Dieu lé Pè, Fis, Sint-Éspri, </p>\n<p>Gloua non ou pap jan-m fini. </p>\n','An-n-chante-louanj-Bon-Dieu-papa-nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1733,'c27e15b4-edac-4a5a-a7b6-d836bf82b759',0,0,'Dépi tan lontan, Bon-Dieu',126,'126_CE_ht','1.\nDépi tan lontan, Bon-Dieu\nTé promèt nou Jézu-Kri.\nJou Noèl, gnou jou dé gloua,\nLi té vi-n bay tout moun joua.\n\nKè:\nMin, bèl jou sa a rivé,\nJou nou ouè lé Kris Li-min’m.\nChanté tou patou sou tè.\nJézu fèt nan Bétléèm!\n\n\n2.\nBèjé, di nou li, tan pri,\nKi nouvèl nou gin pou nou?\nPouki nou vini isit,\nKité troupo yo nan mòn?\n\n\n3.\nLè nou véyé nan la nui,\nMin! nou ouè gnou bèl lumiè!\nZanj yo vini chanté – ap di:\nSovè a vi-n sou tè.\n\n\n4.\nO Jézu, ki gran amou\nTé fè ou vini désan-n –\nKité sièl la, la kay ou,\nPou rété pami lè zòm?\n\n\n5.\nSégnè Jézu, fè nou ouè\nGnou si bèl dousè nan ou,\nKé nou plin gnou gran anvi\nPou sanblé ou plus chak jou.','<p><strong>1.</strong> </p>\n<p>Dépi tan lontan, Bon-Dieu </p>\n<p>Té promèt nou Jézu-Kri. </p>\n<p>Jou Noèl, gnou jou dé gloua, </p>\n<p>Li té vi-n bay tout moun joua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Min, bèl jou sa a rivé,</strong> </p>\n<p><strong>Jou nou ouè lé Kris Li-min’m.</strong> </p>\n<p><strong>Chanté tou patou sou tè.</strong> </p>\n<p><strong>Jézu fèt nan Bétléèm!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bèjé, di nou li, tan pri, </p>\n<p>Ki nouvèl nou gin pou nou? </p>\n<p>Pouki nou vini isit, </p>\n<p>Kité troupo yo nan mòn? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè nou véyé nan la nui, </p>\n<p>Min! nou ouè gnou bèl lumiè! </p>\n<p>Zanj yo vini chanté – ap di: </p>\n<p>Sovè a vi-n sou tè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Jézu, ki gran amou </p>\n<p>Té fè ou vini désan-n – </p>\n<p>Kité sièl la, la kay ou, </p>\n<p>Pou rété pami lè zòm? </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ségnè Jézu, fè nou ouè </p>\n<p>Gnou si bèl dousè nan ou, </p>\n<p>Ké nou plin gnou gran anvi </p>\n<p>Pou sanblé ou plus chak jou. </p>\n','Depi-tan-lontan-Bon-Dieu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1734,'92e597c7-7483-49df-b8ca-a6d8952ea7e3',0,0,'Gin gnou koté mouin kab pozé',137,'137_CE_ht','1.\nGin gnou koté mouin kab pozé,\nSé tou pré kè Bon-Dieu,\nNan koté  sa nan pouin péché,\nSé tou pré kè Bon-Dieu.\n\nKè:\nO Jézu, chè rédanmtè,\nKi sòt nan kè Bon-Dieu,\nKinbin-n kap tan-n prézans ou\nKi tou pré kè Bon-Dieu.\n\n\n2.\nGin koté pou nou konsolé,\nKi tou pré kè Bon-Dieu,\nSé la nap jouin-n avèk Jézu\nKi tou pré kè Bon-Dieu.\n\n\n3.\nGin koté ki tou bèl é vré;\nLi tou pré kè Bon-Dieu,\nSé la nou jouin-n la joua, la pè\nKi tou pré kè Bon-Dieu.','<p><strong>1.</strong> </p>\n<p>Gin gnou koté mouin kab pozé, </p>\n<p>Sé tou pré kè Bon-Dieu, </p>\n<p>Nan koté  sa nan pouin péché, </p>\n<p>Sé tou pré kè Bon-Dieu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O Jézu, chè rédanmtè,</strong> </p>\n<p><strong>Ki sòt nan kè Bon-Dieu,</strong> </p>\n<p><strong>Kinbin-n kap tan-n prézans ou</strong> </p>\n<p><strong>Ki tou pré kè Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin koté pou nou konsolé, </p>\n<p>Ki tou pré kè Bon-Dieu, </p>\n<p>Sé la nap jouin-n avèk Jézu </p>\n<p>Ki tou pré kè Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin koté ki tou bèl é vré; </p>\n<p>Li tou pré kè Bon-Dieu, </p>\n<p>Sé la nou jouin-n la joua, la pè </p>\n<p>Ki tou pré kè Bon-Dieu. </p>\n','Gin-gnou-kote-mouin-kab-poze',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1735,'4c32ec62-4daa-43a8-a6f7-02dbdf3930da',0,0,'Lè la vi mouin va fini',2,'2_MJ_ht','1.\nLè la vi mouin va fini\nÉ mouin janbé riviè-a\nLè mouin ouè briyé\nGnou matin glorié\nMa va konnin Rédanmtè mouin\nLè-m rivé nan sièl la\nÉ souri Li va pou mouin gnou byinvénu.\n\nKè:\nMa va ouè Li, ma konnin Li,\nLè ma kanpé bò koté Jézu-Kri;\nMa va ouè Li, ma konnin Li\nPa mak klou yo Li gingnin nan min Li.\n\n2.\nKi transpò dalégrès\nLè mouin va ouè Li fas a fas\nÉ tout gran amou kap\nBriyé nan jé Li;\nO kè mouin va loué Li\nPou mizérikòd ak gras,\nKi paré pou mouin\nGnou bèl plas nan kay Li.\n\n3.\nNan bèl péi anro-a\nMouin gingnin kèk byin-émé\nMouin sonjé lè yo té di mouin révoua\nAk graan joua yo va\nChanté lè yo ouè mapé rivé,\nMin mouin prémièman.\n\n4.\nMapé antré nan vil la\nNan gnou ròb ki blan é pu,\nKoté pap gin soufrans ni tristès ankò,\nMapé chanté ak zanj yo\nNan gnou sélès amoni\nMin mouin ta vlé ouè\nSovè mouin prémièman.','<p><strong>1.</strong> </p>\n<p>Lè la vi mouin va fini </p>\n<p>É mouin janbé riviè-a </p>\n<p>Lè mouin ouè briyé </p>\n<p>Gnou matin glorié </p>\n<p>Ma va konnin Rédanmtè mouin </p>\n<p>Lè-m rivé nan sièl la </p>\n<p>É souri Li va pou mouin gnou byinvénu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ma va ouè Li, ma konnin Li,</strong> </p>\n<p><strong>Lè ma kanpé bò koté Jézu-Kri;</strong> </p>\n<p><strong>Ma va ouè Li, ma konnin Li</strong> </p>\n<p><strong>Pa mak klou yo Li gingnin nan min Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ki transpò dalégrès </p>\n<p>Lè mouin va ouè Li fas a fas </p>\n<p>É tout gran amou kap </p>\n<p>Briyé nan jé Li; </p>\n<p>O kè mouin va loué Li </p>\n<p>Pou mizérikòd ak gras, </p>\n<p>Ki paré pou mouin </p>\n<p>Gnou bèl plas nan kay Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan bèl péi anro-a </p>\n<p>Mouin gingnin kèk byin-émé </p>\n<p>Mouin sonjé lè yo té di mouin révoua </p>\n<p>Ak graan joua yo va </p>\n<p>Chanté lè yo ouè mapé rivé, </p>\n<p>Min mouin prémièman. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mapé antré nan vil la </p>\n<p>Nan gnou ròb ki blan é pu, </p>\n<p>Koté pap gin soufrans ni tristès ankò, </p>\n<p>Mapé chanté ak zanj yo </p>\n<p>Nan gnou sélès amoni </p>\n<p>Min mouin ta vlé ouè </p>\n<p>Sovè mouin prémièman. </p>\n','Le-la-vi-mouin-va-fini',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1736,'14c79692-1d3b-4974-b892-86be4e2afa22',0,0,'Mouin Pa Tou Sèl Lè-m Maché Nan Chemin La Vi-a',23,'23_MJ_ht','1.\nMouin pa tou sèl lè-m maché nan chémin la vi-a\nMouin gingnin gnou\nZanmi kap maché avè-m\nMouin pa tou sèl\nMin-m si mouin pa ouè pèson-n\nBon Bèjé mouin\nRété koté-m tout tan\nKonsa lè tanpèt la vi-n sou mouin\nÉ tout zanmi-m yo kité-m\nMap chanté, souri nan chémin-an\nJouk mouin rivé nan sièl la.\nMouin pa tou sèl,\nSovè-m rété koté-m.','<p><strong>1.</strong> </p>\n<p>Mouin pa tou sèl lè-m maché nan chémin la vi-a </p>\n<p>Mouin gingnin gnou </p>\n<p>Zanmi kap maché avè-m </p>\n<p>Mouin pa tou sèl </p>\n<p>Min-m si mouin pa ouè pèson-n </p>\n<p>Bon Bèjé mouin </p>\n<p>Rété koté-m tout tan </p>\n<p>Konsa lè tanpèt la vi-n sou mouin </p>\n<p>É tout zanmi-m yo kité-m </p>\n<p>Map chanté, souri nan chémin-an </p>\n<p>Jouk mouin rivé nan sièl la. </p>\n<p>Mouin pa tou sèl, </p>\n<p>Sovè-m rété koté-m. </p>\n','Mouin-Pa-Tou-Sel-Le-m-Mache-Nan-Chemin-La-Vi-a',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1737,'35db54c1-ad9e-4359-ac29-81f5d45f1f2e',0,0,'Li Té Oueté-m Nan Sous La',53,'53_MJ_ht','1.\nLi té ouété-m nan fòs la\nÉ nan la bou glisan,\nLi mété pié-m sou gnou roch,\nOuvri la rout dévan-m;\nLi mété chan nan bouch mouin\nPou-m loué bon non Li\nAnpil moun yo tandé-l\nÉ vi-n kouè nan Li.','<p><strong>1.</strong> </p>\n<p>Li té ouété-m nan fòs la </p>\n<p>É nan la bou glisan, </p>\n<p>Li mété pié-m sou gnou roch, </p>\n<p>Ouvri la rout dévan-m; </p>\n<p>Li mété chan nan bouch mouin </p>\n<p>Pou-m loué bon non Li </p>\n<p>Anpil moun yo tandé-l </p>\n<p>É vi-n kouè nan Li. </p>\n','Li-Te-Ouete-m-Nan-Sous-La',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1738,'b535319e-7ac7-4c38-ab9c-ebc60463fb2e',0,0,'Il y avait des bergers',45,'45_VR_fr','1.	\nIl y avait des bergers qui gardaient leurs troupeaux,\nQui gardaient leurs troupeaux la nuit;\nIl y avait des bergers qui gardaient leurs troupeaux,\nQui passaient dans les champs les veilles de la nuit.\n      \n2.	\nVoici, un ange du Seigneur leur apparut\nEt la gloire du Seigneur resplendit autour d\'eux,\nIls furent saisis de frayeur.            \n\n3.	\nMais l\'ange leur dit, il leur dit;\nMais l\'ange leur dit, il leur dit:\nNe craignez point, car je vous annonce une bonne nouvelle\nQui sera pour tout le peuple le sujet d\'une grande joie.\n\n4.	\nC\'est qu\'aujourd\'hui il vous est né\nC\'est qu\'aujourd\'hui il vous est né,\nDans la ville de David,\nUn Sauveur qui est Christ le Seigneur\nC\'est qu\'aujourd\'hui il vous est né,\nUn Sauveur, un Sauveur, qui est Christ Le Seigneur.\n\n5.	\nGloire à Dieu, au Sauveur,\nGloire à Émmanuel,\nGloire à Dieu, Gloire à Dieu,\nDans les lieux très hauts!\nGloire à Dieu, Gloire au Sauveur,\nGloire à Émmanuel,\nGloire, Gloire à Dieu dans les lieux Très hauts.','<p><strong>1.</strong> </p>\n<p>Il y avait des bergers qui gardaient leurs troupeaux, </p>\n<p>Qui gardaient leurs troupeaux la nuit; </p>\n<p>Il y avait des bergers qui gardaient leurs troupeaux, </p>\n<p>Qui passaient dans les champs les veilles de la nuit. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voici, un ange du Seigneur leur apparut </p>\n<p>Et la gloire du Seigneur resplendit autour d&#39;eux, </p>\n<p>Ils furent saisis de frayeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais l&#39;ange leur dit, il leur dit; </p>\n<p>Mais l&#39;ange leur dit, il leur dit: </p>\n<p>Ne craignez point, car je vous annonce une bonne nouvelle </p>\n<p>Qui sera pour tout le peuple le sujet d&#39;une grande joie. </p>\n<p><br/> <strong>4.</strong></p>\n<p>C&#39;est qu&#39;aujourd&#39;hui il vous est né </p>\n<p>C&#39;est qu&#39;aujourd&#39;hui il vous est né, </p>\n<p>Dans la ville de David, </p>\n<p>Un Sauveur qui est Christ le Seigneur </p>\n<p>C&#39;est qu&#39;aujourd&#39;hui il vous est né, </p>\n<p>Un Sauveur, un Sauveur, qui est Christ Le Seigneur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Gloire à Dieu, au Sauveur, </p>\n<p>Gloire à Émmanuel, </p>\n<p>Gloire à Dieu, Gloire à Dieu, </p>\n<p>Dans les lieux très hauts! </p>\n<p>Gloire à Dieu, Gloire au Sauveur, </p>\n<p>Gloire à Émmanuel, </p>\n<p>Gloire, Gloire à Dieu dans les lieux Très hauts. </p>\n','Il-y-avait-des-bergers',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1739,'6e3f57f8-927b-4612-92c1-95e3c4896d4d',0,0,'Nous Te Présentons Cet Enfant',322,'322_CE_fr','1.\nNous te présentons cet enfant\nQue ta grâce a donné;\nDivin Jésus, Sauveur puissant,\nBénis ce nouveau-né.\n\n\n2.\nQu\'il ait part au règne éternel\nAcquis par ton trépas;\nEt que ton amour paternel\nL\'y prépare ici-bas!\n\n\n3.\nQu\'Il soit régénéré par Toi,\nO tendre Rédempteur;\nQu\'il vive et marche sous la loi\nDu saint Consolateur!\n\n\n4.\nQue, formé  par le Saint-Esprit,\nComme les vrais croyants,\nIl se consacre à Jésus-Christ\nDès ses plus jeunes ans!\n\n\n5.\nQu\'il marche humblement ici-bas\nEn citoyen des cieux,\nSuivant le Sauveur pas à pas\nAvec un coeur joyeux!','<p><strong>1.</strong> </p>\n<p>Nous te présentons cet enfant </p>\n<p>Que ta grâce a donné; </p>\n<p>Divin Jésus, Sauveur puissant, </p>\n<p>Bénis ce nouveau-né. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;il ait part au règne éternel </p>\n<p>Acquis par ton trépas; </p>\n<p>Et que ton amour paternel </p>\n<p>L&#39;y prépare ici-bas! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;Il soit régénéré par Toi, </p>\n<p>O tendre Rédempteur; </p>\n<p>Qu&#39;il vive et marche sous la loi </p>\n<p>Du saint Consolateur! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que, formé  par le Saint-Esprit, </p>\n<p>Comme les vrais croyants, </p>\n<p>Il se consacre à Jésus-Christ </p>\n<p>Dès ses plus jeunes ans! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Qu&#39;il marche humblement ici-bas </p>\n<p>En citoyen des cieux, </p>\n<p>Suivant le Sauveur pas à pas </p>\n<p>Avec un coeur joyeux! </p>\n','Nous-Te-Presentons-Cet-Enfant',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1740,'8c6e540e-f3d2-432f-b8b6-3cbe096b46c7',0,0,'Mouin Kanpé Sezi Nan Prézans',23,'23_CE_ht','1.\nMouin kanpé sézi nan prézans\nJézu, Rédanmtè béni;\nKonman li té kapab rinmin-m,\nMouin-min’m, gnou péchè inpi!\n\nKè:\nSé mèvéyé! Sé admirab!\nMa va chanté pou toujou.\nSé mèvéyé! Sé admirab!\nAmou Jézu gin pou nou.\n\n\n2.\nSé pou mouin l-alé nan jadin,\nPrié pou mouin ta sové,\nLi pa té krié pou li-min’m;\nPou mouin sé san li sué.\n\n\n3.\nLi té pran péché ak pin-n mouin,\nKon-m si tou sa té pou li;\nLi pòté yo tout nan Kalvè,\nSé la l-soufri, li mouri.\n\n\n4.\nLè mouin ouè fas li nan sièl la,\nAk rachté yo nan la gloua,\nMouin va vlé chanté amou li;\nSé nan sa ma jouin-n tout joua.','<p><strong>1.</strong> </p>\n<p>Mouin kanpé sézi nan prézans </p>\n<p>Jézu, Rédanmtè béni; </p>\n<p>Konman li té kapab rinmin-m, </p>\n<p>Mouin-min’m, gnou péchè inpi! </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé mèvéyé! Sé admirab!</strong> </p>\n<p><strong>Ma va chanté pou toujou.</strong> </p>\n<p><strong>Sé mèvéyé! Sé admirab!</strong> </p>\n<p><strong>Amou Jézu gin pou nou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé pou mouin l-alé nan jadin, </p>\n<p>Prié pou mouin ta sové, </p>\n<p>Li pa té krié pou li-min’m; </p>\n<p>Pou mouin sé san li sué. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li té pran péché ak pin-n mouin, </p>\n<p>Kon-m si tou sa té pou li; </p>\n<p>Li pòté yo tout nan Kalvè, </p>\n<p>Sé la l-soufri, li mouri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè mouin ouè fas li nan sièl la, </p>\n<p>Ak rachté yo nan la gloua, </p>\n<p>Mouin va vlé chanté amou li; </p>\n<p>Sé nan sa ma jouin-n tout joua. </p>\n','Mouin-Kanpe-Sezi-Nan-Prezans',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1741,'8285cdab-0306-413b-a83b-3e538b795e74',0,0,'Mwen sonje byen ki jou li te delivre-m',65,'65_CE_ht','1.\nMouin sonjé bi-in ki jou li té délivré-m,\nÉ mouin konnin mouin fini avèk péché,\nPaské mouin jouin-n délivrans\nÉ kè mouin fi-n asuré\nMouin sové nan péché pou toujou.\n\nKè:\nDélivré!  Délivré!\nOui mouin sové nan péché pou toujou!  (2 li-n 2 foua)\n\n\n2.\nMouin sonjé bi-in ki jan kè-m té boulvèsé,\nÉ ki tristès ki té fè nan-m mouin an dèy.\nMin jé mouin ouè Jézu-Kri,\nÉ kè mouin louvri ba li;\nTout chin-n mouin yo tonbé pou toujou.\n\n\n3.\nMouin sonjé bi-in ké sa pa té fèt fasil,\nÉ mouin té rézisté ak Jézu anpil,\nLi pa-t vi-n pou kondanné,\nÉ Li pran pasians avè-m\nJouk gnou jou, m-oblijé ouè lumiè.\n\n\n4.\nE sé pou tèt sa map chanté pou Jézu,\nÉ kè mouin plin é nan-m mouin gin gnou fréchè.\nMouin pa kab pé, fòm chanté,\nMouin vlé mouri ap chanté:\nAlélouya! M-délivré pou toujou.','<p><strong>1.</strong> </p>\n<p>Mouin sonjé bi-in ki jou li té délivré-m, </p>\n<p>É mouin konnin mouin fini avèk péché, </p>\n<p>Paské mouin jouin-n délivrans </p>\n<p>É kè mouin fi-n asuré </p>\n<p>Mouin sové nan péché pou toujou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Délivré!  Délivré!</strong> </p>\n<p><strong>Oui mouin sové nan péché pou toujou!  (2 li-n 2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin sonjé bi-in ki jan kè-m té boulvèsé, </p>\n<p>É ki tristès ki té fè nan-m mouin an dèy. </p>\n<p>Min jé mouin ouè Jézu-Kri, </p>\n<p>É kè mouin louvri ba li; </p>\n<p>Tout chin-n mouin yo tonbé pou toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin sonjé bi-in ké sa pa té fèt fasil, </p>\n<p>É mouin té rézisté ak Jézu anpil, </p>\n<p>Li pa-t vi-n pou kondanné, </p>\n<p>É Li pran pasians avè-m </p>\n<p>Jouk gnou jou, m-oblijé ouè lumiè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>E sé pou tèt sa map chanté pou Jézu, </p>\n<p>É kè mouin plin é nan-m mouin gin gnou fréchè. </p>\n<p>Mouin pa kab pé, fòm chanté, </p>\n<p>Mouin vlé mouri ap chanté: </p>\n<p>Alélouya! M-délivré pou toujou. </p>\n','Mwen-sonje-byen-ki-jou-li-te-delivre-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1742,'693612f1-7093-4f65-8f33-31ca62a2389c',0,0,'Kité lé mon-n bi-in louin ou',90,'90_CE_ht','1.\nKité lé mon-n bi-in louin ou,\nVi-ni, vi-ni avèk nou;\nNap fè gnou nouvo pa chak jou,  (2 foua)\nPou Sion, patri nou.  (2 foua)\n\nKè: Nap fè rout pou Sion,\nBèl Sion, péi Sovè nou!\nKréti-in an avan pou Sion,\nSé bèl, sé bèl péi nou!\n\n\n2.\nNan sièl la, bèl musik la,\nAvèk bèl kantik yo you,\nKapé kondui lamé sièl la,  (2 foua)\nVa réjoui-n tou, gnou jou.  (2 foua)\n\n\n3.\nPou ranpòté la viktoua,\nSé pou nou suiv Jézu-Kri;\nChak solda gin pou résévoua,  (2 foua)\nLa gloua lè-l fi-n soufri.  (2 foua)\n\n\n4.\nJézu-Kri, Rédanmtè a\nInstalé sou gran trò-n li;\nLapé régné nan tout sièl la,  (2 foua)\nAk kouròn sou tèt li.  (2 foua)','<p><strong>1.</strong> </p>\n<p>Kité lé mon-n bi-in louin ou, </p>\n<p>Vi-ni, vi-ni avèk nou; </p>\n<p>Nap fè gnou nouvo pa chak jou,  (2 foua) </p>\n<p>Pou Sion, patri nou.  (2 foua) </p>\n<p>Kè: Nap fè rout pou Sion, </p>\n<p>Bèl Sion, péi Sovè nou! </p>\n<p>Kréti-in an avan pou Sion, </p>\n<p>Sé bèl, sé bèl péi nou! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan sièl la, bèl musik la, </p>\n<p>Avèk bèl kantik yo you, </p>\n<p>Kapé kondui lamé sièl la,  (2 foua) </p>\n<p>Va réjoui-n tou, gnou jou.  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pou ranpòté la viktoua, </p>\n<p>Sé pou nou suiv Jézu-Kri; </p>\n<p>Chak solda gin pou résévoua,  (2 foua) </p>\n<p>La gloua lè-l fi-n soufri.  (2 foua) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu-Kri, Rédanmtè a </p>\n<p>Instalé sou gran trò-n li; </p>\n<p>Lapé régné nan tout sièl la,  (2 foua) </p>\n<p>Ak kouròn sou tèt li.  (2 foua) </p>\n','Kite-le-mon-n-bi-in-louin-ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1743,'23974ae7-6904-4f82-9aaa-705876b5c4b6',0,0,'Li ba nou plus gras',11,'11_MJ_ht','1.\nLi ban nou plus gras\nLè fado nou vi-n pi lou.\nLi ba nou plus fòs\nLè travay nou pi rèd.\nNan mitan lafliksion\nZéprèv yo ak danjé\nNan amou-L Li ba nou\nLa pé ki pi dou.\n\nKè:\nLamou Li san mézu,\nGras Li sé pou tout moun-n\nPuisans Li pi gran\nPasé konésans nou\nNan richès infini\nLi gingnin nan Jézu\nLi ba nou, Li ba nou,\nLi ba nou toujou.\n\n2.\nLè tout amou, tout pasians\nNou gin tan fini;\nLè nou pèdu tout fòs\nMin-m avan midi;\nLè nou manké kouraj\nPou maché nan rout Li;\nLè sa Dieu kòmansé\nBa nou puisans Li.','<p><strong>1.</strong> </p>\n<p>Li ban nou plus gras </p>\n<p>Lè fado nou vi-n pi lou. </p>\n<p>Li ba nou plus fòs </p>\n<p>Lè travay nou pi rèd. </p>\n<p>Nan mitan lafliksion </p>\n<p>Zéprèv yo ak danjé </p>\n<p>Nan amou-L Li ba nou </p>\n<p>La pé ki pi dou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lamou Li san mézu,</strong> </p>\n<p><strong>Gras Li sé pou tout moun-n</strong> </p>\n<p><strong>Puisans Li pi gran</strong> </p>\n<p><strong>Pasé konésans nou</strong> </p>\n<p><strong>Nan richès infini</strong> </p>\n<p><strong>Li gingnin nan Jézu</strong> </p>\n<p><strong>Li ba nou, Li ba nou,</strong> </p>\n<p><strong>Li ba nou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè tout amou, tout pasians </p>\n<p>Nou gin tan fini; </p>\n<p>Lè nou pèdu tout fòs </p>\n<p>Min-m avan midi; </p>\n<p>Lè nou manké kouraj </p>\n<p>Pou maché nan rout Li; </p>\n<p>Lè sa Dieu kòmansé </p>\n<p>Ba nou puisans Li. </p>\n','Li-ba-nou-plus-gras',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1744,'88d37736-ba9a-45eb-aa11-740d1479c4c1',0,0,'Daniel Té Konn Prié',76,'76_MJ_ht','1.\nDanièl Té kon-n prié,\nToua foua chak jou li prié,\nGnou jou yo té jété li\nNan fòs lion sovaj.\nMin li pat jan-m té pè\nLi konfié nan Bon-Dieu,\nBon-Dieu fèmin bouch lion,\nPou yo pa kap mòdé-l.','<p><strong>1.</strong> </p>\n<p>Danièl Té kon-n prié, </p>\n<p>Toua foua chak jou li prié, </p>\n<p>Gnou jou yo té jété li </p>\n<p>Nan fòs lion sovaj. </p>\n<p>Min li pat jan-m té pè </p>\n<p>Li konfié nan Bon-Dieu, </p>\n<p>Bon-Dieu fèmin bouch lion, </p>\n<p>Pou yo pa kap mòdé-l. </p>\n','Daniel-Te-Konn-Prie',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1745,'960fea8b-9e7f-418e-b14e-e6d4cf9d9e1f',0,0,'O Nuit trè Sin, Bèl Zétoual Yo Ap Briyé',82,'82_MJ_ht','1.\nO nuit trè sin, bèl zétoual yo ap briyé,\nSé la nuit lè Jézu-Kri té fèt.\nLontan lé mon-n té égaré nan péché\nJouk Li parèt é nan-m mouin té viv ankò.\n\nKè:\nO jou dé joua lé mon-n apé réjoui\nPou lè gro Lumiè kap fè gnou nouvo jou.\nMété jénou a tè, O tandé sa yap chanté.\nO nuit trè sin, O nuit, O nuit lè Kris té fèt\nO nuit divin, O nuit, O nuit divin.\n\n2.\nNou suiv lumiè sa avèk tout gran joua,\nKè nou briyé avèk amou pou  Jézu-Kri.\nNou adoré Li ki vi-n fèt pou sové nou,\nNou suiv Li min-m jan\nMoun saj té suiv Li.','<p><strong>1.</strong> </p>\n<p>O nuit trè sin, bèl zétoual yo ap briyé, </p>\n<p>Sé la nuit lè Jézu-Kri té fèt. </p>\n<p>Lontan lé mon-n té égaré nan péché </p>\n<p>Jouk Li parèt é nan-m mouin té viv ankò. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O jou dé joua lé mon-n apé réjoui</strong> </p>\n<p><strong>Pou lè gro Lumiè kap fè gnou nouvo jou.</strong> </p>\n<p><strong>Mété jénou a tè, O tandé sa yap chanté.</strong> </p>\n<p><strong>O nuit trè sin, O nuit, O nuit lè Kris té fèt</strong> </p>\n<p><strong>O nuit divin, O nuit, O nuit divin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou suiv lumiè sa avèk tout gran joua, </p>\n<p>Kè nou briyé avèk amou pou  Jézu-Kri. </p>\n<p>Nou adoré Li ki vi-n fèt pou sové nou, </p>\n<p>Nou suiv Li min-m jan </p>\n<p>Moun saj té suiv Li. </p>\n','O-Nuit-tre-Sin-Bel-Zetoual-Yo-Ap-Briye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1746,'4b6c36ce-2c75-4f70-9382-e787338dfd1a',0,0,'Mouin vie mil lang pou chante biin',14,'14_CE_ht','1.\nMouin vlé mil lang pou chanté bi-in\nLouanj Rédanmtè mouin;\nTout gloua, sé pou Bon-Dieu, Roua mouin,\nGras li gin tout viktoua.\n\n\n2.\nMèt mouin, é a-nonsé patou:\nPou-m a-nonsé aptou:\nPou-m répan-n nan la tè antié\nKonnésans non Jézu.\n\n\n3.\nJézu, non ou chasé krintif,\nLi chasé tristès nou.\nJézu, non ou, sé bèl muzik,\nSé vi ak santé tou.\n\n\n4.\nLi krazé puisans vié péché,\nPrizonnié yo lagé.\nSan li né touayé péché yo,\nSan li nétouayé mouin.\n\n\n5.\nNou-min’m, tout pèp, gadé koté-l,\nSé li ki Bon-Dieu nou.\nLa foua sové nou, la foua sèl;\nGras li padonnin nou.\n\n\n6.\nBon-Dieu té rinmin tout péché,\nLi rinmin nou sé si!\nPou tout moun patou sou la tè\nPitit li té mouri.','<p><strong>1.</strong> </p>\n<p>Mouin vlé mil lang pou chanté bi-in </p>\n<p>Louanj Rédanmtè mouin; </p>\n<p>Tout gloua, sé pou Bon-Dieu, Roua mouin, </p>\n<p>Gras li gin tout viktoua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mèt mouin, é a-nonsé patou: </p>\n<p>Pou-m a-nonsé aptou: </p>\n<p>Pou-m répan-n nan la tè antié </p>\n<p>Konnésans non Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu, non ou chasé krintif, </p>\n<p>Li chasé tristès nou. </p>\n<p>Jézu, non ou, sé bèl muzik, </p>\n<p>Sé vi ak santé tou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li krazé puisans vié péché, </p>\n<p>Prizonnié yo lagé. </p>\n<p>San li né touayé péché yo, </p>\n<p>San li nétouayé mouin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Nou-min’m, tout pèp, gadé koté-l, </p>\n<p>Sé li ki Bon-Dieu nou. </p>\n<p>La foua sové nou, la foua sèl; </p>\n<p>Gras li padonnin nou. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Bon-Dieu té rinmin tout péché, </p>\n<p>Li rinmin nou sé si! </p>\n<p>Pou tout moun patou sou la tè </p>\n<p>Pitit li té mouri. </p>\n','Mouin-vie-mil-lang-pou-chante-biin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1747,'37804791-73eb-44a8-a51d-eb91fb5a7360',0,0,'Mouin Vle Suiv Jézu Jouk La Fin',15,'15_CE_ht','1.\nMouin vlé suiv Jézu jouk la fin.\nSé li ki Ségnè mouin!\nMa va pèsévéré avèk li;\nSa, sé lidé-m pou tout la vi;\nLé zòt kab fè sa yo vlé.\nMap déziré pou-m gingnin la pè,\nMa va maché avèk Jézu-Kri,\nMa chaché fè volonté li.\n\n\n2.\nMouin kon-nin m-kapab gin féblès,\nLè-m an fas gran détrès.\nJézu promèt édé ninpòt ki;\nTout moun ki gin la foua nan li.\nJézu kinbé-m avèk min.\nPa gras li, gnou péchè ka vi-n sin.\nTout konfians mouin, sé nan li-min’m,\nKon sa ma suiv li jouk la fin.\n\n\n3.\nTout moun yo kap suiv Jézu-Kri\nDoué konpran-n ya soufri.\nSatan va okupé tanté yo\nPou fè yo suiv chémin ki fò.\nMin sé Jézu-Kri ki Mèt,\nSé li ki té gin gnou viktoua nèt.\nMa va maché avèk Jézu-Kri,\nMa chanté fè volonté li.','<p><strong>1.</strong> </p>\n<p>Mouin vlé suiv Jézu jouk la fin. </p>\n<p>Sé li ki Ségnè mouin! </p>\n<p>Ma va pèsévéré avèk li; </p>\n<p>Sa, sé lidé-m pou tout la vi; </p>\n<p>Lé zòt kab fè sa yo vlé. </p>\n<p>Map déziré pou-m gingnin la pè, </p>\n<p>Ma va maché avèk Jézu-Kri, </p>\n<p>Ma chaché fè volonté li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin kon-nin m-kapab gin féblès, </p>\n<p>Lè-m an fas gran détrès. </p>\n<p>Jézu promèt édé ninpòt ki; </p>\n<p>Tout moun ki gin la foua nan li. </p>\n<p>Jézu kinbé-m avèk min. </p>\n<p>Pa gras li, gnou péchè ka vi-n sin. </p>\n<p>Tout konfians mouin, sé nan li-min’m, </p>\n<p>Kon sa ma suiv li jouk la fin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout moun yo kap suiv Jézu-Kri </p>\n<p>Doué konpran-n ya soufri. </p>\n<p>Satan va okupé tanté yo </p>\n<p>Pou fè yo suiv chémin ki fò. </p>\n<p>Min sé Jézu-Kri ki Mèt, </p>\n<p>Sé li ki té gin gnou viktoua nèt. </p>\n<p>Ma va maché avèk Jézu-Kri, </p>\n<p>Ma chanté fè volonté li. </p>\n','Mouin-Vle-Suiv-Jezu-Jouk-La-Fin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1748,'777fcdd4-3578-45b8-a0ff-1a177a944d7f',0,0,'Seyè Bondie, mwen vle la vi-m',22,'22_CE_ht','1.\nSégnè Bon-Dieu, mouin vlé la vi-m\nKa ranpli ak louanj,\nNan tou sa-m yé, ak tou sa-m fè,\nPlè ou tankou lè zanj.\n\n\n2.\nSé pa sèlman louanj lèv mouin,\nNi sèlman louanj kè,\nMouin mandé ou été-m ofri\nLouanj ou a tout è.\n\n\n3.\nMouin vllé loué ou tout tan, min-m\nNan bagay òdi-nè;\nChak kou mouin sòti ou rantré,\nÉpi nan tou sa-m fè.\n\n\n4.\nFè ké tout pati nan mouin-min-m;\nKò, nan-m avèk léspri,\nKap chanté louanj ou, Ségnè,\nMalgré m-fèb é piti.\n\n\n5.\nSé kon sa, Ségnè, mouin-min-m min-m,\nVa glorifié ou,\nÉ m-va kòmansé sou la tè\nChan ki pou lé siél tou.\n\n\n6.\nTout la jouné avèk lan nuit,\nVa sin, lè m-fè kon sa,\nM-va gin gnou komunion pafè\nAk ou, min-m isi ba.','<p><strong>1.</strong> </p>\n<p>Ségnè Bon-Dieu, mouin vlé la vi-m </p>\n<p>Ka ranpli ak louanj, </p>\n<p>Nan tou sa-m yé, ak tou sa-m fè, </p>\n<p>Plè ou tankou lè zanj. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé pa sèlman louanj lèv mouin, </p>\n<p>Ni sèlman louanj kè, </p>\n<p>Mouin mandé ou été-m ofri </p>\n<p>Louanj ou a tout è. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vllé loué ou tout tan, min-m </p>\n<p>Nan bagay òdi-nè; </p>\n<p>Chak kou mouin sòti ou rantré, </p>\n<p>Épi nan tou sa-m fè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Fè ké tout pati nan mouin-min-m; </p>\n<p>Kò, nan-m avèk léspri, </p>\n<p>Kap chanté louanj ou, Ségnè, </p>\n<p>Malgré m-fèb é piti. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Sé kon sa, Ségnè, mouin-min-m min-m, </p>\n<p>Va glorifié ou, </p>\n<p>É m-va kòmansé sou la tè </p>\n<p>Chan ki pou lé siél tou. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Tout la jouné avèk lan nuit, </p>\n<p>Va sin, lè m-fè kon sa, </p>\n<p>M-va gin gnou komunion pafè </p>\n<p>Ak ou, min-m isi ba. </p>\n','Seye-Bondie-mwen-vle-la-vi-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1749,'53b4c170-de14-4697-90cd-ec7b5b5599b9',0,0,'Tout Moun Ki Rété Rou La Tè',47,'47_CE_ht','1.\nTout moun ki rété sou la tè,\nChanté lé Ségnè ak tout voua;\nSèvi-l ak krint, fè louanj li,\nDévan li chanté avèk joua.\n\n\n2.\nNou konnin sé Bon-Dieu li yé;\nNou pat édé-l lè li fè nou.\nL-a nouri nou, sé pa-l nou yé;\nTankou mouton-l li minnin nou.\n\n\n3.\nAntré nan pòt li ak louanj;\nAvèk joua, proché tanp li tou;\nBéni non li tankou lè zanj,\nSé bon pou fè kon sa toujou.\n\n\n4.\nPouki? Paské Bon-Dieu nou bon,\nAmou-l rété fidèl toujou.\nTou sa li di, li fè-l tout bon;\nParòl Bon-Dieu sètin toujou.','<p><strong>1.</strong> </p>\n<p>Tout moun ki rété sou la tè, </p>\n<p>Chanté lé Ségnè ak tout voua; </p>\n<p>Sèvi-l ak krint, fè louanj li, </p>\n<p>Dévan li chanté avèk joua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou konnin sé Bon-Dieu li yé; </p>\n<p>Nou pat édé-l lè li fè nou. </p>\n<p>L-a nouri nou, sé pa-l nou yé; </p>\n<p>Tankou mouton-l li minnin nou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Antré nan pòt li ak louanj; </p>\n<p>Avèk joua, proché tanp li tou; </p>\n<p>Béni non li tankou lè zanj, </p>\n<p>Sé bon pou fè kon sa toujou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pouki? Paské Bon-Dieu nou bon, </p>\n<p>Amou-l rété fidèl toujou. </p>\n<p>Tou sa li di, li fè-l tout bon; </p>\n<p>Paròl Bon-Dieu sètin toujou. </p>\n','Tout-Moun-Ki-Rete-Rou-La-Te',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1750,'5f1d3867-4dc5-4173-baa2-49ecdbde6cfb',0,0,'Lévé, nam mouin, ak solèy la',54,'54_CE_ht','1.\nLévé, nan-m mouin, ak solèy la,\nKonmansé tach ou jodi a.\nPrémié bagay ou gin pou fè\nSé loué Bon-Dieu ak tout kè.\n\n\n2.\nPi-nga ou gaspiyé tan ou!\nViv jodi a kou dènié jou;\nAnplouayé talan ou yo bi-in,\nNou tout va jujé a la fin.\n\n\n3.\nTout paròl ou yo doué sinsè,\nKonsians ou doué toujou klè;\nPaské Dieu ouè tou sa ouap fè,\nLi kon-n tout pansé nan tout kè.\n\n\n4.\nKè mouin, sé pou ou lévé tou,\nTout zanj yo va chanté ak ou.\nYo-min’m, yo pap jan-m fatigé\nLoué, béni, sèvi Bon-Dieu.\n\n\n5.\nBon-Dieu, mouin vlé di Ou mèsi,\nOu té protéjé-m lè-m dòmi,\nLè mouin dòmi pou dènié foua\nFè-m lévé dévan Ou ak joua.\n\n\n6.\nMouin vlé rémèt mouin nan min Ou,\nNétouayé kè-m ak léspri-m tou.\nGidé volonté-m a chak kou\nÉ ranpli mouin ak Léspri Ou.\n\n\n7.\nGidé-m, Ségnè, m-vlé Ou kondui\nTout sa-m pou pansé, fè ou di.\nPou tout fòs mouin kapab sèvi\nPou la gloua Ou, Ségnè Jézu.\n\n\n8.\nBéni Bon-Dieu kap rinmin nou,\nBéni Li tout moun tou patou.\nBéni Pitit Li, Jézu-Kri.\nBéni Pè, Fis é Sint-Éspri.','<p><strong>1.</strong> </p>\n<p>Lévé, nan-m mouin, ak solèy la, </p>\n<p>Konmansé tach ou jodi a. </p>\n<p>Prémié bagay ou gin pou fè </p>\n<p>Sé loué Bon-Dieu ak tout kè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pi-nga ou gaspiyé tan ou! </p>\n<p>Viv jodi a kou dènié jou; </p>\n<p>Anplouayé talan ou yo bi-in, </p>\n<p>Nou tout va jujé a la fin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout paròl ou yo doué sinsè, </p>\n<p>Konsians ou doué toujou klè; </p>\n<p>Paské Dieu ouè tou sa ouap fè, </p>\n<p>Li kon-n tout pansé nan tout kè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kè mouin, sé pou ou lévé tou, </p>\n<p>Tout zanj yo va chanté ak ou. </p>\n<p>Yo-min’m, yo pap jan-m fatigé </p>\n<p>Loué, béni, sèvi Bon-Dieu. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Bon-Dieu, mouin vlé di Ou mèsi, </p>\n<p>Ou té protéjé-m lè-m dòmi, </p>\n<p>Lè mouin dòmi pou dènié foua </p>\n<p>Fè-m lévé dévan Ou ak joua. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Mouin vlé rémèt mouin nan min Ou, </p>\n<p>Nétouayé kè-m ak léspri-m tou. </p>\n<p>Gidé volonté-m a chak kou </p>\n<p>É ranpli mouin ak Léspri Ou. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Gidé-m, Ségnè, m-vlé Ou kondui </p>\n<p>Tout sa-m pou pansé, fè ou di. </p>\n<p>Pou tout fòs mouin kapab sèvi </p>\n<p>Pou la gloua Ou, Ségnè Jézu. </p>\n<p><br/> <strong>8.</strong></p>\n<p>Béni Bon-Dieu kap rinmin nou, </p>\n<p>Béni Li tout moun tou patou. </p>\n<p>Béni Pitit Li, Jézu-Kri. </p>\n<p>Béni Pè, Fis é Sint-Éspri. </p>\n','Leve-nam-mouin-ak-soley-la',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1751,'5ca83963-f80e-482d-bc07-a85978c4295a',1,0,'Gloua à Dieu nan lè li trè ro',56,'56_CE_ht','1.\nGloua a Dieu nan lè lié trè ro,\nGloua a Dieu isit sou la tè.\nGloua a Dieu, matin, midi, souè,\nGloua a Dieu tout tan, tou patou.\n\n\n2.\nMèsi, Bon-Dieu pou lé solèy\nKap kléré é kap chofé nou;\nMèsi, Bon-Dieu, tou, pou la pli\nOu bay pou rozé jadin nou.\n\n\n3.\nMèsi, Bon-Dieu, pou fanmi nou;\nPou manman, papa, pitit tou;\nMèsi pou tout frè, sè nou yo,\nKap rinmin, kap supòté nou.\n\n\n4.\nMèsi, Bon-Dieu, pou zanmi nou,\nMèsi pou tout moun Lévanjil;\nPou tout moun yo kap rinmin Ou,\nNou tout sé gnou-n nan Jézu-Kri.\n\n\n5.\nMèsi, Bon-Dieu, pou amou Ou.\nMèsi, Bon-Dieu, pou Jézu-Kri.\nMèsi tou pou lé Sint-Éspri\nKap gidé é konsolé nou.\n\n\n6.\nPadonnin nou tout péché nou;\nNétouayé kè nou tou patou.\nBan nou la gras Ou tou lé jou\nPou-n kapab fè volonté Ou.\n\n\n7.\nBéni Bon-Dieu kap rinmin nou,\nBéni Li tout moun tou patou.\nBéni Pitit Li, Jézu-Kri.\nBéni Pè, Fis é Sint-Éspri.','<p><strong>1.</strong> </p>\n<p>Gloua a Dieu nan lè lié trè ro, </p>\n<p>Gloua a Dieu isit sou la tè. </p>\n<p>Gloua a Dieu, matin, midi, souè, </p>\n<p>Gloua a Dieu tout tan, tou patou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mèsi, Bon-Dieu pou lé solèy </p>\n<p>Kap kléré é kap chofé nou; </p>\n<p>Mèsi, Bon-Dieu, tou, pou la pli </p>\n<p>Ou bay pou rozé jadin nou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mèsi, Bon-Dieu, pou fanmi nou; </p>\n<p>Pou manman, papa, pitit tou; </p>\n<p>Mèsi pou tout frè, sè nou yo, </p>\n<p>Kap rinmin, kap supòté nou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mèsi, Bon-Dieu, pou zanmi nou, </p>\n<p>Mèsi pou tout moun Lévanjil; </p>\n<p>Pou tout moun yo kap rinmin Ou, </p>\n<p>Nou tout sé gnou-n nan Jézu-Kri. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mèsi, Bon-Dieu, pou amou Ou. </p>\n<p>Mèsi, Bon-Dieu, pou Jézu-Kri. </p>\n<p>Mèsi tou pou lé Sint-Éspri </p>\n<p>Kap gidé é konsolé nou. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Padonnin nou tout péché nou; </p>\n<p>Nétouayé kè nou tou patou. </p>\n<p>Ban nou la gras Ou tou lé jou </p>\n<p>Pou-n kapab fè volonté Ou. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Béni Bon-Dieu kap rinmin nou, </p>\n<p>Béni Li tout moun tou patou. </p>\n<p>Béni Pitit Li, Jézu-Kri. </p>\n<p>Béni Pè, Fis é Sint-Éspri. </p>\n','Gloua-a-Dieu-nan-le-li-tre-ro',NULL,'ht',7,'2023-07-22 22:41:15','2023-08-14 02:52:23'),(1752,'bb0da476-66b1-4d50-88ca-2190fd676309',0,0,'Ségnè-m ki vi-n régné',58,'58_CE_ht','1.\nSégnè-m ki vi-n régné,\nMouri pou-n ka sové.\nLi té tan nan tonbo\nJou pou-l monté an ro.\n\nKè:\nJézu résusité\nNan gloua é majésté,\nLi résusité pou toujou.\nSé pou nou publié\nLouanj non li koun yé,\nPaské jodi a li vivan.\n\n\n2.\nPouki nou nan tristès,\nNap krié nan détrès?\nBi-into soufrans lan mò\nTounin nan joua ankò.\n\n\n3.\nGro ròch la déplasé,\nLè Kris résusité.\nLi vivan nan sièl la,\nAmou-l pa jan-m kaba.','<p><strong>1.</strong> </p>\n<p>Ségnè-m ki vi-n régné, </p>\n<p>Mouri pou-n ka sové. </p>\n<p>Li té tan nan tonbo </p>\n<p>Jou pou-l monté an ro. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu résusité</strong> </p>\n<p><strong>Nan gloua é majésté,</strong> </p>\n<p><strong>Li résusité pou toujou.</strong> </p>\n<p><strong>Sé pou nou publié</strong> </p>\n<p><strong>Louanj non li koun yé,</strong> </p>\n<p><strong>Paské jodi a li vivan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pouki nou nan tristès, </p>\n<p>Nap krié nan détrès? </p>\n<p>Bi-into soufrans lan mò </p>\n<p>Tounin nan joua ankò. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gro ròch la déplasé, </p>\n<p>Lè Kris résusité. </p>\n<p>Li vivan nan sièl la, </p>\n<p>Amou-l pa jan-m kaba. </p>\n','Segne-m-ki-vi-n-regne',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1753,'d4a29510-8a2f-47f7-b954-94a782f5369c',0,0,'Séré-m Pi Pré Ou, Tou Pré Kè Ou',61,'61_CE_ht','1.\nSéré-m pi pré ou, tou pré kè ou.\nOu sé trézò mouin é ou Sovè-m tou.\nKinbé-m, o kinbé-m, Bon-Dieu damou.\nFè-m jouin-n gnou koté pou-m kaché nan ou.  (2 foua)\n\n2.\nSéré-m pi pré ou; mouin pa angnin.\nMouin pa gin ofran-n pou Jézu, Roua mouin;\nSèlman gnou kè ki tou plin péché.\nLavé-m, Jézu, san ou koulé pou mouin.  (2 foua)\n\n3.\nSéré-m pi pré ou, Ségnè Jézu.\nSé pa pou mon-n sa ké mouin gin dézi.\nMouin lagé péché ak tout lògèy.\nBan-m sèlman Jézu, Ségnè-m krusifié.  (2 foua)\n\n4.\nSéré-m pi pré ou, pandan vi sa,\nJouk tan mouin la avèk ou nan la gloua,\nPou mouin rété toujou avèk ou,\nPi pré ou Sovè-m, pi pré ou toujou.  (2 foua)','<p><strong>1.</strong> </p>\n<p>Séré-m pi pré ou, tou pré kè ou. </p>\n<p>Ou sé trézò mouin é ou Sovè-m tou. </p>\n<p>Kinbé-m, o kinbé-m, Bon-Dieu damou. </p>\n<p>Fè-m jouin-n gnou koté pou-m kaché nan ou.  (2 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Séré-m pi pré ou; mouin pa angnin. </p>\n<p>Mouin pa gin ofran-n pou Jézu, Roua mouin; </p>\n<p>Sèlman gnou kè ki tou plin péché. </p>\n<p>Lavé-m, Jézu, san ou koulé pou mouin.  (2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Séré-m pi pré ou, Ségnè Jézu. </p>\n<p>Sé pa pou mon-n sa ké mouin gin dézi. </p>\n<p>Mouin lagé péché ak tout lògèy. </p>\n<p>Ban-m sèlman Jézu, Ségnè-m krusifié.  (2 foua) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Séré-m pi pré ou, pandan vi sa, </p>\n<p>Jouk tan mouin la avèk ou nan la gloua, </p>\n<p>Pou mouin rété toujou avèk ou, </p>\n<p>Pi pré ou Sovè-m, pi pré ou toujou.  (2 foua) </p>\n','Sere-m-Pi-Pre-Ou-Tou-Pre-Ke-Ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1754,'6b159f10-48c1-48d7-ba74-bf6fc44d9d37',0,0,'Lontan Mwen Té Rété Nan Lògey',62,'62_CE_ht','1.\nLontan mouin té rété nan lògèy;\nM-pat ran-n kont Ségnè-m té krusifié;\nM-pat konnin sé pou mouin san-l koulé\nSou boua Kalvè.\n\nKè:\nTout gras Bon-Dieu té pou grin mési,\nJézu té ban mouin tout padon li.\nNan-m mouin ki té troublé jouin-n la vi\nSou boua Kalvè.\n\n\n2.\nParòl Bon-Dieu té montré-m péché-m;\nLò mouin ouè la loua mouin té tranblé,\nJouk tan nan-m mouin koupab té viré\nBò boua Kalvè.\n\n\n3.\nKoun yé a mouin ran-n Kris tout bagay;\nKoun yé a sé pou li map travay;\nKoun yé a sé rémèsiman m-bay\nPou boua Kalvè.\n\n\n4.\nO lamou ki té fè plan salu!\nO la gras ki té fè li vini!\nSé pou péché mouin Kris té soufri\nSou boua Kalvè.','<p><strong>1.</strong> </p>\n<p>Lontan mouin té rété nan lògèy; </p>\n<p>M-pat ran-n kont Ségnè-m té krusifié; </p>\n<p>M-pat konnin sé pou mouin san-l koulé </p>\n<p>Sou boua Kalvè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Tout gras Bon-Dieu té pou grin mési,</strong> </p>\n<p><strong>Jézu té ban mouin tout padon li.</strong> </p>\n<p><strong>Nan-m mouin ki té troublé jouin-n la vi</strong> </p>\n<p><strong>Sou boua Kalvè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Paròl Bon-Dieu té montré-m péché-m; </p>\n<p>Lò mouin ouè la loua mouin té tranblé, </p>\n<p>Jouk tan nan-m mouin koupab té viré </p>\n<p>Bò boua Kalvè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Koun yé a mouin ran-n Kris tout bagay; </p>\n<p>Koun yé a sé pou li map travay; </p>\n<p>Koun yé a sé rémèsiman m-bay </p>\n<p>Pou boua Kalvè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O lamou ki té fè plan salu! </p>\n<p>O la gras ki té fè li vini! </p>\n<p>Sé pou péché mouin Kris té soufri </p>\n<p>Sou boua Kalvè. </p>\n','Lontan-Mwen-Te-Rete-Nan-Logey',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1755,'c85e25be-966b-411d-9bb8-3eecc1fc725a',0,0,'La bib di nou, té gin gnou non-m',108,'108_CE_ht','1.\nLa bib di nou, té gin gnou non-m\nKi viv nan tan lontan,\nLi té bi-in rich, min li santi\nLi bézouin plus ké lò.\n\nKè:  Oui, mé zanmi, gin gnou bagay\nKi pi plus pasé lò!\nKonnin péché ou padonnin,\nSé pi plus pasé lò!\n\n\n2.\nNon-m nan sé té gnou moun ti tay.\nGnou jou moun yo vi-n di-l\nKé Jézu-Kri va vi-n ofri\nGnou bagay plus ké lò.\n\n\n3.\nNon-m nan té monté gnou pié boua,\nPou li ta kapab ouè\nLé Sovè ki kapab ofri\nGnou bagay plus ké lò.\n\n\n4.\nBi-into Jézu té vi-n bò la,\nLi ouè non-m nan an lè.\nÉpi Li rélé-l, é Li di-l:\n«Fòk mouin al la kay ou.»\n\n\n5.\nNon-m nan té obéi tou suit;\nÉ li té ouè sé vré\nKé bi-infè Jézu té ba li\nSé té plus pasé lò.','<p><strong>1.</strong> </p>\n<p>La bib di nou, té gin gnou non-m </p>\n<p>Ki viv nan tan lontan, </p>\n<p>Li té bi-in rich, min li santi </p>\n<p>Li bézouin plus ké lò. </p>\n<p>Kè:  Oui, mé zanmi, gin gnou bagay </p>\n<p>Ki pi plus pasé lò! </p>\n<p>Konnin péché ou padonnin, </p>\n<p>Sé pi plus pasé lò! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non-m nan sé té gnou moun ti tay. </p>\n<p>Gnou jou moun yo vi-n di-l </p>\n<p>Ké Jézu-Kri va vi-n ofri </p>\n<p>Gnou bagay plus ké lò. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Non-m nan té monté gnou pié boua, </p>\n<p>Pou li ta kapab ouè </p>\n<p>Lé Sovè ki kapab ofri </p>\n<p>Gnou bagay plus ké lò. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bi-into Jézu té vi-n bò la, </p>\n<p>Li ouè non-m nan an lè. </p>\n<p>Épi Li rélé-l, é Li di-l: </p>\n<p>«Fòk mouin al la kay ou.» </p>\n<p><br/> <strong>5.</strong></p>\n<p>Non-m nan té obéi tou suit; </p>\n<p>É li té ouè sé vré </p>\n<p>Ké bi-infè Jézu té ba li </p>\n<p>Sé té plus pasé lò. </p>\n','La-bib-di-nou-te-gin-gnou-non-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1756,'0789b6b6-94f9-4e19-83f2-0e7091ce2a18',0,0,'Gnou foua rout mouin té gin tristès',129,'129_CE_ht','1.\nGnou foua rout mouin té gin tristès,\nPaské kè-m té plin péché.\nKoul yé a mouin pa nan détrès,\nDépi amou Jézu té antré.\n\nKè:\nM-pa kab di konbi-in mouin rinmin Jézu\nM-pa kab di konbi-in li rinmin mouin,\nPaské amou Li san mézu\nÉ profondè li san fin.\nJézu-Kri té rachté mouin ak san Li,\nJoua sièl la nan nan-m mouin kòmansé,\nM-ap viv sèlman pou Sovè a\nDépi amou Jézu té antré.\n\n\n2.\nGingnin gras pou tout kè inb yo,\nGras pou chak moun ki sové,\nGingnin puisans pou fè kè-m pròp\nDépi amou Jézu té antré.\n\n\n3.\nLésé-m bay moun Lévanjil la,\nJouin-n lé zòt pou Jézu-Kri;\nLa koua Jézu, mouin vlé pòté\nJouk tan mouin fini travay pou Li.','<p><strong>1.</strong> </p>\n<p>Gnou foua rout mouin té gin tristès, </p>\n<p>Paské kè-m té plin péché. </p>\n<p>Koul yé a mouin pa nan détrès, </p>\n<p>Dépi amou Jézu té antré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-pa kab di konbi-in mouin rinmin Jézu</strong> </p>\n<p><strong>M-pa kab di konbi-in li rinmin mouin,</strong> </p>\n<p><strong>Paské amou Li san mézu</strong> </p>\n<p><strong>É profondè li san fin.</strong> </p>\n<p><strong>Jézu-Kri té rachté mouin ak san Li,</strong> </p>\n<p><strong>Joua sièl la nan nan-m mouin kòmansé,</strong> </p>\n<p><strong>M-ap viv sèlman pou Sovè a</strong> </p>\n<p><strong>Dépi amou Jézu té antré.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gingnin gras pou tout kè inb yo, </p>\n<p>Gras pou chak moun ki sové, </p>\n<p>Gingnin puisans pou fè kè-m pròp </p>\n<p>Dépi amou Jézu té antré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lésé-m bay moun Lévanjil la, </p>\n<p>Jouin-n lé zòt pou Jézu-Kri; </p>\n<p>La koua Jézu, mouin vlé pòté </p>\n<p>Jouk tan mouin fini travay pou Li. </p>\n','Gnou-foua-rout-mouin-te-gin-tristes',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1757,'165dba43-5260-4be5-adb3-fdf0c352ad78',0,0,'Sièl la si bèl, O Étènèl',142,'142_CE_ht','1.\nSièl la si bèl, O Étènèl,\nLi palé nou sou la gloua ou,\nMin gin gnou bagay ki pi bèl:\nSé lè jé nou ouè Parol ou.\n\n\n2.\nBèl solèy la ki bay lumiè,\nPèmèt nou ouè gran puisans ou,\nMin nan la Bib, bèl liv si chè,\nNou jouin-n justis ak la gras ou.\n\n\n3.\nSolèy, la lun, bèl zétoual tou,\nKlélé la tè, lé mon-n antié.\nSé kon sa tou, vérité ou,\nSé pou tout moun sou la tè.\n\n\n4.\nLévanjil la, mésaj la gras,\nPap jan-m rété, jouk lè tout moun\nVi-n jouin-n ak Jézu fas a fas –\nLi mouri pou-l pa pèdu gnoun.\n\n\n5.\nBon Jézu-Kri, Lumiè lé mon-n,\nKléré tout moun ak lumiè ou.\nLévanjil kap ran-n saj tout òm,\nMin-m sa ki jin-n, min-m sa ki fou!\n\n\n6.\nKi bèl mèvèy nou kapab ouè,\nSé moun ki vi-n chanjé, sové.\nSégnè, sovè-m, ban-m gnou bon kè.\nAk Parol ou map jouin-n la pè.','<p><strong>1.</strong> </p>\n<p>Sièl la si bèl, O Étènèl, </p>\n<p>Li palé nou sou la gloua ou, </p>\n<p>Min gin gnou bagay ki pi bèl: </p>\n<p>Sé lè jé nou ouè Parol ou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bèl solèy la ki bay lumiè, </p>\n<p>Pèmèt nou ouè gran puisans ou, </p>\n<p>Min nan la Bib, bèl liv si chè, </p>\n<p>Nou jouin-n justis ak la gras ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Solèy, la lun, bèl zétoual tou, </p>\n<p>Klélé la tè, lé mon-n antié. </p>\n<p>Sé kon sa tou, vérité ou, </p>\n<p>Sé pou tout moun sou la tè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lévanjil la, mésaj la gras, </p>\n<p>Pap jan-m rété, jouk lè tout moun </p>\n<p>Vi-n jouin-n ak Jézu fas a fas – </p>\n<p>Li mouri pou-l pa pèdu gnoun. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Bon Jézu-Kri, Lumiè lé mon-n, </p>\n<p>Kléré tout moun ak lumiè ou. </p>\n<p>Lévanjil kap ran-n saj tout òm, </p>\n<p>Min-m sa ki jin-n, min-m sa ki fou! </p>\n<p><br/> <strong>6.</strong></p>\n<p>Ki bèl mèvèy nou kapab ouè, </p>\n<p>Sé moun ki vi-n chanjé, sové. </p>\n<p>Ségnè, sovè-m, ban-m gnou bon kè. </p>\n<p>Ak Parol ou map jouin-n la pè. </p>\n','Siel-la-si-bel-O-Etenel',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1758,'937bce9f-e001-4e4a-9746-d6209b6f818e',0,0,'Pran mouin Ségnè',8,'8_MJ_ht','1.\nPran mouin Ségnè,\nMin-m jan ké mouin yé\nMap ba ou tout,\nTout sa mouin gingnin\nFè mouin vi-n sin,\nÉ fè mouin maché\nFè mouin jan ou vlé mouin yé.\n\n2.\nPran mouin Ségnè,\nMouin pa kon-n prié\nM-pa bon du tou,\nMouin pa ka palé\nPran sa mouin yé,\nPasé-m nan dufé\nFè mouin jan ou vlé mouin yé.','<p><strong>1.</strong> </p>\n<p>Pran mouin Ségnè, </p>\n<p>Min-m jan ké mouin yé </p>\n<p>Map ba ou tout, </p>\n<p>Tout sa mouin gingnin </p>\n<p>Fè mouin vi-n sin, </p>\n<p>É fè mouin maché </p>\n<p>Fè mouin jan ou vlé mouin yé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pran mouin Ségnè, </p>\n<p>Mouin pa kon-n prié </p>\n<p>M-pa bon du tou, </p>\n<p>Mouin pa ka palé </p>\n<p>Pran sa mouin yé, </p>\n<p>Pasé-m nan dufé </p>\n<p>Fè mouin jan ou vlé mouin yé. </p>\n','Pran-mouin-Segne',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1759,'f4dd47ac-15d4-4c96-8d5b-3957adedf495',0,0,'Map Viv Pou Jézu',24,'24_MJ_ht','1.\nMap viv pou Jézu\nJou aprè jou,\nMap viv pou Jézu\nNinpò ki lè\nLè Sint-Éspri la\nMouin pa gin pè\nMap viv pou Jézu\nJou aprè jou.','<p><strong>1.</strong> </p>\n<p>Map viv pou Jézu </p>\n<p>Jou aprè jou, </p>\n<p>Map viv pou Jézu </p>\n<p>Ninpò ki lè </p>\n<p>Lè Sint-Éspri la </p>\n<p>Mouin pa gin pè </p>\n<p>Map viv pou Jézu </p>\n<p>Jou aprè jou. </p>\n','Map-Viv-Pou-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1760,'153e42d3-f4b6-4901-9008-deebbe303d99',0,0,'Pouki Ou Kité Tèt Chajé Démin Yo',28,'28_MJ_ht','1.\nPouki ou kité tèt chajé démin yo,\nFè ou tou tris é fatigé kè ou,\nSi zòtolan yo anba\nSouin Bon-Dieu nou\nLi p-ap manké pran souin ou tou.\nLi konnin bézouin ou,\nLi pòté fado ou,\nSi zòtolan yo anba\nSouin Bon-Dieu nou\nLi p-ap manké pran souin nou tou.','<p><strong>1.</strong> </p>\n<p>Pouki ou kité tèt chajé démin yo, </p>\n<p>Fè ou tou tris é fatigé kè ou, </p>\n<p>Si zòtolan yo anba </p>\n<p>Souin Bon-Dieu nou </p>\n<p>Li p-ap manké pran souin ou tou. </p>\n<p>Li konnin bézouin ou, </p>\n<p>Li pòté fado ou, </p>\n<p>Si zòtolan yo anba </p>\n<p>Souin Bon-Dieu nou </p>\n<p>Li p-ap manké pran souin nou tou. </p>\n','Pouki-Ou-Kite-Tet-Chaje-Demin-Yo',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1761,'d31d54dd-d9d3-4232-ab70-c30797e696e6',0,0,'Mèvéyé Kris Nan Yon Krèch Té Vini',33,'33_MJ_ht','1.\nMèvéyé Kris nan gnou krèch té vi-ni,\nFèt nan limaj dé lòm pou lé salu.\nAmou Bon-Dieu anvè lé mon-n pèdu\nPlédé ak péchè pou yo répanti.\n\nKè:\nMèvéyé non Jézu, mèvéyé kouròn Li,\nMèvéyé byinté, triont akonpli.\nMèvéyé boua Kalvè,\nMèvéyé gras Li fè,\nMèvéyé amou, mèvéyé Ségnè.\n\n2.\nMèvéyé vi ki ran-n sèvis gratis,\nLi sé zanmi pòv yo ak maléré.\nBonté-L infi-ni répan-n san mézi\nPou tou sa yo ki kouè kapab éré.\n\n3.\nMèvéyé la mò Li sé pa gnou pèd;\nKalvè té fè gran mision Li konplèt.\nSalu nou akonpli lè Li lévé\nPou chasé dènié lénmi a janmin.','<p><strong>1.</strong> </p>\n<p>Mèvéyé Kris nan gnou krèch té vi-ni, </p>\n<p>Fèt nan limaj dé lòm pou lé salu. </p>\n<p>Amou Bon-Dieu anvè lé mon-n pèdu </p>\n<p>Plédé ak péchè pou yo répanti. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mèvéyé non Jézu, mèvéyé kouròn Li,</strong> </p>\n<p><strong>Mèvéyé byinté, triont akonpli.</strong> </p>\n<p><strong>Mèvéyé boua Kalvè,</strong> </p>\n<p><strong>Mèvéyé gras Li fè,</strong> </p>\n<p><strong>Mèvéyé amou, mèvéyé Ségnè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mèvéyé vi ki ran-n sèvis gratis, </p>\n<p>Li sé zanmi pòv yo ak maléré. </p>\n<p>Bonté-L infi-ni répan-n san mézi </p>\n<p>Pou tou sa yo ki kouè kapab éré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mèvéyé la mò Li sé pa gnou pèd; </p>\n<p>Kalvè té fè gran mision Li konplèt. </p>\n<p>Salu nou akonpli lè Li lévé </p>\n<p>Pou chasé dènié lénmi a janmin. </p>\n','Meveye-Kris-Nan-Yon-Krech-Te-Vini',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1762,'248f2911-d290-410b-8390-44a7c7bfce5d',0,0,'M-ap Swiv Lé Ségnè Jodi a',46,'46_MJ_ht','1.\nM-ap suiv lé Ségnè jodi a,\nM-ap konfié-m nan Li pa-za-pa.\nLi pran tout péché mouin\nLè Li té sové mouin.\nM-ap suiv lé Ségnè jodi a.','<p><strong>1.</strong> </p>\n<p>M-ap suiv lé Ségnè jodi a, </p>\n<p>M-ap konfié-m nan Li pa-za-pa. </p>\n<p>Li pran tout péché mouin </p>\n<p>Lè Li té sové mouin. </p>\n<p>M-ap suiv lé Ségnè jodi a. </p>\n','M-ap-Swiv-Le-Segne-Jodi-a',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1763,'a691955a-376e-42e2-ac05-17f8605317d1',0,0,'Zaché Li Té Gnou Ti Nonm Otè Sa-a',55,'55_MJ_ht','1.\nZaché li té gnou ti non-m otè sa a\nGnou tou piti non-m li té yé;\nGnou jou li té monté gnou gro pié boua\nKa lé Ségnè té rivé la,\nLi gadé nan pié boua, li di:\n«Zaché, kouri désan-n,\nM-ap vi-ni kay ou jodi-a.\nM-ap vi-ni kay ou jodi-a.»','<p><strong>1.</strong> </p>\n<p>Zaché li té gnou ti non-m otè sa a </p>\n<p>Gnou tou piti non-m li té yé; </p>\n<p>Gnou jou li té monté gnou gro pié boua </p>\n<p>Ka lé Ségnè té rivé la, </p>\n<p>Li gadé nan pié boua, li di: </p>\n<p>«Zaché, kouri désan-n, </p>\n<p>M-ap vi-ni kay ou jodi-a. </p>\n<p>M-ap vi-ni kay ou jodi-a.» </p>\n','Zache-Li-Te-Gnou-Ti-Nonm-Ote-Sa-a',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1764,'61b0b11f-8476-4062-8f04-6c234899ad84',0,0,'Zwazo Yo Nan Bèl Boua Yo',77,'77_MJ_ht','1.\nZouazo yo nan bèl boua yo\nAp chanté pou Jézu,\nZanj yo ap chanté kè sa\nTout la jounin;\nTout flè yo nan jadin-an\nRan-n Li bèl odè,\nO – sé pou mouin, sé pou ou,\nLoué Li tou!','<p><strong>1.</strong> </p>\n<p>Zouazo yo nan bèl boua yo </p>\n<p>Ap chanté pou Jézu, </p>\n<p>Zanj yo ap chanté kè sa </p>\n<p>Tout la jounin; </p>\n<p>Tout flè yo nan jadin-an </p>\n<p>Ran-n Li bèl odè, </p>\n<p>O – sé pou mouin, sé pou ou, </p>\n<p>Loué Li tou! </p>\n','Zwazo-Yo-Nan-Bel-Boua-Yo',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1765,'0334d8d8-141f-4ea7-b1f1-89349748bda4',0,0,'Tout sa Mwen Vlé Sé Nan Jézu',78,'78_MJ_ht','1.\nTout sa mouin vlé sé nan Jézu,\nLi satisfè-m,\nJoua Li nan kè-m,\nLa vi sa pa angnin san Li\nTout bagay m-jouin’n nan Jézu.','<p><strong>1.</strong> </p>\n<p>Tout sa mouin vlé sé nan Jézu, </p>\n<p>Li satisfè-m, </p>\n<p>Joua Li nan kè-m, </p>\n<p>La vi sa pa angnin san Li </p>\n<p>Tout bagay m-jouin’n nan Jézu. </p>\n','Tout-sa-Mwen-Vle-Se-Nan-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1766,'0ca5debd-44c7-43e3-979c-85873133a738',0,0,'Amour sublime',42,'42_VR_fr','1.	\nAmour sublime\nImmense charité\nJésus victime\nMéprisé, maltraité\nSouffrit la peine\nDe mes nombreux forfaits\nEt brise ma gloire\nO gloire à jamais.\n\nChoeur\nSeigneur ta grâce\nAtteint jusqu\'aux cieux\nElle surpasse\nTout don merveilleux\nQui dans ce monde\nJ\'aurais souhaité\nCette grâce inonde\nTon racheté.\n\n2.	\nQuand je chancelle\nSur un sombre chemin\nL\'Ami fidèle\nMe saisit par la main\nAlors la crainte\nSe dissipe aussitôt\nPlus d\'amères plaintes\nLe regard là-haut.\n\n3.	\nTa croix sanglante\nEst ma seule rançon\nL\'âme souffrante\nY trouve un plein  pardon\nC\'est mon Refuge\nDans la vie et la mort\nDevant le Grand juge\nOh! Quel heureux sort.','<p><strong>1.</strong> </p>\n<p>Amour sublime </p>\n<p>Immense charité </p>\n<p>Jésus victime </p>\n<p>Méprisé, maltraité </p>\n<p>Souffrit la peine </p>\n<p>De mes nombreux forfaits </p>\n<p>Et brise ma gloire </p>\n<p>O gloire à jamais. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Seigneur ta grâce</strong> </p>\n<p><strong>Atteint jusqu&#39;aux cieux</strong> </p>\n<p><strong>Elle surpasse</strong> </p>\n<p><strong>Tout don merveilleux</strong> </p>\n<p><strong>Qui dans ce monde</strong> </p>\n<p><strong>J&#39;aurais souhaité</strong> </p>\n<p><strong>Cette grâce inonde</strong> </p>\n<p><strong>Ton racheté.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand je chancelle </p>\n<p>Sur un sombre chemin </p>\n<p>L&#39;Ami fidèle </p>\n<p>Me saisit par la main </p>\n<p>Alors la crainte </p>\n<p>Se dissipe aussitôt </p>\n<p>Plus d&#39;amères plaintes </p>\n<p>Le regard là-haut. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ta croix sanglante </p>\n<p>Est ma seule rançon </p>\n<p>L&#39;âme souffrante </p>\n<p>Y trouve un plein  pardon </p>\n<p>C&#39;est mon Refuge </p>\n<p>Dans la vie et la mort </p>\n<p>Devant le Grand juge </p>\n<p>Oh! Quel heureux sort. </p>\n','Amour-sublime',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1767,'7cfaf7f5-75e4-49d7-a63f-48695e2e38a1',0,0,'Flots mugissants',15,'15_VR_fr','1.	\nFlots mugissants, flots en furie\nEntourez-moi je n\'ai pas peur\nQuoiqu\'il en soit paix infinie\nPuisqu\'à la barre est mon Sauveur.\n\nChoeur\nLe gouvernail de ma nacelle\nOh! Quel repos Jésus se tient\nSi dans la nuit mon coeur chancelle\nAvec Jésus tout est bien.\n\n2.	\nEt vous récifs, écueils, menaces\nQui présagez plus d\'un malheur\nCalme je puis vous voir en face\nPuisqu\'à la barre est mon Sauveur.\n\n3.	\nJours après jour nouveaux orages\nNouveaux périls, point de frayeur\nBientôt, j\'atteins le grand rivage\nPuisqu\'à la barre est mon Sauveur.\n\n4.	\nAvec Jésus pour mon Pilote\nPour moi tout est paix et bonheur\nEn sûreté mon esquif flotte\nPuisqu\'à la barre est mon Sauveur.','<p><strong>1.</strong> </p>\n<p>Flots mugissants, flots en furie </p>\n<p>Entourez-moi je n&#39;ai pas peur </p>\n<p>Quoiqu&#39;il en soit paix infinie </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Le gouvernail de ma nacelle</strong> </p>\n<p><strong>Oh! Quel repos Jésus se tient</strong> </p>\n<p><strong>Si dans la nuit mon coeur chancelle</strong> </p>\n<p><strong>Avec Jésus tout est bien.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et vous récifs, écueils, menaces </p>\n<p>Qui présagez plus d&#39;un malheur </p>\n<p>Calme je puis vous voir en face </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jours après jour nouveaux orages </p>\n<p>Nouveaux périls, point de frayeur </p>\n<p>Bientôt, j&#39;atteins le grand rivage </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Avec Jésus pour mon Pilote </p>\n<p>Pour moi tout est paix et bonheur </p>\n<p>En sûreté mon esquif flotte </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n','Flots-mugissants',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1768,'a5aeacce-037c-43f5-a4ff-4617a308fc89',1,0,'Jézu, lésé-m maché ak ou',4,'4_CE_ht','1.\nJézu, lésé-m maché ak ou,\nBi-in kontan pou-m fè sèvis ou;\nDi mouin tout sékrè ou gingnin,\nÉdé-m pa inkiè pou démin.\nMontré-m édé kè moun ki pè\nAvèk bon Parol ou, Ségnè;\nGadé-m tout tan nan bon chémin,\nÉ kondui mouin juska la fin.\n\n\n2.\nFè-m gin plus pasians tankou ou,\nGadé-m tou pré ou tou lè jou\nPou-m fè travay mouin, ak la foua\nÉ konfians ou ki bay viktoua,\nNan éspoua ou ki kon solèy,\nKléré tout bagay sou la tè,\nNan la pè ou sèl ka ban nou,\nLésé-m viv, Jézu, tou prè ou!','<p><strong>1.</strong> </p>\n<p>Jézu, lésé-m maché ak ou, </p>\n<p>Bi-in kontan pou-m fè sèvis ou; </p>\n<p>Di mouin tout sékrè ou gingnin, </p>\n<p>Édé-m pa inkiè pou démin. </p>\n<p>Montré-m édé kè moun ki pè </p>\n<p>Avèk bon Parol ou, Ségnè; </p>\n<p>Gadé-m tout tan nan bon chémin, </p>\n<p>É kondui mouin juska la fin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fè-m gin plus pasians tankou ou, </p>\n<p>Gadé-m tou pré ou tou lè jou </p>\n<p>Pou-m fè travay mouin, ak la foua </p>\n<p>É konfians ou ki bay viktoua, </p>\n<p>Nan éspoua ou ki kon solèy, </p>\n<p>Kléré tout bagay sou la tè, </p>\n<p>Nan la pè ou sèl ka ban nou, </p>\n<p>Lésé-m viv, Jézu, tou prè ou! </p>\n','Jezu-lese-m-mache-ak-ou',NULL,'ht',7,'2023-07-22 22:41:15','2023-09-21 21:30:59'),(1769,'23cf6471-9fb1-484f-9383-aac01eb11635',0,0,'Lè mwen te chèche Bondye',67,'67_CE_ht','1.\nLè mouin té chaché Bon-Dieu,\nGnou pòv péchè pèdu,\nPou mandé-l padon péché-m\nO non Sovè Jézu,\nLi kouté tout kri kè mouin,\nTou suit Li fè-m kontan;\nPaské Jézu pran fado-m\nÉ Li té ban-m gnou chan.\n\nKè:\nOui Jézu té pran fado-m\nMouin pat kapab pòté,\nOui Jézu té pran fado-m,\nLi péyé dèt péché.\nTout krint mouin yo té sòti,\nLi fè kè-m tout kontan;\nPaské Jézu pran fado-m\nÉ Li té ban-m gnou chan.\n\n\n2.\nPafoua chémin an glisan,\nGin pa ki difisil,\nPafoua mouin fèb, mouin bouké,\nZéprèv mouin pa fasil;\nMin lè-m ajénouyé mouin,\n«Ségnè mouin, jouk ki tan?»\nLè sa Jézu pran fado-m.\nToujou Li ban-m gnou chan.\n\n\n3.\nGnou jou tristès té krazé-m,\nMouin pèdu tout éspoua.\nChaj doulè ak touman mouin\nTé sanblé tròp, mil foua.\nLè sa gnou voua vi-n palé-m,\n«Ou pou mouin sètènman.»\nLè sa Jézu pran fado-m,\nÉ Li té ban-m gnou chan.\n\n\n4.\nMa mét tout konfians nan Li,\nLi konnin tout chémin.\nLi va gidé-m ak jé Li\nNan pélérinaj mouin.\nÉ ma palé nan sièl la\nPou tout sièk ak tout tan\nKoman Jézu pran fado-m\nÉ plin kè-m ak gnou chan.','<p><strong>1.</strong> </p>\n<p>Lè mouin té chaché Bon-Dieu, </p>\n<p>Gnou pòv péchè pèdu, </p>\n<p>Pou mandé-l padon péché-m </p>\n<p>O non Sovè Jézu, </p>\n<p>Li kouté tout kri kè mouin, </p>\n<p>Tou suit Li fè-m kontan; </p>\n<p>Paské Jézu pran fado-m </p>\n<p>É Li té ban-m gnou chan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui Jézu té pran fado-m</strong> </p>\n<p><strong>Mouin pat kapab pòté,</strong> </p>\n<p><strong>Oui Jézu té pran fado-m,</strong> </p>\n<p><strong>Li péyé dèt péché.</strong> </p>\n<p><strong>Tout krint mouin yo té sòti,</strong> </p>\n<p><strong>Li fè kè-m tout kontan;</strong> </p>\n<p><strong>Paské Jézu pran fado-m</strong> </p>\n<p><strong>É Li té ban-m gnou chan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pafoua chémin an glisan, </p>\n<p>Gin pa ki difisil, </p>\n<p>Pafoua mouin fèb, mouin bouké, </p>\n<p>Zéprèv mouin pa fasil; </p>\n<p>Min lè-m ajénouyé mouin, </p>\n<p>«Ségnè mouin, jouk ki tan?» </p>\n<p>Lè sa Jézu pran fado-m. </p>\n<p>Toujou Li ban-m gnou chan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou jou tristès té krazé-m, </p>\n<p>Mouin pèdu tout éspoua. </p>\n<p>Chaj doulè ak touman mouin </p>\n<p>Té sanblé tròp, mil foua. </p>\n<p>Lè sa gnou voua vi-n palé-m, </p>\n<p>«Ou pou mouin sètènman.» </p>\n<p>Lè sa Jézu pran fado-m, </p>\n<p>É Li té ban-m gnou chan. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ma mét tout konfians nan Li, </p>\n<p>Li konnin tout chémin. </p>\n<p>Li va gidé-m ak jé Li </p>\n<p>Nan pélérinaj mouin. </p>\n<p>É ma palé nan sièl la </p>\n<p>Pou tout sièk ak tout tan </p>\n<p>Koman Jézu pran fado-m </p>\n<p>É plin kè-m ak gnou chan. </p>\n','Le-mwen-te-cheche-Bondye',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1770,'584fd382-41c7-4be2-ac39-feace60a500c',0,0,'Onore puisans non Jézu',70,'70_CE_ht','1.\nOnoré puisans non Jézu,\nTout zanj doué fas a tè.\nPòté vini kouròn pi sin\nÉ kouroné Li Ségnè.\nPòté vini kouròn pi sin\nÉ kouroné Li Ségnè.\n\n\n2.\nPèp chouazi nan ras Izraèl,\nNou ké Bon-Dieu rachté\nRan-n gloua pou Li ki ban nou sièl\nÉ kouroné Li Ségnè.\nRan-n gloua pou Li ki ban nou sièl\nÉ kouroné Li Ségnè.\n\n\n3.\nSé pou tout moun nan chak nasion\nKi rété sou la tè,\nBa Li lonnè kòm pi gran Chèf\nÉ kouroné Li Ségnè.\nBa Li lonnè kòm pi gran Chèf\nÉ kouroné Li Ségnè.\n\n\n4.\nAn nou uni nou ak zanj yo\nAk adoré nan sièl,\nNa chanté gnou kantik nouvo\nÉ kouroné Li Ségnè.\nNa chanté gnou kantik nouvo\nÉ kouroné Li Ségnè.','<p><strong>1.</strong> </p>\n<p>Onoré puisans non Jézu, </p>\n<p>Tout zanj doué fas a tè. </p>\n<p>Pòté vini kouròn pi sin </p>\n<p>É kouroné Li Ségnè. </p>\n<p>Pòté vini kouròn pi sin </p>\n<p>É kouroné Li Ségnè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pèp chouazi nan ras Izraèl, </p>\n<p>Nou ké Bon-Dieu rachté </p>\n<p>Ran-n gloua pou Li ki ban nou sièl </p>\n<p>É kouroné Li Ségnè. </p>\n<p>Ran-n gloua pou Li ki ban nou sièl </p>\n<p>É kouroné Li Ségnè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé pou tout moun nan chak nasion </p>\n<p>Ki rété sou la tè, </p>\n<p>Ba Li lonnè kòm pi gran Chèf </p>\n<p>É kouroné Li Ségnè. </p>\n<p>Ba Li lonnè kòm pi gran Chèf </p>\n<p>É kouroné Li Ségnè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>An nou uni nou ak zanj yo </p>\n<p>Ak adoré nan sièl, </p>\n<p>Na chanté gnou kantik nouvo </p>\n<p>É kouroné Li Ségnè. </p>\n<p>Na chanté gnou kantik nouvo </p>\n<p>É kouroné Li Ségnè. </p>\n','Onore-puisans-non-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15');
INSERT INTO `songs` (`id`, `uuid`, `views`, `likes`, `title`, `num`, `songId`, `lyrics`, `lyricsHtml`, `slug`, `video`, `language`, `SongCategoryId`, `createdAt`, `updatedAt`) VALUES (1771,'4e841d77-ead9-4def-b834-423821eff47f',0,0,'Préparé-m pou la gè',79,'79_CE_ht','1.\nPréparé-m pou la gè,\nPou-m goumin kont lé mal.\nFè kè-m sinsè, ban-m gnou bon kè\nKi pap di paròl sal.\nGidé tout pansé mouin,\nRétiré tout péché.\nPa gras Ou travay mouin va sin,\nAmou va tout réglé.\n\n\n2.\nAgno Bon-Dieu, ban mouin,\nMin-m léspri dous pa Ou.\nFè tout aktivité pa mouin\nGingnin charité tou.\nKité-m invité moun\nAvèk anpil dousè,\nPaské Ou pa vlé pèdu gnoun,\nMin-m nan lé dènié è!\n\n\n3.\nPa kité mouin konfié-m\nNan angnin sòf Ou-min’m;\nSi-m gin léspri ògèy nan kè-m,\nFè-m santi féblès mouin!\nFè-m konnin m-pa angnin;\nBan mouin gnou éspri inb.\nTout bagay bon ka fèt pa mouin,\nSé Ou-min’m sèl kap fè-l.\n\n\n4.\nBan mouin amou pa Ou,\nPou-m suiv chémin Ou suiv.\nOu rai tout bagay ki fou,\nMin pa moun Ou fè viv.\nChanjé, gidé léspri-m\nLouin tout bagay ki fo,\nPou-m rai péché ak tout kè-m,\nMin rinmin péché yo.','<p><strong>1.</strong> </p>\n<p>Préparé-m pou la gè, </p>\n<p>Pou-m goumin kont lé mal. </p>\n<p>Fè kè-m sinsè, ban-m gnou bon kè </p>\n<p>Ki pap di paròl sal. </p>\n<p>Gidé tout pansé mouin, </p>\n<p>Rétiré tout péché. </p>\n<p>Pa gras Ou travay mouin va sin, </p>\n<p>Amou va tout réglé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Agno Bon-Dieu, ban mouin, </p>\n<p>Min-m léspri dous pa Ou. </p>\n<p>Fè tout aktivité pa mouin </p>\n<p>Gingnin charité tou. </p>\n<p>Kité-m invité moun </p>\n<p>Avèk anpil dousè, </p>\n<p>Paské Ou pa vlé pèdu gnoun, </p>\n<p>Min-m nan lé dènié è! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pa kité mouin konfié-m </p>\n<p>Nan angnin sòf Ou-min’m; </p>\n<p>Si-m gin léspri ògèy nan kè-m, </p>\n<p>Fè-m santi féblès mouin! </p>\n<p>Fè-m konnin m-pa angnin; </p>\n<p>Ban mouin gnou éspri inb. </p>\n<p>Tout bagay bon ka fèt pa mouin, </p>\n<p>Sé Ou-min’m sèl kap fè-l. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ban mouin amou pa Ou, </p>\n<p>Pou-m suiv chémin Ou suiv. </p>\n<p>Ou rai tout bagay ki fou, </p>\n<p>Min pa moun Ou fè viv. </p>\n<p>Chanjé, gidé léspri-m </p>\n<p>Louin tout bagay ki fo, </p>\n<p>Pou-m rai péché ak tout kè-m, </p>\n<p>Min rinmin péché yo. </p>\n','Prepare-m-pou-la-ge',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1772,'8e9af9fc-facf-4249-a9f0-11581fed702f',0,0,'Jézu, Ou ki rinmin mouin',81,'81_CE_ht','1.\nJézu, Ou ki rinmin mouin,\nKité mouin alé jouin-n Ou;\nTout lòt azil kapab vin,\nLè lan mò ménasé nou.\nKaché mouin, O Sovè mouin,\nPandan tout loraj la vi.\nRésévoua mouin a la fin\nNan la pè ki pap fini.\n\n\n2.\nOu sé sèl réfuj mouin gin,\nNan-m mouin pa gin lòt éspoua!\nTanpri, pa bandonné mouin,\nSovè-m, lè tout bagay noua.\nMouin vlé konfié-m nou Ou sèl,\nMouin pa gingnin lòt sékou;\nSèvi pou mouin kou gnou zèl,\nMa va bi-in lè-m avèk Ou.\n\n\n3.\nMouin pa bézouin lòt bagay\nPasé Ou, bon Jézu-Kri.\nSé Ou-min’m ki té kon-n bay\nLa santé é min-m la vi.\nMouin-min’m, mouin pa vo angnin,\nMouin gingnin anpil péché,\nSé Ou sèl ki vrèman sin,\nOu sé gras é vérité.\n\n\n4.\nGingnin anpil gras nan Ou…\nPi gran pasé péché nou.\nGras Ou sé gnou bagay dou,\nKité li travay nan nou.\nJézu, Ou sé sous la vi,\nBan-m Léspri Ou san mézi;\nJayi nan mouin kou gnou pui;\nSé la vi ki pap fi-ni.','<p><strong>1.</strong> </p>\n<p>Jézu, Ou ki rinmin mouin, </p>\n<p>Kité mouin alé jouin-n Ou; </p>\n<p>Tout lòt azil kapab vin, </p>\n<p>Lè lan mò ménasé nou. </p>\n<p>Kaché mouin, O Sovè mouin, </p>\n<p>Pandan tout loraj la vi. </p>\n<p>Résévoua mouin a la fin </p>\n<p>Nan la pè ki pap fini. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou sé sèl réfuj mouin gin, </p>\n<p>Nan-m mouin pa gin lòt éspoua! </p>\n<p>Tanpri, pa bandonné mouin, </p>\n<p>Sovè-m, lè tout bagay noua. </p>\n<p>Mouin vlé konfié-m nou Ou sèl, </p>\n<p>Mouin pa gingnin lòt sékou; </p>\n<p>Sèvi pou mouin kou gnou zèl, </p>\n<p>Ma va bi-in lè-m avèk Ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin pa bézouin lòt bagay </p>\n<p>Pasé Ou, bon Jézu-Kri. </p>\n<p>Sé Ou-min’m ki té kon-n bay </p>\n<p>La santé é min-m la vi. </p>\n<p>Mouin-min’m, mouin pa vo angnin, </p>\n<p>Mouin gingnin anpil péché, </p>\n<p>Sé Ou sèl ki vrèman sin, </p>\n<p>Ou sé gras é vérité. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gingnin anpil gras nan Ou… </p>\n<p>Pi gran pasé péché nou. </p>\n<p>Gras Ou sé gnou bagay dou, </p>\n<p>Kité li travay nan nou. </p>\n<p>Jézu, Ou sé sous la vi, </p>\n<p>Ban-m Léspri Ou san mézi; </p>\n<p>Jayi nan mouin kou gnou pui; </p>\n<p>Sé la vi ki pap fi-ni. </p>\n','Jezu-Ou-ki-rinmin-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1773,'d3c70b99-42e4-4516-9a45-fa6ff1094785',0,0,'Mouin kontan jodia, gin solèy nan kè-m',87,'87_CE_ht','1.\nMouin kontan jodia, gin solèy nan kè-m;\nTout krint ak dout yo pasé;\nJézu di kon sa: ninpòt moun ki vlé\nMèt jouin-n mouin pou-l sové.\n\nKè:\nNan Jan toua sèz  «ki konk» vlé di mouin,\n«Ki konk» vlé di mouin,\nMap mété non mouin la.\nNan Jan toua sèz «ki konk» vlé di mouin;\nMouin ladan! Alélouya!\n\n\n2.\nMouin kontan jodia, gin éspoua nan kè-m;\nA Jézu mouin vlé ran-n gloua,\nPaské li ban-m la viktoua sou péché-m;\nNan kè-m li sèl sé Roua.\n\n\n3.\nAmou li mèvéyé! Gras li mèvéyé!\nPou mouin – Jézu té mouri.\nMouin té rinmin lé mon-n, m-té pèdu nèt,\nJouk tan li té fè-m lib.','<p><strong>1.</strong> </p>\n<p>Mouin kontan jodia, gin solèy nan kè-m; </p>\n<p>Tout krint ak dout yo pasé; </p>\n<p>Jézu di kon sa: ninpòt moun ki vlé </p>\n<p>Mèt jouin-n mouin pou-l sové. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan Jan toua sèz  «ki konk» vlé di mouin,</strong> </p>\n<p><strong>«Ki konk» vlé di mouin,</strong> </p>\n<p><strong>Map mété non mouin la.</strong> </p>\n<p><strong>Nan Jan toua sèz «ki konk» vlé di mouin;</strong> </p>\n<p><strong>Mouin ladan! Alélouya!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin kontan jodia, gin éspoua nan kè-m; </p>\n<p>A Jézu mouin vlé ran-n gloua, </p>\n<p>Paské li ban-m la viktoua sou péché-m; </p>\n<p>Nan kè-m li sèl sé Roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Amou li mèvéyé! Gras li mèvéyé! </p>\n<p>Pou mouin – Jézu té mouri. </p>\n<p>Mouin té rinmin lé mon-n, m-té pèdu nèt, </p>\n<p>Jouk tan li té fè-m lib. </p>\n','Mouin-kontan-jodia-gin-soley-nan-ke-m',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1774,'fd50e6e2-62c1-47dd-9a3a-73f11d0b71b8',0,0,'Map tandé bèl voua bon Sovè mouin',97,'97_CE_ht','1.\nMap tandé bèl voua bon Sovè mouin;\nVi-n suiv mouin;  (3 foua)\nVoua Jézu ap rélé-m tout jounin:\nVi-n suiv mouin;  (3 foua)\nMouin té monté Kalvè pou ou;\nMouin té bay la vi-m pou sové ou;\nSan pa-m vèsé péyé dèt ou;\nVi-n suiv mouin.  (3 foua)\n\n\n2.\nOu té péché, min ou jouin-n padon;\nVi-n suiv mouin;  (3 foua)\nMa lagé ou nan chin-n péché yo;\nVi-n suiv mouin;  (3 foua)\nTout lot bagay apé chanjé,\nMa va dirijé ou, si ou vlé,\nM-va joua ou pou létènité,\nVi-n suiv mouin.  (3 foua)\n\n\n3.\nVi-n rémèt mouin tout chagrin ou yo,\nVi-n suiv mouin;  (3 foua)\nMouin prèt pou ouété fado ou yo,\nVi-n suiv mouin;  (3 foua)\nVi-n jouin-n répo, kité krint ou;\nÉ kouè mouin pou sa-k va rivé tou,\nMa séché tout dlo nan jé ou;\nVi-n suiv mouin.  (3 foua)','<p><strong>1.</strong> </p>\n<p>Map tandé bèl voua bon Sovè mouin; </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Voua Jézu ap rélé-m tout jounin: </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Mouin té monté Kalvè pou ou; </p>\n<p>Mouin té bay la vi-m pou sové ou; </p>\n<p>San pa-m vèsé péyé dèt ou; </p>\n<p>Vi-n suiv mouin.  (3 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou té péché, min ou jouin-n padon; </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Ma lagé ou nan chin-n péché yo; </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Tout lot bagay apé chanjé, </p>\n<p>Ma va dirijé ou, si ou vlé, </p>\n<p>M-va joua ou pou létènité, </p>\n<p>Vi-n suiv mouin.  (3 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vi-n rémèt mouin tout chagrin ou yo, </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Mouin prèt pou ouété fado ou yo, </p>\n<p>Vi-n suiv mouin;  (3 foua) </p>\n<p>Vi-n jouin-n répo, kité krint ou; </p>\n<p>É kouè mouin pou sa-k va rivé tou, </p>\n<p>Ma séché tout dlo nan jé ou; </p>\n<p>Vi-n suiv mouin.  (3 foua) </p>\n','Map-tande-bel-voua-bon-Sove-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1775,'294f5dbc-7172-4239-a197-85e0d05b3778',0,0,'Sé gnou chan jouayé',4,'4_MJ_ht','1.\nSé gnou chan jouayé,\nGnou nouvèl glorié,\nNan pouin lòt kòm li\nNan lé mond antié;\nMouin rinmin chanté-l\nÉ m-a proklamé-l\nBon-Dieu nou kapab\nBa ou la délivrans.\n\nKè:\nSé Bon-Dieu ki kapab délivré,\nSé Li sèl ki toujou délivré,\nTout fado péché, sou Li dépozé,\nPaské Bon-Dieu nou\nKap bay la délivrans.\n\n2.\nMoun-n vlé vini\nPa doué jan-m mizé,\nPòt la byin ouvè pou-l kapab antré;\nKris sé chémin, la vérté, la vi,\nBon-Dieu nou kapab\nBa ou la délivrans.\n\n3.\nSé gnou chan jouayé,\nProklamé-l byin louin,\nPou tout aflijé anba chaj péché,\nKapab résévoua padon pa la foua,\nBon-Dieu non kapab\nBa ou la délivrans.','<p><strong>1.</strong> </p>\n<p>Sé gnou chan jouayé, </p>\n<p>Gnou nouvèl glorié, </p>\n<p>Nan pouin lòt kòm li </p>\n<p>Nan lé mond antié; </p>\n<p>Mouin rinmin chanté-l </p>\n<p>É m-a proklamé-l </p>\n<p>Bon-Dieu nou kapab </p>\n<p>Ba ou la délivrans. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé Bon-Dieu ki kapab délivré,</strong> </p>\n<p><strong>Sé Li sèl ki toujou délivré,</strong> </p>\n<p><strong>Tout fado péché, sou Li dépozé,</strong> </p>\n<p><strong>Paské Bon-Dieu nou</strong> </p>\n<p><strong>Kap bay la délivrans.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moun-n vlé vini </p>\n<p>Pa doué jan-m mizé, </p>\n<p>Pòt la byin ouvè pou-l kapab antré; </p>\n<p>Kris sé chémin, la vérté, la vi, </p>\n<p>Bon-Dieu nou kapab </p>\n<p>Ba ou la délivrans. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé gnou chan jouayé, </p>\n<p>Proklamé-l byin louin, </p>\n<p>Pou tout aflijé anba chaj péché, </p>\n<p>Kapab résévoua padon pa la foua, </p>\n<p>Bon-Dieu non kapab </p>\n<p>Ba ou la délivrans. </p>\n','Se-gnou-chan-jouaye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1776,'893989ac-f069-4457-9a57-3a464c6270cd',0,0,'Gnou bagay pasé nan kè mouin',5,'5_MJ_ht','1.\nGnou bagay pasé nan kè mouin,\nLè mouin té sové  (bis)\nGnou bagay pasé nan kè mouin,\nMin-m lè Jézu té sové-m.','<p><strong>1.</strong> </p>\n<p>Gnou bagay pasé nan kè mouin, </p>\n<p>Lè mouin té sové  (bis) </p>\n<p>Gnou bagay pasé nan kè mouin, </p>\n<p>Min-m lè Jézu té sové-m. </p>\n','Gnou-bagay-pase-nan-ke-mouin',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1777,'e18bf9c5-0996-4d7c-9f6c-af4a0ce352f4',0,0,'Rout La Vi Sé Kon Gnou Chémin',17,'17_MJ_ht','1.\nRout la vi sé kon gnou chémin,\nLa-dan gin anpil danjé;\nLi trè du épi li fè noua,\nGin labou, li byin glisé.\nFòk nou véyé a chak moman,\nSi nou vlé fè chémin sa-a,\nSé pou-n jouin-n\nGnou moun-n kondui nou.\nÉpi suiv li pa-za-pa.\n\nKè:\nO Bon Sovè, vi-n kondui mouin,\nFè mouin rivé jouk lòt bò\nKoté zanj yo ap chanté non ou,\nÉ mouin pap tounin ankò.\n\n2.\nNap monté épi nap désan-n,\nGin falèz a gòch a douat,\nMòn la rèd é rout la fonsé,\nLi apik épi étouat.\nFò-n bay gid la kinbé min nou,\nSi nou vlé fè chémin sa-a\nSé pou fixé jé nou sou Li,\nÉpi suiv Li pa-za-pa.\n\n3.\nGin lòt rout ki laj é byin bèl,\nMin li pa ka fè-m rivé\nLòt bò mòn la avèk Jézu,\nKoté zanj yo ap chanté.\nO Jézu, pa kité-m tonbé,\nNi bronché nan chémin sa-a,\nMin kinbé-m plu prè ou toujou,\nMapé suiv ou pa-za-pa.','<p><strong>1.</strong> </p>\n<p>Rout la vi sé kon gnou chémin, </p>\n<p>La-dan gin anpil danjé; </p>\n<p>Li trè du épi li fè noua, </p>\n<p>Gin labou, li byin glisé. </p>\n<p>Fòk nou véyé a chak moman, </p>\n<p>Si nou vlé fè chémin sa-a, </p>\n<p>Sé pou-n jouin-n </p>\n<p>Gnou moun-n kondui nou. </p>\n<p>Épi suiv li pa-za-pa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O Bon Sovè, vi-n kondui mouin,</strong> </p>\n<p><strong>Fè mouin rivé jouk lòt bò</strong> </p>\n<p><strong>Koté zanj yo ap chanté non ou,</strong> </p>\n<p><strong>É mouin pap tounin ankò.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nap monté épi nap désan-n, </p>\n<p>Gin falèz a gòch a douat, </p>\n<p>Mòn la rèd é rout la fonsé, </p>\n<p>Li apik épi étouat. </p>\n<p>Fò-n bay gid la kinbé min nou, </p>\n<p>Si nou vlé fè chémin sa-a </p>\n<p>Sé pou fixé jé nou sou Li, </p>\n<p>Épi suiv Li pa-za-pa. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin lòt rout ki laj é byin bèl, </p>\n<p>Min li pa ka fè-m rivé </p>\n<p>Lòt bò mòn la avèk Jézu, </p>\n<p>Koté zanj yo ap chanté. </p>\n<p>O Jézu, pa kité-m tonbé, </p>\n<p>Ni bronché nan chémin sa-a, </p>\n<p>Min kinbé-m plu prè ou toujou, </p>\n<p>Mapé suiv ou pa-za-pa. </p>\n','Rout-La-Vi-Se-Kon-Gnou-Chemin',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1778,'c0a6c493-05a4-4a42-90f0-193195117cee',0,0,'Té Gin Douz Disip Yo',27,'27_MJ_ht','1.\nTé gin douz disip yo\nKé Jézu té rélé\nSimon Piè é André,\nJak é frè li Jan\nPhilip, Toma, Matiè,\nJak ki pitit Alfé\nTadé, Simon, Juda,\nÉpi Batélmi.\n\nKè:\nLi rélé nou tou  (bis)\nNou sé disip Jézu,\nLi rélé nou tou.\nLi rélé nou tou  (bis)\nNou sé disip Jézu,\nLi rélé nou tou.','<p><strong>1.</strong> </p>\n<p>Té gin douz disip yo </p>\n<p>Ké Jézu té rélé </p>\n<p>Simon Piè é André, </p>\n<p>Jak é frè li Jan </p>\n<p>Philip, Toma, Matiè, </p>\n<p>Jak ki pitit Alfé </p>\n<p>Tadé, Simon, Juda, </p>\n<p>Épi Batélmi. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li rélé nou tou  (bis)</strong> </p>\n<p><strong>Nou sé disip Jézu,</strong> </p>\n<p><strong>Li rélé nou tou.</strong> </p>\n<p><strong>Li rélé nou tou  (bis)</strong> </p>\n<p><strong>Nou sé disip Jézu,</strong> </p>\n<p><strong>Li rélé nou tou.</strong> </p>\n','Te-Gin-Douz-Disip-Yo',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1779,'3daa732b-fccb-4e69-9444-2b0022807b34',0,0,'Isi-ba Mouin Satisfè Ak Gnou Ti Kay',38,'38_MJ_ht','1.\nIsi-ba mouin satisfè ak gnou ti kay\nAk mézu byin ké, Jézu ap bay.\nNan vil sélès la\nKoté zanj yo ap chanté\nMouin gin gnou palè dépi mouin sové.\n\nKè:\nNan péi-m anro-a\nMouin gingnin gnou palè\nSé lòt bò mòn la mouin anvi rivé,\nGnou jou map ouè li\nLè Jézu va vini\nMap loué Li pou létènité.\n\n2.\nMalgré mouin tanté,\nToumanté, méprizé\nÉ pafoua tèt mouin kon-n trè chajé\nMap alé nan sièl la\nMouin ouè pa la foua\nGnou palè la-ba.\n\n3.\nMouin pa maléré,\nMouin pa dékourajé\nMouin santi mouin rich é,\nMouin vlé chanté\nDévan tròn Bon-Dieu\nNap kanpé ak zanj yo\nJoué hap mouin nan\nGnou kantik nouvo.','<p><strong>1.</strong> </p>\n<p>Isi-ba mouin satisfè ak gnou ti kay </p>\n<p>Ak mézu byin ké, Jézu ap bay. </p>\n<p>Nan vil sélès la </p>\n<p>Koté zanj yo ap chanté </p>\n<p>Mouin gin gnou palè dépi mouin sové. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan péi-m anro-a</strong> </p>\n<p><strong>Mouin gingnin gnou palè</strong> </p>\n<p><strong>Sé lòt bò mòn la mouin anvi rivé,</strong> </p>\n<p><strong>Gnou jou map ouè li</strong> </p>\n<p><strong>Lè Jézu va vini</strong> </p>\n<p><strong>Map loué Li pou létènité.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Malgré mouin tanté, </p>\n<p>Toumanté, méprizé </p>\n<p>É pafoua tèt mouin kon-n trè chajé </p>\n<p>Map alé nan sièl la </p>\n<p>Mouin ouè pa la foua </p>\n<p>Gnou palè la-ba. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin pa maléré, </p>\n<p>Mouin pa dékourajé </p>\n<p>Mouin santi mouin rich é, </p>\n<p>Mouin vlé chanté </p>\n<p>Dévan tròn Bon-Dieu </p>\n<p>Nap kanpé ak zanj yo </p>\n<p>Joué hap mouin nan </p>\n<p>Gnou kantik nouvo. </p>\n','Isi-ba-Mouin-Satisfe-Ak-Gnou-Ti-Kay',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1780,'734246ad-2cd5-4170-8e3f-b19652549774',0,0,'Nan Mitan Gro La mè ak tanpèt mouin té yé',60,'60_MJ_ht','1.\nNan mitan gro la mè ak tanpèt mouin té yé\nSan sékou-m té pèdu,\nSan la foua, san lumiè,\nTout a kou mouin ouè gnou gro bato ap pasé.\nMouin ranmasé tout fòs mouin\nÉ mouin té rélé:\nO sékou!  O sékou!\nKi lès ki pou sové-m?  O sékou!\n\n2.\nSé bato Lévanjil vapè byin ékipé\nKi t-ap minnin fidèl yo\nNan rouayòm Bon-Dieu.\nKapitèn bato a ouè éfò mouin tap fè\nAk gnou min byin an lè mouin té krié trè klè:\nO sékou!  O sékou!\nKi lès ki pou sovè-m?  O sékou!\n\n3.\nTout suit bato sovtaj fè mouin repran-n kouraj\nNan gran bonté li chèf la\nPran ka mouin an chaj.\nKoun yé a moun kontan é zafè mouin réglé\nPaské gran Sovè-a rivé\nKoté-m té yé.\n\n4.\nAmi dékourajé ki pa konnin la pè\nKap fè nofraj anba térib la mè péché\nSovè mouin dispozé sové ou si ou vlé,\nVi-n chèché sékou Li koun yé a san dèlé.\nLi sové-m!  li sové-m!\nAk la joua oua chanté: Li sové-m!','<p><strong>1.</strong> </p>\n<p>Nan mitan gro la mè ak tanpèt mouin té yé </p>\n<p>San sékou-m té pèdu, </p>\n<p>San la foua, san lumiè, </p>\n<p>Tout a kou mouin ouè gnou gro bato ap pasé. </p>\n<p>Mouin ranmasé tout fòs mouin </p>\n<p>É mouin té rélé: </p>\n<p>O sékou!  O sékou! </p>\n<p>Ki lès ki pou sové-m?  O sékou! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé bato Lévanjil vapè byin ékipé </p>\n<p>Ki t-ap minnin fidèl yo </p>\n<p>Nan rouayòm Bon-Dieu. </p>\n<p>Kapitèn bato a ouè éfò mouin tap fè </p>\n<p>Ak gnou min byin an lè mouin té krié trè klè: </p>\n<p>O sékou!  O sékou! </p>\n<p>Ki lès ki pou sovè-m?  O sékou! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout suit bato sovtaj fè mouin repran-n kouraj </p>\n<p>Nan gran bonté li chèf la </p>\n<p>Pran ka mouin an chaj. </p>\n<p>Koun yé a moun kontan é zafè mouin réglé </p>\n<p>Paské gran Sovè-a rivé </p>\n<p>Koté-m té yé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ami dékourajé ki pa konnin la pè </p>\n<p>Kap fè nofraj anba térib la mè péché </p>\n<p>Sovè mouin dispozé sové ou si ou vlé, </p>\n<p>Vi-n chèché sékou Li koun yé a san dèlé. </p>\n<p>Li sové-m!  li sové-m! </p>\n<p>Ak la joua oua chanté: Li sové-m! </p>\n','Nan-Mitan-Gro-La-me-ak-tanpet-mouin-te-ye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1781,'19fe291d-632d-416d-9819-07f64b2e44f8',0,0,'Jézu Fidèl, Sé Dieu Li Yé',62,'62_MJ_ht','1.\nJézu fidèl, sé Dieu Li yé\nJézu fidèl, Paròl Li vré\nJézu fidèl, L-ap édé ou\nAn nou fidèl a Li-min’m tou.','<p><strong>1.</strong> </p>\n<p>Jézu fidèl, sé Dieu Li yé </p>\n<p>Jézu fidèl, Paròl Li vré </p>\n<p>Jézu fidèl, L-ap édé ou </p>\n<p>An nou fidèl a Li-min’m tou. </p>\n','Jezu-Fidel-Se-Dieu-Li-Ye',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1782,'7da906de-b51e-4cd7-a84e-ed4c1233b3c9',0,0,'An Nou Palé De Jézu',63,'63_MJ_ht','1.\nAn nou palé dé Jézu\nSé Roua dè roua Li yé\nSégnè sou tè, nan sièl,\nPou tou létènité\nRout la, vérité-a,\nLa vi é la pòt la,\nAn nou palé dé Jézu pou toujou.','<p><strong>1.</strong> </p>\n<p>An nou palé dé Jézu </p>\n<p>Sé Roua dè roua Li yé </p>\n<p>Ségnè sou tè, nan sièl, </p>\n<p>Pou tou létènité </p>\n<p>Rout la, vérité-a, </p>\n<p>La vi é la pòt la, </p>\n<p>An nou palé dé Jézu pou toujou. </p>\n','An-Nou-Pale-De-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1783,'4a95de4f-747d-4781-beb8-604f8bdd758c',0,0,'Toujou Kouè Nan Jézu',74,'74_MJ_ht','1.\nToujou kouè nan Jézu\nJézu pré nou.\nToujou kouè nan Jézu\nLap kinbé min ou.\nToujou kouè nan Jézu\nSuiv Li toujou\nGin la foua nan la nuit\nÉ pandan chak jou.','<p><strong>1.</strong> </p>\n<p>Toujou kouè nan Jézu </p>\n<p>Jézu pré nou. </p>\n<p>Toujou kouè nan Jézu </p>\n<p>Lap kinbé min ou. </p>\n<p>Toujou kouè nan Jézu </p>\n<p>Suiv Li toujou </p>\n<p>Gin la foua nan la nuit </p>\n<p>É pandan chak jou. </p>\n','Toujou-Koue-Nan-Jezu',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1784,'5ae25414-64a5-4043-bbca-ae83b6212942',0,0,'Clarté Céleste',12,'12_VR_fr','1.	\nDans les vallons et sur la montagne\nA la clarté des rayons divers,\nJésus mon divin Rédempteur m\'accompagne\nIl est mon juge jusqu\'à la fin.           \n\nChoeur\nClarté céleste, clarté céleste\nRemplit mon coeur de rayons divins,\nClarté céleste, clarté céleste\nInonde de soleil mon chemin.\n\n2.	\nLorsque les nuages m\'environnent\nIl ne peut cacher mon Rédempteur\nCar sa lumière sur moi rayonne\nLa nuit a perdu toute sa terreur.\n\n3.	\nEn avant chrétien dans l\'allégresse\nToujours plus haut presse vers les cieux,\nMarche en avant louer sans cesse\nChrist le Seigneur Jésus de Dieu.','<p><strong>1.</strong> </p>\n<p>Dans les vallons et sur la montagne </p>\n<p>A la clarté des rayons divers, </p>\n<p>Jésus mon divin Rédempteur m&#39;accompagne </p>\n<p>Il est mon juge jusqu&#39;à la fin. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Clarté céleste, clarté céleste</strong> </p>\n<p><strong>Remplit mon coeur de rayons divins,</strong> </p>\n<p><strong>Clarté céleste, clarté céleste</strong> </p>\n<p><strong>Inonde de soleil mon chemin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lorsque les nuages m&#39;environnent </p>\n<p>Il ne peut cacher mon Rédempteur </p>\n<p>Car sa lumière sur moi rayonne </p>\n<p>La nuit a perdu toute sa terreur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En avant chrétien dans l&#39;allégresse </p>\n<p>Toujours plus haut presse vers les cieux, </p>\n<p>Marche en avant louer sans cesse </p>\n<p>Christ le Seigneur Jésus de Dieu. </p>\n','Clarte-Celeste',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1785,'66191b33-1b76-4d4c-838b-8afdc83bd2a3',0,0,'Qui Dit Au Soleil Sur La Terre',330,'330_CE_fr','1.\nQui dit au soleil sur la terre\nD\'éclairer tout homme en tout lieu,\nEt qui donne aux nuits leur mystère?\nC\'est l\'Éternel, c\'est notre Dieu!  (bis)\n\n\n2.\nLe bluet et le ciel superbe,\nQui les a teints d\'un si beau bleu?\nQui verdit l\'émeraude et l\'herbe?\nC\'est l\'Éternel, c\'est notre Dieu!  (bis)\n\n\n3.\nQuand nous pleurons, qui nous console?\nQui veille sur nous en tout lieu?\nQui nous soutient par sa Parole?\nC\'est l\'Éternel, c\'est notre Dieu!  (bis)\n\n\n4.\nEt lorsque notre âme immortelle\nA la terre aura dit adieu,\nQui nous recevra sous son aile?\nC\'est l\'Éternel, c\'est notre Dieu!  (bis)','<p><strong>1.</strong> </p>\n<p>Qui dit au soleil sur la terre </p>\n<p>D&#39;éclairer tout homme en tout lieu, </p>\n<p>Et qui donne aux nuits leur mystère? </p>\n<p>C&#39;est l&#39;Éternel, c&#39;est notre Dieu!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le bluet et le ciel superbe, </p>\n<p>Qui les a teints d&#39;un si beau bleu? </p>\n<p>Qui verdit l&#39;émeraude et l&#39;herbe? </p>\n<p>C&#39;est l&#39;Éternel, c&#39;est notre Dieu!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand nous pleurons, qui nous console? </p>\n<p>Qui veille sur nous en tout lieu? </p>\n<p>Qui nous soutient par sa Parole? </p>\n<p>C&#39;est l&#39;Éternel, c&#39;est notre Dieu!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et lorsque notre âme immortelle </p>\n<p>A la terre aura dit adieu, </p>\n<p>Qui nous recevra sous son aile? </p>\n<p>C&#39;est l&#39;Éternel, c&#39;est notre Dieu!  (bis) </p>\n','Qui-Dit-Au-Soleil-Sur-La-Terre',NULL,'fr',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1786,'92a97cb8-5aee-44da-a02f-d1d6f0095f5e',0,0,'Moun ki méténan Jézu',5,'5_CE_ht','1.\nMoun ki mété nan Jézu\nGnou konfians ki san mézu,\nYo kapab pa jan-m tonbé;\nDélivrans yo asuré.\n\nKè\nAk konfians n-a va maché,\nAk konfians n-a trionfé,\nAk konfians juska la fin,\nBon-Dieu va louvri chémin.\n\n\n2.\nAk konfians n-a va maché,\nKout ké kout pèsévéré;\nJézu-Kri fè nou promès,\nLi ak nou nan tout détrès.','<p><strong>1.</strong> </p>\n<p>Moun ki mété nan Jézu </p>\n<p>Gnou konfians ki san mézu, </p>\n<p>Yo kapab pa jan-m tonbé; </p>\n<p>Délivrans yo asuré. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Ak konfians n-a va maché,</strong> </p>\n<p><strong>Ak konfians n-a trionfé,</strong> </p>\n<p><strong>Ak konfians juska la fin,</strong> </p>\n<p><strong>Bon-Dieu va louvri chémin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ak konfians n-a va maché, </p>\n<p>Kout ké kout pèsévéré; </p>\n<p>Jézu-Kri fè nou promès, </p>\n<p>Li ak nou nan tout détrès. </p>\n','Moun-ki-metenan-Jezu',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1787,'6c5da4f7-de72-4672-9ef9-f9910db53210',0,0,'Ségnè Bon-Dieu Ki Papa Nou',27,'27_CE_ht','1.\nSégnè Bon-Dieu ki Papa nou,\nKi rinmin tout moun tou,\nPadonnin nou tout bagay fou,\nChanjé vi nou yo tou patou,\nRan-n nou pi sanblé ou.\n\n\n2.\nÉdé nou montré gnou la foua,\nKon moun yo ki ak joua,\nNan Galilé té suiv la voua\nJézu, lè-l ofri yo gnou choua,\nLa foua o lié la loua.\n\n\n3.\nJété sou nou gnou bèl rozé,\nGnou léspri plin la pè.\nKalmé nan-m yo ki anouyé,\nÉ ba yo tout trankilité,\nAvèk sérénité.\n\n\n4.\nOuété nan nou tout fó dézi,\nKi pa fè ou plézi,\nBan nou féchè nou ka santi\nNan ké nou, é palé san brui,\nDous voua lé Sint-Éspri.','<p><strong>1.</strong> </p>\n<p>Ségnè Bon-Dieu ki Papa nou, </p>\n<p>Ki rinmin tout moun tou, </p>\n<p>Padonnin nou tout bagay fou, </p>\n<p>Chanjé vi nou yo tou patou, </p>\n<p>Ran-n nou pi sanblé ou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Édé nou montré gnou la foua, </p>\n<p>Kon moun yo ki ak joua, </p>\n<p>Nan Galilé té suiv la voua </p>\n<p>Jézu, lè-l ofri yo gnou choua, </p>\n<p>La foua o lié la loua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jété sou nou gnou bèl rozé, </p>\n<p>Gnou léspri plin la pè. </p>\n<p>Kalmé nan-m yo ki anouyé, </p>\n<p>É ba yo tout trankilité, </p>\n<p>Avèk sérénité. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ouété nan nou tout fó dézi, </p>\n<p>Ki pa fè ou plézi, </p>\n<p>Ban nou féchè nou ka santi </p>\n<p>Nan ké nou, é palé san brui, </p>\n<p>Dous voua lé Sint-Éspri. </p>\n','Segne-Bon-Dieu-Ki-Papa-Nou',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1788,'86124abd-f5da-4174-b200-19b7bc4a97cc',0,0,'Tout Moun Ki Rété Sou La Tè',31,'31_CE_ht','1.\nTout moun ki rété sou la tè,\nBéni Bon-Dieu, lé Kréatè.\nAlélouya!  Alélouya!\nNon Rédanmtè nou tèlman dou,\nMoun doué chanté li tou patou.\nAlélouya!  Alélouya!  Alélouya!\nAlélouya!  Alélouya!\n\n\n2.\nBonté Jézu duré toujou,\nVérité li pou tou lé jou,\nAlélouya!  Alélouya!\nTout pèp va chanté louanj li,\nJouk tan solèy la fini.\nAlélouya!  Alélouya! Alélouya!\nAlélouya!  Alélouya!','<p><strong>1.</strong> </p>\n<p>Tout moun ki rété sou la tè, </p>\n<p>Béni Bon-Dieu, lé Kréatè. </p>\n<p>Alélouya!  Alélouya! </p>\n<p>Non Rédanmtè nou tèlman dou, </p>\n<p>Moun doué chanté li tou patou. </p>\n<p>Alélouya!  Alélouya!  Alélouya! </p>\n<p>Alélouya!  Alélouya! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bonté Jézu duré toujou, </p>\n<p>Vérité li pou tou lé jou, </p>\n<p>Alélouya!  Alélouya! </p>\n<p>Tout pèp va chanté louanj li, </p>\n<p>Jouk tan solèy la fini. </p>\n<p>Alélouya!  Alélouya! Alélouya! </p>\n<p>Alélouya!  Alélouya! </p>\n','Tout-Moun-Ki-Rete-Sou-La-Te',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1789,'e74101ad-fb76-4b0f-b36e-901c8b84dc5a',0,0,'Nap chante Roua a ki gin pou vini',40,'40_CE_ht','1.\nNap chanté Roua a ki gin pou vini,\nGloua a Jézu, Lagno a ki mouri\nPou ofri tout moun salu ak la vi.\nTout pèp va kontan lè Jézu va Roua!\n\nKè:\nVini chanté: Gloua a Jézu,\nAléloua, gloua a Jézu!!\nNou ki moun pa Jézu, sa sé chan nou:Tout gloua a Jézu, sé li ki Roua nou.\n\n\n2.\nPèp yo va rété nan bèl lumiè li,\nMoun yo va ansan-m ki pa té uni.\nSé la vérité ki va établi.\nTout tò va kaba, lè Jézu va Roua.\n\n\n3.\nTout bagay va bon nan rouayom pa li,\nLa libèté ak sajès va vini.\nTout énmi nou yo va tounin zanmi.\nTout za-m mété ba, lè Jézu va Roua.\n\n\n4.\nTout moun va maché nan puisans Léspri.\nDout pa pou troublé nan-m moun ki nan li.\nNou pa pè lanfè, fòs lan mò fini,\nLamou tou patou, lè Jézu va Roua.\n\n\n5.\nNap la priè pou Rouayom Kris vini.\nBon-Dieu, fèl vini vit pou ou détoui\nTout mové bagay, pou nou kapab di:\nRègn Satan kaba, Sé Jézu ki Roua.','<p><strong>1.</strong> </p>\n<p>Nap chanté Roua a ki gin pou vini, </p>\n<p>Gloua a Jézu, Lagno a ki mouri </p>\n<p>Pou ofri tout moun salu ak la vi. </p>\n<p>Tout pèp va kontan lè Jézu va Roua! </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Vini chanté: Gloua a Jézu,</strong> </p>\n<p><strong>Aléloua, gloua a Jézu!!</strong> </p>\n<p><strong>Nou ki moun pa Jézu, sa sé chan nou:Tout gloua a Jézu, sé li ki Roua nou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pèp yo va rété nan bèl lumiè li, </p>\n<p>Moun yo va ansan-m ki pa té uni. </p>\n<p>Sé la vérité ki va établi. </p>\n<p>Tout tò va kaba, lè Jézu va Roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout bagay va bon nan rouayom pa li, </p>\n<p>La libèté ak sajès va vini. </p>\n<p>Tout énmi nou yo va tounin zanmi. </p>\n<p>Tout za-m mété ba, lè Jézu va Roua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Tout moun va maché nan puisans Léspri. </p>\n<p>Dout pa pou troublé nan-m moun ki nan li. </p>\n<p>Nou pa pè lanfè, fòs lan mò fini, </p>\n<p>Lamou tou patou, lè Jézu va Roua. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Nap la priè pou Rouayom Kris vini. </p>\n<p>Bon-Dieu, fèl vini vit pou ou détoui </p>\n<p>Tout mové bagay, pou nou kapab di: </p>\n<p>Règn Satan kaba, Sé Jézu ki Roua. </p>\n','Nap-chante-Roua-a-ki-gin-pou-vini',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1790,'5240c57b-f1f1-4b6e-ac72-901824566c87',0,0,'Kè mouin trankil, O Bon-Dieu mouin',107,'107_CE_ht','1.\nKè mouin trankil, O Bon-Dieu mouin,\nMouin vlé di Ou mèsi.\nKè mouin rété nan sous la vi,\nKè mouin an pè ak Ou.\nPa gin angnin sou la tè, sòf Ou\nKi kapab satisfè-m.\nLè mouin bouè nan tout sous lé mon-n yo,\nKè mouin gin souaf toujou.\n\n\n2.\nMouin souaf sous yo la vi sélès,\nÉ yo tou pré-m nan Ou.\nMouin bézouin trézò amou Ou,\nÉ li tou pré mouin tou.\nMouin jouin-n gnou nouvo chan nan bouch mouin\nSou pi bèl muzik la,\nGloua a Bon-Dieu pou tout gras li a.\nMouin poko résévoua.\n\n\n3.\nGloua a Bon-Dieu lè fòs manké,\nPou féblès é bézouin,\nPou la krint ki fè-m jouin-n ak Ou,\nPou sa Ou gin pou mouin.\nMouin gin gnou éritaj la joua,\nMin mouin poko kab ouè-L.\nSé min a ki singnin sou la koua\nKap kinbin li pou mouin.\n\n\n4.\nKè mouin trankil, O Bon-Dieu mouin,\nKè mouin sé nan min Ou,\nMouin tandé gnou bèl voua la joua\nKap sonnin tou patou.\nGin anpil moun kap di parol sa:\n«Létènèl sé lo pa-m.»\nMésaj la nan bèl kantik sa\nPapé janmin suspan-n.','<p><strong>1.</strong> </p>\n<p>Kè mouin trankil, O Bon-Dieu mouin, </p>\n<p>Mouin vlé di Ou mèsi. </p>\n<p>Kè mouin rété nan sous la vi, </p>\n<p>Kè mouin an pè ak Ou. </p>\n<p>Pa gin angnin sou la tè, sòf Ou </p>\n<p>Ki kapab satisfè-m. </p>\n<p>Lè mouin bouè nan tout sous lé mon-n yo, </p>\n<p>Kè mouin gin souaf toujou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin souaf sous yo la vi sélès, </p>\n<p>É yo tou pré-m nan Ou. </p>\n<p>Mouin bézouin trézò amou Ou, </p>\n<p>É li tou pré mouin tou. </p>\n<p>Mouin jouin-n gnou nouvo chan nan bouch mouin </p>\n<p>Sou pi bèl muzik la, </p>\n<p>Gloua a Bon-Dieu pou tout gras li a. </p>\n<p>Mouin poko résévoua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gloua a Bon-Dieu lè fòs manké, </p>\n<p>Pou féblès é bézouin, </p>\n<p>Pou la krint ki fè-m jouin-n ak Ou, </p>\n<p>Pou sa Ou gin pou mouin. </p>\n<p>Mouin gin gnou éritaj la joua, </p>\n<p>Min mouin poko kab ouè-L. </p>\n<p>Sé min a ki singnin sou la koua </p>\n<p>Kap kinbin li pou mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kè mouin trankil, O Bon-Dieu mouin, </p>\n<p>Kè mouin sé nan min Ou, </p>\n<p>Mouin tandé gnou bèl voua la joua </p>\n<p>Kap sonnin tou patou. </p>\n<p>Gin anpil moun kap di parol sa: </p>\n<p>«Létènèl sé lo pa-m.» </p>\n<p>Mésaj la nan bèl kantik sa </p>\n<p>Papé janmin suspan-n. </p>\n','Ke-mouin-trankil-O-Bon-Dieu-mouin',NULL,'ht',7,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1791,'fc8c5dd3-d989-41d1-96fc-31600b864af2',0,0,'Mouin Te Vi-n Jouinn',15,'15_MJ_ht','1.\nMouin té vi-n jouin-n\nJézu byin fatigé,\nJézu ban mouin répo  (bis)\nAlèkilé kè mouin vini byin gé,\nJézu ban mouin répo.\n\nKè:\nJézu sé sèl Sovè-m,\nLi padonnin péché-m.\nLi mété gnou chan nan kè mouin.\nMouin kontan, Jézu sé sèl Sovè-m.\nJézu sé sèl Sovè-m.\n\n2.\nFado péché-m\nTé trò lou pou-m pòté;\nJézu soulajé mouin (bis)\nMouin ba Li tout\nFado-m alèkilé;\nJézu soulajé mouin!\n\n3.\nJézu ouété tout kondanasion mouin\nLi padonnin péché-m  (bis)\nÉ koun yé-a sé la pè nan kè mouin\nLi padonnin péché-m.\n\n4.\nVi-n jouin-n Jézu,\nVi-n jouin-n Li jodi-a\nL-a ouété péché ou  (bis)\nL-a fè ou kontan, Li va sové ou;\nL-a ouété péché ou!','<p><strong>1.</strong> </p>\n<p>Mouin té vi-n jouin-n </p>\n<p>Jézu byin fatigé, </p>\n<p>Jézu ban mouin répo  (bis) </p>\n<p>Alèkilé kè mouin vini byin gé, </p>\n<p>Jézu ban mouin répo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sé sèl Sovè-m,</strong> </p>\n<p><strong>Li padonnin péché-m.</strong> </p>\n<p><strong>Li mété gnou chan nan kè mouin.</strong> </p>\n<p><strong>Mouin kontan, Jézu sé sèl Sovè-m.</strong> </p>\n<p><strong>Jézu sé sèl Sovè-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fado péché-m </p>\n<p>Té trò lou pou-m pòté; </p>\n<p>Jézu soulajé mouin (bis) </p>\n<p>Mouin ba Li tout </p>\n<p>Fado-m alèkilé; </p>\n<p>Jézu soulajé mouin! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu ouété tout kondanasion mouin </p>\n<p>Li padonnin péché-m  (bis) </p>\n<p>É koun yé-a sé la pè nan kè mouin </p>\n<p>Li padonnin péché-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vi-n jouin-n Jézu, </p>\n<p>Vi-n jouin-n Li jodi-a </p>\n<p>L-a ouété péché ou  (bis) </p>\n<p>L-a fè ou kontan, Li va sové ou; </p>\n<p>L-a ouété péché ou! </p>\n','Mouin-Te-Vi-n-Jouinn',NULL,'ht',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1792,'a2dc19b0-47ba-457e-8cdc-6a12b6349215',0,0,'Le laboureur retourne',35,'35_VR_fr','1.	\nLe laboureur retourne à son village,\nBien fatigué des durs labeurs du jour.\nSon coeur est gai, joyeux son visage\nLe jour finit, la nuit vient à son tour\nA travers le jour, le soir, la nuit,\nL\'oeil de la foi voit le matin qui luit.\n\nChoeur\nOh! Le soir vient, le soir enfin.\nLa nuit même devient belle,\nCar le matin, le grand matin\nApporte joie éternelle.\n\n2.	\nLe laboureur le coeur plein d\'espérance\nSemant ensuite avec un chant d\'amour!\nDans le passé Dieu donna la croissance\nLa nuit revient mais il attend le jour.\nCar à travers le jour, le soir, la nuit,\nL\'oeil de la foi voit le matin qui luit.\n\n3.	\nPour le chrétien, le jour a tant de charme,\nC\'est bien le temps pour planter et semer\nEt si parfois nous semons avec larmes\nL\'espoir en Dieu nous aide à triompher\nCar à travers le, le soir, la nuit,\nL\'oeil de la foi voit le matin qui luit.\n\n4.	\nPour nous chrétiens si douce est la soirée\nDonnant la paix et l\'assurance au coeur.\nPour nous la nuit est toujours étoilée\nLe matin vient et l\'éternel bonheur\nCar à travers le jour, le soir, la nuit,\nL\'oeil de la foi voit le matin qui luit.\n\n5.	\nC\'est le matin, c\'est le matin du Père\nC\'est le matin, c\'est donc le doux revoir\nC\'est le matin où l\'âme solitaire\nDevient un centre de l\'amour et l\'espoir\nEnfin le jour, le soir, la nuit ont fui,\nC\'est le matin, le grand matin qui luit.\n\nChoeur\nIl luit, enfin le grand matin\nC\'est la maison paternelle\nOui le matin, le grand matin\nApporte joie éternelle.','<p><strong>1.</strong> </p>\n<p>Le laboureur retourne à son village, </p>\n<p>Bien fatigué des durs labeurs du jour. </p>\n<p>Son coeur est gai, joyeux son visage </p>\n<p>Le jour finit, la nuit vient à son tour </p>\n<p>A travers le jour, le soir, la nuit, </p>\n<p>L&#39;oeil de la foi voit le matin qui luit. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Oh! Le soir vient, le soir enfin.</strong> </p>\n<p><strong>La nuit même devient belle,</strong> </p>\n<p><strong>Car le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le laboureur le coeur plein d&#39;espérance </p>\n<p>Semant ensuite avec un chant d&#39;amour! </p>\n<p>Dans le passé Dieu donna la croissance </p>\n<p>La nuit revient mais il attend le jour. </p>\n<p>Car à travers le jour, le soir, la nuit, </p>\n<p>L&#39;oeil de la foi voit le matin qui luit. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour le chrétien, le jour a tant de charme, </p>\n<p>C&#39;est bien le temps pour planter et semer </p>\n<p>Et si parfois nous semons avec larmes </p>\n<p>L&#39;espoir en Dieu nous aide à triompher </p>\n<p>Car à travers le, le soir, la nuit, </p>\n<p>L&#39;oeil de la foi voit le matin qui luit. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour nous chrétiens si douce est la soirée </p>\n<p>Donnant la paix et l&#39;assurance au coeur. </p>\n<p>Pour nous la nuit est toujours étoilée </p>\n<p>Le matin vient et l&#39;éternel bonheur </p>\n<p>Car à travers le jour, le soir, la nuit, </p>\n<p>L&#39;oeil de la foi voit le matin qui luit. </p>\n<p><br/> <strong>5.</strong></p>\n<p>C&#39;est le matin, c&#39;est le matin du Père </p>\n<p>C&#39;est le matin, c&#39;est donc le doux revoir </p>\n<p>C&#39;est le matin où l&#39;âme solitaire </p>\n<p>Devient un centre de l&#39;amour et l&#39;espoir </p>\n<p>Enfin le jour, le soir, la nuit ont fui, </p>\n<p>C&#39;est le matin, le grand matin qui luit. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Il luit, enfin le grand matin</strong> </p>\n<p><strong>C&#39;est la maison paternelle</strong> </p>\n<p><strong>Oui le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle.</strong> </p>\n','Le-laboureur-retourne',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1793,'f48e70c8-dcce-40f5-b90a-ba562f14845d',0,0,'Ma demeure',32,'32_VR_fr','1.	\nDu monde la gloire ainsi que tous les biens\nNe seront jamais ici-bas mon partage,\nParce que Jésus-Christ, le Nazaréen,\nLà-haut me prépare un plus grand héritage.\n\nChoeur\nMa demeure est là où Jésus fait sa demeure;\nLorsque j\'erre à travers un périlleux sentier,\nIl s\'approche, me guide et près de moi demeure,  \nM\'exhortant toujours à veiller, à prier.\n\n2.	\nBien souvent je suis tenté et ballotté\nPar Satan, le grand et terrible adversaire.\nConnaissant du monde l\'instabilité,\nVers mon sûr abri je cours par la prière.\n\n3.	\nQue j\'affronte labeur, fatigue sur mes pas;\nQue mon père, ma mère, mon frère abandonnent\nJe ne puis chanter que «Gloire Alléluia»\nPuisqu\'au ciel m\'attend ma princière couronne.','<p><strong>1.</strong> </p>\n<p>Du monde la gloire ainsi que tous les biens </p>\n<p>Ne seront jamais ici-bas mon partage, </p>\n<p>Parce que Jésus-Christ, le Nazaréen, </p>\n<p>Là-haut me prépare un plus grand héritage. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Ma demeure est là où Jésus fait sa demeure;</strong> </p>\n<p><strong>Lorsque j&#39;erre à travers un périlleux sentier,</strong> </p>\n<p><strong>Il s&#39;approche, me guide et près de moi demeure,</strong> </p>\n<p><strong>M&#39;exhortant toujours à veiller, à prier.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bien souvent je suis tenté et ballotté </p>\n<p>Par Satan, le grand et terrible adversaire. </p>\n<p>Connaissant du monde l&#39;instabilité, </p>\n<p>Vers mon sûr abri je cours par la prière. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que j&#39;affronte labeur, fatigue sur mes pas; </p>\n<p>Que mon père, ma mère, mon frère abandonnent </p>\n<p>Je ne puis chanter que «Gloire Alléluia» </p>\n<p>Puisqu&#39;au ciel m&#39;attend ma princière couronne. </p>\n','Ma-demeure',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1794,'fa21c5ce-6f0a-4c7d-98ca-756d3f5d7ef7',0,0,'Le jour de la moisson',30,'30_VR_fr','1.	\nLe jour de la moisson viendra\nPour tout fils d\'Adam-né,\nEt ce que l\'homme aura semé\nIl le moissonnera.\n\nChoeur\nLa semence d\'ici-bas\nC\'est la moisson d\'en haut\nSelon ce que nos travaux,\nBien ou mal, il viendra,\nLe jour où tu verras\nCette fois, ta moisson croissera.\n\n2.	\nSi tu veux semer pour la chair\nContenant ses passions,\nTa moisson sera corruption\nEt tourment de l\'enfer.\n\n3.	\nPourquoi semerais-tu le mal\nEt la méchanceté?\nQuand tu sèmes pour le péché\nLe sort sera fatal.\n\n4.	\nSi tu sèmes la charité\nPar la foi en Jésus-Christ,\nTa moisson sera le salut\nEt la félicité.\n\n5.	\nCessez de semer pour la chair\nCar tu vas à la mort.\nLe semence assure le sort\nJésus ou Lucifer.','<p><strong>1.</strong> </p>\n<p>Le jour de la moisson viendra </p>\n<p>Pour tout fils d&#39;Adam-né, </p>\n<p>Et ce que l&#39;homme aura semé </p>\n<p>Il le moissonnera. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>La semence d&#39;ici-bas</strong> </p>\n<p><strong>C&#39;est la moisson d&#39;en haut</strong> </p>\n<p><strong>Selon ce que nos travaux,</strong> </p>\n<p><strong>Bien ou mal, il viendra,</strong> </p>\n<p><strong>Le jour où tu verras</strong> </p>\n<p><strong>Cette fois, ta moisson croissera.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tu veux semer pour la chair </p>\n<p>Contenant ses passions, </p>\n<p>Ta moisson sera corruption </p>\n<p>Et tourment de l&#39;enfer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pourquoi semerais-tu le mal </p>\n<p>Et la méchanceté? </p>\n<p>Quand tu sèmes pour le péché </p>\n<p>Le sort sera fatal. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si tu sèmes la charité </p>\n<p>Par la foi en Jésus-Christ, </p>\n<p>Ta moisson sera le salut </p>\n<p>Et la félicité. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Cessez de semer pour la chair </p>\n<p>Car tu vas à la mort. </p>\n<p>Le semence assure le sort </p>\n<p>Jésus ou Lucifer. </p>\n','Le-jour-de-la-moisson',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1795,'111de1de-63f3-4005-addd-75e8d3221a93',0,0,'Priez sans cesse',39,'39_VR_fr','1.	\nPriez toujours, priez sans cesse\nPriez le Seigneur entendra\nIl est fidèle à sa promesse\nPriez, priez, Il répondra.\n\n2.	\nPriez si vos coeur se déchirent\nLa main qui blesse guérira\nIl entend tous ceux qui soupirent\nIl a souffert, Il guérira!\n\n3.	\nPriez dans la fournaise ardente\nQuand Satan gronde autour de vous\nJésus sait calmer la tourmente,\nPriez, triomphez à genoux.\n\n4.	\nPriez, priez lorsque le doute\nObscurcira votre horizon!\nPriez! Jésus connaît la route,\nIl ouvrira votre prison.\n\n5.	\nPriez pour rendre témoignage\nPriez pour confesser Jésus\nDieu bénira votre langage\nPour sauver les pécheurs perdus.\n\n6.	\nPriez quand votre âme hésitante\nNe sais où choisir son chemin\nMême à travers la mer brillante\nJésus vous tiendra par la main.\n\n7.	\nPriez si votre ciel est sombre\nSi vous tremblez devant la mort!\nJésus vous couvrant de son ombre\nVous ouvrira les portes d\'or.','<p><strong>1.</strong> </p>\n<p>Priez toujours, priez sans cesse </p>\n<p>Priez le Seigneur entendra </p>\n<p>Il est fidèle à sa promesse </p>\n<p>Priez, priez, Il répondra. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Priez si vos coeur se déchirent </p>\n<p>La main qui blesse guérira </p>\n<p>Il entend tous ceux qui soupirent </p>\n<p>Il a souffert, Il guérira! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Priez dans la fournaise ardente </p>\n<p>Quand Satan gronde autour de vous </p>\n<p>Jésus sait calmer la tourmente, </p>\n<p>Priez, triomphez à genoux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Priez, priez lorsque le doute </p>\n<p>Obscurcira votre horizon! </p>\n<p>Priez! Jésus connaît la route, </p>\n<p>Il ouvrira votre prison. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Priez pour rendre témoignage </p>\n<p>Priez pour confesser Jésus </p>\n<p>Dieu bénira votre langage </p>\n<p>Pour sauver les pécheurs perdus. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Priez quand votre âme hésitante </p>\n<p>Ne sais où choisir son chemin </p>\n<p>Même à travers la mer brillante </p>\n<p>Jésus vous tiendra par la main. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Priez si votre ciel est sombre </p>\n<p>Si vous tremblez devant la mort! </p>\n<p>Jésus vous couvrant de son ombre </p>\n<p>Vous ouvrira les portes d&#39;or. </p>\n','Priez-sans-cesse',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1796,'2e93147e-958e-4940-a9a0-9ece35d2d08b',0,0,'Kantik Spésial',5,'5_VR_ht','1.	\nLè Jézu rélé-m, lè Jézu rélé-m\nMa répon-n Li, ma répon-n Li.\nLè Li rélé-m, rélé-m  (ter)\nMa répon-n Li.\n\n2.	\nSégnè jézu mouin vlé sèvi Ou\nSégnè Jézu mouin vlé sèvi Ou\nMouin vlé sèvi Ou, sèvi Ou  (ter)\nTout tan nan vi mouin.\n\n3.	\nDépi lontan mouin té pèdi\nPèson-n pat ouè-m bézouin sové\nBézouin sové, sové  (ter)\nAn ba min Satan.\n\n4.	\nGloua a Jézu ki  té jouin-n mouin\nNan gran chémin san souti-in min’m\nLi té jouin-n mouin, jouin-n mouin (ter)\nLè-m té san souti-in. \nAmen.','<p><strong>1.</strong> </p>\n<p>Lè Jézu rélé-m, lè Jézu rélé-m </p>\n<p>Ma répon-n Li, ma répon-n Li. </p>\n<p>Lè Li rélé-m, rélé-m  (ter) </p>\n<p>Ma répon-n Li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ségnè jézu mouin vlé sèvi Ou </p>\n<p>Ségnè Jézu mouin vlé sèvi Ou </p>\n<p>Mouin vlé sèvi Ou, sèvi Ou  (ter) </p>\n<p>Tout tan nan vi mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dépi lontan mouin té pèdi </p>\n<p>Pèson-n pat ouè-m bézouin sové </p>\n<p>Bézouin sové, sové  (ter) </p>\n<p>An ba min Satan. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gloua a Jézu ki  té jouin-n mouin </p>\n<p>Nan gran chémin san souti-in min’m </p>\n<p>Li té jouin-n mouin, jouin-n mouin (ter) </p>\n<p>Lè-m té san souti-in. </p>\n<p>Amen. </p>\n','Kantik-Spesial',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1797,'364dd048-d85e-45ac-835f-fb945407835f',2,0,'Jésus est ressuscité',1,'1_VR_fr','1.	\nPour nos péchés Christ a été brisé\nIl a souffert sur la Croix\nMais Il est Celui qui vit pour toujours\nIl ne meurt qu\'une seule fois.\n \nChoeur\nAlléluia, Alléluia,\nJésus est ressuscité.  (bis)\n\n2.	\nO Mort où est ta victoire?\nOù est ton pouvoir angoissant?\nPour toujours Satan a été dompté\nJésus-Christ est triomphant.\n\n3.	\nDevant le Père Il intercède,\nIl est notre Médiateur,\nBientôt Il viendra nous prendre\nDans la céleste demeure.\n\n4.	\nVenez, donc, chanter ses louanges,\nIl est vainqueur de la mort,\nIl est mort pour sauver les pécheurs\nMais Il est vivant encore.','<p><strong>1.</strong> </p>\n<p>Pour nos péchés Christ a été brisé </p>\n<p>Il a souffert sur la Croix </p>\n<p>Mais Il est Celui qui vit pour toujours </p>\n<p>Il ne meurt qu&#39;une seule fois. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Alléluia, Alléluia,</strong> </p>\n<p><strong>Jésus est ressuscité.  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O Mort où est ta victoire? </p>\n<p>Où est ton pouvoir angoissant? </p>\n<p>Pour toujours Satan a été dompté </p>\n<p>Jésus-Christ est triomphant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Devant le Père Il intercède, </p>\n<p>Il est notre Médiateur, </p>\n<p>Bientôt Il viendra nous prendre </p>\n<p>Dans la céleste demeure. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Venez, donc, chanter ses louanges, </p>\n<p>Il est vainqueur de la mort, </p>\n<p>Il est mort pour sauver les pécheurs </p>\n<p>Mais Il est vivant encore. </p>\n','Jesus-est-ressuscite',NULL,'fr',2,'2023-07-22 22:41:15','2023-10-03 20:24:55'),(1798,'d915d5c0-4e74-4d92-817e-065c49206833',0,0,'Minuit chrétiens',27,'27_VR_fr','1.	\nMinuit, chrétiens, c\'est l\'heure solennelle\nOù l\'homme Dieu descendit jusqu\'à nous\nPour effacer la tâche originelle\nEt de son Père arrêter le courroux.\nLe monde entier tressaille d\'espérance,\nA cette nuit qui lui donne un Sauveur;\nPeuple, à genoux! Attends ta délivrance,\nNoël, Noël! Voici le Rédempteur,\nNoël, Noël! Voici le Rédempteur.\n\n2.	\nDe notre foi que la lumière ardente\nNous guide tous au berceau de l\'Enfant\nComme autre fois une étoile brillante\nY conduisit les chefs de l\'Orient.\nLe Roi des rois naît la dans une humble crèche.\nPuissants du jour, fiers de votre grandeur,\nA votre orgueil c\'est de là qu\'un Dieu prêche\nCourbez vos fronts devant le Rédempteur.\n\n3.	\nLe Rédempteur a brisé toute entrave;\nLa terre est libre et le ciel est ouvert.\nIl voit un frère où n\'était qu\'un esclave,\nL\'amour unit ceux qu\'enchaînait le fer.\nQui leur dira notre reconnaissance?\nC\'est pour nous tous qu\'Il naît,\nQu\'Il souffre et meurt;\nPeuple debout! Chante ta délivrance,\nNoël, Noël! Chante le Rédempteur,\nNoël, Noël! Chante le Rédempteur!','<p><strong>1.</strong> </p>\n<p>Minuit, chrétiens, c&#39;est l&#39;heure solennelle </p>\n<p>Où l&#39;homme Dieu descendit jusqu&#39;à nous </p>\n<p>Pour effacer la tâche originelle </p>\n<p>Et de son Père arrêter le courroux. </p>\n<p>Le monde entier tressaille d&#39;espérance, </p>\n<p>A cette nuit qui lui donne un Sauveur; </p>\n<p>Peuple, à genoux! Attends ta délivrance, </p>\n<p>Noël, Noël! Voici le Rédempteur, </p>\n<p>Noël, Noël! Voici le Rédempteur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>De notre foi que la lumière ardente </p>\n<p>Nous guide tous au berceau de l&#39;Enfant </p>\n<p>Comme autre fois une étoile brillante </p>\n<p>Y conduisit les chefs de l&#39;Orient. </p>\n<p>Le Roi des rois naît la dans une humble crèche. </p>\n<p>Puissants du jour, fiers de votre grandeur, </p>\n<p>A votre orgueil c&#39;est de là qu&#39;un Dieu prêche </p>\n<p>Courbez vos fronts devant le Rédempteur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Rédempteur a brisé toute entrave; </p>\n<p>La terre est libre et le ciel est ouvert. </p>\n<p>Il voit un frère où n&#39;était qu&#39;un esclave, </p>\n<p>L&#39;amour unit ceux qu&#39;enchaînait le fer. </p>\n<p>Qui leur dira notre reconnaissance? </p>\n<p>C&#39;est pour nous tous qu&#39;Il naît, </p>\n<p>Qu&#39;Il souffre et meurt; </p>\n<p>Peuple debout! Chante ta délivrance, </p>\n<p>Noël, Noël! Chante le Rédempteur, </p>\n<p>Noël, Noël! Chante le Rédempteur! </p>\n','Minuit-chretiens',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1799,'43ead640-676c-4fb1-8b30-5ac15b478b59',0,0,'Je chanterai ton grand amour',21,'21_VR_fr','1.	\nJe chanterai ton grand amour,\nL\'hiver, l\'été, la nuit, le jour,\nCar cet amour est éternel,\nSi grand et pourtant si réel.\n\nChoeur\nToujours, oh oui toujours  (bis)\nJe chanterai ton grand amour  (bis 2 lignes)\n\n2.	\nJe chanterai dès le matin\nQuand tout est clair sur mon chemin\nJe veux aussi chanter le soir,\nQuand la nuit vient et tout est noir.\n\n3.	\nOh! Chantons donc jusqu\'au grand jour\nOù réunis dans ton amour,\nGrand Dieu, nous verrons tes splendeurs\nEt nous dirons de tous nos coeurs','<p><strong>1.</strong> </p>\n<p>Je chanterai ton grand amour, </p>\n<p>L&#39;hiver, l&#39;été, la nuit, le jour, </p>\n<p>Car cet amour est éternel, </p>\n<p>Si grand et pourtant si réel. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Toujours, oh oui toujours  (bis)</strong> </p>\n<p><strong>Je chanterai ton grand amour  (bis 2 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je chanterai dès le matin </p>\n<p>Quand tout est clair sur mon chemin </p>\n<p>Je veux aussi chanter le soir, </p>\n<p>Quand la nuit vient et tout est noir. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh! Chantons donc jusqu&#39;au grand jour </p>\n<p>Où réunis dans ton amour, </p>\n<p>Grand Dieu, nous verrons tes splendeurs </p>\n<p>Et nous dirons de tous nos coeurs </p>\n','Je-chanterai-ton-grand-amour',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1800,'174a2f2d-4421-4fc2-94a5-045e8179773c',0,0,'Seigneur que n’ai-je',8,'8_VR_fr','1.	\nSeigneur que n\'ai-je mille voix\nPour chanter tes louanges  (bis)\nEt faire monter jusqu\'aux anges\nLes gloires de ta croix.  (bis)\n\n2.	\nJésus, mon Seigneur et mon Dieu\nQue ton souffle m\'aime  (bis)\nPour que par moi ton Nom sublime\nRetentisse en tout lieu.  (bis)\n\n3.	\nDoux Nom qui fait tarir nos pleurs\nIneffable harmonie  (bis)\nTu répands ta joie et ta vie\nEt la paix dans nos coeurs.  (bis)\n\n4.	\nDésormais que n\'ai-je plus d\'effroi\nAucun mal ne m\'accable  (bis)\nTon sang rend pur le plus coupable\nTon sang coula pour moi.  (bis)','<p><strong>1.</strong> </p>\n<p>Seigneur que n&#39;ai-je mille voix </p>\n<p>Pour chanter tes louanges  (bis) </p>\n<p>Et faire monter jusqu&#39;aux anges </p>\n<p>Les gloires de ta croix.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, mon Seigneur et mon Dieu </p>\n<p>Que ton souffle m&#39;aime  (bis) </p>\n<p>Pour que par moi ton Nom sublime </p>\n<p>Retentisse en tout lieu.  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Doux Nom qui fait tarir nos pleurs </p>\n<p>Ineffable harmonie  (bis) </p>\n<p>Tu répands ta joie et ta vie </p>\n<p>Et la paix dans nos coeurs.  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Désormais que n&#39;ai-je plus d&#39;effroi </p>\n<p>Aucun mal ne m&#39;accable  (bis) </p>\n<p>Ton sang rend pur le plus coupable </p>\n<p>Ton sang coula pour moi.  (bis) </p>\n','Seigneur-que-n\'ai-je',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1801,'2cc6aa23-6b0a-4215-a9d0-cd7c99cd687b',0,0,'Un parfait séjour',33,'33_VR_fr','1.	\nUn parfait séjour de gloire et de lumière\nT\'es préparé, mon âme: c\'est dans le ciel,\nLà-haut, tout effroi, toute douleur amère\nSe changeront en bonheur saint, éternel.\n\nChoeur\nJe vais au paradis de gloire,\nAu pays de parfait bonheur; \nLà tout est douceur,\nAlléluia! Gloire à mon Sauveur.\n\n2.   \nDans ce beau pays, plus de mort, mais la vie\nPlus de péché, car là tout est pureté,\nL\'Enfant de Dieu c\'est l\'unique patrie\nLà sont comblés tous nos voeux de sainteté.\n\n3.   \nJe retrouverai dans la gloire immortelle\nCeux qu\'ici-bas j\'ai si tendrement aimés,\nEt tous, couronnés d\'allégresse éternelle\nNous te louerons, Seigneur Jésus, pour jamais.\n\n4.	\nPourquoi donc, ô Dieu tant de bonheur suprême\nDe paix et d\'amour dans ce céleste lieu\nC\'est que sur la croix Jésus s\'offrit Lui-même\nPour me donner le beau paradis de Dieu.\n\n5.	\nLe pécheur lavé dans le sang du Calvaire\nIci-bas même connais déjà le ciel\nGloire à Toi, Jésus, mon Sauveur et mon Frère\nThème parfait des cantiques éternels.','<p><strong>1.</strong> </p>\n<p>Un parfait séjour de gloire et de lumière </p>\n<p>T&#39;es préparé, mon âme: c&#39;est dans le ciel, </p>\n<p>Là-haut, tout effroi, toute douleur amère </p>\n<p>Se changeront en bonheur saint, éternel. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Je vais au paradis de gloire,</strong> </p>\n<p><strong>Au pays de parfait bonheur;</strong> </p>\n<p><strong>Là tout est douceur,</strong> </p>\n<p><strong>Alléluia! Gloire à mon Sauveur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans ce beau pays, plus de mort, mais la vie </p>\n<p>Plus de péché, car là tout est pureté, </p>\n<p>L&#39;Enfant de Dieu c&#39;est l&#39;unique patrie </p>\n<p>Là sont comblés tous nos voeux de sainteté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je retrouverai dans la gloire immortelle </p>\n<p>Ceux qu&#39;ici-bas j&#39;ai si tendrement aimés, </p>\n<p>Et tous, couronnés d&#39;allégresse éternelle </p>\n<p>Nous te louerons, Seigneur Jésus, pour jamais. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pourquoi donc, ô Dieu tant de bonheur suprême </p>\n<p>De paix et d&#39;amour dans ce céleste lieu </p>\n<p>C&#39;est que sur la croix Jésus s&#39;offrit Lui-même </p>\n<p>Pour me donner le beau paradis de Dieu. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Le pécheur lavé dans le sang du Calvaire </p>\n<p>Ici-bas même connais déjà le ciel </p>\n<p>Gloire à Toi, Jésus, mon Sauveur et mon Frère </p>\n<p>Thème parfait des cantiques éternels. </p>\n','Un-parfait-sejour',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1802,'d60f9bc5-76c8-4e9a-8b9f-ad2a960ea612',0,0,'Gnou vi pou Jézu',4,'4_VR_ht','1.	\nGnou vi pou Jézu bay la pè\nKon gnou riviè ki pa rété\nDifikulté yo kon-n vini\nÉ Jézu la é sa  sufi.\n\nKè:\nÉdé-m sèvi Ou plus chak jou\nPou-m rété toujou avèk Ou\nM-rinmin Ou Kris plus ké janmin\nTanpri pa kité-m égaré.\n\n2.	\nGnou vi pou Jézu bay répo\nLi ouété nan mouin sa ki fo\nParòl li sé la vérité\nLa gidé-m pou létènité.\n\n3.	\nGnou vi pou Jézu bay la joua\nDèské-m sové pa la foua\nZanmi yo mèt abandon-nin’m\nJézu fidèl, Li va kinbé-m.\n\n4.	\nGnou vi pou Jézu bay viktoua\nAk fòs li m-maché pa-za-pa\nKris té vink diab, lé mon, la chè\nKounié ya mouin plus ké vinkè.','<p><strong>1.</strong> </p>\n<p>Gnou vi pou Jézu bay la pè </p>\n<p>Kon gnou riviè ki pa rété </p>\n<p>Difikulté yo kon-n vini </p>\n<p>É Jézu la é sa  sufi. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Édé-m sèvi Ou plus chak jou</strong> </p>\n<p><strong>Pou-m rété toujou avèk Ou</strong> </p>\n<p><strong>M-rinmin Ou Kris plus ké janmin</strong> </p>\n<p><strong>Tanpri pa kité-m égaré.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou vi pou Jézu bay répo </p>\n<p>Li ouété nan mouin sa ki fo </p>\n<p>Paròl li sé la vérité </p>\n<p>La gidé-m pou létènité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou vi pou Jézu bay la joua </p>\n<p>Dèské-m sové pa la foua </p>\n<p>Zanmi yo mèt abandon-nin’m </p>\n<p>Jézu fidèl, Li va kinbé-m. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou vi pou Jézu bay viktoua </p>\n<p>Ak fòs li m-maché pa-za-pa </p>\n<p>Kris té vink diab, lé mon, la chè </p>\n<p>Kounié ya mouin plus ké vinkè. </p>\n','Gnou-vi-pou-Jezu',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1803,'d1ba5cc1-243f-4cf4-a613-c0378c24af65',0,0,'Jézu pa louin',48,'48_EE_ht','1.\nJézu pa louin pou Li vini\nSa ouap tan-n pou ou préparé\nÈské Jézu pa té di ou\nLap vini tankou gnou vòlè\nSi ou té konnin lè L-ap vini\nOu ta lonjé pié ou jan ou vlé\nAprè sa nou ta préparé pou-n tan-n\nJézu O, O, O, O.\n\nKè:\nPuiské nou pa konnin ki lè\nBon Sovè-a pral rétounin  (bis)\nAn nou jété tout vié péché\nAn nou lagé tout vié plézi-I\nAn nou prié, an nou véyé, Kris pral vini.\n\n2.\nGnou nasion va kont gnou nasion\nGnou rouayòm va kont gnou rouayòm\nMin-m légliz va kont lòt légliz\nMan-man, papa va kont pitit\nLè na ouè tout bagay sa yo\nKonnin Jézu prèt pou vini\nAn nou prié, an nou véyé,\nKris pral vini, O, O, O, O.\n\n3.\nYo va di min Li nan mòn nan\nYo va di min-L nan valé-a\nYo va di min Li nan lé Nò\nYo va di min Li nan lé Sud\nMin-m si yo fè zétoual désan-n\nMin-m si yo fè difé so-t nan sièl\nPa fè a douat, pa fè a goch\nAn-n résté fèm, O, O, O, O.','<p><strong>1.</strong> </p>\n<p>Jézu pa louin pou Li vini </p>\n<p>Sa ouap tan-n pou ou préparé </p>\n<p>Èské Jézu pa té di ou </p>\n<p>Lap vini tankou gnou vòlè </p>\n<p>Si ou té konnin lè L-ap vini </p>\n<p>Ou ta lonjé pié ou jan ou vlé </p>\n<p>Aprè sa nou ta préparé pou-n tan-n </p>\n<p>Jézu O, O, O, O. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Puiské nou pa konnin ki lè</strong> </p>\n<p><strong>Bon Sovè-a pral rétounin  (bis)</strong> </p>\n<p><strong>An nou jété tout vié péché</strong> </p>\n<p><strong>An nou lagé tout vié plézi-I</strong> </p>\n<p><strong>An nou prié, an nou véyé, Kris pral vini.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou nasion va kont gnou nasion </p>\n<p>Gnou rouayòm va kont gnou rouayòm </p>\n<p>Min-m légliz va kont lòt légliz </p>\n<p>Man-man, papa va kont pitit </p>\n<p>Lè na ouè tout bagay sa yo </p>\n<p>Konnin Jézu prèt pou vini </p>\n<p>An nou prié, an nou véyé, </p>\n<p>Kris pral vini, O, O, O, O. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Yo va di min Li nan mòn nan </p>\n<p>Yo va di min-L nan valé-a </p>\n<p>Yo va di min Li nan lé Nò </p>\n<p>Yo va di min Li nan lé Sud </p>\n<p>Min-m si yo fè zétoual désan-n </p>\n<p>Min-m si yo fè difé so-t nan sièl </p>\n<p>Pa fè a douat, pa fè a goch </p>\n<p>An-n résté fèm, O, O, O, O. </p>\n','Jezu-pa-louin',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1804,'82e2e398-fab1-484f-9ecc-777478110922',0,0,'A rébò gnou chémin',83,'83_EE_ht','1.\nA rébò gnou chémin ki minnin Jériko;\nJézu té rankontré gnou mandian\nGnou vié rad sou li tou dé jél té fèmin,\nÉ li tap mandé charité.\n\nRéfrin:\nJézu té di: «Ou mèt alé! » (bis)\nDépi min-m lè-a li ouè.\n\n2.\nLè ou gadé nan Jan chapit 8, vèsè 12\nJézu di sé limin-m ki limiè,\nMoun ki suiv li pap jan-m maché\nNan noua ankò.\nMin li va gin limiè é la vi.\n\n3.\nParòl mouin sé gnou lanp \nNan pié ou si ou suiv mouin\nGnou limiè nan kè nou pou tout tan\nMoun ki suiv mouin \nPap jan-m maché nan fènoua ankò\nMin li va limiè é la vi.','<p><strong>1.</strong> </p>\n<p>A rébò gnou chémin ki minnin Jériko; </p>\n<p>Jézu té rankontré gnou mandian </p>\n<p>Gnou vié rad sou li tou dé jél té fèmin, </p>\n<p>É li tap mandé charité. </p>\n<p><br/> <strong>Réfrin:</strong></p>\n<p><strong>Jézu té di: «Ou mèt alé! » (bis)</strong> </p>\n<p><strong>Dépi min-m lè-a li ouè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou gadé nan Jan chapit 8, vèsè 12 </p>\n<p>Jézu di sé limin-m ki limiè, </p>\n<p>Moun ki suiv li pap jan-m maché </p>\n<p>Nan noua ankò. </p>\n<p>Min li va gin limiè é la vi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Paròl mouin sé gnou lanp </p>\n<p>Nan pié ou si ou suiv mouin </p>\n<p>Gnou limiè nan kè nou pou tout tan </p>\n<p>Moun ki suiv mouin </p>\n<p>Pap jan-m maché nan fènoua ankò </p>\n<p>Min li va limiè é la vi. </p>\n','A-rebo-gnou-chemin',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1805,'a45d5056-c871-40dd-ab10-b9fa9f770d63',0,0,'C\'est le Christ qui nous appelle',6,'6_MJ_fr','1.\nC\'est le Christ qui nous appelle:\n«Levez-vous, n\'hésitez plus!\nIl faut la Bonne Nouvelle\nPorter aux hommes perdus.»\nFormel est l\'ordre du Maître –\n«Partout sur la terre allez»,\nL\'Évangile est pou tout être –\nLa tache est inachevé.','<p><strong>1.</strong> </p>\n<p>C&#39;est le Christ qui nous appelle: </p>\n<p>«Levez-vous, n&#39;hésitez plus! </p>\n<p>Il faut la Bonne Nouvelle </p>\n<p>Porter aux hommes perdus.» </p>\n<p>Formel est l&#39;ordre du Maître – </p>\n<p>«Partout sur la terre allez», </p>\n<p>L&#39;Évangile est pou tout être – </p>\n<p>La tache est inachevé. </p>\n','C\'est-le-Christ-qui-nous-appelle',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1806,'86a3d600-5b2e-4046-a2d8-31d19432bff6',0,0,'Écoutez, frères',38,'38_MJ_fr','1.\nÉcoutez, frères,\nJésus vous appelle,\nLe temps est court,\nLa tâche inachevée;\nRéveillez-vous,\nProclamez son message,\nCherchez les âmes –\nIl veut les sauver.\n\nChoeur:\nEn avant, frères,\nSans plus tarder,\nMenons à bonne fin\nLa tâche inachevée.\n\n2.\nVoici des milliers\nD\'âmes qui périssent\nCaptives dans\nLes chaînes du péché,\nSans le message\nD\'amour et de grâce\nEn Jésus-Christ\nQui veut les libérer.\n\n3.\nProclamons à\nToute peuple, toute nation\nQue Jésus-Christ\nEst mort, ressuscité:\nAu gré de notre Maître\nPour accomplir\nLa tâche inachevée.','<p><strong>1.</strong> </p>\n<p>Écoutez, frères, </p>\n<p>Jésus vous appelle, </p>\n<p>Le temps est court, </p>\n<p>La tâche inachevée; </p>\n<p>Réveillez-vous, </p>\n<p>Proclamez son message, </p>\n<p>Cherchez les âmes – </p>\n<p>Il veut les sauver. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>En avant, frères,</strong> </p>\n<p><strong>Sans plus tarder,</strong> </p>\n<p><strong>Menons à bonne fin</strong> </p>\n<p><strong>La tâche inachevée.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voici des milliers </p>\n<p>D&#39;âmes qui périssent </p>\n<p>Captives dans </p>\n<p>Les chaînes du péché, </p>\n<p>Sans le message </p>\n<p>D&#39;amour et de grâce </p>\n<p>En Jésus-Christ </p>\n<p>Qui veut les libérer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Proclamons à </p>\n<p>Toute peuple, toute nation </p>\n<p>Que Jésus-Christ </p>\n<p>Est mort, ressuscité: </p>\n<p>Au gré de notre Maître </p>\n<p>Pour accomplir </p>\n<p>La tâche inachevée. </p>\n','Ecoutez-freres',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1807,'3bd51630-c298-4882-bbf9-18dbf1abd43b',0,0,'Seigneur à tes pieds',2,'2_VR_fr','1.	\nSeigneur à Tes pieds me voici\nTremblant, humilié.\nJe suis rouge d\'un noir souci\nPitié Seigneur, Seigneur pitié.\n\n2.	\nVeuille abaisser tes yeux sur moi\nJe pleure mon péché\nHélas j\'ai méprisé ta loi\nPitié Seigneur, pitié.\n\n3.	\nDevant ta justice O Seigneur\nJe me sens accablé\nTout m\'accuse et surtout mon coeur\nPitié Seigneur, pitié.\n\n4.	\nL\'enfer m\'épouvante grand Dieu\nCar je l\'ai mérité\nSatan veut m\'avoir en son lieu\nPitié Seigneur, pitié.\n\n5.	\nO ciel, O fortune séjour\nMe serais-je fermé\nPardonne au nom de ton amour\nPitié Seigneur, pitié.\n\nAmen,  Amen,  Amen.','<p><strong>1.</strong> </p>\n<p>Seigneur à Tes pieds me voici </p>\n<p>Tremblant, humilié. </p>\n<p>Je suis rouge d&#39;un noir souci </p>\n<p>Pitié Seigneur, Seigneur pitié. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Veuille abaisser tes yeux sur moi </p>\n<p>Je pleure mon péché </p>\n<p>Hélas j&#39;ai méprisé ta loi </p>\n<p>Pitié Seigneur, pitié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Devant ta justice O Seigneur </p>\n<p>Je me sens accablé </p>\n<p>Tout m&#39;accuse et surtout mon coeur </p>\n<p>Pitié Seigneur, pitié. </p>\n<p><br/> <strong>4.</strong></p>\n<p>L&#39;enfer m&#39;épouvante grand Dieu </p>\n<p>Car je l&#39;ai mérité </p>\n<p>Satan veut m&#39;avoir en son lieu </p>\n<p>Pitié Seigneur, pitié. </p>\n<p><br/> <strong>5.</strong></p>\n<p>O ciel, O fortune séjour </p>\n<p>Me serais-je fermé </p>\n<p>Pardonne au nom de ton amour </p>\n<p>Pitié Seigneur, pitié. </p>\n<p>Amen,  Amen,  Amen. </p>\n','Seigneur-a-tes-pieds',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1808,'18fbc2cc-d5eb-4d0d-86de-b308801fa999',0,0,'Semons dès que brille',11,'11_VR_fr','1.	\nSemons dès que brille l\'aurore,\nSemons dès que le soleil luit,\nPendant le jour semons encore\nSemons avant la sombre nuit.\nSemons: Dieu seul peut faire éclore:\nDe lui seul attendons le fruit.\n\nChoeur\nAh! Répandons la divine semence,\nDans le succès comme dans le mépris\nLe jour s\'élève et la moisson avance\nEt Dieu là-haut nous réserve le prix.\n\n2.	\nSemons sur le bord de la route,\nSemons sur le terrain pierreux\nSemons dans le coeur ou le doute\nSemble étouffer la voix des cieux\nA toute âme qui écoute\nPartons du Sauveur glorieux.\n\n3.	\nSemons lorsque dans sa tristesse,\nNotre coeur murmure: A quoi bon?\nRépétons à tous la promesse\nEt l\'Évangile du pardon.\nSemons: bientôt douce allégresse.\nLuira le jour de la moisson.','<p><strong>1.</strong> </p>\n<p>Semons dès que brille l&#39;aurore, </p>\n<p>Semons dès que le soleil luit, </p>\n<p>Pendant le jour semons encore </p>\n<p>Semons avant la sombre nuit. </p>\n<p>Semons: Dieu seul peut faire éclore: </p>\n<p>De lui seul attendons le fruit. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Ah! Répandons la divine semence,</strong> </p>\n<p><strong>Dans le succès comme dans le mépris</strong> </p>\n<p><strong>Le jour s&#39;élève et la moisson avance</strong> </p>\n<p><strong>Et Dieu là-haut nous réserve le prix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Semons sur le bord de la route, </p>\n<p>Semons sur le terrain pierreux </p>\n<p>Semons dans le coeur ou le doute </p>\n<p>Semble étouffer la voix des cieux </p>\n<p>A toute âme qui écoute </p>\n<p>Partons du Sauveur glorieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Semons lorsque dans sa tristesse, </p>\n<p>Notre coeur murmure: A quoi bon? </p>\n<p>Répétons à tous la promesse </p>\n<p>Et l&#39;Évangile du pardon. </p>\n<p>Semons: bientôt douce allégresse. </p>\n<p>Luira le jour de la moisson. </p>\n','Semons-des-que-brille',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1809,'43d56bf1-d850-4388-8607-67ed403d1567',0,0,'Abondans la vi',14,'14_VR_ht','1.	\nNan Kris é nan Li sèl, n-a jouin-n vrè la vi\nSé gras a Li, vié natu a mouri,\nLi rachté mouin nèt, ni kò, nan-m, ni léspri\nMap viv, min pa mouin-min’m, sé Jézu-Kri.\n\nKè:\nAbondans la vi, mapé jouin-n nan Li,\nL-a transfòmé mouin, pou-m sèvi Li byin,\nSan Li té rachté mouin;\nLi ban mouin Léspri-Sin,\nSé nan Jézu gin abaondans la vi.\n\n2.	\nSé Sint-Éspri ki fè-m aksépté Jézu,\nLi fè-m konfié nan promès Bon-Dieu tou.\nDé gloua an gloua Lap chanjé-m nan imaj Li\nAmou Li dévlopé pi plus chak jou.\n\n3.	\nDlo a ki nan sous la vi ap débòdé,\nÉ sé li ki pòté bénédiksion.\nSé la vi an abondans L-ap transpòté,\nLi bay lamou, la joua, pè ak padon.','<p><strong>1.</strong> </p>\n<p>Nan Kris é nan Li sèl, n-a jouin-n vrè la vi </p>\n<p>Sé gras a Li, vié natu a mouri, </p>\n<p>Li rachté mouin nèt, ni kò, nan-m, ni léspri </p>\n<p>Map viv, min pa mouin-min’m, sé Jézu-Kri. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Abondans la vi, mapé jouin-n nan Li,</strong> </p>\n<p><strong>L-a transfòmé mouin, pou-m sèvi Li byin,</strong> </p>\n<p><strong>San Li té rachté mouin;</strong> </p>\n<p><strong>Li ban mouin Léspri-Sin,</strong> </p>\n<p><strong>Sé nan Jézu gin abaondans la vi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé Sint-Éspri ki fè-m aksépté Jézu, </p>\n<p>Li fè-m konfié nan promès Bon-Dieu tou. </p>\n<p>Dé gloua an gloua Lap chanjé-m nan imaj Li </p>\n<p>Amou Li dévlopé pi plus chak jou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dlo a ki nan sous la vi ap débòdé, </p>\n<p>É sé li ki pòté bénédiksion. </p>\n<p>Sé la vi an abondans L-ap transpòté, </p>\n<p>Li bay lamou, la joua, pè ak padon. </p>\n','Abondans-la-vi',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1810,'6d732f08-2f57-412c-ab93-eb70c839e5ae',0,0,'Gingnin dé monman',9,'9_EE_ht','1.\nGingnin dé moman nan la vi\nOu santi fèb é dékourajé\nLè la kay blanch san manjé\n Ou doué plus pasé très chévé ou\nVoizinaj ap pèsékuté ou\nLan nuit kou jounin yap jouré ou\nOu santi ou dézéspéré\nSan pèson-n ki pou konsolé ou.\n\nKè:\nSovè-a di nan paròl Li, \nPoté tout chaj ou yo ba Li\nLapé tan-n ou, dé bra-L ouvri,\nSé pou ou-L té mouri.\n\n2.\nSouvan maladi vi-n tonbé\nTristès, la pin-n vi-n ogmanté\nOu nan travay yo révoké ou\nTout zanmi ou yo abandoné ou\nNan nuit kou jounnin dlo nan jé ou\nOuap mandé sa ou fè ouap péyé\nOu vlé mouri lanmò pa vlé ou\nVi-n jouin-n Jézu L-a délivré ou.\n\n3.\nGnou krétyin gin dé lè-k rivé\nNou santi-n vrèman boulvèsé\nLè tanpèt fi-n débòdé\nVan ap vanté, loraj grondé\nLè bak nou prèt pou-l chaviré\nNou tonbé sou jnou nou prié.\n\n4.\nSovè-a di-L tou prè\nVi-n séché jé-n é konsolé-n\nFrè-m, sè-m pa lésé ou dékourajé\nLè tout bagay pa jan ou ta vlé\nSonjé ké Job té éprouvé, aprè-l té délivré.','<p><strong>1.</strong> </p>\n<p>Gingnin dé moman nan la vi </p>\n<p>Ou santi fèb é dékourajé </p>\n<p>Lè la kay blanch san manjé </p>\n<p>Ou doué plus pasé très chévé ou </p>\n<p>Voizinaj ap pèsékuté ou </p>\n<p>Lan nuit kou jounin yap jouré ou </p>\n<p>Ou santi ou dézéspéré </p>\n<p>San pèson-n ki pou konsolé ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sovè-a di nan paròl Li,</strong> </p>\n<p><strong>Poté tout chaj ou yo ba Li</strong> </p>\n<p><strong>Lapé tan-n ou, dé bra-L ouvri,</strong> </p>\n<p><strong>Sé pou ou-L té mouri.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan maladi vi-n tonbé </p>\n<p>Tristès, la pin-n vi-n ogmanté </p>\n<p>Ou nan travay yo révoké ou </p>\n<p>Tout zanmi ou yo abandoné ou </p>\n<p>Nan nuit kou jounnin dlo nan jé ou </p>\n<p>Ouap mandé sa ou fè ouap péyé </p>\n<p>Ou vlé mouri lanmò pa vlé ou </p>\n<p>Vi-n jouin-n Jézu L-a délivré ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou krétyin gin dé lè-k rivé </p>\n<p>Nou santi-n vrèman boulvèsé </p>\n<p>Lè tanpèt fi-n débòdé </p>\n<p>Van ap vanté, loraj grondé </p>\n<p>Lè bak nou prèt pou-l chaviré </p>\n<p>Nou tonbé sou jnou nou prié. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sovè-a di-L tou prè </p>\n<p>Vi-n séché jé-n é konsolé-n </p>\n<p>Frè-m, sè-m pa lésé ou dékourajé </p>\n<p>Lè tout bagay pa jan ou ta vlé </p>\n<p>Sonjé ké Job té éprouvé, aprè-l té délivré. </p>\n','Gingnin-de-monman',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1811,'3689aa8c-d956-4c2c-b826-7b2f3629f873',0,0,'Lè-m nan problèm yo',19,'19_EE_ht','1.\nLè-m nan problèm yo map chanté pou Kris\nLè-m nan doulè yo map chanté pou Li\nLè-m nan tristès yo mouin rélé Jézu\nKa Li tou édé-m.\n\nKè:\nOui Kris toujou la, pou Li tandé mouin\nPou Li édé mouin, \nÉ pou-L ban mouin la vi.    (bis)\n\n2.\nNan mitan la nuit mouin pa jan-m tou sèl\nNinpòt koté mouin yé, Li toujou la avè-m,\nAla gnou bon Papa ki rinmin-m tout bon\nMouin vlé soufri pou Li.\n\n3.\nOu min-m zanmi mouin vini jouin-n Jézu\nLi gingnin répo pou kè fatigé\nSi Li kinbé min ou, ou pap jan-m tonbé,\nOu va gingnin la vi.','<p><strong>1.</strong> </p>\n<p>Lè-m nan problèm yo map chanté pou Kris </p>\n<p>Lè-m nan doulè yo map chanté pou Li </p>\n<p>Lè-m nan tristès yo mouin rélé Jézu </p>\n<p>Ka Li tou édé-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui Kris toujou la, pou Li tandé mouin</strong> </p>\n<p><strong>Pou Li édé mouin,</strong> </p>\n<p><strong>É pou-L ban mouin la vi.    (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan mitan la nuit mouin pa jan-m tou sèl </p>\n<p>Ninpòt koté mouin yé, Li toujou la avè-m, </p>\n<p>Ala gnou bon Papa ki rinmin-m tout bon </p>\n<p>Mouin vlé soufri pou Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou min-m zanmi mouin vini jouin-n Jézu </p>\n<p>Li gingnin répo pou kè fatigé </p>\n<p>Si Li kinbé min ou, ou pap jan-m tonbé, </p>\n<p>Ou va gingnin la vi. </p>\n','Le-m-nan-problem-yo',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1812,'9746d478-1190-4916-97c0-abda9c5e0794',2,0,'Je me rappelle un jour, où dans le ciel je vis',25,'25_EE_fr','1.\nJe me rappelle un jour, où dans le ciel je vis\nLe livre dans lequel, tout péché est écrit\nJe vis mon nom très clair\nJe dis mon Dieu que faire?\nEn tombant à ses pieds, je réglai mon affaire.\n\nChoeur\nA l\'instant,  (bis)\nOui le vieux compte fut réglé a l\'instant,\nJe ne suis plus effrayé, \nMa dette est déjà payée\nEt mon compte avec mon Juge est tout réglé.\n\n2.\nMon compte était très lourd\nEt grandissant toujours\nO j\'étais égaré, pêchant plus chaque jour.\nMais quand mes yeux s\'ouvrent\nEt je vis mon état,\nJe me jetais aux pieds du Sauveur a l\'instant.\n\n3.\nAu jour du jugement, devant le trône blanc,\nQuand tous les morts viendront\nLes livres s\'ouvriront\nEt quand on cherchera, mon nom n\'y sera pas\nCar Jésus l\'a écrit, au livre de la vie.\n\n4.\nO pêcheur viens à Lui\nMets-toi en règle aussi\nEt fais-le aujourd\'hui, car l\'occasion s\'enfuit\nIl te pardonnera, si tu fais comme moi\nEn venant à ton Sauveur\nTu fais de Lui ton Roi, a l\'instant.','<p><strong>1.</strong> </p>\n<p>Je me rappelle un jour, où dans le ciel je vis </p>\n<p>Le livre dans lequel, tout péché est écrit </p>\n<p>Je vis mon nom très clair </p>\n<p>Je dis mon Dieu que faire? </p>\n<p>En tombant à ses pieds, je réglai mon affaire. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>A l&#39;instant,  (bis)</strong> </p>\n<p><strong>Oui le vieux compte fut réglé a l&#39;instant,</strong> </p>\n<p><strong>Je ne suis plus effrayé,</strong> </p>\n<p><strong>Ma dette est déjà payée</strong> </p>\n<p><strong>Et mon compte avec mon Juge est tout réglé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon compte était très lourd </p>\n<p>Et grandissant toujours </p>\n<p>O j&#39;étais égaré, pêchant plus chaque jour. </p>\n<p>Mais quand mes yeux s&#39;ouvrent </p>\n<p>Et je vis mon état, </p>\n<p>Je me jetais aux pieds du Sauveur a l&#39;instant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au jour du jugement, devant le trône blanc, </p>\n<p>Quand tous les morts viendront </p>\n<p>Les livres s&#39;ouvriront </p>\n<p>Et quand on cherchera, mon nom n&#39;y sera pas </p>\n<p>Car Jésus l&#39;a écrit, au livre de la vie. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O pêcheur viens à Lui </p>\n<p>Mets-toi en règle aussi </p>\n<p>Et fais-le aujourd&#39;hui, car l&#39;occasion s&#39;enfuit </p>\n<p>Il te pardonnera, si tu fais comme moi </p>\n<p>En venant à ton Sauveur </p>\n<p>Tu fais de Lui ton Roi, a l&#39;instant. </p>\n','Je-me-rappelle-un-jour-ou-dans-le-ciel-je-vis',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-23 00:28:26'),(1813,'33c9565d-1fc9-4f8a-aa1a-74dc86b5fb6a',0,0,'Bon-Dieu voyé pitit li',57,'57_EE_ht','1.\nBon-Dieu voyé pitit li, rélé-l Jézu\nKi té vini é padoné\nLi rachté nou, é Li mété nou lib\nKounyé-a la, Li ak Papa-L lapé tan-n nou.\n\nKè:\nMouin konnin-L vivan\nMap konté sou démin\nPaské-L vivan, inkiétud mouin alé\nPaské-m konnin, O m-konnin\nLi sé tout la vi mouin\nMap konté jou pou-L vi-n chèché-m;\nPaské-L vivan.\n\n2.\nMouin aksépté tout sa ki rivé mouin\nPaské Sovè-m kap défan-n mouin\nJoudin-an mèt dévan-m\nKè mouin pap boulvèsé\nPaské Jézu-m nan pa mouri, min Li vivan.\n\n3.\nSèl éspoua mouin sé sou Li sèlman\nPaské-l di mouin lap vi-n chèché-m\nÉprèv nan la vi sa pap fè-m douté dutou\nBon-Dieu-m nan gran é Li vivan map tan-n Li.','<p><strong>1.</strong> </p>\n<p>Bon-Dieu voyé pitit li, rélé-l Jézu </p>\n<p>Ki té vini é padoné </p>\n<p>Li rachté nou, é Li mété nou lib </p>\n<p>Kounyé-a la, Li ak Papa-L lapé tan-n nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin konnin-L vivan</strong> </p>\n<p><strong>Map konté sou démin</strong> </p>\n<p><strong>Paské-L vivan, inkiétud mouin alé</strong> </p>\n<p><strong>Paské-m konnin, O m-konnin</strong> </p>\n<p><strong>Li sé tout la vi mouin</strong> </p>\n<p><strong>Map konté jou pou-L vi-n chèché-m;</strong> </p>\n<p><strong>Paské-L vivan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin aksépté tout sa ki rivé mouin </p>\n<p>Paské Sovè-m kap défan-n mouin </p>\n<p>Joudin-an mèt dévan-m </p>\n<p>Kè mouin pap boulvèsé </p>\n<p>Paské Jézu-m nan pa mouri, min Li vivan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sèl éspoua mouin sé sou Li sèlman </p>\n<p>Paské-l di mouin lap vi-n chèché-m </p>\n<p>Éprèv nan la vi sa pap fè-m douté dutou </p>\n<p>Bon-Dieu-m nan gran é Li vivan map tan-n Li. </p>\n','Bon-Dieu-voye-pitit-li',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1814,'df904097-4fdd-4485-b491-c668658f0e66',0,0,'San ké Jézu',69,'69_EE_ht','1.\nSan ké Jézu té vèsé pou mouin\nSou boua Kalvè-a\nSan sa-a li ban mouin fòs jou aprè jou\nPap jan-m pèdu puisans Li.\n\nKè:\nLi rivé sou montagn ki pi ro-o-o\nLi koulé nan valé ki pli fon\nSan sa- li ban mouin fòs jou aprè jou\nPap jan-m pèdu puisans Li.\n\n2.\nSan sa apézé doulè ak krint mouin\nÉ san sa séché dlo nan jé mouin\nSan sa li ban mouin fòs jou aprè jou\nPap jan-m pèdu puisans Li.','<p><strong>1.</strong> </p>\n<p>San ké Jézu té vèsé pou mouin </p>\n<p>Sou boua Kalvè-a </p>\n<p>San sa-a li ban mouin fòs jou aprè jou </p>\n<p>Pap jan-m pèdu puisans Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li rivé sou montagn ki pi ro-o-o</strong> </p>\n<p><strong>Li koulé nan valé ki pli fon</strong> </p>\n<p><strong>San sa- li ban mouin fòs jou aprè jou</strong> </p>\n<p><strong>Pap jan-m pèdu puisans Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>San sa apézé doulè ak krint mouin </p>\n<p>É san sa séché dlo nan jé mouin </p>\n<p>San sa li ban mouin fòs jou aprè jou </p>\n<p>Pap jan-m pèdu puisans Li. </p>\n','San-ke-Jezu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1815,'4eb76c5c-299a-4fe6-97fb-0969ad5542a9',0,0,'En Lui tout est paix',28,'28_MJ_fr','1.\nEn Lui tout est paix,\nOui, paix et bonheur,\nCar Il vit à jamais dans mon coeur,\nMon péché n\'est plus,\nSon sang l\'a lavé,\nDans Son grand amour Il m\'a sauvé.\nTout ce qu\'Il promet,\nIl sait le tenir,\nEt mon avenir est sûr,\nTout est dans Sa main,\nMes jours, mon destin,\nEt Sa face luit sur mon chemin.','<p><strong>1.</strong> </p>\n<p>En Lui tout est paix, </p>\n<p>Oui, paix et bonheur, </p>\n<p>Car Il vit à jamais dans mon coeur, </p>\n<p>Mon péché n&#39;est plus, </p>\n<p>Son sang l&#39;a lavé, </p>\n<p>Dans Son grand amour Il m&#39;a sauvé. </p>\n<p>Tout ce qu&#39;Il promet, </p>\n<p>Il sait le tenir, </p>\n<p>Et mon avenir est sûr, </p>\n<p>Tout est dans Sa main, </p>\n<p>Mes jours, mon destin, </p>\n<p>Et Sa face luit sur mon chemin. </p>\n','En-Lui-tout-est-paix',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1816,'70404340-6930-4179-be4b-040326570195',0,0,'Comme Moïse dans le désert',34,'34_MJ_fr','1.\nComme Moïse dans le désert\nÉleva le serpent,\nAinsi le Fils de l\'Homme\nDoit être élevé,\nAfin que quiconque croit en Lui\nAfin que quiconque croit en Lui,\nNe périsse pas, ne périsse pas,\nMais qu\'il ait la vie,\nLa vie éternelle.','<p><strong>1.</strong> </p>\n<p>Comme Moïse dans le désert </p>\n<p>Éleva le serpent, </p>\n<p>Ainsi le Fils de l&#39;Homme </p>\n<p>Doit être élevé, </p>\n<p>Afin que quiconque croit en Lui </p>\n<p>Afin que quiconque croit en Lui, </p>\n<p>Ne périsse pas, ne périsse pas, </p>\n<p>Mais qu&#39;il ait la vie, </p>\n<p>La vie éternelle. </p>\n','Comme-Moise-dans-le-desert',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1817,'4cbe2837-e99d-43d5-bb4a-5f7f9bc9cb7b',0,0,'Grand en fidélité ô Dieu mon Père',43,'43_MJ_fr','1.\nGrand en fidélité ô Dieu mon Père\nChez Toi nulle trace de changement.\nTes compassions\nNe sont pas à leur terme\nTu restes fidèle éternellement.\n\nChoeur:\nGrand en fidélité  (bis)\nJour après jour Tu me tiens par la foi.\nTu as pourvu chaque jour\nÀ mes besoins\nGrand en fidélité – Toi envers moi.\n\n2.\nL\'été, et l\'hiver,\nLes saisons fertiles,\nLes astres brillants que Tu as créés\nSe joignent avec la nature entière\nPour témoigner à Ta fidélité.\n\n3.\nPardon du péché,\nPaix douce et profonde,\nTa sainte présence pour me guider.\nForce aujourd\'hui,\nPour demain espérance –\nMe rassurent de Ta fidélité.','<p><strong>1.</strong> </p>\n<p>Grand en fidélité ô Dieu mon Père </p>\n<p>Chez Toi nulle trace de changement. </p>\n<p>Tes compassions </p>\n<p>Ne sont pas à leur terme </p>\n<p>Tu restes fidèle éternellement. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Grand en fidélité  (bis)</strong> </p>\n<p><strong>Jour après jour Tu me tiens par la foi.</strong> </p>\n<p><strong>Tu as pourvu chaque jour</strong> </p>\n<p><strong>À mes besoins</strong> </p>\n<p><strong>Grand en fidélité – Toi envers moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>L&#39;été, et l&#39;hiver, </p>\n<p>Les saisons fertiles, </p>\n<p>Les astres brillants que Tu as créés </p>\n<p>Se joignent avec la nature entière </p>\n<p>Pour témoigner à Ta fidélité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pardon du péché, </p>\n<p>Paix douce et profonde, </p>\n<p>Ta sainte présence pour me guider. </p>\n<p>Force aujourd&#39;hui, </p>\n<p>Pour demain espérance – </p>\n<p>Me rassurent de Ta fidélité. </p>\n','Grand-en-fidelite-o-Dieu-mon-Pere',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1818,'d85aa736-9886-415d-88a1-fb2bbe8d5102',0,0,'Du Seigneur je suis un soldat',49,'49_MJ_fr','1.\nDu Seigneur je suis un soldat\nPourquoi craindrai-je encore?\nNon, jamais de peur ni de honte\nPour confesser Son nom.\n\n2.\nComment pourrais-je porter Son nom\nSans que je porte Sa croix?\nSeigneur, me voici Ton soldat\nPour Te suivre au combat.\n\n3.\nDebout, debout vaillant soldat\nPartez, n\'hésiter plus\nEt pour que nul ne défaille\nRegardez à Jésus.','<p><strong>1.</strong> </p>\n<p>Du Seigneur je suis un soldat </p>\n<p>Pourquoi craindrai-je encore? </p>\n<p>Non, jamais de peur ni de honte </p>\n<p>Pour confesser Son nom. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Comment pourrais-je porter Son nom </p>\n<p>Sans que je porte Sa croix? </p>\n<p>Seigneur, me voici Ton soldat </p>\n<p>Pour Te suivre au combat. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Debout, debout vaillant soldat </p>\n<p>Partez, n&#39;hésiter plus </p>\n<p>Et pour que nul ne défaille </p>\n<p>Regardez à Jésus. </p>\n','Du-Seigneur-je-suis-un-soldat',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1819,'a121ce71-3374-42f2-be10-15f7675204f7',0,0,'L’Éternel seul est Seigneur',5,'5_VR_fr','1.	\nL\'Éternel seul est Seigneur  (bis)\nSeul Il est dominateur  (bis)\nSur les peuples de la terre\nIl est maître Souverain\nMaître des ouvrages que sa main\nPour sa gloire a voulu faire.\n\nChoeur\nSeigneur Éternel – \nPuissant Créateur\nNous te louons, Amen\nNous t\'adorons, Amen.\n\n2.	\nMais quel bienheureux mortel  (bis)\nAu saint nom de l\'Éternel  (bis)\nAura le droit de paraître\nEt quel ô Puissant\nSeigneur pour demeurer avec Toi\nAssez juste pourrait être?\n\n3.	\nC\'est l\'homme qui dans son coeur  (bis)\nPar Ton Esprit ô Seigneur  (bis)\nHait du péché les souillures\nQui fuyant la fausseté\nToujours te sert en sincérité\nLevant à Toi des mains pures.','<p><strong>1.</strong> </p>\n<p>L&#39;Éternel seul est Seigneur  (bis) </p>\n<p>Seul Il est dominateur  (bis) </p>\n<p>Sur les peuples de la terre </p>\n<p>Il est maître Souverain </p>\n<p>Maître des ouvrages que sa main </p>\n<p>Pour sa gloire a voulu faire. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Seigneur Éternel –</strong> </p>\n<p><strong>Puissant Créateur</strong> </p>\n<p><strong>Nous te louons, Amen</strong> </p>\n<p><strong>Nous t&#39;adorons, Amen.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais quel bienheureux mortel  (bis) </p>\n<p>Au saint nom de l&#39;Éternel  (bis) </p>\n<p>Aura le droit de paraître </p>\n<p>Et quel ô Puissant </p>\n<p>Seigneur pour demeurer avec Toi </p>\n<p>Assez juste pourrait être? </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est l&#39;homme qui dans son coeur  (bis) </p>\n<p>Par Ton Esprit ô Seigneur  (bis) </p>\n<p>Hait du péché les souillures </p>\n<p>Qui fuyant la fausseté </p>\n<p>Toujours te sert en sincérité </p>\n<p>Levant à Toi des mains pures. </p>\n','L\'Eternel-seul-est-Seigneur',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1820,'72f19809-6f9f-4c69-9c43-6471b12c0712',0,0,'Levez les yeux',19,'19_VR_fr','1.	\nLevez les yeux sur tout le vaste monde,\nVers les pays où règne encore la nuit,\nJésus attend que quelqu\'un lui réponde\nSeigneur, j\'irai, conduit par ton Esprit.\n\n2.	\nJe suis avec vous tous les jours,\nComptez sur mon puissant secours,\nAllez partout prêcher le Saint Évangile\nD\'une ville à l\'autre ville.','<p><strong>1.</strong> </p>\n<p>Levez les yeux sur tout le vaste monde, </p>\n<p>Vers les pays où règne encore la nuit, </p>\n<p>Jésus attend que quelqu&#39;un lui réponde </p>\n<p>Seigneur, j&#39;irai, conduit par ton Esprit. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je suis avec vous tous les jours, </p>\n<p>Comptez sur mon puissant secours, </p>\n<p>Allez partout prêcher le Saint Évangile </p>\n<p>D&#39;une ville à l&#39;autre ville. </p>\n','Levez-les-yeux',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1821,'6b990525-b11d-4ef6-a3d5-8251524f6e7c',0,0,'Pour être heureux dans la vie',14,'14_VR_fr','1.	\nPour être heureux dans la vie\nPour être heureux tous les jours\nPour avoir la paix bénie\nIl faut Jésus-Christ toujours.\n\nChoeur\nSur son coeur douce retraite\nHeureux qui veut se cacher\nIci la douleur s\'arrête\nJésus est le bon Berger.\n\n2.	\nSi l\'orage se déchaîne\nJe ne crains pas sa fureur\nCar de sa brebis en peine\nIl veux calmer sa frayeur.\n\n3.	\nSi vos larmes sont amères\nSi vous êtres seuls souffrants\nLa Victime du Calvaire\nPrendra sur Lui vos tourments.\n\n4.	\nVenez au Christ qui console\nEt qui sauve le pécheur\nOn apprend à son école\nLe chemin du vrai bonheur.','<p><strong>1.</strong> </p>\n<p>Pour être heureux dans la vie </p>\n<p>Pour être heureux tous les jours </p>\n<p>Pour avoir la paix bénie </p>\n<p>Il faut Jésus-Christ toujours. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Sur son coeur douce retraite</strong> </p>\n<p><strong>Heureux qui veut se cacher</strong> </p>\n<p><strong>Ici la douleur s&#39;arrête</strong> </p>\n<p><strong>Jésus est le bon Berger.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si l&#39;orage se déchaîne </p>\n<p>Je ne crains pas sa fureur </p>\n<p>Car de sa brebis en peine </p>\n<p>Il veux calmer sa frayeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si vos larmes sont amères </p>\n<p>Si vous êtres seuls souffrants </p>\n<p>La Victime du Calvaire </p>\n<p>Prendra sur Lui vos tourments. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Venez au Christ qui console </p>\n<p>Et qui sauve le pécheur </p>\n<p>On apprend à son école </p>\n<p>Le chemin du vrai bonheur. </p>\n','Pour-etre-heureux-dans-la-vie',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1822,'29a25c0e-f8f0-4214-8572-bf60773011d0',0,0,'Map kalkulé-m gnou jou',1,'1_VR_ht','1.	\nMap kalkilé-m gnou jou\nNan sièl la ma ouè sa\nAn dédan tout liv sa yo\nTout péché té ékri\nMouin ouè non mouin é kri trè klè\nMouin di mon Dié kè fè-m\nMin-m vini tonbé nan pié Sovè-m\nPou-m réglé zafè mouin.\n\nKè\nA linstan koun yé a  (bis)\nOui tout vié péché-m yo parèt a linstan\nMouin pa gin angnin pou mouin pè\nDèt mouin yo déja péyé\nÉ péché avèk jujman-m té tou réglé.\n\n2.	\nSa mouin té fè té trò\nYo ta grandi toujou\nLè sa-m té égaré\nPé ché-m tap plis chak jou\nMin kan jé-m vi-n louvri\nMouin vi-n jété-m nan pié\nNan pié Ségnè Jézu.\n\n3.	\nJou jujman va vini\nDévan gnou tròn ki blanche\nKan tout lè mò vini\nPou yo louvri liv yo\nKan yo vap chaché non-m\nNon pa mouin pa-p la\nKa Jézu té inskri-l\nNan liv dé la vi.\n\n4.	\nPéchè vi-n Jouin-n Jézu\nMété ou an règ ak Li\nÉ fè sa koun yé a\nKa okazion sa pralé\nÉ lap padonnin ou\nSi ou fè kòm mouin min-m\nLè ou vi-n jouin-n Jézu\nOua fè-l tounin Roi ou.\n\n5.	\nAnfan, jè-n é vié-a\nPa di ou ansi-in pasé-m\nLoguèy sé don Satan\nJézu di Simon-Piè\nSatan ap chaché kriblé-ou\nDésan-n pou sa monté\nKréti-in fòk ou dépouyé-ou\nPou kab sanblé ak Jézu.','<p><strong>1.</strong> </p>\n<p>Map kalkilé-m gnou jou </p>\n<p>Nan sièl la ma ouè sa </p>\n<p>An dédan tout liv sa yo </p>\n<p>Tout péché té ékri </p>\n<p>Mouin ouè non mouin é kri trè klè </p>\n<p>Mouin di mon Dié kè fè-m </p>\n<p>Min-m vini tonbé nan pié Sovè-m </p>\n<p>Pou-m réglé zafè mouin. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>A linstan koun yé a  (bis)</strong> </p>\n<p><strong>Oui tout vié péché-m yo parèt a linstan</strong> </p>\n<p><strong>Mouin pa gin angnin pou mouin pè</strong> </p>\n<p><strong>Dèt mouin yo déja péyé</strong> </p>\n<p><strong>É péché avèk jujman-m té tou réglé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sa mouin té fè té trò </p>\n<p>Yo ta grandi toujou </p>\n<p>Lè sa-m té égaré </p>\n<p>Pé ché-m tap plis chak jou </p>\n<p>Min kan jé-m vi-n louvri </p>\n<p>Mouin vi-n jété-m nan pié </p>\n<p>Nan pié Ségnè Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jou jujman va vini </p>\n<p>Dévan gnou tròn ki blanche </p>\n<p>Kan tout lè mò vini </p>\n<p>Pou yo louvri liv yo </p>\n<p>Kan yo vap chaché non-m </p>\n<p>Non pa mouin pa-p la </p>\n<p>Ka Jézu té inskri-l </p>\n<p>Nan liv dé la vi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Péchè vi-n Jouin-n Jézu </p>\n<p>Mété ou an règ ak Li </p>\n<p>É fè sa koun yé a </p>\n<p>Ka okazion sa pralé </p>\n<p>É lap padonnin ou </p>\n<p>Si ou fè kòm mouin min-m </p>\n<p>Lè ou vi-n jouin-n Jézu </p>\n<p>Oua fè-l tounin Roi ou. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Anfan, jè-n é vié-a </p>\n<p>Pa di ou ansi-in pasé-m </p>\n<p>Loguèy sé don Satan </p>\n<p>Jézu di Simon-Piè </p>\n<p>Satan ap chaché kriblé-ou </p>\n<p>Désan-n pou sa monté </p>\n<p>Kréti-in fòk ou dépouyé-ou </p>\n<p>Pou kab sanblé ak Jézu. </p>\n','Map-kalkule-m-gnou-jou',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1823,'d8fab215-d903-4970-b816-c11d52cc808c',0,0,'Mouin gingnin gnou zié kap véyé',13,'13_EE_ht','1.\nMouin gin gnou zié kap véyé sou mouin\nMin-m si yo pa ouè pèson-n\nMouin pa bézouin pè ni douté\nPaské Sovè-m ap véyé-m\nLi fè-m promès Li avèk mouin\nTou lè jou juska la fin\nMouin gin gnou zié \nKap véyé sou mouin tou lè jou.\n\n2.\nMouin gin gnou zié kap véyé sou mouin\nKan map monté montagn yo\nMouin gin gnou zié kap véyé sou mouin\nLè map travèsé plèn yo\nLi kon-n lè-m kouché lè-m lévé\nSovè-m nan pa jan-m dòmi\nMouin gin gnou zié\nKap véyé sou mouin tou lè jou.\n\n\n3.\nMouin gin gnou zié kap véyé sou mouin\nMin-m si kò sa va mouri\nLi va alé nan gnou lòt kò\nKapé  préparé pou mouin\nMin-m lè sièl la ta va fè noua\nLi toujou bò boté mouin\nMouin gin gnou zié \nKap véyé sou mouin tou lè jou.\n\n4.\nMouin gin gnou zié kap véyé sou mouin\nMin-m si map maché nan difé\nLi va vlopé mouin nan manto L\nPou flan-m yo kap pa boulé-m\nMouin gin gnou zié kap véyé sou mouin\nMin-m lè la mè-a mové\n\n5.\nMouin gin gnou zié Kap véyé sou mouin tou lè jou\nÉ lè jou sa yo va tèminé, lé mon-n sa va fini\nMap chita pami tout zanj yo\nPou-m loué Bon-Dieu mouin-an\nPou-m rakonté grandè Li\nMouin gin gnou zié \nKap véyé sou mouin tou lè jou.','<p><strong>1.</strong> </p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Min-m si yo pa ouè pèson-n </p>\n<p>Mouin pa bézouin pè ni douté </p>\n<p>Paské Sovè-m ap véyé-m </p>\n<p>Li fè-m promès Li avèk mouin </p>\n<p>Tou lè jou juska la fin </p>\n<p>Mouin gin gnou zié </p>\n<p>Kap véyé sou mouin tou lè jou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Kan map monté montagn yo </p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Lè map travèsé plèn yo </p>\n<p>Li kon-n lè-m kouché lè-m lévé </p>\n<p>Sovè-m nan pa jan-m dòmi </p>\n<p>Mouin gin gnou zié </p>\n<p>Kap véyé sou mouin tou lè jou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Min-m si kò sa va mouri </p>\n<p>Li va alé nan gnou lòt kò </p>\n<p>Kapé  préparé pou mouin </p>\n<p>Min-m lè sièl la ta va fè noua </p>\n<p>Li toujou bò boté mouin </p>\n<p>Mouin gin gnou zié </p>\n<p>Kap véyé sou mouin tou lè jou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Min-m si map maché nan difé </p>\n<p>Li va vlopé mouin nan manto L </p>\n<p>Pou flan-m yo kap pa boulé-m </p>\n<p>Mouin gin gnou zié kap véyé sou mouin </p>\n<p>Min-m lè la mè-a mové </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mouin gin gnou zié Kap véyé sou mouin tou lè jou </p>\n<p>É lè jou sa yo va tèminé, lé mon-n sa va fini </p>\n<p>Map chita pami tout zanj yo </p>\n<p>Pou-m loué Bon-Dieu mouin-an </p>\n<p>Pou-m rakonté grandè Li </p>\n<p>Mouin gin gnou zié </p>\n<p>Kap véyé sou mouin tou lè jou. </p>\n','Mouin-gingnin-gnou-zie-kap-veye',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1824,'07c5e298-4388-46a8-92c2-21eea9c62b6e',0,0,'Lè mouin santi-m atristé',15,'15_EE_ht','1.\nLè mouin santi-m atristé\nSou chémin la vi sila\nLè tout bagay parèt noua\nLè ou pa gingnin éspoua\nMouin kon-n vlé dékourajé\nÉ min-m démisioné\nSi sé paté ansyin Roch la\nMouin ta abandoné.\n\nKè:\nO, O, ansyin Roch la\nOu min-m ki pa jan-m chanjé\nO, O, ansyin Roch la\nKaché mouin pou jouk al fin.\n\n2.\nDlo nan jé ak kè kontan\nSou chémin la vi sila\nPa gingnin trò gro éspoua\nPou séparé gnoun ak lòt\nSi sé paté ansyin Roch la\nKouman mouin ta fè souri\nNan gnou mon-n kouvri ak ògèy\nMouin ta dézéspéré.\n\n3.\nKaché mouin, kaché-m souplé\nAnba manto la gras la\nMin-m lè pié-m ta vlé glisé\nTanpri pa kité-m tonbé\nOu sé gnou ranpa pou nan-m mouin\nOu sé sous konsolasion-m\nMouin pa gingnin lòt apui\nKilòt pi bon Zanmi.','<p><strong>1.</strong> </p>\n<p>Lè mouin santi-m atristé </p>\n<p>Sou chémin la vi sila </p>\n<p>Lè tout bagay parèt noua </p>\n<p>Lè ou pa gingnin éspoua </p>\n<p>Mouin kon-n vlé dékourajé </p>\n<p>É min-m démisioné </p>\n<p>Si sé paté ansyin Roch la </p>\n<p>Mouin ta abandoné. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O, O, ansyin Roch la</strong> </p>\n<p><strong>Ou min-m ki pa jan-m chanjé</strong> </p>\n<p><strong>O, O, ansyin Roch la</strong> </p>\n<p><strong>Kaché mouin pou jouk al fin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dlo nan jé ak kè kontan </p>\n<p>Sou chémin la vi sila </p>\n<p>Pa gingnin trò gro éspoua </p>\n<p>Pou séparé gnoun ak lòt </p>\n<p>Si sé paté ansyin Roch la </p>\n<p>Kouman mouin ta fè souri </p>\n<p>Nan gnou mon-n kouvri ak ògèy </p>\n<p>Mouin ta dézéspéré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kaché mouin, kaché-m souplé </p>\n<p>Anba manto la gras la </p>\n<p>Min-m lè pié-m ta vlé glisé </p>\n<p>Tanpri pa kité-m tonbé </p>\n<p>Ou sé gnou ranpa pou nan-m mouin </p>\n<p>Ou sé sous konsolasion-m </p>\n<p>Mouin pa gingnin lòt apui </p>\n<p>Kilòt pi bon Zanmi. </p>\n','Le-mouin-santi-m-atriste',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1825,'1ec0ef32-161d-423b-b617-1b3963507b76',0,0,'Ségnè, tanpri souplé',47,'47_EE_ht','1.\nSégnè tanpri souplé, kouté voua pitit ou\nTandé kri kè mouin non, mouin prié Ou\nTantasion, chatiman, oui tou sa nésésè\nMin tanpri souplé kinbé min mouin.\n\nKè:\nGin dé lè mouin santi-m kon-n vlé dékourajé\nLè féblès anvayi léspri mouin\nKinbé min mouin souplé, pa kité mouin tonbé\nSi sé ou ki kinbé-m fò-m rivé.\n\n2.\nLoraj, gro van tanpèt, vlé détounin kè mouin\nÉ souvan zéprèv yo, yo di anpil\nMin mouin konfié-m nan Ou, \nÉ-m konnin m-asuré\n\n3.\nMété fòs ou Ségnè nan féblès mouin souplé\nÉdé mouin, édé-m pou-m pa tonbé\nMouin rémèt nan min Ou,\nKè mouin, kò mouin, nan-m mouin\nSi sé Ou ki kinbé-m ma rivé.','<p><strong>1.</strong> </p>\n<p>Ségnè tanpri souplé, kouté voua pitit ou </p>\n<p>Tandé kri kè mouin non, mouin prié Ou </p>\n<p>Tantasion, chatiman, oui tou sa nésésè </p>\n<p>Min tanpri souplé kinbé min mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gin dé lè mouin santi-m kon-n vlé dékourajé</strong> </p>\n<p><strong>Lè féblès anvayi léspri mouin</strong> </p>\n<p><strong>Kinbé min mouin souplé, pa kité mouin tonbé</strong> </p>\n<p><strong>Si sé ou ki kinbé-m fò-m rivé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Loraj, gro van tanpèt, vlé détounin kè mouin </p>\n<p>É souvan zéprèv yo, yo di anpil </p>\n<p>Min mouin konfié-m nan Ou, </p>\n<p>É-m konnin m-asuré </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mété fòs ou Ségnè nan féblès mouin souplé </p>\n<p>Édé mouin, édé-m pou-m pa tonbé </p>\n<p>Mouin rémèt nan min Ou, </p>\n<p>Kè mouin, kò mouin, nan-m mouin </p>\n<p>Si sé Ou ki kinbé-m ma rivé. </p>\n','Segne-tanpri-souple',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1826,'736c0a5b-584e-4430-a89f-e4e5e6676dba',0,0,'Souvan nou mandé',70,'70_EE_ht','1.\nSouvan nou mandé, sa démin ap poté\nSi sé pa anpil joua, chagrin ou tristès\nSouvan nou pèdu anpil sou avni nou\nSan nou pa min-m pansé si Jézu ak nou.\n\nKè:\nFrè mouin ak sè mouin atansion pa troublé\nKonnin ké avni nou, li nan min Bon-Dieu\nTout sòm la vi-a, lé prézan é lavni\nYo tout byin kalkilé pa Léspri Bon-Dieu.\n\n2.\nNou pa kapab fè angnin pou tèt nou\nSi sé pa min Bon-Dieu ki ap kondui nou\nSi nou vlé gingnin gnou bèl briyant avni\nSèl bagay pou nou fè sé konfié nan Dieu.\n\n3.\nSonjé trè byin Bon-Dieu nou-an puisan\nTout sa ki imposib, ak Li yo posib\nNou doué préparé nou pou retou Jézu-Kri\nPaské Li a la pòt, L-ap vi-n chèché nou.','<p><strong>1.</strong> </p>\n<p>Souvan nou mandé, sa démin ap poté </p>\n<p>Si sé pa anpil joua, chagrin ou tristès </p>\n<p>Souvan nou pèdu anpil sou avni nou </p>\n<p>San nou pa min-m pansé si Jézu ak nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Frè mouin ak sè mouin atansion pa troublé</strong> </p>\n<p><strong>Konnin ké avni nou, li nan min Bon-Dieu</strong> </p>\n<p><strong>Tout sòm la vi-a, lé prézan é lavni</strong> </p>\n<p><strong>Yo tout byin kalkilé pa Léspri Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou pa kapab fè angnin pou tèt nou </p>\n<p>Si sé pa min Bon-Dieu ki ap kondui nou </p>\n<p>Si nou vlé gingnin gnou bèl briyant avni </p>\n<p>Sèl bagay pou nou fè sé konfié nan Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sonjé trè byin Bon-Dieu nou-an puisan </p>\n<p>Tout sa ki imposib, ak Li yo posib </p>\n<p>Nou doué préparé nou pou retou Jézu-Kri </p>\n<p>Paské Li a la pòt, L-ap vi-n chèché nou. </p>\n','Souvan-nou-mande',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1827,'742ea09f-80bf-48bf-aab7-3af44dd35361',0,0,'J\'étais pauvre, mais il m\'a enrichi',13,'13_MJ_fr','1.\nJ\'étais pauvre, mais Il m\'a enrichi;\nÉtant riche, Il s\'est\nFait pauvre pour moi;\nMon Sauveur bien-aimé\nExpia mon péché.\nJ\'étais pauvre, mais Il m\'a enrichi.','<p><strong>1.</strong> </p>\n<p>J&#39;étais pauvre, mais Il m&#39;a enrichi; </p>\n<p>Étant riche, Il s&#39;est </p>\n<p>Fait pauvre pour moi; </p>\n<p>Mon Sauveur bien-aimé </p>\n<p>Expia mon péché. </p>\n<p>J&#39;étais pauvre, mais Il m&#39;a enrichi. </p>\n','J\'etais-pauvre-mais-il-m\'a-enrichi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1828,'7246a0b2-125e-4e80-b4f3-9e1839453e61',0,0,'L\'Éternel est omnipotent',25,'25_MJ_fr','1.\nL\'Éternel est omnipotent,\nSon Nom est glorieux.\nIl est un Dieu de miracles\nC\'est écrit dans les cieux.\n\nChoeur:\nIl fallut un miracle\nPour créer les cieux,\nPlacer en orbite\nLes astres radieux,\nMais pour me délivrer\nDe tous mes péchés\nIl fallut un miracle merveilleux!\n\n2.\nBien que Sa gloire, Sa puissance\nNous soient révélées,\nLes merveilles de Son trône,\nNous sont toujours voilées.\n\n3.\nLa Bible nous parle partout\nDe sagesse infinie,\nChaque oiseau et chaque fleur\nEn est un témoin béni.','<p><strong>1.</strong> </p>\n<p>L&#39;Éternel est omnipotent, </p>\n<p>Son Nom est glorieux. </p>\n<p>Il est un Dieu de miracles </p>\n<p>C&#39;est écrit dans les cieux. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Il fallut un miracle</strong> </p>\n<p><strong>Pour créer les cieux,</strong> </p>\n<p><strong>Placer en orbite</strong> </p>\n<p><strong>Les astres radieux,</strong> </p>\n<p><strong>Mais pour me délivrer</strong> </p>\n<p><strong>De tous mes péchés</strong> </p>\n<p><strong>Il fallut un miracle merveilleux!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bien que Sa gloire, Sa puissance </p>\n<p>Nous soient révélées, </p>\n<p>Les merveilles de Son trône, </p>\n<p>Nous sont toujours voilées. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La Bible nous parle partout </p>\n<p>De sagesse infinie, </p>\n<p>Chaque oiseau et chaque fleur </p>\n<p>En est un témoin béni. </p>\n','L\'Eternel-est-omnipotent',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1829,'41687f6c-2509-4cbc-a3bb-aaf5bd2e62dc',0,0,'Marchant avec Christ',31,'31_MJ_fr','1.\nMarchant avec Christ\nMarchant chaque jour –\nJusqu\'à Son retour\nMarchant avec Christ\nMarchant avec Jésus seul!\n\n2.\nMarchant dans la clarté\nEt dans les ténèbres\nMarchant chaque jour –\nJusqu\'à Son retour.\nMarchant dans la clarté\nEt dans les ténèbres\nMarchant avec Jésus seul.','<p><strong>1.</strong> </p>\n<p>Marchant avec Christ </p>\n<p>Marchant chaque jour – </p>\n<p>Jusqu&#39;à Son retour </p>\n<p>Marchant avec Christ </p>\n<p>Marchant avec Jésus seul! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Marchant dans la clarté </p>\n<p>Et dans les ténèbres </p>\n<p>Marchant chaque jour – </p>\n<p>Jusqu&#39;à Son retour. </p>\n<p>Marchant dans la clarté </p>\n<p>Et dans les ténèbres </p>\n<p>Marchant avec Jésus seul. </p>\n','Marchant-avec-Christ',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1830,'b83b2c97-f263-4eb1-9e39-be2cfa5a9003',0,0,'Pouvez-vous boire ma coupe',39,'39_MJ_fr','1.\n«Pouvez-vous boire ma coupe»,\nDisait Jésus autrefois,\nSes disciples répondirent:\n«Oui, nous mourrons avec Toi».\n\nChoeur:\nOui, nous le pouvons,\nNous sommes à Toi\nChange nos vies, ô divin Roi.\nPar Ta puissance qui agit en nous\nGarde-nous, Seigneur,\nFidèles jusqu\'au bout.\n\n2.\nPouvez-vous enfin comprendre\nQu\'un des brigands sur la croix\nReçoive le don du salut\nPar un regard de la foi?\n\n3.\nPouvez-vous sans nulle crainte\nA notre dernier soupir\nRendre votre âme en silence\nAu Seigneur de l\'avenir?\n\n4.\n«Pouvez-vous,» demande Jésus\nA travers l\'éternité,\nEt les coeurs braves répondent\nToujours comme en Galilée','<p><strong>1.</strong> </p>\n<p>«Pouvez-vous boire ma coupe», </p>\n<p>Disait Jésus autrefois, </p>\n<p>Ses disciples répondirent: </p>\n<p>«Oui, nous mourrons avec Toi». </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, nous le pouvons,</strong> </p>\n<p><strong>Nous sommes à Toi</strong> </p>\n<p><strong>Change nos vies, ô divin Roi.</strong> </p>\n<p><strong>Par Ta puissance qui agit en nous</strong> </p>\n<p><strong>Garde-nous, Seigneur,</strong> </p>\n<p><strong>Fidèles jusqu&#39;au bout.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pouvez-vous enfin comprendre </p>\n<p>Qu&#39;un des brigands sur la croix </p>\n<p>Reçoive le don du salut </p>\n<p>Par un regard de la foi? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pouvez-vous sans nulle crainte </p>\n<p>A notre dernier soupir </p>\n<p>Rendre votre âme en silence </p>\n<p>Au Seigneur de l&#39;avenir? </p>\n<p><br/> <strong>4.</strong></p>\n<p>«Pouvez-vous,» demande Jésus </p>\n<p>A travers l&#39;éternité, </p>\n<p>Et les coeurs braves répondent </p>\n<p>Toujours comme en Galilée </p>\n','Pouvez-vous-boire-ma-coupe',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1831,'83202f53-ca12-4994-a5e4-e1b48745ae1e',0,0,'Mon Sauveur demeure',45,'45_MJ_fr','1.\nMon Sauveur demeure,\nTout près de moi,\nToujours à toute heure,\nTout près de  moi.\nJamais Il ne délaisse\nMon coeur dans la nuit,\nEt dans la détresses\nIl est mon appui.','<p><strong>1.</strong> </p>\n<p>Mon Sauveur demeure, </p>\n<p>Tout près de moi, </p>\n<p>Toujours à toute heure, </p>\n<p>Tout près de  moi. </p>\n<p>Jamais Il ne délaisse </p>\n<p>Mon coeur dans la nuit, </p>\n<p>Et dans la détresses </p>\n<p>Il est mon appui. </p>\n','Mon-Sauveur-demeure',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1832,'aef3e803-1608-4ada-b20a-8e2ec09057d4',75,0,'Il est un nom',22,'22_EE_fr','1.\nIl est un nom, un nom bénit et saint\nUn nom divin, que nul implore en vain.\nDe ce nom vient le salut des humains\nLe nom de Jésus, précieux Jésus.\n\nRefrain\nGlorieux Nom,  (3 fois)\nLe nom de Jésus, précieux Jésus!\n\n2.\nIl est un nom, le seul  qui fut donné\nNom par lequel je puis être sauvé\nDe tous les noms, c\'est le plus élevé\nLe nom de Jésus, précieux Jésus!\n\n3.\nIl est un nom, qui guérit, qui console\nSi doux, si beau, qui pardonne et qui sauve\nEt merveilleux puissant par sa parole\nLe nom de Jésus, précieux Jésus!\n\n4.\nIl est un nom, qui signifie espoir\nQui rend heureux, chassant le désespoir\nEn ce nom Saint Dieu peut nous recevoir\nLe nom de Jésus, précieux Jésus!','<p><strong>1.</strong> </p>\n<p>Il est un nom, un nom bénit et saint </p>\n<p>Un nom divin, que nul implore en vain. </p>\n<p>De ce nom vient le salut des humains </p>\n<p>Le nom de Jésus, précieux Jésus. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Glorieux Nom,  (3 fois)</strong> </p>\n<p><strong>Le nom de Jésus, précieux Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il est un nom, le seul  qui fut donné </p>\n<p>Nom par lequel je puis être sauvé </p>\n<p>De tous les noms, c&#39;est le plus élevé </p>\n<p>Le nom de Jésus, précieux Jésus! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il est un nom, qui guérit, qui console </p>\n<p>Si doux, si beau, qui pardonne et qui sauve </p>\n<p>Et merveilleux puissant par sa parole </p>\n<p>Le nom de Jésus, précieux Jésus! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il est un nom, qui signifie espoir </p>\n<p>Qui rend heureux, chassant le désespoir </p>\n<p>En ce nom Saint Dieu peut nous recevoir </p>\n<p>Le nom de Jésus, précieux Jésus! </p>\n','Il-est-un-nom',NULL,'fr',3,'2023-07-22 22:41:15','2023-09-20 02:22:01'),(1833,'89811e67-107f-421f-b8b0-483e02e27dbb',1,0,'Seigneur prends soin de moi',29,'29_EE_fr','RefrainSeigneur prend soin de moiJ\'ai besoin de ToiSeul je ne suis rienTu es mon seul bien.1.Comment pourais-je vivre Ton amourComment pourrais-je vivreQui es-Tu Seigneur, et qui suis-je?Garde-moi toujours.2.Et ta parole est venue jusqu\'à moiPour me tracer la routeO mon Dieu efface mes doutesDonne-moi ta foi.','<p><strong>Refrain</strong> </p>\n<p><strong>Seigneur prend soin de moi</strong> </p>\n<p><strong>J&#39;ai besoin de Toi</strong> </p>\n<p><strong>Seul je ne suis rien</strong> </p>\n<p><strong>Tu es mon seul bien. </strong></p>\n<p><br/> <strong>1.</strong></p>\n<p>Comment pourais-je vivre Ton amour </p>\n<p>Comment pourrais-je vivre </p>\n<p>Qui es-Tu Seigneur, et qui suis-je? </p>\n<p>Garde-moi toujours. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et ta parole est venue jusqu&#39;à moi </p>\n<p>Pour me tracer la route </p>\n<p>O mon Dieu efface mes doutes </p>\n<p>Donne-moi ta foi. </p>\n','Seigneur-prends-soin-de-moi',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:43'),(1834,'21e276c2-b989-459f-9199-c31d99c4a50a',1,0,'Je ne suis qu’un',37,'37_EE_fr','1.\nJe ne suis qu\'un passant\nSur cette terre maudite\nJe veux aller retrouver mon Père dans le ciel\nQui m\'attend, qui m\'attend\nAvec tous les anges, pour chanter sa gloire.\n\nRefrain\nGloire, Gloire, Alléluia!   (ter)\nJésus revient bientôt!\n\n2.\nLa vie sur cette terre n\'est que lutte et misère\nDans la cité de gloire,\nIl y aura que la paix, la joie\nLà, nous serons, \nNous serons tous ensemble frères\nUnis pour jamais\n\n3.\nJe veux vous dire amis, l\'histoire de mon Jésus\nCloué sur une croix, pour vous et pour moi\nIl reviendra, reviendra, nous prendre \nAvec Lui pour toute la vie.','<p><strong>1.</strong> </p>\n<p>Je ne suis qu&#39;un passant </p>\n<p>Sur cette terre maudite </p>\n<p>Je veux aller retrouver mon Père dans le ciel </p>\n<p>Qui m&#39;attend, qui m&#39;attend </p>\n<p>Avec tous les anges, pour chanter sa gloire. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire, Gloire, Alléluia!   (ter)</strong> </p>\n<p><strong>Jésus revient bientôt!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La vie sur cette terre n&#39;est que lutte et misère </p>\n<p>Dans la cité de gloire, </p>\n<p>Il y aura que la paix, la joie </p>\n<p>Là, nous serons, </p>\n<p>Nous serons tous ensemble frères </p>\n<p>Unis pour jamais </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je veux vous dire amis, l&#39;histoire de mon Jésus </p>\n<p>Cloué sur une croix, pour vous et pour moi </p>\n<p>Il reviendra, reviendra, nous prendre </p>\n<p>Avec Lui pour toute la vie. </p>\n','Je-ne-suis-qu\'un',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:01:00'),(1835,'70eb4711-3994-4c75-9fef-02f9bd0a7559',0,0,'Mouin vlé rakonté sa',61,'61_EE_ht','1.\nMouin vlé rakonté sa Kris fè pou mouin\nDépi Li té jouin-n mouin, afè-m tout chanjé\nNan prizon diab la, Kris ban-m libèté\nOui Jézu té délivré-m.\n\nKè:\nJézu té délivré-m, Jézu té délivré-m\nTénèb péché tounin nan bèl klaté\nOui Jézu té délivré-m.\n\n2.\nChanalité fè-m blié gras Bon-Dieu\nSousi la vi té fè mouin pè lan mò\nJézu sové-m é mouin pa pè ankò\nOui Jézu té délivré-m.\n\n3.\nSa Kris fè pou mouin, la fè-L pou ou tou\nLévanjil la sé pou tout moun tout patou  \nSa-L fè pou mouin, L-a fè-L pou ou tou\nOui Jézu té délivré-m.','<p><strong>1.</strong> </p>\n<p>Mouin vlé rakonté sa Kris fè pou mouin </p>\n<p>Dépi Li té jouin-n mouin, afè-m tout chanjé </p>\n<p>Nan prizon diab la, Kris ban-m libèté </p>\n<p>Oui Jézu té délivré-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu té délivré-m, Jézu té délivré-m</strong> </p>\n<p><strong>Ténèb péché tounin nan bèl klaté</strong> </p>\n<p><strong>Oui Jézu té délivré-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chanalité fè-m blié gras Bon-Dieu </p>\n<p>Sousi la vi té fè mouin pè lan mò </p>\n<p>Jézu sové-m é mouin pa pè ankò </p>\n<p>Oui Jézu té délivré-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa Kris fè pou mouin, la fè-L pou ou tou </p>\n<p>Lévanjil la sé pou tout moun tout patou </p>\n<p>Sa-L fè pou mouin, L-a fè-L pou ou tou </p>\n<p>Oui Jézu té délivré-m. </p>\n','Mouin-vle-rakonte-sa',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1836,'606b9269-2763-4b36-9ad8-2d09596eb2e7',0,0,'Oui, Je me connais',8,'8_MJ_fr','1.\nOui, je ne connais\nQu\'un seul Sauveur,\nCelui que le Père a donné\nJ\'étais perdu,\nSans paix, sans bonheur,\nPar Sa sainte loi condamné,\nOr c\'est Jésus qui,\nDu haut des cieux\nVint mourir, pour moi,\nSur la croix,\nM\'apportant un salut merveilleux\nQu\'Il me donne et que je Reçois.\n(Chants de G. et de G. No. 111)','<p><strong>1.</strong> </p>\n<p>Oui, je ne connais </p>\n<p>Qu&#39;un seul Sauveur, </p>\n<p>Celui que le Père a donné </p>\n<p>J&#39;étais perdu, </p>\n<p>Sans paix, sans bonheur, </p>\n<p>Par Sa sainte loi condamné, </p>\n<p>Or c&#39;est Jésus qui, </p>\n<p>Du haut des cieux </p>\n<p>Vint mourir, pour moi, </p>\n<p>Sur la croix, </p>\n<p>M&#39;apportant un salut merveilleux </p>\n<p>Qu&#39;Il me donne et que je Reçois. </p>\n<p>(Chants de G. et de G. No. 111) </p>\n','Oui-Je-me-connais',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1837,'fdf3b240-0026-46c4-bc20-d11212403c7d',0,0,'Aujourd\'hui chantons en choeur',24,'24_MJ_fr','1.\nAujourd\'hui chantons en choeur\nJésus notre bon Sauveur!\nDans Son grand amour,\nNouveau chaque jour\nIl nous conduit vers le ciel.\nOh! Le meilleur des amis,\nToujours, Il nous l\'a promis\nVeille sur nos pas, ne nous laisse pas\nSon amour est éternel!','<p><strong>1.</strong> </p>\n<p>Aujourd&#39;hui chantons en choeur </p>\n<p>Jésus notre bon Sauveur! </p>\n<p>Dans Son grand amour, </p>\n<p>Nouveau chaque jour </p>\n<p>Il nous conduit vers le ciel. </p>\n<p>Oh! Le meilleur des amis, </p>\n<p>Toujours, Il nous l&#39;a promis </p>\n<p>Veille sur nos pas, ne nous laisse pas </p>\n<p>Son amour est éternel! </p>\n','Aujourd\'hui-chantons-en-choeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1838,'3e44721d-7a14-4660-9104-aef6d00ae451',0,0,'Je sais qu\'un jour',36,'36_MJ_fr','1.\nJe sais qu\'un jour\nMes yeux verront Jésus.\nJe sais qu\'un jour\nMes yeux verront Jésus.\nJe Le suivrai jusqu\'au bout\nPar la foi et malgré tout\nJe sais qu\'un jour\nMes yeux verront Jésus.','<p><strong>1.</strong> </p>\n<p>Je sais qu&#39;un jour </p>\n<p>Mes yeux verront Jésus. </p>\n<p>Je sais qu&#39;un jour </p>\n<p>Mes yeux verront Jésus. </p>\n<p>Je Le suivrai jusqu&#39;au bout </p>\n<p>Par la foi et malgré tout </p>\n<p>Je sais qu&#39;un jour </p>\n<p>Mes yeux verront Jésus. </p>\n','Je-sais-qu\'un-jour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1839,'c7938b2d-85dc-43c7-a414-310fab1bd960',0,0,'Pour toi, pauvre pécheur',47,'47_MJ_fr','1.\nPour toi, pauvre pécheur\nLà-haut sur le Calvaire,\nMoi, l\'Homme de Douleur,\nJ\'ai bu la coupe amère.\nOui, j\'ai versé Mon sang pour toi,\nQu\'as-tu donné pour Moi? (bis 2 lignes)\n\n2.\nJ\'ai quitté, de Mon ciel,\nLa gloire sainte et pure,\nPour connaître du fiel,\nLa saveur âcre, impure.\nJ\'ai tout, oui, tout quitté pour toi!\nQu\'as-tu  laissé pour Moi? (bis 2 lignes)\n\n3.\nOui, sur l\'infâme bois,\nJ\'ai connu l\'agonie,\nPour t\'offrir, si tu crois,\nLe salut et la vie.\nQue n\'ai-je pas subi pour Toi!\nQu\'as-tu souffert pour Moi? (bis 2 lignes)\n\n4.\nOh! Reçois donc, enfin,\nLa richesse éternelle,\nPour que, de ton destin,\nLe cours se renouvelle!\nOui, tous ces trésors sont pour toi!\nN\'as-tu donc rien pour Moi? (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Pour toi, pauvre pécheur </p>\n<p>Là-haut sur le Calvaire, </p>\n<p>Moi, l&#39;Homme de Douleur, </p>\n<p>J&#39;ai bu la coupe amère. </p>\n<p>Oui, j&#39;ai versé Mon sang pour toi, </p>\n<p>Qu&#39;as-tu donné pour Moi? (bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai quitté, de Mon ciel, </p>\n<p>La gloire sainte et pure, </p>\n<p>Pour connaître du fiel, </p>\n<p>La saveur âcre, impure. </p>\n<p>J&#39;ai tout, oui, tout quitté pour toi! </p>\n<p>Qu&#39;as-tu  laissé pour Moi? (bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, sur l&#39;infâme bois, </p>\n<p>J&#39;ai connu l&#39;agonie, </p>\n<p>Pour t&#39;offrir, si tu crois, </p>\n<p>Le salut et la vie. </p>\n<p>Que n&#39;ai-je pas subi pour Toi! </p>\n<p>Qu&#39;as-tu souffert pour Moi? (bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh! Reçois donc, enfin, </p>\n<p>La richesse éternelle, </p>\n<p>Pour que, de ton destin, </p>\n<p>Le cours se renouvelle! </p>\n<p>Oui, tous ces trésors sont pour toi! </p>\n<p>N&#39;as-tu donc rien pour Moi? (bis 2 lignes) </p>\n','Pour-toi-pauvre-pecheur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1840,'61af5984-8af1-4c43-951c-c90226b07673',0,0,'A Jésus tout mon coeur',48,'48_MJ_fr','1.\nA Jésus tout mon coeur\nOui, tout entier pour mon Sauveur\nNon, je n\'hésite plus\nDe donner mon coeur à Jésus.','<p><strong>1.</strong> </p>\n<p>A Jésus tout mon coeur </p>\n<p>Oui, tout entier pour mon Sauveur </p>\n<p>Non, je n&#39;hésite plus </p>\n<p>De donner mon coeur à Jésus. </p>\n','A-Jesus-tout-mon-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1841,'f5d7daca-b3c6-4984-a934-ba6a8b8f8d73',0,0,'M’aimant',41,'41_VR_fr','1.	\nM\'aimant quand la route est obscure\nJésus s\'approche et me rassure;\nIl me conduit par sa main sure \nDans son amour.\n\n2.	\nM\'aimant d\'un amour insondable,\nJésus de la gloire ineffable\nM\'apporte joie intarissable\nPar son amour.\n\n3.	\nM\'aimant pendant les jours d\'orage,\nQuand mon ciel n\'est qu\'un noir nuage,\nJésus me sauve du naufrage\nDans son amour.\n\n4.	\nM\'aimant dans ce monde où tout passa\nOù tout comme un rêve s\'efface.\nDans son coeur Jésus me fait place\nDans son amour.\n\n5.	\nM\'aimant dans la gloire suprême\nDe la crèche à la croix, Il m\'aime\nHier, aujourd\'hui toujours le même\nDans son amour.\n\n6.	\nM\'aimant oh! Jusqu\'à la venue\nQuand Tu descendras sur la nue\nNous chantons l\'hymne, l\'âme émue\nDe ton amour.','<p><strong>1.</strong> </p>\n<p>M&#39;aimant quand la route est obscure </p>\n<p>Jésus s&#39;approche et me rassure; </p>\n<p>Il me conduit par sa main sure </p>\n<p>Dans son amour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>M&#39;aimant d&#39;un amour insondable, </p>\n<p>Jésus de la gloire ineffable </p>\n<p>M&#39;apporte joie intarissable </p>\n<p>Par son amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>M&#39;aimant pendant les jours d&#39;orage, </p>\n<p>Quand mon ciel n&#39;est qu&#39;un noir nuage, </p>\n<p>Jésus me sauve du naufrage </p>\n<p>Dans son amour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>M&#39;aimant dans ce monde où tout passa </p>\n<p>Où tout comme un rêve s&#39;efface. </p>\n<p>Dans son coeur Jésus me fait place </p>\n<p>Dans son amour. </p>\n<p><br/> <strong>5.</strong></p>\n<p>M&#39;aimant dans la gloire suprême </p>\n<p>De la crèche à la croix, Il m&#39;aime </p>\n<p>Hier, aujourd&#39;hui toujours le même </p>\n<p>Dans son amour. </p>\n<p><br/> <strong>6.</strong></p>\n<p>M&#39;aimant oh! Jusqu&#39;à la venue </p>\n<p>Quand Tu descendras sur la nue </p>\n<p>Nous chantons l&#39;hymne, l&#39;âme émue </p>\n<p>De ton amour. </p>\n','M\'aimant',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1842,'4ac59c51-f9e4-4c50-85c7-c34670ca1fc2',0,0,'Notre Chef et Maître',25,'25_VR_fr','1.	\nNotre Chef et Maître c\'est le Roi de rois.\nPortant ferme sa bannière\nSans craintes ni doutes, soldats de la croix\nAvançons dans la lumière.\n\nChoeur\nEn avant! En avant! Pour Jésus-Christ le Roi des rois. \nNous Le couronnons et marchons\nSous la bannière de la croix.\n\n2.	\nQue l\'ennemi vienne, comme l\'ouragan,\nDéployons notre bannière!\nSous sa garde sainte, jamais de Satan\nNotre âme n\'est prisonnière.\n\n3.	\nPar mer et par terre, qu\'à tout être humain\nSoit annoncé l\'Évangile!\nLa sainte bannière de l\'Agneau divin\nPlantons dans l\'arène hostile.\n\n4.	\nEt le jour de gloire bientôt paraîtra!\nL\'aube, nous la voyons luire,\nOù sons la bannière de la croix vaincra\nDe Christ l\'éternel empire.','<p><strong>1.</strong> </p>\n<p>Notre Chef et Maître c&#39;est le Roi de rois. </p>\n<p>Portant ferme sa bannière </p>\n<p>Sans craintes ni doutes, soldats de la croix </p>\n<p>Avançons dans la lumière. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>En avant! En avant! Pour Jésus-Christ le Roi des rois.</strong> </p>\n<p><strong>Nous Le couronnons et marchons</strong> </p>\n<p><strong>Sous la bannière de la croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que l&#39;ennemi vienne, comme l&#39;ouragan, </p>\n<p>Déployons notre bannière! </p>\n<p>Sous sa garde sainte, jamais de Satan </p>\n<p>Notre âme n&#39;est prisonnière. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Par mer et par terre, qu&#39;à tout être humain </p>\n<p>Soit annoncé l&#39;Évangile! </p>\n<p>La sainte bannière de l&#39;Agneau divin </p>\n<p>Plantons dans l&#39;arène hostile. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et le jour de gloire bientôt paraîtra! </p>\n<p>L&#39;aube, nous la voyons luire, </p>\n<p>Où sons la bannière de la croix vaincra </p>\n<p>De Christ l&#39;éternel empire. </p>\n','Notre-Chef-et-Maitre',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1843,'4de148e1-0752-4e68-bad1-5b100268760f',2,0,'On frappe à la porte de votre coeur',4,'4_VR_fr','-\nOn frappe à la porte de votre coeur,\nDe votre coeur, voulez-vous ouvrir…\nAu Sauveur?\n\n1.	\nC\'est Lui qui frappe à votre porte\nDepuis longtemps Il frappe en vain,\nC\'est son amour qu\'Il vous apporte,\nOh, voulez-vous ouvrir enfin, voulez-vous?\n\nChoeur\nOh, voulez-vous ouvrir la porte\nEt recevoir, pauvre pécheur,\nLe grand salut qu\'Il vous apporte?\nOh, voulez-vous ouvrir vos coeurs au Sauveur?\nOh, voulez-vous ouvrir vos coeurs?\n\n2.	\nSa douce voix se fait pressante,\nNe remettez pas à demain\nNe prolongez pas son attente,\nCraignez qu\'Il passe son chemin.\n\n3.	\nIl tend vers vous ses mains percées,\nSa voix d\'amour insiste tant!\nLaissez-Le, donc, âmes lassées, entrer\nEntrer chez vous en cet instant.','<p>On frappe à la porte de votre coeur, </p>\n<p>De votre coeur, voulez-vous ouvrir… </p>\n<p>Au Sauveur? </p>\n<p><strong>1.</strong> </p>\n<p>C&#39;est Lui qui frappe à votre porte </p>\n<p>Depuis longtemps Il frappe en vain, </p>\n<p>C&#39;est son amour qu&#39;Il vous apporte, </p>\n<p>Oh, voulez-vous ouvrir enfin, voulez-vous? </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Oh, voulez-vous ouvrir la porte</strong> </p>\n<p><strong>Et recevoir, pauvre pécheur,</strong> </p>\n<p><strong>Le grand salut qu&#39;Il vous apporte?</strong> </p>\n<p><strong>Oh, voulez-vous ouvrir vos coeurs au Sauveur?</strong> </p>\n<p><strong>Oh, voulez-vous ouvrir vos coeurs?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sa douce voix se fait pressante, </p>\n<p>Ne remettez pas à demain </p>\n<p>Ne prolongez pas son attente, </p>\n<p>Craignez qu&#39;Il passe son chemin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il tend vers vous ses mains percées, </p>\n<p>Sa voix d&#39;amour insiste tant! </p>\n<p>Laissez-Le, donc, âmes lassées, entrer </p>\n<p>Entrer chez vous en cet instant. </p>\n','On-frappe-a-la-porte-de-votre-coeur',NULL,'fr',2,'2023-07-22 22:41:15','2023-10-03 20:25:03'),(1844,'1f6f052b-85fb-4d2f-9972-a11331fb3067',0,0,'Tu dors dans le tombeau',23,'23_VR_fr','1.	\nTu dors dans le tombeau, Jésus mon Sauveur.\nIls L\'on fermé d\'un sceau, Jésus mon Dieu\n  \nChoeur \nJésus est ressuscité – pour toujours,\nL\'enfer est dompté.  Comme Lui,\nTous les fils du Père s\'envoleront\nDans la lumière, Gloire à Dieu (bis)\nAlléluia, gloire à Dieu.\n\n2.	\nLe sceau ni les soldats, Jésus mon Sauveur\nNe te retiendront pas, Jésus mon Dieu.\nTu brises sans effort, Jésus mon Sauveur,\nLes portes de la mort, Jésus mon Dieu.','<p><strong>1.</strong> </p>\n<p>Tu dors dans le tombeau, Jésus mon Sauveur. </p>\n<p>Ils L&#39;on fermé d&#39;un sceau, Jésus mon Dieu </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Jésus est ressuscité – pour toujours,</strong> </p>\n<p><strong>L&#39;enfer est dompté.  Comme Lui,</strong> </p>\n<p><strong>Tous les fils du Père s&#39;envoleront</strong> </p>\n<p><strong>Dans la lumière, Gloire à Dieu (bis)</strong> </p>\n<p><strong>Alléluia, gloire à Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le sceau ni les soldats, Jésus mon Sauveur </p>\n<p>Ne te retiendront pas, Jésus mon Dieu. </p>\n<p>Tu brises sans effort, Jésus mon Sauveur, </p>\n<p>Les portes de la mort, Jésus mon Dieu. </p>\n','Tu-dors-dans-le-tombeau',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1845,'20da6c4c-045f-41d8-89ff-8d4c7ad6140b',0,0,'O Révoua',8,'8_VR_ht','1.	\nKè ou régrèt, fòk séparé,\nAlèkilé nap préparé\nPou nou anbrasé é pou vouayajé\nLa pè – O révoua.\n\n2.	\nNan min Bon-Dié ki pap kaba\nSé Li-min’m kab gidé nou pa-za-pa,\nÉpi nou ka joui protéksion sa a,\nLa pè – O révoua.\n\n3.	\nGras é salu nap résévoua\nAvèk amou Li é puisans la foua\nKounié-a nou pra-l nan gran travay la\nLa pè – O révoua.\n\n4.	\nKouraj, kouraj, kinbé la foua,\nSi nou pa kapab ansan-m isi-ba\nSé pou nou fidè jouk nou nan la gloua,\nLa pè – O révoua','<p><strong>1.</strong> </p>\n<p>Kè ou régrèt, fòk séparé, </p>\n<p>Alèkilé nap préparé </p>\n<p>Pou nou anbrasé é pou vouayajé </p>\n<p>La pè – O révoua. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan min Bon-Dié ki pap kaba </p>\n<p>Sé Li-min’m kab gidé nou pa-za-pa, </p>\n<p>Épi nou ka joui protéksion sa a, </p>\n<p>La pè – O révoua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gras é salu nap résévoua </p>\n<p>Avèk amou Li é puisans la foua </p>\n<p>Kounié-a nou pra-l nan gran travay la </p>\n<p>La pè – O révoua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kouraj, kouraj, kinbé la foua, </p>\n<p>Si nou pa kapab ansan-m isi-ba </p>\n<p>Sé pou nou fidè jouk nou nan la gloua, </p>\n<p>La pè – O révoua </p>\n','O-Revoua',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1846,'ee3ed80b-d8e5-48e2-bf01-1e64bfe9f708',0,0,'Li touché-m',1,'1_EE_ht','1.\nLi touché-m, o Li touché,\nÉ la joua té néyé nan-m mouin\nGnou bagay té pasé kounyé-a m-konnin\nLi touché-m nan gran bonté Li.\n\n2.\nKè mouin té chajé ak fado lou\nPéché ak ront té kouvri mouin\nAlò min Jézu té touché-m\nKounyé-a mouin santi vi-n chanjé.\n\n3.\nDépi-m té jouin-n Sovè béni an\nDépi-L té sové-m é géri-m\nMouin pap janmin suspan-n loué Li\nMap chanté gloua Li étènèlman.\n\n\n\n---\n1.\nÉzaï désan-n bato a\nLétènèl rantré landan-l\nLi maké-l alélouya\nDiab la di-l pa ka rantré.\n\n2.\nLoué, loué gloua a Jézu  (2 foua)\nPéché mouin éfasé\nLoué, loué, loué, Amen, Alélouya.\nLoué Létènèl…Loué Létènèl…','<p><strong>1.</strong> </p>\n<p>Li touché-m, o Li touché, </p>\n<p>É la joua té néyé nan-m mouin </p>\n<p>Gnou bagay té pasé kounyé-a m-konnin </p>\n<p>Li touché-m nan gran bonté Li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Kè mouin té chajé ak fado lou </p>\n<p>Péché ak ront té kouvri mouin </p>\n<p>Alò min Jézu té touché-m </p>\n<p>Kounyé-a mouin santi vi-n chanjé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dépi-m té jouin-n Sovè béni an </p>\n<p>Dépi-L té sové-m é géri-m </p>\n<p>Mouin pap janmin suspan-n loué Li </p>\n<p>Map chanté gloua Li étènèlman. </p>\n<hr>\n<p><br/> <strong>1.</strong></p>\n<p>Ézaï désan-n bato a </p>\n<p>Létènèl rantré landan-l </p>\n<p>Li maké-l alélouya </p>\n<p>Diab la di-l pa ka rantré. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Loué, loué gloua a Jézu  (2 foua) </p>\n<p>Péché mouin éfasé </p>\n<p>Loué, loué, loué, Amen, Alélouya. </p>\n<p>Loué Létènèl…Loué Létènèl… </p>\n','Li-touche-m',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1847,'d734d622-d894-442f-9976-b71f1d0d6c30',0,0,'Mouin pap kapab rété konsa',6,'6_EE_ht','1.\nMouin pa kapab rété konsa\nSan-m pa pansé avèk Jézu\nSé Li min-m ki té géri-m\nLè-m tap soufri ak maladi-m nan\nDoktè pat kapab fè angnin\nLajan pat kapab ban-m la vi.\n\nKè: \nSi-m pa pansé avèk Jézu\nAvèk ki lès pou-m ta pansé.\n\n2.\nA la ingra-m ta ingra\nPou-m pa ta loué non Jézu-m nan\nPaské lè mouin té malad\nSé Li min-m ki té géri mouin\nSa sé mo témouayaj mouin\nPou sa Jézu fè pou kè mouin \nNou min-, kap soufri jodi-a\nGin gnou jou na délivré.\n\n3.\nMouin sonjé lè-m gin lajan\nMouin té gingnin anpil zanmi\nKounié-a m-vi’m maléré\nTout zanmi-m yo kité mouim\nDépi mouin rankronté Jézu\nTout bagay yo té vi-n chanjé\nSanatori-òm zanmi blié ou\nJézu Li min-m Li toujou la.\n\n4.\nZanmi mouin yo nan lé Ségnè\nAn nou maré sintu nou fò\nPou-n pa kité tribulasion\nFè nou blié Jézu Sovè-a\nMin-m si lé mon-n ap kritiké-n\nYo di sé tèt maré nou yé\nAn nou répon-n ak jantiyès\nNou sé tèt maré pou Jézu.','<p><strong>1.</strong> </p>\n<p>Mouin pa kapab rété konsa </p>\n<p>San-m pa pansé avèk Jézu </p>\n<p>Sé Li min-m ki té géri-m </p>\n<p>Lè-m tap soufri ak maladi-m nan </p>\n<p>Doktè pat kapab fè angnin </p>\n<p>Lajan pat kapab ban-m la vi. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Si-m pa pansé avèk Jézu</strong> </p>\n<p><strong>Avèk ki lès pou-m ta pansé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A la ingra-m ta ingra </p>\n<p>Pou-m pa ta loué non Jézu-m nan </p>\n<p>Paské lè mouin té malad </p>\n<p>Sé Li min-m ki té géri mouin </p>\n<p>Sa sé mo témouayaj mouin </p>\n<p>Pou sa Jézu fè pou kè mouin </p>\n<p>Nou min-, kap soufri jodi-a </p>\n<p>Gin gnou jou na délivré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin sonjé lè-m gin lajan </p>\n<p>Mouin té gingnin anpil zanmi </p>\n<p>Kounié-a m-vi’m maléré </p>\n<p>Tout zanmi-m yo kité mouim </p>\n<p>Dépi mouin rankronté Jézu </p>\n<p>Tout bagay yo té vi-n chanjé </p>\n<p>Sanatori-òm zanmi blié ou </p>\n<p>Jézu Li min-m Li toujou la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zanmi mouin yo nan lé Ségnè </p>\n<p>An nou maré sintu nou fò </p>\n<p>Pou-n pa kité tribulasion </p>\n<p>Fè nou blié Jézu Sovè-a </p>\n<p>Min-m si lé mon-n ap kritiké-n </p>\n<p>Yo di sé tèt maré nou yé </p>\n<p>An nou répon-n ak jantiyès </p>\n<p>Nou sé tèt maré pou Jézu. </p>\n','Mouin-pap-kapab-rete-konsa',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1848,'335f5142-7f67-4c75-892b-7a70574f8907',0,0,'Nan-m mouin té souaf',16,'16_EE_ht','1.\nNan-m mouin té souaf\nMouin pat jouin-n satisfaksion\nPlézi lé mon-n té tronpé nan-m mouin\nMin gnou bon jou \nLè-m tap chèché soulajman\nJézu té di-m vi-n jouin-n répo nan Mouin.\n\nKè:\nOuin mouin té jouin-n li\nBèl sous la vi a\nGras a Jézu ki té mouri pou mouin\nSan Li té koulé sou boua Kalvè ya\nÉ kounié-a li satisfè nan-m mouin.\n\n2.\nLa vi-m té tris lè-m pat konnin Jézu\nM-pat gin éspoua, nan-m mouin té boulvèsé\nMin gras Bon-Die té louvri sous la vi-a\nMouin té jouin-n ladan-l padon é libèté.\n\n3.\nChè zanmi mouin, min pou ki sa ou mizé?\nKouri vini nan bèl sous la vi-a\nFado péché trò lou pou ou pòté\nJézu vlé ba ou répo jodi-a.','<p><strong>1.</strong> </p>\n<p>Nan-m mouin té souaf </p>\n<p>Mouin pat jouin-n satisfaksion </p>\n<p>Plézi lé mon-n té tronpé nan-m mouin </p>\n<p>Min gnou bon jou </p>\n<p>Lè-m tap chèché soulajman </p>\n<p>Jézu té di-m vi-n jouin-n répo nan Mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ouin mouin té jouin-n li</strong> </p>\n<p><strong>Bèl sous la vi a</strong> </p>\n<p><strong>Gras a Jézu ki té mouri pou mouin</strong> </p>\n<p><strong>San Li té koulé sou boua Kalvè ya</strong> </p>\n<p><strong>É kounié-a li satisfè nan-m mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La vi-m té tris lè-m pat konnin Jézu </p>\n<p>M-pat gin éspoua, nan-m mouin té boulvèsé </p>\n<p>Min gras Bon-Die té louvri sous la vi-a </p>\n<p>Mouin té jouin-n ladan-l padon é libèté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chè zanmi mouin, min pou ki sa ou mizé? </p>\n<p>Kouri vini nan bèl sous la vi-a </p>\n<p>Fado péché trò lou pou ou pòté </p>\n<p>Jézu vlé ba ou répo jodi-a. </p>\n','Nan-m-mouin-te-souaf',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1849,'a3cc8495-add7-47b1-874a-33a93b1c2d22',0,0,'Un enfant est né',27,'27_EE_fr','1.\nUn soir d\'hiver, j\'étais seule\nDans le ciel de ma fenêtre\nUne étoile qui brille d\'un éclat\nÉblouissant les ténèbres\nAlors un homme s\'est perché\nLe chemin reconnu\nQue mène à la crèche de Bethléem\nEt proclamant tout ce jour sacré.\n\nRefrain\nUn Sauveur est né, Béni soit ce jour\nIl est le Sauveur, des enfants du Seigneur.\n\n2.\nJ\'ai suivi, les bergers\nIls m\'ont montré le chemin\nEt j\'ai vu l\'enfant couché\nSous un lit de froid\nMarie, sa mère Lui sourit\nÉmue, émerveillé et caresse des yeux\nL\'enfant qui lui vient de Dieu.\nUn Sauveur est né, béni soit ce jour\nIl est le Sauveur, des enfants du Seigneur.','<p><strong>1.</strong> </p>\n<p>Un soir d&#39;hiver, j&#39;étais seule </p>\n<p>Dans le ciel de ma fenêtre </p>\n<p>Une étoile qui brille d&#39;un éclat </p>\n<p>Éblouissant les ténèbres </p>\n<p>Alors un homme s&#39;est perché </p>\n<p>Le chemin reconnu </p>\n<p>Que mène à la crèche de Bethléem </p>\n<p>Et proclamant tout ce jour sacré. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Un Sauveur est né, Béni soit ce jour</strong> </p>\n<p><strong>Il est le Sauveur, des enfants du Seigneur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai suivi, les bergers </p>\n<p>Ils m&#39;ont montré le chemin </p>\n<p>Et j&#39;ai vu l&#39;enfant couché </p>\n<p>Sous un lit de froid </p>\n<p>Marie, sa mère Lui sourit </p>\n<p>Émue, émerveillé et caresse des yeux </p>\n<p>L&#39;enfant qui lui vient de Dieu. </p>\n<p>Un Sauveur est né, béni soit ce jour </p>\n<p>Il est le Sauveur, des enfants du Seigneur. </p>\n','Un-enfant-est-ne',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1850,'34ce588e-0330-4b67-ba2b-d2ccb9303aae',0,0,'Épargne-les Seigneur',34,'34_EE_fr','1.\nTous ceux que je courtois, ne veulent pas écouter\nEntendre la douce voix.  Qui voudrait leur parler\nLa vie est bien trop courte.  Nul ne sait si demain\nQu\'il ne prendra la route.  Qui marque leur destin.\n\nRefrain\nÉpargne-les Seigneur,  Épargne-les\nC\'est le cri de mon coeur, Épargne-les.\nÉpargne-les mon Dieu, Épargne-les\nOh je serais heureux de les voir aux cieux.\n\n2.\nPrisonniers de la terre. Qu\'ils sont là si nombreux,\nLe péché qu\'ils préfèrent.  A la bonté de Dieu','<p><strong>1.</strong> </p>\n<p>Tous ceux que je courtois, ne veulent pas écouter </p>\n<p>Entendre la douce voix.  Qui voudrait leur parler </p>\n<p>La vie est bien trop courte.  Nul ne sait si demain </p>\n<p>Qu&#39;il ne prendra la route.  Qui marque leur destin. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Épargne-les Seigneur,  Épargne-les</strong> </p>\n<p><strong>C&#39;est le cri de mon coeur, Épargne-les.</strong> </p>\n<p><strong>Épargne-les mon Dieu, Épargne-les</strong> </p>\n<p><strong>Oh je serais heureux de les voir aux cieux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Prisonniers de la terre. Qu&#39;ils sont là si nombreux, </p>\n<p>Le péché qu&#39;ils préfèrent.  A la bonté de Dieu </p>\n','Epargne-les-Seigneur',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1851,'b53c994e-5925-4c72-ac67-7f518b96aae5',0,0,'Ouapé mandé-m pouki',75,'75_EE_ht','1.\nOuapé mandé pouki nou kontan konsa\nNou kontan vré paské nou gin Jézu-Kri\nSé gnou Zanmi ki papé jan-m blié nou\nNan la détrès, nan maladi, \nNan la doulè.   (bis 2 lign)\n\nKè:\nJézu lavé-m, li délivré-m\nLi rétiré-m anba vié péché koté mouin té yé.\n(kè 2 foua)\n\n2.\nMè byin-émé an nou chanté pou Jézu\nLa jénès an nou loué pou Létènèl\nMè frè é sè an nou pa dékourajé (bis 2 lign)\nAn nou kinbé fè-m \nJouk Jézu vini nap kinbé la foua.\n\nKè:\nJézu bon, Jézu bon, Jézu…\nMouin santi-L nan kè mouin, Jézu bon…\nMouin santi-L nan tèt mouin,\nMouin santi-L nan tout kò mouin,\nMouin santi-L nan pié mouin, Jézu bon…\nJézu bon, Jézu bon…Jézu……','<p><strong>1.</strong> </p>\n<p>Ouapé mandé pouki nou kontan konsa </p>\n<p>Nou kontan vré paské nou gin Jézu-Kri </p>\n<p>Sé gnou Zanmi ki papé jan-m blié nou </p>\n<p>Nan la détrès, nan maladi, </p>\n<p>Nan la doulè.   (bis 2 lign) </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu lavé-m, li délivré-m</strong> </p>\n<p><strong>Li rétiré-m anba vié péché koté mouin té yé.</strong> </p>\n<p><strong>(kè 2 foua)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mè byin-émé an nou chanté pou Jézu </p>\n<p>La jénès an nou loué pou Létènèl </p>\n<p>Mè frè é sè an nou pa dékourajé (bis 2 lign) </p>\n<p>An nou kinbé fè-m </p>\n<p>Jouk Jézu vini nap kinbé la foua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu bon, Jézu bon, Jézu…</strong> </p>\n<p><strong>Mouin santi-L nan kè mouin, Jézu bon…</strong> </p>\n<p><strong>Mouin santi-L nan tèt mouin,</strong> </p>\n<p><strong>Mouin santi-L nan tout kò mouin,</strong> </p>\n<p><strong>Mouin santi-L nan pié mouin, Jézu bon…</strong> </p>\n<p><strong>Jézu bon, Jézu bon…Jézu……</strong> </p>\n','Ouape-mande-m-pouki',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1852,'b6d8bc20-bfee-4879-9015-dd5dda1f0651',0,0,'Racheté! Que j\'aime à l\'annoncer',14,'14_MJ_fr','1.\nRacheté! Que j\'aime à l\'annoncer,\nLavé dans le sang de l\'Agneau,\nMes péchés noirs sont tous effacés,\nJe suis Son fils, né de nouveau.\n\nChoeur:\nSauvé!  Sauvé!\nLavé dans le sang de l\'Agneau,\nSauvé!  Sauvé!\nLavé dans le sang de l\'Agneau.\n\n2.\nRacheté, joyeux dans le Seigneur,\nQui peux raconter mon bonheur?\nLa présence de mon Rédempteur\nEst la lumière de mon coeur.\n\n3.\nJ\'adore Christ Jésus, mon Sauveur,\nJe Le glorifie toujours,\nMon thème est l\'amour de mon Seigneur,\nMon espoir, Son prochain retour.\n\n4.\nEt je Le verrai dans Sa beauté,\nMon Roi, qui fait tout mon délice;\nLui qui prépare dans Sa cité\nUne place pour moi, Son fils.','<p><strong>1.</strong> </p>\n<p>Racheté! Que j&#39;aime à l&#39;annoncer, </p>\n<p>Lavé dans le sang de l&#39;Agneau, </p>\n<p>Mes péchés noirs sont tous effacés, </p>\n<p>Je suis Son fils, né de nouveau. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Sauvé!  Sauvé!</strong> </p>\n<p><strong>Lavé dans le sang de l&#39;Agneau,</strong> </p>\n<p><strong>Sauvé!  Sauvé!</strong> </p>\n<p><strong>Lavé dans le sang de l&#39;Agneau.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Racheté, joyeux dans le Seigneur, </p>\n<p>Qui peux raconter mon bonheur? </p>\n<p>La présence de mon Rédempteur </p>\n<p>Est la lumière de mon coeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;adore Christ Jésus, mon Sauveur, </p>\n<p>Je Le glorifie toujours, </p>\n<p>Mon thème est l&#39;amour de mon Seigneur, </p>\n<p>Mon espoir, Son prochain retour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et je Le verrai dans Sa beauté, </p>\n<p>Mon Roi, qui fait tout mon délice; </p>\n<p>Lui qui prépare dans Sa cité </p>\n<p>Une place pour moi, Son fils. </p>\n','Rachete!-Que-j\'aime-a-l\'annoncer',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1853,'7c972d54-77f8-414b-aa33-ad9dda6b8da3',0,0,'Partout sur la terre',57,'57_MJ_fr','1.\nPartout sur la terre\nPortant le salut,\nTous les rachetés\nMarchent avec Jésus,\nAnnonçant au monde\nLe Sauveur puissant.\nLeur cri de victoire:\n«Pour Christ, en avant!»\n\nChoeur:\nOui, partout avec Christ,\nSans crainte et sans peur\nProclamons le salut\nDe notre Sauveur!\n\n2.\nPartout dans le monde\nBeaucoup de pécheurs,\nPour la délivrance,\nViennent au Sauveur;\nJésus les pardonne,\nTransforme leur coeur,\nA chacun Il donne\nL\'éternel bonheur.\n\n3.\nPartout sur la terre\nBénissons Son nom,\nRacontons Sa gloire\nParmi les nations;\nAu pécheur rebelle,\nPartout, proclamons\nUne vie nouvelle,\nUn joyeux pardon!','<p><strong>1.</strong> </p>\n<p>Partout sur la terre </p>\n<p>Portant le salut, </p>\n<p>Tous les rachetés </p>\n<p>Marchent avec Jésus, </p>\n<p>Annonçant au monde </p>\n<p>Le Sauveur puissant. </p>\n<p>Leur cri de victoire: </p>\n<p>«Pour Christ, en avant!» </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, partout avec Christ,</strong> </p>\n<p><strong>Sans crainte et sans peur</strong> </p>\n<p><strong>Proclamons le salut</strong> </p>\n<p><strong>De notre Sauveur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Partout dans le monde </p>\n<p>Beaucoup de pécheurs, </p>\n<p>Pour la délivrance, </p>\n<p>Viennent au Sauveur; </p>\n<p>Jésus les pardonne, </p>\n<p>Transforme leur coeur, </p>\n<p>A chacun Il donne </p>\n<p>L&#39;éternel bonheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Partout sur la terre </p>\n<p>Bénissons Son nom, </p>\n<p>Racontons Sa gloire </p>\n<p>Parmi les nations; </p>\n<p>Au pécheur rebelle, </p>\n<p>Partout, proclamons </p>\n<p>Une vie nouvelle, </p>\n<p>Un joyeux pardon! </p>\n','Partout-sur-la-terre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1854,'81acb56c-5482-4742-9b96-9c6414f388bb',0,0,'Quand je craindrais',34,'34_VR_fr','1.	\nQuand je craindrais sur la route,\nSeigneur, tiens ma main!\nQuand m\'assaillira le doute,\nSeigneur, tiens ma main.\n\nChoeur\nSeigneur, tiens ma main  (bis)\nOh! Sauveur incomparable,\nToujours tiens ma main.\n\n2.	\nQuand le péché m\'environne\nSeigneur, tiens ma main\nLa victoire tu la donne\nSeigneur, tiens ma main.\n\n3.	\nDans la joie ou la tristesse\nSeigneur, tiens ma main\nDans la paix ou la détresse\nSeigneur, tiens ma main.\n\n4.	\nDans la nuit d\'épreuve amère\nSeigneur, tiens ma main\nConduis-moi vers la lumière\nSeigneur, tiens ma main.\n\n5.	\nJe verrai ta main meurtrie\nPour toujours là-haut\nDans la céleste patrie\nOh! Divin Agneau.','<p><strong>1.</strong> </p>\n<p>Quand je craindrais sur la route, </p>\n<p>Seigneur, tiens ma main! </p>\n<p>Quand m&#39;assaillira le doute, </p>\n<p>Seigneur, tiens ma main. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Seigneur, tiens ma main  (bis)</strong> </p>\n<p><strong>Oh! Sauveur incomparable,</strong> </p>\n<p><strong>Toujours tiens ma main.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand le péché m&#39;environne </p>\n<p>Seigneur, tiens ma main </p>\n<p>La victoire tu la donne </p>\n<p>Seigneur, tiens ma main. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans la joie ou la tristesse </p>\n<p>Seigneur, tiens ma main </p>\n<p>Dans la paix ou la détresse </p>\n<p>Seigneur, tiens ma main. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dans la nuit d&#39;épreuve amère </p>\n<p>Seigneur, tiens ma main </p>\n<p>Conduis-moi vers la lumière </p>\n<p>Seigneur, tiens ma main. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Je verrai ta main meurtrie </p>\n<p>Pour toujours là-haut </p>\n<p>Dans la céleste patrie </p>\n<p>Oh! Divin Agneau. </p>\n','Quand-je-craindrais',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1855,'e9260c8e-e88d-4b04-bbd5-603a2aa33193',0,0,'Travaillons tous pour',28,'28_VR_fr','1.	\nTravaillons tous pour notre Maître\nDonnons lui tout notre coeur\nSoyons prêts à son service\nRépondons à son appel\nTous en Lui soyons unis\nPour le grand travail du ciel\nAnnonçons cette nouvelle\nLe salut de Dieu partout.\n\nChoeur\nTravaillons tous pour notre Maître\nC\'est notre but pour toujours\nAllons y sous ses ordres\nMontrons le Sauveur aux autres\nVoici l\'appel du Maître\nNotre divin Capitaine\nAllons donc en avant toujours\nAvec amour et vigueur.\n\n2.	\nSur le chemin du combat\nMarchons toujours par la foi\nEt nous voulons le servir\nSans condition obéir\nIl nous a donné l\'exemple.\nD\'un soldat fidèle et ferme\nSuivons donc à notre Maître\nDans la vie et dans la mort.\n\n3.	\nBientôt Jésus notre Maître\nViendra pour nous recevoir\nDans la maison de son Père\nPour toujours sa face et voir\nEt si nous étions ici-bas\nFidèle à le servir\nNous aurons la récompense\nDans la gloire un de ces jours.','<p><strong>1.</strong> </p>\n<p>Travaillons tous pour notre Maître </p>\n<p>Donnons lui tout notre coeur </p>\n<p>Soyons prêts à son service </p>\n<p>Répondons à son appel </p>\n<p>Tous en Lui soyons unis </p>\n<p>Pour le grand travail du ciel </p>\n<p>Annonçons cette nouvelle </p>\n<p>Le salut de Dieu partout. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Travaillons tous pour notre Maître</strong> </p>\n<p><strong>C&#39;est notre but pour toujours</strong> </p>\n<p><strong>Allons y sous ses ordres</strong> </p>\n<p><strong>Montrons le Sauveur aux autres</strong> </p>\n<p><strong>Voici l&#39;appel du Maître</strong> </p>\n<p><strong>Notre divin Capitaine</strong> </p>\n<p><strong>Allons donc en avant toujours</strong> </p>\n<p><strong>Avec amour et vigueur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sur le chemin du combat </p>\n<p>Marchons toujours par la foi </p>\n<p>Et nous voulons le servir </p>\n<p>Sans condition obéir </p>\n<p>Il nous a donné l&#39;exemple. </p>\n<p>D&#39;un soldat fidèle et ferme </p>\n<p>Suivons donc à notre Maître </p>\n<p>Dans la vie et dans la mort. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientôt Jésus notre Maître </p>\n<p>Viendra pour nous recevoir </p>\n<p>Dans la maison de son Père </p>\n<p>Pour toujours sa face et voir </p>\n<p>Et si nous étions ici-bas </p>\n<p>Fidèle à le servir </p>\n<p>Nous aurons la récompense </p>\n<p>Dans la gloire un de ces jours. </p>\n','Travaillons-tous-pour',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1856,'6a4809b5-8042-4361-baab-9d5449a6733d',0,0,'Sou gnou mòn abandoné',2,'2_VR_ht','1.	\nSou gnou mò-n abandon-né\nGnou vié koua té planté\nKoté brigan yo gin pou mouri\nMin sé sou min-m koua sa-a\nJézu mouri pou mouin\nPou-m kapab patajé tout gloua.\n\nKè:\nYo té kloué Jézu sou la koua\nÉ sé la li  té mouri pou mouin\nMapé viv nan gloua Li sou tè sa\nÉ gnou jou ma va ouè Li nan sièl.\n\n2.	\nPou tou sa kab rivé\nSou la koua nan jou sa \nMa va toujou gin rékonésans\nPaské tout Jézu fè\nLi té fè-l nan plas mouin\nPou-L ban mouin la vi an abondans.\n\n3.	\nKòm yo touyé Jézu\nKité pitit Bon-Dié\nÉ yo pa vlé ouè mouin min’m non plus\nMin mouin pito suiv Li\nNan tout sa kab rivé\nPasé tout sa-m ka jouin-n sou tè sa a.\n\n4.	\nMouin té pèdi nan boua\nMouin té sal ak péché-m\nMouin pat jouin-n pèson vi-n ban-m la min\nÉ mouin té fi-n pèdi\nJézu ban mouin chémin rout sièl la.','<p><strong>1.</strong> </p>\n<p>Sou gnou mò-n abandon-né </p>\n<p>Gnou vié koua té planté </p>\n<p>Koté brigan yo gin pou mouri </p>\n<p>Min sé sou min-m koua sa-a </p>\n<p>Jézu mouri pou mouin </p>\n<p>Pou-m kapab patajé tout gloua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Yo té kloué Jézu sou la koua</strong> </p>\n<p><strong>É sé la li  té mouri pou mouin</strong> </p>\n<p><strong>Mapé viv nan gloua Li sou tè sa</strong> </p>\n<p><strong>É gnou jou ma va ouè Li nan sièl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou tou sa kab rivé </p>\n<p>Sou la koua nan jou sa </p>\n<p>Ma va toujou gin rékonésans </p>\n<p>Paské tout Jézu fè </p>\n<p>Li té fè-l nan plas mouin </p>\n<p>Pou-L ban mouin la vi an abondans. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kòm yo touyé Jézu </p>\n<p>Kité pitit Bon-Dié </p>\n<p>É yo pa vlé ouè mouin min’m non plus </p>\n<p>Min mouin pito suiv Li </p>\n<p>Nan tout sa kab rivé </p>\n<p>Pasé tout sa-m ka jouin-n sou tè sa a. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin té pèdi nan boua </p>\n<p>Mouin té sal ak péché-m </p>\n<p>Mouin pat jouin-n pèson vi-n ban-m la min </p>\n<p>É mouin té fi-n pèdi </p>\n<p>Jézu ban mouin chémin rout sièl la. </p>\n','Sou-gnou-mon-abandone',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1857,'6495f8ac-1f04-483c-b068-8d0f1bc91247',0,0,'Lè la vi-m sanblé tris',2,'2_EE_ht','1.\nLè la vi-m sanblé tris, lè-m chajé ak problèm\nSé sou jenou-m mouin kap jouin-n la joua\nPèson-n sou la tè sa pa kapab édé mouin\nSèlman Ségnè-m kap fè tout pou mouin.\n\nKè:\nOu min-m ki di frapé é ou va louvri\nOu min-m kap vi-n chèché-m pou mal nan sièl\nSonjé mouin chè Jézu nan rout  la vi sa\nOh sonjé mouin, sonjé mouin souplé.\n\n2.\nTout bagay sou la tè va pasé zanmi mouin\nParòl Bon-Dieu ki va la toujou\nOu min-m ki toujou gin tan vini jouin-n Li\nFè atansion pou-l pa ta pou ou.\n\n3.\nAné yo pasé vit tan yo fini si kout\nSign yo vlé ki ké ou ap vini\nOu min-m kap sèvi Kris an nou suiv Li chak jou\nTa lè konsa lap vi-n chèché ou.','<p><strong>1.</strong> </p>\n<p>Lè la vi-m sanblé tris, lè-m chajé ak problèm </p>\n<p>Sé sou jenou-m mouin kap jouin-n la joua </p>\n<p>Pèson-n sou la tè sa pa kapab édé mouin </p>\n<p>Sèlman Ségnè-m kap fè tout pou mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ou min-m ki di frapé é ou va louvri</strong> </p>\n<p><strong>Ou min-m kap vi-n chèché-m pou mal nan sièl</strong> </p>\n<p><strong>Sonjé mouin chè Jézu nan rout  la vi sa</strong> </p>\n<p><strong>Oh sonjé mouin, sonjé mouin souplé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout bagay sou la tè va pasé zanmi mouin </p>\n<p>Paròl Bon-Dieu ki va la toujou </p>\n<p>Ou min-m ki toujou gin tan vini jouin-n Li </p>\n<p>Fè atansion pou-l pa ta pou ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ané yo pasé vit tan yo fini si kout </p>\n<p>Sign yo vlé ki ké ou ap vini </p>\n<p>Ou min-m kap sèvi Kris an nou suiv Li chak jou </p>\n<p>Ta lè konsa lap vi-n chèché ou. </p>\n','Le-la-vi-m-sanble-tris',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1858,'cecb4739-d7ee-4367-a14c-7069b1644b45',0,0,'Jé tout moun va ouè',65,'65_EE_ht','1.\nJé tout moun va ouè\nYo tout pral tandé\nLa voua sin zanj yo\nLè Jézu va tounin\nChak jénou va fléchi\nÉ chack lang konfésé, Li sé Ségnè\nLè-L tounin, lè-L tounin.\n\nKè:\nKi va planté pié Li fèm sou mò-n Sion-an\nÉ tout moun ki té mouri\nÉ kouè nan Li va lévé\nÉ-m va gnoun pou pran pa\nNan prémié rézurèksion\nLè-L tounin, lè Jézu va tounin.\n\n2.\nTout moun va konnin\nKé sé Li vré ki sèl Sovè ya\nLè-L tounin, lè-L tounin\nTout chanjé, transfòmé\nM-a lévé, lè-L rélé-m, é mapé tan-n Li\nLè-L tounin, lè-L tounin.','<p><strong>1.</strong> </p>\n<p>Jé tout moun va ouè </p>\n<p>Yo tout pral tandé </p>\n<p>La voua sin zanj yo </p>\n<p>Lè Jézu va tounin </p>\n<p>Chak jénou va fléchi </p>\n<p>É chack lang konfésé, Li sé Ségnè </p>\n<p>Lè-L tounin, lè-L tounin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ki va planté pié Li fèm sou mò-n Sion-an</strong> </p>\n<p><strong>É tout moun ki té mouri</strong> </p>\n<p><strong>É kouè nan Li va lévé</strong> </p>\n<p><strong>É-m va gnoun pou pran pa</strong> </p>\n<p><strong>Nan prémié rézurèksion</strong> </p>\n<p><strong>Lè-L tounin, lè Jézu va tounin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout moun va konnin </p>\n<p>Ké sé Li vré ki sèl Sovè ya </p>\n<p>Lè-L tounin, lè-L tounin </p>\n<p>Tout chanjé, transfòmé </p>\n<p>M-a lévé, lè-L rélé-m, é mapé tan-n Li </p>\n<p>Lè-L tounin, lè-L tounin. </p>\n','Je-tout-moun-va-oue',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1859,'7ab3d480-5dda-456e-a45d-c25c5870a5e6',0,0,'Jézu sé ou sèl ki fòs',66,'66_EE_ht','1.\nJézu sé ou sèl ki fòs mouin\nPouki sa ou abandoné mouin\nChak jou-m lévé mapé krié\nLouvri jé ou pou ouè mizè mouin.\n\nKè:\nNan ou min-m ki gingnin la vi\nPouki sa mouin min-m map souri\nKounié-a mouin prèt pou-m mouri\nPa kité-m péri souplé.\n\n2.\nDlo nan jé-m sèvi-m nouriti\nÉ-m konnin ou gin tout pouvoua\nRétiré sak pa bon nan mouin\nPou-m kapab sèvi ou Ségnè.\n\n3.\nSi sé péché-m ki fè map soufri\nMouin vini mandé ou padon\nO bon Sovè di gnou mo pou mouin\nPou-m pa ront dévan énmi-m yo.\n\n4.\nZanmi mouin Jézu ap rélé ou\nDé bra Li louvri lap tan-n ou\nPa kité Satan touyé ou\nVini Li va ba ou la vi.','<p><strong>1.</strong> </p>\n<p>Jézu sé ou sèl ki fòs mouin </p>\n<p>Pouki sa ou abandoné mouin </p>\n<p>Chak jou-m lévé mapé krié </p>\n<p>Louvri jé ou pou ouè mizè mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan ou min-m ki gingnin la vi</strong> </p>\n<p><strong>Pouki sa mouin min-m map souri</strong> </p>\n<p><strong>Kounié-a mouin prèt pou-m mouri</strong> </p>\n<p><strong>Pa kité-m péri souplé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dlo nan jé-m sèvi-m nouriti </p>\n<p>É-m konnin ou gin tout pouvoua </p>\n<p>Rétiré sak pa bon nan mouin </p>\n<p>Pou-m kapab sèvi ou Ségnè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si sé péché-m ki fè map soufri </p>\n<p>Mouin vini mandé ou padon </p>\n<p>O bon Sovè di gnou mo pou mouin </p>\n<p>Pou-m pa ront dévan énmi-m yo. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zanmi mouin Jézu ap rélé ou </p>\n<p>Dé bra Li louvri lap tan-n ou </p>\n<p>Pa kité Satan touyé ou </p>\n<p>Vini Li va ba ou la vi. </p>\n','Jezu-se-ou-sel-ki-fos',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1860,'53aa603d-e68a-4c52-a396-b3149b2f78e9',0,0,'À de nouveaux combats',17,'17_MJ_fr','1.\nÀ de nouveaux combats,\nJésus Tu nous appelles,\nEt nous voici fidèles,\nPrêts à suivre Tes pas.\nConduis-nous aux combats,\nNous sommes tes soldats.\n\nChoeur:\nArme nos bras, arme nos coeurs,\nEt nous seront\nPlus que vainqueurs (bis 2 lignes)\nArme nos bras, arme nos coeurs.\n\n2.\nArme-nous, ô Seigneur,\nDe foi, de hardiesse!\nDe force, de sagesse,\nD\'amour et de ferveur;\nEmbrase-nous, Seigneur,\nD\'une invincible ardeur.\n\n3.\nNous tiendrons ferme et haut\nLa croix, notre bannière,\nPour vaincre l\'adversaire\nPar le sang de l\'Agneau,\nOui, nous tiendrons bien haut\nLa croix, notre drapeau.\n\n4.\nLuttons, prions, souffrons!\nNous aurons la victoire;\nLa couronne de gloire\nUn jour ceindra nos fronts.\nLuttons, prions, souffrons!\nBientôt nous règnerons!','<p><strong>1.</strong> </p>\n<p>À de nouveaux combats, </p>\n<p>Jésus Tu nous appelles, </p>\n<p>Et nous voici fidèles, </p>\n<p>Prêts à suivre Tes pas. </p>\n<p>Conduis-nous aux combats, </p>\n<p>Nous sommes tes soldats. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Arme nos bras, arme nos coeurs,</strong> </p>\n<p><strong>Et nous seront</strong> </p>\n<p><strong>Plus que vainqueurs (bis 2 lignes)</strong> </p>\n<p><strong>Arme nos bras, arme nos coeurs.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Arme-nous, ô Seigneur, </p>\n<p>De foi, de hardiesse! </p>\n<p>De force, de sagesse, </p>\n<p>D&#39;amour et de ferveur; </p>\n<p>Embrase-nous, Seigneur, </p>\n<p>D&#39;une invincible ardeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous tiendrons ferme et haut </p>\n<p>La croix, notre bannière, </p>\n<p>Pour vaincre l&#39;adversaire </p>\n<p>Par le sang de l&#39;Agneau, </p>\n<p>Oui, nous tiendrons bien haut </p>\n<p>La croix, notre drapeau. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Luttons, prions, souffrons! </p>\n<p>Nous aurons la victoire; </p>\n<p>La couronne de gloire </p>\n<p>Un jour ceindra nos fronts. </p>\n<p>Luttons, prions, souffrons! </p>\n<p>Bientôt nous règnerons! </p>\n','A-de-nouveaux-combats',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1861,'0d8bcf8b-4b63-4080-bd6c-0948259536e8',0,0,'Tu voulus de la paix, la foi tu désirais',22,'22_MJ_fr','1.\nTu voulus de la paix, la foi tu désirais,\nEt avec ferveur tu as prié.\nMais tu ne peux d\'en haut savourer le repos\nSi tout n\'est sur l\'autel déposé.\n\n\n\nChoeur:\nToute ta vie est-elle sur l\'autel divin?\nTon coeur gardé par l\'Esprit-Saint?\nTu ne peux du Seigneur obtenir la  faveur\nQu\'en Lui cédant ta vie et ton coeur.\n\n2.\nVeux-tu garder toujours Ses préceptes d\'amour\nEt la douce paix des rachetés?\nTu dois Lui obéir dans ton moindre désir\nEt sur l\'autel, ton tout déposé.\n\n3.\nNous ne pouvons savoir ce que le Dieu de gloire\nVeut procurer à Ses bien-aimés;\nSi toute notre vie: le corps, l\'âme et L\'esprit,\nN\'est pas sur Son autel déposé.','<p><strong>1.</strong> </p>\n<p>Tu voulus de la paix, la foi tu désirais, </p>\n<p>Et avec ferveur tu as prié. </p>\n<p>Mais tu ne peux d&#39;en haut savourer le repos </p>\n<p>Si tout n&#39;est sur l&#39;autel déposé. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Toute ta vie est-elle sur l&#39;autel divin?</strong> </p>\n<p><strong>Ton coeur gardé par l&#39;Esprit-Saint?</strong> </p>\n<p><strong>Tu ne peux du Seigneur obtenir la  faveur</strong> </p>\n<p><strong>Qu&#39;en Lui cédant ta vie et ton coeur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Veux-tu garder toujours Ses préceptes d&#39;amour </p>\n<p>Et la douce paix des rachetés? </p>\n<p>Tu dois Lui obéir dans ton moindre désir </p>\n<p>Et sur l&#39;autel, ton tout déposé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous ne pouvons savoir ce que le Dieu de gloire </p>\n<p>Veut procurer à Ses bien-aimés; </p>\n<p>Si toute notre vie: le corps, l&#39;âme et L&#39;esprit, </p>\n<p>N&#39;est pas sur Son autel déposé. </p>\n','Tu-voulus-de-la-paix-la-foi-tu-desirais',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1862,'b1d81ec4-22c3-49f4-8bed-0add19db5c6e',0,0,'Dieu tout puissant',27,'27_MJ_fr','1.\nDieu tout puissant\nQuand mon coeur considère\nTout l\'univers\nCréé par Ton pouvoir;\nLe ciel d\'azur,\nLes éclairs, la tonnerre\nLe clair matin\nOu les ombres du soir…\nDe tout mon être,\nAlors, s\'élève un chant:\n«Dieu tout puissant,\nQue Tu est grand!» (bis 4 lignes)\n\n2.\nMais quand je songe,\nO sublime mystère!\nQu\'un Dieu si grand\nA pu penser à moi;\nQue Son cher Fils\nEst devenu mon frère,\nEt que je suis\nL\'héritier du grand Roi…\nAlors mon coeur\nRedit, la nuit, le jour:\n«Que Tu es bon,\nO Dieu d\'amour!» (bis 4 lignes)\n\n3.\nQuand mon Sauveur,\nÉclatant de lumière,\nSe lèvera de Son trône éternel,\nEt que, laissant\nLes douleurs de la terre,\nJe pourrai voir\nLes splendeurs de Son ciel…\nJe redirai, dans Son divin séjour:\nRien n\'est plus grand\nQue Ton amour! (bis 3 lignes)','<p><strong>1.</strong> </p>\n<p>Dieu tout puissant </p>\n<p>Quand mon coeur considère </p>\n<p>Tout l&#39;univers </p>\n<p>Créé par Ton pouvoir; </p>\n<p>Le ciel d&#39;azur, </p>\n<p>Les éclairs, la tonnerre </p>\n<p>Le clair matin </p>\n<p>Ou les ombres du soir… </p>\n<p>De tout mon être, </p>\n<p>Alors, s&#39;élève un chant: </p>\n<p>«Dieu tout puissant, </p>\n<p>Que Tu est grand!» (bis 4 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais quand je songe, </p>\n<p>O sublime mystère! </p>\n<p>Qu&#39;un Dieu si grand </p>\n<p>A pu penser à moi; </p>\n<p>Que Son cher Fils </p>\n<p>Est devenu mon frère, </p>\n<p>Et que je suis </p>\n<p>L&#39;héritier du grand Roi… </p>\n<p>Alors mon coeur </p>\n<p>Redit, la nuit, le jour: </p>\n<p>«Que Tu es bon, </p>\n<p>O Dieu d&#39;amour!» (bis 4 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand mon Sauveur, </p>\n<p>Éclatant de lumière, </p>\n<p>Se lèvera de Son trône éternel, </p>\n<p>Et que, laissant </p>\n<p>Les douleurs de la terre, </p>\n<p>Je pourrai voir </p>\n<p>Les splendeurs de Son ciel… </p>\n<p>Je redirai, dans Son divin séjour: </p>\n<p>Rien n&#39;est plus grand </p>\n<p>Que Ton amour! (bis 3 lignes) </p>\n','Dieu-tout-puissant',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1863,'f9b5e4e5-8c3d-43f4-9d21-0d4b293a80bd',0,0,'Car Dieu m\'a tant aimé',54,'54_MJ_fr','1.\nCar Dieu m\'a tant aimé\nQu\'Il a pour moi donné\nSon Fils pour me sauver\nDe la mort, du péché!\nMaintenant, quel bonheur\nGloire à mon Libérateur!\n\n\n2.\nGardé par Son amour,\nJe vais de jour en jour,\nJoyeux, chantant toujours,\nAttendant Son Retour.\nMaintenant, quel bonheur!\nJésus vit dans mon coeur!\nGloire à mon puissant Sauveur.\n\n\n3.\nBientôt, alléluia!\nEnfin Il reviendra.\nJésus m\'enlèvera,\nAvec Lui me prendra.\nAlors, parfait bonheur,\nJésus sera Vainqueur.\nGloire à mon Grand Triomphateur.','<p><strong>1.</strong> </p>\n<p>Car Dieu m&#39;a tant aimé </p>\n<p>Qu&#39;Il a pour moi donné </p>\n<p>Son Fils pour me sauver </p>\n<p>De la mort, du péché! </p>\n<p>Maintenant, quel bonheur </p>\n<p>Gloire à mon Libérateur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gardé par Son amour, </p>\n<p>Je vais de jour en jour, </p>\n<p>Joyeux, chantant toujours, </p>\n<p>Attendant Son Retour. </p>\n<p>Maintenant, quel bonheur! </p>\n<p>Jésus vit dans mon coeur! </p>\n<p>Gloire à mon puissant Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientôt, alléluia! </p>\n<p>Enfin Il reviendra. </p>\n<p>Jésus m&#39;enlèvera, </p>\n<p>Avec Lui me prendra. </p>\n<p>Alors, parfait bonheur, </p>\n<p>Jésus sera Vainqueur. </p>\n<p>Gloire à mon Grand Triomphateur. </p>\n','Car-Dieu-m\'a-tant-aime',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1864,'4259a181-a06e-4687-81d1-9909fbaf4466',0,0,'Si nou sonjé lè nou té',8,'8_EE_ht','1.\nSi nou sonjé lè nou té nan lé mon-n\nNou té nan ésklavaj nou pat gingnin éspoua\nNou té tankou gnou brébi égaré\nKi pat gin paturaj\nDiab la té minnin nou\nNan tou sa ki pa bon\nMin Jézu nan amou-L té vi-n mouri pou nou\nÉ Li té rachté nou anba malédiksion\nKounyé-a nap chanté pou nou kap ba Li gloua\nPaské Li délivré nan-m nou anba péché.\n\nKè:\nSi ou gingnin problèm pandan nap chanté la\nLévé min ou anlè, problèm ou va rézoud, Jézu\nJézu, problèm yo nan min Ou\nProblèm spirituèl, problèm santimantal\nProblèm matérièl, yo tout yo nan min Ou\nGin moun ki san manman,\nGin moun ki san papa\nSé òfélin yo yé, yo tout yo nan min Ou\nJézu problèm yo nan min Ou\nSi ou gingnin la foua ou mèt désan-n min ou\nO non dé Jézu problèm ou yo va rézoud, Jézu\n\n2.\nLé mon-n alèz, yo di yo san problèm\nMin yo tout pè mouri\nPaské vi yo pa pròp\nNou min-m krétyin nou kon-n gin ti problèm\nMin nou pa pè angnin\nPaské Bon-Dieu nou-an sé matématisi-in\nSé Li-k kréyé lé mon-n ak tou sa ki ladan-l\nLi kon-n géri malad, Li kon-n rézoud problèm\nNou pa kab komparé grandè Bon-Dieu nou-an.','<p><strong>1.</strong> </p>\n<p>Si nou sonjé lè nou té nan lé mon-n </p>\n<p>Nou té nan ésklavaj nou pat gingnin éspoua </p>\n<p>Nou té tankou gnou brébi égaré </p>\n<p>Ki pat gin paturaj </p>\n<p>Diab la té minnin nou </p>\n<p>Nan tou sa ki pa bon </p>\n<p>Min Jézu nan amou-L té vi-n mouri pou nou </p>\n<p>É Li té rachté nou anba malédiksion </p>\n<p>Kounyé-a nap chanté pou nou kap ba Li gloua </p>\n<p>Paské Li délivré nan-m nou anba péché. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Si ou gingnin problèm pandan nap chanté la</strong> </p>\n<p><strong>Lévé min ou anlè, problèm ou va rézoud, Jézu</strong> </p>\n<p><strong>Jézu, problèm yo nan min Ou</strong> </p>\n<p><strong>Problèm spirituèl, problèm santimantal</strong> </p>\n<p><strong>Problèm matérièl, yo tout yo nan min Ou</strong> </p>\n<p><strong>Gin moun ki san manman,</strong> </p>\n<p><strong>Gin moun ki san papa</strong> </p>\n<p><strong>Sé òfélin yo yé, yo tout yo nan min Ou</strong> </p>\n<p><strong>Jézu problèm yo nan min Ou</strong> </p>\n<p><strong>Si ou gingnin la foua ou mèt désan-n min ou</strong> </p>\n<p><strong>O non dé Jézu problèm ou yo va rézoud, Jézu</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mon-n alèz, yo di yo san problèm </p>\n<p>Min yo tout pè mouri </p>\n<p>Paské vi yo pa pròp </p>\n<p>Nou min-m krétyin nou kon-n gin ti problèm </p>\n<p>Min nou pa pè angnin </p>\n<p>Paské Bon-Dieu nou-an sé matématisi-in </p>\n<p>Sé Li-k kréyé lé mon-n ak tou sa ki ladan-l </p>\n<p>Li kon-n géri malad, Li kon-n rézoud problèm </p>\n<p>Nou pa kab komparé grandè Bon-Dieu nou-an. </p>\n','Si-nou-sonje-le-nou-te',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1865,'3e60304a-872d-45af-8bba-ec01f42955d1',0,0,'Un parfait Sauveur',33,'33_EE_fr','1.\nUn parfait Sauveur est ici qui t\'invite\nA te jeter dans ses bras\nD\'un salut parfait, Il t\'acquit le mérite\nTu n\'as qu\'à faire un seul pas.\n\nRefrain\nUn seul pas, un seul pas, et Jésus te recevra\nUn seul pas, jette-toi dans ses bras\nCeux qu\'Il affranchit sont entièrement libres\nTu n\'as qu\'à faire un seul pas.\n\n2.\nC\'est Lui, c\'est Lui seul qui peut briser les chaînes   \nDes esclaves de Satan                   \nDes larmes à ses pieds ne sont jamais vaines\nJésus pardonne à l\'instant\nPense aux pas nombreux qu\'Il fit à ta rencontre\nIl marcha jusqu\'au trépas                 \nMais pour te livrer à l\'amour qu\'Il te montre\nIl ne te reste un seul pas.','<p><strong>1.</strong> </p>\n<p>Un parfait Sauveur est ici qui t&#39;invite </p>\n<p>A te jeter dans ses bras </p>\n<p>D&#39;un salut parfait, Il t&#39;acquit le mérite </p>\n<p>Tu n&#39;as qu&#39;à faire un seul pas. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Un seul pas, un seul pas, et Jésus te recevra</strong> </p>\n<p><strong>Un seul pas, jette-toi dans ses bras</strong> </p>\n<p><strong>Ceux qu&#39;Il affranchit sont entièrement libres</strong> </p>\n<p><strong>Tu n&#39;as qu&#39;à faire un seul pas.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est Lui, c&#39;est Lui seul qui peut briser les chaînes </p>\n<p>Des esclaves de Satan </p>\n<p>Des larmes à ses pieds ne sont jamais vaines </p>\n<p>Jésus pardonne à l&#39;instant </p>\n<p>Pense aux pas nombreux qu&#39;Il fit à ta rencontre </p>\n<p>Il marcha jusqu&#39;au trépas </p>\n<p>Mais pour te livrer à l&#39;amour qu&#39;Il te montre </p>\n<p>Il ne te reste un seul pas. </p>\n','Un-parfait-Sauveur',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1866,'fb51b9ca-808b-418b-8281-6b767fcc0236',0,0,'Nan gran jou la rézurèksion',44,'44_EE_ht','1.\nNan gran jou la rézurèksion\nLè tronpèt Bon-Dieu va son-nin\nNa lévé alélouya, na lévé\nNa rankontré ak Sovè béni\nPi gran éspérans nou gingnin\nNa lévé alélouya, na lévé.\n\nKè:\nNa lévé alélouya, na lévé, amè-n na lévé…\nNan gran jou la rézurèksion\nLè pò prizon la mò krazé\nNa lévé alélouya, na lévé.\n\n2.\nNan gran jou la rézurèksion\nKi pral pi bèl gran réunion\nNa lévé alélouya, na lévé\nAk paran nou, frè nou ak sè nou\nKi té chaché lé vrè padon\nNa lévé alélouya, na lévé.\n\n3.\nNan gran la rézuréksion\nNa va jouin-n ak Kris an lè ya\nNa lévé alélouya, na lévé\nLa poté jouk mouin tou nan la gloua\nÉ tout bagay va aranjé\nNa lévé alélouya, na lévé','<p><strong>1.</strong> </p>\n<p>Nan gran jou la rézurèksion </p>\n<p>Lè tronpèt Bon-Dieu va son-nin </p>\n<p>Na lévé alélouya, na lévé </p>\n<p>Na rankontré ak Sovè béni </p>\n<p>Pi gran éspérans nou gingnin </p>\n<p>Na lévé alélouya, na lévé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Na lévé alélouya, na lévé, amè-n na lévé…</strong> </p>\n<p><strong>Nan gran jou la rézurèksion</strong> </p>\n<p><strong>Lè pò prizon la mò krazé</strong> </p>\n<p><strong>Na lévé alélouya, na lévé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan gran jou la rézurèksion </p>\n<p>Ki pral pi bèl gran réunion </p>\n<p>Na lévé alélouya, na lévé </p>\n<p>Ak paran nou, frè nou ak sè nou </p>\n<p>Ki té chaché lé vrè padon </p>\n<p>Na lévé alélouya, na lévé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan gran la rézuréksion </p>\n<p>Na va jouin-n ak Kris an lè ya </p>\n<p>Na lévé alélouya, na lévé </p>\n<p>La poté jouk mouin tou nan la gloua </p>\n<p>É tout bagay va aranjé </p>\n<p>Na lévé alélouya, na lévé </p>\n','Nan-gran-jou-la-rezureksion',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1867,'d26c87ab-d65f-4a82-b863-f51f627aca1b',0,0,'Dépi-m vi-n jouin-n ak Jézu',49,'49_EE_ht','1.\nDépi-m vi-n jouin-n ak Jézu\nLa vi mouin vi-n chanjé\nMouin pa maché min-m jan ankò\nAnpil zié sé sou mouin\nJézu réstoré la vi-m\nMouin kité lòt ouè sa\nTou vlé suiv min-m tras avèk mouin\nPaské Jézu nan mouin.\n\nKè:\nMap chanté paské-m kontan\nMap chanté paské-L sové-m\nLavi-m chanjé konplètman\nMouin vi-n gnou moun nouvo.\n\n2.\nTout éspoua-m nan la vi sa\nSé pou mal jouin-n Jézu\nKi té mouri pou mouin\nLi ki té ban-m la vi\nJou yo pa tèlman si louin,\nProfési yo ap akonpli\nAn nou luté pou nou ouè Li\nNan gran jou jujman-an.  (bis)','<p><strong>1.</strong> </p>\n<p>Dépi-m vi-n jouin-n ak Jézu </p>\n<p>La vi mouin vi-n chanjé </p>\n<p>Mouin pa maché min-m jan ankò </p>\n<p>Anpil zié sé sou mouin </p>\n<p>Jézu réstoré la vi-m </p>\n<p>Mouin kité lòt ouè sa </p>\n<p>Tou vlé suiv min-m tras avèk mouin </p>\n<p>Paské Jézu nan mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Map chanté paské-m kontan</strong> </p>\n<p><strong>Map chanté paské-L sové-m</strong> </p>\n<p><strong>Lavi-m chanjé konplètman</strong> </p>\n<p><strong>Mouin vi-n gnou moun nouvo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout éspoua-m nan la vi sa </p>\n<p>Sé pou mal jouin-n Jézu </p>\n<p>Ki té mouri pou mouin </p>\n<p>Li ki té ban-m la vi </p>\n<p>Jou yo pa tèlman si louin, </p>\n<p>Profési yo ap akonpli </p>\n<p>An nou luté pou nou ouè Li </p>\n<p>Nan gran jou jujman-an.  (bis) </p>\n','Depi-m-vi-n-jouin-n-ak-Jezu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1868,'d545d81e-7c9b-4fad-bc67-8d5d5e274c48',0,0,'J\'ai pris place dans le train de l\'Évangile',7,'7_MJ_fr','1.\nJ\'ai pris place dans le train de l\'Évangile\nA la gare du péché,\nJe roule sur les rails de la voie facile\nVers la céleste cité.\nMon passeport est le sang de Jésus\nLes frontières sont vaincues.\nJe voyage dans ce train tout plein de joie\nSur la ligne Alléluia!','<p><strong>1.</strong> </p>\n<p>J&#39;ai pris place dans le train de l&#39;Évangile </p>\n<p>A la gare du péché, </p>\n<p>Je roule sur les rails de la voie facile </p>\n<p>Vers la céleste cité. </p>\n<p>Mon passeport est le sang de Jésus </p>\n<p>Les frontières sont vaincues. </p>\n<p>Je voyage dans ce train tout plein de joie </p>\n<p>Sur la ligne Alléluia! </p>\n','J\'ai-pris-place-dans-le-train-de-l\'Evangile',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1869,'ac26c3fb-4f79-4605-a22b-03569bb8e537',0,0,'Viens Saint-Esprit, O sainte colombe',40,'40_MJ_fr','1.\nViens Saint-Esprit, O sainte colombe\nAvec Ton divin feu,\nAllume-moi de Ton amour,\nO Saint-Esprit de Dieu!\n\n2.\nRéchauffe mon coeur dur et froid,\nJuge en moi le péché,\nMensonge, orgueil, paresse et haine\nEt que tout soit brûlé.\n\n3.\nOui, brise-moi, console-moi;\nSois mon seul conducteur,\nFaçonne-moi pour ton service,\nO grand Consolateur!','<p><strong>1.</strong> </p>\n<p>Viens Saint-Esprit, O sainte colombe </p>\n<p>Avec Ton divin feu, </p>\n<p>Allume-moi de Ton amour, </p>\n<p>O Saint-Esprit de Dieu! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Réchauffe mon coeur dur et froid, </p>\n<p>Juge en moi le péché, </p>\n<p>Mensonge, orgueil, paresse et haine </p>\n<p>Et que tout soit brûlé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, brise-moi, console-moi; </p>\n<p>Sois mon seul conducteur, </p>\n<p>Façonne-moi pour ton service, </p>\n<p>O grand Consolateur! </p>\n','Viens-Saint-Esprit-O-sainte-colombe',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1870,'01617bc3-ad43-4b91-8879-a715e6934dc4',0,0,'Quand le mal t\'assaille',52,'52_MJ_fr','1.\nQuand le mal t\'assaille,\nSubtil et caché,\nDresse une muraille,\nDevant le péché;\nAu propos infâme\nNe cède jamais,\nOh! Garde ton âme\nDu monde mauvais.\n\nChoeur:\nCherche l\'appui du Maître,\nSoumets-Lui tout ton être;\nAlors tu pourras être,\nPartout plus que vainqueur.\n\n2.\nRésiste à l\'étreinte\nDe l\'impureté,\nVis toujours sans crainte\nDans la sainteté\nContre tous les vices,\nLutte vaillamment,\nEt qu\'en Christ tu puisses\nVaincre constamment.\n\n3.\nFuis la route sombre\nDu plaisir trompeur,\nCar là git dans l\'ombre\nLe grand tentateur:\nQui perd sa victime,\nEn la conduisant\nVers le sombre abîme,\nL\'enfer effrayant.\n\n4.\nChasse à l\'instant même\nTout regard impur,\nEt que Dieu qui t\'aime\nSoit ton abri sûr;\nQuand, dans ta faiblesse,\nTu crois succomber,\nRegarde sans cesse\nAu Christ, bon Berger.','<p><strong>1.</strong> </p>\n<p>Quand le mal t&#39;assaille, </p>\n<p>Subtil et caché, </p>\n<p>Dresse une muraille, </p>\n<p>Devant le péché; </p>\n<p>Au propos infâme </p>\n<p>Ne cède jamais, </p>\n<p>Oh! Garde ton âme </p>\n<p>Du monde mauvais. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Cherche l&#39;appui du Maître,</strong> </p>\n<p><strong>Soumets-Lui tout ton être;</strong> </p>\n<p><strong>Alors tu pourras être,</strong> </p>\n<p><strong>Partout plus que vainqueur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Résiste à l&#39;étreinte </p>\n<p>De l&#39;impureté, </p>\n<p>Vis toujours sans crainte </p>\n<p>Dans la sainteté </p>\n<p>Contre tous les vices, </p>\n<p>Lutte vaillamment, </p>\n<p>Et qu&#39;en Christ tu puisses </p>\n<p>Vaincre constamment. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fuis la route sombre </p>\n<p>Du plaisir trompeur, </p>\n<p>Car là git dans l&#39;ombre </p>\n<p>Le grand tentateur: </p>\n<p>Qui perd sa victime, </p>\n<p>En la conduisant </p>\n<p>Vers le sombre abîme, </p>\n<p>L&#39;enfer effrayant. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Chasse à l&#39;instant même </p>\n<p>Tout regard impur, </p>\n<p>Et que Dieu qui t&#39;aime </p>\n<p>Soit ton abri sûr; </p>\n<p>Quand, dans ta faiblesse, </p>\n<p>Tu crois succomber, </p>\n<p>Regarde sans cesse </p>\n<p>Au Christ, bon Berger. </p>\n','Quand-le-mal-t\'assaille',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1871,'242cd516-6af6-4bfe-af2f-545b74848713',0,0,'Joie...tout le long du jour',53,'53_MJ_fr','1.\nJoie…tout le long du jour,\nJoie…partout à l\'entour;\nQuand Jésus vient dans un coeur,\nIl tressaille de bonheur,\nEt trouve sa joie,\nDans ce grand amour.','<p><strong>1.</strong> </p>\n<p>Joie…tout le long du jour, </p>\n<p>Joie…partout à l&#39;entour; </p>\n<p>Quand Jésus vient dans un coeur, </p>\n<p>Il tressaille de bonheur, </p>\n<p>Et trouve sa joie, </p>\n<p>Dans ce grand amour. </p>\n','Joie...tout-le-long-du-jour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1872,'9a408aa6-c376-48f1-980d-f91bb46e096a',0,0,'Je n’aurai point de disette',31,'31_VR_fr','1.	\nJe n\'aurai point de disette\nChrist est mon Berger\nSous sa divine houlette\nIl n\'est nul danger\nDans ses riches pâturages\nChaque jour nourrit\nSous des bienfaits ombrages\nJe suis à l\'abri.\n\nChoeur\nQuel Berger tend et fidèle\nGardé par sa main\nLe long du chemin\nJusqu\'en la vie éternelle\nA l\'abri sous son aile\nQuel repos divin.\n\n2.	\nLe long des aimables rives\nDes calmes ruisseaux\nDes paisibles eaux\nIl nourrit mon âme avide\nDu céleste pain\nEn son nom l\'Esprit me guide\nDans un sûr chemin.\n\n3.	\nEt dans la sombre vallée\nIl est mon gardien\nSa grâce m\'est assurée\nJe ne craindrai rien\nSa présence et sa parole\nChassent la frayeur\nEt sa houlette console\nDe tout douleur.\n\n4.	\nAux regards remplis de crainte\nDe mes ennemis\nIl dresse une table sainte\nOù je suis admis\nPar Lui ma couple est remplie\nDivine faveur.\nSon Esprit me sanctifie,\nEt garde mon coeur.\n\n5.	\nSa grande miséricorde\nSon divin secours\nSes riches biens Il m\'accorde\nAinsi touts les jours\nBientôt ineffable, grâce et fidélité\nDans son ciel j\'aurai ma place\nPour l\'éternité.','<p><strong>1.</strong> </p>\n<p>Je n&#39;aurai point de disette </p>\n<p>Christ est mon Berger </p>\n<p>Sous sa divine houlette </p>\n<p>Il n&#39;est nul danger </p>\n<p>Dans ses riches pâturages </p>\n<p>Chaque jour nourrit </p>\n<p>Sous des bienfaits ombrages </p>\n<p>Je suis à l&#39;abri. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Quel Berger tend et fidèle</strong> </p>\n<p><strong>Gardé par sa main</strong> </p>\n<p><strong>Le long du chemin</strong> </p>\n<p><strong>Jusqu&#39;en la vie éternelle</strong> </p>\n<p><strong>A l&#39;abri sous son aile</strong> </p>\n<p><strong>Quel repos divin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le long des aimables rives </p>\n<p>Des calmes ruisseaux </p>\n<p>Des paisibles eaux </p>\n<p>Il nourrit mon âme avide </p>\n<p>Du céleste pain </p>\n<p>En son nom l&#39;Esprit me guide </p>\n<p>Dans un sûr chemin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et dans la sombre vallée </p>\n<p>Il est mon gardien </p>\n<p>Sa grâce m&#39;est assurée </p>\n<p>Je ne craindrai rien </p>\n<p>Sa présence et sa parole </p>\n<p>Chassent la frayeur </p>\n<p>Et sa houlette console </p>\n<p>De tout douleur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Aux regards remplis de crainte </p>\n<p>De mes ennemis </p>\n<p>Il dresse une table sainte </p>\n<p>Où je suis admis </p>\n<p>Par Lui ma couple est remplie </p>\n<p>Divine faveur. </p>\n<p>Son Esprit me sanctifie, </p>\n<p>Et garde mon coeur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Sa grande miséricorde </p>\n<p>Son divin secours </p>\n<p>Ses riches biens Il m&#39;accorde </p>\n<p>Ainsi touts les jours </p>\n<p>Bientôt ineffable, grâce et fidélité </p>\n<p>Dans son ciel j&#39;aurai ma place </p>\n<p>Pour l&#39;éternité. </p>\n','Je-n\'aurai-point-de-disette',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1873,'93c1d4be-5ef8-4920-8fa1-fff06d23fbde',0,0,'La foi',10,'10_VR_fr','1.	\nLa foi fait tomber sous nos yeux,\nLes plus fortes murailles,\nLa foi nous rend victorieux,\nEt gagne les batailles;\nLa foi nous ouvre les trésors,\nDe la toute puissance\nLes plus faibles deviennent forts\nSous sa sainte influence.\n\nChoeur\nPar l\'Esprit du Seigneur\nPar Christ le Rédempteur\nPar notre Dieu Sauveur\nCombattons pleins d\'ardeur.\n\n2.	\nProtégés par ce bouclier,\nNous n\'avons nulle crainte;\nQui sait en Dieu se confier,\nDu mal brave l\'atteinte.\nQue n\'ont pas souffert les héros,\nDont nous suivons la trace\nQue de dangers, que de travaux,\nMais quelle sainte audace\n\n3.	\nQue leur victoire et leurs combats\nEnflammant notre zèle\nCroyons et courons sur leurs pas;\nNotre Chef nous appelle.\nQuand la foi vit au fond du coeur\nEt nourrit l\'espérance\nOn est par l\'amour du Sauveur\nJoyeux dans la souffrance.','<p><strong>1.</strong> </p>\n<p>La foi fait tomber sous nos yeux, </p>\n<p>Les plus fortes murailles, </p>\n<p>La foi nous rend victorieux, </p>\n<p>Et gagne les batailles; </p>\n<p>La foi nous ouvre les trésors, </p>\n<p>De la toute puissance </p>\n<p>Les plus faibles deviennent forts </p>\n<p>Sous sa sainte influence. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Par l&#39;Esprit du Seigneur</strong> </p>\n<p><strong>Par Christ le Rédempteur</strong> </p>\n<p><strong>Par notre Dieu Sauveur</strong> </p>\n<p><strong>Combattons pleins d&#39;ardeur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Protégés par ce bouclier, </p>\n<p>Nous n&#39;avons nulle crainte; </p>\n<p>Qui sait en Dieu se confier, </p>\n<p>Du mal brave l&#39;atteinte. </p>\n<p>Que n&#39;ont pas souffert les héros, </p>\n<p>Dont nous suivons la trace </p>\n<p>Que de dangers, que de travaux, </p>\n<p>Mais quelle sainte audace </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que leur victoire et leurs combats </p>\n<p>Enflammant notre zèle </p>\n<p>Croyons et courons sur leurs pas; </p>\n<p>Notre Chef nous appelle. </p>\n<p>Quand la foi vit au fond du coeur </p>\n<p>Et nourrit l&#39;espérance </p>\n<p>On est par l&#39;amour du Sauveur </p>\n<p>Joyeux dans la souffrance. </p>\n','La-foi',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1874,'b60f0081-9a42-4205-acef-94da2b995a04',0,0,'Que devient mon enfant',40,'40_VR_fr','1.	\nQue devient mon enfant ce soir\nIl suit une sombre voie\nLui qui faisait tout mon espoir\nEt ma couronne de joie.\n\nChoeur\nCherchez mon enfant ce soir\nCherchez mon enfant ce soir\nMon coeur est plein, je veux le revoir\nCherchez on enfant ce soir.\n\n2.	\nTout jeune encore, je crois le voir,\nLorsque, fervent et sincère,\nIl disait à Dieu chaque soir\nSon enfantine prière.\n\n3.	\nJe le revois, plus grand, plus fort,\nLorsqu\'aux jours de sa jeunesse\nSon coeur était candide encore\nEt pour moi plein de tendresse.\n\n4.	\nOh! Qui me rendra mon enfant\nComme autrefois plein de charmes?\nJe veille et pleure en l\'attendant\nCherchez mon enfant ce soir.\n\nChoeur\nCherchez mon enfant ce soir  (bis)\nMon coeur est plein, je veux le revoir\nCherchez mon enfant ce soir.\n\n5.	\nCombien j\'ai soif de te revoir\nMon enfant combien je t\'aime\nOh! Dans mes bras reviens ce soir\nEntends mon appel suprême.\n\nChoeur\nReviens mon enfant ce soir  (bis)\nMon coeur est plein, je veux le revoir\nReviens mon enfant ce soir.','<p><strong>1.</strong> </p>\n<p>Que devient mon enfant ce soir </p>\n<p>Il suit une sombre voie </p>\n<p>Lui qui faisait tout mon espoir </p>\n<p>Et ma couronne de joie. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Cherchez mon enfant ce soir</strong> </p>\n<p><strong>Cherchez mon enfant ce soir</strong> </p>\n<p><strong>Mon coeur est plein, je veux le revoir</strong> </p>\n<p><strong>Cherchez on enfant ce soir.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout jeune encore, je crois le voir, </p>\n<p>Lorsque, fervent et sincère, </p>\n<p>Il disait à Dieu chaque soir </p>\n<p>Son enfantine prière. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je le revois, plus grand, plus fort, </p>\n<p>Lorsqu&#39;aux jours de sa jeunesse </p>\n<p>Son coeur était candide encore </p>\n<p>Et pour moi plein de tendresse. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh! Qui me rendra mon enfant </p>\n<p>Comme autrefois plein de charmes? </p>\n<p>Je veille et pleure en l&#39;attendant </p>\n<p>Cherchez mon enfant ce soir. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Cherchez mon enfant ce soir  (bis)</strong> </p>\n<p><strong>Mon coeur est plein, je veux le revoir</strong> </p>\n<p><strong>Cherchez mon enfant ce soir.</strong> </p>\n<p><br/> <strong>5.</strong></p>\n<p>Combien j&#39;ai soif de te revoir </p>\n<p>Mon enfant combien je t&#39;aime </p>\n<p>Oh! Dans mes bras reviens ce soir </p>\n<p>Entends mon appel suprême. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Reviens mon enfant ce soir  (bis)</strong> </p>\n<p><strong>Mon coeur est plein, je veux le revoir</strong> </p>\n<p><strong>Reviens mon enfant ce soir.</strong> </p>\n','Que-devient-mon-enfant',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1875,'4677f83f-0250-45fe-b354-a90091ac1b37',0,0,'Sous le sang',17,'17_VR_fr','1.	\nSous le sang, le précieux sang\nSous le sang, le précieux sang\nJe reçois un coeur nouveau\nSous le précieux sang.','<p><strong>1.</strong> </p>\n<p>Sous le sang, le précieux sang </p>\n<p>Sous le sang, le précieux sang </p>\n<p>Je reçois un coeur nouveau </p>\n<p>Sous le précieux sang. </p>\n','Sous-le-sang',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1876,'83696ee0-f5c0-4869-94d7-ff7ac2ac430a',0,0,'Adeste fideles',46,'46_VR_fr','1.	\nViens, âmes fidèle! Joyeuse nouvelle!\nAuprès de la crèche prosterne-toi:\nVoici l\'Enfant qu\'ont salue les anges.\nViens chanter ses louanges,\nViens chanter ses louanges,\nIl est ton Roi.\n\n2.	\nA Lui notre hommage! Dans notre esclavage\nIl vient pour nous rendre la liberté.\nChante et bénis, réveille-toi, mon âme;\nQue tout en moi proclame,\nQue tout en moi proclame,\nQue tout en moi proclame sa charité.\n\n3.	\nDevant la faiblesse où Jésus s\'abaisse\nJ\'adore et je chante son grand amour.\nOui, ton amour, Sauveur tendre et fidèle,\nPour nous se renouvelle,\nPour nous se renouvelle,\nPour nous se renouvelle, de jour en jour.\n\n4.	\nSauveur débonnaire, à Toi la prière,\nL\'ardente prière de notre coeur.\nRègne sur nous, oui, règne d\'âge en âge;\nA Toi notre humble hommage,\nA Toi notre humble hommage,\nA Toi notre humble hommage, \nJésus, Seigneur!','<p><strong>1.</strong> </p>\n<p>Viens, âmes fidèle! Joyeuse nouvelle! </p>\n<p>Auprès de la crèche prosterne-toi: </p>\n<p>Voici l&#39;Enfant qu&#39;ont salue les anges. </p>\n<p>Viens chanter ses louanges, </p>\n<p>Viens chanter ses louanges, </p>\n<p>Il est ton Roi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>A Lui notre hommage! Dans notre esclavage </p>\n<p>Il vient pour nous rendre la liberté. </p>\n<p>Chante et bénis, réveille-toi, mon âme; </p>\n<p>Que tout en moi proclame, </p>\n<p>Que tout en moi proclame, </p>\n<p>Que tout en moi proclame sa charité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Devant la faiblesse où Jésus s&#39;abaisse </p>\n<p>J&#39;adore et je chante son grand amour. </p>\n<p>Oui, ton amour, Sauveur tendre et fidèle, </p>\n<p>Pour nous se renouvelle, </p>\n<p>Pour nous se renouvelle, </p>\n<p>Pour nous se renouvelle, de jour en jour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sauveur débonnaire, à Toi la prière, </p>\n<p>L&#39;ardente prière de notre coeur. </p>\n<p>Règne sur nous, oui, règne d&#39;âge en âge; </p>\n<p>A Toi notre humble hommage, </p>\n<p>A Toi notre humble hommage, </p>\n<p>A Toi notre humble hommage, </p>\n<p>Jésus, Seigneur! </p>\n','Adeste-fideles',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1877,'d4720fa1-2b25-4dab-9ac3-963138fac346',0,0,'A la ou gran',10,'10_VR_ht','1.	Ségnè Bon-Dié lè nan la nuit sou tè-aMouin ouè zétoual, sa yo ké ou té fèMouin tandé loraj, mouin ouè zéklè-aYo tout déklaré pouvoua ou gingnin.Kè:Nan-m mouin chanté nan ou Bon-dié puisanA la Ou gran, a la Ou gran (bis 2 lign)2.	La nui lè map maché nan boua déyò-aMouin tandé zouazo yo ap chanté nan bouaLè mouin anro gnou montagn ki biin ro-aMin-m dlo ap palé dé Dié avèk joua.3.	Lè mouin sonjé Bon-Dié, Papa, ki ba nouSèl Pitit ou, ki vini pou mouriTout roua pasé gnou Roua poté gnou kourònPikan pou nou tout, ki té doué pèdi.4.	Lè Kris vini nan la gloua ak tout zanj yoMouin va ouè tout grandè majésté LiÉ nan-m mouin va mélé ak tout akanj yoÉ ma tonbé biin ba pou-m adoré-L.','<p><strong>1.</strong> </p>\n<p>Ségnè Bon-Dié lè nan la nuit sou tè-a </p>\n<p>Mouin ouè zétoual, sa yo ké ou té fè </p>\n<p>Mouin tandé loraj, mouin ouè zéklè-a </p>\n<p>Yo tout déklaré pouvwa ou gingnin </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan-m mouin chanté nan ou Bon-dié puisan</strong> </p>\n<p><strong>A la Ou gran, a la Ou gran (bis 2 lign)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La nui lè map maché nan boua déyò-a </p>\n<p>Mouin tandé zouazo yo ap chanté nan boua </p>\n<p>Lè mouin anro gnou montagn ki biin ro-a </p>\n<p>Min-m dlo ap palé dé Dié avèk joua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè mouin sonjé Bon-Dié, Papa, ki ba nou </p>\n<p>Sèl Pitit ou, ki vini pou mouri </p>\n<p>Tout roua pasé gnou Roua poté gnou kouròn </p>\n<p>Pikan pou nou tout, ki té doué pèdi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè Kris vini nan la gloua ak tout zanj yo </p>\n<p>Mouin va ouè tout grandè majésté Li </p>\n<p>É nan-m mouin va mélé ak tout akanj yo </p>\n<p>É ma tonbé biin ba pou-m adoré-L </p>\n','A-la-ou-gran',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1878,'8ca63174-6589-44ac-82b0-ac9702032b57',0,0,'Jézu sé sèl Sovè',13,'13_VR_ht','1.	\nMouin té vi-n jouin-n Jézu byin fatigé,\nJézu ban mouin répo  (bis)\nAlèkilé kè mouin vini byin gé\nJézu ban mouin répo.\n\nKè:\nJézu sé sèl Sovè-m, Li padonnin péché-m\nLi mété gnou chan nan kè mouin,\nMouin kontan, Jézu sé sèl Sovè-m,\nJézu sé sèl Sovè-m.\n\n2.	\nFado péché-m té trò lou pou-m pòté,\nJézu soulajé mouin,  (bis)\nMouin ba Li tout fado-m alèkilé,\nJézu soulajé mouin.\n\n3.	\nJézu ouété tout kondanasion mouin,\nLi padonnin péché-m,  (bis)\nÉ koulyé-a sé la pè nan kè mouin,\nLi padonnin péché-m.','<p><strong>1.</strong> </p>\n<p>Mouin té vi-n jouin-n Jézu byin fatigé, </p>\n<p>Jézu ban mouin répo  (bis) </p>\n<p>Alèkilé kè mouin vini byin gé </p>\n<p>Jézu ban mouin répo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sé sèl Sovè-m, Li padonnin péché-m</strong> </p>\n<p><strong>Li mété gnou chan nan kè mouin,</strong> </p>\n<p><strong>Mouin kontan, Jézu sé sèl Sovè-m,</strong> </p>\n<p><strong>Jézu sé sèl Sovè-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fado péché-m té trò lou pou-m pòté, </p>\n<p>Jézu soulajé mouin,  (bis) </p>\n<p>Mouin ba Li tout fado-m alèkilé, </p>\n<p>Jézu soulajé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu ouété tout kondanasion mouin, </p>\n<p>Li padonnin péché-m,  (bis) </p>\n<p>É koulyé-a sé la pè nan kè mouin, </p>\n<p>Li padonnin péché-m. </p>\n','Jezu-se-sel-Sove',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1879,'85b67ccc-017f-4a63-8f21-d27d2ff8d4a7',0,0,'Konté-m ladan',15,'15_VR_ht','1.	\nLè ou konté moun ki pou Jézu\nKonté-m ladan,\nNan moun ki konfié nan Paròl Li,\nKonté-m ladan.\n\nKè:\nKonté-m pami pitit ki pou Roua sièl la\nKonté-m pami sèvitè kap sèvi a,\nKonté-m pami moun sové alélouya,\nKonté-m ladan.\n\n2.	\nLè ou konté moun ki nan la gras\nKonté-m ladan,\nSa yo an Kris ki gingnin gnou plas\nKonté-m ladan.\n\n3.	\nLè ou konté témouin yo pou Kris,\nKonté-m ladan,\nKi va gingnin kouròn la justis,\nKonté-m ladan.','<p><strong>1.</strong> </p>\n<p>Lè ou konté moun ki pou Jézu </p>\n<p>Konté-m ladan, </p>\n<p>Nan moun ki konfié nan Paròl Li, </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konté-m pami pitit ki pou Roua sièl la</strong> </p>\n<p><strong>Konté-m pami sèvitè kap sèvi a,</strong> </p>\n<p><strong>Konté-m pami moun sové alélouya,</strong> </p>\n<p><strong>Konté-m ladan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou konté moun ki nan la gras </p>\n<p>Konté-m ladan, </p>\n<p>Sa yo an Kris ki gingnin gnou plas </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè ou konté témouin yo pou Kris, </p>\n<p>Konté-m ladan, </p>\n<p>Ki va gingnin kouròn la justis, </p>\n<p>Konté-m ladan. </p>\n','Konte-m-ladan',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1880,'a9abef1a-5f65-449e-9bd4-a147f4423ee5',0,0,'Gnou jou m-té pèdu',7,'7_EE_ht','1.\nGnou jou m-té pèdu nan chémin an\nMouin pa té kab konnin ki koté m-doué alé\nMin gnou Non-m té vini pran min mouin\nÉ Li té kondui mouin koté dlo a byinfré.\n\nKè:\nJézu sé Roua, Jézu sé Roua\nJézu sé Roua dè roua\nLap vi-n chèché mouin\nPou-m alé nan sièl la\nJézu sé Roua dè roua.\n\n2.\nDépi jou min-L té pozésou mouin\nLa vi mouin té chanjé\nÉ nan-m mouin té vi-n lib\nNan kè mouin toujou gingnin gnou chan\nKi témouayé grandè amou Li pou mouin.\n\n3.\nA la gnou si gran jou sa va yé, lè Jézu va vini\nPou pran mouin avèk ou\nAnpil moun va di, Sègnè, Ségnè\nMin yo pap kab antré nan bèl  vil Sélès la','<p><strong>1.</strong> </p>\n<p>Gnou jou m-té pèdu nan chémin an </p>\n<p>Mouin pa té kab konnin ki koté m-doué alé </p>\n<p>Min gnou Non-m té vini pran min mouin </p>\n<p>É Li té kondui mouin koté dlo a byinfré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sé Roua, Jézu sé Roua</strong> </p>\n<p><strong>Jézu sé Roua dè roua</strong> </p>\n<p><strong>Lap vi-n chèché mouin</strong> </p>\n<p><strong>Pou-m alé nan sièl la</strong> </p>\n<p><strong>Jézu sé Roua dè roua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dépi jou min-L té pozésou mouin </p>\n<p>La vi mouin té chanjé </p>\n<p>É nan-m mouin té vi-n lib </p>\n<p>Nan kè mouin toujou gingnin gnou chan </p>\n<p>Ki témouayé grandè amou Li pou mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A la gnou si gran jou sa va yé, lè Jézu va vini </p>\n<p>Pou pran mouin avèk ou </p>\n<p>Anpil moun va di, Sègnè, Ségnè </p>\n<p>Min yo pap kab antré nan bèl  vil Sélès la </p>\n','Gnou-jou-m-te-pedu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1881,'07510d50-d880-4e52-aca5-ee9d93f0fdcd',0,0,'Mouin gin éspoua',12,'12_EE_ht','1.\nMouin gin éspoua\nGnou jou pou-m fi-n soufri\nÉ jou sa-a li louin pou li vini\nFrè-m ak sè-m yo\nÉdé-m nan priè nou\nAfin pou-m saka rankontré Jézu.\n\nKè:\nJézu sé rézon kè mouin\nMouin vlé suiv li jouk la fin\nJou sa ma ouè Li fas a fas\nJézu sé tout pou mouin.\n\n2.\nLi pran min mouin\nLi ouété-m nan ténèb yo\nÉ kounié ya mouin vlé résté pou non Li\nRout la fè noua\nLi apik épil glisé\nMin-m doué konnin fòk Jézu-Kri sé tout.','<p><strong>1.</strong> </p>\n<p>Mouin gin éspoua </p>\n<p>Gnou jou pou-m fi-n soufri </p>\n<p>É jou sa-a li louin pou li vini </p>\n<p>Frè-m ak sè-m yo </p>\n<p>Édé-m nan priè nou </p>\n<p>Afin pou-m saka rankontré Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sé rézon kè mouin</strong> </p>\n<p><strong>Mouin vlé suiv li jouk la fin</strong> </p>\n<p><strong>Jou sa ma ouè Li fas a fas</strong> </p>\n<p><strong>Jézu sé tout pou mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li pran min mouin </p>\n<p>Li ouété-m nan ténèb yo </p>\n<p>É kounié ya mouin vlé résté pou non Li </p>\n<p>Rout la fè noua </p>\n<p>Li apik épil glisé </p>\n<p>Min-m doué konnin fòk Jézu-Kri sé tout. </p>\n','Mouin-gin-espoua',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1882,'6a98cc3c-2683-49b4-a788-b3c69bc266a7',0,0,'Pi fon, pi fon',40,'40_EE_ht','1.\nPi fon, pi fon mouin pralé\nNan lamou Jézu Sovè-m\nMap chèché konnin pouki\nLi té tèlman rinmin-m\nPou ki Li lonjé min\nLi ouété-m nan gran péché\nLi té sové-m, Li té géri-m,\nMalgré-m té byin égaré.\n\n2.\nPi fon, pi fon mouin pralé\nNan la soufrans dé Jézu\nMap suiv Li nan jadin an\nKoté-L té soué san Li\nLa Li pran fado doulè\nPéché lé mon-n antié.\nO chè Ségnè, ségnè édé-m\nPa kité-m dékourajé.\n\n3.\nPi fon, pi fon mouin pralé.\nNan volonté Jézu-Kri\nMap prié, konnin suiv Li.\nPou fè Ségnè-m plézi\nMouin vlé bésé devan Li.\nProstèné nan pié Li\nFè sa ou vlé kasé kè mouin\nFè-m gnou bon vèso tanpri.','<p><strong>1.</strong> </p>\n<p>Pi fon, pi fon mouin pralé </p>\n<p>Nan lamou Jézu Sovè-m </p>\n<p>Map chèché konnin pouki </p>\n<p>Li té tèlman rinmin-m </p>\n<p>Pou ki Li lonjé min </p>\n<p>Li ouété-m nan gran péché </p>\n<p>Li té sové-m, Li té géri-m, </p>\n<p>Malgré-m té byin égaré. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pi fon, pi fon mouin pralé </p>\n<p>Nan la soufrans dé Jézu </p>\n<p>Map suiv Li nan jadin an </p>\n<p>Koté-L té soué san Li </p>\n<p>La Li pran fado doulè </p>\n<p>Péché lé mon-n antié. </p>\n<p>O chè Ségnè, ségnè édé-m </p>\n<p>Pa kité-m dékourajé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pi fon, pi fon mouin pralé. </p>\n<p>Nan volonté Jézu-Kri </p>\n<p>Map prié, konnin suiv Li. </p>\n<p>Pou fè Ségnè-m plézi </p>\n<p>Mouin vlé bésé devan Li. </p>\n<p>Prostèné nan pié Li </p>\n<p>Fè sa ou vlé kasé kè mouin </p>\n<p>Fè-m gnou bon vèso tanpri. </p>\n','Pi-fon-pi-fon',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1883,'43393dd8-40e9-4246-9438-bb07dc0e9dac',0,0,'Ou ouè tribilasion-m',60,'60_EE_ht','1.\nOu ouè tribilasion-m Ségnè Jézu\nOu konnin mizè mouin\nSi ou lagé-m, O ki moun ki pral konsolé-m\nMouin tankou gnou òfélin, O, O,\nNan gnou mon-n si méchan\nOu sé sèl éspoua ké mouin gingnin\nTanpri Ségnè sové mouin.\n\nKè:\nMouin prié ou lé matin\nMouin rélé ou a midi\nÉ mouin prié ou a souè\nMap chèché gnou sékou ki pa kapab vini\nMouin prèt pou mouin mouri\nPou ki sa ou abandoné-m Jézu\nGin pitié pou mouin.\n\n2.\nPou tan nan mon-n sa, gin moun ki trè alèz\nKi pa min-m konnin Bon-Dieu\nMoun pa kap sèvi ou-o, yo toujou méprizé\nPa kité mouin troublé, O,\nPou bagay nan mon-n sa-a\nKa-m konnin min-m si mouin maléré\nGnou jou ouap vi-n chèché-m.','<p><strong>1.</strong> </p>\n<p>Ou ouè tribilasion-m Ségnè Jézu </p>\n<p>Ou konnin mizè mouin </p>\n<p>Si ou lagé-m, O ki moun ki pral konsolé-m </p>\n<p>Mouin tankou gnou òfélin, O, O, </p>\n<p>Nan gnou mon-n si méchan </p>\n<p>Ou sé sèl éspoua ké mouin gingnin </p>\n<p>Tanpri Ségnè sové mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin prié ou lé matin</strong> </p>\n<p><strong>Mouin rélé ou a midi</strong> </p>\n<p><strong>É mouin prié ou a souè</strong> </p>\n<p><strong>Map chèché gnou sékou ki pa kapab vini</strong> </p>\n<p><strong>Mouin prèt pou mouin mouri</strong> </p>\n<p><strong>Pou ki sa ou abandoné-m Jézu</strong> </p>\n<p><strong>Gin pitié pou mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou tan nan mon-n sa, gin moun ki trè alèz </p>\n<p>Ki pa min-m konnin Bon-Dieu </p>\n<p>Moun pa kap sèvi ou-o, yo toujou méprizé </p>\n<p>Pa kité mouin troublé, O, </p>\n<p>Pou bagay nan mon-n sa-a </p>\n<p>Ka-m konnin min-m si mouin maléré </p>\n<p>Gnou jou ouap vi-n chèché-m. </p>\n','Ou-oue-tribilasion-m',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1884,'8f57acbc-7039-4f7c-a7ff-14966c6a2e77',0,0,'Répondant à l\'appel',15,'15_MJ_fr','1.\nRépondant à l\'appel\nDe Jésus mon divin Maître,\nAvec Lui je suis sorti\nDu pays du malheur;\nDélivré par Son pouvoir,\nJe ne veux plus connaître,\nQue la grâce et l\'amour\nDe mon Sauveur!\n\nChoeur:\nJ\'ai trouvé, bien loin du monde,\nUn abri paisible et sûr;\nIci le bonheur abonde,\nSous un ciel toujours d\'azur!\nOh, oui, je veux dresser ma tente,\nDans ce pays merveilleux,\nEt toujours vivre\nPlus près de Dieu!\n\n2.\nBien au loin j\'entends l\'écho\nDes vains bruits de la terre,\nL\'écho triste et douloureux\nD\'un monde révolté;\nMais désormais mon seul bien\nC\'est la croix du Calvaire,\nC\'est Jésus, Sa gloire, Sa vérité!\n\n3.\nPar la ruse et les menaces,\nVoulant ma défaite,\nSatan vide contre moi\nSon infâme carquois,\nMais Dieu m\'offre en Sa Parole\nUne sûre retraite,\nEt pour Capitaine, le Roi des rois!','<p><strong>1.</strong> </p>\n<p>Répondant à l&#39;appel </p>\n<p>De Jésus mon divin Maître, </p>\n<p>Avec Lui je suis sorti </p>\n<p>Du pays du malheur; </p>\n<p>Délivré par Son pouvoir, </p>\n<p>Je ne veux plus connaître, </p>\n<p>Que la grâce et l&#39;amour </p>\n<p>De mon Sauveur! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>J&#39;ai trouvé, bien loin du monde,</strong> </p>\n<p><strong>Un abri paisible et sûr;</strong> </p>\n<p><strong>Ici le bonheur abonde,</strong> </p>\n<p><strong>Sous un ciel toujours d&#39;azur!</strong> </p>\n<p><strong>Oh, oui, je veux dresser ma tente,</strong> </p>\n<p><strong>Dans ce pays merveilleux,</strong> </p>\n<p><strong>Et toujours vivre</strong> </p>\n<p><strong>Plus près de Dieu!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bien au loin j&#39;entends l&#39;écho </p>\n<p>Des vains bruits de la terre, </p>\n<p>L&#39;écho triste et douloureux </p>\n<p>D&#39;un monde révolté; </p>\n<p>Mais désormais mon seul bien </p>\n<p>C&#39;est la croix du Calvaire, </p>\n<p>C&#39;est Jésus, Sa gloire, Sa vérité! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Par la ruse et les menaces, </p>\n<p>Voulant ma défaite, </p>\n<p>Satan vide contre moi </p>\n<p>Son infâme carquois, </p>\n<p>Mais Dieu m&#39;offre en Sa Parole </p>\n<p>Une sûre retraite, </p>\n<p>Et pour Capitaine, le Roi des rois! </p>\n','Repondant-a-l\'appel',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1885,'ec7020ad-e0a9-48cc-a97a-cea728ca83d8',0,0,'Il est l\'élu de mon coeur',20,'20_MJ_fr','1.\nIl est l\'Élu de mon coeur, (bis)\nEn Le trouvant, j\'ai trouvé le bonheur,\nAussi  je veux, moi pécheur,\nRedire de mon Sauveur,\nQu\'Il est l\'Élu de mon coeur.','<p><strong>1.</strong> </p>\n<p>Il est l&#39;Élu de mon coeur, (bis) </p>\n<p>En Le trouvant, j&#39;ai trouvé le bonheur, </p>\n<p>Aussi  je veux, moi pécheur, </p>\n<p>Redire de mon Sauveur, </p>\n<p>Qu&#39;Il est l&#39;Élu de mon coeur. </p>\n','Il-est-l\'elu-de-mon-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1886,'30eb4280-aeda-46df-9ef5-70a6a000e65f',0,0,'A la fin de mon voyage',26,'26_MJ_fr','1.\nA la fin de mon voyage\nOui, je verrai mon Sauveur\nJe verrai mon Sauveur, (bis)\nA la fin de mon voyage\nOui, je verrai mon Sauveur,\nDans la cité de Dieu.\nQuel bonheur, quel bonheur, (bis)\nA la fin de mon voyage\nOui, je verrai mon Sauveur\nDans la cité de Dieu.','<p><strong>1.</strong> </p>\n<p>A la fin de mon voyage </p>\n<p>Oui, je verrai mon Sauveur </p>\n<p>Je verrai mon Sauveur, (bis) </p>\n<p>A la fin de mon voyage </p>\n<p>Oui, je verrai mon Sauveur, </p>\n<p>Dans la cité de Dieu. </p>\n<p>Quel bonheur, quel bonheur, (bis) </p>\n<p>A la fin de mon voyage </p>\n<p>Oui, je verrai mon Sauveur </p>\n<p>Dans la cité de Dieu. </p>\n','A-la-fin-de-mon-voyage',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1887,'3b990f77-4176-4eb1-84d7-1056bac65403',0,0,'Dois-je partir les main vides',33,'33_MJ_fr','1.\nDois-je partir les mains vides,\nPour le séjour éternel,\nEt quitter ces lieux arides,\nSans une âme pour le ciel?\n\nChoeur:\nOui, faut-il que, les mains vides,\nJe rencontre mon Sauveur,\nPendant que des coeurs avides,\nCherchent en vain le bonheur?\n\n2.\nJésus a sauvé mon âme,\nDe la mort je n\'ai plus peur,\nMais ce que mon coeur réclame,\nC\'est un don pour mon Sauveur!\n\n3.\nSi je pouvais, de la vie,\nRecommencer le chemin,\nJe n\'aurais plus qu\'une envie:\nSemer partout le bon grain!\n\n4.\nPour vous aussi l\'heure passe,\nOh! Pendant qu\'il en est temps,\nAnnoncez de Dieu la grâce,\nAu coeurs contrits, repentants!','<p><strong>1.</strong> </p>\n<p>Dois-je partir les mains vides, </p>\n<p>Pour le séjour éternel, </p>\n<p>Et quitter ces lieux arides, </p>\n<p>Sans une âme pour le ciel? </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, faut-il que, les mains vides,</strong> </p>\n<p><strong>Je rencontre mon Sauveur,</strong> </p>\n<p><strong>Pendant que des coeurs avides,</strong> </p>\n<p><strong>Cherchent en vain le bonheur?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus a sauvé mon âme, </p>\n<p>De la mort je n&#39;ai plus peur, </p>\n<p>Mais ce que mon coeur réclame, </p>\n<p>C&#39;est un don pour mon Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si je pouvais, de la vie, </p>\n<p>Recommencer le chemin, </p>\n<p>Je n&#39;aurais plus qu&#39;une envie: </p>\n<p>Semer partout le bon grain! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour vous aussi l&#39;heure passe, </p>\n<p>Oh! Pendant qu&#39;il en est temps, </p>\n<p>Annoncez de Dieu la grâce, </p>\n<p>Au coeurs contrits, repentants! </p>\n','Dois-je-partir-les-main-vides',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1888,'aff04c1e-5e71-456d-98a4-52ab92f1f9dd',0,0,'Regarde à l’Agneau de Dieu',7,'7_VR_fr','1.	\nSi du péché tu veux être délivré\nRegarde à l\'Agneau de Dieu;\nPour toi sur la croix Il a tout payé\nRegarde à l\'Agneau de Dieu.\n\nChoeur\nRegarde à l\'Agneau de Dieu  (bis)\nEn Lui seul tu trouveras le secours;\nRegarde à l\'Agneau de Dieu.\n\n2.	\nQuand Satan vient subtil pour te tenter\nRegarde à l\'Agneau de Dieu\nAvec sa force tu peux triompher\nRegarde à l\'Agneau de Dieu.\n\n3.	\nQuand tu te sens seul et découragé\nRegarde à l\'Agneau de Dieu\nPar ton amour tu seras consolé\nRegarde à l\'Agneau de Dieu.\n\n4.	\nNe crains point lorsque ta route est pénible\nRegarde à l\'Agneau de Dieu  \nAvec Jésus tout te seras possible\nRegarde à l\'Agneau de Dieu.','<p><strong>1.</strong> </p>\n<p>Si du péché tu veux être délivré </p>\n<p>Regarde à l&#39;Agneau de Dieu; </p>\n<p>Pour toi sur la croix Il a tout payé </p>\n<p>Regarde à l&#39;Agneau de Dieu. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Regarde à l&#39;Agneau de Dieu  (bis)</strong> </p>\n<p><strong>En Lui seul tu trouveras le secours;</strong> </p>\n<p><strong>Regarde à l&#39;Agneau de Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand Satan vient subtil pour te tenter </p>\n<p>Regarde à l&#39;Agneau de Dieu </p>\n<p>Avec sa force tu peux triompher </p>\n<p>Regarde à l&#39;Agneau de Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand tu te sens seul et découragé </p>\n<p>Regarde à l&#39;Agneau de Dieu </p>\n<p>Par ton amour tu seras consolé </p>\n<p>Regarde à l&#39;Agneau de Dieu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ne crains point lorsque ta route est pénible </p>\n<p>Regarde à l&#39;Agneau de Dieu </p>\n<p>Avec Jésus tout te seras possible </p>\n<p>Regarde à l&#39;Agneau de Dieu. </p>\n','Regarde-a-l\'Agneau-de-Dieu',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1889,'9bd55649-66ad-4237-bfbc-b61848b3314b',0,0,'La venue de Christ',38,'38_VR_fr','1.	\nVoici, je viens bientôt, attendez ma venue!\nNous dit le Rédempteur dont l\'amour nous sauva\nAlléluia! Alléluia! Alléluia! Alléluia!\nO Jésus! Viens bientôt, ô Jésus viens bientôt\nO Jésus! Viens bientôt, sur l\'éclatante nue.\n\n2.	\nSatan veut nous ravir la couronne de gloire;\nMais dans l\'éternité le combat cessera\nAlléluia! Alléluia! Alléluia! Alléluia!\nO Jésus viens! Bientôt  (ter)\nNous donne la victoire.\n\n3.	\nDu monde et de la chair, du cruel adversaire\nDe tous nos ennemis ta main triomphera!\nAlléluia! Alléluia! Alléluia! Alléluia!\nO Jésus! Viens bientôt,  (ter)\nEn Toi notre âme espère.\n\n4.	\nNotre âme a la douleur ici-bas, est en proie\nMais du ciel, à jamais tout chagrin s\'enfuira\nAlléluia! Alléluia! Alléluia! Alléluia!\nO Jésus! Viens bientôt  (ter)\nEn Toi notre âme espère.','<p><strong>1.</strong> </p>\n<p>Voici, je viens bientôt, attendez ma venue! </p>\n<p>Nous dit le Rédempteur dont l&#39;amour nous sauva </p>\n<p>Alléluia! Alléluia! Alléluia! Alléluia! </p>\n<p>O Jésus! Viens bientôt, ô Jésus viens bientôt </p>\n<p>O Jésus! Viens bientôt, sur l&#39;éclatante nue. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Satan veut nous ravir la couronne de gloire; </p>\n<p>Mais dans l&#39;éternité le combat cessera </p>\n<p>Alléluia! Alléluia! Alléluia! Alléluia! </p>\n<p>O Jésus viens! Bientôt  (ter) </p>\n<p>Nous donne la victoire. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Du monde et de la chair, du cruel adversaire </p>\n<p>De tous nos ennemis ta main triomphera! </p>\n<p>Alléluia! Alléluia! Alléluia! Alléluia! </p>\n<p>O Jésus! Viens bientôt,  (ter) </p>\n<p>En Toi notre âme espère. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Notre âme a la douleur ici-bas, est en proie </p>\n<p>Mais du ciel, à jamais tout chagrin s&#39;enfuira </p>\n<p>Alléluia! Alléluia! Alléluia! Alléluia! </p>\n<p>O Jésus! Viens bientôt  (ter) </p>\n<p>En Toi notre âme espère. </p>\n','La-venue-de-Christ',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1890,'c9333105-75ed-4682-8828-7b4a0b72298b',0,0,'Voyez,  voyez quel amour',20,'20_VR_fr','1.	\nVoyez, voyez quel Amour (bis)\nLe Père nous a témoigné\nQue nous soyons appelés\nEnfants de Dieu.\n\n2.	\nVoyez, voyez quel Amour\nLe Père nous a témoigné\nQue nous soyons appelés\nEnfants de Dieu.\n\n3.	\nSi le monde ne nous connaît pas\nC\'est que Lui non plus\nIl ne l\'a pas connu  (bis)\nBien-aimés nous voici des à présent\nEnfants de Dieu.\n\n4.	\nCe que nous serons n\'est pas visible encore\nMais nous savons que lorsqu\'Il viendra\nNous Lui ressemblerons\nQue lorsqu\'Il viendra nous Lui ressemblerons.\n\n5.	\nEt nous Le verrons tel qu\'Il est,\nLe verrons tel qu\'Il est\nNous Lui ressemblerons,\nNous Lui ressemblerons,\nNous Le verrons tel qu\'Il est, Amen, Amen.','<p><strong>1.</strong> </p>\n<p>Voyez, voyez quel Amour (bis) </p>\n<p>Le Père nous a témoigné </p>\n<p>Que nous soyons appelés </p>\n<p>Enfants de Dieu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voyez, voyez quel Amour </p>\n<p>Le Père nous a témoigné </p>\n<p>Que nous soyons appelés </p>\n<p>Enfants de Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si le monde ne nous connaît pas </p>\n<p>C&#39;est que Lui non plus </p>\n<p>Il ne l&#39;a pas connu  (bis) </p>\n<p>Bien-aimés nous voici des à présent </p>\n<p>Enfants de Dieu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ce que nous serons n&#39;est pas visible encore </p>\n<p>Mais nous savons que lorsqu&#39;Il viendra </p>\n<p>Nous Lui ressemblerons </p>\n<p>Que lorsqu&#39;Il viendra nous Lui ressemblerons. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Et nous Le verrons tel qu&#39;Il est, </p>\n<p>Le verrons tel qu&#39;Il est </p>\n<p>Nous Lui ressemblerons, </p>\n<p>Nous Lui ressemblerons, </p>\n<p>Nous Le verrons tel qu&#39;Il est, Amen, Amen. </p>\n','Voyez-voyez-quel-amour',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1891,'70b65f94-191a-4756-9a9d-05c7c9ebaf7d',0,0,'Lémon-n sé pa lakay mouin',6,'6_VR_ht','1.	\nLé mon-n sé pa la kay mouin nan siè la m-pralé\nTrézò mouin ak kè mouin an ro tout fiksé\nMouin tandé zanj yo kap rélé mouin anlè la\nÉ mouin pa kab santi m-tro alèz isi-ba.\n\nKè:\nJézu ou konnin ou sé sèl zamni mouin\nSi ou pa prn-m nan sièl, O Ségnè sa ma fè\nMouin tandé zanj yo kap rélé mouin anlè la\nÉ mouin pa kab santi m-tro alèz isi-ba.\n\n2.	\nMouin konnin mouin pa dign, mouin sé gnou vié péchè\nMin mouin gin tout éspoua ké Jézu sèl Sovè\nSé Jézu ki té mouri sou lé boua Kalvè\nÉ Li té sové nan-m mouin pou létènité.\n\n3.	\nMouin gin kèk biin-émé ki vouyajé déja\nYo nan bra dé Jézu é yapé tan-n mouin la\nMouin gin pou-m rété lité kòm you bon sòlda\nMin Ségnè lè Ou rélé-m ma vini avèk joua.\n\n4.	\nMouin tandé voua Jézu di-m lité pran kouraj\nAlé louin préché yo chanté é la priè\nPa troublé pa plingnin mouin va la avèk ou\nSé mouin min-m ki chouazi ou pou-m voyé alé la','<p><strong>1.</strong> </p>\n<p>Lé mon-n sé pa la kay mouin nan siè la m-pralé </p>\n<p>Trézò mouin ak kè mouin an ro tout fiksé </p>\n<p>Mouin tandé zanj yo kap rélé mouin anlè la </p>\n<p>É mouin pa kab santi m-tro alèz isi-ba. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu ou konnin ou sé sèl zamni mouin</strong> </p>\n<p><strong>Si ou pa prn-m nan sièl, O Ségnè sa ma fè</strong> </p>\n<p><strong>Mouin tandé zanj yo kap rélé mouin anlè la</strong> </p>\n<p><strong>É mouin pa kab santi m-tro alèz isi-ba.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin konnin mouin pa dign, mouin sé gnou vié péchè </p>\n<p>Min mouin gin tout éspoua ké Jézu sèl Sovè </p>\n<p>Sé Jézu ki té mouri sou lé boua Kalvè </p>\n<p>É Li té sové nan-m mouin pou létènité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin gin kèk biin-émé ki vouyajé déja </p>\n<p>Yo nan bra dé Jézu é yapé tan-n mouin la </p>\n<p>Mouin gin pou-m rété lité kòm you bon sòlda </p>\n<p>Min Ségnè lè Ou rélé-m ma vini avèk joua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin tandé voua Jézu di-m lité pran kouraj </p>\n<p>Alé louin préché yo chanté é la priè </p>\n<p>Pa troublé pa plingnin mouin va la avèk ou </p>\n<p>Sé mouin min-m ki chouazi ou pou-m voyé alé la </p>\n','Lemon-n-se-pa-lakay-mouin',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1892,'916c9523-e075-46b9-868b-520ff5db6cab',0,0,'Dépi nan tan lontan',14,'14_EE_ht','1.\nDépi nan tan lontan\nLèzò-m té toujou méchan\nKayin tiyé Abèl \nSé sa-n ouè lòm pratiké\nJézu vi-n sou la tè\nPou ouété péché kriminèl yo\nYarété-L, yo touyé-L, yo klouré-L sou la koua\n\nKè:\nÉlas!  Élas! \nMouin régrèt sa pou lé mon-n,\nTalè konsa, lé mon-n pral boulvèsé vré.\nÉlas!  Élas!  \nMouin régrèt sa pou lé mon-n\nTalè kon sa, lé mon-n pral dlo nan jé.\n\n2.\nAkab ak Jézabèl mét ansan-m Yo touyé Nabòt\nAkab ak Jézbèl pansé Bon-Dieu pat ouè yo\nSa pat rété konsa Létènèl té kondané yo\nGnou ékzanp, gnou jujman\nBon-Dieu trasé sou lòm.\n\n2.\nLézòm fabriké za-m\nSé pa pou yo détoui pié boua,\nLézòm fabriké za-m\nSé pa pou yo détoui zan-imo\nLézòm fabriké za-m\nSé pou yo détoui lòm parèy yo\nYap touyé, yap jété ou,\nKè yo pa jan-m soté - Élas!\n\n3.\nPou ninpòt ti bagay \nNou ouè lòm ap tuyé lòm\nPou ninpòt ti plézi\nNou ouè lòm ap mét kouto sou lòm\nLòm pa kab gadé lòm gnou moman\nYo mété ou kadav\nÉpi yap banboché – Élas!','<p><strong>1.</strong> </p>\n<p>Dépi nan tan lontan </p>\n<p>Lèzò-m té toujou méchan </p>\n<p>Kayin tiyé Abèl </p>\n<p>Sé sa-n ouè lòm pratiké </p>\n<p>Jézu vi-n sou la tè </p>\n<p>Pou ouété péché kriminèl yo </p>\n<p>Yarété-L, yo touyé-L, yo klouré-L sou la koua </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Élas!  Élas!</strong> </p>\n<p><strong>Mouin régrèt sa pou lé mon-n,</strong> </p>\n<p><strong>Talè konsa, lé mon-n pral boulvèsé vré.</strong> </p>\n<p><strong>Élas!  Élas!</strong> </p>\n<p><strong>Mouin régrèt sa pou lé mon-n</strong> </p>\n<p><strong>Talè kon sa, lé mon-n pral dlo nan jé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Akab ak Jézabèl mét ansan-m Yo touyé Nabòt </p>\n<p>Akab ak Jézbèl pansé Bon-Dieu pat ouè yo </p>\n<p>Sa pat rété konsa Létènèl té kondané yo </p>\n<p>Gnou ékzanp, gnou jujman </p>\n<p>Bon-Dieu trasé sou lòm. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lézòm fabriké za-m </p>\n<p>Sé pa pou yo détoui pié boua, </p>\n<p>Lézòm fabriké za-m </p>\n<p>Sé pa pou yo détoui zan-imo </p>\n<p>Lézòm fabriké za-m </p>\n<p>Sé pou yo détoui lòm parèy yo </p>\n<p>Yap touyé, yap jété ou, </p>\n<p>Kè yo pa jan-m soté - Élas! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pou ninpòt ti bagay </p>\n<p>Nou ouè lòm ap tuyé lòm </p>\n<p>Pou ninpòt ti plézi </p>\n<p>Nou ouè lòm ap mét kouto sou lòm </p>\n<p>Lòm pa kab gadé lòm gnou moman </p>\n<p>Yo mété ou kadav </p>\n<p>Épi yap banboché – Élas! </p>\n','Depi-nan-tan-lontan',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1893,'a1de8d83-0407-411d-a05b-cd84036a5668',0,0,'Byin-émé lévé tèt ou',21,'21_EE_ht','1.\nByin-émé lévé tèt nou\nAn-n gadé ki lè li yé\nSé pa lè pou-n ap dòmi, révéyé nou!\nTan mandé pou-n sourié\nPaské li trè zavansé\nSi nou rété ap soméyé\nNou va pèdi jou sa.\n\nKè:\nAn-n mété an pratik mésaj dènié tan sa-a\nPou-n kapab atin-n la pèféksion \nSi nou rété fidèl, si-n vinkè sou tout pouin\nNou gin éspoua, gnou jou na ouè Jézu.\n\n2.\nNou sé gnou pèp ki méprizé\nAbandoné pa lè zòm\nMin sa pa fè nou angnin nou gin éspoua\nJézu té fè nou kon-n sa\nPiga nou pè péti troupo-m yo\nKa Papa mouin jujé bon\nPou ban nou rouayòm sa-a.\n\n3.\nA la gnou gran jou sa va yé\nLè tronpèt la va son-nin\nPou tout mò yo an Jézu lévé\nPou yal rankontré Li\nVa gin krié, va gin rélé\nPou sa yo ki pat préparé\nO bon Papa gingnin pitié\nPou pitit ou yo non.','<p><strong>1.</strong> </p>\n<p>Byin-émé lévé tèt nou </p>\n<p>An-n gadé ki lè li yé </p>\n<p>Sé pa lè pou-n ap dòmi, révéyé nou! </p>\n<p>Tan mandé pou-n sourié </p>\n<p>Paské li trè zavansé </p>\n<p>Si nou rété ap soméyé </p>\n<p>Nou va pèdi jou sa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>An-n mété an pratik mésaj dènié tan sa-a</strong> </p>\n<p><strong>Pou-n kapab atin-n la pèféksion</strong> </p>\n<p><strong>Si nou rété fidèl, si-n vinkè sou tout pouin</strong> </p>\n<p><strong>Nou gin éspoua, gnou jou na ouè Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou sé gnou pèp ki méprizé </p>\n<p>Abandoné pa lè zòm </p>\n<p>Min sa pa fè nou angnin nou gin éspoua </p>\n<p>Jézu té fè nou kon-n sa </p>\n<p>Piga nou pè péti troupo-m yo </p>\n<p>Ka Papa mouin jujé bon </p>\n<p>Pou ban nou rouayòm sa-a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>A la gnou gran jou sa va yé </p>\n<p>Lè tronpèt la va son-nin </p>\n<p>Pou tout mò yo an Jézu lévé </p>\n<p>Pou yal rankontré Li </p>\n<p>Va gin krié, va gin rélé </p>\n<p>Pou sa yo ki pat préparé </p>\n<p>O bon Papa gingnin pitié </p>\n<p>Pou pitit ou yo non. </p>\n','Byin-eme-leve-tet-ou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1894,'e31a391b-aa97-4373-9e2e-a4ff78aed8c8',1,0,'A la fin de mon voyage',30,'30_EE_fr','1.\nA la fin de on voyage\nOui je verrai mon Sauveur,\nJe verrai mon Sauveur.  (bis)\nA la fin de mon voyage\nOui je verrai mon Sauveur\nDans la cité de Dieu.\nQuel bonheur, quel bonheur.  (bis)\nA la fin de mon voyage\nOui je verrai mon Sauveur\nDans la cité de Dieu.','<p><strong>1.</strong> </p>\n<p>A la fin de on voyage </p>\n<p>Oui je verrai mon Sauveur, </p>\n<p>Je verrai mon Sauveur.  (bis) </p>\n<p>A la fin de mon voyage </p>\n<p>Oui je verrai mon Sauveur </p>\n<p>Dans la cité de Dieu. </p>\n<p>Quel bonheur, quel bonheur.  (bis) </p>\n<p>A la fin de mon voyage </p>\n<p>Oui je verrai mon Sauveur </p>\n<p>Dans la cité de Dieu. </p>\n','A-la-fin-de-mon-voyage',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:46'),(1895,'6f013501-51c4-4eeb-91eb-1b303bf80dc9',0,0,'J’ai trouvé Jésus',35,'35_EE_fr','1.\nJ\'ai trouvé Jésus admirable\nLe Dieu fort le Prince de paix\nQui vient dans ce monde coupable\nSemez à jamais ses bienfaits.\n\nRefrain\nChacun devrait savoir\nQu\'en Christ est mon espoir\nServir mon Sauveur adorable\nC\'est mon suprême devoir.\n\n2.\nJ\'ai trouvé la grande famille\nLes vaillants témoins de l\'Agneau\nTout inscrits aux livres de vie\nEt par l\'esprit né de nouveau.\n\n3.\nJ\'ai trouvé la paix de mon âme\nLe repos si longtemps cherché\nLe repos que chacun réclame\nEn un Dieu se trouve et caché.\n\n4.\nJ\'ai trouvé la sainte boussole\nQui me dirige vers le port\nC\'est de Dieu la bonne parole\nSeule peut me rendre fort et joyeux.','<p><strong>1.</strong> </p>\n<p>J&#39;ai trouvé Jésus admirable </p>\n<p>Le Dieu fort le Prince de paix </p>\n<p>Qui vient dans ce monde coupable </p>\n<p>Semez à jamais ses bienfaits. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chacun devrait savoir</strong> </p>\n<p><strong>Qu&#39;en Christ est mon espoir</strong> </p>\n<p><strong>Servir mon Sauveur adorable</strong> </p>\n<p><strong>C&#39;est mon suprême devoir.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai trouvé la grande famille </p>\n<p>Les vaillants témoins de l&#39;Agneau </p>\n<p>Tout inscrits aux livres de vie </p>\n<p>Et par l&#39;esprit né de nouveau. </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;ai trouvé la paix de mon âme </p>\n<p>Le repos si longtemps cherché </p>\n<p>Le repos que chacun réclame </p>\n<p>En un Dieu se trouve et caché. </p>\n<p><br/> <strong>4.</strong></p>\n<p>J&#39;ai trouvé la sainte boussole </p>\n<p>Qui me dirige vers le port </p>\n<p>C&#39;est de Dieu la bonne parole </p>\n<p>Seule peut me rendre fort et joyeux. </p>\n','J\'ai-trouve-Jesus',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1896,'b7cec526-dc24-49be-9969-a4fbdee11ab0',0,0,'Kinbé fèm travay nap fè',71,'71_EE_ht','1.\nKinbé fè-m trava nap fè pou Jézu-Kri\nRout la pa fasil, fòk nou toujou soufri\nLè satan ap fè ravaj li, nou va tris\nNou konnin Jézu la, Li va kinbé nou.\n\nKè:\nKinbé fè-m jouk sa fini\nKinbé fè-m jouk Kris vini\nKinbé fè-m, piga ou lagé\nLè Jézu la ou pa janmin fatigé.\n\n2.\nGnou sòlda pa jan-m touché san-l pa travay\nSé pou li toujou fidèl nan tout bagay\nLi pap janmin rékilé lè-l nan batay\nNou konnin aprè batay nap gin viktoua.\n\n3.\nJézu séré gnou bèl kouròn pou tout krétyin\nKi toujou rété fidèl jouska la fin\nVa gin tout kalité kouròn nan jou sa-a\nLuté byin pou nou ka pran kouròn yo.','<p><strong>1.</strong> </p>\n<p>Kinbé fè-m trava nap fè pou Jézu-Kri </p>\n<p>Rout la pa fasil, fòk nou toujou soufri </p>\n<p>Lè satan ap fè ravaj li, nou va tris </p>\n<p>Nou konnin Jézu la, Li va kinbé nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kinbé fè-m jouk sa fini</strong> </p>\n<p><strong>Kinbé fè-m jouk Kris vini</strong> </p>\n<p><strong>Kinbé fè-m, piga ou lagé</strong> </p>\n<p><strong>Lè Jézu la ou pa janmin fatigé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou sòlda pa jan-m touché san-l pa travay </p>\n<p>Sé pou li toujou fidèl nan tout bagay </p>\n<p>Li pap janmin rékilé lè-l nan batay </p>\n<p>Nou konnin aprè batay nap gin viktoua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu séré gnou bèl kouròn pou tout krétyin </p>\n<p>Ki toujou rété fidèl jouska la fin </p>\n<p>Va gin tout kalité kouròn nan jou sa-a </p>\n<p>Luté byin pou nou ka pran kouròn yo. </p>\n','Kinbe-fem-travay-nap-fe',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1897,'19b1ab4e-b122-4a6a-bb50-7aee5eada62a',0,0,'Gingnin gnou rout si bon',76,'76_EE_ht','1.\nMouin pap fiksé angnin\nNan lé mòn-n ankò\nLè-m dòmi sé sièl la m-révé\nMouin gingnin  pou-m alé nan péyi anro-a\nPou-m al joui bonè ki pap jan-m fini.\n\nKè:\nBonè ki papé jan-m fini\nMa rété ak Bon-Dieu papa mouin\nMouin pap fiksé lé mòn-n\nZèv li yo pa pou mouin\nMa pralé jouin Papa-m anro-a.\n\n2.\nMouin konnin gin anpil problèm sou rout la\nDoulè ak anpil tèt chajé.\nMouin gingnin pou-m priyé\nSa va gin pou-l volé\nFò-m alé jouin-n Papa-m anro-a.\n\n3.\nKonsa gin anpil baga ki rézèvé\nPou tout krétyin kap luté\nGin anpil bèl bagay kap tan-n nou nan sièl la\nLè-n alé nan péyi anro-a.\n\n4.\nLè nou va rivé nan palè rouaya-l la\nNou va rankontré ak zanj yo\nNou va anbrasé yo, nan palè nan sièl la\nGnou bonè ki pap jan-m fini.','<p><strong>1.</strong> </p>\n<p>Mouin pap fiksé angnin </p>\n<p>Nan lé mòn-n ankò </p>\n<p>Lè-m dòmi sé sièl la m-révé </p>\n<p>Mouin gingnin  pou-m alé nan péyi anro-a </p>\n<p>Pou-m al joui bonè ki pap jan-m fini. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bonè ki papé jan-m fini</strong> </p>\n<p><strong>Ma rété ak Bon-Dieu papa mouin</strong> </p>\n<p><strong>Mouin pap fiksé lé mòn-n</strong> </p>\n<p><strong>Zèv li yo pa pou mouin</strong> </p>\n<p><strong>Ma pralé jouin Papa-m anro-a.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin konnin gin anpil problèm sou rout la </p>\n<p>Doulè ak anpil tèt chajé. </p>\n<p>Mouin gingnin pou-m priyé </p>\n<p>Sa va gin pou-l volé </p>\n<p>Fò-m alé jouin-n Papa-m anro-a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Konsa gin anpil baga ki rézèvé </p>\n<p>Pou tout krétyin kap luté </p>\n<p>Gin anpil bèl bagay kap tan-n nou nan sièl la </p>\n<p>Lè-n alé nan péyi anro-a. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè nou va rivé nan palè rouaya-l la </p>\n<p>Nou va rankontré ak zanj yo </p>\n<p>Nou va anbrasé yo, nan palè nan sièl la </p>\n<p>Gnou bonè ki pap jan-m fini. </p>\n','Gingnin-gnou-rout-si-bon',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1898,'7466d1c3-bb0b-45c9-b3ba-bd62489de66a',0,0,'Lè ou konté moun ki pou Jézu',80,'80_EE_ht','1.\nLè ou konté moun ki pou Jézu\nKonté-m ladan,\nNan moun ki konfié nan paròl Li\nKonté-m ladan.\n\nKè:\nKonté-m pami pitit ki pou Roua sièl la\nKonté-m tankou sèvitè k-ap sèvi ya;\nKonté-m pami moun sové, Alélouya!\nKonté-m ladan.\n\n2.\nLè ou konté moun ki nan la gras\nKonté-m ladan\nSa yo an Kris gingnin gnou plas\nKonté-m ladan.\n\n3.\nLè ou konté témouin yo ki pou Kris\nKonté-m ladan\nKi va gingnin kourò-n la justis\nKonté-m ladan.','<p><strong>1.</strong> </p>\n<p>Lè ou konté moun ki pou Jézu </p>\n<p>Konté-m ladan, </p>\n<p>Nan moun ki konfié nan paròl Li </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konté-m pami pitit ki pou Roua sièl la</strong> </p>\n<p><strong>Konté-m tankou sèvitè k-ap sèvi ya;</strong> </p>\n<p><strong>Konté-m pami moun sové, Alélouya!</strong> </p>\n<p><strong>Konté-m ladan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou konté moun ki nan la gras </p>\n<p>Konté-m ladan </p>\n<p>Sa yo an Kris gingnin gnou plas </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè ou konté témouin yo ki pou Kris </p>\n<p>Konté-m ladan </p>\n<p>Ki va gingnin kourò-n la justis </p>\n<p>Konté-m ladan. </p>\n','Le-ou-konte-moun-ki-pou-Jezu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1899,'172ba277-198a-443a-b90a-3227fbe7e04e',1,0,'Il me conduit, Jésus me conduit',2,'2_MJ_fr','1.\nIl me conduit, Jésus me conduit,\nJour après jour vers le paradis,\nTout en me gardant de l\'ennemi,\nIl me conduit, Jésus me conduit.\nQuand tout est obscur,\nIl est un appui sûr,\nCar Il me conduit.','<p><strong>1.</strong> </p>\n<p>Il me conduit, Jésus me conduit, </p>\n<p>Jour après jour vers le paradis, </p>\n<p>Tout en me gardant de l&#39;ennemi, </p>\n<p>Il me conduit, Jésus me conduit. </p>\n<p>Quand tout est obscur, </p>\n<p>Il est un appui sûr, </p>\n<p>Car Il me conduit. </p>\n','Il-me-conduit-Jesus-me-conduit',NULL,'fr',1,'2023-07-22 22:41:15','2023-08-19 05:01:05'),(1900,'c7359b3c-b42f-4ae0-806e-0470df185eaf',0,0,'Je suis le Roc, Alléluia',11,'11_MJ_fr','1.\nJe suis sur le Roc, alléluia\nSur le Roc à jamais, alléluia\nCar Il me sauva, me donna Sa paix\nSur le Roc à jamais.\n\n2.\nJe suis à l\'abri, alléluia\nA l\'abri à jamais, alléluia\nCar Il me cherchait quand je m\'égarais\nA l\'abri à jamais.','<p><strong>1.</strong> </p>\n<p>Je suis sur le Roc, alléluia </p>\n<p>Sur le Roc à jamais, alléluia </p>\n<p>Car Il me sauva, me donna Sa paix </p>\n<p>Sur le Roc à jamais. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je suis à l&#39;abri, alléluia </p>\n<p>A l&#39;abri à jamais, alléluia </p>\n<p>Car Il me cherchait quand je m&#39;égarais </p>\n<p>A l&#39;abri à jamais. </p>\n','Je-suis-le-Roc-Alleluia',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1901,'28954952-a093-4d6f-a4af-6efa0b6a9fad',0,0,'Amis venez!',55,'55_MJ_fr','1.\nAmis venez!\nLe Maître nous entraîne:\nOuvrons nos coeurs\nTout grands à Son Esprit;\nQue pleins de feu,\nS\'élancent dans l\'arène\nLes plus fervents:\nLes tièdes sont bannis!\n\nChoeur:\nSerrons les rangs sous la bannière\nDe notre Roi victorieux.\nQue rien n\'altère la foi si claire\nQui nous unit au Fils de Dieu.\nAh! Qu\'il fait beau chanter la gloire\nDe notre Roi victorieux.\nPour qui veut croire, c\'est la victoire,\nEt c\'est la joie en notre Dieu.\n\n2.\nPour être forts,\nVivons dans la prière:\nAvec le Christ,\nOn peut braver l\'enfer,\nIl est l\'Ami, pour nous\nPlus près qu\'un frère,\nIl fait fleurir\nLes coeurs les plus déserts.\n\n3.\nTout bon soldat\nRevêt de Dieu l\'armure\nEt sans broncher\nSe charge de sa croix.\nPatrie, exil, succès\nOu vile injure\nTelle est la rude école de la foi.\n\n4.\nDe nous il faut\nQu\'un saint amour s\'empare,\nL\'amour qui sait\nDonner sans se lasser.\nQui va chercher\nLes âmes qui s\'égarent,\nEt verse un baume\nSur les coeur blessés.','<p><strong>1.</strong> </p>\n<p>Amis venez! </p>\n<p>Le Maître nous entraîne: </p>\n<p>Ouvrons nos coeurs </p>\n<p>Tout grands à Son Esprit; </p>\n<p>Que pleins de feu, </p>\n<p>S&#39;élancent dans l&#39;arène </p>\n<p>Les plus fervents: </p>\n<p>Les tièdes sont bannis! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Serrons les rangs sous la bannière</strong> </p>\n<p><strong>De notre Roi victorieux.</strong> </p>\n<p><strong>Que rien n&#39;altère la foi si claire</strong> </p>\n<p><strong>Qui nous unit au Fils de Dieu.</strong> </p>\n<p><strong>Ah! Qu&#39;il fait beau chanter la gloire</strong> </p>\n<p><strong>De notre Roi victorieux.</strong> </p>\n<p><strong>Pour qui veut croire, c&#39;est la victoire,</strong> </p>\n<p><strong>Et c&#39;est la joie en notre Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour être forts, </p>\n<p>Vivons dans la prière: </p>\n<p>Avec le Christ, </p>\n<p>On peut braver l&#39;enfer, </p>\n<p>Il est l&#39;Ami, pour nous </p>\n<p>Plus près qu&#39;un frère, </p>\n<p>Il fait fleurir </p>\n<p>Les coeurs les plus déserts. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout bon soldat </p>\n<p>Revêt de Dieu l&#39;armure </p>\n<p>Et sans broncher </p>\n<p>Se charge de sa croix. </p>\n<p>Patrie, exil, succès </p>\n<p>Ou vile injure </p>\n<p>Telle est la rude école de la foi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>De nous il faut </p>\n<p>Qu&#39;un saint amour s&#39;empare, </p>\n<p>L&#39;amour qui sait </p>\n<p>Donner sans se lasser. </p>\n<p>Qui va chercher </p>\n<p>Les âmes qui s&#39;égarent, </p>\n<p>Et verse un baume </p>\n<p>Sur les coeur blessés. </p>\n','Amis-venez!',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1902,'5d234ea4-463a-4d38-8676-8c12ba271d70',0,0,'Quel bonheur',36,'36_VR_fr','1.	\nQuel bonheur d\'être en famille\nD\'être aimé, surtout d\'aimer\nAu doux foyer qui pétille\nL\'homme fort peut se former\nEntouré de ce qu\'on aime\nOn doit cultiver son coeur\nOn se connait mieux soit-même (bis)\nEt l\'on devient un vainqueur. (bis)\n\n2.	\nLa victoire est difficile\nMais le combat bienfaisant\nSi parfois l\'amour vacille\nCe n\'est pas pour bien longtemps\nOn supporte avec tendresse\nOn pardonne avec bonheur\nLe coeur rajeunit sans cesse (bis)\nL\'amour est un grand vainqueur. (bis)\n\n3.	\nQu\'une humeur toujours sereine\nAvec la céleste paix\nRègle seule en souveraine\nA mon foyer désormais\nCher foyer, combien je t\'aime\nCombien je vais te choyer\nEn attendant qu\'au ciel même (bis)\nSe transporte mon foyer. (bis)','<p><strong>1.</strong> </p>\n<p>Quel bonheur d&#39;être en famille </p>\n<p>D&#39;être aimé, surtout d&#39;aimer </p>\n<p>Au doux foyer qui pétille </p>\n<p>L&#39;homme fort peut se former </p>\n<p>Entouré de ce qu&#39;on aime </p>\n<p>On doit cultiver son coeur </p>\n<p>On se connait mieux soit-même (bis) </p>\n<p>Et l&#39;on devient un vainqueur. (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>La victoire est difficile </p>\n<p>Mais le combat bienfaisant </p>\n<p>Si parfois l&#39;amour vacille </p>\n<p>Ce n&#39;est pas pour bien longtemps </p>\n<p>On supporte avec tendresse </p>\n<p>On pardonne avec bonheur </p>\n<p>Le coeur rajeunit sans cesse (bis) </p>\n<p>L&#39;amour est un grand vainqueur. (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu&#39;une humeur toujours sereine </p>\n<p>Avec la céleste paix </p>\n<p>Règle seule en souveraine </p>\n<p>A mon foyer désormais </p>\n<p>Cher foyer, combien je t&#39;aime </p>\n<p>Combien je vais te choyer </p>\n<p>En attendant qu&#39;au ciel même (bis) </p>\n<p>Se transporte mon foyer. (bis) </p>\n','Quel-bonheur',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1903,'37bead1d-66ee-43ac-b4fa-3078d6f32a90',0,0,'Témouin',11,'11_VR_ht','1.	\nMouin sé témouin Jézu-Kris\nM-pap troublé la Bib\nLé mon-n kouè ya jouin-n Sovè\nSan pa Jézu-Kri.\nSatan ban-n gnou fo dokti-n\nPou-n pa fè kòlè\nMinistè sou la tè\nToujou gin finans.\n\nKè:\nO oui pou-m témouin\nFò-m konnin Jézu\nKa Létènèl fè Li\nAvan nou té la\nLòm pa jouin-n a Jézu-Kri\nSan pa Léspri Sin\nAlélouya-m té pèdi\nMin-m trouvé la vi.\n\n2.	\nAbraham, pè dé la foua\nLi pa sa témouin\nAvan Abraham té fèt\nJézu-Kri té la\nSi-m témouin sa-m pa konnin\nPa la vérité\nPou-m témouin vrè témouin\nFò-m nan Jézu-Kri. \n\n3.	\nLè-m kouè-m té pré Jéova\nPoutan-m té trè louin\nLè map chanjé mo yo\nM-té vlé ouè rézon\nTout pouvoua pou Jézu-Kri\nNan sièl é la tè\nM-oui tap palé vanité\nÉré pa lé van\n\n4.	\nDié toua foua Sin, Jéova\nA travè lé mon-n\nAbraham, Izaak é Jakòb\nYo pa vrè témouin\nLòm ki vrèman vrè témouin\nSòf  Émmanièl\nGloua a Dié, gloua a Dié\nJézu, Roua Dè roua','<p><strong>1.</strong> </p>\n<p>Mouin sé témouin Jézu-Kris </p>\n<p>M-pap troublé la Bib </p>\n<p>Lé mon-n kouè ya jouin-n Sovè </p>\n<p>San pa Jézu-Kri. </p>\n<p>Satan ban-n gnou fo dokti-n </p>\n<p>Pou-n pa fè kòlè </p>\n<p>Ministè sou la tè </p>\n<p>Toujou gin finans. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O oui pou-m témouin</strong> </p>\n<p><strong>Fò-m konnin Jézu</strong> </p>\n<p><strong>Ka Létènèl fè Li</strong> </p>\n<p><strong>Avan nou té la</strong> </p>\n<p><strong>Lòm pa jouin-n a Jézu-Kri</strong> </p>\n<p><strong>San pa Léspri Sin</strong> </p>\n<p><strong>Alélouya-m té pèdi</strong> </p>\n<p><strong>Min-m trouvé la vi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Abraham, pè dé la foua </p>\n<p>Li pa sa témouin </p>\n<p>Avan Abraham té fèt </p>\n<p>Jézu-Kri té la </p>\n<p>Si-m témouin sa-m pa konnin </p>\n<p>Pa la vérité </p>\n<p>Pou-m témouin vrè témouin </p>\n<p>Fò-m nan Jézu-Kri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè-m kouè-m té pré Jéova </p>\n<p>Poutan-m té trè louin </p>\n<p>Lè map chanjé mo yo </p>\n<p>M-té vlé ouè rézon </p>\n<p>Tout pouvoua pou Jézu-Kri </p>\n<p>Nan sièl é la tè </p>\n<p>M-oui tap palé vanité </p>\n<p>Éré pa lé van </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dié toua foua Sin, Jéova </p>\n<p>A travè lé mon-n </p>\n<p>Abraham, Izaak é Jakòb </p>\n<p>Yo pa vrè témouin </p>\n<p>Lòm ki vrèman vrè témouin </p>\n<p>Sòf  Émmanièl </p>\n<p>Gloua a Dié, gloua a Dié </p>\n<p>Jézu, Roua Dè roua </p>\n','Temouin',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1904,'26de4568-0e52-430e-a9bb-23a234c750a4',0,0,'Jézu ou sé mèt mouin',4,'4_EE_ht','1.\nJézu ou sé mè mouin, Sovè mouin\nBan mouin kouraj pou mouin kapab luté\nChémin an noua épi li glisé\nKòman pou mouin fè si Ou pa édé-m.\n\nKè:\nSi Ou pa té la, sa mouin ta fè\nLè-m santi mouin tris é dékourajé\nLè tout bagay parèt noua pou mouin\nOu toujou la pou konsolé-m.\n\n2.\nSégnè kinbé mouin nan gras Ou\nPou mouin pa tonbé nan vié péché\nNan Ou mouin jouin-n la joua\nMouin jouin-n bonè\n\n3.\nSalu étènèl ké-m pat kab achté.\nKi sa ki kab séparé-m ak Ou\nÈské sé problèm, sé touman, sé richès.\nMouin vlé viv pou Ou sèl é pou Ou toujou.','<p><strong>1.</strong> </p>\n<p>Jézu ou sé mè mouin, Sovè mouin </p>\n<p>Ban mouin kouraj pou mouin kapab luté </p>\n<p>Chémin an noua épi li glisé </p>\n<p>Kòman pou mouin fè si Ou pa édé-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Si Ou pa té la, sa mouin ta fè</strong> </p>\n<p><strong>Lè-m santi mouin tris é dékourajé</strong> </p>\n<p><strong>Lè tout bagay parèt noua pou mouin</strong> </p>\n<p><strong>Ou toujou la pou konsolé-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ségnè kinbé mouin nan gras Ou </p>\n<p>Pou mouin pa tonbé nan vié péché </p>\n<p>Nan Ou mouin jouin-n la joua </p>\n<p>Mouin jouin-n bonè </p>\n<p><br/> <strong>3.</strong></p>\n<p>Salu étènèl ké-m pat kab achté. </p>\n<p>Ki sa ki kab séparé-m ak Ou </p>\n<p>Èské sé problèm, sé touman, sé richès. </p>\n<p>Mouin vlé viv pou Ou sèl é pou Ou toujou. </p>\n','Jezu-ou-se-met-mouin',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1905,'f8525f1f-3d2a-42df-8d62-cf58c4d72390',0,0,'Grâce infinie',31,'31_EE_fr','1.\nGrâce infinie, O quel beau don\nPour moi pécheur, qui crois\nJ\'étais perdu, j\'ai le pardon,\nAveugle mais je vois.\n\n2.\nLa grâce a mis la crainte en moi\nEt m\'en a délivré\nO grâce précieuse à ma foi. \nLorsque j\'irai cédé.\n\n3.\nAu travers des temps infinis\nLes chants de tes élus\nSeront toujours ta grâce, O Christ\nComme au temps ou j\'ai cru.','<p><strong>1.</strong> </p>\n<p>Grâce infinie, O quel beau don </p>\n<p>Pour moi pécheur, qui crois </p>\n<p>J&#39;étais perdu, j&#39;ai le pardon, </p>\n<p>Aveugle mais je vois. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La grâce a mis la crainte en moi </p>\n<p>Et m&#39;en a délivré </p>\n<p>O grâce précieuse à ma foi. </p>\n<p>Lorsque j&#39;irai cédé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au travers des temps infinis </p>\n<p>Les chants de tes élus </p>\n<p>Seront toujours ta grâce, O Christ </p>\n<p>Comme au temps ou j&#39;ai cru. </p>\n','Grace-infinie',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1906,'1add2903-0a68-4a03-bcc1-6a090d8e4d44',0,0,'Mouin pa kapab blié',45,'45_EE_ht','1.\nMouin pa kapab blié ki koté-m té yé\nLè Jézu té jouin-n mouin, la vi-m té chanjé\nSé pou sa map chanté paské Li sové-m\nSi Satan vlé baré-m ma va rélé Jézu.\n\nKè:\nJézu va roulé roch la\nKi vi-n baré-m nan mò-n la  (3 foua)\nÉ-m konnin gnou jou sa va chanjé.\n\n2.\nMouin pa janmin blié ki jan sa té yé\nLéspri-m té boulvèsé paské-m té maré\nLè-m vi-n jouin-n ak Jézu tout bagay chanjé\nRoch la té mèt baré-m Jézu va délivré-m.\n\n3.\nPafoi-m kon-n ap krié dévan problèm yo\nMouin désan-n sou jénou map rélé Jézu\nMouin tandé voi Jézu kap palé avè-m\nLi di mouin pran kouraj tout bagay va chanjé.\n\n4.\nSi ou santi ou tris é dékourajé\nOu lévé ouap krié ou pa gin éspoua\nOu mèt jouin-n ak Jézu la konsolé ou\nOui sé Li min-m ki di Li va roulé roch la\n\n5.\nOu té mèt méprizé ou pa gin zanmi\nSi ou sé zanmi Jézu ou gin tout bagay\nLa vi ou té mèt tou noua \nGnou jou sa va chanjé.','<p><strong>1.</strong> </p>\n<p>Mouin pa kapab blié ki koté-m té yé </p>\n<p>Lè Jézu té jouin-n mouin, la vi-m té chanjé </p>\n<p>Sé pou sa map chanté paské Li sové-m </p>\n<p>Si Satan vlé baré-m ma va rélé Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu va roulé roch la</strong> </p>\n<p><strong>Ki vi-n baré-m nan mò-n la  (3 foua)</strong> </p>\n<p><strong>É-m konnin gnou jou sa va chanjé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin pa janmin blié ki jan sa té yé </p>\n<p>Léspri-m té boulvèsé paské-m té maré </p>\n<p>Lè-m vi-n jouin-n ak Jézu tout bagay chanjé </p>\n<p>Roch la té mèt baré-m Jézu va délivré-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pafoi-m kon-n ap krié dévan problèm yo </p>\n<p>Mouin désan-n sou jénou map rélé Jézu </p>\n<p>Mouin tandé voi Jézu kap palé avè-m </p>\n<p>Li di mouin pran kouraj tout bagay va chanjé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si ou santi ou tris é dékourajé </p>\n<p>Ou lévé ouap krié ou pa gin éspoua </p>\n<p>Ou mèt jouin-n ak Jézu la konsolé ou </p>\n<p>Oui sé Li min-m ki di Li va roulé roch la </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ou té mèt méprizé ou pa gin zanmi </p>\n<p>Si ou sé zanmi Jézu ou gin tout bagay </p>\n<p>La vi ou té mèt tou noua </p>\n<p>Gnou jou sa va chanjé. </p>\n','Mouin-pa-kapab-blie',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1907,'5723a7fe-7883-4e7e-a794-c950bf768396',0,0,'Nan la vi-a nou',54,'54_EE_ht','1.\nNan la vi-a nou rankontré anpil problèm\nAnpil bagay ki fè nou pèdi tèt nou\nProblè a douat, problè a goch\nSoufrans jodi, soufrans démin\nNap mandé ki lè pou sa fini\nNou lévé tèt nou di Bon-Dieu\nNou vlé sési, nou vlé séla\nSouvan priyè nou pa ékszosé.\n\nKè:\nChéché Jézu ak tout kè nou\nÉ Li va délivré nou é Li va ékszosé nou\nChèché-L avan vanité yo\nÉ tout bagay mal va fini pou nou.\n\n2.\nRoua Salomon té ba nou gnou trè bèl égzamp\nBon-Dieu té di-l lap bali sa li mandé\nRoua Salomon pa té mandé pou-l gin lajan\nPou-l gin richès\nSèlman-l té mandé Bon-Dieu sajès\nKonsa kè Bon-Dieu té kontan\nLi bay Salomon sajès ak richès\nLè zòm pap jan-m gingnin.','<p><strong>1.</strong> </p>\n<p>Nan la vi-a nou rankontré anpil problèm </p>\n<p>Anpil bagay ki fè nou pèdi tèt nou </p>\n<p>Problè a douat, problè a goch </p>\n<p>Soufrans jodi, soufrans démin </p>\n<p>Nap mandé ki lè pou sa fini </p>\n<p>Nou lévé tèt nou di Bon-Dieu </p>\n<p>Nou vlé sési, nou vlé séla </p>\n<p>Souvan priyè nou pa ékszosé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chéché Jézu ak tout kè nou</strong> </p>\n<p><strong>É Li va délivré nou é Li va ékszosé nou</strong> </p>\n<p><strong>Chèché-L avan vanité yo</strong> </p>\n<p><strong>É tout bagay mal va fini pou nou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Roua Salomon té ba nou gnou trè bèl égzamp </p>\n<p>Bon-Dieu té di-l lap bali sa li mandé </p>\n<p>Roua Salomon pa té mandé pou-l gin lajan </p>\n<p>Pou-l gin richès </p>\n<p>Sèlman-l té mandé Bon-Dieu sajès </p>\n<p>Konsa kè Bon-Dieu té kontan </p>\n<p>Li bay Salomon sajès ak richès </p>\n<p>Lè zòm pap jan-m gingnin. </p>\n','Nan-la-vi-a-nou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1908,'fcc34295-d22d-4d56-bd67-a9dd88eb1576',0,0,'Gin gnou voua',56,'56_EE_ht','1.\nGin gnou voua douséman\nKi rélé-m pandan nuit la\nLi di-m lévé min énmi ou yo déyò-a\nLè-m té louvri jé-m\nM-ouè énmi yo déyò-a\nMouin di Ségnè édé mouin-o non souplé.\n\nKè:\nSégnè rout la du anpil\nLòt bò mòn nan map pralé\nÉ-m konnin ma rivé.\n\n2.\nMouin té malad, paran-m yo di map mouri\nMouin di Ségnè désan-n amé sélès la\nLè-m té louvri zié mouin\nMouin ouè amé ap désan-n\nKapitèn nan pran dévan lap vi-n sové-m.','<p><strong>1.</strong> </p>\n<p>Gin gnou voua douséman </p>\n<p>Ki rélé-m pandan nuit la </p>\n<p>Li di-m lévé min énmi ou yo déyò-a </p>\n<p>Lè-m té louvri jé-m </p>\n<p>M-ouè énmi yo déyò-a </p>\n<p>Mouin di Ségnè édé mouin-o non souplé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ségnè rout la du anpil</strong> </p>\n<p><strong>Lòt bò mòn nan map pralé</strong> </p>\n<p><strong>É-m konnin ma rivé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té malad, paran-m yo di map mouri </p>\n<p>Mouin di Ségnè désan-n amé sélès la </p>\n<p>Lè-m té louvri zié mouin </p>\n<p>Mouin ouè amé ap désan-n </p>\n<p>Kapitèn nan pran dévan lap vi-n sové-m. </p>\n','Gin-gnou-voua',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1909,'dc8d7adb-1178-48f7-b71a-902b015f27ca',0,0,'Supozé Bon-Dieu',64,'64_EE_ht','1.\nSipozé Bon-Dieu té chèché nan sièl\nLi pat kap jouin-n pèson-n ki té vlé\nPou-l té bay siprèm sakrifis nésésè\nLi bay vi Li pou té sové mouin ak ou.\n\nKè:\nO, O, si sé pat pou gnou plas\nYo rélé mò-n Kalvè,\nSi sé pa té sou pié boua la koua-a\nSi sé pat pou Non-m sa-a yo rélé Jézu ya\nÉ pou toujou mouin min-m ak ou ta pèdu.\n\n2.\nM-si kontan Li t-aksépté bouè koup amè\nMalgré priyè, pè lésé-l pasé louin\nM-si kontan Li pat rélé zanj nan sièl yo\nPou vi-n ouété klou kap toumanté min-l yo.','<p><strong>1.</strong> </p>\n<p>Sipozé Bon-Dieu té chèché nan sièl </p>\n<p>Li pat kap jouin-n pèson-n ki té vlé </p>\n<p>Pou-l té bay siprèm sakrifis nésésè </p>\n<p>Li bay vi Li pou té sové mouin ak ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O, O, si sé pat pou gnou plas</strong> </p>\n<p><strong>Yo rélé mò-n Kalvè,</strong> </p>\n<p><strong>Si sé pa té sou pié boua la koua-a</strong> </p>\n<p><strong>Si sé pat pou Non-m sa-a yo rélé Jézu ya</strong> </p>\n<p><strong>É pou toujou mouin min-m ak ou ta pèdu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-si kontan Li t-aksépté bouè koup amè </p>\n<p>Malgré priyè, pè lésé-l pasé louin </p>\n<p>M-si kontan Li pat rélé zanj nan sièl yo </p>\n<p>Pou vi-n ouété klou kap toumanté min-l yo. </p>\n','Supoze-Bon-Dieu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1910,'a47ed336-a697-41f5-86dc-040923c17ac5',0,0,'Homme de douleur il fut',4,'4_MJ_fr','1.\nHomme de douleur Il fut,\nQuand au monde Il est venue\nPour racheter les perdus,\nAlléluia, gloire au Sauveur!\n\n2.\nFrappé, blessé, humilié,\nA ma place Il fut jugé,\nPar Son sang je suis sauvé\nAlléluia, gloire au Sauveur.\n\n3.\nMourant sur le bois maudit\nIl dit «Tout est accompli!»\nEt puis Il rendit l\'esprit –\nAlléluia, gloire au Sauveur.\n\n4.\nQuand Il viendra glorieux\nPour nous introduire aux cieux\nNous dirons victorieux:\nAlléluia, gloire au Seigneur.','<p><strong>1.</strong> </p>\n<p>Homme de douleur Il fut, </p>\n<p>Quand au monde Il est venue </p>\n<p>Pour racheter les perdus, </p>\n<p>Alléluia, gloire au Sauveur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Frappé, blessé, humilié, </p>\n<p>A ma place Il fut jugé, </p>\n<p>Par Son sang je suis sauvé </p>\n<p>Alléluia, gloire au Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mourant sur le bois maudit </p>\n<p>Il dit «Tout est accompli!» </p>\n<p>Et puis Il rendit l&#39;esprit – </p>\n<p>Alléluia, gloire au Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand Il viendra glorieux </p>\n<p>Pour nous introduire aux cieux </p>\n<p>Nous dirons victorieux: </p>\n<p>Alléluia, gloire au Seigneur. </p>\n','Homme-de-douleur-il-fut',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1911,'95e5fc86-256f-4119-8e7e-aed555d219b5',0,0,'Ton amour, ton amour',5,'5_MJ_fr','1.\nTon amour, Ton amour,\nVoilà, Seigneur,\nCe qu\'il faut à mon coeur,\nOui, c\'est Ton amour!\n\n2.\nTon pardon, Ton pardon,\nVoilà, Seigneur,\nCe qu\'il faut à mon coeur,\nOui, c\'est Ton pardon!\n\n3.\nTon salut, Ton salut,\nVoilà Seigneur,\nCe qu\'il faut à mon coeur,\nOui, c\'est Ton salut!','<p><strong>1.</strong> </p>\n<p>Ton amour, Ton amour, </p>\n<p>Voilà, Seigneur, </p>\n<p>Ce qu&#39;il faut à mon coeur, </p>\n<p>Oui, c&#39;est Ton amour! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton pardon, Ton pardon, </p>\n<p>Voilà, Seigneur, </p>\n<p>Ce qu&#39;il faut à mon coeur, </p>\n<p>Oui, c&#39;est Ton pardon! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ton salut, Ton salut, </p>\n<p>Voilà Seigneur, </p>\n<p>Ce qu&#39;il faut à mon coeur, </p>\n<p>Oui, c&#39;est Ton salut! </p>\n','Ton-amour-ton-amour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1912,'d64bf0f0-759b-42d9-a583-21c0322c2e6f',0,0,'C\'est une joie de servir Jésus',10,'10_MJ_fr','1.\nC\'est une joie de servir Jésus\nDans mon voyage ici-bas.\nEt je peux toujours Le louer\nMême au plus fort des combats.\n\nChoeur:\nC\'est une joie, joie, joie de servir Jésus\nJoie qui jaillit dans mon coeur.\nChaque moment, chaque jour\nIl me tient dans Son amour\nC\'est une joie, joie, joie\nQue donne mon Sauveur\n\n2.\nC\'est une joie de servir Jésus\nJoie même dans la détresse,\nQui remplit mon coeur de musique\nEt qui chasse ma tristesse.\n\n3.\nC\'est une joie de servir Jésus\nJoie dans la plus sombre nuit;\nCar Jésus connaît le chemin\nJe peux marcher avec Lui.\n\n4.\nC\'est une joie de servir Jésus\nJoie de servir mon Sauveur\nQui pour mes péchés fut blessé\nAppelé l\'homme de douleur.','<p><strong>1.</strong> </p>\n<p>C&#39;est une joie de servir Jésus </p>\n<p>Dans mon voyage ici-bas. </p>\n<p>Et je peux toujours Le louer </p>\n<p>Même au plus fort des combats. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>C&#39;est une joie, joie, joie de servir Jésus</strong> </p>\n<p><strong>Joie qui jaillit dans mon coeur.</strong> </p>\n<p><strong>Chaque moment, chaque jour</strong> </p>\n<p><strong>Il me tient dans Son amour</strong> </p>\n<p><strong>C&#39;est une joie, joie, joie</strong> </p>\n<p><strong>Que donne mon Sauveur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est une joie de servir Jésus </p>\n<p>Joie même dans la détresse, </p>\n<p>Qui remplit mon coeur de musique </p>\n<p>Et qui chasse ma tristesse. </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est une joie de servir Jésus </p>\n<p>Joie dans la plus sombre nuit; </p>\n<p>Car Jésus connaît le chemin </p>\n<p>Je peux marcher avec Lui. </p>\n<p><br/> <strong>4.</strong></p>\n<p>C&#39;est une joie de servir Jésus </p>\n<p>Joie de servir mon Sauveur </p>\n<p>Qui pour mes péchés fut blessé </p>\n<p>Appelé l&#39;homme de douleur. </p>\n','C\'est-une-joie-de-servir-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1913,'262f23d3-b966-41d0-93a3-6cf94156cca4',0,0,'La fontaine de l’eau vive',47,'47_VR_fr','1.	\nLa fontaine de l\'eau vive\nJaillit pour nous  (bis)\nLa fontaine de l\'eau vive\nJaillit pour nous toujours.\n\nChoeur\nMoi et ma maison, et ma maison, et ma maison,\nMoi et ma maison nous servirons l\'Éternel.\n\n2.   \nLe boulanger le Pain du ciel\nTravaille pour nous  (bis)\nLe boulanger le Pain du ciel\nTravaille pour nous toujours.\n\n3.	\nLe Saint-Esprit en Jésus-Christ,\nDescend sur nous  (bis)\nLe Saint-esprit, en Jésus-Christ,\nDescend sur nous toujours.\n\n4.	\nLe Capitaine Émmanuel\nGouverne-nous, dirige-nous,\nLe Capitaine, le Roi des rois\nFais nous rentrer au port.\n\n5.	\nLa paix de Dieu reste avec nous \nReste avec nous, reste avec nous\nLa paix de Dieu reste avec nous\nReste avec nous toujours.','<p><strong>1.</strong> </p>\n<p>La fontaine de l&#39;eau vive </p>\n<p>Jaillit pour nous  (bis) </p>\n<p>La fontaine de l&#39;eau vive </p>\n<p>Jaillit pour nous toujours. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Moi et ma maison, et ma maison, et ma maison,</strong> </p>\n<p><strong>Moi et ma maison nous servirons l&#39;Éternel.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le boulanger le Pain du ciel </p>\n<p>Travaille pour nous  (bis) </p>\n<p>Le boulanger le Pain du ciel </p>\n<p>Travaille pour nous toujours. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Saint-Esprit en Jésus-Christ, </p>\n<p>Descend sur nous  (bis) </p>\n<p>Le Saint-esprit, en Jésus-Christ, </p>\n<p>Descend sur nous toujours. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le Capitaine Émmanuel </p>\n<p>Gouverne-nous, dirige-nous, </p>\n<p>Le Capitaine, le Roi des rois </p>\n<p>Fais nous rentrer au port. </p>\n<p><br/> <strong>5.</strong></p>\n<p>La paix de Dieu reste avec nous </p>\n<p>Reste avec nous, reste avec nous </p>\n<p>La paix de Dieu reste avec nous </p>\n<p>Reste avec nous toujours. </p>\n','La-fontaine-de-l\'eau-vive',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1914,'c3d486d9-ab28-429f-82a9-24b4c5dd2fe2',0,0,'Louons le Seigneur',6,'6_VR_fr','1.	\nOh! Quelle joie de vivre en Jésus\nD\'être sauvé par Son sang du malheur\nMoi pécheur au rang de ses élus\nPour jouir avec Lui du vrai bonheur.\n\nChoeur\nEn Jésus mon Sauveur,\nJ\'ai trouvé le vrai bonheur.\nEn Christ mon Rédempteur,\nJ\'ai trouvé le vrai bonheur.\nEn Jésus, en Jésus\nLe bonheur, la joie, la vie\nEn Jésus c\'est la paix\nEt la vie éternelle.\n\n2.	\nDe riche qu\'Il était le ciel bénit\nIl s\'est fait pour eux pour m\'enrichir\nOh mon Dieu que veux-tu pour ce don?\nRends-moi nouveau pour ce grand pardon.\n\n3.	\nQui peut compter l\'amour de Jésus\nImitons ce Sauveur glorieux\nTravaillons pour plaire à Jésus\nNous serrons tous aussi glorieux.\nAmen.','<p><strong>1.</strong> </p>\n<p>Oh! Quelle joie de vivre en Jésus </p>\n<p>D&#39;être sauvé par Son sang du malheur </p>\n<p>Moi pécheur au rang de ses élus </p>\n<p>Pour jouir avec Lui du vrai bonheur. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>En Jésus mon Sauveur,</strong> </p>\n<p><strong>J&#39;ai trouvé le vrai bonheur.</strong> </p>\n<p><strong>En Christ mon Rédempteur,</strong> </p>\n<p><strong>J&#39;ai trouvé le vrai bonheur.</strong> </p>\n<p><strong>En Jésus, en Jésus</strong> </p>\n<p><strong>Le bonheur, la joie, la vie</strong> </p>\n<p><strong>En Jésus c&#39;est la paix</strong> </p>\n<p><strong>Et la vie éternelle.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De riche qu&#39;Il était le ciel bénit </p>\n<p>Il s&#39;est fait pour eux pour m&#39;enrichir </p>\n<p>Oh mon Dieu que veux-tu pour ce don? </p>\n<p>Rends-moi nouveau pour ce grand pardon. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui peut compter l&#39;amour de Jésus </p>\n<p>Imitons ce Sauveur glorieux </p>\n<p>Travaillons pour plaire à Jésus </p>\n<p>Nous serrons tous aussi glorieux. </p>\n<p>Amen. </p>\n','Louons-le-Seigneur',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1915,'937fc6d0-cfc5-40f2-adc5-ff16dc33a28f',0,0,'Je me rappelle un jour',29,'29_VR_fr','1.	\nJe me rappelle un jour\nOù dans le ciel je vis\nLe livre dans lequel\nTout péché est écrit.\nJe vis mon nom très clair\nJe dis mon Dieu que faire\nEn tombant à ses pieds\nJe réglai mon affaire.\n\nChoeur\nA l\'instant  (bis)\nOui le vieux compte fut réglé\nA l\'instant\nJe ne suis plus effrayé\nMa dette est déjà payée\nEt mon compte avec mon juge\nEst tout réglé.\n\n2.	\nMon compte était très lourd\nEt grandissant toujours\nO j\'étais égaré\nPêchant plus chaque jour\nMais quand mes yeux s\'ouvrent\nEt je vis mon état\nJe me jetais aux pieds du Sauveur\nA l\'instant.\n\n3.	\nDu jour du jugement\nDevant le trône blanc\nQuand tous les morts viendront\nLes livres s\'ouvriront\nEt quand on cherchera\nMon nom n\'y sera pas\nCar Jésus l\'a écrit\nAu livre de la vie.\n\n4.	\nO pêcheur viens à Lui\nMets toi en règle aussi\nEt fais-le aujourd\'hui\nCar l\'occasion s\'enfuit\nIl te pardonnera\nSi tu fais comme moi\nEn venant à ton Sauveur\nTu fais de Lui ton Roi\nA l\'instant.','<p><strong>1.</strong> </p>\n<p>Je me rappelle un jour </p>\n<p>Où dans le ciel je vis </p>\n<p>Le livre dans lequel </p>\n<p>Tout péché est écrit. </p>\n<p>Je vis mon nom très clair </p>\n<p>Je dis mon Dieu que faire </p>\n<p>En tombant à ses pieds </p>\n<p>Je réglai mon affaire. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>A l&#39;instant  (bis)</strong> </p>\n<p><strong>Oui le vieux compte fut réglé</strong> </p>\n<p><strong>A l&#39;instant</strong> </p>\n<p><strong>Je ne suis plus effrayé</strong> </p>\n<p><strong>Ma dette est déjà payée</strong> </p>\n<p><strong>Et mon compte avec mon juge</strong> </p>\n<p><strong>Est tout réglé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon compte était très lourd </p>\n<p>Et grandissant toujours </p>\n<p>O j&#39;étais égaré </p>\n<p>Pêchant plus chaque jour </p>\n<p>Mais quand mes yeux s&#39;ouvrent </p>\n<p>Et je vis mon état </p>\n<p>Je me jetais aux pieds du Sauveur </p>\n<p>A l&#39;instant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Du jour du jugement </p>\n<p>Devant le trône blanc </p>\n<p>Quand tous les morts viendront </p>\n<p>Les livres s&#39;ouvriront </p>\n<p>Et quand on cherchera </p>\n<p>Mon nom n&#39;y sera pas </p>\n<p>Car Jésus l&#39;a écrit </p>\n<p>Au livre de la vie. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O pêcheur viens à Lui </p>\n<p>Mets toi en règle aussi </p>\n<p>Et fais-le aujourd&#39;hui </p>\n<p>Car l&#39;occasion s&#39;enfuit </p>\n<p>Il te pardonnera </p>\n<p>Si tu fais comme moi </p>\n<p>En venant à ton Sauveur </p>\n<p>Tu fais de Lui ton Roi </p>\n<p>A l&#39;instant. </p>\n','Je-me-rappelle-un-jour',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1916,'6e0f7376-dd0e-4829-880a-1f3ef4c49600',0,0,'Le jour de la moisson viendra',26,'26_EE_fr','1.\nLe jour de la moisson viendra\nPour tout fils d\'adam-né,\nEt ce que l\'homme aura semé,\nIl le moissonnera.\n\nChoeur\nLa moisson d\'ici-bas\nC\'est la moisson d\'en haut\nSelon ce que nos travaux.\nBien ou mal, Il viendra\nCette fois, la moisson croissera.\n\n2.\nSi tu veux semer pour la chair\nContenant ses passions.\nTa moisson sera corruption\nEt tourment de l\'enfer.\n\n3.\nPourquoi sèmerais-tu le mal\nEt la méchanceté?\nQuand tu sèmes pour le péché\nLe sort sera fatal.\n\n4.\nSi tu sèmes la charité\nPar la foi en Jésus-Christ,\nTa moisson sera le salut\nEt la félicité.\n\n5.\nCessez de semer pour la chair\nCar tu vas à la mort\nLa semence assure le sort\nJésus ou Lucifer.','<p><strong>1.</strong> </p>\n<p>Le jour de la moisson viendra </p>\n<p>Pour tout fils d&#39;adam-né, </p>\n<p>Et ce que l&#39;homme aura semé, </p>\n<p>Il le moissonnera. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>La moisson d&#39;ici-bas</strong> </p>\n<p><strong>C&#39;est la moisson d&#39;en haut</strong> </p>\n<p><strong>Selon ce que nos travaux.</strong> </p>\n<p><strong>Bien ou mal, Il viendra</strong> </p>\n<p><strong>Cette fois, la moisson croissera.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tu veux semer pour la chair </p>\n<p>Contenant ses passions. </p>\n<p>Ta moisson sera corruption </p>\n<p>Et tourment de l&#39;enfer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pourquoi sèmerais-tu le mal </p>\n<p>Et la méchanceté? </p>\n<p>Quand tu sèmes pour le péché </p>\n<p>Le sort sera fatal. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si tu sèmes la charité </p>\n<p>Par la foi en Jésus-Christ, </p>\n<p>Ta moisson sera le salut </p>\n<p>Et la félicité. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Cessez de semer pour la chair </p>\n<p>Car tu vas à la mort </p>\n<p>La semence assure le sort </p>\n<p>Jésus ou Lucifer. </p>\n','Le-jour-de-la-moisson-viendra',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1917,'485c2dc2-d68d-44ba-b64b-ee782ad47af0',1,0,'J’ai trouvé la voie',18,'18_VR_fr','1.	\nJ\'ai trouvé la voie\nQui mène au jour sans fin\nLà haut dans le lieu divin.\nMais pour parcourir\nLe chemin sans faillir\nSeigneur tiens-moi près de toi.\n\nChoeur\nJ\'ai trouvé la voie  (bis)\nOh! Gloire alléluia!\nJ\'ai trouvé la voie.\n\n2.	\nQue craindrais-je encore?\nCar avec Jésus-Christ\nJe vais braver l\'ennemi.\nEt mes chants de joie \nEn l\'honneur de mon Roi\nTémoigneront ma victoire.\n\n3.	\nQuand viendra la fin\nConduis-nous par la main\nNous suivons-le sur le chemin\nQui mène au repos\nAlors avec l\'Agneau\nNous verrons la gloire sans fin.','<p><strong>1.</strong> </p>\n<p>J&#39;ai trouvé la voie </p>\n<p>Qui mène au jour sans fin </p>\n<p>Là haut dans le lieu divin. </p>\n<p>Mais pour parcourir </p>\n<p>Le chemin sans faillir </p>\n<p>Seigneur tiens-moi près de toi. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>J&#39;ai trouvé la voie  (bis)</strong> </p>\n<p><strong>Oh! Gloire alléluia!</strong> </p>\n<p><strong>J&#39;ai trouvé la voie.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que craindrais-je encore? </p>\n<p>Car avec Jésus-Christ </p>\n<p>Je vais braver l&#39;ennemi. </p>\n<p>Et mes chants de joie </p>\n<p>En l&#39;honneur de mon Roi </p>\n<p>Témoigneront ma victoire. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand viendra la fin </p>\n<p>Conduis-nous par la main </p>\n<p>Nous suivons-le sur le chemin </p>\n<p>Qui mène au repos </p>\n<p>Alors avec l&#39;Agneau </p>\n<p>Nous verrons la gloire sans fin. </p>\n','J\'ai-trouve-la-voie',NULL,'fr',2,'2023-07-22 22:41:15','2023-09-19 04:50:29'),(1918,'144a4f56-1b6b-4c59-8e40-814874bfdecd',0,0,'Chak pa mouin fè',5,'5_EE_ht','1.\nChak pa mouin fè, Sovè-m alé dévan mouin\nAk min Li kondui mouin nan bon chémin.\nNan zorèy Li mouin palé, m-adoré Li\nNan la joi maché ak Li chak jou.\n\nKè:\nChak pa mouin fè, konnin Jézu ap gidé-m\nNan gnou plin-n pi ro, Li toujou minnin-m,\nJouk tan gnou jou, \nMouin va fè dènié pa mouin\nChak pa mouin fè, kondui-m pi pré sièl la.\n\n2.\nLè asurans mouin kòmansé fayi\nÉ dévan-m gin falèz infini.\nMouin viré jé-m, mouin gadé Bon Sovè mouin\nLi sé fòs mouin, lè mouin maché avè-L.\n\n3.\nMouin konfié nan Kris, malgré sikonstans yo\nJézu gingnin pouvoua an abondans.\nLa pé kinbé klé ya, louvri pòt la\nPou Li minnin-m nan péyi promès la.','<p><strong>1.</strong> </p>\n<p>Chak pa mouin fè, Sovè-m alé dévan mouin </p>\n<p>Ak min Li kondui mouin nan bon chémin. </p>\n<p>Nan zorèy Li mouin palé, m-adoré Li </p>\n<p>Nan la joi maché ak Li chak jou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chak pa mouin fè, konnin Jézu ap gidé-m</strong> </p>\n<p><strong>Nan gnou plin-n pi ro, Li toujou minnin-m,</strong> </p>\n<p><strong>Jouk tan gnou jou,</strong> </p>\n<p><strong>Mouin va fè dènié pa mouin</strong> </p>\n<p><strong>Chak pa mouin fè, kondui-m pi pré sièl la.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè asurans mouin kòmansé fayi </p>\n<p>É dévan-m gin falèz infini. </p>\n<p>Mouin viré jé-m, mouin gadé Bon Sovè mouin </p>\n<p>Li sé fòs mouin, lè mouin maché avè-L. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin konfié nan Kris, malgré sikonstans yo </p>\n<p>Jézu gingnin pouvoua an abondans. </p>\n<p>La pé kinbé klé ya, louvri pòt la </p>\n<p>Pou Li minnin-m nan péyi promès la. </p>\n','Chak-pa-mouin-fe',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1919,'51a14f0d-8c52-4463-adfa-a86451a04150',0,0,'Nan vil Samari',50,'50_EE_ht','1.\nNan vil Samari, bò pui Jakob,\nAk pitit li yo té fouyé\nJézu tap fè gnou pozé bò pui sila\nGnou Samaritè-n parèt la \nNan pui-a vini pran dlo\nÉ Jézu té di li sil vou plè ban-m ti gout dlo.\n\nKè:\nFan-m nan té trè étoné\nPou gnou Jiuf pa pè adrésé-l\nOu ki soti an Izraèl, ki pa vlé ouè Samaritin\nJézu té di li konsa \nSi ou té konnin don Bon-Dieu\nSi ou té konnin ki lès mouin yé ou ta mandé\nMouin ta ba ou dlo, pou lé tan é létènité.\n\n2.\nFan-m nan té di Ségnè av:k ki sa\nNou ka pran dlo nan pui-a\nSé sèlman nan pui sa ki gin dlo fré\nDi-m non si ou pi gran k é pè Jakob\nKi té ba nou pui sila\nLadan-l pitit li bouè, nou puizé pou bèt nou yo.','<p><strong>1.</strong> </p>\n<p>Nan vil Samari, bò pui Jakob, </p>\n<p>Ak pitit li yo té fouyé </p>\n<p>Jézu tap fè gnou pozé bò pui sila </p>\n<p>Gnou Samaritè-n parèt la </p>\n<p>Nan pui-a vini pran dlo </p>\n<p>É Jézu té di li sil vou plè ban-m ti gout dlo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Fan-m nan té trè étoné</strong> </p>\n<p><strong>Pou gnou Jiuf pa pè adrésé-l</strong> </p>\n<p><strong>Ou ki soti an Izraèl, ki pa vlé ouè Samaritin</strong> </p>\n<p><strong>Jézu té di li konsa</strong> </p>\n<p><strong>Si ou té konnin don Bon-Dieu</strong> </p>\n<p><strong>Si ou té konnin ki lès mouin yé ou ta mandé</strong> </p>\n<p><strong>Mouin ta ba ou dlo, pou lé tan é létènité.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fan-m nan té di Ségnè av:k ki sa </p>\n<p>Nou ka pran dlo nan pui-a </p>\n<p>Sé sèlman nan pui sa ki gin dlo fré </p>\n<p>Di-m non si ou pi gran k é pè Jakob </p>\n<p>Ki té ba nou pui sila </p>\n<p>Ladan-l pitit li bouè, nou puizé pou bèt nou yo. </p>\n','Nan-vil-Samari',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1920,'95c56ca7-9b5d-407f-ab1d-4cdfab95da63',0,0,'Otréfoi m-té kouché',58,'58_EE_ht','1.\nOtréfoi-m té kouché,\nÉ zié-m louvri map pansé\nAk kòmandman Jézu-Kri té bay disip Li yo\nMouin t  santi lè sa-a gnou gran transfòmasion\nGnou riviè tap koulé nan zié mouin\n\nKè:\nJézu-Kri apran-n nou nou doué gin tèt ansanm\nPi gran kòmandman an,\nSé rinmin nou gnoun lòt.\n\n2.\nApran-n a padoné-é tout frè tout sè ou yo\nMin-m lè yo méprizé ou kon fatra\nOu pa vo angnin\nPa krié pa plingnin, réziyé ou frè mouin\nSoufrans lan du sé vré min pran kouraj.\n\n3.\nPatajé ak frè ou, tristès li ak lapin-n\nSé sa Jézu mandé pou nou gnoun soufro pou lòt\nPa umilié frè ou, pa umilié sè ou               \nNou tout an jénéral sé pousiè.','<p><strong>1.</strong> </p>\n<p>Otréfoi-m té kouché, </p>\n<p>É zié-m louvri map pansé </p>\n<p>Ak kòmandman Jézu-Kri té bay disip Li yo </p>\n<p>Mouin t  santi lè sa-a gnou gran transfòmasion </p>\n<p>Gnou riviè tap koulé nan zié mouin </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu-Kri apran-n nou nou doué gin tèt ansanm</strong> </p>\n<p><strong>Pi gran kòmandman an,</strong> </p>\n<p><strong>Sé rinmin nou gnoun lòt.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Apran-n a padoné-é tout frè tout sè ou yo </p>\n<p>Min-m lè yo méprizé ou kon fatra </p>\n<p>Ou pa vo angnin </p>\n<p>Pa krié pa plingnin, réziyé ou frè mouin </p>\n<p>Soufrans lan du sé vré min pran kouraj. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Patajé ak frè ou, tristès li ak lapin-n </p>\n<p>Sé sa Jézu mandé pou nou gnoun soufro pou lòt </p>\n<p>Pa umilié frè ou, pa umilié sè ou </p>\n<p>Nou tout an jénéral sé pousiè. </p>\n','Otrefoi-m-te-kouche',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1921,'203608d2-b81a-46bf-9ae4-531e31254c5a',0,0,'Kouté voua Jézu',84,'84_EE_ht','1.\nKouté voua Jézu ki nan dézè-a\nl-ap chèché mouton pèdu-o;\nmouton ki égaré nan paturaj la\nl-ap fè yo rantré nan pak la.\n\nRéfrin:\nVini, vini mouton ki pèdu yo\nVini, vi-n jouin-n mouin\nBèjé ou ap chèché ou\nMin voua-m nan dézè-a\nApé rélé ou\nÉ dé bra-m louvri\nPou-m résévoua ou.','<p><strong>1.</strong> </p>\n<p>Kouté voua Jézu ki nan dézè-a </p>\n<p>l-ap chèché mouton pèdu-o; </p>\n<p>mouton ki égaré nan paturaj la </p>\n<p>l-ap fè yo rantré nan pak la. </p>\n<p><br/> <strong>Réfrin:</strong></p>\n<p><strong>Vini, vini mouton ki pèdu yo</strong> </p>\n<p><strong>Vini, vi-n jouin-n mouin</strong> </p>\n<p><strong>Bèjé ou ap chèché ou</strong> </p>\n<p><strong>Min voua-m nan dézè-a</strong> </p>\n<p><strong>Apé rélé ou</strong> </p>\n<p><strong>É dé bra-m louvri</strong> </p>\n<p><strong>Pou-m résévoua ou.</strong> </p>\n','Koute-voua-Jezu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1922,'e5786394-d113-421e-b79b-25db35746596',0,0,'Le Seigneur dit: Je suis La Porte',9,'9_MJ_fr','1.\nLe Seigneur dit: Je suis la Porte:\nSi quelqu\'un entre par Moi,\nIl sera sauvé.\nIl entrera, il sortira,\nEt trouvera des pâturages.  (bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Le Seigneur dit: Je suis la Porte: </p>\n<p>Si quelqu&#39;un entre par Moi, </p>\n<p>Il sera sauvé. </p>\n<p>Il entrera, il sortira, </p>\n<p>Et trouvera des pâturages.  (bis 2 lignes) </p>\n','Le-Seigneur-dit:-Je-suis-La-Porte',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1923,'ffa02b62-3007-44ca-ade9-f04fe63675a5',1,0,'Notre Dieu règne encore',12,'12_MJ_fr','1.\nNotre Dieu règne encore!\nJamais Son amour ne s\'endort.\nLe vent se déchaîne,\nNos coeurs sont en peine,\nMais Dieu nous conduit jusqu\'au port.\nNotre Dieu règne encore!\nJamais Son amour ne s\'endort.\nCe qu\'Il nous promet\nReste vrai pour jamais\nNotre Dieu règne encore!\\','<p><strong>1.</strong> </p>\n<p>Notre Dieu règne encore! </p>\n<p>Jamais Son amour ne s&#39;endort. </p>\n<p>Le vent se déchaîne, </p>\n<p>Nos coeurs sont en peine, </p>\n<p>Mais Dieu nous conduit jusqu&#39;au port. </p>\n<p>Notre Dieu règne encore! </p>\n<p>Jamais Son amour ne s&#39;endort. </p>\n<p>Ce qu&#39;Il nous promet </p>\n<p>Reste vrai pour jamais </p>\n<p>Notre Dieu règne encore!\\ </p>\n','Notre-Dieu-regne-encore',NULL,'fr',1,'2023-07-22 22:41:15','2023-08-19 05:01:10'),(1924,'9666078d-f63d-4a73-8247-047bd3d9bc99',0,0,'Nous voulons vivre pour Sa gloire',23,'23_MJ_fr','1.\nNous voulons vivre pour Sa gloire,\nComme les témoins d\'autrefois\nEt lutter jusqu\'à la victoire,\nSous le grand signe de la Croix.\n\nChoeur:\nDebout, debout! Le Seigneur\nNous veut pour Lui, pour Lui.\nDebout, debout!\nOh, consacrons-nous aujourd\'hui.\n\n2.\nContre le mal livrant bataille\nNous combattons auprès de Toi,\nSans qu\'aucun jamais ne défaille,\nSous le grand signe de la Croix.\n\n3.\nSi Satan nous crible et nous tente,\nAyons plus d\'ardeur, plus de foi,\nEt progressons, cohorte ardente,\nSous le grand signe de la Croix.\n\n4.\nO tous écoutez la Parole:\n«Préparez le chemin du Roi!»\nDebout! Qu\'on se donne et s\'enrôle,\nSous le grand signe de la Croix.','<p><strong>1.</strong> </p>\n<p>Nous voulons vivre pour Sa gloire, </p>\n<p>Comme les témoins d&#39;autrefois </p>\n<p>Et lutter jusqu&#39;à la victoire, </p>\n<p>Sous le grand signe de la Croix. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Debout, debout! Le Seigneur</strong> </p>\n<p><strong>Nous veut pour Lui, pour Lui.</strong> </p>\n<p><strong>Debout, debout!</strong> </p>\n<p><strong>Oh, consacrons-nous aujourd&#39;hui.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Contre le mal livrant bataille </p>\n<p>Nous combattons auprès de Toi, </p>\n<p>Sans qu&#39;aucun jamais ne défaille, </p>\n<p>Sous le grand signe de la Croix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si Satan nous crible et nous tente, </p>\n<p>Ayons plus d&#39;ardeur, plus de foi, </p>\n<p>Et progressons, cohorte ardente, </p>\n<p>Sous le grand signe de la Croix. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O tous écoutez la Parole: </p>\n<p>«Préparez le chemin du Roi!» </p>\n<p>Debout! Qu&#39;on se donne et s&#39;enrôle, </p>\n<p>Sous le grand signe de la Croix. </p>\n','Nous-voulons-vivre-pour-Sa-gloire',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1925,'eed93d11-bb64-4298-8bf2-3c89ee605fe2',0,0,'Mets-toi d\'accord avec ton Dieu',42,'42_MJ_fr','1.\nMets-toi d\'accord avec ton Dieu\nEt promptement même en ce lieu\nViens à la croix où le pardon\nT\'attends, car Christ fut ta rançon.','<p><strong>1.</strong> </p>\n<p>Mets-toi d&#39;accord avec ton Dieu </p>\n<p>Et promptement même en ce lieu </p>\n<p>Viens à la croix où le pardon </p>\n<p>T&#39;attends, car Christ fut ta rançon. </p>\n','Mets-toi-d\'accord-avec-ton-Dieu',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1926,'422bd962-d9ac-4085-9ad6-f9e303efca56',0,0,'Koté min dèv yo',17,'17_VR_ht','1.	\nKoté min dèv yo ki pou rékòlté\nGran mouason dé za-m ki nan chan péché\nFòk nou byin paré ak la vérité\nPou rantré rékòt pou létènité.\n\nKè:\nKoté min dèv yo pou rékòlté\nGran mouason dé za-m ki nan chan péché\nKi lès va édé-m nan la saf Bon-Dieu\nÉgzanté léz an-m nan jujman difé.\n\n2.	\nTravay la va rèd é li fè byin cho,\nN-a kéyi léz an-m malgré rajé gro,\nPa kité gnou sèl paské tout pou Kris\nTout éfò nou fè sonjé Li fè plus.\n\n3.	\nMouason déja prèt pou Li rékòlté\nTravay la anpil, manké ouvrié,\nByinto l-a pèdu si nou fè réta,\nVini bay konkou nan gran travay sa.\n\n4.	\nAnnou mété tout min nou yo ansan-m,\nNou pa fi-n konnin valè gnou sèl a-m,\nByinto Mèt rékòt la va rétounin,\nLi vlé nou réjoui ak Li a la fin.','<p><strong>1.</strong> </p>\n<p>Koté min dèv yo ki pou rékòlté </p>\n<p>Gran mouason dé za-m ki nan chan péché </p>\n<p>Fòk nou byin paré ak la vérité </p>\n<p>Pou rantré rékòt pou létènité. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Koté min dèv yo pou rékòlté</strong> </p>\n<p><strong>Gran mouason dé za-m ki nan chan péché</strong> </p>\n<p><strong>Ki lès va édé-m nan la saf Bon-Dieu</strong> </p>\n<p><strong>Égzanté léz an-m nan jujman difé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Travay la va rèd é li fè byin cho, </p>\n<p>N-a kéyi léz an-m malgré rajé gro, </p>\n<p>Pa kité gnou sèl paské tout pou Kris </p>\n<p>Tout éfò nou fè sonjé Li fè plus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouason déja prèt pou Li rékòlté </p>\n<p>Travay la anpil, manké ouvrié, </p>\n<p>Byinto l-a pèdu si nou fè réta, </p>\n<p>Vini bay konkou nan gran travay sa. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Annou mété tout min nou yo ansan-m, </p>\n<p>Nou pa fi-n konnin valè gnou sèl a-m, </p>\n<p>Byinto Mèt rékòt la va rétounin, </p>\n<p>Li vlé nou réjoui ak Li a la fin. </p>\n','Kote-min-dev-yo',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1927,'31545953-877d-4e03-aa93-9a829dde00f4',0,0,'Mouin tap maché nan',3,'3_EE_ht','1.\nMouin tap maché nan gnou rout \nDifisil, plin danjé\nLè tout suit mouin fè rankont ak Non-m sa\nLi di mouin, pitit mouin,\nVini jouin-n mouin, pa mizé\nM-obéi san plignin, é san pè, é Li kinbé min-m.\n\nKè:\nNon Li sé Jézu, nan pouin lòt kon Li\nNon Li sé Jézu, vi-n adoré Li\nSa sé sujè kontantman kè mouin\nMouin ouè Sovè ya.\n\n2.\nChémin mouin té kòmansé\nVi-n fasil, byin fasil\nLè mouin vi-n rékon-nèt sé té Non-m sa\nKi gnou jou Li té bay la vi-L\nSou la koua pou sové-m\nKontantman ak la joua, plin kè mouin\nMouin ouè Sovè ya.\n\n3.\nKi gran jou sa va yé, \nLè La tounin vi-n chèché-m;\nTout kriyé, doulè, soufrans va fini\nMa réjoui avèk tout zanj \nNan sièl la pou tout tan.\nMalè pou tout sa ki pat jan-m vlé kouè\nNan sèl Sovè ya.','<p><strong>1.</strong> </p>\n<p>Mouin tap maché nan gnou rout </p>\n<p>Difisil, plin danjé </p>\n<p>Lè tout suit mouin fè rankont ak Non-m sa </p>\n<p>Li di mouin, pitit mouin, </p>\n<p>Vini jouin-n mouin, pa mizé </p>\n<p>M-obéi san plignin, é san pè, é Li kinbé min-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Non Li sé Jézu, nan pouin lòt kon Li</strong> </p>\n<p><strong>Non Li sé Jézu, vi-n adoré Li</strong> </p>\n<p><strong>Sa sé sujè kontantman kè mouin</strong> </p>\n<p><strong>Mouin ouè Sovè ya.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chémin mouin té kòmansé </p>\n<p>Vi-n fasil, byin fasil </p>\n<p>Lè mouin vi-n rékon-nèt sé té Non-m sa </p>\n<p>Ki gnou jou Li té bay la vi-L </p>\n<p>Sou la koua pou sové-m </p>\n<p>Kontantman ak la joua, plin kè mouin </p>\n<p>Mouin ouè Sovè ya. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ki gran jou sa va yé, </p>\n<p>Lè La tounin vi-n chèché-m; </p>\n<p>Tout kriyé, doulè, soufrans va fini </p>\n<p>Ma réjoui avèk tout zanj </p>\n<p>Nan sièl la pou tout tan. </p>\n<p>Malè pou tout sa ki pat jan-m vlé kouè </p>\n<p>Nan sèl Sovè ya. </p>\n','Mouin-tap-mache-nan',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1928,'d2d8a52f-caa1-4985-96d6-8ab7c4311591',1,0,'De la poussière de la terre',23,'23_EE_fr','1.\nDe la poussière de la terre\nMon Dieu fit sortir l\'homme\nSon souffle lui donne une âme vivante\nEt Dieu a tant aimé le monde\nQu\'Il a donné son Fils unique\nVoilà pourquoi je L\'aime tant.\n\nChoeur\nJe suis créé à on image\nOui bien à son image\nJe suis sur terre pour Le servir\nJamais je ne Le nierai\nJe marcherai toujours près de Lui\nCar Il m\'a créé pour Le servir.\n\n2.\nMon coeur, mon corps, mon âme\nNe m\'appartiennent plus\nIls sont au service du Seigneur\nLes années pourront s\'enfuir\nLes jours succédés au jour\nMon corps est le temple du Seigneur','<p><strong>1.</strong> </p>\n<p>De la poussière de la terre </p>\n<p>Mon Dieu fit sortir l&#39;homme </p>\n<p>Son souffle lui donne une âme vivante </p>\n<p>Et Dieu a tant aimé le monde </p>\n<p>Qu&#39;Il a donné son Fils unique </p>\n<p>Voilà pourquoi je L&#39;aime tant. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Je suis créé à on image</strong> </p>\n<p><strong>Oui bien à son image</strong> </p>\n<p><strong>Je suis sur terre pour Le servir</strong> </p>\n<p><strong>Jamais je ne Le nierai</strong> </p>\n<p><strong>Je marcherai toujours près de Lui</strong> </p>\n<p><strong>Car Il m&#39;a créé pour Le servir.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon coeur, mon corps, mon âme </p>\n<p>Ne m&#39;appartiennent plus </p>\n<p>Ils sont au service du Seigneur </p>\n<p>Les années pourront s&#39;enfuir </p>\n<p>Les jours succédés au jour </p>\n<p>Mon corps est le temple du Seigneur </p>\n','De-la-poussiere-de-la-terre',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:33'),(1929,'ca486cc5-bd5e-449a-9e24-7652ef88ae45',1,0,'Prends ma vie dans tes bras',28,'28_EE_fr','1.\nPrends ma vie dans tes bras\nOh Jésus tendre Ami\nCar de toi jaillira la seule source de vie\nPorte-moi dans ma foi quand ton amour sera\nOh! Seigneur guide-moi par ta main.\n\n2.\nSi parfois j\'ai troublé, égaré et confus\nDes tourments et douleurs\nQui assaillent mon coeur\nTu demeures avec moi car Tu m\'aimes Jésus\nGrâce à Toi je triomphe sans peur.\n\n3. \nDans ma vie aujourd\'hui\nRien ne vit, tout est vain.\nC\'est la mort en esprit de l\'humain en humain\nMais 	quand je T\'ai rencontré\nToute ma vie a changé,\nTu m\'as ressuscité, libéré.','<p><strong>1.</strong> </p>\n<p>Prends ma vie dans tes bras </p>\n<p>Oh Jésus tendre Ami </p>\n<p>Car de toi jaillira la seule source de vie </p>\n<p>Porte-moi dans ma foi quand ton amour sera </p>\n<p>Oh! Seigneur guide-moi par ta main. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si parfois j&#39;ai troublé, égaré et confus </p>\n<p>Des tourments et douleurs </p>\n<p>Qui assaillent mon coeur </p>\n<p>Tu demeures avec moi car Tu m&#39;aimes Jésus </p>\n<p>Grâce à Toi je triomphe sans peur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans ma vie aujourd&#39;hui </p>\n<p>Rien ne vit, tout est vain. </p>\n<p>C&#39;est la mort en esprit de l&#39;humain en humain </p>\n<p>Mais     quand je T&#39;ai rencontré </p>\n<p>Toute ma vie a changé, </p>\n<p>Tu m&#39;as ressuscité, libéré. </p>\n','Prends-ma-vie-dans-tes-bras',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:41'),(1930,'96d2b888-19eb-464a-a097-0807efc6174e',0,0,'A la gnou tan Ségnè',55,'55_EE_ht','1.\nA la gnou tan Ségnè, sé tan sila\nKoté tout bagay yo vi-n chanjé\nMin-m krétyin yo vi-n troublé\nO Ségnè gin anpil moun kap soufri\nPanpri Papa, pa kité yo souplé.\n\nKè:\nO Ségnè, gingnin ki grangou\nO Papa, gingnin ki tounu\nOu min-m ki ouè nan lié sékrè\nVoyé jé ou sou yo Jézu pa kité yo souplé\n\n2.\nGin anpil moun ki kouché nan lopital\nKi asouafé ki san éspoua\nYo pa gingnin lòt sékou\nO Ségnè, yo ta rinmin ba ou gloua\nTanpri Papa tandé kri gnou souplé','<p><strong>1.</strong> </p>\n<p>A la gnou tan Ségnè, sé tan sila </p>\n<p>Koté tout bagay yo vi-n chanjé </p>\n<p>Min-m krétyin yo vi-n troublé </p>\n<p>O Ségnè gin anpil moun kap soufri </p>\n<p>Panpri Papa, pa kité yo souplé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O Ségnè, gingnin ki grangou</strong> </p>\n<p><strong>O Papa, gingnin ki tounu</strong> </p>\n<p><strong>Ou min-m ki ouè nan lié sékrè</strong> </p>\n<p><strong>Voyé jé ou sou yo Jézu pa kité yo souplé</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin anpil moun ki kouché nan lopital </p>\n<p>Ki asouafé ki san éspoua </p>\n<p>Yo pa gingnin lòt sékou </p>\n<p>O Ségnè, yo ta rinmin ba ou gloua </p>\n<p>Tanpri Papa tandé kri gnou souplé </p>\n','A-la-gnou-tan-Segne',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1931,'ffe55903-dada-4f1c-8f52-4e507aa4cfb3',0,0,'Konfié-m nan Dieu',67,'67_EE_ht','1.\nKonfié-m nan Dieu ninpòt kòman mouin yé\nNi sou la tè, ni sou la mè troublé\nNan tout traka nan la vi sa\nPapa-m nan sièl la apé véyé mouin.\n\nKè:\nKonfié-m nan Dieu\nM-konnin L-ap pran souin mouin\nNan dézè-a ou nan mové chémin\nLa mè pousé-m Bon-Dieu kinbé-m\nPapa-m nan sièl la apé véyé mouin.\n\n2.\nLi gadé flè yo nan chan tou patou\nLi pran souin zouazo kap chanté pou nou\nJuska la fin, L-ap véyé mouin\nPapa-m nan  sièl la apé véyé mouin.\n\n3.\nRout la kab du é nuaj yo kouvri mouin\nBèjé ya gadé mouton-l jouk la fin\nAprè mon-n sa-a, na ouè sièl la\nPapa-m nan sièl la apé véyé mouin.','<p><strong>1.</strong> </p>\n<p>Konfié-m nan Dieu ninpòt kòman mouin yé </p>\n<p>Ni sou la tè, ni sou la mè troublé </p>\n<p>Nan tout traka nan la vi sa </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konfié-m nan Dieu</strong> </p>\n<p><strong>M-konnin L-ap pran souin mouin</strong> </p>\n<p><strong>Nan dézè-a ou nan mové chémin</strong> </p>\n<p><strong>La mè pousé-m Bon-Dieu kinbé-m</strong> </p>\n<p><strong>Papa-m nan sièl la apé véyé mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li gadé flè yo nan chan tou patou </p>\n<p>Li pran souin zouazo kap chanté pou nou </p>\n<p>Juska la fin, L-ap véyé mouin </p>\n<p>Papa-m nan  sièl la apé véyé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rout la kab du é nuaj yo kouvri mouin </p>\n<p>Bèjé ya gadé mouton-l jouk la fin </p>\n<p>Aprè mon-n sa-a, na ouè sièl la </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n','Konfie-m-nan-Dieu',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1932,'b57c52a8-fc92-4381-9df3-32cc526fe6f0',0,0,'Pouki moun palè kristal sa?',79,'79_EE_ht','1.\nPouki moun palè kristal sa-a?\nSé pou mouin ak Jézu-Kri!\nPouki moun fotèy an nò yo?\nSé pou mouin ak rachté yo!\n\nRéfrin:\nSé gnou bèl palè kristal\nAvèk anpil chèz an nò,\nPou tout moun ki vlé alé\nAvèk Ségnè Jézu.\n\n2.\nJéruzalèm nan bèl anpil,\nLi pi bèl pasé diaman\nLi soti koté Bon-Dieu min-m\nÉ li gin tout gloua Bon-Dieu.\n\n3.\nPa bézouin solèy ak lalu-n\nPou kléré nou jou é nouit\nMin la gloua Bon-Dieu va briyé\nÉkla Li va gnou flanbo.\n\n4.\nPap gin krint, doulè ak touman\nNi lanmò, ni maladi.\nMédizans, kout lang va fini\nSé louanj, lanmou san fin.\n\n5.\nA la gnou bèl jou sa va yé,\nLè tronpèt la va sonnin\nPou tou mò nan Kris yo lévé\nPou y-al rankontré Jézu.','<p><strong>1.</strong> </p>\n<p>Pouki moun palè kristal sa-a? </p>\n<p>Sé pou mouin ak Jézu-Kri! </p>\n<p>Pouki moun fotèy an nò yo? </p>\n<p>Sé pou mouin ak rachté yo! </p>\n<p><br/> <strong>Réfrin:</strong></p>\n<p><strong>Sé gnou bèl palè kristal</strong> </p>\n<p><strong>Avèk anpil chèz an nò,</strong> </p>\n<p><strong>Pou tout moun ki vlé alé</strong> </p>\n<p><strong>Avèk Ségnè Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jéruzalèm nan bèl anpil, </p>\n<p>Li pi bèl pasé diaman </p>\n<p>Li soti koté Bon-Dieu min-m </p>\n<p>É li gin tout gloua Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pa bézouin solèy ak lalu-n </p>\n<p>Pou kléré nou jou é nouit </p>\n<p>Min la gloua Bon-Dieu va briyé </p>\n<p>Ékla Li va gnou flanbo. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pap gin krint, doulè ak touman </p>\n<p>Ni lanmò, ni maladi. </p>\n<p>Médizans, kout lang va fini </p>\n<p>Sé louanj, lanmou san fin. </p>\n<p><br/> <strong>5.</strong></p>\n<p>A la gnou bèl jou sa va yé, </p>\n<p>Lè tronpèt la va sonnin </p>\n<p>Pou tou mò nan Kris yo lévé </p>\n<p>Pou y-al rankontré Jézu. </p>\n','Pouki-moun-pale-kristal-sa',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1933,'6c031dfc-28bf-4588-b843-0504f075d065',0,0,'Gin gnou bon Papa',81,'81_EE_ht','1.\nGingnin gnou bon Papa\nKi konnin fè tout mirak;\nLi kon-n géri malad\nLi kon-n sové na-m yo\nLi kon-n chasé tristès\nÉ Li résusité mò\nLi kon-n chasé péché,\nO Li kon-n sové nan-m yo.\n\nRéfrin:\nSé Jézu bon Mèt la ki kon-n fè toutmirak\nLi kon-n gé malad, Li kon-n chasé démon\nJézu bon Sovè-a\nKon-n lévé min-m moun mouri\nMin-m tanpèt avèk van\nLi gingnin pouvoua sou yo.\n\n2.\nGnou jou Li tap maché,\nLi rankontré dis lépré;\nMin lè yo ouè Jézu, yo rélé: Pitié pou nou!\nMin yo té pansé Li pralé lapidé yo\nMin-m lè-a kè-L touché-L,\nLi gin konpasion pou yo\nLi géri yo min-m lè é yo vini purifié.\n\n3.\nGnou jou-L té sou lanmè \nAvèk kèk nan disip yo\nGnou tanpèt té lévé, bak la prèt pou-l chaviré;\nLi lévé min-L anlè, Li di van fè silans.\n\n4.\nGnou jou, yo té minnin ba Li \nGnou fan-m adultè\nMin yo té pansé li pralé lapidé fan-m nan\nLè Li gadé fan-m nan, Li gingnin pitié pou li\nLi di-l ou mèt alé min pa péché ankò.','<p><strong>1.</strong> </p>\n<p>Gingnin gnou bon Papa </p>\n<p>Ki konnin fè tout mirak; </p>\n<p>Li kon-n géri malad </p>\n<p>Li kon-n sové na-m yo </p>\n<p>Li kon-n chasé tristès </p>\n<p>É Li résusité mò </p>\n<p>Li kon-n chasé péché, </p>\n<p>O Li kon-n sové nan-m yo. </p>\n<p><br/> <strong>Réfrin:</strong></p>\n<p><strong>Sé Jézu bon Mèt la ki kon-n fè toutmirak</strong> </p>\n<p><strong>Li kon-n gé malad, Li kon-n chasé démon</strong> </p>\n<p><strong>Jézu bon Sovè-a</strong> </p>\n<p><strong>Kon-n lévé min-m moun mouri</strong> </p>\n<p><strong>Min-m tanpèt avèk van</strong> </p>\n<p><strong>Li gingnin pouvoua sou yo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gnou jou Li tap maché, </p>\n<p>Li rankontré dis lépré; </p>\n<p>Min lè yo ouè Jézu, yo rélé: Pitié pou nou! </p>\n<p>Min yo té pansé Li pralé lapidé yo </p>\n<p>Min-m lè-a kè-L touché-L, </p>\n<p>Li gin konpasion pou yo </p>\n<p>Li géri yo min-m lè é yo vini purifié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou jou-L té sou lanmè </p>\n<p>Avèk kèk nan disip yo </p>\n<p>Gnou tanpèt té lévé, bak la prèt pou-l chaviré; </p>\n<p>Li lévé min-L anlè, Li di van fè silans. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou jou, yo té minnin ba Li </p>\n<p>Gnou fan-m adultè </p>\n<p>Min yo té pansé li pralé lapidé fan-m nan </p>\n<p>Lè Li gadé fan-m nan, Li gingnin pitié pou li </p>\n<p>Li di-l ou mèt alé min pa péché ankò. </p>\n','Gin-gnou-bon-Papa',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1934,'a16d461d-92c7-4ca4-b2e2-ff9d319a9afa',0,0,'Dans les jours où tout est joie',1,'1_MJ_fr','1.\nDans les jours où tout est joie,\nLumière et félicité\nQuand les fleurs sur notre voie,\nS\'ouvrent au soleil d\'été,\nDieu a tant besoin de nous,\nPeut-Il donc, compter sur vous?\n\nChoeur:\nDieu peut-Il compter sur vous?\nDieu peut-Il compter sur vous?\nLe pourra-t-Il jusqu\'au bout?\nDieu peut-Il compter sur vous?\n\n\n2.\nA l\'heure du sacrifice,\nQui n\'a pas son Morija?\nLorsque notre front se plisse,\nQu\'en nous se livre un combat,\nDieu a tant besoin de nous,\nPeut-Il donc, compter sur vous?\n\n\n3.\nEt si l\'obstacle se dresse,\nEffrayant, sur le chemin,\nSi notre coeur en détresse,\nN\'ose penser à demain,\nDieu a tant besoin de nous,\nPeut-Il donc, compter sur Vous?\n\n\n4.\nPar Son Esprit, par sa grâce,\nDieu pourra compter sur moi.\nJe Le suivrai quoi qu\'Il fasse\nSur le chemin de la croix.\nOui, Il peut compter sur moi.\nJe Le suivrai avec joie.\n\nChoeur:\nOui, Tu peux compter sur moi\nCar je Te couronne Roi\nJe Te suivrai par la foi.\nOui, Tu peux compter sur moi.','<p><strong>1.</strong> </p>\n<p>Dans les jours où tout est joie, </p>\n<p>Lumière et félicité </p>\n<p>Quand les fleurs sur notre voie, </p>\n<p>S&#39;ouvrent au soleil d&#39;été, </p>\n<p>Dieu a tant besoin de nous, </p>\n<p>Peut-Il donc, compter sur vous? </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Dieu peut-Il compter sur vous?</strong> </p>\n<p><strong>Dieu peut-Il compter sur vous?</strong> </p>\n<p><strong>Le pourra-t-Il jusqu&#39;au bout?</strong> </p>\n<p><strong>Dieu peut-Il compter sur vous?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A l&#39;heure du sacrifice, </p>\n<p>Qui n&#39;a pas son Morija? </p>\n<p>Lorsque notre front se plisse, </p>\n<p>Qu&#39;en nous se livre un combat, </p>\n<p>Dieu a tant besoin de nous, </p>\n<p>Peut-Il donc, compter sur vous? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et si l&#39;obstacle se dresse, </p>\n<p>Effrayant, sur le chemin, </p>\n<p>Si notre coeur en détresse, </p>\n<p>N&#39;ose penser à demain, </p>\n<p>Dieu a tant besoin de nous, </p>\n<p>Peut-Il donc, compter sur Vous? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Par Son Esprit, par sa grâce, </p>\n<p>Dieu pourra compter sur moi. </p>\n<p>Je Le suivrai quoi qu&#39;Il fasse </p>\n<p>Sur le chemin de la croix. </p>\n<p>Oui, Il peut compter sur moi. </p>\n<p>Je Le suivrai avec joie. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, Tu peux compter sur moi</strong> </p>\n<p><strong>Car je Te couronne Roi</strong> </p>\n<p><strong>Je Te suivrai par la foi.</strong> </p>\n<p><strong>Oui, Tu peux compter sur moi.</strong> </p>\n','Dans-les-jours-ou-tout-est-joie',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1935,'07e30e2a-c2a6-42b3-be54-fe38553fba9c',0,0,'Rude est la mêlée',21,'21_MJ_fr','1.\nRude est la mêlée,\nNe l\'oublions pas!\nPrenons donc l\'épée,\nPour le saint combat!\nLe Seigneur l\'ordonne\nÉcoutons l\'appel,\nQui descend, pour nous, du ciel.\n\nChoeur:\nEn avant, oui,\nMarchons en avant!\nNous avons un\nMaître tout puissant.\nLuttons contre\nLe péché, l\'erreur,\nAu nom de Jésus,\nLe grand vainqueur!\n\n2.\nNotre Capitaine,\nNous appelle tous,\nToute lutte est vaine,\nS\'Il n\'est avec nous,\nMais Il est fidèle,\nSuivons-Le partout,\nOui, suivons-Le jusqu\'au bout!\n\n3.\nOui, de l\'adversaire,\nLe pouvoir cruel,\nRépand sur la terre,\nSon venin mortel;\nMais, de haute lutte,\nJésus l\'a vaincu,\nEt nous offre un plein salut!\n\n4.\nLuttons sans relâche,\nSans crainte et sans peur;\nPoursuivons la tâche,\nAu nom du Sauveur;\nLe bonheur, la gloire,\nLa paix, le repos,\nNous serons donnés là-haut!','<p><strong>1.</strong> </p>\n<p>Rude est la mêlée, </p>\n<p>Ne l&#39;oublions pas! </p>\n<p>Prenons donc l&#39;épée, </p>\n<p>Pour le saint combat! </p>\n<p>Le Seigneur l&#39;ordonne </p>\n<p>Écoutons l&#39;appel, </p>\n<p>Qui descend, pour nous, du ciel. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>En avant, oui,</strong> </p>\n<p><strong>Marchons en avant!</strong> </p>\n<p><strong>Nous avons un</strong> </p>\n<p><strong>Maître tout puissant.</strong> </p>\n<p><strong>Luttons contre</strong> </p>\n<p><strong>Le péché, l&#39;erreur,</strong> </p>\n<p><strong>Au nom de Jésus,</strong> </p>\n<p><strong>Le grand vainqueur!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Notre Capitaine, </p>\n<p>Nous appelle tous, </p>\n<p>Toute lutte est vaine, </p>\n<p>S&#39;Il n&#39;est avec nous, </p>\n<p>Mais Il est fidèle, </p>\n<p>Suivons-Le partout, </p>\n<p>Oui, suivons-Le jusqu&#39;au bout! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, de l&#39;adversaire, </p>\n<p>Le pouvoir cruel, </p>\n<p>Répand sur la terre, </p>\n<p>Son venin mortel; </p>\n<p>Mais, de haute lutte, </p>\n<p>Jésus l&#39;a vaincu, </p>\n<p>Et nous offre un plein salut! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Luttons sans relâche, </p>\n<p>Sans crainte et sans peur; </p>\n<p>Poursuivons la tâche, </p>\n<p>Au nom du Sauveur; </p>\n<p>Le bonheur, la gloire, </p>\n<p>La paix, le repos, </p>\n<p>Nous serons donnés là-haut! </p>\n','Rude-est-la-melee',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1936,'a474c029-cdec-4733-9479-597756e3009e',0,0,'Oh! Contez-moi les histoires',51,'51_MJ_fr','1.\nOh! Contez-moi les histoires\nDe mon Sauveur,\nLes beaux récits de victoire\nDu Rédempteur.\nJe veux entendre tout Son amour,\nPour le comprendre\nMieux chaque jour.\n\n2.\nOui, dites-moi Sa tendresse\nPour les petits.\nIl est malgré leur faiblesse,\nLeur grand Ami.\nSon beau visage, Sa douce voix\nSont un message même pour moi.\n\n3.\nNe faites point taire ses souffrances,\nSa mort en Sa croix,\nPour pardonner mes offenses\nEt vivre en moi\nPuis il faut dire qu\'Il est vivant,\nEt qu\'Il désire mon coeur d\'enfant.','<p><strong>1.</strong> </p>\n<p>Oh! Contez-moi les histoires </p>\n<p>De mon Sauveur, </p>\n<p>Les beaux récits de victoire </p>\n<p>Du Rédempteur. </p>\n<p>Je veux entendre tout Son amour, </p>\n<p>Pour le comprendre </p>\n<p>Mieux chaque jour. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, dites-moi Sa tendresse </p>\n<p>Pour les petits. </p>\n<p>Il est malgré leur faiblesse, </p>\n<p>Leur grand Ami. </p>\n<p>Son beau visage, Sa douce voix </p>\n<p>Sont un message même pour moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ne faites point taire ses souffrances, </p>\n<p>Sa mort en Sa croix, </p>\n<p>Pour pardonner mes offenses </p>\n<p>Et vivre en moi </p>\n<p>Puis il faut dire qu&#39;Il est vivant, </p>\n<p>Et qu&#39;Il désire mon coeur d&#39;enfant. </p>\n','Oh!-Contez-moi-les-histoires',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1937,'6608ebaf-3b1a-41a4-8815-a2e52924e1f7',0,0,'Lagé-m nan dézè-a',43,'43_EE_ht','1.\nLagé-m nan dézè-a ma val avèk Jézu\nLi sé bon zanmi mouin li papé jan-m tronpé-m\nLi té mouri pou mouin lap toujou protéjé-m\nLagé-m nan dézè-a ma val avèk Jézu.\n\nKè:\nMouin bézouin alé sèl avèk Sovè mouin\nLè mouin santi-m grangou\nL’abitué bay la ma-n\nLi té mouri é Li min-n nan gnou gro trou roch\nLè mouin santi mouin souaf \nRoché yo, yo kon-n bay dlo.\n\n2.\nLé mon-n sé ipokrit\nMouin pa bézouin zanmi-l\nLé mon-n bézouin lajan\nMouin pa bézouin rélasion-l\nDéja-m vlé détaché-m dé tout bagay la tè\nLagé-m nan dézè-a ma val avèk Jézu.\n\n3.\nMouin kon-n sékrè lé mon-n\nPa gin angnin sérié\nSé maché makiyé pou yo pété lé zòt\nSé maché pran vié pouin\nPou yo kapab manjé gra\nLagé-m nan dézè-a ma val avèk Jézu.\n\n4.\nLi té pran-n nan la bou li fè mouin vini pròp\nLi voyé-m koté nou pou mouin chanté gloua Li\nLè mouin sonjé jou sa fò-m gin rékonésans\nLagé-m nan dézè-a ma val avèk Jézu.','<p><strong>1.</strong> </p>\n<p>Lagé-m nan dézè-a ma val avèk Jézu </p>\n<p>Li sé bon zanmi mouin li papé jan-m tronpé-m </p>\n<p>Li té mouri pou mouin lap toujou protéjé-m </p>\n<p>Lagé-m nan dézè-a ma val avèk Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin bézouin alé sèl avèk Sovè mouin</strong> </p>\n<p><strong>Lè mouin santi-m grangou</strong> </p>\n<p><strong>L’abitué bay la ma-n</strong> </p>\n<p><strong>Li té mouri é Li min-n nan gnou gro trou roch</strong> </p>\n<p><strong>Lè mouin santi mouin souaf</strong> </p>\n<p><strong>Roché yo, yo kon-n bay dlo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mon-n sé ipokrit </p>\n<p>Mouin pa bézouin zanmi-l </p>\n<p>Lé mon-n bézouin lajan </p>\n<p>Mouin pa bézouin rélasion-l </p>\n<p>Déja-m vlé détaché-m dé tout bagay la tè </p>\n<p>Lagé-m nan dézè-a ma val avèk Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin kon-n sékrè lé mon-n </p>\n<p>Pa gin angnin sérié </p>\n<p>Sé maché makiyé pou yo pété lé zòt </p>\n<p>Sé maché pran vié pouin </p>\n<p>Pou yo kapab manjé gra </p>\n<p>Lagé-m nan dézè-a ma val avèk Jézu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li té pran-n nan la bou li fè mouin vini pròp </p>\n<p>Li voyé-m koté nou pou mouin chanté gloua Li </p>\n<p>Lè mouin sonjé jou sa fò-m gin rékonésans </p>\n<p>Lagé-m nan dézè-a ma val avèk Jézu. </p>\n','Lage-m-nan-deze-a',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1938,'0dcb26a3-d1ef-4894-91b9-e37ca6259032',0,0,'Oui,  je veux travailler',43,'43_VR_fr','1.	\nOui, je veux travailler pour mon Sauveur,\nMe confiant en sa Parole d\'amour.\nJe chanterai mon bonheur,\nÉtant occupé toujours\nDans la vigne du Seigneur.\n\nChoeur\nTravailler et prier\nDans la vigne (bis) du Seigneur\nOui toujours occupé:\nC\'est mon suprême bonheur\nDans la vigne du Seigneur.\n\n2.	\nOui, chaque jour je voudrais travailler,\nMais tu sais, Seigneur, que tout vient de Toi,\nÉtant mon seul Conseiller;               \nMe voici, Seigneur, dis-moi\nTa  très sainte volonté.\n\n3.	\nJe veux être digne de mon Sauveur,\nComme Lui, souffrir et même mourir\nPour attirer les pécheurs\nA Jésus que veut bénir\nMême le persécuteur.\n\n4.	\nOui, je veux travailler, oui malgré tout.\nLe Christ Seigneur est là.  Cela suffit.\nJ\'ai ce qu\'il me faut surtout:\nLe guide du Saint-Esprit.\nAinsi, j\'irai jusqu\'au bout.','<p><strong>1.</strong> </p>\n<p>Oui, je veux travailler pour mon Sauveur, </p>\n<p>Me confiant en sa Parole d&#39;amour. </p>\n<p>Je chanterai mon bonheur, </p>\n<p>Étant occupé toujours </p>\n<p>Dans la vigne du Seigneur. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Travailler et prier</strong> </p>\n<p><strong>Dans la vigne (bis) du Seigneur</strong> </p>\n<p><strong>Oui toujours occupé:</strong> </p>\n<p><strong>C&#39;est mon suprême bonheur</strong> </p>\n<p><strong>Dans la vigne du Seigneur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, chaque jour je voudrais travailler, </p>\n<p>Mais tu sais, Seigneur, que tout vient de Toi, </p>\n<p>Étant mon seul Conseiller; </p>\n<p>Me voici, Seigneur, dis-moi </p>\n<p>Ta  très sainte volonté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je veux être digne de mon Sauveur, </p>\n<p>Comme Lui, souffrir et même mourir </p>\n<p>Pour attirer les pécheurs </p>\n<p>A Jésus que veut bénir </p>\n<p>Même le persécuteur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oui, je veux travailler, oui malgré tout. </p>\n<p>Le Christ Seigneur est là.  Cela suffit. </p>\n<p>J&#39;ai ce qu&#39;il me faut surtout: </p>\n<p>Le guide du Saint-Esprit. </p>\n<p>Ainsi, j&#39;irai jusqu&#39;au bout. </p>\n','Oui-je-veux-travailler',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1939,'4028e971-d9b0-450d-a129-86ed5c9c50bc',0,0,'An nou travay,  an nou konbat',73,'73_EE_ht','1.\nAn nou travay an nou konbat Jézu-Kri a la pòt\nAn nou kinbé san rélaché Li pa louin pou-L vini\nA la gnou bèl jou lè Jézu vini\nPou-L fè-m monté avè-L\nSé pou nou kapab pèsévéré.\n\nKè:\nKrétyin révéyé nou, Jézu-Kri a la pòt\nKrétyin révéyé nou, nou pa konnin ki jou\n\n2.\nSé bèl bagay, lè Li vini \nNou pap maché nan nou\nMin L-ap vini pou vi-n jujé moun ki inkrédil\nLi di déja fòk Li vini pou-L fè travay sa\nOu min-m ki mété kè ou an nòd\nFòk ou rété sinsè.\n\n3.\nSé bèl bagay lè Li vini, nou pap mouri ankò\nVéyé, prié nui é lé jou\nNou va santi nou fiè\nSé pou tout létènité nou va yé\nNou va nan kay sa-a\nOu min-m ki mété kè ou an nòd\nFòk ou rété fidèl','<p><strong>1.</strong> </p>\n<p>An nou travay an nou konbat Jézu-Kri a la pòt </p>\n<p>An nou kinbé san rélaché Li pa louin pou-L vini </p>\n<p>A la gnou bèl jou lè Jézu vini </p>\n<p>Pou-L fè-m monté avè-L </p>\n<p>Sé pou nou kapab pèsévéré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Krétyin révéyé nou, Jézu-Kri a la pòt</strong> </p>\n<p><strong>Krétyin révéyé nou, nou pa konnin ki jou</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé bèl bagay, lè Li vini </p>\n<p>Nou pap maché nan nou </p>\n<p>Min L-ap vini pou vi-n jujé moun ki inkrédil </p>\n<p>Li di déja fòk Li vini pou-L fè travay sa </p>\n<p>Ou min-m ki mété kè ou an nòd </p>\n<p>Fòk ou rété sinsè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé bèl bagay lè Li vini, nou pap mouri ankò </p>\n<p>Véyé, prié nui é lé jou </p>\n<p>Nou va santi nou fiè </p>\n<p>Sé pou tout létènité nou va yé </p>\n<p>Nou va nan kay sa-a </p>\n<p>Ou min-m ki mété kè ou an nòd </p>\n<p>Fòk ou rété fidèl </p>\n','An-nou-travay-an-nou-konbat',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1940,'349ceed4-0fcd-42aa-84fc-1a3af1c43d98',0,0,'Où va cette foule',22,'22_VR_fr','1.	\nOù va cette foule empressée\nQui d\'où n\'est pas lassée\nQuel est cet homme, dites-moi\nQui cause aujourd\'hui tant d\'émoi\nEntendez, entendez ce cri  (bis)\nJésus de Nazareth est ici.\n\n2.	\nEst-ce Dieu visitant la terre\nQui fait mouvoir la ville entière\nQuel est ce étranger puissant\nA l\'aspect si compatissant,\nLa foule répond par ce cri  (bis)\nJésus de Nazareth est ici.\n\n3.	\nIl guérit de sa maladie\nTout pauvre pécheur qui le prie\nSon bonheur est de soulager\nNos maux dont Il vient se charger.\nDe l\'aveugle, entendez ce cri:\nJésus de Nazareth est ici.\n\n4.	\nRecevez la santé de l\'âme\nC\'est votre coeur qu\'Il réclame\nVous par le monde méprisé\nVenez ô pauvre coeurs brisés\nÉcoutez, écoutez ce cri\nJésus de Nazareth est ici.\n\n5.	\nMais si vous refusez d\'entendre\nL\'appel de sa voix douce et tendre\nBientôt, bientôt Il s\'en ira\nEt vos pleurs, nul ne sèchera\nTrop tard, trop tard, sera le cri\nJésus de Nazareth est parti.  (bis)','<p><strong>1.</strong> </p>\n<p>Où va cette foule empressée </p>\n<p>Qui d&#39;où n&#39;est pas lassée </p>\n<p>Quel est cet homme, dites-moi </p>\n<p>Qui cause aujourd&#39;hui tant d&#39;émoi </p>\n<p>Entendez, entendez ce cri  (bis) </p>\n<p>Jésus de Nazareth est ici. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Est-ce Dieu visitant la terre </p>\n<p>Qui fait mouvoir la ville entière </p>\n<p>Quel est ce étranger puissant </p>\n<p>A l&#39;aspect si compatissant, </p>\n<p>La foule répond par ce cri  (bis) </p>\n<p>Jésus de Nazareth est ici. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il guérit de sa maladie </p>\n<p>Tout pauvre pécheur qui le prie </p>\n<p>Son bonheur est de soulager </p>\n<p>Nos maux dont Il vient se charger. </p>\n<p>De l&#39;aveugle, entendez ce cri: </p>\n<p>Jésus de Nazareth est ici. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Recevez la santé de l&#39;âme </p>\n<p>C&#39;est votre coeur qu&#39;Il réclame </p>\n<p>Vous par le monde méprisé </p>\n<p>Venez ô pauvre coeurs brisés </p>\n<p>Écoutez, écoutez ce cri </p>\n<p>Jésus de Nazareth est ici. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Mais si vous refusez d&#39;entendre </p>\n<p>L&#39;appel de sa voix douce et tendre </p>\n<p>Bientôt, bientôt Il s&#39;en ira </p>\n<p>Et vos pleurs, nul ne sèchera </p>\n<p>Trop tard, trop tard, sera le cri </p>\n<p>Jésus de Nazareth est parti.  (bis) </p>\n','Ou-va-cette-foule',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1941,'6535b9e6-f844-4874-926d-f8f95d5d5092',0,0,'Si ou dékourajé',77,'77_EE_ht','1.\nSi ou dékourajé ak fado é krint\nSan fòs, san éspoua\nPa min-m gnou sékou\nMalgré ou ap viv sèl\nÉ rout ou gin mò-n\nJézu ap pansé, lapé pansé dé ou.  (3 foua)\n\nKè:\nLè ou san zanmi\nÉ ou bézouin gnoun\nJézu ap pansé lapé pansé dé ou.\n\n2.\nJézu ap véyé, lan chafgrin, tristès\nL-ap gadé ou é gidé ou koté ou pralé\nL-ap tan-n pou-L montré ou mizérikòd Li\nJézu ap pansé, lapé pansé dé ou\nJézu ap pansé, lè ou sa zanmi\nÉ ou bézouin gnoun\nJézu ap pansé, lapé pansé dé ou.','<p><strong>1.</strong> </p>\n<p>Si ou dékourajé ak fado é krint </p>\n<p>San fòs, san éspoua </p>\n<p>Pa min-m gnou sékou </p>\n<p>Malgré ou ap viv sèl </p>\n<p>É rout ou gin mò-n </p>\n<p>Jézu ap pansé, lapé pansé dé ou.  (3 foua) </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè ou san zanmi</strong> </p>\n<p><strong>É ou bézouin gnoun</strong> </p>\n<p><strong>Jézu ap pansé lapé pansé dé ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu ap véyé, lan chafgrin, tristès </p>\n<p>L-ap gadé ou é gidé ou koté ou pralé </p>\n<p>L-ap tan-n pou-L montré ou mizérikòd Li </p>\n<p>Jézu ap pansé, lapé pansé dé ou </p>\n<p>Jézu ap pansé, lè ou sa zanmi </p>\n<p>É ou bézouin gnoun </p>\n<p>Jézu ap pansé, lapé pansé dé ou. </p>\n','Si-ou-dekouraje',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1942,'6abb6e61-04cb-4512-873b-029a4d9bc9f6',0,0,'Nap pran kouraj ak',46,'46_EE_ht','1.\nNap pran kouraj ak tout bagay\nSan nou pa jan-m dékourajé\nSèvi Bon-Dieu sé gnou bagay trè difisil,\nPèson-n moun pap dékourajé     (trè difisil\nNan gran travay Bon-Dieu ya\nMouin gin éspoua ma délivré pou toujou.\n\n2.\nMap pran kouraj map supòté\nMouin konnin gnou jou\nTout bagay gingnin pou li fini, pou li fini\nSé Bon-Dieu ki gin tout puisans\nLi va kinbé mouin avèk amou Li\nJou-m délivré ma va chanté\nGloua pou Bon-Dieu.\n\n3.\nPafoua rout la kon difisil, \nAk tout kalité tèt chajé\nYap kritiké-m, yap voyé pouin \nSou mouin min-m sèl\nÉlmi-m yo ap fè tripotaj\nPafoua, min-m moun ki nan légliz\nJou-m délivré ma va chanté\nGloua pou Bon-Dieu.','<p><strong>1.</strong> </p>\n<p>Nap pran kouraj ak tout bagay </p>\n<p>San nou pa jan-m dékourajé </p>\n<p>Sèvi Bon-Dieu sé gnou bagay trè difisil, </p>\n<p>Pèson-n moun pap dékourajé     (trè difisil </p>\n<p>Nan gran travay Bon-Dieu ya </p>\n<p>Mouin gin éspoua ma délivré pou toujou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Map pran kouraj map supòté </p>\n<p>Mouin konnin gnou jou </p>\n<p>Tout bagay gingnin pou li fini, pou li fini </p>\n<p>Sé Bon-Dieu ki gin tout puisans </p>\n<p>Li va kinbé mouin avèk amou Li </p>\n<p>Jou-m délivré ma va chanté </p>\n<p>Gloua pou Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pafoua rout la kon difisil, </p>\n<p>Ak tout kalité tèt chajé </p>\n<p>Yap kritiké-m, yap voyé pouin </p>\n<p>Sou mouin min-m sèl </p>\n<p>Élmi-m yo ap fè tripotaj </p>\n<p>Pafoua, min-m moun ki nan légliz </p>\n<p>Jou-m délivré ma va chanté </p>\n<p>Gloua pou Bon-Dieu. </p>\n','Nap-pran-kouraj-ak',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1943,'f6f90c99-5192-4f7b-916c-987aff901a65',0,0,'A Jésus-Christ pour Son Service',19,'19_MJ_fr','1.\nA Jésus-Christ pour Son service\nDans le détail de chaque instant\nDans ses sentiers qu\'Il affermisse\nLes pas de Son enfant!\n\nChoeur:\nAh tiens-moi prêt pour Ton retour\nGardé par Ton fidèle amour,\nVictorieux, jour après jour,\nOui, tiens-moi prêt pour Ton retour.\n\n2.\nSeigneur, que je vive à Ta gloire\nTu m\'as sauvé pour Te servir,\nTa Parole me dit que croire\nC\'est T\'aimer, T\'obéir.\n\n3.\nEn attendant que l\'on Te voie,\nA l\'univers manifesté\nFais que j\'accomplisse avec joie\nTa sainte volonté.','<p><strong>1.</strong> </p>\n<p>A Jésus-Christ pour Son service </p>\n<p>Dans le détail de chaque instant </p>\n<p>Dans ses sentiers qu&#39;Il affermisse </p>\n<p>Les pas de Son enfant! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Ah tiens-moi prêt pour Ton retour</strong> </p>\n<p><strong>Gardé par Ton fidèle amour,</strong> </p>\n<p><strong>Victorieux, jour après jour,</strong> </p>\n<p><strong>Oui, tiens-moi prêt pour Ton retour.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seigneur, que je vive à Ta gloire </p>\n<p>Tu m&#39;as sauvé pour Te servir, </p>\n<p>Ta Parole me dit que croire </p>\n<p>C&#39;est T&#39;aimer, T&#39;obéir. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En attendant que l&#39;on Te voie, </p>\n<p>A l&#39;univers manifesté </p>\n<p>Fais que j&#39;accomplisse avec joie </p>\n<p>Ta sainte volonté. </p>\n','A-Jesus-Christ-pour-Son-Service',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1944,'19d9d780-a775-4b95-b155-86fea8095976',0,0,'Mouin konnin gnou jou',68,'68_EE_ht','1.\nMouin konnin gnou jou konba-m yo va fini\nLè ma rivé nan vil sélès la\nMa va chanté gnou chan mélanjé ak sin yo\nNan bèl ti vil dò nou-an.\n\nKè:\nMa va di lò adié\nLè ma rivé nan vil sélès la\nMa va chanté gnou chan mélanjé ak zanj yo\nNan péyi koté tristès fini.\n\n2.\nLè-m va rivé bò la ma rakonté lòt yo\nKòman rout la té trè du pou mouin\nMouin té jouin-n gnou puisans \nKi té désan-n sou mouin\nLi kondui-m nan péyi sélès la-a.\n\n3.\nLè-m va rivé anro ma salué ansyin yo\nMa mété ajenou kot Ségnè-m\nLè jou mouin va rivé pou mouin kité tè sa-a\nMa viré, san-m pa di o révoua.','<p><strong>1.</strong> </p>\n<p>Mouin konnin gnou jou konba-m yo va fini </p>\n<p>Lè ma rivé nan vil sélès la </p>\n<p>Ma va chanté gnou chan mélanjé ak sin yo </p>\n<p>Nan bèl ti vil dò nou-an. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ma va di lò adié</strong> </p>\n<p><strong>Lè ma rivé nan vil sélès la</strong> </p>\n<p><strong>Ma va chanté gnou chan mélanjé ak zanj yo</strong> </p>\n<p><strong>Nan péyi koté tristès fini.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè-m va rivé bò la ma rakonté lòt yo </p>\n<p>Kòman rout la té trè du pou mouin </p>\n<p>Mouin té jouin-n gnou puisans </p>\n<p>Ki té désan-n sou mouin </p>\n<p>Li kondui-m nan péyi sélès la-a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè-m va rivé anro ma salué ansyin yo </p>\n<p>Ma mété ajenou kot Ségnè-m </p>\n<p>Lè jou mouin va rivé pou mouin kité tè sa-a </p>\n<p>Ma viré, san-m pa di o révoua. </p>\n','Mouin-konnin-gnou-jou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1945,'cb4a7449-eb0f-410e-a4d4-cf1e6d5db014',0,0,'Chantons et louons',32,'32_MJ_fr','1.\nChantons et louons\nLe beau Nom de Jésus,\nExaltons-Le d\'un même coeur!\nIl est mort sur la croix,\nNos péchés ne sont plus,\nOh! Quel céleste bonheur! Oui bonheur!\nIl offre encore Son pardon, son salut\nO venez donc, oui, venez à Jésus!\n\n2.\nJésus reviendra\nDe son divin séjour,\nAfin de nous prendre avec Lui.\nVeillons donc et prions;\nAttendons Son retour,\nCar déjà l\'étoile luit (dans la nuit).\nQue de nos coeurs\nVers Lui monte en ce jour,\nUn chant joyeux,\nDe louange et d\'amour!','<p><strong>1.</strong> </p>\n<p>Chantons et louons </p>\n<p>Le beau Nom de Jésus, </p>\n<p>Exaltons-Le d&#39;un même coeur! </p>\n<p>Il est mort sur la croix, </p>\n<p>Nos péchés ne sont plus, </p>\n<p>Oh! Quel céleste bonheur! Oui bonheur! </p>\n<p>Il offre encore Son pardon, son salut </p>\n<p>O venez donc, oui, venez à Jésus! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus reviendra </p>\n<p>De son divin séjour, </p>\n<p>Afin de nous prendre avec Lui. </p>\n<p>Veillons donc et prions; </p>\n<p>Attendons Son retour, </p>\n<p>Car déjà l&#39;étoile luit (dans la nuit). </p>\n<p>Que de nos coeurs </p>\n<p>Vers Lui monte en ce jour, </p>\n<p>Un chant joyeux, </p>\n<p>De louange et d&#39;amour! </p>\n','Chantons-et-louons',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1946,'312be112-00de-4e0f-9361-34ee5a1de21f',0,0,'Le trésors si chers de la terre',18,'18_MJ_fr','1.\nLes trésors si chers de la terre\nJe ne les ai pas entassés\nMais un trésor plus grand du Père\nJe possède pour l\'éternité.\n\nChoeur:\nRichesses de l\'amour profond\nRichesses de l\'amour en Jésus\nPlus précieuses que l\'or du monde,\nCes richesses de l\'amour en Jésus.\n\n2.\nCes trésors terrestres sont vains\nL\'honneur du monde est passager\nMais les trésors infinis, divins\nLa mort ne peut pas me les ôter.\n\n3.\nDonc, prends ces richesses de Dieu\nGratuites, inépuisables\nReçois Son Saint-Esprit glorieux\nCe don du Père inexprimable.','<p><strong>1.</strong> </p>\n<p>Les trésors si chers de la terre </p>\n<p>Je ne les ai pas entassés </p>\n<p>Mais un trésor plus grand du Père </p>\n<p>Je possède pour l&#39;éternité. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Richesses de l&#39;amour profond</strong> </p>\n<p><strong>Richesses de l&#39;amour en Jésus</strong> </p>\n<p><strong>Plus précieuses que l&#39;or du monde,</strong> </p>\n<p><strong>Ces richesses de l&#39;amour en Jésus.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ces trésors terrestres sont vains </p>\n<p>L&#39;honneur du monde est passager </p>\n<p>Mais les trésors infinis, divins </p>\n<p>La mort ne peut pas me les ôter. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Donc, prends ces richesses de Dieu </p>\n<p>Gratuites, inépuisables </p>\n<p>Reçois Son Saint-Esprit glorieux </p>\n<p>Ce don du Père inexprimable. </p>\n','Le-tresors-si-chers-de-la-terre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1947,'441cd13d-01dc-4563-979b-e30e147f83d9',0,0,'Par sa grâce je suis sauvé',35,'35_MJ_fr','1.\nPar Sa grâce je suis sauvé\nPar le moyen de la foi\nCar Il paya ma rédemption\nQuand Il mourut sur la croix.\n\nChoeur:\nEt désormais, je veux servir\nJe veux servir mon Sauveur\nGlorifier par ma vie,\nCelui qui est mort\nPour moi pécheur.\n\n2.\nAutrefois j\'étais rebelle\nJ\'étais mort par mon péché;\nDieu dans Sa miséricorde,\nEn Christ m\'a ressuscité.\n\n3.\nLe chemin peut être épineux\nOù Il va me diriger\nMais ma main est à la charrue\nPour Lui je veux travailler.\n\n4.\nJe me place sur Ton autel,\nLié par les liens d\'amour\nReçois mon offrande entière\nJe veux Te servir toujours.','<p><strong>1.</strong> </p>\n<p>Par Sa grâce je suis sauvé </p>\n<p>Par le moyen de la foi </p>\n<p>Car Il paya ma rédemption </p>\n<p>Quand Il mourut sur la croix. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Et désormais, je veux servir</strong> </p>\n<p><strong>Je veux servir mon Sauveur</strong> </p>\n<p><strong>Glorifier par ma vie,</strong> </p>\n<p><strong>Celui qui est mort</strong> </p>\n<p><strong>Pour moi pécheur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Autrefois j&#39;étais rebelle </p>\n<p>J&#39;étais mort par mon péché; </p>\n<p>Dieu dans Sa miséricorde, </p>\n<p>En Christ m&#39;a ressuscité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le chemin peut être épineux </p>\n<p>Où Il va me diriger </p>\n<p>Mais ma main est à la charrue </p>\n<p>Pour Lui je veux travailler. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je me place sur Ton autel, </p>\n<p>Lié par les liens d&#39;amour </p>\n<p>Reçois mon offrande entière </p>\n<p>Je veux Te servir toujours. </p>\n','Par-sa-grace-je-suis-sauve',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1948,'96a7625c-1039-41d4-8898-d55a2a514cf6',0,0,'Jésus divin Roi',29,'29_MJ_fr','1.\nJésus divin roi\nQue je sois pour toi.\nComme un chant d\'espérance,\nD\'amour et de foi.\nTu vis dans mon coeur,\nTu fais tout bonheur\nOh, fais de ma vie une saint harmonie.','<p><strong>1.</strong> </p>\n<p>Jésus divin roi </p>\n<p>Que je sois pour toi. </p>\n<p>Comme un chant d&#39;espérance, </p>\n<p>D&#39;amour et de foi. </p>\n<p>Tu vis dans mon coeur, </p>\n<p>Tu fais tout bonheur </p>\n<p>Oh, fais de ma vie une saint harmonie. </p>\n','Jesus-divin-Roi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1949,'846335eb-1b21-430c-9464-74d6305077bb',0,0,'O grâce merveilleuse',41,'41_MJ_fr','1.\nO grâce merveilleuse\nDon qui nous vient de Dieu\nGrâce si précieuse\nFleuve jailli des cieux.\nQui donc saurait décrire\nSa force et sa fraîcheur?\nEt qui donc a pu dire\nQuelle est sa profondeur?\n\nChoeur:\nNous n\'avons qu\'un seul\nSujet de gloire:\n(Qu\'un seul sujet de gloire)\nC\'est Ta sainte croix divin Sauveur.\n(Ta sainte croix, divin Sauveur)\nEt par Ton sang nous marchons\n(Ta grâce est puissante, toute suffisante)\nEn vainqueurs!\nTous en vainqueurs.\n(Et Ton sang nous rend plus que vainqueurs)\nPlus touchant que\nL\'hymne des archanges\nEt le chant qu\'entonnent les élus\nVenez chrétiens qu\'éclatent vos louanges:\nGloire à Jésus!\n\n2.\nO grâce qui nous donne\nLe vrai repos du coeur,\nEt qui nous environne\nQuand rôde le malheur!\nElle a rompu nos chaînes\nEt couvre nos forfaits.\nQue Satan se démène:\nNous demeurons en Paix.\n\n3.\nGrâce sans fond ni rive,\nPlus vaste que la mer,\nSource dont les eaux vives\nCoulent en plein désert!\nPar ce courant de vie,\nNous nous savons portés\nEt notre âme affranchie\nChante sa liberté.','<p><strong>1.</strong> </p>\n<p>O grâce merveilleuse </p>\n<p>Don qui nous vient de Dieu </p>\n<p>Grâce si précieuse </p>\n<p>Fleuve jailli des cieux. </p>\n<p>Qui donc saurait décrire </p>\n<p>Sa force et sa fraîcheur? </p>\n<p>Et qui donc a pu dire </p>\n<p>Quelle est sa profondeur? </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Nous n&#39;avons qu&#39;un seul</strong> </p>\n<p><strong>Sujet de gloire:</strong> </p>\n<p><strong>(Qu&#39;un seul sujet de gloire)</strong> </p>\n<p><strong>C&#39;est Ta sainte croix divin Sauveur.</strong> </p>\n<p><strong>(Ta sainte croix, divin Sauveur)</strong> </p>\n<p><strong>Et par Ton sang nous marchons</strong> </p>\n<p><strong>(Ta grâce est puissante, toute suffisante)</strong> </p>\n<p><strong>En vainqueurs!</strong> </p>\n<p><strong>Tous en vainqueurs.</strong> </p>\n<p><strong>(Et Ton sang nous rend plus que vainqueurs)</strong> </p>\n<p><strong>Plus touchant que</strong> </p>\n<p><strong>L&#39;hymne des archanges</strong> </p>\n<p><strong>Et le chant qu&#39;entonnent les élus</strong> </p>\n<p><strong>Venez chrétiens qu&#39;éclatent vos louanges:</strong> </p>\n<p><strong>Gloire à Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O grâce qui nous donne </p>\n<p>Le vrai repos du coeur, </p>\n<p>Et qui nous environne </p>\n<p>Quand rôde le malheur! </p>\n<p>Elle a rompu nos chaînes </p>\n<p>Et couvre nos forfaits. </p>\n<p>Que Satan se démène: </p>\n<p>Nous demeurons en Paix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Grâce sans fond ni rive, </p>\n<p>Plus vaste que la mer, </p>\n<p>Source dont les eaux vives </p>\n<p>Coulent en plein désert! </p>\n<p>Par ce courant de vie, </p>\n<p>Nous nous savons portés </p>\n<p>Et notre âme affranchie </p>\n<p>Chante sa liberté. </p>\n','O-grace-merveilleuse',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1950,'3d712f74-9b47-4f96-a42d-a7bbb9f81585',0,0,'Dans de verts pâturages',44,'44_MJ_fr','1.\nDans de verts pâturages\nEt l\'ombrages doux\nDieu dirige Ses chers enfants.\nOù les eaux paisibles\nRafraîchissent partout\nDieu dirige Ses chers enfants.\n\nChoeur:\nSoit un chemin\nSombre et glissant\nSoit par la fournaise –\nEt tous par le Sang\nMais dans l\'affliction\nDieu leur donne un chant.\nSon grand amour\nEntour Ses enfants.\n\n2.\nParfois sur la montagne\nOù le soleil luit\nDieu dirige Ses chers enfants.\nParfois dans la vallée\nA travers la nuit\nDieu dirige Ses chers enfants.\n\n3.\nEt quand les assaille\nLe grand tentateur\nDieu dirige Ses chers enfants.\nPar Sa grâce toujours\nIls seront vainqueurs\nDieu dirige Ses chers enfants.\n\n4.\nBien loin de la fosse\nEt loin de vils péchés\nDieu dirige Ses chers enfants.\nLoin de cette terre\nPour l\'éternité\nDieu dirige Ses chers enfants.','<p><strong>1.</strong> </p>\n<p>Dans de verts pâturages </p>\n<p>Et l&#39;ombrages doux </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p>Où les eaux paisibles </p>\n<p>Rafraîchissent partout </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Soit un chemin</strong> </p>\n<p><strong>Sombre et glissant</strong> </p>\n<p><strong>Soit par la fournaise –</strong> </p>\n<p><strong>Et tous par le Sang</strong> </p>\n<p><strong>Mais dans l&#39;affliction</strong> </p>\n<p><strong>Dieu leur donne un chant.</strong> </p>\n<p><strong>Son grand amour</strong> </p>\n<p><strong>Entour Ses enfants.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Parfois sur la montagne </p>\n<p>Où le soleil luit </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p>Parfois dans la vallée </p>\n<p>A travers la nuit </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et quand les assaille </p>\n<p>Le grand tentateur </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p>Par Sa grâce toujours </p>\n<p>Ils seront vainqueurs </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Bien loin de la fosse </p>\n<p>Et loin de vils péchés </p>\n<p>Dieu dirige Ses chers enfants. </p>\n<p>Loin de cette terre </p>\n<p>Pour l&#39;éternité </p>\n<p>Dieu dirige Ses chers enfants. </p>\n','Dans-de-verts-paturages',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1951,'6f9dbfc2-e4d9-457c-93ef-9224a50ebff0',0,0,'Doucement tendrement',50,'50_MJ_fr','1.\nDoucement tendrement,\nJésus appelle,\nNous appelle, vous et moi;\nPatiemment Jésus attends le rebelle,\nIl nous attend, vous et moi.\n\nChoeur:\nVenez, (venez), Venez à moi!\nTravaillés, chargés, venez à moi.\nDoucement, tendrement,\nJésus appelle,\nVenez! Oh! Venez à moi.\n\n2.\nAu vrai bonheur,\nJésus-Christ nous convie,\nBonheur pour vous et pour moi;\nIl nous l\'acquitte\nQuand Il donna Sa vie,\nSa vie est pour vous et pour moi.','<p><strong>1.</strong> </p>\n<p>Doucement tendrement, </p>\n<p>Jésus appelle, </p>\n<p>Nous appelle, vous et moi; </p>\n<p>Patiemment Jésus attends le rebelle, </p>\n<p>Il nous attend, vous et moi. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Venez, (venez), Venez à moi!</strong> </p>\n<p><strong>Travaillés, chargés, venez à moi.</strong> </p>\n<p><strong>Doucement, tendrement,</strong> </p>\n<p><strong>Jésus appelle,</strong> </p>\n<p><strong>Venez! Oh! Venez à moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Au vrai bonheur, </p>\n<p>Jésus-Christ nous convie, </p>\n<p>Bonheur pour vous et pour moi; </p>\n<p>Il nous l&#39;acquitte </p>\n<p>Quand Il donna Sa vie, </p>\n<p>Sa vie est pour vous et pour moi. </p>\n','Doucement-tendrement',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1952,'b30836c6-5d4d-45a1-b308-9da1c0df1a7c',0,0,'Vivant sur cette terre de péché',56,'56_MJ_fr','1.\nVivant sur cette terre de péché\nDe confort, de bonheur privé\nLuttant contre l\'Adversaire obstiné\nQui peut m\'aider, sinon Jésus.\n\nChoeur:\nA qui crier? Oh à qui aller?\nPour un refuge sûr trouver?\nAyant besoin un ami pour m\'aider\nQui prendre, sinon Jésus-Christ?\n\n2.\nLes amis sont gentils\nEt je les aime\nNous vivons en de très bons termes\nMais quand mon âme\nA soif de paix suprême\nQui peut m\'aider, sinon Jésus?\n\n3.\nLa vie est belle\nAuprès de bon parents\nQui m\'entourent bien tendrement\nMais de la mort\nQuand mugit le torrent\nQui peut m\'aider, sinon Jésus?','<p><strong>1.</strong> </p>\n<p>Vivant sur cette terre de péché </p>\n<p>De confort, de bonheur privé </p>\n<p>Luttant contre l&#39;Adversaire obstiné </p>\n<p>Qui peut m&#39;aider, sinon Jésus. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>A qui crier? Oh à qui aller?</strong> </p>\n<p><strong>Pour un refuge sûr trouver?</strong> </p>\n<p><strong>Ayant besoin un ami pour m&#39;aider</strong> </p>\n<p><strong>Qui prendre, sinon Jésus-Christ?</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les amis sont gentils </p>\n<p>Et je les aime </p>\n<p>Nous vivons en de très bons termes </p>\n<p>Mais quand mon âme </p>\n<p>A soif de paix suprême </p>\n<p>Qui peut m&#39;aider, sinon Jésus? </p>\n<p><br/> <strong>3.</strong></p>\n<p>La vie est belle </p>\n<p>Auprès de bon parents </p>\n<p>Qui m&#39;entourent bien tendrement </p>\n<p>Mais de la mort </p>\n<p>Quand mugit le torrent </p>\n<p>Qui peut m&#39;aider, sinon Jésus? </p>\n','Vivant-sur-cette-terre-de-peche',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1953,'c4d0298e-898b-44b7-b740-e77b9502d252',0,0,'Lè map vini',9,'9_VR_ht','1.	\nLè map vini pèson-n pap jan-m konnin (bis)\nPa min-m zanj di sièl pap konnin non plus tou (bis)\n(bis 2 lign)\n\nKè:\nKinbé-m, ban-m kouraj mouin\nPou Satan pa pran mouin\nKinbé-m, ban-m kouraj mouin\nJouk tan la vi-n chaché-m.\n\n2.	\nLè map vini pèson-n pap jan-m konnin (bis)\nSé sèl Bon-Dié nan sièl ki konnin gran jou sa (bis)\n(bis 2 lign)\n\n3.	\nLè map vini pèson-n pap jan-m konnin (bis)\nGin gnou kout tronpèt kap sonnin nan sièl la  (bis)\n(bis 2 lign)','<p><strong>1.</strong> </p>\n<p>Lè map vini pèson-n pap jan-m konnin (bis) </p>\n<p>Pa min-m zanj di sièl pap konnin non plus tou (bis) </p>\n<p>(bis 2 lign) </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kinbé-m, ban-m kouraj mouin</strong> </p>\n<p><strong>Pou Satan pa pran mouin</strong> </p>\n<p><strong>Kinbé-m, ban-m kouraj mouin</strong> </p>\n<p><strong>Jouk tan la vi-n chaché-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè map vini pèson-n pap jan-m konnin (bis) </p>\n<p>Sé sèl Bon-Dié nan sièl ki konnin gran jou sa (bis) </p>\n<p>(bis 2 lign) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè map vini pèson-n pap jan-m konnin (bis) </p>\n<p>Gin gnou kout tronpèt kap sonnin nan sièl la  (bis) </p>\n<p>(bis 2 lign) </p>\n','Le-map-vini',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1954,'e5232f57-3a67-49d8-a6cf-8f49f2fb3cd2',0,0,'Peut-être au matin',37,'37_VR_fr','1.	\nPeut-être au matin quand le soleil se lève\nEt que ses rayons chassent la nuit si brève\nJésus reviendra dans sa gloire immortelle\nPour prendre du monde les siens.\n\nChoeur\nSeigneur Jésus, combien de temps\nPour qu\'éclatent nos chants?\nJésus revient, Alléluia!\nAlléluia! Amen.  (bis)\n\n2.	\nPeut-être qu\'au soir, ou peut-être qu\'à midi?\nOu peut-être encore qu\'aux ténèbres de minuit,\nTous illuminés par l\'éclat de sa gloire,\nLe Seigneur prendra les siens.\n\n3.	\nTandis que l\'armée du ciel chante Alléluia!\nL\'Époux a l\'épouse pour toujours s\'unira\nSur son front la grâce est un halo de gloire\nQuand Jésus prendra les siens.\n\n4.	\nOh joie! Oh bonheur! Nous irons donc sans mourir\nSans crainte et sans pleurs,\nSans tristesse et sans souffrir!\nEn un clin d\'oeil, nous serons tous glorifiés\nQuand Jésus prendra les siens.','<p><strong>1.</strong> </p>\n<p>Peut-être au matin quand le soleil se lève </p>\n<p>Et que ses rayons chassent la nuit si brève </p>\n<p>Jésus reviendra dans sa gloire immortelle </p>\n<p>Pour prendre du monde les siens. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Seigneur Jésus, combien de temps</strong> </p>\n<p><strong>Pour qu&#39;éclatent nos chants?</strong> </p>\n<p><strong>Jésus revient, Alléluia!</strong> </p>\n<p><strong>Alléluia! Amen.  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Peut-être qu&#39;au soir, ou peut-être qu&#39;à midi? </p>\n<p>Ou peut-être encore qu&#39;aux ténèbres de minuit, </p>\n<p>Tous illuminés par l&#39;éclat de sa gloire, </p>\n<p>Le Seigneur prendra les siens. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tandis que l&#39;armée du ciel chante Alléluia! </p>\n<p>L&#39;Époux a l&#39;épouse pour toujours s&#39;unira </p>\n<p>Sur son front la grâce est un halo de gloire </p>\n<p>Quand Jésus prendra les siens. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh joie! Oh bonheur! Nous irons donc sans mourir </p>\n<p>Sans crainte et sans pleurs, </p>\n<p>Sans tristesse et sans souffrir! </p>\n<p>En un clin d&#39;oeil, nous serons tous glorifiés </p>\n<p>Quand Jésus prendra les siens. </p>\n','Peut-etre-au-matin',NULL,'fr',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1955,'7992af1c-a87e-4243-a659-f9814a67c14f',0,0,'Gnou chan louanj',16,'16_VR_ht','1.	\nKè mouin boulvèsé anba kòlè Bon-Dieu,\nAnvi mon-n nan trinnin-m nan bafon péché\nSégnè, pou gnou pòv mizérab gin pitié,\nTou souit Li vi-n sékouri-m koté-m té yé.\n\nKè:\nLi rétiré-m nan boubié péché,\nLi mété pié-m sou tè fè-m sé vré,\nLi fè kè-m réjoui é m-a chanté\nGnou chan, gloua a Dieu, Alélouya!\n\n2.	\nLi té mété-m sou tè fè-m a koté Li\nPié mouin vi-n asuré nan chémin la vi,\nNan pouin danjé ankò, Li kinbé-m débou,\nMap maché pa-za-pa avèk Li toujou.\n\n3.	\nLi mété gnou chan louanj andan kè mouin\nNan nuit kou la jounin sé Li m-a chanté\nKè mouin ap débòdé, nan-m mouin afranchi,\nTout gloua pou Rédanmtè, Ségnè Jézu-Kri.\n\n4.	\nM-a chanté gran mizérikòd Li pou mouin\nMap félisité-L jouk tan tout moun konnin\nNan pouin lòt salu pou lé-zòm jodi-a,\nVi-n chèché sékou Li avan li tro ta.','<p><strong>1.</strong> </p>\n<p>Kè mouin boulvèsé anba kòlè Bon-Dieu, </p>\n<p>Anvi mon-n nan trinnin-m nan bafon péché </p>\n<p>Ségnè, pou gnou pòv mizérab gin pitié, </p>\n<p>Tou souit Li vi-n sékouri-m koté-m té yé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li rétiré-m nan boubié péché,</strong> </p>\n<p><strong>Li mété pié-m sou tè fè-m sé vré,</strong> </p>\n<p><strong>Li fè kè-m réjoui é m-a chanté</strong> </p>\n<p><strong>Gnou chan, gloua a Dieu, Alélouya!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té mété-m sou tè fè-m a koté Li </p>\n<p>Pié mouin vi-n asuré nan chémin la vi, </p>\n<p>Nan pouin danjé ankò, Li kinbé-m débou, </p>\n<p>Map maché pa-za-pa avèk Li toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li mété gnou chan louanj andan kè mouin </p>\n<p>Nan nuit kou la jounin sé Li m-a chanté </p>\n<p>Kè mouin ap débòdé, nan-m mouin afranchi, </p>\n<p>Tout gloua pou Rédanmtè, Ségnè Jézu-Kri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>M-a chanté gran mizérikòd Li pou mouin </p>\n<p>Map félisité-L jouk tan tout moun konnin </p>\n<p>Nan pouin lòt salu pou lé-zòm jodi-a, </p>\n<p>Vi-n chèché sékou Li avan li tro ta. </p>\n','Gnou-chan-louanj',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1956,'2a38bce5-85c6-4c4c-a746-0a2e1b0f84b6',0,0,'Mouin révé mouin tandé',10,'10_EE_ht','1.\nMouin révé mouin tandé trompèt yo\nKi anonsé gran jou jujman an\nMouin révé nasion yo asanblé\nPou yo jujé dévan tròn blan\nGnou anj éklatan kité tròn nan\nSou la tè ak la mè li kanpé\nLi sèmanté ak min douat sou sièl la\nKé tan-an va suspan-n ékzisté.\n\nKè:\nA la moun pral krié, rélé\nLè koupab yo tandé santans la\nYo rélé pou montagn yo kaché yo  (bis)\nYo prié min sé té trò ta.\n\n2.\nNon-m rich té parèt min lajan li\nDépi lontan té disparèt\nSan angnin dutou nan jujman li\nPou akité gnou si gran dèt\nGran nèg té la, min inpòtans li\nPat konté lè la mò mélé\nLè zanj la ékzaminé achiv li\nNon gran nèg pa gin tan mansioné.\n\n3.\nVèv yo té la ak òfélin yo\nBon-Dieu tandé lè yo krié\nNan pouin tristès janmin nan sièl la\nTout dlo nan zié yo fi-n suyé\nTafiatè té la ak  banbochè ya\nInsi ké moun kapé van-n tafia\nOuangatè, vòlè ak adultè yo\nTout ansan-m té désan-n nan lanfè.\n\n4.\nMésié moral rivé nan jujman\nMin bon avèv moral li pat sufi\nSa yo ki té krisifié Jézu\nPasé pou moun moral osi\nÉ sa yo ki pa té ko pasé\nPi ta na gin tan sové oui\nSé pa kòlè pou la rélijion,\nAnfin trouvé tan pou-l mouri.','<p><strong>1.</strong> </p>\n<p>Mouin révé mouin tandé trompèt yo </p>\n<p>Ki anonsé gran jou jujman an </p>\n<p>Mouin révé nasion yo asanblé </p>\n<p>Pou yo jujé dévan tròn blan </p>\n<p>Gnou anj éklatan kité tròn nan </p>\n<p>Sou la tè ak la mè li kanpé </p>\n<p>Li sèmanté ak min douat sou sièl la </p>\n<p>Ké tan-an va suspan-n ékzisté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la moun pral krié, rélé</strong> </p>\n<p><strong>Lè koupab yo tandé santans la</strong> </p>\n<p><strong>Yo rélé pou montagn yo kaché yo  (bis)</strong> </p>\n<p><strong>Yo prié min sé té trò ta.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non-m rich té parèt min lajan li </p>\n<p>Dépi lontan té disparèt </p>\n<p>San angnin dutou nan jujman li </p>\n<p>Pou akité gnou si gran dèt </p>\n<p>Gran nèg té la, min inpòtans li </p>\n<p>Pat konté lè la mò mélé </p>\n<p>Lè zanj la ékzaminé achiv li </p>\n<p>Non gran nèg pa gin tan mansioné. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vèv yo té la ak òfélin yo </p>\n<p>Bon-Dieu tandé lè yo krié </p>\n<p>Nan pouin tristès janmin nan sièl la </p>\n<p>Tout dlo nan zié yo fi-n suyé </p>\n<p>Tafiatè té la ak  banbochè ya </p>\n<p>Insi ké moun kapé van-n tafia </p>\n<p>Ouangatè, vòlè ak adultè yo </p>\n<p>Tout ansan-m té désan-n nan lanfè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mésié moral rivé nan jujman </p>\n<p>Min bon avèv moral li pat sufi </p>\n<p>Sa yo ki té krisifié Jézu </p>\n<p>Pasé pou moun moral osi </p>\n<p>É sa yo ki pa té ko pasé </p>\n<p>Pi ta na gin tan sové oui </p>\n<p>Sé pa kòlè pou la rélijion, </p>\n<p>Anfin trouvé tan pou-l mouri. </p>\n','Mouin-reve-mouin-tande',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1957,'51238dcf-6a3c-44c8-aace-2bcfaeacb46f',0,0,'Si Kris pat avè-m',17,'17_EE_ht','1.\nSi Kris pat avè-m kouman-m ta yé\nLè zéprèv yo ménasé vi mouin\nAvèk amou-L ki antouré mouin\nMét nan kè mouin gnou vrè chan pou Li.\n\nKè:\nKris gin supriz pou ou frè mouin\nKris gin supriz pou ou zanmi mouin\nSi ou lèsé-L dirijé la vi ou\nGras Li va gnou supriz pou ou.\n\n2.\nSi dlo nan zié-m ta koulé souvan\nSi angouas yo ta véglé zié mouin\nSi énmi-m yo ta ban mouin gnou défi\nDélivrans mouin sé nan gras ou.\n\n3.\nSégnè édé-m, fè mouin rivé,\nLòt bò mò-m nan, sé la-m vlé alé\nKoté ki gin la pè, é la joi\nSé la-m va pozé nan-m mouin.','<p><strong>1.</strong> </p>\n<p>Si Kris pat avè-m kouman-m ta yé </p>\n<p>Lè zéprèv yo ménasé vi mouin </p>\n<p>Avèk amou-L ki antouré mouin </p>\n<p>Mét nan kè mouin gnou vrè chan pou Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kris gin supriz pou ou frè mouin</strong> </p>\n<p><strong>Kris gin supriz pou ou zanmi mouin</strong> </p>\n<p><strong>Si ou lèsé-L dirijé la vi ou</strong> </p>\n<p><strong>Gras Li va gnou supriz pou ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si dlo nan zié-m ta koulé souvan </p>\n<p>Si angouas yo ta véglé zié mouin </p>\n<p>Si énmi-m yo ta ban mouin gnou défi </p>\n<p>Délivrans mouin sé nan gras ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ségnè édé-m, fè mouin rivé, </p>\n<p>Lòt bò mò-m nan, sé la-m vlé alé </p>\n<p>Koté ki gin la pè, é la joi </p>\n<p>Sé la-m va pozé nan-m mouin. </p>\n','Si-Kris-pat-ave-m',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1958,'2e1998c9-0f9b-4e80-ac02-190a6993b3c1',0,0,'J’étais pauvre',32,'32_EE_fr','1\nJ\'étais pauvre mais Il m\'a enrichi\nÉtant riche, Il s\'est fait pauvre pour moi\nMon Sauveur bien-aimé expia mon péché\nJ\'étais pauvre, amis Il m\'a enrichi.','<p>J&#39;étais pauvre mais Il m&#39;a enrichi </p>\n<p>Étant riche, Il s&#39;est fait pauvre pour moi </p>\n<p>Mon Sauveur bien-aimé expia mon péché </p>\n<p>J&#39;étais pauvre, amis Il m&#39;a enrichi. </p>\n','J\'etais-pauvre',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1959,'c99ee5ba-4992-47ef-bc8b-d4ac5c2716c0',1,0,'Mon ami que fais-tu',36,'36_EE_fr','1.\nMon ami que fais-tu dans ce monde\nOu la vie est pleine de tristesse\nVois-tu que le bonheur est ici-bas\nPourquoi ne penses-tu pas à Jésus.\n\nChoeur\nHosanna, Hosanna, Maranatha, Christ revient!\nIl était venu dans ce bas monde\nPour sauver les hommes en détresse\nCrois en Lui pendant que tu es ici-bas\nIl revient, oui bientôt, Il revient!','<p><strong>1.</strong> </p>\n<p>Mon ami que fais-tu dans ce monde </p>\n<p>Ou la vie est pleine de tristesse </p>\n<p>Vois-tu que le bonheur est ici-bas </p>\n<p>Pourquoi ne penses-tu pas à Jésus. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Hosanna, Hosanna, Maranatha, Christ revient!</strong> </p>\n<p><strong>Il était venu dans ce bas monde</strong> </p>\n<p><strong>Pour sauver les hommes en détresse</strong> </p>\n<p><strong>Crois en Lui pendant que tu es ici-bas</strong> </p>\n<p><strong>Il revient, oui bientôt, Il revient!</strong> </p>\n','Mon-ami-que-fais-tu',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:54'),(1960,'bca17ec0-2a47-443d-a48c-db25b6d4b353',0,0,'Mouin révé malé nan',41,'41_EE_ht','1.\nMouin révé-m alé nan gnou sité\nSité koté pap gin la nuit\nMouin té ouè zanj yo briyé nan gloua yo\nMouin té ouè bèl palè kristal la\nMouin té kab ouè frè ansyin mouin yo\nMouin té ouè amé sélès Kris la\nMin sa ki té pi bèl nan vizion an\nMouin rév1—m trouvé sièl la pou ou.\n\nKè:\nMouin révé-m trouvé sièl la pou ou\nO, oui-m trouvé sièl la pou ou\nÈské ou vlé préparé ou pou rankontré-m la\nMouin révé-m trouvé sièl la pou ou.\n\n2.\nMouin té ouè Kris nan bèl rouayòm Li\nMouin té ouè rachté yo avèk Li\nMouin té ouè rédanmtè béni mouin an\nKi té bay vi Li pou sové mouin\nTout sin yo tap chanté gnou bèl kantik\nYo tap bay Bon-Dieu gloua chak moman\nMouin té kontan pou-m kontanplé sin yo\nMouin révé-m trouvé sièl la pou ou.\n\n3.\nÉ ou min-m ki koté ou vlé alé\nÈské ou vlé alé nan sité sa-a\nKris ap tan-n ou dé bra Li louvri\nLi vlé fè gnou plas anro pou ou\nGnou jou ou va konvèti, min élas \nLi kapab byin tro ta pou ou jou sa\nZanmi, èské ou vlé tandé voua Li\nLi vlé louvri pòt sièl la pou ou.','<p><strong>1.</strong> </p>\n<p>Mouin révé-m alé nan gnou sité </p>\n<p>Sité koté pap gin la nuit </p>\n<p>Mouin té ouè zanj yo briyé nan gloua yo </p>\n<p>Mouin té ouè bèl palè kristal la </p>\n<p>Mouin té kab ouè frè ansyin mouin yo </p>\n<p>Mouin té ouè amé sélès Kris la </p>\n<p>Min sa ki té pi bèl nan vizion an </p>\n<p>Mouin rév1—m trouvé sièl la pou ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin révé-m trouvé sièl la pou ou</strong> </p>\n<p><strong>O, oui-m trouvé sièl la pou ou</strong> </p>\n<p><strong>Èské ou vlé préparé ou pou rankontré-m la</strong> </p>\n<p><strong>Mouin révé-m trouvé sièl la pou ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té ouè Kris nan bèl rouayòm Li </p>\n<p>Mouin té ouè rachté yo avèk Li </p>\n<p>Mouin té ouè rédanmtè béni mouin an </p>\n<p>Ki té bay vi Li pou sové mouin </p>\n<p>Tout sin yo tap chanté gnou bèl kantik </p>\n<p>Yo tap bay Bon-Dieu gloua chak moman </p>\n<p>Mouin té kontan pou-m kontanplé sin yo </p>\n<p>Mouin révé-m trouvé sièl la pou ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>É ou min-m ki koté ou vlé alé </p>\n<p>Èské ou vlé alé nan sité sa-a </p>\n<p>Kris ap tan-n ou dé bra Li louvri </p>\n<p>Li vlé fè gnou plas anro pou ou </p>\n<p>Gnou jou ou va konvèti, min élas </p>\n<p>Li kapab byin tro ta pou ou jou sa </p>\n<p>Zanmi, èské ou vlé tandé voua Li </p>\n<p>Li vlé louvri pòt sièl la pou ou. </p>\n','Mouin-reve-male-nan',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1961,'5de9a2fc-122f-4963-bfa9-03c91e2a4451',0,0,'Nan la vi sila fòk gnou',52,'52_EE_ht','1.\nNan la vi sila fòk gnou moun pa tro présé\nLèzòm di san fè, lé Ségnè min-m fè san di\nLè ouapé priyé, fòk ou aji foua ou\nLè sa Jézu va voyé délivrans pou ou.\n\nKè:\nOu min-m krétyin priyé, pa dékourajé\nPèsévéré nan la priè, mandé Li pasians\nSé Li ki mèt ou, sé Li k-éspoua ou,\nSé Li k-zanmi ou,\nÉpi oua ouè tout bagay réglé.\n\n2.\nMin-m si ou pat gin éspoua, Priyé Jézu chak jou\nSé Li-k pou délivré ou pou ba ou la vi\nDi Li mèsi toutan, répanti tout péché ou\nKonfésé ou ak Jézu, oua délivré.\n\n\n3.\nLè ou sonjé David, son non-m yo té méprizé\nLi té rivé roua pa puisans Pè Étènèl\nSé min-m jan ou min-m, si ou kinbé la foua\nLè sa Jézu va voyé délivrans pou ou.','<p><strong>1.</strong> </p>\n<p>Nan la vi sila fòk gnou moun pa tro présé </p>\n<p>Lèzòm di san fè, lé Ségnè min-m fè san di </p>\n<p>Lè ouapé priyé, fòk ou aji foua ou </p>\n<p>Lè sa Jézu va voyé délivrans pou ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ou min-m krétyin priyé, pa dékourajé</strong> </p>\n<p><strong>Pèsévéré nan la priè, mandé Li pasians</strong> </p>\n<p><strong>Sé Li ki mèt ou, sé Li k-éspoua ou,</strong> </p>\n<p><strong>Sé Li k-zanmi ou,</strong> </p>\n<p><strong>Épi oua ouè tout bagay réglé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m si ou pat gin éspoua, Priyé Jézu chak jou </p>\n<p>Sé Li-k pou délivré ou pou ba ou la vi </p>\n<p>Di Li mèsi toutan, répanti tout péché ou </p>\n<p>Konfésé ou ak Jézu, oua délivré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè ou sonjé David, son non-m yo té méprizé </p>\n<p>Li té rivé roua pa puisans Pè Étènèl </p>\n<p>Sé min-m jan ou min-m, si ou kinbé la foua </p>\n<p>Lè sa Jézu va voyé délivrans pou ou. </p>\n','Nan-la-vi-sila-fok-gnou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1962,'7a7148b8-d6c4-40b9-bf92-99e9cad03a86',0,0,'Lè mouin sèl é dékourajé',62,'62_EE_ht','1.\nLè mouin sèl é dékourajé\nLè-m pa gin moun pou mouin konfié-m\nMouin konnin Jézu Li toujou la pou-L édé mouin\nNan-m mouin vi-n kal\nLéspri-m vi-n lib, mouin konnin Jézu\nLi toujou la pou-L édé mouin.\n\nKè:\nSé Jézu sèl ki kap konpran-n mouin\nSé Jézu sèl ki kap édé\nÉ protéjé-m nan tout traka nan la vi sa-a\nSé Jézu sèl ki kinbé min-m\nSé Jézu sèl ki sékouré-m\nSé Jézu sèl, oui Li min-m sèl ki kon-n bézouin-m\n\n2.\nSi ou bézouin Li jodi-a\nOu mèt aproché bò la koua\nMouin konnin Jézu, Li toujou la pou-L édé ou\nOu mèt poté tout bézouin ou ba Li\nÉ Li va rézoud tout pou ou\nMouin konnin Jézu, Li toujou la pou-L édé ou.','<p><strong>1.</strong> </p>\n<p>Lè mouin sèl é dékourajé </p>\n<p>Lè-m pa gin moun pou mouin konfié-m </p>\n<p>Mouin konnin Jézu Li toujou la pou-L édé mouin </p>\n<p>Nan-m mouin vi-n kal </p>\n<p>Léspri-m vi-n lib, mouin konnin Jézu </p>\n<p>Li toujou la pou-L édé mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé Jézu sèl ki kap konpran-n mouin</strong> </p>\n<p><strong>Sé Jézu sèl ki kap édé</strong> </p>\n<p><strong>É protéjé-m nan tout traka nan la vi sa-a</strong> </p>\n<p><strong>Sé Jézu sèl ki kinbé min-m</strong> </p>\n<p><strong>Sé Jézu sèl ki sékouré-m</strong> </p>\n<p><strong>Sé Jézu sèl, oui Li min-m sèl ki kon-n bézouin-m</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si ou bézouin Li jodi-a </p>\n<p>Ou mèt aproché bò la koua </p>\n<p>Mouin konnin Jézu, Li toujou la pou-L édé ou </p>\n<p>Ou mèt poté tout bézouin ou ba Li </p>\n<p>É Li va rézoud tout pou ou </p>\n<p>Mouin konnin Jézu, Li toujou la pou-L édé ou. </p>\n','Le-mouin-sel-e-dekouraje',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1963,'1f343a22-f056-4661-98ef-3dd40409a167',0,0,'Nan mòn-n sa napé viv',72,'72_EE_ht','1.\nNan mòn-n sa napé viv\nSan Dieu san éspérans\nNou pa pansé ak malè jodi, démin\nKoté ou asuré, nan-m ou pa gin démè\nSé na Jézu nan-m ou va gin la pè.\n\nKè:\nOu min-m ki pòv, vi-n jouin-n Jézu\nOu min-m ki rich, vi-n jouin-n Jézu\nLévé pié ou, tè sa-a pral disparèt.\n\n2.\nNan mòn-n sa napé viv\nTout machi-n asuré\nOu min0m ki lò-m, ki koté ou asuré\nSé pa la kay bòkò pou al asuré ou\nSé nan Jézu nan-m ou va jouin-n la pè.\n\n3.\nNou sonjé lè flora, tout tan siklò-n azèl\nKi té pasé ki té détoui pèp Bon-Dieu\nLò-m dé sians té kaché, yo té fè jenou tè\nÉ témouayé grandè Bon-Dieu nou-an.\n\n4.\nGin gnou pi gran siklò-n kap vi-n avan lontan\nOu min-m frè, sè, ki tankou gnou Kay koulé\nOu ka tronpé solèy, ou pa kap tronpé la pli\nSé Jézu-Kri ki sondé kè tout moun.','<p><strong>1.</strong> </p>\n<p>Nan mòn-n sa napé viv </p>\n<p>San Dieu san éspérans </p>\n<p>Nou pa pansé ak malè jodi, démin </p>\n<p>Koté ou asuré, nan-m ou pa gin démè </p>\n<p>Sé na Jézu nan-m ou va gin la pè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ou min-m ki pòv, vi-n jouin-n Jézu</strong> </p>\n<p><strong>Ou min-m ki rich, vi-n jouin-n Jézu</strong> </p>\n<p><strong>Lévé pié ou, tè sa-a pral disparèt.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan mòn-n sa napé viv </p>\n<p>Tout machi-n asuré </p>\n<p>Ou min0m ki lò-m, ki koté ou asuré </p>\n<p>Sé pa la kay bòkò pou al asuré ou </p>\n<p>Sé nan Jézu nan-m ou va jouin-n la pè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nou sonjé lè flora, tout tan siklò-n azèl </p>\n<p>Ki té pasé ki té détoui pèp Bon-Dieu </p>\n<p>Lò-m dé sians té kaché, yo té fè jenou tè </p>\n<p>É témouayé grandè Bon-Dieu nou-an. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gin gnou pi gran siklò-n kap vi-n avan lontan </p>\n<p>Ou min-m frè, sè, ki tankou gnou Kay koulé </p>\n<p>Ou ka tronpé solèy, ou pa kap tronpé la pli </p>\n<p>Sé Jézu-Kri ki sondé kè tout moun. </p>\n','Nan-mon-n-sa-nape-viv',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1964,'289fbec5-786f-444e-96ff-96da764e607f',0,0,'Gin gnou bè jou',82,'82_EE_ht','1.\nGin gnou bèl jou lè la vi-m va fini\nMap vòltijé, map vòltijé.\nPou mouin alé nan kay sélès la;\nMap vòltijé, map vòltijé.\n\nRéfrin:\nMap vòltijé!  Ki bèl gloua!  Map vòltijé!\nLè mouin mouri, o alélouya pa lontant\nMap vòltijé!\n\n2.\nTout éprèv nan vi sa-a va fini,\nMap vòltijé!\nKou gnou zouazo ki té prizonié\nMap vòltijé!\n\n3.\nNan pé dé tan, jou yo pa tro louin,\nMap vòltijé,\nNan péyi koté jou yo pap fini\nMap vòltijé.','<p><strong>1.</strong> </p>\n<p>Gin gnou bèl jou lè la vi-m va fini </p>\n<p>Map vòltijé, map vòltijé. </p>\n<p>Pou mouin alé nan kay sélès la; </p>\n<p>Map vòltijé, map vòltijé. </p>\n<p><br/> <strong>Réfrin:</strong></p>\n<p><strong>Map vòltijé!  Ki bèl gloua!  Map vòltijé!</strong> </p>\n<p><strong>Lè mouin mouri, o alélouya pa lontant</strong> </p>\n<p><strong>Map vòltijé!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout éprèv nan vi sa-a va fini, </p>\n<p>Map vòltijé! </p>\n<p>Kou gnou zouazo ki té prizonié </p>\n<p>Map vòltijé! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan pé dé tan, jou yo pa tro louin, </p>\n<p>Map vòltijé, </p>\n<p>Nan péyi koté jou yo pap fini </p>\n<p>Map vòltijé. </p>\n','Gin-gnou-be-jou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1965,'a0ffddbb-03d1-4322-b8c5-e9ecf1adfd87',0,0,'Quelques mots à Jésus',3,'3_MJ_fr','1.\nQuelques mots à Jésus,\nOui quelques mots souvent,\nEt savoir tout Lui dire,\nOh que c\'est bienfaisant!\nNos troubles, nos ennuis,\nBien souvent je l\'ai vu,\nS\'apaisent vite,\nAprès quelques mots à Jésus.\n\n2.\nQuelques mots à Jésus,\nQuand on est affligé,\nQuand on a fait du mal\nEt qu\'on en est fâché,\nOui ces mots de regrets,\nEnfants, n\'hésitez plus,\nPour être consolés,\nDites-les à Jésus.\n\n3.\nQuelques mots à Jésus,\nAu milieu du bonheur,\nLui répéter souvent:\nMerci, merci Seigneur;\nVoilà ce qui lui plaît,\nSon coeur en est ému;\nAh! Sachons dire\nCes quelques mots à Jésus.','<p><strong>1.</strong> </p>\n<p>Quelques mots à Jésus, </p>\n<p>Oui quelques mots souvent, </p>\n<p>Et savoir tout Lui dire, </p>\n<p>Oh que c&#39;est bienfaisant! </p>\n<p>Nos troubles, nos ennuis, </p>\n<p>Bien souvent je l&#39;ai vu, </p>\n<p>S&#39;apaisent vite, </p>\n<p>Après quelques mots à Jésus. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quelques mots à Jésus, </p>\n<p>Quand on est affligé, </p>\n<p>Quand on a fait du mal </p>\n<p>Et qu&#39;on en est fâché, </p>\n<p>Oui ces mots de regrets, </p>\n<p>Enfants, n&#39;hésitez plus, </p>\n<p>Pour être consolés, </p>\n<p>Dites-les à Jésus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quelques mots à Jésus, </p>\n<p>Au milieu du bonheur, </p>\n<p>Lui répéter souvent: </p>\n<p>Merci, merci Seigneur; </p>\n<p>Voilà ce qui lui plaît, </p>\n<p>Son coeur en est ému; </p>\n<p>Ah! Sachons dire </p>\n<p>Ces quelques mots à Jésus. </p>\n','Quelques-mots-a-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1966,'a8dfbe7b-5558-4852-8aed-fa8b3e67d4e7',0,0,'Onde sur onde',16,'16_MJ_fr','1.\nOnde sur onde,\nComme un flot puissant;\nTon amour m\'inonde\nComme un océan.\n\n2.\nC\'est l\'océan de Ton amour\nO Jésus qui m\'inonde;\nC\'est l\'océan de Ton amour\nQui coule nuit et jour.','<p><strong>1.</strong> </p>\n<p>Onde sur onde, </p>\n<p>Comme un flot puissant; </p>\n<p>Ton amour m&#39;inonde </p>\n<p>Comme un océan. </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est l&#39;océan de Ton amour </p>\n<p>O Jésus qui m&#39;inonde; </p>\n<p>C&#39;est l&#39;océan de Ton amour </p>\n<p>Qui coule nuit et jour. </p>\n','Onde-sur-onde',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1967,'ab9538a5-549a-40a1-821a-37668b39bfc2',0,0,'Nous monterons',30,'30_MJ_fr','1.\nNous monterons,\nOh oui, nous monterons!\nQuand Jésus viendra du ciel\nNous monterons!\nLa trompette sonnera\nEt le Seigneur descendra\nPour nous prendre avec Lui\nDans Sa gloire,\nAlléluia!','<p><strong>1.</strong> </p>\n<p>Nous monterons, </p>\n<p>Oh oui, nous monterons! </p>\n<p>Quand Jésus viendra du ciel </p>\n<p>Nous monterons! </p>\n<p>La trompette sonnera </p>\n<p>Et le Seigneur descendra </p>\n<p>Pour nous prendre avec Lui </p>\n<p>Dans Sa gloire, </p>\n<p>Alléluia! </p>\n','Nous-monterons',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1968,'9a21a2e0-041c-4a84-85b3-460cf33baf52',0,0,'Nan la Bib yo rakonté',3,'3_VR_ht','1.	\nNan la Bib yo rakonté istoua gnou profèt apélé\nA gnou vil ki té pèdi nan péché    \nOkin-n moun la li tandé palé konnin gras Bon-Dié\nÉ profèt la pat gin kouraj pou-l alé.\n\nKè:\nO la ba, oui la ba nan péi-a bon é bèl\nLi va rakonté-m tou sa nan sièl la\nNan péi-a «Allélouya» map pran min Jonas a pa\nÉ la rakonté-m tou sa, oui la ba.\n\n2.	\nO Bon-Dié voyé Jonas préché vil-la répantans\nMin nan gnou bak a Tasis li vlé alé\nMin van yo vanté li fò li pat kapab rét abò\nSé nan vant pouason an li sonjé prié.\n\n3.	\nO-milié gnou gran lanmè dlo nan jé-l lap la priè\nMin pouason réjté Jonas sou rivaj\nAvèk joua lalé tou souit préché Niniv jou é noui-t\nDézobéisans fini é li vi-n saj.\n\n4.	\nLè Jonas palé konsa pèp Niniv yo konvèti\nRè-n dé midi konvèti nan tròn la\nRoua Niniv la konvèti li krié dlo nan jé li\nFòs la nan min Létènèl, oui la ba.','<p><strong>1.</strong> </p>\n<p>Nan la Bib yo rakonté istoua gnou profèt apélé </p>\n<p>A gnou vil ki té pèdi nan péché </p>\n<p>Okin-n moun la li tandé palé konnin gras Bon-Dié </p>\n<p>É profèt la pat gin kouraj pou-l alé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O la ba, oui la ba nan péi-a bon é bèl</strong> </p>\n<p><strong>Li va rakonté-m tou sa nan sièl la</strong> </p>\n<p><strong>Nan péi-a «Allélouya» map pran min Jonas a pa</strong> </p>\n<p><strong>É la rakonté-m tou sa, oui la ba.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O Bon-Dié voyé Jonas préché vil-la répantans </p>\n<p>Min nan gnou bak a Tasis li vlé alé </p>\n<p>Min van yo vanté li fò li pat kapab rét abò </p>\n<p>Sé nan vant pouason an li sonjé prié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O-milié gnou gran lanmè dlo nan jé-l lap la priè </p>\n<p>Min pouason réjté Jonas sou rivaj </p>\n<p>Avèk joua lalé tou souit préché Niniv jou é noui-t </p>\n<p>Dézobéisans fini é li vi-n saj. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè Jonas palé konsa pèp Niniv yo konvèti </p>\n<p>Rè-n dé midi konvèti nan tròn la </p>\n<p>Roua Niniv la konvèti li krié dlo nan jé li </p>\n<p>Fòs la nan min Létènèl, oui la ba. </p>\n','Nan-la-Bib-yo-rakonte',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1969,'74247172-f235-4dbc-a6f5-f0426af54112',0,0,'Pi bon zanmi mouin',7,'7_VR_ht','1.	\nPi bon zanmi mouin gingnin sé Jézu\nLi avèk mouin koté-m pasé\nSé Li min-m rakonté tout afè-m\nÉ-L ban-m toujou konsèy.\n\nKè:\nPa gin gnou lòt ki rinmin-m kon Li\nLi konnin tout sa ki nan kè mouin\nPi bon zanmi mouin gingnin sé Jézu\nPa gin gnou lòt kon Li.\n\n2.	\nLé mon-n kouè yo ka séparé-m ak Kris\nYo pa kapab, yo pa kapab,\nPaské alians la té fèt ak san Li\nYo pa kapab krazé-l.\n\n3.	\nMouin jouinn nan Jézu tout sa-m tap chaché\nLa pè, la joua, min-m la santé\nÉ Jézu ban mouin vi étènèl\nSa-m pat kapab achté-l.\n\n4.	\nJézu bézouin lòt moun pou zanmi-L\nLap rélé ou, lap rélé ou\nSi ou répon-n Li la résévoua ou\nÉ-L va sové nan-m ou.','<p><strong>1.</strong> </p>\n<p>Pi bon zanmi mouin gingnin sé Jézu </p>\n<p>Li avèk mouin koté-m pasé </p>\n<p>Sé Li min-m rakonté tout afè-m </p>\n<p>É-L ban-m toujou konsèy. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pa gin gnou lòt ki rinmin-m kon Li</strong> </p>\n<p><strong>Li konnin tout sa ki nan kè mouin</strong> </p>\n<p><strong>Pi bon zanmi mouin gingnin sé Jézu</strong> </p>\n<p><strong>Pa gin gnou lòt kon Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mon-n kouè yo ka séparé-m ak Kris </p>\n<p>Yo pa kapab, yo pa kapab, </p>\n<p>Paské alians la té fèt ak san Li </p>\n<p>Yo pa kapab krazé-l. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin jouinn nan Jézu tout sa-m tap chaché </p>\n<p>La pè, la joua, min-m la santé </p>\n<p>É Jézu ban mouin vi étènèl </p>\n<p>Sa-m pat kapab achté-l. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu bézouin lòt moun pou zanmi-L </p>\n<p>Lap rélé ou, lap rélé ou </p>\n<p>Si ou répon-n Li la résévoua ou </p>\n<p>É-L va sové nan-m ou. </p>\n','Pi-bon-zanmi-mouin',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1970,'951cab8f-f444-4d45-b25a-32f7b5dce4ae',0,0,'Pitit byin-émé',12,'12_VR_ht','1.	\nPiti byin-émé nan Jézu,\nMèvéyé paròl la gras;\nNan Pitit Li Papa ouè nou\nGras a Kris Li ba nou plas.\n\nKè:\nAlèkilé nou sé pitit Bon-Dieu\nÉ sa nou va yé pakò manifésté,\nMin nou konnin lè Li va parèt,\nNou va sanblé Li,\nNou va sanblé Li,\nPaské n-a ouè Li jan Li yé.\n\n2.	\nÉspérans béni pa louin nou\nByinto n-a ouè gloua Bon-Dieu\nLè ékla sélès parèt tou\nN-a kontanplé fas Jézu.\n\n3.	\nPromès Kris va réalizé,\nN-a gin min-m imaj ak Li,\nSé puisans Li ki transfòmé,\nChak moun ki pral konvèti.','<p><strong>1.</strong> </p>\n<p>Piti byin-émé nan Jézu, </p>\n<p>Mèvéyé paròl la gras; </p>\n<p>Nan Pitit Li Papa ouè nou </p>\n<p>Gras a Kris Li ba nou plas. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alèkilé nou sé pitit Bon-Dieu</strong> </p>\n<p><strong>É sa nou va yé pakò manifésté,</strong> </p>\n<p><strong>Min nou konnin lè Li va parèt,</strong> </p>\n<p><strong>Nou va sanblé Li,</strong> </p>\n<p><strong>Nou va sanblé Li,</strong> </p>\n<p><strong>Paské n-a ouè Li jan Li yé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Éspérans béni pa louin nou </p>\n<p>Byinto n-a ouè gloua Bon-Dieu </p>\n<p>Lè ékla sélès parèt tou </p>\n<p>N-a kontanplé fas Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Promès Kris va réalizé, </p>\n<p>N-a gin min-m imaj ak Li, </p>\n<p>Sé puisans Li ki transfòmé, </p>\n<p>Chak moun ki pral konvèti. </p>\n','Pitit-byin-eme',NULL,'ht',2,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1971,'7d8873e1-08cd-4bb4-947e-71787487d6d3',0,0,'Mouin gingnin gnou bagay',11,'11_EE_ht','1.\nMouin gingnin gnou bagay\nKi fè kè mouin kontan\nAnpil moun étoné, pou ouè koman-m kontan\nÉ ma-p toujou kontan.\nÉspoua-m pa sou tè sa-a, \nDaprè promès Bon-Dieu.\nMin-m si mouin té grangou,\nPat gin kay pou-m rété\nMap rét tan-n gran jou sa-a.\n\nKè:\nPap gin palé fransè, konésans pral fini\nRichès pral disparèt, Kris ap vini\nPap gingnin gro mésié, \nPap gingnin gro madan-m\nNou tout va min-m bagay, lè jézu va vini\nLi pap vini chèché savan ak zotobré\nSèlman li va bézouin sa ki fè volonté-L\nYo va min nan la min. (bis)\n\n2.\nAnpil moun konvèti, yo pap chèché Bon-Dieu\nYapé prié sèlman pou kay avèk oto\nPou kay avèk oto, sé bagay kap pasé\nYo pa prié Bon-dieu\nPou padon péché yo, fòk nou fèt atansyon\nPaské kay ak oto\nPap monté gran jou sa.\n\n3.\nPou kapab al légliz, fòk ou byin abiyé\nSak pa gin pou-l  mété, pa doué chita koté ou\nKè ou ranpli ak ògèy\nSonjé sa Kris té di avèk gran Nikodèm\nSi ou ta vlé gin la vi, fòk ou né dé nouvo\nÉ oua ouè rouayòm sa-a.\n\n4.\nLa sians apé travay malgré sa sé nulman\nSak kon-n fè méchansté ap vini pi méchan\nLòm pa kap chanjé lòm sinon san Jézu-Kri\nMin-m si ou té lougarou\nOu té kon-n tuyé moun\nJézu kapab chanjé ou.','<p><strong>1.</strong> </p>\n<p>Mouin gingnin gnou bagay </p>\n<p>Ki fè kè mouin kontan </p>\n<p>Anpil moun étoné, pou ouè koman-m kontan </p>\n<p>É ma-p toujou kontan. </p>\n<p>Éspoua-m pa sou tè sa-a, </p>\n<p>Daprè promès Bon-Dieu. </p>\n<p>Min-m si mouin té grangou, </p>\n<p>Pat gin kay pou-m rété </p>\n<p>Map rét tan-n gran jou sa-a. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pap gin palé fransè, konésans pral fini</strong> </p>\n<p><strong>Richès pral disparèt, Kris ap vini</strong> </p>\n<p><strong>Pap gingnin gro mésié,</strong> </p>\n<p><strong>Pap gingnin gro madan-m</strong> </p>\n<p><strong>Nou tout va min-m bagay, lè jézu va vini</strong> </p>\n<p><strong>Li pap vini chèché savan ak zotobré</strong> </p>\n<p><strong>Sèlman li va bézouin sa ki fè volonté-L</strong> </p>\n<p><strong>Yo va min nan la min. (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Anpil moun konvèti, yo pap chèché Bon-Dieu </p>\n<p>Yapé prié sèlman pou kay avèk oto </p>\n<p>Pou kay avèk oto, sé bagay kap pasé </p>\n<p>Yo pa prié Bon-dieu </p>\n<p>Pou padon péché yo, fòk nou fèt atansyon </p>\n<p>Paské kay ak oto </p>\n<p>Pap monté gran jou sa. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pou kapab al légliz, fòk ou byin abiyé </p>\n<p>Sak pa gin pou-l  mété, pa doué chita koté ou </p>\n<p>Kè ou ranpli ak ògèy </p>\n<p>Sonjé sa Kris té di avèk gran Nikodèm </p>\n<p>Si ou ta vlé gin la vi, fòk ou né dé nouvo </p>\n<p>É oua ouè rouayòm sa-a. </p>\n<p><br/> <strong>4.</strong></p>\n<p>La sians apé travay malgré sa sé nulman </p>\n<p>Sak kon-n fè méchansté ap vini pi méchan </p>\n<p>Lòm pa kap chanjé lòm sinon san Jézu-Kri </p>\n<p>Min-m si ou té lougarou </p>\n<p>Ou té kon-n tuyé moun </p>\n<p>Jézu kapab chanjé ou. </p>\n','Mouin-gingnin-gnou-bagay',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1972,'df02b2e4-bf8b-4bee-9bfd-62fb580bcdf9',0,0,'Mouin vlé sanblé ou',18,'18_EE_ht','1.\nMouin vlé sanblé ou Rédanmtè béni\nSé sa dézi-m ak la priè-m\nTout trézò tè sa pa intérésé-m\nSi sèlman mouin kap sanblé Ségnè-m.\n\nKè:\nMouin vlé sanblé Ou, mouin vlé sanblé Ou,\nRédanmtè b1 ni konplètman Sin,\nVi-n nan dousè Ou, répon-n priè mouin\nInprimé pòtré Ou nan mouin.\n\n2.\nMouin vlé sanblé Ou nan umilité\nSin, inosan, san tach, san bla-m\nDousman, supòtan reproch méchan yo\nPrèt pou soufri lè zòt pou sové.','<p><strong>1.</strong> </p>\n<p>Mouin vlé sanblé ou Rédanmtè béni </p>\n<p>Sé sa dézi-m ak la priè-m </p>\n<p>Tout trézò tè sa pa intérésé-m </p>\n<p>Si sèlman mouin kap sanblé Ségnè-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin vlé sanblé Ou, mouin vlé sanblé Ou,</strong> </p>\n<p><strong>Rédanmtè b1 ni konplètman Sin,</strong> </p>\n<p><strong>Vi-n nan dousè Ou, répon-n priè mouin</strong> </p>\n<p><strong>Inprimé pòtré Ou nan mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin vlé sanblé Ou nan umilité </p>\n<p>Sin, inosan, san tach, san bla-m </p>\n<p>Dousman, supòtan reproch méchan yo </p>\n<p>Prèt pou soufri lè zòt pou sové. </p>\n','Mouin-vle-sanble-ou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1973,'e0a44196-8384-4f90-92f1-cd8b736fe819',0,0,'Antre dé brigan',20,'20_EE_ht','1.\nAntré dé brigan, dé zasasin, dé malfètè\nSou gnou koua an boua, Yo té kloué Jézu\nLi pat fè mal, Sé gnou Non-m dé byin dé Nazarèt\nSa yo di Li fè, pou ki yo krusifié-L?\n\nKè:\nOui mon ami, sé pou mouin, sé pou ou\nYo krusifié Jézu pou péché-m kap lavé\nSévré zanmi-m si ou kouè nan Jézu-Kri\nJanmin ou pap péri, oua gin la vi étènèl.\n\n2.\nLi té mouri résusité é Li monté\nLap rétounin pou-L vi-n chèché moun pal\nÈské ou ta vlé ou ta rinmin alé nan sièl;\nSa ou di zanmi, pouki ou pa vini?','<p><strong>1.</strong> </p>\n<p>Antré dé brigan, dé zasasin, dé malfètè </p>\n<p>Sou gnou koua an boua, Yo té kloué Jézu </p>\n<p>Li pat fè mal, Sé gnou Non-m dé byin dé Nazarèt </p>\n<p>Sa yo di Li fè, pou ki yo krusifié-L? </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui mon ami, sé pou mouin, sé pou ou</strong> </p>\n<p><strong>Yo krusifié Jézu pou péché-m kap lavé</strong> </p>\n<p><strong>Sévré zanmi-m si ou kouè nan Jézu-Kri</strong> </p>\n<p><strong>Janmin ou pap péri, oua gin la vi étènèl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té mouri résusité é Li monté </p>\n<p>Lap rétounin pou-L vi-n chèché moun pal </p>\n<p>Èské ou ta vlé ou ta rinmin alé nan sièl; </p>\n<p>Sa ou di zanmi, pouki ou pa vini? </p>\n','Antre-de-brigan',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1974,'769ed6b8-a26a-4807-9d27-407092f4a7a6',1,0,'J\'ai toujours voulu trouver la lumière',24,'24_EE_fr','1.\nJ\'ai toujours voulu trouver la lumière\nJ\'étais confus mais sur cette terre\nTu m\'as reconnu\nSi parfois je tombe, je n\'aurais pas honte\nJe prends la vie quand tu me l\'as dit.\n\nRefrain\nUn jour à la fois, doux Jésus,\nC\'est tout ce que je peux donner\nTu m\'as donné la foi pour changer ma vie\nEt je crois en Toi\nHier n\'est plus, doux Jésus,\nEt demain n\'est pas encore là\nSi je ne le vois pas\nAssiste-moi, un jour à la fois.\n\n2.\nJe T\'ai rencontré, Tu sommeillais en moi\nOh Jésus tu sais, j\'étais si loin de Toi\nTu veillais sur moi n\'oublie pas mon nom\nEntends ma voix que ma chanson\nMonte vers Toi, un jour à la fois.','<p><strong>1.</strong> </p>\n<p>J&#39;ai toujours voulu trouver la lumière </p>\n<p>J&#39;étais confus mais sur cette terre </p>\n<p>Tu m&#39;as reconnu </p>\n<p>Si parfois je tombe, je n&#39;aurais pas honte </p>\n<p>Je prends la vie quand tu me l&#39;as dit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Un jour à la fois, doux Jésus,</strong> </p>\n<p><strong>C&#39;est tout ce que je peux donner</strong> </p>\n<p><strong>Tu m&#39;as donné la foi pour changer ma vie</strong> </p>\n<p><strong>Et je crois en Toi</strong> </p>\n<p><strong>Hier n&#39;est plus, doux Jésus,</strong> </p>\n<p><strong>Et demain n&#39;est pas encore là</strong> </p>\n<p><strong>Si je ne le vois pas</strong> </p>\n<p><strong>Assiste-moi, un jour à la fois.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je T&#39;ai rencontré, Tu sommeillais en moi </p>\n<p>Oh Jésus tu sais, j&#39;étais si loin de Toi </p>\n<p>Tu veillais sur moi n&#39;oublie pas mon nom </p>\n<p>Entends ma voix que ma chanson </p>\n<p>Monte vers Toi, un jour à la fois. </p>\n','J\'ai-toujours-voulu-trouver-la-lumiere',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:38'),(1975,'cc9f664a-2699-4a09-aad3-e5bb15f37742',1,0,'Je vous envoie',38,'38_EE_fr','1.\nJe vous envoie travailler sans relâche\nTenez sans peur vos labeurs de cette croix,\nSupportez tout ce monde être méprisé\nJe vous envoie travailler seul pour moi.\n\nRefrain\nComme le Père m\'a envoyé, Je vous envoie\nJe vous envoie bander pour Moi les plaies\nCherchant des armes reportant à la croix\nPortez le fardeau de ceux qui chancellent\nJe vous envoie souffrir pleurer pour Moi.\n\n2.\nJe vous envoie au coeur dur et rebelle\nLà où Satan s\'approche près de toi\nVous vous sacrifiez jusqu\'à donner la vie\nJe vous envoie monter au Calvaire pour Moi.\n\n3.\nJe vous envoie renoncer à vous-même\nAbandonnez ce  qui fait votre joie\nVous pardonnerez tous ceux qui vous haïssent\nJe vous envoie mourir pour vivre en Moi.','<p><strong>1.</strong> </p>\n<p>Je vous envoie travailler sans relâche </p>\n<p>Tenez sans peur vos labeurs de cette croix, </p>\n<p>Supportez tout ce monde être méprisé </p>\n<p>Je vous envoie travailler seul pour moi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Comme le Père m&#39;a envoyé, Je vous envoie</strong> </p>\n<p><strong>Je vous envoie bander pour Moi les plaies</strong> </p>\n<p><strong>Cherchant des armes reportant à la croix</strong> </p>\n<p><strong>Portez le fardeau de ceux qui chancellent</strong> </p>\n<p><strong>Je vous envoie souffrir pleurer pour Moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je vous envoie au coeur dur et rebelle </p>\n<p>Là où Satan s&#39;approche près de toi </p>\n<p>Vous vous sacrifiez jusqu&#39;à donner la vie </p>\n<p>Je vous envoie monter au Calvaire pour Moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je vous envoie renoncer à vous-même </p>\n<p>Abandonnez ce  qui fait votre joie </p>\n<p>Vous pardonnerez tous ceux qui vous haïssent </p>\n<p>Je vous envoie mourir pour vivre en Moi. </p>\n','Je-vous-envoie',NULL,'fr',3,'2023-07-22 22:41:15','2023-08-19 05:00:50'),(1976,'41b75c67-f061-41df-9c60-ae48a2f8c103',0,0,'Trilogie',39,'39_EE_fr','1.\nPéchè pouki ou égaré dé Bon-Dieu\nLouin di chémin an koté Jézu pasé\nAvan kò ou répozé nan gazon an\nTravèsé rout la mò-a.\n\nKè:\nOu doué janbé rout la mò-a\nVi-n maché nan lumiè sièl la\nAvan kò ou répozé nan tonbo-a\nTravèsé rout la mò-a.\n\n2.\nOu na danjé zanmi mouin\nSa ouap fè toujou\nDémin va vini, min li kapab tro ta\nSa va gnou tristès si ou pèdi pòt la\nTravèsé rout la mò-a.\n\n3.\nOu min-m zanmi ki lan zò-n nan kap kouté\nLè ou va mouri, ki lès ki pral résévoua ou\nTout sa yo ki gin tan aksépté Jézu\nTravèsé rout la mò-a.','<p><strong>1.</strong> </p>\n<p>Péchè pouki ou égaré dé Bon-Dieu </p>\n<p>Louin di chémin an koté Jézu pasé </p>\n<p>Avan kò ou répozé nan gazon an </p>\n<p>Travèsé rout la mò-a. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ou doué janbé rout la mò-a</strong> </p>\n<p><strong>Vi-n maché nan lumiè sièl la</strong> </p>\n<p><strong>Avan kò ou répozé nan tonbo-a</strong> </p>\n<p><strong>Travèsé rout la mò-a.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou na danjé zanmi mouin </p>\n<p>Sa ouap fè toujou </p>\n<p>Démin va vini, min li kapab tro ta </p>\n<p>Sa va gnou tristès si ou pèdi pòt la </p>\n<p>Travèsé rout la mò-a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou min-m zanmi ki lan zò-n nan kap kouté </p>\n<p>Lè ou va mouri, ki lès ki pral résévoua ou </p>\n<p>Tout sa yo ki gin tan aksépté Jézu </p>\n<p>Travèsé rout la mò-a. </p>\n','Trilogie',NULL,'fr',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1977,'994dfc9b-2c99-4c1a-94d8-6e35be3650fd',0,0,'Mouin sonjé gnou jou',51,'51_EE_ht','1.\nMouin sonjé gnou jou ki jan mouin té malad\nDoktè t  di konsa li pa kouè map sové\nParna-m yo tandé sa, yo otut pran kriyé\nMin Jézu té palé nan jou sa.\n\nKè:\nMouin pap kapab blié, blié jou sa\nKòman pou-m blié jou sa\nJou-m té jouin-n soulajman pou nan-m mouin\nGloua a Dieu. (bis 3 lign)\n\n2.\nLé mouin pat gin Jézu, kè mouin té toujou tris\nMouin pat kapab manjé mouin pat kapab dòmi\nLè-m vi-n jouin-n ak Jézu tout bagay té chanjé\nA la gnou bon Zanmi sé Jézu.\n\n3.\nLè mouin té nan mon lan mouin toujou tap krié\nPéché mouin té rouj é tèt mouin té chajé\nChak jou mouin lévé, problèm a goch a douat\nMouin té rémèt Jézu ti rès la\n\n4.\nJoi-a si gin moun ki poko gin Jézi\nKouri vit aksépté-L, paské lè-a rivé\nNan Jézu gin la vi, gin la joua nan Jézu\nNan Jézu gin gnou lèt ap koulé.','<p><strong>1.</strong> </p>\n<p>Mouin sonjé gnou jou ki jan mouin té malad </p>\n<p>Doktè t  di konsa li pa kouè map sové </p>\n<p>Parna-m yo tandé sa, yo otut pran kriyé </p>\n<p>Min Jézu té palé nan jou sa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin pap kapab blié, blié jou sa</strong> </p>\n<p><strong>Kòman pou-m blié jou sa</strong> </p>\n<p><strong>Jou-m té jouin-n soulajman pou nan-m mouin</strong> </p>\n<p><strong>Gloua a Dieu. (bis 3 lign)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mouin pat gin Jézu, kè mouin té toujou tris </p>\n<p>Mouin pat kapab manjé mouin pat kapab dòmi </p>\n<p>Lè-m vi-n jouin-n ak Jézu tout bagay té chanjé </p>\n<p>A la gnou bon Zanmi sé Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè mouin té nan mon lan mouin toujou tap krié </p>\n<p>Péché mouin té rouj é tèt mouin té chajé </p>\n<p>Chak jou mouin lévé, problèm a goch a douat </p>\n<p>Mouin té rémèt Jézu ti rès la </p>\n<p><br/> <strong>4.</strong></p>\n<p>Joi-a si gin moun ki poko gin Jézi </p>\n<p>Kouri vit aksépté-L, paské lè-a rivé </p>\n<p>Nan Jézu gin la vi, gin la joua nan Jézu </p>\n<p>Nan Jézu gin gnou lèt ap koulé. </p>\n','Mouin-sonje-gnou-jou',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1978,'3b50523f-83aa-464d-aec3-0b7f3837876d',0,0,'Montagn yo pral',59,'59_EE_ht','1.\nMontagn yo pral tranblé\nTout roch yo pral roulé\nÉtoula yo pral kouché, lè Jézu va vini\nMokè yo pral chaché koté pou yo kaché\nTrou yo pralé louvri, pral gin anpil krié\nLé mon-n pral boulvèsé, lè Jézu va vini\nZanmi-m ou bézouin\nJouin-n gnou koté pou kaché.\n\nKè:\nÈské ou va jouin-n gnou koté pou ou kaché\nLè tronpèt la va son-nin, koté ou va kaché\nÈské ou va jouin-n gnou koté pou ou  kaché\nZanmi-m ou bézouin\nKaché nan-m ou nan Jézu-Kri.\n\n2.\nZanmi-m pral gin krié\nZanmi-m pral gin rélé\nTout mò yo pral lévé, lè Jézu va vini\nPou vi-n jujé lé mon-n, pral gin anpil rélé\nSolèy la pral vi-n noua, la li-n pral tounin san\nLé mon-n pral boulvèsé, lè Jézu va vini\nZanmi-m ou bézouin\nKaché nan-m ou nan Jézu-Kri.','<p><strong>1.</strong> </p>\n<p>Montagn yo pral tranblé </p>\n<p>Tout roch yo pral roulé </p>\n<p>Étoula yo pral kouché, lè Jézu va vini </p>\n<p>Mokè yo pral chaché koté pou yo kaché </p>\n<p>Trou yo pralé louvri, pral gin anpil krié </p>\n<p>Lé mon-n pral boulvèsé, lè Jézu va vini </p>\n<p>Zanmi-m ou bézouin </p>\n<p>Jouin-n gnou koté pou kaché. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Èské ou va jouin-n gnou koté pou ou kaché</strong> </p>\n<p><strong>Lè tronpèt la va son-nin, koté ou va kaché</strong> </p>\n<p><strong>Èské ou va jouin-n gnou koté pou ou  kaché</strong> </p>\n<p><strong>Zanmi-m ou bézouin</strong> </p>\n<p><strong>Kaché nan-m ou nan Jézu-Kri.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Zanmi-m pral gin krié </p>\n<p>Zanmi-m pral gin rélé </p>\n<p>Tout mò yo pral lévé, lè Jézu va vini </p>\n<p>Pou vi-n jujé lé mon-n, pral gin anpil rélé </p>\n<p>Solèy la pral vi-n noua, la li-n pral tounin san </p>\n<p>Lé mon-n pral boulvèsé, lè Jézu va vini </p>\n<p>Zanmi-m ou bézouin </p>\n<p>Kaché nan-m ou nan Jézu-Kri. </p>\n','Montagn-yo-pral',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1979,'6e702e4e-dabc-4cb4-bd8e-4141c958e182',0,0,'Gin kèk foua',63,'63_EE_ht','1.\nGin kèk foua moman yo kon-n trè di pou ou\nOu kon-n di Bon-Dieu blié-ou\nSé pa vré Li sonjé ou \nSé ékspérians L-ap fè avèk ou.\n\nKè:\nGnou sèl bagay mouin konnin fò sa chanjé\nPa dékourajé, toujou rét ap priyé\nPaské bagay la pap rété konsa\nLi pap rété konsa, fòk sa chanjé.\n\n2.\nByin-émé Bon-Dieu gran moun Li fè sal vlé\nLi akonpli mèvèy Li jan-L vlé\nSi ou ta ouè ou poko délivré\nPa inkiété ou, konnin sa pral chanjé.','<p><strong>1.</strong> </p>\n<p>Gin kèk foua moman yo kon-n trè di pou ou </p>\n<p>Ou kon-n di Bon-Dieu blié-ou </p>\n<p>Sé pa vré Li sonjé ou </p>\n<p>Sé ékspérians L-ap fè avèk ou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gnou sèl bagay mouin konnin fò sa chanjé</strong> </p>\n<p><strong>Pa dékourajé, toujou rét ap priyé</strong> </p>\n<p><strong>Paské bagay la pap rété konsa</strong> </p>\n<p><strong>Li pap rété konsa, fòk sa chanjé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Byin-émé Bon-Dieu gran moun Li fè sal vlé </p>\n<p>Li akonpli mèvèy Li jan-L vlé </p>\n<p>Si ou ta ouè ou poko délivré </p>\n<p>Pa inkiété ou, konnin sa pral chanjé. </p>\n','Gin-kek-foua',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1980,'e7452142-d35b-4ab3-927b-52f4827bdf38',0,0,'Distans m-rivé',74,'74_EE_ht','1.\nDistans m-rivé santi-m pap kap tounin ankò\nMouin pap tounin, mouin pap tounin\nMin-m si loraj, danjé ménasé, frapé mouin\nAnkò mouin di, mouin pap tounin.\n\nKè:\nO non, non, non mouin pap tounin\nPaské anro ki atiré-m\nDéja avion mouin dékolé\nDépasé pouin pou-m pa tounin.\n\n2.\nLé mòn-n ak plézi-l yo rélé légliz tounin\nAnro rélé-m pou-m pa tounin\nMin-m si nuaj yo lévé ésprè pou vi-n baré-m\nGnou pa ankò map ouè Sové-a.\n\n3.\nLè gro van tanpèt yo lévé kont mouin\nLégliz kanpé sou gnou sèl mo\nM-déja pati pou gnou dikté lòt moun anro\nKoté mèt mouin ap di bravo.\n\n4.\nMin-n si anpil lòt yo tonbé nan rout chak jou\nLégliz Kris la pa chaviré\nPilòt la trè sètin énmi ap ranvèsé\nLè-a sonnin fòk nou monté.','<p><strong>1.</strong> </p>\n<p>Distans m-rivé santi-m pap kap tounin ankò </p>\n<p>Mouin pap tounin, mouin pap tounin </p>\n<p>Min-m si loraj, danjé ménasé, frapé mouin </p>\n<p>Ankò mouin di, mouin pap tounin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O non, non, non mouin pap tounin</strong> </p>\n<p><strong>Paské anro ki atiré-m</strong> </p>\n<p><strong>Déja avion mouin dékolé</strong> </p>\n<p><strong>Dépasé pouin pou-m pa tounin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lé mòn-n ak plézi-l yo rélé légliz tounin </p>\n<p>Anro rélé-m pou-m pa tounin </p>\n<p>Min-m si nuaj yo lévé ésprè pou vi-n baré-m </p>\n<p>Gnou pa ankò map ouè Sové-a. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè gro van tanpèt yo lévé kont mouin </p>\n<p>Légliz kanpé sou gnou sèl mo </p>\n<p>M-déja pati pou gnou dikté lòt moun anro </p>\n<p>Koté mèt mouin ap di bravo. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Min-n si anpil lòt yo tonbé nan rout chak jou </p>\n<p>Légliz Kris la pa chaviré </p>\n<p>Pilòt la trè sètin énmi ap ranvèsé </p>\n<p>Lè-a sonnin fòk nou monté. </p>\n','Distans-m-rive',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1981,'c2a5c727-d4a8-4744-b5a7-cb0a3f3b2707',0,0,'Mouin gin gnou fèm',78,'78_EE_ht','1.\nMouin gin gnou fè-m asurans\nKé Jézu ap véyé mouin patou koté-m alé\nAmou Li si mèvéyé, Li pap abandoné mouin\nNan mòn-n pédu sa-a.\n\nKè:\nNan gnou péyi plin dé péché\nKris voyé mouin kòm gnou anbasadè\nLi amé mouin ak Léspri Li\nGloua a Jézu-Kri mouin pap maché mouin sèl.\n\n2.\nOu kapab byin maléré, pèsékité sou la tè\nTout souit nou di gloua a Dieu\nÉprèv yo sé pou krétyin\nÉ Jézu, Li chatié pitit ké Li rinmin.\n\n3.\nMalgré tout tribulasion, malgré tout pèsékusion\nTout souit nou di gloua a Dieu\nBonè nou pa isiba\nPlas nou sé nan sièl li yé, pami lè rachté.','<p><strong>1.</strong> </p>\n<p>Mouin gin gnou fè-m asurans </p>\n<p>Ké Jézu ap véyé mouin patou koté-m alé </p>\n<p>Amou Li si mèvéyé, Li pap abandoné mouin </p>\n<p>Nan mòn-n pédu sa-a. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan gnou péyi plin dé péché</strong> </p>\n<p><strong>Kris voyé mouin kòm gnou anbasadè</strong> </p>\n<p><strong>Li amé mouin ak Léspri Li</strong> </p>\n<p><strong>Gloua a Jézu-Kri mouin pap maché mouin sèl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou kapab byin maléré, pèsékité sou la tè </p>\n<p>Tout souit nou di gloua a Dieu </p>\n<p>Éprèv yo sé pou krétyin </p>\n<p>É Jézu, Li chatié pitit ké Li rinmin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Malgré tout tribulasion, malgré tout pèsékusion </p>\n<p>Tout souit nou di gloua a Dieu </p>\n<p>Bonè nou pa isiba </p>\n<p>Plas nou sé nan sièl li yé, pami lè rachté. </p>\n','Mouin-gin-gnou-fem',NULL,'ht',3,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1982,'dc87750b-1e3f-4f74-a4b8-3c2ab164ad68',0,0,'Jésus est venu dans Sa puissance',37,'37_MJ_fr','1.\nJésus est venu dans Sa puissance\nSa paix mon âme inonde.\nDès lors à jamais ma vie est à Lui\nSa grâce surabonde\nSa grâce surabonde.\n\nChoeur:\nO que c\'est merveilleux\n(merveilleux, merveilleux)\nQue c\'est émouvant et merveilleux\n(émouvant et merveilleux)\nCe que Jésus a fait pour mon âme\n(ce qu\'il a fait pour mon âme )\nO que c\'est ineffable\n(ô que c\'est ineffable)\nO (merveilleux)\nQue c\'est merveilleux\nQue c\'est émouvant et merveilleux\nCe que Jésus a fait pour mon âme\nO que c\'est ineffable!\n\n2.\nDe gloire en gloire Jésus me conduit\nDe grâce en grâce toujours.\nA l\'horizon vois briller l\'aurore\nVers le céleste séjour\nVers le céleste séjour.\n\n3.\nSi le nom de Jésus ici-bas est\nInexprimablement doux\nO que sera-t-il de voir Sa face\nAu céleste rendez-vous?\nAu céleste rendez-vous?','<p><strong>1.</strong> </p>\n<p>Jésus est venu dans Sa puissance </p>\n<p>Sa paix mon âme inonde. </p>\n<p>Dès lors à jamais ma vie est à Lui </p>\n<p>Sa grâce surabonde </p>\n<p>Sa grâce surabonde. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>O que c&#39;est merveilleux</strong> </p>\n<p><strong>(merveilleux, merveilleux)</strong> </p>\n<p><strong>Que c&#39;est émouvant et merveilleux</strong> </p>\n<p><strong>(émouvant et merveilleux)</strong> </p>\n<p><strong>Ce que Jésus a fait pour mon âme</strong> </p>\n<p><strong>(ce qu&#39;il a fait pour mon âme )</strong> </p>\n<p><strong>O que c&#39;est ineffable</strong> </p>\n<p><strong>(ô que c&#39;est ineffable)</strong> </p>\n<p><strong>O (merveilleux)</strong> </p>\n<p><strong>Que c&#39;est merveilleux</strong> </p>\n<p><strong>Que c&#39;est émouvant et merveilleux</strong> </p>\n<p><strong>Ce que Jésus a fait pour mon âme</strong> </p>\n<p><strong>O que c&#39;est ineffable!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De gloire en gloire Jésus me conduit </p>\n<p>De grâce en grâce toujours. </p>\n<p>A l&#39;horizon vois briller l&#39;aurore </p>\n<p>Vers le céleste séjour </p>\n<p>Vers le céleste séjour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si le nom de Jésus ici-bas est </p>\n<p>Inexprimablement doux </p>\n<p>O que sera-t-il de voir Sa face </p>\n<p>Au céleste rendez-vous? </p>\n<p>Au céleste rendez-vous? </p>\n','Jesus-est-venu-dans-Sa-puissance',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1983,'15bc12d4-1b7c-44fd-b8f4-4b584a97bd43',0,0,'Seigneur, mon âme soupire',46,'46_MJ_fr','1.\nSeigneur, mon âme soupire\nAprès Tes divins parvis,\nPour T\'\'écouter et m\'instruire,\nA Tes pieds toujours assis.\n\nChoeur:\nForme-moi pour Ton service,\nA Ton école, ô Seigneur,\nQue par Ton Esprit je puisse\nTe servir avec ardeur.\n\n2.\nSeigneur, répands dans mon âme\nFlot sur flot de Ton Esprit;\nQu\'ainsi, brûlant de Ta flamme,\nEn moi tout mal soit détruit.\n\n3.\nDans Tes parcs herbeux,fertiles,\nNourris mon coeur affamé.\nQu\'assis près des eaux tranquilles,\nPar Toi, je sois transformé!\n\n4.\nSeigneur, Tu sais toute chose,\nJe T\'aime et je suis à Toi;\nDe moi tout entier dispose,\nJe suis l\'esclave du Roi!','<p><strong>1.</strong> </p>\n<p>Seigneur, mon âme soupire </p>\n<p>Après Tes divins parvis, </p>\n<p>Pour T&#39;&#39;écouter et m&#39;instruire, </p>\n<p>A Tes pieds toujours assis. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Forme-moi pour Ton service,</strong> </p>\n<p><strong>A Ton école, ô Seigneur,</strong> </p>\n<p><strong>Que par Ton Esprit je puisse</strong> </p>\n<p><strong>Te servir avec ardeur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seigneur, répands dans mon âme </p>\n<p>Flot sur flot de Ton Esprit; </p>\n<p>Qu&#39;ainsi, brûlant de Ta flamme, </p>\n<p>En moi tout mal soit détruit. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans Tes parcs herbeux,fertiles, </p>\n<p>Nourris mon coeur affamé. </p>\n<p>Qu&#39;assis près des eaux tranquilles, </p>\n<p>Par Toi, je sois transformé! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Seigneur, Tu sais toute chose, </p>\n<p>Je T&#39;aime et je suis à Toi; </p>\n<p>De moi tout entier dispose, </p>\n<p>Je suis l&#39;esclave du Roi! </p>\n','Seigneur-mon-ame-soupire',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1984,'fa47f1e9-1f26-4a51-9d3a-0ea102897f52',0,0,'Beau pays de lumière et d\'amour',68,'68_MJ_fr','1.\nBeau pays de lumière et d\'amour\nPar la foi nous voyons ton rivage;\nNos pensées s\'arrêtent chaque jour,\nAux bien-aimés déjà sur ta plage.\n\nChoeur:\nOh! Quel doux rendez-vous,\nAprès notre terrestre voyage,\nOh! Quel doux rendez-vous,\nTon céleste repos est pour nous.\n\n2.\nEt le beau cantique de l\'Agneau,\nNous le chanterons avec les anges,\nLe bonheur sera toujours nouveau,\nJamais ne cesseront les louanges.\n\n3.\nOui, nous voulons bénir le Seigneur\nLouer d\'un coeur joyeux notre Père,\nDont l\'amour nous donna pour Sauveur\nJésus-Christ qui se fit notre Frère.','<p><strong>1.</strong> </p>\n<p>Beau pays de lumière et d&#39;amour </p>\n<p>Par la foi nous voyons ton rivage; </p>\n<p>Nos pensées s&#39;arrêtent chaque jour, </p>\n<p>Aux bien-aimés déjà sur ta plage. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oh! Quel doux rendez-vous,</strong> </p>\n<p><strong>Après notre terrestre voyage,</strong> </p>\n<p><strong>Oh! Quel doux rendez-vous,</strong> </p>\n<p><strong>Ton céleste repos est pour nous.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et le beau cantique de l&#39;Agneau, </p>\n<p>Nous le chanterons avec les anges, </p>\n<p>Le bonheur sera toujours nouveau, </p>\n<p>Jamais ne cesseront les louanges. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui, nous voulons bénir le Seigneur </p>\n<p>Louer d&#39;un coeur joyeux notre Père, </p>\n<p>Dont l&#39;amour nous donna pour Sauveur </p>\n<p>Jésus-Christ qui se fit notre Frère. </p>\n','Beau-pays-de-lumiere-et-d\'amour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1985,'00914ad1-632a-49f3-924d-621b94e560c5',0,0,'Lorsque le ciel',71,'71_MJ_fr','1.\nLorsque le ciel\nRetentit de louanges\nEt qu\'ici-bas\nRégnait la sombre nuit,\nJésus naquît plus\nHumble que les anges,\nPour me sauver\nSi bas il descendit.\n\nChoeur:\nBonheur suprême,\nMon Sauveur m\'aime,\nLui qui mourut et fut ressuscité,\nEt sur la nue à Sa venue,\nJe Le verrai, Lui qui m\'a racheté.\n\n2.\nLorsqu\'à la croix\nLe Prince de la vie\nLava, blanchit mes péchés,\nMes forfaits,\nLe grand pouvoir\nDe Sa grâce infinie,\nFerma l\'abîme où\nSatan m\'entraînait.\n\n3.\nLorsqu\'un matin\nDe la grande victoire,\nIl triompha de la mort,\nDu tombeau,\nIl effaça pour toujours\nLa mémoire\nDu noir passé,\nCet écrasant fardeau!\n\n4.\nLorsqu\'Il viendra\nDans Sa magnificence,\nRavir les Siens pour\nJamais dans les cieux,\nJ\'entonnerai l\'hymne\nDe délivrance,\nEn exaltant\nSon grand nom glorieux.','<p><strong>1.</strong> </p>\n<p>Lorsque le ciel </p>\n<p>Retentit de louanges </p>\n<p>Et qu&#39;ici-bas </p>\n<p>Régnait la sombre nuit, </p>\n<p>Jésus naquît plus </p>\n<p>Humble que les anges, </p>\n<p>Pour me sauver </p>\n<p>Si bas il descendit. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Bonheur suprême,</strong> </p>\n<p><strong>Mon Sauveur m&#39;aime,</strong> </p>\n<p><strong>Lui qui mourut et fut ressuscité,</strong> </p>\n<p><strong>Et sur la nue à Sa venue,</strong> </p>\n<p><strong>Je Le verrai, Lui qui m&#39;a racheté.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lorsqu&#39;à la croix </p>\n<p>Le Prince de la vie </p>\n<p>Lava, blanchit mes péchés, </p>\n<p>Mes forfaits, </p>\n<p>Le grand pouvoir </p>\n<p>De Sa grâce infinie, </p>\n<p>Ferma l&#39;abîme où </p>\n<p>Satan m&#39;entraînait. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lorsqu&#39;un matin </p>\n<p>De la grande victoire, </p>\n<p>Il triompha de la mort, </p>\n<p>Du tombeau, </p>\n<p>Il effaça pour toujours </p>\n<p>La mémoire </p>\n<p>Du noir passé, </p>\n<p>Cet écrasant fardeau! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lorsqu&#39;Il viendra </p>\n<p>Dans Sa magnificence, </p>\n<p>Ravir les Siens pour </p>\n<p>Jamais dans les cieux, </p>\n<p>J&#39;entonnerai l&#39;hymne </p>\n<p>De délivrance, </p>\n<p>En exaltant </p>\n<p>Son grand nom glorieux. </p>\n','Lorsque-le-ciel',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1986,'91374f59-3580-4012-825e-1cc4fa790084',0,0,'Je sais que Jésus m\'aime',77,'77_MJ_fr','1.\nJe sais que Jésus m\'aime,\nDans la Bible Dieu le dit;\nCherche-Le jour après jour,\nTu verra Son grand amour.\n\nChoeur:\nOui, Jésus m\'aime, (ter)\nLa Bible me le dit.\n\n2.\nJésus viendra, Il l\'a dit,\nDans la Bible, Il l\'a promis,\nJean quatorze au verset trois,\nIl me prendra, je le crois.\n\nChoeur:\nJésus reviendra, (ter)\nLa Bible me le dit.','<p><strong>1.</strong> </p>\n<p>Je sais que Jésus m&#39;aime, </p>\n<p>Dans la Bible Dieu le dit; </p>\n<p>Cherche-Le jour après jour, </p>\n<p>Tu verra Son grand amour. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, Jésus m&#39;aime, (ter)</strong> </p>\n<p><strong>La Bible me le dit.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus viendra, Il l&#39;a dit, </p>\n<p>Dans la Bible, Il l&#39;a promis, </p>\n<p>Jean quatorze au verset trois, </p>\n<p>Il me prendra, je le crois. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Jésus reviendra, (ter)</strong> </p>\n<p><strong>La Bible me le dit.</strong> </p>\n','Je-sais-que-Jesus-m\'aime',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1987,'8fff9be7-a440-42f6-8223-0f6c1359a10f',0,0,'Quand un sombre nuage',60,'60_MJ_fr','1.\nQuand un sombre nuage\nVoile ton chemin\nPourquoi craindre l\'orage?\nJésus tient ta main!\nIl n\'est plus de tristesse,\nSi tu sais prier;\nTout est paix, allégresse,\nSi tu sais prier.\n\nChoeur:\nSi tu sais aimer,\nSi tu sais prier\nTu pourras chanter\nDans la plus sombre nuit!\nSi Dieu vit en toi,\nS\'Il soutient ta foi,\nOui le soleil se lèvera\nSi Dieu vit en toi.\n\n2.\nEs-tu seul dans la vie?\nChante malgré tout\nMarche vers ta patrie,\nMarche jusqu\'au bout;\nSi ton sentier est rude,\nDieu peut te donner\nCourage, certitude,\nSi tu sais aimer.\n\n3.\nAux frimas, à la neige,\nSuivra la moisson,\nDélivré de tout piège,\nTrace un pur sillon!\nDe ton labeur, ta peine,\nDieu fera germer\nUne moisson certaine,\nSi tu sais aimer.','<p><strong>1.</strong> </p>\n<p>Quand un sombre nuage </p>\n<p>Voile ton chemin </p>\n<p>Pourquoi craindre l&#39;orage? </p>\n<p>Jésus tient ta main! </p>\n<p>Il n&#39;est plus de tristesse, </p>\n<p>Si tu sais prier; </p>\n<p>Tout est paix, allégresse, </p>\n<p>Si tu sais prier. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Si tu sais aimer,</strong> </p>\n<p><strong>Si tu sais prier</strong> </p>\n<p><strong>Tu pourras chanter</strong> </p>\n<p><strong>Dans la plus sombre nuit!</strong> </p>\n<p><strong>Si Dieu vit en toi,</strong> </p>\n<p><strong>S&#39;Il soutient ta foi,</strong> </p>\n<p><strong>Oui le soleil se lèvera</strong> </p>\n<p><strong>Si Dieu vit en toi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Es-tu seul dans la vie? </p>\n<p>Chante malgré tout </p>\n<p>Marche vers ta patrie, </p>\n<p>Marche jusqu&#39;au bout; </p>\n<p>Si ton sentier est rude, </p>\n<p>Dieu peut te donner </p>\n<p>Courage, certitude, </p>\n<p>Si tu sais aimer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Aux frimas, à la neige, </p>\n<p>Suivra la moisson, </p>\n<p>Délivré de tout piège, </p>\n<p>Trace un pur sillon! </p>\n<p>De ton labeur, ta peine, </p>\n<p>Dieu fera germer </p>\n<p>Une moisson certaine, </p>\n<p>Si tu sais aimer. </p>\n','Quand-un-sombre-nuage',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1988,'2c72be94-b6cf-4ab6-a4a8-8971fce34632',0,0,'Tu tiendras en parfaite paix',59,'59_MJ_fr','1.\nTu tiendras en parfaite paix\nQui s\'appuie sur Toi,\nDans les ombres et l\'obscurité\nTu donnes Ta clarté;\nOh, Tu es la seule place de repos\nEt de parfaite paix\nTu tiendras en parfaite paix\nQui s\'appuie sur Toi.','<p><strong>1.</strong> </p>\n<p>Tu tiendras en parfaite paix </p>\n<p>Qui s&#39;appuie sur Toi, </p>\n<p>Dans les ombres et l&#39;obscurité </p>\n<p>Tu donnes Ta clarté; </p>\n<p>Oh, Tu es la seule place de repos </p>\n<p>Et de parfaite paix </p>\n<p>Tu tiendras en parfaite paix </p>\n<p>Qui s&#39;appuie sur Toi. </p>\n','Tu-tiendras-en-parfaite-paix',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1989,'d42b459f-c69d-4557-9339-fbf1acfd5497',0,0,'Saint-Esprit, que Ta clarté',69,'69_MJ_fr','1.\nSaint-Esprit, que Ta clarté\nVienne briller sur nos coeurs;\nEt que ses rayons vainqueurs\nChasse toute l\'obscurité.\n\n2.\nSaint-Esprit, Consolateur,\nViens calmer tous nos effrois.\nApaise, à Ta douce voix,\nLa révolte et la douleur.\n\n3.\nSaint-Esprit, Libérateur,\nDont la puissance est pour tous;\nA jamais délivre-nous\nDes liens de l\'oppresseur.','<p><strong>1.</strong> </p>\n<p>Saint-Esprit, que Ta clarté </p>\n<p>Vienne briller sur nos coeurs; </p>\n<p>Et que ses rayons vainqueurs </p>\n<p>Chasse toute l&#39;obscurité. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Saint-Esprit, Consolateur, </p>\n<p>Viens calmer tous nos effrois. </p>\n<p>Apaise, à Ta douce voix, </p>\n<p>La révolte et la douleur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Saint-Esprit, Libérateur, </p>\n<p>Dont la puissance est pour tous; </p>\n<p>A jamais délivre-nous </p>\n<p>Des liens de l&#39;oppresseur. </p>\n','Saint-Esprit-que-Ta-clarte',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1990,'ae51739d-9f42-4afa-8364-dc5c6e38ce47',0,0,'Prends ma vie, elle doit être',91,'91_MJ_fr','1.\nPrends ma vie, elle doit être\nA Toi seul, ô divin Maître;\nQue sur le flot de mes jours\nTon regard brille toujours!  (bis)\n\n2.\nQue mes mains, à Ton service,\nS\'offrent pour le sacrifice;\nQu\'à te suivre, pas à pas,\nMes pieds ne faiblissent pas!  (bis)\n\n3.\nPrends ma voix et qu\'elle chante\nTa grâce auguste et touchante;\nPar mes lèvres, que ton Nom\nParle aux pécheurs de pardon!  (bis)\n\n4.\nQue ma  volonté devienne\nLa servante de la tienne;\nFais ton trône de mon coeur,\nIl t\'appartient, bon Sauveur.  (bis)','<p><strong>1.</strong> </p>\n<p>Prends ma vie, elle doit être </p>\n<p>A Toi seul, ô divin Maître; </p>\n<p>Que sur le flot de mes jours </p>\n<p>Ton regard brille toujours!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que mes mains, à Ton service, </p>\n<p>S&#39;offrent pour le sacrifice; </p>\n<p>Qu&#39;à te suivre, pas à pas, </p>\n<p>Mes pieds ne faiblissent pas!  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prends ma voix et qu&#39;elle chante </p>\n<p>Ta grâce auguste et touchante; </p>\n<p>Par mes lèvres, que ton Nom </p>\n<p>Parle aux pécheurs de pardon!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que ma  volonté devienne </p>\n<p>La servante de la tienne; </p>\n<p>Fais ton trône de mon coeur, </p>\n<p>Il t&#39;appartient, bon Sauveur.  (bis) </p>\n','Prends-ma-vie-elle-doit-etre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1991,'b5b4ba93-2e0c-4c18-93e7-a359fb11618a',0,0,'Par delà monts et vaux',64,'64_MJ_fr','1.\nPar delà monts et vaux\nS\'élèvent les échos\nDe «Loyauté, loyauté,\nLoyauté à Christ»\nLe visage serein\nRépétons le refrain\nDe «Loyauté, loyauté,\nOui, loyauté à Christ.»\n\nChoeur:\nDebout pour la guerre\nAu grand adversaire.\nCrie le messager du Père\nAvec Jésus bientôt\nNous rentrerons là-haut\nPar loyauté, loyauté, oui\nLoyauté à Christ.\n\n2.\nDe l\'amour éternel\nEntendez-vous l\'appel\nDe loyauté, loyauté,\nLoyauté à Christ.\nChantant à l\'unisson\nPartons pour la moisson\nEn redisant «Loyauté, oui\nLoyauté à Christ».\n\n3.\nJeunesse au coeur vaillant\nRépondez à l\'instant\nEn loyauté, loyauté,\nLoyauté à Christ\nEt à travers les âges\nProclamez le message,\nDe loyauté, loyauté, oui\nLoyauté à Christ.','<p><strong>1.</strong> </p>\n<p>Par delà monts et vaux </p>\n<p>S&#39;élèvent les échos </p>\n<p>De «Loyauté, loyauté, </p>\n<p>Loyauté à Christ» </p>\n<p>Le visage serein </p>\n<p>Répétons le refrain </p>\n<p>De «Loyauté, loyauté, </p>\n<p>Oui, loyauté à Christ.» </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Debout pour la guerre</strong> </p>\n<p><strong>Au grand adversaire.</strong> </p>\n<p><strong>Crie le messager du Père</strong> </p>\n<p><strong>Avec Jésus bientôt</strong> </p>\n<p><strong>Nous rentrerons là-haut</strong> </p>\n<p><strong>Par loyauté, loyauté, oui</strong> </p>\n<p><strong>Loyauté à Christ.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De l&#39;amour éternel </p>\n<p>Entendez-vous l&#39;appel </p>\n<p>De loyauté, loyauté, </p>\n<p>Loyauté à Christ. </p>\n<p>Chantant à l&#39;unisson </p>\n<p>Partons pour la moisson </p>\n<p>En redisant «Loyauté, oui </p>\n<p>Loyauté à Christ». </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jeunesse au coeur vaillant </p>\n<p>Répondez à l&#39;instant </p>\n<p>En loyauté, loyauté, </p>\n<p>Loyauté à Christ </p>\n<p>Et à travers les âges </p>\n<p>Proclamez le message, </p>\n<p>De loyauté, loyauté, oui </p>\n<p>Loyauté à Christ. </p>\n','Par-dela-monts-et-vaux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1992,'56b2a1d3-25d3-41bb-b968-90add1fd0b4d',0,0,'Altéré dans la terre',74,'74_MJ_fr','1.\nAltéré dans la terre\nAride de péché\nA soulager mon coeur\nJ\'ai beau chercher.\nMais je suis venu un jour\nAu pied de la croix\nOù jaillit une source\nD\'eau pour moi.\n\nChoeur:\nPuisant à la source d\'eau vivante\nJ\'ai enfin la paix –\nMon coeur est satisfait\nPuisant à la source d\'eau vivante\nQuelle source intarissable à jamais!\n\n2.\nO qu\'elle est agréable\nCette eau vivante\nQui m\'arrose de joie abondante!\nJe marche dans le chemin\nDe grâce et d\'amour\nEt je chante alléluia chaque jour.\n\n3.\nO viens aujourd\'hui\nA la croix du Calvaire\nOù Jésus a vaincu l\'adversaire.\nO, toi, qui as soif\nDe cette eau de la vie\nViens, puiser à la source infinie.','<p><strong>1.</strong> </p>\n<p>Altéré dans la terre </p>\n<p>Aride de péché </p>\n<p>A soulager mon coeur </p>\n<p>J&#39;ai beau chercher. </p>\n<p>Mais je suis venu un jour </p>\n<p>Au pied de la croix </p>\n<p>Où jaillit une source </p>\n<p>D&#39;eau pour moi. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Puisant à la source d&#39;eau vivante</strong> </p>\n<p><strong>J&#39;ai enfin la paix –</strong> </p>\n<p><strong>Mon coeur est satisfait</strong> </p>\n<p><strong>Puisant à la source d&#39;eau vivante</strong> </p>\n<p><strong>Quelle source intarissable à jamais!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O qu&#39;elle est agréable </p>\n<p>Cette eau vivante </p>\n<p>Qui m&#39;arrose de joie abondante! </p>\n<p>Je marche dans le chemin </p>\n<p>De grâce et d&#39;amour </p>\n<p>Et je chante alléluia chaque jour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O viens aujourd&#39;hui </p>\n<p>A la croix du Calvaire </p>\n<p>Où Jésus a vaincu l&#39;adversaire. </p>\n<p>O, toi, qui as soif </p>\n<p>De cette eau de la vie </p>\n<p>Viens, puiser à la source infinie. </p>\n','Altere-dans-la-terre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1993,'a83d5dc4-3b4f-4a24-8855-ada94787c35a',0,0,'Allo et bonsoir',92,'92_MJ_fr','1.\nAllô et bonsoir,\nNos très chers amis,\nNous sommes très heureux\nDe vous revoir,\nSoyez bienvenus!\n\nBon, bon,\nQu\'il est bon d\'être réunis\nChaque année dans ce lieu,\nAvec nos ami!\n\nMmm…mmm…mmm…mmm  (2 lignes)\nAllô, chers amis!','<p><strong>1.</strong> </p>\n<p>Allô et bonsoir, </p>\n<p>Nos très chers amis, </p>\n<p>Nous sommes très heureux </p>\n<p>De vous revoir, </p>\n<p>Soyez bienvenus! </p>\n<p>Bon, bon, </p>\n<p>Qu&#39;il est bon d&#39;être réunis </p>\n<p>Chaque année dans ce lieu, </p>\n<p>Avec nos ami! </p>\n<p>Mmm…mmm…mmm…mmm  (2 lignes) </p>\n<p>Allô, chers amis! </p>\n','Allo-et-bonsoir',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1994,'ca948031-d4a8-4206-81d9-9f7b9306eeba',0,0,'Je n\'ai qu\'un ami suprême',111,'111_MJ_fr','1.\nJe n\'ai qu\'un ami suprême,\nC\'est Jésus, mon Rédempteur,\nC\'est Lui qui sauva mon âme,\nEt m\'apporta le bonheur.\n\nChoeur:\nOui, en Lui je me confie,\nEn Lui, je serai vainqueur;\nCar je sais que Jésus m\'aime\nD\'un amour sublime  et fort.\n\n2.\nOh, mon Jésus est tout pour moi,\nRédempteur, Ami, Berger,\nEt contre le grand tentateur\nUn abri pour me cacher.\n\n3.\nSeigneur, voici mon envie\nPour le reste de mes jours,\nC\'est de te donner ma vie,\nT\'aimer, te servir toujours.','<p><strong>1.</strong> </p>\n<p>Je n&#39;ai qu&#39;un ami suprême, </p>\n<p>C&#39;est Jésus, mon Rédempteur, </p>\n<p>C&#39;est Lui qui sauva mon âme, </p>\n<p>Et m&#39;apporta le bonheur. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oui, en Lui je me confie,</strong> </p>\n<p><strong>En Lui, je serai vainqueur;</strong> </p>\n<p><strong>Car je sais que Jésus m&#39;aime</strong> </p>\n<p><strong>D&#39;un amour sublime  et fort.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh, mon Jésus est tout pour moi, </p>\n<p>Rédempteur, Ami, Berger, </p>\n<p>Et contre le grand tentateur </p>\n<p>Un abri pour me cacher. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur, voici mon envie </p>\n<p>Pour le reste de mes jours, </p>\n<p>C&#39;est de te donner ma vie, </p>\n<p>T&#39;aimer, te servir toujours. </p>\n','Je-n\'ai-qu\'un-ami-supreme',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1995,'fad5d461-13a3-4016-8362-ffe0ea1c7cf5',0,0,'Allélu, allélu, allélu, alléluia',123,'123_MJ_fr','1.\nAllélu, allélu, allélu, alléluia!\nGloire au Seigneur!  (bis 2 lignes)\nGloire au Seigneur, alléluia!  (ter)\nGloire au Seigneur!\n\n2.\nAllélu, allélu, allélu, alléluia!\nChrist, mon Sauveur,\nAllélu, allélu, allélu, alléluia!\nPour moi, pécheur,\nMort sur la croix, alléluia!\nA Golgotha, alléluia!\nIl peut sauver, alléluia!\nParfaitement.','<p><strong>1.</strong> </p>\n<p>Allélu, allélu, allélu, alléluia! </p>\n<p>Gloire au Seigneur!  (bis 2 lignes) </p>\n<p>Gloire au Seigneur, alléluia!  (ter) </p>\n<p>Gloire au Seigneur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Allélu, allélu, allélu, alléluia! </p>\n<p>Christ, mon Sauveur, </p>\n<p>Allélu, allélu, allélu, alléluia! </p>\n<p>Pour moi, pécheur, </p>\n<p>Mort sur la croix, alléluia! </p>\n<p>A Golgotha, alléluia! </p>\n<p>Il peut sauver, alléluia! </p>\n<p>Parfaitement. </p>\n','Allelu-allelu-allelu-alleluia',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1996,'476338ad-796a-4687-a553-0e0d4939319f',0,0,'Que rien dans ta vie',131,'131_MJ_fr','1.\nQue rien dans la vie,\nRien n\'entrave plus\nL\'union qui lie ton âme à Jésus!\nMaintiens à toute heure ce lien béni;\nQue Jésus demeure\nTon meilleur Ami!','<p><strong>1.</strong> </p>\n<p>Que rien dans la vie, </p>\n<p>Rien n&#39;entrave plus </p>\n<p>L&#39;union qui lie ton âme à Jésus! </p>\n<p>Maintiens à toute heure ce lien béni; </p>\n<p>Que Jésus demeure </p>\n<p>Ton meilleur Ami! </p>\n','Que-rien-dans-ta-vie',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1997,'ac166497-8b35-4538-9544-80f8a809c55f',0,0,'Rassemblés autour du feu de camp',174,'174_MJ_fr','1.\nRassemblés autour du feu de camp\nCoeurs unis dans un céleste chant\nEnfants de Béthel loyaux\n(Campeurs fidèles et loyaux)\nNous nous donnons à nouveau au Seigneur\nQui nous à tant aimés\nQu\'Il nous garde quoique séparés\nNous souvenant de ces jours\nJusqu\'à Son retour.','<p><strong>1.</strong> </p>\n<p>Rassemblés autour du feu de camp </p>\n<p>Coeurs unis dans un céleste chant </p>\n<p>Enfants de Béthel loyaux </p>\n<p>(Campeurs fidèles et loyaux) </p>\n<p>Nous nous donnons à nouveau au Seigneur </p>\n<p>Qui nous à tant aimés </p>\n<p>Qu&#39;Il nous garde quoique séparés </p>\n<p>Nous souvenant de ces jours </p>\n<p>Jusqu&#39;à Son retour. </p>\n','Rassembles-autour-du-feu-de-camp',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1998,'d005c7d0-09d4-46c8-8ac2-2ed8fabf46a6',0,0,'Je cherchais dans l\'ombre',182,'182_MJ_fr','1.\nJe cherchais dans l\'ombre,\nJe cherchais nuit et jour,\nEt Dieu daigna m\'entendre!\nIl vint à mon secours!\n\nChoeur:\nClamez sur la montagne,\nSur la colline et tout partout,\nAllez sur la montagne\nDire à tous: «Jésus est né!»\n\n2.\nIl m\'a mis de garde\nAux murs de la cité,\nJe suis chrétien, Dieu merci!\nMais je suis le dernier!','<p><strong>1.</strong> </p>\n<p>Je cherchais dans l&#39;ombre, </p>\n<p>Je cherchais nuit et jour, </p>\n<p>Et Dieu daigna m&#39;entendre! </p>\n<p>Il vint à mon secours! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Clamez sur la montagne,</strong> </p>\n<p><strong>Sur la colline et tout partout,</strong> </p>\n<p><strong>Allez sur la montagne</strong> </p>\n<p><strong>Dire à tous: «Jésus est né!»</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il m&#39;a mis de garde </p>\n<p>Aux murs de la cité, </p>\n<p>Je suis chrétien, Dieu merci! </p>\n<p>Mais je suis le dernier! </p>\n','Je-cherchais-dans-l\'ombre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(1999,'03eb4c9e-08f3-4e7c-b9b5-efc7fe8df1de',0,0,'Mouin Vlé Sanblé Ou',21,'21_HC_ht','1.\nMouin vlé sanblé Ou Rédanmtè béni\nSé sa ki dézi ak la priè-m,\nTout trézò tè a pa intérésan\nSi sèlman mouin kab sanblé Ségnè-m.\n\nKè:\nMouin vlé sanblé Ou,\nMouin vlé sanblé Ou,\nRédanmtè béni, konplètman sin,\nVi-n nan dousè Ou, répon-n priè nou\nInprimé pòtré Ou nan kè mouin.\n\n2.\nMouin vlésanblé Ou plin ak konpasion\nByinémab, padonnan, plin tandrès,\nSékouri maléré avèk fèb yo,\nAp édé mizérab nan détrès.\n\n3.\nMouin vlé sanblé Ou nan lumilité\nSin, inosan, san tach, san blamé\nDousman supòtan réproch méchan yo\nPrèt pou soufri, léz òt pou sové.\n\n4.\nMouin vlé sanblé Ou, min mouin chè Ségnè\nRanpli kè mouin ak puisans anro\nTou sa-m posédé mouin pòté ba ou,\nMouin vlé transfòmé, né dé nouvo.','<p><strong>1.</strong> </p>\n<p>Mouin vlé sanblé Ou Rédanmtè béni </p>\n<p>Sé sa ki dézi ak la priè-m, </p>\n<p>Tout trézò tè a pa intérésan </p>\n<p>Si sèlman mouin kab sanblé Ségnè-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin vlé sanblé Ou,</strong> </p>\n<p><strong>Mouin vlé sanblé Ou,</strong> </p>\n<p><strong>Rédanmtè béni, konplètman sin,</strong> </p>\n<p><strong>Vi-n nan dousè Ou, répon-n priè nou</strong> </p>\n<p><strong>Inprimé pòtré Ou nan kè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin vlésanblé Ou plin ak konpasion </p>\n<p>Byinémab, padonnan, plin tandrès, </p>\n<p>Sékouri maléré avèk fèb yo, </p>\n<p>Ap édé mizérab nan détrès. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vlé sanblé Ou nan lumilité </p>\n<p>Sin, inosan, san tach, san blamé </p>\n<p>Dousman supòtan réproch méchan yo </p>\n<p>Prèt pou soufri, léz òt pou sové. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mouin vlé sanblé Ou, min mouin chè Ségnè </p>\n<p>Ranpli kè mouin ak puisans anro </p>\n<p>Tou sa-m posédé mouin pòté ba ou, </p>\n<p>Mouin vlé transfòmé, né dé nouvo. </p>\n','Mouin-Vle-Sanble-Ou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2000,'7b90a67d-1585-49cc-9a3f-102d1c3c9ba6',0,0,'Nan Pi Fon Kè Mouin',53,'53_HC_ht','1.\nLè mouin ouvri pòt kè mouin ba Jézu\nLi ba mouin gnou joua mèvéyé,\nSé gnou joua ki va duré tout tan\nSé sou Jézu li konsantré.\n\nKè:\nNan pi fon kè mouin\nLamou Jézu apé chanté,\nNan pi fon kè mouin\nLa pé ak la joua ap pousé\nNan pi fon kè mouin\nLéspérans apé soulajé,\nJoua, pè, amou, éspérans,\nNan pi fon kè mouin.\n\n2.\nLi ba mouin gnou kalité la pè\nKi va supòté tout détrès,\nAkòz mizèrikòd infini\nM-ap joui gnou amou ki sélèst.\n\n3.\nLi ba mouin gnou pafèt éspérans,\nKé gnou jou figu Li ma ouè,\nM-ap fè rout mouin plin ak réjouisans\nAk la joua, léspérans, la pè.','<p><strong>1.</strong> </p>\n<p>Lè mouin ouvri pòt kè mouin ba Jézu </p>\n<p>Li ba mouin gnou joua mèvéyé, </p>\n<p>Sé gnou joua ki va duré tout tan </p>\n<p>Sé sou Jézu li konsantré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan pi fon kè mouin</strong> </p>\n<p><strong>Lamou Jézu apé chanté,</strong> </p>\n<p><strong>Nan pi fon kè mouin</strong> </p>\n<p><strong>La pé ak la joua ap pousé</strong> </p>\n<p><strong>Nan pi fon kè mouin</strong> </p>\n<p><strong>Léspérans apé soulajé,</strong> </p>\n<p><strong>Joua, pè, amou, éspérans,</strong> </p>\n<p><strong>Nan pi fon kè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li ba mouin gnou kalité la pè </p>\n<p>Ki va supòté tout détrès, </p>\n<p>Akòz mizèrikòd infini </p>\n<p>M-ap joui gnou amou ki sélèst. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li ba mouin gnou pafèt éspérans, </p>\n<p>Ké gnou jou figu Li ma ouè, </p>\n<p>M-ap fè rout mouin plin ak réjouisans </p>\n<p>Ak la joua, léspérans, la pè. </p>\n','Nan-Pi-Fon-Ke-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2001,'3e356236-b084-4f88-8855-76468087ca32',0,0,'Jézu Va Tounin Ankò',77,'77_HC_ht','1.\nTandé mésaj mèvéyé,\nChanté kantik an tou lié,\nKouté paròl Bon-Dieu byin,\nJézu va tounin ankò.\n\nKè:\nTounin ankò, tounin ankò,\nJézu va tounin gnou jou,\nLi va vini pou chèché nou!\nTounin ankò, tounin ankò,\nA la gnou mèvéyé jou sa va yé,\nLè Jézu tounin ankò.\n\n\n\n2.\nMin-m pié boua ak flè chanté,\nMòn yo ak plin-n répété,\nTout sièl ak tè a rélé,\nJézu va tounin ankò.\n\n3.\nNou va kanpé dévan Li,\nTout troub ak problèm fini,\nNa pòté annè bay Li,\nJézu va tounin ankò.','<p><strong>1.</strong> </p>\n<p>Tandé mésaj mèvéyé, </p>\n<p>Chanté kantik an tou lié, </p>\n<p>Kouté paròl Bon-Dieu byin, </p>\n<p>Jézu va tounin ankò. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Tounin ankò, tounin ankò,</strong> </p>\n<p><strong>Jézu va tounin gnou jou,</strong> </p>\n<p><strong>Li va vini pou chèché nou!</strong> </p>\n<p><strong>Tounin ankò, tounin ankò,</strong> </p>\n<p><strong>A la gnou mèvéyé jou sa va yé,</strong> </p>\n<p><strong>Lè Jézu tounin ankò.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m pié boua ak flè chanté, </p>\n<p>Mòn yo ak plin-n répété, </p>\n<p>Tout sièl ak tè a rélé, </p>\n<p>Jézu va tounin ankò. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nou va kanpé dévan Li, </p>\n<p>Tout troub ak problèm fini, </p>\n<p>Na pòté annè bay Li, </p>\n<p>Jézu va tounin ankò. </p>\n','Jezu-Va-Tounin-Anko',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2002,'a4554a1b-15dd-462c-91b8-c4e49acbb231',0,0,'Jézu Sé Sèl Sovè',80,'80_HC_ht','1.\nMouin té vi-n jouin-n Jézu byin fatigé,\nJézu ban mouin répo,  (bis)\nA lè kilé kè mouin vini byin gé\nJézu ban mouin répo.\n\nKè:\nJézu sé sèl Sovè-m, Li padonnin péché-m\nLi mété gnou chan nan kè mouin,\nMouin kontan, Jézu sé sèl Sovè-m,\nJézu sé sèl Sovè-m.\n\n2.\nFado péché-m té tro lou pou-m pòté,\nJézu soulajé mouin,  (bis)\nMouin ba Li tout fado-m a lè kilé,\nJézu soulajé mouin.\n\n3.\nJézu ouété tout kondanasion mouin,\nLi padonnin péché-m, (bis)\nÉ koul yé a sé la pè nan kè mouin,\nLi padonnin péché-m.','<p><strong>1.</strong> </p>\n<p>Mouin té vi-n jouin-n Jézu byin fatigé, </p>\n<p>Jézu ban mouin répo,  (bis) </p>\n<p>A lè kilé kè mouin vini byin gé </p>\n<p>Jézu ban mouin répo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu sé sèl Sovè-m, Li padonnin péché-m</strong> </p>\n<p><strong>Li mété gnou chan nan kè mouin,</strong> </p>\n<p><strong>Mouin kontan, Jézu sé sèl Sovè-m,</strong> </p>\n<p><strong>Jézu sé sèl Sovè-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fado péché-m té tro lou pou-m pòté, </p>\n<p>Jézu soulajé mouin,  (bis) </p>\n<p>Mouin ba Li tout fado-m a lè kilé, </p>\n<p>Jézu soulajé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu ouété tout kondanasion mouin, </p>\n<p>Li padonnin péché-m, (bis) </p>\n<p>É koul yé a sé la pè nan kè mouin, </p>\n<p>Li padonnin péché-m. </p>\n','Jezu-Se-Sel-Sove',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2003,'db1e8895-b8ae-4eb6-980c-99a4fe343e27',0,0,'Tout pourvoir m\'a été donné',80,'80_MJ_fr','1.\nTout pouvoir m\'a été donné (bis)\nAllez partout prêcher la bonne nouvelle.\nEt je suis avec vous toujours.','<p><strong>1.</strong> </p>\n<p>Tout pouvoir m&#39;a été donné (bis) </p>\n<p>Allez partout prêcher la bonne nouvelle. </p>\n<p>Et je suis avec vous toujours. </p>\n','Tout-pourvoir-m\'a-ete-donne',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2004,'99abcf4a-865b-4356-ba1b-72ece5b54de5',0,0,'J’ai un cantique dans mon cœur',81,'81_MJ_fr','1.\nJ\'ai un cantique dans mon coeur,\nEt j\'aime à le chanter;\nC\'est le secret de mon bonheur,\nDès que je suis sauvé.\n\nChoeur:\nDès que je suis sauvé,  (bis)\nEn Lui je me glorifie;\nDès que je suis sauvé\nEn mon Sauveur je me glorifie.\n\n2.\nJ\'ai une source puissante,\nOn ne peut l\'épuiser;\nSource de grâce abondante,\nDès que je suis sauvé.\n\n3.\nJ\'ai une place dans les cieux,\nJésus me l\'a donnée,\nOù je vivrai toujours heureux,\nDès que je suis sauvé.','<p><strong>1.</strong> </p>\n<p>J&#39;ai un cantique dans mon coeur, </p>\n<p>Et j&#39;aime à le chanter; </p>\n<p>C&#39;est le secret de mon bonheur, </p>\n<p>Dès que je suis sauvé. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Dès que je suis sauvé,  (bis)</strong> </p>\n<p><strong>En Lui je me glorifie;</strong> </p>\n<p><strong>Dès que je suis sauvé</strong> </p>\n<p><strong>En mon Sauveur je me glorifie.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai une source puissante, </p>\n<p>On ne peut l&#39;épuiser; </p>\n<p>Source de grâce abondante, </p>\n<p>Dès que je suis sauvé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;ai une place dans les cieux, </p>\n<p>Jésus me l&#39;a donnée, </p>\n<p>Où je vivrai toujours heureux, </p>\n<p>Dès que je suis sauvé. </p>\n','J\'ai-un-cantique-dans-mon-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2005,'b6c6d2b8-501c-4727-a206-00702a841efc',0,0,'Jeunesse ardente et généreuse',85,'85_MJ_fr','1.\nJeunesse ardente et généreuse\nQue rien n\'effraie et rien n\'abat,\nComme une élite valeureuse\nLève-toi pour le bon combat! (Fine)\nEspoir vivant de la Patrie,\nEn tes vertus son coeur a foi;\nPar trop de coups, hélas, meurtrie,\nJeunesse, elle a besoin de toi.\n\n2.\nPour être forte, reste pur,\nEt pour croire au bien, croire en Dieu.\nSur ton front vierge de souillure,\nReçois le baptême de feu.\nDes honteux plaisirs de l\'ivresse\nFuis l\'esclavage avec effroi:\nPour sauver Haïti en détresse,\nJeunesse, on a besoin de toi.\n\n3.\nLa tâche est grande et glorieuse,\nLe labeur presque surhumain;\nLa lutte est sainte périlleuse,\nMais la victoire est dans ta main.\nCombats sans peur, combats sans trêve,\nSous le drapeau du divin Roi:\nNous croyons à notre beau rêve,\nJeunesse, en regardant à toi.','<p><strong>1.</strong> </p>\n<p>Jeunesse ardente et généreuse </p>\n<p>Que rien n&#39;effraie et rien n&#39;abat, </p>\n<p>Comme une élite valeureuse </p>\n<p>Lève-toi pour le bon combat! (Fine) </p>\n<p>Espoir vivant de la Patrie, </p>\n<p>En tes vertus son coeur a foi; </p>\n<p>Par trop de coups, hélas, meurtrie, </p>\n<p>Jeunesse, elle a besoin de toi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour être forte, reste pur, </p>\n<p>Et pour croire au bien, croire en Dieu. </p>\n<p>Sur ton front vierge de souillure, </p>\n<p>Reçois le baptême de feu. </p>\n<p>Des honteux plaisirs de l&#39;ivresse </p>\n<p>Fuis l&#39;esclavage avec effroi: </p>\n<p>Pour sauver Haïti en détresse, </p>\n<p>Jeunesse, on a besoin de toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La tâche est grande et glorieuse, </p>\n<p>Le labeur presque surhumain; </p>\n<p>La lutte est sainte périlleuse, </p>\n<p>Mais la victoire est dans ta main. </p>\n<p>Combats sans peur, combats sans trêve, </p>\n<p>Sous le drapeau du divin Roi: </p>\n<p>Nous croyons à notre beau rêve, </p>\n<p>Jeunesse, en regardant à toi. </p>\n','Jeunesse-ardente-et-genereuse',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2006,'81704288-a562-4f8e-81ce-c6473d41b16b',0,0,'La fontaine de l\'eau Vive',108,'108_MJ_fr','1.\nLa fontaine de l\'Eau Vive\nJaillit pour nous,\nJaillit pour nous,\nLa fontaine de l\'Eau Vive\nJaillit pour nous toujours.\n\n2.\nLe Pain Vivant, venu du ciel,\nNous rassasie, nous rassasie,\nLe Pain Vivant, venu du ciel,\nNous rassasie toujours.\n\n3.\nLe Bon Berger nous est donné\nPour nous sauver,\nPour nous sauver,\nLe Bon Berger nous est donné\nPour nous sauver toujours.\n\n4.\nO Saint-Esprit du Dieu vivant,\nDescends sur nous,\nDescends sur nous,\nO Saint-Esprit du Dieu vivant,\nDescends sur nous.','<p><strong>1.</strong> </p>\n<p>La fontaine de l&#39;Eau Vive </p>\n<p>Jaillit pour nous, </p>\n<p>Jaillit pour nous, </p>\n<p>La fontaine de l&#39;Eau Vive </p>\n<p>Jaillit pour nous toujours. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le Pain Vivant, venu du ciel, </p>\n<p>Nous rassasie, nous rassasie, </p>\n<p>Le Pain Vivant, venu du ciel, </p>\n<p>Nous rassasie toujours. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Bon Berger nous est donné </p>\n<p>Pour nous sauver, </p>\n<p>Pour nous sauver, </p>\n<p>Le Bon Berger nous est donné </p>\n<p>Pour nous sauver toujours. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Saint-Esprit du Dieu vivant, </p>\n<p>Descends sur nous, </p>\n<p>Descends sur nous, </p>\n<p>O Saint-Esprit du Dieu vivant, </p>\n<p>Descends sur nous. </p>\n','La-fontaine-de-l\'eau-Vive',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2007,'fae14529-795b-436f-906d-69f757396251',0,0,'Lève-toi, pars chercher les âmes',119,'119_MJ_fr','1.\nLève-toi, pars chercher les âmes,\nM\'a dit mon Maître, Jésus-Christ,\nGagne-les-moi, je les réclame\nPour mes joyaux du plus grand prix.\n\n2.\nEt maintenant l\'amour me presse\nEt me fait brûler du désir\nD\'aller aux pécheurs en détresse,\nPour sauver ceux qui vont périr.\n\n3.\nLa flamme ardente qui m\'anime\nMe consumera tout entier;\nEn servant la cause sublime,\nJe perdrai tout pour tout gagner.\n\n4.\nViens avec moi dans la carrière,\nToi, qui mourus pour le pécheur,\nEt ta croix me sera légère,\nSi ton amour remplit mon coeur.','<p><strong>1.</strong> </p>\n<p>Lève-toi, pars chercher les âmes, </p>\n<p>M&#39;a dit mon Maître, Jésus-Christ, </p>\n<p>Gagne-les-moi, je les réclame </p>\n<p>Pour mes joyaux du plus grand prix. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et maintenant l&#39;amour me presse </p>\n<p>Et me fait brûler du désir </p>\n<p>D&#39;aller aux pécheurs en détresse, </p>\n<p>Pour sauver ceux qui vont périr. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La flamme ardente qui m&#39;anime </p>\n<p>Me consumera tout entier; </p>\n<p>En servant la cause sublime, </p>\n<p>Je perdrai tout pour tout gagner. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Viens avec moi dans la carrière, </p>\n<p>Toi, qui mourus pour le pécheur, </p>\n<p>Et ta croix me sera légère, </p>\n<p>Si ton amour remplit mon coeur. </p>\n','Leve-toi-pars-chercher-les-ames',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2008,'f8096ebe-1087-4c0f-a5ae-c3fad011c225',0,0,'Jésus est le plus beau nom connu',142,'142_MJ_fr','1.\nJésus est le plus beau nom connu,\nIl est mon Sauveur,\nEt si doux de coeur,\nLe péché par Sa mort est vaincu,\nOui, Jésus est le plus beau nom connu.','<p><strong>1.</strong> </p>\n<p>Jésus est le plus beau nom connu, </p>\n<p>Il est mon Sauveur, </p>\n<p>Et si doux de coeur, </p>\n<p>Le péché par Sa mort est vaincu, </p>\n<p>Oui, Jésus est le plus beau nom connu. </p>\n','Jesus-est-le-plus-beau-nom-connu',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2009,'9c77d4f6-3716-4286-bd76-646919c78547',0,0,'Oui, j\'aime, j\'aime',150,'150_MJ_fr','1.\nOui, j\'aime, j\'aime\nL\'Homme de Galilée  (bis 2 lignes)\nMon fardeau enlevé,\nMes péchés effacés\nOui j\'aime, j\'aime\nL\'Homme de Galilée.','<p><strong>1.</strong> </p>\n<p>Oui, j&#39;aime, j&#39;aime </p>\n<p>L&#39;Homme de Galilée  (bis 2 lignes) </p>\n<p>Mon fardeau enlevé, </p>\n<p>Mes péchés effacés </p>\n<p>Oui j&#39;aime, j&#39;aime </p>\n<p>L&#39;Homme de Galilée. </p>\n','Oui-j\'aime-j\'aime',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2010,'e5fbcc7d-4064-442c-a544-73c4c1b42adb',0,0,'Soleil céleste, Soleil céleste',153,'153_MJ_fr','1.\nSoleil céleste, Soleil céleste\nRemplis mon âme de ton amour;\nSoleil céleste, Soleil céleste,\nAlléluia, Jésus en moi!','<p><strong>1.</strong> </p>\n<p>Soleil céleste, Soleil céleste </p>\n<p>Remplis mon âme de ton amour; </p>\n<p>Soleil céleste, Soleil céleste, </p>\n<p>Alléluia, Jésus en moi! </p>\n','Soleil-celeste-Soleil-celeste',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2011,'6c33b00e-5dff-46ae-bd37-1dde36fa6c63',0,0,'Béni soit le nom!',155,'155_MJ_fr','1.\nBéni soit le nom! Béni soit le nom!\nBéni soit le nom du Seigneur!\nJésus est le même, oui toujours le même.\nBéni soit le nom du Seigneur!','<p><strong>1.</strong> </p>\n<p>Béni soit le nom! Béni soit le nom! </p>\n<p>Béni soit le nom du Seigneur! </p>\n<p>Jésus est le même, oui toujours le même. </p>\n<p>Béni soit le nom du Seigneur! </p>\n','Beni-soit-le-nom!',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2012,'f3b130d1-ef5e-4216-97e5-393d77649dfe',0,0,'Seigneur, garde-moi toujours confiant',169,'169_MJ_fr','1.\nSeigneur garde-moi toujours confiant\nO que chaque jour je sois triomphant\nSi tu m\'envoies le jour ou la nuit\nJe sais bien que ta grâce me suffit.\n\nChoeur:\nPlus confiant, plus confiant\nQue chaque jour je sois plus confiant\nDans les orages bien terrifiants\nSeigneur garde-moi toujours plus confiant.\n\n2.\nQue je ne craigne pas le lendemain\nAssuré que ton secours est certain.\nCe que tu m\'envoies de jour en jour\nEst toujours scellé de ton grand amour.\n\n3.\nSeigneur garde-moi confiant en Toi;\nQuand la vue remplacera ma foi\nJe te chanterai le coeur débordant;\nSeigneur garde-moi toujours confiant.','<p><strong>1.</strong> </p>\n<p>Seigneur garde-moi toujours confiant </p>\n<p>O que chaque jour je sois triomphant </p>\n<p>Si tu m&#39;envoies le jour ou la nuit </p>\n<p>Je sais bien que ta grâce me suffit. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Plus confiant, plus confiant</strong> </p>\n<p><strong>Que chaque jour je sois plus confiant</strong> </p>\n<p><strong>Dans les orages bien terrifiants</strong> </p>\n<p><strong>Seigneur garde-moi toujours plus confiant.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que je ne craigne pas le lendemain </p>\n<p>Assuré que ton secours est certain. </p>\n<p>Ce que tu m&#39;envoies de jour en jour </p>\n<p>Est toujours scellé de ton grand amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur garde-moi confiant en Toi; </p>\n<p>Quand la vue remplacera ma foi </p>\n<p>Je te chanterai le coeur débordant; </p>\n<p>Seigneur garde-moi toujours confiant. </p>\n','Seigneur-garde-moi-toujours-confiant',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2013,'09ddb344-0fd2-4d3c-bf07-1a87b11376b9',0,0,'Fas A Fas',57,'57_HC_ht','1.\nFas a fas lè m-a ouè Jézu,\nM-a kontanplé bonté Li,\nPou toujou m-a va ouè Jézu,\nPou toujou viv avèk Li.\n\nKè:\nOui, nan tout magnifisans Li,\nM-a va ouè Jézu gnou jou,\nLè tout bon m-a nan prézans Li,\nM-a loué Li pou toujou.\n\n2.\nGloua Jézu pako révélé\nGin gnou voual ki  kouvri Li,\nMin kan voual la va déchiré,\nN-a va ouè tout bonté Li.\n\n3.\nOh!  Kè mouin va plin alégrès,\nKan gnou jou m-a va ouè Li,\nÉ kan la di mouin ak tandrès,\n«Pou ou tou mouin té mouri.»','<p><strong>1.</strong> </p>\n<p>Fas a fas lè m-a ouè Jézu, </p>\n<p>M-a kontanplé bonté Li, </p>\n<p>Pou toujou m-a va ouè Jézu, </p>\n<p>Pou toujou viv avèk Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui, nan tout magnifisans Li,</strong> </p>\n<p><strong>M-a va ouè Jézu gnou jou,</strong> </p>\n<p><strong>Lè tout bon m-a nan prézans Li,</strong> </p>\n<p><strong>M-a loué Li pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gloua Jézu pako révélé </p>\n<p>Gin gnou voual ki  kouvri Li, </p>\n<p>Min kan voual la va déchiré, </p>\n<p>N-a va ouè tout bonté Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh!  Kè mouin va plin alégrès, </p>\n<p>Kan gnou jou m-a va ouè Li, </p>\n<p>É kan la di mouin ak tandrès, </p>\n<p>«Pou ou tou mouin té mouri.» </p>\n','Fas-A-Fas',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2014,'181a1b14-db24-4fa7-8247-450af8a1e9e8',0,0,'Ak Léspri Ou Ranpli-m',62,'62_HC_ht','1.\nKris, posédé mouin tanpri\nSé pou ou mouin yé jodi-a,\nÉdé-m apran-n obéyi,\nAk Léspri Ou ranpli-m.\n\nKè:\nAk Léspri Ou ranpli-m,  (bis)\nMouin vlé pou antièman,\nAk Léspri Ou ranpli-m.\n\n2.\nOh Ségnè mouin ran-n ba ou\nTout la vi mouin pou toujou\nPlin kè mouin avèk amou\nAk Léspri Ou ranpli-m.\n\n3.\nPréparé-m, oh Jézu-Kri,\nSé Ou sèl mouin vlé sèvi,\nÉ chèché lèz a-m pèdu,\nAk Léspri Ou ranpli-m.','<p><strong>1.</strong> </p>\n<p>Kris, posédé mouin tanpri </p>\n<p>Sé pou ou mouin yé jodi-a, </p>\n<p>Édé-m apran-n obéyi, </p>\n<p>Ak Léspri Ou ranpli-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ak Léspri Ou ranpli-m,  (bis)</strong> </p>\n<p><strong>Mouin vlé pou antièman,</strong> </p>\n<p><strong>Ak Léspri Ou ranpli-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oh Ségnè mouin ran-n ba ou </p>\n<p>Tout la vi mouin pou toujou </p>\n<p>Plin kè mouin avèk amou </p>\n<p>Ak Léspri Ou ranpli-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Préparé-m, oh Jézu-Kri, </p>\n<p>Sé Ou sèl mouin vlé sèvi, </p>\n<p>É chèché lèz a-m pèdu, </p>\n<p>Ak Léspri Ou ranpli-m. </p>\n','Ak-Lespri-Ou-Ranpli-m',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2015,'ff354670-a3d3-45a5-aade-e4e83dd19884',0,0,'M-konfié Nan Dieu',79,'79_HC_ht','1.\nM-konfié nan Dieu ninpòt koté mouin yé,\nNi sou la tè, ni sou la mè troublé,\nNan tout traka, nan la vi sa a\nPapa-m nan sièl la apé véyé mouin.\n\nKè:\nKonfié nan Dieu,\nM-konnin L-ap pran souin mouin,\nNan dézè a ou nan mové chémin,\nLa mè pousé-m, Bon-Dieu kinbé-m\nPapa-m nan sièl la apé véyé mouin.\n\n2.\nLi gadé flè yo nan chan tou patou,\nLi pran souin zouazo kap chanté pou nou\nJuska la fin, L-a sonjé mouin,\nPapa-m nan sièl la apé véyé mouin.\n\n3.\nRout la kap du, nuaj yo kouvri mouin,\nBèjé a gadé mouton-L jouk la fin,\nApré mon-n sa, na ouè sièl la,\nPapa-m nan sièl la apé véyé mouin.','<p><strong>1.</strong> </p>\n<p>M-konfié nan Dieu ninpòt koté mouin yé, </p>\n<p>Ni sou la tè, ni sou la mè troublé, </p>\n<p>Nan tout traka, nan la vi sa a </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konfié nan Dieu,</strong> </p>\n<p><strong>M-konnin L-ap pran souin mouin,</strong> </p>\n<p><strong>Nan dézè a ou nan mové chémin,</strong> </p>\n<p><strong>La mè pousé-m, Bon-Dieu kinbé-m</strong> </p>\n<p><strong>Papa-m nan sièl la apé véyé mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li gadé flè yo nan chan tou patou, </p>\n<p>Li pran souin zouazo kap chanté pou nou </p>\n<p>Juska la fin, L-a sonjé mouin, </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rout la kap du, nuaj yo kouvri mouin, </p>\n<p>Bèjé a gadé mouton-L jouk la fin, </p>\n<p>Apré mon-n sa, na ouè sièl la, </p>\n<p>Papa-m nan sièl la apé véyé mouin. </p>\n','M-konfie-Nan-Dieu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2016,'52f562b7-b207-49fe-8800-870ec23f42ea',0,0,'M-Pa Mérité Li',84,'84_HC_ht','1.\nM-pa mérité pi piti nan favè Li\nMin Kris kité sièl la pou mouin\nParòl la tounin chè,\nNan plas mouin Li mouri\nSou boua Kalvè abandonnin.\n\nKè:\nM-pa mérité li! Lang sa déklaré li\nM-pa mérité li! Kè mouin konstaté li\nKris troké plas ak mouin pou ba-m la vi!\nSou boua Kalvè abandonnin.\n\n2.\nM-pa mérité pi piti nan favè Li\nMin nan Jézu-Kri mouin kanpé.\nLi té akòdé mouin tout richès infini\nAk Kris mouin gnou ko-éritié.\n\n3.\nM-pa mérité pi piti nan favè Li\nMin L-alé préparé gnou plas,\nKoté na joui prézans gran Rédanmtè béni,\nPou tout tan na ouè-L fas a fas.','<p><strong>1.</strong> </p>\n<p>M-pa mérité pi piti nan favè Li </p>\n<p>Min Kris kité sièl la pou mouin </p>\n<p>Paròl la tounin chè, </p>\n<p>Nan plas mouin Li mouri </p>\n<p>Sou boua Kalvè abandonnin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-pa mérité li! Lang sa déklaré li</strong> </p>\n<p><strong>M-pa mérité li! Kè mouin konstaté li</strong> </p>\n<p><strong>Kris troké plas ak mouin pou ba-m la vi!</strong> </p>\n<p><strong>Sou boua Kalvè abandonnin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-pa mérité pi piti nan favè Li </p>\n<p>Min nan Jézu-Kri mouin kanpé. </p>\n<p>Li té akòdé mouin tout richès infini </p>\n<p>Ak Kris mouin gnou ko-éritié. </p>\n<p><br/> <strong>3.</strong></p>\n<p>M-pa mérité pi piti nan favè Li </p>\n<p>Min L-alé préparé gnou plas, </p>\n<p>Koté na joui prézans gran Rédanmtè béni, </p>\n<p>Pou tout tan na ouè-L fas a fas. </p>\n','M-Pa-Merite-Li',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2017,'294f2c8e-b1f7-4ad0-ad14-68c9e5a58f5c',0,0,'Croire, obéir',97,'97_MJ_fr','1.\nCroire, obéir,\nSi nous voulons jouir\nDu vrai bonheur en Jésus,\nIl faut croire, obéir.','<p><strong>1.</strong> </p>\n<p>Croire, obéir, </p>\n<p>Si nous voulons jouir </p>\n<p>Du vrai bonheur en Jésus, </p>\n<p>Il faut croire, obéir. </p>\n','Croire-obeir',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2018,'d9718728-926d-4a67-9077-bcf015a0cfaf',0,0,'Prêt a souffrir tristesse et douleur',135,'135_MJ_fr','1.\nPrêt à souffrir tristesse et douleur,\nPrêt à tout supporter,\nPrêt à voir aller les volontaires\nQuand il me faut rester.\n\nChoeur:\nPrêt à aller, prêt à rester,\nPrêt à être employé,\nPrêt pour le service,\nQuel qu\'il en soit,\nPrêt pour sa volonté.\n\n2.\nprêt à aller, prêt à subir,\nprêt à veiller, prier,\nprêt à donner et sacrifier,\nprêt à être guidé.\n\n3.\nPrêt à parler, prêt à enseigner,\nPrêt avec tout mon coeur,\nPrêt à me tenir en messager,\nPrêt à plaire au Sauveur.\n\n4.\nPrêt à parler, prêt à relever,\nPrêt à porter secours,\nPrêt dans la vie,\nPrêt à son prompt retour.','<p><strong>1.</strong> </p>\n<p>Prêt à souffrir tristesse et douleur, </p>\n<p>Prêt à tout supporter, </p>\n<p>Prêt à voir aller les volontaires </p>\n<p>Quand il me faut rester. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Prêt à aller, prêt à rester,</strong> </p>\n<p><strong>Prêt à être employé,</strong> </p>\n<p><strong>Prêt pour le service,</strong> </p>\n<p><strong>Quel qu&#39;il en soit,</strong> </p>\n<p><strong>Prêt pour sa volonté.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>prêt à aller, prêt à subir, </p>\n<p>prêt à veiller, prier, </p>\n<p>prêt à donner et sacrifier, </p>\n<p>prêt à être guidé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Prêt à parler, prêt à enseigner, </p>\n<p>Prêt avec tout mon coeur, </p>\n<p>Prêt à me tenir en messager, </p>\n<p>Prêt à plaire au Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Prêt à parler, prêt à relever, </p>\n<p>Prêt à porter secours, </p>\n<p>Prêt dans la vie, </p>\n<p>Prêt à son prompt retour. </p>\n','Pret-a-souffrir-tristesse-et-douleur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2019,'689947c5-18d9-44bd-b87c-7512ef9750c5',0,0,'Lorsqu\'au ciel l\'appel sera fait',148,'148_MJ_fr','1.\nLorsqu\'au ciel l\'appel sera fait\nJe serai sur le chemin de Dieu;\nRedites-moi l\'histoire,\nChaque jour je l\'aime mieux, alléluia!\n«Je vous ferai pécheurs des hommes\nSi partout vous me suivez»,\nAlléluia, quel bon Sauveur!\nIl m\'a délivré!','<p><strong>1.</strong> </p>\n<p>Lorsqu&#39;au ciel l&#39;appel sera fait </p>\n<p>Je serai sur le chemin de Dieu; </p>\n<p>Redites-moi l&#39;histoire, </p>\n<p>Chaque jour je l&#39;aime mieux, alléluia! </p>\n<p>«Je vous ferai pécheurs des hommes </p>\n<p>Si partout vous me suivez», </p>\n<p>Alléluia, quel bon Sauveur! </p>\n<p>Il m&#39;a délivré! </p>\n','Lorsqu\'au-ciel-l\'appel-sera-fait',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2020,'d71ae5ba-e7de-4953-ba4a-3d2c528b473e',0,0,'Chaque jour je suis heureux',165,'165_MJ_fr','1.\nChaque jour je suis heureux\nJésus est dans mon coeur,\nEt je sais que désormais\nJe suis plus que vainqueur.  Amen!\nJésus-Christ mon Sauveur\nEst avec moi à jamais\nVoici la raison pourquoi je heureux\nJ\'ai la paix!','<p><strong>1.</strong> </p>\n<p>Chaque jour je suis heureux </p>\n<p>Jésus est dans mon coeur, </p>\n<p>Et je sais que désormais </p>\n<p>Je suis plus que vainqueur.  Amen! </p>\n<p>Jésus-Christ mon Sauveur </p>\n<p>Est avec moi à jamais </p>\n<p>Voici la raison pourquoi je heureux </p>\n<p>J&#39;ai la paix! </p>\n','Chaque-jour-je-suis-heureux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2021,'5b971309-3bfc-45e9-b447-1d8de5ad4f29',0,0,'Sé Bon-Dieu Ki Kapab Délivré',51,'51_HC_ht','1.\nSé gnou chan jouayé, gnou nouvèl glorié\nNan pouin lòt kòm Li nan lé mon-n antié\nMouin rinmin chanté-l é ma proklamé-l\nBon-Dieu nou kapab ba ou la délivrans.\n\nKè:\nSé Bon-Deiu ki kapab délivré,\nSé Li sèl ki toujou délivré,\nTout fado péché sou Li dépozé,\nPaské Bon-Dieu nou kap bay la délivrans.\n\n2.\nMoun ki vlé vini pa doué jan-m mizé\nPòt la byin ouvè pou-l kapab antré\nKris sé chémin, la vérité, la vi,\nBon-Dieu nou  kapab ba ou la délivrans.\n\n3.\nSé gnou chan jouayé,\nProklamé-l byin louin,\nPou tout aflijé an ba chaj péché,\nKapab résévoua padon pa la foua,\nBon-Dieu nou kapab ba ou la délivrans.','<p><strong>1.</strong> </p>\n<p>Sé gnou chan jouayé, gnou nouvèl glorié </p>\n<p>Nan pouin lòt kòm Li nan lé mon-n antié </p>\n<p>Mouin rinmin chanté-l é ma proklamé-l </p>\n<p>Bon-Dieu nou kapab ba ou la délivrans. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sé Bon-Deiu ki kapab délivré,</strong> </p>\n<p><strong>Sé Li sèl ki toujou délivré,</strong> </p>\n<p><strong>Tout fado péché sou Li dépozé,</strong> </p>\n<p><strong>Paské Bon-Dieu nou kap bay la délivrans.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moun ki vlé vini pa doué jan-m mizé </p>\n<p>Pòt la byin ouvè pou-l kapab antré </p>\n<p>Kris sé chémin, la vérité, la vi, </p>\n<p>Bon-Dieu nou  kapab ba ou la délivrans. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé gnou chan jouayé, </p>\n<p>Proklamé-l byin louin, </p>\n<p>Pou tout aflijé an ba chaj péché, </p>\n<p>Kapab résévoua padon pa la foua, </p>\n<p>Bon-Dieu nou kapab ba ou la délivrans. </p>\n','Se-Bon-Dieu-Ki-Kapab-Delivre',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2022,'f46bb0e2-26ca-4664-9086-b427ff90f284',0,0,'Pòt Salu A',74,'74_HC_ht','1.\nNan rout vi sa siokupé\nGnou pòt kléré nou jouin-n\nPòt sa sé rout salu Bon-Dieu\nLi ofri ban nou sa,\nAnpil moun gin tan antré la,\nKi té kon-n soufri pin-n\nÉ ti moun tou kab ouvri-l laj,\nPòt sa pou maché la.\n\n2.\nPòt sa égzijé rout étouat\nPou sièl li asé laj,\nNinpòt moun kapab antré la,\nÉ chapé jujman mal.\nMalgré rout la gin karotaj\nNou pa fè krin-n nofraj,\nSé min Jézu ki prè chak jou,\nPou soutni mouton pa-L.\n\n3.\nPòt sa kléré, Jézu ouvri-l,\nLè-L soufri mò Kalvè,\nFron Li pèsé ak pikan boua,\nKonsa Li rachté nou,\nPitit Bon-Dieu lè-L krusifié,\nDétoui rékla-m lanfè\nFason ké ti moun tou patou,\nMèt résévoua-L toujou.\n\n4.\nJézu té lévé nan la mò,\nPlas Li sé sièl anro,\nKouté ki jan Li invité\nTi moun vi-n répanti.\nSé pou nou tout Li soufri\nSan Li koulé kom dlo,\nL-ap tan-n nou prèt pou résévoua-n\nPou ban nou padon Li.','<p><strong>1.</strong> </p>\n<p>Nan rout vi sa siokupé </p>\n<p>Gnou pòt kléré nou jouin-n </p>\n<p>Pòt sa sé rout salu Bon-Dieu </p>\n<p>Li ofri ban nou sa, </p>\n<p>Anpil moun gin tan antré la, </p>\n<p>Ki té kon-n soufri pin-n </p>\n<p>É ti moun tou kab ouvri-l laj, </p>\n<p>Pòt sa pou maché la. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pòt sa égzijé rout étouat </p>\n<p>Pou sièl li asé laj, </p>\n<p>Ninpòt moun kapab antré la, </p>\n<p>É chapé jujman mal. </p>\n<p>Malgré rout la gin karotaj </p>\n<p>Nou pa fè krin-n nofraj, </p>\n<p>Sé min Jézu ki prè chak jou, </p>\n<p>Pou soutni mouton pa-L. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pòt sa kléré, Jézu ouvri-l, </p>\n<p>Lè-L soufri mò Kalvè, </p>\n<p>Fron Li pèsé ak pikan boua, </p>\n<p>Konsa Li rachté nou, </p>\n<p>Pitit Bon-Dieu lè-L krusifié, </p>\n<p>Détoui rékla-m lanfè </p>\n<p>Fason ké ti moun tou patou, </p>\n<p>Mèt résévoua-L toujou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jézu té lévé nan la mò, </p>\n<p>Plas Li sé sièl anro, </p>\n<p>Kouté ki jan Li invité </p>\n<p>Ti moun vi-n répanti. </p>\n<p>Sé pou nou tout Li soufri </p>\n<p>San Li koulé kom dlo, </p>\n<p>L-ap tan-n nou prèt pou résévoua-n </p>\n<p>Pou ban nou padon Li. </p>\n','Pot-Salu-A',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2023,'8ae1b096-b03f-47fe-ba9a-18bb4514dab0',0,0,'Mouin Té Jouin-n Bèl Sous La Vi a',75,'75_HC_ht','1.\nNan-m mouin té souaf\nLi pat jouin-n satisfaksion\nÉ plézi lé mon-n té tronpé nan-m mouin,\nMin gnou bon jou\nLè-m tap chèché soulajman,\nJézu té di: «Vi-n jouin-n la vi nan mouin.»\n\nKè:\nOui, mouin té jouin-n ni, bèl sous la vi a\nGras a Jézu, ki té mouri pou mouin,\nSan Li té koulé, sou mòn Kalvè a\nÉ koun yé a Li satisfè kè mouin.\n\n2.\nLa vi-m té tris lè-m pat ko konnin Jézu,\nPat gin éspoua, nan-m mouin té boulvèsé,\nMin gras Bon-Dieu té louvri sous la vi a,\nM-té jouin-n ladan padon é libèté.\n\n3.\nChè zanmi mouin, O pouki sa ou mizé\nVi-n koun yé a, vi-n jouin-n sous la vi sa,\nFado péché tro lou pou ou supòté,\nJézu vlé bay ou répo jodi a.','<p><strong>1.</strong> </p>\n<p>Nan-m mouin té souaf </p>\n<p>Li pat jouin-n satisfaksion </p>\n<p>É plézi lé mon-n té tronpé nan-m mouin, </p>\n<p>Min gnou bon jou </p>\n<p>Lè-m tap chèché soulajman, </p>\n<p>Jézu té di: «Vi-n jouin-n la vi nan mouin.» </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui, mouin té jouin-n ni, bèl sous la vi a</strong> </p>\n<p><strong>Gras a Jézu, ki té mouri pou mouin,</strong> </p>\n<p><strong>San Li té koulé, sou mòn Kalvè a</strong> </p>\n<p><strong>É koun yé a Li satisfè kè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La vi-m té tris lè-m pat ko konnin Jézu, </p>\n<p>Pat gin éspoua, nan-m mouin té boulvèsé, </p>\n<p>Min gras Bon-Dieu té louvri sous la vi a, </p>\n<p>M-té jouin-n ladan padon é libèté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chè zanmi mouin, O pouki sa ou mizé </p>\n<p>Vi-n koun yé a, vi-n jouin-n sous la vi sa, </p>\n<p>Fado péché tro lou pou ou supòté, </p>\n<p>Jézu vlé bay ou répo jodi a. </p>\n','Mouin-Te-Jouin-n-Bel-Sous-La-Vi-a',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2024,'a3553556-a82a-462c-870b-b36c73c39b6a',0,0,'Combattons ici-bàs',66,'66_MJ_fr','1.\nCombattons ici-bas\nPour la cause du Sauveur,\nNous reposant sur Son bras\nToujours vainqueur.\n\n2.\nEn avant, en avant!\nLa bataille est au Seigneur\nIl rserve au combattant\nLa couronne et le bonheur!','<p><strong>1.</strong> </p>\n<p>Combattons ici-bas </p>\n<p>Pour la cause du Sauveur, </p>\n<p>Nous reposant sur Son bras </p>\n<p>Toujours vainqueur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>En avant, en avant! </p>\n<p>La bataille est au Seigneur </p>\n<p>Il rserve au combattant </p>\n<p>La couronne et le bonheur! </p>\n','Combattons-ici-bas',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2025,'53a64d6c-a6a6-4fa0-8ae9-85c507b3e9a7',0,0,'Il y un seul Dieu',94,'94_MJ_fr','1.\nIl y a un seul Dieu\nEt un seul Médiateur\nEntre Dieu et les hommes,\nIl y a un seul Dieu\nEt un seul Médiateur,\nJésus-Christ, homme.\nQui s\'est donné Lui-même\nEn rançon pour nous tous, (ter 2 lignes)\nOh, quel amour merveilleux!\nIl y a un seul Dieu\nEt un seul Médiateur\nEntre Dieu et les hommes,\nIl y a un seul Dieu\nEt un seul Médiateur,\nJésus-Christ, homme.','<p><strong>1.</strong> </p>\n<p>Il y a un seul Dieu </p>\n<p>Et un seul Médiateur </p>\n<p>Entre Dieu et les hommes, </p>\n<p>Il y a un seul Dieu </p>\n<p>Et un seul Médiateur, </p>\n<p>Jésus-Christ, homme. </p>\n<p>Qui s&#39;est donné Lui-même </p>\n<p>En rançon pour nous tous, (ter 2 lignes) </p>\n<p>Oh, quel amour merveilleux! </p>\n<p>Il y a un seul Dieu </p>\n<p>Et un seul Médiateur </p>\n<p>Entre Dieu et les hommes, </p>\n<p>Il y a un seul Dieu </p>\n<p>Et un seul Médiateur, </p>\n<p>Jésus-Christ, homme. </p>\n','Il-y-un-seul-Dieu',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2026,'dbdd1eae-0278-4a18-9004-1f075d419654',0,0,'Viens, Esprit de Dieu vivant',112,'112_MJ_fr','1.\nViens, Esprit du Dieu vivant,\nSois le Maître en moi.\nViens, Esprit du Dieu vivant,\nSois le Maître en moi.\nSonde-moi, courbe-moi,\nBrise-moi, façonne-moi;\nViens, Esprit du Dieu vivant,\nViens et règne en moi.','<p><strong>1.</strong> </p>\n<p>Viens, Esprit du Dieu vivant, </p>\n<p>Sois le Maître en moi. </p>\n<p>Viens, Esprit du Dieu vivant, </p>\n<p>Sois le Maître en moi. </p>\n<p>Sonde-moi, courbe-moi, </p>\n<p>Brise-moi, façonne-moi; </p>\n<p>Viens, Esprit du Dieu vivant, </p>\n<p>Viens et règne en moi. </p>\n','Viens-Esprit-de-Dieu-vivant',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2027,'926c3ee9-8b55-4dde-8702-1b1717556fbd',0,0,'Levez Les yeux',96,'96_MJ_fr','1.Levez les yeux Sur tout le vaste monde, Vers les paysOù règne encore la nuit,Jésus attend queQuelqu\'un lui réponde:«Seigneur, j\'irai,Conduit par ton Esprit».Choeur:«Je suis avec vous tous les jours,Comptes sur mon puissant secours,Allez partout prêcher, Le saint Évangile,D\'une ville à l\'autre ville.»2.Levez les yeuxCar la moisson est mûre,Pourquoi resterSans rien faire aujourd\'hui?Avec Jésus, la tâcheN\'est point dure,N\'hésitez plus,Ne comptez que sur Lui.3.Levez les yeuxSonnez de la trompette,Pour railler sousL\'étendard sanglantDe Jésus-Christ,Tout héros, tout prophète,Prêt à mourir,S\'il faut, en combattant.4.Levez les yeuxSur l\'immense détresseDe tout mortelIgnorant du salut;Réveillez-vous!Arrière la paresse!Allez! Partez!Courrez droit vers le but!','<p><strong>1.</strong> </p>\n<p>Levez les yeux </p>\n<p>Sur tout le vaste monde, </p>\n<p>Vers les pays </p>\n<p>Où règne encore la nuit, </p>\n<p>Jésus attend que </p>\n<p>Quelqu&#39;un lui réponde: </p>\n<p>«Seigneur, j&#39;irai, </p>\n<p>Conduit par ton Esprit». </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>«Je suis avec vous tous les jours,</strong> </p>\n<p><strong>Comptes sur mon puissant secours,</strong> </p>\n<p><strong>Allez partout prêcher</strong></p>\n<p><strong>Le saint Évangile,</strong> </p>\n<p><strong>D&#39;une ville à l&#39;autre ville.»</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Levez les yeux </p>\n<p>Car la moisson est mûre, </p>\n<p>Pourquoi rester </p>\n<p>Sans rien faire aujourd&#39;hui? </p>\n<p>Avec Jésus, la tâche </p>\n<p>N&#39;est point dure, </p>\n<p>N&#39;hésitez plus, </p>\n<p>Ne comptez que sur Lui. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Levez les yeux </p>\n<p>Sonnez de la trompette, </p>\n<p>Pour railler sous </p>\n<p>L&#39;étendard sanglant </p>\n<p>De Jésus-Christ, </p>\n<p>Tout héros, tout prophète, </p>\n<p>Prêt à mourir, </p>\n<p>S&#39;il faut, en combattant. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Levez les yeux </p>\n<p>Sur l&#39;immense détresse </p>\n<p>De tout mortel </p>\n<p>Ignorant du salut; </p>\n<p>Réveillez-vous! </p>\n<p>Arrière la paresse! </p>\n<p>Allez! Partez! </p>\n<p>Courrez droit vers le but! </p>\n','Levez-Les-yeux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2028,'afc01879-299b-4624-9bde-c006bd454849',0,0,'Jésus, mon suprême',117,'117_MJ_fr','1.\nJésus, nom suprême –\nSon mélodieux,\nTout racheté l\'aime –\nSur la terre et dans les cieux.\n\n2.\nJésus, don céleste –\nSauveur merveilleux;\nTon amour me reste –\nComme un joyau précieux.\n\n3.\nJésus, point de vie –\nToi qui nourris,\nMon âme est ravie –\nQuand du ciel tu me souris.\n\n4.\nJésus, ma richesse –\nMa vie et mon lot;\nSelon ta promesse –\nDu ciel tu viendras bientôt.','<p><strong>1.</strong> </p>\n<p>Jésus, nom suprême – </p>\n<p>Son mélodieux, </p>\n<p>Tout racheté l&#39;aime – </p>\n<p>Sur la terre et dans les cieux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus, don céleste – </p>\n<p>Sauveur merveilleux; </p>\n<p>Ton amour me reste – </p>\n<p>Comme un joyau précieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus, point de vie – </p>\n<p>Toi qui nourris, </p>\n<p>Mon âme est ravie – </p>\n<p>Quand du ciel tu me souris. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus, ma richesse – </p>\n<p>Ma vie et mon lot; </p>\n<p>Selon ta promesse – </p>\n<p>Du ciel tu viendras bientôt. </p>\n','Jesus-mon-supreme',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2029,'3c8af9c6-fa4b-4a22-96b9-62abf33d5599',0,0,'Grâce infinie, ô quel beau don',98,'98_MJ_fr','1.\nGrâce infinie, ô quel beau don\nPour moi, pécheur, qui crois;\nJ\'étais perdu, j\'ai le pardon,\nAveugle, mais je vois.\n\n2.\nLa grâce a mis la crainte en moi,\nEt m\'en a délivré;\nO grâce, précieuse à ma foi\nLorsque j\'y ai cédé.\n\n3.\nAu travers des temps infinis\nLes chants de tes élus\nSeront toujours ta grâce, ô Christ,\nComme au temps où j\'ai cru.','<p><strong>1.</strong> </p>\n<p>Grâce infinie, ô quel beau don </p>\n<p>Pour moi, pécheur, qui crois; </p>\n<p>J&#39;étais perdu, j&#39;ai le pardon, </p>\n<p>Aveugle, mais je vois. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La grâce a mis la crainte en moi, </p>\n<p>Et m&#39;en a délivré; </p>\n<p>O grâce, précieuse à ma foi </p>\n<p>Lorsque j&#39;y ai cédé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au travers des temps infinis </p>\n<p>Les chants de tes élus </p>\n<p>Seront toujours ta grâce, ô Christ, </p>\n<p>Comme au temps où j&#39;ai cru. </p>\n','Grace-infinie-o-quel-beau-don',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2030,'ed4da32f-b7d7-4b11-a254-05ffe6264e27',0,0,'Toujours joyeux!',162,'162_MJ_fr','1.\nToujours joyeux!\nTel est notre devise\nJoyeux d\'avoir Jésus-christ\nPour Sauveur.  Oui, joyeux même\nAlors qu\'on nous méprise\nRien ne saurait assombrir notre coeur.\nN\'ayons pas peur de l\'adversaire.\nMarchons en chantant vers les cieux,\nTriomphant déjà sur la terre,\nToujours joyeux,\nToujours joyeux.  (bis 3 lignes)\n\n2.\nJoyeux encore\nAu fort de la bataille,\nNous combattrons,\nPour Lui jusqu\'au tombeau.\nAh!  Pourrions-nous,\nPour plaire à qui nous raille,\nTrahir jamais de Jésus le drapeau?\nN\'est-Il pas un Roi glorieux?\nBravons pour Lui la moquerie,\nToujours joyeux,\nToujours joyeux!  (bis 3 lignes)\n\n3.\nToujours joyeux!\nUn jour devant les anges,\nJésus dira: «Ces vaillants sont à moi»\nNos coeurs alors\nD\'éclater en louanges,\nPour ce Jésus, notre bien-aimé Roi.\nSouffrir pour Lui, bonheur suprême!\nVictorieux par son secours,\nNous resterons, puisqu\'Il nous aime,\nToujours joyeux,\nToujours joyeux!  (bis 3 lignes)','<p><strong>1.</strong> </p>\n<p>Toujours joyeux! </p>\n<p>Tel est notre devise </p>\n<p>Joyeux d&#39;avoir Jésus-christ </p>\n<p>Pour Sauveur.  Oui, joyeux même </p>\n<p>Alors qu&#39;on nous méprise </p>\n<p>Rien ne saurait assombrir notre coeur. </p>\n<p>N&#39;ayons pas peur de l&#39;adversaire. </p>\n<p>Marchons en chantant vers les cieux, </p>\n<p>Triomphant déjà sur la terre, </p>\n<p>Toujours joyeux, </p>\n<p>Toujours joyeux.  (bis 3 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Joyeux encore </p>\n<p>Au fort de la bataille, </p>\n<p>Nous combattrons, </p>\n<p>Pour Lui jusqu&#39;au tombeau. </p>\n<p>Ah!  Pourrions-nous, </p>\n<p>Pour plaire à qui nous raille, </p>\n<p>Trahir jamais de Jésus le drapeau? </p>\n<p>N&#39;est-Il pas un Roi glorieux? </p>\n<p>Bravons pour Lui la moquerie, </p>\n<p>Toujours joyeux, </p>\n<p>Toujours joyeux!  (bis 3 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Toujours joyeux! </p>\n<p>Un jour devant les anges, </p>\n<p>Jésus dira: «Ces vaillants sont à moi» </p>\n<p>Nos coeurs alors </p>\n<p>D&#39;éclater en louanges, </p>\n<p>Pour ce Jésus, notre bien-aimé Roi. </p>\n<p>Souffrir pour Lui, bonheur suprême! </p>\n<p>Victorieux par son secours, </p>\n<p>Nous resterons, puisqu&#39;Il nous aime, </p>\n<p>Toujours joyeux, </p>\n<p>Toujours joyeux!  (bis 3 lignes) </p>\n','Toujours-joyeux!',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2031,'11ce9b87-0295-470c-af15-335d1220777f',0,0,'Je me rappelle un jour',101,'101_MJ_fr','1.\nJe me rappelle un jour\nOù dans le ciel je vis\nLe livre dans lequel\nTout pécheur est inscrit;\nJe vis mon nom très clair,\nJe dis, mon Dieu  que faire?\nEn tombant à ses pieds,\nJe réglai mon affaire.\n\nChoeur:\nA l\'instant, à l\'instant,\nOui, le vieux compte fut réglé à l\'instant;\nJe ne suis plus effrayé\nMa dette est déjà payée,\nEt mon compte avec mon juge\nEst tout réglé.\n\n2.\nMon compte était très lourd,\nEt je voulais toujours,\nQuand j\'étais égaré,\nPécher plus chaque jour;\nMais quand mes yeux s\'ouvrirent\nEt je vis mon état,\nJe me jetai aux pieds\nDu Sauveur, à l\'instant.\n\n3.\nAu jour du jugement,\nDevant le trône blanc,\nQuand tous les morts viendront,\nLes livres s\'ouvriront;\nEt quand on cherchera,\nMon nom n\'y sera pas,\nCar Jésus l\'a inscrit\nAu Livre de la Vie.','<p><strong>1.</strong> </p>\n<p>Je me rappelle un jour </p>\n<p>Où dans le ciel je vis </p>\n<p>Le livre dans lequel </p>\n<p>Tout pécheur est inscrit; </p>\n<p>Je vis mon nom très clair, </p>\n<p>Je dis, mon Dieu  que faire? </p>\n<p>En tombant à ses pieds, </p>\n<p>Je réglai mon affaire. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>A l&#39;instant, à l&#39;instant,</strong> </p>\n<p><strong>Oui, le vieux compte fut réglé à l&#39;instant;</strong> </p>\n<p><strong>Je ne suis plus effrayé</strong> </p>\n<p><strong>Ma dette est déjà payée,</strong> </p>\n<p><strong>Et mon compte avec mon juge</strong> </p>\n<p><strong>Est tout réglé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon compte était très lourd, </p>\n<p>Et je voulais toujours, </p>\n<p>Quand j&#39;étais égaré, </p>\n<p>Pécher plus chaque jour; </p>\n<p>Mais quand mes yeux s&#39;ouvrirent </p>\n<p>Et je vis mon état, </p>\n<p>Je me jetai aux pieds </p>\n<p>Du Sauveur, à l&#39;instant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au jour du jugement, </p>\n<p>Devant le trône blanc, </p>\n<p>Quand tous les morts viendront, </p>\n<p>Les livres s&#39;ouvriront; </p>\n<p>Et quand on cherchera, </p>\n<p>Mon nom n&#39;y sera pas, </p>\n<p>Car Jésus l&#39;a inscrit </p>\n<p>Au Livre de la Vie. </p>\n','Je-me-rappelle-un-jour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2032,'56e861a3-e46c-4f51-ac7e-53d5dc3101ec',0,0,'Faut-il nous quitter sans espoir',176,'176_MJ_fr','1.\nFaut-il nous quitter sans espoir\nSans espoir de retour?\nFaut-il nous quitter sans espoir\nDe nous revoir un jour?\n\nChoeur:\nCe n\'est qu\'un au revoir, mes frères\nCe n\'est qu\'un au revoir\nOui, nous nous reverrons, mes frères\nCe n\'est qu\'un au revoir.\n\n2.\nFormons de nos mains qui s\'enlacent\nAu déclin de ce jour,\nFormons de nos mains qui s\'enlacent\nUne chaîne d\'amour.\n\n3.\nCar Dieu, qui nous voit tous ensemble\nEt qui veut nous bénir,\nCar Dieu, qui nous voit tous ensemble\nSaura nous réunir!','<p><strong>1.</strong> </p>\n<p>Faut-il nous quitter sans espoir </p>\n<p>Sans espoir de retour? </p>\n<p>Faut-il nous quitter sans espoir </p>\n<p>De nous revoir un jour? </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Ce n&#39;est qu&#39;un au revoir, mes frères</strong> </p>\n<p><strong>Ce n&#39;est qu&#39;un au revoir</strong> </p>\n<p><strong>Oui, nous nous reverrons, mes frères</strong> </p>\n<p><strong>Ce n&#39;est qu&#39;un au revoir.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Formons de nos mains qui s&#39;enlacent </p>\n<p>Au déclin de ce jour, </p>\n<p>Formons de nos mains qui s&#39;enlacent </p>\n<p>Une chaîne d&#39;amour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Car Dieu, qui nous voit tous ensemble </p>\n<p>Et qui veut nous bénir, </p>\n<p>Car Dieu, qui nous voit tous ensemble </p>\n<p>Saura nous réunir! </p>\n','Faut-il-nous-quitter-sans-espoir',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2033,'73b8e3cf-e96d-41f2-b626-04632f34a458',0,0,'M-O-I, c\'est moi que Christ appelle',104,'104_MJ_fr','1.\nM-O-I, c\'est moi que Christ appelle,\nL-U-I, Lui seul peut me sauver;\nOh, joyeuse nouvelle,\nLe Dieu fort et fidèle,\nVeut racheter mon âme\nEt mes péchés laver.','<p><strong>1.</strong> </p>\n<p>M-O-I, c&#39;est moi que Christ appelle, </p>\n<p>L-U-I, Lui seul peut me sauver; </p>\n<p>Oh, joyeuse nouvelle, </p>\n<p>Le Dieu fort et fidèle, </p>\n<p>Veut racheter mon âme </p>\n<p>Et mes péchés laver. </p>\n','M-O-I-c\'est-moi-que-Christ-appelle',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2034,'98d8464a-1f7b-4814-a6f6-9beda1939d74',0,0,'Que Dieu vous donne du repos',177,'177_MJ_fr','1.\nQue Dieu vous donne du repos,\nSoyez tous sans crainte,\nCar le Christ notre Sauveur\nCe jour sans contrainte\nFut incarné pour nous sauver,\nDans la ville sainte.\n\nChoeur:\nO nouvelle de joie et paix,\nDe joie et paix\nO nouvelle de joie et paix!\n\n2.\nUn ange de Dieu vint du ciel\nAnnoncer aux bergers,\nUne bonne nouvelle,\nAu monde fatigué\nUn message de joie et paix,\nLe pardon du péché.\n\n3.\nNe craignez point l\'ange leur dit,\nUn Sauveur nous est né\nDans la ville de David,\nNaquit ce saint Bébé\nLes bergers y allèrent en hâte\nEt l\'enfant ont trouvé.\n\n4.\nIls trouvèrent L\'Enfant si saint\nDans la crèche couché\nEt Sa mère, Marie,\nSon Dieu a exalté.\nPuis les bergers s\'en allèrent,\nL\'histoire raconter.\n\n5.\nChantons tous avec louange,\nAu Seigneur en ce jour\nEt que nos coeurs ensemble\nSoient unis par l\'amour.\nDonnons-Lui nos vies entières\nPour Le servir toujours.','<p><strong>1.</strong> </p>\n<p>Que Dieu vous donne du repos, </p>\n<p>Soyez tous sans crainte, </p>\n<p>Car le Christ notre Sauveur </p>\n<p>Ce jour sans contrainte </p>\n<p>Fut incarné pour nous sauver, </p>\n<p>Dans la ville sainte. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>O nouvelle de joie et paix,</strong> </p>\n<p><strong>De joie et paix</strong> </p>\n<p><strong>O nouvelle de joie et paix!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Un ange de Dieu vint du ciel </p>\n<p>Annoncer aux bergers, </p>\n<p>Une bonne nouvelle, </p>\n<p>Au monde fatigué </p>\n<p>Un message de joie et paix, </p>\n<p>Le pardon du péché. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ne craignez point l&#39;ange leur dit, </p>\n<p>Un Sauveur nous est né </p>\n<p>Dans la ville de David, </p>\n<p>Naquit ce saint Bébé </p>\n<p>Les bergers y allèrent en hâte </p>\n<p>Et l&#39;enfant ont trouvé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ils trouvèrent L&#39;Enfant si saint </p>\n<p>Dans la crèche couché </p>\n<p>Et Sa mère, Marie, </p>\n<p>Son Dieu a exalté. </p>\n<p>Puis les bergers s&#39;en allèrent, </p>\n<p>L&#39;histoire raconter. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Chantons tous avec louange, </p>\n<p>Au Seigneur en ce jour </p>\n<p>Et que nos coeurs ensemble </p>\n<p>Soient unis par l&#39;amour. </p>\n<p>Donnons-Lui nos vies entières </p>\n<p>Pour Le servir toujours. </p>\n','Que-Dieu-vous-donne-du-repos',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2035,'3909b9da-daa6-4922-90eb-6647042b2b4a',0,0,'Tu payas mon salut',106,'106_MJ_fr','1.\nTu payas mon salut\nPar ton grand sacrifice,\nO Jésus, quel merveilleux amour!\nDésormais pour ton saint\nEt glorieux service\nTon racheté se donne en retour.\n\nChoeur:\nJe viens à ton autel,\nOffrande volontaire,\nLibrement je veux servir mon Roi;\nJe ne veux désormais\nQue ta gloire, ô mon Père\nNe vivre que par Toi,\nQue pour Toi.\n\n2.\nPrends tout ce que je suis,\nPrends mon coeur et mon âme,\nQue tout mon être te soit livré!\nContrôlé par l\'Esprit,\nAnimé de ta flamme,\nEt de ta Parole pénétré.\n\n3.\nSeigneur, de jour en jour,\nTransforme à ton image\nTon enfant, ton heureux racheté;\nDevant moi n\'est qu\'un but\nJusqu\'au bout du voyage,\nConnaître et faire ta volonté.','<p><strong>1.</strong> </p>\n<p>Tu payas mon salut </p>\n<p>Par ton grand sacrifice, </p>\n<p>O Jésus, quel merveilleux amour! </p>\n<p>Désormais pour ton saint </p>\n<p>Et glorieux service </p>\n<p>Ton racheté se donne en retour. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Je viens à ton autel,</strong> </p>\n<p><strong>Offrande volontaire,</strong> </p>\n<p><strong>Librement je veux servir mon Roi;</strong> </p>\n<p><strong>Je ne veux désormais</strong> </p>\n<p><strong>Que ta gloire, ô mon Père</strong> </p>\n<p><strong>Ne vivre que par Toi,</strong> </p>\n<p><strong>Que pour Toi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Prends tout ce que je suis, </p>\n<p>Prends mon coeur et mon âme, </p>\n<p>Que tout mon être te soit livré! </p>\n<p>Contrôlé par l&#39;Esprit, </p>\n<p>Animé de ta flamme, </p>\n<p>Et de ta Parole pénétré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur, de jour en jour, </p>\n<p>Transforme à ton image </p>\n<p>Ton enfant, ton heureux racheté; </p>\n<p>Devant moi n&#39;est qu&#39;un but </p>\n<p>Jusqu&#39;au bout du voyage, </p>\n<p>Connaître et faire ta volonté. </p>\n','Tu-payas-mon-salut',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2036,'b35ac198-78eb-4761-8f91-8ac2714833da',0,0,'Sèlman Jézu',14,'14_HC_ht','1.\nDépi-m trouvé Jézu mouin trouvé répo,\nJézu kaché-m nan gran amou Li,\nLi konpran-n mouin,\nL-édé’m pòté tout fado\nChak jou Li ban-m prèv gran amou Li.\n\nKè:\nSèlman Jézu, sèlman Jézu,\nLi-min’m sèl kab satisfè,\nLi ban-m la joua, Li ban mouin la pè,\nLi ban mouin bonnè pafè.\n\n2.\nDépi-m trouvé Jézu mouin trouvé la joua,\nMouin pa-t pansé sou la tè sa a,\nMouin pa jan-m kab trouvé.\nGnou si gran joua\nM-ap gouté déjà bonnè sièl la.\n\n\n3.\nDépi-m trouvé Jézu mouin gingnin éspoua\nMin-m nan mizè-m,\nMin-m nan soufrans mouin,\nNan pouin loraj ki kab fè-m pèdu la foua,\nTénèb chanjé an lumiè pou mouin.','<p><strong>1.</strong> </p>\n<p>Dépi-m trouvé Jézu mouin trouvé répo, </p>\n<p>Jézu kaché-m nan gran amou Li, </p>\n<p>Li konpran-n mouin, </p>\n<p>L-édé’m pòté tout fado </p>\n<p>Chak jou Li ban-m prèv gran amou Li. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sèlman Jézu, sèlman Jézu,</strong> </p>\n<p><strong>Li-min’m sèl kab satisfè,</strong> </p>\n<p><strong>Li ban-m la joua, Li ban mouin la pè,</strong> </p>\n<p><strong>Li ban mouin bonnè pafè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dépi-m trouvé Jézu mouin trouvé la joua, </p>\n<p>Mouin pa-t pansé sou la tè sa a, </p>\n<p>Mouin pa jan-m kab trouvé. </p>\n<p>Gnou si gran joua </p>\n<p>M-ap gouté déjà bonnè sièl la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dépi-m trouvé Jézu mouin gingnin éspoua </p>\n<p>Min-m nan mizè-m, </p>\n<p>Min-m nan soufrans mouin, </p>\n<p>Nan pouin loraj ki kab fè-m pèdu la foua, </p>\n<p>Ténèb chanjé an lumiè pou mouin. </p>\n','Selman-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2037,'3a5520ed-8c7c-4d81-b3df-7edc37d00f9d',0,0,'Ton amour, mon Sauveur',147,'147_MJ_fr','1.\nTon amour, mon Sauveur,\nA du ciel la hauteur,\nDe l\'océan la largeur,\nComme sa profondeur;\nJe n\'ai pas de valeur,\nMais Tu m\'as sur ton coeur,\nJe me dis, Rédempteur,\nQue Tu veux le bonheur du pécheur.','<p><strong>1.</strong> </p>\n<p>Ton amour, mon Sauveur, </p>\n<p>A du ciel la hauteur, </p>\n<p>De l&#39;océan la largeur, </p>\n<p>Comme sa profondeur; </p>\n<p>Je n&#39;ai pas de valeur, </p>\n<p>Mais Tu m&#39;as sur ton coeur, </p>\n<p>Je me dis, Rédempteur, </p>\n<p>Que Tu veux le bonheur du pécheur. </p>\n','Ton-amour-mon-Sauveur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2038,'82fcc6de-d8dc-41dd-b424-abd6f5ac7eee',0,0,'Mouin Gin Gnou Anvi Nan Kè Mouin Jézu',33,'33_HC_ht','1.\nMouin gin gnou anvi\nNan kè mouin pou Jézu\nMouin gin gnou anvi nan kè mouin pou Li\nMalgré mouin konnin\nMouin gin gnou anvi nan kè mouin pou Li:\n\nKè:\nKonbyin mouin anvi Jézu,\nMouin gin gnou anvi nan kè mouin pou Li,\nPou mouin tou prè Li prézans Li,\nMouin gin gnou anvi nan kè mouin pou Li.\n\n2.\nMouin ta kontan\nPou mouin maché ak Jézu,\nMouin ta byin kontan pou ba Li la min,\nSépandan mouin konnin\nLi la pou gidé-m,\nMouin konnin amou Li pap jan-m lagé-m.\n\n\n\n\n3.\nPou nou-min’m ki pa ko\nKonnin Jézu-Kri,\nNou manké pi gran la joua ki gingnin,\nVi-n résévoua Li kom Sovè é Mèt nou\nLi vlé ouété nou anba kòd péché.','<p><strong>1.</strong> </p>\n<p>Mouin gin gnou anvi </p>\n<p>Nan kè mouin pou Jézu </p>\n<p>Mouin gin gnou anvi nan kè mouin pou Li </p>\n<p>Malgré mouin konnin </p>\n<p>Mouin gin gnou anvi nan kè mouin pou Li: </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konbyin mouin anvi Jézu,</strong> </p>\n<p><strong>Mouin gin gnou anvi nan kè mouin pou Li,</strong> </p>\n<p><strong>Pou mouin tou prè Li prézans Li,</strong> </p>\n<p><strong>Mouin gin gnou anvi nan kè mouin pou Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin ta kontan </p>\n<p>Pou mouin maché ak Jézu, </p>\n<p>Mouin ta byin kontan pou ba Li la min, </p>\n<p>Sépandan mouin konnin </p>\n<p>Li la pou gidé-m, </p>\n<p>Mouin konnin amou Li pap jan-m lagé-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pou nou-min’m ki pa ko </p>\n<p>Konnin Jézu-Kri, </p>\n<p>Nou manké pi gran la joua ki gingnin, </p>\n<p>Vi-n résévoua Li kom Sovè é Mèt nou </p>\n<p>Li vlé ouété nou anba kòd péché. </p>\n','Mouin-Gin-Gnou-Anvi-Nan-Ke-Mouin-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2039,'2717a7ab-7b70-49f9-a511-983cbb78f0f7',0,0,'O Toi qui sondes les mers profondes',154,'154_MJ_fr','1.\nO Toi qui sondes les mers profondes,\nEt qui lis au fond de mon coeur,\nTu sais mes chutes, mes vaines luttes;\nJ\'ai méprisé ta loi, Seigneur!\n\n2.\nVois, à cette heure, je souffre et je pleure\nEt n\'ose regarder vers toi!\nJ\'implore grâce, Seigneur efface!\nTout mon péché pèse sur moi!\n\n3.\nMais, joie immense! De ta clémence\nJ\'entends comme un écho lointain\nÉmu, j\'écoute, et, plus de doute!\nLe Sauveur m\'apparaît soudain.\n\n4.\nSublime chose! Son sang m\'arrose,\nDans ce sang mon coeur est lavé!\nBonheur et gloire! Chant de victoire!\nLe plus grand pécheur est sauvé!','<p><strong>1.</strong> </p>\n<p>O Toi qui sondes les mers profondes, </p>\n<p>Et qui lis au fond de mon coeur, </p>\n<p>Tu sais mes chutes, mes vaines luttes; </p>\n<p>J&#39;ai méprisé ta loi, Seigneur! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois, à cette heure, je souffre et je pleure </p>\n<p>Et n&#39;ose regarder vers toi! </p>\n<p>J&#39;implore grâce, Seigneur efface! </p>\n<p>Tout mon péché pèse sur moi! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais, joie immense! De ta clémence </p>\n<p>J&#39;entends comme un écho lointain </p>\n<p>Ému, j&#39;écoute, et, plus de doute! </p>\n<p>Le Sauveur m&#39;apparaît soudain. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sublime chose! Son sang m&#39;arrose, </p>\n<p>Dans ce sang mon coeur est lavé! </p>\n<p>Bonheur et gloire! Chant de victoire! </p>\n<p>Le plus grand pécheur est sauvé! </p>\n','O-Toi-qui-sondes-les-mers-profondes',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2040,'5ff2371a-95f4-44e6-a84a-36aabd1aa9f4',0,0,'Mouin Fè Li Ségnè',38,'38_HC_ht','1.\nLè-m té vi-n jouin-n Jézu,\nMalgré tout péché-m\nLa min-m li sové mouin, é Li padonnin-m,\nLa foua mouin vi-n fèm,\nKè mouin ranpli ak joua\nLè-m fè Li Ségnè-m.\n\nKè:\nMouin fè Li Ségnè konplètmn sou la vi-m,\nÉ m-a maché avèk Li tout lè.\nSé gnou privilèj sévi Li jan Li vlé,\nMouin kouroné Li Ségnè.\n\n2.\nKoun yé a mouin bay Kris\nTou sa-m posédé,\nNan pouin sakrifis ké Li pa mérité,\nM-ap dispozé tan, fòs, ak kapasité\nLè mouin ba Li tout,\nAtò mouin kab chanté.\n\n3.\nEské-m pa doué rinmin-L?\nPou mouin Li mouri,\nEské-m pa doué sèvi-L?\nSé mouin L-afranchi!\nEské-m pa doué obéyi tou sa Li di?\nMin mouin, Ségnè Jézu,\nPran mouin nèt, tanpi!','<p><strong>1.</strong> </p>\n<p>Lè-m té vi-n jouin-n Jézu, </p>\n<p>Malgré tout péché-m </p>\n<p>La min-m li sové mouin, é Li padonnin-m, </p>\n<p>La foua mouin vi-n fèm, </p>\n<p>Kè mouin ranpli ak joua </p>\n<p>Lè-m fè Li Ségnè-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin fè Li Ségnè konplètmn sou la vi-m,</strong> </p>\n<p><strong>É m-a maché avèk Li tout lè.</strong> </p>\n<p><strong>Sé gnou privilèj sévi Li jan Li vlé,</strong> </p>\n<p><strong>Mouin kouroné Li Ségnè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Koun yé a mouin bay Kris </p>\n<p>Tou sa-m posédé, </p>\n<p>Nan pouin sakrifis ké Li pa mérité, </p>\n<p>M-ap dispozé tan, fòs, ak kapasité </p>\n<p>Lè mouin ba Li tout, </p>\n<p>Atò mouin kab chanté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Eské-m pa doué rinmin-L? </p>\n<p>Pou mouin Li mouri, </p>\n<p>Eské-m pa doué sèvi-L? </p>\n<p>Sé mouin L-afranchi! </p>\n<p>Eské-m pa doué obéyi tou sa Li di? </p>\n<p>Min mouin, Ségnè Jézu, </p>\n<p>Pran mouin nèt, tanpi! </p>\n','Mouin-Fe-Li-Segne',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2041,'c6fa0650-4874-4690-a37c-e9102c24ff46',0,0,'Mouin P-ap Blié Ou',10,'10_HC_ht','1.\nMin gnou dous promès,\n«Mouin pap jan-m blié ou»\nAngnin pap molèsté ni détounin mouin.\nMin-m si ténèb la fè mouin pa ouè dutou\nPi dévan la jounin sélès ap kléré.\n\nKè:\nMouin pap kité ou ni blié ou,\nNan min mouin m-a kinbé ou\nNan bra mouin antouré ou\nMouin pap kité ou ni blié ou\nMouin sé Rédanmtè ou, m-a okupé ou.\n\n2.\nMap kouè promès la,\n«Mouin pap jan-m blié ou»\nAn-avan map maché ak gnou chan jouayé,\nZanmi lé mon-n blié-m é méprizé-m tou\nMin Jézu sonjé mouin, sé sa ki konté.\n\n3.\nKan lè a rivé pou mouin kité tè sa,\nM-a tandé la voua Jézu-Kri Rédanmtè,\n«Ou té kinbé la foua tout tan ou la-ba,\nantré isit bon é fidèl sèvité.','<p><strong>1.</strong> </p>\n<p>Min gnou dous promès, </p>\n<p>«Mouin pap jan-m blié ou» </p>\n<p>Angnin pap molèsté ni détounin mouin. </p>\n<p>Min-m si ténèb la fè mouin pa ouè dutou </p>\n<p>Pi dévan la jounin sélès ap kléré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin pap kité ou ni blié ou,</strong> </p>\n<p><strong>Nan min mouin m-a kinbé ou</strong> </p>\n<p><strong>Nan bra mouin antouré ou</strong> </p>\n<p><strong>Mouin pap kité ou ni blié ou</strong> </p>\n<p><strong>Mouin sé Rédanmtè ou, m-a okupé ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Map kouè promès la, </p>\n<p>«Mouin pap jan-m blié ou» </p>\n<p>An-avan map maché ak gnou chan jouayé, </p>\n<p>Zanmi lé mon-n blié-m é méprizé-m tou </p>\n<p>Min Jézu sonjé mouin, sé sa ki konté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kan lè a rivé pou mouin kité tè sa, </p>\n<p>M-a tandé la voua Jézu-Kri Rédanmtè, </p>\n<p>«Ou té kinbé la foua tout tan ou la-ba, </p>\n<p>antré isit bon é fidèl sèvité. </p>\n','Mouin-P-ap-Blie-Ou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2042,'5bccbd5b-a092-4651-96cc-ee44aaf562ea',0,0,'Nan Gran Jou Rézurèksion',61,'61_HC_ht','1.\nNan gran jou la rézurèksion\nLè tronpèt Bon-Dieu sonnin,\nN-a lévé, alélouya, n-a lévé.\nN-a ranpli ak bénédiksion\nKi gran joua nou va gingnin,\nN-a lévé, alélouya, n-a lévé.\n\nKè:\nN-a lévé, n-a lévé, n-a lévé,\nNan gran jou la rézurèksion,\nLè pòt prizon la mo krazé,\nN-a lévé, alélouya, n-a lévé.\n\n2.\nNan gran jou la rézurèksion\nA la gnou gran réunion,\nN-a lévé, alélouya, n-a lévé.\nAk paran nou, frè ak sè nou\nKi té chèché vrè padon,\nN-a lévé, alélouya, n-a lévé.\n\n3.\nNan gran jou la rézurèksion\nPi gran éspérans mouin gin,\nN-a lévé, alélouya, n-a lévé.\nM-a kontré ak Sovè béni,\nKi té mouri nan plas mouin,\nN-a lévé, alélouya, n-a lévé.\n\n4.\nNan gran jou la rézurèksion\nN-a jouin-n avèk Kris an lè\nN-a lévé, alélouya, n-a lévé.\nL-a pòté nou jouk nan la gloua\nÉ tout bagay va chanjé,\nN-a lévé, alèlouya, n-a lévé.','<p><strong>1.</strong> </p>\n<p>Nan gran jou la rézurèksion </p>\n<p>Lè tronpèt Bon-Dieu sonnin, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p>N-a ranpli ak bénédiksion </p>\n<p>Ki gran joua nou va gingnin, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>N-a lévé, n-a lévé, n-a lévé,</strong> </p>\n<p><strong>Nan gran jou la rézurèksion,</strong> </p>\n<p><strong>Lè pòt prizon la mo krazé,</strong> </p>\n<p><strong>N-a lévé, alélouya, n-a lévé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan gran jou la rézurèksion </p>\n<p>A la gnou gran réunion, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p>Ak paran nou, frè ak sè nou </p>\n<p>Ki té chèché vrè padon, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan gran jou la rézurèksion </p>\n<p>Pi gran éspérans mouin gin, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p>M-a kontré ak Sovè béni, </p>\n<p>Ki té mouri nan plas mouin, </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan gran jou la rézurèksion </p>\n<p>N-a jouin-n avèk Kris an lè </p>\n<p>N-a lévé, alélouya, n-a lévé. </p>\n<p>L-a pòté nou jouk nan la gloua </p>\n<p>É tout bagay va chanjé, </p>\n<p>N-a lévé, alèlouya, n-a lévé. </p>\n','Nan-Gran-Jou-Rezureksion',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2043,'4a60a512-d6f1-49e1-bd58-350469636eaf',0,0,'Jézu Péyé Tout',12,'12_HC_ht','1.\nMouin tandé Sovè-m di,\nFòs ou piti anpil,\nKont féblès véyé, prié,\nOu-a jouin-n tout bagay fasil.\n\nKè:\nJézu péyé tout,\nLi-min’m sèl mouin doué,\nPéché-m té kòm gnou tach rouj\nJézu té blanchi-m vré.\n\n2.\nPou gran mèsi mouin gin\nDoua gras ou réklamé,\nMouin va lavé rad mouin\nNan san Lagno byinn-émé.\n\n3.\nÉ lè dévan tròn Li\nPou toujou m-a rété,\nKris, Ou té sové nan-m mouin,\nMo sa yo m-a répété','<p><strong>1.</strong> </p>\n<p>Mouin tandé Sovè-m di, </p>\n<p>Fòs ou piti anpil, </p>\n<p>Kont féblès véyé, prié, </p>\n<p>Ou-a jouin-n tout bagay fasil. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu péyé tout,</strong> </p>\n<p><strong>Li-min’m sèl mouin doué,</strong> </p>\n<p><strong>Péché-m té kòm gnou tach rouj</strong> </p>\n<p><strong>Jézu té blanchi-m vré.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou gran mèsi mouin gin </p>\n<p>Doua gras ou réklamé, </p>\n<p>Mouin va lavé rad mouin </p>\n<p>Nan san Lagno byinn-émé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>É lè dévan tròn Li </p>\n<p>Pou toujou m-a rété, </p>\n<p>Kris, Ou té sové nan-m mouin, </p>\n<p>Mo sa yo m-a répété </p>\n','Jezu-Peye-Tout',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2044,'328b5ff6-51ae-4c2c-8e77-24b065e47894',0,0,'Chèché Pou Prézanté Ou Dévan Bon-Dieu',66,'66_HC_ht','1.\nAndan Paròl Bon-Dieu na jouin-n\nGnou trézò vérité kaché,\nMin si nou fouyé trézò sa\nRichès li va manifésté.\n\nKè:\nChèché pou prézanté ou dévan Bon-Dieu\nTankou ouvrié ki éprouvé nan tout lié,\nKi pa gingnin pou li ront dévan Létènèl,\nAp bay mésaj Lévanjil fidèl.\n\n2.\nSé pou ou li Bib ou chak jou,\nPou profité richès Léspri,\nKonsa oua prospéré tout bon,\nProjè ou yo va réusi.\n\n3.\nBib la montré chémin la vi\nÉ Kris akité péché nou\nMin, fòk nou konnin Bib la byin,\nPou ékspliké mésaj la tou.\n\n4.\nSé moun Bon-Dieu té inspiré\nKi ékri la Paròl Sakré\nÉ lè jujman dènié rivé,\nSé nan Liv sa nou va jujé.','<p><strong>1.</strong> </p>\n<p>Andan Paròl Bon-Dieu na jouin-n </p>\n<p>Gnou trézò vérité kaché, </p>\n<p>Min si nou fouyé trézò sa </p>\n<p>Richès li va manifésté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chèché pou prézanté ou dévan Bon-Dieu</strong> </p>\n<p><strong>Tankou ouvrié ki éprouvé nan tout lié,</strong> </p>\n<p><strong>Ki pa gingnin pou li ront dévan Létènèl,</strong> </p>\n<p><strong>Ap bay mésaj Lévanjil fidèl.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé pou ou li Bib ou chak jou, </p>\n<p>Pou profité richès Léspri, </p>\n<p>Konsa oua prospéré tout bon, </p>\n<p>Projè ou yo va réusi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bib la montré chémin la vi </p>\n<p>É Kris akité péché nou </p>\n<p>Min, fòk nou konnin Bib la byin, </p>\n<p>Pou ékspliké mésaj la tou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sé moun Bon-Dieu té inspiré </p>\n<p>Ki ékri la Paròl Sakré </p>\n<p>É lè jujman dènié rivé, </p>\n<p>Sé nan Liv sa nou va jujé. </p>\n','Cheche-Pou-Prezante-Ou-Devan-Bon-Dieu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2045,'021af9c3-1e80-4a08-9408-3fa912008bf3',0,0,'Mouin Kanpé Sou Tout Promès Bon-Dieu',20,'20_HC_ht','1.\nMouin kanpé sou tout promès\nDé Kris, Roua mouin,\nPandan létènité map chanté louanj Li.\nGloua nan tout sièl la\nSa va sujè chan mouin,\nMouin kanpé sou tout promès Bon-Dieu.\n\n\n\nKè:\nKanpé, kanpé,\nMouin kanpé sou tout promès\nKé Bon-Dieu fè nou,\nKanpé, kanpé,\nM-apé kanpé sou tout promès Bon-Dieu.\n\n2.\nMouin kanpé sou tout promès ki pa fayi\nLè mové tanpèt é tout krint vlé koulé-m,\nPa voua Paròl Bon-Dieu ma sòti vinkè,\nMouin kanpé sou tout promès Bon-Dieu.','<p><strong>1.</strong> </p>\n<p>Mouin kanpé sou tout promès </p>\n<p>Dé Kris, Roua mouin, </p>\n<p>Pandan létènité map chanté louanj Li. </p>\n<p>Gloua nan tout sièl la </p>\n<p>Sa va sujè chan mouin, </p>\n<p>Mouin kanpé sou tout promès Bon-Dieu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kanpé, kanpé,</strong> </p>\n<p><strong>Mouin kanpé sou tout promès</strong> </p>\n<p><strong>Ké Bon-Dieu fè nou,</strong> </p>\n<p><strong>Kanpé, kanpé,</strong> </p>\n<p><strong>M-apé kanpé sou tout promès Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin kanpé sou tout promès ki pa fayi </p>\n<p>Lè mové tanpèt é tout krint vlé koulé-m, </p>\n<p>Pa voua Paròl Bon-Dieu ma sòti vinkè, </p>\n<p>Mouin kanpé sou tout promès Bon-Dieu. </p>\n','Mouin-Kanpe-Sou-Tout-Promes-Bon-Dieu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2046,'4eb74713-1424-42c7-ba6e-29ffcbc246cf',0,0,'Gin Gnou Non Nouvo Ékri Nan La Gloua',71,'71_HC_ht','1.\nMouin Té gnou gran péchè lè-m vini,\nChèché padon nan min Bon-Dieu\nKris tandé priè mouin, é-m trouvé,\nKé Li pap fayi-m janmin.\n\nKè:\nGin gnou non nouvo ékri nan la gloua,\nSé pou mouin, oui, sé pou mouin,\nÉ zanj yo ap rakonté istoua la\n«Gnou lòt péchè sové.»\nGin gnou non nouvo ékri nan la gloua\nSé pou mouin, oui, sé pou mouin.\nPéché-m yo padonnin,\nNan sièl la mouin pralé pou létènité.\n\n2.\nDévan la koua Jézu, mouin bésé,\nPaské sé gnou koupab mouin yé,\nÉpi sièl la ouvri, é mouin ouè,\nKoté yo ékri non mouin.','<p><strong>1.</strong> </p>\n<p>Mouin Té gnou gran péchè lè-m vini, </p>\n<p>Chèché padon nan min Bon-Dieu </p>\n<p>Kris tandé priè mouin, é-m trouvé, </p>\n<p>Ké Li pap fayi-m janmin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gin gnou non nouvo ékri nan la gloua,</strong> </p>\n<p><strong>Sé pou mouin, oui, sé pou mouin,</strong> </p>\n<p><strong>É zanj yo ap rakonté istoua la</strong> </p>\n<p><strong>«Gnou lòt péchè sové.»</strong> </p>\n<p><strong>Gin gnou non nouvo ékri nan la gloua</strong> </p>\n<p><strong>Sé pou mouin, oui, sé pou mouin.</strong> </p>\n<p><strong>Péché-m yo padonnin,</strong> </p>\n<p><strong>Nan sièl la mouin pralé pou létènité.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dévan la koua Jézu, mouin bésé, </p>\n<p>Paské sé gnou koupab mouin yé, </p>\n<p>Épi sièl la ouvri, é mouin ouè, </p>\n<p>Koté yo ékri non mouin. </p>\n','Gin-Gnou-Non-Nouvo-Ekri-Nan-La-Gloua',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2047,'b4e5aa22-2d0e-4051-b72d-6641d1112855',0,0,'Kouté Gnou Nouvèl',8,'8_HC_ht','1.\nKouté gnou nouvèl, li bon é li bèl,\nKé mouin gingnin é m-a ba ou Li\nTout péché mouin fè yo tout padonnin,\nÉ rékonpans mouin nan Jézu-Kri.\nJuska prézan yo gin plas ankò,\nNan palè sélès, rich ann-ò\nToujou rézèvé pou sa yo ki vlé,\nProfité salu Bon-Dieu.\n\n2.\nTout plézi mondèn pa intérésé-m,\nPaské yo pa kapab satisfè,\nMouin pito la pè ké Jézu ba mouin,\nKi va duré pou létènité.\nKoun yé a mouin kontan nan Jézu\nMouin pral nan sièl la gras a Li\nLi toujou gidé-m, Paròl Li édé-m,\nMaché nan la vérité.\n\n3.\nSa pa fè-m angnin si yo jouré mouin\nPaské yo pa konnin bon Ségnè-m.\nLi té chaché mouin é Li sové mouin\nSé amou Li ki andédan kè-m.\nSou tè a m-a gin éprèv isi,\nMin kè mouin nan répo nan Li\nSa va gnou gran joua pou rivé laba\nKoté tout péché fini.','<p><strong>1.</strong> </p>\n<p>Kouté gnou nouvèl, li bon é li bèl, </p>\n<p>Ké mouin gingnin é m-a ba ou Li </p>\n<p>Tout péché mouin fè yo tout padonnin, </p>\n<p>É rékonpans mouin nan Jézu-Kri. </p>\n<p>Juska prézan yo gin plas ankò, </p>\n<p>Nan palè sélès, rich ann-ò </p>\n<p>Toujou rézèvé pou sa yo ki vlé, </p>\n<p>Profité salu Bon-Dieu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout plézi mondèn pa intérésé-m, </p>\n<p>Paské yo pa kapab satisfè, </p>\n<p>Mouin pito la pè ké Jézu ba mouin, </p>\n<p>Ki va duré pou létènité. </p>\n<p>Koun yé a mouin kontan nan Jézu </p>\n<p>Mouin pral nan sièl la gras a Li </p>\n<p>Li toujou gidé-m, Paròl Li édé-m, </p>\n<p>Maché nan la vérité. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa pa fè-m angnin si yo jouré mouin </p>\n<p>Paské yo pa konnin bon Ségnè-m. </p>\n<p>Li té chaché mouin é Li sové mouin </p>\n<p>Sé amou Li ki andédan kè-m. </p>\n<p>Sou tè a m-a gin éprèv isi, </p>\n<p>Min kè mouin nan répo nan Li </p>\n<p>Sa va gnou gran joua pou rivé laba </p>\n<p>Koté tout péché fini. </p>\n','Koute-Gnou-Nouvel',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2048,'853c9ed3-d6b6-4b64-965e-9930b421c427',0,0,'O Sékou',30,'30_HC_ht','1.\nNan mitan gro la mè\nAk tanpèt mouin té yé,\nSan sékou m-té pèdu\nSan la foua san lumiè,\nTout a kou mouin ouè gnou\nGro bato ap pasé\nMouin ranmasé tout fòs mouin\nÉ mouin té rélé: «O sékou! O sékou!\nKi lès ki pou sové-m?  O sékou!»\n\n2.\nSé bato Lévanjil, vapè byin ékipé\nKi tap minnin fidèl yo\nNan rouayòm Bon-Dieu,\nKapitèn bato a ouè éfò mouin tap fè,\nAk gnou min byin an lè\nMouin té krié trè klè: «O sékou! O sékou!\nKi lès ki pou sové-m? O sékou!»\n\n\n3.\nTout suit bato sovtaj\nFè mouin répran-n kouraj,\nNan gran bonté Li,\nChèf la pran ka mouin an chaj,\nKoun yé a mouin kontan\nÉ za fè mouin réglé,\nPaské gran Sovè a rivé koté-m té yé,\nGloua  a Dieu!  Gloua a Dieu!\nKonsa a nan-m mouin chanté:\n«Gloua a Dieu!»\n\n4.\nAmi dékourajé, ki pa konnin la pè,\nKap fè nofraj anba térib la mè péché,\nSégnè mouin dispozé sové ou si ou vlé\nVi-n chèché sékou Li koun yé a san dèlé,\nLi sové-m!  Li sové-m!\nAk la joua map chanté: «Li sové-m!»','<p><strong>1.</strong> </p>\n<p>Nan mitan gro la mè </p>\n<p>Ak tanpèt mouin té yé, </p>\n<p>San sékou m-té pèdu </p>\n<p>San la foua san lumiè, </p>\n<p>Tout a kou mouin ouè gnou </p>\n<p>Gro bato ap pasé </p>\n<p>Mouin ranmasé tout fòs mouin </p>\n<p>É mouin té rélé: «O sékou! O sékou! </p>\n<p>Ki lès ki pou sové-m?  O sékou!» </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé bato Lévanjil, vapè byin ékipé </p>\n<p>Ki tap minnin fidèl yo </p>\n<p>Nan rouayòm Bon-Dieu, </p>\n<p>Kapitèn bato a ouè éfò mouin tap fè, </p>\n<p>Ak gnou min byin an lè </p>\n<p>Mouin té krié trè klè: «O sékou! O sékou! </p>\n<p>Ki lès ki pou sové-m? O sékou!» </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout suit bato sovtaj </p>\n<p>Fè mouin répran-n kouraj, </p>\n<p>Nan gran bonté Li, </p>\n<p>Chèf la pran ka mouin an chaj, </p>\n<p>Koun yé a mouin kontan </p>\n<p>É za fè mouin réglé, </p>\n<p>Paské gran Sovè a rivé koté-m té yé, </p>\n<p>Gloua  a Dieu!  Gloua a Dieu! </p>\n<p>Konsa a nan-m mouin chanté: </p>\n<p>«Gloua a Dieu!» </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ami dékourajé, ki pa konnin la pè, </p>\n<p>Kap fè nofraj anba térib la mè péché, </p>\n<p>Ségnè mouin dispozé sové ou si ou vlé </p>\n<p>Vi-n chèché sékou Li koun yé a san dèlé, </p>\n<p>Li sové-m!  Li sové-m! </p>\n<p>Ak la joua map chanté: «Li sové-m!» </p>\n','O-Sekou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2049,'f4d2b435-6fe8-4c3b-a77d-c230cfbf6a8b',0,0,'Papa-m Ranjé-l Kon sa',39,'39_HC_ht','1.\nSa sa fè si rout la mové\nMin-m si gin mové pa\nLi konnin byin tou sa L-ap fè\nPapa-L ranjé-l konsa.\n\nKè:\nM-a chanté nan solèy, nan fè noua\nNinpòt sa-k rivé, m-a kontan.\nFo-m chanté, mouin pa kapab rété,\nPapa-m ranjé-l konsa.\n\n2.\nSikonstans yo kapab chanjé,\nSa pa fè mouin angnin,\nDèské sé konsa Li chouazi\nPlan Papa-m bon poi mouin.\n\n3.\nLi té déja pasé rout sa\nL-ap édé-m pa-za-pa.\nMouin gin konfians ké santié sa\nAp minnin-m nan sièl la.\n\n4.\nSèlman lumiè ak réjouisans\nAp tan-n mouin a la fin\nDonk mouin pa pè gnou ti soufrans\nPapa-m ranjé li byin.','<p><strong>1.</strong> </p>\n<p>Sa sa fè si rout la mové </p>\n<p>Min-m si gin mové pa </p>\n<p>Li konnin byin tou sa L-ap fè </p>\n<p>Papa-L ranjé-l konsa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>M-a chanté nan solèy, nan fè noua</strong> </p>\n<p><strong>Ninpòt sa-k rivé, m-a kontan.</strong> </p>\n<p><strong>Fo-m chanté, mouin pa kapab rété,</strong> </p>\n<p><strong>Papa-m ranjé-l konsa.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sikonstans yo kapab chanjé, </p>\n<p>Sa pa fè mouin angnin, </p>\n<p>Dèské sé konsa Li chouazi </p>\n<p>Plan Papa-m bon poi mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li té déja pasé rout sa </p>\n<p>L-ap édé-m pa-za-pa. </p>\n<p>Mouin gin konfians ké santié sa </p>\n<p>Ap minnin-m nan sièl la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sèlman lumiè ak réjouisans </p>\n<p>Ap tan-n mouin a la fin </p>\n<p>Donk mouin pa pè gnou ti soufrans </p>\n<p>Papa-m ranjé li byin. </p>\n','Papa-m-Ranje-l-Kon-sa',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2050,'f62d265b-80ce-48a7-a4b5-49cac78b9d0c',0,0,'Alélouya Mouin Jouin-n Jézu',69,'69_HC_ht','1.\nTout la vi mouin, mouin tap soufri,\nM-tap chèché gnou dlo pou-m bouè,\nKi tap soulajé pou tout tan\nSouaf la ki té plin kè mouin.\n\nKè:\nAlélouya, mouin jouin-n Jézu\nKé lontan nan-m mouin chèché,\nJézu satifè tout kè mouin\nSé pa san Li mouin sové.\n\n2.\nManjé lé mon-n té ranpli mouin\nMin mouin pat gin pè dutou\nMouin tap chèché sa ki pi bon\nNan-m mouin té toujou grangou.\n\n3.\nMouin té pòv, lajan m-tap chèché\nGnou bagay pou satisfè-m,\nMin tou sa mouin tap rasanblé,\nPat fè angnin pou nan-m mouin.\n\n4.\nGnou sous dlo ki toujou koulé\nPin la vi si bon, san pri,\nÉ richès ki satisfè mouin,\nMouin jouin-n tou sa nan Jézu.','<p><strong>1.</strong> </p>\n<p>Tout la vi mouin, mouin tap soufri, </p>\n<p>M-tap chèché gnou dlo pou-m bouè, </p>\n<p>Ki tap soulajé pou tout tan </p>\n<p>Souaf la ki té plin kè mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alélouya, mouin jouin-n Jézu</strong> </p>\n<p><strong>Ké lontan nan-m mouin chèché,</strong> </p>\n<p><strong>Jézu satifè tout kè mouin</strong> </p>\n<p><strong>Sé pa san Li mouin sové.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Manjé lé mon-n té ranpli mouin </p>\n<p>Min mouin pat gin pè dutou </p>\n<p>Mouin tap chèché sa ki pi bon </p>\n<p>Nan-m mouin té toujou grangou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin té pòv, lajan m-tap chèché </p>\n<p>Gnou bagay pou satisfè-m, </p>\n<p>Min tou sa mouin tap rasanblé, </p>\n<p>Pat fè angnin pou nan-m mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gnou sous dlo ki toujou koulé </p>\n<p>Pin la vi si bon, san pri, </p>\n<p>É richès ki satisfè mouin, </p>\n<p>Mouin jouin-n tou sa nan Jézu. </p>\n','Alelouya-Mouin-Jouin-n-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2051,'3017b73d-d9a7-4030-a9f1-bf99eb6a2abf',0,0,'A La Gnou Gran La Pli',73,'73_HC_ht','1.\nSa fè sink mil ané pasé Noé bati lach la\nBon-Dieu di: «Map voyé déluj\nPou détoui la tè a.»\nZanimo antré andédan, bétizè yo mandé\nSi Noé pansé fè lach la sou tè sèk dérapé.\nAk fanmi li, Noé antré, Bon-dieu fèmin pòt la\nSièl la vi-n son-m, zéklè pété,\nPasians Bon-Dieu kaba.\n\nKè:\nA la gnou la pli!  (bis)\nLi tonbé pandan karan-n jou,\nSan li pa rété dutou, dutou,\nA la gnou la pli!  (bis)\nMin lach la vouyajé toujou\nA la gnou la pli!\n\n2.\nMoiz alé sou lòd Bon-Dieu\nBa pèp la libèté,\nRoua Éjip déklaré: «Mouin pap kité ésklav ale\nBanboché nan dézè a,\nPuiské you tout parésé,\nO lié mouin lagé yo,\nMa fè travay yo rédoublé.»\nMoiz répon-n: «Ou pral soufri.»\nMoiz min-m pa té pè.\nLi kanpé bò riviè é lévé baton li an lè.\n\nKè:\nA la gnou la pli!  (bis)\nSotrèl, krapo, mouch, pou, ak grèl\nGringolé sou Léjip sòt nan sièl.\nA la gnou la pli!  (bis)\nFaraon pat kab supòté-l, a la gnou la pli!\n\n3.\nNan tan Éli an-n Izraèl lè roua Akab régné,\nSéchrès té rèd, toua zan èdmi\nLa pli pa tonbé,\nAkab banboché sou Kamèl, pandan Éli prie\nSis foua nan poun angnin\nMin Éli pa dékourajé,\nByinto gnou ti nuaj parèt,\nÉli té pran kouri, li di Akab:\n«Ou doué paré pou gnou kokin-n la pli!»\n\nkè:\nA la gnou la pli!  (bis)\nSièl la tou noua, dlo a té frèt\nAkab ran-n kont sé pa gnou jouèt\nA la gnou la pli!  (bis)\nLè li rivé li mouyé nèt.  A la gnou la pli!\n\n4.\nAvan Jézu tounin nan sièl koté Li té sòti\nAk onz apòt yo Li prominnin jouk nan Betani\nLi di: «Lè-m rivé nan sièl la\nMa voyé Sint-Éspri\nDonk, rété a Jéruzalèm\nJouk puisans la vini.»\nSan-vin disip nan chanm-òt la té véyé é prie\nGnou van subit sékoué kay la,\nBénédiksion vidé.\n\nKè:\nA la gnou la pli!  (bis)\nBon-Dieu voyé lé Sint-Éspri\nYo préché chak moun nan lang pa li\nA la gnou la pli!  (bis)\nMésaj répan-n nan tout péyi,\nA la gnou la pli!','<p><strong>1.</strong> </p>\n<p>Sa fè sink mil ané pasé Noé bati lach la </p>\n<p>Bon-Dieu di: «Map voyé déluj </p>\n<p>Pou détoui la tè a.» </p>\n<p>Zanimo antré andédan, bétizè yo mandé </p>\n<p>Si Noé pansé fè lach la sou tè sèk dérapé. </p>\n<p>Ak fanmi li, Noé antré, Bon-dieu fèmin pòt la </p>\n<p>Sièl la vi-n son-m, zéklè pété, </p>\n<p>Pasians Bon-Dieu kaba. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Li tonbé pandan karan-n jou,</strong> </p>\n<p><strong>San li pa rété dutou, dutou,</strong> </p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Min lach la vouyajé toujou</strong> </p>\n<p><strong>A la gnou la pli!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moiz alé sou lòd Bon-Dieu </p>\n<p>Ba pèp la libèté, </p>\n<p>Roua Éjip déklaré: «Mouin pap kité ésklav ale </p>\n<p>Banboché nan dézè a, </p>\n<p>Puiské you tout parésé, </p>\n<p>O lié mouin lagé yo, </p>\n<p>Ma fè travay yo rédoublé.» </p>\n<p>Moiz répon-n: «Ou pral soufri.» </p>\n<p>Moiz min-m pa té pè. </p>\n<p>Li kanpé bò riviè é lévé baton li an lè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Sotrèl, krapo, mouch, pou, ak grèl</strong> </p>\n<p><strong>Gringolé sou Léjip sòt nan sièl.</strong> </p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Faraon pat kab supòté-l, a la gnou la pli!</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan tan Éli an-n Izraèl lè roua Akab régné, </p>\n<p>Séchrès té rèd, toua zan èdmi </p>\n<p>La pli pa tonbé, </p>\n<p>Akab banboché sou Kamèl, pandan Éli prie </p>\n<p>Sis foua nan poun angnin </p>\n<p>Min Éli pa dékourajé, </p>\n<p>Byinto gnou ti nuaj parèt, </p>\n<p>Éli té pran kouri, li di Akab: </p>\n<p>«Ou doué paré pou gnou kokin-n la pli!» </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Sièl la tou noua, dlo a té frèt</strong> </p>\n<p><strong>Akab ran-n kont sé pa gnou jouèt</strong> </p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Lè li rivé li mouyé nèt.  A la gnou la pli!</strong> </p>\n<p><br/> <strong>4.</strong></p>\n<p>Avan Jézu tounin nan sièl koté Li té sòti </p>\n<p>Ak onz apòt yo Li prominnin jouk nan Betani </p>\n<p>Li di: «Lè-m rivé nan sièl la </p>\n<p>Ma voyé Sint-Éspri </p>\n<p>Donk, rété a Jéruzalèm </p>\n<p>Jouk puisans la vini.» </p>\n<p>San-vin disip nan chanm-òt la té véyé é prie </p>\n<p>Gnou van subit sékoué kay la, </p>\n<p>Bénédiksion vidé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Bon-Dieu voyé lé Sint-Éspri</strong> </p>\n<p><strong>Yo préché chak moun nan lang pa li</strong> </p>\n<p><strong>A la gnou la pli!  (bis)</strong> </p>\n<p><strong>Mésaj répan-n nan tout péyi,</strong> </p>\n<p><strong>A la gnou la pli!</strong> </p>\n','A-La-Gnou-Gran-La-Pli',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2052,'79ff610a-ed05-433b-8b46-d87d3b5e22b9',0,0,'Dieu Damou',85,'85_HC_ht','1.\nDieu damoui!  Sièl la va di li,\nBèl zétoual manifésté li,\nDieu damou! Réjoui, chanté nan-m mouin\nTout koli-n répété gloua Li\nMin-m ravi-n chanté istoua Li,\nDieu damou!  Ranfòsé bèl réfrin.\nKouté lè van an ap souflé\nDousman nan pié boua\nTandé rosignol la prononsé, Alélouya\nEsk ou tandé chan y-ap chanté?\nKréasion ap manifésté,\nDieu damou Li sé gnou Dieu damou.\n\n2.\nDieu damou!  Anonsé nouvèl,\nSé mésaj ki pi ésansièl,\nDieu damou! Bib la anonsé Li\nJézu-Kri gran Kréatè a\nTé vini kom Rédanmtè a,\nNan plas nou Li aksépté soufri.\nNan gnou tribunal Romin yo kondané Jézu,\nLéz an-m égaré té méprizé gras é salu\nSou la koua Li ba nou la vi,\nPa la foua nou aksépté Li,\nPa la foua nou aksépté Li,\nDieu damou! Li sé gnou Dieu damou!','<p><strong>1.</strong> </p>\n<p>Dieu damoui!  Sièl la va di li, </p>\n<p>Bèl zétoual manifésté li, </p>\n<p>Dieu damou! Réjoui, chanté nan-m mouin </p>\n<p>Tout koli-n répété gloua Li </p>\n<p>Min-m ravi-n chanté istoua Li, </p>\n<p>Dieu damou!  Ranfòsé bèl réfrin. </p>\n<p>Kouté lè van an ap souflé </p>\n<p>Dousman nan pié boua </p>\n<p>Tandé rosignol la prononsé, Alélouya </p>\n<p>Esk ou tandé chan y-ap chanté? </p>\n<p>Kréasion ap manifésté, </p>\n<p>Dieu damou Li sé gnou Dieu damou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dieu damou!  Anonsé nouvèl, </p>\n<p>Sé mésaj ki pi ésansièl, </p>\n<p>Dieu damou! Bib la anonsé Li </p>\n<p>Jézu-Kri gran Kréatè a </p>\n<p>Té vini kom Rédanmtè a, </p>\n<p>Nan plas nou Li aksépté soufri. </p>\n<p>Nan gnou tribunal Romin yo kondané Jézu, </p>\n<p>Léz an-m égaré té méprizé gras é salu </p>\n<p>Sou la koua Li ba nou la vi, </p>\n<p>Pa la foua nou aksépté Li, </p>\n<p>Pa la foua nou aksépté Li, </p>\n<p>Dieu damou! Li sé gnou Dieu damou! </p>\n','Dieu-Damou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2053,'0269f201-eb37-4372-b212-ee637f66689f',0,0,'Pou Ki \\Moun Pale Kristal Sa-a',7,'7_RN_ht','1.\nPou ki moun pale Kristal sa-a\nSe pou mwen ak Jezu kri,\nPou ki moun fotey an no yo?\nSe pou mwen ak rachte yo\n \nRefrain:\nSe yon bel pale Kristal\nAvek anpil chez an nor\nPou tout moun ki vle ale\nAvek Segnè Jezu\n \n2.\nJerizalem nan bel anpil,\nLi pi bel pase dyaman\nLi soti kote Bondye minm\nE li gen tout glwa bondye\n \n3.\nPa bezwen soley al lali-n\nPou klere nou jou e nwit\nMin la glwa Bondye va briye\nEkla li va yon flanbo\n \n4.\nPap gen krint, doule, a touman\nNi lanmo, ni malady\nMedizans, koutlang va fini\nSe lajwa lanmou san fin\n \n5.\nAla yon bel jou, sa va ye,\nLè tronpet la va sonnin\nPou tout mo nan Kris leve\nPou y-al rankontre Jezu','<p><strong>1.</strong> </p>\n<p>Pou ki moun pale Kristal sa-a </p>\n<p>Se pou mwen ak Jezu kri, </p>\n<p>Pou ki moun fotey an no yo? </p>\n<p>Se pou mwen ak rachte yo </p>\n<p>Refrain: </p>\n<p>Se yon bel pale Kristal </p>\n<p>Avek anpil chez an nor </p>\n<p>Pou tout moun ki vle ale </p>\n<p>Avek Segnè Jezu </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jerizalem nan bel anpil, </p>\n<p>Li pi bel pase dyaman </p>\n<p>Li soti kote Bondye minm </p>\n<p>E li gen tout glwa bondye </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pa bezwen soley al lali-n </p>\n<p>Pou klere nou jou e nwit </p>\n<p>Min la glwa Bondye va briye </p>\n<p>Ekla li va yon flanbo </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pap gen krint, doule, a touman </p>\n<p>Ni lanmo, ni malady </p>\n<p>Medizans, koutlang va fini </p>\n<p>Se lajwa lanmou san fin </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ala yon bel jou, sa va ye, </p>\n<p>Lè tronpet la va sonnin </p>\n<p>Pou tout mo nan Kris leve </p>\n<p>Pou y-al rankontre Jezu </p>\n','Pou-Ki-Moun-Pale-Kristal-Sa-a',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2054,'ef1c430b-0565-456f-9a63-7cb8c06b0437',0,0,'M’ap Viv Pou Jezu',10,'10_RN_ht','1.\nM’ap viv pou Jezu\nJou aprè jou\nM’ap viv pou Jezu\nNenpot ki lè\nLe Sentespri la,\nMwen va gen pè\nM’ap viv pou Jezu\nJou aprè jou.','<p><strong>1.</strong> </p>\n<p>M’ap viv pou Jezu </p>\n<p>Jou aprè jou </p>\n<p>M’ap viv pou Jezu </p>\n<p>Nenpot ki lè </p>\n<p>Le Sentespri la, </p>\n<p>Mwen va gen pè </p>\n<p>M’ap viv pou Jezu </p>\n<p>Jou aprè jou. </p>\n','M\'ap-Viv-Pou-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2055,'f9d4c065-de7d-4014-bbbf-baf6614463b7',0,0,'O mon frère, connais-tu Jésus?',67,'67_MJ_fr','1.\nO mon frère, connais-tu Jésus?\nAs-tu entendu Sa voix?\nIl est le Rocher du salut.\nIl y a du miel en Christ pour toi.\n\nChoeur:\nOh! Du miel dans le Rocher,\nO mon frère,\nDu miel dans le Rocher pour toi,\nApporte tes péchés au Calvaire;\nIl y a du miel en Christ pour toi.\n\n2.\nAs-tu trouvé cet ami fidèle?\nDe Lui as-tu fait ton choix?\nMarches-tu dans la vie nouvelle?\nIl y a du miel dans le Rocher pour toi.\n\n3.\nPries-tu par Jésus, Médiateur?\nEn Lui Dieu entend ta voix.\nDis-Lui les soupirs de ton coeur;\nIl y a du miel dans le Rocher pour toi.\n\n4.\nO mon frère, le long du chemin,\nTous les jours et sans effroi.\nDis-le à chaque pèlerin:\nIl y a du miel dans le Rocher pour toi.','<p><strong>1.</strong> </p>\n<p>O mon frère, connais-tu Jésus? </p>\n<p>As-tu entendu Sa voix? </p>\n<p>Il est le Rocher du salut. </p>\n<p>Il y a du miel en Christ pour toi. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oh! Du miel dans le Rocher,</strong> </p>\n<p><strong>O mon frère,</strong> </p>\n<p><strong>Du miel dans le Rocher pour toi,</strong> </p>\n<p><strong>Apporte tes péchés au Calvaire;</strong> </p>\n<p><strong>Il y a du miel en Christ pour toi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>As-tu trouvé cet ami fidèle? </p>\n<p>De Lui as-tu fait ton choix? </p>\n<p>Marches-tu dans la vie nouvelle? </p>\n<p>Il y a du miel dans le Rocher pour toi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pries-tu par Jésus, Médiateur? </p>\n<p>En Lui Dieu entend ta voix. </p>\n<p>Dis-Lui les soupirs de ton coeur; </p>\n<p>Il y a du miel dans le Rocher pour toi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O mon frère, le long du chemin, </p>\n<p>Tous les jours et sans effroi. </p>\n<p>Dis-le à chaque pèlerin: </p>\n<p>Il y a du miel dans le Rocher pour toi. </p>\n','O-mon-frere-connais-tu-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2056,'5eae9d95-e1a8-49ea-9861-5afa512cc0c9',0,0,'Ce que tu fais, ce que tu dis',70,'70_MJ_fr','1.\nCe que tu fais, ce que tu dis,\nJésus le voit, Sa main l\'écrit (oh, oui)\nCe que tu fais, ce que tu dis,\nJésus le voit, Sa main l\'écrit!','<p><strong>1.</strong> </p>\n<p>Ce que tu fais, ce que tu dis, </p>\n<p>Jésus le voit, Sa main l&#39;écrit (oh, oui) </p>\n<p>Ce que tu fais, ce que tu dis, </p>\n<p>Jésus le voit, Sa main l&#39;écrit! </p>\n','Ce-que-tu-fais-ce-que-tu-dis',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2057,'f8fa6aef-bebe-4601-800f-caa3764e4ac5',0,0,'Courez au but! Courez, amis',75,'75_MJ_fr','1.\nCourez au but! Courez, amis,\nSi vous voulez gagner le prix!\nLes yeux sur Christ,\nIl faut, pour bien courir,\nÊtre patients, le péché haïr!','<p><strong>1.</strong> </p>\n<p>Courez au but! Courez, amis, </p>\n<p>Si vous voulez gagner le prix! </p>\n<p>Les yeux sur Christ, </p>\n<p>Il faut, pour bien courir, </p>\n<p>Être patients, le péché haïr! </p>\n','Courez-au-but!-Courez-amis',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2058,'415a4eff-64c7-4b31-970a-cc54b47621cd',0,0,'A nos cotés, laissons la foule',89,'89_MJ_fr','1.A nos côtés, laissons la foulePasse comme un torrent,Le flot la prend, le flot la roule,Brisons ce fort courant:Choeur:Nous lutterons seuls contre tous,Pourvu que Dieu soit avec nous.  (bis)2.Parmi l\'erreur, parmi les doute,Fidèle et pas à pasLa vérité poursuit sa routeAu prix de longs combats.3.De Gédéon les camaradesÉtaient en tout trois cents,Mais ils valaient des myriadesTant ils étaient vaillants.4.Fuyons la foule au loin menéeAinsi qu\'un vil troupeau,Et joignons-nous à la poignéeDe braves, de héros.','<p><strong>1.</strong> </p>\n<p>A nos côtés, laissons la foule </p>\n<p>Passe comme un torrent, </p>\n<p>Le flot la prend, le flot la roule, </p>\n<p>Brisons ce fort courant: </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Nous lutterons seuls contre tous,</strong> </p>\n<p><strong>Pourvu que Dieu soit avec nous.  (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Parmi l&#39;erreur, parmi les doutes, </p>\n<p>Fidèle et pas à pas </p>\n<p>La vérité poursuit sa route </p>\n<p>Au prix de longs combats. </p>\n<p><br/> <strong>3.</strong></p>\n<p>De Gédéon les camarades </p>\n<p>Étaient en tout trois cents, </p>\n<p>Mais ils valaient des myriades </p>\n<p>Tant ils étaient vaillants. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Fuyons la foule au loin menée </p>\n<p>Ainsi qu&#39;un vil troupeau, </p>\n<p>Et joignons-nous à la poignée </p>\n<p>De braves, de héros. </p>\n','A-nos-cotes-laissons-la-foule',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2059,'46e3c90f-7cf5-4ec8-844e-98c258dbb004',0,0,'La B-I-B-L-E',105,'105_MJ_fr','1.\nLa B-I-B-L-E,\nQuel livre merveilleux!\nJe m\'y tiens ferme,\nJe crois toujours\nA la B-I-B-L-E.\n\n2.\nPar son S-A-N-G,\nJésus m\'a libéré,\nCar sur la croix\nIl est mort pour moi,\nSon sang m\'a racheté.\n\n3.\nSa G-R-A-C-E me S-A-U-V-E,\nLe moyen, c\'est par la F-O-I,\nDit la B-I-B-L-E.','<p><strong>1.</strong> </p>\n<p>La B-I-B-L-E, </p>\n<p>Quel livre merveilleux! </p>\n<p>Je m&#39;y tiens ferme, </p>\n<p>Je crois toujours </p>\n<p>A la B-I-B-L-E. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Par son S-A-N-G, </p>\n<p>Jésus m&#39;a libéré, </p>\n<p>Car sur la croix </p>\n<p>Il est mort pour moi, </p>\n<p>Son sang m&#39;a racheté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa G-R-A-C-E me S-A-U-V-E, </p>\n<p>Le moyen, c&#39;est par la F-O-I, </p>\n<p>Dit la B-I-B-L-E. </p>\n','La-B-I-B-L-E',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2060,'ecbb88f1-3ce0-4f7e-9521-2a24953c2520',0,0,'Tout ton coeur, tout ton coeur',118,'118_MJ_fr','1.\nTout ton coeur, tout ton coeur,\nJésus veut demeurer dans ton coeur;\nDans ton coeur, tout ton coeur,\nJésus veut demeurer dans ton coeur.\n\n2.\nTout mon coeur, tout mon coeur,\nJésus veut demeurer dans mon coeur;\nDans mon coeur, tout mon coeur,\nViens, Jésus, demeurer dans mon coeur.','<p><strong>1.</strong> </p>\n<p>Tout ton coeur, tout ton coeur, </p>\n<p>Jésus veut demeurer dans ton coeur; </p>\n<p>Dans ton coeur, tout ton coeur, </p>\n<p>Jésus veut demeurer dans ton coeur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout mon coeur, tout mon coeur, </p>\n<p>Jésus veut demeurer dans mon coeur; </p>\n<p>Dans mon coeur, tout mon coeur, </p>\n<p>Viens, Jésus, demeurer dans mon coeur. </p>\n','Tout-ton-coeur-tout-ton-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2061,'e45863aa-898b-4b3a-9b72-10687b482013',0,0,'Si tu confesses tes péchés',139,'139_MJ_fr','1.\nSi tu confesse tes péchés, (ter)\nJe te pardonnerai.  (1 Jean 1:9)\n\n2.\nLe sang de Christ peut rendre pur, (ter)\nLe coeur le plus mauvais.  (1 Jean 1:7)\n\n3.\nJe garderai ton coeur en paix, (ter)\nEn moi, confie-toi.  (Esa. 31:15)\n\n4.\nDans ta faiblesse, mon enfant,  (ter)\nMa grâce te suffit.  (II Cor. 12:9)\n\n5.\nEt si tu passes par le feu,  (ter)\nAvec toi je serai.  (Esa. 43:2)','<p><strong>1.</strong> </p>\n<p>Si tu confesse tes péchés, (ter) </p>\n<p>Je te pardonnerai.  (1 Jean 1:9) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le sang de Christ peut rendre pur, (ter) </p>\n<p>Le coeur le plus mauvais.  (1 Jean 1:7) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je garderai ton coeur en paix, (ter) </p>\n<p>En moi, confie-toi.  (Esa. 31:15) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dans ta faiblesse, mon enfant,  (ter) </p>\n<p>Ma grâce te suffit.  (II Cor. 12:9) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Et si tu passes par le feu,  (ter) </p>\n<p>Avec toi je serai.  (Esa. 43:2) </p>\n','Si-tu-confesses-tes-peches',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2062,'619a9f1f-d3b7-4ea1-8b82-7ce315718f4e',0,0,'Levez-vous, témoins de la Bible',141,'141_MJ_fr','1.\nLevez-vous, témoins de la Bible!\nPriez et luttez en tout lieu!\nProclamez qu\'elle est infaillible,\nLa Parole de notre Dieu!','<p><strong>1.</strong> </p>\n<p>Levez-vous, témoins de la Bible! </p>\n<p>Priez et luttez en tout lieu! </p>\n<p>Proclamez qu&#39;elle est infaillible, </p>\n<p>La Parole de notre Dieu! </p>\n','Levez-vous-temoins-de-la-Bible',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2063,'adfb7066-ba5e-43d8-844d-dd288d71e4ce',0,0,'C\'est la foi qui vient dans mon cœur',168,'168_MJ_fr','1.\nC\'est la foi qui vient dans mon coeur, (bis)\nQuand Jésus passe et qu\'Il fait grâce,\nLa foi vient alors habiter dans mon coeur.\n\n2.\nC\'est la joie qui vient dans mon coeur, (bis)\nQuand Jésus passe et qu\'Il fait grâce,\nLa joie vient alors habiter dans mon coeur.\n\n3.\nC\'est l\'amour qui vient dans mon coeur, (bis)\nQuand Jésus passe et qu\'Il fait grâce,\nL\'amour vient alors habiter dans mon coeur.','<p><strong>1.</strong> </p>\n<p>C&#39;est la foi qui vient dans mon coeur, (bis) </p>\n<p>Quand Jésus passe et qu&#39;Il fait grâce, </p>\n<p>La foi vient alors habiter dans mon coeur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est la joie qui vient dans mon coeur, (bis) </p>\n<p>Quand Jésus passe et qu&#39;Il fait grâce, </p>\n<p>La joie vient alors habiter dans mon coeur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>C&#39;est l&#39;amour qui vient dans mon coeur, (bis) </p>\n<p>Quand Jésus passe et qu&#39;Il fait grâce, </p>\n<p>L&#39;amour vient alors habiter dans mon coeur. </p>\n','C\'est-la-foi-qui-vient-dans-mon-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2064,'d3857e9a-15ea-4f2e-8f58-180c995d8182',0,0,'Dans l\'amour divin cache',171,'171_MJ_fr','1.\nDans l\'amour divin caché\nMon coeur n\'a nulle crainte\nCet abri m\'est assuré\nPar sa Parole sainte;\nQuand l\'orage fait rage\nMon coeur est abattu\nMais mon Dieu m\'encourage\nEt je ne doute plus.\n\n2.\nMon Berger me dirige\nJe Le suivrai partout\nChaque jour Il me soigne\nDans Son amour si doux.\nSa sagesse infinie –\nSa force me soutient\nDésormais que ma vie\nSuivre son seul dessein.\n\n3.\nDans de verts pâturages\nIl me fait reposer,\nCar les sombres nuages\nDe mon ciel passer.\nMon espérance est sûre\nMon destin est certain\nSa Parole m\'assure\nPourquoi craindre demain?','<p><strong>1.</strong> </p>\n<p>Dans l&#39;amour divin caché </p>\n<p>Mon coeur n&#39;a nulle crainte </p>\n<p>Cet abri m&#39;est assuré </p>\n<p>Par sa Parole sainte; </p>\n<p>Quand l&#39;orage fait rage </p>\n<p>Mon coeur est abattu </p>\n<p>Mais mon Dieu m&#39;encourage </p>\n<p>Et je ne doute plus. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mon Berger me dirige </p>\n<p>Je Le suivrai partout </p>\n<p>Chaque jour Il me soigne </p>\n<p>Dans Son amour si doux. </p>\n<p>Sa sagesse infinie – </p>\n<p>Sa force me soutient </p>\n<p>Désormais que ma vie </p>\n<p>Suivre son seul dessein. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans de verts pâturages </p>\n<p>Il me fait reposer, </p>\n<p>Car les sombres nuages </p>\n<p>De mon ciel passer. </p>\n<p>Mon espérance est sûre </p>\n<p>Mon destin est certain </p>\n<p>Sa Parole m&#39;assure </p>\n<p>Pourquoi craindre demain? </p>\n','Dans-l\'amour-divin-cache',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2065,'7c8d50e9-e6bb-4a45-9a8a-e9e39bababc2',0,0,'Les anges dans nos campagnes',179,'179_MJ_fr','1.\nLes anges dans nos campagnes\nOnt entonné des choeurs joyeux\nEt l\'écho de nos montagnes\nRedit ce chant mélodieux;\n\nChoeur:\nGloria in excelsis Deo!\nGloria in excelsis Deo!\n\n2.\nBergers, grande est la nouvelle;\nLe Christ est né, le Dieu Sauveur\nVenez, le ciel vous appelle\nA rendre hommage au Rédempteur!\n\n3.\nVers l\'Enfant qui vient de naître,\nAccourons tous avec bonheur!\nLe ciel nousl\'a fait connaître;\nAmour au Christ, au Dieu Sauveur.','<p><strong>1.</strong> </p>\n<p>Les anges dans nos campagnes </p>\n<p>Ont entonné des choeurs joyeux </p>\n<p>Et l&#39;écho de nos montagnes </p>\n<p>Redit ce chant mélodieux; </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Gloria in excelsis Deo!</strong> </p>\n<p><strong>Gloria in excelsis Deo!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bergers, grande est la nouvelle; </p>\n<p>Le Christ est né, le Dieu Sauveur </p>\n<p>Venez, le ciel vous appelle </p>\n<p>A rendre hommage au Rédempteur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vers l&#39;Enfant qui vient de naître, </p>\n<p>Accourons tous avec bonheur! </p>\n<p>Le ciel nousl&#39;a fait connaître; </p>\n<p>Amour au Christ, au Dieu Sauveur. </p>\n','Les-anges-dans-nos-campagnes',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2066,'42f075ca-721c-4ce8-835a-2b274ba44995',0,0,'Jour de bonheur, jour mémorable',180,'180_MJ_fr','1.\nJour de bonheur, jour mémorable,\nNoël est de retour.\nEt vers la crèche de l\'étable,\nJoyeux, le monde accourt,  (bis)\nJoyeux, joyeux, le monde accourt.\n\n2.\nDans cette crèche est le Messie,\nQue tout Son peuple attend,\nPar la divine prophétie\nPromis depuis longtemps,  (bis)\nPromis, promis depuis longtemps.\n\n3.\nIl est un Roi, mais sans couronne,\nLes humbles sont Sa cour;\nC\'est dans les coeurs qu\'Il a Son trône,\nSa loi n\'est que d\'amour,  (bis)\nSa loi, Sa loi n\'est que d\'amour.\n\n4.\nRoi désiré, que notre monde,\nDont l\'espoir n\'est qu\'en toi,\nA ton appel d\'amour réponde:\n«Jésus, sois notre Roi!»  (bis)\nJésus, Jésus sois notre Roi!','<p><strong>1.</strong> </p>\n<p>Jour de bonheur, jour mémorable, </p>\n<p>Noël est de retour. </p>\n<p>Et vers la crèche de l&#39;étable, </p>\n<p>Joyeux, le monde accourt,  (bis) </p>\n<p>Joyeux, joyeux, le monde accourt. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans cette crèche est le Messie, </p>\n<p>Que tout Son peuple attend, </p>\n<p>Par la divine prophétie </p>\n<p>Promis depuis longtemps,  (bis) </p>\n<p>Promis, promis depuis longtemps. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il est un Roi, mais sans couronne, </p>\n<p>Les humbles sont Sa cour; </p>\n<p>C&#39;est dans les coeurs qu&#39;Il a Son trône, </p>\n<p>Sa loi n&#39;est que d&#39;amour,  (bis) </p>\n<p>Sa loi, Sa loi n&#39;est que d&#39;amour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Roi désiré, que notre monde, </p>\n<p>Dont l&#39;espoir n&#39;est qu&#39;en toi, </p>\n<p>A ton appel d&#39;amour réponde: </p>\n<p>«Jésus, sois notre Roi!»  (bis) </p>\n<p>Jésus, Jésus sois notre Roi! </p>\n','Jour-de-bonheur-jour-memorable',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2067,'48a1fd2b-dc0f-4f6b-8780-c6fc4f65e99d',0,0,'Suiv Mouin',31,'31_HC_ht','1.\nMouin tap fè gnou rout difisil\nÉ-m té santi tou sèl.\nChay lou ki té sou do mouin\nTé bésé-m tèt anba\nMouin té plingnin a Jézu\nKouman moun maltrété-m,\nLè sa mouin té tandé Li di dousman:\n«Pié-m té fatigé osi\nSou rout mòn Kalvè a,\nKoua la tè vini si lou,\nChay la té fè-m tonbé.\nSouayé fidèl pèlérin\nSièl la mouin kapab ouè,\nLévé koua ou é suiv mouin pa-za-pa.\n\n2.\nM-ap travay du pou Jézu,\nMouin té di trè souvan,\nMouin gin tan fè gran sakrifis,\nPou maché avèk li,\nMouin té lésé gro lajan,\nMouin vo anpil, m-té di:\nÉ mouin tandé Li palé mouin dousman.\nMouin té kité la gloua mouin,\nMouin gadé-l kòm gnou pèd.\nYo klouré-m sou boua Kalvè.\nMouin fè tout sa pou ou.\nAnnou maché byin ansan-m\nNan chémin la vi sa a,\nPran la koua ou\nÉ suiv mouin jouk la fin.','<p><strong>1.</strong> </p>\n<p>Mouin tap fè gnou rout difisil </p>\n<p>É-m té santi tou sèl. </p>\n<p>Chay lou ki té sou do mouin </p>\n<p>Té bésé-m tèt anba </p>\n<p>Mouin té plingnin a Jézu </p>\n<p>Kouman moun maltrété-m, </p>\n<p>Lè sa mouin té tandé Li di dousman: </p>\n<p>«Pié-m té fatigé osi </p>\n<p>Sou rout mòn Kalvè a, </p>\n<p>Koua la tè vini si lou, </p>\n<p>Chay la té fè-m tonbé. </p>\n<p>Souayé fidèl pèlérin </p>\n<p>Sièl la mouin kapab ouè, </p>\n<p>Lévé koua ou é suiv mouin pa-za-pa. </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-ap travay du pou Jézu, </p>\n<p>Mouin té di trè souvan, </p>\n<p>Mouin gin tan fè gran sakrifis, </p>\n<p>Pou maché avèk li, </p>\n<p>Mouin té lésé gro lajan, </p>\n<p>Mouin vo anpil, m-té di: </p>\n<p>É mouin tandé Li palé mouin dousman. </p>\n<p>Mouin té kité la gloua mouin, </p>\n<p>Mouin gadé-l kòm gnou pèd. </p>\n<p>Yo klouré-m sou boua Kalvè. </p>\n<p>Mouin fè tout sa pou ou. </p>\n<p>Annou maché byin ansan-m </p>\n<p>Nan chémin la vi sa a, </p>\n<p>Pran la koua ou </p>\n<p>É suiv mouin jouk la fin. </p>\n','Suiv-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2068,'2be684ea-1a9a-4929-8e36-414cf0cc27e9',0,0,'Amou Si Mèvéyé',35,'35_HC_ht','1.\nSé lamou ki fè Sovè a kité kay sièl la,\nFèt sou tè a kom gnou bébé\nViv é mouri sou la koua.\n\nKè:\nAmou si mèvéyé, amou ki pa bòné,\nAmou ki anbrazé soufrans lan\nAmou ki dépozé-l nan krèch la\nAmou si mèvéyé, sé lamou Bon-Dieu.\n\n2.\nSé lamou ki fè li san kay fè Noèl sa a\nPandan zanj yo chanté an lè\nÉ yo réyonnin nuit la.\n\n3.\nSé lamou ki fè Li dispozé kité tron-n Li\nPou vini gnou Sèvitè\nÉ pou rachté sa ki pèdu.','<p><strong>1.</strong> </p>\n<p>Sé lamou ki fè Sovè a kité kay sièl la, </p>\n<p>Fèt sou tè a kom gnou bébé </p>\n<p>Viv é mouri sou la koua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Amou si mèvéyé, amou ki pa bòné,</strong> </p>\n<p><strong>Amou ki anbrazé soufrans lan</strong> </p>\n<p><strong>Amou ki dépozé-l nan krèch la</strong> </p>\n<p><strong>Amou si mèvéyé, sé lamou Bon-Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé lamou ki fè li san kay fè Noèl sa a </p>\n<p>Pandan zanj yo chanté an lè </p>\n<p>É yo réyonnin nuit la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé lamou ki fè Li dispozé kité tron-n Li </p>\n<p>Pou vini gnou Sèvitè </p>\n<p>É pou rachté sa ki pèdu. </p>\n','Amou-Si-Meveye',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2069,'3cbdcbbf-32cf-4ad6-a67f-762c3bd5cc06',0,0,'A La Gnou Mèvéyé  Mévéyé Jou',45,'45_HC_ht','1.\nA la gnou mèvéyé, mèvéyé jou,\nJou m-pa kab janmin blié\nLè-m té nan ténèb, pèdu nan la bou,\nJézu Sovè-m mouin kontré,\nA la gnou zanmi ki gin charité,\nLi satisfè-m ak gras Li\nLi pran tristès mouin, Li ba-m alégrès,\nÉ Li fè tout ténèb mouin pati.\n\nKè:\nSièl la désan’n, Li plin nan’m mouin ak la joua\nLè  Sovè mouin té sové-m a la koua\nPéché-m tout padonnin,\nTénèb nan lumiè tounin,\nSièl la désan’n, Li plin nan’m mouin ak la joua\n\n2.\nFèt dé nouvo pa puisans Léspri sin,\nLi fè-m gnou pitit Bon-Dieu\nAmou Kalvè justifié-m, sé sètin,\nLi ba-m gnou chan glorié.\nTravay sa té fè nan gnou ti moman\nLè-m répanti nan péché-m\nLè-m aksépté Li é lè-m konfésé Li,\nO béni non Li, Li sové-m.','<p><strong>1.</strong> </p>\n<p>A la gnou mèvéyé, mèvéyé jou, </p>\n<p>Jou m-pa kab janmin blié </p>\n<p>Lè-m té nan ténèb, pèdu nan la bou, </p>\n<p>Jézu Sovè-m mouin kontré, </p>\n<p>A la gnou zanmi ki gin charité, </p>\n<p>Li satisfè-m ak gras Li </p>\n<p>Li pran tristès mouin, Li ba-m alégrès, </p>\n<p>É Li fè tout ténèb mouin pati. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sièl la désan’n, Li plin nan’m mouin ak la joua</strong> </p>\n<p><strong>Lè  Sovè mouin té sové-m a la koua</strong> </p>\n<p><strong>Péché-m tout padonnin,</strong> </p>\n<p><strong>Ténèb nan lumiè tounin,</strong> </p>\n<p><strong>Sièl la désan’n, Li plin nan’m mouin ak la joua</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fèt dé nouvo pa puisans Léspri sin, </p>\n<p>Li fè-m gnou pitit Bon-Dieu </p>\n<p>Amou Kalvè justifié-m, sé sètin, </p>\n<p>Li ba-m gnou chan glorié. </p>\n<p>Travay sa té fè nan gnou ti moman </p>\n<p>Lè-m répanti nan péché-m </p>\n<p>Lè-m aksépté Li é lè-m konfésé Li, </p>\n<p>O béni non Li, Li sové-m. </p>\n','A-La-Gnou-Meveye-Meveye-Jou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2070,'4593d660-c504-46bc-a55e-eae29598ade1',0,0,'Chémin An Pa Fasil',47,'47_HC_ht','1.\nChémin an pa fasil,\nJouk nou rivé nan sièl la\nLi gingnin pikan é pièj anpil\nChémin an pa fasil,\nMin Jézu maché ak nou,\nLi ran-n kè nou tout afè trankil.\n\nKè:\nNon, non, chémin an pa fasil,\nNon, non, chémin an pa fasil,\nMin Jézu maché ak nou,\nLi poté fado nou,\nGras Li fè kè nou réjoui anpil.\n\n\n2.\nChémin an pa fsil\nGin éprèv, gin traka,\nTout koté nou pasé gin danjé,\nRout la mèt difisil,\nSi Jézu kinbé min nou\nN-a rété fè-m lè pié nou glisé.\n\n3.\nKom nou kon-n fatigé,\nDékourajé nan rout la,\nÉpi Satan kon-n vi-n tanté nou,\nMin napé tan-n lé Ségnè,\nKap parèt nan gloua Li,\nLapé pran nou ak Li gnou bèl jou.','<p><strong>1.</strong> </p>\n<p>Chémin an pa fasil, </p>\n<p>Jouk nou rivé nan sièl la </p>\n<p>Li gingnin pikan é pièj anpil </p>\n<p>Chémin an pa fasil, </p>\n<p>Min Jézu maché ak nou, </p>\n<p>Li ran-n kè nou tout afè trankil. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Non, non, chémin an pa fasil,</strong> </p>\n<p><strong>Non, non, chémin an pa fasil,</strong> </p>\n<p><strong>Min Jézu maché ak nou,</strong> </p>\n<p><strong>Li poté fado nou,</strong> </p>\n<p><strong>Gras Li fè kè nou réjoui anpil.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chémin an pa fsil </p>\n<p>Gin éprèv, gin traka, </p>\n<p>Tout koté nou pasé gin danjé, </p>\n<p>Rout la mèt difisil, </p>\n<p>Si Jézu kinbé min nou </p>\n<p>N-a rété fè-m lè pié nou glisé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kom nou kon-n fatigé, </p>\n<p>Dékourajé nan rout la, </p>\n<p>Épi Satan kon-n vi-n tanté nou, </p>\n<p>Min napé tan-n lé Ségnè, </p>\n<p>Kap parèt nan gloua Li, </p>\n<p>Lapé pran nou ak Li gnou bèl jou. </p>\n','Chemin-An-Pa-Fasil',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2071,'cf44582a-397b-40ae-9b5d-b91b4eb2d35b',0,0,'Mèvéyé  Abondan Gras Bon-Dieu',48,'48_HC_ht','1.\nMèvéyé, abondan gras Bon-Dieu\nGras ki pi gran ké tout péché nou.\nSou koua Kalvé Jézu krusifié\nSan Li té vèsé la pou nou tout.\n\nKè:\nGras, gras, gras Bon-Dieu\nGras ki padonnin é purifié,\nGras, gras, gras Bon-Dieu.\nGras ki pi gran ké tout péché.\n\n2.\nPéché ki gran kou gro vag la mè\nMénasé nan-m nou pou nou pèdu,\nGras ki pi gran, oui, gras Li si chè,\nMontré nou gnou réfuj nan koua Li.\n\n3.\nMèvéyé gras Li ki san mézu,\nBon-Dieu bay li pou tout moun ki kouè,\nOu, ki gnou jou vlé ouè fas Jézu,\nEské oua résévoua gras Kalvè.','<p><strong>1.</strong> </p>\n<p>Mèvéyé, abondan gras Bon-Dieu </p>\n<p>Gras ki pi gran ké tout péché nou. </p>\n<p>Sou koua Kalvé Jézu krusifié </p>\n<p>San Li té vèsé la pou nou tout. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gras, gras, gras Bon-Dieu</strong> </p>\n<p><strong>Gras ki padonnin é purifié,</strong> </p>\n<p><strong>Gras, gras, gras Bon-Dieu.</strong> </p>\n<p><strong>Gras ki pi gran ké tout péché.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Péché ki gran kou gro vag la mè </p>\n<p>Ménasé nan-m nou pou nou pèdu, </p>\n<p>Gras ki pi gran, oui, gras Li si chè, </p>\n<p>Montré nou gnou réfuj nan koua Li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mèvéyé gras Li ki san mézu, </p>\n<p>Bon-Dieu bay li pou tout moun ki kouè, </p>\n<p>Ou, ki gnou jou vlé ouè fas Jézu, </p>\n<p>Eské oua résévoua gras Kalvè. </p>\n','Meveye-Abondan-Gras-Bon-Dieu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2072,'32db8ed1-d094-4b4c-92d0-d908d35e7c5f',0,0,'Lè Zouazo Tonbé Bon-Dieu Ouè-l',55,'55_HC_ht','1.\nLè zouazo tonbé Bon-Dieu ouè-l,\nLi véyé yo toujou\nSi Bon-Dieu rinmin ti zouazo\nMouin konnin-L rinmin-m tou.\n\nKè:\nLi rinmin-m tou, Li rinmin-m tou,\nMouin konnin-L rinmin-m tou,\nPuiské Li rinmin ti bagay,\nMouin konnin-L rinmin-m tou.\n\n2.\nLi fè bèl flè lis nan chan yo,\nLi rouzé yo ak dlo,\nSi-L gin doua rinmin bèl ti flè\nL-a rinmin ti mon-n yo.\n\n3.\nNi flè, ni bèt, ni ti pouason\nBon-Dieu té fè yo tout,\nLi pap blié gnou sèl ti moun\nL-a rinmin yo san dout.','<p><strong>1.</strong> </p>\n<p>Lè zouazo tonbé Bon-Dieu ouè-l, </p>\n<p>Li véyé yo toujou </p>\n<p>Si Bon-Dieu rinmin ti zouazo </p>\n<p>Mouin konnin-L rinmin-m tou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li rinmin-m tou, Li rinmin-m tou,</strong> </p>\n<p><strong>Mouin konnin-L rinmin-m tou,</strong> </p>\n<p><strong>Puiské Li rinmin ti bagay,</strong> </p>\n<p><strong>Mouin konnin-L rinmin-m tou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li fè bèl flè lis nan chan yo, </p>\n<p>Li rouzé yo ak dlo, </p>\n<p>Si-L gin doua rinmin bèl ti flè </p>\n<p>L-a rinmin ti mon-n yo. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ni flè, ni bèt, ni ti pouason </p>\n<p>Bon-Dieu té fè yo tout, </p>\n<p>Li pap blié gnou sèl ti moun </p>\n<p>L-a rinmin yo san dout. </p>\n','Le-Zouazo-Tonbe-Bon-Dieu-Oue-l',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2073,'07b883da-a442-40bc-9d72-f2c41495fdf3',0,0,'Pitit Mouin, Rétounin',56,'56_HC_ht','1.\nEské ou gin tan kité souin Papa ou,\nOu régrèt ké ou égaré louin,\nGin gnou voua dou\nÉ tand kap mandé pou ou,\nPitit mouin, pitit mouin, rétounin.\n\nKè:\nRétounin  (Pitit mouin)\nRétounin  (Pitit mouin)\nO pitit, ou égaré,\nPapa ou ap plédé,\nPitit mouin, rétounin.\n\n2.\nEské ou san sékou nan loraj la vi,\nBato ou prèt pou plonjé anfin,\nMin!  lumiè Lévanjil, antré nan labri,\nPitit mouin, pitit mouin rétounin.\n\n3.\nJodi a L-ap plédé avèk ou ankò,\nAvantaj lé mon-n pa vo angnin,\nAntré vit pa néglijé gnou vrè trézò\nPitit mouin, pitit mouin, rétounin.','<p><strong>1.</strong> </p>\n<p>Eské ou gin tan kité souin Papa ou, </p>\n<p>Ou régrèt ké ou égaré louin, </p>\n<p>Gin gnou voua dou </p>\n<p>É tand kap mandé pou ou, </p>\n<p>Pitit mouin, pitit mouin, rétounin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Rétounin  (Pitit mouin)</strong> </p>\n<p><strong>Rétounin  (Pitit mouin)</strong> </p>\n<p><strong>O pitit, ou égaré,</strong> </p>\n<p><strong>Papa ou ap plédé,</strong> </p>\n<p><strong>Pitit mouin, rétounin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Eské ou san sékou nan loraj la vi, </p>\n<p>Bato ou prèt pou plonjé anfin, </p>\n<p>Min!  lumiè Lévanjil, antré nan labri, </p>\n<p>Pitit mouin, pitit mouin rétounin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jodi a L-ap plédé avèk ou ankò, </p>\n<p>Avantaj lé mon-n pa vo angnin, </p>\n<p>Antré vit pa néglijé gnou vrè trézò </p>\n<p>Pitit mouin, pitit mouin, rétounin. </p>\n','Pitit-Mouin-Retounin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2074,'33dfb332-3787-4dbd-9b44-65674fe4c2dd',0,0,'Chan Gèp Yo',68,'68_HC_ht','1.\nLè pèp Kanaan fè kè yo du kont Bon-Dieu\nYo atristé Li ak péché,\nDonk Bon-Dieu voyé gèp yo pou réglé yo\nÉ pou édé pèp Li yo gangnin.\nGèp yo pèsouadé yo li tap préférab\nPrésé vit é pa pou yo mizé,\nDieu pa fè nou aji kont volonté nou,\nSèlman Li fè yo byin dispozé.\n\nKè:\nLi papé fòsé nou alé (non, non)\nLé papé fòsé nou alé,\nLé pa fè nou aji kont volonté nou,\nSèlman Li fè nou byin dispozé.\n\n2.\nSi gnou nich gèp vivan antré nan chan-m sa\nÉpi bèt yo komansé volé\nNou pa ta bézouin konvink ou pou sòti,\nOu ta anvi sòti, nèspa vré?\nSé pa akòz fòs yo pou yo broté ou\nJété ou nan fénèt la, non\nYo pap fè ou aji kont volonté ou\nSèlman ya fè ou byin dispozé.\n\n3.\nLè yo voyé Jonas nan lèv lé Ségné,\nLi pa té vlé alé laba,\nLi pa janmin fè gnou travay ki si rèd,\nDonk, li abandonnin travay la.\nMin Bon-Dieu fè gnou gro pouason avalé-l,\nSé gnou istoua nou tout konnin,\nLi pap fè li aji kont volonté li,\nSèlman li fè li byin dispozé.\n\n4.\nLè Balaam al koté Moab la\nLi té vlé réglé zafè li\nBourik li réproché-l nan mitan chémin,\nProfèt admèt pou li obéyi.\nSi-l kab sèvi ak gnou bourik, Bon-Dieu gin doua\nFè ak nou ninpòt sa Li vlé\nLi pa fè nou aji kont volonté nou\nSèlman Li fè nou byin dispozé.\n\n5.\nGin anpil nan lèz òm ki sanblé ak bourik\nTou dé sé bèt rébèl, sé vré\nSi ou di-l: «Viré» sé tou douat la alé,\nOu di: «La» sé atò li maché\nChan mouin sé tou drol,\nMin m-konnin ké sé vré\nÉ mouin pa vlé ouè gèp yo vi-n piké kò  mouin\nDonk, pa rézisté kont la parol Bon-Dieu\nKité li fè ou byin dispozé.','<p><strong>1.</strong> </p>\n<p>Lè pèp Kanaan fè kè yo du kont Bon-Dieu </p>\n<p>Yo atristé Li ak péché, </p>\n<p>Donk Bon-Dieu voyé gèp yo pou réglé yo </p>\n<p>É pou édé pèp Li yo gangnin. </p>\n<p>Gèp yo pèsouadé yo li tap préférab </p>\n<p>Présé vit é pa pou yo mizé, </p>\n<p>Dieu pa fè nou aji kont volonté nou, </p>\n<p>Sèlman Li fè yo byin dispozé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li papé fòsé nou alé (non, non)</strong> </p>\n<p><strong>Lé papé fòsé nou alé,</strong> </p>\n<p><strong>Lé pa fè nou aji kont volonté nou,</strong> </p>\n<p><strong>Sèlman Li fè nou byin dispozé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si gnou nich gèp vivan antré nan chan-m sa </p>\n<p>Épi bèt yo komansé volé </p>\n<p>Nou pa ta bézouin konvink ou pou sòti, </p>\n<p>Ou ta anvi sòti, nèspa vré? </p>\n<p>Sé pa akòz fòs yo pou yo broté ou </p>\n<p>Jété ou nan fénèt la, non </p>\n<p>Yo pap fè ou aji kont volonté ou </p>\n<p>Sèlman ya fè ou byin dispozé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè yo voyé Jonas nan lèv lé Ségné, </p>\n<p>Li pa té vlé alé laba, </p>\n<p>Li pa janmin fè gnou travay ki si rèd, </p>\n<p>Donk, li abandonnin travay la. </p>\n<p>Min Bon-Dieu fè gnou gro pouason avalé-l, </p>\n<p>Sé gnou istoua nou tout konnin, </p>\n<p>Li pap fè li aji kont volonté li, </p>\n<p>Sèlman li fè li byin dispozé. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè Balaam al koté Moab la </p>\n<p>Li té vlé réglé zafè li </p>\n<p>Bourik li réproché-l nan mitan chémin, </p>\n<p>Profèt admèt pou li obéyi. </p>\n<p>Si-l kab sèvi ak gnou bourik, Bon-Dieu gin doua </p>\n<p>Fè ak nou ninpòt sa Li vlé </p>\n<p>Li pa fè nou aji kont volonté nou </p>\n<p>Sèlman Li fè nou byin dispozé. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Gin anpil nan lèz òm ki sanblé ak bourik </p>\n<p>Tou dé sé bèt rébèl, sé vré </p>\n<p>Si ou di-l: «Viré» sé tou douat la alé, </p>\n<p>Ou di: «La» sé atò li maché </p>\n<p>Chan mouin sé tou drol, </p>\n<p>Min m-konnin ké sé vré </p>\n<p>É mouin pa vlé ouè gèp yo vi-n piké kò  mouin </p>\n<p>Donk, pa rézisté kont la parol Bon-Dieu </p>\n<p>Kité li fè ou byin dispozé. </p>\n','Chan-Gep-Yo',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2075,'d11adfa0-f371-4ff4-b3b1-6833775d0b4b',0,0,'Le Zeprèv Yo Vi-n,  Nou Mande Pouki',3,'3_RN_ht','1.\nLe zeprèv yo vi-n, nou mande pouki\nNou bezwen soufri kè atiste?\nPoutan lèzot yo ki antoure nou,\nApe rejwi menm nan peche\n\nRefren:\nPi devan na wè Jezu nan glwa li,\nPi devan n-ape konprann pou ki,\nBon kouraj frè mwen, fè kè ou kontan,\nPaske you jou na konprann pou ki.\n\n2.\nSouvan nou mande pouki nou bezwen\nPase nan rout sa-a pou-n swive Jezu\nPansan lèzot yo ap viv byen alez\nn-ap bat pou-n chèche lèzam pèdi.\n\n3.\nRete fidèl jiska lanmo frè mwen\nLe Senyè Jezu egzote nou\nNou va bliye tout traka nou pase\nLè nou antre nan syèl la yon jou\n \n4.\nByento n’ap la ak Jezu sovè nou,\nNan bel kay nou, lè li va vi-ni,\nAnlè n’ap kontre byeneme nou yo,\nLèe sa-a n’ape konprann pouki','<p><strong>1.</strong> </p>\n<p>Le zeprèv yo vi-n, nou mande pouki </p>\n<p>Nou bezwen soufri kè atiste? </p>\n<p>Poutan lèzot yo ki antoure nou, </p>\n<p>Ape rejwi menm nan peche </p>\n<p>Refren: </p>\n<p>Pi devan na wè Jezu nan glwa li, </p>\n<p>Pi devan n-ape konprann pou ki, </p>\n<p>Bon kouraj frè mwen, fè kè ou kontan, </p>\n<p>Paske you jou na konprann pou ki. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan nou mande pouki nou bezwen </p>\n<p>Pase nan rout sa-a pou-n swive Jezu </p>\n<p>Pansan lèzot yo ap viv byen alez </p>\n<p>n-ap bat pou-n chèche lèzam pèdi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Rete fidèl jiska lanmo frè mwen </p>\n<p>Le Senyè Jezu egzote nou </p>\n<p>Nou va bliye tout traka nou pase </p>\n<p>Lè nou antre nan syèl la yon jou </p>\n<p><br/> <strong>4.</strong></p>\n<p>Byento n’ap la ak Jezu sovè nou, </p>\n<p>Nan bel kay nou, lè li va vi-ni, </p>\n<p>Anlè n’ap kontre byeneme nou yo, </p>\n<p>Lèe sa-a n’ape konprann pouki </p>\n','Le-Zeprev-Yo-Vi-n-Nou-Mande-Pouki',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2076,'a3d40bec-b25c-4053-b53e-1cbdb162e2e8',0,0,'Lè Ou Konte Moun Ki Pou Jezu',9,'9_RN_ht','1.\nLè ou konte moun ki pou Jezu\nKonte-m ladan,\nNan moun ki konfye nan pawol li,\nKonte-m ladan\n \nRefren:\nKonte-m pami pitit ki pou Rwa syèl la,\nKonte-m tankou sèvitè k’ap sèvi ya\nKonte-m pami moun sove alelouya!\nKonte-m ladan\n \n2.\nLè ou konte moun ki nan la gras,\nKonte-m ladan\nSa yo an Kris ki genyen yon plas\nKonte-m ladan\n \n3.\nLè ou konte temwen yo ki pou Kris\nKonte-m ladan\nKi va genyen kouron la jistis\nKonte-m ladan','<p><strong>1.</strong> </p>\n<p>Lè ou konte moun ki pou Jezu </p>\n<p>Konte-m ladan, </p>\n<p>Nan moun ki konfye nan pawol li, </p>\n<p>Konte-m ladan </p>\n<p>Refren: </p>\n<p>Konte-m pami pitit ki pou Rwa syèl la, </p>\n<p>Konte-m tankou sèvitè k’ap sèvi ya </p>\n<p>Konte-m pami moun sove alelouya! </p>\n<p>Konte-m ladan </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou konte moun ki nan la gras, </p>\n<p>Konte-m ladan </p>\n<p>Sa yo an Kris ki genyen yon plas </p>\n<p>Konte-m ladan </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè ou konte temwen yo ki pou Kris </p>\n<p>Konte-m ladan </p>\n<p>Ki va genyen kouron la jistis </p>\n<p>Konte-m ladan </p>\n','Le-Ou-Konte-Moun-Ki-Pou-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2077,'69245f23-781b-4fe8-969d-b6ac4d19b100',0,0,'Jésus revient bientôt',78,'78_MJ_fr','1.\nJésus revient bientôt,\nQuel heureux jour!\nCar mes yeux Le verront,\nEt mes peines finiront,\nSi Jésus revient bientôt.\n\nChoeur:\nQuel heureux jour,\nLe revoir de Jésus!\nJe vivrai triomphant et sans peur\nEn la présence du Sauveur,\nQuel heureux jour,\nLe revoir de Jésus!\n\n2.\nAu ciel j\'irai bientôt,\nQuel heureux jour!\nJ\'entends déjà la voix\nDes rachetés de la croix,\nBientôt je serai là-haut.\n\n3.\nPlus d\'angoisse là-haut\nQuel heureux jour!\nLe Vainqueur de la mort\nMe pilote vers le port,\nIl apparaîtra bientôt.\n\n4.\nJ\'appartiens à mon Roi,\nQuel heureux jour!\nJe veux dire aux perdus\nPourquoi j\'adore Jésus:\nSon amour est pour moi.','<p><strong>1.</strong> </p>\n<p>Jésus revient bientôt, </p>\n<p>Quel heureux jour! </p>\n<p>Car mes yeux Le verront, </p>\n<p>Et mes peines finiront, </p>\n<p>Si Jésus revient bientôt. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Quel heureux jour,</strong> </p>\n<p><strong>Le revoir de Jésus!</strong> </p>\n<p><strong>Je vivrai triomphant et sans peur</strong> </p>\n<p><strong>En la présence du Sauveur,</strong> </p>\n<p><strong>Quel heureux jour,</strong> </p>\n<p><strong>Le revoir de Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Au ciel j&#39;irai bientôt, </p>\n<p>Quel heureux jour! </p>\n<p>J&#39;entends déjà la voix </p>\n<p>Des rachetés de la croix, </p>\n<p>Bientôt je serai là-haut. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Plus d&#39;angoisse là-haut </p>\n<p>Quel heureux jour! </p>\n<p>Le Vainqueur de la mort </p>\n<p>Me pilote vers le port, </p>\n<p>Il apparaîtra bientôt. </p>\n<p><br/> <strong>4.</strong></p>\n<p>J&#39;appartiens à mon Roi, </p>\n<p>Quel heureux jour! </p>\n<p>Je veux dire aux perdus </p>\n<p>Pourquoi j&#39;adore Jésus: </p>\n<p>Son amour est pour moi. </p>\n','Jesus-revient-bientot',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2078,'dcf489e5-1577-42ec-abf5-6ffbf90fef3d',0,0,'Merci, Seigneur, merci',90,'90_MJ_fr','1.\nMerci, Seigneur, merci\nQue dirai-je de plus?\nMerci pour le salut,\nO mon Sauveur Jésus;\nOù je suis invité,\nO merci, merci pour ta grâce\nEt pour ta charité.','<p><strong>1.</strong> </p>\n<p>Merci, Seigneur, merci </p>\n<p>Que dirai-je de plus? </p>\n<p>Merci pour le salut, </p>\n<p>O mon Sauveur Jésus; </p>\n<p>Où je suis invité, </p>\n<p>O merci, merci pour ta grâce </p>\n<p>Et pour ta charité. </p>\n','Merci-Seigneur-merci',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2079,'9a41e6aa-5e20-43f1-9b32-147756f165de',0,0,'Tu m\'aime Jésus',95,'95_MJ_fr','1.\nTu m\'aime, Jésus,\nTu me tiens en ton coeur,\nOh, que je suis heureux\nDe t\'avoir pour Sauveur!\n\nChoeur:\nAlléluia, Jésus m\'aime!\nAlléluia, Amen!\nAlléluia, Jésus m\'aime!\nEt j\'aime Jésus!\n\n2.\nOui, Tu m\'as sauvé,\nJ\'en rends gloire à Ton nom,\nEt de tous mes péchés\nJ\'ai reçu le pardon.\n\n3.\nMe savoir sauvé,\nConnaître, aimer Jésus…\nJ\'ai déjà tant de biens,\nQue me faut-il de plus?\n\n4.\nMais le ciel m\'attend;\nVers ce séjour de paix\nViendrez-vous avec moi\nPour régner à jamais?','<p><strong>1.</strong> </p>\n<p>Tu m&#39;aime, Jésus, </p>\n<p>Tu me tiens en ton coeur, </p>\n<p>Oh, que je suis heureux </p>\n<p>De t&#39;avoir pour Sauveur! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Alléluia, Jésus m&#39;aime!</strong> </p>\n<p><strong>Alléluia, Amen!</strong> </p>\n<p><strong>Alléluia, Jésus m&#39;aime!</strong> </p>\n<p><strong>Et j&#39;aime Jésus!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, Tu m&#39;as sauvé, </p>\n<p>J&#39;en rends gloire à Ton nom, </p>\n<p>Et de tous mes péchés </p>\n<p>J&#39;ai reçu le pardon. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Me savoir sauvé, </p>\n<p>Connaître, aimer Jésus… </p>\n<p>J&#39;ai déjà tant de biens, </p>\n<p>Que me faut-il de plus? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais le ciel m&#39;attend; </p>\n<p>Vers ce séjour de paix </p>\n<p>Viendrez-vous avec moi </p>\n<p>Pour régner à jamais? </p>\n','Tu-m\'aime-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2080,'ba27d150-a8f5-4cad-9e46-6959a4fda1f1',0,0,'Moi et ma maison',121,'121_MJ_fr','1.\nMoi et ma maison,\nEt ma maison, et ma maison,\nMoi et ma maison,\nNous servirons l\'Éternel.  (bis)','<p><strong>1.</strong> </p>\n<p>Moi et ma maison, </p>\n<p>Et ma maison, et ma maison, </p>\n<p>Moi et ma maison, </p>\n<p>Nous servirons l&#39;Éternel.  (bis) </p>\n','Moi-et-ma-maison',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2081,'180c8a8e-ebc0-4feb-8720-12c8d3677bf2',0,0,'Jésus est merveilleux',124,'124_MJ_fr','1.\nJésus est merveilleux,\nMerveilleux, merveilleux,\nJésus mon Seigneur est merveilleux;\nOn a vu, entendu,\nDans la Bible je l\'ai lu,\nJésus mon Seigneur est merveilleux!','<p><strong>1.</strong> </p>\n<p>Jésus est merveilleux, </p>\n<p>Merveilleux, merveilleux, </p>\n<p>Jésus mon Seigneur est merveilleux; </p>\n<p>On a vu, entendu, </p>\n<p>Dans la Bible je l&#39;ai lu, </p>\n<p>Jésus mon Seigneur est merveilleux! </p>\n','Jesus-est-merveilleux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2082,'fb96d871-2d22-46e1-aa74-72dc43806176',0,0,'Si tu prends mon Jésus',132,'132_MJ_fr','1.\nSi tu prends mon Jésus,\nO âme sans espoir,\nSi tu prends mon Jésus,\nIl te recevra,\nSi tu prends mon Jésus,\nIl te sauvera,\nSi tu prends mon Jésus ce soir.','<p><strong>1.</strong> </p>\n<p>Si tu prends mon Jésus, </p>\n<p>O âme sans espoir, </p>\n<p>Si tu prends mon Jésus, </p>\n<p>Il te recevra, </p>\n<p>Si tu prends mon Jésus, </p>\n<p>Il te sauvera, </p>\n<p>Si tu prends mon Jésus ce soir. </p>\n','Si-tu-prends-mon-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2083,'b77ab012-d82a-4f94-9852-6227808ea05d',0,0,'Merveilleux, Merveilleux Jésus',143,'143_MJ_fr','1.\nMerveilleux, merveilleux Jésus,\nQui est semblable à Toi?\nMerveilleux, merveilleux Jésus,\nTu fus crucifié pour moi;\nMerveilleux, merveilleux Jésus,\nO mon Sauveur, mon Roi,\nTu es l\'Ami fidèle,\nLe soutien de ma foi.','<p><strong>1.</strong> </p>\n<p>Merveilleux, merveilleux Jésus, </p>\n<p>Qui est semblable à Toi? </p>\n<p>Merveilleux, merveilleux Jésus, </p>\n<p>Tu fus crucifié pour moi; </p>\n<p>Merveilleux, merveilleux Jésus, </p>\n<p>O mon Sauveur, mon Roi, </p>\n<p>Tu es l&#39;Ami fidèle, </p>\n<p>Le soutien de ma foi. </p>\n','Merveilleux-Merveilleux-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2084,'02950577-37ae-4843-9478-f69a44f0a1c7',0,0,'Dans mon cœur chante une mélodie',146,'146_MJ_fr','1.\nDans mon coeur chante une mélodie,\nDouce mélodie, céleste harmonie;\nDans mon coeur chante une mélodie\nA la gloire du Seigneur.\n\n2.\nMatin, midi, soir,\nJ\'ai un cantique que j\'aime chanter,\nC\'est un cantique de mon coeur\nQue je chante au Seigneur.','<p><strong>1.</strong> </p>\n<p>Dans mon coeur chante une mélodie, </p>\n<p>Douce mélodie, céleste harmonie; </p>\n<p>Dans mon coeur chante une mélodie </p>\n<p>A la gloire du Seigneur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Matin, midi, soir, </p>\n<p>J&#39;ai un cantique que j&#39;aime chanter, </p>\n<p>C&#39;est un cantique de mon coeur </p>\n<p>Que je chante au Seigneur. </p>\n','Dans-mon-coeur-chante-une-melodie',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2085,'7082755d-1ef7-4255-95c0-a04e0cd3671c',0,0,'Je cherchais le bonheur, O Christ',164,'164_MJ_fr','1.\nJe cherchais le bonheur, O Christ,\nTous les bienfaits de Ta croix;\nJe voulais le don de l\'Esprit\nPour travailler, O, pour Toi!\nJe voulais sentir dans mon coeur\nLe vrai zèle et la chaleur,\nMais tu me dis que mon bonheur\nN\'est que Toi seul, O Seigneur!\n\nChoeur:\nC\'est Toi, Jésus, c\'est Toi, Seigneur\nTout en tout, Tu es pour moi:\nJoie, lumière, amour, sagesse.\nTout mon bonheur, Christ, c\'est Toi!\n\n2.\nAutrefois je cherchais le Don\nSans trouver le Donateur.\nEt je voulais la guérison,\nMais voici le Guérisseur!\nJadis, Christ, je voulais briller\nPour Toi, O mon Rédempteur.\nMaintenant, je veux refléter\nQue Ta gloire, cher Sauveur!\n\n3.\nJe voulais Te servir, Jésus.\nSers-Toi de moi amintenant.\nTon pouvoir ne demande plus.\nJe Te veux seul, tout-Puissant!\nJe cherchais le bonheur des cieux:\nL\'amour, la foi et la paix;\nMais désormais c\'est Toi, mon Dieu.\nC\'est Toi, c\'est Toi à jamais!','<p><strong>1.</strong> </p>\n<p>Je cherchais le bonheur, O Christ, </p>\n<p>Tous les bienfaits de Ta croix; </p>\n<p>Je voulais le don de l&#39;Esprit </p>\n<p>Pour travailler, O, pour Toi! </p>\n<p>Je voulais sentir dans mon coeur </p>\n<p>Le vrai zèle et la chaleur, </p>\n<p>Mais tu me dis que mon bonheur </p>\n<p>N&#39;est que Toi seul, O Seigneur! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>C&#39;est Toi, Jésus, c&#39;est Toi, Seigneur</strong> </p>\n<p><strong>Tout en tout, Tu es pour moi:</strong> </p>\n<p><strong>Joie, lumière, amour, sagesse.</strong> </p>\n<p><strong>Tout mon bonheur, Christ, c&#39;est Toi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Autrefois je cherchais le Don </p>\n<p>Sans trouver le Donateur. </p>\n<p>Et je voulais la guérison, </p>\n<p>Mais voici le Guérisseur! </p>\n<p>Jadis, Christ, je voulais briller </p>\n<p>Pour Toi, O mon Rédempteur. </p>\n<p>Maintenant, je veux refléter </p>\n<p>Que Ta gloire, cher Sauveur! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je voulais Te servir, Jésus. </p>\n<p>Sers-Toi de moi amintenant. </p>\n<p>Ton pouvoir ne demande plus. </p>\n<p>Je Te veux seul, tout-Puissant! </p>\n<p>Je cherchais le bonheur des cieux: </p>\n<p>L&#39;amour, la foi et la paix; </p>\n<p>Mais désormais c&#39;est Toi, mon Dieu. </p>\n<p>C&#39;est Toi, c&#39;est Toi à jamais! </p>\n','Je-cherchais-le-bonheur-O-Christ',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2086,'f5719522-3566-4ddb-aea3-368e63abea30',0,0,'Je suis un jeune soldat',173,'173_MJ_fr','1.\nJe suis un jeune soldat\nDe la petite armée.\nLe Seigneur ne me dit pas\nD\'attendre des années.\nAu diable je résiste.\nPour Jésus, je veux vivre\nEt mes pas de soldat\nMarquent des alléluias!  (bis)','<p><strong>1.</strong> </p>\n<p>Je suis un jeune soldat </p>\n<p>De la petite armée. </p>\n<p>Le Seigneur ne me dit pas </p>\n<p>D&#39;attendre des années. </p>\n<p>Au diable je résiste. </p>\n<p>Pour Jésus, je veux vivre </p>\n<p>Et mes pas de soldat </p>\n<p>Marquent des alléluias!  (bis) </p>\n','Je-suis-un-jeune-soldat',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2087,'d1f0bf24-eb10-4b38-8c71-88453ec193ae',0,0,'Mouin Vlé Sanblé Ak Jézu',15,'15_HC_ht','1.\nMouin gingnin gnou pi gran dézi,\nPou mouin sanblé ak Jézu,\nNan pouin gnou lòt pi gran plézi,\nPasé sanblé ak Jézu.\nMouin vlé kè mouin sèvi tròn Li\nPou ké lé mon-n kap chèché la vi\nKab ouè Li-min’m nan mouin, tanpri,\nMouin vlé sanblé ak Jézu.\n\n2.\nLi sèvi léz òt san rété,\nMouin vlé sanblé ak Jézu,\nLi maché ak umilité,\nMouin vlé sanblé ak Jézu.\nPou foul la Li gin simpati,\nLi palé gnou mésaj béni,\nPéchè yo Li vlé akéyi,\nMouin vlé sanblé ak Jézu.\n\n3.\nLa vi li té san tach é sin,\nMouin vlé sanblé ak Jézu,\nObéisan juska la fin,\nMouin vlé sanblé ak Jézu.\nLè tan Li rivé pou mouri,\n«Padonnin yo» sé té kri Li,\npou moun sa yo ki té lénmi,\nmouin vlé sanblé ak Jézu.\n\n4.\nA la Kris viv gnou vi pafèt,\nMouin vlé sanblé ak Jézu,\nÉ ré konpans mouin va konplèt,\nLè mouin sanblé ak Jézu.\nPlin nan-m grangou mouin ak Léspri,\nFò Li doué kontrolé tout la vi,\nPou mouin nan pouin lòt pi gran bu,\nPasé sanblé ak Jézu.','<p><strong>1.</strong> </p>\n<p>Mouin gingnin gnou pi gran dézi, </p>\n<p>Pou mouin sanblé ak Jézu, </p>\n<p>Nan pouin gnou lòt pi gran plézi, </p>\n<p>Pasé sanblé ak Jézu. </p>\n<p>Mouin vlé kè mouin sèvi tròn Li </p>\n<p>Pou ké lé mon-n kap chèché la vi </p>\n<p>Kab ouè Li-min’m nan mouin, tanpri, </p>\n<p>Mouin vlé sanblé ak Jézu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li sèvi léz òt san rété, </p>\n<p>Mouin vlé sanblé ak Jézu, </p>\n<p>Li maché ak umilité, </p>\n<p>Mouin vlé sanblé ak Jézu. </p>\n<p>Pou foul la Li gin simpati, </p>\n<p>Li palé gnou mésaj béni, </p>\n<p>Péchè yo Li vlé akéyi, </p>\n<p>Mouin vlé sanblé ak Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>La vi li té san tach é sin, </p>\n<p>Mouin vlé sanblé ak Jézu, </p>\n<p>Obéisan juska la fin, </p>\n<p>Mouin vlé sanblé ak Jézu. </p>\n<p>Lè tan Li rivé pou mouri, </p>\n<p>«Padonnin yo» sé té kri Li, </p>\n<p>pou moun sa yo ki té lénmi, </p>\n<p>mouin vlé sanblé ak Jézu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>A la Kris viv gnou vi pafèt, </p>\n<p>Mouin vlé sanblé ak Jézu, </p>\n<p>É ré konpans mouin va konplèt, </p>\n<p>Lè mouin sanblé ak Jézu. </p>\n<p>Plin nan-m grangou mouin ak Léspri, </p>\n<p>Fò Li doué kontrolé tout la vi, </p>\n<p>Pou mouin nan pouin lòt pi gran bu, </p>\n<p>Pasé sanblé ak Jézu. </p>\n','Mouin-Vle-Sanble-Ak-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2088,'b09d6b63-6546-4ac3-bd8f-0f521e1b01b0',0,0,'Onoré Lé Ségnè Jézu',42,'42_HC_ht','1.\nOnoré lé Ségnè Jézu\nAnnou tout félisité Li,\nSonjé byin ki jan nou doué Li\nSa nou posédé.\n\n2.\nChè Jézu ou kontanté nou\nNan tout lut ou protéjé nou\nOkin-n mal pap déranjé nou,\nLè-n konfié nan Ou.\n\n3.\nNap maché nan gran puisans Ou\nAp konté sou délivrans Ou\nOu mérité tout konfians nou,\nChak jou la vi nou.\n\n4.\nN-a rivé koté nou vlé yé\nN-a dévni tou sa nou doué yé\nÉ byinto nou  va posédé,\nSa Kris promèt nou.','<p><strong>1.</strong> </p>\n<p>Onoré lé Ségnè Jézu </p>\n<p>Annou tout félisité Li, </p>\n<p>Sonjé byin ki jan nou doué Li </p>\n<p>Sa nou posédé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chè Jézu ou kontanté nou </p>\n<p>Nan tout lut ou protéjé nou </p>\n<p>Okin-n mal pap déranjé nou, </p>\n<p>Lè-n konfié nan Ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nap maché nan gran puisans Ou </p>\n<p>Ap konté sou délivrans Ou </p>\n<p>Ou mérité tout konfians nou, </p>\n<p>Chak jou la vi nou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>N-a rivé koté nou vlé yé </p>\n<p>N-a dévni tou sa nou doué yé </p>\n<p>É byinto nou  va posédé, </p>\n<p>Sa Kris promèt nou. </p>\n','Onore-Le-Segne-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2089,'859f96bc-3802-4e65-91f4-6f8dcd2b40c4',0,0,'Pa Tro Piti',52,'52_HC_ht','1.\nGnou jou késion sa té pozé\nPa pitit gnou tèl\nEské gnou ti moun kapab\nAntré nan Lévanjil\nMin répons ti moun nan\nTé tandé san okin-n dout\n«Kité ti moun vi-n jouin-n Jézu,\nLi rinmin yo tout.»\n\nKè:\nPa tro piti, Jézu rinmin yo  (3 foua)\nKité ti moun vi-n jouin-n Jézu,\nLi rinmin yo tout.\n\n2.\nNinpòt ras ak ninpòt koulè\nSa pa fè angnin,\nJézu rinmin yo tout\nDistinksion sa sé tintin,\nInstoui, ou ilétré,\nDépi yo mandé, yo-a jouin-n,\n«Kité ti moun vi-n jouin-n Jézu,\nSé yo Li rinmin.»','<p><strong>1.</strong> </p>\n<p>Gnou jou késion sa té pozé </p>\n<p>Pa pitit gnou tèl </p>\n<p>Eské gnou ti moun kapab </p>\n<p>Antré nan Lévanjil </p>\n<p>Min répons ti moun nan </p>\n<p>Té tandé san okin-n dout </p>\n<p>«Kité ti moun vi-n jouin-n Jézu, </p>\n<p>Li rinmin yo tout.» </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pa tro piti, Jézu rinmin yo  (3 foua)</strong> </p>\n<p><strong>Kité ti moun vi-n jouin-n Jézu,</strong> </p>\n<p><strong>Li rinmin yo tout.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ninpòt ras ak ninpòt koulè </p>\n<p>Sa pa fè angnin, </p>\n<p>Jézu rinmin yo tout </p>\n<p>Distinksion sa sé tintin, </p>\n<p>Instoui, ou ilétré, </p>\n<p>Dépi yo mandé, yo-a jouin-n, </p>\n<p>«Kité ti moun vi-n jouin-n Jézu, </p>\n<p>Sé yo Li rinmin.» </p>\n','Pa-Tro-Piti',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2090,'22a5a025-baf7-4db7-930a-c9fe7086d17b',0,0,'Pi Bon Zanmi Sé Jézu',7,'7_HC_ht','1.\nPi bon zanmi mouin gin sé Jézu,\nLi avèk mouin, koté-m pasé\nSé Li-min’m kon-n rakonté afè-m\nÉ-L ba-m toujou konsèy.\n\nKè:\nNan pouin gnou lòt ki  rinmin-m kon Li\nLi konnin tout sa ki nan kè mouin,\nPi bon zanmi mouin gin sé Jézu,\nPa gin gnou lòt kon Li.\n\n2.\nMouin jouin-n nan Li tout sa-m tap chèché\nLa pè, la joua, min-m la santé,\nÉ Li bay mouin al vi étènèl,\nSa-m pat kapab achté-l.','<p><strong>1.</strong> </p>\n<p>Pi bon zanmi mouin gin sé Jézu, </p>\n<p>Li avèk mouin, koté-m pasé </p>\n<p>Sé Li-min’m kon-n rakonté afè-m </p>\n<p>É-L ba-m toujou konsèy. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nan pouin gnou lòt ki  rinmin-m kon Li</strong> </p>\n<p><strong>Li konnin tout sa ki nan kè mouin,</strong> </p>\n<p><strong>Pi bon zanmi mouin gin sé Jézu,</strong> </p>\n<p><strong>Pa gin gnou lòt kon Li.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin jouin-n nan Li tout sa-m tap chèché </p>\n<p>La pè, la joua, min-m la santé, </p>\n<p>É Li bay mouin al vi étènèl, </p>\n<p>Sa-m pat kapab achté-l. </p>\n','Pi-Bon-Zanmi-Se-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2091,'bfd8414b-19cd-4840-bd46-cf4c648abd2f',0,0,'Angnin Pa Ta Inposib',78,'78_HC_ht','1.\nMouin li bèl promès Bon-Dieu\nNan Paròl Li\nKé Li kapab fè tout bagay,\n«Nan mouin angnin inposib\npou mouin», Li di,\nsi nou konfié nan Paròl mouin.\n\nKè:\nAngnin pa ta inposib,\nLè konfians nou nan Bon-Dieu.\nAngnin pa ta inposib\nLè-n konfié nan Paròl Li,\nKouté la voua lé Ségnè ankò,\n«Angnin pa ta inposib pou Mouin»,\nmété kofians ou nan Bon-Dieu,\nÉ kouè nan Paròl Li,\nOh angnin, oh angnin, oh angnin\nPa ta inposib pou Dieu.\n\n2.\nParòl Bon-Dieu di, Tout bagay sé posib,\nPromès yo pou ou, sèlman kouè,\nKouè tout sa Li di paské Bon-Dieu fidèl,\nÉ sa Li promèt, Li kab fè.\n\n3.\nParòl Bon-Dieu sé gnou lank byin asuré,\nNan mitan anrajé.\nPaské nan féblès nou kab souvan fayi,\nMin Paròl Bon-Dieu rété fè-m.','<p><strong>1.</strong> </p>\n<p>Mouin li bèl promès Bon-Dieu </p>\n<p>Nan Paròl Li </p>\n<p>Ké Li kapab fè tout bagay, </p>\n<p>«Nan mouin angnin inposib </p>\n<p>pou mouin», Li di, </p>\n<p>si nou konfié nan Paròl mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Angnin pa ta inposib,</strong> </p>\n<p><strong>Lè konfians nou nan Bon-Dieu.</strong> </p>\n<p><strong>Angnin pa ta inposib</strong> </p>\n<p><strong>Lè-n konfié nan Paròl Li,</strong> </p>\n<p><strong>Kouté la voua lé Ségnè ankò,</strong> </p>\n<p><strong>«Angnin pa ta inposib pou Mouin»,</strong> </p>\n<p><strong>mété kofians ou nan Bon-Dieu,</strong> </p>\n<p><strong>É kouè nan Paròl Li,</strong> </p>\n<p><strong>Oh angnin, oh angnin, oh angnin</strong> </p>\n<p><strong>Pa ta inposib pou Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Paròl Bon-Dieu di, Tout bagay sé posib, </p>\n<p>Promès yo pou ou, sèlman kouè, </p>\n<p>Kouè tout sa Li di paské Bon-Dieu fidèl, </p>\n<p>É sa Li promèt, Li kab fè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Paròl Bon-Dieu sé gnou lank byin asuré, </p>\n<p>Nan mitan anrajé. </p>\n<p>Paské nan féblès nou kab souvan fayi, </p>\n<p>Min Paròl Bon-Dieu rété fè-m. </p>\n','Angnin-Pa-Ta-Inposib',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2092,'af187d5f-f29f-4d91-b755-8d15e5cce04a',0,0,'Dans Ces Jours-Ci',82,'82_HC_fr','1.\nDans ces jours-ci, il faut un Sauveur\nDans ces jours-ci, Christ est le Rocher,\nSois très certain, sois très certain,\nQue ton ancre croche le roc solide.\n\nChoeur:\nCe roc est Jésus, oh, oui c’est Lui,\nCe roc est Jésus, seulement Lui.\nSois très certain, sois très certain,\nQue ton ancre croche le roc solide.\n\n2.\nDans ces jours-ci, il faut la Bible,\nDans ces jours-ci, sois invincible,\nSois très certain, sois très certain,\nQue ton ancre croche le roc solide.\n\n3.\nDans ces jours-ci, oh, je suis sauvé,\nDans ces jours-ci, je suis bien ancré,\nJe suis certain, je suis certain,\nQue mon ancre croche le roc solide.','<p><strong>1.</strong> </p>\n<p>Dans ces jours-ci, il faut un Sauveur </p>\n<p>Dans ces jours-ci, Christ est le Rocher, </p>\n<p>Sois très certain, sois très certain, </p>\n<p>Que ton ancre croche le roc solide. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Ce roc est Jésus, oh, oui c’est Lui,</strong> </p>\n<p><strong>Ce roc est Jésus, seulement Lui.</strong> </p>\n<p><strong>Sois très certain, sois très certain,</strong> </p>\n<p><strong>Que ton ancre croche le roc solide.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans ces jours-ci, il faut la Bible, </p>\n<p>Dans ces jours-ci, sois invincible, </p>\n<p>Sois très certain, sois très certain, </p>\n<p>Que ton ancre croche le roc solide. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans ces jours-ci, oh, je suis sauvé, </p>\n<p>Dans ces jours-ci, je suis bien ancré, </p>\n<p>Je suis certain, je suis certain, </p>\n<p>Que mon ancre croche le roc solide. </p>\n','Dans-Ces-Jours-Ci',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2093,'fec60c54-0715-4f5d-b87a-430ca042076e',0,0,'Jour Par Jour',83,'83_HC_fr','1.\nJour par jour quand chaque moment passe,\nJe trouve la force pour tout supporter.\nConfiant dans les promesses du Père,\nJe n’ai pas besoin de m’inquieter.\nCelui dont le coeur est si aimable,\nChaque jour nous donne ce qu’il nous faut,\nPlein d’amour, Il partage nos peines\nNos labeurs avec paix et repos.\n\n2.\nTous les jours Jésus Lui-même est ici,\nEt sa grâce suffit pour chaque moment.\nSans contraintre Il porta tous nos soucis,\nSon nom est Conseiller, Dieu Puissant.\nLa sûreté de son enfant pour toujours,\nIl voulait de Lui-même la chargée,\nQue ta vigueur dure autant que tes jours\nCette promesse Il l’a donnée.\n\n3.\nAide-moi à l’heure de la souffrance,\nA me confier dans toutes tes promesses\nJamais perdre le réconfort de la foi\nQui m’est offerte remplie de tendresse.\nAide-moi dans les troubles de la vie,\nUn à un, les jours, les moments s’enfuient,\nJusqu’à l’heure où je verrai mon Roi.','<p><strong>1.</strong> </p>\n<p>Jour par jour quand chaque moment passe, </p>\n<p>Je trouve la force pour tout supporter. </p>\n<p>Confiant dans les promesses du Père, </p>\n<p>Je n’ai pas besoin de m’inquieter. </p>\n<p>Celui dont le coeur est si aimable, </p>\n<p>Chaque jour nous donne ce qu’il nous faut, </p>\n<p>Plein d’amour, Il partage nos peines </p>\n<p>Nos labeurs avec paix et repos. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tous les jours Jésus Lui-même est ici, </p>\n<p>Et sa grâce suffit pour chaque moment. </p>\n<p>Sans contraintre Il porta tous nos soucis, </p>\n<p>Son nom est Conseiller, Dieu Puissant. </p>\n<p>La sûreté de son enfant pour toujours, </p>\n<p>Il voulait de Lui-même la chargée, </p>\n<p>Que ta vigueur dure autant que tes jours </p>\n<p>Cette promesse Il l’a donnée. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Aide-moi à l’heure de la souffrance, </p>\n<p>A me confier dans toutes tes promesses </p>\n<p>Jamais perdre le réconfort de la foi </p>\n<p>Qui m’est offerte remplie de tendresse. </p>\n<p>Aide-moi dans les troubles de la vie, </p>\n<p>Un à un, les jours, les moments s’enfuient, </p>\n<p>Jusqu’à l’heure où je verrai mon Roi. </p>\n','Jour-Par-Jour',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2094,'c563cf0a-8304-4b08-94d9-13478aa08c5b',0,0,'Anba Lonbraj Lakwa Jezu-a',5,'5_RN_ht','1.\nAnba lonbraj lakwa Jezu-a\nSe la nou va repose\nWi repose nan gras Bondye\nA la bèl bagay lè n’ap mache\nMen nou nan men Jezu-kri,\nN’ap rete fèm nan gras Bondye\n \nRefren:\nJezu bon bèje ap veye sou nou\nLi fè nou pwomès, li pap janm domi\nSe pou sa nan bèl kay bèje-a\nMouton yo gen kè poze\nWi repose nan gras Bondye\n\n2.\nNan pi bèl patiraj ki genyen\nSe la li fè nou manje\nNan pi bèl rivyè k’ap koule,\nK’ape koule byen trankil,\nLi fè nou bwè, swaf nou pase.\n \n3.\nOu menm k’ap travay pou Jezu\nOu pa bezwen dekouraje\nKenbe pi fèm, ou va rive,\nGras Bondye se benediksyon,\nEske ou pa ta renmen-l tou\nChwazi Jezu Jodi-a menm.','<p><strong>1.</strong> </p>\n<p>Anba lonbraj lakwa Jezu-a </p>\n<p>Se la nou va repose </p>\n<p>Wi repose nan gras Bondye </p>\n<p>A la bèl bagay lè n’ap mache </p>\n<p>Men nou nan men Jezu-kri, </p>\n<p>N’ap rete fèm nan gras Bondye </p>\n<p>Refren: </p>\n<p>Jezu bon bèje ap veye sou nou </p>\n<p>Li fè nou pwomès, li pap janm domi </p>\n<p>Se pou sa nan bèl kay bèje-a </p>\n<p>Mouton yo gen kè poze </p>\n<p>Wi repose nan gras Bondye </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan pi bèl patiraj ki genyen </p>\n<p>Se la li fè nou manje </p>\n<p>Nan pi bèl rivyè k’ap koule, </p>\n<p>K’ape koule byen trankil, </p>\n<p>Li fè nou bwè, swaf nou pase. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou menm k’ap travay pou Jezu </p>\n<p>Ou pa bezwen dekouraje </p>\n<p>Kenbe pi fèm, ou va rive, </p>\n<p>Gras Bondye se benediksyon, </p>\n<p>Eske ou pa ta renmen-l tou </p>\n<p>Chwazi Jezu Jodi-a menm. </p>\n','Anba-Lonbraj-Lakwa-Jezu-a',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2095,'e570ddea-f18a-4a3b-ad98-f3dd652470c4',0,0,'Maranatha Christ revient',79,'79_MJ_fr','1.\nMaranatha! (ter) Christ revient!\nProclamez ce beau message,\nEt qu\'au plus lointain rivage\nIl résonne d\'âge en âge,\nMaranatha! Christ revient!  (bis)\n\n2.\nMaranatha! (ter) Christ revient!\nLa nature tout entière\nPense à l\'aube qu\'elle espère,\nL\'aube que promet le Père,\nMaranatha! Christ revient! (bis)\n\n3.\nMaranatha! (ter) Christ revient!\nNous croyons à ta venue,\nO Jésus; que sur la nue\nElle éclate à notre vue!\nMaranatha! Christ revient!  (bis)\n\n4.\nMarantha! (ter) Christ revient!\nEn avant sans plus de crainte,\nPrends ta croix sans nulle plainte,\nDans l\'espoir de l\'heure sainte,\nMaranatha! Christ revient!  (bis)\n\n5.\nMaranatha! (ter) Christ revient!\nÂme si longtemps rebelle,\nViens à Christ, car Il t\'appelle,\nViens, entends sa voix fidèle,\nMaranatha! Christ revient!  (bis)','<p><strong>1.</strong> </p>\n<p>Maranatha! (ter) Christ revient! </p>\n<p>Proclamez ce beau message, </p>\n<p>Et qu&#39;au plus lointain rivage </p>\n<p>Il résonne d&#39;âge en âge, </p>\n<p>Maranatha! Christ revient!  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Maranatha! (ter) Christ revient! </p>\n<p>La nature tout entière </p>\n<p>Pense à l&#39;aube qu&#39;elle espère, </p>\n<p>L&#39;aube que promet le Père, </p>\n<p>Maranatha! Christ revient! (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Maranatha! (ter) Christ revient! </p>\n<p>Nous croyons à ta venue, </p>\n<p>O Jésus; que sur la nue </p>\n<p>Elle éclate à notre vue! </p>\n<p>Maranatha! Christ revient!  (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Marantha! (ter) Christ revient! </p>\n<p>En avant sans plus de crainte, </p>\n<p>Prends ta croix sans nulle plainte, </p>\n<p>Dans l&#39;espoir de l&#39;heure sainte, </p>\n<p>Maranatha! Christ revient!  (bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Maranatha! (ter) Christ revient! </p>\n<p>Âme si longtemps rebelle, </p>\n<p>Viens à Christ, car Il t&#39;appelle, </p>\n<p>Viens, entends sa voix fidèle, </p>\n<p>Maranatha! Christ revient!  (bis) </p>\n','Maranatha-Christ-revient',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2096,'bf6eb565-9413-4882-b40a-7da748e5e5a8',0,0,'Montons l\'échelle de Jacob',133,'133_MJ_fr','1.\nMontons l\'échelle de Jacob, Jacob  (ter)\nSoldats de la croix.\n\n2.\nChaque pas nous rendra plus haut, plus haut  (ter)\nSoldats de la croix.\n\n3.\nAmi, connais-tu mon Jésus, Jésus?   (ter)\nSoldats de la croix.\n\n4.\nSi tu L\'aime, tu Le suivras, suivras  (ter)\nSoldats de la croix.\n\n5.\nDonnons nos coeurs tous à Jésus, Jésus  (ter)\nSoldats de la croix.','<p><strong>1.</strong> </p>\n<p>Montons l&#39;échelle de Jacob, Jacob  (ter) </p>\n<p>Soldats de la croix. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chaque pas nous rendra plus haut, plus haut  (ter) </p>\n<p>Soldats de la croix. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ami, connais-tu mon Jésus, Jésus?   (ter) </p>\n<p>Soldats de la croix. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si tu L&#39;aime, tu Le suivras, suivras  (ter) </p>\n<p>Soldats de la croix. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Donnons nos coeurs tous à Jésus, Jésus  (ter) </p>\n<p>Soldats de la croix. </p>\n','Montons-l\'echelle-de-Jacob',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2097,'455ae43b-05e4-4521-8629-433d9a6895be',0,0,'Flot mugissants, flots en furie',72,'72_MJ_fr','1.\nFlots mugissants, flots en furie,\nEntourez-moi, je n\'ai pas peur;\nQuoi qu\'il en soit, paix infinie,\nPuisqu\'à la barre est mon Sauveur.\n\nChoeur:\nLe gouvernail de ma nacelle,\nOh! Quel repos, Jésus se tient,\nSi dans la nuit mon coeur chancelle,\nAvec Jésus, oui tout est bien.\n\n2.\nEt vous, récifs, écueils, menaces\nQui présagez plus d\'un malheur,\nCalme je puis vous voir en face,\nPuisqu\'à la barre est mon Sauveur.\n\n3.\nJour après jour nouveaux orages,\nNouveaux périls: point de frayeur!\nBientôt j\'atteins le grand rivage\nPuisqu\'à la barre est mon Sauveur.\n\n4.\nAvec Jésus pour mon Pilote,\nPour moi tout est paix et bonheur.\nEn sûreté mon esquif flotte,\nPuisqu\'à la barre est mon Sauveur.','<p><strong>1.</strong> </p>\n<p>Flots mugissants, flots en furie, </p>\n<p>Entourez-moi, je n&#39;ai pas peur; </p>\n<p>Quoi qu&#39;il en soit, paix infinie, </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Le gouvernail de ma nacelle,</strong> </p>\n<p><strong>Oh! Quel repos, Jésus se tient,</strong> </p>\n<p><strong>Si dans la nuit mon coeur chancelle,</strong> </p>\n<p><strong>Avec Jésus, oui tout est bien.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et vous, récifs, écueils, menaces </p>\n<p>Qui présagez plus d&#39;un malheur, </p>\n<p>Calme je puis vous voir en face, </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jour après jour nouveaux orages, </p>\n<p>Nouveaux périls: point de frayeur! </p>\n<p>Bientôt j&#39;atteins le grand rivage </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Avec Jésus pour mon Pilote, </p>\n<p>Pour moi tout est paix et bonheur. </p>\n<p>En sûreté mon esquif flotte, </p>\n<p>Puisqu&#39;à la barre est mon Sauveur. </p>\n','Flot-mugissants-flots-en-furie',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2098,'63998f17-b074-4041-8f6a-5ce7ebade0fc',0,0,'Tout est différent',82,'82_MJ_fr','1.\nTout est différent,\nJe vous dirai pourquoi –\nJ\'ai donné mon coeur à Jésus!\nTout est différent,\nChangé complètement,\nJ\'ai donné mon coeur à Jésus!\nLes choses anciennes sont passées,\nVoici, tout est devenu nouveau;\nTout est différent,\nJe vous dirai pourquoi –\nJ\'ai donné mon coeur à Jésus!','<p><strong>1.</strong> </p>\n<p>Tout est différent, </p>\n<p>Je vous dirai pourquoi – </p>\n<p>J&#39;ai donné mon coeur à Jésus! </p>\n<p>Tout est différent, </p>\n<p>Changé complètement, </p>\n<p>J&#39;ai donné mon coeur à Jésus! </p>\n<p>Les choses anciennes sont passées, </p>\n<p>Voici, tout est devenu nouveau; </p>\n<p>Tout est différent, </p>\n<p>Je vous dirai pourquoi – </p>\n<p>J&#39;ai donné mon coeur à Jésus! </p>\n','Tout-est-different',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2099,'3e4c3d5f-a931-4de5-8d5e-b203f75ff563',0,0,'Il tient ma main',88,'88_MJ_fr','1.\nIl tient ma main,\nJésus tient ma main,\nIl me mène au céleste séjour,\nIl me gardera de jour en jour;\nIl tient ma main,\nJésus tient ma main;\nLe chemin est dur,\nMais Jésus me rassure,\nEt Il tient ma main.','<p><strong>1.</strong> </p>\n<p>Il tient ma main, </p>\n<p>Jésus tient ma main, </p>\n<p>Il me mène au céleste séjour, </p>\n<p>Il me gardera de jour en jour; </p>\n<p>Il tient ma main, </p>\n<p>Jésus tient ma main; </p>\n<p>Le chemin est dur, </p>\n<p>Mais Jésus me rassure, </p>\n<p>Et Il tient ma main. </p>\n','Il-tient-ma-main',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2100,'8b6c5445-ce88-4404-89fb-c9b131dbbdb8',0,0,'Nous chantons au matin',93,'93_MJ_fr','1.\nNous chantons au matin\nLes hymnes de salut,\nNous chantons à midi\nLes chants de l\'amour;\nEt quand nous Le verrons\nDans sa gloire là-haut,\nLes chants de Sion retentiront\nPour toujours!\n\nSi Dieu est pour nous,\nQui sera contre nous?  (ter)\nSi Dieu est pour nous,\nQui sera contre nous?\nPersonne, personne, personne!','<p><strong>1.</strong> </p>\n<p>Nous chantons au matin </p>\n<p>Les hymnes de salut, </p>\n<p>Nous chantons à midi </p>\n<p>Les chants de l&#39;amour; </p>\n<p>Et quand nous Le verrons </p>\n<p>Dans sa gloire là-haut, </p>\n<p>Les chants de Sion retentiront </p>\n<p>Pour toujours! </p>\n<p>Si Dieu est pour nous, </p>\n<p>Qui sera contre nous?  (ter) </p>\n<p>Si Dieu est pour nous, </p>\n<p>Qui sera contre nous? </p>\n<p>Personne, personne, personne! </p>\n','Nous-chantons-au-matin',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2101,'91c57b71-6340-45b4-8be4-3f33c6f86847',0,0,'Heureux, béni',100,'100_MJ_fr','1.\nHeureux, béni,\nVoici la bonne nouvelle,\nJésus enleva tous mes péchés!\nEt je chante\nCar la vie est belle,\nJésus enleva tous mes péchés!\nAutrefois la tristesse\nGagnait mon coeur,\nMais la paix est entrée\nAvec mon Sauveur,\nEt je chante\nCar la vie est belle,\nJésus enleva tous mes péchés!','<p><strong>1.</strong> </p>\n<p>Heureux, béni, </p>\n<p>Voici la bonne nouvelle, </p>\n<p>Jésus enleva tous mes péchés! </p>\n<p>Et je chante </p>\n<p>Car la vie est belle, </p>\n<p>Jésus enleva tous mes péchés! </p>\n<p>Autrefois la tristesse </p>\n<p>Gagnait mon coeur, </p>\n<p>Mais la paix est entrée </p>\n<p>Avec mon Sauveur, </p>\n<p>Et je chante </p>\n<p>Car la vie est belle, </p>\n<p>Jésus enleva tous mes péchés! </p>\n','Heureux-beni',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2102,'d9f4149e-3e0b-4d95-9843-d1c108255fac',0,0,'Mon Sauveur est à moi',127,'127_MJ_fr','1.\nMon Sauveur est à moi!\nMon Sauveur est tout à moi!\nEt je suis tout à Lui!\nPour toujours je suis à Lui!','<p><strong>1.</strong> </p>\n<p>Mon Sauveur est à moi! </p>\n<p>Mon Sauveur est tout à moi! </p>\n<p>Et je suis tout à Lui! </p>\n<p>Pour toujours je suis à Lui! </p>\n','Mon-Sauveur-est-a-moi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2103,'4fad9444-051f-40c1-a762-ddec99537388',0,0,'Jésus satisfait mon coeur',137,'137_MJ_fr','1.\nJésus satisfait mon coeur,  (bis)\nJe suis de jour en jour\nJoyeux dans son amour.\nJésus satisfait mon coeur.','<p><strong>1.</strong> </p>\n<p>Jésus satisfait mon coeur,  (bis) </p>\n<p>Je suis de jour en jour </p>\n<p>Joyeux dans son amour. </p>\n<p>Jésus satisfait mon coeur. </p>\n','Jesus-satisfait-mon-coeur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2104,'add512f6-8a42-4514-9aee-fd11ab4c5a3a',0,0,'Oh, je connais un lieu de paix',140,'140_MJ_fr','1.\nOh, je connais un lieu de paix,\nTout près du coeur de Dieu;\nCar le péché n\'y est jamais,\nTout près du coeur de Dieu.\n\nChoeur:\nJésus, douce assurance,\nVenu du sein de Dieu,\nTiens-moi dans ta présence,\nTout près du coeur de Dieu.\n\n2.\nJe connais un lieu de bonheur,\nTout près du coeur de Dieu;\nC\'est là que mon Sauveur demeure,\nTout près du coeur de Dieu.\n\n3.\nJe connais un lieu de repos,\nTout près du coeur de Dieu;\nCar c\'est la maison du Très-Haut,\nTout près du coeur de Dieu.','<p><strong>1.</strong> </p>\n<p>Oh, je connais un lieu de paix, </p>\n<p>Tout près du coeur de Dieu; </p>\n<p>Car le péché n&#39;y est jamais, </p>\n<p>Tout près du coeur de Dieu. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Jésus, douce assurance,</strong> </p>\n<p><strong>Venu du sein de Dieu,</strong> </p>\n<p><strong>Tiens-moi dans ta présence,</strong> </p>\n<p><strong>Tout près du coeur de Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je connais un lieu de bonheur, </p>\n<p>Tout près du coeur de Dieu; </p>\n<p>C&#39;est là que mon Sauveur demeure, </p>\n<p>Tout près du coeur de Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je connais un lieu de repos, </p>\n<p>Tout près du coeur de Dieu; </p>\n<p>Car c&#39;est la maison du Très-Haut, </p>\n<p>Tout près du coeur de Dieu. </p>\n','Oh-je-connais-un-lieu-de-paix',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2105,'4baaf63d-3828-4596-a639-a331b415c9ef',0,0,'Dans mon cœur il est un cri',170,'170_MJ_fr','1.\nDans mon coeur il est un cri\nPour Jésus,\nOh, comme je veux goûter plus\nDe sa grâce;\nJe suis fatigué du chemin,\nEt je ne peux plus;\nJe veux voir mon Sauveur\nFace à face.','<p><strong>1.</strong> </p>\n<p>Dans mon coeur il est un cri </p>\n<p>Pour Jésus, </p>\n<p>Oh, comme je veux goûter plus </p>\n<p>De sa grâce; </p>\n<p>Je suis fatigué du chemin, </p>\n<p>Et je ne peux plus; </p>\n<p>Je veux voir mon Sauveur </p>\n<p>Face à face. </p>\n','Dans-mon-coeur-il-est-un-cri',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2106,'5c531ed0-3282-4dab-9a72-3ad2c74c4575',0,0,'Bon-Dieu Ak Mouin',2,'2_HC_ht','1.\nBon-Dieu ak mouin n-ap maché\nSi byin ansan-m,\nNou sé zanmi intim, amou Li gran,\nNou kozé souvan nan chémin la vi a\nM-pa bézouin pè,\nLi pran souin mouin tout tan..(bis 3 lign)\n\n2.\nBon-Dieu ak mouin n-ap maché\nSi byin ansan-m,\nTout bagay konkou ansan-m\nPou byin mouin,\nLi toujou la, malgré mouin pa kab ouè Li\nLi égzanté mouin nan mové chémin.\n(3 lign 2 foua)\n\n3.\nBon-Dieu ak mouin n-ap maché\nSi byin ansan-m,\nLi fè-m konnin tout plan sélès Li fè,\nLi ba mouin fòs pou maché nan la vi sa a\nM-a réjoui ak Li pou létènité.  (bis 2 lign)','<p><strong>1.</strong> </p>\n<p>Bon-Dieu ak mouin n-ap maché </p>\n<p>Si byin ansan-m, </p>\n<p>Nou sé zanmi intim, amou Li gran, </p>\n<p>Nou kozé souvan nan chémin la vi a </p>\n<p>M-pa bézouin pè, </p>\n<p>Li pran souin mouin tout tan..(bis 3 lign) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bon-Dieu ak mouin n-ap maché </p>\n<p>Si byin ansan-m, </p>\n<p>Tout bagay konkou ansan-m </p>\n<p>Pou byin mouin, </p>\n<p>Li toujou la, malgré mouin pa kab ouè Li </p>\n<p>Li égzanté mouin nan mové chémin. </p>\n<p>(3 lign 2 foua) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bon-Dieu ak mouin n-ap maché </p>\n<p>Si byin ansan-m, </p>\n<p>Li fè-m konnin tout plan sélès Li fè, </p>\n<p>Li ba mouin fòs pou maché nan la vi sa a </p>\n<p>M-a réjoui ak Li pou létènité.  (bis 2 lign) </p>\n','Bon-Dieu-Ak-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2107,'ac8e134e-0513-41c1-af92-fd93c0f38b65',0,0,'Apré',28,'28_HC_ht','1.\nApré fatig ak travay sou la tè\nDifikulté mouin fini.\nLè-m fi-n pasé tout tristès isi-ba,\nAnfin mouin va ouè Jézu.\n\nKè:\nMin Li la ap éspéré-m,\nJézu Sovè-m é Roua-m,\nInstalé sou trò-n Li,\nL-a souété-m byin-vénu,\nLè vouayaj mouin fini.\n\n2.\nLè maladi ak soufrans fi-n pasé,\nÉ mouin ran-n dènié soupi,\nApré batay m-ap éspéré la pè,\nAnfin mouin va ouè Jézu.\n\n3.\nLè solèy kouché é Li fè moua nèt,\nLè tout dévoua akonpli,\nM-ap éspéré tandé voua du Bon Mèt,\nAnfin mouin va ouè Jézu.','<p><strong>1.</strong> </p>\n<p>Apré fatig ak travay sou la tè </p>\n<p>Difikulté mouin fini. </p>\n<p>Lè-m fi-n pasé tout tristès isi-ba, </p>\n<p>Anfin mouin va ouè Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Min Li la ap éspéré-m,</strong> </p>\n<p><strong>Jézu Sovè-m é Roua-m,</strong> </p>\n<p><strong>Instalé sou trò-n Li,</strong> </p>\n<p><strong>L-a souété-m byin-vénu,</strong> </p>\n<p><strong>Lè vouayaj mouin fini.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè maladi ak soufrans fi-n pasé, </p>\n<p>É mouin ran-n dènié soupi, </p>\n<p>Apré batay m-ap éspéré la pè, </p>\n<p>Anfin mouin va ouè Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè solèy kouché é Li fè moua nèt, </p>\n<p>Lè tout dévoua akonpli, </p>\n<p>M-ap éspéré tandé voua du Bon Mèt, </p>\n<p>Anfin mouin va ouè Jézu. </p>\n','Apre',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2108,'47789428-f4c4-4172-bf8a-e54397b1820c',0,0,'Tout Gloua A Jézu',70,'70_HC_ht','1.\nJézu té sové-m, mèvéyé Sovè\nJézu té sové-m, mouin pa kab di kouman,\nSèl sa mouin konnin, Li péyé dèt la,\nLè Li té mouri sou Kalvè soufran.\n\nKè:\nTout gloua a Jézu, mèvéyé Sovè,\nTout gloua a Jézu, sé Li m-adoré,\nTout gloua a Jézu, mèvéyé Sovè,\nTout gloua a Jézu pou létènité.\n\n2.\nJézu va kondui-m, mèvéyé Sovè\nJézu va kondui-m, mouin pa kon-n koté,\nMin mouin va suiv Li, nan joua ou tristès\nTout koté Li minnin-m, ak Li m-alé.\n\n3.\nJézu va chèché-m, mèvéyé Sovè,\nJézu va chèché-m, mouin pa kon-n ki lè,\nGnou jou ma ouè Li, chita sou tron-n Li,\nLi va kouron-nin mouin, dévan anj yo.','<p><strong>1.</strong> </p>\n<p>Jézu té sové-m, mèvéyé Sovè </p>\n<p>Jézu té sové-m, mouin pa kab di kouman, </p>\n<p>Sèl sa mouin konnin, Li péyé dèt la, </p>\n<p>Lè Li té mouri sou Kalvè soufran. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Tout gloua a Jézu, mèvéyé Sovè,</strong> </p>\n<p><strong>Tout gloua a Jézu, sé Li m-adoré,</strong> </p>\n<p><strong>Tout gloua a Jézu, mèvéyé Sovè,</strong> </p>\n<p><strong>Tout gloua a Jézu pou létènité.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu va kondui-m, mèvéyé Sovè </p>\n<p>Jézu va kondui-m, mouin pa kon-n koté, </p>\n<p>Min mouin va suiv Li, nan joua ou tristès </p>\n<p>Tout koté Li minnin-m, ak Li m-alé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu va chèché-m, mèvéyé Sovè, </p>\n<p>Jézu va chèché-m, mouin pa kon-n ki lè, </p>\n<p>Gnou jou ma ouè Li, chita sou tron-n Li, </p>\n<p>Li va kouron-nin mouin, dévan anj yo. </p>\n','Tout-Gloua-A-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2109,'018250b8-6d5b-4fad-a249-6c84d239f0ca',0,0,'Je Veux Monter Sur Les Montagnes',81,'81_HC_fr','1.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvre les cieux.\nJe veux laisser les joies du monde,\nEt me tenir tout près de Dieu.\nJe veux monter sur la montagne\nC’est là que l’on est heureux.\nSi le chemin est rocailleux\nSi les cailloux me s’étampe\nLe Saint Esprit me guidera\nEt Jésus me consolera.\n\n2.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvre les cieux\nJe veux laisser les joie du monde\nEt me tenir tout près de Dieu\nJe veux monter sur la montagne\nC’est là que l’on est heureux.\nSur ce chemin Jésus monta\nEt mon Sauveur me précéda\nAvec Lui je veux m’élever\nJusque sur les plus hauts sommets.\n\n3.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvre les cieux.\nJe veux laisser les joies du monde,\nEt me tenir tout près de Dieu.\nJe veux monter sur la montagne\nC’est là que l’on est heureux.','<p><strong>1.</strong> </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvre les cieux. </p>\n<p>Je veux laisser les joies du monde, </p>\n<p>Et me tenir tout près de Dieu. </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n<p>Si le chemin est rocailleux </p>\n<p>Si les cailloux me s’étampe </p>\n<p>Le Saint Esprit me guidera </p>\n<p>Et Jésus me consolera. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvre les cieux </p>\n<p>Je veux laisser les joie du monde </p>\n<p>Et me tenir tout près de Dieu </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n<p>Sur ce chemin Jésus monta </p>\n<p>Et mon Sauveur me précéda </p>\n<p>Avec Lui je veux m’élever </p>\n<p>Jusque sur les plus hauts sommets. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvre les cieux. </p>\n<p>Je veux laisser les joies du monde, </p>\n<p>Et me tenir tout près de Dieu. </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n','Je-Veux-Monter-Sur-Les-Montagnes',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2110,'d49d1cea-35eb-4de8-80f4-7c2dbcde3f0a',0,0,'Quand Dieu Parle',86,'86_HC_fr','1.\nQuand Dieu parle,\nLes hauts montagnes tremblent,\nQuand Dieu parle, les ondes retentient,\nQuand Dieu parle,\nMon coeur veut l’entendre,\nEt obéir ce qu’Il me dit.\n\nChoeur:\nParle à mon coeur! Parle je t’en prie!\nO Dieu Créateur, mon Seigneur, Rédempteur\nParle à mon coeur aujourd’hui.\n\n2.\nQuand Dieu parle, les anges Lui obéissent,\nQuand Dieu parle, la nature sourit,\nQuand Dieu parle, les coeurs durs sont changés\nCar sa voix plus doux n’est entendue.\n\n3.\nQuand Dieu parle,\nLes tristes sèchent leurs larmes,\nQuand Dieu parle, la fatigue s’arrête,\nQuand Dieu parle, sa voix consolante\nM’inonde de sa paix parfaite.\n\n4.\nQuand Dieu parle, à mon tour je réponds,\nQuand Dieu parle, apaiser ma tempête,\nQuand Dieu parle, à moi de Le suivre,\nEt faire sa volonté complète.','<p><strong>1.</strong> </p>\n<p>Quand Dieu parle, </p>\n<p>Les hauts montagnes tremblent, </p>\n<p>Quand Dieu parle, les ondes retentient, </p>\n<p>Quand Dieu parle, </p>\n<p>Mon coeur veut l’entendre, </p>\n<p>Et obéir ce qu’Il me dit. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Parle à mon coeur! Parle je t’en prie!</strong> </p>\n<p><strong>O Dieu Créateur, mon Seigneur, Rédempteur</strong> </p>\n<p><strong>Parle à mon coeur aujourd’hui.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand Dieu parle, les anges Lui obéissent, </p>\n<p>Quand Dieu parle, la nature sourit, </p>\n<p>Quand Dieu parle, les coeurs durs sont changés </p>\n<p>Car sa voix plus doux n’est entendue. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand Dieu parle, </p>\n<p>Les tristes sèchent leurs larmes, </p>\n<p>Quand Dieu parle, la fatigue s’arrête, </p>\n<p>Quand Dieu parle, sa voix consolante </p>\n<p>M’inonde de sa paix parfaite. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Quand Dieu parle, à mon tour je réponds, </p>\n<p>Quand Dieu parle, apaiser ma tempête, </p>\n<p>Quand Dieu parle, à moi de Le suivre, </p>\n<p>Et faire sa volonté complète. </p>\n','Quand-Dieu-Parle',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2111,'791bb0d6-1d3e-42a8-b921-db3634c02240',0,0,'Se Pa Yon Cheman Tankou Gran Ri',4,'4_RN_ht','1.\nSe pa yon cheman tankou gran ri\nKi plen avèk gwo magazen\nE kote tout machine pase,\nE ou jwenn tout byen materiel.\n\nRefren:\nNou menm nou jwenn\nYon vrè chemen pou n’ale nan syèl\n\n2.\nSe pa yon chemen tankou channmas,\nKote tout moun al pran bon van,\nKe gen frechè, ki gen lonbraj,\nE kit te gen jaden ak bèl flè.\n\n3.\nMande mwenn: ke chemen li ye?\nEm-a va di ou se yon ti chemen li ye\nYon ti chemen etwat anpil\nKote tout moun pa kapab vi-ni.\n\n4.\nSe yon chemen trè danjere,\nKi gen falèz, ki gen danje\nE ki gen tout tan, tout lajounen,\nPlen malady ak pèsekisyon\n\n5.\nOu pa bezwen pè, ou mèt vi-ni\nSi ou vi-ni, ou p’ape sèl\nOu’ap jwenn repo, lapè, lajwa\nAvèk bone isiba.','<p><strong>1.</strong> </p>\n<p>Se pa yon cheman tankou gran ri </p>\n<p>Ki plen avèk gwo magazen </p>\n<p>E kote tout machine pase, </p>\n<p>E ou jwenn tout byen materiel. </p>\n<p>Refren: </p>\n<p>Nou menm nou jwenn </p>\n<p>Yon vrè chemen pou n’ale nan syèl </p>\n<p><br/> <strong>2.</strong></p>\n<p>Se pa yon chemen tankou channmas, </p>\n<p>Kote tout moun al pran bon van, </p>\n<p>Ke gen frechè, ki gen lonbraj, </p>\n<p>E kit te gen jaden ak bèl flè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mande mwenn: ke chemen li ye? </p>\n<p>Em-a va di ou se yon ti chemen li ye </p>\n<p>Yon ti chemen etwat anpil </p>\n<p>Kote tout moun pa kapab vi-ni. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Se yon chemen trè danjere, </p>\n<p>Ki gen falèz, ki gen danje </p>\n<p>E ki gen tout tan, tout lajounen, </p>\n<p>Plen malady ak pèsekisyon </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ou pa bezwen pè, ou mèt vi-ni </p>\n<p>Si ou vi-ni, ou p’ape sèl </p>\n<p>Ou’ap jwenn repo, lapè, lajwa </p>\n<p>Avèk bone isiba. </p>\n','Se-Pa-Yon-Cheman-Tankou-Gran-Ri',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2112,'4def4047-c7d1-40ef-ac4d-580edf8f724a',0,0,'Enseigne-moi à prier, Seigneur',145,'145_MJ_fr','1.\nEnseigne-moi à prier, Seigneur,\nC\'est là le cri constant de mon coeur;\nApprends-moi à prier, Seigneur.\nJe vis en Toi, et Toi, en moi vis,\nDemeurer en Toi, c\'est là mon cri;\nDonne-moi ton pouvoir infini\nDe gagner des âmes en ton Esprit:','<p><strong>1.</strong> </p>\n<p>Enseigne-moi à prier, Seigneur, </p>\n<p>C&#39;est là le cri constant de mon coeur; </p>\n<p>Apprends-moi à prier, Seigneur. </p>\n<p>Je vis en Toi, et Toi, en moi vis, </p>\n<p>Demeurer en Toi, c&#39;est là mon cri; </p>\n<p>Donne-moi ton pouvoir infini </p>\n<p>De gagner des âmes en ton Esprit: </p>\n','Enseigne-moi-a-prier-Seigneur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2113,'70a3ea1c-e483-473d-a611-75a998df416f',0,0,'Dans ces jours-ci, il faut te sauver',157,'157_MJ_fr','1.\nDans ces jours-ci, il faut te sauver\nDans ces jours-ci,\nChrist est le Rocher.\nSois très certain, sois très certain\nQue ton ancre croche le Roc solide.\n\nChoeur:\nCe Roc est Jésus, O, oui, c\'est Lui\nCe Roc est Jésus, seulement Lui.\nSois très certain, sois très certain\nQue ton ancre croche le Roc solide.\n\n2.\nDans ces jours-ci, il faut la Bible\nDans ces jours-ci, sois invincible.\nSois très certain, sois très certain\nQue ton ancre croche le Roc solide.\n\n3.\nDans ces jours-ci, O, je suis sauvé\nDans ces jours-ci, je suis bien ancré\nJe suis certain, je suis certain\nQue mon ancre croche le Roc solide.','<p><strong>1.</strong> </p>\n<p>Dans ces jours-ci, il faut te sauver </p>\n<p>Dans ces jours-ci, </p>\n<p>Christ est le Rocher. </p>\n<p>Sois très certain, sois très certain </p>\n<p>Que ton ancre croche le Roc solide. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Ce Roc est Jésus, O, oui, c&#39;est Lui</strong> </p>\n<p><strong>Ce Roc est Jésus, seulement Lui.</strong> </p>\n<p><strong>Sois très certain, sois très certain</strong> </p>\n<p><strong>Que ton ancre croche le Roc solide.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans ces jours-ci, il faut la Bible </p>\n<p>Dans ces jours-ci, sois invincible. </p>\n<p>Sois très certain, sois très certain </p>\n<p>Que ton ancre croche le Roc solide. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dans ces jours-ci, O, je suis sauvé </p>\n<p>Dans ces jours-ci, je suis bien ancré </p>\n<p>Je suis certain, je suis certain </p>\n<p>Que mon ancre croche le Roc solide. </p>\n','Dans-ces-jours-ci-il-faut-te-sauver',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2114,'26bfb805-5d98-4f24-8071-64d1161ecbc1',0,0,'Doux Maître, laisse-moi marcher',160,'160_MJ_fr','1.\nDoux Maître, laisse-moi marcher\nAvec Toi, dans l\'humilité.\nDonne-moi la puissante clé\nDe patience et de bonté.\n\n2.\nO source de Rédemption\nRemplis-moi de compassion.\nVers tes brebis, conduis mes pas\nQue je les conduise à Tes bras.\n\n3.\nAttire-moi, oh! Chaque jour,\nSeigneur, près de ton coeur d\'amour,\nSoutiens ma foi, Puissant Sauveur,\nRends Ton enfant plus que vainqueur.\n\n4.\nDans l\'espoir de ce grand salut,\nQue tu me procures, Jésus,\nJ\'ai trouvé le repos, la paix\nEt veux demeurer à jamais.','<p><strong>1.</strong> </p>\n<p>Doux Maître, laisse-moi marcher </p>\n<p>Avec Toi, dans l&#39;humilité. </p>\n<p>Donne-moi la puissante clé </p>\n<p>De patience et de bonté. </p>\n<p><br/> <strong>2.</strong></p>\n<p>O source de Rédemption </p>\n<p>Remplis-moi de compassion. </p>\n<p>Vers tes brebis, conduis mes pas </p>\n<p>Que je les conduise à Tes bras. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Attire-moi, oh! Chaque jour, </p>\n<p>Seigneur, près de ton coeur d&#39;amour, </p>\n<p>Soutiens ma foi, Puissant Sauveur, </p>\n<p>Rends Ton enfant plus que vainqueur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dans l&#39;espoir de ce grand salut, </p>\n<p>Que tu me procures, Jésus, </p>\n<p>J&#39;ai trouvé le repos, la paix </p>\n<p>Et veux demeurer à jamais. </p>\n','Doux-Maitre-laisse-moi-marcher',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2115,'2f3cdb0e-2d32-45e6-a2de-89187bf205b4',0,0,'O glorieux message',172,'172_MJ_fr','1.\nO glorieux message\nQui pour nous est un gage\nRevêtu des nuages\nLe Seigneur revient bientôt.\n\nChoeur:\nJésus revient; Jésus revient!\nSoit le matin ou à midi\nSoit le soir, ô jour béni\nJésus revient; Jésus revient!\nO oui, ce sera un jour merveilleux\nCe Jésus revient bientôt!\n\n2.\nLe monde Le diffame\nSon épouse L\'acclame\nLa création proclame\nQue Jésus revient bientôt!\n\n3.\nEnfin dans Sa présence\nAyant Sa ressemblance\nNous verrons Sa puissance\nCar Jésus revient bientôt!','<p><strong>1.</strong> </p>\n<p>O glorieux message </p>\n<p>Qui pour nous est un gage </p>\n<p>Revêtu des nuages </p>\n<p>Le Seigneur revient bientôt. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Jésus revient; Jésus revient!</strong> </p>\n<p><strong>Soit le matin ou à midi</strong> </p>\n<p><strong>Soit le soir, ô jour béni</strong> </p>\n<p><strong>Jésus revient; Jésus revient!</strong> </p>\n<p><strong>O oui, ce sera un jour merveilleux</strong> </p>\n<p><strong>Ce Jésus revient bientôt!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le monde Le diffame </p>\n<p>Son épouse L&#39;acclame </p>\n<p>La création proclame </p>\n<p>Que Jésus revient bientôt! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Enfin dans Sa présence </p>\n<p>Ayant Sa ressemblance </p>\n<p>Nous verrons Sa puissance </p>\n<p>Car Jésus revient bientôt! </p>\n','O-glorieux-message',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2116,'6d0509d1-113c-4a7c-9804-b40d413c2eee',0,0,'M-a Toujou Piloté Ou',41,'41_HC_ht','1.\nPafoua lè kè mouin vlé douté\nÉ-m pa kapab ouè solèy\nMouin voyé jé mouin sou Jézu,\nTanpri, Mèt, piloté mouin.\n\nKè:\nPa pè paské m-a avèk ou,\nM-a toujou piloté ou,\nPa okupé vag la mè a\nKonfié nan mouin pou toujou.\n\n2.\nSouvan lè nan-m mouin fatigé,\nÉ jounin an vlé trénin,\nMouin gadé koté pilot la,\nÉ sé chan sa mouin tandé.\n\n3.\nLè tantasion yo antouré-m,\nÉ-m prèské pèdu chémin,\nNan mitan tanpèt anrajé,\nSovè a palé ak mouin.\n\n4.\nLè mouin rivé ba Joudin an,\nRiviè ap débòdé,\nSou rivaj mouin ouè Sovè a,\nLi la pou piloté mouin.','<p><strong>1.</strong> </p>\n<p>Pafoua lè kè mouin vlé douté </p>\n<p>É-m pa kapab ouè solèy </p>\n<p>Mouin voyé jé mouin sou Jézu, </p>\n<p>Tanpri, Mèt, piloté mouin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pa pè paské m-a avèk ou,</strong> </p>\n<p><strong>M-a toujou piloté ou,</strong> </p>\n<p><strong>Pa okupé vag la mè a</strong> </p>\n<p><strong>Konfié nan mouin pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan lè nan-m mouin fatigé, </p>\n<p>É jounin an vlé trénin, </p>\n<p>Mouin gadé koté pilot la, </p>\n<p>É sé chan sa mouin tandé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè tantasion yo antouré-m, </p>\n<p>É-m prèské pèdu chémin, </p>\n<p>Nan mitan tanpèt anrajé, </p>\n<p>Sovè a palé ak mouin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè mouin rivé ba Joudin an, </p>\n<p>Riviè ap débòdé, </p>\n<p>Sou rivaj mouin ouè Sovè a, </p>\n<p>Li la pou piloté mouin. </p>\n','M-a-Toujou-Pilote-Ou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2117,'ea13ce7b-213b-4931-8090-3d1ee08746d3',0,0,'Pi Fon,  Pi Fon',46,'46_HC_ht','1.\nPi fon, pi fon mouin pralé,\nNan lamou Jézu, Sovè-m,\nM-ap chèché konnin pouki,\nLi té tèlman rinmin-m,\nPouki Li lonjé min Li,\nOuété-m nan grav péché,\nLi té sové-m, Li té géri-m,\nMalgré-m té byin égaré.\n\n2.\nPi fon, pi fon mouin pralé,\nNan la soufrans dé Jézu.\nM-ap suiv Li nan jadin an\nKoté-L té soué san Li.\nLa Li pran fado doulè,\nPéché lé mon-n antié\nO chè Ségnè, Ségnè édé-m,\nPa kité-m dékourajé.\n\n3.\nPi fon, pi fon mouin pralé,\nNan volonté Jézu-Kri,\nM-ap prié konnin suiv Li\nPou fè Ségnè-m plézi.\nMouin vlé bésé dévan Li,\nProstèné nan pié Li.\nFè sa ou vlé, kasé kè mouin,\nFè-m gnou bon vèso tanpri.','<p><strong>1.</strong> </p>\n<p>Pi fon, pi fon mouin pralé, </p>\n<p>Nan lamou Jézu, Sovè-m, </p>\n<p>M-ap chèché konnin pouki, </p>\n<p>Li té tèlman rinmin-m, </p>\n<p>Pouki Li lonjé min Li, </p>\n<p>Ouété-m nan grav péché, </p>\n<p>Li té sové-m, Li té géri-m, </p>\n<p>Malgré-m té byin égaré. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pi fon, pi fon mouin pralé, </p>\n<p>Nan la soufrans dé Jézu. </p>\n<p>M-ap suiv Li nan jadin an </p>\n<p>Koté-L té soué san Li. </p>\n<p>La Li pran fado doulè, </p>\n<p>Péché lé mon-n antié </p>\n<p>O chè Ségnè, Ségnè édé-m, </p>\n<p>Pa kité-m dékourajé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pi fon, pi fon mouin pralé, </p>\n<p>Nan volonté Jézu-Kri, </p>\n<p>M-ap prié konnin suiv Li </p>\n<p>Pou fè Ségnè-m plézi. </p>\n<p>Mouin vlé bésé dévan Li, </p>\n<p>Prostèné nan pié Li. </p>\n<p>Fè sa ou vlé, kasé kè mouin, </p>\n<p>Fè-m gnou bon vèso tanpri. </p>\n','Pi-Fon-Pi-Fon',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2118,'d8a4e962-bef4-43f3-b977-84a0590e7a56',0,0,'Lè-m Fini Pasé Rout La Vi Sa',5,'5_HC_ht','1.\nVa gingnin gnou lumiè sòti palè anlè\nLè-m fini pasé rout la vi sa,\nSoulajman tout fado apé tan-n mouin anro,\nLè-m fini pasé rout la vi sa.\n\nKè:\nLè vouayaj la fini épi jou mouin rivé\nM-a rantré nan démè sélès la,\nKa Sovè mouin rinmin apé\nTan-n la pou mouin,\nLè-m fini pasé rout la vi sa.\n\n2.\nMalgré rout la mové byinto m-a va chanté\nLè-m fini pasé rout la vi sa,\nSi mouin pèsékuté, Kris va fè mouin blié,\nLè-m fini  pasé rout la vi sa.\n\n3.\nPòt sièl la va ouvri é m-a ouè Jézu-Kri,\nLè-m fini pasé rout la vi sa,\nM-a va adoré Li pou gnou si gran salu,\nLè-m fini pasé rout la vi sa.','<p><strong>1.</strong> </p>\n<p>Va gingnin gnou lumiè sòti palè anlè </p>\n<p>Lè-m fini pasé rout la vi sa, </p>\n<p>Soulajman tout fado apé tan-n mouin anro, </p>\n<p>Lè-m fini pasé rout la vi sa. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè vouayaj la fini épi jou mouin rivé</strong> </p>\n<p><strong>M-a rantré nan démè sélès la,</strong> </p>\n<p><strong>Ka Sovè mouin rinmin apé</strong> </p>\n<p><strong>Tan-n la pou mouin,</strong> </p>\n<p><strong>Lè-m fini pasé rout la vi sa.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Malgré rout la mové byinto m-a va chanté </p>\n<p>Lè-m fini pasé rout la vi sa, </p>\n<p>Si mouin pèsékuté, Kris va fè mouin blié, </p>\n<p>Lè-m fini  pasé rout la vi sa. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pòt sièl la va ouvri é m-a ouè Jézu-Kri, </p>\n<p>Lè-m fini pasé rout la vi sa, </p>\n<p>M-a va adoré Li pou gnou si gran salu, </p>\n<p>Lè-m fini pasé rout la vi sa. </p>\n','Le-m-Fini-Pase-Rout-La-Vi-Sa',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2119,'9279cb1b-b3e8-4a84-808d-52dd2e76de0d',0,0,'Alé Chaché Yo',54,'54_HC_ht','1.\nAlé chèché tou sa yo ki pèdu\nNan mon-n la yo, yo tout égaré,\n«Vini jouin-n mouin» sa sé mésaj Jézu\nSé paròl Mèt la ap répété.\n\nKè:\nAlé byin louin nan tout montagn yo\nTounin ak mouton pèdu yo,\nFè yo antré nan pak Rédanmtè\nJézu Lagno, té pran plas yo.\n\n2.\nAlé chèché mouton yo ki pèdu,\nJézu mandé kè yo ki brizé,\nSé pou kondui yo nan chémin Jézu,\nKondui yo byin vit pinga mizé.\n\n3.\nMouin pralé nan mision avèk Sovè-m,\nMouin va suiv Jézu jou aprè jou,\nMouin va édé tout fèb yo jouin-n Jézu,\nLi vlé ké nou fè sa pou toujou.','<p><strong>1.</strong> </p>\n<p>Alé chèché tou sa yo ki pèdu </p>\n<p>Nan mon-n la yo, yo tout égaré, </p>\n<p>«Vini jouin-n mouin» sa sé mésaj Jézu </p>\n<p>Sé paròl Mèt la ap répété. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alé byin louin nan tout montagn yo</strong> </p>\n<p><strong>Tounin ak mouton pèdu yo,</strong> </p>\n<p><strong>Fè yo antré nan pak Rédanmtè</strong> </p>\n<p><strong>Jézu Lagno, té pran plas yo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Alé chèché mouton yo ki pèdu, </p>\n<p>Jézu mandé kè yo ki brizé, </p>\n<p>Sé pou kondui yo nan chémin Jézu, </p>\n<p>Kondui yo byin vit pinga mizé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin pralé nan mision avèk Sovè-m, </p>\n<p>Mouin va suiv Jézu jou aprè jou, </p>\n<p>Mouin va édé tout fèb yo jouin-n Jézu, </p>\n<p>Li vlé ké nou fè sa pou toujou. </p>\n','Ale-Chache-Yo',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2120,'532e7786-5095-4b55-9807-0d39983f1462',0,0,'Mouin Vlé Alé Koté Ou Vlé',76,'76_HC_ht','1.\nPétèt sé pa-p sou gnou gro montagn\nNi nan gnou péi byin louin,\nPétèt sé pa nan gnou gran batay\nKé Ou va sèvi ak mouin\nMin si sé voua pa Ou ki palé,\nMin-m si mouin pa gin angnin,\nM-ap répon-n: «Bon-Dieu, ak min Ou sou mouin\nMouin va alé koté Ou vlé.»\n\nKè:\nMouin va alé koté Ou vlé, Bon-Dieu,\nNan mòn, sou la mè, nan valé,\nMouin va di sa Ou vlé mouin di, Ségnè,\nMouin va sa Ou vlé pou mouin yé.\n\n2.\nPétèt jodi a s paròl dou\nKé Jézu vlé pou-m palé,\nSan dout doué gingnin sou rout la tou\nGnou égaré pou-m chèché.\nO Bon Sovè si ou va gidé-m,\nSi sou rout la ou kinbé-m,\nMouin va répété mésaj la vi\nMouin va di sa Ou vlé mouin di.\n\n3.\nSètènman gingnin gnou vié ti plas,\nNan gran rékolt mon-n sa a,\nKoté-m kab fè min-m sé gnou ti kras,\nPou Jézu krusifié a.\nMa mété konfians mouin nan Ou, Ségnè-m,\nPaské Ou té rinmin-m vré.\nMa fè volonté Ou, chè Sovè-m,\nMouin va sa Ou vlé pou mouin yé.','<p><strong>1.</strong> </p>\n<p>Pétèt sé pa-p sou gnou gro montagn </p>\n<p>Ni nan gnou péi byin louin, </p>\n<p>Pétèt sé pa nan gnou gran batay </p>\n<p>Ké Ou va sèvi ak mouin </p>\n<p>Min si sé voua pa Ou ki palé, </p>\n<p>Min-m si mouin pa gin angnin, </p>\n<p>M-ap répon-n: «Bon-Dieu, ak min Ou sou mouin </p>\n<p>Mouin va alé koté Ou vlé.» </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin va alé koté Ou vlé, Bon-Dieu,</strong> </p>\n<p><strong>Nan mòn, sou la mè, nan valé,</strong> </p>\n<p><strong>Mouin va di sa Ou vlé mouin di, Ségnè,</strong> </p>\n<p><strong>Mouin va sa Ou vlé pou mouin yé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pétèt jodi a s paròl dou </p>\n<p>Ké Jézu vlé pou-m palé, </p>\n<p>San dout doué gingnin sou rout la tou </p>\n<p>Gnou égaré pou-m chèché. </p>\n<p>O Bon Sovè si ou va gidé-m, </p>\n<p>Si sou rout la ou kinbé-m, </p>\n<p>Mouin va répété mésaj la vi </p>\n<p>Mouin va di sa Ou vlé mouin di. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sètènman gingnin gnou vié ti plas, </p>\n<p>Nan gran rékolt mon-n sa a, </p>\n<p>Koté-m kab fè min-m sé gnou ti kras, </p>\n<p>Pou Jézu krusifié a. </p>\n<p>Ma mété konfians mouin nan Ou, Ségnè-m, </p>\n<p>Paské Ou té rinmin-m vré. </p>\n<p>Ma fè volonté Ou, chè Sovè-m, </p>\n<p>Mouin va sa Ou vlé pou mouin yé. </p>\n','Mouin-Vle-Ale-Kote-Ou-Vle',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2121,'a9aaab3b-23f2-434f-94b9-e0aec136380f',0,0,'A La Gnou Gran',88,'88_HC_ht','1.\nSégnè Bon-Dieu mouin sézi nan prézans ou\nLè-m ouè mon-n sa ké min Ou té kréyé\nZétoual yo la, mouin tandé loraj grondé,\nSé puisans Ou tout lunivè montré.\n\nKè:\nAlò nan-m mouin chanté dévan Bon-Dieu\nA la Ou gran, a la Ou gran,\nAlò nan-m mouin chanté dévan Bon-Dieu,\nA la Ou gran, a la Ou gran.\n\n2.\nLè mouin sonjé Bon-Dieu bay sèl Pitit Li\nPou-L vi-n mouri, sé sa ké touché kè-m\nSou boua Kalvè Li pòté tout fado mouin,\nSan Li koulé pou ouété tout péché-m.\n\n3.\nLè Kris parèt anj yo va admiré Li\nL-a pran-m nan sièl nan réjouisans tout tan,\nMa prostèné nan pié-L pou adoré Li,\nÉ proklamé, Bon-Dieu, a la Ou gran.','<p><strong>1.</strong> </p>\n<p>Ségnè Bon-Dieu mouin sézi nan prézans ou </p>\n<p>Lè-m ouè mon-n sa ké min Ou té kréyé </p>\n<p>Zétoual yo la, mouin tandé loraj grondé, </p>\n<p>Sé puisans Ou tout lunivè montré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Alò nan-m mouin chanté dévan Bon-Dieu</strong> </p>\n<p><strong>A la Ou gran, a la Ou gran,</strong> </p>\n<p><strong>Alò nan-m mouin chanté dévan Bon-Dieu,</strong> </p>\n<p><strong>A la Ou gran, a la Ou gran.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè mouin sonjé Bon-Dieu bay sèl Pitit Li </p>\n<p>Pou-L vi-n mouri, sé sa ké touché kè-m </p>\n<p>Sou boua Kalvè Li pòté tout fado mouin, </p>\n<p>San Li koulé pou ouété tout péché-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè Kris parèt anj yo va admiré Li </p>\n<p>L-a pran-m nan sièl nan réjouisans tout tan, </p>\n<p>Ma prostèné nan pié-L pou adoré Li, </p>\n<p>É proklamé, Bon-Dieu, a la Ou gran. </p>\n','A-La-Gnou-Gran',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2122,'4ee46bb7-89c5-481a-bd6b-5455f30c8c47',0,0,'Lè Kè Ou Fè Tris',9,'9_HC_ht','1.\nLè kè ou fè tris, O gadé Jézu\nLi sé pi bon zanmi pasé tout\nOu va jouin-n Jézu kanpé tou pré ou\nPou konsolé ou chak lè, chak jou.\n\nKè:\nKè tris, di tou sa a Jézu,\nAlé koun yé a pa mizé pou di Li tout\nKè tris, bay tout chay a Jézu\nLi va ranpli kè ou avèk joua.\n\n2.\nJézu va bay ou joua pou chak tristès\nBèl soulajman L-a bay pou la pin-n\nJézu-Kri sé toujou Gran Doktè a\nLè ou chèché-L oua jouin-n Li san dout.\n\n3.\nJézu konnin ninpòt traka ou gin\nÉ Li vlé géri nan-m ou troublé\nEsk ou va gin konfians nan amou Li\nJézu sèl kap géri léspri ou.','<p><strong>1.</strong> </p>\n<p>Lè kè ou fè tris, O gadé Jézu </p>\n<p>Li sé pi bon zanmi pasé tout </p>\n<p>Ou va jouin-n Jézu kanpé tou pré ou </p>\n<p>Pou konsolé ou chak lè, chak jou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Kè tris, di tou sa a Jézu,</strong> </p>\n<p><strong>Alé koun yé a pa mizé pou di Li tout</strong> </p>\n<p><strong>Kè tris, bay tout chay a Jézu</strong> </p>\n<p><strong>Li va ranpli kè ou avèk joua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu va bay ou joua pou chak tristès </p>\n<p>Bèl soulajman L-a bay pou la pin-n </p>\n<p>Jézu-Kri sé toujou Gran Doktè a </p>\n<p>Lè ou chèché-L oua jouin-n Li san dout. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu konnin ninpòt traka ou gin </p>\n<p>É Li vlé géri nan-m ou troublé </p>\n<p>Esk ou va gin konfians nan amou Li </p>\n<p>Jézu sèl kap géri léspri ou. </p>\n','Le-Ke-Ou-Fe-Tris',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2123,'51e5f3ce-ab74-4e9a-81ec-3120c7f14f46',0,0,'Dans la sainte cité',84,'84_MJ_fr','1.\nDans la sainte cité, (bis)\nJésus prépare une maison pour moi,\nDéjà je vois sa gloire par la foi;\nDans la sainte cité, (bis)\nAprès la lutte dure\nEt la victoire sure,\nDans la sainte cité.','<p><strong>1.</strong> </p>\n<p>Dans la sainte cité, (bis) </p>\n<p>Jésus prépare une maison pour moi, </p>\n<p>Déjà je vois sa gloire par la foi; </p>\n<p>Dans la sainte cité, (bis) </p>\n<p>Après la lutte dure </p>\n<p>Et la victoire sure, </p>\n<p>Dans la sainte cité. </p>\n','Dans-la-sainte-cite',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2124,'babbd019-93f5-4143-909a-a973832396c8',0,0,'La Parole du Seigneur',109,'109_MJ_fr','1.\nLa Parole du Seigneur\nDemeure éternellement,\nDit l\'apôtre, dit l\'apôtre.','<p><strong>1.</strong> </p>\n<p>La Parole du Seigneur </p>\n<p>Demeure éternellement, </p>\n<p>Dit l&#39;apôtre, dit l&#39;apôtre. </p>\n','La-Parole-du-Seigneur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2125,'4b646499-96d7-44eb-85c2-9a9f798cfd3d',0,0,'Mes yeux ont vu la gloire',115,'115_MJ_fr','1.\nMes yeux ont vu la gloire\nDe Jésus-Christ, mon Sauveur,\nQui remporta la victoire\nSur la croix pour le pécheur;\nLe clairon a déjà sonné,\nÉcoutez, soldats de Dieu!\nJésus va de l\'avant!\n\nChoeur:\nGloire, gloire, alléluia!  (ter)\nJésus va de l\'avant!\n\n2.\nMes yeux ont vu le Calvaire\nEt le bois cruel dressé;\nEt j\'entendis sa prière,\nCri de son coeur angoissé;\nL\'épine sur son front blessé\nMon coeur d\'angoisse a percé,\nJésus va de l\'avant!\n\n3.\nAu jardin, gloire au Père,\nJe Le vois ressuscité;\nLe sceau brisé sur la pierre\nSigna sa divinité!\nIl est sorti tout triomphant,\nIl est vainqueur du péché,\nJésus va de l\'avant!','<p><strong>1.</strong> </p>\n<p>Mes yeux ont vu la gloire </p>\n<p>De Jésus-Christ, mon Sauveur, </p>\n<p>Qui remporta la victoire </p>\n<p>Sur la croix pour le pécheur; </p>\n<p>Le clairon a déjà sonné, </p>\n<p>Écoutez, soldats de Dieu! </p>\n<p>Jésus va de l&#39;avant! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Gloire, gloire, alléluia!  (ter)</strong> </p>\n<p><strong>Jésus va de l&#39;avant!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mes yeux ont vu le Calvaire </p>\n<p>Et le bois cruel dressé; </p>\n<p>Et j&#39;entendis sa prière, </p>\n<p>Cri de son coeur angoissé; </p>\n<p>L&#39;épine sur son front blessé </p>\n<p>Mon coeur d&#39;angoisse a percé, </p>\n<p>Jésus va de l&#39;avant! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au jardin, gloire au Père, </p>\n<p>Je Le vois ressuscité; </p>\n<p>Le sceau brisé sur la pierre </p>\n<p>Signa sa divinité! </p>\n<p>Il est sorti tout triomphant, </p>\n<p>Il est vainqueur du péché, </p>\n<p>Jésus va de l&#39;avant! </p>\n','Mes-yeux-ont-vu-la-gloire',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2126,'2e38cd25-3430-4dfe-97c4-bce3a96557fd',0,0,'Christ pour moi',125,'125_MJ_fr','1.\nChrist pour moi,\nOui, c\'est Christ pour moi,\nIl est mon Sauveur,\nMon Seigneur, mon Roi,\nJe suis content Alléluia!\nChrist pour moi,\nOui, c\'est Christ pour moi,\nChaque jour marchant vers les cieux,\nOui, c\'est Christ pour moi!','<p><strong>1.</strong> </p>\n<p>Christ pour moi, </p>\n<p>Oui, c&#39;est Christ pour moi, </p>\n<p>Il est mon Sauveur, </p>\n<p>Mon Seigneur, mon Roi, </p>\n<p>Je suis content Alléluia! </p>\n<p>Christ pour moi, </p>\n<p>Oui, c&#39;est Christ pour moi, </p>\n<p>Chaque jour marchant vers les cieux, </p>\n<p>Oui, c&#39;est Christ pour moi! </p>\n','Christ-pour-moi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2127,'fc2e25df-171f-4583-922a-71ff276c19ac',0,0,'Bientôt Jésus va venir',128,'128_MJ_fr','1.\nBientôt Jésus va venir\nNous ressusciter,\nNous Le verrons tel qu\'Il est,\nTout glorifié!\nDouleurs, travaux terminés,\nAu ciel enfin arrivés,\nLorsque Jésus-Christ sera venu.','<p><strong>1.</strong> </p>\n<p>Bientôt Jésus va venir </p>\n<p>Nous ressusciter, </p>\n<p>Nous Le verrons tel qu&#39;Il est, </p>\n<p>Tout glorifié! </p>\n<p>Douleurs, travaux terminés, </p>\n<p>Au ciel enfin arrivés, </p>\n<p>Lorsque Jésus-Christ sera venu. </p>\n','Bientot-Jesus-va-venir',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2128,'67d939e1-682d-46ec-9d72-2c374f490ccb',0,0,'Oh, j\'aime entendre sa voix',138,'138_MJ_fr','1.\nOh, j\'aime entendre sa voix\nMe dire: «Tu m\'appartiens;\nJe t\'ai racheté\nPar mon sang précieux;\nGlorifie-moi toujours.»\nEt j\'aime l\'entendre dire:\n«Je t\'ai sauvé par ma grâce»;\nEt quand j\'arriverai là-haut\nOh, ce sera plus beau,\nCar je verrai sa face.','<p><strong>1.</strong> </p>\n<p>Oh, j&#39;aime entendre sa voix </p>\n<p>Me dire: «Tu m&#39;appartiens; </p>\n<p>Je t&#39;ai racheté </p>\n<p>Par mon sang précieux; </p>\n<p>Glorifie-moi toujours.» </p>\n<p>Et j&#39;aime l&#39;entendre dire: </p>\n<p>«Je t&#39;ai sauvé par ma grâce»; </p>\n<p>Et quand j&#39;arriverai là-haut </p>\n<p>Oh, ce sera plus beau, </p>\n<p>Car je verrai sa face. </p>\n','Oh-j\'aime-entendre-sa-voix',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2129,'6c9bff47-2795-43b8-85f7-6b72f09ddf0d',0,0,'Je suis heureux car Jésus m\'a sauvé',152,'152_MJ_fr','1.\nJe suis heureux car Jésus m\'a sauvé;\nDans son amour Il m\'a tout pardonné.\nVoilà pourquoi je me mets à chanter:\nJe suis heureux car Jésus m\'a sauvé.','<p><strong>1.</strong> </p>\n<p>Je suis heureux car Jésus m&#39;a sauvé; </p>\n<p>Dans son amour Il m&#39;a tout pardonné. </p>\n<p>Voilà pourquoi je me mets à chanter: </p>\n<p>Je suis heureux car Jésus m&#39;a sauvé. </p>\n','Je-suis-heureux-car-Jesus-m\'a-sauve',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2130,'87490529-86aa-409f-a3fc-d00d189a7aa3',0,0,'Je suis la lumière, a dit le Seigneur',158,'158_MJ_fr','1.\nJe suis la lumière, a dit le Seigneur;\nEt le monde est ténébreux.\nJe veux briller pour Toi, O mon Sauveur;\nEmbrase-moi de ton feu.\n\nChoeur:\nFais briller en moi ton amour, Jésus!\nFais briller en moi ton Saint-Esprit\nFais briller en moi, O Seigneur Jésus!\nTon grand amour pour le monde perdu.\n\n2.\nQu\'il fait bon à ton service, Seigneur,\nJe veux travailler pou Toi,\nToi, Jésus, mon espérance et mon bonheur,\nViens, détruis tout mal en moi.\n\n3.\nSoumis à Ta volonté, mon Sauveur,\nMalgré le glissant sentier,\nMourir à moi, vivre pour Toi, Seigneur,\nC\'est mon désir désormais.','<p><strong>1.</strong> </p>\n<p>Je suis la lumière, a dit le Seigneur; </p>\n<p>Et le monde est ténébreux. </p>\n<p>Je veux briller pour Toi, O mon Sauveur; </p>\n<p>Embrase-moi de ton feu. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Fais briller en moi ton amour, Jésus!</strong> </p>\n<p><strong>Fais briller en moi ton Saint-Esprit</strong> </p>\n<p><strong>Fais briller en moi, O Seigneur Jésus!</strong> </p>\n<p><strong>Ton grand amour pour le monde perdu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Qu&#39;il fait bon à ton service, Seigneur, </p>\n<p>Je veux travailler pou Toi, </p>\n<p>Toi, Jésus, mon espérance et mon bonheur, </p>\n<p>Viens, détruis tout mal en moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Soumis à Ta volonté, mon Sauveur, </p>\n<p>Malgré le glissant sentier, </p>\n<p>Mourir à moi, vivre pour Toi, Seigneur, </p>\n<p>C&#39;est mon désir désormais. </p>\n','Je-suis-la-lumiere-a-dit-le-Seigneur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2131,'d728fb67-9b38-4a2c-8acc-61724bacd729',0,0,'Voici, Jésus revient des cieux',167,'167_MJ_fr','1.\nVoici, Jésus revient des cieux.\nQuelle joie, glorieuse attente:\nBientôt nous le verrons des yeux\nRevenir dans la nue éclatante.\n\nChoeur:\nIl vient bientôt   (bis)\nJoyeux nous acclamerons Jésus.\nTenons-nous prêts,\nLevons les yeux en haut\nJésus vient sur la nue.\n\n2.\nLes signes des temps, sur terre, sur mer\nNous disent que le grand jour approche\nPromesse suprême de Dieu le Père,\nJésus doit régner, le temps est proche.\n\n3.\nCe doux espoir ranime nos coeurs.\nHâtons ce jour de fête, mon frère.\nHeureux le coeur où Christ demeure,\nTravaillons, luttons dans la prière.','<p><strong>1.</strong> </p>\n<p>Voici, Jésus revient des cieux. </p>\n<p>Quelle joie, glorieuse attente: </p>\n<p>Bientôt nous le verrons des yeux </p>\n<p>Revenir dans la nue éclatante. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Il vient bientôt   (bis)</strong> </p>\n<p><strong>Joyeux nous acclamerons Jésus.</strong> </p>\n<p><strong>Tenons-nous prêts,</strong> </p>\n<p><strong>Levons les yeux en haut</strong> </p>\n<p><strong>Jésus vient sur la nue.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les signes des temps, sur terre, sur mer </p>\n<p>Nous disent que le grand jour approche </p>\n<p>Promesse suprême de Dieu le Père, </p>\n<p>Jésus doit régner, le temps est proche. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ce doux espoir ranime nos coeurs. </p>\n<p>Hâtons ce jour de fête, mon frère. </p>\n<p>Heureux le coeur où Christ demeure, </p>\n<p>Travaillons, luttons dans la prière. </p>\n','Voici-Jesus-revient-des-cieux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2132,'916cb60c-53ba-4d18-a99c-494336810332',0,0,'Li Vlé Ouété Tout Fado',23,'23_HC_ht','1.\nLé mon-n plin tristès ak mizé\nKè nou chajé chak jou,\nJézu té ouété tout fado yo\nSou mòn Kalvè pou nou.\n\nKè:\nLi vlé ouété tout fado pou ou\nBò Kalvè, bò Kalvè,\nLi vlé ouété tout fado pou ou\nLi tou pré nou toujou.\n\n\n\n\n2.\nLagé tout chaj ou sou Jézu\nKité krint ak sousi\nKà Jézu té ouété fado nou\nSou Kalvè Li mouri.\n\n3.\nNan-m troublé lé Ségnè konnin\nChagrin ki nan kè ou\nMin Li vlé ouété tout fado yo\nSou mòn Kalvè pou ou.','<p><strong>1.</strong> </p>\n<p>Lé mon-n plin tristès ak mizé </p>\n<p>Kè nou chajé chak jou, </p>\n<p>Jézu té ouété tout fado yo </p>\n<p>Sou mòn Kalvè pou nou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li vlé ouété tout fado pou ou</strong> </p>\n<p><strong>Bò Kalvè, bò Kalvè,</strong> </p>\n<p><strong>Li vlé ouété tout fado pou ou</strong> </p>\n<p><strong>Li tou pré nou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lagé tout chaj ou sou Jézu </p>\n<p>Kité krint ak sousi </p>\n<p>Kà Jézu té ouété fado nou </p>\n<p>Sou Kalvè Li mouri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan-m troublé lé Ségnè konnin </p>\n<p>Chagrin ki nan kè ou </p>\n<p>Min Li vlé ouété tout fado yo </p>\n<p>Sou mòn Kalvè pou ou. </p>\n','Li-Vle-Ouete-Tout-Fado',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2133,'c36eba7a-967d-4974-84c5-8ca4b884ac88',0,0,'Ki Sa Pou-m Bay Ou  Mèt Mouin?',26,'26_HC_ht','1.\nKi sa pou-m ba Ou Mèt mouin?\nOu té mouri pou mouin,\nEské-m doué bay gnou vié ti bagay,\nKonbyin pou Li réklamé.\n\nKè:\nJézu Sovè ak Ségnè,\nOu sové-m tout antié,\nOu té kité tròn-n nan sièl\nMouri sou boua Kalvè,\nKi sa pou-m ba ou Mèt mouin,\nOu sové-m tout antié\nPa gnou mòso, ni ti kichoy, non,\nM-a ba ou tout, Ségnè.\n\n2.\nKi sa pou-m ba ou Mèt mouin?\nOu té rachté nan-m mouin,\nKado-m piti, min tout sé pou Li,\nPou Li fè tou sa Li vlé.\n\n3.\nKi sa pou-m ba ou Mèt mouin?\nGran byinfètè divin,\nMouin pap kinbé sa mouin posédé,\nM-a dispozé sa-m gingnin.','<p><strong>1.</strong> </p>\n<p>Ki sa pou-m ba Ou Mèt mouin? </p>\n<p>Ou té mouri pou mouin, </p>\n<p>Eské-m doué bay gnou vié ti bagay, </p>\n<p>Konbyin pou Li réklamé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu Sovè ak Ségnè,</strong> </p>\n<p><strong>Ou sové-m tout antié,</strong> </p>\n<p><strong>Ou té kité tròn-n nan sièl</strong> </p>\n<p><strong>Mouri sou boua Kalvè,</strong> </p>\n<p><strong>Ki sa pou-m ba ou Mèt mouin,</strong> </p>\n<p><strong>Ou sové-m tout antié</strong> </p>\n<p><strong>Pa gnou mòso, ni ti kichoy, non,</strong> </p>\n<p><strong>M-a ba ou tout, Ségnè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ki sa pou-m ba ou Mèt mouin? </p>\n<p>Ou té rachté nan-m mouin, </p>\n<p>Kado-m piti, min tout sé pou Li, </p>\n<p>Pou Li fè tou sa Li vlé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ki sa pou-m ba ou Mèt mouin? </p>\n<p>Gran byinfètè divin, </p>\n<p>Mouin pap kinbé sa mouin posédé, </p>\n<p>M-a dispozé sa-m gingnin. </p>\n','Ki-Sa-Pou-m-Bay-Ou-Met-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2134,'99e48258-ebfa-41c8-b0ab-f692a7903a57',0,0,'Gnou Bagay Pasé Nan Kè Mouin',3,'3_HC_ht','1.\nGnou bagay pasé lè mouin di Jézu,\n«Vini démèré Ségnè Jézu,»\nLi kasé kòd péché ki té maré-m,\nLi afranchi nan-m sa pou toujou.\n\nKè:\nGnou bagay pasé nan kè mouin,\nLè mouin té sové, lè mouin té sové\nGnou bagay pasé nan kè mouin,\nMin-m lè Jézu té sové-m.\n\n2.\nSak pasé mouin pa kab ékspliké li,\nMin, m-konnin kichoy pasé kan min-m,\nKris antré é a la gloua mèvéyé,\nÉ mouin santi la joua nan nan-m mouin.\n\n3.\nGnou bagay pasé, la vi Li mété,\nAndédan mouin vi-n pi dous chak jou,\nVié la vi tris é fatigé chanjé,\nÉ Bon-Dieu parèt pou mouin pa tou.','<p><strong>1.</strong> </p>\n<p>Gnou bagay pasé lè mouin di Jézu, </p>\n<p>«Vini démèré Ségnè Jézu,» </p>\n<p>Li kasé kòd péché ki té maré-m, </p>\n<p>Li afranchi nan-m sa pou toujou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Gnou bagay pasé nan kè mouin,</strong> </p>\n<p><strong>Lè mouin té sové, lè mouin té sové</strong> </p>\n<p><strong>Gnou bagay pasé nan kè mouin,</strong> </p>\n<p><strong>Min-m lè Jézu té sové-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sak pasé mouin pa kab ékspliké li, </p>\n<p>Min, m-konnin kichoy pasé kan min-m, </p>\n<p>Kris antré é a la gloua mèvéyé, </p>\n<p>É mouin santi la joua nan nan-m mouin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou bagay pasé, la vi Li mété, </p>\n<p>Andédan mouin vi-n pi dous chak jou, </p>\n<p>Vié la vi tris é fatigé chanjé, </p>\n<p>É Bon-Dieu parèt pou mouin pa tou. </p>\n','Gnou-Bagay-Pase-Nan-Ke-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2135,'7be4205c-1fe7-48ee-964a-8d8537c62082',0,0,'Mouin Dékouvri',36,'36_HC_ht','1.\nGin moun kap chèché san rété\nPou kichoy ki nouvèl,\nMouin min-m té jouin-n pi bon bagay\nSé la vi étènèl.\n\nKè:\nMouin dékouvri chémin kontantman\nMouin dékouvri chémin la joua\nMouin dékouvri gnou gran soulajman\nSé gnou bonnè ki pa jan-m kaba.\nMouin dékouvri sous bénédiksion\nSété pi bon travay pasé yo tout,\nLè mouin jouin-n Jézu, Sovè-m.\n\n2.\nMouin dékouvri pi bon bijou\nKi gingnin sou tè sa a\nNan Jézu mouin rich pou toujou,\nNan sièl, é isi-ba.','<p><strong>1.</strong> </p>\n<p>Gin moun kap chèché san rété </p>\n<p>Pou kichoy ki nouvèl, </p>\n<p>Mouin min-m té jouin-n pi bon bagay </p>\n<p>Sé la vi étènèl. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin dékouvri chémin kontantman</strong> </p>\n<p><strong>Mouin dékouvri chémin la joua</strong> </p>\n<p><strong>Mouin dékouvri gnou gran soulajman</strong> </p>\n<p><strong>Sé gnou bonnè ki pa jan-m kaba.</strong> </p>\n<p><strong>Mouin dékouvri sous bénédiksion</strong> </p>\n<p><strong>Sété pi bon travay pasé yo tout,</strong> </p>\n<p><strong>Lè mouin jouin-n Jézu, Sovè-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin dékouvri pi bon bijou </p>\n<p>Ki gingnin sou tè sa a </p>\n<p>Nan Jézu mouin rich pou toujou, </p>\n<p>Nan sièl, é isi-ba. </p>\n','Mouin-Dekouvri',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2136,'c161ec56-9977-4547-a27e-dad12d0dcb75',0,0,'Eské Sé Posib Pou-m Ront Lé Ségnè?',67,'67_HC_ht','1.\nEské sa posib pou-m ront lé Ségné\nLè-L té bay vi-L, pou-m ta kab rachté?\nPadonni-m Ségnè, fòt mouin ak péché-m\nDèské mouin té pè jan lòt yo ta moké-m.\n\nKè:\nFè-m byin vayan pou-m konfésé\nA ninpòt lè nan tout koté,\nKé Ou sé Sovè-m, Roua mouin é Ségnè-m\nKé gnou jou nan sièl ou va konfésé-m.\n\n2.\nEské sa posib pou-m ront lé Ségnè\nLòské Li nan sièl apé plédé pou mouin?\nDévan tron-n Bon-Dieu Li fè la priè,\nPou-m toujou vinkè sou lé tantatè.\n\n\n3.\nEské sa posib pou-m ront lé Ségnè\nLè-L ban mouin promès\nKé ma jouin-n Li an lè?\nSi-m palé pou Li, pandan-m isi-ba,\nLi pap ront mouin-min’m lè mouin rivé la.','<p><strong>1.</strong> </p>\n<p>Eské sa posib pou-m ront lé Ségné </p>\n<p>Lè-L té bay vi-L, pou-m ta kab rachté? </p>\n<p>Padonni-m Ségnè, fòt mouin ak péché-m </p>\n<p>Dèské mouin té pè jan lòt yo ta moké-m. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Fè-m byin vayan pou-m konfésé</strong> </p>\n<p><strong>A ninpòt lè nan tout koté,</strong> </p>\n<p><strong>Ké Ou sé Sovè-m, Roua mouin é Ségnè-m</strong> </p>\n<p><strong>Ké gnou jou nan sièl ou va konfésé-m.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Eské sa posib pou-m ront lé Ségnè </p>\n<p>Lòské Li nan sièl apé plédé pou mouin? </p>\n<p>Dévan tron-n Bon-Dieu Li fè la priè, </p>\n<p>Pou-m toujou vinkè sou lé tantatè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Eské sa posib pou-m ront lé Ségnè </p>\n<p>Lè-L ban mouin promès </p>\n<p>Ké ma jouin-n Li an lè? </p>\n<p>Si-m palé pou Li, pandan-m isi-ba, </p>\n<p>Li pap ront mouin-min’m lè mouin rivé la. </p>\n','Eske-Se-Posib-Pou-m-Ront-Le-Segne',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2137,'0297e8f0-eeb0-4a66-bded-66993d262890',0,0,'Anbasadè Dé Jézu-Kri',89,'89_HC_ht','1.\nJézu apé voyé Pitit Li publié\nTou patou bon nouvèl dé la vi étènèl,\nNap anonsé péchè amou gnou Rédanmtè\nKi vi-n sou la tè kom Sovè.\n\nKè:\nAnbasadè dé Jézu-Kri,\nKè nou plin puisans Sint-Éspri,\nNapé pòté la pè, nap publié salu,\nPou sové tout moun ki pèdu.\n\n2.\nAvèk joua é kouraj nap publié mésaj\nRoua nou mandé-n pòté\nPou moun ki nan péché.\nPéchè, ouvri kè ou, Jézu ap rélé ou,\nDévan Li tonbé a jénou.\n\n3.\nKouté lè nap palé dévan la koua pòté,\nTout péchè, tout mizè, di Jézu touman ou,\nLi-min’m résévoua ou, San Li kab lavé ou\nSan Jézu-Kri va lavé ou.','<p><strong>1.</strong> </p>\n<p>Jézu apé voyé Pitit Li publié </p>\n<p>Tou patou bon nouvèl dé la vi étènèl, </p>\n<p>Nap anonsé péchè amou gnou Rédanmtè </p>\n<p>Ki vi-n sou la tè kom Sovè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Anbasadè dé Jézu-Kri,</strong> </p>\n<p><strong>Kè nou plin puisans Sint-Éspri,</strong> </p>\n<p><strong>Napé pòté la pè, nap publié salu,</strong> </p>\n<p><strong>Pou sové tout moun ki pèdu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Avèk joua é kouraj nap publié mésaj </p>\n<p>Roua nou mandé-n pòté </p>\n<p>Pou moun ki nan péché. </p>\n<p>Péchè, ouvri kè ou, Jézu ap rélé ou, </p>\n<p>Dévan Li tonbé a jénou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kouté lè nap palé dévan la koua pòté, </p>\n<p>Tout péchè, tout mizè, di Jézu touman ou, </p>\n<p>Li-min’m résévoua ou, San Li kab lavé ou </p>\n<p>San Jézu-Kri va lavé ou. </p>\n','Anbasade-De-Jezu-Kri',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2138,'5aefde0a-a910-43cd-a532-3fef509cb939',0,0,'Jésu Sauveur Pilote-Moi',92,'92_HC_fr','1.\nJésus Sauveur, pilot-moi\nSur la mer pleine d’effroi\nVers moi roulent des flots noirs,\nQui font perdre tout espoir\nMa carte et mon compas, c’est Toi,\nOh Seigneur, pilote-moi.\n\n2.\nLa mère sait calmer l’enfant\nEt comme elle aux flots bruyants\nQuand Tu dis: «Apaisez-vous,»\nLe vent perd tout son courroux,\nToi, de la mer le puissant Roi,\nOh Seigneur, pilote-moi.\n\n3.\nLorsque je m’approche du port\nDes récifs, le courant fort\nM’enlèvent paix et repos,\nIndique à travers les flots,\nLe seul passage sûr et droit,\nOh Seigneur, pilote-moi.','<p><strong>1.</strong> </p>\n<p>Jésus Sauveur, pilot-moi </p>\n<p>Sur la mer pleine d’effroi </p>\n<p>Vers moi roulent des flots noirs, </p>\n<p>Qui font perdre tout espoir </p>\n<p>Ma carte et mon compas, c’est Toi, </p>\n<p>Oh Seigneur, pilote-moi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>La mère sait calmer l’enfant </p>\n<p>Et comme elle aux flots bruyants </p>\n<p>Quand Tu dis: «Apaisez-vous,» </p>\n<p>Le vent perd tout son courroux, </p>\n<p>Toi, de la mer le puissant Roi, </p>\n<p>Oh Seigneur, pilote-moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lorsque je m’approche du port </p>\n<p>Des récifs, le courant fort </p>\n<p>M’enlèvent paix et repos, </p>\n<p>Indique à travers les flots, </p>\n<p>Le seul passage sûr et droit, </p>\n<p>Oh Seigneur, pilote-moi. </p>\n','Jesu-Sauveur-Pilote-Moi',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2139,'1aa02674-2711-410e-8f82-914083b4e55c',0,0,'Meurtri pour moi',87,'87_MJ_fr','1.\nMeurtri pour moi,  (bis)\nOui, sur la croix,\nIl fut meurtri pour moi;\nDu mal affranchi,\nSauvé par la foi,\nGloire à Jésus\nQui fut meurtri pour moi!\n\n2.\nRessuscité, ressuscité,\nOui, c\'est pour moi\nQu\'Il est ressuscité!\nC\'est dans le ciel\nQue Jésus est monté,\nGloire à Jésus,\nPour moi ressuscité!\n\n3.\nIl reviendra, Il reviendra!\nCéleste espoir,\nLe Seigneur reviendra\nPour m\'enlever\nAuprès de Lui, là-haut!\nGloire à Jésus\nQui pour moi reviendra!','<p><strong>1.</strong> </p>\n<p>Meurtri pour moi,  (bis) </p>\n<p>Oui, sur la croix, </p>\n<p>Il fut meurtri pour moi; </p>\n<p>Du mal affranchi, </p>\n<p>Sauvé par la foi, </p>\n<p>Gloire à Jésus </p>\n<p>Qui fut meurtri pour moi! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ressuscité, ressuscité, </p>\n<p>Oui, c&#39;est pour moi </p>\n<p>Qu&#39;Il est ressuscité! </p>\n<p>C&#39;est dans le ciel </p>\n<p>Que Jésus est monté, </p>\n<p>Gloire à Jésus, </p>\n<p>Pour moi ressuscité! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il reviendra, Il reviendra! </p>\n<p>Céleste espoir, </p>\n<p>Le Seigneur reviendra </p>\n<p>Pour m&#39;enlever </p>\n<p>Auprès de Lui, là-haut! </p>\n<p>Gloire à Jésus </p>\n<p>Qui pour moi reviendra! </p>\n','Meurtri-pour-moi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2140,'860886a1-4a7a-4a49-bf27-982a42d8f385',0,0,'Tout à fait aimable',110,'110_MJ_fr','1.\nTout à fait aimable,\nIl est tout à fait aimable,\nLe plus beau entre dix mille,\nCe Sauveur glorieux;\nIl s\'est donné pour me sauver,\nIl vit au ciel pour me garder,\nIl est tout à fait aimable,\nCe merveilleux Sauveur divin.','<p><strong>1.</strong> </p>\n<p>Tout à fait aimable, </p>\n<p>Il est tout à fait aimable, </p>\n<p>Le plus beau entre dix mille, </p>\n<p>Ce Sauveur glorieux; </p>\n<p>Il s&#39;est donné pour me sauver, </p>\n<p>Il vit au ciel pour me garder, </p>\n<p>Il est tout à fait aimable, </p>\n<p>Ce merveilleux Sauveur divin. </p>\n','Tout-a-fait-aimable',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2141,'39fd6c74-6d65-450b-8761-1227de9ac163',0,0,'Laisse-les, laisse-les',113,'113_MJ_fr','1.\nLaisse-les, laisse-les,\nDonne à Jésus tes fardeaux\nEt laisse-les.\nSi tu crois sans douter\nIl te donnera la paix,\nDonne à Jésus tes fardeaux\nEt laisse-les.','<p><strong>1.</strong> </p>\n<p>Laisse-les, laisse-les, </p>\n<p>Donne à Jésus tes fardeaux </p>\n<p>Et laisse-les. </p>\n<p>Si tu crois sans douter </p>\n<p>Il te donnera la paix, </p>\n<p>Donne à Jésus tes fardeaux </p>\n<p>Et laisse-les. </p>\n','Laisse-les-laisse-les',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2142,'ab961f42-9909-4084-9b08-65a4e17bc96f',0,0,'Je chante, je chante',120,'120_MJ_fr','1.\nJe chante, je chante,\nParce qu\'il m\'a donné un beau chant.\nSa présence me remplit de joie\nEt de victoire.\nJe l\'adore, je l\'adore\nPour les signes de son amour;\nEt bien que je l\'aime ici-bas,\nUn jour je Le verrai face à face.','<p><strong>1.</strong> </p>\n<p>Je chante, je chante, </p>\n<p>Parce qu&#39;il m&#39;a donné un beau chant. </p>\n<p>Sa présence me remplit de joie </p>\n<p>Et de victoire. </p>\n<p>Je l&#39;adore, je l&#39;adore </p>\n<p>Pour les signes de son amour; </p>\n<p>Et bien que je l&#39;aime ici-bas, </p>\n<p>Un jour je Le verrai face à face. </p>\n','Je-chante-je-chante',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2143,'aed0f658-9a59-4b47-b625-7577eae5d1a1',0,0,'Souffle, Esprit de vie',144,'144_MJ_fr','1.\nSouffle, Esprit de vie,\nSouffle sur nous tous;\nViens et purifie\nTon peuple à genoux.\nMets à nu notre âme,\nBrise nos liens,\nQue ta sainte flamme\nNous unisse enfin!','<p><strong>1.</strong> </p>\n<p>Souffle, Esprit de vie, </p>\n<p>Souffle sur nous tous; </p>\n<p>Viens et purifie </p>\n<p>Ton peuple à genoux. </p>\n<p>Mets à nu notre âme, </p>\n<p>Brise nos liens, </p>\n<p>Que ta sainte flamme </p>\n<p>Nous unisse enfin! </p>\n','Souffle-Esprit-de-vie',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2144,'0f002304-7a0d-4f7a-9301-701611b8e5f7',0,0,'Promès La Yo Byin Dous',11,'11_HC_ht','1.\nPromès la yo byin dou, Paròl la bon,\nLi pi présié pasé ninpòt ki lòt mésaj\nKris té gin pansé prop, Li san péché,\nLi sé pi gran égzanp ki janmin égzisté.\n\nKè:\nLè-l kondui-m ma suiv Li\nSuiv Li pou toujou,\nLè-l kondui-m ma suiv Li,\nOui, ma suiv Li pou toujou.\n\n2.\nLamou Jézu montré-m sé mèvéyé,\nPi dous pasé tou sa lèz òm kapab pansé,\nPou sa yo ki pèdu, L-apé plédé,\nLi sé pi gran égzanp ki  janmin égzisté.\n\n3.\n«Vini jouin-n mouin» sé sa L-ap répété,\nPou ou gingnin répo ou-min’m ki fatigé,\nKonfié nan promès Li, sé asuré,\nLi sé pi gran égzanp ki janmin égzisté.','<p><strong>1.</strong> </p>\n<p>Promès la yo byin dou, Paròl la bon, </p>\n<p>Li pi présié pasé ninpòt ki lòt mésaj </p>\n<p>Kris té gin pansé prop, Li san péché, </p>\n<p>Li sé pi gran égzanp ki janmin égzisté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Lè-l kondui-m ma suiv Li</strong> </p>\n<p><strong>Suiv Li pou toujou,</strong> </p>\n<p><strong>Lè-l kondui-m ma suiv Li,</strong> </p>\n<p><strong>Oui, ma suiv Li pou toujou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lamou Jézu montré-m sé mèvéyé, </p>\n<p>Pi dous pasé tou sa lèz òm kapab pansé, </p>\n<p>Pou sa yo ki pèdu, L-apé plédé, </p>\n<p>Li sé pi gran égzanp ki  janmin égzisté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>«Vini jouin-n mouin» sé sa L-ap répété, </p>\n<p>Pou ou gingnin répo ou-min’m ki fatigé, </p>\n<p>Konfié nan promès Li, sé asuré, </p>\n<p>Li sé pi gran égzanp ki janmin égzisté. </p>\n','Promes-La-Yo-Byin-Dous',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2145,'9c27bb48-af5b-4326-a32d-76b6a45fcb69',0,0,'Mèvéyé  Mèvéyé Jézu',17,'17_HC_ht','1.\nOui, jounin nou mèt dékourajan,\nLa nuit nou mèt fatigan\nMin si nou konfié nou nan Jézu,\nN-a jouin-n nan kè nou gnou chan.\n\nKè:\nMèvéyé, mèvéyé Jézu\nSé nan kè nou Li planté gnou chan,\nGnou chan ankourajman,\nGnou chan délivrans,\nOui, nan  kè nou Li planté gnou chan.\n\n2.\nOui, la koua nou pòté mèt byin lou\nN-ap pòté gnou mizè pézan,\nMin Jézu anvi soulajé nou,\nLi rinmin lé mon-n tèlman.\n\n\n3.\nLè sousi apé krazé kè nou,\nLè tristès mété nou an dèy\nSé lè sa Jézu rékonfòté nou,\nLi fè joua nou débòdé.','<p><strong>1.</strong> </p>\n<p>Oui, jounin nou mèt dékourajan, </p>\n<p>La nuit nou mèt fatigan </p>\n<p>Min si nou konfié nou nan Jézu, </p>\n<p>N-a jouin-n nan kè nou gnou chan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mèvéyé, mèvéyé Jézu</strong> </p>\n<p><strong>Sé nan kè nou Li planté gnou chan,</strong> </p>\n<p><strong>Gnou chan ankourajman,</strong> </p>\n<p><strong>Gnou chan délivrans,</strong> </p>\n<p><strong>Oui, nan  kè nou Li planté gnou chan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, la koua nou pòté mèt byin lou </p>\n<p>N-ap pòté gnou mizè pézan, </p>\n<p>Min Jézu anvi soulajé nou, </p>\n<p>Li rinmin lé mon-n tèlman. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè sousi apé krazé kè nou, </p>\n<p>Lè tristès mété nou an dèy </p>\n<p>Sé lè sa Jézu rékonfòté nou, </p>\n<p>Li fè joua nou débòdé. </p>\n','Meveye-Meveye-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2146,'039d32b5-7573-4ac4-9b25-74f0709dfacb',0,0,'Min  Oui  Mouin Kontan',27,'27_HC_ht','1.\nGingnin gnou chan nan kè mouin jodi\nKi pa té la lontan,\nLi la dépi mouin vi-n konvèti,\nPou sa mouin kontan.\n\nKè:\nMin, oui, mouin kontan, kontan,\nMin, oui, mouin kontan, kontan,\nPaské Jézu déjà viv nan kè mouin\nMin, oui, mouin kontan.\n\n2.\nSé lamou admirab Li mété\nNan gnou kè atristé\nMin-m jan fè noua mouin gin doua chanté\nLi fè-m satisfè.\n\n3.\nKomunion ak Kris bay gnou fréchè\nLang pa kab fi-n palé\nM-ap maché avèk Li nan lumiè\nPou sa m-a chanté.\n\n4.\nVini jouin-n Jézu-Kri ou min-m tou\nPandan ou gingnin tan\nÉ ou va patajé la joua nou\nÉ ou va kontan.','<p><strong>1.</strong> </p>\n<p>Gingnin gnou chan nan kè mouin jodi </p>\n<p>Ki pa té la lontan, </p>\n<p>Li la dépi mouin vi-n konvèti, </p>\n<p>Pou sa mouin kontan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Min, oui, mouin kontan, kontan,</strong> </p>\n<p><strong>Min, oui, mouin kontan, kontan,</strong> </p>\n<p><strong>Paské Jézu déjà viv nan kè mouin</strong> </p>\n<p><strong>Min, oui, mouin kontan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé lamou admirab Li mété </p>\n<p>Nan gnou kè atristé </p>\n<p>Min-m jan fè noua mouin gin doua chanté </p>\n<p>Li fè-m satisfè. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Komunion ak Kris bay gnou fréchè </p>\n<p>Lang pa kab fi-n palé </p>\n<p>M-ap maché avèk Li nan lumiè </p>\n<p>Pou sa m-a chanté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vini jouin-n Jézu-Kri ou min-m tou </p>\n<p>Pandan ou gingnin tan </p>\n<p>É ou va patajé la joua nou </p>\n<p>É ou va kontan. </p>\n','Min-Oui-Mouin-Kontan',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2147,'36d403b2-48b3-4f0b-a26d-99a017087e23',0,0,'Min-m Jan Moun Nan Tan Lontan',29,'29_HC_ht','1.\nMin-m jan moun nan tan lontan\nOuè zétoual ann orion.\nJan lumiè fè yo réjoui\nGras a li yo té kondui,\nKonsa Ségnè fè kè nou,\nKapab rivé jouin-n Ou to.\n\n2.\nMin-m jan pié yo té maché\nKoté ti Jézu kouché\nÉ yo mété a jénou\nPou adoré Roua sièl tou.\nKonsa fè pié nou kouri,\nChèché sékou Jézu-Kri.\n\n3.\nMin-m jan yo ofri kado\nLa dévan gnou ti bèso,\nSé konsa nou min-m péchè\nAk gnou kè pròp é sinsè,\nVlé prézantézanté trézò nou,\nBay Roua Jézu pou toujou.\n\n4.\nDirijé nou pa-za-pa,\nSégnè, nan rout étouat la,\nÉ lè vi sa fi-n pasé\nMété nou, tanpri, koté,\nAngnin pap sa anpéché,\nOuè la gloua Ou a janmin.','<p><strong>1.</strong> </p>\n<p>Min-m jan moun nan tan lontan </p>\n<p>Ouè zétoual ann orion. </p>\n<p>Jan lumiè fè yo réjoui </p>\n<p>Gras a li yo té kondui, </p>\n<p>Konsa Ségnè fè kè nou, </p>\n<p>Kapab rivé jouin-n Ou to. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min-m jan pié yo té maché </p>\n<p>Koté ti Jézu kouché </p>\n<p>É yo mété a jénou </p>\n<p>Pou adoré Roua sièl tou. </p>\n<p>Konsa fè pié nou kouri, </p>\n<p>Chèché sékou Jézu-Kri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Min-m jan yo ofri kado </p>\n<p>La dévan gnou ti bèso, </p>\n<p>Sé konsa nou min-m péchè </p>\n<p>Ak gnou kè pròp é sinsè, </p>\n<p>Vlé prézantézanté trézò nou, </p>\n<p>Bay Roua Jézu pou toujou. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dirijé nou pa-za-pa, </p>\n<p>Ségnè, nan rout étouat la, </p>\n<p>É lè vi sa fi-n pasé </p>\n<p>Mété nou, tanpri, koté, </p>\n<p>Angnin pap sa anpéché, </p>\n<p>Ouè la gloua Ou a janmin. </p>\n','Min-m-Jan-Moun-Nan-Tan-Lontan',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2148,'c5d748c9-4462-4fd9-9948-858f46fbc562',0,0,'Eské Gnou Ti Moun Kou Mouin',32,'32_HC_ht','1.\nEské gnou ti moun kou mouin,\nAsé gro pou prié Dieu,\nO, min oui, rété fidèl\nAk pasians é avèk zèl,\nRinmin Kris, obéyi-kouè\nAk tout kè ou déklaré.\n\nKè:\nMèsi, bon Papa, mèsi bon Papa,\nPapa nan sièl nou rémèsié Ou.\n\n2.\nPou légum avèk bèl foui\nPou solèy é pou la pli,\nPou zanmi avèk kay nou\nÉ pou Paròl Bon-Dieu tou,\nPou lékol Dominikal\nÉ sutou pou Lévanjil.\n\n3.\nPou santé é pou la pè,\nPou travay é tan pou joué\nPou la vi ki komansé\nPou la joua ké nou gingnin\nPou Sovè nou, Jézu-Kri\nÉ amou Li san mézi.','<p><strong>1.</strong> </p>\n<p>Eské gnou ti moun kou mouin, </p>\n<p>Asé gro pou prié Dieu, </p>\n<p>O, min oui, rété fidèl </p>\n<p>Ak pasians é avèk zèl, </p>\n<p>Rinmin Kris, obéyi-kouè </p>\n<p>Ak tout kè ou déklaré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mèsi, bon Papa, mèsi bon Papa,</strong> </p>\n<p><strong>Papa nan sièl nou rémèsié Ou.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou légum avèk bèl foui </p>\n<p>Pou solèy é pou la pli, </p>\n<p>Pou zanmi avèk kay nou </p>\n<p>É pou Paròl Bon-Dieu tou, </p>\n<p>Pou lékol Dominikal </p>\n<p>É sutou pou Lévanjil. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pou santé é pou la pè, </p>\n<p>Pou travay é tan pou joué </p>\n<p>Pou la vi ki komansé </p>\n<p>Pou la joua ké nou gingnin </p>\n<p>Pou Sovè nou, Jézu-Kri </p>\n<p>É amou Li san mézi. </p>\n','Eske-Gnou-Ti-Moun-Kou-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2149,'6c15e098-3e3e-4183-a7bf-cf091e22a52b',0,0,'Bib La Kanpé Fèm',34,'34_HC_ht','1.\nBib la kanpé fèm kou gnou rok solid\nMalgré lénmi ataké-l.\nMésaj li gravé nan kè péchè yo\nPou ba yo la vi étènèl.\n\nKè:\nBib la kanpé fèm lè mòn yo tranblé\nLap rété solid si tè a déplasé,\nAnnou bazé la foua nou sou li\nPaské Bib la rété fèm.\n\n2.\nBib la kanpé, l-a kanpé pou toujou\nLè lé mon-n gin tan pasé,\nMouin vlé obéyi tout présèt li yo\nSé lè liv sa ki inspiré.\n\n3.\nBib la pasé tout éprèv nou ba li,\nPaské otè li divin.\nPa gras mouin vlé viv dakò avèk li\nOui, Paròl Bon-Dieu sé pou mouin.','<p><strong>1.</strong> </p>\n<p>Bib la kanpé fèm kou gnou rok solid </p>\n<p>Malgré lénmi ataké-l. </p>\n<p>Mésaj li gravé nan kè péchè yo </p>\n<p>Pou ba yo la vi étènèl. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Bib la kanpé fèm lè mòn yo tranblé</strong> </p>\n<p><strong>Lap rété solid si tè a déplasé,</strong> </p>\n<p><strong>Annou bazé la foua nou sou li</strong> </p>\n<p><strong>Paské Bib la rété fèm.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bib la kanpé, l-a kanpé pou toujou </p>\n<p>Lè lé mon-n gin tan pasé, </p>\n<p>Mouin vlé obéyi tout présèt li yo </p>\n<p>Sé lè liv sa ki inspiré. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bib la pasé tout éprèv nou ba li, </p>\n<p>Paské otè li divin. </p>\n<p>Pa gras mouin vlé viv dakò avèk li </p>\n<p>Oui, Paròl Bon-Dieu sé pou mouin. </p>\n','Bib-La-Kanpe-Fem',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2150,'fc3ed74f-c491-4866-aa09-40008d71aaf3',0,0,'Chak Pa Mouin Fè',49,'49_HC_ht','1.\nChak pa mouin fè\nSovè-m alé dévan mouin\nAk min Li, Li kondui-m nan bon chémin\nNan zorèy Li, mouin palé: «M-adoré Ou»\nNan la joua maché ak Li chak jou.\n\nKè:\nChak pa mouin fè\nM-konnin Jézu va gidé-m,\nNan gnou plin-n pi ro Li toujou minnin-m\nJouk tan gnou jou\nMouin va fè dènié pa mouin\nChak pa mouin fè\nKondui-m pi prè sièl la.\n\n2.\nLè asurans mouin komansé fayi\nÉ dévan-m gin gnou falèz infini.\nMouin viré jé-m é gadé bo Sovè mouin\nLi sé fò mouin lè-m maché avè-l.\n\n3.\nMouin konfié nan Kris malgré sikonstans,\nJézu gingnin la vi an abondans.\nL-apé kinbé klé a ki louvri pòt la,\nPou-m antré nan péyi promès la.','<p><strong>1.</strong> </p>\n<p>Chak pa mouin fè </p>\n<p>Sovè-m alé dévan mouin </p>\n<p>Ak min Li, Li kondui-m nan bon chémin </p>\n<p>Nan zorèy Li, mouin palé: «M-adoré Ou» </p>\n<p>Nan la joua maché ak Li chak jou. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chak pa mouin fè</strong> </p>\n<p><strong>M-konnin Jézu va gidé-m,</strong> </p>\n<p><strong>Nan gnou plin-n pi ro Li toujou minnin-m</strong> </p>\n<p><strong>Jouk tan gnou jou</strong> </p>\n<p><strong>Mouin va fè dènié pa mouin</strong> </p>\n<p><strong>Chak pa mouin fè</strong> </p>\n<p><strong>Kondui-m pi prè sièl la.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè asurans mouin komansé fayi </p>\n<p>É dévan-m gin gnou falèz infini. </p>\n<p>Mouin viré jé-m é gadé bo Sovè mouin </p>\n<p>Li sé fò mouin lè-m maché avè-l. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin konfié nan Kris malgré sikonstans, </p>\n<p>Jézu gingnin la vi an abondans. </p>\n<p>L-apé kinbé klé a ki louvri pòt la, </p>\n<p>Pou-m antré nan péyi promès la. </p>\n','Chak-Pa-Mouin-Fe',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2151,'5bac45be-68ec-4b9c-9987-22e4007d1f7f',0,0,'Lè Nou Ouè Kris',63,'63_HC_ht','1.\nChak jou sanblé si lou, éprèv dézolé nou\nNou tanté mumuré, plingnin, dézéspéré,\nMin Jézu va parèt pou ouété’n nan tanpèt,\nDlo nan jé nou va séché pou tout létènité.\n\nKè:\nSa va vo tout pa pin-n lè nou ouè Jézu\nÉprèv yo pap angnin lè nou ouè Kris.\nLè-m gadé figu Li, tristès yo va fini,\nAk joua nou va kouri, jouk nou ouè Kris.\n\n2.\nLè nou manké lumiè nou maché nan nouasè\nDétrès la vi bat nou, fè nou pa ouè sékou.\nJézu, ki bon Bèjé konnin nou aflijé,\nLi va kléré chémin nou, ouété tristès nou tou.\n\n3.\nLè la mò va vini tout loraj va fini,\nNou va kité la tè pou nou jouin-n tout bonè\nN-a rantré ak la joua jouin-n Jézu nan la gloua\nBon-Dieu va jujé malin\nN-a jété tout chagrin.','<p><strong>1.</strong> </p>\n<p>Chak jou sanblé si lou, éprèv dézolé nou </p>\n<p>Nou tanté mumuré, plingnin, dézéspéré, </p>\n<p>Min Jézu va parèt pou ouété’n nan tanpèt, </p>\n<p>Dlo nan jé nou va séché pou tout létènité. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sa va vo tout pa pin-n lè nou ouè Jézu</strong> </p>\n<p><strong>Éprèv yo pap angnin lè nou ouè Kris.</strong> </p>\n<p><strong>Lè-m gadé figu Li, tristès yo va fini,</strong> </p>\n<p><strong>Ak joua nou va kouri, jouk nou ouè Kris.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè nou manké lumiè nou maché nan nouasè </p>\n<p>Détrès la vi bat nou, fè nou pa ouè sékou. </p>\n<p>Jézu, ki bon Bèjé konnin nou aflijé, </p>\n<p>Li va kléré chémin nou, ouété tristès nou tou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè la mò va vini tout loraj va fini, </p>\n<p>Nou va kité la tè pou nou jouin-n tout bonè </p>\n<p>N-a rantré ak la joua jouin-n Jézu nan la gloua </p>\n<p>Bon-Dieu va jujé malin </p>\n<p>N-a jété tout chagrin. </p>\n','Le-Nou-Oue-Kris',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2152,'870b5e2f-6918-4454-836c-f932eaa6f58a',0,0,'Dousman Ségnè Ou Palé Ak Nou',90,'90_HC_ht','1.\nDousman Ségnè, ou palé ak nou\nVini suiv mouin,\nÉpi lè nou té suiv tras pié Ou,\nOu pa tro louin.\n\nKè:\nTras pié Jézu ki fè rout la byin kléré,\nNou va pousuiv tras sa yo tout koté yo alé.\n\n2.\nSi sé pou mouin alé nan tanp la épi\nOu byin la kay pi malérèz yo      (préché,\nPou-m sèvi Bon-Dieu.\n\n3.\nÉ apré tout travay isi-ba lèv nou fini,\nNa rété nan dènié tras pié yo\nDévan tròn-n Li.','<p><strong>1.</strong> </p>\n<p>Dousman Ségnè, ou palé ak nou </p>\n<p>Vini suiv mouin, </p>\n<p>Épi lè nou té suiv tras pié Ou, </p>\n<p>Ou pa tro louin. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Tras pié Jézu ki fè rout la byin kléré,</strong> </p>\n<p><strong>Nou va pousuiv tras sa yo tout koté yo alé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si sé pou mouin alé nan tanp la épi </p>\n<p>Ou byin la kay pi malérèz yo      (préché, </p>\n<p>Pou-m sèvi Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>É apré tout travay isi-ba lèv nou fini, </p>\n<p>Na rété nan dènié tras pié yo </p>\n<p>Dévan tròn-n Li. </p>\n','Dousman-Segne-Ou-Pale-Ak-Nou',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2153,'f655e6bc-c95a-4469-b121-4298f40dc25c',0,0,'Sòlda Jezu-Kri Debou,  Lè-a rive',14,'14_RN_ht','1.\nSòlda Jezu-Kri debou, lè-a rive\nSovè nou prale parèt an nou leve\nLaresistans ap lite nan tout peyi\nPou nou ranvèse rwayòm movè zespri\n \nRefren :\nN’ap reziste (2 fwa)\nLannwit, lajounen tout tan n’ap reziste\nN’ap konbat rwayòm satan n’ap reziste\n \n2.\nKont tout tantasyon satan n’ap reziste\nKont tout atirans lajan n’ap reziste\nKont lemonn avèk plezi n’ap reziste\nKont tout movè zespri n’ap reziste\n \n3.\nKont tout kalite peche n’ap reziste\nKont tout move karaktè n’ap reziste\nKont  lògèy avèk gro san n’ap reziste\nKont tripotaj, medizans n’ap reziste\n \n4.\nKont satan, kont malady n’ap reziste\nMalgre tout sa n’ap soufri n’ap reziste\nNan lavi kòm lanmò n’ap reziste\nAndedan kòm andeyò n’ap reziste\n \n5.\nMalgre nou nan tout mizè n’ap reziste\nMalgre nou soufri grangou n’ap reziste\nMalgre kò nou touni n’ap reziste\nMalgre soufrans kretyen sou tè-a n’ap  reziste\n \n6.\nMenm si fanmi nou kite nou n’ap reziste\nZanmi nou mèt kite nou n’ap reziste\nYo te mèt kritike nou nap reziste\nMenm Jezu te konn soufri n’ap reziste','<p><strong>1.</strong> </p>\n<p>Sòlda Jezu-Kri debou, lè-a rive </p>\n<p>Sovè nou prale parèt an nou leve </p>\n<p>Laresistans ap lite nan tout peyi </p>\n<p>Pou nou ranvèse rwayòm movè zespri </p>\n<p>Refren : </p>\n<p>N’ap reziste (2 fwa) </p>\n<p>Lannwit, lajounen tout tan n’ap reziste </p>\n<p>N’ap konbat rwayòm satan n’ap reziste </p>\n<p><br/> <strong>2.</strong></p>\n<p>Kont tout tantasyon satan n’ap reziste </p>\n<p>Kont tout atirans lajan n’ap reziste </p>\n<p>Kont lemonn avèk plezi n’ap reziste </p>\n<p>Kont tout movè zespri n’ap reziste </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kont tout kalite peche n’ap reziste </p>\n<p>Kont tout move karaktè n’ap reziste </p>\n<p>Kont  lògèy avèk gro san n’ap reziste </p>\n<p>Kont tripotaj, medizans n’ap reziste </p>\n<p><br/> <strong>4.</strong></p>\n<p>Kont satan, kont malady n’ap reziste </p>\n<p>Malgre tout sa n’ap soufri n’ap reziste </p>\n<p>Nan lavi kòm lanmò n’ap reziste </p>\n<p>Andedan kòm andeyò n’ap reziste </p>\n<p><br/> <strong>5.</strong></p>\n<p>Malgre nou nan tout mizè n’ap reziste </p>\n<p>Malgre nou soufri grangou n’ap reziste </p>\n<p>Malgre kò nou touni n’ap reziste </p>\n<p>Malgre soufrans kretyen sou tè-a n’ap  reziste </p>\n<p><br/> <strong>6.</strong></p>\n<p>Menm si fanmi nou kite nou n’ap reziste </p>\n<p>Zanmi nou mèt kite nou n’ap reziste </p>\n<p>Yo te mèt kritike nou nap reziste </p>\n<p>Menm Jezu te konn soufri n’ap reziste </p>\n','Solda-Jezu-Kri-Debou-Le-a-rive',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2154,'f537d9f2-074f-450e-ae58-7b5effd5df7d',0,0,'Yon Jour Nou Te Konvèti',16,'16_RN_ht','1.\nYon jour nou te konvèti,\nAla yon bèl jou sa\nJezu te padone nou,\nLi te ba nou lafwa,\nNou te di: m’ap swiv Jezu pou letènite\nEske nou toujou fidèl?\nEske nou kretyen vre?\n \nRefren:\nO, nou pa kapab preche lemon so nou gen peche\nSe pou tèt sa n’ap mande  Bondye pou yon revèy\nFidèl yo, misyonè yo, paste yo, se vre\nNa wè pwisans le Seyè lè nou gen yon revèy','<p><strong>1.</strong> </p>\n<p>Yon jour nou te konvèti, </p>\n<p>Ala yon bèl jou sa </p>\n<p>Jezu te padone nou, </p>\n<p>Li te ba nou lafwa, </p>\n<p>Nou te di: m’ap swiv Jezu pou letènite </p>\n<p>Eske nou toujou fidèl? </p>\n<p>Eske nou kretyen vre? </p>\n<p>Refren: </p>\n<p>O, nou pa kapab preche lemon so nou gen peche </p>\n<p>Se pou tèt sa n’ap mande  Bondye pou yon revèy </p>\n<p>Fidèl yo, misyonè yo, paste yo, se vre </p>\n<p>Na wè pwisans le Seyè lè nou gen yon revèy </p>\n','Yon-Jour-Nou-Te-Konveti',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2155,'173dbed4-7698-48a8-9a8f-a53455cde84c',0,0,'Aux hommes perdus',61,'61_MJ_fr','1.\nAux hommes perdus\nQue le péché tourmente\nEnvoyez l\'Évangile;\nA leur cri resterons-nous indifférents?\nEnvoyez l\'Évangile.\n\nChoeur:\nEnvoyez la lumière de vie\nAux hommes qui\nMeurent dans la nuit.\nIls attendent, la moisson blanchit,\nEnvoyez l\'Évangile.\n\n2.\nDans le profond péché\nLe monde s\'égare\nEnvoyez l\'Évangile.\nChercher les âmes\nAvant qu\'il soit trop tard,\nEnvoyez l\'Évangile.\n\n3.\nJésus nous commande\nDe les secourir,\nEnvoyez l\'Évangile.\nPouvons-nous\nRefuser de Lui obéir?\nEnvoyez l\'Évangile!','<p><strong>1.</strong> </p>\n<p>Aux hommes perdus </p>\n<p>Que le péché tourmente </p>\n<p>Envoyez l&#39;Évangile; </p>\n<p>A leur cri resterons-nous indifférents? </p>\n<p>Envoyez l&#39;Évangile. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Envoyez la lumière de vie</strong> </p>\n<p><strong>Aux hommes qui</strong> </p>\n<p><strong>Meurent dans la nuit.</strong> </p>\n<p><strong>Ils attendent, la moisson blanchit,</strong> </p>\n<p><strong>Envoyez l&#39;Évangile.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans le profond péché </p>\n<p>Le monde s&#39;égare </p>\n<p>Envoyez l&#39;Évangile. </p>\n<p>Chercher les âmes </p>\n<p>Avant qu&#39;il soit trop tard, </p>\n<p>Envoyez l&#39;Évangile. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus nous commande </p>\n<p>De les secourir, </p>\n<p>Envoyez l&#39;Évangile. </p>\n<p>Pouvons-nous </p>\n<p>Refuser de Lui obéir? </p>\n<p>Envoyez l&#39;Évangile! </p>\n','Aux-hommes-perdus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2156,'c897a950-236e-406f-af8b-ccac7ac5b168',0,0,'Maranatha soyons en fête!',76,'76_MJ_fr','1.\n«Maranatha» soyons en fête!\nMaranatha! Jésus s\'apprête.\nDéjà s\'ouvre le ciel,\nOh! Bonheur éternel,\nMaranatha! Soyons en fête!\n\n2.\n«Maranatha» bonheur suprême!\nMaranatha! Jésus Lui-même,\nVa descendre du ciel,\nOh! Bonheur éternel,\nMaranatha! Bonheur suprême!\n\n3.\n«Maranantha» bonheur et gloire!\nMaranatha! Joie et victoire,\nQuand Jésus reviendra,\nQu\'Il nous enlèvera,\nMaranatha! Bonheur et gloire!','<p><strong>1.</strong> </p>\n<p>«Maranatha» soyons en fête! </p>\n<p>Maranatha! Jésus s&#39;apprête. </p>\n<p>Déjà s&#39;ouvre le ciel, </p>\n<p>Oh! Bonheur éternel, </p>\n<p>Maranatha! Soyons en fête! </p>\n<p><br/> <strong>2.</strong></p>\n<p>«Maranatha» bonheur suprême! </p>\n<p>Maranatha! Jésus Lui-même, </p>\n<p>Va descendre du ciel, </p>\n<p>Oh! Bonheur éternel, </p>\n<p>Maranatha! Bonheur suprême! </p>\n<p><br/> <strong>3.</strong></p>\n<p>«Maranantha» bonheur et gloire! </p>\n<p>Maranatha! Joie et victoire, </p>\n<p>Quand Jésus reviendra, </p>\n<p>Qu&#39;Il nous enlèvera, </p>\n<p>Maranatha! Bonheur et gloire! </p>\n','Maranatha-soyons-en-fete!',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2157,'48fe3ec4-e111-4527-a997-ab7cd5a95b61',0,0,'Sommes-nous tristes?',65,'65_MJ_fr','1.\nSommes-nous tristes?\nNon, non, non.\nSommes-nous tristes?\nNon,non,non,\nLes troubles viennent,\nLes troubles vont\nMais en Jésus nous nous confions\nSommes-nous tristes?\nNon, non, non.','<p><strong>1.</strong> </p>\n<p>Sommes-nous tristes? </p>\n<p>Non, non, non. </p>\n<p>Sommes-nous tristes? </p>\n<p>Non,non,non, </p>\n<p>Les troubles viennent, </p>\n<p>Les troubles vont </p>\n<p>Mais en Jésus nous nous confions </p>\n<p>Sommes-nous tristes? </p>\n<p>Non, non, non. </p>\n','Sommes-nous-tristes',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2158,'9f07b616-2da6-450d-a503-8a5c01b98d7d',0,0,'Par grâce vous êtes sauvés',103,'103_MJ_fr','1.\nPar grâce vous êtes sauvés,\nPar grâce vous êtes sauvés,\nCela ne vient pas de vous,\nMais c\'est le don de Dieu,\nMais c\'est le don de Dieu.\n\n2.\nNon par les oeuvres sauvés,\nNon par les oeuvres sauvés,\nMais par la foi, mais par la foi,\nPour ne pas vous glorifier.\nPar grâce vous êtes sauvés.','<p><strong>1.</strong> </p>\n<p>Par grâce vous êtes sauvés, </p>\n<p>Par grâce vous êtes sauvés, </p>\n<p>Cela ne vient pas de vous, </p>\n<p>Mais c&#39;est le don de Dieu, </p>\n<p>Mais c&#39;est le don de Dieu. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non par les oeuvres sauvés, </p>\n<p>Non par les oeuvres sauvés, </p>\n<p>Mais par la foi, mais par la foi, </p>\n<p>Pour ne pas vous glorifier. </p>\n<p>Par grâce vous êtes sauvés. </p>\n','Par-grace-vous-etes-sauves',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2159,'7d0c46a7-10cc-4892-ac3e-8bf93d1a6134',0,0,'Il sauve, il garde, il satisfait',107,'107_MJ_fr','1.\nIl sauve, Il garde, Il satisfait,\nJésus, mon Ami divin;\nIl sauve, Il garde, Il satisfait\nTout le long de mon chemin.','<p><strong>1.</strong> </p>\n<p>Il sauve, Il garde, Il satisfait, </p>\n<p>Jésus, mon Ami divin; </p>\n<p>Il sauve, Il garde, Il satisfait </p>\n<p>Tout le long de mon chemin. </p>\n','Il-sauve-il-garde-il-satisfait',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2160,'467a1b6e-7b32-45c9-a548-161b3f8fbf2a',0,0,'Sa grâce me suffit',166,'166_MJ_fr','1.\nSa grâce me suffit,  (bis)\nCar dans ma faiblesse,\nSa force s\'accomplit.  (bis)','<p><strong>1.</strong> </p>\n<p>Sa grâce me suffit,  (bis) </p>\n<p>Car dans ma faiblesse, </p>\n<p>Sa force s&#39;accomplit.  (bis) </p>\n','Sa-grace-me-suffit',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2161,'567430e7-41b5-4492-b8d3-8c5ebdd319b6',0,0,'A La Gnou Amou Mèvéyé',22,'22_HC_ht','1.\nBon-Dieu rinmin gnou péchè kou mouin yé\nLi ouété tristès é fè-m byinn-éré,\nLi ban-m plas tou pré koté Li rété,\nA la gnou amou mèvéyé.\n\nKè:\nAmou, si mèvéyé, amou, si mèvéyé,\nBon-Dieu rinmin gnou péchè kou mouin yé\nNan pouin gnou lòt amou parèy.\n\n2.\nPou plan sa Jézu té byin dispozé,\nMalgré sa té vlé di fòk Li mouri,\nLi obéi Papa-L san ézité,\nA la gnou amou infini.\n\n3.\nNan plan rédanmsion Li Bon-Dieu achté\nGnou ésklav inutil sé sa m-té yé,\nSovè a mouri, san Li té vèsé,\nDélivré gnou varyin antèté.\n\n4.\nLi résévoua-m pito kòm gnou pitit,\nLi pa mété mouin nan ran sèvitè,\nNan gras Li Papa-m anbrasé-m tou souit,\nLè-m chèché padon Li ak tout kè.','<p><strong>1.</strong> </p>\n<p>Bon-Dieu rinmin gnou péchè kou mouin yé </p>\n<p>Li ouété tristès é fè-m byinn-éré, </p>\n<p>Li ban-m plas tou pré koté Li rété, </p>\n<p>A la gnou amou mèvéyé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Amou, si mèvéyé, amou, si mèvéyé,</strong> </p>\n<p><strong>Bon-Dieu rinmin gnou péchè kou mouin yé</strong> </p>\n<p><strong>Nan pouin gnou lòt amou parèy.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou plan sa Jézu té byin dispozé, </p>\n<p>Malgré sa té vlé di fòk Li mouri, </p>\n<p>Li obéi Papa-L san ézité, </p>\n<p>A la gnou amou infini. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan plan rédanmsion Li Bon-Dieu achté </p>\n<p>Gnou ésklav inutil sé sa m-té yé, </p>\n<p>Sovè a mouri, san Li té vèsé, </p>\n<p>Délivré gnou varyin antèté. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li résévoua-m pito kòm gnou pitit, </p>\n<p>Li pa mété mouin nan ran sèvitè, </p>\n<p>Nan gras Li Papa-m anbrasé-m tou souit, </p>\n<p>Lè-m chèché padon Li ak tout kè. </p>\n','A-La-Gnou-Amou-Meveye',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2162,'0b7ac28e-ba17-4ab1-8350-d1abb0962faf',0,0,'Chèché Dieu',37,'37_HC_ht','1.\nLavi sa byinto fini\nTankou fèy yo l-a dévni\nÉ la mò kapab vini – chèché Dieu.\nPaské tan sa ap pasé\nNan-m nou yo va déplasé\nJustis nou pa jan-m asé – chèché Dieu.\n\nKè:\nChèché Dieu, chèché Dieu,\nTandis ké ou nan la vi sa, chèché Dieu.\nSi ou tan-n trop, ap mizé,\nBon chans sa va épuizé,\nÉ ou pap kapab sové, chèché Dieu.\n\n2.\nLòské n-ap maché nan rout\nÉ nou vi-n rivé sou bout\nFok nou sové kout ké kout– Chèché Dieu\nSé Kris min’m ki sèl Sovè\nSèl Pitit Bon-Dieu ki Pè,\nLi rpomèt nan sièl démè – Chèché Dieu.\n\n3.\nVi-n aksépté Jézu-Kri\nNan pouin lòt Sovè kom Li\nLa ba ou lé Sint-Éspri – Chèché Dieu\nNan kè ou L-a mété joua\nSou péché L-a ba ou pouvoua\nVi-n mété nan Li la foua – Chèché Dieu.','<p><strong>1.</strong> </p>\n<p>Lavi sa byinto fini </p>\n<p>Tankou fèy yo l-a dévni </p>\n<p>É la mò kapab vini – chèché Dieu. </p>\n<p>Paské tan sa ap pasé </p>\n<p>Nan-m nou yo va déplasé </p>\n<p>Justis nou pa jan-m asé – chèché Dieu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Chèché Dieu, chèché Dieu,</strong> </p>\n<p><strong>Tandis ké ou nan la vi sa, chèché Dieu.</strong> </p>\n<p><strong>Si ou tan-n trop, ap mizé,</strong> </p>\n<p><strong>Bon chans sa va épuizé,</strong> </p>\n<p><strong>É ou pap kapab sové, chèché Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lòské n-ap maché nan rout </p>\n<p>É nou vi-n rivé sou bout </p>\n<p>Fok nou sové kout ké kout– Chèché Dieu </p>\n<p>Sé Kris min’m ki sèl Sovè </p>\n<p>Sèl Pitit Bon-Dieu ki Pè, </p>\n<p>Li rpomèt nan sièl démè – Chèché Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vi-n aksépté Jézu-Kri </p>\n<p>Nan pouin lòt Sovè kom Li </p>\n<p>La ba ou lé Sint-Éspri – Chèché Dieu </p>\n<p>Nan kè ou L-a mété joua </p>\n<p>Sou péché L-a ba ou pouvoua </p>\n<p>Vi-n mété nan Li la foua – Chèché Dieu. </p>\n','Cheche-Dieu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2163,'228c46e1-315d-4e17-9494-c2164d880a23',0,0,'Nan Jou Sa Yo',44,'44_HC_ht','1.\nKoun yé a nou  bézouin gnou apui,\nKoun yé anou bézouin gnou abri,\nFèt atansion, fèt atansion,\nGin gnou sèl koté ki bay protéksion.\n\nKè:\nSa sé nan Jézu, ki sèl Sovè,\nSa sé nan Jézu, lé vrè bonnè,\nFèt atansion, fèt atansion,\nGin gnou sèl koté ki bay protéksion.\n\n2.\nKoun yé a nou va bézouin Bib nou,\nKoun yé a nou  maché ak konfians,\nFèt atansion, fèt atansion,\nGin gnou sèl koté ki bay protéksion.\n\n3.\nKoun yé a nou gingnin asurans,\nKoun yé a nou maché ak konfians,\nAlélouya,  Alélouya,\nNou déja profité chémin sièl la.','<p><strong>1.</strong> </p>\n<p>Koun yé a nou  bézouin gnou apui, </p>\n<p>Koun yé anou bézouin gnou abri, </p>\n<p>Fèt atansion, fèt atansion, </p>\n<p>Gin gnou sèl koté ki bay protéksion. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Sa sé nan Jézu, ki sèl Sovè,</strong> </p>\n<p><strong>Sa sé nan Jézu, lé vrè bonnè,</strong> </p>\n<p><strong>Fèt atansion, fèt atansion,</strong> </p>\n<p><strong>Gin gnou sèl koté ki bay protéksion.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Koun yé a nou va bézouin Bib nou, </p>\n<p>Koun yé a nou  maché ak konfians, </p>\n<p>Fèt atansion, fèt atansion, </p>\n<p>Gin gnou sèl koté ki bay protéksion. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Koun yé a nou gingnin asurans, </p>\n<p>Koun yé a nou maché ak konfians, </p>\n<p>Alélouya,  Alélouya, </p>\n<p>Nou déja profité chémin sièl la. </p>\n','Nan-Jou-Sa-Yo',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2164,'c872cad8-ccb0-49d0-9dc7-2d99662d3e43',0,0,'Pitit Byinn-Émé Nan Jézu',6,'6_HC_ht','1.\nPitit byinn-émé ana Jézu,\nMèvéyé paròl la gras:\nNan Pitit Li Papa ouè nou\nGras a Kris Li banou plas.\n\nKè:\nA lè-ki lé nou sé pitit Bon-Dieu\nÉ sa nou va yé pakò manifèsté,\nMin nou konnin lè Li va parèt,\nNou konnin lè Li va parèt,\nNou va sanblé Li,\nNou va sanblé Li,\nPaské n-a ouè Li jan Li yé.\n\n2.\nÉspérans béni pa louin nou\nByinto n-a ouè gloua Bon-Dieu\nLè ékla sélès parèt tou\nN-a kontanpl fas Jézu.\n\n3.\nPromès Kris va réalizé,\nN-a gin min-m imaj ak Li,\nSé puisans Li ki transfòmé,\nChak moun ki pral konvèti.','<p><strong>1.</strong> </p>\n<p>Pitit byinn-émé ana Jézu, </p>\n<p>Mèvéyé paròl la gras: </p>\n<p>Nan Pitit Li Papa ouè nou </p>\n<p>Gras a Kris Li banou plas. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A lè-ki lé nou sé pitit Bon-Dieu</strong> </p>\n<p><strong>É sa nou va yé pakò manifèsté,</strong> </p>\n<p><strong>Min nou konnin lè Li va parèt,</strong> </p>\n<p><strong>Nou konnin lè Li va parèt,</strong> </p>\n<p><strong>Nou va sanblé Li,</strong> </p>\n<p><strong>Nou va sanblé Li,</strong> </p>\n<p><strong>Paské n-a ouè Li jan Li yé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Éspérans béni pa louin nou </p>\n<p>Byinto n-a ouè gloua Bon-Dieu </p>\n<p>Lè ékla sélès parèt tou </p>\n<p>N-a kontanpl fas Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Promès Kris va réalizé, </p>\n<p>N-a gin min-m imaj ak Li, </p>\n<p>Sé puisans Li ki transfòmé, </p>\n<p>Chak moun ki pral konvèti. </p>\n','Pitit-Byinn-Eme-Nan-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2165,'094080e0-f0f9-4006-99eb-4e0a351f31f3',0,0,'Mwen Pa Gen Moun K’ap Separe-m Ak Kris',8,'8_RN_ht','1.\nMwen pa gen moun k’ap separe-m ak Kris (2 fwa)\nMwen sonje byen koman m’te pèdi\nNan bafon peche kote m’te ye\nJezu te rachte-m ak san li\nMwen pa gen moun ki kap separe-m ak Kris\n \n2.\nMwen sonje byen koman m’te esklav,\nJezu-Kri te vini ak san li.\nE li peye det peche moun yo,\nMwen pa gen moun k’ap separe-m ak Kris\n \n3.\nO Jezu se mèveye pou mwen,\nKi moun ke k’ap separe-m ak Kris?\nJezu-Kri li menm bon bèje.\nLi te bay pwóp tet li pou sove-m,\nE li te rachte-m nan men satan.\nKi moun k’ap separe-m ak Kris ?\n \n4.\nMwen pa kapab bliye soufrans Kris mwen (2 fwa)\nYo te pandye-l anro lakwa,\nYo te souflete li ak kalót,\nE li aksepte tou sa pou mwen,\nKi moun ki k’ap separe-m ak Kris mwen? (2 fwa)','<p><strong>1.</strong> </p>\n<p>Mwen pa gen moun k’ap separe-m ak Kris (2 fwa) </p>\n<p>Mwen sonje byen koman m’te pèdi </p>\n<p>Nan bafon peche kote m’te ye </p>\n<p>Jezu te rachte-m ak san li </p>\n<p>Mwen pa gen moun ki kap separe-m ak Kris </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen sonje byen koman m’te esklav, </p>\n<p>Jezu-Kri te vini ak san li. </p>\n<p>E li peye det peche moun yo, </p>\n<p>Mwen pa gen moun k’ap separe-m ak Kris </p>\n<p><br/> <strong>3.</strong></p>\n<p>O Jezu se mèveye pou mwen, </p>\n<p>Ki moun ke k’ap separe-m ak Kris? </p>\n<p>Jezu-Kri li menm bon bèje. </p>\n<p>Li te bay pwóp tet li pou sove-m, </p>\n<p>E li te rachte-m nan men satan. </p>\n<p>Ki moun k’ap separe-m ak Kris ? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mwen pa kapab bliye soufrans Kris mwen (2 fwa) </p>\n<p>Yo te pandye-l anro lakwa, </p>\n<p>Yo te souflete li ak kalót, </p>\n<p>E li aksepte tou sa pou mwen, </p>\n<p>Ki moun ki k’ap separe-m ak Kris mwen? (2 fwa) </p>\n','Mwen-Pa-Gen-Moun-K\'ap-Separe-m-Ak-Kris',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2166,'225efb02-1753-4891-8dd3-f7c8b91f11b0',0,0,'Jan Peche-n Yo Miltiplye',11,'11_RN_ht','1.\nJan peche-n yo miltiplye,\nNou mande ou gras Senyè\nPeche kanpe adwat agoch\nNou mande ou gras Senyè\n \nRefren :\nGras, gras padon Senyè\nGras, gras padone nou,\nGras, gras pou fót nou yo,\nNou rekonèt peche nou\n \n2.\nPadone nou, O Dye damou,\nNou rekonèt peche nou,\nAdiltè, vol ak mechanste,\nYo vini miltiolye.\n \n3.\nLang nou se yon kouto debó\nNou mande ou gras Senyè.\nBouch nou pale sa nou pa wè\nNou mande ou gras Senyè\n 4.\nZorèy nou toujou ap tande,\nPou nou fè lamedizans,\nPye nou pa janmen fatige,\nPou nou ka al fè lemal.','<p><strong>1.</strong> </p>\n<p>Jan peche-n yo miltiplye, </p>\n<p>Nou mande ou gras Senyè </p>\n<p>Peche kanpe adwat agoch </p>\n<p>Nou mande ou gras Senyè </p>\n<p>Refren : </p>\n<p>Gras, gras padon Senyè </p>\n<p>Gras, gras padone nou, </p>\n<p>Gras, gras pou fót nou yo, </p>\n<p>Nou rekonèt peche nou </p>\n<p><br/> <strong>2.</strong></p>\n<p>Padone nou, O Dye damou, </p>\n<p>Nou rekonèt peche nou, </p>\n<p>Adiltè, vol ak mechanste, </p>\n<p>Yo vini miltiolye. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lang nou se yon kouto debó </p>\n<p>Nou mande ou gras Senyè. </p>\n<p>Bouch nou pale sa nou pa wè </p>\n<p>Nou mande ou gras Senyè </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zorèy nou toujou ap tande, </p>\n<p>Pou nou fè lamedizans, </p>\n<p>Pye nou pa janmen fatige, </p>\n<p>Pou nou ka al fè lemal. </p>\n','Jan-Peche-n-Yo-Miltiplye',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2167,'778db6fb-dcf1-41bc-a933-6bcf42bd0c4c',0,0,'Mwen si kontan',15,'15_RN_ht','1.\nMwen si kontan\nDi-m pouki ou kontan\nMwen si kontan\nDi mwen pouki sa?\nMwen si kontan\nDi-m pouki sa ou kontan (gason)\nPou mwen kontan tou\nGen yon rezon\nGen yon rezon pouki mkontan\n \n(tout ansanm)\nJezu te sove-m\nKounyea-a li viv nan kèm\nSe pou sa mwen kontan Jodi-a wi\nSe pou sa mwen kontan Jodi-a','<p><strong>1.</strong> </p>\n<p>Mwen si kontan </p>\n<p>Di-m pouki ou kontan </p>\n<p>Mwen si kontan </p>\n<p>Di mwen pouki sa? </p>\n<p>Mwen si kontan </p>\n<p>Di-m pouki sa ou kontan (gason) </p>\n<p>Pou mwen kontan tou </p>\n<p>Gen yon rezon </p>\n<p>Gen yon rezon pouki mkontan </p>\n<p>(tout ansanm) </p>\n<p>Jezu te sove-m </p>\n<p>Kounyea-a li viv nan kèm </p>\n<p>Se pou sa mwen kontan Jodi-a wi </p>\n<p>Se pou sa mwen kontan Jodi-a </p>\n','Mwen-si-kontan',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2168,'fc662d63-17dd-4512-8a38-a8008116225d',0,0,'Voir Jésus à l\'aube du jour',62,'62_MJ_fr','1.\nVoir, Jésus à l\'aube du jour,\nSa présence est douce toujours,\nDans la nuit noire,\nJ\'entends Sa voix,\nQui me dit tout bas: «Crois en Moi.»','<p><strong>1.</strong> </p>\n<p>Voir, Jésus à l&#39;aube du jour, </p>\n<p>Sa présence est douce toujours, </p>\n<p>Dans la nuit noire, </p>\n<p>J&#39;entends Sa voix, </p>\n<p>Qui me dit tout bas: «Crois en Moi.» </p>\n','Voir-Jesus-a-l\'aube-du-jour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2169,'894d44bb-d624-4e52-b29e-be1450c642fb',0,0,'A toi, Seigneur, je m\'abandonne',151,'151_MJ_fr','1.A Toi, Seigneur, je m\'abandonne;Ton grand amour m\'a terrassé.Ma vie entière je te donne:Mes biens, mes rêves et mon passé.2.Que puis-je Te donner encore?Les cieux, la terre sont à Toi.Un coeur qui t\'aime et qui T\'adoreEst plus que tout l\'argent des rois.3.Et je T\'aime, O tendre Maître,Te suivre sans aucun détour.A tous Tes ordres me soumettreSera ma joie pour toujours.4.Pour notre mon qui T\'oublie,Un monde pauvre en vrai bonheur,Accepte, O Christ, et multiplieLe tout que vient t\'offrir mon coeur.','<p><strong>1.</strong> </p>\n<p>A Toi, Seigneur, je m&#39;abandonne; </p>\n<p>Ton grand amour m&#39;a terrassé. </p>\n<p>Ma vie entière je te donne: </p>\n<p>Mes biens, mes rêves et mon passé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que puis-je te donner encore? </p>\n<p>Les cieux, la terre sont à Toi. </p>\n<p>Un coeur qui t&#39;aime et qui T&#39;adore </p>\n<p>Est plus que tout l&#39;argent des rois. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et je T&#39;aime, O tendre Maître, </p>\n<p>Te suivre sans aucun détour. </p>\n<p>A tous Tes ordres me soumettre </p>\n<p>Sera ma joie pour toujours. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour notre mon qui T&#39;oublie, </p>\n<p>Un monde pauvre en vrai bonheur, </p>\n<p>Accepte, O Christ, et multiplie </p>\n<p>Le tout que vient t&#39;offrir mon coeur. </p>\n','A-toi-Seigneur-je-m\'abandonne',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2170,'6fe95833-6923-480b-ac88-9727199933d8',0,0,'A B C D E F G',159,'159_MJ_fr','1.A B C D E F GJésus pour moi s\'est donné,H I J K L M NVers Lui son Esprit me mène.O P Q R S T UIl m\'a donné son salut,V W X Y ZA mon Jésus je me cède.','<p><strong>1.</strong> </p>\n<p>A B C D E F G </p>\n<p>Jésus pour moi s&#39;est donné, </p>\n<p>H I J K L M N </p>\n<p>Vers Lui son Esprit me mène. </p>\n<p>O P Q R S T U </p>\n<p>Il m&#39;a donné son salut, </p>\n<p>V W X Y Z </p>\n<p>A mon Jésus je me cède. </p>\n','A-B-C-D-E-F-G',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2171,'04343522-023c-4d3f-8b9f-9f6915e6dbb7',0,0,'Tout le monde doit savoir',161,'161_MJ_fr','1.\nTout le monde doit savoir  (ter)\nQui Jésus est.\nIl est l\'Étoile du Matin,\nIl est le Lis des Vallons,\nEntre dix mille, le plus beau,\nLui, la Rose de Saron.\nTout le monde doit savoir  (ter)\nQui Jésus est.','<p><strong>1.</strong> </p>\n<p>Tout le monde doit savoir  (ter) </p>\n<p>Qui Jésus est. </p>\n<p>Il est l&#39;Étoile du Matin, </p>\n<p>Il est le Lis des Vallons, </p>\n<p>Entre dix mille, le plus beau, </p>\n<p>Lui, la Rose de Saron. </p>\n<p>Tout le monde doit savoir  (ter) </p>\n<p>Qui Jésus est. </p>\n','Tout-le-monde-doit-savoir',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2172,'c236dc88-5754-4083-9ebf-a7f31393a90d',0,0,'O Kanaan,  Bèl Kanaan',16,'16_HC_ht','1.\nMap luté pou-m rivé laba\nKoté ki gin la pè, la joua,\nSé la mouin vlé rété tout tan\nNan bèl péi dé Kanaan.\n\nKè:\nO Kanaan, bèl Kanaan,\nSé la mouin vlé rété tout tan\nKoté lèt la ak mièl koulé\nKoté ki pa jan-m gin péché\nSé la map rété viv san fin,\nPou toujou ak Kris, Sovè mouin.\n\n2.\nRout la mové min map rivé\nSatan ap chèché fè-m tonbé\nMin Jézu kinbé-m pa-za-pa\nJouk mouin rivé la nan la gloua.\n\n3.\nMouin pap mumuré, ni krié\nMin map toujou pèsévéré\nKa Kris té soufri trop pou mouin\nFòk mouin suiv Li juska la fin.','<p><strong>1.</strong> </p>\n<p>Map luté pou-m rivé laba </p>\n<p>Koté ki gin la pè, la joua, </p>\n<p>Sé la mouin vlé rété tout tan </p>\n<p>Nan bèl péi dé Kanaan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>O Kanaan, bèl Kanaan,</strong> </p>\n<p><strong>Sé la mouin vlé rété tout tan</strong> </p>\n<p><strong>Koté lèt la ak mièl koulé</strong> </p>\n<p><strong>Koté ki pa jan-m gin péché</strong> </p>\n<p><strong>Sé la map rété viv san fin,</strong> </p>\n<p><strong>Pou toujou ak Kris, Sovè mouin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Rout la mové min map rivé </p>\n<p>Satan ap chèché fè-m tonbé </p>\n<p>Min Jézu kinbé-m pa-za-pa </p>\n<p>Jouk mouin rivé la nan la gloua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin pap mumuré, ni krié </p>\n<p>Min map toujou pèsévéré </p>\n<p>Ka Kris té soufri trop pou mouin </p>\n<p>Fòk mouin suiv Li juska la fin. </p>\n','O-Kanaan-Bel-Kanaan',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2173,'077b095a-bdba-4bbe-b1f7-c719b3294efa',0,0,'Vini Jouin-n Mouin',18,'18_HC_ht','1.\nVini jouin-n mouin moun fatigé,\nVini m-a fè nou répozé,\nM-a fè sousi nou yo alé\nPaské mouin dou, plin umilité,\nPaské mouin dou, plin umilité,\nÉ m-a ba nou répo.\n\nKè:\nNou ki chajé, boulvèsé, fatigé,\nVi-n  jouin-n mouin,\nVini donk, m-a ba nou léson,\nJouk pa mouin pa rèd, é chaj mouin léjé,\nJouk pa mouin pa rèd, é chaj mouin léjé,\nVi-n jouin-n mouin é m-a ba nou répo.\n\n2.\nMoun ki anba fado péché\nVi-n résévoua la joua, la pè,\nM-a lévé fado sou kè ou,\nM-a fè nou jouin-n padon pou toujou,\nM-a fè nou jouin-n padon pou toujou,\nÉ m-a ba nou répo.\n\n3.\nAvèk tandrès Sovè-m plédé,\nMoun pa Li L-apé protéjé,\nToujou L-ap rélé vi-n jouin-n mouin,\nÉ m-a fè nan-m pa nou répozé,\nÉ m-a fè nan-m pa nou répozé,\nÉ m-a ba nou répo.\n(Favorites 2 – No. 79)','<p><strong>1.</strong> </p>\n<p>Vini jouin-n mouin moun fatigé, </p>\n<p>Vini m-a fè nou répozé, </p>\n<p>M-a fè sousi nou yo alé </p>\n<p>Paské mouin dou, plin umilité, </p>\n<p>Paské mouin dou, plin umilité, </p>\n<p>É m-a ba nou répo. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Nou ki chajé, boulvèsé, fatigé,</strong> </p>\n<p><strong>Vi-n  jouin-n mouin,</strong> </p>\n<p><strong>Vini donk, m-a ba nou léson,</strong> </p>\n<p><strong>Jouk pa mouin pa rèd, é chaj mouin léjé,</strong> </p>\n<p><strong>Jouk pa mouin pa rèd, é chaj mouin léjé,</strong> </p>\n<p><strong>Vi-n jouin-n mouin é m-a ba nou répo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moun ki anba fado péché </p>\n<p>Vi-n résévoua la joua, la pè, </p>\n<p>M-a lévé fado sou kè ou, </p>\n<p>M-a fè nou jouin-n padon pou toujou, </p>\n<p>M-a fè nou jouin-n padon pou toujou, </p>\n<p>É m-a ba nou répo. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Avèk tandrès Sovè-m plédé, </p>\n<p>Moun pa Li L-apé protéjé, </p>\n<p>Toujou L-ap rélé vi-n jouin-n mouin, </p>\n<p>É m-a fè nan-m pa nou répozé, </p>\n<p>É m-a fè nan-m pa nou répozé, </p>\n<p>É m-a ba nou répo. </p>\n<p>(Favorites 2 – No. 79) </p>\n','Vini-Jouin-n-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2174,'7d9076b1-a590-4d4f-b18a-23ab38649dad',0,0,'Mouin Vlé La Vi-m Konté Pou Jézu',50,'50_HC_ht','1.\nPami lé mon-n kap vouayajé\nSan kalkulé létènité,\nMin tout anbision mouin gingnin\nMouin vlé la vi-m konté pou Jézu.\n\nKè:\nMouin vlé la vi-m konté pou Jézu,\nMouin vlé la vi-m konté pou Jézu,\nNinpòt koté m-alé,\nSé Kris yo kapab ouè,\nMouin vlé la vi-m konté pou Jézu.\n\n\n\n\n2.\nMouin vlé sèvi kom gnou flanbo\nÉ kléré rout pou lèz òt yo\nJouk lè mouin rivé nan tonbo\nMouin vlé la vi-m konté pou Jézu.\n\n3.\nMouin vlé kaché-m nan Jézu-Kri\nÉ fè sèlman sa Li va di\nÉ prézanté lé vrè salu\nMouin vlé la vi-m konté pou Jézu.\n\n4.\nLonnè, lajan, mouin pa anvi,\nMin pito la pè dé Jézu,\nNan ninpòt vil, ninpòt péyi\nMouin vlé la vi-m konté pou Jézu.','<p><strong>1.</strong> </p>\n<p>Pami lé mon-n kap vouayajé </p>\n<p>San kalkulé létènité, </p>\n<p>Min tout anbision mouin gingnin </p>\n<p>Mouin vlé la vi-m konté pou Jézu. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Mouin vlé la vi-m konté pou Jézu,</strong> </p>\n<p><strong>Mouin vlé la vi-m konté pou Jézu,</strong> </p>\n<p><strong>Ninpòt koté m-alé,</strong> </p>\n<p><strong>Sé Kris yo kapab ouè,</strong> </p>\n<p><strong>Mouin vlé la vi-m konté pou Jézu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin vlé sèvi kom gnou flanbo </p>\n<p>É kléré rout pou lèz òt yo </p>\n<p>Jouk lè mouin rivé nan tonbo </p>\n<p>Mouin vlé la vi-m konté pou Jézu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouin vlé kaché-m nan Jézu-Kri </p>\n<p>É fè sèlman sa Li va di </p>\n<p>É prézanté lé vrè salu </p>\n<p>Mouin vlé la vi-m konté pou Jézu. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lonnè, lajan, mouin pa anvi, </p>\n<p>Min pito la pè dé Jézu, </p>\n<p>Nan ninpòt vil, ninpòt péyi </p>\n<p>Mouin vlé la vi-m konté pou Jézu. </p>\n','Mouin-Vle-La-Vi-m-Konte-Pou-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2175,'d92aa764-d5b8-4c45-b639-4c0b644c7512',0,0,'Gran Jou Jujman',72,'72_HC_ht','1.\nMouin révé mouin tandé tronpèt yo\nKi anonsé gran jou jujman.\nMouin révé nasion yo asanblé\nPou yo jujé dévan gran ton-n blan.\nGnou anj éklatan kité tron-n nan,\nSou la tè ak la mè li kanpé.\nLi sèmanté ak min douat sou sièl la\nKé tan va suspan-n égzisté.\n\nKè:\nA la moun kap krié é rélé,\nLè koupab yo tandé santans la\nYo plédé pou montagn yo kaché yo,\nYo priè, min sé té tro ta.\n\n2.\nNon-m rich té parèt, min lajan li\nDépi lontan té disparèt.\nSan angnin dutou nan jujman li\nPou akité gnou si gran dèt.\nGnan nèg té la, min inpòtans li\nPa konté lè la mò mélé,\nLè anj égzaminé achiv la\nGrandè nèg la pa mansioné.\n\n3.\nVèv la té avèk oflin yo\nBon-Dieu tandé lè yo krié.\nNan pouin tristès janmin nan sièl la\nTout dlo nan jé yo fi-n suyé.\nTafiatè té la ak banbochè,\nInsi ké moun ki van-n tafia\nOuangatè, volè ak  adultè a\nTout ansan-m désan-n nan lanfè.\n\n4.\nMésié moral rivé nan jujman\nMin bon zèv moral li pat sufi,\nSa yo ki té krusifié Jézu\nPasé pou moun moral osi\nÉ moun nan ki pa té ko paré\n«Pi ta ma gin tan sové, oui!\nSé pa ko lè pou la rélijion»\nAnfin trouvé tan pou mouri.','<p><strong>1.</strong> </p>\n<p>Mouin révé mouin tandé tronpèt yo </p>\n<p>Ki anonsé gran jou jujman. </p>\n<p>Mouin révé nasion yo asanblé </p>\n<p>Pou yo jujé dévan gran ton-n blan. </p>\n<p>Gnou anj éklatan kité tron-n nan, </p>\n<p>Sou la tè ak la mè li kanpé. </p>\n<p>Li sèmanté ak min douat sou sièl la </p>\n<p>Ké tan va suspan-n égzisté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>A la moun kap krié é rélé,</strong> </p>\n<p><strong>Lè koupab yo tandé santans la</strong> </p>\n<p><strong>Yo plédé pou montagn yo kaché yo,</strong> </p>\n<p><strong>Yo priè, min sé té tro ta.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non-m rich té parèt, min lajan li </p>\n<p>Dépi lontan té disparèt. </p>\n<p>San angnin dutou nan jujman li </p>\n<p>Pou akité gnou si gran dèt. </p>\n<p>Gnan nèg té la, min inpòtans li </p>\n<p>Pa konté lè la mò mélé, </p>\n<p>Lè anj égzaminé achiv la </p>\n<p>Grandè nèg la pa mansioné. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vèv la té avèk oflin yo </p>\n<p>Bon-Dieu tandé lè yo krié. </p>\n<p>Nan pouin tristès janmin nan sièl la </p>\n<p>Tout dlo nan jé yo fi-n suyé. </p>\n<p>Tafiatè té la ak banbochè, </p>\n<p>Insi ké moun ki van-n tafia </p>\n<p>Ouangatè, volè ak  adultè a </p>\n<p>Tout ansan-m désan-n nan lanfè. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mésié moral rivé nan jujman </p>\n<p>Min bon zèv moral li pat sufi, </p>\n<p>Sa yo ki té krusifié Jézu </p>\n<p>Pasé pou moun moral osi </p>\n<p>É moun nan ki pa té ko paré </p>\n<p>«Pi ta ma gin tan sové, oui! </p>\n<p>Sé pa ko lè pou la rélijion» </p>\n<p>Anfin trouvé tan pou mouri. </p>\n','Gran-Jou-Jujman',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2176,'a479968d-2bb2-4956-aa48-346f4b97111e',0,0,'Foi De Nos Aïeux',91,'91_HC_fr','1.\nFoi de nos aïeux, foi vivante,\nFoi des martyrs, vainqueurs du feu,\nDe joie l’âme est rayonnante,\nAu son de ton nom glorieux.\nFoi de nos aïeux, sainte foi,\nQui nous rend forts jusqu’à la mort.\n\n2.\nFoi de nos aïeux, pure, austère,\nQui nous donne la liberté,\nQui nous unit à Dieu, le Père,\nA ses enfants, les rachetés.\nFoi de nos aïeux, sainte foi,\nQui nous rend forts jusqu’à la mort.\n\n3.\nFoi de nos aïeux, foi bénie,\nFaisant amis des ennemis,\nNous t’annonçons par mort, par vie,\nTémoignage d’un coeur contrit.\nFoi de nos aïeux, sainte foi,\nQui nous rend forts jusqu’à la mort.','<p><strong>1.</strong> </p>\n<p>Foi de nos aïeux, foi vivante, </p>\n<p>Foi des martyrs, vainqueurs du feu, </p>\n<p>De joie l’âme est rayonnante, </p>\n<p>Au son de ton nom glorieux. </p>\n<p>Foi de nos aïeux, sainte foi, </p>\n<p>Qui nous rend forts jusqu’à la mort. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Foi de nos aïeux, pure, austère, </p>\n<p>Qui nous donne la liberté, </p>\n<p>Qui nous unit à Dieu, le Père, </p>\n<p>A ses enfants, les rachetés. </p>\n<p>Foi de nos aïeux, sainte foi, </p>\n<p>Qui nous rend forts jusqu’à la mort. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Foi de nos aïeux, foi bénie, </p>\n<p>Faisant amis des ennemis, </p>\n<p>Nous t’annonçons par mort, par vie, </p>\n<p>Témoignage d’un coeur contrit. </p>\n<p>Foi de nos aïeux, sainte foi, </p>\n<p>Qui nous rend forts jusqu’à la mort. </p>\n','Foi-De-Nos-Aieux',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2177,'de8d1749-1731-4055-9ea4-a8b5ae4f7c52',0,0,'Kom Bib La Menm Te Di',1,'1_RN_ht','1.\nKom Bib la menm te di\nN’ap viv nan dènye tan\nAntekris yo deja, toupatou nan lemon\nOu menm ki e avèti, kenbe Bib ou fo\nLe-a deja rive, Sove-a pral parèt\n\nRefrain:\nOu menm pechè vini jwenn Jezu\nFè you retou nan Jezu-Kri\nLi si fidèl la sove ou\n \n2.\nMatye 24 pale lagè ki va rive\nAvek tout katastrof ki va rive toupatou\nVrèman se sa nou wè sou late pa gen pè\nSe lagè ak soufrans, outraj e lanmo\n\n3.\nGen anpil jèn gason avek anpil jèn fi\nYap profite letan, Yap rejwi nan peche\nSi yo te gen bon sans, yo ta vi-n jwenn Jezu\nPa gen anyen serye nan vi sou tè-a','<p><strong>1.</strong> </p>\n<p>Kom Bib la menm te di </p>\n<p>N’ap viv nan dènye tan </p>\n<p>Antekris yo deja, toupatou nan lemon </p>\n<p>Ou menm ki e avèti, kenbe Bib ou fo </p>\n<p>Le-a deja rive, Sove-a pral parèt </p>\n<p>Refrain: </p>\n<p>Ou menm pechè vini jwenn Jezu </p>\n<p>Fè you retou nan Jezu-Kri </p>\n<p>Li si fidèl la sove ou </p>\n<p><br/> <strong>2.</strong></p>\n<p>Matye 24 pale lagè ki va rive </p>\n<p>Avek tout katastrof ki va rive toupatou </p>\n<p>Vrèman se sa nou wè sou late pa gen pè </p>\n<p>Se lagè ak soufrans, outraj e lanmo </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gen anpil jèn gason avek anpil jèn fi </p>\n<p>Yap profite letan, Yap rejwi nan peche </p>\n<p>Si yo te gen bon sans, yo ta vi-n jwenn Jezu </p>\n<p>Pa gen anyen serye nan vi sou tè-a </p>\n','Kom-Bib-La-Menm-Te-Di',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2178,'1c689c6b-0d31-4017-b0f5-cb7c81f2f67f',0,0,'Sa Sa Fe Si Rout La Move',2,'2_RN_ht','1.\nSa sa fe si rout la move?\nMenm si gen move pa?\nLi konnen byen tou sa’m kap fe,\nPapa’m ranje’l konsa.\n\nRefrain:\nM’ap chante nan solèy, nan fè nwa,\nNenpòt sa ki rive ma kontan,\nFò’m chante mwen pa kapab rete,\nPapa’m ranje’l konsa.\n \n2.\nSikonstans yo kapab chanje,\nSa pa fe mwen anyen,\nDeske se konsa Li chwazi,\nPlan Papa’m bon pou mwen.\n \n3.\nLe te deja pase rout sa,\nM’ap swiv Li pa-za-pa,\nMwen gen konfyans ke le Senye,\nAp mennen’m nan syel la.\n\n4.\nSelman lumye a rejwisans,\nAp tann mwen a la fen,\nDonk, mwen pa pe yon ti soufrans,\nPapa’m ranje li byen.','<p><strong>1.</strong> </p>\n<p>Sa sa fe si rout la move? </p>\n<p>Menm si gen move pa? </p>\n<p>Li konnen byen tou sa’m kap fe, </p>\n<p>Papa’m ranje’l konsa. </p>\n<p>Refrain: </p>\n<p>M’ap chante nan solèy, nan fè nwa, </p>\n<p>Nenpòt sa ki rive ma kontan, </p>\n<p>Fò’m chante mwen pa kapab rete, </p>\n<p>Papa’m ranje’l konsa. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sikonstans yo kapab chanje, </p>\n<p>Sa pa fe mwen anyen, </p>\n<p>Deske se konsa Li chwazi, </p>\n<p>Plan Papa’m bon pou mwen. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le te deja pase rout sa, </p>\n<p>M’ap swiv Li pa-za-pa, </p>\n<p>Mwen gen konfyans ke le Senye, </p>\n<p>Ap mennen’m nan syel la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Selman lumye a rejwisans, </p>\n<p>Ap tann mwen a la fen, </p>\n<p>Donk, mwen pa pe yon ti soufrans, </p>\n<p>Papa’m ranje li byen. </p>\n','Sa-Sa-Fe-Si-Rout-La-Move',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2179,'5db3a686-2590-4e97-b5f7-92c23456cb9f',0,0,'Ann Mete-n Pre Pou Jezu',12,'12_RN_ht','1.\nAnn mete-n pre pou Jezu\nMen Jezu ap vi-ni\nAvèk yon avyon nan syèl la\nOua monte ladan li.\n \nRefren :\nAn nou lite pou-n na avyon Kris la\nAn nou lite nou va wè jan sa bèl.\n \n2.\nSe Jezu sèl ki pilot la\nK’ap kondwi avyon sa-a\nPa gen louragan ni tanpèt\nNou pa bezwen pè anyen.\n \n3.\nNou chita nan avyon sa-a\nN’ap chante nou prale\nNou p’ap sonje anyen anko\nSèlman ann al nan syèl la','<p><strong>1.</strong> </p>\n<p>Ann mete-n pre pou Jezu </p>\n<p>Men Jezu ap vi-ni </p>\n<p>Avèk yon avyon nan syèl la </p>\n<p>Oua monte ladan li. </p>\n<p>Refren : </p>\n<p>An nou lite pou-n na avyon Kris la </p>\n<p>An nou lite nou va wè jan sa bèl. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Se Jezu sèl ki pilot la </p>\n<p>K’ap kondwi avyon sa-a </p>\n<p>Pa gen louragan ni tanpèt </p>\n<p>Nou pa bezwen pè anyen. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nou chita nan avyon sa-a </p>\n<p>N’ap chante nou prale </p>\n<p>Nou p’ap sonje anyen anko </p>\n<p>Sèlman ann al nan syèl la </p>\n','Ann-Mete-n-Pre-Pou-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2180,'26c50500-66db-41d7-a5a4-ab54018b21a9',0,0,'Tu nous combles de biens, Seigneur',122,'122_MJ_fr','1.\nTu nous combles de biens, Seigneur!\nTa main de donner ne se lasse.\nAccorde-nous aussi la grâce\nD\'user de tout à ton honneur.','<p><strong>1.</strong> </p>\n<p>Tu nous combles de biens, Seigneur! </p>\n<p>Ta main de donner ne se lasse. </p>\n<p>Accorde-nous aussi la grâce </p>\n<p>D&#39;user de tout à ton honneur. </p>\n','Tu-nous-combles-de-biens-Seigneur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2181,'c116b636-5ec5-44fd-acc5-925c7f9f859b',0,0,'Réveille-toi, jeunesse!',130,'130_MJ_fr','1.\nRéveille-toi, jeunesse!\nEntends l\'appel pressant:\nLe monde est en détresse,\nEn pleurs, en feu, en sang.\nDu Christ la grande armée\nSe lève avec ardeur;\nCelui qui l\'a formée\nRéclame tout ton coeur.\n\n2.\nRéveille-toi, jeunesse!\nDéjà le jour s\'enfuit;\nL\'amour de Dieu te presse:\nTravaille avant la nuit.\nPourquoi gâcher ta vie\nEn la vivant pour toi?\nRenonce à la folie,\nEt suis le Roi des rois.\n\n3.\nRéveille-toi, jeunesse!\nEt fuis les vains plaisirs.\nIl faut qu\'en toi renaisse\nLa flamme des martyrs.\nA l\'aube de la vie\nJoyeuse, élance-toi\nVers Dieu qui te convie\nAu ciel, mais par la croix.','<p><strong>1.</strong> </p>\n<p>Réveille-toi, jeunesse! </p>\n<p>Entends l&#39;appel pressant: </p>\n<p>Le monde est en détresse, </p>\n<p>En pleurs, en feu, en sang. </p>\n<p>Du Christ la grande armée </p>\n<p>Se lève avec ardeur; </p>\n<p>Celui qui l&#39;a formée </p>\n<p>Réclame tout ton coeur. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Réveille-toi, jeunesse! </p>\n<p>Déjà le jour s&#39;enfuit; </p>\n<p>L&#39;amour de Dieu te presse: </p>\n<p>Travaille avant la nuit. </p>\n<p>Pourquoi gâcher ta vie </p>\n<p>En la vivant pour toi? </p>\n<p>Renonce à la folie, </p>\n<p>Et suis le Roi des rois. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Réveille-toi, jeunesse! </p>\n<p>Et fuis les vains plaisirs. </p>\n<p>Il faut qu&#39;en toi renaisse </p>\n<p>La flamme des martyrs. </p>\n<p>A l&#39;aube de la vie </p>\n<p>Joyeuse, élance-toi </p>\n<p>Vers Dieu qui te convie </p>\n<p>Au ciel, mais par la croix. </p>\n','Reveille-toi-jeunesse!',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2182,'0ac867f2-052c-465f-a15d-1ca095c40ce5',0,0,'Alléluia, Jésus',134,'134_MJ_fr','1.\nAlléluia, Jésus\nM\'a sauvé, moi pécheur,\nJe suis pur, racheté,\nPar le sang du Sauveur.  (2 fois)','<p><strong>1.</strong> </p>\n<p>Alléluia, Jésus </p>\n<p>M&#39;a sauvé, moi pécheur, </p>\n<p>Je suis pur, racheté, </p>\n<p>Par le sang du Sauveur.  (2 fois) </p>\n','Alleluia-Jesus',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2183,'0ee8e119-4445-4c0d-8618-395450108860',0,0,'Seulement quelques chagrins',149,'149_MJ_fr','1.\nSeulement quelques chagrins,\nQuelques tristes soupirs,\nQuelques fatigues de l\'étroit chemin;\nPuis, la grande trompette\nDans les cieux va retentir,\nSeulement quelques chagrins,\nPuis, Il vient!\n\nChoeur:\nSeulement quelques chagrins,\nPuis, Il vient.\nSeulement quelques chagrins,\nPuis, Il vient.\nOh, quelle glorieuse bienvenue\nNous attend!\nSeulement quelques chagrins,\nPuis, Il vient!\n\n2.\nSeulement quelque chagrins,\nQuelques tristes adieux,\nQuelques souffrances dans ce monde vain;\nPuis, avec notre Sauveur\nUne rencontrée aux cieux,\nSeulement quelques chagrins,\nPuis, Il vient!\n\n3.\nSeulement quelques chagrins,\nPuis, contempler Sa face\nDans la gloire de joyeux lendemain;\nLes moments passent,\nVite finira ce jour de grâce,\nSeulement quelques chagrins,\nPuis, Il vient!','<p><strong>1.</strong> </p>\n<p>Seulement quelques chagrins, </p>\n<p>Quelques tristes soupirs, </p>\n<p>Quelques fatigues de l&#39;étroit chemin; </p>\n<p>Puis, la grande trompette </p>\n<p>Dans les cieux va retentir, </p>\n<p>Seulement quelques chagrins, </p>\n<p>Puis, Il vient! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Seulement quelques chagrins,</strong> </p>\n<p><strong>Puis, Il vient.</strong> </p>\n<p><strong>Seulement quelques chagrins,</strong> </p>\n<p><strong>Puis, Il vient.</strong> </p>\n<p><strong>Oh, quelle glorieuse bienvenue</strong> </p>\n<p><strong>Nous attend!</strong> </p>\n<p><strong>Seulement quelques chagrins,</strong> </p>\n<p><strong>Puis, Il vient!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seulement quelque chagrins, </p>\n<p>Quelques tristes adieux, </p>\n<p>Quelques souffrances dans ce monde vain; </p>\n<p>Puis, avec notre Sauveur </p>\n<p>Une rencontrée aux cieux, </p>\n<p>Seulement quelques chagrins, </p>\n<p>Puis, Il vient! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seulement quelques chagrins, </p>\n<p>Puis, contempler Sa face </p>\n<p>Dans la gloire de joyeux lendemain; </p>\n<p>Les moments passent, </p>\n<p>Vite finira ce jour de grâce, </p>\n<p>Seulement quelques chagrins, </p>\n<p>Puis, Il vient! </p>\n','Seulement-quelques-chagrins',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2184,'9ca10b94-cb42-4f6d-9c1e-c6953843bcf4',0,0,'Tu laissas ton trône et ta couronne',178,'178_MJ_fr','1.\nTu laissas Ton trône et Ta couronne\nQuand Tu vins sur la terre pour moi,\nMais Tu fus sans amis à l\'hôtellerie\nQuoique Tu sois le roi des rois.\n\nChoeur:\nViens donc dans mon coeur\nSeigneur Jésus;\nPrends Ta place dans mon coeur.\n\n2.\nLe ciel a résonné,\nLes anges ont chanté:\n«Gloire à Dieu dans les lieux très haut»\nMais Tu vins à l\'étable,\nO amour insondable\nEt la paille fut Ton berceau.\n\n3.\nTu es venu Seigneur,\nO seul Rédempteur,\nNous sauver de notre misère;\nMais Tu fus rejeté,\nPar tous abandonné,\nCloué à la croix du Calvaire.\n\n4.\nLe ciel résonnera\nEt son choeur chantera\nQuand Tu viendras, O Roi des rois.\nJe serai dans la joie\nQuand j\'entendrai Ta voix:\n«J\'ai là une place pour toi.»\n\nChoeur:\nMon coeur se réjouira, Seigneur Jésus,\nQuand Tu viendras encore pour moi.','<p><strong>1.</strong> </p>\n<p>Tu laissas Ton trône et Ta couronne </p>\n<p>Quand Tu vins sur la terre pour moi, </p>\n<p>Mais Tu fus sans amis à l&#39;hôtellerie </p>\n<p>Quoique Tu sois le roi des rois. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Viens donc dans mon coeur</strong> </p>\n<p><strong>Seigneur Jésus;</strong> </p>\n<p><strong>Prends Ta place dans mon coeur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le ciel a résonné, </p>\n<p>Les anges ont chanté: </p>\n<p>«Gloire à Dieu dans les lieux très haut» </p>\n<p>Mais Tu vins à l&#39;étable, </p>\n<p>O amour insondable </p>\n<p>Et la paille fut Ton berceau. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu es venu Seigneur, </p>\n<p>O seul Rédempteur, </p>\n<p>Nous sauver de notre misère; </p>\n<p>Mais Tu fus rejeté, </p>\n<p>Par tous abandonné, </p>\n<p>Cloué à la croix du Calvaire. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le ciel résonnera </p>\n<p>Et son choeur chantera </p>\n<p>Quand Tu viendras, O Roi des rois. </p>\n<p>Je serai dans la joie </p>\n<p>Quand j&#39;entendrai Ta voix: </p>\n<p>«J&#39;ai là une place pour toi.» </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Mon coeur se réjouira, Seigneur Jésus,</strong> </p>\n<p><strong>Quand Tu viendras encore pour moi.</strong> </p>\n','Tu-laissas-ton-trone-et-ta-couronne',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2185,'5a9995ee-2449-4c9a-987c-edd942406be5',0,0,'Nous, les trois mages d\'Orient',181,'181_MJ_fr','1.\nNous, les trois mages d\'Orient,\nVoyageons portant nos présents,\nBelle étoile, luis sans voile,\nGuide-nous au saint lieu.\n\nChoeur:\nO étoile mystérieuse,\nScintillante et radieuse,\nQui de l\'azur profond des cieux.\nAnnonces un jour glorieux.\n\n2.\nMelchior:\nRoi des rois, mon or est à Toi!\nJe soumets mon coeur à Ta loi:\nIl s\'incline: Tu domines\nAu royaume des cieux.\n\n3.\nGaspar:\nFils de Dieu, Price glorieux\nCet encens, parfum précieux,\nD\'un roi-mage, est l\'hommage\nAu Souverain des cieux.\n\n4.\nBalthazar:\nO Sauveur, notre Rédempteur\nCette myrrhe, présent de mon coeur,\nEmbaumera, exaltera,\nLe son du Dieu des cieux.\n\n5.\nTous:\nHosanna au Dieu tout puissant\nA celui qui s\'est fait enfant!\nPrends notre être, fais-le naître\nAu royaume des cieux!','<p><strong>1.</strong> </p>\n<p>Nous, les trois mages d&#39;Orient, </p>\n<p>Voyageons portant nos présents, </p>\n<p>Belle étoile, luis sans voile, </p>\n<p>Guide-nous au saint lieu. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>O étoile mystérieuse,</strong> </p>\n<p><strong>Scintillante et radieuse,</strong> </p>\n<p><strong>Qui de l&#39;azur profond des cieux.</strong> </p>\n<p><strong>Annonces un jour glorieux.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Melchior: </p>\n<p>Roi des rois, mon or est à Toi! </p>\n<p>Je soumets mon coeur à Ta loi: </p>\n<p>Il s&#39;incline: Tu domines </p>\n<p>Au royaume des cieux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gaspar: </p>\n<p>Fils de Dieu, Price glorieux </p>\n<p>Cet encens, parfum précieux, </p>\n<p>D&#39;un roi-mage, est l&#39;hommage </p>\n<p>Au Souverain des cieux. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Balthazar: </p>\n<p>O Sauveur, notre Rédempteur </p>\n<p>Cette myrrhe, présent de mon coeur, </p>\n<p>Embaumera, exaltera, </p>\n<p>Le son du Dieu des cieux. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Tous: </p>\n<p>Hosanna au Dieu tout puissant </p>\n<p>A celui qui s&#39;est fait enfant! </p>\n<p>Prends notre être, fais-le naître </p>\n<p>Au royaume des cieux! </p>\n','Nous-les-trois-mages-d\'Orient',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2186,'80651acb-855a-42a5-bef7-236f28603968',0,0,'Konté-m Ladan-n',1,'1_HC_ht','1.\nLè ou konté moun ki pou Jézu\nKonté-m ladan,\nNan mounki konfié nan Paròl Li,\nKonté-m ladan.\n\nKè:\nKonté-m pami pitit ki pou Roua sièl la\nKonté-m pami sèvitè kap sèvi a,\nKonté-m pami moun sové, alélouya,\nKonté-m ladan.\n\n2.\nLè ou konté moun ki nan la gras\nKonté-m ladan,\nSa yo an Kris ki gingnin gnou plas\nKonté-m ladan.\n\n3.\nLè ou konté témouin yo pou Kris,\nKonté-m ladan,\nKi va gingnin kouròn la justis,\nKonté-m ladan.','<p><strong>1.</strong> </p>\n<p>Lè ou konté moun ki pou Jézu </p>\n<p>Konté-m ladan, </p>\n<p>Nan mounki konfié nan Paròl Li, </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Konté-m pami pitit ki pou Roua sièl la</strong> </p>\n<p><strong>Konté-m pami sèvitè kap sèvi a,</strong> </p>\n<p><strong>Konté-m pami moun sové, alélouya,</strong> </p>\n<p><strong>Konté-m ladan.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou konté moun ki nan la gras </p>\n<p>Konté-m ladan, </p>\n<p>Sa yo an Kris ki gingnin gnou plas </p>\n<p>Konté-m ladan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè ou konté témouin yo pou Kris, </p>\n<p>Konté-m ladan, </p>\n<p>Ki va gingnin kouròn la justis, </p>\n<p>Konté-m ladan. </p>\n','Konte-m-Ladan-n',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2187,'9a29a0e3-b255-4c1d-8afe-5b4f49b17de1',0,0,'Jézu Éfasé Péché-m Tout Bon',13,'13_HC_ht','1.\nKouté mésaj Bon-Dieu ki pi bèl,\nLi éfasé péché-m tout bon!\nDépi min-m jou mouin té aksépté-L,\nLi éfasé péché-m tout bon.\n\nKè:\nJézu éfasé péché-m tout bon,\nLi éfasé péché-m tout bon,\nYo tout antéré nan pati pi fon nan la mè\nLi éfasé péché-m toutbon.\n\n2.\nKè-m sal, min, gloua a Jézu-Kri,\nLi éfasé péché-m tout bon,\nTout bagay chanjé, Li ban-m la vi,\nLi éfasé péché-m tout bon.\n\n3.\nDévan Roua mouin gnou jou ma kanpé,\nLi éfasé péché-m tout bon,\nÉ ak rachté yo ma va chanté,\nLi éfasé péché-m tout bon.','<p><strong>1.</strong> </p>\n<p>Kouté mésaj Bon-Dieu ki pi bèl, </p>\n<p>Li éfasé péché-m tout bon! </p>\n<p>Dépi min-m jou mouin té aksépté-L, </p>\n<p>Li éfasé péché-m tout bon. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Jézu éfasé péché-m tout bon,</strong> </p>\n<p><strong>Li éfasé péché-m tout bon,</strong> </p>\n<p><strong>Yo tout antéré nan pati pi fon nan la mè</strong> </p>\n<p><strong>Li éfasé péché-m toutbon.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Kè-m sal, min, gloua a Jézu-Kri, </p>\n<p>Li éfasé péché-m tout bon, </p>\n<p>Tout bagay chanjé, Li ban-m la vi, </p>\n<p>Li éfasé péché-m tout bon. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dévan Roua mouin gnou jou ma kanpé, </p>\n<p>Li éfasé péché-m tout bon, </p>\n<p>É ak rachté yo ma va chanté, </p>\n<p>Li éfasé péché-m tout bon. </p>\n','Jezu-Efase-Peche-m-Tout-Bon',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2188,'98fb7cb0-a771-4ff5-a838-fbbc635dac36',0,0,'Nésans Gnou Roua',40,'40_HC_ht','1.\nPré Bétléèm zanj yo tap chanté,\nBon nouvèl lé mon-n yo tap pòté,\nO chan mèvéyé, O chan glorié,\nKi anonsé nésans gnou Roua.\n\nKè:\n«Gloua a Dieu» zanj yo chanté,\n«Gloua a Dieu» voua yo sonnin,\nO chan mèvéyé, O chan glorifié,\nKi anonsé nésans gnou Roua.\n\n2.\nTou sézi bèjé yo adoré-L\nMaj yo ak trézo yo vi-n chèché-L,\nLè yo ouè zétoual, pi briyan zétoual,\nKi anonsé nésans gnou Roua.\n\n3.\nBèjé, proklamé témouyaj nou,\nÉ maj yo asi nan vouayaj nou\nMésaj ki pi bèl, ki sòti nan sièl\nN-ap anonsé nésans gnou Roua.\n\n4.\nKi sa m-a fè avèk mésaj sa?\nKap sové ninpòt moun ki kouè a?\nDépi fèt Noèl, mouin pa doué kaché-l,\nM-ap anonsé nésans gnou Roua.','<p><strong>1.</strong> </p>\n<p>Pré Bétléèm zanj yo tap chanté, </p>\n<p>Bon nouvèl lé mon-n yo tap pòté, </p>\n<p>O chan mèvéyé, O chan glorié, </p>\n<p>Ki anonsé nésans gnou Roua. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>«Gloua a Dieu» zanj yo chanté,</strong> </p>\n<p><strong>«Gloua a Dieu» voua yo sonnin,</strong> </p>\n<p><strong>O chan mèvéyé, O chan glorifié,</strong> </p>\n<p><strong>Ki anonsé nésans gnou Roua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tou sézi bèjé yo adoré-L </p>\n<p>Maj yo ak trézo yo vi-n chèché-L, </p>\n<p>Lè yo ouè zétoual, pi briyan zétoual, </p>\n<p>Ki anonsé nésans gnou Roua. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bèjé, proklamé témouyaj nou, </p>\n<p>É maj yo asi nan vouayaj nou </p>\n<p>Mésaj ki pi bèl, ki sòti nan sièl </p>\n<p>N-ap anonsé nésans gnou Roua. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ki sa m-a fè avèk mésaj sa? </p>\n<p>Kap sové ninpòt moun ki kouè a? </p>\n<p>Dépi fèt Noèl, mouin pa doué kaché-l, </p>\n<p>M-ap anonsé nésans gnou Roua. </p>\n','Nesans-Gnou-Roua',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2189,'adec5a4e-00e9-4015-9a94-1f2636116777',0,0,'Létènèl Sé Gadyin Mouin',43,'43_HC_ht','1.\nLétènèl sé gadyin, mouin pa manké\nPaské Li fè-m répozé\nLi nouri-m byin, Li minnin mouin,\nKoté dlo a byin frè.\n\nKè:\nOui, jouk Li pa rèd, fado Li léjé,\nSé sa m-trouvé, sé sa m-trouvé,\nLi nouri-m byin, Li toujou minnin-m\nKoté dlo a byin frè.\n\n2.\nChak jou Bon Bèjé a réfè nan-m mouin\nLi kondui-m nan bon chémin\nMouin ta égaré, min Gadyin\nPou tèt non Li kinbé-m.\n\n3.\nNan kay Létènèl la m-a va rété,\nÉnmi pa fouti proché-m,\nTou lé jou la vi isi-ba\nGras Li akonpagné-m.','<p><strong>1.</strong> </p>\n<p>Létènèl sé gadyin, mouin pa manké </p>\n<p>Paské Li fè-m répozé </p>\n<p>Li nouri-m byin, Li minnin mouin, </p>\n<p>Koté dlo a byin frè. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Oui, jouk Li pa rèd, fado Li léjé,</strong> </p>\n<p><strong>Sé sa m-trouvé, sé sa m-trouvé,</strong> </p>\n<p><strong>Li nouri-m byin, Li toujou minnin-m</strong> </p>\n<p><strong>Koté dlo a byin frè.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chak jou Bon Bèjé a réfè nan-m mouin </p>\n<p>Li kondui-m nan bon chémin </p>\n<p>Mouin ta égaré, min Gadyin </p>\n<p>Pou tèt non Li kinbé-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan kay Létènèl la m-a va rété, </p>\n<p>Énmi pa fouti proché-m, </p>\n<p>Tou lé jou la vi isi-ba </p>\n<p>Gras Li akonpagné-m. </p>\n','Letenel-Se-Gadyin-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2190,'3a8741a1-3e14-42c3-878b-3bdc9572eeed',0,0,'Koté Min- d-èv Yo?',59,'59_HC_ht','1.\nKoté min-d-èv yo ki pou rékolté\nGran mouason dé zam ki nan chan péché\nFòk nou byin paré al la vérité\nPou rantré rékòt pou létènité.\n\nKè:\nKoté min-d-èv yo pou rékolté\nGran mouason dé zam ki nan chan péché\nKi lès va édé-m nan la saf Bon-Dieu\nÉgzanté léz a-m nan jujman difé.\n\n2.\nTravay la va rèd é li fè  byin cho,\nN-a kéyi lé a-m malgré rajé gro,\nPa kité gnou sèl paské tout pou Kris\nTout éfò nou fè sonjé Li fè plus.\n\n3.\nMouason déja prèt pou Li rékolté\nTravay la anpil, manké ouvrié,\nByinto la pèdu si nou fè réta,\nVini bay konkou nan gran travay sa.\n\n4.\nAnnou mété tout min nou yo ansan-m,\nNou pa fi-n konnin valè gnou sèl a-m,\nByinto Mèt rékòt la va rétounnin,\nLi vlé nou réjoui ak Li a la fin.','<p><strong>1.</strong> </p>\n<p>Koté min-d-èv yo ki pou rékolté </p>\n<p>Gran mouason dé zam ki nan chan péché </p>\n<p>Fòk nou byin paré al la vérité </p>\n<p>Pou rantré rékòt pou létènité. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Koté min-d-èv yo pou rékolté</strong> </p>\n<p><strong>Gran mouason dé zam ki nan chan péché</strong> </p>\n<p><strong>Ki lès va édé-m nan la saf Bon-Dieu</strong> </p>\n<p><strong>Égzanté léz a-m nan jujman difé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Travay la va rèd é li fè  byin cho, </p>\n<p>N-a kéyi lé a-m malgré rajé gro, </p>\n<p>Pa kité gnou sèl paské tout pou Kris </p>\n<p>Tout éfò nou fè sonjé Li fè plus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mouason déja prèt pou Li rékolté </p>\n<p>Travay la anpil, manké ouvrié, </p>\n<p>Byinto la pèdu si nou fè réta, </p>\n<p>Vini bay konkou nan gran travay sa. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Annou mété tout min nou yo ansan-m, </p>\n<p>Nou pa fi-n konnin valè gnou sèl a-m, </p>\n<p>Byinto Mèt rékòt la va rétounnin, </p>\n<p>Li vlé nou réjoui ak Li a la fin. </p>\n','Kote-Min-d-ev-Yo',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2191,'923c9ec5-5a75-4992-8cba-c93c8730bbd9',0,0,'Kay La Ki Pa Tonbé',65,'65_HC_ht','1.\nKonsa ninpòt moun ki tandé paròl mouin\nKonsa ninpòt moun ki tandé paròl mouin\nÉ ki pa suiv yo, é ki pa suiv yo,\nVa sanblé ak gnou nonm ki san bon sans\nÉ ki bati kay li sou sab.\n\nKè:\nÉpi la pli tonbé, lavalas débòdé,\nVan yo souflé, souflé, van yo souflé,\nYo déchinnin kont kay la\nÉ li tonbé! Li tonbé! É dézas li té konplèt.\n\n2.\nKonsa ninpòt moun ki tandé paròl mouin\nKonsa ninpòt moun ki tandé paròl mouin,\nÉ suiv yo byin, é suiv yo byin,\nVa sanblé ak gnou nom réfléchi\nKi bati kay li sou roché.\nKè:\nÉpi la pli tonbé, lavalas débòdé,\nVan yo souflé, souflé, van yo souflé,\nYo déchinnin kot kay sa a,\nDéchinnin kont kay la.\nLi pa tonbé! Li pa tonbé!\nPaské fondasion li sou gnou roché!\nPaské fondasion li sou roché (sou gnou roché).','<p><strong>1.</strong> </p>\n<p>Konsa ninpòt moun ki tandé paròl mouin </p>\n<p>Konsa ninpòt moun ki tandé paròl mouin </p>\n<p>É ki pa suiv yo, é ki pa suiv yo, </p>\n<p>Va sanblé ak gnou nonm ki san bon sans </p>\n<p>É ki bati kay li sou sab. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Épi la pli tonbé, lavalas débòdé,</strong> </p>\n<p><strong>Van yo souflé, souflé, van yo souflé,</strong> </p>\n<p><strong>Yo déchinnin kont kay la</strong> </p>\n<p><strong>É li tonbé! Li tonbé! É dézas li té konplèt.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Konsa ninpòt moun ki tandé paròl mouin </p>\n<p>Konsa ninpòt moun ki tandé paròl mouin, </p>\n<p>É suiv yo byin, é suiv yo byin, </p>\n<p>Va sanblé ak gnou nom réfléchi </p>\n<p>Ki bati kay li sou roché. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Épi la pli tonbé, lavalas débòdé,</strong> </p>\n<p><strong>Van yo souflé, souflé, van yo souflé,</strong> </p>\n<p><strong>Yo déchinnin kot kay sa a,</strong> </p>\n<p><strong>Déchinnin kont kay la.</strong> </p>\n<p><strong>Li pa tonbé! Li pa tonbé!</strong> </p>\n<p><strong>Paské fondasion li sou gnou roché!</strong> </p>\n<p><strong>Paské fondasion li sou roché (sou gnou roché).</strong> </p>\n','Kay-La-Ki-Pa-Tonbe',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2192,'67fd2665-2006-4c6e-9a1d-de645497a1fa',0,0,'Vivre sans cesse',86,'86_MJ_fr','1.\nVivre sans cesse,\nPlein de tendresse\nPour mon Sauveur,\nEt Le servir;\nC\'est là ma joie,\nCar Il m\'envoie\nGagner tous ceux\nQu\'Il veut bénir.','<p><strong>1.</strong> </p>\n<p>Vivre sans cesse, </p>\n<p>Plein de tendresse </p>\n<p>Pour mon Sauveur, </p>\n<p>Et Le servir; </p>\n<p>C&#39;est là ma joie, </p>\n<p>Car Il m&#39;envoie </p>\n<p>Gagner tous ceux </p>\n<p>Qu&#39;Il veut bénir. </p>\n','Vivre-sans-cesse',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2193,'2a6bea10-61c5-4c11-8189-729e8d799b39',0,0,'Jésus sauve',116,'116_MJ_fr','1.\nJésus sauve,  (5 fois)\nGloire à Dieu,\nJésus sauve.  (bis)\n\n2.\nJ\'ai la paix,  (5 fois)\nGloire à Dieu,\nJ\'ai la paix.  (bis)','<p><strong>1.</strong> </p>\n<p>Jésus sauve,  (5 fois) </p>\n<p>Gloire à Dieu, </p>\n<p>Jésus sauve.  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai la paix,  (5 fois) </p>\n<p>Gloire à Dieu, </p>\n<p>J&#39;ai la paix.  (bis) </p>\n','Jesus-sauve',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2194,'1301b396-5802-4f6a-a2d5-849e12713aef',0,0,'Nous marchons vers le ciel',156,'156_MJ_fr','1.\nNous marchons vers le ciel,\nNous marchons vers le ciel,\nChantant, chantant,\nTout le monde chantant,\nVers le ciel;\nNous marchons vers le ciel,\nNous marchons vers le ciel,\nMarchons, marchons,\nVers le ciel!','<p><strong>1.</strong> </p>\n<p>Nous marchons vers le ciel, </p>\n<p>Nous marchons vers le ciel, </p>\n<p>Chantant, chantant, </p>\n<p>Tout le monde chantant, </p>\n<p>Vers le ciel; </p>\n<p>Nous marchons vers le ciel, </p>\n<p>Nous marchons vers le ciel, </p>\n<p>Marchons, marchons, </p>\n<p>Vers le ciel! </p>\n','Nous-marchons-vers-le-ciel',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2195,'cd3c131d-40d9-4097-ab7a-ab8db6dd249c',0,0,'Quel Sauveur merveilleux je possède',163,'163_MJ_fr','1.\nQuel Sauveur merveilleux je possède,\nIl s\'est sacrifié pour moi,\nEt sa vie innocente Il cède\nPour mourir sur l\'infâme bois.\n\nChoeur:\nAttaché à la croix pour moi,\nAttaché à la croix pour moi,\nIl a pris mon péché et m\'a délivré,\nAttaché à la croix pour moi.\n\n2.\nIl renonce un moment à son trône\nPour le plan rédempteur de Dieu,\nDéposant pour moi sa couronne,\nUn tel prix pour me rendre heureux!\n\n3.\nMéprisé, rejeté par les hommes,\nPour mes iniquités brisé,\nIl me dit, Vois combien je t\'aime,\nEt Il meurt pour me racheter.\n\n4.\nLe salut accompli sur la terre,\nMon Sauveur remonta au ciel,\nMais bientôt, Il va reparaître,\nMon bonheur sera éternel.','<p><strong>1.</strong> </p>\n<p>Quel Sauveur merveilleux je possède, </p>\n<p>Il s&#39;est sacrifié pour moi, </p>\n<p>Et sa vie innocente Il cède </p>\n<p>Pour mourir sur l&#39;infâme bois. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Attaché à la croix pour moi,</strong> </p>\n<p><strong>Attaché à la croix pour moi,</strong> </p>\n<p><strong>Il a pris mon péché et m&#39;a délivré,</strong> </p>\n<p><strong>Attaché à la croix pour moi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il renonce un moment à son trône </p>\n<p>Pour le plan rédempteur de Dieu, </p>\n<p>Déposant pour moi sa couronne, </p>\n<p>Un tel prix pour me rendre heureux! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Méprisé, rejeté par les hommes, </p>\n<p>Pour mes iniquités brisé, </p>\n<p>Il me dit, Vois combien je t&#39;aime, </p>\n<p>Et Il meurt pour me racheter. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le salut accompli sur la terre, </p>\n<p>Mon Sauveur remonta au ciel, </p>\n<p>Mais bientôt, Il va reparaître, </p>\n<p>Mon bonheur sera éternel. </p>\n','Quel-Sauveur-merveilleux-je-possede',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2196,'97d1d876-d585-48cc-9fe9-0ce8c8460c31',0,0,'Pèson-n Pa Pran Sousi Mouin',19,'19_HC_ht','1.\nByin louin nan lòt péi yo\nAnpil moun dézéspéré,\nPaské kè yo tris é lou ak chay péché.\nÉ ak larm yo ap plédé\nPou gnou moun vi-n soulajé\nNan-m yo ki krintif, toujou pèsékuté.\n\nKè:\nPèson-n pa pran sousi mouin\nMilion ap krié,\nPèson-n pa pran sousi mouin,\nTandé plint yo,\nEské ou vlé bay la vi ou pou al préché\nAfin ké Kris sové é lagé nan-m yo.\n\n2.\nMoun pèdu, jé yo avèg\nYo pè maché nan nouasè\nÉ prostènin dévan zidòl nan tout lié,\nEské ou va kité yo\nMouri san okin-n lumiè?\nSan éspoua yo va parèt dévan Bon-Dieu.\n\n3.\nSégnè, mouin gin tan tandé\nTout kri moun yo ki pèdu\nMouin bay ou la vi-m, ma chèché yo patou\nVoyé mouin ninpòt ki bò\nKité-m péyé ninpòt pri,\nLésé-m di chakun:\n«Kris pran sousi nan-m ou.»','<p><strong>1.</strong> </p>\n<p>Byin louin nan lòt péi yo </p>\n<p>Anpil moun dézéspéré, </p>\n<p>Paské kè yo tris é lou ak chay péché. </p>\n<p>É ak larm yo ap plédé </p>\n<p>Pou gnou moun vi-n soulajé </p>\n<p>Nan-m yo ki krintif, toujou pèsékuté. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Pèson-n pa pran sousi mouin</strong> </p>\n<p><strong>Milion ap krié,</strong> </p>\n<p><strong>Pèson-n pa pran sousi mouin,</strong> </p>\n<p><strong>Tandé plint yo,</strong> </p>\n<p><strong>Eské ou vlé bay la vi ou pou al préché</strong> </p>\n<p><strong>Afin ké Kris sové é lagé nan-m yo.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moun pèdu, jé yo avèg </p>\n<p>Yo pè maché nan nouasè </p>\n<p>É prostènin dévan zidòl nan tout lié, </p>\n<p>Eské ou va kité yo </p>\n<p>Mouri san okin-n lumiè? </p>\n<p>San éspoua yo va parèt dévan Bon-Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ségnè, mouin gin tan tandé </p>\n<p>Tout kri moun yo ki pèdu </p>\n<p>Mouin bay ou la vi-m, ma chèché yo patou </p>\n<p>Voyé mouin ninpòt ki bò </p>\n<p>Kité-m péyé ninpòt pri, </p>\n<p>Lésé-m di chakun: </p>\n<p>«Kris pran sousi nan-m ou.» </p>\n','Peson-n-Pa-Pran-Sousi-Mouin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2197,'770397e5-2423-4f66-a5b8-cf09b5e27a34',0,0,'Réfuj An Jézu',58,'58_HC_ht','1.\nMouin gin gnou réfuj lè gran van ap souflé\nMouin gin gnou pòt koté mouin kap kaché,\nSé Ròk étènèl ki pa jan-m kap fayi,\nAk konfians mouin byin asuré.\n\nKè:\nRòk la sé Jézu,\nSégnè-m ki té pan-n sou la koua,\nL-ap ba mouin protéksion ak gran joua,\nNan Li m-a mété tout la foua.\n\n2.\nSi lé mon-n tanté-m avèk tout plézi li,\nPou fè-m kité rout ké Jézu trasé,\nMouin chèché réfuj la koté-m répozé,\nAk tout tantasion fi-n pasé.\n\n3.\nO péchè, vi-n joui ak mouin nan réfuj sa,\nGin plas pou tout, pou ninpòt moun ki vlé,\nSi ou bouké, fatigé ak péché ou yo,\nL-a padonnin é bay la pè.','<p><strong>1.</strong> </p>\n<p>Mouin gin gnou réfuj lè gran van ap souflé </p>\n<p>Mouin gin gnou pòt koté mouin kap kaché, </p>\n<p>Sé Ròk étènèl ki pa jan-m kap fayi, </p>\n<p>Ak konfians mouin byin asuré. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Ròk la sé Jézu,</strong> </p>\n<p><strong>Ségnè-m ki té pan-n sou la koua,</strong> </p>\n<p><strong>L-ap ba mouin protéksion ak gran joua,</strong> </p>\n<p><strong>Nan Li m-a mété tout la foua.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si lé mon-n tanté-m avèk tout plézi li, </p>\n<p>Pou fè-m kité rout ké Jézu trasé, </p>\n<p>Mouin chèché réfuj la koté-m répozé, </p>\n<p>Ak tout tantasion fi-n pasé. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O péchè, vi-n joui ak mouin nan réfuj sa, </p>\n<p>Gin plas pou tout, pou ninpòt moun ki vlé, </p>\n<p>Si ou bouké, fatigé ak péché ou yo, </p>\n<p>L-a padonnin é bay la pè. </p>\n','Refuj-An-Jezu',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2198,'c4edf3a3-8d2d-4ca1-9bda-a85dbc2176a8',0,0,'Sé Nan Jézu Gin Abondans La Vi',60,'60_HC_ht','1.\nNan Kris é nan Li sèl na jouin-n vrè la vi\nSé gras a Li, vié natu a mouri,\nLi rachté mouin nèt, ni kò, nan-m, ni léspri\nM-a viv, min pa mouin-min’m, sé Jézu-Kri.\n\nKè:\nAbondans la vi, mapé jouin-n nan Li,\nL-a transfòmé mouin, pou-m sèvi Li byin,\nSan Li té rachté mouin\nLi ba mouin Léspri-Sin,\nSé nan Jézu gin abondans la vi.\n\n2.\nSé Sint-Éspri ki fè-m aksépté Jézu,\nLi fè-m konfié nan promès Bon-Dieu tou.\nDé gloua an gloua Lap chanjé-m nan imaj Li\nAmou Li dévlopé pi plus chak jou.\n\n3.\nDlo a ki nan sous la vi ap débòdé,\nÉ sé li ki pòté bénédiksion.\nSé la vi an n-abondans L-ap transpòté,\nLi bay lamou, la joua, pè ak padon.','<p><strong>1.</strong> </p>\n<p>Nan Kris é nan Li sèl na jouin-n vrè la vi </p>\n<p>Sé gras a Li, vié natu a mouri, </p>\n<p>Li rachté mouin nèt, ni kò, nan-m, ni léspri </p>\n<p>M-a viv, min pa mouin-min’m, sé Jézu-Kri. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Abondans la vi, mapé jouin-n nan Li,</strong> </p>\n<p><strong>L-a transfòmé mouin, pou-m sèvi Li byin,</strong> </p>\n<p><strong>San Li té rachté mouin</strong> </p>\n<p><strong>Li ba mouin Léspri-Sin,</strong> </p>\n<p><strong>Sé nan Jézu gin abondans la vi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sé Sint-Éspri ki fè-m aksépté Jézu, </p>\n<p>Li fè-m konfié nan promès Bon-Dieu tou. </p>\n<p>Dé gloua an gloua Lap chanjé-m nan imaj Li </p>\n<p>Amou Li dévlopé pi plus chak jou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dlo a ki nan sous la vi ap débòdé, </p>\n<p>É sé li ki pòté bénédiksion. </p>\n<p>Sé la vi an n-abondans L-ap transpòté, </p>\n<p>Li bay lamou, la joua, pè ak padon. </p>\n','Se-Nan-Jezu-Gin-Abondans-La-Vi',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2199,'b9a58af9-5848-4466-86d0-4c7c2a7d5c8e',0,0,'Au Mont Des Oliviers',87,'87_HC_fr','1.\nAlors, il faisait nuit\nLe Fils de Dieu marchait\nDans un jardin couvert de rosée,\nIl se mit à genoux dans un calme complète,\nLouin des bruits de la foule agitée.\n\n\n\n\n\nChoeur:\nAu mont des Oliviers, au mont des Oliviers,\nSe rendit mon Sauveur pour prier,\nTa volonté soit faite, divinité parfaite,\nEt dans lamienne, et jusqu’à la fin.\n\n\n2.\nÉcoutez les plaintes de son âme angoissée,\nLorsqu’Il avait senti tous les poids,\nDes crimes de la terre, oh! La terre souillée,\n«Coupe amère, éloignez-vous de moi.»\n\n3.\nUne grande tristesse, une terreur mortelle,\nAussitôt de son coeur s’empara,\nEt sa sueur profuse, pour toi amère, belle,\nComme des grumeaux de sang coula.','<p><strong>1.</strong> </p>\n<p>Alors, il faisait nuit </p>\n<p>Le Fils de Dieu marchait </p>\n<p>Dans un jardin couvert de rosée, </p>\n<p>Il se mit à genoux dans un calme complète, </p>\n<p>Louin des bruits de la foule agitée. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Au mont des Oliviers, au mont des Oliviers,</strong> </p>\n<p><strong>Se rendit mon Sauveur pour prier,</strong> </p>\n<p><strong>Ta volonté soit faite, divinité parfaite,</strong> </p>\n<p><strong>Et dans lamienne, et jusqu’à la fin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Écoutez les plaintes de son âme angoissée, </p>\n<p>Lorsqu’Il avait senti tous les poids, </p>\n<p>Des crimes de la terre, oh! La terre souillée, </p>\n<p>«Coupe amère, éloignez-vous de moi.» </p>\n<p><br/> <strong>3.</strong></p>\n<p>Une grande tristesse, une terreur mortelle, </p>\n<p>Aussitôt de son coeur s’empara, </p>\n<p>Et sa sueur profuse, pour toi amère, belle, </p>\n<p>Comme des grumeaux de sang coula. </p>\n','Au-Mont-Des-Oliviers',NULL,'fr',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2200,'786832ba-fe17-4e48-b6ac-92135b44509c',0,0,'Letènèl Konnen Kretyen Satan Genyen Pisans',13,'13_RN_ht','1.\nLetènèl konnen kretyen satan genyen pisans\nLè nou nan prezans li, lave nou tou lè jou,\nPriye veye jou e nwit, satan panan domi,\nJezu la tou jè jou si m’konfye-m nan li sèl\n \nRefren :\nJezu-Kri la, Li avek nou, Li pa pi lwen\nJezu la tou lè jou, li delivre nou.\n \n2.\nLè-n te chita na Legliz, m’te de m’ansyen \nM’te mete baryè pou levanjil pa preche,\nKris konnen moun li chwazi pou preche levanjil\nFòk mwen preche levanjil, Jezu ban mwen pisans\n \n3.\nLevanjil ki pou preche pou key o konvèti,\nFòk se pas an ak lachè lògèy, filozofi.\nLobeyisans pa la fwa ak pisans Sentespri\nSentespri la tout tan e li va ede nou\n \n4.\nLè-m t’ap chwazi relijion m’te chaje ak lògèy,\nMoun pat pale avèk mwen Pou-m pat bay repons,\nLè Jezu rantre nan kè mwen.\nMwen vi’n chanje konplètman.\nKris fè-m konnen l’pran chaj mwen\nSe pou-m rete kote-l','<p><strong>1.</strong> </p>\n<p>Letènèl konnen kretyen satan genyen pisans </p>\n<p>Lè nou nan prezans li, lave nou tou lè jou, </p>\n<p>Priye veye jou e nwit, satan panan domi, </p>\n<p>Jezu la tou jè jou si m’konfye-m nan li sèl </p>\n<p>Refren : </p>\n<p>Jezu-Kri la, Li avek nou, Li pa pi lwen </p>\n<p>Jezu la tou lè jou, li delivre nou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè-n te chita na Legliz, m’te de m’ansyen </p>\n<p>M’te mete baryè pou levanjil pa preche, </p>\n<p>Kris konnen moun li chwazi pou preche levanjil </p>\n<p>Fòk mwen preche levanjil, Jezu ban mwen pisans </p>\n<p><br/> <strong>3.</strong></p>\n<p>Levanjil ki pou preche pou key o konvèti, </p>\n<p>Fòk se pas an ak lachè lògèy, filozofi. </p>\n<p>Lobeyisans pa la fwa ak pisans Sentespri </p>\n<p>Sentespri la tout tan e li va ede nou </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lè-m t’ap chwazi relijion m’te chaje ak lògèy, </p>\n<p>Moun pat pale avèk mwen Pou-m pat bay repons, </p>\n<p>Lè Jezu rantre nan kè mwen. </p>\n<p>Mwen vi’n chanje konplètman. </p>\n<p>Kris fè-m konnen l’pran chaj mwen </p>\n<p>Se pou-m rete kote-l </p>\n','Letenel-Konnen-Kretyen-Satan-Genyen-Pisans',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2201,'1d247c55-016b-4106-b25a-a82e432b6f00',0,0,'Quel bonheur de marcher avec Lui',58,'58_MJ_fr','1.\nQuel bonheur de marcher avec Lui\nQuel bonheur de marcher avec Lui\nIl conduira tous les pas\nDe mon voyage ici-bas\nQuel bonheur de marcher avec Lui.','<p><strong>1.</strong> </p>\n<p>Quel bonheur de marcher avec Lui </p>\n<p>Quel bonheur de marcher avec Lui </p>\n<p>Il conduira tous les pas </p>\n<p>De mon voyage ici-bas </p>\n<p>Quel bonheur de marcher avec Lui. </p>\n','Quel-bonheur-de-marcher-avec-Lui',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2202,'c564f488-8c96-4215-84c3-2503cd8d6cbb',0,0,'Dans les cieux on est heureux',63,'63_MJ_fr','1.\nDans le ciel on est heureux,\nBien heureux, bien heureux,\nDans le ciel on est heureux,\nAlléluia!\n\n2.\nIl ne fera jamais nuit,\nJamais nuit, jamais nuit,\nIl ne fera jamais nuit,\nAlléluia!\n\n3.\nDieu, Là-haut, est sur Son trône,\nSur Son trône, sur Son trône,\nDieu, Là-haut est sur Son trône,\nAlléluia!\n\n4.\nJésus est le seul chemin,\nSeul chemin, seul chemin,\nJésus est le seul chemin,\nAlléluia!\n\n5.\nOh, que nous serons heureux,\nTrès heureux, très heureux,\nToujours joyeux,\nChère jeunesse,\nToujours joyeux!','<p><strong>1.</strong> </p>\n<p>Dans le ciel on est heureux, </p>\n<p>Bien heureux, bien heureux, </p>\n<p>Dans le ciel on est heureux, </p>\n<p>Alléluia! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il ne fera jamais nuit, </p>\n<p>Jamais nuit, jamais nuit, </p>\n<p>Il ne fera jamais nuit, </p>\n<p>Alléluia! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dieu, Là-haut, est sur Son trône, </p>\n<p>Sur Son trône, sur Son trône, </p>\n<p>Dieu, Là-haut est sur Son trône, </p>\n<p>Alléluia! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus est le seul chemin, </p>\n<p>Seul chemin, seul chemin, </p>\n<p>Jésus est le seul chemin, </p>\n<p>Alléluia! </p>\n<p><br/> <strong>5.</strong></p>\n<p>Oh, que nous serons heureux, </p>\n<p>Très heureux, très heureux, </p>\n<p>Toujours joyeux, </p>\n<p>Chère jeunesse, </p>\n<p>Toujours joyeux! </p>\n','Dans-les-cieux-on-est-heureux',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2203,'de36fcae-4966-4304-a593-f063292f38ed',0,0,'A Dieu soit la Gloire, Par Son grand amour',73,'73_MJ_fr','1.A Dieu soit la Gloire,Par Son grand amourDans mon âme noireC\'est levé le jourJésus à ma place,Mourut sur la croix,Il m\'offre Sa grâceEt je la reçois.Choeur:Gloire à Dieu, Gloire à dieuTerre, écoute Sa voix!Gloire à Dieu, gloire à DieuMonde, réjouis-toi!Oh! Venez au Père,Jésus est vainqueur;Que toute la terreChante en Son honneur.2.De Jésus, la joie,Remplit notre coeur;Qu\'importe qu\'on voieToute notre bonheur.Selon Sa promesseJésus changeraDeuil en allégresse,Quand Il reviendra.3.Céleste patrie,Mon coeur pense à toi;Là, mon âme unieA Jésus son Roi,Bénira sans cesseLe grand Dieu-Sauveur,Qui, dans Sa tendresse,Sauve le pécheur.','<p><strong>1.</strong> </p>\n<p>A Dieu soit la Gloire, </p>\n<p>Par son grand amour </p>\n<p>Dans mon âme noire </p>\n<p>C&#39;est levé le jour </p>\n<p>Jésus à ma place, </p>\n<p>Mourut sur la croix, </p>\n<p>Il m&#39;offre Sa grâce </p>\n<p>Et je la reçois. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Gloire à Dieu, Gloire à dieu</strong> </p>\n<p><strong>Terre, écoute Sa voix!</strong> </p>\n<p><strong>Gloire à Dieu, gloire à Dieu</strong> </p>\n<p><strong>Monde, réjouis-toi!</strong> </p>\n<p><strong>Oh! Venez au Père,</strong> </p>\n<p><strong>Jésus est vainqueur;</strong> </p>\n<p><strong>Que toute la terre</strong> </p>\n<p><strong>Chante en Son honneur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De Jésus, la joie, </p>\n<p>Remplit notre coeur; </p>\n<p>Qu&#39;importe qu&#39;on voie </p>\n<p>Tout notre bonheur. </p>\n<p>Selon Sa promesse </p>\n<p>Jésus changera </p>\n<p>Deuil en allégresse, </p>\n<p>Quand Il reviendra. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Céleste patrie, </p>\n<p>Mon coeur pense à toi; </p>\n<p>Là, mon âme unie </p>\n<p>A Jésus son Roi, </p>\n<p>Bénira sans cesse </p>\n<p>Le grand Dieu-Sauveur, </p>\n<p>Qui, dans Sa tendresse, </p>\n<p>Sauve le pécheur. </p>\n','A-Dieu-soit-la-Gloire-Par-Son-grand-amour',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2204,'6de12306-973c-485a-ba45-27c741f5d9cd',0,0,'J’ai trouvé un si bon Sauveur',83,'83_MJ_fr','1.\nJ\'ai trouvé un si bon Sauveur,\nJésus-Christ ma joie, ma paix;\nJe demeure dans sa faveur\nGloire à Son nom à jamais.\n\nChoeur:\nJésus fait ma joie de vivre,\nA jamais je suis à Lui\nEntre ses mains je me livre\nPas à pas Il me conduit.\nDésormais je veux Le suivre,\nMon appui; ses bras ouverts.\nJésus à fait ma joie de vivre,\nIl est l\'Ami le plus cher.\n\n2.\nO que cette vie est belle,\nMême le travail est doux;\nChaque tâche solennelle\nSanctifiée par Jésus.\n\n3.\nIl me donne sa sagesse,\nSa grâce est ma liberté;\nIl m\'amène avec tendresse\nDans Sa sainte volonté.','<p><strong>1.</strong> </p>\n<p>J&#39;ai trouvé un si bon Sauveur, </p>\n<p>Jésus-Christ ma joie, ma paix; </p>\n<p>Je demeure dans sa faveur </p>\n<p>Gloire à Son nom à jamais. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Jésus fait ma joie de vivre,</strong> </p>\n<p><strong>A jamais je suis à Lui</strong> </p>\n<p><strong>Entre ses mains je me livre</strong> </p>\n<p><strong>Pas à pas Il me conduit.</strong> </p>\n<p><strong>Désormais je veux Le suivre,</strong> </p>\n<p><strong>Mon appui; ses bras ouverts.</strong> </p>\n<p><strong>Jésus à fait ma joie de vivre,</strong> </p>\n<p><strong>Il est l&#39;Ami le plus cher.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O que cette vie est belle, </p>\n<p>Même le travail est doux; </p>\n<p>Chaque tâche solennelle </p>\n<p>Sanctifiée par Jésus. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il me donne sa sagesse, </p>\n<p>Sa grâce est ma liberté; </p>\n<p>Il m&#39;amène avec tendresse </p>\n<p>Dans Sa sainte volonté. </p>\n','J\'ai-trouve-un-si-bon-Sauveur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2205,'f5e865d6-d6be-4cf3-8361-bbcc9982907a',0,0,'J\'ai un merveilleux trésor',99,'99_MJ_fr','1.\nJ\'ai un merveilleux trésor,\nLe don de Dieu sans mesure,\nNous voyageons ensemble –\nMa Bible et moi.','<p><strong>1.</strong> </p>\n<p>J&#39;ai un merveilleux trésor, </p>\n<p>Le don de Dieu sans mesure, </p>\n<p>Nous voyageons ensemble – </p>\n<p>Ma Bible et moi. </p>\n','J\'ai-un-merveilleux-tresor',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2206,'53d98c20-2276-4a06-a638-2a24f39d070d',0,0,'Le grand jour de résurrection',102,'102_MJ_fr','1.\nLe grand jour de résurrection\nQuand nous tous monterons\nVers le ciel (Alléluia),\nQuel beau jour!\nDevant notre bien-aimé Sauveur\nJésus nous paraîtrons,\nQuel beau jour (Alléluia)!\nQuel beau jour  (Quel beau jour)!\n\n\n\nChoeur:\nQuel beau jour (Alléluia)!\nQuel beau jour (Amen)!\nQuel beau jour (Alléluia)\nLe grand jour de résurrection,\nNous l\'attendons,\nNous l\'attendons,\nQuel beau jour  (Alléluia)!\nQuel beau jour  (Quel beau jour)!\n\n2.\nLe grand jour de résurrection\nAu son de la trompette\nD\'un clin d\'oeil (Alléluia);\nQuel beau jour!\nQuand ce corps mortel\nSera revêtu d\'immortalité,\nQuel beau jour (Alléluia)!\nQuel beau jour  (Quel  beau jour)!\n\n3.\nLe grand jour de résurrection\nQuand nous serons en haut,\nAvec Christ (Alléluia) le Sauveur,\nNous le verrons face à face\nEt nous serons semblables à Lui,\nQuel beau jour  (Alléluia)!\nQuel beau jour  (Quel beau jour)!','<p><strong>1.</strong> </p>\n<p>Le grand jour de résurrection </p>\n<p>Quand nous tous monterons </p>\n<p>Vers le ciel (Alléluia), </p>\n<p>Quel beau jour! </p>\n<p>Devant notre bien-aimé Sauveur </p>\n<p>Jésus nous paraîtrons, </p>\n<p>Quel beau jour (Alléluia)! </p>\n<p>Quel beau jour  (Quel beau jour)! </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Quel beau jour (Alléluia)!</strong> </p>\n<p><strong>Quel beau jour (Amen)!</strong> </p>\n<p><strong>Quel beau jour (Alléluia)</strong> </p>\n<p><strong>Le grand jour de résurrection,</strong> </p>\n<p><strong>Nous l&#39;attendons,</strong> </p>\n<p><strong>Nous l&#39;attendons,</strong> </p>\n<p><strong>Quel beau jour  (Alléluia)!</strong> </p>\n<p><strong>Quel beau jour  (Quel beau jour)!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le grand jour de résurrection </p>\n<p>Au son de la trompette </p>\n<p>D&#39;un clin d&#39;oeil (Alléluia); </p>\n<p>Quel beau jour! </p>\n<p>Quand ce corps mortel </p>\n<p>Sera revêtu d&#39;immortalité, </p>\n<p>Quel beau jour (Alléluia)! </p>\n<p>Quel beau jour  (Quel  beau jour)! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le grand jour de résurrection </p>\n<p>Quand nous serons en haut, </p>\n<p>Avec Christ (Alléluia) le Sauveur, </p>\n<p>Nous le verrons face à face </p>\n<p>Et nous serons semblables à Lui, </p>\n<p>Quel beau jour  (Alléluia)! </p>\n<p>Quel beau jour  (Quel beau jour)! </p>\n','Le-grand-jour-de-resurrection',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2207,'f4706268-c5ed-4d7b-9019-944509e671af',0,0,'Je voudrais, Jésus, être comme Toi',114,'114_MJ_fr','1.\nJe voulais, Jésus, être comme Toi;\nSeigneur, viens, mettre ton Esprit en moi,\nTon zèle et ton amour de la vérité;\nMe voici pour accomplir ta volonté.\n\nChoeur:\nOh, prends (Oh, prends) mon coeur,\nEt qu\'il soit à Toi seul;\nJe veux, Seigneur, être pur comme Toi,\nLave et garde-moi à l\'ombre de ta croix.\n\n2.\nÊtre comme Toi, c\'est mon seul désir,\nSi vient la croix, ta force aide à souffrir,\nDonne-moi à proclamer ton grand salut,\nEt par ton Esprit e gagner les perdus:\n\n3.\nÊtre comme Toi, voilà mon seul but,\nJe veux aimer de ton amour, Jésus,\nOublier tout comme Tu le fis jadis,\nÊtre comme Toi, c\'est mon éternel cri.','<p><strong>1.</strong> </p>\n<p>Je voulais, Jésus, être comme Toi; </p>\n<p>Seigneur, viens, mettre ton Esprit en moi, </p>\n<p>Ton zèle et ton amour de la vérité; </p>\n<p>Me voici pour accomplir ta volonté. </p>\n<p><br/> <strong>Choeur:</strong></p>\n<p><strong>Oh, prends (Oh, prends) mon coeur,</strong> </p>\n<p><strong>Et qu&#39;il soit à Toi seul;</strong> </p>\n<p><strong>Je veux, Seigneur, être pur comme Toi,</strong> </p>\n<p><strong>Lave et garde-moi à l&#39;ombre de ta croix.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Être comme Toi, c&#39;est mon seul désir, </p>\n<p>Si vient la croix, ta force aide à souffrir, </p>\n<p>Donne-moi à proclamer ton grand salut, </p>\n<p>Et par ton Esprit e gagner les perdus: </p>\n<p><br/> <strong>3.</strong></p>\n<p>Être comme Toi, voilà mon seul but, </p>\n<p>Je veux aimer de ton amour, Jésus, </p>\n<p>Oublier tout comme Tu le fis jadis, </p>\n<p>Être comme Toi, c&#39;est mon éternel cri. </p>\n','Je-voudrais-Jesus-etre-comme-Toi',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2208,'5bed4bd3-5cb0-4c37-b0fa-10347305540d',0,0,'Suivre, je veux suivre mon Seigneur',126,'126_MJ_fr','1.\nSuivre, je veux suivre mon Seigneur,\nJe veux Le suivre partout;\nMes demains sont tous à toi, Seigneur,\nJe te suivrai jusqu\'au bout.','<p><strong>1.</strong> </p>\n<p>Suivre, je veux suivre mon Seigneur, </p>\n<p>Je veux Le suivre partout; </p>\n<p>Mes demains sont tous à toi, Seigneur, </p>\n<p>Je te suivrai jusqu&#39;au bout. </p>\n','Suivre-je-veux-suivre-mon-Seigneur',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2209,'39659e50-fff8-4397-a7ae-a6ba97400302',0,0,'O jeunesse, Christ t\'appelle',129,'129_MJ_fr','1.\nO jeunesse, Christ t\'appelle,\nOui, t\'appelle au bon combat;\nIl te veut toujours fidèle\nPour le suivre pas à pas.\nSois royale, sois vaillant,\nPure et forte chaque jour;\nSois active, sois fervente,\nDieu t\'accorde son secours.\n\n2.\nVois, partout le mal augmente,\nIl redouble son effort;\nIl déchaîne la tourmente,\nC\'est la ruine et c\'est la mort.\nMais c\'est Dieu le Roi, le Maître,\nIl dissipera la nuit;\nTu verras l\'espoir renaître\nSi ta foi s\'attache à Lui.\n\n3.\nO jeunesse, Christ t\'appelle,\nTout entière, livre-toi.\nDans tes rangs, aucun rebelle,\nSuis Jésus jusqu\'à la Croix.\nVois déjà du ciel la gloire,\nLà, Dieu séchera tes pleurs;\nCrois toujours à la victoire,\nCrois, et tu seras vainqueur.\n\n4.\nChante et sois dans l\'allégresse,\nCar bientôt Jésus viendra.\nPlus d\'angoisse, de détresse,\nSon royaume alors naîtra.\nA Lui force, honneur, puissance,\nJusque dans l\'éternité;\nCours au but sans défaillance,\nSuis le Christ ressuscité.','<p><strong>1.</strong> </p>\n<p>O jeunesse, Christ t&#39;appelle, </p>\n<p>Oui, t&#39;appelle au bon combat; </p>\n<p>Il te veut toujours fidèle </p>\n<p>Pour le suivre pas à pas. </p>\n<p>Sois royale, sois vaillant, </p>\n<p>Pure et forte chaque jour; </p>\n<p>Sois active, sois fervente, </p>\n<p>Dieu t&#39;accorde son secours. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois, partout le mal augmente, </p>\n<p>Il redouble son effort; </p>\n<p>Il déchaîne la tourmente, </p>\n<p>C&#39;est la ruine et c&#39;est la mort. </p>\n<p>Mais c&#39;est Dieu le Roi, le Maître, </p>\n<p>Il dissipera la nuit; </p>\n<p>Tu verras l&#39;espoir renaître </p>\n<p>Si ta foi s&#39;attache à Lui. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O jeunesse, Christ t&#39;appelle, </p>\n<p>Tout entière, livre-toi. </p>\n<p>Dans tes rangs, aucun rebelle, </p>\n<p>Suis Jésus jusqu&#39;à la Croix. </p>\n<p>Vois déjà du ciel la gloire, </p>\n<p>Là, Dieu séchera tes pleurs; </p>\n<p>Crois toujours à la victoire, </p>\n<p>Crois, et tu seras vainqueur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Chante et sois dans l&#39;allégresse, </p>\n<p>Car bientôt Jésus viendra. </p>\n<p>Plus d&#39;angoisse, de détresse, </p>\n<p>Son royaume alors naîtra. </p>\n<p>A Lui force, honneur, puissance, </p>\n<p>Jusque dans l&#39;éternité; </p>\n<p>Cours au but sans défaillance, </p>\n<p>Suis le Christ ressuscité. </p>\n','O-jeunesse-Christ-t\'appelle',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2210,'db65d949-55a2-4e5b-b691-7960e3a71cf0',0,0,'Oh, je voudrais, je voudrais suivre',136,'136_MJ_fr','1.\nOh, je voudrais, je voudrais suivre\nIci-bas, pas à pas,\nJésus mon Sauveur!\nPour Lui, toujours je voudrais vivre,\nEt les yeux sur Lui,\nL\'aimer de tout mon coeur!','<p><strong>1.</strong> </p>\n<p>Oh, je voudrais, je voudrais suivre </p>\n<p>Ici-bas, pas à pas, </p>\n<p>Jésus mon Sauveur! </p>\n<p>Pour Lui, toujours je voudrais vivre, </p>\n<p>Et les yeux sur Lui, </p>\n<p>L&#39;aimer de tout mon coeur! </p>\n','Oh-je-voudrais-je-voudrais-suivre',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2211,'ea59f833-5cce-4c85-bed4-9d655bf94986',0,0,'Jésus, sois à notre table',175,'175_MJ_fr','1.\nJésus, sois à notre table\nL\'Hôte aimé , nous T\'en prions\nEt dans Ta grâce adorable\nBénis pour nous tous ces dons.\nBénis pour nous tous ces dons.','<p><strong>1.</strong> </p>\n<p>Jésus, sois à notre table </p>\n<p>L&#39;Hôte aimé , nous T&#39;en prions </p>\n<p>Et dans Ta grâce adorable </p>\n<p>Bénis pour nous tous ces dons. </p>\n<p>Bénis pour nous tous ces dons. </p>\n','Jesus-sois-a-notre-table',NULL,'fr',1,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2212,'b2af8f8e-0be7-4d49-b3cc-514bc700fa87',0,0,'Mouin Vlé Rétounin',24,'24_HC_ht','1.\nMouin gin tan maché louin Bon-Dieu\nMin m-ap rétounin.\nTro lontan mouin souiv rout péché,\nMouin vlé rétounn.\n\nKè:\nRétounin, rétounin,\nLa kay Sovè mouin,\nOuvri bra ou laj pou mouin,\nKris, m-ap rétounin.\n\n2.\nNan-m mouin malad, kè mouin fè mal,\nMin m-ap rétounin,\nM-ap abandonnin sa ki sal,\nMouin vlé rétounin.\n\n3.\nSé sèl éspoua-m, sé sèl dézi-m,\nM-ap rétounin,\nNan Jézu sèl mouin pran plézi-m,\nMouin vlé rétounin.\n\n4.\nSé nan plas mouin Kris té mouri,\nM-apé rétounin,\nSan Li vèsé pou ran-n mouin pu,\nMouin vlé rétounin','<p><strong>1.</strong> </p>\n<p>Mouin gin tan maché louin Bon-Dieu </p>\n<p>Min m-ap rétounin. </p>\n<p>Tro lontan mouin souiv rout péché, </p>\n<p>Mouin vlé rétounn. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Rétounin, rétounin,</strong> </p>\n<p><strong>La kay Sovè mouin,</strong> </p>\n<p><strong>Ouvri bra ou laj pou mouin,</strong> </p>\n<p><strong>Kris, m-ap rétounin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nan-m mouin malad, kè mouin fè mal, </p>\n<p>Min m-ap rétounin, </p>\n<p>M-ap abandonnin sa ki sal, </p>\n<p>Mouin vlé rétounin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sé sèl éspoua-m, sé sèl dézi-m, </p>\n<p>M-ap rétounin, </p>\n<p>Nan Jézu sèl mouin pran plézi-m, </p>\n<p>Mouin vlé rétounin. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sé nan plas mouin Kris té mouri, </p>\n<p>M-apé rétounin, </p>\n<p>San Li vèsé pou ran-n mouin pu, </p>\n<p>Mouin vlé rétounin </p>\n','Mouin-Vle-Retounin',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2213,'babb6577-a5eb-485f-9369-a91860dfa3dc',0,0,'Sé Jézu Sèl Ki Satisfè-m',25,'25_HC_ht','1.\nKi kab bay kouraj kòm Jézu\nAk prézans Li si divin?\nFidèl é sansib é présié\nMouin byinn-éré, Li pou mouin.\n\n2.\nA la gnou mèvéyé salu,\nMouin pap jan-m kapab konnin,\nKouman gnou nan-m ki plin péché,\nJouin-n mouayin pou padonnin.\n\n3.\nLè mouin bò la riviè la vi,\nAvèk sa yo ki rachté\nNou va louanjé Ségnè nou,\nÉ pou toujou na chanté.\n(Favorites 2 – No. 4)','<p><strong>1.</strong> </p>\n<p>Ki kab bay kouraj kòm Jézu </p>\n<p>Ak prézans Li si divin? </p>\n<p>Fidèl é sansib é présié </p>\n<p>Mouin byinn-éré, Li pou mouin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>A la gnou mèvéyé salu, </p>\n<p>Mouin pap jan-m kapab konnin, </p>\n<p>Kouman gnou nan-m ki plin péché, </p>\n<p>Jouin-n mouayin pou padonnin. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè mouin bò la riviè la vi, </p>\n<p>Avèk sa yo ki rachté </p>\n<p>Nou va louanjé Ségnè nou, </p>\n<p>É pou toujou na chanté. </p>\n<p>(Favorites 2 – No. 4) </p>\n','Se-Jezu-Sel-Ki-Satisfe-m',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2214,'2ae55b2e-a288-4c37-99dd-ffe42ca29da2',0,0,'Gnou Chan Louanj',4,'4_HC_ht','1.\nKè mouin boulvèsé anba kolè Bon-Dieu,\nAnvi mon-n nan trinnin-m nan bafon péché\nSégnè, pou gnou pov mizérab gin pitié,\nTou souit Li vi-n sékouri-m koté-m té yé.\n\nKè:\nLi rétiré-m nan boubié péché,\nLi mété piè-m sou tè fèm sé vré,\nLi fè kè-m réjoui é m-a chanté\nGnou chan, gloua a Dieu, Alélouya.\n\n2.\nLi té mété-m sou tè fèm a koté Li\nPié mouin vi-n asuré nan chémin la vi,\nNan pouin danjé ankò, Li kinbé-m débou,\nMap maché pa-za-pa avèk Li toujou.\n\n3.\nLi mété gnou chan louanj andan kè mouin\nNan nuit kou la jounin sé Li m-a chanté\nKè mouin ap débòdé, nanm mouin afranchi,\nTout gloua pou Rédanmtè, Ségnè Jézu-Kri.\n\n4.\nM-a chanté gran mizérikod Li pou mouin,\nMap félisité-L jouk tan tout moun konnin\nNan pouin lòt salu pou lé-zòm jodi a,\nVi-n chèché sékou Li avan li tro ta.','<p><strong>1.</strong> </p>\n<p>Kè mouin boulvèsé anba kolè Bon-Dieu, </p>\n<p>Anvi mon-n nan trinnin-m nan bafon péché </p>\n<p>Ségnè, pou gnou pov mizérab gin pitié, </p>\n<p>Tou souit Li vi-n sékouri-m koté-m té yé. </p>\n<p><br/> <strong>Kè:</strong></p>\n<p><strong>Li rétiré-m nan boubié péché,</strong> </p>\n<p><strong>Li mété piè-m sou tè fèm sé vré,</strong> </p>\n<p><strong>Li fè kè-m réjoui é m-a chanté</strong> </p>\n<p><strong>Gnou chan, gloua a Dieu, Alélouya.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li té mété-m sou tè fèm a koté Li </p>\n<p>Pié mouin vi-n asuré nan chémin la vi, </p>\n<p>Nan pouin danjé ankò, Li kinbé-m débou, </p>\n<p>Map maché pa-za-pa avèk Li toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li mété gnou chan louanj andan kè mouin </p>\n<p>Nan nuit kou la jounin sé Li m-a chanté </p>\n<p>Kè mouin ap débòdé, nanm mouin afranchi, </p>\n<p>Tout gloua pou Rédanmtè, Ségnè Jézu-Kri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>M-a chanté gran mizérikod Li pou mouin, </p>\n<p>Map félisité-L jouk tan tout moun konnin </p>\n<p>Nan pouin lòt salu pou lé-zòm jodi a, </p>\n<p>Vi-n chèché sékou Li avan li tro ta. </p>\n','Gnou-Chan-Louanj',NULL,'ht',4,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2215,'6dba5c85-a2c6-48e6-bf2e-3bfb5c9f0843',0,0,'Gen De Bagay Mwen Pa De Konnen',6,'6_RN_ht','1.\nGen de bagay mwen pa de konnen\nGen de kote, m’pa dwe ale\nMen gen yon bagay, mwen konnen siman\nWi Jezu bon, m’santi-l nan kè-m\n \nRefren:\nWi Bondye bon, li bon pou nan-m mwen\nKa li lave-m e li fè-m kenbe-l\nTankou yon dlo pi, o wi Bondye bon\nLi telman bon, m’santi-l nan kè-m\n \n2.\nGenyen anpil moun ki pa kren-n Bondye\nO dènye jou li va tro ta\nPou yow è tou sa pisans li te ye,\nO wi li bon, m’renmen-l konsa.\n \n3.\nVa genyen jou soley va kouche\nPap genyen lumyè menm tout tan,\nOu menm ki pa kwè, ki boo u prale?\nP’ap gen kote pou ou kache.\n \n4.\nE tout frè e sè ki genyen pisans\nNou dwe ran glwa pou riches sa-a\nLemonn pa konnen kisa nou jwenn\nSe sovè-a ki renmen nou','<p><strong>1.</strong> </p>\n<p>Gen de bagay mwen pa de konnen </p>\n<p>Gen de kote, m’pa dwe ale </p>\n<p>Men gen yon bagay, mwen konnen siman </p>\n<p>Wi Jezu bon, m’santi-l nan kè-m </p>\n<p>Refren: </p>\n<p>Wi Bondye bon, li bon pou nan-m mwen </p>\n<p>Ka li lave-m e li fè-m kenbe-l </p>\n<p>Tankou yon dlo pi, o wi Bondye bon </p>\n<p>Li telman bon, m’santi-l nan kè-m </p>\n<p><br/> <strong>2.</strong></p>\n<p>Genyen anpil moun ki pa kren-n Bondye </p>\n<p>O dènye jou li va tro ta </p>\n<p>Pou yow è tou sa pisans li te ye, </p>\n<p>O wi li bon, m’renmen-l konsa. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Va genyen jou soley va kouche </p>\n<p>Pap genyen lumyè menm tout tan, </p>\n<p>Ou menm ki pa kwè, ki boo u prale? </p>\n<p>P’ap gen kote pou ou kache. </p>\n<p><br/> <strong>4.</strong></p>\n<p>E tout frè e sè ki genyen pisans </p>\n<p>Nou dwe ran glwa pou riches sa-a </p>\n<p>Lemonn pa konnen kisa nou jwenn </p>\n<p>Se sovè-a ki renmen nou </p>\n','Gen-De-Bagay-Mwen-Pa-De-Konnen',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2216,'9b121d69-bf27-45a3-a470-66bf327eebf8',0,0,'Pawòl Bondye Di Nou: Lè Kris Prèt Pou-l Vini',28,'28_RN_ht','1.\nPawòl Bondye di nou: lè Kris prèt pou-l vini\nLamou va refwadi anpil fwa va branle\nPeche va ogmante, lè syèk la pral fini,\nMalgre sa, satan pa janm repanti\n \nRefren:\nJezu ap vi-n maten, middi ou a minwi\nTout mò ki nan Kris y ova tande twonpèt la\nYo va resisite pou yo ale nan syèl,\nKote pap gen lanmò, se viv tout tan.\n \n2.\nTraka isiba yo ranpli kè nou ak krint,\nJouk tan nou fi-n bliye tout byenfè Bondye yo\nImilye nou pou-n sòti anba chatiman,\nFòk nou kretyen pou nou vi-n pèleren.\n \n3.\nEprèv y ova fini lè nou monte anwo,\nNou va jwaye tout tanlè nou ale nan syèl,\nna bliye tout sousi, na  di lemon adye\npou-n ale jwi bone ki p-ap fini.\n \n4.\nNou tout te kondane, Jezu fè nou pwomès\nSi nou vle kenbe li, na jwenn la delivrans\nPwomès la akonpli sou lkwa di kalvè.\nNou di mèsi Seyè pou amou li.','<p><strong>1.</strong> </p>\n<p>Pawòl Bondye di nou: lè Kris prèt pou-l vini </p>\n<p>Lamou va refwadi anpil fwa va branle </p>\n<p>Peche va ogmante, lè syèk la pral fini, </p>\n<p>Malgre sa, satan pa janm repanti </p>\n<p>Refren: </p>\n<p>Jezu ap vi-n maten, middi ou a minwi </p>\n<p>Tout mò ki nan Kris y ova tande twonpèt la </p>\n<p>Yo va resisite pou yo ale nan syèl, </p>\n<p>Kote pap gen lanmò, se viv tout tan. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Traka isiba yo ranpli kè nou ak krint, </p>\n<p>Jouk tan nou fi-n bliye tout byenfè Bondye yo </p>\n<p>Imilye nou pou-n sòti anba chatiman, </p>\n<p>Fòk nou kretyen pou nou vi-n pèleren. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Eprèv y ova fini lè nou monte anwo, </p>\n<p>Nou va jwaye tout tanlè nou ale nan syèl, </p>\n<p>na bliye tout sousi, na  di lemon adye </p>\n<p>pou-n ale jwi bone ki p-ap fini. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nou tout te kondane, Jezu fè nou pwomès </p>\n<p>Si nou vle kenbe li, na jwenn la delivrans </p>\n<p>Pwomès la akonpli sou lkwa di kalvè. </p>\n<p>Nou di mèsi Seyè pou amou li. </p>\n','Pawol-Bondye-Di-Nou:-Le-Kris-Pret-Pou-l-Vini',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2217,'4cb9ad43-4fea-42f9-95c0-71c05be4143b',0,0,'Gen Yon Non Mwen Renmen Chante',17,'17_RN_ht','1.\nGen yon non mwen renmen chante,\nSe yon non mwen p’ap janm bliye\nPaske se pa li mwen sove,\nPrèsye non Jezu-Kri\n \nRefren:\nO wi mwen renmen Jezu (3 fwa)\nPaske li renmen-m anvan\n \n2.\nBwa kalvè, mwen pap janm bliye\nSe la li mouri pou sove\nTout pechè ki vle vi-n lave\nNan san li ki koule la\n \n3.\nPawòl Bondye mape sere,\nNan kè mwen pou mwen pa peche\nKont Jezu kit e vle sove nou,\nLè li mouri pou nou.','<p><strong>1.</strong> </p>\n<p>Gen yon non mwen renmen chante, </p>\n<p>Se yon non mwen p’ap janm bliye </p>\n<p>Paske se pa li mwen sove, </p>\n<p>Prèsye non Jezu-Kri </p>\n<p>Refren: </p>\n<p>O wi mwen renmen Jezu (3 fwa) </p>\n<p>Paske li renmen-m anvan </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bwa kalvè, mwen pap janm bliye </p>\n<p>Se la li mouri pou sove </p>\n<p>Tout pechè ki vle vi-n lave </p>\n<p>Nan san li ki koule la </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pawòl Bondye mape sere, </p>\n<p>Nan kè mwen pou mwen pa peche </p>\n<p>Kont Jezu kit e vle sove nou, </p>\n<p>Lè li mouri pou nou. </p>\n','Gen-Yon-Non-Mwen-Renmen-Chante',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2218,'a4a6733a-e578-494c-8291-8ab8c8e443a2',0,0,'Ou pa kapab ale nan syel-la',60,'60_RN_ht','1.\nOu pa kapab ale nan syel-la,\nSi ou viv nan peche isiba (2 fwa)\nSe pou ou vi-n jwenn ak Jezukri.\n\nKè\nSe pou ou vi-n jwenn ak Jezukri (2 fwa)\n\n2.\nOu pa kapab ale nan la,\nSi ou sevi satan (2 fwa)\nSe pou w vi-n jwenn ak Jezu Kri\n\n3.\nOu pakab ale nan syel la,\nSi ou pa lave nan bel sous la (2 fwa)\nSe pou ou vin jwenn ak Jezu Kri','<p><strong>1.</strong> </p>\n<p>Ou pa kapab ale nan syel-la, </p>\n<p>Si ou viv nan peche isiba (2 fwa) </p>\n<p>Se pou ou vi-n jwenn ak Jezukri. </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Se pou ou vi-n jwenn ak Jezukri (2 fwa)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou pa kapab ale nan la, </p>\n<p>Si ou sevi satan (2 fwa) </p>\n<p>Se pou w vi-n jwenn ak Jezu Kri </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou pakab ale nan syel la, </p>\n<p>Si ou pa lave nan bel sous la (2 fwa) </p>\n<p>Se pou ou vin jwenn ak Jezu Kri </p>\n','Ou-pa-kapab-ale-nan-syel-la',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2219,'32c0c353-d8d3-40cf-b3e7-39e15108f8b7',0,0,'Yon jou m\'te pedi nan chemen an',77,'77_RN_ht','1.\nYon jou m’te pèdi nan chemen an,\nMwen pat kapab konprann ki kote’m dwe ale,\nMen yon moun té vini pran men mwen\nE ki te kondwi mwen kote dlo-a byen fre.\nKe\nJezu se Rwa, Jezu se Rwa,\nJezu se Rwa de Rwa \nLap vin’ chache mwen pou’l\nMennem an sytel la,\nJezu se Rwa de Rwa.\n2.\nDepi jou men’l te poze sou mwen\nLa vi mwen te chanje, e nan’m mwen te vi’n lib.\nNan ke mwen te toujou gen yon chan\nTemwaye grande gran amou li pou mwen,\n3.\nAla yon si gran jou sa va ye,\nLe Jezu va vini pou-l pran mwen menm ak ou \nAnpil moun va di Seye, Seye\nMen yo pap kapab antre nan bel vil seles la.','<p><strong>1.</strong> </p>\n<p>Yon jou m’te pèdi nan chemen an, </p>\n<p>Mwen pat kapab konprann ki kote’m dwe ale, </p>\n<p>Men yon moun té vini pran men mwen </p>\n<p>E ki te kondwi mwen kote dlo-a byen fre. </p>\n<p>Jezu se Rwa, Jezu se Rwa, </p>\n<p>Jezu se Rwa de Rwa </p>\n<p>Lap vin’ chache mwen pou’l </p>\n<p>Mennem an sytel la, </p>\n<p>Jezu se Rwa de Rwa. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Depi jou men’l te poze sou mwen </p>\n<p>La vi mwen te chanje, e nan’m mwen te vi’n lib. </p>\n<p>Nan ke mwen te toujou gen yon chan </p>\n<p>Temwaye grande gran amou li pou mwen, </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ala yon si gran jou sa va ye, </p>\n<p>Le Jezu va vini pou-l pran mwen menm ak ou </p>\n<p>Anpil moun va di Seye, Seye </p>\n<p>Men yo pap kapab antre nan bel vil seles la. </p>\n','Yon-jou-m\'te-pedi-nan-chemen-an',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2220,'33083bdd-3dca-421d-816c-97e5dab65165',0,0,'Sipoze Bondye Te Chèche Nan Syèl',47,'47_RN_ht','1.\nSipoze Bondye te chèche nan syèl,\nLi pat kab jwenn pèson kit e vle’l\nPou-l te bay siprèm sakrifis nesesè\nLi bay vi li pou sove mwen ak ou\n \nRefren:\nO, o si se pat pou yon plas yo rele mòn kalvè\nSi se pa te sou vye bwa la kwa-a\nSi se pat pou nonm sa-a yo rele Jezu-a\nE pou toujou mwen menm ak ou ta pèdi\n \n2.\nM-si kontan L-te aksepte bwè koup amè\nMalgre priyè, pè lese-l pase lwen\nm-si kontan li pat rele zanj nan syèl yo\npou vi-n wete klou k-ap toumante menlyo','<p><strong>1.</strong> </p>\n<p>Sipoze Bondye te chèche nan syèl, </p>\n<p>Li pat kab jwenn pèson kit e vle’l </p>\n<p>Pou-l te bay siprèm sakrifis nesesè </p>\n<p>Li bay vi li pou sove mwen ak ou </p>\n<p>Refren: </p>\n<p>O, o si se pat pou yon plas yo rele mòn kalvè </p>\n<p>Si se pa te sou vye bwa la kwa-a </p>\n<p>Si se pat pou nonm sa-a yo rele Jezu-a </p>\n<p>E pou toujou mwen menm ak ou ta pèdi </p>\n<p><br/> <strong>2.</strong></p>\n<p>M-si kontan L-te aksepte bwè koup amè </p>\n<p>Malgre priyè, pè lese-l pase lwen </p>\n<p>m-si kontan li pat rele zanj nan syèl yo </p>\n<p>pou vi-n wete klou k-ap toumante menlyo </p>\n','Sipoze-Bondye-Te-Cheche-Nan-Syel',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2221,'e3df254b-6cf9-4e6a-8984-778c50d655b9',0,0,'Nan Mitan Gwo Lanmè',18,'18_RN_ht','1.\nNan mitan gwo lanmè,\nAk tranpèt mwen te ye.\nSan sekou m’te pèdi,\nSan lafwa, san limyè\nToutakou mwen wè yon gwo bato ap pase\nMwen ranmase tout fòs mwen\nE mwen te rele “O sekou” (2 fwa)\n \n2.\nSe bato levanjil vapè byen ekipe\nM’ap Mennen fidèl yo nan rwayòm Bondye,\nKapitènbato-a wè efò mwen t’ap fè\nAk yon men byen anlè\nMwen te kriye trè klè: “O sekou” (2 fwa)\n \n3.\nTouswit bato sovtaj,\nFè mwen reprann kouraj,\nNan gran bonte-l chef la\nPran ka mwen an chaj,\nKounye-a mwen kontan e zafè mwen regle,\nPaske gran Sovè-a rive kote-m te ye\nGlwa a Dye! Glwa a Dye konsa nanm\nMwen chante “glwa a Dye!”\n \n4.\nZanmi dekoiuraje ki ki pa konnen lapè,\nK’ap fè nofraj anba tèrib lanmè peche,\nSenyè mwen dispose sove ou si ou vle,\nvi-n chase sekou li kounye-a san delè\nli sove-m! li sove-m!\nak lajwa m’ap chante li sove-m.','<p><strong>1.</strong> </p>\n<p>Nan mitan gwo lanmè, </p>\n<p>Ak tranpèt mwen te ye. </p>\n<p>San sekou m’te pèdi, </p>\n<p>San lafwa, san limyè </p>\n<p>Toutakou mwen wè yon gwo bato ap pase </p>\n<p>Mwen ranmase tout fòs mwen </p>\n<p>E mwen te rele “O sekou” (2 fwa) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Se bato levanjil vapè byen ekipe </p>\n<p>M’ap Mennen fidèl yo nan rwayòm Bondye, </p>\n<p>Kapitènbato-a wè efò mwen t’ap fè </p>\n<p>Ak yon men byen anlè </p>\n<p>Mwen te kriye trè klè: “O sekou” (2 fwa) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Touswit bato sovtaj, </p>\n<p>Fè mwen reprann kouraj, </p>\n<p>Nan gran bonte-l chef la </p>\n<p>Pran ka mwen an chaj, </p>\n<p>Kounye-a mwen kontan e zafè mwen regle, </p>\n<p>Paske gran Sovè-a rive kote-m te ye </p>\n<p>Glwa a Dye! Glwa a Dye konsa nanm </p>\n<p>Mwen chante “glwa a Dye!” </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zanmi dekoiuraje ki ki pa konnen lapè, </p>\n<p>K’ap fè nofraj anba tèrib lanmè peche, </p>\n<p>Senyè mwen dispose sove ou si ou vle, </p>\n<p>vi-n chase sekou li kounye-a san delè </p>\n<p>li sove-m! li sove-m! </p>\n<p>ak lajwa m’ap chante li sove-m. </p>\n','Nan-Mitan-Gwo-Lanme',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2222,'c44ba179-4c72-418b-aa2b-51d756d21184',0,0,'Le nou nan lamize,  akable toumente',66,'66_RN_ht','1.\nLe nou nan lamize, akable toumante,\nLe la vi nou boulvese \nLe nou pedi espwa \nLe tout bagay fe nwa,\nLe nou pa we devan,\nLe tristes nan ke nou,\nLe nou nan laflilksyon\nLe nou vle dekouraje\nLeve zye nou anle,\nEpi gade Jezu\nN’a santi nou pi fo.\n\nRefren\nJézu ! Jézu !\nTanpri ban-nou fos pou nou lite,\nJezu ! Jezu !\nSi ou pa kenbe-n nou va tonbe.\n2.\nPafwa se tantasyon\nChak fwa nou vle fe byen,\nMen se mal ki prezante,\nNou gen bon volonte,\nMen twop difikilte fe nou pedi lafwa.\nPafwa se maladi, gen de le se lanmo,\nElas ! nou dezespere,\nSi nou priye Jezu, ke se nan limenm sel\nNou gen konsolasyon\n3.\nSa vre nou vle travay,\nNou vle machje pou glwa li,\nMen nou manke kouraj\nLe zeprev yo vini, gen kote nou febli,\nMalgre kalamite,\nMalgre difikilte \nMen nou pa janm ebranle,\nAnnou priye Jezu,\nPaske se limenm sel\nKi kab ban ou lajwa.','<p><strong>1.</strong> </p>\n<p>Le nou nan lamize, akable toumante, </p>\n<p>Le la vi nou boulvese </p>\n<p>Le nou pedi espwa </p>\n<p>Le tout bagay fe nwa, </p>\n<p>Le nou pa we devan, </p>\n<p>Le tristes nan ke nou, </p>\n<p>Le nou nan laflilksyon </p>\n<p>Le nou vle dekouraje </p>\n<p>Leve zye nou anle, </p>\n<p>Epi gade Jezu </p>\n<p>N’a santi nou pi fo. </p>\n<p>Refren </p>\n<p>Jézu ! Jézu ! </p>\n<p>Tanpri ban-nou fos pou nou lite, </p>\n<p>Jezu ! Jezu ! </p>\n<p>Si ou pa kenbe-n nou va tonbe. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pafwa se tantasyon </p>\n<p>Chak fwa nou vle fe byen, </p>\n<p>Men se mal ki prezante, </p>\n<p>Nou gen bon volonte, </p>\n<p>Men twop difikilte fe nou pedi lafwa. </p>\n<p>Pafwa se maladi, gen de le se lanmo, </p>\n<p>Elas ! nou dezespere, </p>\n<p>Si nou priye Jezu, ke se nan limenm sel </p>\n<p>Nou gen konsolasyon </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sa vre nou vle travay, </p>\n<p>Nou vle machje pou glwa li, </p>\n<p>Men nou manke kouraj </p>\n<p>Le zeprev yo vini, gen kote nou febli, </p>\n<p>Malgre kalamite, </p>\n<p>Malgre difikilte </p>\n<p>Men nou pa janm ebranle, </p>\n<p>Annou priye Jezu, </p>\n<p>Paske se limenm sel </p>\n<p>Ki kab ban ou lajwa. </p>\n','Le-nou-nan-lamize-akable-toumente',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2223,'08da0c2a-84a2-470b-9847-78d2d86a9167',0,0,'Mwen sonje gen yon you kijan mwen te malad',75,'75_RN_ht','1.\nMwen sonje gen yon jou kijan\nMwen te malad \nDokte te di’m konsa li pa kwe map sove.\nParan’m te tande sa, yo tout te pran krye\nMen Jezu te pale nan jou sa-a\nRefren\nMwen pa kapab bliye, bliye jou sa-a\nKoman’w ou vle pou’m ta bliye jou sa-a\nJou’m te jwenn soulajman pou nan’m mwen \nGlwa a Dye (bis)\n2.\nLe mwen pat gen Jezu, ke mwen te tout tris\nMwen pat kapab manje, e mwen pa kapab domi,\nLe’m vini jwenn ka Jezu tout bagay te change \nA la yon bon zanmi se Jezu.\n3.\nLe mwen te nanmond la, m’te toujou ap kriye \nPeche mwen te leve, pwoblem agoch adwat \nMwen te remet Jezi ti res la.\n4.\nJodi-a si gen yon moun ki poko gen Jezu,\nKouri vi’n aksepte’l paske le-a\nNan Jezi gen la pe, gen lavi nan Jezu\nNan Jezi gen yon let kap koule.\n5.\nOu menm fre’m, se’m yo ki déjà nan legliz\nFok ou kapab konnen ke satan frekan,\nMet tripotaj ate, mete logey ate,\nPou satan pa vire men-l pran w.\n6.\nOu vle kraze Legliz avek lan ou genyen\nOu fe pawol mache sou do fre\nLanj Bondye ba w a, se pou lwe non li\nKonveti’w koulie-a piga’w jwe.','<p><strong>1.</strong> </p>\n<p>Mwen sonje gen yon jou kijan </p>\n<p>Mwen te malad </p>\n<p>Dokte te di’m konsa li pa kwe map sove. </p>\n<p>Paran’m te tande sa, yo tout te pran krye </p>\n<p>Men Jezu te pale nan jou sa-a </p>\n<p>Refren </p>\n<p>Mwen pa kapab bliye, bliye jou sa-a </p>\n<p>Koman’w ou vle pou’m ta bliye jou sa-a </p>\n<p>Jou’m te jwenn soulajman pou nan’m mwen </p>\n<p>Glwa a Dye (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le mwen pat gen Jezu, ke mwen te tout tris </p>\n<p>Mwen pat kapab manje, e mwen pa kapab domi, </p>\n<p>Le’m vini jwenn ka Jezu tout bagay te change </p>\n<p>A la yon bon zanmi se Jezu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le mwen te nanmond la, m’te toujou ap kriye </p>\n<p>Peche mwen te leve, pwoblem agoch adwat </p>\n<p>Mwen te remet Jezi ti res la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jodi-a si gen yon moun ki poko gen Jezu, </p>\n<p>Kouri vi’n aksepte’l paske le-a </p>\n<p>Nan Jezi gen la pe, gen lavi nan Jezu </p>\n<p>Nan Jezi gen yon let kap koule. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ou menm fre’m, se’m yo ki déjà nan legliz </p>\n<p>Fok ou kapab konnen ke satan frekan, </p>\n<p>Met tripotaj ate, mete logey ate, </p>\n<p>Pou satan pa vire men-l pran w. </p>\n<p><br/> <strong>6.</strong></p>\n<p>Ou vle kraze Legliz avek lan ou genyen </p>\n<p>Ou fe pawol mache sou do fre </p>\n<p>Lanj Bondye ba w a, se pou lwe non li </p>\n<p>Konveti’w koulie-a piga’w jwe. </p>\n','Mwen-sonje-gen-yon-you-kijan-mwen-te-malad',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2224,'f8378c29-e8e0-431e-ab99-9d2fb9cefb2b',0,0,'Que Nos Chants Retentissent',4,'4_RN_fr','1.\nQue nos chants retentissent,\nEt montent vers les cieux\nQue les airs réjouissent,\nDe mille cris joyeux\nEcho encore, écho sonore,\n \nRéveille-toi  réveille-toi\nPour dire encore (bis)\nTout notre émoi, tout notre émoi !\n \n2.\nPetites fleurs coquettes,\nEclatent vos couleurs\nToujours de grandes fêtes\nRépandez vos odeurs.\n \n3.\nBlancs, li as, fraiches rose\nMortels c’emi-eclos\nTout ceux-là passent\nMes paroles ne passeront pas.','<p><strong>1.</strong> </p>\n<p>Que nos chants retentissent, </p>\n<p>Et montent vers les cieux </p>\n<p>Que les airs réjouissent, </p>\n<p>De mille cris joyeux </p>\n<p>Echo encore, écho sonore, </p>\n<p>Réveille-toi  réveille-toi </p>\n<p>Pour dire encore (bis) </p>\n<p>Tout notre émoi, tout notre émoi ! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Petites fleurs coquettes, </p>\n<p>Eclatent vos couleurs </p>\n<p>Toujours de grandes fêtes </p>\n<p>Répandez vos odeurs. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Blancs, li as, fraiches rose </p>\n<p>Mortels c’emi-eclos </p>\n<p>Tout ceux-là passent </p>\n<p>Mes paroles ne passeront pas. </p>\n','Que-Nos-Chants-Retentissent',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2225,'272dc0cf-c61f-49df-8dfb-a838af8cb39c',0,0,'Nan gran jou larezireksyon',63,'63_RN_ht','1.\nNan gran jou larezireksyon,\nLe twonpet Bondye sonnen,\nN’a ranpli ak benediksyon\nKi gran jwa nou va genyen \nN’a levé, alelouia, n’a levé\nRefren\nN’a levé n’a levé, n’a levé\nNan gran jou larezireksyon\nLè pot prison lanmo krazé,\nN’a leve, n’a levé, Alelouya','<p><strong>1.</strong> </p>\n<p>Nan gran jou larezireksyon, </p>\n<p>Le twonpet Bondye sonnen, </p>\n<p>N’a ranpli ak benediksyon </p>\n<p>Ki gran jwa nou va genyen </p>\n<p>N’a levé, alelouia, n’a levé </p>\n<p>Refren </p>\n<p>N’a levé n’a levé, n’a levé </p>\n<p>Nan gran jou larezireksyon </p>\n<p>Lè pot prison lanmo krazé, </p>\n<p>N’a leve, n’a levé, Alelouya </p>\n','Nan-gran-jou-larezireksyon',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2226,'c67dfa07-211c-47f2-aebf-fe4aec466701',0,0,'Kenbe Fèm Nan Travay N’ap Fè Pou Jezu-Kri',19,'19_RN_ht','1.\nKenbe fèm nan travay n’ap fè pou Jezu-Kri\nRout la pa faisil, fòf nou toujou soufri\nLè satan fè ravaj li toupatou,\nMwne konnen Jezu la li va kenbe nou\n \nRefren:\nKenbe fèm jouk sa fini\nKenbe fèm jouk Kris vini,\nKenbe fèm piga ou lage\nlè Jezu la ou p’ap janmè fatige\n \n2.\nYon sòlda p’ap janm touché san-l pa travay\nKi toujou rete fidèl nan tout bagay\nKi p’ap janmè rekile lè ou nan batay\nNou konnen aprè batay n’ap gen viktwa\n \n3.\nJezu sere yon bèl kouròn pou tout kretyen\nKi toujou rete fidèl nan tout jiska lafen,\nVa gen tout kalite kouròn nan jou sa\nLite byen pou nou ka pran tout kouròn yo.','<p><strong>1.</strong> </p>\n<p>Kenbe fèm nan travay n’ap fè pou Jezu-Kri </p>\n<p>Rout la pa faisil, fòf nou toujou soufri </p>\n<p>Lè satan fè ravaj li toupatou, </p>\n<p>Mwne konnen Jezu la li va kenbe nou </p>\n<p>Refren: </p>\n<p>Kenbe fèm jouk sa fini </p>\n<p>Kenbe fèm jouk Kris vini, </p>\n<p>Kenbe fèm piga ou lage </p>\n<p>lè Jezu la ou p’ap janmè fatige </p>\n<p><br/> <strong>2.</strong></p>\n<p>Yon sòlda p’ap janm touché san-l pa travay </p>\n<p>Ki toujou rete fidèl nan tout bagay </p>\n<p>Ki p’ap janmè rekile lè ou nan batay </p>\n<p>Nou konnen aprè batay n’ap gen viktwa </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jezu sere yon bèl kouròn pou tout kretyen </p>\n<p>Ki toujou rete fidèl nan tout jiska lafen, </p>\n<p>Va gen tout kalite kouròn nan jou sa </p>\n<p>Lite byen pou nou ka pran tout kouròn yo. </p>\n','Kenbe-Fem-Nan-Travay-N\'ap-Fe-Pou-Jezu-Kri',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2227,'ba548ddd-5e63-44b0-8902-e1567a82c723',0,0,'Mesi Seyè mesi Seyè',71,'71_RN_ht','1.\nMesi Seye mesi Seye\nTout bagay va vini tre byen\nMesi Seye, mesi Seye\nTout bagay va vini tre byen.\n2.\nSyel la pase te a pase\nPawol w demere toujou\nMesi Seye mesi Seye\nTout bagay va vini tre byen.','<p><strong>1.</strong> </p>\n<p>Mesi Seye mesi Seye </p>\n<p>Tout bagay va vini tre byen </p>\n<p>Mesi Seye, mesi Seye </p>\n<p>Tout bagay va vini tre byen. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Syel la pase te a pase </p>\n<p>Pawol w demere toujou </p>\n<p>Mesi Seye mesi Seye </p>\n<p>Tout bagay va vini tre byen. </p>\n','Mesi-Seye-mesi-Seye',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2228,'afdd3f25-755e-416b-9db4-077f6c116713',0,0,'Lè Moman-an Te Pi Difisil',44,'44_RN_ht','1.\nLè moman-an te pi difisil,\nPitit, madanm tonbe ,alad,\nKouche sou kabann lopital\nAk lapenn mwen pa na travay.\nYo ban mwen papye preskripsyon\nMezanmi mwen m’pa gen a touché\nMwen louvri bra mwen bay Jezu\nSe li menm sèl ki ka sove-m\n \nRefren:\nMwen p-ap lache Jezu yon may\nAvèk li mwen pi asire\nMwen p-ap kite Jezu sovè-m\nAvèk li m-ap genyen viktwa.\n \n2.\nS-ak te di-m ale kay book,\nAk lajan m-al rele vye loua,\nAjenouye nan pye pechè,\nPitit, madanm mwen pral mouri,\nMwen fè yon ekzamen konsyans,\nSi yon nonm te kab bay lavi\nNonm sila-a pa te janm mouri\nTout byen sou late ta pou li\n \n3.\nDoktè di-m pa genyen espwa\nTou de moun yo prèt pou mouri\nMen mwen di “O non de Jezu!\nPa gen anyen dròl k-ap pase”\nTou de moun yo pat k-ap manje\nMen demen, yo mande manje\nWuit jou pase yo ekzeyate\nSou proteksyon Jezu sovè-m','<p><strong>1.</strong> </p>\n<p>Lè moman-an te pi difisil, </p>\n<p>Pitit, madanm tonbe ,alad, </p>\n<p>Kouche sou kabann lopital </p>\n<p>Ak lapenn mwen pa na travay. </p>\n<p>Yo ban mwen papye preskripsyon </p>\n<p>Mezanmi mwen m’pa gen a touché </p>\n<p>Mwen louvri bra mwen bay Jezu </p>\n<p>Se li menm sèl ki ka sove-m </p>\n<p>Refren: </p>\n<p>Mwen p-ap lache Jezu yon may </p>\n<p>Avèk li mwen pi asire </p>\n<p>Mwen p-ap kite Jezu sovè-m </p>\n<p>Avèk li m-ap genyen viktwa. </p>\n<p><br/> <strong>2.</strong></p>\n<p>S-ak te di-m ale kay book, </p>\n<p>Ak lajan m-al rele vye loua, </p>\n<p>Ajenouye nan pye pechè, </p>\n<p>Pitit, madanm mwen pral mouri, </p>\n<p>Mwen fè yon ekzamen konsyans, </p>\n<p>Si yon nonm te kab bay lavi </p>\n<p>Nonm sila-a pa te janm mouri </p>\n<p>Tout byen sou late ta pou li </p>\n<p><br/> <strong>3.</strong></p>\n<p>Doktè di-m pa genyen espwa </p>\n<p>Tou de moun yo prèt pou mouri </p>\n<p>Men mwen di “O non de Jezu! </p>\n<p>Pa gen anyen dròl k-ap pase” </p>\n<p>Tou de moun yo pat k-ap manje </p>\n<p>Men demen, yo mande manje </p>\n<p>Wuit jou pase yo ekzeyate </p>\n<p>Sou proteksyon Jezu sovè-m </p>\n','Le-Moman-an-Te-Pi-Difisil',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2229,'2c1a1dbd-334b-4b67-80e9-ee4aa3ee4c86',0,0,'Gen moun ki konprann ale nan syel la',76,'76_RN_ht','1.\nGen moun ki konprann ale nan syèl la\nSe tankou pran panye ale nan mache,\nSe pa bel kay, ni lajan ak bel oto.\nRefren\nAle nansyel se pa jan ou konpran nan,\nW gen anpil bagay pou fe pou ka ale nan syel.\nAle nan syel se pa jan w konpran nan,\nW gen anpil travay pou fe pou kab ale.\n2.\nMalgre n’ap chante, malgre na’p priye\nMalgre n’ap jene temwaye tou le jou,\nEpi gen yon moun ki mare sou ke nou\nEpi gen yon moun ki mare sou ke nou\nEpi nou ront pale de syel la.\n3.\nW gen si lontan depi ou nan legliz\nKondwit ou few e ke ou pako ketyen,\nRenmen an kachet avek lemonn yo,\nEpi nou konprann pou nale nan syel.\n4.\nOu genyen dwet lou genyen lang long,\nOu genyen 2 madan’m ou genyen 2 menaj\nGenle ou konprann jan wap antre legliz\nSe konsa ou prale nan syel la.','<p><strong>1.</strong> </p>\n<p>Gen moun ki konprann ale nan syèl la </p>\n<p>Se tankou pran panye ale nan mache, </p>\n<p>Se pa bel kay, ni lajan ak bel oto. </p>\n<p>Refren </p>\n<p>Ale nansyel se pa jan ou konpran nan, </p>\n<p>W gen anpil bagay pou fe pou ka ale nan syel. </p>\n<p>Ale nan syel se pa jan w konpran nan, </p>\n<p>W gen anpil travay pou fe pou kab ale. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Malgre n’ap chante, malgre na’p priye </p>\n<p>Malgre n’ap jene temwaye tou le jou, </p>\n<p>Epi gen yon moun ki mare sou ke nou </p>\n<p>Epi gen yon moun ki mare sou ke nou </p>\n<p>Epi nou ront pale de syel la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>W gen si lontan depi ou nan legliz </p>\n<p>Kondwit ou few e ke ou pako ketyen, </p>\n<p>Renmen an kachet avek lemonn yo, </p>\n<p>Epi nou konprann pou nale nan syel. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ou genyen dwet lou genyen lang long, </p>\n<p>Ou genyen 2 madan’m ou genyen 2 menaj </p>\n<p>Genle ou konprann jan wap antre legliz </p>\n<p>Se konsa ou prale nan syel la. </p>\n','Gen-moun-ki-konprann-ale-nan-syel-la',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2230,'fa472305-b6d7-4702-a504-82d18363ab90',0,0,'Petet se pa sou yon gwo montagn',62,'62_RN_ht','1.\nPetet se pa sou yon gwo montagn\nNi nan yon peyi byen lwen.\nPetet se pa nan yon gran batay,\nMenm si mwen pat gen anyen,\nM-ap reponn Bondye Ak men w sou mwen\nMwen ale kote ou vle.\nRefren\nMwen vle ale kote w vle, Bondye\nNan mon sou lame nan vale\nMwen va di sa ou vle mwen di Bondye\n2.\nPetet jodi-a se pawol dou\nJezu Kri vle pou-m pale \nSandout dwe genyen dou rout la tou,\nMwen egare, pou-m cheche.\nO, Bon Sove si ou va gide-m\nE si sou wout la w kenben-m\nMwen va repete mesaj lavi,\nMwen va di sa ou vle mwen di.\n3.\nSetenman genyen yon vie ti plas,\nNan gran rekot monn sa-a \nKote-m kab fe menm yon ti kras\nPou Jezu krisifye-a.\nM’a mete konfyans mwen nan ou Seye-m\nPaske w te renmen mwen vre,\nM’a fe volonte’w che sovem \nMwen va sa w vle ou mwen ye.','<p><strong>1.</strong> </p>\n<p>Petet se pa sou yon gwo montagn </p>\n<p>Ni nan yon peyi byen lwen. </p>\n<p>Petet se pa nan yon gran batay, </p>\n<p>Menm si mwen pat gen anyen, </p>\n<p>M-ap reponn Bondye Ak men w sou mwen </p>\n<p>Mwen ale kote ou vle. </p>\n<p>Refren </p>\n<p>Mwen vle ale kote w vle, Bondye </p>\n<p>Nan mon sou lame nan vale </p>\n<p>Mwen va di sa ou vle mwen di Bondye </p>\n<p><br/> <strong>2.</strong></p>\n<p>Petet jodi-a se pawol dou </p>\n<p>Jezu Kri vle pou-m pale </p>\n<p>Sandout dwe genyen dou rout la tou, </p>\n<p>Mwen egare, pou-m cheche. </p>\n<p>O, Bon Sove si ou va gide-m </p>\n<p>E si sou wout la w kenben-m </p>\n<p>Mwen va repete mesaj lavi, </p>\n<p>Mwen va di sa ou vle mwen di. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Setenman genyen yon vie ti plas, </p>\n<p>Nan gran rekot monn sa-a </p>\n<p>Kote-m kab fe menm yon ti kras </p>\n<p>Pou Jezu krisifye-a. </p>\n<p>M’a mete konfyans mwen nan ou Seye-m </p>\n<p>Paske w te renmen mwen vre, </p>\n<p>M’a fe volonte’w che sovem </p>\n<p>Mwen va sa w vle ou mwen ye. </p>\n','Petet-se-pa-sou-yon-gwo-montagn',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2231,'7ac118fe-7367-4a6a-8f1f-7d84dc9e173a',0,0,'Si je parlais le plus parfait language',1,'1_RN_fr','1.\nSi je parlais le plus parfait language,\nSi même un ange me prêtait le sien,\nQue me vaudrait ce futile avantage?\nSi je n’ai pas l’amour, je ne suis rien\nJe ne suis rien qu’un airain qui résonne\nQu’une cymbale au rythme répète.\nTout manqué encore aux grâces que Dieu Donne\nSi je n’ai pas pas au Cœur l’amour, la charité.\n \n2.\nEt quand j’aurais le don de prophétie,\nQue tout secret me serait révèle\nQuand j’atteindrais au savoir, au génie\nQuand je devrais, martyr être brule,\nOu si ma foi transportait la montagne\nEt ma pitié distribuait mon bien\nQuelque mérite en cela que je gagne\nSans cet amour, tout ne me servirait de rien\n \n3.\nLa charité se montre patiente\nEt bienveillante à l’égard du prochain\nN’est pas jalouse et non plus ne se vante\nNe s’enfle point d’orgueil et sans dédain\nPlaine d’égard, ne fait tort à personne\nTout égoïsme est chez elle écarté,\nPoint ne s’irrite et jamais ne soupçonne\nReprouve l’injustice et veut la vérité\n \n4.\nQue de douceur, et de paix elle apporte\nCar elle excuse tout, elle croit tout\nTout, elle espère et tout elle supporte\nJetant son baume et son parfum si doux\nLes plus beaux dons disparaissent et meurent\nCette vertu vit en éternité\nFoi, espérance et charité demeurent\nMais de ces trois, la plus grande est la ’’Charité’’','<p><strong>1.</strong> </p>\n<p>Si je parlais le plus parfait language, </p>\n<p>Si même un ange me prêtait le sien, </p>\n<p>Que me vaudrait ce futile avantage? </p>\n<p>Si je n’ai pas l’amour, je ne suis rien </p>\n<p>Je ne suis rien qu’un airain qui résonne </p>\n<p>Qu’une cymbale au rythme répète. </p>\n<p>Tout manqué encore aux grâces que Dieu Donne </p>\n<p>Si je n’ai pas pas au Cœur l’amour, la charité. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et quand j’aurais le don de prophétie, </p>\n<p>Que tout secret me serait révèle </p>\n<p>Quand j’atteindrais au savoir, au génie </p>\n<p>Quand je devrais, martyr être brule, </p>\n<p>Ou si ma foi transportait la montagne </p>\n<p>Et ma pitié distribuait mon bien </p>\n<p>Quelque mérite en cela que je gagne </p>\n<p>Sans cet amour, tout ne me servirait de rien </p>\n<p><br/> <strong>3.</strong></p>\n<p>La charité se montre patiente </p>\n<p>Et bienveillante à l’égard du prochain </p>\n<p>N’est pas jalouse et non plus ne se vante </p>\n<p>Ne s’enfle point d’orgueil et sans dédain </p>\n<p>Plaine d’égard, ne fait tort à personne </p>\n<p>Tout égoïsme est chez elle écarté, </p>\n<p>Point ne s’irrite et jamais ne soupçonne </p>\n<p>Reprouve l’injustice et veut la vérité </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que de douceur, et de paix elle apporte </p>\n<p>Car elle excuse tout, elle croit tout </p>\n<p>Tout, elle espère et tout elle supporte </p>\n<p>Jetant son baume et son parfum si doux </p>\n<p>Les plus beaux dons disparaissent et meurent </p>\n<p>Cette vertu vit en éternité </p>\n<p>Foi, espérance et charité demeurent </p>\n<p>Mais de ces trois, la plus grande est la ’’Charité’’ </p>\n','Si-je-parlais-le-plus-parfait-language',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2232,'b721c0ae-987b-4648-bc6e-aecd808e4792',0,0,'Bondye voye pitit,  li rele Jezu',67,'67_RN_ht','1.\nBondye voye pitit, li rele Jezu,\nKi te vini e padone-n.\nLi rachte nou e li mete nou lib.\nKounye-a li ak papa-l,\nLap ret tann nou.\nRefren\nMwen konnen-l vivan,\nM-ap konte sou demen, paske-l vivan\nInkyetid yo ale \nPaske-m konnen  O, m-konnen\nLi se tout lavi mwen,\nM-ap konte jou pou-l vin-n cheche-m\nPaske-l vivan.\n2.\nMwen aksepte tout sa ki rive mwen,\nPaske sove-m ap defann mwen,\nJouden-an met devan-m,\nKe mwen p-ap boulvese.\nPaske Jezu-m nan pa mouri,\nE li vivan.\n3.\nTout espwa mwen se sou li selman\nPaske li di-m l’ap vinn cheche-m\nEprev nan lavi sa-a pap fem twouble ditou\nBondye-m nan gran e li vivan\nM’ap ret tann li.','<p><strong>1.</strong> </p>\n<p>Bondye voye pitit, li rele Jezu, </p>\n<p>Ki te vini e padone-n. </p>\n<p>Li rachte nou e li mete nou lib. </p>\n<p>Kounye-a li ak papa-l, </p>\n<p>Lap ret tann nou. </p>\n<p>Refren </p>\n<p>Mwen konnen-l vivan, </p>\n<p>M-ap konte sou demen, paske-l vivan </p>\n<p>Inkyetid yo ale </p>\n<p>Paske-m konnen  O, m-konnen </p>\n<p>Li se tout lavi mwen, </p>\n<p>M-ap konte jou pou-l vin-n cheche-m </p>\n<p>Paske-l vivan. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen aksepte tout sa ki rive mwen, </p>\n<p>Paske sove-m ap defann mwen, </p>\n<p>Jouden-an met devan-m, </p>\n<p>Ke mwen p-ap boulvese. </p>\n<p>Paske Jezu-m nan pa mouri, </p>\n<p>E li vivan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tout espwa mwen se sou li selman </p>\n<p>Paske li di-m l’ap vinn cheche-m </p>\n<p>Eprev nan lavi sa-a pap fem twouble ditou </p>\n<p>Bondye-m nan gran e li vivan </p>\n<p>M’ap ret tann li. </p>\n','Bondye-voye-pitit-li-rele-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2233,'f81d6a25-a1e0-4bb0-ab08-51c737678c6b',1,0,'Je Ne Suis Pas A Moi-meme',2,'2_RN_fr','1.\nJe ne suis pas à moi-même,\nJ’appartiens à mon sauveur\nCrée pas mon Dieu suprême\nSauve par le rédempteur\n \nChoeur\nPas à moi oh ! Non ! (bis)\nSeigneur j’appartiens à toi\nTout mon cœur, toute ma vie\nDésormais tout est à toi\n \n2.\nPas à moi ou sans réserve.\nJe livre tout au sauveur.\nEt c’est lui qui me préserve\nDes pièges du tentateur\n \n3.\nPas à moi! Jésus m’engage\nDans le bataillon sacré\nJe suis à lui sans partage\nA lui pour l’éternité.','<p><strong>1.</strong> </p>\n<p>Je ne suis pas à moi-même, </p>\n<p>J’appartiens à mon sauveur </p>\n<p>Crée pas mon Dieu suprême </p>\n<p>Sauve par le rédempteur </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Pas à moi oh ! Non ! (bis)</strong> </p>\n<p><strong>Seigneur j’appartiens à toi</strong> </p>\n<p><strong>Tout mon cœur, toute ma vie</strong> </p>\n<p><strong>Désormais tout est à toi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pas à moi ou sans réserve. </p>\n<p>Je livre tout au sauveur. </p>\n<p>Et c’est lui qui me préserve </p>\n<p>Des pièges du tentateur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pas à moi! Jésus m’engage </p>\n<p>Dans le bataillon sacré </p>\n<p>Je suis à lui sans partage </p>\n<p>A lui pour l’éternité. </p>\n','Je-Ne-Suis-Pas-A-Moi-meme',NULL,'fr',5,'2023-07-22 22:41:15','2023-08-19 05:01:18'),(2234,'c2632434-11c9-4e24-aa02-18c9d3be344a',2,0,'J’ai trouvé la voie',3,'3_RN_fr','1.\nJ’ai trouvé la voie\nQui mène au jour sans fin.\nLà-haut dans le lieu divin.\nMais pour parcourir\nLe chemin sans faillir.\nSeigneur tiens-moi près de toi.\n \nRefrain\nJ’ai trouvé la voie (bis)\nLa voie du seigneur oh ! Gloire\nAlléluia,\nJ’ai trouvé la voie\n \n2.\nQue craindrais-je encore ?\nCar avec Jésus-Christ,\nJà vais braver l’ennemi.\nEt l’honneur de mon Roi\nTémoigneront ma victoire.\n \n3.\nQuand viendra la fin,\nConduis-nous par la main \nNous suivons le sur chemin\nQui mène au repos\nAlors avec l’agneau\nNous verrons la gloire sans fin.','<p><strong>1.</strong> </p>\n<p>J’ai trouvé la voie </p>\n<p>Qui mène au jour sans fin. </p>\n<p>Là-haut dans le lieu divin. </p>\n<p>Mais pour parcourir </p>\n<p>Le chemin sans faillir. </p>\n<p>Seigneur tiens-moi près de toi. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>J’ai trouvé la voie (bis)</strong> </p>\n<p><strong>La voie du seigneur oh ! Gloire</strong> </p>\n<p><strong>Alléluia,</strong> </p>\n<p><strong>J’ai trouvé la voie</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que craindrais-je encore ? </p>\n<p>Car avec Jésus-Christ, </p>\n<p>Jà vais braver l’ennemi. </p>\n<p>Et l’honneur de mon Roi </p>\n<p>Témoigneront ma victoire. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand viendra la fin, </p>\n<p>Conduis-nous par la main </p>\n<p>Nous suivons le sur chemin </p>\n<p>Qui mène au repos </p>\n<p>Alors avec l’agneau </p>\n<p>Nous verrons la gloire sans fin. </p>\n','J\'ai-trouve-la-voie',NULL,'fr',5,'2023-07-22 22:41:15','2023-09-20 02:21:54'),(2235,'e1346dfe-e68e-47bd-add8-063fbd1f31ac',0,0,'Voulez-vous Y Aller',6,'6_RN_fr','1.\nVoulez-vous y aller\nAu camp des bienheureux\nLa, nous aurons concert avec les anges\nQue nos pas, chaque jour,\nSa dirigent vers les cieux\nEntonnant les cantiques des archanges\n \nRefrain\nMarchons vers la gloire (3 foi)\nPour rencontrer Jésus\n \n2.\nVoulez-vous arriver\nAu céleste rivage\nRegardez à jésus le grand vainqueur\nCar dans l’éternité,\nC’est là qu’est le passage\nEn avant, vers le ciel, vers le bonheur\n \n3.\nEn avant bien- aime,\nPartons pour la milice\nLe tout-puissant est là pour nous defender\nSans craindre et sans douter, prêt à notre justice\nIl est toujours fidèle à notre entendre.','<p><strong>1.</strong> </p>\n<p>Voulez-vous y aller </p>\n<p>Au camp des bienheureux </p>\n<p>La, nous aurons concert avec les anges </p>\n<p>Que nos pas, chaque jour, </p>\n<p>Sa dirigent vers les cieux </p>\n<p>Entonnant les cantiques des archanges </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Marchons vers la gloire (3 foi)</strong> </p>\n<p><strong>Pour rencontrer Jésus</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Voulez-vous arriver </p>\n<p>Au céleste rivage </p>\n<p>Regardez à jésus le grand vainqueur </p>\n<p>Car dans l’éternité, </p>\n<p>C’est là qu’est le passage </p>\n<p>En avant, vers le ciel, vers le bonheur </p>\n<p><br/> <strong>3.</strong></p>\n<p>En avant bien- aime, </p>\n<p>Partons pour la milice </p>\n<p>Le tout-puissant est là pour nous defender </p>\n<p>Sans craindre et sans douter, prêt à notre justice </p>\n<p>Il est toujours fidèle à notre entendre. </p>\n','Voulez-vous-Y-Aller',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2236,'98c4edba-0e59-4080-853e-8882729d2977',0,0,'Otrefwa-m te kouche',79,'79_RN_ht','1.\nOtrefwa-m té kouché,\nZyem louvri m’ap pansé,\nAk komandman Jezi-Kri\nTap bay disip li yo,\nMwen te santi lè sa-a\nYon gwo transformasyon\nYon rivye t’ap koulé nan zye mwen\n\nRefren\nJezu Kri apran-n nou,\nNou dwe gen tet ansan’m\nPi gran Komandman-an\nSe renmen nou youn lot\nAk tout fos de tou kè\nPadone frè nou yo\nPi gran Komandman-an\nSe renmen nou youn lot.\n\n2\nApran’n a padoné\ntout fre e se nou yo\nMen’m le yo meprize-n\nKou yon fatra ki abandone,\nPa kriye, pa plenyen\nresiye ou frè mwen,\nSoufrans lan di se vre, pran Kouraj\n\n3\nPataje ak frè ou\nTristes li ak lapen-n\nSe sa Jezu mande\nPou nou yon soufri pou lot,\nPa imilye fre’w, pa imilye se’w\nNou tout en jeneral sé pousyè.','<p><strong>1.</strong> </p>\n<p>Otrefwa-m té kouché, </p>\n<p>Zyem louvri m’ap pansé, </p>\n<p>Ak komandman Jezi-Kri </p>\n<p>Tap bay disip li yo, </p>\n<p>Mwen te santi lè sa-a </p>\n<p>Yon gwo transformasyon </p>\n<p>Yon rivye t’ap koulé nan zye mwen </p>\n<p>Refren </p>\n<p>Jezu Kri apran-n nou, </p>\n<p>Nou dwe gen tet ansan’m </p>\n<p>Pi gran Komandman-an </p>\n<p>Se renmen nou youn lot </p>\n<p>Ak tout fos de tou kè </p>\n<p>Padone frè nou yo </p>\n<p>Pi gran Komandman-an </p>\n<p>Se renmen nou youn lot. </p>\n<p>Apran’n a padoné </p>\n<p>tout fre e se nou yo </p>\n<p>Men’m le yo meprize-n </p>\n<p>Kou yon fatra ki abandone, </p>\n<p>Pa kriye, pa plenyen </p>\n<p>resiye ou frè mwen, </p>\n<p>Soufrans lan di se vre, pran Kouraj </p>\n<p>Pataje ak frè ou </p>\n<p>Tristes li ak lapen-n </p>\n<p>Se sa Jezu mande </p>\n<p>Pou nou yon soufri pou lot, </p>\n<p>Pa imilye fre’w, pa imilye se’w </p>\n<p>Nou tout en jeneral sé pousyè. </p>\n','Otrefwa-m-te-kouche',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2237,'d132e887-dccb-4178-ba4c-0c85758fc694',0,0,'Ou bezwen Konnen',80,'80_RN_ht','1\nMwen renmen chante yon si bel istwa,\nDepi Kris te santifye mwen,\nKa nam’m mwen te jwenn yon kado lajwa,\nDepi Kris te santifye mwen\n \nRefren\nMwen pap janm bliye, jou sa a dife tonbe\nDife tonbe, dife tonbe.\nMwen pap jan m bliye, jou sa a dife tonbe\nLe Jezu te sanktifye mwen.\n\n2\nTout dout ak krent mwen efase\nDepi Kris te santifye mwen\nafen li te konble m ak gras Li\nLe Jezu te santifye mwen.\n\n3\nPlezi lemonn vin n pa interese m\nLe Jezu te sanktifye m\nLespri tonbe sou mwen kon you dife\nLe Jezi te sanktifye mwen\n\n4\nMwen pa kapab bay frem yo manti anko,\nDepi Kris te sanktifye mwen,\nMwen pa kapab fe tripotaj sou se yo,\nDepi Kris te sanktifwe mwen.\n\n5\nKo mwen vini yon tanp pou Lesentespri\nDepi Kris te sanktifye mwen,\nMwen pakab viv nan peche sal anko\nDepi Kris te sankfifye mwen.','<p>Mwen renmen chante yon si bel istwa, </p>\n<p>Depi Kris te santifye mwen, </p>\n<p>Ka nam’m mwen te jwenn yon kado lajwa, </p>\n<p>Depi Kris te santifye mwen </p>\n<p>Refren </p>\n<p>Mwen pap janm bliye, jou sa a dife tonbe </p>\n<p>Dife tonbe, dife tonbe. </p>\n<p>Mwen pap jan m bliye, jou sa a dife tonbe </p>\n<p>Le Jezu te sanktifye mwen. </p>\n<p>Tout dout ak krent mwen efase </p>\n<p>Depi Kris te santifye mwen </p>\n<p>afen li te konble m ak gras Li </p>\n<p>Le Jezu te santifye mwen. </p>\n<p>Plezi lemonn vin n pa interese m </p>\n<p>Le Jezu te sanktifye m </p>\n<p>Lespri tonbe sou mwen kon you dife </p>\n<p>Le Jezi te sanktifye mwen </p>\n<p>Mwen pa kapab bay frem yo manti anko, </p>\n<p>Depi Kris te sanktifye mwen, </p>\n<p>Mwen pa kapab fe tripotaj sou se yo, </p>\n<p>Depi Kris te sanktifwe mwen. </p>\n<p>Ko mwen vini yon tanp pou Lesentespri </p>\n<p>Depi Kris te sanktifye mwen, </p>\n<p>Mwen pakab viv nan peche sal anko </p>\n<p>Depi Kris te sankfifye mwen. </p>\n','Ou-bezwen-Konnen',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2238,'4152540a-b4e0-49e2-ab9b-37b685669aa4',0,0,'Senyè Mwen Mande Ou Fòs',23,'23_RN_ht','1.\nSenyè mwen mande ou fòs\nPou-m reziste nan konba (2 fwa)\nMwen vle rele fidèl jan ou vle mwen ye\nNan konbat (2 fwa)\n \nRefren:\nKan lespri mwen ta vle boulvèse\nMwen pa pè mwen konnen m’asire\nNan konba (2 fwa)\n \n2.\nPran feblès mwen ban-m fòs\nPou-m kab lite nan konba (2 fwa)\nMwen vle swiv ou e ale kote ou vle\nNan konba (2 fwa)\n \n3.\nSenyè kenbe-m fèm pou mwen pa trouble\nNan konba (2 fwa)\nOu te di mwen tout sa genyen pou rive,\nNan konba (2 fwa)\n \n4.\nNan moman sa nou ye si nou ye si nou pa kenbe ou\nSa na fè (2 fwa)\nNou vle re fidèl pou-n k’ap kourobe\nAprè konba (2 fwa)','<p><strong>1.</strong> </p>\n<p>Senyè mwen mande ou fòs </p>\n<p>Pou-m reziste nan konba (2 fwa) </p>\n<p>Mwen vle rele fidèl jan ou vle mwen ye </p>\n<p>Nan konbat (2 fwa) </p>\n<p>Refren: </p>\n<p>Kan lespri mwen ta vle boulvèse </p>\n<p>Mwen pa pè mwen konnen m’asire </p>\n<p>Nan konba (2 fwa) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pran feblès mwen ban-m fòs </p>\n<p>Pou-m kab lite nan konba (2 fwa) </p>\n<p>Mwen vle swiv ou e ale kote ou vle </p>\n<p>Nan konba (2 fwa) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Senyè kenbe-m fèm pou mwen pa trouble </p>\n<p>Nan konba (2 fwa) </p>\n<p>Ou te di mwen tout sa genyen pou rive, </p>\n<p>Nan konba (2 fwa) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan moman sa nou ye si nou ye si nou pa kenbe ou </p>\n<p>Sa na fè (2 fwa) </p>\n<p>Nou vle re fidèl pou-n k’ap kourobe </p>\n<p>Aprè konba (2 fwa) </p>\n','Senye-Mwen-Mande-Ou-Fos',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2239,'31367451-f41d-4815-8bf7-37395fa0fa62',0,0,'Mwen Gen Yon Bon Papa',30,'30_RN_ht','1.\nMwen gen yon bon papa\nKi konnen fè tout mirak\nLi konn geri malad\nE li konn chase demon\nLi konn chase tristès,\nE li resisite mò\nLi kon chase peche,\nE li konn sove nanm yo\n\nRefren:\nSe Jezu bon mèt la,\nLi konn fè tout mirak\nLi konn geri malad,\nLi konnen chase demon\nJezu bon Sovè-a\nKonn leve menm moun mouri.\nMenm tanpèt avèk van\nLi genyen pouvwa sou yo\n\n2.\nYon jou li tap mache,\nLi rankontre dis lepre\nMen lè yow è Jezu yo rele\nPitye pou nou.\nMen yo te panse\nLi ta prale lapide yo\nMenm lè-a kè-l touché-l\nLi gen konpasyon pou yo,\nLi geri yo menm lè\nE yo vini pirifye.\n\n3.\nYon jou li te sou lanmè\nAvèk kèk nan disip yo\nYon tanpèt te leve\nBak la-prèt pou-l chavire\nJezu te nan domi\nDisip yo te pra rele’l\nLi leve men’l anlè,\nLi di “van fè silans”\n\n4.\nYon jou yo te Mennen ba li\nYon fanm adiltè\nMen yo te panse\nLi prale lapide fanm nan\nLè li gade fanm nan\nLi genyen pitye pou li,\nLi di’l mèt ale men pa fè peche ankò','<p><strong>1.</strong> </p>\n<p>Mwen gen yon bon papa </p>\n<p>Ki konnen fè tout mirak </p>\n<p>Li konn geri malad </p>\n<p>E li konn chase demon </p>\n<p>Li konn chase tristès, </p>\n<p>E li resisite mò </p>\n<p>Li kon chase peche, </p>\n<p>E li konn sove nanm yo </p>\n<p>Refren: </p>\n<p>Se Jezu bon mèt la, </p>\n<p>Li konn fè tout mirak </p>\n<p>Li konn geri malad, </p>\n<p>Li konnen chase demon </p>\n<p>Jezu bon Sovè-a </p>\n<p>Konn leve menm moun mouri. </p>\n<p>Menm tanpèt avèk van </p>\n<p>Li genyen pouvwa sou yo </p>\n<p><br/> <strong>2.</strong></p>\n<p>Yon jou li tap mache, </p>\n<p>Li rankontre dis lepre </p>\n<p>Men lè yow è Jezu yo rele </p>\n<p>Pitye pou nou. </p>\n<p>Men yo te panse </p>\n<p>Li ta prale lapide yo </p>\n<p>Menm lè-a kè-l touché-l </p>\n<p>Li gen konpasyon pou yo, </p>\n<p>Li geri yo menm lè </p>\n<p>E yo vini pirifye. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Yon jou li te sou lanmè </p>\n<p>Avèk kèk nan disip yo </p>\n<p>Yon tanpèt te leve </p>\n<p>Bak la-prèt pou-l chavire </p>\n<p>Jezu te nan domi </p>\n<p>Disip yo te pra rele’l </p>\n<p>Li leve men’l anlè, </p>\n<p>Li di “van fè silans” </p>\n<p><br/> <strong>4.</strong></p>\n<p>Yon jou yo te Mennen ba li </p>\n<p>Yon fanm adiltè </p>\n<p>Men yo te panse </p>\n<p>Li prale lapide fanm nan </p>\n<p>Lè li gade fanm nan </p>\n<p>Li genyen pitye pou li, </p>\n<p>Li di’l mèt ale men pa fè peche ankò </p>\n','Mwen-Gen-Yon-Bon-Papa',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2240,'7a0df1b8-b91e-4b43-a3ec-607b074cd461',0,0,'Mwen kanpe sou tout pwomès de Kris',68,'68_RN_ht','1.\nMwen kanpe sou tout pwomes de\nKris rwa mwen,\nPandan letenite M’ap chante lwanj li\nGlwa nan tout syel la sa va sije chan mwen,\nMwen kanpe sou tout pwomes Bondye.\nRefren\nKanpe, kanpe, mwen kanpe sou tout\nPwomes ke Bondye fe nou \nKanpe, kanpe m’ape kanpe\nSou tou pwomes Bondye.\n2.\nMwen kanpe sou tout pwomes kip a fayi,\nLe move tanpet e tout krent vle koule-m \nPa pouvwa pawol Bondye, ma soti vinke\nMwen kanpe sou tout pwomes Bondye','<p><strong>1.</strong> </p>\n<p>Mwen kanpe sou tout pwomes de </p>\n<p>Kris rwa mwen, </p>\n<p>Pandan letenite M’ap chante lwanj li </p>\n<p>Glwa nan tout syel la sa va sije chan mwen, </p>\n<p>Mwen kanpe sou tout pwomes Bondye. </p>\n<p>Refren </p>\n<p>Kanpe, kanpe, mwen kanpe sou tout </p>\n<p>Pwomes ke Bondye fe nou </p>\n<p>Kanpe, kanpe m’ape kanpe </p>\n<p>Sou tou pwomes Bondye. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen kanpe sou tout pwomes kip a fayi, </p>\n<p>Le move tanpet e tout krent vle koule-m </p>\n<p>Pa pouvwa pawol Bondye, ma soti vinke </p>\n<p>Mwen kanpe sou tout pwomes Bondye </p>\n','Mwen-kanpe-sou-tout-pwomes-de-Kris',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2241,'efa68f5a-0c34-4d2f-a42d-7e92a9026c74',0,0,'Ala Yon Mèveye Mèveye Jou',22,'22_RN_ht','1.\nAla yon mèveye mèveye jou\nJou m’pamka janmè bliye.\nLè m’te nan tenèb pèdi nan labou\nJezu sovè-m mwen kontre\nAla yon zanmi ki gen charite,\nLi satisfè-m ak gras li\nLi pran tristès mwen li ban-m alegrès\nE li fè tout tenèb mwen pati.\n \nRefren:\nSyèl la desann plan nanm mwen ak lajwa\nLè sovè mwen te sove-m ak lakwa.\nPeche-m, tout padone\nTenèb nan limyè tounen,\nSyèl la desann plen nanm mwenn ak lajwa.\n \n2.\nFèt de nouvo pa pisans lespri sen,\nLi fèm yon pitit Bondye\nAmou kalvè justifye se sèten\nLi ban-m yon non glorye\nTravay sa-a fèt nan yon ti moman\nLè-m repanti nan peche-m\nLè-m aksepte li e lè-m konfese,\nO beni non li, li sove-m\n \n3.\nAlèkile lesperans ke m’twouve,\nVa dire jouk lavi sa-a\nVa fini yon jou pui m’tou asire.\nM’a jwen yon plas nan syèl la\nTout sa-a rive paske yon bèl jou\nMwen te krè nan Jezu-Kri,\nByenfè yo si bèl ak Sali etènèl\nMwen resevwa tout nan men li','<p><strong>1.</strong> </p>\n<p>Ala yon mèveye mèveye jou </p>\n<p>Jou m’pamka janmè bliye. </p>\n<p>Lè m’te nan tenèb pèdi nan labou </p>\n<p>Jezu sovè-m mwen kontre </p>\n<p>Ala yon zanmi ki gen charite, </p>\n<p>Li satisfè-m ak gras li </p>\n<p>Li pran tristès mwen li ban-m alegrès </p>\n<p>E li fè tout tenèb mwen pati. </p>\n<p>Refren: </p>\n<p>Syèl la desann plan nanm mwen ak lajwa </p>\n<p>Lè sovè mwen te sove-m ak lakwa. </p>\n<p>Peche-m, tout padone </p>\n<p>Tenèb nan limyè tounen, </p>\n<p>Syèl la desann plen nanm mwenn ak lajwa. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Fèt de nouvo pa pisans lespri sen, </p>\n<p>Li fèm yon pitit Bondye </p>\n<p>Amou kalvè justifye se sèten </p>\n<p>Li ban-m yon non glorye </p>\n<p>Travay sa-a fèt nan yon ti moman </p>\n<p>Lè-m repanti nan peche-m </p>\n<p>Lè-m aksepte li e lè-m konfese, </p>\n<p>O beni non li, li sove-m </p>\n<p><br/> <strong>3.</strong></p>\n<p>Alèkile lesperans ke m’twouve, </p>\n<p>Va dire jouk lavi sa-a </p>\n<p>Va fini yon jou pui m’tou asire. </p>\n<p>M’a jwen yon plas nan syèl la </p>\n<p>Tout sa-a rive paske yon bèl jou </p>\n<p>Mwen te krè nan Jezu-Kri, </p>\n<p>Byenfè yo si bèl ak Sali etènèl </p>\n<p>Mwen resevwa tout nan men li </p>\n','Ala-Yon-Meveye-Meveye-Jou',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2242,'58d59bbe-ddf5-4a46-8252-f49a0c01b7b5',0,0,'Kè Mwen Boulvèse Anba Kolè Bondye',21,'21_RN_ht','1.\nKè mwen boulvèse anba kolè Bondye,\nAnvi monn nan trennen-m nan bafon peche.\nSenyè pou yon pòv miserfab, gen pitye,\nTou swit li vi-n sekoure-m kotem’te ye\n \nRefren:\nLi retire’m nan boubye peche,\nLi mete pye-m sou tè fè-m se vre\nLi fè kè-m rejwi e m’ap chante\nYon chan glwa a Dye!\nAleloya!\n \n2.\nLi te mete-m sou tè fèm, a kote li\nPye mwen vi-n asire nan chemen lavi\nNan pwen danje ankò, li kenbe-m debou\nM’ap mache pazapa avèk li toujou.\n \n3.\nLi mete yon chan louanje anndan kè mwen\nNan nwit kou lajounen.\nKè mwen ap debòde nanm mwen afranchi\nTout glwa pou redanmtè Seyè Jezu-Kri.\n \n4.\nM’ap chante gran mizerikòd li pou mwen\nM’ap felisite jouk tan yon moun konnen\nNan pwen lòt Sali pou lèzò Jodi-a\nVi-n chache sekou li anvan li  trò ta.','<p><strong>1.</strong> </p>\n<p>Kè mwen boulvèse anba kolè Bondye, </p>\n<p>Anvi monn nan trennen-m nan bafon peche. </p>\n<p>Senyè pou yon pòv miserfab, gen pitye, </p>\n<p>Tou swit li vi-n sekoure-m kotem’te ye </p>\n<p>Refren: </p>\n<p>Li retire’m nan boubye peche, </p>\n<p>Li mete pye-m sou tè fè-m se vre </p>\n<p>Li fè kè-m rejwi e m’ap chante </p>\n<p>Yon chan glwa a Dye! </p>\n<p>Aleloya! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Li te mete-m sou tè fèm, a kote li </p>\n<p>Pye mwen vi-n asire nan chemen lavi </p>\n<p>Nan pwen danje ankò, li kenbe-m debou </p>\n<p>M’ap mache pazapa avèk li toujou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li mete yon chan louanje anndan kè mwen </p>\n<p>Nan nwit kou lajounen. </p>\n<p>Kè mwen ap debòde nanm mwen afranchi </p>\n<p>Tout glwa pou redanmtè Seyè Jezu-Kri. </p>\n<p><br/> <strong>4.</strong></p>\n<p>M’ap chante gran mizerikòd li pou mwen </p>\n<p>M’ap felisite jouk tan yon moun konnen </p>\n<p>Nan pwen lòt Sali pou lèzò Jodi-a </p>\n<p>Vi-n chache sekou li anvan li  trò ta. </p>\n','Ke-Mwen-Boulvese-Anba-Kole-Bondye',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2243,'04dd6440-a2f9-4074-a1db-10916407a7b5',0,0,'M’ape Swiv Ou,  O Bon Sovè Mwen',25,'25_RN_ht','1.\nM’ape swiv ou, O bon Sovè mwen,\nNenpòt kote ou prale\nTout kote ou vle kondwi mwen\nPran devan m’ape mache\nRefren:\nChemen-an te mèt difisil\nSan limyè e san tras\nDepi ou la, l’ap vini fasil,\nM’ap swiv tras ou, e m’ap mache.\n \n3.\nSi mwen fèb, si mwen akable,\nSi lapenn anvayi kè mwen\nM’ap swiv ou ankò san twouble\nWi Senyè konte sou mwen.\n \n4.\nSi ou Mennen-m nan gran jouden-an\nGwo kouran p’ap fè mwen pè,\nPaske ou traverse-l anvan-m\nBon Jezu, m’ap swiv ou an pè.','<p><strong>1.</strong> </p>\n<p>M’ape swiv ou, O bon Sovè mwen, </p>\n<p>Nenpòt kote ou prale </p>\n<p>Tout kote ou vle kondwi mwen </p>\n<p>Pran devan m’ape mache </p>\n<p>Refren: </p>\n<p>Chemen-an te mèt difisil </p>\n<p>San limyè e san tras </p>\n<p>Depi ou la, l’ap vini fasil, </p>\n<p>M’ap swiv tras ou, e m’ap mache. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si mwen fèb, si mwen akable, </p>\n<p>Si lapenn anvayi kè mwen </p>\n<p>M’ap swiv ou ankò san twouble </p>\n<p>Wi Senyè konte sou mwen. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si ou Mennen-m nan gran jouden-an </p>\n<p>Gwo kouran p’ap fè mwen pè, </p>\n<p>Paske ou traverse-l anvan-m </p>\n<p>Bon Jezu, m’ap swiv ou an pè. </p>\n','M\'ape-Swiv-Ou-O-Bon-Sove-Mwen',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2244,'d5409d28-cb16-4b30-b863-2aac527b8a49',0,0,'Lè Mwen Sèl E Dekouraje',41,'41_RN_ht','1.\nLè mwen sèl e dekouraje,\nLè-m pa gen moun pou mwen konfye-m\nMwen konnen Jezu, li toujou la e li konprann\nLè Jezu la, tout tristès ale\nNanm mwen vi-n kalm, lespri-m vi-n lib\nMwen konnen Jezu toujou la pou’l ede mwen\n\n\nRefren:\nSe Jezu sèl ki k’ab konprann mwen\nSe Jezu sèl ki k’ab ede-m\nE pwoteje-m nan tout traka nan lavi sa-a\nSe Jezu sèl ki kenbe men-m\nSe Jezu sèl ki sekoure-m\nSe Jezu sèl, wi se li menm sèl ki konn bezwen-m\n\n\n2.\nSi ou bezwen li Jodi-a\nOu mèt aproche’w bò kwa-a\nMwen konnen Jezu, li toujou la e li konprann\nOu mèt pote tout fado-ou ba li,\nE li va rezoud yo pou nou\nMwen konnen Jezu li toujou la pou’l ede mwen','<p><strong>1.</strong> </p>\n<p>Lè mwen sèl e dekouraje, </p>\n<p>Lè-m pa gen moun pou mwen konfye-m </p>\n<p>Mwen konnen Jezu, li toujou la e li konprann </p>\n<p>Lè Jezu la, tout tristès ale </p>\n<p>Nanm mwen vi-n kalm, lespri-m vi-n lib </p>\n<p>Mwen konnen Jezu toujou la pou’l ede mwen </p>\n<p>Refren: </p>\n<p>Se Jezu sèl ki k’ab konprann mwen </p>\n<p>Se Jezu sèl ki k’ab ede-m </p>\n<p>E pwoteje-m nan tout traka nan lavi sa-a </p>\n<p>Se Jezu sèl ki kenbe men-m </p>\n<p>Se Jezu sèl ki sekoure-m </p>\n<p>Se Jezu sèl, wi se li menm sèl ki konn bezwen-m </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si ou bezwen li Jodi-a </p>\n<p>Ou mèt aproche’w bò kwa-a </p>\n<p>Mwen konnen Jezu, li toujou la e li konprann </p>\n<p>Ou mèt pote tout fado-ou ba li, </p>\n<p>E li va rezoud yo pou nou </p>\n<p>Mwen konnen Jezu li toujou la pou’l ede mwen </p>\n','Le-Mwen-Sel-E-Dekouraje',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2245,'bcc5dac3-2e31-4c54-96a7-397a9087fd26',0,0,'Lè Lavi Mouin Va Fini',55,'55_RN_ht','1.\nLe la vi mouin va fini \nE moiun jambe rivie-a \nle mouin oue briye\ngnou matin glorie \nma va konnin redampte mouin\nle-m rive nan siel la\nE souri  li va pou mouin gnou byinvenu\n \nRefren:\nma va oue li, ma konnin li, \nle ma kanpe bo kote jezu –kri\nma va oue li, ma konnin li\npa mak klou yo li gingnin nan min li.\n \n2.\nKi transpo dalegres \nle mouin va oue li fas a fas\nE tout gran a mou kap\nbriye nan je li\no ke mouin va loue li \npou mizerikod ak gras,\nki pare pou mouin\ngnou bel plas nan kay li.\n \n3.\nNan bel peyi anro –a\nmouin gingnin kek byinn-eme\nmoin sonje le yo te di mouin \no revoua\nak gran joua yo va\nchante le yo oue mape rive, \nmin mouin ta vle\nsove mouin premieman.\n \n4.\nMape antre nan vil la\nnan gnou rob ki blan e pu,\nkote pap gin soufrans ni tristes anko\nmape chante ak zanj yo \nNan yon seles amoni\nmin mouin ta vle oue\nsove mouin premieman','<p><strong>1.</strong> </p>\n<p>Le la vi mouin va fini </p>\n<p>E moiun jambe rivie-a </p>\n<p>le mouin oue briye </p>\n<p>gnou matin glorie </p>\n<p>ma va konnin redampte mouin </p>\n<p>le-m rive nan siel la </p>\n<p>E souri  li va pou mouin gnou byinvenu </p>\n<p>Refren: </p>\n<p>ma va oue li, ma konnin li, </p>\n<p>le ma kanpe bo kote jezu –kri </p>\n<p>ma va oue li, ma konnin li </p>\n<p>pa mak klou yo li gingnin nan min li. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ki transpo dalegres </p>\n<p>le mouin va oue li fas a fas </p>\n<p>E tout gran a mou kap </p>\n<p>briye nan je li </p>\n<p>o ke mouin va loue li </p>\n<p>pou mizerikod ak gras, </p>\n<p>ki pare pou mouin </p>\n<p>gnou bel plas nan kay li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan bel peyi anro –a </p>\n<p>mouin gingnin kek byinn-eme </p>\n<p>moin sonje le yo te di mouin </p>\n<p>o revoua </p>\n<p>ak gran joua yo va </p>\n<p>chante le yo oue mape rive, </p>\n<p>min mouin ta vle </p>\n<p>sove mouin premieman. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mape antre nan vil la </p>\n<p>nan gnou rob ki blan e pu, </p>\n<p>kote pap gin soufrans ni tristes anko </p>\n<p>mape chante ak zanj yo </p>\n<p>Nan yon seles amoni </p>\n<p>min mouin ta vle oue </p>\n<p>sove mouin premieman </p>\n','Le-Lavi-Mouin-Va-Fini',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2246,'8fced6b3-66c7-4ae3-abd1-61f8771b8968',0,0,'Le la vi-m sanble tri,  le-m chaje ak pwoble',61,'61_RN_ht','1.\nLè la vi-m sanble tris, lè-m chaje ak pwoblem\nSe sou jenou-m mwen kap jwenn la jwa.\nPesonn sou late sa-a pap kapab édé mwen,\nSelman Seye k-ap fe tout pou mwen.\nRefren\nOu menm ki di frapé é ou va louvri\nOu menm kab vin cheche-m pou-m al nan syel,\nSonje mwen che Jezu nan rout lavi-a\nO, sonjé mwen, sonjé mwen souplé\n2.\nAne yo pasé vit, mwa yo vini pi kout \nSiy nan yo menm vi-n di ou pap vini\nOu menm k-ap sevi Kris Annou swiv li chak jou','<p><strong>1.</strong> </p>\n<p>Lè la vi-m sanble tris, lè-m chaje ak pwoblem </p>\n<p>Se sou jenou-m mwen kap jwenn la jwa. </p>\n<p>Pesonn sou late sa-a pap kapab édé mwen, </p>\n<p>Selman Seye k-ap fe tout pou mwen. </p>\n<p>Refren </p>\n<p>Ou menm ki di frapé é ou va louvri </p>\n<p>Ou menm kab vin cheche-m pou-m al nan syel, </p>\n<p>Sonje mwen che Jezu nan rout lavi-a </p>\n<p>O, sonjé mwen, sonjé mwen souplé </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ane yo pasé vit, mwa yo vini pi kout </p>\n<p>Siy nan yo menm vi-n di ou pap vini </p>\n<p>Ou menm k-ap sevi Kris Annou swiv li chak jou </p>\n','Le-la-vi-m-sanble-tri-le-m-chaje-ak-pwoble',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2247,'175bf461-402f-4b19-893b-c8ce3e6c1145',0,0,'Ale,  joli kolonb mesaje Bondye',69,'69_RN_ht','1.\nAle, joli kolonb mesaje Bondye,\nAle voye mesaj la bay tout le zimen\nAle nan lesinayi\nNan laband de gaza\nAle opre de Jwif e opre de Arab,\nDi yo : Li le pou yo sispann trennen\nNan yon la vi kot chen ap manje chen\nLi le pou yo siyen yon trete de pe\nPaske jistis Bondye pa lwen pou’l vini\nRefren\nAle di yo sa  se Granmet la ki voye-ou\nAle di yo sa,\nAvni-an kapab apokaliptik\n2.\nAle jis anro tet tribi-n nasyonzini,\nAle voye mesaj bay lemonn antye\nAle pwofite pale ak gwo pwisans yo.\nAle di yo pou yo sispann frabrike zam \nNan tye monn la gen moun kinan grangou\nGen timoun k’ap grandi nan malsite\nKip a men-m gen anyen pou yo manje \nDi yo kile dyalog no-sid la va serye.\n3.\nAle nan tou azil, nan tout sanatoryom,\nAle kote lavi-a pa sanble ak lavi lom \nAle kote k’gen inosan k’ap anprizone\nAle di yo pran kouraj pe-n yo pral fini\nAle pale ak tout « Boat People » yo \nTout moun ke yo frape pou ide yo\nDi yo : ya gen chans re we  peyi yo,\nPaske jistis Bondye-a pa lwen pou-l vini\n4.\nAle opre de rwa e opre de pwisan,\nAle bo moun ki renmen we espetak san.\nAle di yo Agno Bondye-a an kole\nAle di yo jou sa-a ki bo yo pral kache ?\nAle mache nan tout gran tribinal,\nKote jistis ap vann lajan kontan \nDi jij la yo, se pou yo repanti,\nPaske jistis Bondye-a pa lwen pou-l vini\n5.\nAle bo l’O.M.S et bo la F.A.O.\nAle di yo ke tye monn an konvalesan.\nAle tout bo lakouarouj intenasyonal \nAle di yo ke survi monn la an danje\nAle pale ak peyi l’OPEP yo\nKi ap monte pri petwol la byen wo \nDi yo, pou yo genyen pitye pou nou\nPaske bidje tye monn la déjà fin kreve\n6.\nAle, nan tout « night-club » nan tout disko \nAle kote y-ap pwofite twoke jen yo.\nAle, di yo genyen pitye pou jenes la.\nAle mache nan tout gran bodel\nKote y’ap eskplwate fanm de nwi yo,\nDi yo : pa kontinye peche anko,\nPaske jistis Bondye-a pa lwen pou-l vini.\n7.\nAle nan tout vale nan tout mon nan tout plenn\nAle konsole sila yo ki aflije\nAle kote pitit Bondye ap mouri grangou\nAle di yo pran kouraj yo va rasasye\nAle pale ak peyizan-l yo kip a gen\nYon ti kal te pou yo travay\nDi yo an atandan ya pran kouraj\nPaske jistis Bondye-a pa lwen pou’l vini.','<p><strong>1.</strong> </p>\n<p>Ale, joli kolonb mesaje Bondye, </p>\n<p>Ale voye mesaj la bay tout le zimen </p>\n<p>Ale nan lesinayi </p>\n<p>Nan laband de gaza </p>\n<p>Ale opre de Jwif e opre de Arab, </p>\n<p>Di yo : Li le pou yo sispann trennen </p>\n<p>Nan yon la vi kot chen ap manje chen </p>\n<p>Li le pou yo siyen yon trete de pe </p>\n<p>Paske jistis Bondye pa lwen pou’l vini </p>\n<p>Refren </p>\n<p>Ale di yo sa  se Granmet la ki voye-ou </p>\n<p>Ale di yo sa, </p>\n<p>Avni-an kapab apokaliptik </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ale jis anro tet tribi-n nasyonzini, </p>\n<p>Ale voye mesaj bay lemonn antye </p>\n<p>Ale pwofite pale ak gwo pwisans yo. </p>\n<p>Ale di yo pou yo sispann frabrike zam </p>\n<p>Nan tye monn la gen moun kinan grangou </p>\n<p>Gen timoun k’ap grandi nan malsite </p>\n<p>Kip a men-m gen anyen pou yo manje </p>\n<p>Di yo kile dyalog no-sid la va serye. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ale nan tou azil, nan tout sanatoryom, </p>\n<p>Ale kote lavi-a pa sanble ak lavi lom </p>\n<p>Ale kote k’gen inosan k’ap anprizone </p>\n<p>Ale di yo pran kouraj pe-n yo pral fini </p>\n<p>Ale pale ak tout « Boat People » yo </p>\n<p>Tout moun ke yo frape pou ide yo </p>\n<p>Di yo : ya gen chans re we  peyi yo, </p>\n<p>Paske jistis Bondye-a pa lwen pou-l vini </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ale opre de rwa e opre de pwisan, </p>\n<p>Ale bo moun ki renmen we espetak san. </p>\n<p>Ale di yo Agno Bondye-a an kole </p>\n<p>Ale di yo jou sa-a ki bo yo pral kache ? </p>\n<p>Ale mache nan tout gran tribinal, </p>\n<p>Kote jistis ap vann lajan kontan </p>\n<p>Di jij la yo, se pou yo repanti, </p>\n<p>Paske jistis Bondye-a pa lwen pou-l vini </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ale bo l’O.M.S et bo la F.A.O. </p>\n<p>Ale di yo ke tye monn an konvalesan. </p>\n<p>Ale tout bo lakouarouj intenasyonal </p>\n<p>Ale di yo ke survi monn la an danje </p>\n<p>Ale pale ak peyi l’OPEP yo </p>\n<p>Ki ap monte pri petwol la byen wo </p>\n<p>Di yo, pou yo genyen pitye pou nou </p>\n<p>Paske bidje tye monn la déjà fin kreve </p>\n<p><br/> <strong>6.</strong></p>\n<p>Ale, nan tout « night-club » nan tout disko </p>\n<p>Ale kote y-ap pwofite twoke jen yo. </p>\n<p>Ale, di yo genyen pitye pou jenes la. </p>\n<p>Ale mache nan tout gran bodel </p>\n<p>Kote y’ap eskplwate fanm de nwi yo, </p>\n<p>Di yo : pa kontinye peche anko, </p>\n<p>Paske jistis Bondye-a pa lwen pou-l vini. </p>\n<p><br/> <strong>7.</strong></p>\n<p>Ale nan tout vale nan tout mon nan tout plenn </p>\n<p>Ale konsole sila yo ki aflije </p>\n<p>Ale kote pitit Bondye ap mouri grangou </p>\n<p>Ale di yo pran kouraj yo va rasasye </p>\n<p>Ale pale ak peyizan-l yo kip a gen </p>\n<p>Yon ti kal te pou yo travay </p>\n<p>Di yo an atandan ya pran kouraj </p>\n<p>Paske jistis Bondye-a pa lwen pou’l vini. </p>\n','Ale-joli-kolonb-mesaje-Bondye',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2248,'4e976a6f-6381-492e-8c89-46250b796c62',0,0,'Anvan-l te monte nan syèl ale jwenn papa-l',70,'70_RN_ht','1.\nAnvan-l te monte nan syel ale jwenn papa-l\nLi te kite disip yo, pou preche laverite.\nTout sa-k, pa vle kwe levanjil déjà kondane\nSa ki obeyi lalwa, prale viv nan syel avel.\nRefren\nJou Jezu desann sou late avek\nLame zanj papa-l yo \nYon moun kip a la, ou p’ap pran avyon ave-l,\nJou Jezu desann sou late\nVi-n cheche tout pitit li yo\nYon moun ki pa la, ou pap pran avyon avel.\n2.\nLevanjil preche gratis, yo p’ap aksepte-l\nFok nou pa konprann denye\nJou-a nou kap pran daso.\nLe avyon Jezu-a prale, sa-k p’ap\nGen visa fok nou pa konprann\nRakek kab voye-n ale ave-l.\n3.\nMak 15 ak 16 di nou « ale toupatou,\nAl pote levanjil bay tout moun ki sou late \nSa ki kwe ki batize, so yo ka sove,\nSa ki meprize levanjil, déjà kondane. »','<p><strong>1.</strong> </p>\n<p>Anvan-l te monte nan syel ale jwenn papa-l </p>\n<p>Li te kite disip yo, pou preche laverite. </p>\n<p>Tout sa-k, pa vle kwe levanjil déjà kondane </p>\n<p>Sa ki obeyi lalwa, prale viv nan syel avel. </p>\n<p>Refren </p>\n<p>Jou Jezu desann sou late avek </p>\n<p>Lame zanj papa-l yo </p>\n<p>Yon moun kip a la, ou p’ap pran avyon ave-l, </p>\n<p>Jou Jezu desann sou late </p>\n<p>Vi-n cheche tout pitit li yo </p>\n<p>Yon moun ki pa la, ou pap pran avyon avel. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Levanjil preche gratis, yo p’ap aksepte-l </p>\n<p>Fok nou pa konprann denye </p>\n<p>Jou-a nou kap pran daso. </p>\n<p>Le avyon Jezu-a prale, sa-k p’ap </p>\n<p>Gen visa fok nou pa konprann </p>\n<p>Rakek kab voye-n ale ave-l. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mak 15 ak 16 di nou « ale toupatou, </p>\n<p>Al pote levanjil bay tout moun ki sou late </p>\n<p>Sa ki kwe ki batize, so yo ka sove, </p>\n<p>Sa ki meprize levanjil, déjà kondane. » </p>\n','Anvan-l-te-monte-nan-syel-ale-jwenn-papa-l',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2249,'fcf83f3c-48c9-4993-820a-8bcbd055f201',0,0,'Mwen se pelerin e vwayaje',72,'72_RN_ht','1.\nMwen se peleren e vwayajè,\nMwen pap rete toutan sou la tè\nE mwen konnen avek asirans\nKe ma we Jezu nan syel\nKè\nKi gran jou, ki gran pou, jou sa-a va ye\nMwen va ajenouye nan pye-li\nPou-m bali tout one-l merite\nLe sa-a ke mwen va ranpil avek jwa\nLe ma we-m nan bra Jezi.\n2.\nSoufrans pa mwen se pou yon ti tan\nMwen pa plenyen pou pwoblem la vi\nRomen chapit 8 vese premye,\nMwen pa gen kondanasyon.\n3.\nPlezi nan mond sa-a pape gare-m\nRiches nan mo-n sa-a pap ebranle-m\nJezu déjà fe mwen yon pwomes :\nNan syel, la va pi dous.','<p><strong>1.</strong> </p>\n<p>Mwen se peleren e vwayajè, </p>\n<p>Mwen pap rete toutan sou la tè </p>\n<p>E mwen konnen avek asirans </p>\n<p>Ke ma we Jezu nan syel </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Ki gran jou, ki gran pou, jou sa-a va ye</strong> </p>\n<p><strong>Mwen va ajenouye nan pye-li</strong> </p>\n<p><strong>Pou-m bali tout one-l merite</strong> </p>\n<p><strong>Le sa-a ke mwen va ranpil avek jwa</strong> </p>\n<p><strong>Le ma we-m nan bra Jezi.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Soufrans pa mwen se pou yon ti tan </p>\n<p>Mwen pa plenyen pou pwoblem la vi </p>\n<p>Romen chapit 8 vese premye, </p>\n<p>Mwen pa gen kondanasyon. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Plezi nan mond sa-a pape gare-m </p>\n<p>Riches nan mo-n sa-a pap ebranle-m </p>\n<p>Jezu déjà fe mwen yon pwomes : </p>\n<p>Nan syel, la va pi dous. </p>\n','Mwen-se-pelerin-e-vwayaje',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2250,'8b608100-d750-4d15-8079-81b2f3923d99',0,0,'Moun vle rakonte sa Kris fe pou mwen',73,'73_RN_ht','1.\nMoun vle rakonte sa Kris fè pou mwen\nDepi-m te jwen li tout bagay chanje\nNan prizon diab la li ban-m libete\nWi Jezu te delivre-m\n2.\nTèt mwen te chajé mwen pat gin répo,\nSousi la vi te fe-m vi-n pe lanmo \nJezu sove-m e mwen p ape anko.\nWi Jezu te delivre-m\n3.\nChanalite fe-m bliye gras  Bondye,\nTout zev mwen pat bon, peche-m yo te sal \nMen travay Jezu te sifi pou mwen\nWi Jezu te delivrem.\nRefren\nJezu té delivre-m (2 fwa)\nTeneb peche-m tounen yon bel klate\nWi Jezu te delivre-m\n4.\nSa Kris fe pou mwen, la fe-l pou ou tou \nLevanjil la se pou mwen, se pou ou\nSa Kris fe pou mwen, la fel pou ou tou \nWi Jezu te delivre-m','<p><strong>1.</strong> </p>\n<p>Moun vle rakonte sa Kris fè pou mwen </p>\n<p>Depi-m te jwen li tout bagay chanje </p>\n<p>Nan prizon diab la li ban-m libete </p>\n<p>Wi Jezu te delivre-m </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tèt mwen te chajé mwen pat gin répo, </p>\n<p>Sousi la vi te fe-m vi-n pe lanmo </p>\n<p>Jezu sove-m e mwen p ape anko. </p>\n<p>Wi Jezu te delivre-m </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chanalite fe-m bliye gras  Bondye, </p>\n<p>Tout zev mwen pat bon, peche-m yo te sal </p>\n<p>Men travay Jezu te sifi pou mwen </p>\n<p>Wi Jezu te delivrem. </p>\n<p>Refren </p>\n<p>Jezu té delivre-m (2 fwa) </p>\n<p>Teneb peche-m tounen yon bel klate </p>\n<p>Wi Jezu te delivre-m </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sa Kris fe pou mwen, la fe-l pou ou tou </p>\n<p>Levanjil la se pou mwen, se pou ou </p>\n<p>Sa Kris fe pou mwen, la fel pou ou tou </p>\n<p>Wi Jezu te delivre-m </p>\n','Moun-vle-rakonte-sa-Kris-fe-pou-mwen',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2251,'d90964f1-dfc0-4957-911d-1af9fe1c8dac',0,0,'Mwen se temwen Jezu Kri',64,'64_RN_ht','1.\nMwen se temwen Jezu Kri\nM-ap twouble bib la \nLemonn kwe ya jwenn Sove,\nSan pa Jezu Kri\nSatan ban-n yon fos dokrin-n\nPou-n pa fe kole\nMiniste sou late toujou gen finans\nRefren\nO, oui pou-m temwen, fo-m konnen Jezu\nKa Letenel te fe li anvan nou te la,\nLom pa jwenn ak Jezu Kris san pa lesprisen\nAlelouya, m-te pedi men m’trouve lavi.\n2.\nAbraham pe de lafwa, li pa ka temwen\nAnvan Abraham te fet Jezu Kri te la\nSi-m temwen san-m pa konnen pa la verite.\nPou-m temwen, vre temwen fom nan Jezu-Kri\n3.\nLe-m kwe m-te pre Jeova,\nPoutan m-te tre lwen \nLe mwen ap change mo yo m-te vle we rezon.\nTout pouvwa pou Jesu Kri, nan syel e late,\nMwen tap pale vanite, tankou pase van.\n4.\nDye twa fwa sen, Jeova atrave lemonn,\nAbraham, Izarak e Jakob, y opa vre temwen\nLom ki vreman vre temwen sove Emanyel,\nGlwa a Dye, glwa a Dye,\nJezu Rwa de rwa.','<p><strong>1.</strong> </p>\n<p>Mwen se temwen Jezu Kri </p>\n<p>M-ap twouble bib la </p>\n<p>Lemonn kwe ya jwenn Sove, </p>\n<p>San pa Jezu Kri </p>\n<p>Satan ban-n yon fos dokrin-n </p>\n<p>Pou-n pa fe kole </p>\n<p>Ministe sou late toujou gen finans </p>\n<p>Refren </p>\n<p>O, oui pou-m temwen, fo-m konnen Jezu </p>\n<p>Ka Letenel te fe li anvan nou te la, </p>\n<p>Lom pa jwenn ak Jezu Kris san pa lesprisen </p>\n<p>Alelouya, m-te pedi men m’trouve lavi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Abraham pe de lafwa, li pa ka temwen </p>\n<p>Anvan Abraham te fet Jezu Kri te la </p>\n<p>Si-m temwen san-m pa konnen pa la verite. </p>\n<p>Pou-m temwen, vre temwen fom nan Jezu-Kri </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le-m kwe m-te pre Jeova, </p>\n<p>Poutan m-te tre lwen </p>\n<p>Le mwen ap change mo yo m-te vle we rezon. </p>\n<p>Tout pouvwa pou Jesu Kri, nan syel e late, </p>\n<p>Mwen tap pale vanite, tankou pase van. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dye twa fwa sen, Jeova atrave lemonn, </p>\n<p>Abraham, Izarak e Jakob, y opa vre temwen </p>\n<p>Lom ki vreman vre temwen sove Emanyel, </p>\n<p>Glwa a Dye, glwa a Dye, </p>\n<p>Jezu Rwa de rwa. </p>\n','Mwen-se-temwen-Jezu-Kri',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2252,'d07a9097-4158-483f-934b-ac49dc4d6834',0,0,'Je Veux Monter Sur La Montagne',5,'5_RN_fr','1.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvrent les cieux\nJe veux laisser les joies du monde,\nEt me tenir tout près de Dieu,\nJe veux monter sur la montagne\nC’est là que l’on est heureux.\nSi le chemin est rocailleux\nSi les cailloux me blessent un peut\nLe Saint-Esprit me guidera\nEt Jésus me consolera.\n \n2.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvrent les cieux\nJe veux laisser les joies du monde,\nEt me tenir tout près de Dieu,\nJe veux monter sur la montagne\nC’est là que l’on est heureux.\nSur ce chemin jésus monta\nEt mon sauveur me précéda\nAvec lui je veux m’élever\nJusque sur les plus hauts sommets\n \n3.\nJe veux monter sur la montagne\nC’est là que l’on rencontre Dieu\nC’est là que la joie nous inonde\nEt que pour nous s’ouvrent les cieux\nJe veux laisser les joies du monde,\nEt me tenir tout près de Dieu,\nJe veux monter sur la montagne\nC’est là que l’on est heureux.','<p><strong>1.</strong> </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvrent les cieux </p>\n<p>Je veux laisser les joies du monde, </p>\n<p>Et me tenir tout près de Dieu, </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n<p>Si le chemin est rocailleux </p>\n<p>Si les cailloux me blessent un peut </p>\n<p>Le Saint-Esprit me guidera </p>\n<p>Et Jésus me consolera. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvrent les cieux </p>\n<p>Je veux laisser les joies du monde, </p>\n<p>Et me tenir tout près de Dieu, </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n<p>Sur ce chemin jésus monta </p>\n<p>Et mon sauveur me précéda </p>\n<p>Avec lui je veux m’élever </p>\n<p>Jusque sur les plus hauts sommets </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on rencontre Dieu </p>\n<p>C’est là que la joie nous inonde </p>\n<p>Et que pour nous s’ouvrent les cieux </p>\n<p>Je veux laisser les joies du monde, </p>\n<p>Et me tenir tout près de Dieu, </p>\n<p>Je veux monter sur la montagne </p>\n<p>C’est là que l’on est heureux. </p>\n','Je-Veux-Monter-Sur-La-Montagne',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2253,'1d00bdeb-9480-40ef-88ca-2eba4ee7d83a',0,0,'Eske Tout Vrè Kretyen',45,'45_RN_ht','1.\nEske tout vrè kretyen\nPata dwe yon evanjelis?\nEske manm de legliz\nKapab rete ak tèt atè?\nEske predicate pa dwe mete yo tout ansanm,\nPou preche levanjil la toupatou\n \nRefren:\nO, wi an avan, (bis)\nm-ape travay ansanm\nfòk “Ayiti pou Kris\nsa se mèveye!\nE Jezi va beni-l','<p><strong>1.</strong> </p>\n<p>Eske tout vrè kretyen </p>\n<p>Pata dwe yon evanjelis? </p>\n<p>Eske manm de legliz </p>\n<p>Kapab rete ak tèt atè? </p>\n<p>Eske predicate pa dwe mete yo tout ansanm, </p>\n<p>Pou preche levanjil la toupatou </p>\n<p>Refren: </p>\n<p>O, wi an avan, (bis) </p>\n<p>m-ape travay ansanm </p>\n<p>fòk “Ayiti pou Kris </p>\n<p>sa se mèveye! </p>\n<p>E Jezi va beni-l </p>\n','Eske-Tout-Vre-Kretyen',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2254,'4245ab3e-60fa-47c4-a8c0-1f81c3aadb42',0,0,'Bo pi Jakob la yon jou le Seye',74,'74_RN_ht','1.\nBo pi Jakob la yon jou le Seyè\nRankontre yon samariten\nKi vini pran dlo, Jezu mande li bwe\nFan’m sila etone\nEske’w pa konnen ke jwif avek samariten\nPagen oken’n rapo, pa gen oken relasyon \nPouki ou mande mwen bwe.\nSi ou te konnen ki don Bondye\nE kiles ki di ou bali bwe\nSe ou menm pito ki ta mande\nE mwen Ta ba’w yon bon dlo \nYon dlo ki pa’p jan fini\nE ki va dire nan lavi ou\nPou tout létènité\nKè\nMezanmi tande mwen jwenn yon Nonm\nKi di mwen tou sa mwen fè\nNon sila-a se yon pwofet\nEske  se pa li menm ki Kris-la !\nMezanmi tande mwen jwenn yon nonm\nKi di mwen tout sa mwen fè,\nNonm sila-a li se pitit, Bondyé\nSe li menm ki mesi-a,\nLi konnen tout sa ki nan ke mwen.\nSa se yon bagay ki meveye !\nLi ban mwen yon dlo pou mwen pa Jan’m swaf.\n2.\nLe fan’m nan tande ke Jezu\nKapab bali yon dlo pou li pa jan’m swaf,\nFan’m nan etone piplis\nLi mande kikote Ou va jwenn bon dlo ?\nTout de men ou vid\nOu pa gen anyen pou pran dlo\nEske ou pi gran pase Jakob ki bay pi sa-a,\nBan mwen yon ti gout ladan’n \nJezu di fan’m nan kote mari’w\nAle chache’l, epi vi’n jwenn mwen \nFan’m nan bay manti, men elas\nPagen Pies moun ki kab’ twonpe Jezu.\nOu di ou pa gen mari, e poutan\nOu pase senk déjà e denye ya pa pou ou.\n3.\nMezanmi tande\nMwen jwenn yon non’m\nKi ban mwen yon dlo\nPou mwen pa jan’m swa (bis 2 liy)\nVini bwe dlo sa-a\nEpi swaf ou va pase.','<p><strong>1.</strong> </p>\n<p>Bo pi Jakob la yon jou le Seyè </p>\n<p>Rankontre yon samariten </p>\n<p>Ki vini pran dlo, Jezu mande li bwe </p>\n<p>Fan’m sila etone </p>\n<p>Eske’w pa konnen ke jwif avek samariten </p>\n<p>Pagen oken’n rapo, pa gen oken relasyon </p>\n<p>Pouki ou mande mwen bwe. </p>\n<p>Si ou te konnen ki don Bondye </p>\n<p>E kiles ki di ou bali bwe </p>\n<p>Se ou menm pito ki ta mande </p>\n<p>E mwen Ta ba’w yon bon dlo </p>\n<p>Yon dlo ki pa’p jan fini </p>\n<p>E ki va dire nan lavi ou </p>\n<p>Pou tout létènité </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Mezanmi tande mwen jwenn yon Nonm</strong> </p>\n<p><strong>Ki di mwen tou sa mwen fè</strong> </p>\n<p><strong>Non sila-a se yon pwofet</strong> </p>\n<p><strong>Eske  se pa li menm ki Kris-la !</strong> </p>\n<p><strong>Mezanmi tande mwen jwenn yon nonm</strong> </p>\n<p><strong>Ki di mwen tout sa mwen fè,</strong> </p>\n<p><strong>Nonm sila-a li se pitit, Bondyé</strong> </p>\n<p><strong>Se li menm ki mesi-a,</strong> </p>\n<p><strong>Li konnen tout sa ki nan ke mwen.</strong> </p>\n<p><strong>Sa se yon bagay ki meveye !</strong> </p>\n<p><strong>Li ban mwen yon dlo pou mwen pa Jan’m swaf.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le fan’m nan tande ke Jezu </p>\n<p>Kapab bali yon dlo pou li pa jan’m swaf, </p>\n<p>Fan’m nan etone piplis </p>\n<p>Li mande kikote Ou va jwenn bon dlo ? </p>\n<p>Tout de men ou vid </p>\n<p>Ou pa gen anyen pou pran dlo </p>\n<p>Eske ou pi gran pase Jakob ki bay pi sa-a, </p>\n<p>Ban mwen yon ti gout ladan’n </p>\n<p>Jezu di fan’m nan kote mari’w </p>\n<p>Ale chache’l, epi vi’n jwenn mwen </p>\n<p>Fan’m nan bay manti, men elas </p>\n<p>Pagen Pies moun ki kab’ twonpe Jezu. </p>\n<p>Ou di ou pa gen mari, e poutan </p>\n<p>Ou pase senk déjà e denye ya pa pou ou. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mezanmi tande </p>\n<p>Mwen jwenn yon non’m </p>\n<p>Ki ban mwen yon dlo </p>\n<p>Pou mwen pa jan’m swa (bis 2 liy) </p>\n<p>Vini bwe dlo sa-a </p>\n<p>Epi swaf ou va pase. </p>\n','Bo-pi-Jakob-la-yon-jou-le-Seye',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2255,'ae84b541-3a43-43b5-978a-3ac0ab938867',0,0,'Nanm Mwen Te Swaf,  Mwen Pat Jwenn Satisfaksyon',46,'46_RN_ht','1.\nNanm mwen te swaf, mwen pat jwenn satisfaksyon\nPlezi lemon yo te twonpe nanm mwen\nMen yon bon jou mwen t-ap chache soulaje mwen\nJezu te di-m “vi-n jwen repo nan mwen.”\n \nRefren:\nWi mwen te jwen li,\nBèl sous lavi-a\nGras a Jezu, kit e mouri pou mwen \nSan I te koule sou bwa kalvè-a\nE kounye-a, li satisfè kè mwen\n \n2.\nlvi-m te tris, lè-m pat konnen Jezu\nm-pat gen espwa, nanmmwen te boulvèse\nmen gras Bondye te louvri sous lavi-a\nm-te jwenn ladann padon e libète\n \n3.\nChè zanmi, O, pou kisa ou mize?\nKouri vini, vi-n jwenn sous lavi-a\nFado peche yo te trò lou pou pote\nJezu vle ba-ou repo Jodi-a','<p><strong>1.</strong> </p>\n<p>Nanm mwen te swaf, mwen pat jwenn satisfaksyon </p>\n<p>Plezi lemon yo te twonpe nanm mwen </p>\n<p>Men yon bon jou mwen t-ap chache soulaje mwen </p>\n<p>Jezu te di-m “vi-n jwen repo nan mwen.” </p>\n<p>Refren: </p>\n<p>Wi mwen te jwen li, </p>\n<p>Bèl sous lavi-a </p>\n<p>Gras a Jezu, kit e mouri pou mwen </p>\n<p>San I te koule sou bwa kalvè-a </p>\n<p>E kounye-a, li satisfè kè mwen </p>\n<p><br/> <strong>2.</strong></p>\n<p>lvi-m te tris, lè-m pat konnen Jezu </p>\n<p>m-pat gen espwa, nanmmwen te boulvèse </p>\n<p>men gras Bondye te louvri sous lavi-a </p>\n<p>m-te jwenn ladann padon e libète </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chè zanmi, O, pou kisa ou mize? </p>\n<p>Kouri vini, vi-n jwenn sous lavi-a </p>\n<p>Fado peche yo te trò lou pou pote </p>\n<p>Jezu vle ba-ou repo Jodi-a </p>\n','Nanm-Mwen-Te-Swaf-Mwen-Pat-Jwenn-Satisfaksyon',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2256,'99e6beaf-4a0c-4979-8886-9a2fd4957703',0,0,'A Rebò Yon Chemin Ki Mennen Jeriko',43,'43_RN_ht','1.\nA rebo yon chemin ki mennen Jeriko\nJezu te rankontre yon mandian\nyon vye rad te sou li tou de jel te fèmin, \nLi tap mande charite\n \nRefren:\nJezu te di ’ou mèt ale’ (bis)\ndepi menm, lè sa-a li wè.\n \n2.\nLè ou gade nan Jan chapit 8 vèsè 12,\nJezu di se limenm ki limyè\nMoun ki swiv li pap janm mache nwa anko\nMen li va gen limyè lavi.\n \n3.\nPawol mwen se yon lanp nan pye’w\nsi ou swiv mwen\nYon limyè nan kè nou pou tout tan\nMache nan fè nwa anko\nMen li va gen limyè lavi','<p><strong>1.</strong> </p>\n<p>A rebo yon chemin ki mennen Jeriko </p>\n<p>Jezu te rankontre yon mandian </p>\n<p>yon vye rad te sou li tou de jel te fèmin, </p>\n<p>Li tap mande charite </p>\n<p>Refren: </p>\n<p>Jezu te di ’ou mèt ale’ (bis) </p>\n<p>depi menm, lè sa-a li wè. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè ou gade nan Jan chapit 8 vèsè 12, </p>\n<p>Jezu di se limenm ki limyè </p>\n<p>Moun ki swiv li pap janm mache nwa anko </p>\n<p>Men li va gen limyè lavi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pawol mwen se yon lanp nan pye’w </p>\n<p>si ou swiv mwen </p>\n<p>Yon limyè nan kè nou pou tout tan </p>\n<p>Mache nan fè nwa anko </p>\n<p>Men li va gen limyè lavi </p>\n','A-Rebo-Yon-Chemin-Ki-Mennen-Jeriko',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2257,'0ead21f6-daf0-4f24-bc03-b43c7a7c963b',0,0,'Chemen-an Pa Fasil',20,'20_RN_ht','1.\nChemen-an pa fasil,\nJouk nou rive nan syèl la,\nLi genyen pikan e pieèj anpil,\nCheman-an pa fasil,\nMen Jezu mache ak nou,\nLi ran kè nou tout  toutafè trankil\n \nRefren:\nNon, non chemen-an pa fasil (2 fwa)\nMen Jezu mache ak nou\nLi pote fado nou,\nGras li fè kè nou rejwi anpil.\n \n2.\nChemen-an pa fasil\nGen zeprèv, genyen traka\nTout kote nou pase gendanje\nRout la mèt difisil\nSi Jezu kenbe nou,\nNa rete fèm lè pye nou glise.\n \n3.\nKò nou konn fatige,\nDekouraje nan rout la\nEpi satan konn vi-n tante nou\nMen n’ape tann le Senyè\nKi va parèt nan glwa li,\nL’ape pran nou ak li yon bèl jou.','<p><strong>1.</strong> </p>\n<p>Chemen-an pa fasil, </p>\n<p>Jouk nou rive nan syèl la, </p>\n<p>Li genyen pikan e pieèj anpil, </p>\n<p>Cheman-an pa fasil, </p>\n<p>Men Jezu mache ak nou, </p>\n<p>Li ran kè nou tout  toutafè trankil </p>\n<p>Refren: </p>\n<p>Non, non chemen-an pa fasil (2 fwa) </p>\n<p>Men Jezu mache ak nou </p>\n<p>Li pote fado nou, </p>\n<p>Gras li fè kè nou rejwi anpil. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chemen-an pa fasil </p>\n<p>Gen zeprèv, genyen traka </p>\n<p>Tout kote nou pase gendanje </p>\n<p>Rout la mèt difisil </p>\n<p>Si Jezu kenbe nou, </p>\n<p>Na rete fèm lè pye nou glise. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kò nou konn fatige, </p>\n<p>Dekouraje nan rout la </p>\n<p>Epi satan konn vi-n tante nou </p>\n<p>Men n’ape tann le Senyè </p>\n<p>Ki va parèt nan glwa li, </p>\n<p>L’ape pran nou ak li yon bèl jou. </p>\n','Chemen-an-Pa-Fasil',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2258,'8b042d44-14d2-4f31-a3b1-140a8f2595b6',0,0,'Lemonn Plen Tristès Ak Mizè',52,'52_RN_ht','1.\nLemonn plen tristès ak mizè \nKè nou chaje chak jou.\nJezu te wete tout fado yo.\nSou mòn kalvè pou nou.\n \nRefren:\nLi vle wete tout fado pou ou\nBò kalvè,  bò kalvè \nLi vle wete tout fado pou ou.\nLi tout pre nou toujou.\n \n2.\nLage tout chaj ou sou Jezu \nPote krent ak sousi \nSan Jezu te wete fado nou.\nSou kalvè li mouri.\n \n3.\nNanm twouble, Leseyè konnen\nChagren ki nan kè ou \nMen li vle wete toiut fado yo\nSou mòn kalvè pou ou','<p><strong>1.</strong> </p>\n<p>Lemonn plen tristès ak mizè </p>\n<p>Kè nou chaje chak jou. </p>\n<p>Jezu te wete tout fado yo. </p>\n<p>Sou mòn kalvè pou nou. </p>\n<p>Refren: </p>\n<p>Li vle wete tout fado pou ou </p>\n<p>Bò kalvè,  bò kalvè </p>\n<p>Li vle wete tout fado pou ou. </p>\n<p>Li tout pre nou toujou. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lage tout chaj ou sou Jezu </p>\n<p>Pote krent ak sousi </p>\n<p>San Jezu te wete fado nou. </p>\n<p>Sou kalvè li mouri. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nanm twouble, Leseyè konnen </p>\n<p>Chagren ki nan kè ou </p>\n<p>Men li vle wete toiut fado yo </p>\n<p>Sou mòn kalvè pou ou </p>\n','Lemonn-Plen-Tristes-Ak-Mize',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2259,'a8686bdb-61bd-4327-bc7d-d92ed6dc5107',0,0,'Gen Yon jou Kap Vini',26,'26_RN_ht','1.\nGen yon jou kap vini,\nLe sa-a pap genyen chagrin\nPap gen nyaj nan syel la,\nNi gro dlo pou twouble zye-m\nVa gen lape pou toutan\nNan bel ti vil do sa-a\nki gran jou, jou glorye sa va ye\n\n\nRefren:\nKi gran jou sa va ye, le m’a rakontre Jezu\nE mwen va gade figi-l,\nLi menm ki sove-m pa gras\nli va genbe nen mwen\npoul mennen mwen nan te promes la\nki gran jou jou sa va ye\n \n2.\nPape genyen tristes,\nNi fado pou nou pote,\nNi maladi, ni doule,\nNi pataj nan peyi sa-a.\nVa gen lape pou tout tan\nNan bel ti vil do sa-a\nKi gran jou, jou gloye sa va ye\n\n3.\nOu min-m ki san Jezu\nsa ou va fe nan jou sa a?\nMin Jezu pral vini\nPot lagras prale femin\nKole Bondie pral tonbe\nSou tout moun ki san Jezu\nKi gran jou, jou glorye sa va ye','<p><strong>1.</strong> </p>\n<p>Gen yon jou kap vini, </p>\n<p>Le sa-a pap genyen chagrin </p>\n<p>Pap gen nyaj nan syel la, </p>\n<p>Ni gro dlo pou twouble zye-m </p>\n<p>Va gen lape pou toutan </p>\n<p>Nan bel ti vil do sa-a </p>\n<p>ki gran jou, jou glorye sa va ye </p>\n<p>Refren: </p>\n<p>Ki gran jou sa va ye, le m’a rakontre Jezu </p>\n<p>E mwen va gade figi-l, </p>\n<p>Li menm ki sove-m pa gras </p>\n<p>li va genbe nen mwen </p>\n<p>poul mennen mwen nan te promes la </p>\n<p>ki gran jou jou sa va ye </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pape genyen tristes, </p>\n<p>Ni fado pou nou pote, </p>\n<p>Ni maladi, ni doule, </p>\n<p>Ni pataj nan peyi sa-a. </p>\n<p>Va gen lape pou tout tan </p>\n<p>Nan bel ti vil do sa-a </p>\n<p>Ki gran jou, jou gloye sa va ye </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou min-m ki san Jezu </p>\n<p>sa ou va fe nan jou sa a? </p>\n<p>Min Jezu pral vini </p>\n<p>Pot lagras prale femin </p>\n<p>Kole Bondie pral tonbe </p>\n<p>Sou tout moun ki san Jezu </p>\n<p>Ki gran jou, jou glorye sa va ye </p>\n','Gen-Yon-jou-Kap-Vini',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2260,'2ce05dde-890a-4dd6-98be-20320e0345f6',0,0,'Te Gen Yon Fanm Kit E',29,'29_RN_ht','1.\nTe gen yon fanm kit e,\nLi pase douzan, li pat kapab geri.\nTout kote l-ale li pat jwenn gerizon\nLi vi-n kouche, pou li tan lanmò li.\n \nRefren:\nPandan li kouche, li tande y-ap pale\nGen yon sovè ki fè anpil ,irak,\nLi pran kouraj li di konsa\n“mwen gen espwa ke m-ap jwenn gerizon”\n \n2.\nYon jou li leve, li te ale’\nFoul la anpil, li pa kapab antre,\nLi te fòse-l touché ròb Jezu,\nMenm lè sa-a fanm nan te jwenn gerizon\n \n3.\nPandan fanm nan touché ròb Jezu\nJezu santi li pa menm jan ankò\nLi te kanpe, li foul la:\n“mwen santi yon moun ki touché mwen”\n \n4.\nDisip yo reponn, yo di “Senyè\nFoul la anpil kòman ou fòman ou fè konn sa”\nFanm nan parèt, li di “Senyè\nSe mwen menm ki touché ròb ou-a”\n \n5.\n“fanm nan kanpe tou ap tranble\nJezu rete, li gade fanm nan\nLi di fanm nan: “ou gen lafwa,\nAvèk lafwa wa jwenn gerizon.”','<p><strong>1.</strong> </p>\n<p>Te gen yon fanm kit e, </p>\n<p>Li pase douzan, li pat kapab geri. </p>\n<p>Tout kote l-ale li pat jwenn gerizon </p>\n<p>Li vi-n kouche, pou li tan lanmò li. </p>\n<p>Refren: </p>\n<p>Pandan li kouche, li tande y-ap pale </p>\n<p>Gen yon sovè ki fè anpil ,irak, </p>\n<p>Li pran kouraj li di konsa </p>\n<p>“mwen gen espwa ke m-ap jwenn gerizon” </p>\n<p><br/> <strong>2.</strong></p>\n<p>Yon jou li leve, li te ale’ </p>\n<p>Foul la anpil, li pa kapab antre, </p>\n<p>Li te fòse-l touché ròb Jezu, </p>\n<p>Menm lè sa-a fanm nan te jwenn gerizon </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pandan fanm nan touché ròb Jezu </p>\n<p>Jezu santi li pa menm jan ankò </p>\n<p>Li te kanpe, li foul la: </p>\n<p>“mwen santi yon moun ki touché mwen” </p>\n<p><br/> <strong>4.</strong></p>\n<p>Disip yo reponn, yo di “Senyè </p>\n<p>Foul la anpil kòman ou fòman ou fè konn sa” </p>\n<p>Fanm nan parèt, li di “Senyè </p>\n<p>Se mwen menm ki touché ròb ou-a” </p>\n<p><br/> <strong>5.</strong></p>\n<p>“fanm nan kanpe tou ap tranble </p>\n<p>Jezu rete, li gade fanm nan </p>\n<p>Li di fanm nan: “ou gen lafwa, </p>\n<p>Avèk lafwa wa jwenn gerizon.” </p>\n','Te-Gen-Yon-Fanm-Kit-E',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2261,'f3e5fd8f-694b-4c4c-b2d7-58465876ea5d',0,0,'O Ala Mwen Boulvèse',27,'27_RN_ht','1.\nO ala mwen boulvèse,\nO, ala mwen artiste (2 fwa)\nSe lè nan kè-m\nKris pa pran premye plas,\nTout bagay chanje pou mwen.\n \n2.\nO! pou kisa li ale?\nO! pou kisa’l kite-m sèl? (2 fwa)\nSe lè mwen fè sa k’pa nan volonte’l\nLi fè’m santi li pa la.\n \n3.\nO! ala yon bon Jezu!\nO! oui mwen santi li bon! (2 fwa)\nKòm la parol-la di nous a klèman\nLi chatye moun li renmen\n \n4.\nO! an-nou tout byen-eme\nChèche konprann sa li vle,\nLè sa-a nou pape janm fè li fache\nTout bagay va byen mache\n(Repete chak line 2 fwa)','<p><strong>1.</strong> </p>\n<p>O ala mwen boulvèse, </p>\n<p>O, ala mwen artiste (2 fwa) </p>\n<p>Se lè nan kè-m </p>\n<p>Kris pa pran premye plas, </p>\n<p>Tout bagay chanje pou mwen. </p>\n<p><br/> <strong>2.</strong></p>\n<p>O! pou kisa li ale? </p>\n<p>O! pou kisa’l kite-m sèl? (2 fwa) </p>\n<p>Se lè mwen fè sa k’pa nan volonte’l </p>\n<p>Li fè’m santi li pa la. </p>\n<p><br/> <strong>3.</strong></p>\n<p>O! ala yon bon Jezu! </p>\n<p>O! oui mwen santi li bon! (2 fwa) </p>\n<p>Kòm la parol-la di nous a klèman </p>\n<p>Li chatye moun li renmen </p>\n<p><br/> <strong>4.</strong></p>\n<p>O! an-nou tout byen-eme </p>\n<p>Chèche konprann sa li vle, </p>\n<p>Lè sa-a nou pape janm fè li fache </p>\n<p>Tout bagay va byen mache </p>\n<p>(Repete chak line 2 fwa) </p>\n','O-Ala-Mwen-Boulvese',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2262,'2797796a-27d8-4f5e-9717-972fbada194f',0,0,'Rwa Nebikanetza fè yo leve yon estati',58,'58_RN_ht','1.\nRwa Nebikanetza fe yo leve yon estati \nLi di: Yon moun ki pa vle adore estati sa-a\nSe pou yo mare pye-l pou yo lage yo nan fou-a\nTe gen twa jenn gason, yo te refize obeyi.\n\nRefren\nYo te genyen konfyans (3 fwa)\nNan Bondye yo t’ap sevi-a\nTelman yo te fidel\nPa menm cheve yo pat boule\n\n2.\nTwa jenn sa yo sete: Chadrak, Mechak, Abednego\nTwa jenn sa yo, te devwe pou yo sevi Letenel\nTelman yo te fidel, yo pat vle sevi satan.\nRwa-a te mare pye yo et te lage yon an dife-a\n\n3.\nLe rwa sonje twa jenn sa yo ki te nan dife-a\nLi voye al gade : yo vi-in di-l yo we ka om\nKatriyem nan, se te anj Bondye ki te pami yo,\nYo t-ap mache nan fou-a, yo t-ap lwe non Bondye.','<p><strong>1.</strong> </p>\n<p>Rwa Nebikanetza fe yo leve yon estati </p>\n<p>Li di: Yon moun ki pa vle adore estati sa-a </p>\n<p>Se pou yo mare pye-l pou yo lage yo nan fou-a </p>\n<p>Te gen twa jenn gason, yo te refize obeyi. </p>\n<p>Refren </p>\n<p>Yo te genyen konfyans (3 fwa) </p>\n<p>Nan Bondye yo t’ap sevi-a </p>\n<p>Telman yo te fidel </p>\n<p>Pa menm cheve yo pat boule </p>\n<p><br/> <strong>2.</strong></p>\n<p>Twa jenn sa yo sete: Chadrak, Mechak, Abednego </p>\n<p>Twa jenn sa yo, te devwe pou yo sevi Letenel </p>\n<p>Telman yo te fidel, yo pat vle sevi satan. </p>\n<p>Rwa-a te mare pye yo et te lage yon an dife-a </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le rwa sonje twa jenn sa yo ki te nan dife-a </p>\n<p>Li voye al gade : yo vi-in di-l yo we ka om </p>\n<p>Katriyem nan, se te anj Bondye ki te pami yo, </p>\n<p>Yo t-ap mache nan fou-a, yo t-ap lwe non Bondye. </p>\n','Rwa-Nebikanetza-fe-yo-leve-yon-estati',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2263,'046bd3f2-db74-40f9-b1a1-10b9131cc2b0',0,0,'Mwen Wè Sous La Ki Plen Ak San',50,'50_RN_ht','1.\nMwen wè sous la ki plen ak san\nFontèn-nan fon e laj\nJezu kapab chanje’w.\nBlese-l yo montre-m sa\n \nRefren:\nBèl kouran dlo mwen wè,\nMwen plonje e li lave mwen\nO, Glwa a Dye, li lave mwen,\nLi lave mwen, wi li lave mwen\n \n2.\nMwen wè monn la vini nouvèl,\nSe yon mesaj de san,\nLi preche pami pèdi yo\nSe yon kouran de san\n \n3.\nm-ap mache nan limyè syèl la\nnan mitan pechè yo.\nAvèk kè pwòp epi rad blanch,\nm-a nan tròn nan ak Kris.','<p><strong>1.</strong> </p>\n<p>Mwen wè sous la ki plen ak san </p>\n<p>Fontèn-nan fon e laj </p>\n<p>Jezu kapab chanje’w. </p>\n<p>Blese-l yo montre-m sa </p>\n<p>Refren: </p>\n<p>Bèl kouran dlo mwen wè, </p>\n<p>Mwen plonje e li lave mwen </p>\n<p>O, Glwa a Dye, li lave mwen, </p>\n<p>Li lave mwen, wi li lave mwen </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen wè monn la vini nouvèl, </p>\n<p>Se yon mesaj de san, </p>\n<p>Li preche pami pèdi yo </p>\n<p>Se yon kouran de san </p>\n<p><br/> <strong>3.</strong></p>\n<p>m-ap mache nan limyè syèl la </p>\n<p>nan mitan pechè yo. </p>\n<p>Avèk kè pwòp epi rad blanch, </p>\n<p>m-a nan tròn nan ak Kris. </p>\n','Mwen-We-Sous-La-Ki-Plen-Ak-San',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2264,'f560ac03-e367-442f-b6a0-91d48807536c',0,0,'Jezi se ou menm ki sove-a',59,'59_RN_ht','1.\nJezi se ou menm ki sove-a\nSan ou menm mwen pa genyen la vi (3 fwa)\n\nRefren\nYo panse fe mwen mal,\nMal change anbyen\nSe pou glwa Bondye manifeste.\n\n2.\nYo pran-n, lage-m nan siten nan,\nPou m-pedi lavi materyel mwen \nJezu ki kon-n sa li pra-l fe,\nMwen kouri lage mwen nan bra li.\n\n3.\nMalgre mwen nan bouch delivrans mwen\nSatan vi-n pou-l lage-m nan lemonn\nJezu ki ranpli ak tout fos li\nMwen kouri lage mwen nan bra li.\n\n4.\nJezu, menm nan mitan brigan yo\nTou sa mwen fe yo leve kont mwen,\nSi ou pa vire zye gade mwen,\nBrigan yo ta lage-m nan lemonn.\n\n5.\nMalgre mwen nan bouch delivrans mwen,\nMadanm Potifa vi-n po jete-m \nMadanm Potifa kenbe rad mwen,\nMwen kouri lag eves mwen ba li','<p><strong>1.</strong> </p>\n<p>Jezi se ou menm ki sove-a </p>\n<p>San ou menm mwen pa genyen la vi (3 fwa) </p>\n<p>Refren </p>\n<p>Yo panse fe mwen mal, </p>\n<p>Mal change anbyen </p>\n<p>Se pou glwa Bondye manifeste. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Yo pran-n, lage-m nan siten nan, </p>\n<p>Pou m-pedi lavi materyel mwen </p>\n<p>Jezu ki kon-n sa li pra-l fe, </p>\n<p>Mwen kouri lage mwen nan bra li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Malgre mwen nan bouch delivrans mwen </p>\n<p>Satan vi-n pou-l lage-m nan lemonn </p>\n<p>Jezu ki ranpli ak tout fos li </p>\n<p>Mwen kouri lage mwen nan bra li. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jezu, menm nan mitan brigan yo </p>\n<p>Tou sa mwen fe yo leve kont mwen, </p>\n<p>Si ou pa vire zye gade mwen, </p>\n<p>Brigan yo ta lage-m nan lemonn. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Malgre mwen nan bouch delivrans mwen, </p>\n<p>Madanm Potifa vi-n po jete-m </p>\n<p>Madanm Potifa kenbe rad mwen, </p>\n<p>Mwen kouri lag eves mwen ba li </p>\n','Jezi-se-ou-menm-ki-sove-a',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2265,'04c65c48-3186-478c-bfae-54ffc75d664a',0,0,'Koute Vwa Jezu Ki Nan Dezè-a',56,'56_RN_ht','1.\nKoute vwa Jezu ki nan dezè-a \nl-ap chèche mouton ki pèdu yo \nmouton ki egare nan patiraj la\nl-ap fè yo rantre nan pak la.\n \nRefren:\nVini, vini, mouton ki pèdu yo \nVini, vi-n jwenn mwen  bèje w-ap chache’w \nMen vwa-m nan dezè-a ape rele’w,\nE de bra-m louvri pou-m resevwa ou\nvi-n jwenn mwen\n \n2.\nSonje te gen 100 mouton nan pak la \nGenyen youn ki te egare\nByen lwen nan mòn, lwen nan vale-a \nLi te trouse pye-l l-al chèche-l\n \n3.\nSonje te genyen anfan prodig la \nLi te mande papa-l byen pa-l\nE lè-l te pati fi-n depanse yo \nLi te oblije retounen.\n \n4.\nLi te lèse katrevin-diznèf yo,\nPoul ale chèche yon sèl la.\nE lè-l jwenn li, li te tèlman kontan\nE sou zepòl li, li pote-l\n \n5.\nOu menm si ou vle vini jodi-a tou,\nKonsa, la va resevwa-ou\nKite pote lòt. O, la sou zepòl li\nOu menm, t-ap vlope ou nan manto-l.','<p><strong>1.</strong> </p>\n<p>Koute vwa Jezu ki nan dezè-a </p>\n<p>l-ap chèche mouton ki pèdu yo </p>\n<p>mouton ki egare nan patiraj la </p>\n<p>l-ap fè yo rantre nan pak la. </p>\n<p>Refren: </p>\n<p>Vini, vini, mouton ki pèdu yo </p>\n<p>Vini, vi-n jwenn mwen  bèje w-ap chache’w </p>\n<p>Men vwa-m nan dezè-a ape rele’w, </p>\n<p>E de bra-m louvri pou-m resevwa ou </p>\n<p>vi-n jwenn mwen </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sonje te gen 100 mouton nan pak la </p>\n<p>Genyen youn ki te egare </p>\n<p>Byen lwen nan mòn, lwen nan vale-a </p>\n<p>Li te trouse pye-l l-al chèche-l </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sonje te genyen anfan prodig la </p>\n<p>Li te mande papa-l byen pa-l </p>\n<p>E lè-l te pati fi-n depanse yo </p>\n<p>Li te oblije retounen. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li te lèse katrevin-diznèf yo, </p>\n<p>Poul ale chèche yon sèl la. </p>\n<p>E lè-l jwenn li, li te tèlman kontan </p>\n<p>E sou zepòl li, li pote-l </p>\n<p><br/> <strong>5.</strong></p>\n<p>Ou menm si ou vle vini jodi-a tou, </p>\n<p>Konsa, la va resevwa-ou </p>\n<p>Kite pote lòt. O, la sou zepòl li </p>\n<p>Ou menm, t-ap vlope ou nan manto-l. </p>\n','Koute-Vwa-Jezu-Ki-Nan-Deze-a',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2266,'1bfd58f6-2dde-4c5e-bce1-3987f27db485',0,0,'Seye Bondye mwen sezi nan prezans',65,'65_RN_ht','1.\nSeye Bondye mwen sezi nan prezans ou,\nLe-m we monn sa-a ke men ou te kreye,\nZetwal yo la, mwen tande loraj gwonde \nSe pwisans ou tout linive montre.\nRefren\nNanm mwen chante non ou Bondye pwisan :\nAla ou gran, ala ou gran (2 fwa)\n2.\nLe mwen sonje, Bondye bay sel pitit li\nPou-l vin mouri, se sa ki touche ke-m\nSou bwa kalve, li pote tout fado mwen,\nSan li koule pou wete tout peche-m.\n3.\nLe Kris paret, zanj yo va admire li\nLa pran-m nan syel nan rejwisans tout tan \nM-a pwostene nan pye-l pou adore li,\nE proklame « Bondye ala ou gran. »','<p><strong>1.</strong> </p>\n<p>Seye Bondye mwen sezi nan prezans ou, </p>\n<p>Le-m we monn sa-a ke men ou te kreye, </p>\n<p>Zetwal yo la, mwen tande loraj gwonde </p>\n<p>Se pwisans ou tout linive montre. </p>\n<p>Refren </p>\n<p>Nanm mwen chante non ou Bondye pwisan : </p>\n<p>Ala ou gran, ala ou gran (2 fwa) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le mwen sonje, Bondye bay sel pitit li </p>\n<p>Pou-l vin mouri, se sa ki touche ke-m </p>\n<p>Sou bwa kalve, li pote tout fado mwen, </p>\n<p>San li koule pou wete tout peche-m. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Kris paret, zanj yo va admire li </p>\n<p>La pran-m nan syel nan rejwisans tout tan </p>\n<p>M-a pwostene nan pye-l pou adore li, </p>\n<p>E proklame « Bondye ala ou gran. » </p>\n','Seye-Bondye-mwen-sezi-nan-prezans',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2267,'b32f87d7-59a9-40c6-8346-bf215a2c7b33',0,0,'Lemonn se pa lakay',81,'81_RN_ht','1\nOu bezwen Konnen\nKoman Jezu té sové mwen?\nLi te vin wete m nan monn égaré\nMwen té gen lajan,\nMen mwen pat genyen yon bi\nSete plezi, manje byen bwe e mouri?\nJezu nan amou Li,\nTe fè mwen apel sa a\n\"Vin jwen mwen, moun ki fatige et chage\nM a va soulaje ou, m a va pwteje ou\nE tout problem ou\nVa rezoud youn aprè lot\nOu va gen lavi, jwa e menm,\nLa sante, ou va gen espwa, lavi étenel.\n2\nMwen kite satan\nPou mwen vi n aksepté Jezu\nE dei le sa a tout lavi m te change\nMwen té toujou gen tristes\nAndedan ke mwen.\nLajan m kounye a,\nMwen pap pote li bay ougan\nM ap chita manje li avek tout fanm m.\nBondye pwotéjé m,\nLi pran sousi mwen,\nGade kijan m viv byennéré.\nMwen vin san pwoblem\nKa mwen gen Jezu\nJezu vini li régénéré m (2 fwa)','<p>Ou bezwen Konnen </p>\n<p>Koman Jezu té sové mwen? </p>\n<p>Li te vin wete m nan monn égaré </p>\n<p>Mwen té gen lajan, </p>\n<p>Men mwen pat genyen yon bi </p>\n<p>Sete plezi, manje byen bwe e mouri? </p>\n<p>Jezu nan amou Li, </p>\n<p>Te fè mwen apel sa a </p>\n<p>&quot;Vin jwen mwen, moun ki fatige et chage </p>\n<p>M a va soulaje ou, m a va pwteje ou </p>\n<p>E tout problem ou </p>\n<p>Va rezoud youn aprè lot </p>\n<p>Ou va gen lavi, jwa e menm, </p>\n<p>La sante, ou va gen espwa, lavi étenel. </p>\n<p>Mwen kite satan </p>\n<p>Pou mwen vi n aksepté Jezu </p>\n<p>E dei le sa a tout lavi m te change </p>\n<p>Mwen té toujou gen tristes </p>\n<p>Andedan ke mwen. </p>\n<p>Lajan m kounye a, </p>\n<p>Mwen pap pote li bay ougan </p>\n<p>M ap chita manje li avek tout fanm m. </p>\n<p>Bondye pwotéjé m, </p>\n<p>Li pran sousi mwen, </p>\n<p>Gade kijan m viv byennéré. </p>\n<p>Mwen vin san pwoblem </p>\n<p>Ka mwen gen Jezu </p>\n<p>Jezu vini li régénéré m (2 fwa) </p>\n','Lemonn-se-pa-lakay',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2268,'8a035d03-03f4-4328-9885-32b524fda4c7',0,0,'Dis-moi Ou Je Dois Aller',7,'7_RN_fr','1.\nDis-moi ou je dois aller\nDis-moi, Dieu, ce qu’il faut parler\nTant d’amis et de voisins\nOnt perdu le chemin\nPeu ont l’air qu’ils s’en soucient\nEncore, moins qui prient,\nGuide-moi par ton esprit\nVers une âme aujourd’hui.','<p><strong>1.</strong> </p>\n<p>Dis-moi ou je dois aller </p>\n<p>Dis-moi, Dieu, ce qu’il faut parler </p>\n<p>Tant d’amis et de voisins </p>\n<p>Ont perdu le chemin </p>\n<p>Peu ont l’air qu’ils s’en soucient </p>\n<p>Encore, moins qui prient, </p>\n<p>Guide-moi par ton esprit </p>\n<p>Vers une âme aujourd’hui. </p>\n','Dis-moi-Ou-Je-Dois-Aller',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2269,'7c5c9daa-93dd-42d8-8a7d-a5eefcac5c97',0,0,'Chak Fwa Mwen Fè Yon Bagay',24,'24_RN_ht','1.\nChak fwa mwen fè yon bagay,\nMwen pat pou fè\nChak fwa mwen di yon bagay,\nMwen pat pou di\nKite-m di ou sa mwen fè,\nE nanm mwen va beni\nMwen kache yon kote pou-m priye\n \nRefren:\nMen wi mwen kache (2 fwa)\nMwen mande sovè mwen pou li dirije-m\nMwen kache yon kote pou-m priye.\n \n2.\nFrè ze sè dan le senyè\nMete nou prè\nAn nou priye pou nou kapab reveye.\nJezu-Kri fè nou pwomès\nFòk li jwenn nou prepare\nApòt la di nou priye tout tan.\n \n3.\nChak fwa mwen fi-n la priyè\nMwen vi-n pi fè\nMwen vi-n pi fèm\nPaske sentespri Bondye pale nan kèm\nNan galat 5 vèsè 25\nMoun kab swiv selon lespri\nSe pou yo mache daprè lespri\n \n4.\nNan Bib la nou jwenn moun kite kon priye\nPami yo nou jwenn Moize ak Danyèl\nNan 1 Tesalonisyen cahpit 5 vèsè 17\nApòt la di nou priye tout tan.','<p><strong>1.</strong> </p>\n<p>Chak fwa mwen fè yon bagay, </p>\n<p>Mwen pat pou fè </p>\n<p>Chak fwa mwen di yon bagay, </p>\n<p>Mwen pat pou di </p>\n<p>Kite-m di ou sa mwen fè, </p>\n<p>E nanm mwen va beni </p>\n<p>Mwen kache yon kote pou-m priye </p>\n<p>Refren: </p>\n<p>Men wi mwen kache (2 fwa) </p>\n<p>Mwen mande sovè mwen pou li dirije-m </p>\n<p>Mwen kache yon kote pou-m priye. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Frè ze sè dan le senyè </p>\n<p>Mete nou prè </p>\n<p>An nou priye pou nou kapab reveye. </p>\n<p>Jezu-Kri fè nou pwomès </p>\n<p>Fòk li jwenn nou prepare </p>\n<p>Apòt la di nou priye tout tan. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chak fwa mwen fi-n la priyè </p>\n<p>Mwen vi-n pi fè </p>\n<p>Mwen vi-n pi fèm </p>\n<p>Paske sentespri Bondye pale nan kèm </p>\n<p>Nan galat 5 vèsè 25 </p>\n<p>Moun kab swiv selon lespri </p>\n<p>Se pou yo mache daprè lespri </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nan Bib la nou jwenn moun kite kon priye </p>\n<p>Pami yo nou jwenn Moize ak Danyèl </p>\n<p>Nan 1 Tesalonisyen cahpit 5 vèsè 17 </p>\n<p>Apòt la di nou priye tout tan. </p>\n','Chak-Fwa-Mwen-Fe-Yon-Bagay',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2270,'fde82e90-96cd-41c2-975f-377becfc16b2',0,0,'Mwen Genyen Yon Bagay',49,'49_RN_ht','1.\nMwen genyen yon bagay\nKi fè kè mwen kontan\nAnpil moun etone, pou wè\nKòman-m kontan,\nE m’ap toujou kontan\nEspwa-m pa sou tè sa-a\nDaprè pwomès Bondye\nMenm si mwen te grangou,\nm-pat gen kay pou-m rete,\nm-ape tann gran jou sa-a\n \nRefren:\np-ap gen pale franse konesans\npral fini riches pral disparèt\np-ap genyen gwo mesye:\nni p’ap gen gwo madanm,\ntout moun va menm bagay.\nLè jezu retounen, li pap vini chèche\nSavan ak zotobre sèlman li va bezwen\nMoun ki fè volonte-l, y ova men nan la men.\n \n2.\n \nPou ou kapab al legliz, fòk ou byen abiye\nSa-k pa gen pou’l mete,\nPa dwe chita kote-w\nOu ranpli ak lògèy\nSonje sa Kris te di avèk gran nikodèm\n“si-w ta vle gen lavi, fòk ou ne de nouvo, \nOu va wè gran jou sa-a.”\n \n3.\nAnpil moun konvèti, y opa cheche Bondye,\ny-ape priye sèlman pou kay avèk \noto, se bgay k’ap pase yo p’ap\npriye Bondye pou padon peche yo\nfòk nou fèt atansyon, paske ka yak oto\np-ap rantre nan syè la.\n \n4.\nLasyans ape travay, malgre sa se nilman,\nSa kon-n fè mechanste, syans fè-l\nVin pi mechan, syans pa ka bay lavi\nLòm pa kab chanje lòm, sinan san Jezu Kri\nMenm si-w te lougawou,\nOu te konn touye moun\nJezu kapab chanje-w','<p><strong>1.</strong> </p>\n<p>Mwen genyen yon bagay </p>\n<p>Ki fè kè mwen kontan </p>\n<p>Anpil moun etone, pou wè </p>\n<p>Kòman-m kontan, </p>\n<p>E m’ap toujou kontan </p>\n<p>Espwa-m pa sou tè sa-a </p>\n<p>Daprè pwomès Bondye </p>\n<p>Menm si mwen te grangou, </p>\n<p>m-pat gen kay pou-m rete, </p>\n<p>m-ape tann gran jou sa-a </p>\n<p>Refren: </p>\n<p>p-ap gen pale franse konesans </p>\n<p>pral fini riches pral disparèt </p>\n<p>p-ap genyen gwo mesye: </p>\n<p>ni p’ap gen gwo madanm, </p>\n<p>tout moun va menm bagay. </p>\n<p>Lè jezu retounen, li pap vini chèche </p>\n<p>Savan ak zotobre sèlman li va bezwen </p>\n<p>Moun ki fè volonte-l, y ova men nan la men. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pou ou kapab al legliz, fòk ou byen abiye </p>\n<p>Sa-k pa gen pou’l mete, </p>\n<p>Pa dwe chita kote-w </p>\n<p>Ou ranpli ak lògèy </p>\n<p>Sonje sa Kris te di avèk gran nikodèm </p>\n<p>“si-w ta vle gen lavi, fòk ou ne de nouvo, </p>\n<p>Ou va wè gran jou sa-a.” </p>\n<p><br/> <strong>3.</strong></p>\n<p>Anpil moun konvèti, y opa cheche Bondye, </p>\n<p>y-ape priye sèlman pou kay avèk </p>\n<p>oto, se bgay k’ap pase yo p’ap </p>\n<p>priye Bondye pou padon peche yo </p>\n<p>fòk nou fèt atansyon, paske ka yak oto </p>\n<p>p-ap rantre nan syè la. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lasyans ape travay, malgre sa se nilman, </p>\n<p>Sa kon-n fè mechanste, syans fè-l </p>\n<p>Vin pi mechan, syans pa ka bay lavi </p>\n<p>Lòm pa kab chanje lòm, sinan san Jezu Kri </p>\n<p>Menm si-w te lougawou, </p>\n<p>Ou te konn touye moun </p>\n<p>Jezu kapab chanje-w </p>\n','Mwen-Genyen-Yon-Bagay',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2271,'a4f49825-26dc-4661-a15d-fef690d49a4f',0,0,'A La Bèl Bagay Lè Yon Moun Nan Jezu',54,'54_RN_ht','1.\nA la bèl bagay lè yon moun nan Jezu \nZyel pa genyen vwal ki pou\nEksite-l fè sa ki mal (2 fwa)\n \nRefren :\nKris ap reke-w, li di : retire kè ou\nNan tout vye plezi kab mennen\nLanmò pita (2 fwa)\n \n2.\nTout plezi sa yo kab fè ou bliye\nSi genyen Bondye k-ap ba ou\nTout sa ou bezwen (2 fwa)\n \n3.\nGen moun ki chwazi satan\nPou yo kite Bondye, yo pito al anba gid yo.\nGenyen kèk bagay ou dwe reflechi,\nTout bagay, li va ba ou lavi étènèl.','<p><strong>1.</strong> </p>\n<p>A la bèl bagay lè yon moun nan Jezu </p>\n<p>Zyel pa genyen vwal ki pou </p>\n<p>Eksite-l fè sa ki mal (2 fwa) </p>\n<p>Refren : </p>\n<p>Kris ap reke-w, li di : retire kè ou </p>\n<p>Nan tout vye plezi kab mennen </p>\n<p>Lanmò pita (2 fwa) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout plezi sa yo kab fè ou bliye </p>\n<p>Si genyen Bondye k-ap ba ou </p>\n<p>Tout sa ou bezwen (2 fwa) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gen moun ki chwazi satan </p>\n<p>Pou yo kite Bondye, yo pito al anba gid yo. </p>\n<p>Genyen kèk bagay ou dwe reflechi, </p>\n<p>Tout bagay, li va ba ou lavi étènèl. </p>\n','A-La-Bel-Bagay-Le-Yon-Moun-Nan-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2272,'ef90ecd0-48b0-4302-9848-77eda26c6adb',0,0,'Sa fe sink mil ane pase',57,'57_RN_ht','1.\nSa fe sink mil ane pase\nNoe bati lach la, Bondye di\n« M’ap voye yon delij pou detwi late-a\nZannimo antre andedan-l\nMedizan yo mande\nSi Noe panse fe lach la\nSou te sek la, l=ap fel\nAk fanmi-li, Noe antre,\nBondye femen pot la\nSyel la vin-n sonb,\nZekle pete  pasyans Bondye kaba\nAla yon lapli  (2 Fwa)\nLi tonbe pandan karant jou\nSan li pat rete ditou\nAla yon lapli… (2 Fwa)\nMen lach, la vwayaje toujou \nAla yon lapli\n\n2.\nMoyiz ale soukont Bondye\nBay pep-la libete\nRwa Ejip deklare : mwen pap lese\nEsklav ale\nBanboch nan deze ya.\nMen yo tout va rete\nOlye mwen lese yo m’ap fe\nTravay yo redouble\nMoyiz reponn: “Ou pral soufri”.\nMoyiz menm pate pe,\nLi kanpe bo rivye, e,\nLeve baton li anle,\nAla yon lapli… (2 Fwa)\nSotrel, krapo, mouch, pou ak grel\nGrengole ou lejip sot nan syel.\nAla yon lapli  (2 Fwa)\nFararon pat kab sipote.\nAla yon lapli…\n\n3.\nNan tan Eli Izrael,\nLe rwa Akab reye,\nSechres te fe twa zan edmi,\nLapli-a pat tonbe,\nAkab banboche sou Kamel,\nPandan Eli priye\nSis fwa, nan pwen anyen,\nMen Eli pat dekouraje\nByento yon ti nyaj paret,\nEli te pran kouri\nL’al di Akab: “pou ou prepare ou,\nPou yon kokenn lapli »\nAla yon lapli… (2 Fwa)\nSyel la tou nwa, dlo sa pa yon jwet\nAla yon lapli… (2 Fwa)\nLe li rive, li mouye net.\nAla yon lapli.\n\n4.\nAnvan Jezu tounen nan syel, kote li soti \nAk onz apot yo, li promenen\nJouk nan Betani.\nLi di le-l rive nan syel la, l-ap voye sentespri.\nDonk rete tann nan Jerizalem\nJouk pwisans la vini,\nSanvin disip nan chanmot la te veye e priye\nYon van sibit sekwe kay la,\nBenediksyon vide.\nAla yon lapli…(2 fwa)\nBondye voye lesentespri,\nPreche chak moun nan lang pa li.\nAla yon lapli… (2 Fwa)\nMesaj repann nan tout peyi,\nAla yon lapli','<p><strong>1.</strong> </p>\n<p>Sa fe sink mil ane pase </p>\n<p>Noe bati lach la, Bondye di </p>\n<p>« M’ap voye yon delij pou detwi late-a </p>\n<p>Zannimo antre andedan-l </p>\n<p>Medizan yo mande </p>\n<p>Si Noe panse fe lach la </p>\n<p>Sou te sek la, l=ap fel </p>\n<p>Ak fanmi-li, Noe antre, </p>\n<p>Bondye femen pot la </p>\n<p>Syel la vin-n sonb, </p>\n<p>Zekle pete  pasyans Bondye kaba </p>\n<p>Ala yon lapli  (2 Fwa) </p>\n<p>Li tonbe pandan karant jou </p>\n<p>San li pat rete ditou </p>\n<p>Ala yon lapli… (2 Fwa) </p>\n<p>Men lach, la vwayaje toujou </p>\n<p>Ala yon lapli </p>\n<p><br/> <strong>2.</strong></p>\n<p>Moyiz ale soukont Bondye </p>\n<p>Bay pep-la libete </p>\n<p>Rwa Ejip deklare : mwen pap lese </p>\n<p>Esklav ale </p>\n<p>Banboch nan deze ya. </p>\n<p>Men yo tout va rete </p>\n<p>Olye mwen lese yo m’ap fe </p>\n<p>Travay yo redouble </p>\n<p>Moyiz reponn: “Ou pral soufri”. </p>\n<p>Moyiz menm pate pe, </p>\n<p>Li kanpe bo rivye, e, </p>\n<p>Leve baton li anle, </p>\n<p>Ala yon lapli… (2 Fwa) </p>\n<p>Sotrel, krapo, mouch, pou ak grel </p>\n<p>Grengole ou lejip sot nan syel. </p>\n<p>Ala yon lapli  (2 Fwa) </p>\n<p>Fararon pat kab sipote. </p>\n<p>Ala yon lapli… </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nan tan Eli Izrael, </p>\n<p>Le rwa Akab reye, </p>\n<p>Sechres te fe twa zan edmi, </p>\n<p>Lapli-a pat tonbe, </p>\n<p>Akab banboche sou Kamel, </p>\n<p>Pandan Eli priye </p>\n<p>Sis fwa, nan pwen anyen, </p>\n<p>Men Eli pat dekouraje </p>\n<p>Byento yon ti nyaj paret, </p>\n<p>Eli te pran kouri </p>\n<p>L’al di Akab: “pou ou prepare ou, </p>\n<p>Pou yon kokenn lapli » </p>\n<p>Ala yon lapli… (2 Fwa) </p>\n<p>Syel la tou nwa, dlo sa pa yon jwet </p>\n<p>Ala yon lapli… (2 Fwa) </p>\n<p>Le li rive, li mouye net. </p>\n<p>Ala yon lapli. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Anvan Jezu tounen nan syel, kote li soti </p>\n<p>Ak onz apot yo, li promenen </p>\n<p>Jouk nan Betani. </p>\n<p>Li di le-l rive nan syel la, l-ap voye sentespri. </p>\n<p>Donk rete tann nan Jerizalem </p>\n<p>Jouk pwisans la vini, </p>\n<p>Sanvin disip nan chanmot la te veye e priye </p>\n<p>Yon van sibit sekwe kay la, </p>\n<p>Benediksyon vide. </p>\n<p>Ala yon lapli…(2 fwa) </p>\n<p>Bondye voye lesentespri, </p>\n<p>Preche chak moun nan lang pa li. </p>\n<p>Ala yon lapli… (2 Fwa) </p>\n<p>Mesaj repann nan tout peyi, </p>\n<p>Ala yon lapli </p>\n','Sa-fe-sink-mil-ane-pase',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2273,'a49bf1fb-0ace-434e-9b45-b2679bd3a9d8',0,0,'Lè Nou Nan dezè-a Lè Nou Gade Syèl La',42,'42_RN_ht','1.\nLè nou nan dezè-a lè nou gade syèl la\nN’ap sonje gin lòt kay papa-m al prepare \nSi mwen fè volonte-l, l-ap ban mwen yon kouwòn\n\nRefren:\nSatan patisipe prèske nan tout bagay \nSi Jezu pa te la, di mwen sa mwen ta fè (bis 2 liy)\n\n2.\nMwen santi-m vle ale se pase m’ap pase\nMwen pa moun isiba\nVwayajè wout mwen long\nPeyi-m se nan syèl la\nSi Bondye va ede-m\n\n3.\nKamyon-an trè chaje\nGen lè-l pra’l chavire\nBato-a pra’l koule Jezu se vòl dirèk\nSe nan li mwen konfye’m\nSe li menm k’ap sovem\n\n4.\nN’ap priye, satan la\nN’ap mache satan la\nN’ap dòmi satan la\nN’ap jene satan la\nNan misyon satan la satan nan tout bagay\n\n5.\nN’ap jwaye e satan la\nN’ap preche satna la\nNan legliz satan la nou vire santan la\nNou tounen satan la.\nSatan nan tout bagay.','<p><strong>1.</strong> </p>\n<p>Lè nou nan dezè-a lè nou gade syèl la </p>\n<p>N’ap sonje gin lòt kay papa-m al prepare </p>\n<p>Si mwen fè volonte-l, l-ap ban mwen yon kouwòn </p>\n<p>Refren: </p>\n<p>Satan patisipe prèske nan tout bagay </p>\n<p>Si Jezu pa te la, di mwen sa mwen ta fè (bis 2 liy) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen santi-m vle ale se pase m’ap pase </p>\n<p>Mwen pa moun isiba </p>\n<p>Vwayajè wout mwen long </p>\n<p>Peyi-m se nan syèl la </p>\n<p>Si Bondye va ede-m </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kamyon-an trè chaje </p>\n<p>Gen lè-l pra’l chavire </p>\n<p>Bato-a pra’l koule Jezu se vòl dirèk </p>\n<p>Se nan li mwen konfye’m </p>\n<p>Se li menm k’ap sovem </p>\n<p><br/> <strong>4.</strong></p>\n<p>N’ap priye, satan la </p>\n<p>N’ap mache satan la </p>\n<p>N’ap dòmi satan la </p>\n<p>N’ap jene satan la </p>\n<p>Nan misyon satan la satan nan tout bagay </p>\n<p><br/> <strong>5.</strong></p>\n<p>N’ap jwaye e satan la </p>\n<p>N’ap preche satna la </p>\n<p>Nan legliz satan la nou vire santan la </p>\n<p>Nou tounen satan la. </p>\n<p>Satan nan tout bagay. </p>\n','Le-Nou-Nan-deze-a-Le-Nou-Gade-Syel-La',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2274,'3e089d3d-9833-4fec-a7d5-0ecdedef160e',0,0,'Mwen Kontan,  Kouwòn Mwen Va Yon Etwal',51,'51_RN_ht','1.\nMwen kontan, kouwòn mwen va yon etwal.\nLè’m rive nan bèl pòt syèl la mwen gen yon boukè\nE rout la va klere plis pou mwen.\n \nRefren:\nTravay pou kouwèn nan,\nBèl kouwòn nou-an\nTravay pou kouwòn nan,\nKe m’ap pote pou jou\n \n2.\nKilè mwen va ale nan peyi byennere,\nKote gen rekolt pou tout tan \nKote ki va genyen anpil ouvriye \nKote pwomès Kris la akonpli\n \n3.\nE kòman pou-m gen yon \nEtwal nan kouwòn mwen ?\nEske se lè-m rive nan syèl la ?\nMwen dwe travay pou nan Kris la kounye-a \nPou wa ki mouri pou mwen.','<p><strong>1.</strong> </p>\n<p>Mwen kontan, kouwòn mwen va yon etwal. </p>\n<p>Lè’m rive nan bèl pòt syèl la mwen gen yon boukè </p>\n<p>E rout la va klere plis pou mwen. </p>\n<p>Refren: </p>\n<p>Travay pou kouwèn nan, </p>\n<p>Bèl kouwòn nou-an </p>\n<p>Travay pou kouwòn nan, </p>\n<p>Ke m’ap pote pou jou </p>\n<p><br/> <strong>2.</strong></p>\n<p>Kilè mwen va ale nan peyi byennere, </p>\n<p>Kote gen rekolt pou tout tan </p>\n<p>Kote ki va genyen anpil ouvriye </p>\n<p>Kote pwomès Kris la akonpli </p>\n<p><br/> <strong>3.</strong></p>\n<p>E kòman pou-m gen yon </p>\n<p>Etwal nan kouwòn mwen ? </p>\n<p>Eske se lè-m rive nan syèl la ? </p>\n<p>Mwen dwe travay pou nan Kris la kounye-a </p>\n<p>Pou wa ki mouri pou mwen. </p>\n','Mwen-Kontan-Kouwon-Mwen-Va-Yon-Etwal',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2275,'a2e2c211-9eb3-4a5b-af09-ad86904032a6',0,0,'Pour Toi Redempteur Precieux',8,'8_RN_fr','1.\nPour toi rédempteur précieux\nJ’entonne un hymne d’amour\nMa voix s’élevé vers toi, vers mon Dieu\nCar de toi vient le secours\nDe ma vie tu suis le cours\nMa force de chaque jour\nC’est jésus\n \nREFRAIN\nMon jésus, mon jésus\nQuel ami précieux\nJe trouve en jésus\nPar son sang, il m’a lave\nPar sa mort, il m’a sauve\nDe sa vie il m’a donne\nMon jésus.\n \n2.\nTu subis mon châtiment\nPour me donner le salut\nTu pris sur toi mes tourments mon jésus\nMon cœur ne désespère plus\nMon amé n’est plus abattue, je suis toujours soutenu par jésus\n \n3.\nDe ma vie, mon temps mes bien\nA ton gré dispose de moi\nMon être entier t’appartient O ! Jésus\nMa vie cachée dans ta croix\nNe veut suivre que ta voie\nPrendre pour son divin Roi que jésus.','<p><strong>1.</strong> </p>\n<p>Pour toi rédempteur précieux </p>\n<p>J’entonne un hymne d’amour </p>\n<p>Ma voix s’élevé vers toi, vers mon Dieu </p>\n<p>Car de toi vient le secours </p>\n<p>De ma vie tu suis le cours </p>\n<p>Ma force de chaque jour </p>\n<p>C’est jésus </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon jésus, mon jésus</strong> </p>\n<p><strong>Quel ami précieux</strong> </p>\n<p><strong>Je trouve en jésus</strong> </p>\n<p><strong>Par son sang, il m’a lave</strong> </p>\n<p><strong>Par sa mort, il m’a sauve</strong> </p>\n<p><strong>De sa vie il m’a donne</strong> </p>\n<p><strong>Mon jésus.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tu subis mon châtiment </p>\n<p>Pour me donner le salut </p>\n<p>Tu pris sur toi mes tourments mon jésus </p>\n<p>Mon cœur ne désespère plus </p>\n<p>Mon amé n’est plus abattue, je suis toujours soutenu par jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>De ma vie, mon temps mes bien </p>\n<p>A ton gré dispose de moi </p>\n<p>Mon être entier t’appartient O ! Jésus </p>\n<p>Ma vie cachée dans ta croix </p>\n<p>Ne veut suivre que ta voie </p>\n<p>Prendre pour son divin Roi que jésus. </p>\n','Pour-Toi-Redempteur-Precieux',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2276,'52d1a853-9db6-4be5-84a0-5175abad1eb4',0,0,'Pourqoi Ne Pas Chanter',9,'9_RN_fr','1.\nPourquoi ne pas chanter\nLa gloire du seigneur\nPourquoi ne pas louer\nLa bonté du sauveur ?\nTout l’univers d’accord\nSemble exalter l’amour\nD’un dieu compatissant et fidèle a toujours\n \nREFRAIN\nFaisons de même ne nous lassons pas !\nL’amour suprême veille sur nos pas\nDans la l’lumière, marchons dignement\nQue la prière s’unisse à nos chants !\n \n2.\nEntends l’oiseau de l’air\nIl chante faiblement pourtant le créateur\nReçoit son humble accent\nVois-tu la fleur des champs\nSon doux parfum parvient\nJusqu’au trône de Dieu qui veille sur les siens\n \n3.\nEt toi, petit ruisseau,\nTu cours en murmurant\nTon chant si simple et doux\nEst plein d’enchantement\nMême l’étoile dit :\nNous chantons en concert\nLa gloire de celui qui régit l’univers','<p><strong>1.</strong> </p>\n<p>Pourquoi ne pas chanter </p>\n<p>La gloire du seigneur </p>\n<p>Pourquoi ne pas louer </p>\n<p>La bonté du sauveur ? </p>\n<p>Tout l’univers d’accord </p>\n<p>Semble exalter l’amour </p>\n<p>D’un dieu compatissant et fidèle a toujours </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Faisons de même ne nous lassons pas !</strong> </p>\n<p><strong>L’amour suprême veille sur nos pas</strong> </p>\n<p><strong>Dans la l’lumière, marchons dignement</strong> </p>\n<p><strong>Que la prière s’unisse à nos chants !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Entends l’oiseau de l’air </p>\n<p>Il chante faiblement pourtant le créateur </p>\n<p>Reçoit son humble accent </p>\n<p>Vois-tu la fleur des champs </p>\n<p>Son doux parfum parvient </p>\n<p>Jusqu’au trône de Dieu qui veille sur les siens </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et toi, petit ruisseau, </p>\n<p>Tu cours en murmurant </p>\n<p>Ton chant si simple et doux </p>\n<p>Est plein d’enchantement </p>\n<p>Même l’étoile dit : </p>\n<p>Nous chantons en concert </p>\n<p>La gloire de celui qui régit l’univers </p>\n','Pourqoi-Ne-Pas-Chanter',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2277,'103f5f75-1ca7-498c-b687-82b8509a1f03',0,0,'Jezu Mande Nou Ale Vit Preche',53,'53_RN_ht','1.\nJezu mande nou ale vit preche\nBon pawòl la \nNou gen dwa obeyi li\nJiska lafen monn sa-a\n \nRefren:\nAle, gaye toupatou\nLa pawòl Bondye damou,\nSe Kris ki mande : ale, bay\nByen lwen ak tou pre  priye\n \n2.\nPriye pou fòs  rete fidèl,\nE viv nan pwisans li \nKonsa tout moun va kapab wè-l\nE resevwa gras li.\n \n3.\nGen anpil moun tou ki deyò \nKris vle pou yo vini\nLafè noue de voye moun \nRanmase y oba li','<p><strong>1.</strong> </p>\n<p>Jezu mande nou ale vit preche </p>\n<p>Bon pawòl la </p>\n<p>Nou gen dwa obeyi li </p>\n<p>Jiska lafen monn sa-a </p>\n<p>Refren: </p>\n<p>Ale, gaye toupatou </p>\n<p>La pawòl Bondye damou, </p>\n<p>Se Kris ki mande : ale, bay </p>\n<p>Byen lwen ak tou pre  priye </p>\n<p><br/> <strong>2.</strong></p>\n<p>Priye pou fòs  rete fidèl, </p>\n<p>E viv nan pwisans li </p>\n<p>Konsa tout moun va kapab wè-l </p>\n<p>E resevwa gras li. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gen anpil moun tou ki deyò </p>\n<p>Kris vle pou yo vini </p>\n<p>Lafè noue de voye moun </p>\n<p>Ranmase y oba li </p>\n','Jezu-Mande-Nou-Ale-Vit-Preche',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2278,'de114c4d-e58d-4ca2-bb02-4ce6be23d76f',0,0,'Kite-m Touche,  Kite-m Touche Jezu',48,'48_RN_ht','1.\nKite-m touché, kite-m touché Jezu,\nKite-m touché-l tan l’ap pase la-a\nE, lè-l va lonje men-l ba lòt yo,\nYo va konnen y ova viv, na va sove\n \nRefren:\nO, pou mwen kapab wè men ki lonje,\nSovè say o ki oprime\nKite-m touché: kite-m touché Jezu,\nKonsa lòt y ova konnen, y ova sove.\n \n2.\nMwen te pèdi lwen Jezu-Kri,\nMwen te poukont mwen, mwen pat gen lavi\nE lè sa-a Sovèm nan te touché-m\nKounye-a m’ap ede lòt yo k-ap viv nan peche.\n \n3.\nGen yon rivyè, yon rivyè k-ap koule, koule\nAk dlo-a, li lave nanm mwen,\nE dlo sa-a fè lavi mwen briye\nSentespri plis pase lajan avèk lò.','<p><strong>1.</strong> </p>\n<p>Kite-m touché, kite-m touché Jezu, </p>\n<p>Kite-m touché-l tan l’ap pase la-a </p>\n<p>E, lè-l va lonje men-l ba lòt yo, </p>\n<p>Yo va konnen y ova viv, na va sove </p>\n<p>Refren: </p>\n<p>O, pou mwen kapab wè men ki lonje, </p>\n<p>Sovè say o ki oprime </p>\n<p>Kite-m touché: kite-m touché Jezu, </p>\n<p>Konsa lòt y ova konnen, y ova sove. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mwen te pèdi lwen Jezu-Kri, </p>\n<p>Mwen te poukont mwen, mwen pat gen lavi </p>\n<p>E lè sa-a Sovèm nan te touché-m </p>\n<p>Kounye-a m’ap ede lòt yo k-ap viv nan peche. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gen yon rivyè, yon rivyè k-ap koule, koule </p>\n<p>Ak dlo-a, li lave nanm mwen, </p>\n<p>E dlo sa-a fè lavi mwen briye </p>\n<p>Sentespri plis pase lajan avèk lò. </p>\n','Kite-m-Touche-Kite-m-Touche-Jezu',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2279,'54e30236-4f24-4ff6-b748-26f377dad62a',0,0,'Elas,  elas,  mwen regret sa pou lemon\'n',78,'78_RN_ht','Ke\nElas, elas, mwen regrèt sa pou lemon’n\nTalè konsa lemon’n pral boulvese\nElas, élas, mwen regret sa pou lemon’n\nTale konsa lemon’n pral dlo nan je.\nPou nenpot ti bagay\nOu we you’n meté kouto sou lot,\nPou nenpot ti bagay\nOu tande lom ap tiye lom,\nLom kapab gade lom\nYon moman yo mete’l kadav,\nYap touye, yap jete-ou\nKe yo pa jan’m sole.\nLezom fabrike zam\nSe pa pou yo touye pye bwa\nLezom fabrike zam\nSe pa pou touye zannimo,\nLezom fabrike zam\nSe pou touye  lom parey yo\nYap touye’w, Yap jete’w\nEpi yap banboche\nAkaba k Jezabel\nMet ansan’m pou touye Nabot,\nAkab ak Jezabel\nTe panse Bondye pat we yo.\nSa pat rete konsa\nLetenel te kondane yo,\nSe yon ekzanp, yon ekzanp\nBondye trase sou yo.','<p>Elas, elas, mwen regrèt sa pou lemon’n </p>\n<p>Talè konsa lemon’n pral boulvese </p>\n<p>Elas, élas, mwen regret sa pou lemon’n </p>\n<p>Tale konsa lemon’n pral dlo nan je. </p>\n<p>Pou nenpot ti bagay </p>\n<p>Ou we you’n meté kouto sou lot, </p>\n<p>Pou nenpot ti bagay </p>\n<p>Ou tande lom ap tiye lom, </p>\n<p>Lom kapab gade lom </p>\n<p>Yon moman yo mete’l kadav, </p>\n<p>Yap touye, yap jete-ou </p>\n<p>Ke yo pa jan’m sole. </p>\n<p>Lezom fabrike zam </p>\n<p>Se pa pou yo touye pye bwa </p>\n<p>Lezom fabrike zam </p>\n<p>Se pa pou touye zannimo, </p>\n<p>Lezom fabrike zam </p>\n<p>Se pou touye  lom parey yo </p>\n<p>Yap touye’w, Yap jete’w </p>\n<p>Epi yap banboche </p>\n<p>Akaba k Jezabel </p>\n<p>Met ansan’m pou touye Nabot, </p>\n<p>Akab ak Jezabel </p>\n<p>Te panse Bondye pat we yo. </p>\n<p>Sa pat rete konsa </p>\n<p>Letenel te kondane yo, </p>\n<p>Se yon ekzanp, yon ekzanp </p>\n<p>Bondye trase sou yo. </p>\n','Elas-elas-mwen-regret-sa-pou-lemon\'n',NULL,'ht',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2280,'7519f83f-ba6f-4d71-a8f4-7be48d1e5f04',0,0,'Jésus est mon Divin Berger',28,'28_RN_fr','1.\nJésus est mon Divin Berger\nRejouis toi mon âme\nSeul Il délivre du danger\nTout en moi le réclame\n2.\nNon je ne manquerai de  rien\nCar oh! Grâce infinie\nJésus est l’ami le gardien\nEn qui je me confie\n3.\nJour après jour Il me  conduit\nDans ses verts pâturages\nDe son amour Il nourrit\nSous ses divins ombrages\n4.\nJe ne craindrai donc aucun  mal\nQuand au bout du voyage\nIl me faudra du sombre val\nFranchir le noir passage\n5.\nToujours sa grâce et son  amour\nMe suivront sur la terre\nMais tout sera dans son  séjour\nPaix, gloire, amour, Lumière','<p><strong>1.</strong> </p>\n<p>Jésus est mon Divin Berger </p>\n<p>Rejouis toi mon âme </p>\n<p>Seul Il délivre du danger </p>\n<p>Tout en moi le réclame </p>\n<p><br/> <strong>2.</strong></p>\n<p>Non je ne manquerai de  rien </p>\n<p>Car oh! Grâce infinie </p>\n<p>Jésus est l’ami le gardien </p>\n<p>En qui je me confie </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jour après jour Il me  conduit </p>\n<p>Dans ses verts pâturages </p>\n<p>De son amour Il nourrit </p>\n<p>Sous ses divins ombrages </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je ne craindrai donc aucun  mal </p>\n<p>Quand au bout du voyage </p>\n<p>Il me faudra du sombre val </p>\n<p>Franchir le noir passage </p>\n<p><br/> <strong>5.</strong></p>\n<p>Toujours sa grâce et son  amour </p>\n<p>Me suivront sur la terre </p>\n<p>Mais tout sera dans son  séjour </p>\n<p>Paix, gloire, amour, Lumière </p>\n','Jesus-est-mon-Divin-Berger',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2281,'1e9afb76-c7b5-4334-ae75-4b11e84bf3e5',0,0,'Ecoutez l’apel de notre maitre',20,'20_RN_fr','1.\nEcoutez l’appel de notre  Maître\nAllez au monde annoncer  la nouvelle\nA toutes les races à toutes les nations\nPréchez la parole en temps  hors de temps\n\nRefrain\nAllez partout le monde\nJoyeusement proclamer\nAux Nicodemes aux Zaches\nChrist est mort pour nos  peches\n2.\nLa moisson est grande, a dit le maitre\nQui veut pour moi travailler\nIl demande que nul ne dise\nJe ne puis rien faire  lorsque Jésus\nNotre chef nous commande\n\n3.\nVoyez l’etendard Celeste  devant nous\nC’est un secours manifeste  et puissant\nLe combat est long mais  n’ayez pas peur\nCar avec Christ nous  serons plus que vainqueurs','<p><strong>1.</strong> </p>\n<p>Ecoutez l’appel de notre  Maître </p>\n<p>Allez au monde annoncer  la nouvelle </p>\n<p>A toutes les races à toutes les nations </p>\n<p>Préchez la parole en temps  hors de temps </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Allez partout le monde</strong> </p>\n<p><strong>Joyeusement proclamer</strong> </p>\n<p><strong>Aux Nicodemes aux Zaches</strong> </p>\n<p><strong>Christ est mort pour nos  peches</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La moisson est grande, a dit le maitre </p>\n<p>Qui veut pour moi travailler </p>\n<p>Il demande que nul ne dise </p>\n<p>Je ne puis rien faire  lorsque Jésus </p>\n<p>Notre chef nous commande </p>\n<p><br/> <strong>3.</strong></p>\n<p>Voyez l’etendard Celeste  devant nous </p>\n<p>C’est un secours manifeste  et puissant </p>\n<p>Le combat est long mais  n’ayez pas peur </p>\n<p>Car avec Christ nous  serons plus que vainqueurs </p>\n','Ecoutez-l\'apel-de-notre-maitre',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2282,'8befcc32-dbd2-4af2-8844-87d8ab5d854f',0,0,'Je ne sais pourquoi Christ m’a tant aimé',22,'22_RN_fr','1.\nJe ne sais pourquoi Christ  m’a tant aimé\nIl a fait pour moi des  chose merveilleuses\nJe vois des choses dont  je ne sais pourquoi\nChrist m’a montré qu’Il a  fait pour moi\nRefrain\nGloire à Toi Christ mon  Rédempteur\nGloire a Toi Jesus m’a  délivrance\nToi qui a eu pitié de moi\nEt qui m’a sauvé de tout  mal\n2.\nJesus Christ est mon  berger\nJamais Il ne laisse  abandonner\nJe te suis enfin sans  effroi\nToujours je vois, il est  près de moi\n3.\nQuand les hommes  m’auraient condanné\nAlors par eux je serai  maltraité\nTout comme Jésus je me  résignais\nMon âme en Dieu se réjourais\n4.\nIl m’a retiré d’entre  leurs mains\nIl me protègeras pas sa  main\nIl m’envoie enrefuge sous  son ombre\nJe me repose toujours sous  son ombre\n5.\nJe me souviens du prophète  Elie\nLe roi Achab l’avait  poursuivi\nUn corbeau lui avait  apportait du pain\nPour qu’il ne mourru pas  de fain','<p><strong>1.</strong> </p>\n<p>Je ne sais pourquoi Christ  m’a tant aimé </p>\n<p>Il a fait pour moi des  chose merveilleuses </p>\n<p>Je vois des choses dont  je ne sais pourquoi </p>\n<p>Christ m’a montré qu’Il a  fait pour moi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à Toi Christ mon  Rédempteur</strong> </p>\n<p><strong>Gloire a Toi Jesus m’a  délivrance</strong> </p>\n<p><strong>Toi qui a eu pitié de moi</strong> </p>\n<p><strong>Et qui m’a sauvé de tout  mal</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jesus Christ est mon  berger </p>\n<p>Jamais Il ne laisse  abandonner </p>\n<p>Je te suis enfin sans  effroi </p>\n<p>Toujours je vois, il est  près de moi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand les hommes  m’auraient condanné </p>\n<p>Alors par eux je serai  maltraité </p>\n<p>Tout comme Jésus je me  résignais </p>\n<p>Mon âme en Dieu se réjourais </p>\n<p><br/> <strong>4.</strong></p>\n<p>Il m’a retiré d’entre  leurs mains </p>\n<p>Il me protègeras pas sa  main </p>\n<p>Il m’envoie enrefuge sous  son ombre </p>\n<p>Je me repose toujours sous  son ombre </p>\n<p><br/> <strong>5.</strong></p>\n<p>Je me souviens du prophète  Elie </p>\n<p>Le roi Achab l’avait  poursuivi </p>\n<p>Un corbeau lui avait  apportait du pain </p>\n<p>Pour qu’il ne mourru pas  de fain </p>\n','Je-ne-sais-pourquoi-Christ-m\'a-tant-aime',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2283,'5433a77e-ad6c-4749-9506-6aa0f414585a',0,0,'Dieu ouvres d’en haut les écluses',21,'21_RN_fr','1.\nDieu ouvre d’en haut les  écluses\nIl répand sur nous ses  bienfaits\nMon coeur de joie est  rempli\nCar Jésus m’a donné sa  paix\nJe n’ai plus de vêtements  souillés\nMa robe est blanche c’est  un fait\nSa manche d’en haut il m’a  donné\nEn Lui mon Bonheur est  parfait','<p><strong>1.</strong> </p>\n<p>Dieu ouvre d’en haut les  écluses </p>\n<p>Il répand sur nous ses  bienfaits </p>\n<p>Mon coeur de joie est  rempli </p>\n<p>Car Jésus m’a donné sa  paix </p>\n<p>Je n’ai plus de vêtements  souillés </p>\n<p>Ma robe est blanche c’est  un fait </p>\n<p>Sa manche d’en haut il m’a  donné </p>\n<p>En Lui mon Bonheur est  parfait </p>\n','Dieu-ouvres-d\'en-haut-les-ecluses',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2284,'0c3a38c7-cdd9-4562-b4a7-8ce831ebd426',0,0,'Sommes-nous Tristes?',11,'11_RN_fr','1.\nSommes-nous tristes?\nNon, non non!\nSommes nous tristes?\nNon, non, non!\nLes troubles viennent\nLes trouble vont\nMes en Jesus nous nous confions\nSommes-nous tristes? (bis)\nNon, non, non!','<p><strong>1.</strong> </p>\n<p>Sommes-nous tristes? </p>\n<p>Non, non non! </p>\n<p>Sommes nous tristes? </p>\n<p>Non, non, non! </p>\n<p>Les troubles viennent </p>\n<p>Les trouble vont </p>\n<p>Mes en Jesus nous nous confions </p>\n<p>Sommes-nous tristes? (bis) </p>\n<p>Non, non, non! </p>\n','Sommes-nous-Tristes',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2285,'6c9e7421-670f-40f0-9acb-8c82472b987e',0,0,'Âme souffrante',42,'42_RN_fr','1.\nÂme souffrante,\nÂme  angoissante et vacillante\nDans  cette vie,\nToute espérance  est déjà perdue\nRegarde le  ciel en écoute la douce voix\nQui aujourd’hui  t’appelle\nToujours  t’appelle sans cesse\nChoeur\nEntends  la voix de ton Sauveur\nNe le méprise  pas  pour toi toujours\nOh !  Entends la voix qui t’appelle\nEt en écoutant,  elle changera ton\nTriste  sort\nEntends  la voix et tu auras en toi la paix\n2.\nMalade  et triste je me trouve en cette vie\nPas de  docteur, pas de remède pour me soulager\nSans espérance  je vivais mes jours en agonie\nSa douce  voix que j’écoutais me donna la paix','<p><strong>1.</strong> </p>\n<p>Âme souffrante, </p>\n<p>Âme  angoissante et vacillante </p>\n<p>Dans  cette vie, </p>\n<p>Toute espérance  est déjà perdue </p>\n<p>Regarde le  ciel en écoute la douce voix </p>\n<p>Qui aujourd’hui  t’appelle </p>\n<p>Toujours  t’appelle sans cesse </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Entends  la voix de ton Sauveur</strong> </p>\n<p><strong>Ne le méprise  pas  pour toi toujours</strong> </p>\n<p><strong>Oh !  Entends la voix qui t’appelle</strong> </p>\n<p><strong>Et en écoutant,  elle changera ton</strong> </p>\n<p><strong>Triste  sort</strong> </p>\n<p><strong>Entends  la voix et tu auras en toi la paix</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Malade  et triste je me trouve en cette vie </p>\n<p>Pas de  docteur, pas de remède pour me soulager </p>\n<p>Sans espérance  je vivais mes jours en agonie </p>\n<p>Sa douce  voix que j’écoutais me donna la paix </p>\n','Ame-souffrante',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2286,'0ea52dbe-1f9c-4d3d-af3c-d3be688c5e00',0,0,'Par son grand amour divin',25,'25_RN_fr','1.\nPar son grand amour divin\nJesus invite au festin\nTous les saint qu’il a  racheté\nOh! venez\nSa manne vous nourrira\nEt chaque jour suffira\nVenez vous tous les  invités\nOh! Mangez\n Refrain\nOh venez! Quelle merveille\nOh venez!\nQuelle fête sans pareille\nOh mangez!\nCelui qui créa le pain\nEt qui de l’eau fut du vin\nInvite tous au festin\nOh mangez\n2.\nComme les saints autrefois\nObéissaient à sa voix\nSoyez aussi bien disposés\nO venez!\nVoice le poisson le pain\nMangez vous n’aurez plus  faim\nEnfants du Seigneur\nOh venez et mangez\n3.\nBientot descendra l’agneau\nEnlever son cher troupeau\nOh qu’il sera beau de le  voir désormais\nCouvert d’immortalité\nEt parfais dans l’unite\nFrère vivons dans cet  espoir à jamais','<p><strong>1.</strong> </p>\n<p>Par son grand amour divin </p>\n<p>Jesus invite au festin </p>\n<p>Tous les saint qu’il a  racheté </p>\n<p>Oh! venez </p>\n<p>Sa manne vous nourrira </p>\n<p>Et chaque jour suffira </p>\n<p>Venez vous tous les  invités </p>\n<p>Oh! Mangez </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh venez! Quelle merveille</strong> </p>\n<p><strong>Oh venez!</strong> </p>\n<p><strong>Quelle fête sans pareille</strong> </p>\n<p><strong>Oh mangez!</strong> </p>\n<p><strong>Celui qui créa le pain</strong> </p>\n<p><strong>Et qui de l’eau fut du vin</strong> </p>\n<p><strong>Invite tous au festin</strong> </p>\n<p><strong>Oh mangez</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Comme les saints autrefois </p>\n<p>Obéissaient à sa voix </p>\n<p>Soyez aussi bien disposés </p>\n<p>O venez! </p>\n<p>Voice le poisson le pain </p>\n<p>Mangez vous n’aurez plus  faim </p>\n<p>Enfants du Seigneur </p>\n<p>Oh venez et mangez </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientot descendra l’agneau </p>\n<p>Enlever son cher troupeau </p>\n<p>Oh qu’il sera beau de le  voir désormais </p>\n<p>Couvert d’immortalité </p>\n<p>Et parfais dans l’unite </p>\n<p>Frère vivons dans cet  espoir à jamais </p>\n','Par-son-grand-amour-divin',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2287,'c075bfa8-6809-43ce-9073-90c51f579738',0,0,'Lorsqu’on est jeune, on danse et l’on rit',34,'34_RN_fr','1.\nLorsqu’on  est jeune, on danse et l’on rit\nOn rêve  de grands espoirs,\nOn aime,  on chante, tout nous sourit.\nMais il  nous faut faire un choix.\nRefrain\nChante  O ! Jeunesse to Dieu ton Roi\nC’est  lui qui nous mène au but à coup sur\nA lui la  flamme de notre foi\nNos  cœurs qu’il veut rendre purs.\n2.\nVous les  amis, quel sens a pour vous\nLa vie  et ses durs combats ?\nEn  Christ on trouve le ciel de tout\nSa grâce  affermit nos pas.\n3.\nVous les  amis pour qui cette peur ?\nQuand  passe soudain la mort,\nChrist  est vivant  c’est le grand vainqueur\nIl veut  rendre libre et fort.\n4.\nVous les  amis comment donc bâtir\nUn monde  qui soit meilleur\nIl est  le chef qu’il nous faut servir\nC’est  Christ le seul vrai Sauveur.','<p><strong>1.</strong> </p>\n<p>Lorsqu’on  est jeune, on danse et l’on rit </p>\n<p>On rêve  de grands espoirs, </p>\n<p>On aime,  on chante, tout nous sourit. </p>\n<p>Mais il  nous faut faire un choix. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chante  O ! Jeunesse to Dieu ton Roi</strong> </p>\n<p><strong>C’est  lui qui nous mène au but à coup sur</strong> </p>\n<p><strong>A lui la  flamme de notre foi</strong> </p>\n<p><strong>Nos  cœurs qu’il veut rendre purs.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vous les  amis, quel sens a pour vous </p>\n<p>La vie  et ses durs combats ? </p>\n<p>En  Christ on trouve le ciel de tout </p>\n<p>Sa grâce  affermit nos pas. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vous les  amis pour qui cette peur ? </p>\n<p>Quand  passe soudain la mort, </p>\n<p>Christ  est vivant  c’est le grand vainqueur </p>\n<p>Il veut  rendre libre et fort. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vous les  amis comment donc bâtir </p>\n<p>Un monde  qui soit meilleur </p>\n<p>Il est  le chef qu’il nous faut servir </p>\n<p>C’est  Christ le seul vrai Sauveur. </p>\n','Lorsqu\'on-est-jeune-on-danse-et-l\'on-rit',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2288,'808a766e-0cb7-4290-87cd-da8c25c5e66d',0,0,'Mon Dieu je t’aime',37,'37_RN_fr','1.\nMon Dieu  je t’aime,\nJe t’apporte mon cœur\nMon cœur  Dieu je t’aime,\nJe veux  le vrai bonheur\nRefrain\nSi par mégarde,\nJ’arrive  à chanceler\nTon ange  me garde,\nMe  veille de tout danger\n2.\nA toi ma  vie,\nMon cœur  et mon esprit\nA toi ma  vie,\nJe vois  le ciel qui luit\n3.\nMon cœur  te prie,\nMon Dieu  écoute-le\nJe te  supplie,\nMon Dieu  exauce-le\n4.\nMa vie entière,\nEst consacrée a toi\nDans ma litière,\nMon Dieu  protège moi\nRefrain\nTu me  gardes, tu me nourris\nMon  esprit s’attache a toi\nMon Dieu  je t’aime (bis)\nMes yeux  se ferment\nQuand  parfois tu me souris','<p><strong>1.</strong> </p>\n<p>Mon Dieu  je t’aime, </p>\n<p>Je t’apporte mon cœur </p>\n<p>Mon cœur  Dieu je t’aime, </p>\n<p>Je veux  le vrai bonheur </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Si par mégarde,</strong> </p>\n<p><strong>J’arrive  à chanceler</strong> </p>\n<p><strong>Ton ange  me garde,</strong> </p>\n<p><strong>Me  veille de tout danger</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A toi ma  vie, </p>\n<p>Mon cœur  et mon esprit </p>\n<p>A toi ma  vie, </p>\n<p>Je vois  le ciel qui luit </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon cœur  te prie, </p>\n<p>Mon Dieu  écoute-le </p>\n<p>Je te  supplie, </p>\n<p>Mon Dieu  exauce-le </p>\n<p><br/> <strong>4.</strong></p>\n<p>Ma vie entière, </p>\n<p>Est consacrée a toi </p>\n<p>Dans ma litière, </p>\n<p>Mon Dieu  protège moi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Tu me  gardes, tu me nourris</strong> </p>\n<p><strong>Mon  esprit s’attache a toi</strong> </p>\n<p><strong>Mon Dieu  je t’aime (bis)</strong> </p>\n<p><strong>Mes yeux  se ferment</strong> </p>\n<p><strong>Quand  parfois tu me souris</strong> </p>\n','Mon-Dieu-je-t\'aime',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2289,'197d4064-4610-48ab-b64d-d09aa48340cb',0,0,'Au tribunal Jésus se tient',38,'38_RN_fr','1.\nAu  tribunal Jésus se tient\nMéprise  seul et sans soutien\nPilate,  au peuple, dit soudain\n« Que  dois-je faire de Jésus »\nRefrain\nQue feras  tu de Jésus\nQui se  tient devant toi\nUn jour  tu diras plein d’effroi\n« Que  feras tu de moi »\n2.\nComme  Pilate indiffèrent\nCondamnez-vous  l’innocent ?\nC’est  vous qui jugez maintenant\nQue  ferez-vous de Jésus ?\n3.\nComme  Judas trahirai- vous\nCelui  qui veut mourir pour nous\nOu le  suivez-vous jusqu’au bout\nQue  ferez-vous de Jésus ?\n4.\nJésus je  te donne mon cœur\nToi seul  es mon parfait bonheur\nT’obéir  sera mon bonheur\nJe  t’appartiens  O! Jésus','<p><strong>1.</strong> </p>\n<p>Au  tribunal Jésus se tient </p>\n<p>Méprise  seul et sans soutien </p>\n<p>Pilate,  au peuple, dit soudain </p>\n<p>« Que  dois-je faire de Jésus » </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Que feras  tu de Jésus</strong> </p>\n<p><strong>Qui se  tient devant toi</strong> </p>\n<p><strong>Un jour  tu diras plein d’effroi</strong> </p>\n<p><strong>« Que  feras tu de moi »</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Comme  Pilate indiffèrent </p>\n<p>Condamnez-vous  l’innocent ? </p>\n<p>C’est  vous qui jugez maintenant </p>\n<p>Que  ferez-vous de Jésus ? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme  Judas trahirai- vous </p>\n<p>Celui  qui veut mourir pour nous </p>\n<p>Ou le  suivez-vous jusqu’au bout </p>\n<p>Que  ferez-vous de Jésus ? </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus je  te donne mon cœur </p>\n<p>Toi seul  es mon parfait bonheur </p>\n<p>T’obéir  sera mon bonheur </p>\n<p>Je  t’appartiens  O! Jésus </p>\n','Au-tribunal-Jesus-se-tient',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2290,'a9d925b5-ec94-4111-902f-2636670afab6',0,0,'Jeunesse ardente',52,'52_RN_fr','1.\nJeunesse ardente,\nL’aube s’argente\nEt ta voix chante\nAu clair matin\nDéjà la vie\nS’ouvre infinie,\nEpanouie,\nDans le lointain\n2.\n \nJeunesse éprise,\nQuand vient la crise\nEn Dieu seul, puise\nLa fermeté.\nApre et tenace\nLe mal menace.\nPrends pour cuirasse,\nLa pureté.\n3.\nJeunesse aimée,\nTelle une armée,\nSois enflammée.\nPour ton Sauveur,\nDonne-toi toute,\nPoursuis ta route,\nToujours, écoute\nLe Christ Vainqueur','<p><strong>1.</strong> </p>\n<p>Jeunesse ardente, </p>\n<p>L’aube s’argente </p>\n<p>Et ta voix chante </p>\n<p>Au clair matin </p>\n<p>Déjà la vie </p>\n<p>S’ouvre infinie, </p>\n<p>Epanouie, </p>\n<p>Dans le lointain </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jeunesse éprise, </p>\n<p>Quand vient la crise </p>\n<p>En Dieu seul, puise </p>\n<p>La fermeté. </p>\n<p>Apre et tenace </p>\n<p>Le mal menace. </p>\n<p>Prends pour cuirasse, </p>\n<p>La pureté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jeunesse aimée, </p>\n<p>Telle une armée, </p>\n<p>Sois enflammée. </p>\n<p>Pour ton Sauveur, </p>\n<p>Donne-toi toute, </p>\n<p>Poursuis ta route, </p>\n<p>Toujours, écoute </p>\n<p>Le Christ Vainqueur </p>\n','Jeunesse-ardente',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2291,'534a77d9-5403-467e-b1ad-e4b5559d9f0d',0,0,'Nous prêchons la vérité',67,'67_RN_fr','1.\nNous prêchons la vérité,\nCombattons l’iniquité,\nRejetons tous dogmes fondés  sur l’erreur.\nEt toutes inventions\nAinsi que la tradition.\nQui détruisent la parole du  Seigneur\nREFRAIN\nGloire à Dieu Jésus peut  vous sauver,\nDe tout péché racheté,\nSeul il est libérateur,  l’Unique Médiateur,\nJésus Christ seul peut  sauver et racheter.\n2.\nNous ne pouvons raconter,\nNi ne devons confesser\nTous nos péchés à l’oreille  d’un pécheur\nCelui qui vient repentant,\nDevant Dieu comme un enfant,\nTrouve le pardon et la paix  du Seigneur.\n3.\nDemandons aussi pourquoi\nL’on retranche de la loi,\nLe sixième commandement que  Dieu donne.\nTu ne feras point  d’images \nNi ne lui rendras hommages,\nAdorez Dieu   seul !  Voila ce qu’il ordonne.\n4.\nNous rejetons le latin,\nDont l’emploi vient du  malin.\nNous lisons notre Bible en  langue vulgaire.\nMessage de vérité,\nLivre de l’éternité,\nElle offre le salut acquis  au calvaire.\n5.\nL’on chante tous les  matins\nPour exploiter les humains\nDe leur messe de requiem  sans valeur\nCependant le purgatoire\nN’existe point c’est la  gloire\nQue doit attendre du  Seigneur','<p><strong>1.</strong> </p>\n<p>Nous prêchons la vérité, </p>\n<p>Combattons l’iniquité, </p>\n<p>Rejetons tous dogmes fondés  sur l’erreur. </p>\n<p>Et toutes inventions </p>\n<p>Ainsi que la tradition. </p>\n<p>Qui détruisent la parole du  Seigneur </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à Dieu Jésus peut  vous sauver,</strong> </p>\n<p><strong>De tout péché racheté,</strong> </p>\n<p><strong>Seul il est libérateur,  l’Unique Médiateur,</strong> </p>\n<p><strong>Jésus Christ seul peut  sauver et racheter.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nous ne pouvons raconter, </p>\n<p>Ni ne devons confesser </p>\n<p>Tous nos péchés à l’oreille  d’un pécheur </p>\n<p>Celui qui vient repentant, </p>\n<p>Devant Dieu comme un enfant, </p>\n<p>Trouve le pardon et la paix  du Seigneur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Demandons aussi pourquoi </p>\n<p>L’on retranche de la loi, </p>\n<p>Le sixième commandement que  Dieu donne. </p>\n<p>Tu ne feras point  d’images </p>\n<p>Ni ne lui rendras hommages, </p>\n<p>Adorez Dieu   seul !  Voila ce qu’il ordonne. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Nous rejetons le latin, </p>\n<p>Dont l’emploi vient du  malin. </p>\n<p>Nous lisons notre Bible en  langue vulgaire. </p>\n<p>Message de vérité, </p>\n<p>Livre de l’éternité, </p>\n<p>Elle offre le salut acquis  au calvaire. </p>\n<p><br/> <strong>5.</strong></p>\n<p>L’on chante tous les  matins </p>\n<p>Pour exploiter les humains </p>\n<p>De leur messe de requiem  sans valeur </p>\n<p>Cependant le purgatoire </p>\n<p>N’existe point c’est la  gloire </p>\n<p>Que doit attendre du  Seigneur </p>\n','Nous-prechons-la-verite',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2292,'076e29ae-38bb-4d54-879d-7f433dc84982',0,0,'Sentinelle vigilante',69,'69_RN_fr','1.\nSentinelle vigilante\nQu’en est-il donc de la  nuit ?\nDis à l’âme somnolente\nQue déjà le matin  luit !\n2.\nLes gardes sur la  muraille\nNous l’ont dit,  entendez-vous ?\nAu loin gronde la bataille.\nTout est sombre autour de  nous.\n3.\n \nPoint de repos, de relâche,\nRachetés de l’Eternel,\nTravaillez à votre tache,\nCar Jésus revient du ciel\nREFRAIN\nLa nuit passe, le matin du  grand jour luit !\nSentinelle, sois au poste  jour et nuit. (bis)','<p><strong>1.</strong> </p>\n<p>Sentinelle vigilante </p>\n<p>Qu’en est-il donc de la  nuit ? </p>\n<p>Dis à l’âme somnolente </p>\n<p>Que déjà le matin  luit ! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les gardes sur la  muraille </p>\n<p>Nous l’ont dit,  entendez-vous ? </p>\n<p>Au loin gronde la bataille. </p>\n<p>Tout est sombre autour de  nous. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Point de repos, de relâche, </p>\n<p>Rachetés de l’Eternel, </p>\n<p>Travaillez à votre tache, </p>\n<p>Car Jésus revient du ciel </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>La nuit passe, le matin du  grand jour luit !</strong> </p>\n<p><strong>Sentinelle, sois au poste  jour et nuit. (bis)</strong> </p>\n','Sentinelle-vigilante',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2293,'97b54639-87e8-4b45-81d6-cc210c1650ed',0,0,'Dirige-moi, Guide mes pas',39,'39_RN_fr','1.\nDirige-moi,  Guide mes pas\nOu  dois-je aller ? Je ne sais pas\nJe ne  suis rien, je ne suis rien\nSi tu me  tiens, tout ira bien\nRefrain\nTout mon  désir est de servir\nOh !  Parle-moi, dirige-moi\nComment  te plaire ?\nJe veux  tout faire\nEn toi  seul et pour toi\n2.\nQue  dois-je lire ?\nEnseigne-moi\nTon  faible enfant\nS’attend  à toi\nDans ton  secours\nEncourage-moi\nParle de  toi, parle de toi\n3.\nTu m’as  sauve, je suis a toi\nJe ne  puis plus vivre pour moi\nSois mon  trésor, jour après jour\n4.\nQue  dois-je dire Oh ! J’ai besoin\nDe ton  fidele et tendre soin\nEt quand  tout temps, par ton esprit\nDe  vérité je sois conduit','<p><strong>1.</strong> </p>\n<p>Dirige-moi,  Guide mes pas </p>\n<p>Ou  dois-je aller ? Je ne sais pas </p>\n<p>Je ne  suis rien, je ne suis rien </p>\n<p>Si tu me  tiens, tout ira bien </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Tout mon  désir est de servir</strong> </p>\n<p><strong>Oh !  Parle-moi, dirige-moi</strong> </p>\n<p><strong>Comment  te plaire ?</strong> </p>\n<p><strong>Je veux  tout faire</strong> </p>\n<p><strong>En toi  seul et pour toi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Que  dois-je lire ? </p>\n<p>Enseigne-moi </p>\n<p>Ton  faible enfant </p>\n<p>S’attend  à toi </p>\n<p>Dans ton  secours </p>\n<p>Encourage-moi </p>\n<p>Parle de  toi, parle de toi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tu m’as  sauve, je suis a toi </p>\n<p>Je ne  puis plus vivre pour moi </p>\n<p>Sois mon  trésor, jour après jour </p>\n<p><br/> <strong>4.</strong></p>\n<p>Que  dois-je dire Oh ! J’ai besoin </p>\n<p>De ton  fidele et tendre soin </p>\n<p>Et quand  tout temps, par ton esprit </p>\n<p>De  vérité je sois conduit </p>\n','Dirige-moi-Guide-mes-pas',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2294,'247c2d1d-6e7a-43de-8d5c-8b4b9a61f042',0,0,'Dieu tout puissant',65,'65_RN_fr','1.\nDieu tout-puissant,\nQuand mon cœur considère\nTout l’univers\nCree par ton pouvoir,\nLe ciel d’azur,\nLes éclairs, le tonnerre.\nLe clair matin\nOu les ombres du soir\nDe tout mon être,\nAlors s’élève un chant\nDieu tout-puissant\nQue tu es grand (bis)\n2.\nMais quand je songe\nO sublime mystère !\nQu’un Dieu si grand\nA pu penser à moi\nQue son cher Fils\nEst devenu mon frère\nEt que je suis\nL’héritier du grand Roi,\nAlors mon cœur\nRedit la nuit, le  jour :\n« Que tu es bon\nO Dieu d’amour ! »  (bis)\n3.\nQuand mon Sauveur,\nEclatant de lumière,\nSe lèvera de son trône  eternel,\nEt que laissant\nLes douleurs de la terre,\nJe pourrai voir\nLes splendeurs de Son ciel,\nJe redirai dans son divin séjour\n« Rien n’est plus grand\nQue ton amour ! »  (Bis 4 lignes)','<p><strong>1.</strong> </p>\n<p>Dieu tout-puissant, </p>\n<p>Quand mon cœur considère </p>\n<p>Tout l’univers </p>\n<p>Cree par ton pouvoir, </p>\n<p>Le ciel d’azur, </p>\n<p>Les éclairs, le tonnerre. </p>\n<p>Le clair matin </p>\n<p>Ou les ombres du soir </p>\n<p>De tout mon être, </p>\n<p>Alors s’élève un chant </p>\n<p>Dieu tout-puissant </p>\n<p>Que tu es grand (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais quand je songe </p>\n<p>O sublime mystère ! </p>\n<p>Qu’un Dieu si grand </p>\n<p>A pu penser à moi </p>\n<p>Que son cher Fils </p>\n<p>Est devenu mon frère </p>\n<p>Et que je suis </p>\n<p>L’héritier du grand Roi, </p>\n<p>Alors mon cœur </p>\n<p>Redit la nuit, le  jour : </p>\n<p>« Que tu es bon </p>\n<p>O Dieu d’amour ! »  (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand mon Sauveur, </p>\n<p>Eclatant de lumière, </p>\n<p>Se lèvera de son trône  eternel, </p>\n<p>Et que laissant </p>\n<p>Les douleurs de la terre, </p>\n<p>Je pourrai voir </p>\n<p>Les splendeurs de Son ciel, </p>\n<p>Je redirai dans son divin séjour </p>\n<p>« Rien n’est plus grand </p>\n<p>Que ton amour ! »  (Bis 4 lignes) </p>\n','Dieu-tout-puissant',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2295,'633c8213-b8eb-48b2-ba35-2d68d1334e94',0,0,'Marchant avec Christ.',66,'66_RN_fr','1.\nMarchant avec Christ.\nMarchant chaque jour,\nJusqu’à son retour\nMarchant avec Chris\nMarchant avec Jésus seul\n2.\nMarchant dans la clarté,\nEt dans les ténèbres,\nMarchant chaque jour\nJusqu’à son retour,\nMon sauveur, je te reçois\nMarchant avec Jésus seul.','<p><strong>1.</strong> </p>\n<p>Marchant avec Christ. </p>\n<p>Marchant chaque jour, </p>\n<p>Jusqu’à son retour </p>\n<p>Marchant avec Chris </p>\n<p>Marchant avec Jésus seul </p>\n<p><br/> <strong>2.</strong></p>\n<p>Marchant dans la clarté, </p>\n<p>Et dans les ténèbres, </p>\n<p>Marchant chaque jour </p>\n<p>Jusqu’à son retour, </p>\n<p>Mon sauveur, je te reçois </p>\n<p>Marchant avec Jésus seul. </p>\n','Marchant-avec-Christ.',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2296,'ca11f8ac-7a84-44e4-920d-a4aa2e17c52e',0,0,'Chantons a Dieu pour que notre patrie',58,'58_RN_fr','1.\nCrions Dieu pour que  notre patrie\nS’éveille enfin de son  sommeil de mort\nQue constamment tout enfant  de Dieu prie !\nAh par la foi changeons son  triste sort.\n2.\nOui, qu’à ces soins  chacun de nous s’applique\nNous l’obtiendrons,  indicible bonheur.\nTemple et maison, rue et  place publique,\nTout redira le beau nom du  Sauveur.\n3.\n \nAllons, Allons, enfants du Dieu de gloire,\nLevons nos fronts trop  longtemps abattus.\nComme aux beaux jours de  notre sainte histoire,\nChrétiens, vivons ou mourons  pour Jésus.\n4.\nL’aurore vient de ce jour  magnifique,\nOu secourant les fers de  l’oppresseur\nTout l’univers, dans un même  cantique\nProclamera le beau nom du  Sauveur','<p><strong>1.</strong> </p>\n<p>Crions Dieu pour que  notre patrie </p>\n<p>S’éveille enfin de son  sommeil de mort </p>\n<p>Que constamment tout enfant  de Dieu prie ! </p>\n<p>Ah par la foi changeons son  triste sort. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Oui, qu’à ces soins  chacun de nous s’applique </p>\n<p>Nous l’obtiendrons,  indicible bonheur. </p>\n<p>Temple et maison, rue et  place publique, </p>\n<p>Tout redira le beau nom du  Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Allons, Allons, enfants du Dieu de gloire, </p>\n<p>Levons nos fronts trop  longtemps abattus. </p>\n<p>Comme aux beaux jours de  notre sainte histoire, </p>\n<p>Chrétiens, vivons ou mourons  pour Jésus. </p>\n<p><br/> <strong>4.</strong></p>\n<p>L’aurore vient de ce jour  magnifique, </p>\n<p>Ou secourant les fers de  l’oppresseur </p>\n<p>Tout l’univers, dans un même  cantique </p>\n<p>Proclamera le beau nom du  Sauveur </p>\n','Chantons-a-Dieu-pour-que-notre-patrie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2297,'c36ab31e-44f2-4a48-8b23-daac0e9f2cd4',0,0,'Contemplez, O Chrétiens, vous qui fuyez le monde',60,'60_RN_fr','1.\nContemplez, ô Chrétiens,  vous qui fuyez le monde,\nLe divin Rédempteur  descendant au Jourdain.\nIl s’offre à ce tombeau que représente  l’ordre.\nMais en vainqueur, Jésus se révèle  soudain.\n2.\n \nLes siens veulent aussi dans cet instant  propice,\nAnimes d’un saint zèle, imiter  le Sauveur.\nEt. Selon sa parole,  accomplir la justice,\nDans la joie obéir   trouvant la paix du cœur.\n3.\nBaptises en ton nom, nous  vivons sur la terre,\nPour mourir au péché, ne  plus vivre qu’en toi,\nPour suivre en ces bas lieux  ton sentier solitaire,\nPuis avec toi régner dans ta  gloire, o grand Roi !','<p><strong>1.</strong> </p>\n<p>Contemplez, ô Chrétiens,  vous qui fuyez le monde, </p>\n<p>Le divin Rédempteur  descendant au Jourdain. </p>\n<p>Il s’offre à ce tombeau que représente  l’ordre. </p>\n<p>Mais en vainqueur, Jésus se révèle  soudain. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les siens veulent aussi dans cet instant  propice, </p>\n<p>Animes d’un saint zèle, imiter  le Sauveur. </p>\n<p>Et. Selon sa parole,  accomplir la justice, </p>\n<p>Dans la joie obéir   trouvant la paix du cœur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Baptises en ton nom, nous  vivons sur la terre, </p>\n<p>Pour mourir au péché, ne  plus vivre qu’en toi, </p>\n<p>Pour suivre en ces bas lieux  ton sentier solitaire, </p>\n<p>Puis avec toi régner dans ta  gloire, o grand Roi ! </p>\n','Contemplez-O-Chretiens-vous-qui-fuyez-le-monde',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2298,'2ab56f4b-b42b-44e4-bbf6-34eb28c5e96f',0,0,'A Dieu soit la gloire',27,'27_RN_fr','1.\nA Dieu soit la gloire\nPar son grand amour\nDans mon âme noire\nS’est levé le jour\nJésus à ma place\nMouru sur la croix\nIl m’offre sa grâce\nEt je la reçois\n\nRefrain\nGloire à Dieu (bis)\nTerre écoute sa voix\nGloire à Dieu (bis)\nMonde rejouis-toi\nOh! Venez au Père\nJésus est vainqueur\nQue toute la terre\nChante en son honneur\n2.\nDe Jésus la joie\nRemplit notre Coeur\nQu’importe qu’on voie\nTout notre bohneur\nSelon sa promesse\nJésus changera\nDeuil en allégresse\nQuand Il reviendra\n3.\nCéleste patrie\nMon Coeur pense à toi\nLa mon âme unie\nA Jésus son Roi\nBénira sans cesse\nLe grand Dieu Sauveur\nQui dans sa tendresse\nSauve le pécheur','<p><strong>1.</strong> </p>\n<p>A Dieu soit la gloire </p>\n<p>Par son grand amour </p>\n<p>Dans mon âme noire </p>\n<p>S’est levé le jour </p>\n<p>Jésus à ma place </p>\n<p>Mouru sur la croix </p>\n<p>Il m’offre sa grâce </p>\n<p>Et je la reçois </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Gloire à Dieu (bis)</strong> </p>\n<p><strong>Terre écoute sa voix</strong> </p>\n<p><strong>Gloire à Dieu (bis)</strong> </p>\n<p><strong>Monde rejouis-toi</strong> </p>\n<p><strong>Oh! Venez au Père</strong> </p>\n<p><strong>Jésus est vainqueur</strong> </p>\n<p><strong>Que toute la terre</strong> </p>\n<p><strong>Chante en son honneur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De Jésus la joie </p>\n<p>Remplit notre Coeur </p>\n<p>Qu’importe qu’on voie </p>\n<p>Tout notre bohneur </p>\n<p>Selon sa promesse </p>\n<p>Jésus changera </p>\n<p>Deuil en allégresse </p>\n<p>Quand Il reviendra </p>\n<p><br/> <strong>3.</strong></p>\n<p>Céleste patrie </p>\n<p>Mon Coeur pense à toi </p>\n<p>La mon âme unie </p>\n<p>A Jésus son Roi </p>\n<p>Bénira sans cesse </p>\n<p>Le grand Dieu Sauveur </p>\n<p>Qui dans sa tendresse </p>\n<p>Sauve le pécheur </p>\n','A-Dieu-soit-la-gloire',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2299,'bd4331f6-5327-4148-9ffe-fce5ed04863a',0,0,'Vivre près de mon Sauveur',29,'29_RN_fr','1.\nVivre près de mon Sauveur,\nSeigneur tiens-moi près de toi,\nC’est le désir de mon cœur\nEn marchant toujours plus près de toi. (bis)\n\nRefrain\nJe suis faible, tu es fort,\nJésus, tu es mon soutien.\nJe serai toujours content.\nQue tu reste constamment près de moi!\n2.\nJe suis faible par le péché,\nQui veut terrasser mon Cœur.\nJésus sois mon bouclier\nEt reste toujours plus près de moi.\n\n3.\nMalgré  mon indignité\nJe veux être un de tes disciples.\nEnseigne-moi bien tes voies\nAfin d’être toujours avec toi.\n\n4.\nJe veux mourir dans tes bras,\nApprends-moi comment…\nPour être avec toi au ciel\nParmi ceux qui sont plus près de toi.','<p><strong>1.</strong> </p>\n<p>Vivre près de mon Sauveur, </p>\n<p>Seigneur tiens-moi près de toi, </p>\n<p>C’est le désir de mon cœur </p>\n<p>En marchant toujours plus près de toi. (bis) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je suis faible, tu es fort,</strong> </p>\n<p><strong>Jésus, tu es mon soutien.</strong> </p>\n<p><strong>Je serai toujours content.</strong> </p>\n<p><strong>Que tu reste constamment près de moi!</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je suis faible par le péché, </p>\n<p>Qui veut terrasser mon Cœur. </p>\n<p>Jésus sois mon bouclier </p>\n<p>Et reste toujours plus près de moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Malgré  mon indignité </p>\n<p>Je veux être un de tes disciples. </p>\n<p>Enseigne-moi bien tes voies </p>\n<p>Afin d’être toujours avec toi. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je veux mourir dans tes bras, </p>\n<p>Apprends-moi comment… </p>\n<p>Pour être avec toi au ciel </p>\n<p>Parmi ceux qui sont plus près de toi. </p>\n','Vivre-pres-de-mon-Sauveur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2300,'ee8cf9d7-324c-4126-95b1-5cf142d1c5a7',0,0,'Plus doux qu’une halte en chemin',46,'46_RN_fr','1.\nPlus doux qu’une halte en chemin,\nTu me rends le gout de l’amour \nMême quand je dis : que sera demain ?\nTu me rends le gout de chanter.\n\nRefrain\nMerci, merci Seigneur\nTu me rends le gout de l’amour.\nMerci, merci Seigneur,\nTu me rends le gout de chanter.\n\n2.\nPlus fort que les journées d’espoir\nTu me rends le gout de l’amour.\nMême quand se fait loin l’ami qu’on attend,\nTu me rends le gout de chanter.\n\n3.\nTon nom soit chanté O notre père !\nTu m1 ce rends le gout de l’amour.\nTon règne exalte par l’univers\nTu me donnes le gout de chanter.','<p><strong>1.</strong> </p>\n<p>Plus doux qu’une halte en chemin, </p>\n<p>Tu me rends le gout de l’amour </p>\n<p>Même quand je dis : que sera demain ? </p>\n<p>Tu me rends le gout de chanter. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Merci, merci Seigneur</strong> </p>\n<p><strong>Tu me rends le gout de l’amour.</strong> </p>\n<p><strong>Merci, merci Seigneur,</strong> </p>\n<p><strong>Tu me rends le gout de chanter.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Plus fort que les journées d’espoir </p>\n<p>Tu me rends le gout de l’amour. </p>\n<p>Même quand se fait loin l’ami qu’on attend, </p>\n<p>Tu me rends le gout de chanter. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ton nom soit chanté O notre père ! </p>\n<p>Tu m1 ce rends le gout de l’amour. </p>\n<p>Ton règne exalte par l’univers </p>\n<p>Tu me donnes le gout de chanter. </p>\n','Plus-doux-qu\'une-halte-en-chemin',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2301,'701e92d9-813e-41c5-98ae-39f864e8db73',0,0,'Passant, si tard que veux-tu ?',64,'64_RN_fr','1.\nPassant, si tard que veux-tu ?\nQui donc ici  cherches-tu ?\nPourquoi voyageur étranger\nA ma porte es-tu venu ?\nN’es tu pas quelque saint  ange\nSur la terre descendue ?\n2.\nC’est moi, c’est moi ton  Sauveur !\nQui veux entrer dans ton  cœur\nPourquoi me laisser  attendre ?\nOuvre, c’est un bienfaiteur\nQui, chez toi, cherche à répandre\nTous les trésors du bonheur.\n3.\nVraiment !  Vraiment ! C’est sa voix \nC’est lui, c’est lui que je  vois\nOh !  Ta grâce est la plus forte.\nMon Sauveur je te reçois,\nJe ne puis fermer la porte\nQuand tu me montres ta  croix.','<p><strong>1.</strong> </p>\n<p>Passant, si tard que veux-tu ? </p>\n<p>Qui donc ici  cherches-tu ? </p>\n<p>Pourquoi voyageur étranger </p>\n<p>A ma porte es-tu venu ? </p>\n<p>N’es tu pas quelque saint  ange </p>\n<p>Sur la terre descendue ? </p>\n<p><br/> <strong>2.</strong></p>\n<p>C’est moi, c’est moi ton  Sauveur ! </p>\n<p>Qui veux entrer dans ton  cœur </p>\n<p>Pourquoi me laisser  attendre ? </p>\n<p>Ouvre, c’est un bienfaiteur </p>\n<p>Qui, chez toi, cherche à répandre </p>\n<p>Tous les trésors du bonheur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vraiment !  Vraiment ! C’est sa voix </p>\n<p>C’est lui, c’est lui que je  vois </p>\n<p>Oh !  Ta grâce est la plus forte. </p>\n<p>Mon Sauveur je te reçois, </p>\n<p>Je ne puis fermer la porte </p>\n<p>Quand tu me montres ta  croix. </p>\n','Passant-si-tard-que-veux-tu',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2302,'d70be9a6-6ec6-4ae3-a934-4da06ba2e250',0,0,'Dieu tout Puissant, Dieu de ma délivrance',59,'59_RN_fr','1.\nDieu tout Puissant, Dieu de  ma délivrance,\nJe mets en toit toute mon espérance,\nLes grosses eaux débordent  en fureur\nVeille sur moi, soutient mon  faible cœur. (bis)\n2.\nAh ! Quel ami  pourrait sur cette terre\nMe consoler dans ma douleur amère ?\nOu donc aller, si ce n’est  point à toi ?\nToujours, Seigneur, tes bras\nS’ouvrent pour moi (bis)\n3.\nLes malheureux qui, dans  son infortune,\nS’adresse à toi, jamais ne  t’importune.\nAllez a lui, travailles et  charges,\nVous reviendrez guéris et  soulages.\n4.\nSi je n’avais cette foi  consolante,\nJe trouverais chaque peine  accablante.\nMais puisque Dieu me prête  son secours.\nIl n’est pour moi plus de  fardeau trop lourd.','<p><strong>1.</strong> </p>\n<p>Dieu tout Puissant, Dieu de  ma délivrance, </p>\n<p>Je mets en toit toute mon espérance, </p>\n<p>Les grosses eaux débordent  en fureur </p>\n<p>Veille sur moi, soutient mon  faible cœur. (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ah ! Quel ami  pourrait sur cette terre </p>\n<p>Me consoler dans ma douleur amère ? </p>\n<p>Ou donc aller, si ce n’est  point à toi ? </p>\n<p>Toujours, Seigneur, tes bras </p>\n<p>S’ouvrent pour moi (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Les malheureux qui, dans  son infortune, </p>\n<p>S’adresse à toi, jamais ne  t’importune. </p>\n<p>Allez a lui, travailles et  charges, </p>\n<p>Vous reviendrez guéris et  soulages. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si je n’avais cette foi  consolante, </p>\n<p>Je trouverais chaque peine  accablante. </p>\n<p>Mais puisque Dieu me prête  son secours. </p>\n<p>Il n’est pour moi plus de  fardeau trop lourd. </p>\n','Dieu-tout-Puissant-Dieu-de-ma-delivrance',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2303,'187da1f6-5f76-40a9-8004-3b23881792f8',0,0,'Je viens, Seigneur, à ce baptême',61,'61_RN_fr','1.\nJe viens, Seigneur, à ce baptême,\nOù tu descendis avant moi,\nHeureux de mourir moi-même,\nAfin de vivre comme toi.\n2.\nAux plaisirs, aux gloires  du monde.\nJe dis un eternel  adieu \nJe livre aux profondeurs de  l’onde\nTout ce qui déplait a Dieu.\n3.\nApres tant d’erreurs et  de chutes\nTant de péchés et de remords\nJe renonce à mes vaines  luttes.\nEt me refugie en ta mort.\n4.\nC’est pour moi que tu  l’as souffert\nVictime sainte, Agneau très  doux !\nPour moi ton âme fut offerte\nEt ton corps meurtri par les  clous\n5.\nA ton baptême, a ton  calice,\nJe veux avoir part en ce  jour\nEt gouter, dans le  sacrifice,\nLa douceur de ton grand  amour !','<p><strong>1.</strong> </p>\n<p>Je viens, Seigneur, à ce baptême, </p>\n<p>Où tu descendis avant moi, </p>\n<p>Heureux de mourir moi-même, </p>\n<p>Afin de vivre comme toi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Aux plaisirs, aux gloires  du monde. </p>\n<p>Je dis un eternel  adieu </p>\n<p>Je livre aux profondeurs de  l’onde </p>\n<p>Tout ce qui déplait a Dieu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Apres tant d’erreurs et  de chutes </p>\n<p>Tant de péchés et de remords </p>\n<p>Je renonce à mes vaines  luttes. </p>\n<p>Et me refugie en ta mort. </p>\n<p><br/> <strong>4.</strong></p>\n<p>C’est pour moi que tu  l’as souffert </p>\n<p>Victime sainte, Agneau très  doux ! </p>\n<p>Pour moi ton âme fut offerte </p>\n<p>Et ton corps meurtri par les  clous </p>\n<p><br/> <strong>5.</strong></p>\n<p>A ton baptême, a ton  calice, </p>\n<p>Je veux avoir part en ce  jour </p>\n<p>Et gouter, dans le  sacrifice, </p>\n<p>La douceur de ton grand  amour ! </p>\n','Je-viens-Seigneur-a-ce-bapteme',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2304,'f992b487-32db-4bf3-a207-cbcf999faa4c',0,0,'Le saint consolateur',31,'31_RN_fr','1.\nLe saint  consolateur\nL’envoyé du Seigneur\nEst dans le cœur contrit\nComme libérateur\nAnnoncez la nouvelle\nAux hommes et aux pécheurs\nL’Esprit Saint est venu\nRefrain\nL’Esprit  saint est venu,\nSi longtemps attendu\nNous sentons sur la terre\nLa promesse du Père\nProclamez le message aux\nHommes, d’âge  en âge\nL’Esprit Saint est venu.\n2.\nPauvre  enfant de la nuit\nLe péché  vous poursuit\nRenoncez  au plaisir\nDu monde  qui séduit\nEcoutez  le message\nEn vous  le soleil luit\nL’esprit  Saint est venu.\n3.\nOut tout  est accompli\nDe  l’Esprit sois rempli\nBien que  sa plénitude\nVaincra  l’ennemi\nO  Chrétien c’est pour toi,\nReçois  cd don béni\nL’Esprit  Saint est venu.','<p><strong>1.</strong> </p>\n<p>Le saint  consolateur </p>\n<p>L’envoyé du Seigneur </p>\n<p>Est dans le cœur contrit </p>\n<p>Comme libérateur </p>\n<p>Annoncez la nouvelle </p>\n<p>Aux hommes et aux pécheurs </p>\n<p>L’Esprit Saint est venu </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>L’Esprit  saint est venu,</strong> </p>\n<p><strong>Si longtemps attendu</strong> </p>\n<p><strong>Nous sentons sur la terre</strong> </p>\n<p><strong>La promesse du Père</strong> </p>\n<p><strong>Proclamez le message aux</strong> </p>\n<p><strong>Hommes, d’âge  en âge</strong> </p>\n<p><strong>L’Esprit Saint est venu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pauvre  enfant de la nuit </p>\n<p>Le péché  vous poursuit </p>\n<p>Renoncez  au plaisir </p>\n<p>Du monde  qui séduit </p>\n<p>Ecoutez  le message </p>\n<p>En vous  le soleil luit </p>\n<p>L’esprit  Saint est venu. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Out tout  est accompli </p>\n<p>De  l’Esprit sois rempli </p>\n<p>Bien que  sa plénitude </p>\n<p>Vaincra  l’ennemi </p>\n<p>O  Chrétien c’est pour toi, </p>\n<p>Reçois  cd don béni </p>\n<p>L’Esprit  Saint est venu. </p>\n','Le-saint-consolateur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2305,'097de219-55ee-467c-898c-fec4bdf715d2',0,0,'Je vois les cieux que tu as créés',32,'32_RN_fr','1.\nJe vois les cieux que tu as créés\nJ’entends l’orage et je vois les Etoiles\nIls déclarent tous les pouvoirs que tu as\nRefrain\nMon Rédempteur\nMon âme  chante en toi\nComme tu  es grand !\n2.\nLa nuit, lorsque je marche dans les forets\nJ’entends  les oiseaux chanter les bois\nQuand je  suis sur une haute montagne\nMême  l’eau parle de Dieu avec joie.\n3.\nEn me  rappelant qu’un père qui nous aime\nNous envoie  un roi, son fils unique\nParce  d’épines souffrir, mourir\nPour  tous ceux qui étaient perdus.','<p><strong>1.</strong> </p>\n<p>Je vois les cieux que tu as créés </p>\n<p>J’entends l’orage et je vois les Etoiles </p>\n<p>Ils déclarent tous les pouvoirs que tu as </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon Rédempteur</strong> </p>\n<p><strong>Mon âme  chante en toi</strong> </p>\n<p><strong>Comme tu  es grand !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La nuit, lorsque je marche dans les forets </p>\n<p>J’entends  les oiseaux chanter les bois </p>\n<p>Quand je  suis sur une haute montagne </p>\n<p>Même  l’eau parle de Dieu avec joie. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En me  rappelant qu’un père qui nous aime </p>\n<p>Nous envoie  un roi, son fils unique </p>\n<p>Parce  d’épines souffrir, mourir </p>\n<p>Pour  tous ceux qui étaient perdus. </p>\n','Je-vois-les-cieux-que-tu-as-crees',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2306,'b5e27557-5d69-4e2d-a038-97ebec22d79a',0,0,'A la fin de mon voyage',19,'19_RN_fr','1.\nA la  fin de mon voyage\nOui  je verrai mon Sauveur (bis)\nA la  fin de mon voyage\nOui  je verrai mon Sauveur\nDans  la cite de Dieu\nQuel  Bonheur! Quel Bonheur! (bis)\nA la  fin de mon voyage\nOui  je verrai mon Sauveur\nDans  la cité de Dieu','<p><strong>1.</strong> </p>\n<p>A la  fin de mon voyage </p>\n<p>Oui  je verrai mon Sauveur (bis) </p>\n<p>A la  fin de mon voyage </p>\n<p>Oui  je verrai mon Sauveur </p>\n<p>Dans  la cite de Dieu </p>\n<p>Quel  Bonheur! Quel Bonheur! (bis) </p>\n<p>A la  fin de mon voyage </p>\n<p>Oui  je verrai mon Sauveur </p>\n<p>Dans  la cité de Dieu </p>\n','A-la-fin-de-mon-voyage',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2307,'af82cf1d-586a-4392-b4ac-e94820d143dc',0,0,'Sans Jésus mon Ciel est voilé',23,'23_RN_fr','1.Sans Jésus mon Ciel est  voiléSans Lui tout pour moi  semble durSans Jésus tout perd sa  beautéLe jour le plus clair  semble obscurLe chant des oiseaux n’est  plus douxLes fleurs ne peuvent  m’égayerJe veux Jésus-Christ avant  toutC\'est Lui seul qui peut me charmerRefrainAvec ce Sauveur bien-aiméJe veux demeurer  constammentIl change l’hiver an étéL’automne est un nouveau  printemps2.Son beau non fait vibrer  mon CoeurSa voie semble un écho des  cieuxC’est bien son regard sa  faveurQui bannit les pleurs de  mes yeuxJésus ton amour me sufitLe monde est pour moi sans  attraitCar je suis quand tu me  sourisHeureux en janvier comme  en mai3.Heureux et fière de  témoignerLe non de Jésus en tout  lieuxOh qu’il est doux de le  louerProclamons sa gloire en  tout lieuOh regardons cette humble  croixLà son sang a coulé pour  moiSa main fut percé sur la  croixJésus mouru pour nos  péchés','<p><strong>1.</strong> </p>\n<p>Sans Jésus mon Ciel est voilé </p>\n<p>Sans Lui tout pour moi semble dur </p>\n<p>Sans Jésus tout perd sa beauté </p>\n<p>Le jour le plus clair semble obscur </p>\n<p>Le chant des oiseaux n’est  plus doux </p>\n<p>Les fleurs ne peuvent  m’égayer </p>\n<p>Je veux Jésus-Christ avant  tout </p>\n<p>C&#39;est Lui seul qui peut me charmer </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Avec ce Sauveur bien-aimé</strong> </p>\n<p><strong>Je veux demeurer  constamment</strong> </p>\n<p><strong>Il change l’hiver an été</strong> </p>\n<p><strong>L’automne est un nouveau  printemps</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Son beau nom fait vibrer  mon Coeur </p>\n<p>Sa voie semble un écho des  cieux </p>\n<p>C’est bien son regard sa  faveur </p>\n<p>Qui bannit les pleurs de  mes yeux </p>\n<p>Jésus ton amour me sufit </p>\n<p>Le monde est pour moi sans  attrait </p>\n<p>Car je suis quand tu me  souris </p>\n<p>Heureux en janvier comme  en mai </p>\n<p><br/> <strong>3.</strong></p>\n<p>Heureux et fière de  témoigner </p>\n<p>Le nom de Jésus en tout  lieux </p>\n<p>Oh qu’il est doux de le  louer </p>\n<p>Proclamons sa gloire en  tout lieu </p>\n<p>Oh regardons cette humble  croix </p>\n<p>Là son sang a coulé pour  moi </p>\n<p>Sa main fut percé sur la  croix </p>\n<p>Jésus mourut pour nos  péchés </p>\n','Sans-Jesus-mon-Ciel-est-voile',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2308,'e4c65645-2743-4a86-8d76-478d9dc9ac4d',0,0,'Longeant péniblement',14,'14_RN_fr','1.\nLongeant péniblement\nLa route de Jéricho\nLa vie devenait noire\nEt lourd mon fardeau\nJésus Christ vint vers moi\nDès lors me fis grâce\nMe donna l’asurrance\nQu’au Ciel est ma place\n\nRefrain\nChemin de Jéricho\nChemin du Calvaire\nSentier ou l’abri\nPure et salutaire\nFait cesser nos maux\nEt tarir nos pleurs\nAmène aux perdus\nLeur Dieu, leur Sauveur\n\n2.\nAinsi à Jéricho\nS’asseyait Bartimé\nAveugle et misérable\nEt l’âme assoiffée\nMais Christ passait soudain\nIl cria plein de foi:\n“Jésus de Nazareth,\nAi pitié de moi”\n\n3.\nEt le Seigneur Soudain\nL’appela, le benit\nEt lui rendit la vue\nPour lui plus de nuit!\nPlus d’ombre plus de faim!\nDe soif, de misère\nIl vient de rencontrer\nL’écho du calvaire\n\n4.\nDe même qu’un seul mot\nBannit la cécité\nDe même qu’un seul mot\nOte les péchés\nSi tu t’aventures,\nEnfin sur la route\nDe Jéricho sans peur\nS’enfuiera le doute','<p><strong>1.</strong> </p>\n<p>Longeant péniblement </p>\n<p>La route de Jéricho </p>\n<p>La vie devenait noire </p>\n<p>Et lourd mon fardeau </p>\n<p>Jésus Christ vint vers moi </p>\n<p>Dès lors me fis grâce </p>\n<p>Me donna l’asurrance </p>\n<p>Qu’au Ciel est ma place </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chemin de Jéricho</strong> </p>\n<p><strong>Chemin du Calvaire</strong> </p>\n<p><strong>Sentier ou l’abri</strong> </p>\n<p><strong>Pure et salutaire</strong> </p>\n<p><strong>Fait cesser nos maux</strong> </p>\n<p><strong>Et tarir nos pleurs</strong> </p>\n<p><strong>Amène aux perdus</strong> </p>\n<p><strong>Leur Dieu, leur Sauveur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ainsi à Jéricho </p>\n<p>S’asseyait Bartimé </p>\n<p>Aveugle et misérable </p>\n<p>Et l’âme assoiffée </p>\n<p>Mais Christ passait soudain </p>\n<p>Il cria plein de foi: </p>\n<p>“Jésus de Nazareth, </p>\n<p>Ai pitié de moi” </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et le Seigneur Soudain </p>\n<p>L’appela, le benit </p>\n<p>Et lui rendit la vue </p>\n<p>Pour lui plus de nuit! </p>\n<p>Plus d’ombre plus de faim! </p>\n<p>De soif, de misère </p>\n<p>Il vient de rencontrer </p>\n<p>L’écho du calvaire </p>\n<p><br/> <strong>4.</strong></p>\n<p>De même qu’un seul mot </p>\n<p>Bannit la cécité </p>\n<p>De même qu’un seul mot </p>\n<p>Ote les péchés </p>\n<p>Si tu t’aventures, </p>\n<p>Enfin sur la route </p>\n<p>De Jéricho sans peur </p>\n<p>S’enfuiera le doute </p>\n','Longeant-peniblement',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2309,'7c36ce88-14f2-45ab-9055-11a4c0dd05db',0,0,'Du maitre qui m’appelle',13,'13_RN_fr','1.\nDu maître qui m’appelle\nA le suivre ici-bas\nJ’entends la voix fidèle\nQui me dit: “Ne crains rien pas\nL’ennemi te menace:\nJe suis le tout-puissant\nJe verse en toi ma grâce\nMoi le vivant\n\nRefrain\nIl vit, Il vit Jésus vit à jamais\nIl vit au ciel, Il vit en moi\nLui le Prince de paix\nIl vit, Il vit me regarde, me conduit\nEt par Lui, je marche en vainqueur\nCar il vit dans mon Coeur\n2.\nIl vit et rien au monde\nNe saurait me ravir\nLe Bonheur qui m’inonde\nEt m’aide à le servir\nEn lui j’ai force et joie\nTout le long du chemin\nOu son amour m’envoie\nMoi faible humain!\n3.\nTressaille d’allégresse\nRacheté de Jésus\nRetentir sans cesse\nL’hymne saint des elus\nOh! Sublime Espérance\nMerveilleuse assurance\nIl va venir, rien ne peux tenir ma foi','<p><strong>1.</strong> </p>\n<p>Du maître qui m’appelle </p>\n<p>A le suivre ici-bas </p>\n<p>J’entends la voix fidèle </p>\n<p>Qui me dit: “Ne crains rien pas </p>\n<p>L’ennemi te menace: </p>\n<p>Je suis le tout-puissant </p>\n<p>Je verse en toi ma grâce </p>\n<p>Moi le vivant </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il vit, Il vit Jésus vit à jamais</strong> </p>\n<p><strong>Il vit au ciel, Il vit en moi</strong> </p>\n<p><strong>Lui le Prince de paix</strong> </p>\n<p><strong>Il vit, Il vit me regarde, me conduit</strong> </p>\n<p><strong>Et par Lui, je marche en vainqueur</strong> </p>\n<p><strong>Car il vit dans mon Coeur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il vit et rien au monde </p>\n<p>Ne saurait me ravir </p>\n<p>Le Bonheur qui m’inonde </p>\n<p>Et m’aide à le servir </p>\n<p>En lui j’ai force et joie </p>\n<p>Tout le long du chemin </p>\n<p>Ou son amour m’envoie </p>\n<p>Moi faible humain! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Tressaille d’allégresse </p>\n<p>Racheté de Jésus </p>\n<p>Retentir sans cesse </p>\n<p>L’hymne saint des elus </p>\n<p>Oh! Sublime Espérance </p>\n<p>Merveilleuse assurance </p>\n<p>Il va venir, rien ne peux tenir ma foi </p>\n','Du-maitre-qui-m\'appelle',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2310,'90b4828f-0652-4d8e-8581-74556628f81c',0,0,'L’amour de Dieu est trop sublime',16,'16_RN_fr','1.\nL’amour  de Dieu est trop sublime\nPour  le langage de la voix\nIl  est plus haut que les etoiles\nEt  que l’abime le plus profound\nDieu  nous donna le Redempteur\nQui  soufrit sur la croix\nSon  fils unique vint mourir\nCe  bien-aime du Dieu bon\n\nRefrain\nAmour  de Dieu, puissant et tendre\nInexprimable  bonté, alleluia\nC’est  le cantique\nDu  bien heureux racheté\n2.\nO  pour écrire cet amour\nLes  Cieux ne sont pas assez\nL’océan  d’encre ne suffit\nPour  contenir ces mots d’amour\nSi  chaque tige était une plume\nEcrivains  grands et petits\nEnsemble  toutes leurs louanges\nNe  sont que l’ombre d’un jour\n3.\nDe  même qu’il est difficile\nDe  bien pouvoir rimer ces vers\nDe  même frère est-il facile\nDe  sonder cette charité\nJe  suis sauvé, quelle pensée\nPlus  profonde que la mer\nDès  ici-bas vie éternelle\nGloire  au Christ réssuscité\n4.\nLorsque  la nuit ne sera plus\nQuand  les royaumes passeront\nQuand  les moqueurs prendront la fuite\nVers  les collines, les rochers\nL’amour  de Dieu demeura\nToujours  fidele et profond\nLes  rejetons: Adam et Eve\nAuront  été rachetés','<p><strong>1.</strong> </p>\n<p>L’amour  de Dieu est trop sublime </p>\n<p>Pour  le langage de la voix </p>\n<p>Il  est plus haut que les etoiles </p>\n<p>Et  que l’abime le plus profound </p>\n<p>Dieu  nous donna le Redempteur </p>\n<p>Qui  soufrit sur la croix </p>\n<p>Son  fils unique vint mourir </p>\n<p>Ce  bien-aime du Dieu bon </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Amour  de Dieu, puissant et tendre</strong> </p>\n<p><strong>Inexprimable  bonté, alleluia</strong> </p>\n<p><strong>C’est  le cantique</strong> </p>\n<p><strong>Du  bien heureux racheté</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O  pour écrire cet amour </p>\n<p>Les  Cieux ne sont pas assez </p>\n<p>L’océan  d’encre ne suffit </p>\n<p>Pour  contenir ces mots d’amour </p>\n<p>Si  chaque tige était une plume </p>\n<p>Ecrivains  grands et petits </p>\n<p>Ensemble  toutes leurs louanges </p>\n<p>Ne  sont que l’ombre d’un jour </p>\n<p><br/> <strong>3.</strong></p>\n<p>De  même qu’il est difficile </p>\n<p>De  bien pouvoir rimer ces vers </p>\n<p>De  même frère est-il facile </p>\n<p>De  sonder cette charité </p>\n<p>Je  suis sauvé, quelle pensée </p>\n<p>Plus  profonde que la mer </p>\n<p>Dès  ici-bas vie éternelle </p>\n<p>Gloire  au Christ réssuscité </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lorsque  la nuit ne sera plus </p>\n<p>Quand  les royaumes passeront </p>\n<p>Quand  les moqueurs prendront la fuite </p>\n<p>Vers  les collines, les rochers </p>\n<p>L’amour  de Dieu demeura </p>\n<p>Toujours  fidele et profond </p>\n<p>Les  rejetons: Adam et Eve </p>\n<p>Auront  été rachetés </p>\n','L\'amour-de-Dieu-est-trop-sublime',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2311,'cd65bc46-7d35-4551-aa9b-523b92d5f317',0,0,'De la poussière de la terre',30,'30_RN_fr','1.\nDe la poussière de la terre\nMon Dieu fit sortir l’homme\nSon souffle lui donna une âme vivante\nEt Dieu a tant aime le monde\nQu’il a donné son fils unique\nVoilà pourquoi je l’aime  tant\nRefrain\nJe suis créé à son image\nOui bien à son image\nJe suis sur terre pour le servir\nJamais je ne le renierai\nJe Marcherai toujours près de lui.\nCar il m’a créé pour le servir.\n2.\nS’il m’a donné mes mains\nC’est pour aider mes frères\nMes yeux pour lire sa parole\nMes pieds pour marcher dans le droit chemin\nMon corps est le temple du Seigneur\n3.\nMon corps, mon cœur, mon âme\nNe m’appartiennent plus\nIls sont au service du Seigneur\nLes années pourront s’enfuir\nLes jours succéder au jour\nToujours je chanterai ses louanges','<p><strong>1.</strong> </p>\n<p>De la poussière de la terre </p>\n<p>Mon Dieu fit sortir l’homme </p>\n<p>Son souffle lui donna une âme vivante </p>\n<p>Et Dieu a tant aime le monde </p>\n<p>Qu’il a donné son fils unique </p>\n<p>Voilà pourquoi je l’aime  tant </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je suis créé à son image</strong> </p>\n<p><strong>Oui bien à son image</strong> </p>\n<p><strong>Je suis sur terre pour le servir</strong> </p>\n<p><strong>Jamais je ne le renierai</strong> </p>\n<p><strong>Je Marcherai toujours près de lui.</strong> </p>\n<p><strong>Car il m’a créé pour le servir.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>S’il m’a donné mes mains </p>\n<p>C’est pour aider mes frères </p>\n<p>Mes yeux pour lire sa parole </p>\n<p>Mes pieds pour marcher dans le droit chemin </p>\n<p>Mon corps est le temple du Seigneur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mon corps, mon cœur, mon âme </p>\n<p>Ne m’appartiennent plus </p>\n<p>Ils sont au service du Seigneur </p>\n<p>Les années pourront s’enfuir </p>\n<p>Les jours succéder au jour </p>\n<p>Toujours je chanterai ses louanges </p>\n','De-la-poussiere-de-la-terre',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2312,'6949c05e-a85e-4928-a4f2-39ef06085f6e',0,0,'Chantons Notre Bonheur',12,'12_RN_fr','1.\nChantons notre bonheur\nEn des accents joyeux\nNous, les enfants du père\nCar nos noms sont inscrits\nDans le livre des cieux\nPar le sang du calvaire.\n\nRefrain\nAlléluia, chantons en chœur,\nCar le seigneur nous aime\nOui le seigneur nous aime\nAlléluia, c’est le bonheur.\n\n2.\nCe monde n’est pour nous\nQu’un pays étranger\nOu notre chemin passe\nEt nous le traversons, conduit par le berger\nOui nous comble de grâces.\n\n3.\nTravail, peine et douleur\nEt larmes ici-bas\nSombre nuit d’orage\nRien ne saurait nous tenter\nDe notre beau voyage\nEt détourner nos pas\n\n4.\nEt, s’il nous faut passer\nA l’ombre de la mort\nA tant d’autres funestes\nNous verrons, le seigneur\nOuvrir la porte d’or\nDe la cite céleste.','<p><strong>1.</strong> </p>\n<p>Chantons notre bonheur </p>\n<p>En des accents joyeux </p>\n<p>Nous, les enfants du père </p>\n<p>Car nos noms sont inscrits </p>\n<p>Dans le livre des cieux </p>\n<p>Par le sang du calvaire. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Alléluia, chantons en chœur,</strong> </p>\n<p><strong>Car le seigneur nous aime</strong> </p>\n<p><strong>Oui le seigneur nous aime</strong> </p>\n<p><strong>Alléluia, c’est le bonheur.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ce monde n’est pour nous </p>\n<p>Qu’un pays étranger </p>\n<p>Ou notre chemin passe </p>\n<p>Et nous le traversons, conduit par le berger </p>\n<p>Oui nous comble de grâces. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Travail, peine et douleur </p>\n<p>Et larmes ici-bas </p>\n<p>Sombre nuit d’orage </p>\n<p>Rien ne saurait nous tenter </p>\n<p>De notre beau voyage </p>\n<p>Et détourner nos pas </p>\n<p><br/> <strong>4.</strong></p>\n<p>Et, s’il nous faut passer </p>\n<p>A l’ombre de la mort </p>\n<p>A tant d’autres funestes </p>\n<p>Nous verrons, le seigneur </p>\n<p>Ouvrir la porte d’or </p>\n<p>De la cite céleste. </p>\n','Chantons-Notre-Bonheur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2313,'3b659189-a501-4c97-ac65-d6e699e73033',0,0,'Grand en fidélité O Dieu mon père',24,'24_RN_fr','1.\nGrand en fidélité O Dieu  mon père\nChez nulle trâce de  changement\nTes compassions ne sont  pas à leurs terme\nTu restes fidèle  éternellement\n\nRefrain\nGrand en fidélité (bis)\nJour après jour tu me  tiens par la foi\nTu as pourvu chaque jour a  mes besoins\nGrand en fidelite toi  envers moi\n2.\nL’été et l’hiver, les  saisons fertiles\nLes astres brillants que  tu as créés\nSe joignent avec la nature  entière\nPour témoigner à ta  fidelite\n3.\nPardon du péché, paix douce  et profonde\nTa sainte présence pour me  guider\nForce aujourd’hui pour\nDemain Espérance\nMe rassurent de ta fidélité','<p><strong>1.</strong> </p>\n<p>Grand en fidélité O Dieu  mon père </p>\n<p>Chez nulle trâce de  changement </p>\n<p>Tes compassions ne sont  pas à leurs terme </p>\n<p>Tu restes fidèle  éternellement </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Grand en fidélité (bis)</strong> </p>\n<p><strong>Jour après jour tu me  tiens par la foi</strong> </p>\n<p><strong>Tu as pourvu chaque jour a  mes besoins</strong> </p>\n<p><strong>Grand en fidelite toi  envers moi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>L’été et l’hiver, les  saisons fertiles </p>\n<p>Les astres brillants que  tu as créés </p>\n<p>Se joignent avec la nature  entière </p>\n<p>Pour témoigner à ta  fidelite </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pardon du péché, paix douce  et profonde </p>\n<p>Ta sainte présence pour me  guider </p>\n<p>Force aujourd’hui pour </p>\n<p>Demain Espérance </p>\n<p>Me rassurent de ta fidélité </p>\n','Grand-en-fidelite-O-Dieu-mon-pere',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2314,'3bb6a1ba-9003-4f76-8e63-eb600f88457d',0,0,'Levez-vous témoins de la Bible',33,'33_RN_fr','1.\nLevez-vous  témoins de la Bible\nPriez et  luttez en tout lieu\nProclamez  qu’elle est infaillible\nLa  parole de notre Dieu.','<p><strong>1.</strong> </p>\n<p>Levez-vous  témoins de la Bible </p>\n<p>Priez et  luttez en tout lieu </p>\n<p>Proclamez  qu’elle est infaillible </p>\n<p>La  parole de notre Dieu. </p>\n','Levez-vous-temoins-de-la-Bible',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2315,'eda96f24-d887-4fa0-b1d1-679cf8686768',0,0,'Seigneur ce que mon cœur désire',35,'35_RN_fr','1.\nSeigneur  ce que mon cœur désire\nC’est de t’avoir pour mon maître précieux (bis)\nJe t’aurai  pour mon seul maître\nComme un  guide sur les flots (bis)\nRefrain\nTu es le  Gouvernail\nEt moi  le bateau,\nTu es le  Capitaine\nEt moi  le bateau\n2.\nA  travers les mers dures Seigneur Jésus\nSois  pour moi un guide précieux\n3.\nAu  moment de deuil, de calamité\nSois pour moi Jésus un guide précieux.','<p><strong>1.</strong> </p>\n<p>Seigneur  ce que mon cœur désire </p>\n<p>C’est de t’avoir pour mon maître précieux (bis) </p>\n<p>Je t’aurai  pour mon seul maître </p>\n<p>Comme un  guide sur les flots (bis) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Tu es le  Gouvernail</strong> </p>\n<p><strong>Et moi  le bateau,</strong> </p>\n<p><strong>Tu es le  Capitaine</strong> </p>\n<p><strong>Et moi  le bateau</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A  travers les mers dures Seigneur Jésus </p>\n<p>Sois  pour moi un guide précieux </p>\n<p><br/> <strong>3.</strong></p>\n<p>Au  moment de deuil, de calamité </p>\n<p>Sois pour moi Jésus un guide précieux. </p>\n','Seigneur-ce-que-mon-coeur-desire',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2316,'843ca74b-e050-4e0a-ba2a-1db58e3c31c3',0,0,'Nous allons faire le tour',43,'43_RN_fr','1.\nNous  allons faire le tour\nDe la ville,  de la ville\nNous  allons faire le tour\nDe la  ville de Jéricho\nRefrain\nJéricho,  Jéricho il faut que les murs tombent\nJéricho,  Jéricho Josué nous l’a dit\n2.\nApres le  premier jour \nLes  murailles, Les murailles\nApres le  premier jour\nLes  murailles n’ont pas bouge\n3.\nApres le  deuxième jour\nLes  murailles, les murailles\nApres le  deuxième jour\nLes  murailles n’ont pas bouge\n4.\nApres le  septième jour,\nLes  murailles, les murailles\nApres  les le septième jour\nD’un  seul coup tout s’est écroulé\n Refrain\nJéricho,  Jéricho tes grands murs sont en ruine\nJéricho,  Jéricho Dieu nous l’avait promis','<p><strong>1.</strong> </p>\n<p>Nous  allons faire le tour </p>\n<p>De la ville,  de la ville </p>\n<p>Nous  allons faire le tour </p>\n<p>De la  ville de Jéricho </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jéricho,  Jéricho il faut que les murs tombent</strong> </p>\n<p><strong>Jéricho,  Jéricho Josué nous l’a dit</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Apres le  premier jour </p>\n<p>Les  murailles, Les murailles </p>\n<p>Apres le  premier jour </p>\n<p>Les  murailles n’ont pas bouge </p>\n<p><br/> <strong>3.</strong></p>\n<p>Apres le  deuxième jour </p>\n<p>Les  murailles, les murailles </p>\n<p>Apres le  deuxième jour </p>\n<p>Les  murailles n’ont pas bouge </p>\n<p><br/> <strong>4.</strong></p>\n<p>Apres le  septième jour, </p>\n<p>Les  murailles, les murailles </p>\n<p>Apres  les le septième jour </p>\n<p>D’un  seul coup tout s’est écroulé </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jéricho,  Jéricho tes grands murs sont en ruine</strong> </p>\n<p><strong>Jéricho,  Jéricho Dieu nous l’avait promis</strong> </p>\n','Nous-allons-faire-le-tour',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2317,'85a176a3-dcca-4ade-94d6-45bf59964a41',0,0,'C’est dans l’évangile qu’on trouve',47,'47_RN_fr','1.\nC’est dans l’évangile qu’on trouve\nLa paix, le pardon, le bonheur, la vie.\nOuvrons notre cœur a la grâce infinie,\nDonnons tous nos biens aux pécheurs.\n\nRefrain\nAuprès du Seigneur avec lui sur la route,\nMarchons de tout cœur en chantant,\nIl sauve, il guérit, puis enlevé le doute\nIl vit, Il est la maintenant\n\n2.\nIl montre des endroits pour jeter la souillure.\nVoila c’est ici Golgotha\nUn lieu de folie, un endroit de rupture.\nMais crois, ce pardon est bien là.\n3.\nSouvent en chemin, il vient nous entrainer\nAu sentier de la vérité \nPendant que l’amour de Jésus nous entraine,\nAu règne de l’éternité.','<p><strong>1.</strong> </p>\n<p>C’est dans l’évangile qu’on trouve </p>\n<p>La paix, le pardon, le bonheur, la vie. </p>\n<p>Ouvrons notre cœur a la grâce infinie, </p>\n<p>Donnons tous nos biens aux pécheurs. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Auprès du Seigneur avec lui sur la route,</strong> </p>\n<p><strong>Marchons de tout cœur en chantant,</strong> </p>\n<p><strong>Il sauve, il guérit, puis enlevé le doute</strong> </p>\n<p><strong>Il vit, Il est la maintenant</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il montre des endroits pour jeter la souillure. </p>\n<p>Voila c’est ici Golgotha </p>\n<p>Un lieu de folie, un endroit de rupture. </p>\n<p>Mais crois, ce pardon est bien là. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Souvent en chemin, il vient nous entrainer </p>\n<p>Au sentier de la vérité </p>\n<p>Pendant que l’amour de Jésus nous entraine, </p>\n<p>Au règne de l’éternité. </p>\n','C\'est-dans-l\'evangile-qu\'on-trouve',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2318,'5c9a283f-3563-4d41-8d37-7d5ffd8aea47',0,0,'Je chanterai ton grand amour',53,'53_RN_fr','1.\nJe chanterai ton grand amour\nL’hiver, l’été, la nuit, le  jour,\nCar cet amour est éternel,\nSi grand et pourtant si réel  (bis)\nCHŒUR\n2.\nJe chanterai dès le matin\nQuand tout est clair sur mon  chemin.\nJe veux aussi chanter le  soir,\nQuand la nuit vient, quand  tout est noir (bis)\n3.\nJe veux chanter sans me  lasser\nTu m’as aimé, toi mon  berger,\nJ’ai pleuré, tu m’as consolé\nJ’étais perdu, tu m’as  trouvé (bis)\n4.\nJe chanterai quand ton  enfant\nEst menacé de l’ouragan,\nCar vents et flots sont mesurés,\nEt par ta main sont diriges.\n5.\nOh chantons donc jusqu’au  grand jour\nOù réunis dans ton amour,\nGrand Dieu ! Nous  verrons tes splendeurs\nEt nous le dirons de tous  nos cœurs (bis)','<p><strong>1.</strong> </p>\n<p>Je chanterai ton grand amour </p>\n<p>L’hiver, l’été, la nuit, le  jour, </p>\n<p>Car cet amour est éternel, </p>\n<p>Si grand et pourtant si réel  (bis) </p>\n<p>CHŒUR </p>\n<p><br/> <strong>2.</strong></p>\n<p>Je chanterai dès le matin </p>\n<p>Quand tout est clair sur mon  chemin. </p>\n<p>Je veux aussi chanter le  soir, </p>\n<p>Quand la nuit vient, quand  tout est noir (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je veux chanter sans me  lasser </p>\n<p>Tu m’as aimé, toi mon  berger, </p>\n<p>J’ai pleuré, tu m’as consolé </p>\n<p>J’étais perdu, tu m’as  trouvé (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je chanterai quand ton  enfant </p>\n<p>Est menacé de l’ouragan, </p>\n<p>Car vents et flots sont mesurés, </p>\n<p>Et par ta main sont diriges. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Oh chantons donc jusqu’au  grand jour </p>\n<p>Où réunis dans ton amour, </p>\n<p>Grand Dieu ! Nous  verrons tes splendeurs </p>\n<p>Et nous le dirons de tous  nos cœurs (bis) </p>\n','Je-chanterai-ton-grand-amour',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2319,'0084e552-72c8-4597-8af3-8c84c3609605',0,0,'Vivant sur cette terre de péché',15,'15_RN_fr','1.\nVivant  sur cette terre de péché\nde confort  de Bonheur prive\nLutant  contre l’adversaire Obstiné\nQui  peut m’aider si non Jesus\nRefrain\nA qui  crier? Oh, à qui aller?\nPour  un refuge sur trouver\nAyant  besoins d’un ami pour m’aider\nQui  prendre si non Jésus-Christ\n2.\nLes  amis sont gentils et je les aiment\nNous  vivant en de très bon termes\nMais  quand mon âme\nA  soif de paix suprême\nQui  peut m’aider, si non Jésus\n\n3.\nLa  vie est belle auprès de bons parents\nQui  m’entourent bien tendrement\nMais  de la mort, Quand mugit le torrent\nQui  peut m’aider si non Jésus','<p><strong>1.</strong> </p>\n<p>Vivant  sur cette terre de péché </p>\n<p>de confort  de Bonheur prive </p>\n<p>Lutant  contre l’adversaire Obstiné </p>\n<p>Qui  peut m’aider si non Jesus </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>A qui  crier? Oh, à qui aller?</strong> </p>\n<p><strong>Pour  un refuge sur trouver</strong> </p>\n<p><strong>Ayant  besoins d’un ami pour m’aider</strong> </p>\n<p><strong>Qui  prendre si non Jésus-Christ</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les  amis sont gentils et je les aiment </p>\n<p>Nous  vivant en de très bon termes </p>\n<p>Mais  quand mon âme </p>\n<p>A  soif de paix suprême </p>\n<p>Qui  peut m’aider, si non Jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>La  vie est belle auprès de bons parents </p>\n<p>Qui  m’entourent bien tendrement </p>\n<p>Mais  de la mort, Quand mugit le torrent </p>\n<p>Qui  peut m’aider si non Jésus </p>\n','Vivant-sur-cette-terre-de-peche',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2320,'1ed51f36-9d52-40ed-9e2f-535111b93a0b',0,0,'Je suis un débiteur',26,'26_RN_fr','1.\nJe suis un débiteur\nChaque jour de ma vie\nC’est pourquoi O seigneur\nJe chante et je prie\nJe n’ai rien ni argent\nNi or pour donner\nMais l’amour de Dieu\nJe veux confesser\nRefrain\nJe suis un pauvre debiteur  insolvable\nCar le prix est rop grand\nQui peux payer?\nIl s’est donner pour moi\nMon Sauveur adorable\nMon divin Redempteur\nJe veux le chanter\n2.\nIndigne d’etre appele\nUn enfant de enfant de  Dieu\nDans l’ereur le peche\nJ’errais malheureux\nMais au seigneur Jesus\nJ’ai donne ma vie\nJe suis son debiteur\nQuelle paix benie\n3.\nQuand est t’il de ton âme\nN’as tu pas levé\nDans le sang de l’agneau\nTon iniquité\nIl est mort pour blanchir\nÔter ta souillure\nTe cherir et ta tête orner  de parure','<p><strong>1.</strong> </p>\n<p>Je suis un débiteur </p>\n<p>Chaque jour de ma vie </p>\n<p>C’est pourquoi O seigneur </p>\n<p>Je chante et je prie </p>\n<p>Je n’ai rien ni argent </p>\n<p>Ni or pour donner </p>\n<p>Mais l’amour de Dieu </p>\n<p>Je veux confesser </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je suis un pauvre debiteur  insolvable</strong> </p>\n<p><strong>Car le prix est rop grand</strong> </p>\n<p><strong>Qui peux payer?</strong> </p>\n<p><strong>Il s’est donner pour moi</strong> </p>\n<p><strong>Mon Sauveur adorable</strong> </p>\n<p><strong>Mon divin Redempteur</strong> </p>\n<p><strong>Je veux le chanter</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Indigne d’etre appele </p>\n<p>Un enfant de enfant de  Dieu </p>\n<p>Dans l’ereur le peche </p>\n<p>J’errais malheureux </p>\n<p>Mais au seigneur Jesus </p>\n<p>J’ai donne ma vie </p>\n<p>Je suis son debiteur </p>\n<p>Quelle paix benie </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand est t’il de ton âme </p>\n<p>N’as tu pas levé </p>\n<p>Dans le sang de l’agneau </p>\n<p>Ton iniquité </p>\n<p>Il est mort pour blanchir </p>\n<p>Ôter ta souillure </p>\n<p>Te cherir et ta tête orner  de parure </p>\n','Je-suis-un-debiteur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2321,'c9dca2fc-777d-49fc-ba3d-642ea3e4e346',0,0,'Céleste voix, tu nous convies',62,'62_RN_fr','1.\nCéleste voix, tu nous  convies\nAu festin de la charité \nTu remplis nos âmes ravies\nD’une sainte félicité.\nNous venons, Seigneur, à ta  table,\nOù tu veux nourrir notre foi,\nPour ôter ta grâce ineffable\nEt pour nous consacrer a toi\n2.\n \nApprochez, âmes accablées\nSous le fardeau de vos péchés \nApprochez, soyez consolées\nVoici les biens que vous  cherchez.\nNe craignez point, Jésus  pardonne\nAu cœur contrit, et pénitent,\nEt la gloire qui l’environne\nN’empêche pas qu’il soit clément.\n3.\nVous qu’abandonne l’espérance,\nVous qui marchez triste et  las,\nVous dont le deuil ou  l’indigence\nEst la lourde part ici-bas,\nApprochez : Jésus voit  vos larmes \nQu’elles coulent plus  doucement !\nLe Dieu qui connait vos  larmes\nSe donne à vous en ce  moment.\n4.\nChrétiens, célébrons la  mémoire\nD’un bienfaiteur si généreux,\nJusqu’au jour ou, brillant  de gloire,\nIl viendra nous ouvrir les  cieux.\nLa nous chanterons les  louanges\nDevant le trône du Dieu  fort,\nO Roi des hommes et des  anges\nQui nous a sauves par la  mort.','<p><strong>1.</strong> </p>\n<p>Céleste voix, tu nous  convies </p>\n<p>Au festin de la charité </p>\n<p>Tu remplis nos âmes ravies </p>\n<p>D’une sainte félicité. </p>\n<p>Nous venons, Seigneur, à ta  table, </p>\n<p>Où tu veux nourrir notre foi, </p>\n<p>Pour ôter ta grâce ineffable </p>\n<p>Et pour nous consacrer a toi </p>\n<p><br/> <strong>2.</strong></p>\n<p>Approchez, âmes accablées </p>\n<p>Sous le fardeau de vos péchés </p>\n<p>Approchez, soyez consolées </p>\n<p>Voici les biens que vous  cherchez. </p>\n<p>Ne craignez point, Jésus  pardonne </p>\n<p>Au cœur contrit, et pénitent, </p>\n<p>Et la gloire qui l’environne </p>\n<p>N’empêche pas qu’il soit clément. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vous qu’abandonne l’espérance, </p>\n<p>Vous qui marchez triste et  las, </p>\n<p>Vous dont le deuil ou  l’indigence </p>\n<p>Est la lourde part ici-bas, </p>\n<p>Approchez : Jésus voit  vos larmes </p>\n<p>Qu’elles coulent plus  doucement ! </p>\n<p>Le Dieu qui connait vos  larmes </p>\n<p>Se donne à vous en ce  moment. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Chrétiens, célébrons la  mémoire </p>\n<p>D’un bienfaiteur si généreux, </p>\n<p>Jusqu’au jour ou, brillant  de gloire, </p>\n<p>Il viendra nous ouvrir les  cieux. </p>\n<p>La nous chanterons les  louanges </p>\n<p>Devant le trône du Dieu  fort, </p>\n<p>O Roi des hommes et des  anges </p>\n<p>Qui nous a sauves par la  mort. </p>\n','Celeste-voix-tu-nous-convies',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2322,'69ee6f0b-23bd-4dba-9af3-eb91e5d096af',0,0,'Que l’Eternel nous bénisse',71,'71_RN_fr','1.\nQue l’Eternel nous bénisse,\nEt qu’il nous garde !\nQue l’Eternel fasse luire\nSa face sur nous,\nEt qu’il nous accorde sa grâce !  (bis)\nQue l’Eternel tourne sa face  ver nous,\nEt nous donne la paix (bis)','<p><strong>1.</strong> </p>\n<p>Que l’Eternel nous bénisse, </p>\n<p>Et qu’il nous garde ! </p>\n<p>Que l’Eternel fasse luire </p>\n<p>Sa face sur nous, </p>\n<p>Et qu’il nous accorde sa grâce !  (bis) </p>\n<p>Que l’Eternel tourne sa face  ver nous, </p>\n<p>Et nous donne la paix (bis) </p>\n','Que-l\'Eternel-nous-benisse',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2323,'c0982804-81ff-4b1c-8170-db10bf8b1f9a',0,0,'Nous reclamons Seigneur ta presence',18,'18_RN_fr','1.\nNous  réclamons Seigneur ta présence\nDe  ton Esprit le feu la puissance\nViens  et demeure au milieu de nous\nBénis  ce rendez-vous\n\nRefrain\nParle  bien-aimé Maitre\nOh!  Oui parle à nos coeurs\nTa  grâce fait paraître\nVerse en nous tes faveurs\n2.\nViens-nous  donner selon ta promesse\nPaix et Bonheur, parfaite allégresse\nFais abonder en nous chaque jour\nTa  grâce ton amour\n3.\nMets  sur nos yeux ton divin Collyre\nPlace  nos coeurs sous ton doux empire\nQue  t’aimer et de t’obéir\nSois  notre seul désir\n4.\nAlors par nous le flot salutaire\nDe  ton amour coulant au calvaire\nSe  répendra parmi les perdus\nPour ta gloire O Jesus','<p><strong>1.</strong> </p>\n<p>Nous  réclamons Seigneur ta présence </p>\n<p>De  ton Esprit le feu la puissance </p>\n<p>Viens  et demeure au milieu de nous </p>\n<p>Bénis  ce rendez-vous </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Parle  bien-aimé Maitre</strong> </p>\n<p><strong>Oh!  Oui parle à nos coeurs</strong> </p>\n<p><strong>Ta  grâce fait paraître</strong> </p>\n<p><strong>Verse en nous tes faveurs</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Viens-nous  donner selon ta promesse </p>\n<p>Paix et Bonheur, parfaite allégresse </p>\n<p>Fais abonder en nous chaque jour </p>\n<p>Ta  grâce ton amour </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mets  sur nos yeux ton divin Collyre </p>\n<p>Place  nos coeurs sous ton doux empire </p>\n<p>Que  t’aimer et de t’obéir </p>\n<p>Sois  notre seul désir </p>\n<p><br/> <strong>4.</strong></p>\n<p>Alors par nous le flot salutaire </p>\n<p>De  ton amour coulant au calvaire </p>\n<p>Se  répendra parmi les perdus </p>\n<p>Pour ta gloire O Jesus </p>\n','Nous-reclamons-Seigneur-ta-presence',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2324,'fb5a5579-8f34-414f-996c-ad8487a9b5d2',0,0,'Sans Jésus, Je ne pourrai rien faire',41,'41_RN_fr','1.\nSans  Jésus, Je ne pourrai rien faire\nSans  Jésus, J’aurais failli\nSans  Jésus, je serais perdu\nComme un  bateau qui fait naufrage\nRefrain\nJésus  Oh ! Jésus, le connais tu Aujourd’hui\nNe te  détourne pas de lui Oh Jésus\nOh !  Jésus sans le Seigneur\nHélas !  Je serais perdu\n2.\nSans  Jésus ma vie serait bien triste\nSans  Jésus, je serai esclave\nSans  jésus tout est désespéré\nMais  avec Lui, Gloire à Dieu je suis sauve','<p><strong>1.</strong> </p>\n<p>Sans  Jésus, Je ne pourrai rien faire </p>\n<p>Sans  Jésus, J’aurais failli </p>\n<p>Sans  Jésus, je serais perdu </p>\n<p>Comme un  bateau qui fait naufrage </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus  Oh ! Jésus, le connais tu Aujourd’hui</strong> </p>\n<p><strong>Ne te  détourne pas de lui Oh Jésus</strong> </p>\n<p><strong>Oh !  Jésus sans le Seigneur</strong> </p>\n<p><strong>Hélas !  Je serais perdu</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sans  Jésus ma vie serait bien triste </p>\n<p>Sans  Jésus, je serai esclave </p>\n<p>Sans  jésus tout est désespéré </p>\n<p>Mais  avec Lui, Gloire à Dieu je suis sauve </p>\n','Sans-Jesus-Je-ne-pourrai-rien-faire',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2325,'bdd58bf4-204b-408d-9d1f-fe44b596a77b',0,0,'Pour être heureux dans la vie',45,'45_RN_fr','1.\nPour  être heureux dans la vie\nPour  être heureux tout les jours\nPour  avoir la paix bénie\nIl faut  Jésus-Christ toujours\nRefrain\nSur son  cœur, douce retraite,\nHeureux  qui va se cacher,\nIci la  douleur s’arête,\nJésus  est le bon berger\n2.\nSi  l’orage se déchaine,’\nJe ne  crains pas sa fureur\nCar de  sa brebis en peine\nIl veut  calmer la frayeur\n3.\nSi vos âmes  sont amères\nSi vous êtes  seuls souffrants\nLa  victime du calvaire\nPrendras  sur lui, vos tourments\n4.\nVenez au  Christ qui console\nEt qui  sauve le pécheur\nOn  apprendra a son école\nLe  chemin du vrai bonheur','<p><strong>1.</strong> </p>\n<p>Pour  être heureux dans la vie </p>\n<p>Pour  être heureux tout les jours </p>\n<p>Pour  avoir la paix bénie </p>\n<p>Il faut  Jésus-Christ toujours </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Sur son  cœur, douce retraite,</strong> </p>\n<p><strong>Heureux  qui va se cacher,</strong> </p>\n<p><strong>Ici la  douleur s’arête,</strong> </p>\n<p><strong>Jésus  est le bon berger</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si  l’orage se déchaine,’ </p>\n<p>Je ne  crains pas sa fureur </p>\n<p>Car de  sa brebis en peine </p>\n<p>Il veut  calmer la frayeur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si vos âmes  sont amères </p>\n<p>Si vous êtes  seuls souffrants </p>\n<p>La  victime du calvaire </p>\n<p>Prendras  sur lui, vos tourments </p>\n<p><br/> <strong>4.</strong></p>\n<p>Venez au  Christ qui console </p>\n<p>Et qui  sauve le pécheur </p>\n<p>On  apprendra a son école </p>\n<p>Le  chemin du vrai bonheur </p>\n','Pour-etre-heureux-dans-la-vie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2326,'9871b956-98fe-43ee-b889-22c9b1f3f3fe',0,0,'O! Mon enfant reprend la lyre!',44,'44_RN_fr','1.\nO !  Mon enfant reprend la lyre !\nOui mon  enfant tu dois chanter\nSi vers  le ciel ton cœur t’attire\nComme le  ciel tu dois vibrer\nVibrer  de joie et de tendresse\nVibrer  d’amour, de douce paix\nN’est tu  pas protéger sans cesse\nPar Dieu  qui t’aime a tout jamais\n2.\nO !  Mon enfant reprend ta route\nIl faut  monter malgré les flots\nMonter  toujours, coute que coute\nMonter  vers Dieu, monter plus haut !\nReprends  donc l’ouvre de la vie\nVa consoler,  sécher les pleurs\nChante  et va créer l’harmonie\nUn chant  d’amour dans d’autres chœurs\n3.\nO !  Mon enfant reprend la tache\nReprends  la lyre  tu dois chanter,\nPuisque  ton cœur au ciel s’attache,\nDe  l’infini va témoigner !\nBientôt,  bientôt c’est l’arrivée,\nC’est  l’au-revoir du grand matin\nQuand  l’âme atteint sa destinée,\nDe dieu  la plénitude enfin','<p><strong>1.</strong> </p>\n<p>O !  Mon enfant reprend la lyre ! </p>\n<p>Oui mon  enfant tu dois chanter </p>\n<p>Si vers  le ciel ton cœur t’attire </p>\n<p>Comme le  ciel tu dois vibrer </p>\n<p>Vibrer  de joie et de tendresse </p>\n<p>Vibrer  d’amour, de douce paix </p>\n<p>N’est tu  pas protéger sans cesse </p>\n<p>Par Dieu  qui t’aime a tout jamais </p>\n<p><br/> <strong>2.</strong></p>\n<p>O !  Mon enfant reprend ta route </p>\n<p>Il faut  monter malgré les flots </p>\n<p>Monter  toujours, coute que coute </p>\n<p>Monter  vers Dieu, monter plus haut ! </p>\n<p>Reprends  donc l’ouvre de la vie </p>\n<p>Va consoler,  sécher les pleurs </p>\n<p>Chante  et va créer l’harmonie </p>\n<p>Un chant  d’amour dans d’autres chœurs </p>\n<p><br/> <strong>3.</strong></p>\n<p>O !  Mon enfant reprend la tache </p>\n<p>Reprends  la lyre  tu dois chanter, </p>\n<p>Puisque  ton cœur au ciel s’attache, </p>\n<p>De  l’infini va témoigner ! </p>\n<p>Bientôt,  bientôt c’est l’arrivée, </p>\n<p>C’est  l’au-revoir du grand matin </p>\n<p>Quand  l’âme atteint sa destinée, </p>\n<p>De dieu  la plénitude enfin </p>\n','O!-Mon-enfant-reprend-la-lyre!',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2327,'bbd6663b-6623-4018-abf4-356e02f63824',0,0,'Minuit Chrétien, c’est l’heure solennelle',56,'56_RN_fr','1.\nMinuit Chrétien, c’est  l’heure solennelle\nOù l’homme Dieu descendit  jusqu’à nous\nPour effacer la tâche  originelle\nEt de son Père arrêter le  courroux.\nLe monde entier trésaille d’espérance,\nA cette nuit qui nous donne  un Sauveur,\nPeuple à genoux !  Attends la délivrance.\n\nREFRAIN\nNoël! Noël! Voici le Rédempteur\nNoël! Noël! Voici le Rédempteur\n\n2.\nDe notre foi que la lumière  ardente\nNous guide tous au berceau  de l’enfant\nComme autrefois une étoile  brillante,\nY conduit les chefs de  l’orient.\nLe Roi des rois nait dans  une humble crèche,\nPuissant du jour, fier de  votre grandeur,\nA votre orgueil, c’est la  qu’au Dieu, pécheurs\nCourbez vos fronts devant le  Rédempteur.\nREFRAIN\nNoël! Noël! Voici le Rédempteur\nNoël! Noël! Voici le Rédempteur\n\n3.\nLe Rédempteur a brise  toute entrave\nLa terre est libre et le  ciel est ouvert,\nIl voit un père ou n’était  qu’un esclave,\nL’amour unit ceux  qu’enchainait le fer\nQui lui dira notre  reconnaissance ?\nC’est pour nous tous qu’il  nait, qu’il souffre et meurt.\nPeuple debout, chante la délivrance.\nREFRAIN\nNoel ! Noel !  Voici le Rédempteur\nNoel ! Noel !  Voici le Rédempteur','<p><strong>1.</strong> </p>\n<p>Minuit Chrétien, c’est  l’heure solennelle </p>\n<p>Où l’homme Dieu descendit  jusqu’à nous </p>\n<p>Pour effacer la tâche  originelle </p>\n<p>Et de son Père arrêter le  courroux. </p>\n<p>Le monde entier trésaille d’espérance, </p>\n<p>A cette nuit qui nous donne  un Sauveur, </p>\n<p>Peuple à genoux !  Attends la délivrance. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Noël! Noël! Voici le Rédempteur</strong> </p>\n<p><strong>Noël! Noël! Voici le Rédempteur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>De notre foi que la lumière  ardente </p>\n<p>Nous guide tous au berceau  de l’enfant </p>\n<p>Comme autrefois une étoile  brillante, </p>\n<p>Y conduit les chefs de  l’orient. </p>\n<p>Le Roi des rois nait dans  une humble crèche, </p>\n<p>Puissant du jour, fier de  votre grandeur, </p>\n<p>A votre orgueil, c’est la  qu’au Dieu, pécheurs </p>\n<p>Courbez vos fronts devant le  Rédempteur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Noël! Noël! Voici le Rédempteur</strong> </p>\n<p><strong>Noël! Noël! Voici le Rédempteur</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Rédempteur a brise  toute entrave </p>\n<p>La terre est libre et le  ciel est ouvert, </p>\n<p>Il voit un père ou n’était  qu’un esclave, </p>\n<p>L’amour unit ceux  qu’enchainait le fer </p>\n<p>Qui lui dira notre  reconnaissance ? </p>\n<p>C’est pour nous tous qu’il  nait, qu’il souffre et meurt. </p>\n<p>Peuple debout, chante la délivrance. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Noel ! Noel !  Voici le Rédempteur</strong> </p>\n<p><strong>Noel ! Noel !  Voici le Rédempteur</strong> </p>\n','Minuit-Chretien-c\'est-l\'heure-solennelle',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2328,'8c311403-72f2-4478-9369-168fdbe5733d',0,0,'Que devient mon enfant ce soir?',51,'51_RN_fr','1.\nQue devient mon enfant ce  soir ?\nIl suit une sombre voie,\nLui qui faisait tout mon  espoir\nEt ma couronne de joie\nCHOEUR\nChercher mon enfant ce soir  (bis)\nMon cœur est plein, je veux  le revoir\nChercher mon enfant ce soir\n2.\nTout jeune encore je  crois le voir\nLorsque fervent et sincère\nIl disait à Dieu, chaque  soir,\nSon enfantine prière.\n3.\nJe le revois plus grand  plus fort\nLorsqu’aux jours de sa  jeunesse,\nSon cœur était candide  encore,\nEt pour moi plein de  tendresse.\n4.\nOh ! Qui me rendra  mon enfant\nComme autrefois plein de  charmes ?\nJe veille et pleure en  l’attendant.\nCherchez le fils de mes  larmes ?\n5.\nCombien j’ai soif de te  revoir\nMon enfant, combien je  l’aime !\nOh ! Dans mes bras,  reviens ce soir,\nEntends mon appel suprême !\nChoeur\nReviens ! Mon enfant ce  soir ! (bis)\nMon cœur est plein, je veux  te revoir.\nReviens ! Mon enfant,  ce soir.','<p><strong>1.</strong> </p>\n<p>Que devient mon enfant ce  soir ? </p>\n<p>Il suit une sombre voie, </p>\n<p>Lui qui faisait tout mon  espoir </p>\n<p>Et ma couronne de joie </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Chercher mon enfant ce soir  (bis)</strong> </p>\n<p><strong>Mon cœur est plein, je veux  le revoir</strong> </p>\n<p><strong>Chercher mon enfant ce soir</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Tout jeune encore je  crois le voir </p>\n<p>Lorsque fervent et sincère </p>\n<p>Il disait à Dieu, chaque  soir, </p>\n<p>Son enfantine prière. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je le revois plus grand  plus fort </p>\n<p>Lorsqu’aux jours de sa  jeunesse, </p>\n<p>Son cœur était candide  encore, </p>\n<p>Et pour moi plein de  tendresse. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Oh ! Qui me rendra  mon enfant </p>\n<p>Comme autrefois plein de  charmes ? </p>\n<p>Je veille et pleure en  l’attendant. </p>\n<p>Cherchez le fils de mes  larmes ? </p>\n<p><br/> <strong>5.</strong></p>\n<p>Combien j’ai soif de te  revoir </p>\n<p>Mon enfant, combien je  l’aime ! </p>\n<p>Oh ! Dans mes bras,  reviens ce soir, </p>\n<p>Entends mon appel suprême ! </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Reviens ! Mon enfant ce  soir ! (bis)</strong> </p>\n<p><strong>Mon cœur est plein, je veux  te revoir.</strong> </p>\n<p><strong>Reviens ! Mon enfant,  ce soir.</strong> </p>\n','Que-devient-mon-enfant-ce-soir',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2329,'b441af38-5faa-4e94-830f-289807af02ea',0,0,'Cloches, sonnez l’évangile',48,'48_RN_fr','1.\nCloches, sonnez l’évangile \nRetentissez en tous lieux \nQu’a la campagne,  la ville,\nOn entende un chant joyeux !\nDieu, jadis aima le monde\nLivra son fils a la mort.\nQue la terre au ciel réponde\nEn acclamant le Dieu fort.\nChoeur\nPaix ! Salut! En Jésus.\nCloches résonnez encore\nPaix ! Salut ! en Jésus.\nRecevons tous ce trésor.\n\n2.\nAnnoncez, cloches célestes\nLa naissance d’un sauveur\nDont les bontés manifestes\nS’étendent a tout pécheur.\nRépandez dans tout le monde\nLa nouvelle du pardon.\nQue le ciel, la terre et l’onde\nRépètent a l’unisson.\n\n3.\nSonnez ! de l’heure suprême,\nEt la honte et le mépris.\nQuand le fils de Dieu, lui-même,\ndu salut paya le prix,\nEclatez en cris de joie,\nLouez le Triomphateur !\nLa tombe a lâché sa proie.\nSur la mort, Christ est vainqueur !\n\n4.\nVotre joie, cloches fidèles,\nVa, parcourant l’univers,\nSupplier tous les rebelle\nQui gémissent dans les fers\nDe chercher la délivrance,\nLe pardon, la liberté,\nEn Christ, leur seule espérance\nPour le temps, l’éternité.','<p><strong>1.</strong> </p>\n<p>Cloches, sonnez l’évangile </p>\n<p>Retentissez en tous lieux </p>\n<p>Qu’a la campagne,  la ville, </p>\n<p>On entende un chant joyeux ! </p>\n<p>Dieu, jadis aima le monde </p>\n<p>Livra son fils a la mort. </p>\n<p>Que la terre au ciel réponde </p>\n<p>En acclamant le Dieu fort. </p>\n<p><br/> <strong>Choeur</strong></p>\n<p><strong>Paix ! Salut! En Jésus.</strong> </p>\n<p><strong>Cloches résonnez encore</strong> </p>\n<p><strong>Paix ! Salut ! en Jésus.</strong> </p>\n<p><strong>Recevons tous ce trésor.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Annoncez, cloches célestes </p>\n<p>La naissance d’un sauveur </p>\n<p>Dont les bontés manifestes </p>\n<p>S’étendent a tout pécheur. </p>\n<p>Répandez dans tout le monde </p>\n<p>La nouvelle du pardon. </p>\n<p>Que le ciel, la terre et l’onde </p>\n<p>Répètent a l’unisson. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sonnez ! de l’heure suprême, </p>\n<p>Et la honte et le mépris. </p>\n<p>Quand le fils de Dieu, lui-même, </p>\n<p>du salut paya le prix, </p>\n<p>Eclatez en cris de joie, </p>\n<p>Louez le Triomphateur ! </p>\n<p>La tombe a lâché sa proie. </p>\n<p>Sur la mort, Christ est vainqueur ! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Votre joie, cloches fidèles, </p>\n<p>Va, parcourant l’univers, </p>\n<p>Supplier tous les rebelle </p>\n<p>Qui gémissent dans les fers </p>\n<p>De chercher la délivrance, </p>\n<p>Le pardon, la liberté, </p>\n<p>En Christ, leur seule espérance </p>\n<p>Pour le temps, l’éternité. </p>\n','Cloches-sonnez-l\'evangile',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2330,'16dade05-4398-4925-8e9f-a06dc65ca054',0,0,'Je viens Seigneur, à la table bénie',63,'63_RN_fr','1.\nJe viens Seigneur, à la  table bénie,\nLe cœur brise, soupirant après  toi \nC’est dans ta mort que je  trouve la vie \nTa croix, Jésus est l’espoir  de ma foi.\n2.\nJ’ai faim, j’ai soif de  la vie éternelle,\nSoif de pardon et soif de sainteté.\nRéponds, Seigneur a ma voix  qui t’appelle !\nOh ! Redis-moi que tu  m’as racheté.\n3.\nQu’en venant tous a la  table sacrée,\nNous n’apportions que  l’amour et la paix \nDe notre cœur, garde, o Jésus  l’entrée !\nQue l’ennemi n’y pénètre  jamais !\n4.\nQu’ici, Jésus en ta  sainte présence,\nNous goutons tous les délices  du ciel !\nViens raffermir la céleste  alliance,\nQui nous unit, O Sauveur  Eternel.\n5.\nSeigneur, descends !\nDescends ! Esprit de  flamme.\nViens pour toujours  embrasser notre cœur !\nDétruis en nous tout  penchant que Dieu blâme,\nCree en nous l’image du  Sauveur.','<p><strong>1.</strong> </p>\n<p>Je viens Seigneur, à la  table bénie, </p>\n<p>Le cœur brise, soupirant après  toi </p>\n<p>C’est dans ta mort que je  trouve la vie </p>\n<p>Ta croix, Jésus est l’espoir  de ma foi. </p>\n<p><br/> <strong>2.</strong></p>\n<p>J’ai faim, j’ai soif de  la vie éternelle, </p>\n<p>Soif de pardon et soif de sainteté. </p>\n<p>Réponds, Seigneur a ma voix  qui t’appelle ! </p>\n<p>Oh ! Redis-moi que tu  m’as racheté. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qu’en venant tous a la  table sacrée, </p>\n<p>Nous n’apportions que  l’amour et la paix </p>\n<p>De notre cœur, garde, o Jésus  l’entrée ! </p>\n<p>Que l’ennemi n’y pénètre  jamais ! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Qu’ici, Jésus en ta  sainte présence, </p>\n<p>Nous goutons tous les délices  du ciel ! </p>\n<p>Viens raffermir la céleste  alliance, </p>\n<p>Qui nous unit, O Sauveur  Eternel. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Seigneur, descends ! </p>\n<p>Descends ! Esprit de  flamme. </p>\n<p>Viens pour toujours  embrasser notre cœur ! </p>\n<p>Détruis en nous tout  penchant que Dieu blâme, </p>\n<p>Cree en nous l’image du  Sauveur. </p>\n','Je-viens-Seigneur-a-la-table-benie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2331,'d7ae942b-9624-4049-aae0-9b5173b8f12c',0,0,'Je ne vois aucun espoir de paix pour le monde',73,'73_RN_fr','1.\nJe ne vois aucun espoir de paix pour le monde\nS’il ne passe par de nouvelle naissance.\nLa civilisation est condamnée,\nPar les causes de son incrédulité.\nLes problèmes qu’il confronte aujourd’hui\nDépassent l’humanité tout entière.\nRefrain\nNous trouvons cette consolation\nDans la parole qu’il nous a donnée :\nSur cette pierre je bâtirai mon Eglise,\nEt ses portes ne seront point fermées.\n2.\nLe monde maintenant est sous l’agitation.\nL’homme sans Dieu n’a plus d’espérance.\nLes démons de l’enfer sont relâchés,\nL’histoire est sérieusement engagée,\nNous traversons une impasse très critique,\nLe toit va bientôt s’effondrer.\n3.\nNous sommes semblables à un peuple condamne\nA la peine de mort, sans espoir d’amnistie.\nLes hommes sont pris dans un engrenage :\nUn événement va se produire,\nMais nous ne pouvons l’éviter,\nCar c’est déjà prédit dans la Bible.\n4.\nLes marxistes sont en train de conquérir le monde,\nCar ils ne trouvent aucun sacrifice trop grand.\nLa fin justifie les moyens.\nMême s’ils ont tort, ils ont un but\nIls peuvent vaincre aujourd’hui ou demain\nSi le christianisme vient d’échouer.\n5.\nLe christianisme est une nécessité vitale\nL’Eglise doit se réveiller de son sommeil\nPour proclamer cette bonne nouvelle\nQu’il nous a recommandée,\nCar c’est un gain pour ceux qui sont chrétiens.\nUne réponse finale sera bientôt','<p><strong>1.</strong> </p>\n<p>Je ne vois aucun espoir de paix pour le monde </p>\n<p>S’il ne passe par de nouvelle naissance. </p>\n<p>La civilisation est condamnée, </p>\n<p>Par les causes de son incrédulité. </p>\n<p>Les problèmes qu’il confronte aujourd’hui </p>\n<p>Dépassent l’humanité tout entière. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nous trouvons cette consolation</strong> </p>\n<p><strong>Dans la parole qu’il nous a donnée :</strong> </p>\n<p><strong>Sur cette pierre je bâtirai mon Eglise,</strong> </p>\n<p><strong>Et ses portes ne seront point fermées.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le monde maintenant est sous l’agitation. </p>\n<p>L’homme sans Dieu n’a plus d’espérance. </p>\n<p>Les démons de l’enfer sont relâchés, </p>\n<p>L’histoire est sérieusement engagée, </p>\n<p>Nous traversons une impasse très critique, </p>\n<p>Le toit va bientôt s’effondrer. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous sommes semblables à un peuple condamne </p>\n<p>A la peine de mort, sans espoir d’amnistie. </p>\n<p>Les hommes sont pris dans un engrenage : </p>\n<p>Un événement va se produire, </p>\n<p>Mais nous ne pouvons l’éviter, </p>\n<p>Car c’est déjà prédit dans la Bible. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Les marxistes sont en train de conquérir le monde, </p>\n<p>Car ils ne trouvent aucun sacrifice trop grand. </p>\n<p>La fin justifie les moyens. </p>\n<p>Même s’ils ont tort, ils ont un but </p>\n<p>Ils peuvent vaincre aujourd’hui ou demain </p>\n<p>Si le christianisme vient d’échouer. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Le christianisme est une nécessité vitale </p>\n<p>L’Eglise doit se réveiller de son sommeil </p>\n<p>Pour proclamer cette bonne nouvelle </p>\n<p>Qu’il nous a recommandée, </p>\n<p>Car c’est un gain pour ceux qui sont chrétiens. </p>\n<p>Une réponse finale sera bientôt </p>\n','Je-ne-vois-aucun-espoir-de-paix-pour-le-monde',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2332,'ee3f617e-514f-48de-b3a8-7dd18fd94642',0,0,'O mon frère connait tu Jesus?',17,'17_RN_fr','1.\nO mon  frère connait tu Jesus?\nAs-tu  entendu sa voix?\nIl  est le roche du salut\nIl y  a du miel en Christ pour toi\n\nRefrain\nOh! Du miel dans le rocher\nO mon  frère\nDu  miel dans le rocher pour toi\nApporte  tes péchés au calvaire\nIl y  a du miel en Christ pour toi\n\n\n2.\nAs-tu  trouvé cet ami fidèle?\nDe  lui, as tu fait ton choix\nMarches-tu  dans la vie nouvelle\nIl y  a du miel en Christ pour toi\n\n3.\nO mon  frère le long du chemin\nTous  les jours et sans effroi\nDis-le à chaque pèlerin\nIl y  a du miel en Christ pour toi','<p><strong>1.</strong> </p>\n<p>O mon  frère connait tu Jesus? </p>\n<p>As-tu  entendu sa voix? </p>\n<p>Il  est le roche du salut </p>\n<p>Il y  a du miel en Christ pour toi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! Du miel dans le rocher</strong> </p>\n<p><strong>O mon  frère</strong> </p>\n<p><strong>Du  miel dans le rocher pour toi</strong> </p>\n<p><strong>Apporte  tes péchés au calvaire</strong> </p>\n<p><strong>Il y  a du miel en Christ pour toi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>As-tu  trouvé cet ami fidèle? </p>\n<p>De  lui, as tu fait ton choix </p>\n<p>Marches-tu  dans la vie nouvelle </p>\n<p>Il y  a du miel en Christ pour toi </p>\n<p><br/> <strong>3.</strong></p>\n<p>O mon  frère le long du chemin </p>\n<p>Tous  les jours et sans effroi </p>\n<p>Dis-le à chaque pèlerin </p>\n<p>Il y  a du miel en Christ pour toi </p>\n','O-mon-frere-connait-tu-Jesus',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2333,'24c562e4-8af3-41dc-a3bf-828a009f8c88',0,0,'Tout prés de moi toujours',40,'40_RN_fr','1.\nTout  prés de moi toujours\nDans la  plus sombre nuit\nTout  prés de moi Oh ! Jésus mon Roi\nEt le  long du chemin\nIl me  tient par la main\nTout  prés de moi\nOh mon  Sauveur, mon Roi\nRefrain\nToujours,  tout prés de moi\nSe tient  Jésus mon Roi\nEt le  long du chemin\nIl me  tient par la main\nEt je  suis si heureux,\nSi  content, si joyeux\nDes que  mon Roi se tient\n2.\nQuand la  terre passera et quand tout finira\nJésus  mon Roi se tient tout prés de moi\nDans la  céleste nuit, je serais pour toujours\nTout  prés de toi, O mon Sauveur mon Roi\n3.\nQuand  j’étais sans espoir\nQuand  tout me semble noir\nJésus  mon roi, Se tient tout prés de moi\nQuand le  monde m’abandonne\nQuand le  mal m’environne\nTout  prés de moi\nO mon  Sauveur, mon Roi','<p><strong>1.</strong> </p>\n<p>Tout  prés de moi toujours </p>\n<p>Dans la  plus sombre nuit </p>\n<p>Tout  prés de moi Oh ! Jésus mon Roi </p>\n<p>Et le  long du chemin </p>\n<p>Il me  tient par la main </p>\n<p>Tout  prés de moi </p>\n<p>Oh mon  Sauveur, mon Roi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Toujours,  tout prés de moi</strong> </p>\n<p><strong>Se tient  Jésus mon Roi</strong> </p>\n<p><strong>Et le  long du chemin</strong> </p>\n<p><strong>Il me  tient par la main</strong> </p>\n<p><strong>Et je  suis si heureux,</strong> </p>\n<p><strong>Si  content, si joyeux</strong> </p>\n<p><strong>Des que  mon Roi se tient</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand la  terre passera et quand tout finira </p>\n<p>Jésus  mon Roi se tient tout prés de moi </p>\n<p>Dans la  céleste nuit, je serais pour toujours </p>\n<p>Tout  prés de toi, O mon Sauveur mon Roi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand  j’étais sans espoir </p>\n<p>Quand  tout me semble noir </p>\n<p>Jésus  mon roi, Se tient tout prés de moi </p>\n<p>Quand le  monde m’abandonne </p>\n<p>Quand le  mal m’environne </p>\n<p>Tout  prés de moi </p>\n<p>O mon  Sauveur, mon Roi </p>\n','Tout-pres-de-moi-toujours',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2334,'b17d1f26-bf56-469a-b86c-67b6ec184c04',0,0,'Vaillants soldats de Jésus-Christ.',49,'49_RN_fr','1.\nVaillants soldats de Jésus-Christ.\nD’un cœur toujours joyeux, toujours contrit \nPar la vertu de son Esprit\nMarchons avec persévérance !\n\nRefrain\nPar la foi, par la foi,\nSans trêve et sans effroi,\nPar la foi, par la foi,\nNous suivons notre Roi.\nBientôt Seigneur, auprès de toi,\nNous chanterons la délivrance !\n\n2.\nSi nous rencontrons sur nos pas\nLabeur, fatigue et périlleux combats,\nNe craignons rien, ne cédons pas,\nNe perdons point notre assurance.\n\n3.\nEt si pour te gagner les cœurs,\nIl faut passer par le sentier des pleurs,\nTon bras nous rend plus que vainqueurs\nEt ton ciel est notre espérance.','<p><strong>1.</strong> </p>\n<p>Vaillants soldats de Jésus-Christ. </p>\n<p>D’un cœur toujours joyeux, toujours contrit </p>\n<p>Par la vertu de son Esprit </p>\n<p>Marchons avec persévérance ! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Par la foi, par la foi,</strong> </p>\n<p><strong>Sans trêve et sans effroi,</strong> </p>\n<p><strong>Par la foi, par la foi,</strong> </p>\n<p><strong>Nous suivons notre Roi.</strong> </p>\n<p><strong>Bientôt Seigneur, auprès de toi,</strong> </p>\n<p><strong>Nous chanterons la délivrance !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si nous rencontrons sur nos pas </p>\n<p>Labeur, fatigue et périlleux combats, </p>\n<p>Ne craignons rien, ne cédons pas, </p>\n<p>Ne perdons point notre assurance. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et si pour te gagner les cœurs, </p>\n<p>Il faut passer par le sentier des pleurs, </p>\n<p>Ton bras nous rend plus que vainqueurs </p>\n<p>Et ton ciel est notre espérance. </p>\n','Vaillants-soldats-de-Jesus-Christ.',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2335,'84d49009-eb6e-45a9-8ac0-37a4cb0f5cd6',0,0,'A ta cène bénie, O Sauveur charitable',50,'50_RN_fr','1.\nA ta cène bénie, O Sauveur charitable !\nTu viens nous convier de nouveau en ce jour.\nAu milieu du désert tu dresses cette table\nQui nous rappelle\nO Christ, ton ineffable amour !\n\n2.\nTon troupeau, d’un seul amour\nAutour de toi se groupe\nPour annoncer ta mort jusqu’à ton retour\nIl veut rompre ton pain i veut boire ta coupe.\nPour proclamer, O Christ ton ineffable amour !\n\n3.\nRévèle a tes enfants ta divine présence !\nIls bénissent ton non  bénis-les a ton tour !\nAugment-leur la foi, remplis-les d’Esperance\nFais-leur gouter, O Christ, ton ineffable amour !\n\n4.\nViens, et dans tous nos cœurs, verse ta sainte vie,\nDonne-nous l’avant-gout du céleste séjour \nOu bientôt, a jamais, dans la gloire infinie,\nNous bénirons, O Christ ton ineffable amour!','<p><strong>1.</strong> </p>\n<p>A ta cène bénie, O Sauveur charitable ! </p>\n<p>Tu viens nous convier de nouveau en ce jour. </p>\n<p>Au milieu du désert tu dresses cette table </p>\n<p>Qui nous rappelle </p>\n<p>O Christ, ton ineffable amour ! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton troupeau, d’un seul amour </p>\n<p>Autour de toi se groupe </p>\n<p>Pour annoncer ta mort jusqu’à ton retour </p>\n<p>Il veut rompre ton pain i veut boire ta coupe. </p>\n<p>Pour proclamer, O Christ ton ineffable amour ! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Révèle a tes enfants ta divine présence ! </p>\n<p>Ils bénissent ton non  bénis-les a ton tour ! </p>\n<p>Augment-leur la foi, remplis-les d’Esperance </p>\n<p>Fais-leur gouter, O Christ, ton ineffable amour ! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Viens, et dans tous nos cœurs, verse ta sainte vie, </p>\n<p>Donne-nous l’avant-gout du céleste séjour </p>\n<p>Ou bientôt, a jamais, dans la gloire infinie, </p>\n<p>Nous bénirons, O Christ ton ineffable amour! </p>\n','A-ta-cene-benie-O-Sauveur-charitable',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2336,'dc555ded-2c5d-4042-b0d9-421b58571652',0,0,'A ton école, divin Maitre',54,'54_RN_fr','1.\nA ton école, divin Maitre,\nNous sommes venus nous  former.\nEnseigne-nous à te  connaitre.\nToi-même apprends-nous a  t’aimer.\n2.\nSeigneur, qu’attentifs et  tranquilles\nNos esprits s’ouvrent à ta  voix\nEt que nos cœurs, toujours  dociles,\nDemeurent soumis à tes lois.','<p><strong>1.</strong> </p>\n<p>A ton école, divin Maitre, </p>\n<p>Nous sommes venus nous  former. </p>\n<p>Enseigne-nous à te  connaitre. </p>\n<p>Toi-même apprends-nous a  t’aimer. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Seigneur, qu’attentifs et  tranquilles </p>\n<p>Nos esprits s’ouvrent à ta  voix </p>\n<p>Et que nos cœurs, toujours  dociles, </p>\n<p>Demeurent soumis à tes lois. </p>\n','A-ton-ecole-divin-Maitre',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2337,'29525e41-cc23-4be4-ab1e-71da5985f112',0,0,'Invoque-moi du sein de la détresse',68,'68_RN_fr','1.\n« Invoque-moi du sein  de la détresse\nMon bras puissant te  sauvera. »\nC’est Dieu qui parle,  Oh ! Croix en sa promesse !\nBientôt ta voix l’exaltera  (bis)\n2.\n \nSi tout est sombre oh !  Ne perds pas courage.\nNe doute pas du Dieu vivant.\nMême au milieu des fureurs  de l’orage,\nDemeure en paix, crois  seulement (bis)\n3.\nMais n’attends pas que le  mal te domine.\nQue l’ennemi soit dans ton  cœur\nElle est à toi, la puissance  divine.\nSaisis-la donc et sois  vainqueur. (bis)\n4.\nJamais, jamais, Dieu qui  fit la promesse.\nNe manquera de l’accomplir.\nQue ton enfant, sauve de la détresse,\nNe manque pas de le bénir !  (bis)','<p><strong>1.</strong> </p>\n<p>« Invoque-moi du sein  de la détresse </p>\n<p>Mon bras puissant te  sauvera. » </p>\n<p>C’est Dieu qui parle,  Oh ! Croix en sa promesse ! </p>\n<p>Bientôt ta voix l’exaltera  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tout est sombre oh !  Ne perds pas courage. </p>\n<p>Ne doute pas du Dieu vivant. </p>\n<p>Même au milieu des fureurs  de l’orage, </p>\n<p>Demeure en paix, crois  seulement (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais n’attends pas que le  mal te domine. </p>\n<p>Que l’ennemi soit dans ton  cœur </p>\n<p>Elle est à toi, la puissance  divine. </p>\n<p>Saisis-la donc et sois  vainqueur. (bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jamais, jamais, Dieu qui  fit la promesse. </p>\n<p>Ne manquera de l’accomplir. </p>\n<p>Que ton enfant, sauve de la détresse, </p>\n<p>Ne manque pas de le bénir !  (bis) </p>\n','Invoque-moi-du-sein-de-la-detresse',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2338,'f1fce5cf-96c3-40fd-8de9-5b2e1a75765a',0,0,'Que la grâce de notre Seigneur Jésus –Christ',70,'70_RN_fr','1.\nQue la grâce de notre  Seigneur Jésus –Christ\nEt l’amour de Dieu le Père,\nEt la communion du Saint  Esprit\nSoient avec nous tous !  Amen !\nAven nous tous !  Amen !','<p><strong>1.</strong> </p>\n<p>Que la grâce de notre  Seigneur Jésus –Christ </p>\n<p>Et l’amour de Dieu le Père, </p>\n<p>Et la communion du Saint  Esprit </p>\n<p>Soient avec nous tous !  Amen ! </p>\n<p>Aven nous tous !  Amen ! </p>\n','Que-la-grace-de-notre-Seigneur-Jesus-Christ',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2339,'8685504e-abbd-4e6e-b30a-f4223abc0c53',0,0,'Veux-tu briser du péché le pouvoir',72,'72_RN_fr','1.\nVeux-tu briser du péché le pouvoir\nLa force est en Christ  (bis)\nSi dans ton cœur, tu veux le  recevoir\nLa force est dans le sang de  Christ !\nRefrain\nSoyons forts, forts Oui,  plus que vainqueurs,\nPar le sang de Jésus le  Sauveur !\n\n2.\nVeux-tu braver et la mort et  l’enfer ?\nLa force est en  Christ (bis)\nJésus d’un mot fait tomber  tous les fers.\nLa force est dans le sang de  Christ.\n3.\nVeux-tu marcher toujours pur,  triomphant ?\nLa force est en Christ (bis)\nPour te garder Jésus est  tout puissant\nLa force est dans le sang de  Christ !\n4.\nVeux-tu du ciel t’approcher chaque  jour ?\nLa force est en Christ (bis)\nAvec Jésus, demeurer pour  toujours ?\nLa force est dans le sang de  Christ !','<p><strong>1.</strong> </p>\n<p>Veux-tu briser du péché le pouvoir </p>\n<p>La force est en Christ  (bis) </p>\n<p>Si dans ton cœur, tu veux le  recevoir </p>\n<p>La force est dans le sang de  Christ ! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Soyons forts, forts Oui,  plus que vainqueurs,</strong> </p>\n<p><strong>Par le sang de Jésus le  Sauveur !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Veux-tu braver et la mort et  l’enfer ? </p>\n<p>La force est en  Christ (bis) </p>\n<p>Jésus d’un mot fait tomber  tous les fers. </p>\n<p>La force est dans le sang de  Christ. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Veux-tu marcher toujours pur,  triomphant ? </p>\n<p>La force est en Christ (bis) </p>\n<p>Pour te garder Jésus est  tout puissant </p>\n<p>La force est dans le sang de  Christ ! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Veux-tu du ciel t’approcher chaque  jour ? </p>\n<p>La force est en Christ (bis) </p>\n<p>Avec Jésus, demeurer pour  toujours ? </p>\n<p>La force est dans le sang de  Christ ! </p>\n','Veux-tu-briser-du-peche-le-pouvoir',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2340,'9f830f65-1c81-4da5-b66b-dd703e29a0b2',0,0,'Jésus est notre Roi',36,'36_RN_fr','1.\nJésus  est notre Roi\nJésus  est tout pour moi (bis)\nRefrain\nC’est Jésus  (bis)\nQui peut  nous affermir\nC’est  Jésus (bis)\nQui peut  nous rendre heureux\n2.\nJésus  nous veut toujours\nIl veut  nous bien garder (bis)\n3.\nJésus  viendra bientôt\nPour nous conduire au ciel (bis)','<p><strong>1.</strong> </p>\n<p>Jésus  est notre Roi </p>\n<p>Jésus  est tout pour moi (bis) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>C’est Jésus  (bis)</strong> </p>\n<p><strong>Qui peut  nous affermir</strong> </p>\n<p><strong>C’est  Jésus (bis)</strong> </p>\n<p><strong>Qui peut  nous rendre heureux</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus  nous veut toujours </p>\n<p>Il veut  nous bien garder (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus  viendra bientôt </p>\n<p>Pour nous conduire au ciel (bis) </p>\n','Jesus-est-notre-Roi',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2341,'f160bf49-4c59-489b-a6ab-b7194c530905',0,0,'Flots mugissants, flots en furie',55,'55_RN_fr','1.\nFlots mugissants, flots en  furie,\nEntourez-moi, je n’ai pas  peur.\nQuoi qu’il en soit, paix  infinie\nPuisqu’à la barre est mon  sauveur\nREFRAIN\nLe Gouvernail de ma nacelle  (bis)\nOh quel repos (bis) Jésus le  tient \nSi dans la nuit (bis) mon  cœur chancelle\nAvec Jésus (bis) oui tout  est bien.\n2.\nEt vous récifs, écueils  menaces\nOui présagez plus d’un  malheur\nCalme, je puis vous voir,\nPuisqu’a la barre est mon  Sauveur.\n3.\nJour après jour, nouveaux  orages,\nNouveaux périls, point de  frayeur !\nBientôt j’atteins le grand  rivage,\nPuisqu’a la barre est mon  Sauveur.\n4.\nAvec Jésus pour mon  pilote,\nPour moi tout est paix et  bonheur.\nEn sureté, mon esprit  flotte,\nPuisqu’a la barre est mon  Sauveur','<p><strong>1.</strong> </p>\n<p>Flots mugissants, flots en  furie, </p>\n<p>Entourez-moi, je n’ai pas  peur. </p>\n<p>Quoi qu’il en soit, paix  infinie </p>\n<p>Puisqu’à la barre est mon  sauveur </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Le Gouvernail de ma nacelle  (bis)</strong> </p>\n<p><strong>Oh quel repos (bis) Jésus le  tient</strong> </p>\n<p><strong>Si dans la nuit (bis) mon  cœur chancelle</strong> </p>\n<p><strong>Avec Jésus (bis) oui tout  est bien.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et vous récifs, écueils  menaces </p>\n<p>Oui présagez plus d’un  malheur </p>\n<p>Calme, je puis vous voir, </p>\n<p>Puisqu’a la barre est mon  Sauveur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jour après jour, nouveaux  orages, </p>\n<p>Nouveaux périls, point de  frayeur ! </p>\n<p>Bientôt j’atteins le grand  rivage, </p>\n<p>Puisqu’a la barre est mon  Sauveur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Avec Jésus pour mon  pilote, </p>\n<p>Pour moi tout est paix et  bonheur. </p>\n<p>En sureté, mon esprit  flotte, </p>\n<p>Puisqu’a la barre est mon  Sauveur </p>\n','Flots-mugissants-flots-en-furie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2342,'389ea60c-bcbb-454c-9516-1009e9693938',0,0,'Examinez-vous, vous-même',57,'57_RN_fr','1.\nExaminez-vous, vous-même  (bis)\nRegardez dans la foi si vous  êtes sauvé\nExaminez-vous, vous-même  (bis)\nExaminez-vous, vous-meme  (bis)\n2.\nRegardez dans la foi si  vous êtes sauvé.\nExaminez-vous, vous-même.\nDieu peut-il compter sur  vous ?\nLe pourra-t-il jusqu’au bout\nDieu peut-il compter sur  vous ? (bis)','<p><strong>1.</strong> </p>\n<p>Examinez-vous, vous-même  (bis) </p>\n<p>Regardez dans la foi si vous  êtes sauvé </p>\n<p>Examinez-vous, vous-même  (bis) </p>\n<p>Examinez-vous, vous-meme  (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Regardez dans la foi si  vous êtes sauvé. </p>\n<p>Examinez-vous, vous-même. </p>\n<p>Dieu peut-il compter sur  vous ? </p>\n<p>Le pourra-t-il jusqu’au bout </p>\n<p>Dieu peut-il compter sur  vous ? (bis) </p>\n','Examinez-vous-vous-meme',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2343,'b1bbb458-5fd4-4905-8ebb-5f378192da6e',0,0,'Le Seigneur nous a aimes...',74,'74_RN_fr','1.\nLe Seigneur nous a aimés comme on n’a jamais aimé\nIl nous guide chaque jour comme une étoile dans la nuit.\nQuand nous partageons le pain,\nIl nous donne son amour.\nC’est le pain de l’amitié le pain de Dieu\n2.\nLe Seigneur nous a aimés comme on n’a jamais aimé.\nUn enfant nous est donne dans cette nuit de Béthléem.\nLes bergers l’ont reconnu et les mages sont venus.\nIls ont vu le fils de Dieu Petit Enfant.\n3.\nLe Seigneur nous a aimés comme on n’a jamais aimé.\nPour les gens de son village c’est le Fils du charpentier,\nIl travaille de ses mains comme font tous ses amis.\nIl connait le dur labeur de chaque jour.\n4.\nLe Seigneur nous a aimés comme on n’a jamais aim..\nAu hasard de ses rencontres par les villes et les cités,\nIl révèle son amour et nous parle de son Père\nEt chacun retrouve espoir en l’écoutant.\n5.\nLe Seigneur nous a aimés, comme on n’a jamais aimé.\nSon amour était si grand qu’il en mourut sur une croix.\nSon amour et si fort qu’il triomphe de la mort.\nIl sortit de son tombeau libre et vainqueur.\nRefrain\nC’est mon cœur : prenez et mangez\nC’est mon sang : prenez et buvez.\nCar je suis la vie et je suis l’amour.\nO Seigneur emporte-nous dans ton amour.','<p><strong>1.</strong> </p>\n<p>Le Seigneur nous a aimés comme on n’a jamais aimé </p>\n<p>Il nous guide chaque jour comme une étoile dans la nuit. </p>\n<p>Quand nous partageons le pain, </p>\n<p>Il nous donne son amour. </p>\n<p>C’est le pain de l’amitié le pain de Dieu </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le Seigneur nous a aimés comme on n’a jamais aimé. </p>\n<p>Un enfant nous est donne dans cette nuit de Béthléem. </p>\n<p>Les bergers l’ont reconnu et les mages sont venus. </p>\n<p>Ils ont vu le fils de Dieu Petit Enfant. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Le Seigneur nous a aimés comme on n’a jamais aimé. </p>\n<p>Pour les gens de son village c’est le Fils du charpentier, </p>\n<p>Il travaille de ses mains comme font tous ses amis. </p>\n<p>Il connait le dur labeur de chaque jour. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Le Seigneur nous a aimés comme on n’a jamais aim.. </p>\n<p>Au hasard de ses rencontres par les villes et les cités, </p>\n<p>Il révèle son amour et nous parle de son Père </p>\n<p>Et chacun retrouve espoir en l’écoutant. </p>\n<p><br/> <strong>5.</strong></p>\n<p>Le Seigneur nous a aimés, comme on n’a jamais aimé. </p>\n<p>Son amour était si grand qu’il en mourut sur une croix. </p>\n<p>Son amour et si fort qu’il triomphe de la mort. </p>\n<p>Il sortit de son tombeau libre et vainqueur. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>C’est mon cœur : prenez et mangez</strong> </p>\n<p><strong>C’est mon sang : prenez et buvez.</strong> </p>\n<p><strong>Car je suis la vie et je suis l’amour.</strong> </p>\n<p><strong>O Seigneur emporte-nous dans ton amour.</strong> </p>\n','Le-Seigneur-nous-a-aimes...',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2344,'b5508368-d5d7-4bd5-8641-c8e4da022b05',0,0,'Belle Haïti ! Chère Haïti !',90,'90_RN_fr','1.\nBelle Haïti ! Chère Haïti !\nBeau pays de verdure !\nNotre terrestre paradis.\nA l’eau limpide et pure,\nBelle Haïti ! Chère Haïti !\nDieu te comble de grâce,\nQue ton âme soit belle aussi !\nDe laideurs poindre de trace !\n2.\nBelle Haïti ! tes voyageurs,\nCharges de tes denrées\nNourissent le monde en douleur\nDes âmes affamées.\nBelle Haïti ! Chère Haïti !\nO ! Sois aussi nourrie\nPar ton Protecteur, Jésus Christ\nEt qu’il te sanctifie !\n3.\nTon histoire, chère Haïti,\nDe ton sang est tracée\nTes fils cherchent tes droits bénis,\nLa victoire assurée,\nBelle Haïti, chère Haïti,\nNous louons ta noblesse\nQue ton mal en toi soit banni,\nVaincu par ta sagesse.\n4.\nMais Haïti, chère Haïti,\nPour que tu sois plus belle,\nSuis en toutes choses le Christ\nTon glorieux modèle.\nO ! Haïti ! Chère Haïti !\nCherche le bien suprême\nPrends pour guide le Saint-Esprit\nDemeure en paix sereine.','<p><strong>1.</strong> </p>\n<p>Belle Haïti ! Chère Haïti ! </p>\n<p>Beau pays de verdure ! </p>\n<p>Notre terrestre paradis. </p>\n<p>A l’eau limpide et pure, </p>\n<p>Belle Haïti ! Chère Haïti ! </p>\n<p>Dieu te comble de grâce, </p>\n<p>Que ton âme soit belle aussi ! </p>\n<p>De laideurs poindre de trace ! </p>\n<p><br/> <strong>2.</strong></p>\n<p>Belle Haïti ! tes voyageurs, </p>\n<p>Charges de tes denrées </p>\n<p>Nourissent le monde en douleur </p>\n<p>Des âmes affamées. </p>\n<p>Belle Haïti ! Chère Haïti ! </p>\n<p>O ! Sois aussi nourrie </p>\n<p>Par ton Protecteur, Jésus Christ </p>\n<p>Et qu’il te sanctifie ! </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ton histoire, chère Haïti, </p>\n<p>De ton sang est tracée </p>\n<p>Tes fils cherchent tes droits bénis, </p>\n<p>La victoire assurée, </p>\n<p>Belle Haïti, chère Haïti, </p>\n<p>Nous louons ta noblesse </p>\n<p>Que ton mal en toi soit banni, </p>\n<p>Vaincu par ta sagesse. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mais Haïti, chère Haïti, </p>\n<p>Pour que tu sois plus belle, </p>\n<p>Suis en toutes choses le Christ </p>\n<p>Ton glorieux modèle. </p>\n<p>O ! Haïti ! Chère Haïti ! </p>\n<p>Cherche le bien suprême </p>\n<p>Prends pour guide le Saint-Esprit </p>\n<p>Demeure en paix sereine. </p>\n','Belle-Haiti-!-Chere-Haiti-!',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2345,'d551f913-91fc-47c0-a827-f3852a7c8135',0,0,'Jésus-Christ est ma sagesse',29,'29_GA_fr','1.\nJésus-Christ est ma sagesse\nIl éclaire mon chemin\nEt je marche en ma faiblesse\nConduis par sa sûre main\nII éclaire mon chemin (bls)\nEt je marche en ma faiblesse\nConduit par sa sûre main\n\n2.\nJésus-Christ est ma justice\nSon sang a coulé pour moi\nJe trouve en son sacrifice\nPaix et pardon par la foi\nSon sang a coulé pour moi (bis)\nJe trouve en son sacrifice\nPaix et pardon par la foi\n\n3.\nJésus-Christ me sanctifie\nAu divin cep attaché\nJe reçois de lui la vie\nQui m\'affranchit du péché\nA toi Jésus attaché (bis)\nJe reçois de toi la vie\nQui m\'affranchit du péché\n\n4.\nJésus-Christ en payant ma dette\nA grand prix m\'a racheté\nPrès de lui ma place est prête\nAu ciel, pour l\'éternité\nJésus Christ m\'a racheté (bis)\nEt déjà ma place est prête\nAu ciel, pour l\'éternité','<p><strong>1.</strong> </p>\n<p>Jésus-Christ est ma sagesse </p>\n<p>Il éclaire mon chemin </p>\n<p>Et je marche en ma faiblesse </p>\n<p>Conduis par sa sûre main </p>\n<p>II éclaire mon chemin (bls) </p>\n<p>Et je marche en ma faiblesse </p>\n<p>Conduit par sa sûre main </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus-Christ est ma justice </p>\n<p>Son sang a coulé pour moi </p>\n<p>Je trouve en son sacrifice </p>\n<p>Paix et pardon par la foi </p>\n<p>Son sang a coulé pour moi (bis) </p>\n<p>Je trouve en son sacrifice </p>\n<p>Paix et pardon par la foi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus-Christ me sanctifie </p>\n<p>Au divin cep attaché </p>\n<p>Je reçois de lui la vie </p>\n<p>Qui m&#39;affranchit du péché </p>\n<p>A toi Jésus attaché (bis) </p>\n<p>Je reçois de toi la vie </p>\n<p>Qui m&#39;affranchit du péché </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus-Christ en payant ma dette </p>\n<p>A grand prix m&#39;a racheté </p>\n<p>Près de lui ma place est prête </p>\n<p>Au ciel, pour l&#39;éternité </p>\n<p>Jésus Christ m&#39;a racheté (bis) </p>\n<p>Et déjà ma place est prête </p>\n<p>Au ciel, pour l&#39;éternité </p>\n','Jesus-Christ-est-ma-sagesse',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2346,'794a1152-f8df-4618-be38-9a0b66ed2be3',0,0,'Un merveilleux Saveur est Christ mon Seigneur',75,'75_RN_fr','1.\nUn merveilleux Sauveur est Christ mon Seigneur,\nUn merveilleux Sauveur pour moi \nCar mon âme cachée au fond de son cœur.\nY trouve l’eau vive et j’en bois.\nRefrain\nMon âme en le roc crevassé s’est cachée\nEt Dieu m’y couvre par sa main \nDu monde, ma vie, enfin, s’est détachée\nPourquoi craindre le lendemain ? (bis)\n2.\nUn merveilleux Sauveur est mon Christ mon Seigneur,\nIl ôte tout fardeau pour moi,\nIl me soutient afin que je sois vainqueur.\nJe marche aujourd’hui par la foi.\n3.\nUn merveilleux Sauveur est Christ mon Seigneur,\nIl me bénit de jour en jour,\nTout joyeux plein d’élan, moi jadis pécheur \nJe chante sa grâce a toujours.\n4.\nLorsque dans sa beauté, le Roi je verrai,\nJe serai semblable a Jésus \nLe thème des chants des élus pour jamais\nSera son amour, son salut !','<p><strong>1.</strong> </p>\n<p>Un merveilleux Sauveur est Christ mon Seigneur, </p>\n<p>Un merveilleux Sauveur pour moi </p>\n<p>Car mon âme cachée au fond de son cœur. </p>\n<p>Y trouve l’eau vive et j’en bois. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mon âme en le roc crevassé s’est cachée</strong> </p>\n<p><strong>Et Dieu m’y couvre par sa main</strong> </p>\n<p><strong>Du monde, ma vie, enfin, s’est détachée</strong> </p>\n<p><strong>Pourquoi craindre le lendemain ? (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Un merveilleux Sauveur est mon Christ mon Seigneur, </p>\n<p>Il ôte tout fardeau pour moi, </p>\n<p>Il me soutient afin que je sois vainqueur. </p>\n<p>Je marche aujourd’hui par la foi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Un merveilleux Sauveur est Christ mon Seigneur, </p>\n<p>Il me bénit de jour en jour, </p>\n<p>Tout joyeux plein d’élan, moi jadis pécheur </p>\n<p>Je chante sa grâce a toujours. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Lorsque dans sa beauté, le Roi je verrai, </p>\n<p>Je serai semblable a Jésus </p>\n<p>Le thème des chants des élus pour jamais </p>\n<p>Sera son amour, son salut ! </p>\n','Un-merveilleux-Saveur-est-Christ-mon-Seigneur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2347,'69e99c87-b85f-4cfa-8b35-3b02fa228991',0,0,'Par son grand amour divin',21,'21_GA_fr','1.\nPar son grand amour divin\nJésus invite au festin\nTous les saints qu\'il a rachetés\nOh! Venez\nSa manne vous nourrira\nEt chaque jour suffira\nVenez vous tous les invités\nOh! mangez\nRefrain\nOh! venez quelle merveille Oh! venez\nQuelle fête sans pareille Oh! mangez\nCelui qui créa le pain\nEt qui de I\'eau fit du vin\nInvite tous au festin Oh! Mangez\n\n2.\nComme les siens autrefois\nObéissant à sa voix\nSoyez aussi bien dispose Oh! Venez\nVoici le poisson, le pain\nMangez vous n\'aurez plus faim\nEnfants du Seigneur! Oh! Venez Oh mangez\n\n3.\nBientôt descendra I\'Agneau\nEnlever son cher troupeau\nOh! qu\'il sera beau de le voir désormais\nVêtus d\'immortalité\nEt parfait dans I\'unité\nFrères vivons dans cet espoir à jamais','<p><strong>1.</strong> </p>\n<p>Par son grand amour divin </p>\n<p>Jésus invite au festin </p>\n<p>Tous les saints qu&#39;il a rachetés </p>\n<p>Oh! Venez </p>\n<p>Sa manne vous nourrira </p>\n<p>Et chaque jour suffira </p>\n<p>Venez vous tous les invités </p>\n<p>Oh! mangez </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh! venez quelle merveille Oh! venez</strong> </p>\n<p><strong>Quelle fête sans pareille Oh! mangez</strong> </p>\n<p><strong>Celui qui créa le pain</strong> </p>\n<p><strong>Et qui de I&#39;eau fit du vin</strong> </p>\n<p><strong>Invite tous au festin Oh! Mangez</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Comme les siens autrefois </p>\n<p>Obéissant à sa voix </p>\n<p>Soyez aussi bien dispose Oh! Venez </p>\n<p>Voici le poisson, le pain </p>\n<p>Mangez vous n&#39;aurez plus faim </p>\n<p>Enfants du Seigneur! Oh! Venez Oh mangez </p>\n<p><br/> <strong>3.</strong></p>\n<p>Bientôt descendra I&#39;Agneau </p>\n<p>Enlever son cher troupeau </p>\n<p>Oh! qu&#39;il sera beau de le voir désormais </p>\n<p>Vêtus d&#39;immortalité </p>\n<p>Et parfait dans I&#39;unité </p>\n<p>Frères vivons dans cet espoir à jamais </p>\n','Par-son-grand-amour-divin',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2348,'1a03f7d5-b671-4b84-9ddf-e4f7e320214f',0,0,'La Bible nous a tant parlée',85,'85_RN_fr','1.\nLa Bible nous a tant parlée\nD’un grand voyage à Jérusalem\nLa où les enfants du Seigneur\nIront habiter pour l’éternité','<p><strong>1.</strong> </p>\n<p>La Bible nous a tant parlée </p>\n<p>D’un grand voyage à Jérusalem </p>\n<p>La où les enfants du Seigneur </p>\n<p>Iront habiter pour l’éternité </p>\n','La-Bible-nous-a-tant-parlee',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2349,'2ad011aa-df80-461c-b7a3-82087d8f3a1a',0,0,'Segnè Jézu gad jan ou bon Segnè',55,'55_GA_ht','1.\nSegnè Jézu gad jan ou bon Segnè\nNap fè louanj, pou ou, pou tout sa ou fè\nOu deliver nou, nan aksidan yo\nNan malady yo, nan péché nou yo\nSé pou tèt sa, nap fè louanj pou ou\n\nKè\nMin jus ki lè ouap\nretounin Segnè\nAnpil pitit ou apé mouri mal\nMéchan tuyé yo, méchan manjé yo\nMin nou konnin\nouap vi-n jugé\nTout kriminèl yo\n\n2.\nNou gin konsians, sé nan labou ou jouin-n nou\nOu lave nou é pui ou fè-n vi-n pròp\nOu soufflé sou nou, souf dé vi pa ou la\nGras a ou Segnè, nous anti-n sové\nSé pou tèt sa, nap fè louanj pou ou\n\n3.\nChak jou-n levé le mon-mandé chanjman\nMin yo pansé sé gouvènman kak fè-l\nDe jou an jou nou ouè, sa ap pi mal\nPaské sé Jézu, kap vi-n fè chanjman\nSé pou tèt sa, an-n prepare tan-li\n','<p><strong>1.</strong> </p>\n<p>Segnè Jézu gad jan ou bon Segnè </p>\n<p>Nap fè louanj, pou ou, pou tout sa ou fè </p>\n<p>Ou deliver nou, nan aksidan yo </p>\n<p>Nan malady yo, nan péché nou yo </p>\n<p>Sé pou tèt sa, nap fè louanj pou ou </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Min jus ki lè ouap</strong> </p>\n<p><strong>retounin Segnè</strong> </p>\n<p><strong>Anpil pitit ou apé mouri mal</strong> </p>\n<p><strong>Méchan tuyé yo, méchan manjé yo</strong> </p>\n<p><strong>Min nou konnin</strong> </p>\n<p><strong>ouap vi-n jugé</strong> </p>\n<p><strong>Tout kriminèl yo</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Nou gin konsians, sé nan labou ou jouin-n nou </p>\n<p>Ou lave nou é pui ou fè-n vi-n pròp </p>\n<p>Ou soufflé sou nou, souf dé vi pa ou la </p>\n<p>Gras a ou Segnè, nous anti-n sové </p>\n<p>Sé pou tèt sa, nap fè louanj pou ou </p>\n<p><br/> <strong>3.</strong></p>\n<p>Chak jou-n levé le mon-mandé chanjman </p>\n<p>Min yo pansé sé gouvènman kak fè-l </p>\n<p>De jou an jou nou ouè, sa ap pi mal </p>\n<p>Paské sé Jézu, kap vi-n fè chanjman </p>\n<p>Sé pou tèt sa, an-n prepare tan-li </p>\n','Segne-Jezu-gad-jan-ou-bon-Segne',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2350,'99bb4731-3e42-41db-8383-54cf3353718e',0,0,'Seigneur je voudrais que ma vie',28,'28_GA_fr','1.\nSeigneur je voudrais que ma vie\nParle de ton amour pour moi\nEt que ma volonté se plie\nAux ordres qui viennent de toi\n\nRefrain\nPrès de la Croix à cette heure sainte\nJe m\'humilie en t\'adorant\nPour qu\'il te serve sans nulle crainte\nSeigneur prépare ton faible enfant\n\n2.\nRemplis de force ma faiblesse\nÉclaire mon aveuglement\nGuide mes pas vers toi sans cesse\nEt saisis-toi de ce moment\n\n3.\nArme Seigneur ton Serviteur\nQui chaque jour lutte à genoux\nPour remporter la victoire à l\'heure\nSeigneur Jésus veille sur nous','<p><strong>1.</strong> </p>\n<p>Seigneur je voudrais que ma vie </p>\n<p>Parle de ton amour pour moi </p>\n<p>Et que ma volonté se plie </p>\n<p>Aux ordres qui viennent de toi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Près de la Croix à cette heure sainte</strong> </p>\n<p><strong>Je m&#39;humilie en t&#39;adorant</strong> </p>\n<p><strong>Pour qu&#39;il te serve sans nulle crainte</strong> </p>\n<p><strong>Seigneur prépare ton faible enfant</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Remplis de force ma faiblesse </p>\n<p>Éclaire mon aveuglement </p>\n<p>Guide mes pas vers toi sans cesse </p>\n<p>Et saisis-toi de ce moment </p>\n<p><br/> <strong>3.</strong></p>\n<p>Arme Seigneur ton Serviteur </p>\n<p>Qui chaque jour lutte à genoux </p>\n<p>Pour remporter la victoire à l&#39;heure </p>\n<p>Seigneur Jésus veille sur nous </p>\n','Seigneur-je-voudrais-que-ma-vie',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2351,'2532d9fa-2dc7-427b-a24b-3a82cb37d9dc',0,0,'Nous les chrétiens qui attendons Jésus Christ',80,'80_RN_fr','1.\nNous les chrétiens qui attendons Jésus-Christ\nSoyons joyeux car il est notre Sauveur,\nDans sa cité d’or il nous attend et soyons prêts\nCar un jour il viendra nous prendre avec lui.\nRefrain\nChrétiens soyons victorieux\nSur la tentation et le péché\nCar il a dit : je compte sur les chrétiens en tout temps\nC’est pourquoi nous devons être victorieux\n2.\nLa tentation veut nous vaincre toujours,\nPrions Dieu il nous donnera la victoire\nCar Dieu nous donnera la victoire sur la tentation\nIl est notre médiateur pour toujours.\n3.\nOui nous attendons le Seigneur glorieux\nD’un cœur joyeux et rempli de confiance\nIl va régner sur la terre, la mer et les cieux,\nDans ce merveilleux jour de notre délivrance.\n4.\nSi la tempête, les ouragans se déchainent\nNous ne perdons pas courage, Jésus est vivant\nIl est le Prince de la paix, et le Seigneur des Seigneurs\nDans ce merveilleux jour de notre délivrance.','<p><strong>1.</strong> </p>\n<p>Nous les chrétiens qui attendons Jésus-Christ </p>\n<p>Soyons joyeux car il est notre Sauveur, </p>\n<p>Dans sa cité d’or il nous attend et soyons prêts </p>\n<p>Car un jour il viendra nous prendre avec lui. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chrétiens soyons victorieux</strong> </p>\n<p><strong>Sur la tentation et le péché</strong> </p>\n<p><strong>Car il a dit : je compte sur les chrétiens en tout temps</strong> </p>\n<p><strong>C’est pourquoi nous devons être victorieux</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>La tentation veut nous vaincre toujours, </p>\n<p>Prions Dieu il nous donnera la victoire </p>\n<p>Car Dieu nous donnera la victoire sur la tentation </p>\n<p>Il est notre médiateur pour toujours. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oui nous attendons le Seigneur glorieux </p>\n<p>D’un cœur joyeux et rempli de confiance </p>\n<p>Il va régner sur la terre, la mer et les cieux, </p>\n<p>Dans ce merveilleux jour de notre délivrance. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si la tempête, les ouragans se déchainent </p>\n<p>Nous ne perdons pas courage, Jésus est vivant </p>\n<p>Il est le Prince de la paix, et le Seigneur des Seigneurs </p>\n<p>Dans ce merveilleux jour de notre délivrance. </p>\n','Nous-les-chretiens-qui-attendons-Jesus-Christ',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2352,'45824a71-d239-435f-85e7-fadf1051b9ef',0,0,'Krétyin an nou palé de Jézu',65,'65_GA_ht','1.\nKrétyin an nou palé de Jézu\nSé gnou non-m nou papé jan-m blié\nSé lik té vin-n\npou sové lemond\nEpi li kon-n\nfè anpil mirak\nKè\nJézu sé Roi oui\nJézu sé Roi\nJézu sé Roi,\nJézu sé roi des rois\n\n2.\nJézu monté a Jéruzalèm\nGnou ban-n mou-n té vlé pou\'l vini roi\nE lè li antré\nandédan tanp la\nLi chase moun ki tap fè komès yo\n\n3.\nJézu monté a Getsémané\nGnou ban-n\nsolda té vin-n arété\'l\nTout disip li yo viré kite li\nYo di yo pa\nkonnin non-m sila\n\n4.\nEské nou min-m nou pa pou Jézu\nSi nou dakò pou-n\nsoufri pou li\nSé nou min-m yo\nva pi meprizé\nSi gnou jou nou pral rejoui avèl li','<p><strong>1.</strong> </p>\n<p>Krétyin an nou palé de Jézu </p>\n<p>Sé gnou non-m nou papé jan-m blié </p>\n<p>Sé lik té vin-n </p>\n<p>pou sové lemond </p>\n<p>Epi li kon-n </p>\n<p>fè anpil mirak </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Jézu sé Roi oui</strong> </p>\n<p><strong>Jézu sé Roi</strong> </p>\n<p><strong>Jézu sé Roi,</strong> </p>\n<p><strong>Jézu sé roi des rois</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu monté a Jéruzalèm </p>\n<p>Gnou ban-n mou-n té vlé pou&#39;l vini roi </p>\n<p>E lè li antré </p>\n<p>andédan tanp la </p>\n<p>Li chase moun ki tap fè komès yo </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jézu monté a Getsémané </p>\n<p>Gnou ban-n </p>\n<p>solda té vin-n arété&#39;l </p>\n<p>Tout disip li yo viré kite li </p>\n<p>Yo di yo pa </p>\n<p>konnin non-m sila </p>\n<p><br/> <strong>4.</strong></p>\n<p>Eské nou min-m nou pa pou Jézu </p>\n<p>Si nou dakò pou-n </p>\n<p>soufri pou li </p>\n<p>Sé nou min-m yo </p>\n<p>va pi meprizé </p>\n<p>Si gnou jou nou pral rejoui avèl li </p>\n','Kretyin-an-nou-pale-de-Jezu',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2353,'415a5b60-e31c-49a1-b0cd-a9f7b25f10a9',0,0,'Sais-tu que seul Jésus-Christ purifie',12,'12_GA_fr','1.\nSais-tu que seul Jésus-Christ purifie\nEs-tu lavé dans le sang de l\'Agneau\nAs-tu trouvé le pardon et la vie\nEs-tu lavé dans le sang de l\'agneau\n\n2.\nDans son amour il te sauve et fait grâce\nEs-tu lavé dans le sang de I\'Agneau\nÀ Golgotha il expire à ta place\nEs-tu lavé dans le sang de I\'Agneau\n\n3.\nEst-il roi, ton unique espérance\nEs-tu lavé dans le sang de l\'Agneau\nVas-tu sans peur paraître en sa présence\nEs-tu lavé dans le sang de l\'Agneau\nRefrain\nEs-tu lavé, Es-tu lavé\nEs-tu lavé dans le sang de l\'Agneau','<p><strong>1.</strong> </p>\n<p>Sais-tu que seul Jésus-Christ purifie </p>\n<p>Es-tu lavé dans le sang de l&#39;Agneau </p>\n<p>As-tu trouvé le pardon et la vie </p>\n<p>Es-tu lavé dans le sang de l&#39;agneau </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dans son amour il te sauve et fait grâce </p>\n<p>Es-tu lavé dans le sang de I&#39;Agneau </p>\n<p>À Golgotha il expire à ta place </p>\n<p>Es-tu lavé dans le sang de I&#39;Agneau </p>\n<p><br/> <strong>3.</strong></p>\n<p>Est-il roi, ton unique espérance </p>\n<p>Es-tu lavé dans le sang de l&#39;Agneau </p>\n<p>Vas-tu sans peur paraître en sa présence </p>\n<p>Es-tu lavé dans le sang de l&#39;Agneau </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Es-tu lavé, Es-tu lavé</strong> </p>\n<p><strong>Es-tu lavé dans le sang de l&#39;Agneau</strong> </p>\n','Sais-tu-que-seul-Jesus-Christ-purifie',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2354,'b7cf1f99-c116-4548-8c44-25eb015b9d0e',1,0,'Ici-bas bonheur suprême',2,'2_GA_fr','1.\nIci-bas bonheur suprême\nAvec Dieu je ne crains rien\nIl fait tout puisque je l\'aime\nConcourir à mon seul bien\nJe puis croire à sa promesse\nSon amour ne change pas\nJusqu\'à ma blanche vieillesse\nIl dirigera mes pas\n\n2.\nIl me dit quand il m\'éprouve\nDemande et tu recevras\nEt voici qui cherche, trouve\nA qui frappe, on ouvrira\nJésus dit qui cherche trouve\nQui demande, recevra\nIl permet que je les trouve\nSi je frappe, il ouvrira\n\n3.\nQuand Satan sème le doute\nSi I\'orage éclate un jour\nQuand la nuit voile ma route\nJe me fie en son amour\nPourquoi donc perdre courage\nDieu demeure il m\'aime encore\nEt pour moi chaque nuage\nA toujous sa frange d\'or','<p><strong>1.</strong> </p>\n<p>Ici-bas bonheur suprême </p>\n<p>Avec Dieu je ne crains rien </p>\n<p>Il fait tout puisque je l&#39;aime </p>\n<p>Concourir à mon seul bien </p>\n<p>Je puis croire à sa promesse </p>\n<p>Son amour ne change pas </p>\n<p>Jusqu&#39;à ma blanche vieillesse </p>\n<p>Il dirigera mes pas </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il me dit quand il m&#39;éprouve </p>\n<p>Demande et tu recevras </p>\n<p>Et voici qui cherche, trouve </p>\n<p>A qui frappe, on ouvrira </p>\n<p>Jésus dit qui cherche trouve </p>\n<p>Qui demande, recevra </p>\n<p>Il permet que je les trouve </p>\n<p>Si je frappe, il ouvrira </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand Satan sème le doute </p>\n<p>Si I&#39;orage éclate un jour </p>\n<p>Quand la nuit voile ma route </p>\n<p>Je me fie en son amour </p>\n<p>Pourquoi donc perdre courage </p>\n<p>Dieu demeure il m&#39;aime encore </p>\n<p>Et pour moi chaque nuage </p>\n<p>A toujous sa frange d&#39;or </p>\n','Ici-bas-bonheur-supreme',NULL,'fr',6,'2023-07-22 22:41:15','2023-10-03 20:28:41'),(2355,'21524c96-3cfd-488d-bab8-73371b256e07',0,0,'Moin maché nan gnou du chemin',53,'53_GA_ht','1.\nMoin maché nan gnou du chemin\nPèson-n pa ban-m sekou\nFado mouin dékourajé moin, li sanblé tèlman lou\nMouin plinyin devan Jezu, Moun-n yo pa gin amou\nEpi li repon-n mouin ak\nvoua si dous\nMouin té bouké moin moin-m tou\nLè sou kalvè maché\nKoua moin te vini si lou, moin fini pa tombé\nKrétyin se pou ou fidèl repo ou pa tro louin\nPran koua ou non,\né maché avèk mouin\n\n2.\nSouvan sé mouin vanté tèt mouin\nM-di: Mouin fè gran sèvis\nE pou-m suiv nan le bon chemin\nM-fè gran sakrifis\nMoin renonsé si bèl bagay\nPou-m vini disip Kris\nPui-m tandé-l répon-n moin ak voua byin tris\nMoin kite bèl la gloua moin, moin renonsé-l ak joua\nLe mon-n pa resevoua mouin,\nYo klouré-m sou gnou koua\nOu min-m kap fè chemin-an ak min ou nan min mouin\nPran koua ou non,\né maché avèk moin\n\n3.\nKounié-a Jézum m-vle suiv ou, avèk umilité\nE si-m bezouin mouri pou ou,\nMin-m sa pap jan-m péyé\nTout lamou ou san mezi, ke la mò ou montré-m\nAk voua byin dous,\nli repon-n li mandé-m\nSi sé sèlman ti vè dlo mouin vlé ou bay pou mouin\nOu byin poté gro fado,\nki pezé jouk la fin\nMin-m si-m bezouin ou soufri e mouri pou mouin tou\nJézu ma pran koua mouin ma fè-l pou ou','<p><strong>1.</strong> </p>\n<p>Moin maché nan gnou du chemin </p>\n<p>Pèson-n pa ban-m sekou </p>\n<p>Fado mouin dékourajé moin, li sanblé tèlman lou </p>\n<p>Mouin plinyin devan Jezu, Moun-n yo pa gin amou </p>\n<p>Epi li repon-n mouin ak </p>\n<p>voua si dous </p>\n<p>Mouin té bouké moin moin-m tou </p>\n<p>Lè sou kalvè maché </p>\n<p>Koua moin te vini si lou, moin fini pa tombé </p>\n<p>Krétyin se pou ou fidèl repo ou pa tro louin </p>\n<p>Pran koua ou non, </p>\n<p>é maché avèk mouin </p>\n<p><br/> <strong>2.</strong></p>\n<p>Souvan sé mouin vanté tèt mouin </p>\n<p>M-di: Mouin fè gran sèvis </p>\n<p>E pou-m suiv nan le bon chemin </p>\n<p>M-fè gran sakrifis </p>\n<p>Moin renonsé si bèl bagay </p>\n<p>Pou-m vini disip Kris </p>\n<p>Pui-m tandé-l répon-n moin ak voua byin tris </p>\n<p>Moin kite bèl la gloua moin, moin renonsé-l ak joua </p>\n<p>Le mon-n pa resevoua mouin, </p>\n<p>Yo klouré-m sou gnou koua </p>\n<p>Ou min-m kap fè chemin-an ak min ou nan min mouin </p>\n<p>Pran koua ou non, </p>\n<p>é maché avèk moin </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kounié-a Jézum m-vle suiv ou, avèk umilité </p>\n<p>E si-m bezouin mouri pou ou, </p>\n<p>Min-m sa pap jan-m péyé </p>\n<p>Tout lamou ou san mezi, ke la mò ou montré-m </p>\n<p>Ak voua byin dous, </p>\n<p>li repon-n li mandé-m </p>\n<p>Si sé sèlman ti vè dlo mouin vlé ou bay pou mouin </p>\n<p>Ou byin poté gro fado, </p>\n<p>ki pezé jouk la fin </p>\n<p>Min-m si-m bezouin ou soufri e mouri pou mouin tou </p>\n<p>Jézu ma pran koua mouin ma fè-l pou ou </p>\n','Moin-mache-nan-gnou-du-chemin',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2356,'6c7c02d4-e435-4652-a458-17ea3d3a448b',0,0,'Mezanmi lè-n gadé, le mon-n dégénéré',60,'60_GA_ht','1.\nMezanmi lè-n gadé, le mon-n dégénéré\n(Sé abomination)\nAbita nap redi, la tè pa vlé produi\n(Sé abomination)\nMaléré vi-n pi pòv,\nmange ap vi-n chè\n(Sé abomination)\nMoun-n ki rich ap pi rich, sa ki pòv ap pi pòv\n(Sé abomination)\n\nKè\nKretyin nou priyé,\nfò-n pa dekouraje\nJezu di nou konsa,\nlè ouè bagay sa yo\n(Konnin lap retounin)\nPiga nou artiste,\npiga nou boulvèsé,\nBonè nou pa isit\nFòk nou konnin you jou, kay papa nou nan sièl\nTout mizè va fini\n\n2.\nLè-n gadé Jodi ya, pa gingnin diférans\n(Ant pitit ak papa)\nChak moun gnou sigarèt, yap pouse la fimin,\n(Sé abomination)\nPitit gason kon fi pa respekté gran moun\n(Sé abomination)\nTout moun apé rélé, lontan pat jan-m gin sa.\n\n3.\nL\'Evanjil ap préché, pou lè zòm ta chanjé, se pi mal yap pi mal\nFan-m ap koupé chevé, gason fè pèmanant\n(Sé abomination)\nGason ap mété kèl, pou chevé yo vi-n soua\n(Sé abomination)\nFan-m ap mét pantalon, pou yo tounin gason\n(Sé abomination)\n\n4.\nYon seri malady kap antré nan le mond Ki koté yo soti\nDoctè pa ka konnin, di ki say o soufri,\n(Sé abomination)\nFan-m ap karessé fan-m, gason chèché gason\n(Sé abomination)\nLè zòm vi-n égaré, yo tout blié Bon dié\n(Sé abomination)\n','<p><strong>1.</strong> </p>\n<p>Mezanmi lè-n gadé, le mon-n dégénéré </p>\n<p>(Sé abomination) </p>\n<p>Abita nap redi, la tè pa vlé produi </p>\n<p>(Sé abomination) </p>\n<p>Maléré vi-n pi pòv, </p>\n<p>mange ap vi-n chè </p>\n<p>(Sé abomination) </p>\n<p>Moun-n ki rich ap pi rich, sa ki pòv ap pi pòv </p>\n<p>(Sé abomination) </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Kretyin nou priyé,</strong> </p>\n<p><strong>fò-n pa dekouraje</strong> </p>\n<p><strong>Jezu di nou konsa,</strong> </p>\n<p><strong>lè ouè bagay sa yo</strong> </p>\n<p><strong>(Konnin lap retounin)</strong> </p>\n<p><strong>Piga nou artiste,</strong> </p>\n<p><strong>piga nou boulvèsé,</strong> </p>\n<p><strong>Bonè nou pa isit</strong> </p>\n<p><strong>Fòk nou konnin you jou, kay papa nou nan sièl</strong> </p>\n<p><strong>Tout mizè va fini</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè-n gadé Jodi ya, pa gingnin diférans </p>\n<p>(Ant pitit ak papa) </p>\n<p>Chak moun gnou sigarèt, yap pouse la fimin, </p>\n<p>(Sé abomination) </p>\n<p>Pitit gason kon fi pa respekté gran moun </p>\n<p>(Sé abomination) </p>\n<p>Tout moun apé rélé, lontan pat jan-m gin sa. </p>\n<p><br/> <strong>3.</strong></p>\n<p>L&#39;Evanjil ap préché, pou lè zòm ta chanjé, se pi mal yap pi mal </p>\n<p>Fan-m ap koupé chevé, gason fè pèmanant </p>\n<p>(Sé abomination) </p>\n<p>Gason ap mété kèl, pou chevé yo vi-n soua </p>\n<p>(Sé abomination) </p>\n<p>Fan-m ap mét pantalon, pou yo tounin gason </p>\n<p>(Sé abomination) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Yon seri malady kap antré nan le mond Ki koté yo soti </p>\n<p>Doctè pa ka konnin, di ki say o soufri, </p>\n<p>(Sé abomination) </p>\n<p>Fan-m ap karessé fan-m, gason chèché gason </p>\n<p>(Sé abomination) </p>\n<p>Lè zòm vi-n égaré, yo tout blié Bon dié </p>\n<p>(Sé abomination) </p>\n','Mezanmi-le-n-gade-le-mon-n-degenere',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2357,'95f0a676-76f3-4b30-beeb-05ad0da41792',0,0,'Si I\'ombre de la nuit',9,'9_GA_fr','1.\nSi I\'ombre de la nuit vient obscurcir ma route\nMon Dieu en qui je crois\nEst mon ferme soutien\nSi I\'orage en mon coeur\nÉveille quelques doutes\nII me garde en sa main\nJe ne crains rien\n\n\nRefrain\nC\'est dans sa main que Dieu me tient\nII m\'aime, il me protège, il me soutient\nPour moi, jour après jour, il a son plan d\'amour\nDieu me tient dans sa main je ne crains rien\nNon je ne connais pas mon avenir d\'avance\n\n2.\nMais je m\'attends a Dieu,\nQui ne veut que mon bien\nJe sais que je peux mettre en lui ma confiance\nII me garde en sa main, je ne crains rien\n\n3.\nJe sais que son amour sera toujours le même\nQu\'il m\'accompagnera jusqu\'au bout du chemin\nOui Ie Dieu que je sers, que j\'adore et que j\'aime\nMe garde dans sa main je ne crains rien','<p><strong>1.</strong> </p>\n<p>Si I&#39;ombre de la nuit vient obscurcir ma route </p>\n<p>Mon Dieu en qui je crois </p>\n<p>Est mon ferme soutien </p>\n<p>Si I&#39;orage en mon coeur </p>\n<p>Éveille quelques doutes </p>\n<p>II me garde en sa main </p>\n<p>Je ne crains rien </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>C&#39;est dans sa main que Dieu me tient</strong> </p>\n<p><strong>II m&#39;aime, il me protège, il me soutient</strong> </p>\n<p><strong>Pour moi, jour après jour, il a son plan d&#39;amour</strong> </p>\n<p><strong>Dieu me tient dans sa main je ne crains rien</strong> </p>\n<p><strong>Non je ne connais pas mon avenir d&#39;avance</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mais je m&#39;attends a Dieu, </p>\n<p>Qui ne veut que mon bien </p>\n<p>Je sais que je peux mettre en lui ma confiance </p>\n<p>II me garde en sa main, je ne crains rien </p>\n<p><br/> <strong>3.</strong></p>\n<p>Je sais que son amour sera toujours le même </p>\n<p>Qu&#39;il m&#39;accompagnera jusqu&#39;au bout du chemin </p>\n<p>Oui Ie Dieu que je sers, que j&#39;adore et que j&#39;aime </p>\n<p>Me garde dans sa main je ne crains rien </p>\n','Si-I\'ombre-de-la-nuit',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2358,'bb4fc8ff-aa00-4782-be24-cbb665621d97',0,0,'Un vêtement blanc, une harpe d’or',77,'77_RN_fr','1.\nUn vêtement blanc, une harpe d’or\nUn beau palais, une couronne\nLa sainte joie, le vrai trésor\nLa haut le Sauveur donne\n\nRefrain\nCar Jésus est mon Sauveur qui m’a tant aimé,\nPayé ma dette sur le calvaire,\nIl m’a dans son grand amour sauvé du péché,\nJe vis dans sa sainte lumière\nOui nous marchons nous marchons tous joyeux,\nNous marchons, nous marchons vers les cieux (bis)\n2.\nDimanche je suis heureux, lundi plein de joie\nMardi j’ai une paix que le monde ne connait pas,\nMercredi et Jeudi je vis dans la lumière.\nVendredi et samedi c’est le ciel sur la terre.\n3.\nEn avant contre le péché,\nAllons soldats de courage\nProclamer ceux qui condamnes\nVers la céleste plage.\nRefrain\nJe veux louer mon Sauveur qui m’a racheté\nPayé ma dette sur le calvaire\nIl m’a dans son grand amour sauve de péché\nJe vis dans sa sainte lumière\nOui nous marchons, nous marchons tous joyeux\nNous marchons tous joyeux. (Bis)','<p><strong>1.</strong> </p>\n<p>Un vêtement blanc, une harpe d’or </p>\n<p>Un beau palais, une couronne </p>\n<p>La sainte joie, le vrai trésor </p>\n<p>La haut le Sauveur donne </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Car Jésus est mon Sauveur qui m’a tant aimé,</strong> </p>\n<p><strong>Payé ma dette sur le calvaire,</strong> </p>\n<p><strong>Il m’a dans son grand amour sauvé du péché,</strong> </p>\n<p><strong>Je vis dans sa sainte lumière</strong> </p>\n<p><strong>Oui nous marchons nous marchons tous joyeux,</strong> </p>\n<p><strong>Nous marchons, nous marchons vers les cieux (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dimanche je suis heureux, lundi plein de joie </p>\n<p>Mardi j’ai une paix que le monde ne connait pas, </p>\n<p>Mercredi et Jeudi je vis dans la lumière. </p>\n<p>Vendredi et samedi c’est le ciel sur la terre. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En avant contre le péché, </p>\n<p>Allons soldats de courage </p>\n<p>Proclamer ceux qui condamnes </p>\n<p>Vers la céleste plage. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je veux louer mon Sauveur qui m’a racheté</strong> </p>\n<p><strong>Payé ma dette sur le calvaire</strong> </p>\n<p><strong>Il m’a dans son grand amour sauve de péché</strong> </p>\n<p><strong>Je vis dans sa sainte lumière</strong> </p>\n<p><strong>Oui nous marchons, nous marchons tous joyeux</strong> </p>\n<p><strong>Nous marchons tous joyeux. (Bis)</strong> </p>\n','Un-vetement-blanc-une-harpe-d\'or',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2359,'74e21aa9-6426-40be-9ece-4c05601ad605',0,0,'Bethleem naquit mon Rédempteur',89,'89_RN_fr','1.\nBéthléem naquit mon Rédempteur\nQui connait le mépris et la douleur \nO ! Meilleur amour, gloire au Sauveur\nQui m’a cherché, sauvé,\nIl m’a cherché, sauvé (bis)\nO ! Meilleur amour de mon Sauveur\nQui m’a cherché, sauvé.\n2.\nIl paya ma dette sur le calvaire,\nDélivra mon âme de sa misère\nIl m’apporta le pardon de son père,\nJésus est mort pour moi !\nJésus est mort pour moi ! (bis)\nIl m’apporta le pardon de son père.\nJésus est mort pour moi.\n3.\nFuyant le bercail, je cherchais en vain\nUn asile sur…quand sa voix soudain\nMe dit : « Mes brebis, je suis le chemin «\nDe la vie Eternelle !\nOui, je suis le chemin… (Bis)\nJésus me dit, de la vie éternelle,\nJe suis le seul chemin !\n4.\nJe suis heureux, même dans la souffrance\nJésus revient  j’attends la délivrance,\nJe m’en vais à lui plein de confiance\nIl m’appelle là-haut, (bis)\nJe m’en vais à lui plein de confiance.\nIl m’appelle là-haut.','<p><strong>1.</strong> </p>\n<p>Béthléem naquit mon Rédempteur </p>\n<p>Qui connait le mépris et la douleur </p>\n<p>O ! Meilleur amour, gloire au Sauveur </p>\n<p>Qui m’a cherché, sauvé, </p>\n<p>Il m’a cherché, sauvé (bis) </p>\n<p>O ! Meilleur amour de mon Sauveur </p>\n<p>Qui m’a cherché, sauvé. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il paya ma dette sur le calvaire, </p>\n<p>Délivra mon âme de sa misère </p>\n<p>Il m’apporta le pardon de son père, </p>\n<p>Jésus est mort pour moi ! </p>\n<p>Jésus est mort pour moi ! (bis) </p>\n<p>Il m’apporta le pardon de son père. </p>\n<p>Jésus est mort pour moi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fuyant le bercail, je cherchais en vain </p>\n<p>Un asile sur…quand sa voix soudain </p>\n<p>Me dit : « Mes brebis, je suis le chemin « </p>\n<p>De la vie Eternelle ! </p>\n<p>Oui, je suis le chemin… (Bis) </p>\n<p>Jésus me dit, de la vie éternelle, </p>\n<p>Je suis le seul chemin ! </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je suis heureux, même dans la souffrance </p>\n<p>Jésus revient  j’attends la délivrance, </p>\n<p>Je m’en vais à lui plein de confiance </p>\n<p>Il m’appelle là-haut, (bis) </p>\n<p>Je m’en vais à lui plein de confiance. </p>\n<p>Il m’appelle là-haut. </p>\n','Bethleem-naquit-mon-Redempteur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2360,'0f20b70a-16df-4762-b434-130de2d9a429',0,0,'Dans les vallons et sur les montagnes',91,'91_RN_fr','1.\nDans les vallons et sur les montagnes\nA la clarté d’un rayon divin,\nJésus mon rédempteur m’accompagne,\nIl est mon guide jusqu’à la fin.\n\nRefrain\nClarté céleste ! Clarté céleste !\nRemplis mon cœur d’un rayon divin.\nClarté céleste ! Clarté céleste !\nInonde de soleil mon chemin.\n\n2.\nLorsque le nuage m’environne,\nIl ne peut cacher mon rédempteur,\nCar sa lumière sur moi rayonne\nLa nuit perdu toute terreur.\n\n3.\nEn avant chrétiens dans l’allégresse !\nToujours plus haut, montez vers les cieux\nMarchez en chantant, louez sans cesse\nChrist le Seigneur, Jésus le fils de Dieu.','<p><strong>1.</strong> </p>\n<p>Dans les vallons et sur les montagnes </p>\n<p>A la clarté d’un rayon divin, </p>\n<p>Jésus mon rédempteur m’accompagne, </p>\n<p>Il est mon guide jusqu’à la fin. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Clarté céleste ! Clarté céleste !</strong> </p>\n<p><strong>Remplis mon cœur d’un rayon divin.</strong> </p>\n<p><strong>Clarté céleste ! Clarté céleste !</strong> </p>\n<p><strong>Inonde de soleil mon chemin.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lorsque le nuage m’environne, </p>\n<p>Il ne peut cacher mon rédempteur, </p>\n<p>Car sa lumière sur moi rayonne </p>\n<p>La nuit perdu toute terreur. </p>\n<p><br/> <strong>3.</strong></p>\n<p>En avant chrétiens dans l’allégresse ! </p>\n<p>Toujours plus haut, montez vers les cieux </p>\n<p>Marchez en chantant, louez sans cesse </p>\n<p>Christ le Seigneur, Jésus le fils de Dieu. </p>\n','Dans-les-vallons-et-sur-les-montagnes',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2361,'c82ad21b-adb3-4e6c-a8c7-acc7e07dd201',0,0,'Dors bien-aimé(e)',22,'22_GA_fr','1.\nDors bien-aimé(e)\nTon oeuvre est terminée\nVoici pour toi le soir de la journée\nDu grand repos I\'heure est enfin sonnée\nA Dieu mon frère (soeur) à Dieu\n\n2.\nSi nous t\'aimons!\nChris t\'aime d\'avantage\nII t\'a conduit dans ton pèlerinage\nII te soutient dans le sombre passage\nA Dieu mon frère (soeur) à Dieu\n\n3.\nÂme fidèle entre dans la patrie\nD\'où la souffrance est à jamais bannie\nBois à longs traits au fleuve de la vie\nA Dieu mon frere (soeur) a Dieu\n\n4.\nSublime espoir Jésus-Christ va paraître\nPour saluer le retour de ton maître\nTon corps bientôt glorieux va reparaître\nA Dieu mon frère (soeur) à Dieu','<p><strong>1.</strong> </p>\n<p>Dors bien-aimé(e) </p>\n<p>Ton oeuvre est terminée </p>\n<p>Voici pour toi le soir de la journée </p>\n<p>Du grand repos I&#39;heure est enfin sonnée </p>\n<p>A Dieu mon frère (soeur) à Dieu </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si nous t&#39;aimons! </p>\n<p>Chris t&#39;aime d&#39;avantage </p>\n<p>II t&#39;a conduit dans ton pèlerinage </p>\n<p>II te soutient dans le sombre passage </p>\n<p>A Dieu mon frère (soeur) à Dieu </p>\n<p><br/> <strong>3.</strong></p>\n<p>Âme fidèle entre dans la patrie </p>\n<p>D&#39;où la souffrance est à jamais bannie </p>\n<p>Bois à longs traits au fleuve de la vie </p>\n<p>A Dieu mon frere (soeur) a Dieu </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sublime espoir Jésus-Christ va paraître </p>\n<p>Pour saluer le retour de ton maître </p>\n<p>Ton corps bientôt glorieux va reparaître </p>\n<p>A Dieu mon frère (soeur) à Dieu </p>\n','Dors-bien-aime(e)',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2362,'67f59c0c-5955-44ec-9f85-f304c0546505',0,0,'Merci, Seigneur merci',23,'23_GA_fr','1.\nMerci, Seigneur merci\nQue dirais-je de plus\nMerci pour le salut\nO mon Seigneur Jésus\nMerci pour le grand festin que je suis invité\nOui merci, merci pour ta grâce et pour ta charité','<p><strong>1.</strong> </p>\n<p>Merci, Seigneur merci </p>\n<p>Que dirais-je de plus </p>\n<p>Merci pour le salut </p>\n<p>O mon Seigneur Jésus </p>\n<p>Merci pour le grand festin que je suis invité </p>\n<p>Oui merci, merci pour ta grâce et pour ta charité </p>\n','Merci-Seigneur-merci',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2363,'f65d59d9-634c-4b42-b1e7-4a130b5b1602',0,0,'Mon Sauveur est vêtu de fin lin',33,'33_GA_fr','1.\nMon Sauveur est vêtu de fin lin\nEt de myrrhe embaumé\nAu milieu du celeste jardin\nLes cieux I\'ont acclame.\n\nRefrain\nDe ses merveilleux palais d\'ivoire\nDans ce monde perdu\nAprès avoir quitté la gloire\nJésus est descendu\n\n2.\nAlors il a connu la douleur\nEt I\'aloès amer\nCe fut pour nous sauver du malheur\nÀ jamais de I\'enfer\n\n3.\nDe cassia ses habits royaux\nOnt dû se recouvrir\nII a bien voulu porter nos maux\nPour pouvoir les guerir\n\n4.\nGloire à toi bien-aimé Rédempteur\nPour ton immense amour\nDe ta descente en triomphateur\nNous saluons le jour','<p><strong>1.</strong> </p>\n<p>Mon Sauveur est vêtu de fin lin </p>\n<p>Et de myrrhe embaumé </p>\n<p>Au milieu du celeste jardin </p>\n<p>Les cieux I&#39;ont acclame. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>De ses merveilleux palais d&#39;ivoire</strong> </p>\n<p><strong>Dans ce monde perdu</strong> </p>\n<p><strong>Après avoir quitté la gloire</strong> </p>\n<p><strong>Jésus est descendu</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Alors il a connu la douleur </p>\n<p>Et I&#39;aloès amer </p>\n<p>Ce fut pour nous sauver du malheur </p>\n<p>À jamais de I&#39;enfer </p>\n<p><br/> <strong>3.</strong></p>\n<p>De cassia ses habits royaux </p>\n<p>Ont dû se recouvrir </p>\n<p>II a bien voulu porter nos maux </p>\n<p>Pour pouvoir les guerir </p>\n<p><br/> <strong>4.</strong></p>\n<p>Gloire à toi bien-aimé Rédempteur </p>\n<p>Pour ton immense amour </p>\n<p>De ta descente en triomphateur </p>\n<p>Nous saluons le jour </p>\n','Mon-Sauveur-est-vetu-de-fin-lin',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2364,'850ccfba-c1c4-46e4-a548-4b35c6bc9864',0,0,'Je possède un Sauveur',34,'34_GA_fr','1.\nJe possède un Sauveur\nDon l\'amour fut si grand\nIl connut la suprême agonie\nDon sublime Oh c\'est moi\nQue le divin mourrant\nVeut sauveur dans sa grâce infinie\n\nRefrain\nMoi, moi, il sauve un pêcheur tel que moi\nLes épines déchirent et son fron adorable\nPour sauveur un pêcheur tel que moi\n\n2.\nC\'est pour moi qu\'il souffrit\net qu\'en Gethsémané\nIl pleura buvant la coupe amère\nDans son immense amour il m\'a tant pardonné\nEn mourrant sur la croix du calvaire\n\n3.\nD\'un amour si profound\nJe fus convit, brisé\nÀ genoux près de la croix benie\nJe reçus son pardon et mon coeur apaisé\nA trouvé le Bonheur et la vie','<p><strong>1.</strong> </p>\n<p>Je possède un Sauveur </p>\n<p>Don l&#39;amour fut si grand </p>\n<p>Il connut la suprême agonie </p>\n<p>Don sublime Oh c&#39;est moi </p>\n<p>Que le divin mourrant </p>\n<p>Veut sauveur dans sa grâce infinie </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Moi, moi, il sauve un pêcheur tel que moi</strong> </p>\n<p><strong>Les épines déchirent et son fron adorable</strong> </p>\n<p><strong>Pour sauveur un pêcheur tel que moi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>C&#39;est pour moi qu&#39;il souffrit </p>\n<p>et qu&#39;en Gethsémané </p>\n<p>Il pleura buvant la coupe amère </p>\n<p>Dans son immense amour il m&#39;a tant pardonné </p>\n<p>En mourrant sur la croix du calvaire </p>\n<p><br/> <strong>3.</strong></p>\n<p>D&#39;un amour si profound </p>\n<p>Je fus convit, brisé </p>\n<p>À genoux près de la croix benie </p>\n<p>Je reçus son pardon et mon coeur apaisé </p>\n<p>A trouvé le Bonheur et la vie </p>\n','Je-possede-un-Sauveur',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2365,'37ac170a-467d-48d3-866a-4ed3308fe46c',0,0,'Min you dous promès',47,'47_GA_ht','1.\nMin you dous promès moin pa janm blié ou\nAnyin pap molesté, ni detounin moin\nMin-m si ténèb la fè mouin pa ouè du tou\nPi devan la jouinin selès ap kléré-m\n\nKè\nMouin pap kite ou ni blie ou\nNan min mouin ma kimbe ou\nNan bra mouin antoure ou\nMouin pa kité ou ni blié ou\nMouin sé Rédanmtè ou, ma okipé ou\n\n2.\nMap kouè promès la, moin pap janm bliè ou\nAn avan map maché ak gnou chan jouye\nZanmi le mon-n blié-m é méprizé-m tou\nMin Jézu sonjé moin\nsé sa ki konté\n\n3.\nKan lè-a rive pou moin\nkit tè sa-a\nMap tandè la voua Jézu-kri Redanmtè\nOu té kinbé la fouaa, tout tan ou la ba\nAntré isit bon é fidèl Sèvitè','<p><strong>1.</strong> </p>\n<p>Min you dous promès moin pa janm blié ou </p>\n<p>Anyin pap molesté, ni detounin moin </p>\n<p>Min-m si ténèb la fè mouin pa ouè du tou </p>\n<p>Pi devan la jouinin selès ap kléré-m </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Mouin pap kite ou ni blie ou</strong> </p>\n<p><strong>Nan min mouin ma kimbe ou</strong> </p>\n<p><strong>Nan bra mouin antoure ou</strong> </p>\n<p><strong>Mouin pa kité ou ni blié ou</strong> </p>\n<p><strong>Mouin sé Rédanmtè ou, ma okipé ou</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Map kouè promès la, moin pap janm bliè ou </p>\n<p>An avan map maché ak gnou chan jouye </p>\n<p>Zanmi le mon-n blié-m é méprizé-m tou </p>\n<p>Min Jézu sonjé moin </p>\n<p>sé sa ki konté </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kan lè-a rive pou moin </p>\n<p>kit tè sa-a </p>\n<p>Map tandè la voua Jézu-kri Redanmtè </p>\n<p>Ou té kinbé la fouaa, tout tan ou la ba </p>\n<p>Antré isit bon é fidèl Sèvitè </p>\n','Min-you-dous-promes',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2366,'48f6a51d-3a81-48b4-ab7d-e9f0a80eca0d',0,0,'Tribulation nou Segnè',40,'40_GA_ht','1.\nTribulation nou Segnè, Segnè, Segnè, Segnè\nProblèm nou y oak kalamité\nYo tout vlé poté nou alé (bis)\n\nChak jou-n levé sé gnou sèl kozé\nNou pa kab manjé, nou pa kab abiyé\nPitit nou yo pa kap alé lékol\nNa pé mande ki koté sa pralé. O mon Dié\n\nKè\nProblèm nou yo, traka nou yo\nYo tout vlé poté nou alé\n\n2.\nLè gro problem vini pou\'l troubé nou\nLè tout espoua sanblé fini pou nou\nLè-n pa konnin si pou-n monté, ni desan-n\nNou vin-n koté pou-n monté, ni desan-n\nNou vin-n koté pou toujou\ndeliver-n o mon Dié\n\nLè-n gin gnou problem ki pa ka rézoud\nLè-n pa konnin si pou-n monté ni desan-n\nPran téléfòn siel la e pi telephone anro\nPriè va monté, délivrans va desan-n','<p><strong>1.</strong> </p>\n<p>Tribulation nou Segnè, Segnè, Segnè, Segnè </p>\n<p>Problèm nou y oak kalamité </p>\n<p>Yo tout vlé poté nou alé (bis) </p>\n<p>Chak jou-n levé sé gnou sèl kozé </p>\n<p>Nou pa kab manjé, nou pa kab abiyé </p>\n<p>Pitit nou yo pa kap alé lékol </p>\n<p>Na pé mande ki koté sa pralé. O mon Dié </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Problèm nou yo, traka nou yo</strong> </p>\n<p><strong>Yo tout vlé poté nou alé</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè gro problem vini pou&#39;l troubé nou </p>\n<p>Lè tout espoua sanblé fini pou nou </p>\n<p>Lè-n pa konnin si pou-n monté, ni desan-n </p>\n<p>Nou vin-n koté pou-n monté, ni desan-n </p>\n<p>Nou vin-n koté pou toujou </p>\n<p>deliver-n o mon Dié </p>\n<p>Lè-n gin gnou problem ki pa ka rézoud </p>\n<p>Lè-n pa konnin si pou-n monté ni desan-n </p>\n<p>Pran téléfòn siel la e pi telephone anro </p>\n<p>Priè va monté, délivrans va desan-n </p>\n','Tribulation-nou-Segne',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2367,'7d9125ab-b929-4e68-9a70-aa9990415ea1',0,0,'Sur cette Terre où Dieu veut que j\'habite',4,'4_GA_fr','1.\nSur cette Terre où Dieu veut que j\'habite\nAu jour d\'orage en son coeur je m\'abrite\nEt son amour ne m\'abandonne pas\n\nRefrain\nDe I\'avenir je ne sais bien d\'avance\nMais sans effroi, pas à pas, je m\'avance\nPuisqu\'en mon Dieu je mets ma confiance\nII me conduit et son regard me suit\n\n2.\nQuand sur ma route un obstacle se dresse\nQuand je me blesse au caillou du chemin\nSans hésiter à mon Dieu je m\'adresse\nII prend alors ma main faible en sa main\n\n3.\nQuand finira le chemin de ma vie\nQuand j\'atteindrai le rivage éternel\nDieu m\'ouvrira dans sa grâce infinie\nTous les sentiers de la gloire du ciel','<p><strong>1.</strong> </p>\n<p>Sur cette Terre où Dieu veut que j&#39;habite </p>\n<p>Au jour d&#39;orage en son coeur je m&#39;abrite </p>\n<p>Et son amour ne m&#39;abandonne pas </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>De I&#39;avenir je ne sais bien d&#39;avance</strong> </p>\n<p><strong>Mais sans effroi, pas à pas, je m&#39;avance</strong> </p>\n<p><strong>Puisqu&#39;en mon Dieu je mets ma confiance</strong> </p>\n<p><strong>II me conduit et son regard me suit</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand sur ma route un obstacle se dresse </p>\n<p>Quand je me blesse au caillou du chemin </p>\n<p>Sans hésiter à mon Dieu je m&#39;adresse </p>\n<p>II prend alors ma main faible en sa main </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand finira le chemin de ma vie </p>\n<p>Quand j&#39;atteindrai le rivage éternel </p>\n<p>Dieu m&#39;ouvrira dans sa grâce infinie </p>\n<p>Tous les sentiers de la gloire du ciel </p>\n','Sur-cette-Terre-ou-Dieu-veut-que-j\'habite',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2368,'bb006777-2532-4bec-9884-47187ab7239e',0,0,'Jézu pral jugé kriminèl yo',42,'42_GA_ht','1.\nJézu pral jugé kriminèl yo\nE lap kondané yo\nJézu pral jujé kriminèl yo\nLap prononsé santans yo\nLè li va louvri gro liv la\nPoul fè apèl mechan yo\nJézu pral jugé kriminèl yo\nE yap kondané\n\n2.\nLè-n rete nan gadé sa kap pasé\nNou pakap pa pale\nMéchan yo, ap touyé, yap maltreté\nTout pitit Bon Dié yo\nYo tuyé ti bébé nan bèso yo\nSa a sé méchansté\nL\'étènèl pral vanjé san inosan\nYo té sakrifié\n\n3.\nLè nou ouè sa kap pasé pitit Bondié yo\nDlo kouri nan jé nou\nYareté yo, maspinin yo, krazé zo yo\nYo min-m fizié yo\nYo mété yo nan prizon yo bliyé yo\nSa-a sé méchansté\nGin gnou jou ki pral rive\nYo pap ka kinbé yo, fòk yo libéré','<p><strong>1.</strong> </p>\n<p>Jézu pral jugé kriminèl yo </p>\n<p>E lap kondané yo </p>\n<p>Jézu pral jujé kriminèl yo </p>\n<p>Lap prononsé santans yo </p>\n<p>Lè li va louvri gro liv la </p>\n<p>Poul fè apèl mechan yo </p>\n<p>Jézu pral jugé kriminèl yo </p>\n<p>E yap kondané </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè-n rete nan gadé sa kap pasé </p>\n<p>Nou pakap pa pale </p>\n<p>Méchan yo, ap touyé, yap maltreté </p>\n<p>Tout pitit Bon Dié yo </p>\n<p>Yo tuyé ti bébé nan bèso yo </p>\n<p>Sa a sé méchansté </p>\n<p>L&#39;étènèl pral vanjé san inosan </p>\n<p>Yo té sakrifié </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lè nou ouè sa kap pasé pitit Bondié yo </p>\n<p>Dlo kouri nan jé nou </p>\n<p>Yareté yo, maspinin yo, krazé zo yo </p>\n<p>Yo min-m fizié yo </p>\n<p>Yo mété yo nan prizon yo bliyé yo </p>\n<p>Sa-a sé méchansté </p>\n<p>Gin gnou jou ki pral rive </p>\n<p>Yo pap ka kinbé yo, fòk yo libéré </p>\n','Jezu-pral-juge-kriminel-yo',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2369,'c3f01487-9de0-477b-9fcb-494a2d5e29b9',0,0,'Mapé suiv ou O Bon Sovè',51,'51_GA_ht','1.\nMapé suiv ou O Bon Sovè\nNinpòt koté ou pralé\nTout koté ou vle kondui moin\nPran devan mape mache\n\nKè\nBon Sovè mapé suiv ou\nOu kite mouri pou mouin\nMin-m si tout lòt\nmoun ta nié ou\nMin Segnè konté sou mouin\n\n2.\nChemin an té mèt difisil\nSan lumiè e sans trasé\nDepi ou la, lap vini fasil\nMap suiv tras ou\né mapé maché\n\n3.\nSi ou minin-m nan gran joudin an\nGran kouran pap fè mouin pè\nPaské ou traverse-l avan\nMin Jézu map suiv ou pi pré','<p><strong>1.</strong> </p>\n<p>Mapé suiv ou O Bon Sovè </p>\n<p>Ninpòt koté ou pralé </p>\n<p>Tout koté ou vle kondui moin </p>\n<p>Pran devan mape mache </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Bon Sovè mapé suiv ou</strong> </p>\n<p><strong>Ou kite mouri pou mouin</strong> </p>\n<p><strong>Min-m si tout lòt</strong> </p>\n<p><strong>moun ta nié ou</strong> </p>\n<p><strong>Min Segnè konté sou mouin</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Chemin an té mèt difisil </p>\n<p>San lumiè e sans trasé </p>\n<p>Depi ou la, lap vini fasil </p>\n<p>Map suiv tras ou </p>\n<p>é mapé maché </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ou minin-m nan gran joudin an </p>\n<p>Gran kouran pap fè mouin pè </p>\n<p>Paské ou traverse-l avan </p>\n<p>Min Jézu map suiv ou pi pré </p>\n','Mape-suiv-ou-O-Bon-Sove',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2370,'993b5124-3790-4ecc-9e12-48ff150ab946',0,0,'Pouki ou bat kò ou',45,'45_GA_ht','1.\nPouki ou bat kò ou pou sa kap vin-n demin\nPou tout bagay kè nou pa kap konpran-n\nSi nap viv chak jou pou-n atin-n bu an\nSé pou-n véyé pou-n touché ké ròb le Ségnè\n\nKè\nLòt pa nou kapab minnin nou a la pèfeksyon\nLòt revèy Bon Dié va pi gran ké tout sa avan\nE lè voi sa pale sé voi Jézu\nAn nou véyé pou-n touché ké ròb le Segnè\n\n2.\nPouki bat kò ou pou gnou mariaj fòsé\nPouki ou pa tan-n le Segnè fè choua pou ou\nEské marié vlé di sové pou sa\nSé pou-n véyé pou-n touché ké ròb le Segnè\n\n3.\nFòk nou fò,fòok nou krié an avan\nFò-n pa faché lè bagay yo maché mal\nJézu konnin bezoin chak pitit li\nSé pou-n véyé pou-n touché ké ròb le Segnè','<p><strong>1.</strong> </p>\n<p>Pouki ou bat kò ou pou sa kap vin-n demin </p>\n<p>Pou tout bagay kè nou pa kap konpran-n </p>\n<p>Si nap viv chak jou pou-n atin-n bu an </p>\n<p>Sé pou-n véyé pou-n touché ké ròb le Ségnè </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Lòt pa nou kapab minnin nou a la pèfeksyon</strong> </p>\n<p><strong>Lòt revèy Bon Dié va pi gran ké tout sa avan</strong> </p>\n<p><strong>E lè voi sa pale sé voi Jézu</strong> </p>\n<p><strong>An nou véyé pou-n touché ké ròb le Segnè</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pouki bat kò ou pou gnou mariaj fòsé </p>\n<p>Pouki ou pa tan-n le Segnè fè choua pou ou </p>\n<p>Eské marié vlé di sové pou sa </p>\n<p>Sé pou-n véyé pou-n touché ké ròb le Segnè </p>\n<p><br/> <strong>3.</strong></p>\n<p>Fòk nou fò,fòok nou krié an avan </p>\n<p>Fò-n pa faché lè bagay yo maché mal </p>\n<p>Jézu konnin bezoin chak pitit li </p>\n<p>Sé pou-n véyé pou-n touché ké ròb le Segnè </p>\n','Pouki-ou-bat-ko-ou',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2371,'2f46ead0-4824-4299-8c59-1a47606a0b5f',0,0,'Jésus, Ô nom qui surpasse',6,'6_GA_fr','1.\nJésus, Ô nom qui surpasse\nTout nom qu\'on puisse exafter\nQue jamais je ne me lasse\nNom béni de te chanter\n\n2.\nII t\'appelle ne crains plus\nMon frère entend, écoute\nSuis ses pas et sur la route\nChante ce beau nom Jésus\n\n3.\nQui pleura sur ceux qui pleurent\nC\'est lui I\'homme méprise\nQui mourut pour ceux qui meurent\nC\'est lui l\'homme au coeur brisé\n\n4.\nJésus par qui Dieu pardonne\nRoi d\'épines couronne\nQue le monde t\'abandonne\nA toi mon coeur s\'est donné','<p><strong>1.</strong> </p>\n<p>Jésus, Ô nom qui surpasse </p>\n<p>Tout nom qu&#39;on puisse exafter </p>\n<p>Que jamais je ne me lasse </p>\n<p>Nom béni de te chanter </p>\n<p><br/> <strong>2.</strong></p>\n<p>II t&#39;appelle ne crains plus </p>\n<p>Mon frère entend, écoute </p>\n<p>Suis ses pas et sur la route </p>\n<p>Chante ce beau nom Jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>Qui pleura sur ceux qui pleurent </p>\n<p>C&#39;est lui I&#39;homme méprise </p>\n<p>Qui mourut pour ceux qui meurent </p>\n<p>C&#39;est lui l&#39;homme au coeur brisé </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus par qui Dieu pardonne </p>\n<p>Roi d&#39;épines couronne </p>\n<p>Que le monde t&#39;abandonne </p>\n<p>A toi mon coeur s&#39;est donné </p>\n','Jesus-O-nom-qui-surpasse',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2372,'11d2e940-52aa-4e81-89a9-d28b03ce608e',0,0,'Je vais chantant l\'amour de mon sauveur',17,'17_GA_fr','1.\nJe vais chantant l\'amour de mon sauveur\nSur le chemin de la vie\nSous le ciel bleu comme au jour du malheur\nSa grâce est infinie\nRefrain\nChante aussi avec moi\nMalgré I\'horizon sombre\nOui chante par la foi\nCar pour celui qui croit\nJésus dissipe I\'ombre\n\n2.\nSi tu pressens le danger imminent\nDu péché qui t\'accable\nLève les yeux vers Jésus triomphant\nSa grâce est secourable\n\n3.\nQuand tu verras surgir sur ton chemin\nLes épreuves mystérieuses\nRegarde au Christ qui saisira ta main\nSa grâce est merveilleuse','<p><strong>1.</strong> </p>\n<p>Je vais chantant l&#39;amour de mon sauveur </p>\n<p>Sur le chemin de la vie </p>\n<p>Sous le ciel bleu comme au jour du malheur </p>\n<p>Sa grâce est infinie </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chante aussi avec moi</strong> </p>\n<p><strong>Malgré I&#39;horizon sombre</strong> </p>\n<p><strong>Oui chante par la foi</strong> </p>\n<p><strong>Car pour celui qui croit</strong> </p>\n<p><strong>Jésus dissipe I&#39;ombre</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tu pressens le danger imminent </p>\n<p>Du péché qui t&#39;accable </p>\n<p>Lève les yeux vers Jésus triomphant </p>\n<p>Sa grâce est secourable </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand tu verras surgir sur ton chemin </p>\n<p>Les épreuves mystérieuses </p>\n<p>Regarde au Christ qui saisira ta main </p>\n<p>Sa grâce est merveilleuse </p>\n','Je-vais-chantant-l\'amour-de-mon-sauveur',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2373,'c7533b3c-8a9a-4827-80bb-8e943e37bc26',0,0,'J\'ai trouve Jésus l\'admirable',26,'26_GA_fr','1.\nJ\'ai trouve Jésus l\'admirable\nLe Dieu fort, le prince de paix\nQui vint dans un monde coupable\nSemez en tous lieux des bienfaits\n\nRefrain\nChacun devrait savoir\nQu\'en Christ est mon espoir\nServir mon sauveur adorable\nC\'est mon suprême devoir\n\n2.\nJ\'ai trouvé la paix de mon âme\nLe repos si longtemps cherche\nCe bonheur que chacun réclame\nEn Jésus se trouve caché\n\n3.\nJ\'ai trouvé la grande famille\nDes vaillants terrains de I\'Agneau\nTous inscrits au livre de vie\nEt par I\'esprit nés de nouveau\n\n4.\nJ\'ai trouvé la sainte boussole\nQui me dirige vers le port\nC\'est de Dieu la bonne parole\nSeule, me rend joyeux et fort','<p><strong>1.</strong> </p>\n<p>J&#39;ai trouve Jésus l&#39;admirable </p>\n<p>Le Dieu fort, le prince de paix </p>\n<p>Qui vint dans un monde coupable </p>\n<p>Semez en tous lieux des bienfaits </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Chacun devrait savoir</strong> </p>\n<p><strong>Qu&#39;en Christ est mon espoir</strong> </p>\n<p><strong>Servir mon sauveur adorable</strong> </p>\n<p><strong>C&#39;est mon suprême devoir</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>J&#39;ai trouvé la paix de mon âme </p>\n<p>Le repos si longtemps cherche </p>\n<p>Ce bonheur que chacun réclame </p>\n<p>En Jésus se trouve caché </p>\n<p><br/> <strong>3.</strong></p>\n<p>J&#39;ai trouvé la grande famille </p>\n<p>Des vaillants terrains de I&#39;Agneau </p>\n<p>Tous inscrits au livre de vie </p>\n<p>Et par I&#39;esprit nés de nouveau </p>\n<p><br/> <strong>4.</strong></p>\n<p>J&#39;ai trouvé la sainte boussole </p>\n<p>Qui me dirige vers le port </p>\n<p>C&#39;est de Dieu la bonne parole </p>\n<p>Seule, me rend joyeux et fort </p>\n','J\'ai-trouve-Jesus-l\'admirable',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2374,'81ec25ed-ab5f-4172-af03-35f02433c6a7',0,0,'Eské ou pa santi ou preské desidé',43,'43_GA_ht','1.\nEské ou pa santi ou preské desidé\nEskè ou pa manké angnin pou ou fè\nMin pou ki sa ou\ndi a Jézu-Kri\nDemin map suiv ou\nJodi-a non\n(2 lignes 2 foua)\n\n2.\nEské ou santi ou déja konvèti\nKité le mon-m\nvini jouin-n Jézu\nE si ou moun san konvèti\nKonnin byin ké ou\npèdi nan-m ou\n(bis 2 lign)\n\n3.\nKonnin Jézu se gnou bon bègé\nLè ou va an prézans\ngran juj la\nPaské Jodi sé lè pou fè li\nDemin li kapab tro ta, pou ou\n(bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Eské ou pa santi ou preské desidé </p>\n<p>Eskè ou pa manké angnin pou ou fè </p>\n<p>Min pou ki sa ou </p>\n<p>di a Jézu-Kri </p>\n<p>Demin map suiv ou </p>\n<p>Jodi-a non </p>\n<p>(2 lignes 2 foua) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Eské ou santi ou déja konvèti </p>\n<p>Kité le mon-m </p>\n<p>vini jouin-n Jézu </p>\n<p>E si ou moun san konvèti </p>\n<p>Konnin byin ké ou </p>\n<p>pèdi nan-m ou </p>\n<p>(bis 2 lign) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Konnin Jézu se gnou bon bègé </p>\n<p>Lè ou va an prézans </p>\n<p>gran juj la </p>\n<p>Paské Jodi sé lè pou fè li </p>\n<p>Demin li kapab tro ta, pou ou </p>\n<p>(bis 2 lignes) </p>\n','Eske-ou-pa-santi-ou-preske-deside',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2375,'88e53228-5f9d-459e-9b1e-6be34f4925b2',0,0,'Gin gnou non moin rinmin chanté',57,'57_GA_ht','1.\nGin gnou non moin rinmin chanté\nSé gnou chan moin\npap jan-m bliyé\nPaské sé pa li moin sové\nSé présié nin de Jézu\n\nKè\nO oui mouin rinmin Jézu (ter)\nPaské li rinmin-m avan\n\n2.\nBoua kalvé moin pap jan-m blié\nSé la li moui pou sové-m\nTout péché ki vlé vi-n lave\nNan san li ki koulé la\n\n3.\nParòl Bon Dieu mapé sere\nNan kè moin pou mouin pa péché\nKont Jézu ki té vlc sovc-m\nLè li té mouri pou nou','<p><strong>1.</strong> </p>\n<p>Gin gnou non moin rinmin chanté </p>\n<p>Sé gnou chan moin </p>\n<p>pap jan-m bliyé </p>\n<p>Paské sé pa li moin sové </p>\n<p>Sé présié nin de Jézu </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>O oui mouin rinmin Jézu (ter)</strong> </p>\n<p><strong>Paské li rinmin-m avan</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Boua kalvé moin pap jan-m blié </p>\n<p>Sé la li moui pou sové-m </p>\n<p>Tout péché ki vlé vi-n lave </p>\n<p>Nan san li ki koulé la </p>\n<p><br/> <strong>3.</strong></p>\n<p>Paròl Bon Dieu mapé sere </p>\n<p>Nan kè moin pou mouin pa péché </p>\n<p>Kont Jézu ki té vlc sovc-m </p>\n<p>Lè li té mouri pou nou </p>\n','Gin-gnou-non-moin-rinmin-chante',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2376,'585f4e96-eb5d-422f-b89b-3f5573d2dd07',0,0,'Lè ou anba nuaj ak tanpèt yo',58,'58_GA_ht','1.\nLè ou anba nuaj ak tanpèt yo\nChan e souri yo alé\nSanblé gin gnou-n\nki rinmin ou\nMin-m lè la nui la tro long\nGin gnoun ankò pou édé ou\nGingnin gnoun\nki ka repon-n ou\nGin gnoun ki ouè\ntout sò sou yo\nGin gnoun ki konnin sa\n\nKè\nLi konnin sa (bis)\nTout tantasion ak doulè yo\nSé Jézu sèl espoua\n\n2.\nLè plézi tè sa disparèt\nJounin ak nuit rasanblé\nPou ou nan poin angnin anko\nMin-m kouraj nan pouin ankò\nOu pansé pa gingnin espoua\nPoutan gin gnou-n\npou édé ou\nSé li min-m ki va\npran soin ou\nLi konin bezouin ou\n\n3.\nKité Sovè a pran soin ou\nChèché, Chèché prézans li\nLi bay la pè, konsolasion\nLi fè tout dout yo alé\nLi chanjé vi ou konplètman\nSièl la va louvri pou ou\nSé Jézu ki va pran souin ou\nPaské li konnin sa','<p><strong>1.</strong> </p>\n<p>Lè ou anba nuaj ak tanpèt yo </p>\n<p>Chan e souri yo alé </p>\n<p>Sanblé gin gnou-n </p>\n<p>ki rinmin ou </p>\n<p>Min-m lè la nui la tro long </p>\n<p>Gin gnoun ankò pou édé ou </p>\n<p>Gingnin gnoun </p>\n<p>ki ka repon-n ou </p>\n<p>Gin gnoun ki ouè </p>\n<p>tout sò sou yo </p>\n<p>Gin gnoun ki konnin sa </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Li konnin sa (bis)</strong> </p>\n<p><strong>Tout tantasion ak doulè yo</strong> </p>\n<p><strong>Sé Jézu sèl espoua</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Lè plézi tè sa disparèt </p>\n<p>Jounin ak nuit rasanblé </p>\n<p>Pou ou nan poin angnin anko </p>\n<p>Min-m kouraj nan pouin ankò </p>\n<p>Ou pansé pa gingnin espoua </p>\n<p>Poutan gin gnou-n </p>\n<p>pou édé ou </p>\n<p>Sé li min-m ki va </p>\n<p>pran soin ou </p>\n<p>Li konin bezouin ou </p>\n<p><br/> <strong>3.</strong></p>\n<p>Kité Sovè a pran soin ou </p>\n<p>Chèché, Chèché prézans li </p>\n<p>Li bay la pè, konsolasion </p>\n<p>Li fè tout dout yo alé </p>\n<p>Li chanjé vi ou konplètman </p>\n<p>Sièl la va louvri pou ou </p>\n<p>Sé Jézu ki va pran souin ou </p>\n<p>Paské li konnin sa </p>\n','Le-ou-anba-nuaj-ak-tanpet-yo',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2377,'b5a818cb-c044-48a1-9d60-8f12dc224dd6',0,0,'Le laboureur retourne à son village',83,'83_RN_fr','1.\nLe laboureur retourne à son village,\nBien fatigué des durs labeurs du jour,\nSon cœur est gai, joyeux est son visage,\nLe jour fini, la nuit vient à son tour\nRefrain\nMais à travers le jour, le soir, la nuit,\nL’œil de la foi le matin qui lui.\nOh le soir vient, le soir enfin\nLa nuit même devient belle,\nCar le matin, le grand matin\nApporte joie éternelle !\n2.\nIl laboura le cœur plein d’espérance,\nSemant ensuite avec un chant d’amour.\nDans le passé, Dieu donna la croissance,\nLa nuit revient mais il attend le jour.\n3.\nPour le chrétien, le jour à tant charme,\nC’est bien le temps pour planter et semer \nEt si parfois nous semons avec larmes\nEspoir en Dieu nous aide à triompher\n4.\nPour nous chrétiens, si douce est la soirée\nDonnant la paix et l’assurance au cœur !\nPour nous la nuit est toujours étoilée,\nLe matin vient et l’Éternel bonheur.\n5.\nC’est le matin, c’est la maison du père.\nC’est le matin, c’est donc le doux revoir,\nC’est le matin, ou l’âme solitaire\nRevient un centre ou d’amour et d’espoir\nEnfin le jour, le soir, la nuit ont fuit\nC’est le matin, le grand matin qui luit.\nRefrain\nIl lui enfin le grand matin\nC’est la maison paternelle\nOui le matin, le grand matin\nApporte joie éternelle.','<p><strong>1.</strong> </p>\n<p>Le laboureur retourne à son village, </p>\n<p>Bien fatigué des durs labeurs du jour, </p>\n<p>Son cœur est gai, joyeux est son visage, </p>\n<p>Le jour fini, la nuit vient à son tour </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mais à travers le jour, le soir, la nuit,</strong> </p>\n<p><strong>L’œil de la foi le matin qui lui.</strong> </p>\n<p><strong>Oh le soir vient, le soir enfin</strong> </p>\n<p><strong>La nuit même devient belle,</strong> </p>\n<p><strong>Car le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il laboura le cœur plein d’espérance, </p>\n<p>Semant ensuite avec un chant d’amour. </p>\n<p>Dans le passé, Dieu donna la croissance, </p>\n<p>La nuit revient mais il attend le jour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour le chrétien, le jour à tant charme, </p>\n<p>C’est bien le temps pour planter et semer </p>\n<p>Et si parfois nous semons avec larmes </p>\n<p>Espoir en Dieu nous aide à triompher </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour nous chrétiens, si douce est la soirée </p>\n<p>Donnant la paix et l’assurance au cœur ! </p>\n<p>Pour nous la nuit est toujours étoilée, </p>\n<p>Le matin vient et l’Éternel bonheur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>C’est le matin, c’est la maison du père. </p>\n<p>C’est le matin, c’est donc le doux revoir, </p>\n<p>C’est le matin, ou l’âme solitaire </p>\n<p>Revient un centre ou d’amour et d’espoir </p>\n<p>Enfin le jour, le soir, la nuit ont fuit </p>\n<p>C’est le matin, le grand matin qui luit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il lui enfin le grand matin</strong> </p>\n<p><strong>C’est la maison paternelle</strong> </p>\n<p><strong>Oui le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle.</strong> </p>\n','Le-laboureur-retourne-a-son-village',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15');
INSERT INTO `songs` (`id`, `uuid`, `views`, `likes`, `title`, `num`, `songId`, `lyrics`, `lyricsHtml`, `slug`, `video`, `language`, `SongCategoryId`, `createdAt`, `updatedAt`) VALUES (2378,'abc0f689-35da-4256-8d35-edcb2cea9cd1',0,0,'Quant l’esprit de Dieu habite en moi',88,'88_RN_fr','1.\nQuant l’esprit de Dieu habite en moi\nJe chante comme David\nJe chante, je chante,\nJe chante comme David (bis)\n2.\nQuand l’esprit de Dieu habite en moi\nJe prie comme David\nJe prie, je prie, je prie comme David (bis)\n3.\nQuand l’esprit de Dieu habite en moi,\nJe danse comme David.\nJe danse, je danse,\nJe danse comme David (bis).','<p><strong>1.</strong> </p>\n<p>Quant l’esprit de Dieu habite en moi </p>\n<p>Je chante comme David </p>\n<p>Je chante, je chante, </p>\n<p>Je chante comme David (bis) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Quand l’esprit de Dieu habite en moi </p>\n<p>Je prie comme David </p>\n<p>Je prie, je prie, je prie comme David (bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Quand l’esprit de Dieu habite en moi, </p>\n<p>Je danse comme David. </p>\n<p>Je danse, je danse, </p>\n<p>Je danse comme David (bis). </p>\n','Quant-l\'esprit-de-Dieu-habite-en-moi',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2379,'841e31fc-34f0-41bd-a9de-513042f1fe52',0,0,'Au petit bourg de Bethleem',95,'95_RN_fr','1.\nAu petit bourg de Bethléem,\nPaisible dans la nuit :\nVeillant sur ton repos profond\nL’étoile du soir luit,\nMais voici dans tes parvis\nBrille un rayon divin,\nLes espoirs de tous les âges\nSont accomplis enfin.\n2.\nVenu de la gloire des cieux\nL’Ange avait proclame\nLa naissance de l’humble enfant,\nAux bergers étonnés.\nOui, chantez sa naissance\nEtoiles du matin,\nCélébrez la puissance\nDe ce Sauveur divin\n\n3.\nIl vint silencieusement\nCe don si merveilleux !\nAinsi Dieu donne à cœur de l’homme\nLes bienfaits de ces cieux.\nSans bruit fut sa venue\nDans ce monde pécheur,\nToujours Christ veut venir des cieux\nChez les humbles de cœur.\n\n4.\nO Saint Enfant de Bethleem,\nDescends t’en prions-nous,\nOte le mal, Christ sans égal,\nSois en ce jour en tous,\nNous entendons les anges\nLeur cantiques chanter \nO viens à nous reste avec nous\nEmmanuel ! Entrez !','<p><strong>1.</strong> </p>\n<p>Au petit bourg de Bethléem, </p>\n<p>Paisible dans la nuit : </p>\n<p>Veillant sur ton repos profond </p>\n<p>L’étoile du soir luit, </p>\n<p>Mais voici dans tes parvis </p>\n<p>Brille un rayon divin, </p>\n<p>Les espoirs de tous les âges </p>\n<p>Sont accomplis enfin. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Venu de la gloire des cieux </p>\n<p>L’Ange avait proclame </p>\n<p>La naissance de l’humble enfant, </p>\n<p>Aux bergers étonnés. </p>\n<p>Oui, chantez sa naissance </p>\n<p>Etoiles du matin, </p>\n<p>Célébrez la puissance </p>\n<p>De ce Sauveur divin </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il vint silencieusement </p>\n<p>Ce don si merveilleux ! </p>\n<p>Ainsi Dieu donne à cœur de l’homme </p>\n<p>Les bienfaits de ces cieux. </p>\n<p>Sans bruit fut sa venue </p>\n<p>Dans ce monde pécheur, </p>\n<p>Toujours Christ veut venir des cieux </p>\n<p>Chez les humbles de cœur. </p>\n<p><br/> <strong>4.</strong></p>\n<p>O Saint Enfant de Bethleem, </p>\n<p>Descends t’en prions-nous, </p>\n<p>Ote le mal, Christ sans égal, </p>\n<p>Sois en ce jour en tous, </p>\n<p>Nous entendons les anges </p>\n<p>Leur cantiques chanter </p>\n<p>O viens à nous reste avec nous </p>\n<p>Emmanuel ! Entrez ! </p>\n','Au-petit-bourg-de-Bethleem',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2380,'c673fb72-0c0e-4757-b9b1-2487761757c6',0,0,'Je veux parler de la croix',10,'10_GA_fr','1.\nJe veux parler de la croix\nOù mourût Ie Roi des rois\nL\'unique objet de mon choix\nMa seule espérance\nCroix bénie du Sauveur\nSeul refuge du pécheur\nEt sa délivrance\nRefrain\nAmour merveilleux, amour merveilleux\nII meurt sur Ie bois infâme de la croix\nAmour merveilleux\n\n2.\nO regarde maintenant\nLe sacrifice sanglant\nDu Sauveur agonisant\nSur le bois infâme\nC\'est pour toi qu\'il est blessé\nEt pour tes forfaits brisés\nPour sauver ton âme.\n\n3.\nViens, réponds à cet amour\nDu plein salut c\'est le jour\nApproche-toi sans détour\nViens, car il pardonne\nEnrôle-toi maintenant\nSous la croix du Dieu clément\nViens, la paix il donne','<p><strong>1.</strong> </p>\n<p>Je veux parler de la croix </p>\n<p>Où mourût Ie Roi des rois </p>\n<p>L&#39;unique objet de mon choix </p>\n<p>Ma seule espérance </p>\n<p>Croix bénie du Sauveur </p>\n<p>Seul refuge du pécheur </p>\n<p>Et sa délivrance </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Amour merveilleux, amour merveilleux</strong> </p>\n<p><strong>II meurt sur Ie bois infâme de la croix</strong> </p>\n<p><strong>Amour merveilleux</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>O regarde maintenant </p>\n<p>Le sacrifice sanglant </p>\n<p>Du Sauveur agonisant </p>\n<p>Sur le bois infâme </p>\n<p>C&#39;est pour toi qu&#39;il est blessé </p>\n<p>Et pour tes forfaits brisés </p>\n<p>Pour sauver ton âme. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Viens, réponds à cet amour </p>\n<p>Du plein salut c&#39;est le jour </p>\n<p>Approche-toi sans détour </p>\n<p>Viens, car il pardonne </p>\n<p>Enrôle-toi maintenant </p>\n<p>Sous la croix du Dieu clément </p>\n<p>Viens, la paix il donne </p>\n','Je-veux-parler-de-la-croix',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2381,'9e3732a5-86d2-4b8d-89c4-9d2e362c9693',0,0,'Au tribunal Jésus se tient',18,'18_GA_fr','1.\nAu tribunal Jésus se tient\nMéprisé, seul et sans soutien\nPilate au peuple dit soudain\nQue dois-je faire à Jésus \nRefrain\nQue feras-tu de Jésus\nII se tient devant toi\nUn jour tu diras plein d\'effroi\nQue fera-t-il de moi\n\n2.\nComme Judas trahirez-vous\nCelui qui vient mouiir pour nous\nOu le suivrez-vous jusqu\'au bout\nQue ferez-vous de Jésus\n\n3.\nComme Pilate indifférent\nCondamnerez-vous I\'innocent\nC\'est vous qui jugez maintenant\nQue ferez-vous de Jésus\n\n4.\nJésus je te donne mon coeur\nToi seul est mon parfait Sauveur\nT\'obéir sera mon bonheur\nJe t\'appartiens! O Jésus','<p><strong>1.</strong> </p>\n<p>Au tribunal Jésus se tient </p>\n<p>Méprisé, seul et sans soutien </p>\n<p>Pilate au peuple dit soudain </p>\n<p>Que dois-je faire à Jésus </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Que feras-tu de Jésus</strong> </p>\n<p><strong>II se tient devant toi</strong> </p>\n<p><strong>Un jour tu diras plein d&#39;effroi</strong> </p>\n<p><strong>Que fera-t-il de moi</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Comme Judas trahirez-vous </p>\n<p>Celui qui vient mouiir pour nous </p>\n<p>Ou le suivrez-vous jusqu&#39;au bout </p>\n<p>Que ferez-vous de Jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme Pilate indifférent </p>\n<p>Condamnerez-vous I&#39;innocent </p>\n<p>C&#39;est vous qui jugez maintenant </p>\n<p>Que ferez-vous de Jésus </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jésus je te donne mon coeur </p>\n<p>Toi seul est mon parfait Sauveur </p>\n<p>T&#39;obéir sera mon bonheur </p>\n<p>Je t&#39;appartiens! O Jésus </p>\n','Au-tribunal-Jesus-se-tient',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2382,'c2432f81-7dc3-4154-8454-790068455ee4',0,0,'Bien pa-m pa isiba',54,'54_GA_ht','1.\nBien pa-m pa isiba\nSé passage moin yé\nBien ké moin maléré\nMin sa pa fèm angnin\nJézu sé tréso moin avèk li moin satisfè\nAllélouia-m gin gnou plas nan siel la\n\nKè\nO oui-m santi-m Kontan\nNon moin ékri nan siel la\nMin-m si sou tè sa-a\nMouin jouin-n difikilté\nMin sa pa fèm angnin\nJézu té pasé plis\nAllélouia-m gin gnou plas nan siel la\n\n2.\nFrèm yo dan le Segnè\nPiga-n dékourajé\nChemin an pa facil\nPou-n rive nan siel la\nMépri pèsèkision sé sa ki sou rout la\nAllélouia gnou jou sa va fini\n\n3.\nPa okipé le mond\nKap manjé jodia\nYap fè gnou vié progrè\nPou gnou vi passage\nNou min-m ki sé krétien\nNou gin vi étènèl\nAllélouia sé nou min-m ki rich\n','<p><strong>1.</strong> </p>\n<p>Bien pa-m pa isiba </p>\n<p>Sé passage moin yé </p>\n<p>Bien ké moin maléré </p>\n<p>Min sa pa fèm angnin </p>\n<p>Jézu sé tréso moin avèk li moin satisfè </p>\n<p>Allélouia-m gin gnou plas nan siel la </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>O oui-m santi-m Kontan</strong> </p>\n<p><strong>Non moin ékri nan siel la</strong> </p>\n<p><strong>Min-m si sou tè sa-a</strong> </p>\n<p><strong>Mouin jouin-n difikilté</strong> </p>\n<p><strong>Min sa pa fèm angnin</strong> </p>\n<p><strong>Jézu té pasé plis</strong> </p>\n<p><strong>Allélouia-m gin gnou plas nan siel la</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Frèm yo dan le Segnè </p>\n<p>Piga-n dékourajé </p>\n<p>Chemin an pa facil </p>\n<p>Pou-n rive nan siel la </p>\n<p>Mépri pèsèkision sé sa ki sou rout la </p>\n<p>Allélouia gnou jou sa va fini </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pa okipé le mond </p>\n<p>Kap manjé jodia </p>\n<p>Yap fè gnou vié progrè </p>\n<p>Pou gnou vi passage </p>\n<p>Nou min-m ki sé krétien </p>\n<p>Nou gin vi étènèl </p>\n<p>Allélouia sé nou min-m ki rich </p>\n','Bien-pa-m-pa-isiba',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2383,'674442cc-2aab-45b4-895e-3890a1c4bb8d',0,0,'Gin moun ki pral krié',56,'56_GA_ht','1.\nGin moun ki pral krié\nGin moun ki pral rélé\nPaské konpasion Bon Dieu pral fini\nTronpèt la pral soné\nSak fè pou Jézu ap monté\nSak pou Satan ap rete pou jujman\n\nKè\nA la krié pral gingnin\nA la relé pral gingnin\nPaské konpasion Bon Dieu pral fini\n(bis 2 lign)','<p><strong>1.</strong> </p>\n<p>Gin moun ki pral krié </p>\n<p>Gin moun ki pral rélé </p>\n<p>Paské konpasion Bon Dieu pral fini </p>\n<p>Tronpèt la pral soné </p>\n<p>Sak fè pou Jézu ap monté </p>\n<p>Sak pou Satan ap rete pou jujman </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>A la krié pral gingnin</strong> </p>\n<p><strong>A la relé pral gingnin</strong> </p>\n<p><strong>Paské konpasion Bon Dieu pral fini</strong> </p>\n<p><strong>(bis 2 lign)</strong> </p>\n','Gin-moun-ki-pral-krie',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2384,'d961658d-e901-4774-aaee-b1284c125368',0,0,'Moin vlé chanté pou Jézu',62,'62_GA_ht','1.\nMoin vlé chanté pou Jézu\nMoun vlé pale pou sa Jézu fè pou mouin\nMoin pap suspan\'n ba li gloua\nMoin pap suspan\'n témoigné,\njustan li pran vi moin\n\nKè\nSé nan fatra Kris té ramasé-m\nAnba péché li\nté soulajé-m\nSé nan fatra Kris té ramasé-m\nAnba péché li\nté delivré-m\n\n2.\nOu min-m zanmi kap kouté\nEské ou pa ouèli ta bon pou chanté Jezu\nNan Jézu-Kri sé la joua nan Jézu Kri sé la pè\nPou tèt sa map chanté\nLè-m va monté anro-a\nPou-m al rankontré Jézu, eské ou va la tou\nE byin map invite nou pou-n aksepté Jézu\nOua gin la joua, la vi','<p><strong>1.</strong> </p>\n<p>Moin vlé chanté pou Jézu </p>\n<p>Moun vlé pale pou sa Jézu fè pou mouin </p>\n<p>Moin pap suspan&#39;n ba li gloua </p>\n<p>Moin pap suspan&#39;n témoigné, </p>\n<p>justan li pran vi moin </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Sé nan fatra Kris té ramasé-m</strong> </p>\n<p><strong>Anba péché li</strong> </p>\n<p><strong>té soulajé-m</strong> </p>\n<p><strong>Sé nan fatra Kris té ramasé-m</strong> </p>\n<p><strong>Anba péché li</strong> </p>\n<p><strong>té delivré-m</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou min-m zanmi kap kouté </p>\n<p>Eské ou pa ouèli ta bon pou chanté Jezu </p>\n<p>Nan Jézu-Kri sé la joua nan Jézu Kri sé la pè </p>\n<p>Pou tèt sa map chanté </p>\n<p>Lè-m va monté anro-a </p>\n<p>Pou-m al rankontré Jézu, eské ou va la tou </p>\n<p>E byin map invite nou pou-n aksepté Jézu </p>\n<p>Oua gin la joua, la vi </p>\n','Moin-vle-chante-pou-Jezu',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2385,'873c1253-1aa4-41d1-bd95-eec6ea4b68fe',0,0,'Es-tu lassé, rempli de tristesse',7,'7_GA_fr','1.\nEs-tu lassé, rempli de tristesse\nDis tout à Jésus, dis tout à Jésus\nSon coeur est ouvert à ta voix sans cesse\nDis tout à Jésus, dis tout à Jésus\n\n\nRefrain\nDis tout à Jésus, Dis tout à Jésus\nSon coeur est ouvert à ta voix sans cesse\nDis tout à Jésus, Dis tout à Jésus\nII voit tes yeux rougis par les larmes\nDis tout à Jésus, dis tout à Jésus\nII connait ton Coeur, il sait tes alarmes\nDis tout à Jésus, Dis tout à Jésus\n\n2.\nSi ton passé surgit comme une ombre\nDis tout à Jésus, dis tout à Jésus\nII peut effacer des fautes sans nombre\nDis tout à Jésus, dis tout à Jésus\n\n3.\nEt pour demain, ce que tu redoutes\nDis tout à Jésus, dis tout à Jésus\nII est près de toi le long de la route\nOh dis tout à Jésus, dis tout à Jésus','<p><strong>1.</strong> </p>\n<p>Es-tu lassé, rempli de tristesse </p>\n<p>Dis tout à Jésus, dis tout à Jésus </p>\n<p>Son coeur est ouvert à ta voix sans cesse </p>\n<p>Dis tout à Jésus, dis tout à Jésus </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Dis tout à Jésus, Dis tout à Jésus</strong> </p>\n<p><strong>Son coeur est ouvert à ta voix sans cesse</strong> </p>\n<p><strong>Dis tout à Jésus, Dis tout à Jésus</strong> </p>\n<p><strong>II voit tes yeux rougis par les larmes</strong> </p>\n<p><strong>Dis tout à Jésus, dis tout à Jésus</strong> </p>\n<p><strong>II connait ton Coeur, il sait tes alarmes</strong> </p>\n<p><strong>Dis tout à Jésus, Dis tout à Jésus</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si ton passé surgit comme une ombre </p>\n<p>Dis tout à Jésus, dis tout à Jésus </p>\n<p>II peut effacer des fautes sans nombre </p>\n<p>Dis tout à Jésus, dis tout à Jésus </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et pour demain, ce que tu redoutes </p>\n<p>Dis tout à Jésus, dis tout à Jésus </p>\n<p>II est près de toi le long de la route </p>\n<p>Oh dis tout à Jésus, dis tout à Jésus </p>\n','Es-tu-lasse-rempli-de-tristesse',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2386,'9c41f6a1-0a42-4d43-ab7e-a280d30c2fd5',0,0,'Jésus-Christ me donna Sa vie',76,'76_RN_fr','1.\nJésus-Christ me donna Sa vie\nPuis-je lui donner la mienne ?\nJe me consacre à toi Seigneur\nJe serai tout à toi\nRefrain\nMa vie Ô Seigneur je te donne\nMes talents et mon tout.\nJe te sers et je suis à toi\nJ’entendrai ton appel\n2.\nOù le Seigneur me dirigera\nJ’accomplirai sa tâche\nJe sais qu’il protège celui\nQui fait sa volonté\n3.\nS’il me faut traverser la mer\nAvec Jésus j’irai\nRaconter aux perdus son amour\nPour qu’ils voient sa puissance.\n4.\nMon foyer, mes amis sont chers\nPour moi Christ est plus cher\nPremier de mes affectionnés\nSa juste volonté.','<p><strong>1.</strong> </p>\n<p>Jésus-Christ me donna Sa vie </p>\n<p>Puis-je lui donner la mienne ? </p>\n<p>Je me consacre à toi Seigneur </p>\n<p>Je serai tout à toi </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Ma vie Ô Seigneur je te donne</strong> </p>\n<p><strong>Mes talents et mon tout.</strong> </p>\n<p><strong>Je te sers et je suis à toi</strong> </p>\n<p><strong>J’entendrai ton appel</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Où le Seigneur me dirigera </p>\n<p>J’accomplirai sa tâche </p>\n<p>Je sais qu’il protège celui </p>\n<p>Qui fait sa volonté </p>\n<p><br/> <strong>3.</strong></p>\n<p>S’il me faut traverser la mer </p>\n<p>Avec Jésus j’irai </p>\n<p>Raconter aux perdus son amour </p>\n<p>Pour qu’ils voient sa puissance. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Mon foyer, mes amis sont chers </p>\n<p>Pour moi Christ est plus cher </p>\n<p>Premier de mes affectionnés </p>\n<p>Sa juste volonté. </p>\n','Jesus-Christ-me-donna-Sa-vie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2387,'f161db90-1afc-4301-8f8f-30bea464a62b',0,0,'Depuis que Jésus Christ est entre dans mon cœur',79,'79_RN_fr','1.\nDepuis que Jésus-Christ est entré dans mon cœur,\nDe la joie y déborde,\nDepuis que Jésus-Christ est entré dans ma vie\nAh ! Oui le soleil y brille\nRefrain\nSeigneur ton entrée dans ma vie est vraiment lumineuse,\nCar j’étais dans les ténèbres\nDès maintenant je suis sauvé.\n2.\nJésus m’a racheté dans ce monde pervers,\nJe suis parmi les élus\nDepuis qu’il est entre, mon être est transformé\nEt puis le soleil y brille\n3.\nOh gloire à Jésus pour ce don précieux,\nCar ma vie a changé\nJ’étais nettement perdu, mais tu m’as retrouvé\nOh gloire à ton nom suprême.','<p><strong>1.</strong> </p>\n<p>Depuis que Jésus-Christ est entré dans mon cœur, </p>\n<p>De la joie y déborde, </p>\n<p>Depuis que Jésus-Christ est entré dans ma vie </p>\n<p>Ah ! Oui le soleil y brille </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Seigneur ton entrée dans ma vie est vraiment lumineuse,</strong> </p>\n<p><strong>Car j’étais dans les ténèbres</strong> </p>\n<p><strong>Dès maintenant je suis sauvé.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus m’a racheté dans ce monde pervers, </p>\n<p>Je suis parmi les élus </p>\n<p>Depuis qu’il est entre, mon être est transformé </p>\n<p>Et puis le soleil y brille </p>\n<p><br/> <strong>3.</strong></p>\n<p>Oh gloire à Jésus pour ce don précieux, </p>\n<p>Car ma vie a changé </p>\n<p>J’étais nettement perdu, mais tu m’as retrouvé </p>\n<p>Oh gloire à ton nom suprême. </p>\n','Depuis-que-Jesus-Christ-est-entre-dans-mon-coeur',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2388,'6e1f9f59-0c0b-4047-9953-7bd0f6e4939e',0,0,'Pèlerin sur le chemin de la vie',82,'82_RN_fr','1.\nPèlerin sur le chemin de la vie,\nVoyageur vers la céleste patrie\nQu’il est grand ce Dieu d’amour, de grâce\nDe nous jamais il ne se lasse,\nQu’il est grand !\nRefrain\nAimez, aimez, et vous serez fils de Dieu \nAimez, aimez, l’amour est la loi des cieux\nAimez, aimez, d’un amour grand et tendre\nCherchons toujours à rendre gloire à Dieu.\n2.\nPèlerin sur le chemin de la vie \nVoyageur vers la céleste patrie\nQu’il est doux, sa douceur est si tendre,\nLes faibles, Dieu peut les comprendre,\nQu’il est doux.\n3.\nPèlerin sur le chemin de la vie,\nVoyageur vers la céleste patrie,\nQu’il est bon son bonté paternelle\nDieu la montre à l’âme rebelle \nQu’il est bon.','<p><strong>1.</strong> </p>\n<p>Pèlerin sur le chemin de la vie, </p>\n<p>Voyageur vers la céleste patrie </p>\n<p>Qu’il est grand ce Dieu d’amour, de grâce </p>\n<p>De nous jamais il ne se lasse, </p>\n<p>Qu’il est grand ! </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Aimez, aimez, et vous serez fils de Dieu</strong> </p>\n<p><strong>Aimez, aimez, l’amour est la loi des cieux</strong> </p>\n<p><strong>Aimez, aimez, d’un amour grand et tendre</strong> </p>\n<p><strong>Cherchons toujours à rendre gloire à Dieu.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pèlerin sur le chemin de la vie </p>\n<p>Voyageur vers la céleste patrie </p>\n<p>Qu’il est doux, sa douceur est si tendre, </p>\n<p>Les faibles, Dieu peut les comprendre, </p>\n<p>Qu’il est doux. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pèlerin sur le chemin de la vie, </p>\n<p>Voyageur vers la céleste patrie, </p>\n<p>Qu’il est bon son bonté paternelle </p>\n<p>Dieu la montre à l’âme rebelle </p>\n<p>Qu’il est bon. </p>\n','Pelerin-sur-le-chemin-de-la-vie',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2389,'d64f0f66-9201-4f47-b1cd-d5467ef821e9',0,0,'Le laboureur retourne à son village',84,'84_RN_fr','1.\nLe laboureur retourne à son village,\nBien fatigué des durs labeurs du jour,\nSon cœur est gai, joyeux est son visage,\nLe jour fini, la nuit vient à son tour\nRefrain\nMais à travers le jour, le soir, la nuit,\nL’œil de la foi le matin qui lui.\nOh le soir vient, le soir enfin\nLa nuit même devient belle,\nCar le matin, le grand matin\nApporte joie éternelle !\n2.\nIl laboura le cœur plein d’espérance,\nSemant ensuite avec un chant d’amour.\nDans le passé, Dieu donna la croissance,\nLa nuit revient mais il attend le jour.\n3.\nPour le chrétien, le jour à tant charme,\nC’est bien le temps pour planter et semer \nEt si parfois nous semons avec larmes\nEspoir en Dieu nous aide à triompher\n4.\nPour nous chrétiens, si douce est la soirée\nDonnant la paix et l’assurance au cœur !\nPour nous la nuit est toujours étoilée,\nLe matin vient et l’Éternel bonheur.\n5.\nC’est le matin, c’est la maison du père.\nC’est le matin, c’est donc le doux revoir,\nC’est le matin, ou l’âme solitaire\nRevient un centre ou d’amour et d’espoir\nEnfin le jour, le soir, la nuit ont fuit\nC’est le matin, le grand matin qui luit.\nRefrain\nIl lui enfin le grand matin\nC’est la maison paternelle\nOui le matin, le grand matin\nApporte joie éternelle.','<p><strong>1.</strong> </p>\n<p>Le laboureur retourne à son village, </p>\n<p>Bien fatigué des durs labeurs du jour, </p>\n<p>Son cœur est gai, joyeux est son visage, </p>\n<p>Le jour fini, la nuit vient à son tour </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Mais à travers le jour, le soir, la nuit,</strong> </p>\n<p><strong>L’œil de la foi le matin qui lui.</strong> </p>\n<p><strong>Oh le soir vient, le soir enfin</strong> </p>\n<p><strong>La nuit même devient belle,</strong> </p>\n<p><strong>Car le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il laboura le cœur plein d’espérance, </p>\n<p>Semant ensuite avec un chant d’amour. </p>\n<p>Dans le passé, Dieu donna la croissance, </p>\n<p>La nuit revient mais il attend le jour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour le chrétien, le jour à tant charme, </p>\n<p>C’est bien le temps pour planter et semer </p>\n<p>Et si parfois nous semons avec larmes </p>\n<p>Espoir en Dieu nous aide à triompher </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pour nous chrétiens, si douce est la soirée </p>\n<p>Donnant la paix et l’assurance au cœur ! </p>\n<p>Pour nous la nuit est toujours étoilée, </p>\n<p>Le matin vient et l’Éternel bonheur. </p>\n<p><br/> <strong>5.</strong></p>\n<p>C’est le matin, c’est la maison du père. </p>\n<p>C’est le matin, c’est donc le doux revoir, </p>\n<p>C’est le matin, ou l’âme solitaire </p>\n<p>Revient un centre ou d’amour et d’espoir </p>\n<p>Enfin le jour, le soir, la nuit ont fuit </p>\n<p>C’est le matin, le grand matin qui luit. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il lui enfin le grand matin</strong> </p>\n<p><strong>C’est la maison paternelle</strong> </p>\n<p><strong>Oui le matin, le grand matin</strong> </p>\n<p><strong>Apporte joie éternelle.</strong> </p>\n','Le-laboureur-retourne-a-son-village',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2390,'c80d62e1-9019-4759-87b7-e6db265d2430',0,0,'Manifestons nos joies',86,'86_RN_fr','1.\nManifestons nos joies,\nEt nos contentements\nRendons gloire au Seigneur\nPour ce don si précieux\nIl nous envoie son fils\nPour racheter nos péchés \nCrions à haute voix\nHosanna au plus haut des cieux.\n2.\nPour ce grand sacrifice,\nDescendant ses bas-lieux,\nNous donnons à l’Eglise\nDes âmes autrefois perdues\nSoyons reconnaissants\nA l’enfant nouveau-né\nDescend à notre voix,\nVient pour sauver l’homme pécheur.','<p><strong>1.</strong> </p>\n<p>Manifestons nos joies, </p>\n<p>Et nos contentements </p>\n<p>Rendons gloire au Seigneur </p>\n<p>Pour ce don si précieux </p>\n<p>Il nous envoie son fils </p>\n<p>Pour racheter nos péchés </p>\n<p>Crions à haute voix </p>\n<p>Hosanna au plus haut des cieux. </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pour ce grand sacrifice, </p>\n<p>Descendant ses bas-lieux, </p>\n<p>Nous donnons à l’Eglise </p>\n<p>Des âmes autrefois perdues </p>\n<p>Soyons reconnaissants </p>\n<p>A l’enfant nouveau-né </p>\n<p>Descend à notre voix, </p>\n<p>Vient pour sauver l’homme pécheur. </p>\n','Manifestons-nos-joies',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2391,'596923e7-ce29-4643-8e1b-b0b0aac69cdf',0,0,'Vois là-bas mettre le seigneur en croix',15,'15_GA_fr','1.\nVois là-bas mettre le seigneur en croix, vois là-bas\nVois là-bas mettre le seigneur en croix\nO O O O le coeur saisit d\'effroi\nJe tremble, tremble, tremble\nVois là-bas mettre le Seigneur en croix\n\n2.\nVois là-bas cloue le Seigneur au bois, vois là-bas\nVois là-bas cloue le Seigneur au bois\nO O O O le coeur saisit d\'effroi\nJe tremble, tremble, tremble\nVois là-bas cloue le Seigneur au bois\n\n3.\nVois là-bas de leur lance ils l\'ont percé, vois là-bas\nVois là-bas de leur lance ils l\'ont percé\nO O O O le coeur saisit d\'effroi\nJe tremble, tremble, tremble\nVois là-bas de leur lance ils l\'ont percé\n\n4.\nVois là-bas soudain s\'enfuit le soleil\nVois là-bas soudain s\'enfuit le soleil\nO O O O le coeur saisit d\'effroi\nJe tremble, tremble, tremble\nVois là-bas soudain s\'enfuit le soleil\n\n5.\nVois là-bas dans sa tombe ils I\'ont porté, vois lé-bas\nVois là-bas dans sa tombe ils I\'ont porté\nO O O O le coeur saisit d\'effroi\nJe tremble, tremble, tremble\nVois là-bas dans sa tombe ils I\'ont porté\n\n6.\nViens et vois Jésus est ressucité, Viens et vois\nViens et vois ton Sauveur ressucité\nO O O O le coeur rempli de joie\nJe chante, chante, chante\nGloire à toi mon Sauveur ressuscité','<p><strong>1.</strong> </p>\n<p>Vois là-bas mettre le seigneur en croix, vois là-bas </p>\n<p>Vois là-bas mettre le seigneur en croix </p>\n<p>O O O O le coeur saisit d&#39;effroi </p>\n<p>Je tremble, tremble, tremble </p>\n<p>Vois là-bas mettre le Seigneur en croix </p>\n<p><br/> <strong>2.</strong></p>\n<p>Vois là-bas cloue le Seigneur au bois, vois là-bas </p>\n<p>Vois là-bas cloue le Seigneur au bois </p>\n<p>O O O O le coeur saisit d&#39;effroi </p>\n<p>Je tremble, tremble, tremble </p>\n<p>Vois là-bas cloue le Seigneur au bois </p>\n<p><br/> <strong>3.</strong></p>\n<p>Vois là-bas de leur lance ils l&#39;ont percé, vois là-bas </p>\n<p>Vois là-bas de leur lance ils l&#39;ont percé </p>\n<p>O O O O le coeur saisit d&#39;effroi </p>\n<p>Je tremble, tremble, tremble </p>\n<p>Vois là-bas de leur lance ils l&#39;ont percé </p>\n<p><br/> <strong>4.</strong></p>\n<p>Vois là-bas soudain s&#39;enfuit le soleil </p>\n<p>Vois là-bas soudain s&#39;enfuit le soleil </p>\n<p>O O O O le coeur saisit d&#39;effroi </p>\n<p>Je tremble, tremble, tremble </p>\n<p>Vois là-bas soudain s&#39;enfuit le soleil </p>\n<p><br/> <strong>5.</strong></p>\n<p>Vois là-bas dans sa tombe ils I&#39;ont porté, vois lé-bas </p>\n<p>Vois là-bas dans sa tombe ils I&#39;ont porté </p>\n<p>O O O O le coeur saisit d&#39;effroi </p>\n<p>Je tremble, tremble, tremble </p>\n<p>Vois là-bas dans sa tombe ils I&#39;ont porté </p>\n<p><br/> <strong>6.</strong></p>\n<p>Viens et vois Jésus est ressucité, Viens et vois </p>\n<p>Viens et vois ton Sauveur ressucité </p>\n<p>O O O O le coeur rempli de joie </p>\n<p>Je chante, chante, chante </p>\n<p>Gloire à toi mon Sauveur ressuscité </p>\n','Vois-la-bas-mettre-le-seigneur-en-croix',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2392,'bf29ff23-e1d9-447a-b96d-b30d4254929d',0,0,'Map vouyagé pou gnou bèl cité',50,'50_GA_ht','1.\nMap vouyagé pou gnou bèl cité\nKoté tout ri yo sé lo\nKoté-n va demere an pe\nE joua moin pap jan-m fini\n\nKè\nEské ou va rankontré\nmoin la (bis)\nPou male avèk zanj yo\nEské ou va rankontré moin la\nKoté nou pap jan-m di adie\n\n2.\nSa va pou moin\ngnou gran reunion\nAk Sin yo kit te devan\nDi moin si ou prèt\npou apei yo\nVini vit ma ran n ou pu\n\n3.\nOu trè mouin di moin ki jan ouape viv\nJézu vléou alé la\nO ki gran joua, jou reunion sa\nSé va la vi pou tout tan','<p><strong>1.</strong> </p>\n<p>Map vouyagé pou gnou bèl cité </p>\n<p>Koté tout ri yo sé lo </p>\n<p>Koté-n va demere an pe </p>\n<p>E joua moin pap jan-m fini </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Eské ou va rankontré</strong> </p>\n<p><strong>moin la (bis)</strong> </p>\n<p><strong>Pou male avèk zanj yo</strong> </p>\n<p><strong>Eské ou va rankontré moin la</strong> </p>\n<p><strong>Koté nou pap jan-m di adie</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sa va pou moin </p>\n<p>gnou gran reunion </p>\n<p>Ak Sin yo kit te devan </p>\n<p>Di moin si ou prèt </p>\n<p>pou apei yo </p>\n<p>Vini vit ma ran n ou pu </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ou trè mouin di moin ki jan ouape viv </p>\n<p>Jézu vléou alé la </p>\n<p>O ki gran joua, jou reunion sa </p>\n<p>Sé va la vi pou tout tan </p>\n','Map-vouyage-pou-gnou-bel-cite',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2393,'1c8e8738-a72b-4cd3-921f-d1b0370e32a5',0,0,'Té gin gnou fan-m ki té fè 12 an malad',61,'61_GA_ht','1.\nTé gin gnou fan-m ki té fè 12 an malad\nLi tape vèsé san tout la jouné\nDépi 12 an ni la nuit, ni la jouné\nMalerez la depensé tout sa-l gingnin\nApanté tout kay doktè\nMaché la kay gangnan pou gran mesi\nTout moun tap di pat gen espoua\nFanm sa se jou li tap konté poul mouri\n\n2.\nPandant Jézu ta pral kay Jairus la\nAk on pakèt moun-n ki tap avansé\nFan-m na presé-l\nkouri antré nan fou-la\nKon you koulèv li tap fofilé\nFoul la anpil li té fòsé-l avansé\nPiès moun-n pat kon-n sal té vlé\nLi fè anpil efò jouk tan rive\nFan-m nou kanpé,\nli té touché rob la\nLi té join-n you puisans nan ròb la\n\nKè\nA la yon puissans ki gin nan ròb Jézy ya\nFan-m nan té agi foua li\nSèlman li touché ròb la epui-l té\njoin-n gérizon\nGin yon bagay Segnè ya té di fan-m nan\nSa se yon leson ki vlé pou-n pratiké\nAvèk la foua nou va jouin tout sa nou vlé\nMin fòk nou met konfians\nnou nan Jézu\n\n3.\nMin-m Jodi-a ya pandan nou nan legliz la\nJezu gin lòt mirak pou-l opéré\nMoun-n kap prié ou mèt kontinye prié\nGin anpil muray ki pral krazé\nPèsévéré pa dékourajé frè-m yo\nKinbe la foua vié sè-m yo\nTalè konsa nou tout pral deliver\nTout moun-n ki vlé\nKapab gouté puissans la\nSi yo vlé met konfians nan Jézu\nJehovah Nissy','<p><strong>1.</strong> </p>\n<p>Té gin gnou fan-m ki té fè 12 an malad </p>\n<p>Li tape vèsé san tout la jouné </p>\n<p>Dépi 12 an ni la nuit, ni la jouné </p>\n<p>Malerez la depensé tout sa-l gingnin </p>\n<p>Apanté tout kay doktè </p>\n<p>Maché la kay gangnan pou gran mesi </p>\n<p>Tout moun tap di pat gen espoua </p>\n<p>Fanm sa se jou li tap konté poul mouri </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pandant Jézu ta pral kay Jairus la </p>\n<p>Ak on pakèt moun-n ki tap avansé </p>\n<p>Fan-m na presé-l </p>\n<p>kouri antré nan fou-la </p>\n<p>Kon you koulèv li tap fofilé </p>\n<p>Foul la anpil li té fòsé-l avansé </p>\n<p>Piès moun-n pat kon-n sal té vlé </p>\n<p>Li fè anpil efò jouk tan rive </p>\n<p>Fan-m nou kanpé, </p>\n<p>li té touché rob la </p>\n<p>Li té join-n you puisans nan ròb la </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>A la yon puissans ki gin nan ròb Jézy ya</strong> </p>\n<p><strong>Fan-m nan té agi foua li</strong> </p>\n<p><strong>Sèlman li touché ròb la epui-l té</strong> </p>\n<p><strong>join-n gérizon</strong> </p>\n<p><strong>Gin yon bagay Segnè ya té di fan-m nan</strong> </p>\n<p><strong>Sa se yon leson ki vlé pou-n pratiké</strong> </p>\n<p><strong>Avèk la foua nou va jouin tout sa nou vlé</strong> </p>\n<p><strong>Min fòk nou met konfians</strong> </p>\n<p><strong>nou nan Jézu</strong> </p>\n<p><br/> <strong>3.</strong></p>\n<p>Min-m Jodi-a ya pandan nou nan legliz la </p>\n<p>Jezu gin lòt mirak pou-l opéré </p>\n<p>Moun-n kap prié ou mèt kontinye prié </p>\n<p>Gin anpil muray ki pral krazé </p>\n<p>Pèsévéré pa dékourajé frè-m yo </p>\n<p>Kinbe la foua vié sè-m yo </p>\n<p>Talè konsa nou tout pral deliver </p>\n<p>Tout moun-n ki vlé </p>\n<p>Kapab gouté puissans la </p>\n<p>Si yo vlé met konfians nan Jézu </p>\n<p>Jehovah Nissy </p>\n','Te-gin-gnou-fan-m-ki-te-fe-12-an-malad',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2394,'5e15d893-2b5e-4a60-ac59-1cab435a0df6',0,0,'Voici cher Seigneur tes pieux serviteurs',81,'81_RN_fr','1.\nVoici cher Seigneur tes pieux serviteurs\nQui viennent pour s’unir dans le mariage (bis)\nRefrain\nJette sur eux ta bénédiction\nPour que ta parole soit gardée (bis)\n2.\nJeune homme aime ta femme\nCar saint Paul a dit :\nAime-la, protège-la en toute chose. (Bis)\n3.\nEt toi jeune fille,\nSois toujours fidele,\nRespecte ton mari, obéis-lui. (Bis)\n4.\nJeune homme et jeune fille\nQui viennent donc s’unir\nVous êtes en Jésus qu’une seule chair. (Bis)\n5.\nQue la grâce de Dieu\nVous soit abondée\nPour que vous viviez toujours en paix. (Bis)','<p><strong>1.</strong> </p>\n<p>Voici cher Seigneur tes pieux serviteurs </p>\n<p>Qui viennent pour s’unir dans le mariage (bis) </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jette sur eux ta bénédiction</strong> </p>\n<p><strong>Pour que ta parole soit gardée (bis)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jeune homme aime ta femme </p>\n<p>Car saint Paul a dit : </p>\n<p>Aime-la, protège-la en toute chose. (Bis) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et toi jeune fille, </p>\n<p>Sois toujours fidele, </p>\n<p>Respecte ton mari, obéis-lui. (Bis) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jeune homme et jeune fille </p>\n<p>Qui viennent donc s’unir </p>\n<p>Vous êtes en Jésus qu’une seule chair. (Bis) </p>\n<p><br/> <strong>5.</strong></p>\n<p>Que la grâce de Dieu </p>\n<p>Vous soit abondée </p>\n<p>Pour que vous viviez toujours en paix. (Bis) </p>\n','Voici-cher-Seigneur-tes-pieux-serviteurs',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2395,'8ed8e761-a24c-458d-aaf6-2346ab96a63b',0,0,'Si la route est jonchée de peine et de douleurs',78,'78_RN_fr','1.\nSi la route est jonchée de peine et de douleurs\nSi ton soleil sombre dans un noir nuage\nEt que nul rayon d’espoir n’illumine ton cœur\nPense à Christ, à son message\nRefrain\nJésus ton Sauveur sera ton guide\nA lui seul confie ton sort, il agira\nRappelles-toi bien que dans les lieux arides,\nTon âme, il rassasiera.\n2.\nSi la personne sur la terre ne peut te comprendre\nSi la tentation t’a charmée à t’assiéger\nSi tes vains efforts ne suffisent pour te défendre\nFais de Christ ton bouclier.\n3.\nSi ton pauvre cœur à force de revêts sans flamme\nEt que tu finis par te décourager,\nSaches que Christ connait les tortures de ton âme\nEt veut bien te soulager.','<p><strong>1.</strong> </p>\n<p>Si la route est jonchée de peine et de douleurs </p>\n<p>Si ton soleil sombre dans un noir nuage </p>\n<p>Et que nul rayon d’espoir n’illumine ton cœur </p>\n<p>Pense à Christ, à son message </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus ton Sauveur sera ton guide</strong> </p>\n<p><strong>A lui seul confie ton sort, il agira</strong> </p>\n<p><strong>Rappelles-toi bien que dans les lieux arides,</strong> </p>\n<p><strong>Ton âme, il rassasiera.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si la personne sur la terre ne peut te comprendre </p>\n<p>Si la tentation t’a charmée à t’assiéger </p>\n<p>Si tes vains efforts ne suffisent pour te défendre </p>\n<p>Fais de Christ ton bouclier. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ton pauvre cœur à force de revêts sans flamme </p>\n<p>Et que tu finis par te décourager, </p>\n<p>Saches que Christ connait les tortures de ton âme </p>\n<p>Et veut bien te soulager. </p>\n','Si-la-route-est-jonchee-de-peine-et-de-douleurs',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2396,'20b2698c-3e12-484a-b813-feed401125ab',0,0,'Seigneur ce que mon coeur réclame',25,'25_GA_fr','1.\nSeigneur ce que mon coeur réclame\nC\'est le feu! (ter)\nQue sur moi descende la flamme\nDe ton feu (ter)\nQue ce sort une chambre haute Qu\'une nouvelle\nPentecôte fasse de nous de vrais apôtres\nPlein de feu (ter)\n\n2.\nLe seul secret de la victoire\nC\'est lefeu! (ter)\nNous pouvons refléter ta gloire\nPar le feu (ter)\nO Dieu tout-puissant qui nous aime\nVenez nous affranchir de nous-memes\nRemplis-nous d\'un amour suprême\nPar le feu! (ter)\n\n3.\nNous voulons porter I\'espérance\nPar le feu! (ter)\nEt proclamer la délivrance\nPar le feu! (ter)\nTon regne vienne sur la terre\nTa volonté soit faite, O Pere\nExauce enfin notre prière\nPar le feu! (ter)','<p><strong>1.</strong> </p>\n<p>Seigneur ce que mon coeur réclame </p>\n<p>C&#39;est le feu! (ter) </p>\n<p>Que sur moi descende la flamme </p>\n<p>De ton feu (ter) </p>\n<p>Que ce sort une chambre haute Qu&#39;une nouvelle </p>\n<p>Pentecôte fasse de nous de vrais apôtres </p>\n<p>Plein de feu (ter) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Le seul secret de la victoire </p>\n<p>C&#39;est lefeu! (ter) </p>\n<p>Nous pouvons refléter ta gloire </p>\n<p>Par le feu (ter) </p>\n<p>O Dieu tout-puissant qui nous aime </p>\n<p>Venez nous affranchir de nous-memes </p>\n<p>Remplis-nous d&#39;un amour suprême </p>\n<p>Par le feu! (ter) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Nous voulons porter I&#39;espérance </p>\n<p>Par le feu! (ter) </p>\n<p>Et proclamer la délivrance </p>\n<p>Par le feu! (ter) </p>\n<p>Ton regne vienne sur la terre </p>\n<p>Ta volonté soit faite, O Pere </p>\n<p>Exauce enfin notre prière </p>\n<p>Par le feu! (ter) </p>\n','Seigneur-ce-que-mon-coeur-reclame',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2397,'836b340c-3258-4ed3-b1ef-3e7d0a1cd830',0,0,'Du monde la gloire ainsi que tous les biens',37,'37_GA_fr','1.\nDu monde la gloire ainsi que tous les biens\nNe seront jamais ici-bàs mon partage\nParce que Jésus-Christ le Nazaréen\nLà haut me prépare un si grand héritage\n\nRefrain\nMa demeure est là où Jésus fait sa demeure\nLorsqu\'à travers un périlleux sentier\nIl s\'approche, me guide auprès de ma demeure\nM\'exhortant toujours à veiller, à prier\n\n2.\nBien souvent je suis tenté et balloté\nPour Satan le grand terrible adversaire\nConnaissant du monde l\'instabilité\nVers mon sûr abri, je crois par la prière\n\n3.\nQue j\'affronte laboeur, fatigue sur mes pas\nQue mon père, ma mere, mon frère m\'abondonnent\nQue je suis content Oh gloire alleluia\nJusqu\'au ciel m\'attend ma précieuse couronne','<p><strong>1.</strong> </p>\n<p>Du monde la gloire ainsi que tous les biens </p>\n<p>Ne seront jamais ici-bàs mon partage </p>\n<p>Parce que Jésus-Christ le Nazaréen </p>\n<p>Là haut me prépare un si grand héritage </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Ma demeure est là où Jésus fait sa demeure</strong> </p>\n<p><strong>Lorsqu&#39;à travers un périlleux sentier</strong> </p>\n<p><strong>Il s&#39;approche, me guide auprès de ma demeure</strong> </p>\n<p><strong>M&#39;exhortant toujours à veiller, à prier</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Bien souvent je suis tenté et balloté </p>\n<p>Pour Satan le grand terrible adversaire </p>\n<p>Connaissant du monde l&#39;instabilité </p>\n<p>Vers mon sûr abri, je crois par la prière </p>\n<p><br/> <strong>3.</strong></p>\n<p>Que j&#39;affronte laboeur, fatigue sur mes pas </p>\n<p>Que mon père, ma mere, mon frère m&#39;abondonnent </p>\n<p>Que je suis content Oh gloire alleluia </p>\n<p>Jusqu&#39;au ciel m&#39;attend ma précieuse couronne </p>\n','Du-monde-la-gloire-ainsi-que-tous-les-biens',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2398,'7f055b39-95f1-41ae-8e48-b66cf6a18551',0,0,'Moin sé solda Jézu',59,'59_GA_ht','1.\nMoin sé solda Jézu (bis)\nMin-m si moin\npa abyé an kaki\nPoté gnou revolvè,\ntire fizi an lè\nMin-m si moin pa gouvènin bato a\nMoin sé solda Jézu','<p><strong>1.</strong> </p>\n<p>Moin sé solda Jézu (bis) </p>\n<p>Min-m si moin </p>\n<p>pa abyé an kaki </p>\n<p>Poté gnou revolvè, </p>\n<p>tire fizi an lè </p>\n<p>Min-m si moin pa gouvènin bato a </p>\n<p>Moin sé solda Jézu </p>\n','Moin-se-solda-Jezu',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2399,'768ad3d5-63d1-4e45-bcae-f45ed62c4b5f',0,0,'Jézu ki rinmin moin',66,'66_GA_ht','1.\nJézu, Jézu, Jézu ki rinmin moin\nLi vi-n sou la tè,\nli ban moin la vi\nLi mouri pou moin é moin sové\nJézu résisté mò li té\ngéri malad yo\nLi chase demon li fè\navèg yo ouè\nLi té géri leper yo é min-m paralitik yo\nJézu bon Sovè ya\nté fè anpil mirak\n\nKè\nO mouin rinmin li map adore li\nMap chanté pou li sé mèvéyé\n\n2.\nMin yo té arétéli yo minnin-l tribunal\nYo té fè anpil ruz pou yo té pran-l nan pièj\nJézu pa té koupab min yo té kondané li\nYo té krisifié li, li resisité\nKounié yal vivan map chanté pou li\nO oui moin kontan li ban moin la vi\n\n3.\nLi mouri pou nou tout, li ban nou la vi\nNou gin rékonésans, map chanté pou li\nJézu, Jézu\nSi sé pou Jézu nou ap chanté\nMin si sé nan Jézu nou mèt dansé Jézu, Jézu\n\n4.\nLi té mouri pou moin, li té mouri pou ou\nSak fè li fè tou sa paské li rinmin nou\nLi alé nan sièl lap retounin ankò\nPou li kap vin chèché sa ki fè volonté-l\nLi fè nou promès sa sé vérité\nNou pa kap rete san-n pa chanté pou Jézu\n','<p><strong>1.</strong> </p>\n<p>Jézu, Jézu, Jézu ki rinmin moin </p>\n<p>Li vi-n sou la tè, </p>\n<p>li ban moin la vi </p>\n<p>Li mouri pou moin é moin sové </p>\n<p>Jézu résisté mò li té </p>\n<p>géri malad yo </p>\n<p>Li chase demon li fè </p>\n<p>avèg yo ouè </p>\n<p>Li té géri leper yo é min-m paralitik yo </p>\n<p>Jézu bon Sovè ya </p>\n<p>té fè anpil mirak </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>O mouin rinmin li map adore li</strong> </p>\n<p><strong>Map chanté pou li sé mèvéyé</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Min yo té arétéli yo minnin-l tribunal </p>\n<p>Yo té fè anpil ruz pou yo té pran-l nan pièj </p>\n<p>Jézu pa té koupab min yo té kondané li </p>\n<p>Yo té krisifié li, li resisité </p>\n<p>Kounié yal vivan map chanté pou li </p>\n<p>O oui moin kontan li ban moin la vi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Li mouri pou nou tout, li ban nou la vi </p>\n<p>Nou gin rékonésans, map chanté pou li </p>\n<p>Jézu, Jézu </p>\n<p>Si sé pou Jézu nou ap chanté </p>\n<p>Min si sé nan Jézu nou mèt dansé Jézu, Jézu </p>\n<p><br/> <strong>4.</strong></p>\n<p>Li té mouri pou moin, li té mouri pou ou </p>\n<p>Sak fè li fè tou sa paské li rinmin nou </p>\n<p>Li alé nan sièl lap retounin ankò </p>\n<p>Pou li kap vin chèché sa ki fè volonté-l </p>\n<p>Li fè nou promès sa sé vérité </p>\n<p>Nou pa kap rete san-n pa chanté pou Jézu </p>\n','Jezu-ki-rinmin-moin',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2400,'a3bbcf0d-13a1-4166-8d3c-88deb23fb8dd',0,0,'Nouvelle agréable',87,'87_RN_fr','1.\nNouvelle agréable,\nUn sauveur enfant nous est né \nC’est dans une étable\nQu’il nous est donne\n2.\nEn cette nuit le Christ est né,\nC’est pour nous qu’il est incarne\nVenez pasteurs offrir vos cœurs\nAimez cet enfant tout aimable\n3.\nSatan revenez dans ses bras\nLes peuples de tout l’univers,\nMais cette nuit Satan s’enfuie,\nDevant cet enfant adorable\n4.\nEn cette nuit le Christ est né\nC’est pour nous qu’il est incarné,\nVenez chercher et partager\nA vivre de sa naissance.','<p><strong>1.</strong> </p>\n<p>Nouvelle agréable, </p>\n<p>Un sauveur enfant nous est né </p>\n<p>C’est dans une étable </p>\n<p>Qu’il nous est donne </p>\n<p><br/> <strong>2.</strong></p>\n<p>En cette nuit le Christ est né, </p>\n<p>C’est pour nous qu’il est incarne </p>\n<p>Venez pasteurs offrir vos cœurs </p>\n<p>Aimez cet enfant tout aimable </p>\n<p><br/> <strong>3.</strong></p>\n<p>Satan revenez dans ses bras </p>\n<p>Les peuples de tout l’univers, </p>\n<p>Mais cette nuit Satan s’enfuie, </p>\n<p>Devant cet enfant adorable </p>\n<p><br/> <strong>4.</strong></p>\n<p>En cette nuit le Christ est né </p>\n<p>C’est pour nous qu’il est incarné, </p>\n<p>Venez chercher et partager </p>\n<p>A vivre de sa naissance. </p>\n','Nouvelle-agreable',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2401,'dce16e3d-a61d-417a-859f-41516eacc3b4',0,0,'Il est un nom, un nom béni et saint',11,'11_GA_fr','1.\nIl est un nom, un nom béni et saint\nUn nom divin que nul n\'implore en vain\nDe ce nom vient le salut des humains\nLe nom de Jésus, Précieux Jésus.\n\nRefrain\nGlorieux nom, Glorieux nom,\nGlorieux nom\nLe nom de Jésus, Precieux Jésus\nIl est un nom qui signifie espoir\nQui rend heureux chassant le désespoir\nEn ce saint nom Dieu veut nous recevoir\nLe nom de Jésus, Précieux Jésus\n\n2.\nIl est un nom le seul qui fût donné\nNom par lequel je puis être sauvé\nDe tous les noms c\'est le plus élevé\nLe nom de Jésus précieux Jésus.','<p><strong>1.</strong> </p>\n<p>Il est un nom, un nom béni et saint </p>\n<p>Un nom divin que nul n&#39;implore en vain </p>\n<p>De ce nom vient le salut des humains </p>\n<p>Le nom de Jésus, Précieux Jésus. </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Glorieux nom, Glorieux nom,</strong> </p>\n<p><strong>Glorieux nom</strong> </p>\n<p><strong>Le nom de Jésus, Precieux Jésus</strong> </p>\n<p><strong>Il est un nom qui signifie espoir</strong> </p>\n<p><strong>Qui rend heureux chassant le désespoir</strong> </p>\n<p><strong>En ce saint nom Dieu veut nous recevoir</strong> </p>\n<p><strong>Le nom de Jésus, Précieux Jésus</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il est un nom le seul qui fût donné </p>\n<p>Nom par lequel je puis être sauvé </p>\n<p>De tous les noms c&#39;est le plus élevé </p>\n<p>Le nom de Jésus précieux Jésus. </p>\n','Il-est-un-nom-un-nom-beni-et-saint',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2402,'3464c188-8519-4083-bd16-ddac16b49aa9',0,0,'Peut-être au matin',24,'24_GA_fr','1.\nPeut-être au matin,\nQuand le soleil se lève\nEt que ses rayons chassent la nuit si brève\nJésus reviendra dans sa gloire immortelle\nPour prendre du monde les siens\n\nRefrain\nSeigneur Jésus, combien de temps\nPour qu\'éclatent nos chants\nJésus revient Alleluia! Alleluia! Amen\n\n2.\nPeut-être qu\'au soir,\nOù peut-être qu\'a midi\nOù peut-être encore qu\'aux ténèbres de minuit\nTout illumine par l\'éclat de sa gloire\nLe Seigneur prendra les siens.','<p><strong>1.</strong> </p>\n<p>Peut-être au matin, </p>\n<p>Quand le soleil se lève </p>\n<p>Et que ses rayons chassent la nuit si brève </p>\n<p>Jésus reviendra dans sa gloire immortelle </p>\n<p>Pour prendre du monde les siens </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Seigneur Jésus, combien de temps</strong> </p>\n<p><strong>Pour qu&#39;éclatent nos chants</strong> </p>\n<p><strong>Jésus revient Alleluia! Alleluia! Amen</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Peut-être qu&#39;au soir, </p>\n<p>Où peut-être qu&#39;a midi </p>\n<p>Où peut-être encore qu&#39;aux ténèbres de minuit </p>\n<p>Tout illumine par l&#39;éclat de sa gloire </p>\n<p>Le Seigneur prendra les siens. </p>\n','Peut-etre-au-matin',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2403,'59eb4709-fd33-4b2f-905e-182fc4cdc04c',0,0,'Si la mer se déchaîne',35,'35_GA_fr','1.\nSi la mer se déchaîne\nSi le vent soufflé fort\nSi la vague t\'entraîne\nN\'aie pas peur de la mort\n\nRefrain\nIl n\'a pas dit que tu coulerais\nIl n\'a pas dit que tu tomberais\nIl a dit: Allons de l\'autre bord\nIl a dit: Je te délivrerai\nIl a dit: Je te protègerai\n\n2.\nSi ton coeur est en peine\nSi ton corps est souffrant\nCrois en Jésus qui t\'aime\nIl est le tout-puissant\n\n3.\nSi un jour sur ta route\nTu croises le méchant\nNe sois pas dans le doute\nDieu prendra soin de son enfant','<p><strong>1.</strong> </p>\n<p>Si la mer se déchaîne </p>\n<p>Si le vent soufflé fort </p>\n<p>Si la vague t&#39;entraîne </p>\n<p>N&#39;aie pas peur de la mort </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Il n&#39;a pas dit que tu coulerais</strong> </p>\n<p><strong>Il n&#39;a pas dit que tu tomberais</strong> </p>\n<p><strong>Il a dit: Allons de l&#39;autre bord</strong> </p>\n<p><strong>Il a dit: Je te délivrerai</strong> </p>\n<p><strong>Il a dit: Je te protègerai</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si ton coeur est en peine </p>\n<p>Si ton corps est souffrant </p>\n<p>Crois en Jésus qui t&#39;aime </p>\n<p>Il est le tout-puissant </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si un jour sur ta route </p>\n<p>Tu croises le méchant </p>\n<p>Ne sois pas dans le doute </p>\n<p>Dieu prendra soin de son enfant </p>\n','Si-la-mer-se-dechaine',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2404,'833395fb-934c-4052-94b1-7e9a37e8d8d9',0,0,'Jan péché yo multiplié',52,'52_GA_ht','1.\nJan péché yo multiplié\nNou mandé ou gras Segnè\nPéché kampé a douat a goch\nNou mandé ou gras Segnè\n\nKè\nGras, gras padon Segnè\nGras, gras padoné nou\nGras, gras pou fot nou yo\nNou rekonèt péché nou\n\n2.\nPadoné nou O Dié damou\nNou rekonèt péché nou\nAdultè, vol ak méchansté\nYo vini multiplié\n\n3.\nLang nou sé gnou kouto de bò\nNou mandé ou gras Segnè\nBouch nou pale sa nou pa ouè\nNou mandé ou gras Segnè\n\n4.\nZorèy nou toujou ap tandé\nPou nou fè la médizans\nPié nou pa janmè fatigue\nPou nou ka al fè le mal\n','<p><strong>1.</strong> </p>\n<p>Jan péché yo multiplié </p>\n<p>Nou mandé ou gras Segnè </p>\n<p>Péché kampé a douat a goch </p>\n<p>Nou mandé ou gras Segnè </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Gras, gras padon Segnè</strong> </p>\n<p><strong>Gras, gras padoné nou</strong> </p>\n<p><strong>Gras, gras pou fot nou yo</strong> </p>\n<p><strong>Nou rekonèt péché nou</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Padoné nou O Dié damou </p>\n<p>Nou rekonèt péché nou </p>\n<p>Adultè, vol ak méchansté </p>\n<p>Yo vini multiplié </p>\n<p><br/> <strong>3.</strong></p>\n<p>Lang nou sé gnou kouto de bò </p>\n<p>Nou mandé ou gras Segnè </p>\n<p>Bouch nou pale sa nou pa ouè </p>\n<p>Nou mandé ou gras Segnè </p>\n<p><br/> <strong>4.</strong></p>\n<p>Zorèy nou toujou ap tandé </p>\n<p>Pou nou fè la médizans </p>\n<p>Pié nou pa janmè fatigue </p>\n<p>Pou nou ka al fè le mal </p>\n','Jan-peche-yo-multiplie',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2405,'1d6e2236-2fb1-436f-a758-6e201dfc4701',0,0,'Entre vite dans le vrai chemin',92,'92_RN_fr','1.\nEntre vite dans le vrai chemin,\nCrois, pécheur, à la vérité\nDe Christ reçoit la vie enfin,\nTu seras sauvé\nRefrain\nOh ! Viens sans tarder (bis)\nAccepte Jésus tu seras sauve.\n2.\nJésus est ton parfait Sauveur,\nL’Envoyé de ton Dieu d’amour\nA l’instant donne-lui ton cœur,\nOh ! Viens en ce jour.\n3.\nJésus reçoit chaque pécheur\nDans ta misère, viens à lui.\nNe refuse pas ce Sauveur,\nTon plus sur appui.\n4.\nPourquoi renvoyer à demain\nCe choix de qui dépend ton sort\nCar qui sait si le lendemain\nNe suivra ta mort','<p><strong>1.</strong> </p>\n<p>Entre vite dans le vrai chemin, </p>\n<p>Crois, pécheur, à la vérité </p>\n<p>De Christ reçoit la vie enfin, </p>\n<p>Tu seras sauvé </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh ! Viens sans tarder (bis)</strong> </p>\n<p><strong>Accepte Jésus tu seras sauve.</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus est ton parfait Sauveur, </p>\n<p>L’Envoyé de ton Dieu d’amour </p>\n<p>A l’instant donne-lui ton cœur, </p>\n<p>Oh ! Viens en ce jour. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus reçoit chaque pécheur </p>\n<p>Dans ta misère, viens à lui. </p>\n<p>Ne refuse pas ce Sauveur, </p>\n<p>Ton plus sur appui. </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pourquoi renvoyer à demain </p>\n<p>Ce choix de qui dépend ton sort </p>\n<p>Car qui sait si le lendemain </p>\n<p>Ne suivra ta mort </p>\n','Entre-vite-dans-le-vrai-chemin',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2406,'5a250ea3-3562-42f3-9052-690a66c9f765',0,0,'Dans les parvis des lieux très hauts',14,'14_GA_fr','1.\nDans les parvis des lieux très hauts\nJésus vivant en forme de Dieu\nAbandonna la gloire du ciel\nOn l\'appela Emmanuel\n\n2.\nIl s\'est lui-même dépouillé\nPrenant la forme d\'un Serviteur\nEt devenant mystère divin\nSemblable aux hommes, quel destin\n\n3.\nComme un simple homme ayant paru\nIl s\'est lui-même abaissé pour nous\nObeissant jusqu\'à la mort\nEt même la mort de la croix\n\n4.\nC\'est pourquoi aussi notre Dieu\nL\'a souverainement élevé\nEt lui a donné le beau nom\nQui est au-dessus de tout nom\nAfin qu\'au nom de Jésus-Christ\n\n5.\nTous les genoux viennent aflechir\nDans tous les cieux, sur terre et sous terre\nAlleluia! Alleluia!','<p><strong>1.</strong> </p>\n<p>Dans les parvis des lieux très hauts </p>\n<p>Jésus vivant en forme de Dieu </p>\n<p>Abandonna la gloire du ciel </p>\n<p>On l&#39;appela Emmanuel </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il s&#39;est lui-même dépouillé </p>\n<p>Prenant la forme d&#39;un Serviteur </p>\n<p>Et devenant mystère divin </p>\n<p>Semblable aux hommes, quel destin </p>\n<p><br/> <strong>3.</strong></p>\n<p>Comme un simple homme ayant paru </p>\n<p>Il s&#39;est lui-même abaissé pour nous </p>\n<p>Obeissant jusqu&#39;à la mort </p>\n<p>Et même la mort de la croix </p>\n<p><br/> <strong>4.</strong></p>\n<p>C&#39;est pourquoi aussi notre Dieu </p>\n<p>L&#39;a souverainement élevé </p>\n<p>Et lui a donné le beau nom </p>\n<p>Qui est au-dessus de tout nom </p>\n<p>Afin qu&#39;au nom de Jésus-Christ </p>\n<p><br/> <strong>5.</strong></p>\n<p>Tous les genoux viennent aflechir </p>\n<p>Dans tous les cieux, sur terre et sous terre </p>\n<p>Alleluia! Alleluia! </p>\n','Dans-les-parvis-des-lieux-tres-hauts',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2407,'078c6456-075e-4f36-a9ce-c8ff3f009a11',1,0,'Si tu sens vaciller ta foi',3,'3_GA_fr','1.\nSi tu sens vaciller ta foi\nDevant la tempête à garde\nCalme-toi, Calme-toi\nDieu te garde, calme-toi\nDieu te garde, calme-toi\nDieu te garde, Dieu te garde\nDieu te garde, calme-toi\n\n2.\nSi d\'après la commune loi\nDans le néant tombe chaque rue\nCalme-toi, Calme-toi\nDieu demeure, calme-toi\nDieu demeure, calme-toi\nDieu demeure - Dieu demeure\nDieu demeure, calme toi\n\n3.\nSi ton coeur est rempli d\'émoi\nSi le désespoir t\'environne\nCalme-toi - Calme-toi\nDieu pardonne, calme-toi\nDieu pardonne, calme-toi\nDieu pardonne, calme-toi\n\n4.\nSi la mort te comble d\'effroi\nSi tu crains I\'ombre où tu someilles\nCalme-toi - Calme-toi\nDieu réveille, calme-toi\nDieu réveille, Dieu réveille\nDieu réveille, calme-toi','<p><strong>1.</strong> </p>\n<p>Si tu sens vaciller ta foi </p>\n<p>Devant la tempête à garde </p>\n<p>Calme-toi, Calme-toi </p>\n<p>Dieu te garde, calme-toi </p>\n<p>Dieu te garde, calme-toi </p>\n<p>Dieu te garde, Dieu te garde </p>\n<p>Dieu te garde, calme-toi </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si d&#39;après la commune loi </p>\n<p>Dans le néant tombe chaque rue </p>\n<p>Calme-toi, Calme-toi </p>\n<p>Dieu demeure, calme-toi </p>\n<p>Dieu demeure, calme-toi </p>\n<p>Dieu demeure - Dieu demeure </p>\n<p>Dieu demeure, calme toi </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ton coeur est rempli d&#39;émoi </p>\n<p>Si le désespoir t&#39;environne </p>\n<p>Calme-toi - Calme-toi </p>\n<p>Dieu pardonne, calme-toi </p>\n<p>Dieu pardonne, calme-toi </p>\n<p>Dieu pardonne, calme-toi </p>\n<p><br/> <strong>4.</strong></p>\n<p>Si la mort te comble d&#39;effroi </p>\n<p>Si tu crains I&#39;ombre où tu someilles </p>\n<p>Calme-toi - Calme-toi </p>\n<p>Dieu réveille, calme-toi </p>\n<p>Dieu réveille, Dieu réveille </p>\n<p>Dieu réveille, calme-toi </p>\n','Si-tu-sens-vaciller-ta-foi',NULL,'fr',6,'2023-07-22 22:41:15','2023-10-03 20:30:29'),(2408,'f51a96e6-2b2e-411f-ab3f-d4a03b70edb8',0,0,'Il s\'est tu devant ses bourreaux',30,'30_GA_fr','1.\nIl s\'est tu devant ses bourreaux\nIl s\'est tu pourtant lui le maître\nIl est allé comme un agneau\nVers le lieu qui meurtrit son être\n\n2.\nIl s\'est laissé abandonner\nOh lui qui méritait la gloire\nÊtre injurieux et maltraité\nVoilà le prix de la victoire\n\n3.\nDont je n\'aurais jamais pensé\nQu\'il m\'eut tant aimé\nJusqu\'à donner sa vie pour moi\nJusqu\'à la mort de la Croix\nOOO OOOO\n\n4.\nJe ne sais pas quelle mystère\nIl vint sur la Terre\nPour apporter aux humains\nLe bonheur dont ils avaient besoin\n\n5.\nIl s\'est tu devant ses bourreaux\nIl s\'est tu pourtant lui le maître\nIl est allé comme un agneau\nVers le lieu qui meurtrit son être','<p><strong>1.</strong> </p>\n<p>Il s&#39;est tu devant ses bourreaux </p>\n<p>Il s&#39;est tu pourtant lui le maître </p>\n<p>Il est allé comme un agneau </p>\n<p>Vers le lieu qui meurtrit son être </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il s&#39;est laissé abandonner </p>\n<p>Oh lui qui méritait la gloire </p>\n<p>Être injurieux et maltraité </p>\n<p>Voilà le prix de la victoire </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dont je n&#39;aurais jamais pensé </p>\n<p>Qu&#39;il m&#39;eut tant aimé </p>\n<p>Jusqu&#39;à donner sa vie pour moi </p>\n<p>Jusqu&#39;à la mort de la Croix </p>\n<p>OOO OOOO </p>\n<p><br/> <strong>4.</strong></p>\n<p>Je ne sais pas quelle mystère </p>\n<p>Il vint sur la Terre </p>\n<p>Pour apporter aux humains </p>\n<p>Le bonheur dont ils avaient besoin </p>\n<p><br/> <strong>5.</strong></p>\n<p>Il s&#39;est tu devant ses bourreaux </p>\n<p>Il s&#39;est tu pourtant lui le maître </p>\n<p>Il est allé comme un agneau </p>\n<p>Vers le lieu qui meurtrit son être </p>\n','Il-s\'est-tu-devant-ses-bourreaux',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2409,'6f96572a-66d4-4438-8e51-718214d15cf1',0,0,'Ton amour et ta bonté',36,'36_GA_fr','Refrain\nEl Shaddai, El Shaddai\nEl Elyona Adonai\nD\'âge en âge tu es le même\nPar le nom puissant que j\'aime\nEl Shaddai, El Shaddai\nEl Elyona Adonai\nJe te loue O nom béni, El Shaddai\n\n1.\nTon amour et ta bonté\nPour Abraham ont tout changé\nLa puissance de ta main\nDans la main fit son chemin\nTu accordas tes bienfaits\nPar ta grandeur tu les as libérés\n\n2.\nLes années l\'ont bien montré\nChrist est venu en son temps\nParmi les gens aveuglés\nQue tu as pourtant aimés\nTa parole, ton divin plan\nPour eux n\'étaient que du vent\nTu avais tout accompli\nPar ton fils donnant sa vie\n\nRefrain\nEl Shaddai, El Shaddai\nEl Elyona Adonai\nD\'âge en âge tu es le même\nPar le nom puissant que j\'aime\nEl Shaddai, El Shaddai\nEl Elyona Adonai\nTu as gagné la bataille, El Shaddai','<p><strong>Refrain</strong> </p>\n<p>El Shaddai, El Shaddai </p>\n<p>El Elyona Adonai </p>\n<p>D&#39;âge en âge tu es le même </p>\n<p>Par le nom puissant que j&#39;aime </p>\n<p>El Shaddai, El Shaddai </p>\n<p>El Elyona Adonai </p>\n<p>Je te loue O nom béni, El Shaddai </p>\n<p><br/> <strong>1.</strong></p>\n<p>Ton amour et ta bonté </p>\n<p>Pour Abraham ont tout changé </p>\n<p>La puissance de ta main </p>\n<p>Dans la main fit son chemin </p>\n<p>Tu accordas tes bienfaits </p>\n<p>Par ta grandeur tu les as libérés </p>\n<p><br/> <strong>2.</strong></p>\n<p>Les années l&#39;ont bien montré </p>\n<p>Christ est venu en son temps </p>\n<p>Parmi les gens aveuglés </p>\n<p>Que tu as pourtant aimés </p>\n<p>Ta parole, ton divin plan </p>\n<p>Pour eux n&#39;étaient que du vent </p>\n<p>Tu avais tout accompli </p>\n<p>Par ton fils donnant sa vie </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>El Shaddai, El Shaddai</strong> </p>\n<p><strong>El Elyona Adonai</strong> </p>\n<p><strong>D&#39;âge en âge tu es le même</strong> </p>\n<p><strong>Par le nom puissant que j&#39;aime</strong> </p>\n<p><strong>El Shaddai, El Shaddai</strong> </p>\n<p><strong>El Elyona Adonai</strong> </p>\n<p><strong>Tu as gagné la bataille, El Shaddai</strong> </p>\n','Ton-amour-et-ta-bonte',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2410,'41bd7d28-9244-4744-a7a2-a24c1b4b1ed1',0,0,'Sainte, Trinité puissante',38,'38_GA_fr','1.\nSainte, Sainte, Sainte, Trinité puissante\nDès l\'aurore du matin, vers toi montent nos chants\nSainte, Sainte, Sainte Lumière éclatante\nEn trois personnes, un Dieu bienfaisant\n\n2.\nSainte, Sainte, Sainte, tous les saints t\'adorent\nEn jetant leurs couronnes à tes pieds, Dieu d\'amour\nChérubins et Seraphins te louent, t\'honorent\nToi qui fus, es et seras pour toujours\n\n3.\nSainte, Sainte, Sainte Trinité mystère\nDont la gloire ne peut voir l\'oeil de l\'homme pécheur\nToi sur la sainte et divine lumière\nParfait en puissance, amour et bonheur\n\n4.\nSainte, Sainte, Sainte Trinité puissante\nChaque créature doit glorifier ton Nom\nSainte, Sainte, Sainte Lumière éclatante\nDieu de grâce et d\'amour et de pardon','<p><strong>1.</strong> </p>\n<p>Sainte, Sainte, Sainte, Trinité puissante </p>\n<p>Dès l&#39;aurore du matin, vers toi montent nos chants </p>\n<p>Sainte, Sainte, Sainte Lumière éclatante </p>\n<p>En trois personnes, un Dieu bienfaisant </p>\n<p><br/> <strong>2.</strong></p>\n<p>Sainte, Sainte, Sainte, tous les saints t&#39;adorent </p>\n<p>En jetant leurs couronnes à tes pieds, Dieu d&#39;amour </p>\n<p>Chérubins et Seraphins te louent, t&#39;honorent </p>\n<p>Toi qui fus, es et seras pour toujours </p>\n<p><br/> <strong>3.</strong></p>\n<p>Sainte, Sainte, Sainte Trinité mystère </p>\n<p>Dont la gloire ne peut voir l&#39;oeil de l&#39;homme pécheur </p>\n<p>Toi sur la sainte et divine lumière </p>\n<p>Parfait en puissance, amour et bonheur </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sainte, Sainte, Sainte Trinité puissante </p>\n<p>Chaque créature doit glorifier ton Nom </p>\n<p>Sainte, Sainte, Sainte Lumière éclatante </p>\n<p>Dieu de grâce et d&#39;amour et de pardon </p>\n','Sainte-Trinite-puissante',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2411,'2218188b-149e-4a86-955b-037a5379090a',0,0,'Padoné moin Segnè Jezu',41,'41_GA_ht','1.\nPadoné moin Segnè Jezu\nPadoné moin pou fot moin yo\nOu min-m kit e desann sou tè ya\nPou nan-m moin té kap sove\nGingnin pitié pou la vi moin\nMin-m si éta-m pa bon devan ou\nDi gnou mo pou moin nan jujman sa\nPou-m ka sové\n\nKè\nKi sa ma fè nan gran jou sa\nSi Jézu pa resevoua moin\nLan pòt sièl la gin sentinel\nKi kanpé la, baton-l nan min-l\nSi ou sové la resevoua ou\nSi ou pa sove lap di ou alé\nDi gnou mo pou moin Segnè Jézu\npou-m ka sové\n\n2.\nGin gnou jujman ki gin pou-l vini\nGin gnou balans ki pral balansé\nGin anpil pay ki pral boulé,\nNan difé sa-a gin pou\'l vini\nSové-m Ségnè ak zanmi-m yo\nFanmi-yo, ak tout frè moin yo\nPou-m pa gnou pay, pou-n pa al boulé\nNan difé sa\n\n3.\nGin anpil mou-n ki kouè se jouèt\nPa gin gnou jujman kap vini\nNou min m kretyin nou asuré\nGin gnou jujman ki gin pou vini\nNap fè éfo, nape travay\nNap fè éfo pou nou sové\nNan gran jou sa-a\nPou-n pa al boulé nan difé sa-a','<p><strong>1.</strong> </p>\n<p>Padoné moin Segnè Jezu </p>\n<p>Padoné moin pou fot moin yo </p>\n<p>Ou min-m kit e desann sou tè ya </p>\n<p>Pou nan-m moin té kap sove </p>\n<p>Gingnin pitié pou la vi moin </p>\n<p>Min-m si éta-m pa bon devan ou </p>\n<p>Di gnou mo pou moin nan jujman sa </p>\n<p>Pou-m ka sové </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Ki sa ma fè nan gran jou sa</strong> </p>\n<p><strong>Si Jézu pa resevoua moin</strong> </p>\n<p><strong>Lan pòt sièl la gin sentinel</strong> </p>\n<p><strong>Ki kanpé la, baton-l nan min-l</strong> </p>\n<p><strong>Si ou sové la resevoua ou</strong> </p>\n<p><strong>Si ou pa sove lap di ou alé</strong> </p>\n<p><strong>Di gnou mo pou moin Segnè Jézu</strong> </p>\n<p><strong>pou-m ka sové</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin gnou jujman ki gin pou-l vini </p>\n<p>Gin gnou balans ki pral balansé </p>\n<p>Gin anpil pay ki pral boulé, </p>\n<p>Nan difé sa-a gin pou&#39;l vini </p>\n<p>Sové-m Ségnè ak zanmi-m yo </p>\n<p>Fanmi-yo, ak tout frè moin yo </p>\n<p>Pou-m pa gnou pay, pou-n pa al boulé </p>\n<p>Nan difé sa </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin anpil mou-n ki kouè se jouèt </p>\n<p>Pa gin gnou jujman kap vini </p>\n<p>Nou min m kretyin nou asuré </p>\n<p>Gin gnou jujman ki gin pou vini </p>\n<p>Nap fè éfo, nape travay </p>\n<p>Nap fè éfo pou nou sové </p>\n<p>Nan gran jou sa-a </p>\n<p>Pou-n pa al boulé nan difé sa-a </p>\n','Padone-moin-Segne-Jezu',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2412,'b1ec5241-2650-4011-bc78-a375a26e3431',0,0,'Gnou zanmi rinmin-m',46,'46_GA_ht','1.\nGnou zanmi rinmin-m, gnou zanmi raiy-m\nGnou zanmi sové moin\nMouin gin gnou zanmi toutan-m gin lajan\nLè-m pa gin lajan mouin pa angnin\n\nKè\nJézu sové-m, li deliver-m, Jézu sové-m nan mouin\nO moin vlé chanté istoua li, glorifié non li\nJézu ou sé mèt moin\nMoin rankontré Jézu nan gnou chemin\nLi mandé mouin koté-m pralé\nMoin di le Segnè\nsé ou map chaché\nMap suiv ou juska la fin\n\n2.\nMouin té kondané pou-m té mouri\nPaské péché-m té gro\nJézu rélé moin\nsou mo-n Golgota\nPou-l ban moin la vi étènèl\n\n3.\nGnou zanmi ban moin, karès amou\nTou tan pa lan tonbo\nMoin gin gnou zanmi toutan-m gin lajan\nLè-m pa gin lajan\nmoin pa angnin\n','<p><strong>1.</strong> </p>\n<p>Gnou zanmi rinmin-m, gnou zanmi raiy-m </p>\n<p>Gnou zanmi sové moin </p>\n<p>Mouin gin gnou zanmi toutan-m gin lajan </p>\n<p>Lè-m pa gin lajan mouin pa angnin </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Jézu sové-m, li deliver-m, Jézu sové-m nan mouin</strong> </p>\n<p><strong>O moin vlé chanté istoua li, glorifié non li</strong> </p>\n<p><strong>Jézu ou sé mèt moin</strong> </p>\n<p><strong>Moin rankontré Jézu nan gnou chemin</strong> </p>\n<p><strong>Li mandé mouin koté-m pralé</strong> </p>\n<p><strong>Moin di le Segnè</strong> </p>\n<p><strong>sé ou map chaché</strong> </p>\n<p><strong>Map suiv ou juska la fin</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Mouin té kondané pou-m té mouri </p>\n<p>Paské péché-m té gro </p>\n<p>Jézu rélé moin </p>\n<p>sou mo-n Golgota </p>\n<p>Pou-l ban moin la vi étènèl </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gnou zanmi ban moin, karès amou </p>\n<p>Tou tan pa lan tonbo </p>\n<p>Moin gin gnou zanmi toutan-m gin lajan </p>\n<p>Lè-m pa gin lajan </p>\n<p>moin pa angnin </p>\n','Gnou-zanmi-rinmin-m',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2413,'d3ec075e-129a-40de-b8b7-202ff9f06329',0,0,'Pécheur, ton Dieu t’offre sa grâce',94,'94_RN_fr','1.\nPécheur, ton Dieu t’offre sa grâce,\nNe veux-tu pas la recevoir ?\nNe détourne plus ta face\nSois sauvé ! Pourquoi pas ce soir ?\n\nRefrain\nOh ! Sois sauvé, sauvé ce soir (bis)\nTon Dieu t’invite pécheur, viens vite,\nOh ! Sois sauvé, sauvé ce soir !\n2.\nJésus ne repousse personne,\nIl t’attend pour te recevoir \nViens avant que ton heure sonne.\nSois sauvé ! Pourquoi pas ce soir ?\n3.\nPourquoi vivre dans la souffrance ?\nSans joie et sans céleste espoir \nOh ! Mets en Christ ta confiance ?\nEt tu seras sauvé ce soir.','<p><strong>1.</strong> </p>\n<p>Pécheur, ton Dieu t’offre sa grâce, </p>\n<p>Ne veux-tu pas la recevoir ? </p>\n<p>Ne détourne plus ta face </p>\n<p>Sois sauvé ! Pourquoi pas ce soir ? </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Oh ! Sois sauvé, sauvé ce soir (bis)</strong> </p>\n<p><strong>Ton Dieu t’invite pécheur, viens vite,</strong> </p>\n<p><strong>Oh ! Sois sauvé, sauvé ce soir !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus ne repousse personne, </p>\n<p>Il t’attend pour te recevoir </p>\n<p>Viens avant que ton heure sonne. </p>\n<p>Sois sauvé ! Pourquoi pas ce soir ? </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pourquoi vivre dans la souffrance ? </p>\n<p>Sans joie et sans céleste espoir </p>\n<p>Oh ! Mets en Christ ta confiance ? </p>\n<p>Et tu seras sauvé ce soir. </p>\n','Pecheur-ton-Dieu-t\'offre-sa-grace',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2414,'4c3dd2db-b7ff-4795-9393-1df330cb8f03',0,0,'Il va bientôt venir',32,'32_GA_fr','1.\nIl va bientôt venir! Gloire, Gloire à Jésus!\nDans le Ciel il veut reunir \nSes bien-aimés elus\nLevons en haut les yeux,\nTressaillons le bonheur\nIl vient sublime et glorieux,\nLe grand liberateur\n\n2.\nIl va bientot venir! Gloire, Gloire a Jésus\nToutes nos douleurs vont finir,\nLe mal ne sera plus\nLa mort disparaitra.  A la voix du Sauveur\nQuand pour toujours, il reviendra,\nLe grand libérateur\n\n3.\nIl va bientot venir, Gloire, Gloire à Jésus\nSur la nue il va resplendir, Frères ne craignons pas\nLevons, levons les yeux voici notre Sauveur\nC\'est pour nous qu\'il revient des cieux\nLe grand libérateur','<p><strong>1.</strong> </p>\n<p>Il va bientôt venir! Gloire, Gloire à Jésus! </p>\n<p>Dans le Ciel il veut reunir </p>\n<p>Ses bien-aimés elus </p>\n<p>Levons en haut les yeux, </p>\n<p>Tressaillons le bonheur </p>\n<p>Il vient sublime et glorieux, </p>\n<p>Le grand liberateur </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il va bientot venir! Gloire, Gloire a Jésus </p>\n<p>Toutes nos douleurs vont finir, </p>\n<p>Le mal ne sera plus </p>\n<p>La mort disparaitra.  A la voix du Sauveur </p>\n<p>Quand pour toujours, il reviendra, </p>\n<p>Le grand libérateur </p>\n<p><br/> <strong>3.</strong></p>\n<p>Il va bientot venir, Gloire, Gloire à Jésus </p>\n<p>Sur la nue il va resplendir, Frères ne craignons pas </p>\n<p>Levons, levons les yeux voici notre Sauveur </p>\n<p>C&#39;est pour nous qu&#39;il revient des cieux </p>\n<p>Le grand libérateur </p>\n','Il-va-bientot-venir',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2415,'8d04d1b8-d478-44a5-b34b-0a99dbfaf124',0,0,'Invoque-moi de sein de la détresse',39,'39_GA_fr','1.\nInvoque-moi du sein de la détresse\nMon bras puissant te sauvera\nC\'est Dieu qui parle \nOh! Crois à sa promesse\nBientôt sa voix t\'exaltera\n(bis 2 lignes)\n\n2.\nSi tout est sombre Oh! ne perds pas courage\nNe doute pas du Dieu vivant\nMême au milieu des fureurs de l\'orage\nDemeure en paix, crois seulement\n(bis 2 lignes)\n\n3.\nMais n\'attends pas que le mal te domine\nQue l\'ennemi soit dans ton coeur\nElle est à toi, la puissance divine\nSaisis-la donc et sois vainqueur\n(bis 2 lignes)\n\n4.\nJamais, jamais Dieu qui fit la promesse\nNe manquera de l\'accomplir\nQue ton enfant, sauvé de la détresse\nNe manqué pas de le bénir\n(bis 2 lignes)','<p><strong>1.</strong> </p>\n<p>Invoque-moi du sein de la détresse </p>\n<p>Mon bras puissant te sauvera </p>\n<p>C&#39;est Dieu qui parle </p>\n<p>Oh! Crois à sa promesse </p>\n<p>Bientôt sa voix t&#39;exaltera </p>\n<p>(bis 2 lignes) </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tout est sombre Oh! ne perds pas courage </p>\n<p>Ne doute pas du Dieu vivant </p>\n<p>Même au milieu des fureurs de l&#39;orage </p>\n<p>Demeure en paix, crois seulement </p>\n<p>(bis 2 lignes) </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mais n&#39;attends pas que le mal te domine </p>\n<p>Que l&#39;ennemi soit dans ton coeur </p>\n<p>Elle est à toi, la puissance divine </p>\n<p>Saisis-la donc et sois vainqueur </p>\n<p>(bis 2 lignes) </p>\n<p><br/> <strong>4.</strong></p>\n<p>Jamais, jamais Dieu qui fit la promesse </p>\n<p>Ne manquera de l&#39;accomplir </p>\n<p>Que ton enfant, sauvé de la détresse </p>\n<p>Ne manqué pas de le bénir </p>\n<p>(bis 2 lignes) </p>\n','Invoque-moi-de-sein-de-la-detresse',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2416,'7073f2c3-10fd-4751-81bf-e28e333ed07e',0,0,'Moin tandé Sovè-m kap rélé-m ',63,'63_GA_ht','1.\nMoin tandé Sovè-m kap rélé-m (ter)\nLi di-m pechè, pechè vin-n suiv moin\n\n2.\nMap suiv li jouk nan jadin an (ter)\nMoin vlé suiv li, suiv li nèt alé\n\n3.\nMap suiv li jouk devan Pilate (ter)\nMoin vlé suiv li, suiv li nèt alé\n\n4.\nMap suiv li jouk mòn Goldotha (ter)\nMouin vlé suiv, suiv li nèt alé\n\n5.\nMap suiv li jouk nan tonbo a (ter)\nMouin vlé suiv li, suiv li nèt alé\n\n6.\nMap suiv li jouk nan tonbo a (ter)\nMouin vlé suiv li, suiv li nèt alé\n\n7.\nMap suiv li jouk nan la gloua li (ter)\nMouin vlé suiv li, suiv li nèt alé','<p><strong>1.</strong> </p>\n<p>Moin tandé Sovè-m kap rélé-m (ter) </p>\n<p>Li di-m pechè, pechè vin-n suiv moin </p>\n<p><br/> <strong>2.</strong></p>\n<p>Map suiv li jouk nan jadin an (ter) </p>\n<p>Moin vlé suiv li, suiv li nèt alé </p>\n<p><br/> <strong>3.</strong></p>\n<p>Map suiv li jouk devan Pilate (ter) </p>\n<p>Moin vlé suiv li, suiv li nèt alé </p>\n<p><br/> <strong>4.</strong></p>\n<p>Map suiv li jouk mòn Goldotha (ter) </p>\n<p>Mouin vlé suiv, suiv li nèt alé </p>\n<p><br/> <strong>5.</strong></p>\n<p>Map suiv li jouk nan tonbo a (ter) </p>\n<p>Mouin vlé suiv li, suiv li nèt alé </p>\n<p><br/> <strong>6.</strong></p>\n<p>Map suiv li jouk nan tonbo a (ter) </p>\n<p>Mouin vlé suiv li, suiv li nèt alé </p>\n<p><br/> <strong>7.</strong></p>\n<p>Map suiv li jouk nan la gloua li (ter) </p>\n<p>Mouin vlé suiv li, suiv li nèt alé </p>\n','Moin-tande-Sove-m-kap-rele-m',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2417,'93a183bf-8d84-4a72-b773-b9df67884234',0,0,'Jésus de Nazareth, il passe ici',8,'8_GA_fr','1.\nJésus de Nazareth, il passe ici\nDe même il demeure à jamais\nEt comme autrefois il vient, il bénit\nIl aime à nous donner sa paix\n\n\nRefrain\nAujourd\'hui tandis qu\'il passe\nAujourd\'hui tandis qu\'il passe\nAujourd\'hui tandis qu\'il passe\nOuvrez-lui, ouvrez-lui ton coeur\n\n2.\nJésus de Nazareth vient-il en vain\nPourquoi voudrais-tu résister\nO hâte-toi donc de saisir sa main\nCar bientôt la nuit va tomber\nJésus de Nazareth son amour est grand\n\n3.\nPour tous ceux qui veulent sa paix\nPartout ou il passe, il pardonne, guérissant\nTous ceux qui sont pesés et chargés','<p><strong>1.</strong> </p>\n<p>Jésus de Nazareth, il passe ici </p>\n<p>De même il demeure à jamais </p>\n<p>Et comme autrefois il vient, il bénit </p>\n<p>Il aime à nous donner sa paix </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Aujourd&#39;hui tandis qu&#39;il passe</strong> </p>\n<p><strong>Aujourd&#39;hui tandis qu&#39;il passe</strong> </p>\n<p><strong>Aujourd&#39;hui tandis qu&#39;il passe</strong> </p>\n<p><strong>Ouvrez-lui, ouvrez-lui ton coeur</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jésus de Nazareth vient-il en vain </p>\n<p>Pourquoi voudrais-tu résister </p>\n<p>O hâte-toi donc de saisir sa main </p>\n<p>Car bientôt la nuit va tomber </p>\n<p>Jésus de Nazareth son amour est grand </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pour tous ceux qui veulent sa paix </p>\n<p>Partout ou il passe, il pardonne, guérissant </p>\n<p>Tous ceux qui sont pesés et chargés </p>\n','Jesus-de-Nazareth-il-passe-ici',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2418,'d1d013cb-8ecd-446d-ad27-c45791454309',0,0,'Jésus aime les enfants',93,'93_RN_fr','1.\nJésus aime les enfants,\nIl les conduit et les bénit\nPour venir, c’est le moment\nVenez au meilleur Ami\n\nRefrain\nVenez enfants ! Venez enfants !\nVenez, oh ! Venez au Sauveur !\nVenez enfants, venez ! Venez enfants !\nVenez, oh ! Venez sans peur !\n2.\nIl appelle son agneau,\nSa douce voix jusqu’à toi \nToi, brebis de ton troupeau\nAu nom connu par ton Roi.\n3.\nJésus vous dit, mes enfants\nParlant tout bas « hâtez vos pas\nMarchez ensemble, en avant,\nJe vous tiendrai dans mes bras.»','<p><strong>1.</strong> </p>\n<p>Jésus aime les enfants, </p>\n<p>Il les conduit et les bénit </p>\n<p>Pour venir, c’est le moment </p>\n<p>Venez au meilleur Ami </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Venez enfants ! Venez enfants !</strong> </p>\n<p><strong>Venez, oh ! Venez au Sauveur !</strong> </p>\n<p><strong>Venez enfants, venez ! Venez enfants !</strong> </p>\n<p><strong>Venez, oh ! Venez sans peur !</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Il appelle son agneau, </p>\n<p>Sa douce voix jusqu’à toi </p>\n<p>Toi, brebis de ton troupeau </p>\n<p>Au nom connu par ton Roi. </p>\n<p><br/> <strong>3.</strong></p>\n<p>Jésus vous dit, mes enfants </p>\n<p>Parlant tout bas « hâtez vos pas </p>\n<p>Marchez ensemble, en avant, </p>\n<p>Je vous tiendrai dans mes bras.» </p>\n','Jesus-aime-les-enfants',NULL,'fr',5,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2419,'739adb3f-a532-4e19-8cee-d60fe204bc45',0,0,'Si la route est jonchée',31,'31_GA_fr','1.\nSi la route est jonchée de peines et de douleurs\nSi ton soleil semble dans un noir nuage\nEt que nul rayon d\'éclat n\'illumine ton coeur\nPense à Christ, à son message\n\nRefrain\nJésus ton Sauveur sera toujours ton guide\nÀ lui seul confie ton sort il agira\nRappelles-toi bien que dans les lieux arides\nTon âme il rassasiera\n\n2.\nSi personne sur la terre ne peut te comprendre\nSi la tentation s\'acharne à t\'assieger\nSi tes vains efforts ne suffisent pour te défendre\nFais de Christ ton bouclier\n\n3.\nSi ton pauvre coeur à force de revers s\'enflamme\nEt que tu finis par te decourager\nSache que Christ connait les tortures de ton âme\nEt veut bien te soulager','<p><strong>1.</strong> </p>\n<p>Si la route est jonchée de peines et de douleurs </p>\n<p>Si ton soleil semble dans un noir nuage </p>\n<p>Et que nul rayon d&#39;éclat n&#39;illumine ton coeur </p>\n<p>Pense à Christ, à son message </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus ton Sauveur sera toujours ton guide</strong> </p>\n<p><strong>À lui seul confie ton sort il agira</strong> </p>\n<p><strong>Rappelles-toi bien que dans les lieux arides</strong> </p>\n<p><strong>Ton âme il rassasiera</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si personne sur la terre ne peut te comprendre </p>\n<p>Si la tentation s&#39;acharne à t&#39;assieger </p>\n<p>Si tes vains efforts ne suffisent pour te défendre </p>\n<p>Fais de Christ ton bouclier </p>\n<p><br/> <strong>3.</strong></p>\n<p>Si ton pauvre coeur à force de revers s&#39;enflamme </p>\n<p>Et que tu finis par te decourager </p>\n<p>Sache que Christ connait les tortures de ton âme </p>\n<p>Et veut bien te soulager </p>\n','Si-la-route-est-jonchee',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2420,'8a0ee28d-a731-44d3-bf8f-c2a020bf7b45',0,0,'Minnin mouin o bon Sovè',49,'49_GA_ht','1.\nMinnin mouin o bon Sovè\nKondui-m nan chemin salu\nAvèk ou m-pa bezouin pè\nNan ou sèl moin jouin-n la vi\n\nKè\nJézu, Jézu pa kite-m,\nO bon Sové\nFè-m soti nan tantasion\nOua fè moin plis ké vinke\n\n2.\nJézu pa kite-m isit\nSou tè sa plin ak doulè\nSan ou nan pouin lòt zanmi\nMin nan ou moin jouin-n bone\n\n3.\nOut e levé-m nan la bou\nDepi profon méchansté\nOua fè m rive nan siel tou\nSe la gloua ou ma chante\n','<p><strong>1.</strong> </p>\n<p>Minnin mouin o bon Sovè </p>\n<p>Kondui-m nan chemin salu </p>\n<p>Avèk ou m-pa bezouin pè </p>\n<p>Nan ou sèl moin jouin-n la vi </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Jézu, Jézu pa kite-m,</strong> </p>\n<p><strong>O bon Sové</strong> </p>\n<p><strong>Fè-m soti nan tantasion</strong> </p>\n<p><strong>Oua fè moin plis ké vinke</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Jézu pa kite-m isit </p>\n<p>Sou tè sa plin ak doulè </p>\n<p>San ou nan pouin lòt zanmi </p>\n<p>Min nan ou moin jouin-n bone </p>\n<p><br/> <strong>3.</strong></p>\n<p>Out e levé-m nan la bou </p>\n<p>Depi profon méchansté </p>\n<p>Oua fè m rive nan siel tou </p>\n<p>Se la gloua ou ma chante </p>\n','Minnin-mouin-o-bon-Sove',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2421,'2e57a526-c29f-4e41-8ceb-4f9e7b11cf0c',0,0,'Seigneur par ton bras puissant',13,'13_GA_fr','1.\nSeigneur par ton bras puissant\nSoutiens, guide ton enfant\nConduis-moi jour après jour\nJusqu\'à ton divin séjour, divin sejour\n\nRefrain\nJésus, Jésus! vois mes luttes, ma souffrance\nDonne-moi ton assurance\nSois ma force et mon soutien\n\n2.\nSi tu me conduis, Seigneur\nSi tu remplis tout mon coeur\nRien ne saurait me plier\nTu seras mon bouclier, mon bouclier\n\n3.\nSeigneur c\'est de ton amour\nQue j\'attends jour après jour\nLe soutien, le reconfort\nQui seuls rendent l\'homme fort, l\'homme fort','<p><strong>1.</strong> </p>\n<p>Seigneur par ton bras puissant </p>\n<p>Soutiens, guide ton enfant </p>\n<p>Conduis-moi jour après jour </p>\n<p>Jusqu&#39;à ton divin séjour, divin sejour </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Jésus, Jésus! vois mes luttes, ma souffrance</strong> </p>\n<p><strong>Donne-moi ton assurance</strong> </p>\n<p><strong>Sois ma force et mon soutien</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Si tu me conduis, Seigneur </p>\n<p>Si tu remplis tout mon coeur </p>\n<p>Rien ne saurait me plier </p>\n<p>Tu seras mon bouclier, mon bouclier </p>\n<p><br/> <strong>3.</strong></p>\n<p>Seigneur c&#39;est de ton amour </p>\n<p>Que j&#39;attends jour après jour </p>\n<p>Le soutien, le reconfort </p>\n<p>Qui seuls rendent l&#39;homme fort, l&#39;homme fort </p>\n','Seigneur-par-ton-bras-puissant',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2422,'410f9daa-d54c-44b8-be33-135cf8e1f9d4',1,0,'Ton nom le plus beau des poêmes',1,'1_GA_fr','1.\nTon nom le plus beau des poêmes\nTon nom la plus belle chanson\nTon nom c\'est celui-là que j\'aime\nTon nom c\'est le plus grand des temps\n\nRefrain\nJe le glorifierai et je l\'exalterai\nJe le proclamerai sur la terre des hommes\nJe parlerai de toi je porterai ta croix\nToujours je chanterai Jésus ce nom qui sauve\n\n2.\nTon nom humilié sur la terre\nTon nom méprisé par les tiens\nTon nom c\'est en lui que j\'espère\nTon nom c\'est le nom des chrétiens\n\n3.\nTon nom grand salut des humains\nOh ton nom qui apporte la Paix\nTon nom c\'est le Roi qui revient\nTon nom symbole d\'éternité','<p><strong>1.</strong> </p>\n<p>Ton nom le plus beau des poêmes </p>\n<p>Ton nom la plus belle chanson </p>\n<p>Ton nom c&#39;est celui-là que j&#39;aime </p>\n<p>Ton nom c&#39;est le plus grand des temps </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Je le glorifierai et je l&#39;exalterai</strong> </p>\n<p><strong>Je le proclamerai sur la terre des hommes</strong> </p>\n<p><strong>Je parlerai de toi je porterai ta croix</strong> </p>\n<p><strong>Toujours je chanterai Jésus ce nom qui sauve</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ton nom humilié sur la terre </p>\n<p>Ton nom méprisé par les tiens </p>\n<p>Ton nom c&#39;est en lui que j&#39;espère </p>\n<p>Ton nom c&#39;est le nom des chrétiens </p>\n<p><br/> <strong>3.</strong></p>\n<p>Ton nom grand salut des humains </p>\n<p>Oh ton nom qui apporte la Paix </p>\n<p>Ton nom c&#39;est le Roi qui revient </p>\n<p>Ton nom symbole d&#39;éternité </p>\n','Ton-nom-le-plus-beau-des-poemes',NULL,'fr',6,'2023-07-22 22:41:15','2023-10-03 20:30:24'),(2423,'7363f4bb-76d6-4d5f-bf60-68dda65e0a8d',0,0,'Dieu soit avec toi jusqu\'au revoir',20,'20_GA_fr','1.\nDieu soit avec toi jusqu\'au revoir\nÂme fidèle et soumise\nQu\'il te garde et te conduise\njusqu\'au grand repos du soir\nRefrain\nAu revoir, Au revoir,\nDieu t\'accompagne jusqu\'au soir\nAu revoir, Au revoir\nDieu soit avec toi jusqu\'au revoir\n\n2.\nDieu soit avec toi jusqu\'au revoir\nAbite-toi sous ton aile\nQu\'en ton coeur il renouvelle\nLa foi, l\'amour et I\'espoir\n\n3.\nDieu soit avec toi jusqu\'au revoir\nSi des périls te menacent\nSes bras éternels t\'enlacent\nContre eux qui peut prevaloir\n\n4.\nDieu soit avec toi jusqu\'au revoir\nBientot Jésus va paraître\nDans sa gloire, notre maître\nBientôt va nous recevoir','<p><strong>1.</strong> </p>\n<p>Dieu soit avec toi jusqu&#39;au revoir </p>\n<p>Âme fidèle et soumise </p>\n<p>Qu&#39;il te garde et te conduise </p>\n<p>jusqu&#39;au grand repos du soir </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Au revoir, Au revoir,</strong> </p>\n<p><strong>Dieu t&#39;accompagne jusqu&#39;au soir</strong> </p>\n<p><strong>Au revoir, Au revoir</strong> </p>\n<p><strong>Dieu soit avec toi jusqu&#39;au revoir</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Dieu soit avec toi jusqu&#39;au revoir </p>\n<p>Abite-toi sous ton aile </p>\n<p>Qu&#39;en ton coeur il renouvelle </p>\n<p>La foi, l&#39;amour et I&#39;espoir </p>\n<p><br/> <strong>3.</strong></p>\n<p>Dieu soit avec toi jusqu&#39;au revoir </p>\n<p>Si des périls te menacent </p>\n<p>Ses bras éternels t&#39;enlacent </p>\n<p>Contre eux qui peut prevaloir </p>\n<p><br/> <strong>4.</strong></p>\n<p>Dieu soit avec toi jusqu&#39;au revoir </p>\n<p>Bientot Jésus va paraître </p>\n<p>Dans sa gloire, notre maître </p>\n<p>Bientôt va nous recevoir </p>\n','Dieu-soit-avec-toi-jusqu\'au-revoir',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2424,'5e4c3420-9a69-4518-aaf1-ec0330318cf9',0,0,'Je ne suis pas à moi-même',27,'27_GA_fr','1.\nJe ne suis pas à moi-même\nJ\'appartiens à mon Sauveur\nCréé par mon Dieu suprême\nSauvé par le Redempteur\n\nRefrain\nSeigneur j\'appartiens à toi\nTout mon coeur toute ma vie\nDésormais je suis à toi (bis 3 lignes)\n\n2.\nPas à moi, car de l\'abîme\nJésus vient me retirer\nQuand du péché la victime\nJ\'allais cesser d\'espérer\n\n3.\nPas à moi car sans réserve\nJe livre tout au Sauveur\nEt c\'est lui qui me préserve\nDes pièges du tentateur\n\n4.\nPas à moi, Jésus m\'engage\nDans le bataillon sacré\nJe suis à lui sans partage\nA lui pour l\'éternité','<p><strong>1.</strong> </p>\n<p>Je ne suis pas à moi-même </p>\n<p>J&#39;appartiens à mon Sauveur </p>\n<p>Créé par mon Dieu suprême </p>\n<p>Sauvé par le Redempteur </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Seigneur j&#39;appartiens à toi</strong> </p>\n<p><strong>Tout mon coeur toute ma vie</strong> </p>\n<p><strong>Désormais je suis à toi (bis 3 lignes)</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Pas à moi, car de l&#39;abîme </p>\n<p>Jésus vient me retirer </p>\n<p>Quand du péché la victime </p>\n<p>J&#39;allais cesser d&#39;espérer </p>\n<p><br/> <strong>3.</strong></p>\n<p>Pas à moi car sans réserve </p>\n<p>Je livre tout au Sauveur </p>\n<p>Et c&#39;est lui qui me préserve </p>\n<p>Des pièges du tentateur </p>\n<p><br/> <strong>4.</strong></p>\n<p>Pas à moi, Jésus m&#39;engage </p>\n<p>Dans le bataillon sacré </p>\n<p>Je suis à lui sans partage </p>\n<p>A lui pour l&#39;éternité </p>\n','Je-ne-suis-pas-a-moi-meme',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2425,'0e81200b-77d1-4f34-987e-ee764bc97bf7',0,0,'Byin pré pa lakay',44,'44_GA_ht','\n\nKè\nByin pré pa lakay, min-m si ouè-w preské rivé\nByin pré pa lakay, min-m si ou ouè vil la devan-ou\nByin pré pa lakay, min-m si problem yo rézoud\nByin pré pa lakay,\nou pa bezouin trionfé\n\n1.\nGin kèk gro krétyin ki la lontan nan légliz\nYo telman ansyin min-m limon levé sou yo\nYo chita, chita yo, yo pa jan-m vlé fè progrè\nLè yo egzoté yo, yo di yo déja sové\n\n2.\nGin kèk gro kretyin ki gin sinkantan légliz\nSe yok fè la sèn,\nkoté misyon yé yo la\nLè yo vin légliz yo toujou pran plas devan\nMin kou gnou frè pile yo,\nou ouè yo\njouk nan bare, yo pralé\n\n3.\nGin kèk gro levit, ki vin-m tou cho nan légliz\nYo zanmi paste yo zanmi predicate\nYo bay gro lajan, yo nan tout aktivité\nMin yo pa jan-m chanjé, yo konpran-n yo fi-n rive\n\n4.\nSonjé lè Moyiz té fi-n\ndeliver pèp la\nLi ta pé maché min nan min ak létènèl\nLi rive sou rout lite fe yon ti kolè\nMoyiz ouè Kanaan,\nmin li pat antré ladan-l\nGin anpil krétyin ki ranpli ak ògèy\nYo toujou pansé yo pibon pasé tout moun\nLè yo jouin-n avèk ou yo di ti frè kòman ou yé\nMin devan létènèl pa ti frè,\nni gran frè','<p><strong>Kè</strong> </p>\n<p>Byin pré pa lakay, min-m si ouè-w preské rivé </p>\n<p>Byin pré pa lakay, min-m si ou ouè vil la devan-ou </p>\n<p>Byin pré pa lakay, min-m si problem yo rézoud </p>\n<p>Byin pré pa lakay, </p>\n<p>ou pa bezouin trionfé </p>\n<p><br/> <strong>1.</strong></p>\n<p>Gin kèk gro krétyin ki la lontan nan légliz </p>\n<p>Yo telman ansyin min-m limon levé sou yo </p>\n<p>Yo chita, chita yo, yo pa jan-m vlé fè progrè </p>\n<p>Lè yo egzoté yo, yo di yo déja sové </p>\n<p><br/> <strong>2.</strong></p>\n<p>Gin kèk gro kretyin ki gin sinkantan légliz </p>\n<p>Se yok fè la sèn, </p>\n<p>koté misyon yé yo la </p>\n<p>Lè yo vin légliz yo toujou pran plas devan </p>\n<p>Min kou gnou frè pile yo, </p>\n<p>ou ouè yo </p>\n<p>jouk nan bare, yo pralé </p>\n<p><br/> <strong>3.</strong></p>\n<p>Gin kèk gro levit, ki vin-m tou cho nan légliz </p>\n<p>Yo zanmi paste yo zanmi predicate </p>\n<p>Yo bay gro lajan, yo nan tout aktivité </p>\n<p>Min yo pa jan-m chanjé, yo konpran-n yo fi-n rive </p>\n<p><br/> <strong>4.</strong></p>\n<p>Sonjé lè Moyiz té fi-n </p>\n<p>deliver pèp la </p>\n<p>Li ta pé maché min nan min ak létènèl </p>\n<p>Li rive sou rout lite fe yon ti kolè </p>\n<p>Moyiz ouè Kanaan, </p>\n<p>min li pat antré ladan-l </p>\n<p>Gin anpil krétyin ki ranpli ak ògèy </p>\n<p>Yo toujou pansé yo pibon pasé tout moun </p>\n<p>Lè yo jouin-n avèk ou yo di ti frè kòman ou yé </p>\n<p>Min devan létènèl pa ti frè, </p>\n<p>ni gran frè </p>\n','Byin-pre-pa-lakay',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2426,'4ff6a4ee-c63a-40b9-b16a-f9c94dcaf2e7',0,0,'Si ton sentier devient rude et pénible',5,'5_GA_fr','1.\nSi ton sentier devient rude et pénible\nRassures-toi Jésus est là\nSouviens-toi qu\'avec lui tout est possible\nSon amour te conduira il est la\nTous tes soucis ne les connait-il pas\nPar sa puissance invincible ll t\'en delivrera\n\n2.\nEt si Satan dans sa rage t\'assaille\nRassures-toi, Jésus est la\nDans le combat si ta force défaille\nSon bras fort te soutiendra\nRassures-toi Jésus est la\nTes ennemis ne les connait-il pas\nAu plus fort de la bataille\nII en triomphera\n\n3.\nEt si tu souffres triste et solitaire\nRassures-toi, Jésus est la\nPour toi n\'a t-il pas gravi le calvaire\nSon amour te suffira\nRassures-toi, Jésus est la\nTous tes soupirs ne les connait-il pas\nII t\'aime et plus doux qu\'un frère \nII te consolera','<p><strong>1.</strong> </p>\n<p>Si ton sentier devient rude et pénible </p>\n<p>Rassures-toi Jésus est là </p>\n<p>Souviens-toi qu&#39;avec lui tout est possible </p>\n<p>Son amour te conduira il est la </p>\n<p>Tous tes soucis ne les connait-il pas </p>\n<p>Par sa puissance invincible ll t&#39;en delivrera </p>\n<p><br/> <strong>2.</strong></p>\n<p>Et si Satan dans sa rage t&#39;assaille </p>\n<p>Rassures-toi, Jésus est la </p>\n<p>Dans le combat si ta force défaille </p>\n<p>Son bras fort te soutiendra </p>\n<p>Rassures-toi Jésus est la </p>\n<p>Tes ennemis ne les connait-il pas </p>\n<p>Au plus fort de la bataille </p>\n<p>II en triomphera </p>\n<p><br/> <strong>3.</strong></p>\n<p>Et si tu souffres triste et solitaire </p>\n<p>Rassures-toi, Jésus est la </p>\n<p>Pour toi n&#39;a t-il pas gravi le calvaire </p>\n<p>Son amour te suffira </p>\n<p>Rassures-toi, Jésus est la </p>\n<p>Tous tes soupirs ne les connait-il pas </p>\n<p>II t&#39;aime et plus doux qu&#39;un frère </p>\n<p>II te consolera </p>\n','Si-ton-sentier-devient-rude-et-penible',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2427,'a138d861-5e38-491c-be17-7ba702c58e62',0,0,'Jézu sové moin, map di ou mèsi',64,'64_GA_ht','1.\nJézu sové moin, map di ou mèsi\nDeské ou rachté-m, ou pat kite-m peri\nNan peché-m yo, mèsi papa\nPa kite-m tonbé, pa kite enmi yo\nTrionfé sou moin\n\n2.\nOu jistifié moin, avèk prop san ou\nOu mété-m a pa pou-m fè louanj ou\nO Bondié moin konfié-m nan ou\nPa kite-m tonbé pa kité èlmi yo\nTrionfé sou moin\n\n3.\nMoin konfié-m nan ou O Bon Sovè moin\nPran moin par la min pou-m ka fe rout la\nO Bondié moin konfié-m nan ou\nPa kite-m tonbé pa kite èlmi yo\nTrionfé sou moin\n','<p><strong>1.</strong> </p>\n<p>Jézu sové moin, map di ou mèsi </p>\n<p>Deské ou rachté-m, ou pat kite-m peri </p>\n<p>Nan peché-m yo, mèsi papa </p>\n<p>Pa kite-m tonbé, pa kite enmi yo </p>\n<p>Trionfé sou moin </p>\n<p><br/> <strong>2.</strong></p>\n<p>Ou jistifié moin, avèk prop san ou </p>\n<p>Ou mété-m a pa pou-m fè louanj ou </p>\n<p>O Bondié moin konfié-m nan ou </p>\n<p>Pa kite-m tonbé pa kité èlmi yo </p>\n<p>Trionfé sou moin </p>\n<p><br/> <strong>3.</strong></p>\n<p>Moin konfié-m nan ou O Bon Sovè moin </p>\n<p>Pran moin par la min pou-m ka fe rout la </p>\n<p>O Bondié moin konfié-m nan ou </p>\n<p>Pa kite-m tonbé pa kite èlmi yo </p>\n<p>Trionfé sou moin </p>\n','Jezu-sove-moin-map-di-ou-mesi',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2428,'c2d74693-b4af-4ff5-a3a3-b66d36e4b745',0,0,'J\'errais dans le péché quand Jésus m\'a sauvé',16,'16_GA_fr','1.\nJ\'errais dans le péché quand Jésus m\'a sauvé\nDans le courant des vives eaux\nLa main toute meurtrie je recouvrai la vie\nÀ la source des vives eaux\nRefrain\nVivant auprès des vives eaux\nVives eaux\nLoin du péché, loin de tous maux\nJésus est ma lumière, je vis sous sa bannière\nToujours auprès des vives eaux\n\n2.\nA mon coté Jésus que desirai-je de plus\nJe suis auprès des vives eaux\nChrist est mon espérance joyeux en sa présence\nJe suis au sein des vives eaux\n\n3.\nMes combats vont finir et le compte va venir\nBientôt au sein des vives eaux\nDans la céleste armée ma place est préparée\nPrès des ruisseaux des vives eaux.','<p><strong>1.</strong> </p>\n<p>J&#39;errais dans le péché quand Jésus m&#39;a sauvé </p>\n<p>Dans le courant des vives eaux </p>\n<p>La main toute meurtrie je recouvrai la vie </p>\n<p>À la source des vives eaux </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Vivant auprès des vives eaux</strong> </p>\n<p><strong>Vives eaux</strong> </p>\n<p><strong>Loin du péché, loin de tous maux</strong> </p>\n<p><strong>Jésus est ma lumière, je vis sous sa bannière</strong> </p>\n<p><strong>Toujours auprès des vives eaux</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>A mon coté Jésus que desirai-je de plus </p>\n<p>Je suis auprès des vives eaux </p>\n<p>Christ est mon espérance joyeux en sa présence </p>\n<p>Je suis au sein des vives eaux </p>\n<p><br/> <strong>3.</strong></p>\n<p>Mes combats vont finir et le compte va venir </p>\n<p>Bientôt au sein des vives eaux </p>\n<p>Dans la céleste armée ma place est préparée </p>\n<p>Près des ruisseaux des vives eaux. </p>\n','J\'errais-dans-le-peche-quand-Jesus-m\'a-sauve',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2429,'50a04b02-f505-4a5e-9e27-7ffbc96c0651',0,0,'Je suis un soldat de mon Roi',19,'19_GA_fr','1.\nJe suis un soldat de mon Roi\nDisciple de l\'agneau\nSans peur je confesse ma foi\nEn Jésus nom si beau\nRefrain\nNous serons couronnés après la bataille\nDieu nous couronnera que nul ne defaille\nNous serons couronnés après la bataille\nDans tes murs sainte Sion\nDans tes murs (ter) Sainte Sion\nNous serons couronnés après la bataille\nDans tes murs Sainte Sion\n\n2.\nIrai-je à mon tour dans les cieux\nSans lutter, sans douleur?\nQuand d\'autres, martyrs glorieux\nOnt verse tant de pleurs\nDu monde aux funestres attraits des pièges du malin\nJésus me garde, et pour jamais\nJ\'ai le secours divin\nVeux tu régner? Il faut lutter\nDonne Seigneur, la foi\nPour souffrir, vaincre et remporter\nLe prix mon Dieu pour toi.','<p><strong>1.</strong> </p>\n<p>Je suis un soldat de mon Roi </p>\n<p>Disciple de l&#39;agneau </p>\n<p>Sans peur je confesse ma foi </p>\n<p>En Jésus nom si beau </p>\n<p><br/> <strong>Refrain</strong></p>\n<p><strong>Nous serons couronnés après la bataille</strong> </p>\n<p><strong>Dieu nous couronnera que nul ne defaille</strong> </p>\n<p><strong>Nous serons couronnés après la bataille</strong> </p>\n<p><strong>Dans tes murs sainte Sion</strong> </p>\n<p><strong>Dans tes murs (ter) Sainte Sion</strong> </p>\n<p><strong>Nous serons couronnés après la bataille</strong> </p>\n<p><strong>Dans tes murs Sainte Sion</strong> </p>\n<p><br/> <strong>2.</strong></p>\n<p>Irai-je à mon tour dans les cieux </p>\n<p>Sans lutter, sans douleur? </p>\n<p>Quand d&#39;autres, martyrs glorieux </p>\n<p>Ont verse tant de pleurs </p>\n<p>Du monde aux funestres attraits des pièges du malin </p>\n<p>Jésus me garde, et pour jamais </p>\n<p>J&#39;ai le secours divin </p>\n<p>Veux tu régner? Il faut lutter </p>\n<p>Donne Seigneur, la foi </p>\n<p>Pour souffrir, vaincre et remporter </p>\n<p>Le prix mon Dieu pour toi. </p>\n','Je-suis-un-soldat-de-mon-Roi',NULL,'fr',6,'2023-07-22 22:41:15','2023-07-22 22:41:15'),(2430,'af49d454-1277-487e-a70e-6deffa11be5f',0,0,'Gnou jou lè zòm ap péyé trè chè',48,'48_GA_ht','1.\nGnou jou lè zòm ap péyé trè chè la rebelion yo\nMaché nan lidé yo, pratiké la méchansté\nBòn Dié nan gran amou li, mété sou tout la tè\nLévanjil kou gnou lumiè, min lè zòm pa kompran-n sa\n\n2.\nTé gin gnou non-m ki té rich yo té rélé Nabal\nDavid té voyé tout grans sevis yo,\nDavid té ran-n Nabal\nLè li té nan dézè-a, min Nabal té méprizé-l\nMin Jézu nan amou li, si ou ta bay la min\nAvèk gnou sèvitè li, sé avèk li ou fè sa\nBon Dié nan lié sékré-a apé gadé chak moun\nMalgré tout méchansté yo, min Bon Dié vlé yo sové\n\nKè\nGnou jou tankou Jodi-a, Jézu pral retounin\nLa tè pral souké kò li, méchan yo pral tombé (bis)','<p><strong>1.</strong> </p>\n<p>Gnou jou lè zòm ap péyé trè chè la rebelion yo </p>\n<p>Maché nan lidé yo, pratiké la méchansté </p>\n<p>Bòn Dié nan gran amou li, mété sou tout la tè </p>\n<p>Lévanjil kou gnou lumiè, min lè zòm pa kompran-n sa </p>\n<p><br/> <strong>2.</strong></p>\n<p>Té gin gnou non-m ki té rich yo té rélé Nabal </p>\n<p>David té voyé tout grans sevis yo, </p>\n<p>David té ran-n Nabal </p>\n<p>Lè li té nan dézè-a, min Nabal té méprizé-l </p>\n<p>Min Jézu nan amou li, si ou ta bay la min </p>\n<p>Avèk gnou sèvitè li, sé avèk li ou fè sa </p>\n<p>Bon Dié nan lié sékré-a apé gadé chak moun </p>\n<p>Malgré tout méchansté yo, min Bon Dié vlé yo sové </p>\n<p><br/> <strong>Kè</strong></p>\n<p><strong>Gnou jou tankou Jodi-a, Jézu pral retounin</strong> </p>\n<p><strong>La tè pral souké kò li, méchan yo pral tombé (bis)</strong> </p>\n','Gnou-jou-le-zom-ap-peye-tre-che',NULL,'ht',6,'2023-07-22 22:41:15','2023-07-22 22:41:15');
