INSERT INTO `verseofthedays` (`id`, `uuid`, `version`, `bookName`, `bookNumber`, `chapter`, `verses`, `texts`, `createdAt`, `updatedAt`) VALUES (3,'84dcede3-f6f2-44b9-85e6-b0243e40f27d','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Genèse',10,1,'4,7,8','[{\"text\": \"Dieu vit que la lumière était bonne; et Dieu sépara la lumière d’avec les ténèbres.\", \"verse\": 4, \"chapter\": 1, \"book_number\": 10}, {\"text\": \"Et Dieu fit l’étendue, et il sépara les eaux qui sont au-dessous de l’étendue d’avec les eaux qui sont au-dessus de l’étendue. Et cela fut ainsi.\", \"verse\": 7, \"chapter\": 1, \"book_number\": 10}, {\"text\": \"Dieu appela l’étendue ciel. Ainsi, il y eut un soir, et il y eut un matin: ce fut le second jour.\", \"verse\": 8, \"chapter\": 1, \"book_number\": 10}]','2023-08-23 05:23:11','2023-08-23 05:23:11'),(4,'72220303-07ce-4093-96eb-0df65fa8b76d','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Juges',70,21,'5,6','[{\"text\": \"Les enfants d’Israël dirent: Quel est celui d’entre toutes les tribus d’Israël qui n’est pas monté à l’assemblée devant l’Éternel? Car on avait fait un serment solennel contre quiconque ne monterait pas vers l’Éternel à Mitspa, on avait dit: Il sera puni de mort.\", \"verse\": 5, \"chapter\": 21, \"book_number\": 70}, {\"text\": \"Les enfants d’Israël éprouvaient du repentir au sujet de Benjamin, leur frère, et ils disaient: Aujourd’hui une tribu a été retranchée d’Israël.\", \"verse\": 6, \"chapter\": 21, \"book_number\": 70}]','2023-08-26 03:21:48','2023-08-26 03:21:48'),(5,'748c5a75-163f-42b5-a8e2-e7a2c5d32ee3','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Genèse',10,1,'7,8','[{\"text\": \"Et Dieu fit l’étendue, et il sépara les eaux qui sont au-dessous de l’étendue d’avec les eaux qui sont au-dessus de l’étendue. Et cela fut ainsi.\", \"verse\": 7, \"chapter\": 1, \"book_number\": 10}, {\"text\": \"Dieu appela l’étendue ciel. Ainsi, il y eut un soir, et il y eut un matin: ce fut le second jour.\", \"verse\": 8, \"chapter\": 1, \"book_number\": 10}]','2023-08-30 02:39:53','2023-08-30 02:39:53'),(6,'b391a19f-fa9a-4632-bbca-1efc8b7a276d','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Juges',70,16,'3,4','[{\"text\": \"Samson demeura couché jusqu’à minuit. Vers minuit, il se leva; et il saisit les battants de la porte de la ville et les deux poteaux, les arracha avec la barre, les mit sur ses épaules, et les porta sur le sommet de la montagne qui est en face d’Hébron.\", \"verse\": 3, \"chapter\": 16, \"book_number\": 70}, {\"text\": \"Après cela, il aima une femme dans la vallée de Sorek. Elle se nommait Delila.\", \"verse\": 4, \"chapter\": 16, \"book_number\": 70}]','2023-09-07 03:49:38','2023-09-07 03:49:38'),(7,'a53fc811-7bd3-4f99-b998-6678ff0be588','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Juges',70,16,'17,18','[{\"text\": \"il lui ouvrit tout son cœur, et lui dit: Le rasoir n’a point passé sur ma tête, parce que je suis consacré à Dieu dès le ventre de ma mère. Si j’étais rasé, ma force m’abandonnerait, je deviendrais faible, et je serais comme tout autre homme.\", \"verse\": 17, \"chapter\": 16, \"book_number\": 70}, {\"text\": \"Delila, voyant qu’il lui avait ouvert tout son cœur, envoya appeler les princes des Philistins, et leur fit dire: Montez cette fois, car il m’a ouvert tout son cœur. Et les princes des Philistins montèrent vers elle, et apportèrent l’argent dans leurs mains.\", \"verse\": 18, \"chapter\": 16, \"book_number\": 70}]','2023-09-07 03:50:38','2023-09-07 03:50:38'),(8,'44c267dd-64b3-47e2-b898-affc09ea998f','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Juges',70,16,'22','[{\"text\": \"<pb/>Cependant les cheveux de sa tête recommençaient à croître, depuis qu’il avait été rasé.\", \"verse\": 22, \"chapter\": 16, \"book_number\": 70}]','2023-09-07 03:50:57','2023-09-07 03:50:57'),(9,'7cdc0c4c-2f72-493f-8403-30a9a34672b0','{\"file\": \"LSG.SQLite3\", \"name\": \"LSG\", \"language\": \"fr\", \"description\": \"Bible Segond 1910\", \"longLanguage\": \"Français\"}','Genèse',10,1,'16,18','[{\"text\": \"Que notre seigneur parle! Tes serviteurs sont devant toi. Ils chercheront un homme qui sache jouer de la harpe; et, quand le mauvais esprit de Dieu sera sur toi, il jouera de sa main, et tu seras soulagé.\", \"verse\": 16, \"chapter\": 16, \"book_number\": 90}, {\"text\": \"L’un des serviteurs prit la parole, et dit: Voici, j’ai vu un fils d’Isaï, Bethléhémite, qui sait jouer; c’est aussi un homme fort et vaillant, un guerrier, parlant bien et d’une belle figure, et l’Éternel est avec lui.\", \"verse\": 18, \"chapter\": 16, \"book_number\": 90}]','2023-09-20 03:27:13','2023-09-20 03:27:13');
